import * as React from 'react';
import { lazy, Suspense, useEffect } from 'react';
import { SystemIntegration } from './components/core/SystemIntegration';
import { ThresholdIntegration } from './components/core/ThresholdIntegration';
import { GameStateMonitor } from './components/debug/GameStateMonitor';
import { TooltipProvider } from './components/ui/TooltipProvider';
import { defaultColony, defaultMothership } from './config/buildings/defaultBuildings';
import { defaultModuleConfigs } from './config/modules/defaultModuleConfigs';
import { GameActionType, GameProvider, useGameDispatch } from './contexts/GameContext';
import { ModuleActionType, ModuleProvider, useModuleDispatch } from './contexts/ModuleContext';
import { ResourceRatesProvider } from './contexts/ResourceRatesContext';
import { ThresholdProvider } from './contexts/ThresholdContext';
import { assetManager } from './managers/game/assetManager';
import { ResourceManager } from './managers/game/ResourceManager';
import { TechNode, techTreeManager } from './managers/game/techTreeManager';
import { moduleManager } from './managers/module/ModuleManager';
import { OfficerManager } from './managers/module/OfficerManager';
import { ShipHangarManager } from './managers/module/ShipHangarManager';
import { ModuleType } from './types/buildings/ModuleTypes';
import { ModuleStatus } from './types/modules/ModuleTypes';
import { ResourceType } from './types/resources/ResourceTypes';

// Import the GlobalErrorBoundary component
import { GlobalErrorBoundary } from './components/ui/GlobalErrorBoundary';
// Import error services
import { IntegrationErrorHandler } from './components/core/IntegrationErrorHandler';
import ResourceVisualization from './components/ui/ResourceVisualization';
import { useComponentProfiler } from './hooks/ui/useComponentProfiler';
import { useProfilingOverlay } from './hooks/ui/useProfilingOverlay';
import { errorLoggingService, ErrorSeverity, ErrorType } from './services/ErrorLoggingService';
import { eventPropagationService } from './services/EventPropagationService';
import { recoveryService } from './services/RecoveryService';
import { BaseEvent } from './types/events/EventTypes';

// Lazy load components that aren't needed on initial render
const GameLayout = lazy(() =>
  import('./components/ui/GameLayout').then(module => ({ default: module.GameLayout }))
);

// Loading component
const LoadingComponent = () => (
  <div className="flex h-screen w-full flex-col items-center justify-center bg-gray-900 text-white">
    <div className="mb-4 h-12 w-12 animate-spin rounded-full border-4 border-blue-500 border-t-transparent"></div>
    <h2 className="mb-2 text-xl font-medium">Loading Game Components...</h2>
    <p className="text-gray-400">Preparing your galactic adventure</p>
  </div>
);

// Initial tech tree setup
const initialTechs: TechNode[] = [
  {
    id: 'basic-ship-hangar',
    name: 'Basic Ship Hangar',
    description: 'Enables basic ship construction',
    type: 'hangar',
    tier: 1 as const,
    requirements: [],
    unlocked: true,
    category: 'infrastructure',
  },
  {
    id: 'basic-weapons',
    name: 'Basic Weapons',
    description: 'Enables basic weapon systems',
    type: 'weapons',
    tier: 1 as const,
    requirements: ['basic-ship-hangar'],
    unlocked: false,
    category: 'weapons',
  },
  {
    id: 'basic-sensors',
    name: 'Basic Sensors',
    description: 'Enables basic scanning capabilities',
    type: 'recon',
    tier: 1 as const,
    requirements: ['basic-ship-hangar'],
    unlocked: false,
    category: 'reconFleet',
  },
];

interface ResourceEvent extends BaseEvent {
  moduleId: string;
  data: {
    resources: {
      current: number;
      [key: string]: unknown;
    };
  };
}

interface ThresholdEvent extends BaseEvent {
  resourceId: ResourceType;
  details: {
    type: 'below_minimum' | 'above_maximum';
    current: number;
    min?: number;
    max?: number;
  };
}

// GameInitializer component to handle game initialization
const GameInitializer = ({ children }: { children: React.ReactNode }) => {
  const dispatch = useGameDispatch();
  const [isInitialized, setIsInitialized] = React.useState(false);
  const moduleDispatch = useModuleDispatch();
  const [resourceManagerInstance] = React.useState(() => new ResourceManager());

  useEffect(() => {
    const initializeGame = async () => {
      if (!isInitialized) {
        console.warn('Starting game initialization...');
        try {
          // Initialize resource manager
          console.warn('Initializing resource manager...');
          // ResourceManager is already initialized via useState

          // Register module configurations
          console.warn('Registering module configurations...');
          if (defaultModuleConfigs) {
            Object.values(defaultModuleConfigs).forEach(config => {
              if (config) {
                console.warn(`Registering module config: ${config.type}`);
                moduleManager.registerModuleConfig(config);
              }
            });
          } else {
            console.warn('defaultModuleConfigs is null or undefined');
          }

          // Register default buildings
          console.warn('Registering default buildings...');
          if (defaultMothership) {
            console.warn(`Registering mothership: ${defaultMothership.id}`);
            moduleManager.registerBuilding(defaultMothership);

            // Also register the building with the ModuleContext
            moduleDispatch({
              type: ModuleActionType.ADD_MODULE,
              payload: {
                module: {
                  ...defaultMothership,
                  name: 'Mothership',
                  position: { x: 0, y: 0 },
                  isActive: true,
                  status: ModuleStatus.ACTIVE,
                  type: 'resource-manager' as ModuleType,
                },
              },
            });
          }

          if (defaultColony) {
            console.warn(`Registering colony: ${defaultColony.id}`);
            moduleManager.registerBuilding(defaultColony);

            // Also register the building with the ModuleContext
            moduleDispatch({
              type: ModuleActionType.ADD_MODULE,
              payload: {
                module: {
                  ...defaultColony,
                  name: 'Colony',
                  position: { x: 0, y: 0 },
                  isActive: true,
                  status: ModuleStatus.ACTIVE,
                  type: 'resource-manager' as ModuleType,
                },
              },
            });
          }

          // Initialize asset manager
          console.warn('Initializing asset manager...');
          await assetManager.initialize();

          // Register initial technologies
          console.warn('Registering initial technologies...');
          if (initialTechs) {
            initialTechs.forEach(tech => {
              if (tech) {
                console.warn(`Registering tech: ${tech.id}`);
                techTreeManager.registerNode(tech);
              }
            });
          } else {
            console.warn('initialTechs is null or undefined');
          }

          // Add initial resources
          console.warn('Adding initial resources...');
          dispatch({
            type: GameActionType.UPDATE_RESOURCES,
            payload: {
              minerals: 2000, // Increased initial resources to allow for early module building
              energy: 2000,
              research: 0,
              population: 100,
            },
          });

          // Update systems count
          console.warn('Updating system counts...');
          dispatch({
            type: GameActionType.UPDATE_SYSTEMS,
            payload: {
              total: 1,
              colonized: 1,
              explored: 1,
            },
          });

          // Initialize the officer manager
          console.warn('Initializing officer manager...');
          const officerManager = new OfficerManager();

          // Initialize the ship hangar manager
          console.warn('Initializing ship hangar manager...');
          const shipHangarManager = new ShipHangarManager(resourceManagerInstance, officerManager);

          // Register the ship hangar manager with the global window object for development access
          if (process.env.NODE_ENV === 'development') {
            // Make manager available for debugging
            (
              window as Window & typeof globalThis & { shipHangarManager: ShipHangarManager }
            ).shipHangarManager = shipHangarManager;
          }

          // Initialize event propagation service
          console.warn('Initializing event propagation service...');

          // Register event mappings
          eventPropagationService.subscribe({
            eventType: 'RESOURCE_UPDATED',
            priority: 1,
            callback: (eventData: unknown) => {
              const event = eventData as ResourceEvent;
              const resources = event.data.resources;
              const current = resources.current;

              return {
                resourceId: event.moduleId,
                details: {
                  current,
                  type: 'below_minimum',
                },
                timestamp: Date.now(),
              };
            },
          });

          // Register threshold to module mappings
          eventPropagationService.subscribe({
            eventType: 'THRESHOLD_VIOLATED',
            priority: 1,
            callback: (eventData: unknown) => {
              const event = eventData as ThresholdEvent;
              return {
                moduleId: 'threshold-service',
                moduleType: 'resource' as ModuleType,
                timestamp: Date.now(),
                data: {
                  resourceType: event.resourceId,
                  thresholdType: event.details.type === 'below_minimum' ? 'min' : 'max',
                  current: event.details.current,
                  threshold:
                    event.details.type === 'below_minimum' ? event.details.min : event.details.max,
                },
              };
            },
          });

          // Initialize the service
          eventPropagationService.initialize();

          // Set initialization flag
          console.warn('Game initialization complete!');

          // Log the current state of the module manager
          console.warn('Module manager state:', {
            buildings: moduleManager.getBuildings(),
            modules: moduleManager.getActiveModules(),
            configs: 'Module configurations registered',
          });

          setIsInitialized(true);
        } catch (error) {
          console.error('Error during game initialization:', error);
          errorLoggingService.logError(
            error instanceof Error ? error : new Error(String(error)),
            ErrorType.INITIALIZATION,
            ErrorSeverity.HIGH,
            {
              action: 'initialization',
            }
          );

          // Attempt recovery
          const snapshot = {
            gameState: 'error',
            error: error instanceof Error ? error.message : String(error),
          };
          recoveryService.createSnapshot(snapshot, { reason: 'Error during initialization' });
        }
      }
    };

    initializeGame();
  }, [dispatch, isInitialized, moduleDispatch]);

  if (!isInitialized) {
    return (
      <div className="flex h-screen w-full flex-col items-center justify-center bg-gray-900 text-white">
        <div className="mb-4 h-12 w-12 animate-spin rounded-full border-4 border-blue-500 border-t-transparent"></div>
        <h2 className="mb-2 text-xl font-medium">Initializing Game Systems...</h2>
        <p className="text-gray-400">Preparing galaxy for exploration</p>
      </div>
    );
  }

  return (
    <IntegrationErrorHandler componentName="SystemIntegration">
      <SystemIntegration resourceManager={resourceManagerInstance}>
        <IntegrationErrorHandler componentName="ThresholdIntegration">
          <ThresholdIntegration resourceManager={resourceManagerInstance}>
            {children}
          </ThresholdIntegration>
        </IntegrationErrorHandler>
      </SystemIntegration>
    </IntegrationErrorHandler>
  );
};

// Handler for global errors
const handleGlobalError = (error: Error, errorInfo: React.ErrorInfo) => {
  // Log the error using our error logging service
  errorLoggingService.logError(error, ErrorType.RUNTIME, ErrorSeverity.HIGH, {
    componentName: 'GlobalErrorBoundary',
    errorInfo,
  });

  // Log to console for development purposes
  console.error('Global error caught:', error, errorInfo);
};

// A wrapper for the GameLayout component to provide the required props
const GameLayoutWrapper = () => {
  return (
    <GameLayout empireName="Stellar Dominion" bannerColor="#4FD1C5">
      <div className="min-h-screen bg-gray-900">
        <ResourceVisualization type={ResourceType.ENERGY} value={100} />
      </div>
    </GameLayout>
  );
};

export default function App() {
  // Enable app-level profiling
  const profiler = useComponentProfiler('App', {
    enabled: true,
    logToConsole: false,
    trackPropChanges: true,
  });

  // Show profiling overlay in development
  useProfilingOverlay({
    enabledByDefault: process.env.NODE_ENV === 'development',
    enableInProduction: false,
    toggleKey: 'p',
    persistState: true,
  });

  // Make the ResourceManager accessible in development
  const [resourceManager] = React.useState(() => new ResourceManager());

  if (process.env.NODE_ENV === 'development') {
    // Make ResourceManager available for debugging
    (window as Window & typeof globalThis & { resourceManager: ResourceManager }).resourceManager =
      resourceManager;
  }

  // Development mode debug tools
  const showDebugTools = process.env.NODE_ENV === 'development';

  return (
    <div className="app-container">
      <GlobalErrorBoundary onError={handleGlobalError}>
        <GameProvider>
          <ModuleProvider>
            <ResourceRatesProvider>
              <ThresholdProvider>
                <TooltipProvider>
                  <GameInitializer>
                    <Suspense fallback={<LoadingComponent />}>
                      <GameLayoutWrapper />
                      {showDebugTools && <GameStateMonitor expanded={false} />}
                    </Suspense>
                  </GameInitializer>
                </TooltipProvider>
              </ThresholdProvider>
            </ResourceRatesProvider>
          </ModuleProvider>
        </GameProvider>
      </GlobalErrorBoundary>
    </div>
  );
}

import { motion } from 'framer-motion';
import {
  AlertTriangle,
  CheckCircle,
  ChevronDown,
  ChevronUp,
  Clock,
  Pause,
  Play,
  RotateCcw,
  Settings,
  TrendingUp,
  Users,
} from 'lucide-react';
import { useCallback, useEffect, useState } from 'react';
import { useModuleEvents } from '../../../hooks/events/useModuleEvents';
import { moduleEventBus } from '../../../lib/events/ModuleEventBus';
import { EventType } from '../../../types/events/EventTypes';
import { StandardizedEvent } from '../../../types/events/StandardizedEvents';
import { ResourceType } from '../../../types/resources/ResourceTypes';

interface PopulationEvent {
  id: string;
  timestamp: number;
  type: 'growth' | 'decline' | 'migration' | 'disaster' | 'policy';
  amount: number;
  reason: string;
}

interface AutomatedPopulationManagerProps {
  colonyId: string;
  colonyName: string;
  currentPopulation: number;
  maxPopulation: number;
  growthRate: number; // Effective growth rate per cycle
  cycleLength: number; // Length of a growth cycle in milliseconds
  autoGrowthEnabled?: boolean; // Whether automatic growth is enabled
  events?: PopulationEvent[]; // Population events history
  onPopulationChange?: (newPopulation: number) => void;
  onAutoGrowthToggle?: (enabled: boolean) => void;
  onCycleComplete?: (cycleCount: number) => void;
  onSettingsChange?: (settings: { cycleLength: number }) => void;
}

/**
 * AutomatedPopulationManager component
 *
 * Manages automated population growth for a colony.
 * Handles growth cycles, population events, and provides controls for automation.
 */
export function AutomatedPopulationManager({
  colonyId,
  colonyName,
  currentPopulation,
  maxPopulation,
  growthRate,
  cycleLength,
  autoGrowthEnabled = false,
  events = [],
  onPopulationChange,
  onAutoGrowthToggle,
  onCycleComplete,
  onSettingsChange,
}: AutomatedPopulationManagerProps) {
  const [isRunning, setIsRunning] = useState(autoGrowthEnabled);
  const [cycleProgress, setCycleProgress] = useState(0);
  const [cycleCount, setCycleCount] = useState(0);
  const [nextGrowthAmount, setNextGrowthAmount] = useState(0);
  const [showSettings, setShowSettings] = useState(false);
  const [customCycleLength, setCustomCycleLength] = useState(cycleLength);
  const [showEvents, setShowEvents] = useState(false);

  const { subscribe } = useModuleEvents();

  // Calculate the next growth amount based on current population and growth rate
  const calculateNextGrowthAmount = useCallback(() => {
    if (currentPopulation >= maxPopulation) {
      return 0;
    }

    // Calculate raw growth
    const rawGrowth = Math.floor(currentPopulation * growthRate);

    // Ensure we don't exceed max population
    const adjustedGrowth = Math.min(rawGrowth, maxPopulation - currentPopulation);

    // Ensure at least 1 population growth if any growth is possible
    return Math.max(1, adjustedGrowth);
  }, [currentPopulation, maxPopulation, growthRate]);

  // Update next growth amount when dependencies change
  useEffect(() => {
    setNextGrowthAmount(calculateNextGrowthAmount());
  }, [calculateNextGrowthAmount]);

  // Emit population update event
  const emitPopulationUpdate = useCallback(
    (newPopulation: number) => {
      const event: StandardizedEvent = {
        type: EventType.MODULE_UPDATED,
        moduleId: colonyId,
        moduleType: ResourceType.POPULATION,
        timestamp: Date.now(),
        data: {
          stats: {
            [ResourceType.POPULATION]: newPopulation,
          },
        },
      };
      moduleEventBus.emit(event);
    },
    [colonyId]
  );

  // Handle population growth cycle
  useEffect(() => {
    if (!isRunning) return;

    let lastUpdate = Date.now();
    let progress = 0;

    const updateInterval = setInterval(() => {
      const now = Date.now();
      const delta = now - lastUpdate;
      lastUpdate = now;

      progress += delta;
      const progressPercent = (progress / customCycleLength) * 100;
      setCycleProgress(progressPercent);

      if (progress >= customCycleLength) {
        // Reset progress
        progress = 0;
        setCycleProgress(0);
        setCycleCount(prev => prev + 1);

        // Calculate and apply population growth
        const growthAmount = calculateNextGrowthAmount();
        if (growthAmount > 0) {
          const newPopulation = currentPopulation + growthAmount;
          onPopulationChange?.(newPopulation);
          emitPopulationUpdate(newPopulation);
        }

        // Notify cycle completion
        onCycleComplete?.(cycleCount + 1);
      }
    }, 100); // Update progress every 100ms

    return () => {
      clearInterval(updateInterval);
    };
  }, [
    isRunning,
    customCycleLength,
    currentPopulation,
    calculateNextGrowthAmount,
    onPopulationChange,
    onCycleComplete,
    cycleCount,
    colonyId,
    emitPopulationUpdate,
  ]);

  // Handle automation toggle
  const handleAutomationToggle = useCallback(() => {
    const newState = !isRunning;
    setIsRunning(newState);
    onAutoGrowthToggle?.(newState);
  }, [isRunning, onAutoGrowthToggle]);

  // Handle cycle length change
  const handleCycleLengthChange = useCallback(
    (newLength: number) => {
      setCustomCycleLength(newLength);
      onSettingsChange?.({ cycleLength: newLength });
    },
    [onSettingsChange]
  );

  // Format time in minutes and seconds
  const formatTime = (ms: number) => {
    const totalSeconds = Math.floor(ms / 1000);
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;

    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  // Format timestamp to readable date/time
  const formatTimestamp = (timestamp: number) => {
    const date = new Date(timestamp);
    return date.toLocaleString();
  };

  // Get color for population percentage
  const getPopulationColor = () => {
    const percentage = (currentPopulation / maxPopulation) * 100;

    if (percentage >= 90) {
      return 'text-red-400';
    } else if (percentage >= 75) {
      return 'text-amber-400';
    } else if (percentage >= 50) {
      return 'text-green-400';
    } else if (percentage >= 25) {
      return 'text-blue-400';
    }

    return 'text-gray-400';
  };

  // Get icon for event type
  const getEventIcon = (type: PopulationEvent['type']) => {
    switch (type) {
      case 'growth':
        return <TrendingUp className="h-4 w-4 text-green-400" />;
      case 'decline':
        return <TrendingUp className="h-4 w-4 rotate-180 transform text-red-400" />;
      case 'migration':
        return <Users className="h-4 w-4 text-blue-400" />;
      case 'disaster':
        return <AlertTriangle className="h-4 w-4 text-amber-400" />;
      case 'policy':
        return <CheckCircle className="h-4 w-4 text-purple-400" />;
      default:
        return <Users className="h-4 w-4 text-gray-400" />;
    }
  };

  return (
    <div className="rounded-lg border border-gray-700 bg-gray-800 p-4">
      <div className="mb-4 flex items-center justify-between">
        <h3 className="text-lg font-medium text-white">Population Management</h3>
        <div className="flex items-center space-x-2">
          <button
            className="rounded-md border border-gray-700 bg-gray-900 p-1 text-gray-400 hover:bg-gray-700 hover:text-white"
            onClick={() => setShowSettings(!showSettings)}
            title="Settings"
          >
            <Settings className="h-4 w-4" />
          </button>
        </div>
      </div>

      {/* Population Stats */}
      <div className="mb-4 grid grid-cols-2 gap-4">
        <div className="rounded-md border border-gray-700 bg-gray-900 p-3">
          <div className="mb-1 text-xs text-gray-400">Current Population</div>
          <div className="flex items-end justify-between">
            <div className="flex items-center">
              <Users className="mr-2 h-5 w-5 text-blue-400" />
              <span className={`text-xl font-bold ${getPopulationColor()}`}>
                {currentPopulation.toLocaleString()}
              </span>
            </div>
            <div className="text-xs text-gray-500">
              {((currentPopulation / maxPopulation) * 100).toFixed(1)}% of capacity
            </div>
          </div>
          <div className="mt-2 h-2 w-full overflow-hidden rounded-full bg-gray-800">
            <motion.div
              className="h-full bg-blue-500"
              style={{ width: `${(currentPopulation / maxPopulation) * 100}%` }}
              initial={{ width: 0 }}
              animate={{ width: `${(currentPopulation / maxPopulation) * 100}%` }}
              transition={{ duration: 1 }}
            />
          </div>
        </div>

        <div className="rounded-md border border-gray-700 bg-gray-900 p-3">
          <div className="mb-1 text-xs text-gray-400">Growth Rate</div>
          <div className="flex items-end justify-between">
            <div className="flex items-center">
              <TrendingUp className="mr-2 h-5 w-5 text-green-400" />
              <span className="text-xl font-bold text-green-400">
                {(growthRate * 100).toFixed(2)}%
              </span>
            </div>
            <div className="text-xs text-gray-500">per cycle ({formatTime(cycleLength)})</div>
          </div>
          <div className="mt-2 flex items-center justify-between">
            <span className="text-xs text-gray-400">Next growth:</span>
            <span className="text-xs font-medium text-green-400">
              +{nextGrowthAmount.toLocaleString()}
            </span>
          </div>
        </div>
      </div>

      {/* Growth Cycle Progress */}
      <div className="mb-4 rounded-md border border-gray-700 bg-gray-900 p-3">
        <div className="mb-2 flex items-center justify-between">
          <div className="flex items-center">
            <Clock className="mr-2 h-4 w-4 text-blue-400" />
            <span className="text-sm font-medium text-white">Growth Cycle</span>
          </div>
          <div className="text-xs text-gray-400">
            {isRunning ? 'Running' : 'Paused'} â€¢ Cycle #{cycleCount}
          </div>
        </div>

        <div className="mb-2 h-2 w-full overflow-hidden rounded-full bg-gray-800">
          <motion.div
            className="h-full bg-blue-500"
            style={{ width: `${cycleProgress}%` }}
            animate={{ width: `${cycleProgress}%` }}
            transition={{ duration: 0.5 }}
          />
        </div>

        <div className="flex items-center justify-between">
          <div className="flex space-x-2">
            <button
              className={`flex items-center space-x-1 rounded-md px-2 py-1 text-xs ${
                isRunning
                  ? 'bg-red-900/30 text-red-400 hover:bg-red-800/50'
                  : 'bg-green-900/30 text-green-400 hover:bg-green-800/50'
              }`}
              onClick={handleAutomationToggle}
            >
              {isRunning ? (
                <>
                  <Pause className="h-3 w-3" />
                  <span>Pause</span>
                </>
              ) : (
                <>
                  <Play className="h-3 w-3" />
                  <span>Start</span>
                </>
              )}
            </button>

            <button
              className="flex items-center space-x-1 rounded-md bg-gray-800 px-2 py-1 text-xs text-gray-400 hover:bg-gray-700"
              onClick={() => {
                setCycleCount(0);
                onCycleComplete?.(0);
              }}
            >
              <RotateCcw className="h-3 w-3" />
              <span>Reset</span>
            </button>
          </div>

          <div className="text-xs text-gray-400">
            {formatTime(cycleLength * (1 - cycleProgress / 100))} remaining
          </div>
        </div>
      </div>

      {/* Settings Panel */}
      {showSettings && (
        <motion.div
          className="mb-4 rounded-md border border-gray-700 bg-gray-900 p-3"
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: 'auto' }}
          exit={{ opacity: 0, height: 0 }}
        >
          <div className="mb-3 text-sm font-medium text-white">Growth Settings</div>

          <div className="mb-3">
            <label className="mb-1 block text-xs text-gray-400">Cycle Length (milliseconds)</label>
            <div className="flex items-center space-x-2">
              <input
                type="number"
                min="1000"
                step="1000"
                value={customCycleLength}
                onChange={e => handleCycleLengthChange(Number(e.target.value))}
                className="w-full rounded-md border border-gray-700 bg-gray-800 px-3 py-1 text-sm text-white"
              />
              <button
                className="rounded-md bg-blue-900/30 px-2 py-1 text-xs text-blue-400 hover:bg-blue-800/50"
                onClick={() => handleCycleLengthChange(customCycleLength)}
              >
                Apply
              </button>
            </div>
            <div className="mt-1 text-xs text-gray-500">
              {formatTime(customCycleLength)} per cycle
            </div>
          </div>
        </motion.div>
      )}

      {/* Population Events */}
      <div className="overflow-hidden rounded-md border border-gray-700 bg-gray-900">
        <div
          className="flex cursor-pointer items-center justify-between p-3"
          onClick={() => setShowEvents(!showEvents)}
        >
          <div className="flex items-center space-x-2">
            <Users className="h-4 w-4 text-blue-400" />
            <span className="text-sm font-medium text-white">Population Events</span>
          </div>
          {showEvents ? (
            <ChevronUp className="h-4 w-4 text-gray-400" />
          ) : (
            <ChevronDown className="h-4 w-4 text-gray-400" />
          )}
        </div>

        {showEvents && (
          <div className="border-t border-gray-700">
            {events.length === 0 ? (
              <div className="p-3 text-center text-sm text-gray-500">
                No population events recorded
              </div>
            ) : (
              <div className="max-h-48 overflow-y-auto">
                {events.map(event => (
                  <div key={event.id} className="border-b border-gray-700 p-3 last:border-b-0">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-2">
                        {getEventIcon(event.type)}
                        <span className="text-sm font-medium capitalize text-white">
                          {event.type}
                        </span>
                      </div>
                      <span
                        className={`text-sm font-medium ${
                          event.amount > 0 ? 'text-green-400' : 'text-red-400'
                        }`}
                      >
                        {event.amount > 0 ? '+' : ''}
                        {event.amount.toLocaleString()}
                      </span>
                    </div>
                    <div className="mt-1 text-xs text-gray-400">{event.reason}</div>
                    <div className="mt-1 text-xs text-gray-500">
                      {formatTimestamp(event.timestamp)}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

import { AnimatePresence, motion } from 'framer-motion';
import { AlertTriangle, Droplet, Leaf, TrendingUp, Users, Zap } from 'lucide-react';
import { useCallback, useEffect, useState } from 'react';
import { useModuleEvents } from '../../../hooks/events/useModuleEvents';
import { moduleEventBus } from '../../../lib/events/ModuleEventBus';
import { EventType } from '../../../types/events/EventTypes';
import { StandardizedEvent } from '../../../types/events/StandardizedEvents';
import { ResourceType } from '../../../types/resources/ResourceTypes';

interface GrowthModifier {
  id: string;
  name: string;
  description: string;
  effect: number; // Percentage modifier (e.g., 1.1 = +10%)
  type: ResourceType.FOOD | 'housing' | 'healthcare' | 'environment' | ResourceType.ENERGY;
  active: boolean;
}

interface PopulationGrowthModuleProps {
  colonyId: string;
  currentPopulation: number;
  maxPopulation: number;
  baseGrowthRate: number; // Base growth rate per cycle (e.g., 0.05 = 5%)
  growthModifiers: GrowthModifier[];
  cycleLength: number; // Length of a growth cycle in milliseconds
  quality: 'low' | 'medium' | 'high';
  onPopulationChange?: (newPopulation: number) => void;
  onModifierToggle?: (modifierId: string, active: boolean) => void;
}

/**
 * PopulationGrowthModule component
 *
 * Displays and manages population growth mechanics for a colony.
 * Shows growth rate, modifiers, and provides controls for adjusting growth parameters.
 */
export function PopulationGrowthModule({
  colonyId,
  currentPopulation,
  maxPopulation,
  baseGrowthRate,
  growthModifiers,
  cycleLength,
  quality: _quality,
  onPopulationChange,
  onModifierToggle,
}: PopulationGrowthModuleProps) {
  const [population, setPopulation] = useState(currentPopulation);
  const [isGrowing, setIsGrowing] = useState(false);
  const [growthHistory, setGrowthHistory] = useState<number[]>([]);
  const [showModifiers, setShowModifiers] = useState(false);
  const [autoGrowth, setAutoGrowth] = useState(false);
  const [growthInterval, setGrowthInterval] = useState<NodeJS.Timeout | null>(null);

  const { subscribe } = useModuleEvents();

  // Calculate effective growth rate with all active modifiers
  const effectiveGrowthRate = useCallback(() => {
    const activeModifiers = growthModifiers.filter(m => m.active);
    if (activeModifiers.length === 0) {
      return baseGrowthRate;
    }

    const totalEffect = activeModifiers.reduce((total, modifier) => {
      return total * modifier.effect;
    }, 1);

    return baseGrowthRate * totalEffect;
  }, [baseGrowthRate, growthModifiers]);

  // Format growth rate as percentage
  const formattedGrowthRate = `${(effectiveGrowthRate() * 100).toFixed(2)}%`;

  // Population percentage of maximum
  const populationPercentage = Math.min(100, Math.round((population / maxPopulation) * 100));

  // Population status
  const getPopulationStatus = useCallback(() => {
    if (populationPercentage < 30) {
      return 'low';
    }
    if (populationPercentage > 90) {
      return 'critical';
    }
    return 'normal';
  }, [populationPercentage]);

  const populationStatus = getPopulationStatus();

  // Handle manual population growth
  const handleGrowthCycle = useCallback(() => {
    if (population >= maxPopulation) {
      setIsGrowing(false);
      return;
    }

    setIsGrowing(true);

    // Calculate growth amount
    const growthAmount = Math.floor(population * effectiveGrowthRate());
    const newPopulation = Math.min(maxPopulation, population + growthAmount);

    // Update population
    setPopulation(newPopulation);
    onPopulationChange?.(newPopulation);

    // Update growth history
    setGrowthHistory(prev => [...prev.slice(-9), growthAmount]);

    // Emit population update event
    const event: StandardizedEvent = {
      type: EventType.MODULE_UPDATED,
      moduleId: colonyId,
      moduleType: ResourceType.POPULATION,
      timestamp: Date.now(),
      data: {
        stats: {
          [ResourceType.POPULATION]: newPopulation,
        },
      },
    };
    moduleEventBus.emit(event);

    setTimeout(() => {
      setIsGrowing(false);
    }, 1000);
  }, [population, maxPopulation, effectiveGrowthRate, colonyId, onPopulationChange]);

  // Handle modifier toggle
  const handleModifierToggle = useCallback(
    (modifierId: string) => {
      const modifier = growthModifiers.find(m => m.id === modifierId);
      if (modifier) {
        onModifierToggle?.(modifierId, !modifier.active);
      }
    },
    [growthModifiers, onModifierToggle]
  );

  // Handle auto growth toggle
  useEffect(() => {
    if (autoGrowth) {
      const interval = setInterval(() => {
        handleGrowthCycle();
      }, cycleLength);

      setGrowthInterval(interval);

      return () => {
        clearInterval(interval);
        setGrowthInterval(null);
      };
    } else if (growthInterval) {
      clearInterval(growthInterval);
      setGrowthInterval(null);
    }
  }, [autoGrowth, cycleLength, handleGrowthCycle]);

  // Update local population when prop changes
  useEffect(() => {
    setPopulation(currentPopulation);
  }, [currentPopulation]);

  // Get icon for modifier type
  const getModifierIcon = useCallback((type: GrowthModifier['type']) => {
    switch (type) {
      case ResourceType.FOOD:
        return <Droplet className="h-4 w-4 text-blue-400" />;
      case 'housing':
        return <Users className="h-4 w-4 text-indigo-400" />;
      case 'healthcare':
        return <AlertTriangle className="h-4 w-4 text-red-400" />;
      case 'environment':
        return <Leaf className="h-4 w-4 text-green-400" />;
      case ResourceType.ENERGY:
        return <Zap className="h-4 w-4 text-yellow-400" />;
    }
  }, []);

  return (
    <div className="rounded-lg border border-gray-700 bg-gray-800 p-4">
      <div className="mb-4 flex items-center justify-between">
        <h3 className="text-lg font-medium text-white">Population Growth</h3>
        <div className="flex items-center space-x-2">
          <span className="text-sm text-gray-400">Auto Growth</span>
          <button
            className={`h-6 w-12 rounded-full ${
              autoGrowth ? 'bg-green-600' : 'bg-gray-600'
            } relative transition-colors`}
            onClick={() => setAutoGrowth(!autoGrowth)}
          >
            <span
              className={`absolute top-1 h-4 w-4 rounded-full bg-white transition-all ${
                autoGrowth ? 'left-7' : 'left-1'
              }`}
            />
          </button>
        </div>
      </div>

      {/* Population Display */}
      <div className="mb-6">
        <div className="mb-1 flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Users className="h-5 w-5 text-blue-400" />
            <span className="text-sm font-medium text-gray-300">Current Population</span>
          </div>
          <span className="text-sm font-medium text-white">{population.toLocaleString()}</span>
        </div>

        <div className="mb-1 flex justify-between text-xs">
          <span className="text-gray-400">Capacity</span>
          <span
            className={`${
              populationStatus === 'critical'
                ? 'text-red-400'
                : populationStatus === 'low'
                  ? 'text-yellow-400'
                  : 'text-gray-300'
            }`}
          >
            {populationPercentage}%
          </span>
        </div>

        <div className="h-2 overflow-hidden rounded-full bg-gray-700">
          <motion.div
            className={`h-full rounded-full ${
              populationStatus === 'critical'
                ? 'bg-red-500'
                : populationStatus === 'low'
                  ? 'bg-yellow-500'
                  : 'bg-blue-500'
            }`}
            initial={{ width: 0 }}
            animate={{ width: `${populationPercentage}%` }}
            transition={{ duration: 0.5 }}
          />
        </div>

        <div className="mt-1 flex justify-between text-xs text-gray-500">
          <span>0</span>
          <span>{maxPopulation.toLocaleString()}</span>
        </div>
      </div>

      {/* Growth Rate */}
      <div className="mb-4">
        <div className="mb-1 flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <TrendingUp className="h-5 w-5 text-green-400" />
            <span className="text-sm font-medium text-gray-300">Growth Rate</span>
          </div>
          <div className="flex items-center space-x-1">
            <span className="text-sm font-medium text-green-400">{formattedGrowthRate}</span>
            <span className="text-xs text-gray-500">per cycle</span>
          </div>
        </div>

        {/* Growth History Chart */}
        <div className="mt-2 h-12 w-full">
          <div className="flex h-full items-end justify-between">
            {growthHistory.length === 0 ? (
              <div className="flex h-full w-full items-center justify-center">
                <span className="text-xs text-gray-500">No growth data yet</span>
              </div>
            ) : (
              growthHistory.map((amount, index) => {
                const percentage = Math.min(100, (amount / (maxPopulation * 0.1)) * 100);
                return (
                  <motion.div
                    key={index}
                    className="w-[8%] bg-green-500"
                    initial={{ height: 0 }}
                    animate={{ height: `${percentage}%` }}
                    transition={{ duration: 0.3, delay: index * 0.05 }}
                  />
                );
              })
            )}
          </div>
        </div>
      </div>

      {/* Growth Controls */}
      <div className="mb-4">
        <button
          className={`w-full rounded-md py-2 text-sm font-medium transition-colors ${
            isGrowing || population >= maxPopulation
              ? 'cursor-not-allowed bg-gray-700 text-gray-500'
              : 'bg-blue-600 text-white hover:bg-blue-700'
          }`}
          onClick={handleGrowthCycle}
          disabled={isGrowing || population >= maxPopulation}
        >
          {isGrowing
            ? 'Growing...'
            : population >= maxPopulation
              ? 'Maximum Population Reached'
              : 'Trigger Growth Cycle'}
        </button>
      </div>

      {/* Growth Modifiers */}
      <div>
        <button
          className="mb-2 flex w-full items-center justify-between rounded-md bg-gray-700 px-3 py-2 text-sm font-medium text-gray-300 hover:bg-gray-600"
          onClick={() => setShowModifiers(!showModifiers)}
        >
          <span>Growth Modifiers</span>
          <span className="text-xs text-gray-400">
            {growthModifiers.filter(m => m.active).length} Active
          </span>
        </button>

        <AnimatePresence>
          {showModifiers && (
            <motion.div
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: 'auto', opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.3 }}
              className="overflow-hidden"
            >
              <div className="space-y-2 rounded-md bg-gray-900 p-3">
                {growthModifiers.map(modifier => (
                  <div
                    key={modifier.id}
                    className="flex items-center justify-between rounded-md bg-gray-800 p-2"
                  >
                    <div className="flex items-center space-x-2">
                      {getModifierIcon(modifier.type)}
                      <div>
                        <div className="text-sm font-medium text-gray-300">{modifier.name}</div>
                        <div className="text-xs text-gray-500">{modifier.description}</div>
                      </div>
                    </div>
                    <div className="flex items-center space-x-3">
                      <span
                        className={`text-sm ${
                          modifier.effect > 1 ? 'text-green-400' : 'text-red-400'
                        }`}
                      >
                        {modifier.effect > 1 ? '+' : ''}
                        {((modifier.effect - 1) * 100).toFixed(0)}%
                      </span>
                      <button
                        className={`h-5 w-10 rounded-full ${
                          modifier.active ? 'bg-green-600' : 'bg-gray-600'
                        } relative transition-colors`}
                        onClick={() => handleModifierToggle(modifier.id)}
                      >
                        <span
                          className={`absolute top-0.5 h-4 w-4 rounded-full bg-white transition-all ${
                            modifier.active ? 'left-5' : 'left-1'
                          }`}
                        />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
}

import * as React from "react";
import { useCallback, useEffect, useState } from 'react';
import { Pause, Play, SkipBack, SkipForward, X } from 'lucide-react';
import { useGameState } from '../../../../contexts/GameContext';
import { GameEvent, GameEventType, Position } from '../../../../types/core/GameTypes';

interface MissionReplayProps {
  missionId: string;
  onClose: () => void;
}

interface EventPosition {
  x: number;
  y: number;
}

interface Mission {
  id: string;
  timestamp: number;
  description: string;
}

interface GameState {
  missions: {
    history: Mission[];
  };
  events: GameEvent[];
}

const selectGameState = (state: unknown): GameState => {
  if (
    typeof state === 'object' &&
    state !== null &&
    'missions' in state &&
    'events' in state &&
    typeof state.missions === 'object' &&
    state.missions !== null &&
    'history' in state.missions &&
    Array.isArray(state.missions.history) &&
    Array.isArray(state.events)
  ) {
    return state as GameState;
  }
  return {
    missions: {
      history: [],
    },
    events: [],
  };
};

export function MissionReplay({ missionId, onClose }: MissionReplayProps) {
  const gameState = useGameState(selectGameState);

  // Ensure state is available
  if (!gameState) {
    return null;
  }

  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [playbackSpeed, setPlaybackSpeed] = useState(1);
  const [currentEventIndex, setCurrentEventIndex] = useState<number | null>(null);

  // Find the mission and related events
  const mission = gameState.missions.history.find(m => m.id === missionId);
  const events = gameState.events.filter(
    e =>
      e.timestamp >= (mission?.timestamp || 0) && e.timestamp <= (mission?.timestamp || 0) + 3600000 // 1 hour window
  );

  // Calculate total duration
  const duration =
    events.length > 0 ? events[events.length - 1].timestamp - events[0].timestamp : 0;

  // Handle playback controls
  const togglePlayback = useCallback(() => {
    setIsPlaying(!isPlaying);
  }, [isPlaying]);

  const handleSeek = useCallback(
    (time: number) => {
      setCurrentTime(Math.max(0, Math.min(time, duration)));
    },
    [duration]
  );

  const handleSpeedChange = useCallback((speed: number) => {
    setPlaybackSpeed(speed);
  }, []);

  // Update time during playback
  useEffect(() => {
    if (!isPlaying) {
      return;
    }

    const interval = setInterval(() => {
      setCurrentTime(time => {
        const newTime = time + 100 * playbackSpeed;
        if (newTime >= duration) {
          setIsPlaying(false);
          return duration;
        }
        return newTime;
      });
    }, 100);

    return () => clearInterval(interval);
  }, [isPlaying, playbackSpeed, duration]);

  if (!mission) {
    return null;
  }

  // Get events up to current time
  const currentEvents = events.filter(
    (e: GameEvent) => e.timestamp <= events[0].timestamp + currentTime
  );

  // Render map with ship paths and events
  const renderEvents = (events: GameEvent[]) => {
    return events.map((event: GameEvent, index: number) => {
      // Calculate position based on event data or use random positioning as fallback
      const eventPosition: EventPosition = {
        x:
          typeof event.data === 'object' &&
          event.data !== null &&
          'position' in event.data &&
          typeof event.data.position === 'object' &&
          event.data.position !== null &&
          'x' in event.data.position
            ? (event.data.position as Position).x
            : Math.random() * 100,
        y:
          typeof event.data === 'object' &&
          event.data !== null &&
          'position' in event.data &&
          typeof event.data.position === 'object' &&
          event.data.position !== null &&
          'y' in event.data.position
            ? (event.data.position as Position).y
            : Math.random() * 100,
      };

      // Determine event marker appearance based on event type
      const getEventColor = (type: GameEventType) => {
        switch (type) {
          case 'exploration':
            return 'bg-teal-400';
          case 'combat':
            return 'bg-red-400';
          case 'trade':
            return 'bg-amber-400';
          case 'diplomacy':
            return 'bg-purple-400';
          default:
            return 'bg-teal-400';
        }
      };

      // Check if this is the current event being viewed
      const isCurrentEvent = index === currentEventIndex;

      return (
        <div
          key={`event-${index}`}
          className="absolute h-2 w-2 rounded-full"
          style={{
            left: `${eventPosition.x}%`,
            top: `${eventPosition.y}%`,
            backgroundColor: getEventColor(event.type),
          }}
          onClick={() => {
            // Set current event index to this event when clicked
            setCurrentEventIndex(index);
          }}
        />
      );
    });
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50">
      <div className="mx-4 w-full max-w-4xl rounded-lg border border-gray-700 bg-gray-900 p-6">
        <div className="mb-6 flex items-center justify-between">
          <h2 className="text-xl font-bold text-white">Mission Replay</h2>
          <button onClick={onClose} className="rounded-lg p-2 transition-colors hover:bg-gray-800">
            <X className="h-5 w-5 text-gray-400" />
          </button>
        </div>

        {/* Mission Details */}
        <div className="mb-6">
          <div className="mb-2 text-lg text-white">{mission.description}</div>
          <div className="text-sm text-gray-400">
            {new Date(mission.timestamp).toLocaleString()}
          </div>
        </div>

        {/* Replay Visualization */}
        <div className="relative mb-6 h-96 overflow-hidden rounded-lg bg-gray-800/50">
          {/* Map View */}
          <div className="absolute inset-0">{renderEvents(currentEvents)}</div>

          {/* Event Timeline */}
          <div className="absolute bottom-0 left-0 right-0 h-24 bg-gradient-to-t from-gray-900/90 to-transparent p-4">
            <div className="mb-4 flex items-center space-x-4">
              {currentEvents.map((event: GameEvent, index: number) => (
                <div
                  key={index}
                  className={`h-2 w-2 rounded-full ${
                    index === currentEventIndex ? 'bg-white' : 'bg-teal-400'
                  }`}
                  style={{
                    left: `${((event.timestamp - events[0].timestamp) / duration) * 100}%`,
                  }}
                  onClick={() => setCurrentEventIndex(index)}
                />
              ))}
            </div>

            {/* Current Event Details */}
            {currentEventIndex !== null && currentEvents[currentEventIndex] && (
              <div className="mb-2 text-sm text-white">
                <span className="font-bold">{currentEvents[currentEventIndex].type}</span> event at{' '}
                {new Date(currentEvents[currentEventIndex].timestamp).toLocaleTimeString()}
              </div>
            )}
          </div>
        </div>

        {/* Playback Controls */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => handleSeek(0)}
              className="rounded-lg p-2 transition-colors hover:bg-gray-800"
            >
              <SkipBack className="h-5 w-5 text-teal-400" />
            </button>
            <button
              onClick={togglePlayback}
              className="rounded-lg p-2 transition-colors hover:bg-gray-800"
            >
              {isPlaying ? (
                <Pause className="h-5 w-5 text-teal-400" />
              ) : (
                <Play className="h-5 w-5 text-teal-400" />
              )}
            </button>
            <button
              onClick={() => handleSeek(duration)}
              className="rounded-lg p-2 transition-colors hover:bg-gray-800"
            >
              <SkipForward className="h-5 w-5 text-teal-400" />
            </button>
          </div>

          {/* Playback Speed */}
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-400">Speed:</span>
            {[0.5, 1, 2, 4].map(speed => (
              <button
                key={speed}
                onClick={() => handleSpeedChange(speed)}
                className={`rounded px-2 py-1 text-sm ${
                  playbackSpeed === speed
                    ? 'bg-teal-600 text-white'
                    : 'bg-gray-800 text-gray-400 hover:bg-gray-700'
                }`}
              >
                {speed}x
              </button>
            ))}
          </div>

          {/* Progress */}
          <div className="text-sm text-gray-400">
            {Math.floor(currentTime / 1000)}s / {Math.floor(duration / 1000)}s
          </div>
        </div>
      </div>
    </div>
  );
}

import { ResourceType } from "./../../../../types/resources/ResourceTypes";
/** @jsx React.createElement */
/** @jsxFrag React.Fragment */
import { AlertTriangle } from 'lucide-react';
import * as React from "react";
import { ShipBlueprint, getAvailableShips } from '../../../../config/ShipBlueprints';
import { techTreeManager } from '../../../../managers/game/techTreeManager';
import { ShipHangarManager } from '../../../../managers/module/ShipHangarManager';
import { ResourceCost } from '../../../../types/resources/ResourceTypes';
import { PlayerShipCategory, PlayerShipClass } from '../../../../types/ships/PlayerShipTypes';

interface ShipBuildingInterfaceProps {
  manager: ShipHangarManager;
  onStartBuild: (shipClass: PlayerShipClass) => void;
}

export function ShipBuildingInterface({ manager, onStartBuild }: ShipBuildingInterfaceProps) {
  const [selectedCategory, setSelectedCategory] = React.useState<PlayerShipCategory | 'all'>('all');
  const [selectedShip, setSelectedShip] = React.useState<ShipBlueprint | null>(null);
  const [availableShips, setAvailableShips] = React.useState<ShipBlueprint[]>([]);
  const [buildableShips, setBuildableShips] = React.useState<Set<PlayerShipClass>>(new Set());
  const [errors, setErrors] = React.useState<Map<PlayerShipClass, string[]>>(new Map());
  const [resourceRequirements, setResourceRequirements] = React.useState<ResourceCost[]>([]);

  // Load available ships based on tech level and other requirements
  React.useEffect(() => {
    const currentTier = manager.getCurrentTier();
    const ships = getAvailableShips(currentTier);
    setAvailableShips(ships);

    // Check which ships are buildable
    const buildable = new Set<PlayerShipClass>();
    const newErrors = new Map<PlayerShipClass, string[]>();

    ships.forEach(ship => {
      const errors: string[] = [];

      // Check tech requirements
      if (ship.requirements.prerequisites?.technology) {
        const missingTech = ship.requirements.prerequisites.technology.filter(
          tech => !techTreeManager.getNode(tech)?.unlocked
        );
        if (missingTech.length > 0) {
          errors.push(`Missing technologies: ${missingTech.join(', ')}`);
        }
      }

      // Check resource requirements
      ship.requirements.resourceCost.forEach(cost => {
        const requirements = manager.getBuildRequirements(ship.shipClass);
        const available =
          requirements.resourceCost.find((r: { type: string }) => r.type === cost.type)?.amount ||
          0;
        if (available < cost.amount) {
          errors.push(`Insufficient ${cost.type}: ${available}/${cost.amount}`);
        }
      });

      // Check officer requirements
      if (ship.requirements.prerequisites?.officers) {
        const req = ship.requirements.prerequisites.officers;
        const hasQualifiedOfficer = manager.hasOfficerMeetingRequirements(
          req.minLevel,
          req.specialization
        );
        if (!hasQualifiedOfficer) {
          errors.push(`Requires level ${req.minLevel} ${req.specialization} officer`);
        }
      }

      if (errors.length === 0) {
        buildable.add(ship.shipClass);
      } else {
        newErrors.set(ship.shipClass, errors);
      }
    });

    setBuildableShips(buildable);
    setErrors(newErrors);
  }, [manager]);

  // Update resource requirements when ship is selected
  React.useEffect(() => {
    if (selectedShip) {
      const requirements = manager.getBuildRequirements(selectedShip.shipClass);
      setResourceRequirements(requirements.resourceCost);
    } else {
      setResourceRequirements([]);
    }
  }, [selectedShip, manager]);

  const handleStartBuild = () => {
    if (!selectedShip || !buildableShips.has(selectedShip.shipClass)) {
      return;
    }
    onStartBuild(selectedShip.shipClass);
    setSelectedShip(null);
  };

  const filteredShips = availableShips.filter(
    ship => selectedCategory === 'all' || ship.category === selectedCategory
  );

  return React.createElement(
    'div',
    { className: 'flex h-full flex-col' },
    // Category Selection
    React.createElement(
      'div',
