import { ReactNode, useCallback, useEffect, useRef, useState } from 'react';
import { GameActionType, useGameDispatch } from '../../contexts/GameContext';
import { ModuleActionType, useModuleContext } from '../../contexts/ModuleContext';
import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import { GameLoopManager, UpdatePriority } from '../../managers/game/GameLoopManager';
import { ResourceManager } from '../../managers/game/ResourceManager';
import { moduleManager } from '../../managers/module/ModuleManager';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import { BaseEvent, EventType } from '../../types/events/EventTypes';
import { Module } from '../../types/modules/ModuleTypes';
import { ResourceType } from "./../../types/resources/ResourceTypes";

interface SystemIntegrationProps {
  children: ReactNode;
  resourceManager: ResourceManager;
  gameLoopManager?: GameLoopManager;
  updateInterval?: number;
}

// Types of resource events to listen for
const RESOURCE_EVENT_TYPES = [
  EventType.RESOURCE_PRODUCED,
  EventType.RESOURCE_CONSUMED,
  EventType.RESOURCE_TRANSFERRED,
  EventType.RESOURCE_SHORTAGE,
  EventType.RESOURCE_PRODUCTION_REGISTERED,
  EventType.RESOURCE_CONSUMPTION_REGISTERED,
];

// Types of module events to listen for
const MODULE_EVENT_TYPES = [
  'MODULE_CREATED',
  'MODULE_UPDATED',
  'STATUS_CHANGED',
  'MODULE_ACTIVATED',
  'MODULE_DEACTIVATED',
] as const;

type ModuleEventType = (typeof MODULE_EVENT_TYPES)[number];

/**
 * SystemIntegration component
 *
 * This component serves as a bridge between the frontend React contexts and
 * the backend manager classes. It synchronizes state between them and ensures
 * that updates from managers are propagated to the UI.
 */
export function SystemIntegration({
  children,
  resourceManager,
  gameLoopManager,
  updateInterval = 1000,
}: SystemIntegrationProps) {
  const gameDispatch = useGameDispatch();
  const { dispatch: moduleDispatch } = useModuleContext();

  const lastResourceState = useRef<Record<string, number>>({});
  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  const [isInitialized, setIsInitialized] = useState(false);
  const [error, setError] = useState<Error | null>(null);

  // Initialize managers
  useEffect(() => {
    const initializeManagers = async () => {
      try {
        // Initialize the resource manager
        await resourceManager.initialize();

        // Connect resource manager to game loop if provided
        if (gameLoopManager) {
          gameLoopManager.registerUpdate(
            resourceManager.id,
            deltaTime => resourceManager.update(deltaTime),
            UpdatePriority.NORMAL
          );
        }

        setIsInitialized(true);
        console.warn('Managers initialized successfully');
      } catch (err) {
        console.error('Failed to initialize managers:', err);
        setError(err instanceof Error ? err : new Error(String(err)));
      }
    };

    initializeManagers();

    // Cleanup on unmount
    return () => {
      if (gameLoopManager) {
        gameLoopManager.unregisterUpdate(resourceManager.id);
      }
      resourceManager.dispose();
    };
  }, [resourceManager, gameLoopManager]);

  // Sync resource state from ResourceManager to GameContext
  const syncResourceState = useCallback(() => {
    if (!gameDispatch) return;

    // Get current resources from manager
    const currentResources = resourceManager.getAllResources();
    const lastResourceStateValue = lastResourceState.current;

    // Check if resources have changed
    const hasChanges =
      !lastResourceStateValue ||
      Object.entries(currentResources).some(
        ([key, value]) => lastResourceStateValue[key] !== value
      );

    // Only update if there are changes
    if (hasChanges) {
      console.warn('Syncing resource state:', currentResources);

      // Add resource rates to the update if we can calculate them
      let resourceRates = {};

      if (resourceManager.getAllResourceStates) {
        const states = resourceManager.getAllResourceStates();

        // Calculate rates from production and consumption
        resourceRates = {
          mineralRate:
            (states[ResourceType.MINERALS]?.production || 0) -
            (states[ResourceType.MINERALS]?.consumption || 0),
          energyRate:
            (states[ResourceType.ENERGY]?.production || 0) -
            (states[ResourceType.ENERGY]?.consumption || 0),
          populationRate:
            (states[ResourceType.POPULATION]?.production || 0) -
            (states[ResourceType.POPULATION]?.consumption || 0),
          researchRate:
            (states[ResourceType.RESEARCH]?.production || 0) -
            (states[ResourceType.RESEARCH]?.consumption || 0),
        };
      }

      // Update resources with the calculated values
      gameDispatch({
        type: GameActionType.UPDATE_RESOURCES,
        payload: {
          minerals: currentResources[ResourceType.MINERALS] || 0,
          energy: currentResources[ResourceType.ENERGY] || 0,
          population: currentResources[ResourceType.POPULATION] || 0,
          research: currentResources[ResourceType.RESEARCH] || 0,
          ...resourceRates, // Add rates if available
        },
      });

      lastResourceState.current = { ...currentResources };
    }
  }, [gameDispatch, resourceManager]);

  // Sync module state from ModuleManager to ModuleContext
  const syncModuleState = useCallback(() => {
    if (!moduleDispatch) return;

    const moduleBuildings = moduleManager.getBuildings();
    const modules = moduleManager.getActiveModules();

    // Only update if there are modules available
    if (modules.length > 0) {
      moduleDispatch({
        type: ModuleActionType.SET_ACTIVE_MODULES,
        payload: { activeModuleIds: modules.map(m => m.id) },
      });
    }

    // Update buildings if available
    if (moduleBuildings.length > 0) {
      // Register each building individually
      moduleBuildings.forEach(building => {
        // Convert building to Module type
        const moduleData: Module = {
          id: building.id,
          name: building.id, // Use ID as name if not available
          type: 'resource-manager' as ModuleType, // Default to resource-manager type
          status: 'active', // Default to active
          position: { x: 0, y: 0 }, // Default position
          isActive: true, // Default to active
          level: 1, // Default level
        };

        moduleDispatch({
          type: ModuleActionType.ADD_MODULE,
          payload: { module: moduleData },
        });
      });
    }
  }, [moduleDispatch]);

  // Set up event listeners and sync intervals
  useEffect(() => {
    if (!isInitialized || !gameDispatch || !moduleDispatch) return;

    const unsubscribes: Array<() => void> = [];

    // Subscribe to resource events from the resource manager
    RESOURCE_EVENT_TYPES.forEach(eventType => {
      const unsubscribe = resourceManager.subscribeToEvent(eventType, (_event: BaseEvent) => {
        syncResourceState();
      });
      unsubscribes.push(unsubscribe);
    });

    // Listen for module events
    MODULE_EVENT_TYPES.forEach(eventType => {
      const unsubscribe = moduleEventBus.subscribe(eventType, () => {
        syncModuleState();
      });
      unsubscribes.push(unsubscribe);
    });

    // Only set up interval if we don't have a game loop manager
    if (!gameLoopManager) {
      // Set up interval for periodic updates
      intervalRef.current = setInterval(() => {
        syncResourceState();
        syncModuleState();

        // Manually call update on resource manager since we don't have a game loop
        resourceManager.update(updateInterval);
      }, updateInterval);
    }

    // Initial sync
    syncResourceState();
    syncModuleState();

    // Cleanup
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
      unsubscribes.forEach(unsubscribe => unsubscribe());
    };
  }, [
    syncResourceState,
    syncModuleState,
    updateInterval,
    isInitialized,
    resourceManager,
    gameLoopManager,
    gameDispatch,
    moduleDispatch,
  ]);

  // Show loading state while initializing
  if (!isInitialized) {
    return (
      <div className="flex h-20 items-center justify-center rounded bg-gray-100 p-4 shadow-sm">
        <span className="text-gray-700">Initializing game systems...</span>
      </div>
    );
  }

  // Show error state if initialization failed
  if (error) {
    return (
      <div className="flex h-20 items-center justify-center rounded bg-red-100 p-4 shadow-sm">
        <span className="text-red-700">Error initializing systems: {error.message}</span>
      </div>
    );
  }

  // Return children since this component doesn't render anything itself when initialized
  return <>{children}</>;
}

import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  FormControl,
  IconButton,
  InputLabel,
  List,
  ListItem,
  ListItemText,
  MenuItem,
  Select,
  TextField,
  Typography,
} from '@mui/material';
import * as React from 'react';
import { useState } from 'react';
import {
  AnalysisConfig,
  AnalysisType,
  Dataset,
  VisualizationType,
} from '../../types/exploration/DataAnalysisTypes';

interface AnalysisConfigManagerProps {
  configs: AnalysisConfig[];
  datasets: Dataset[];
  selectedConfig: AnalysisConfig | null;
  onSelectConfig: (config: AnalysisConfig) => void;
  onCreateConfig: (config: Omit<AnalysisConfig, 'id' | 'createdAt' | 'updatedAt'>) => string;
  onUpdateConfig: (id: string, updates: Partial<AnalysisConfig>) => void;
  onDeleteConfig: (id: string) => void;
}

const AnalysisConfigManager: React.FC<AnalysisConfigManagerProps> = ({
  configs,
  datasets,
  selectedConfig,
  onSelectConfig,
  onCreateConfig,
  onUpdateConfig,
  onDeleteConfig,
}) => {
  const [openDialog, setOpenDialog] = useState(false);
  const [newConfigName, setNewConfigName] = useState('');
  const [newConfigDescription, setNewConfigDescription] = useState('');
  const [newConfigType, setNewConfigType] = useState<AnalysisType>('trend');
  const [newConfigDatasetId, setNewConfigDatasetId] = useState('');
  const [newVisualizationType, setNewVisualizationType] = useState<VisualizationType>('lineChart');

  const handleCreateConfig = () => {
    if (!newConfigName.trim() || !newConfigDatasetId) return;

    // Generate default parameters based on analysis type
    const parameters = generateDefaultParameters(newConfigType);

    // Generate default visualization config
    const visualizationConfig = generateDefaultVisualizationConfig(newVisualizationType);

    onCreateConfig({
      name: newConfigName,
      description: newConfigDescription,
      type: newConfigType,
      datasetId: newConfigDatasetId,
      parameters,
      visualizationType: newVisualizationType,
      visualizationConfig,
    });

    setNewConfigName('');
    setNewConfigDescription('');
    setNewConfigType('trend');
    setNewConfigDatasetId('');
    setNewVisualizationType('lineChart');
    setOpenDialog(false);
  };

  // Generate default parameters based on analysis type
  const generateDefaultParameters = (type: AnalysisType): Record<string, unknown> => {
    switch (type) {
      case 'trend':
        return {
          xAxis: 'date',
          yAxis: 'value',
          groupBy: 'type',
          timeRange: [Date.now() - 30 * 86400000, Date.now()], // Last 30 days
          aggregation: 'average',
        };
      case 'correlation':
        return {
          variables: ['resourcePotential', 'habitabilityScore', 'anomalyCount'],
          method: 'pearson',
          threshold: 0.5,
        };
      case 'distribution':
        return {
          variable: 'resourcePotential',
          bins: 10,
          normalize: true,
        };
      case 'clustering':
        return {
          variables: ['resourcePotential', 'habitabilityScore'],
          clusters: 3,
          method: 'kmeans',
        };
      case 'prediction':
        return {
          target: 'resourcePotential',
          features: ['habitabilityScore', 'anomalyCount'],
          method: 'linear',
          testSize: 0.2,
        };
      default:
        return {};
    }
  };

  // Generate default visualization config based on visualization type
  const generateDefaultVisualizationConfig = (type: VisualizationType): Record<string, unknown> => {
    switch (type) {
      case 'lineChart':
        return {
          xAxisLabel: 'Date',
          yAxisLabel: 'Value',
          showLegend: true,
          showGrid: true,
          colors: ['#4299E1', '#48BB78', '#F6AD55', '#F56565'],
        };
      case 'barChart':
        return {
          xAxisLabel: 'Category',
          yAxisLabel: 'Value',
          showLegend: true,
          showGrid: true,
          colors: ['#4299E1', '#48BB78', '#F6AD55', '#F56565'],
          barSize: 20,
        };
      case 'scatterPlot':
        return {
          xAxisLabel: 'X',
          yAxisLabel: 'Y',
          showLegend: true,
          showGrid: true,
          colors: ['#4299E1', '#48BB78', '#F6AD55', '#F56565'],
          pointSize: 5,
        };
      case 'pieChart':
        return {
          showLegend: true,
          colors: ['#4299E1', '#48BB78', '#F6AD55', '#F56565', '#9F7AEA', '#ED64A6'],
          innerRadius: 0,
          outerRadius: 80,
        };
      default:
        return {};
    }
  };

  return (
    <div>
      <Button
        variant="outlined"
        startIcon={<AddIcon />}
        onClick={() => setOpenDialog(true)}
        sx={{ mb: 2 }}
      >
        Create Analysis Config
      </Button>

      {configs.length === 0 ? (
        <Typography variant="body2" color="text.secondary" align="center">
          No analysis configurations available. Create one to get started.
        </Typography>
      ) : (
        <List sx={{ maxHeight: 400, overflow: 'auto' }}>
          {configs.map(config => (
            <ListItem
              key={config.id}
              button
              selected={selectedConfig?.id === config.id}
              onClick={() => onSelectConfig(config)}
              secondaryAction={
                <IconButton
                  edge="end"
                  aria-label="delete"
                  onClick={(e: React.MouseEvent) => {
                    e.stopPropagation();
                    onDeleteConfig(config.id);
                  }}
                >
                  <DeleteIcon />
                </IconButton>
              }
            >
              <ListItemText
                primary={config.name}
                secondary={
                  <>
                    <Typography component="span" variant="body2" color="text.primary">
                      {config.type}
                    </Typography>
                    {` â€” ${datasets.find(d => d.id === config.datasetId)?.name || 'Unknown dataset'}`}
                  </>
                }
              />
            </ListItem>
          ))}
        </List>
      )}

      {/* Create Config Dialog */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>Create New Analysis Configuration</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Configuration Name"
            fullWidth
            value={newConfigName}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setNewConfigName(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Description"
            fullWidth
            value={newConfigDescription}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
              setNewConfigDescription(e.target.value)
            }
          />
          <FormControl fullWidth margin="dense">
            <InputLabel>Dataset</InputLabel>
            <Select
              value={newConfigDatasetId}
              label="Dataset"
              onChange={e => setNewConfigDatasetId(e.target.value as string)}
            >
              <MenuItem value="">
                <em>Select a dataset</em>
              </MenuItem>
              {datasets.map(dataset => (
                <MenuItem key={dataset.id} value={dataset.id}>
                  {dataset.name} ({dataset.source}, {dataset.dataPoints.length} points)
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <FormControl fullWidth margin="dense">
            <InputLabel>Analysis Type</InputLabel>
            <Select
              value={newConfigType}
              label="Analysis Type"
              onChange={e => setNewConfigType(e.target.value as AnalysisType)}
            >
              <MenuItem value="trend">Trend Analysis</MenuItem>
              <MenuItem value="correlation">Correlation Analysis</MenuItem>
              <MenuItem value="distribution">Distribution Analysis</MenuItem>
              <MenuItem value="clustering">Clustering Analysis</MenuItem>
              <MenuItem value="prediction">Prediction Analysis</MenuItem>
              <MenuItem value="comparison">Comparison Analysis</MenuItem>
              <MenuItem value="anomalyDetection">Anomaly Detection</MenuItem>
              <MenuItem value="resourceMapping">Resource Mapping</MenuItem>
              <MenuItem value="sectorAnalysis">Sector Analysis</MenuItem>
            </Select>
          </FormControl>
          <FormControl fullWidth margin="dense">
            <InputLabel>Visualization Type</InputLabel>
            <Select
              value={newVisualizationType}
              label="Visualization Type"
              onChange={e => setNewVisualizationType(e.target.value as VisualizationType)}
            >
              <MenuItem value="lineChart">Line Chart</MenuItem>
              <MenuItem value="barChart">Bar Chart</MenuItem>
              <MenuItem value="scatterPlot">Scatter Plot</MenuItem>
              <MenuItem value="pieChart">Pie Chart</MenuItem>
              <MenuItem value="heatMap">Heat Map</MenuItem>
              <MenuItem value="radar">Radar Chart</MenuItem>
              <MenuItem value="histogram">Histogram</MenuItem>
              <MenuItem value="boxPlot">Box Plot</MenuItem>
              <MenuItem value="table">Table</MenuItem>
              <MenuItem value="map">Map</MenuItem>
              <MenuItem value="network">Network Graph</MenuItem>
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
          <Button
            onClick={handleCreateConfig}
            disabled={!newConfigName.trim() || !newConfigDatasetId}
          >
            Create
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default AnalysisConfigManager;

import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  FormControl,
  IconButton,
  InputLabel,
  List,
  ListItem,
  ListItemText,
  MenuItem,
  Select,
  TextField,
  Typography,
} from '@mui/material';
import * as React from "react";
import { useState } from 'react';
import { Dataset } from '../../types/exploration/DataAnalysisTypes';

interface DatasetManagerProps {
  datasets: Dataset[];
  selectedDataset: Dataset | null;
  onSelectDataset: (dataset: Dataset) => void;
  onCreateDataset: (dataset: Omit<Dataset, 'id' | 'createdAt' | 'updatedAt'>) => string;
  onUpdateDataset: (id: string, updates: Partial<Dataset>) => void;
  onDeleteDataset: (id: string) => void;
}

const DatasetManager: React.FC<DatasetManagerProps> = ({
  datasets,
  selectedDataset,
  onSelectDataset,
  onCreateDataset,
  onUpdateDataset,
  onDeleteDataset,
}) => {
  const [openDialog, setOpenDialog] = useState(false);
  const [newDatasetName, setNewDatasetName] = useState('');
  const [newDatasetDescription, setNewDatasetDescription] = useState('');
  const [newDatasetSource, setNewDatasetSource] = useState<
    'sectors' | 'anomalies' | 'resources' | 'mixed'
  >('sectors');

  const handleCreateDataset = () => {
    if (!newDatasetName.trim()) return;

    onCreateDataset({
      name: newDatasetName,
      description: newDatasetDescription,
      dataPoints: [],
      source: newDatasetSource,
    });

    setNewDatasetName('');
    setNewDatasetDescription('');
    setNewDatasetSource('sectors');
    setOpenDialog(false);
  };

  return (
    <div>
      <Button
        variant="outlined"
        startIcon={<AddIcon />}
        onClick={() => setOpenDialog(true)}
        sx={{ mb: 2 }}
      >
        Create Dataset
      </Button>

      {datasets.length === 0 ? (
        <Typography variant="body2" color="text.secondary" align="center">
          No datasets available. Create one to get started.
        </Typography>
      ) : (
        <List sx={{ maxHeight: 400, overflow: 'auto' }}>
          {datasets.map(dataset => (
            <ListItem
              key={dataset.id}
              button
              selected={selectedDataset?.id === dataset.id}
              onClick={() => onSelectDataset(dataset)}
              secondaryAction={
                <IconButton
                  edge="end"
                  aria-label="delete"
                  onClick={e => {
                    e.stopPropagation();
                    onDeleteDataset(dataset.id);
                  }}
                >
                  <DeleteIcon />
                </IconButton>
              }
            >
              <ListItemText
                primary={dataset.name}
                secondary={
                  <>
                    <Typography component="span" variant="body2" color="text.primary">
                      {dataset.source}
                    </Typography>
                    {` â€” ${dataset.dataPoints.length} data points`}
                  </>
                }
              />
            </ListItem>
          ))}
        </List>
      )}

      {/* Create Dataset Dialog */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>Create New Dataset</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Dataset Name"
            fullWidth
            value={newDatasetName}
            onChange={e => setNewDatasetName(e.target.value)}
          />
          <TextField
            margin="dense"
            label="Description"
            fullWidth
            value={newDatasetDescription}
            onChange={e => setNewDatasetDescription(e.target.value)}
          />
          <FormControl fullWidth margin="dense">
            <InputLabel>Source</InputLabel>
            <Select
              value={newDatasetSource}
              label="Source"
              onChange={e =>
                setNewDatasetSource(
                  e.target.value as 'sectors' | 'anomalies' | 'resources' | 'mixed'
                )
              }
            >
              <MenuItem value="sectors">Sectors</MenuItem>
              <MenuItem value="anomalies">Anomalies</MenuItem>
              <MenuItem value="resources">Resources</MenuItem>
              <MenuItem value="mixed">Mixed</MenuItem>
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
          <Button onClick={handleCreateDataset} disabled={!newDatasetName.trim()}>
            Create
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default DatasetManager;

import { ResourceType } from "./../../types/resources/ResourceTypes";
import {
  AlertTriangle,
  Brain,
  Check,
  ChevronDown,
  ChevronUp,
  Database,
  FolderTree,
  Search,
  X,
} from 'lucide-react';
import * as React from "react";
import { useEffect, useMemo, useState } from 'react';
import { useClassification } from '../../contexts/ClassificationContext';
import {
  ClassifiableDiscovery,
  Classification,
  ClassificationSuggestion,
  ConfidenceLevel,
} from '../../types/exploration/ClassificationTypes';

interface DiscoveryClassificationProps {
  discovery: ClassifiableDiscovery;
  onClassify?: (classification: Classification) => void;
  className?: string;
  compact?: boolean;
}

export function DiscoveryClassification({
  discovery,
  onClassify,
  className = '',
  compact = false,
}: DiscoveryClassificationProps) {
  const {
    taxonomyCategories,
    addClassification,
    getClassificationsForDiscovery,
    getTaxonomyCategory,
    generateClassificationSuggestions,
    getSimilarDiscoveries,
  } = useClassification();

  // State for the classification form
  const [selectedCategoryId, setSelectedCategoryId] = useState<string>('');
  const [confidence, setConfidence] = useState<number>(0.7);
  const [notes, setNotes] = useState<string>('');
  const [propertyValues, setPropertyValues] = useState<
    Record<string, string | number | boolean | string[]>
  >({});

  // State for UI
  const [showSuggestions, setShowSuggestions] = useState<boolean>(true);
  const [_showSimilarDiscoveries, _setShowSimilarDiscoveries] = useState<boolean>(false);
  const [showTaxonomyBrowser, setShowTaxonomyBrowser] = useState<boolean>(false);
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [activeTab, setActiveTab] = useState<'classify' | 'history' | 'similar'>('classify');
  const [isProcessing, setIsProcessing] = useState<boolean>(false);

  // Get existing classifications for this discovery
  const existingClassifications = useMemo(
    () => getClassificationsForDiscovery(discovery.id),
    [getClassificationsForDiscovery, discovery.id]
  );

  // Generate AI suggestions
  const [suggestions, setSuggestions] = useState<ClassificationSuggestion[]>([]);

  useEffect(() => {
    // Generate suggestions when the component mounts
    setSuggestions(generateClassificationSuggestions(discovery));
  }, [discovery, generateClassificationSuggestions]);

  // Get similar discoveries
  const similarDiscoveries = useMemo(
    () => getSimilarDiscoveries(discovery.id),
    [getSimilarDiscoveries, discovery.id]
  );

  // Get properties for the selected category
  const selectedCategoryProperties = useMemo(() => {
    const category = getTaxonomyCategory(selectedCategoryId);
    return category?.properties || [];
  }, [getTaxonomyCategory, selectedCategoryId]);

  // Handle property value changes
  const handlePropertyChange = (
    propertyId: string,
    value: string | number | boolean | string[]
  ) => {
    setPropertyValues(prev => ({
      ...prev,
      [propertyId]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Simulate processing
    setIsProcessing(true);

    setTimeout(() => {
      // Create the classification object
      const newClassification: Omit<Classification, 'id'> = {
        discoveryId: discovery.id,
        discoveryType: discovery.type,
        categoryId: selectedCategoryId,
        confidence,
        confidenceLevel: getConfidenceLevelFromScore(confidence),
        properties: propertyValues,
        notes,
        classifiedBy: 'user',
        classifiedDate: Date.now(),
        previousClassifications: existingClassifications.map(c => c.id),
      };

      // Add the classification
      addClassification(newClassification);

      // Call the onClassify callback if provided
      if (onClassify) {
        onClassify({
          ...newClassification,
          id: 'temp-id', // This will be replaced by the actual ID in the context
        });
      }

      // Reset the form
      setSelectedCategoryId('');
      setConfidence(0.7);
      setNotes('');
      setPropertyValues({});
      setIsProcessing(false);
    }, 1000);
  };

  // Apply a suggestion
  const applySuggestion = (suggestion: ClassificationSuggestion) => {
    setSelectedCategoryId(suggestion.categoryId);
    setConfidence(suggestion.confidence);
    setPropertyValues(suggestion.propertyValues);
  };

  // Helper function to get confidence level from score
  const getConfidenceLevelFromScore = (score: number): ConfidenceLevel => {
    if (score >= 0.9) {
      return 'confirmed';
    }
    if (score >= 0.7) {
      return 'high';
    }
    if (score >= 0.4) {
      return 'medium';
    }
    return 'low';
  };

  // Helper function to get color for confidence level
  const getConfidenceColor = (level: ConfidenceLevel): string => {
    switch (level) {
      case 'confirmed':
        return 'text-green-500';
      case 'high':
        return 'text-blue-500';
      case 'medium':
        return 'text-yellow-500';
      case 'low':
        return 'text-red-500';
      default:
        return 'text-gray-500';
    }
  };

  // Render the category hierarchy
  const renderCategoryHierarchy = (categoryId: string, level = 0) => {
    const category = getTaxonomyCategory(categoryId);
    if (!category) {
      return null;
    }

    // Find child categories
    const children = taxonomyCategories.filter(c => c.parentId === categoryId);

    return (
      <div key={categoryId} className="mb-1">
        <div
          className={`flex cursor-pointer items-center rounded p-2 ${
            selectedCategoryId === categoryId
              ? 'bg-blue-100 dark:bg-blue-900'
              : 'hover:bg-gray-100 dark:hover:bg-gray-800'
          }`}
          style={{ marginLeft: `${level * 16}px` }}
          onClick={() => setSelectedCategoryId(categoryId)}
        >
          <div
            className="mr-2 h-3 w-3 rounded-full"
            style={{ backgroundColor: category.color || '#6b7280' }}
          ></div>
          <span>{category.name}</span>
        </div>
        {children.map(child => renderCategoryHierarchy(child.id, level + 1))}
      </div>
    );
  };

  // Render the compact version
  if (compact) {
    return (
      <div className={`rounded-lg border p-4 shadow-sm ${className}`}>
        <h3 className="mb-2 text-lg font-semibold">Classification</h3>

        {existingClassifications.length > 0 ? (
          <div>
            {existingClassifications.map(classification => {
              const category = getTaxonomyCategory(classification.categoryId);
              return (
                <div
                  key={classification.id}
                  className="mb-2 rounded bg-gray-50 p-2 dark:bg-gray-800"
                >
                  <div className="flex items-center">
                    <div
                      className="mr-2 h-3 w-3 rounded-full"
                      style={{ backgroundColor: category?.color || '#6b7280' }}
                    ></div>
                    <span className="font-medium">{category?.name || 'Unknown Category'}</span>
                    <span
                      className={`ml-2 text-xs ${getConfidenceColor(classification.confidenceLevel)}`}
                    >
                      {classification.confidenceLevel}
                    </span>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <div className="text-sm text-gray-500 dark:text-gray-400">
            No classifications yet. Click to classify.
          </div>
        )}
      </div>
    );
  }

  // Render the full version
  return (
    <div className={`rounded-lg border shadow-sm ${className}`}>
      {/* Header */}
      <div className="border-b p-4">
        <h2 className="text-xl font-semibold">Discovery Classification</h2>
        <div className="mt-1 text-sm text-gray-500 dark:text-gray-400">
          Classify and categorize discoveries for better organization and analysis
        </div>
      </div>

      {/* Tabs */}
      <div className="flex border-b">
        <button
          className={`px-4 py-2 font-medium ${
            activeTab === 'classify'
              ? 'border-b-2 border-blue-500 text-blue-600'
              : 'text-gray-600 hover:text-gray-800'
          }`}
          onClick={() => setActiveTab('classify')}
        >
          Classify
        </button>
        <button
          className={`px-4 py-2 font-medium ${
            activeTab === 'history'
              ? 'border-b-2 border-blue-500 text-blue-600'
              : 'text-gray-600 hover:text-gray-800'
          }`}
          onClick={() => setActiveTab('history')}
        >
          History ({existingClassifications.length})
        </button>
        <button
          className={`px-4 py-2 font-medium ${
            activeTab === 'similar'
              ? 'border-b-2 border-blue-500 text-blue-600'
              : 'text-gray-600 hover:text-gray-800'
          }`}
          onClick={() => setActiveTab('similar')}
        >
          Similar ({similarDiscoveries.length})
        </button>
      </div>

      {/* Content */}
      <div className="p-4">
        {activeTab === 'classify' && (
          <div>
            {/* Discovery Info */}
            <div className="mb-4 rounded-lg bg-gray-50 p-3 dark:bg-gray-800">
              <div className="mb-2 flex items-center">
                <div className="mr-2">
                  {discovery.type === 'anomaly' ? (
                    <AlertTriangle className="text-yellow-500" size={20} />
                  ) : (
                    <Database className="text-blue-500" size={20} />
                  )}
                </div>
                <h3 className="text-lg font-medium">{discovery.name}</h3>
              </div>
              <div className="text-sm text-gray-600 dark:text-gray-300">
                <div>
                  Type:{' '}
                  {discovery.type === 'anomaly'
                    ? `Anomaly (${discovery.anomalyType})`
                    : `Resource (${discovery.resourceType})`}
                </div>
                <div>Sector: {discovery.sectorName}</div>
                <div>Discovered: {new Date(discovery.discoveryDate).toLocaleDateString()}</div>
              </div>
            </div>

            {/* AI Suggestions */}
            <div className="mb-4">
              <div
                className="flex cursor-pointer items-center justify-between rounded-t-lg bg-blue-50 p-2 dark:bg-blue-900"
                onClick={() => setShowSuggestions(!showSuggestions)}
              >
                <div className="flex items-center">
                  <Brain size={18} className="mr-2 text-blue-600 dark:text-blue-400" />
                  <span className="font-medium">AI Classification Suggestions</span>
                </div>
                {showSuggestions ? <ChevronUp size={18} /> : <ChevronDown size={18} />}
              </div>

              {showSuggestions && (
                <div className="rounded-b-lg border border-t-0 p-3">
                  {suggestions.length > 0 ? (
                    <div>
                      {suggestions.map((suggestion, index) => {
                        const category = getTaxonomyCategory(suggestion.categoryId);
                        const confidenceLevel = getConfidenceLevelFromScore(suggestion.confidence);

                        return (
                          <div
                            key={index}
                            className="mb-2 cursor-pointer rounded border p-2 hover:bg-gray-50 dark:hover:bg-gray-800"
                            onClick={() => applySuggestion(suggestion)}
                          >
                            <div className="flex items-center justify-between">
                              <div className="flex items-center">
                                <div
                                  className="mr-2 h-3 w-3 rounded-full"
                                  style={{ backgroundColor: category?.color || '#6b7280' }}
                                ></div>
                                <span className="font-medium">
                                  {category?.name || 'Unknown Category'}
                                </span>
                              </div>
                              <span className={`text-sm ${getConfidenceColor(confidenceLevel)}`}>
                                {Math.round(suggestion.confidence * 100)}% confidence
                              </span>
                            </div>
                            <div className="mt-1 text-sm text-gray-600 dark:text-gray-400">
                              {suggestion.reasoning}
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  ) : (
                    <div className="text-sm text-gray-500 dark:text-gray-400">
                      No suggestions available. Try providing more information about the discovery.
                    </div>
                  )}
                </div>
              )}
            </div>

            {/* Classification Form */}
            <form onSubmit={handleSubmit}>
              {/* Taxonomy Browser */}
              <div className="mb-4">
                <div
                  className="flex cursor-pointer items-center justify-between rounded-t-lg bg-gray-100 p-2 dark:bg-gray-700"
                  onClick={() => setShowTaxonomyBrowser(!showTaxonomyBrowser)}
                >
                  <div className="flex items-center">
                    <FolderTree size={18} className="mr-2" />
                    <span className="font-medium">Taxonomy Browser</span>
                  </div>
                  {showTaxonomyBrowser ? <ChevronUp size={18} /> : <ChevronDown size={18} />}
                </div>

                {showTaxonomyBrowser && (
                  <div className="rounded-b-lg border border-t-0 p-3">
                    <div className="mb-3">
                      <div className="relative">
                        <Search
                          className="absolute left-3 top-1/2 -translate-y-1/2 transform text-gray-400"
                          size={16}
                        />
                        <input
                          type="text"
                          placeholder="Search categories..."
                          className="w-full rounded border py-2 pl-10 pr-4"
                          value={searchQuery}
                          onChange={e => setSearchQuery(e.target.value)}
                        />
                      </div>
                    </div>

                    <div className="max-h-60 overflow-y-auto">
                      {discovery.type === 'anomaly'
                        ? renderCategoryHierarchy('anomaly-root')
                        : renderCategoryHierarchy('resource-root')}
                    </div>
                  </div>
                )}
              </div>

              {/* Selected Category */}
              <div className="mb-4">
                <label className="mb-1 block text-sm font-medium">Selected Category</label>
                {selectedCategoryId ? (
                  <div className="flex items-center rounded border p-2">
                    <div
                      className="mr-2 h-3 w-3 rounded-full"
                      style={{
                        backgroundColor:
                          getTaxonomyCategory(selectedCategoryId)?.color || '#6b7280',
                      }}
                    ></div>
                    <span>
                      {getTaxonomyCategory(selectedCategoryId)?.name || 'Unknown Category'}
                    </span>
                    <button
                      type="button"
                      className="ml-auto text-gray-400 hover:text-gray-600"
                      onClick={() => setSelectedCategoryId('')}
                    >
                      <X size={16} />
                    </button>
                  </div>
                ) : (
                  <div className="rounded border p-2 text-gray-500 dark:text-gray-400">
                    No category selected. Use the taxonomy browser or AI suggestions to select a
                    category.
                  </div>
                )}
              </div>

              {/* Confidence Slider */}
              <div className="mb-4">
                <label className="mb-1 block text-sm font-medium">
                  Classification Confidence: {Math.round(confidence * 100)}%
                </label>
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.01"
                  value={confidence}
                  onChange={e => setConfidence(parseFloat(e.target.value))}
                  className="w-full"
                />
                <div className="flex justify-between text-xs text-gray-500">
                  <span>Low</span>
                  <span>Medium</span>
                  <span>High</span>
                  <span>Confirmed</span>
                </div>
              </div>

              {/* Category Properties */}
              {selectedCategoryProperties.length > 0 && (
                <div className="mb-4">
                  <h4 className="mb-2 text-sm font-medium">Category Properties</h4>
                  {selectedCategoryProperties.map(property => (
                    <div key={property.id} className="mb-3">
                      <label className="mb-1 block text-sm font-medium">
                        {property.name}
                        {property.required && <span className="ml-1 text-red-500">*</span>}
                      </label>
                      {property.type === 'string' && (
                        <input
                          type="text"
                          className="w-full rounded border p-2"
                          value={(propertyValues[property.id] as string) || ''}
                          onChange={e => handlePropertyChange(property.id, e.target.value)}
                          required={property.required}
                        />
                      )}
                      {property.type === 'number' && (
                        <input
                          type="number"
                          className="w-full rounded border p-2"
                          value={(propertyValues[property.id] as number) || ''}
                          onChange={e =>
                            handlePropertyChange(property.id, parseFloat(e.target.value))
                          }
                          required={property.required}
                        />
                      )}
                      {property.type === 'boolean' && (
                        <div className="flex items-center">
                          <input
                            type="checkbox"
                            className="mr-2"
                            checked={(propertyValues[property.id] as boolean) || false}
                            onChange={e => handlePropertyChange(property.id, e.target.checked)}
                            required={property.required}
                          />
                          <span className="text-sm">Yes</span>
                        </div>
                      )}
                      {property.type === 'enum' && property.options && (
                        <select
                          className="w-full rounded border p-2"
                          value={(propertyValues[property.id] as string) || ''}
                          onChange={e => handlePropertyChange(property.id, e.target.value)}
                          required={property.required}
                        >
                          <option value="">Select an option</option>
                          {property.options.map(option => (
                            <option key={option} value={option}>
                              {option}
                            </option>
                          ))}
                        </select>
                      )}
                      {property.description && (
                        <div className="mt-1 text-xs text-gray-500">{property.description}</div>
                      )}
                    </div>
                  ))}
                </div>
              )}

              {/* Notes */}
              <div className="mb-4">
                <label className="mb-1 block text-sm font-medium">Notes</label>
                <textarea
                  className="w-full rounded border p-2"
                  rows={3}
                  value={notes}
                  onChange={e => setNotes(e.target.value)}
                  placeholder="Add any additional notes about this classification..."
                />
              </div>

              {/* Submit Button */}
              <div className="flex justify-end">
                <button
                  type="submit"
                  className="flex items-center rounded bg-blue-600 px-4 py-2 text-white hover:bg-blue-700"
                  disabled={!selectedCategoryId || isProcessing}
                >
                  {isProcessing ? (
                    <>
                      <div className="mr-2 animate-spin">
                        <div className="h-4 w-4 rounded-full border-2 border-white border-t-transparent"></div>
                      </div>
                      Processing...
                    </>
                  ) : (
                    <>
                      <Check size={18} className="mr-2" />
                      Classify Discovery
                    </>
                  )}
                </button>
              </div>
            </form>
          </div>
        )}

        {activeTab === 'history' && (
          <div>
            <h3 className="mb-3 text-lg font-medium">Classification History</h3>

            {existingClassifications.length > 0 ? (
              <div>
                {existingClassifications.map(classification => {
                  const category = getTaxonomyCategory(classification.categoryId);

                  return (
                    <div key={classification.id} className="mb-3 rounded border p-3">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center">
                          <div
                            className="mr-2 h-3 w-3 rounded-full"
                            style={{ backgroundColor: category?.color || '#6b7280' }}
                          ></div>
                          <span className="font-medium">
                            {category?.name || 'Unknown Category'}
                          </span>
                        </div>
                        <span
                          className={`text-sm ${getConfidenceColor(classification.confidenceLevel)}`}
                        >
                          {Math.round(classification.confidence * 100)}% confidence
                        </span>
                      </div>

                      <div className="mt-2 text-sm text-gray-600 dark:text-gray-400">
                        Classified by: {classification.classifiedBy} on{' '}
                        {new Date(classification.classifiedDate).toLocaleString()}
                      </div>

                      {Object.keys(classification.properties).length > 0 && (
                        <div className="mt-2">
                          <h4 className="mb-1 text-sm font-medium">Properties</h4>
                          <div className="grid grid-cols-2 gap-2 text-sm">
                            {Object.entries(classification.properties).map(([key, value]) => (
                              <div key={key}>
                                <span className="font-medium">{key}:</span> {String(value)}
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {classification.notes && (
                        <div className="mt-2">
                          <h4 className="mb-1 text-sm font-medium">Notes</h4>
                          <div className="text-sm">{classification.notes}</div>
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            ) : (
              <div className="text-gray-500 dark:text-gray-400">
                No classification history available for this discovery.
              </div>
            )}
          </div>
        )}

        {activeTab === 'similar' && (
          <div>
            <h3 className="mb-3 text-lg font-medium">Similar Discoveries</h3>

            {similarDiscoveries.length > 0 ? (
              <div>
                {similarDiscoveries.map(discovery => {
                  const classification = discovery.classification;
                  const category = classification
                    ? getTaxonomyCategory(classification.categoryId)
                    : null;

                  return (
                    <div key={discovery.id} className="mb-3 rounded border p-3">
                      <div className="mb-2 flex items-center">
                        <div className="mr-2">
                          {discovery.type === 'anomaly' ? (
                            <AlertTriangle className="text-yellow-500" size={18} />
                          ) : (
                            <Database className="text-blue-500" size={18} />
                          )}
                        </div>
                        <h4 className="font-medium">{discovery.name}</h4>
                      </div>

                      <div className="text-sm text-gray-600 dark:text-gray-400">
                        <div>
                          Type:{' '}
                          {discovery.type === 'anomaly'
                            ? `Anomaly (${discovery.anomalyType})`
                            : `Resource (${discovery.resourceType})`}
                        </div>
                        <div>Sector: {discovery.sectorName}</div>
                      </div>

                      {classification && category && (
                        <div className="mt-2 rounded bg-gray-50 p-2 dark:bg-gray-800">
                          <div className="flex items-center">
                            <div
                              className="mr-2 h-3 w-3 rounded-full"
                              style={{ backgroundColor: category.color || '#6b7280' }}
                            ></div>
                            <span className="font-medium">{category.name}</span>
                            <span
                              className={`ml-2 text-xs ${getConfidenceColor(
                                classification.confidenceLevel
                              )}`}
                            >
                              {Math.round(classification.confidence * 100)}% confidence
                            </span>
                          </div>
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            ) : (
              <div className="text-gray-500 dark:text-gray-400">
                No similar discoveries found. Try classifying this discovery first.
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

import { ChevronUp, Plus, Radar, Users, Zap } from 'lucide-react';
import { useCallback, useEffect, useMemo, useState } from 'react';
import { moduleEventBus } from '../../lib/events/ModuleEventBus';
import { Position } from '../../types/core/GameTypes';
import { EventType } from '../../types/events/EventTypes';
import { StandardizedEvent } from '../../types/events/StandardizedEvents';
import { ExplorationStatus, Sector } from '../../types/exploration/unified/ExplorationTypes';
import { cn } from '../../utils/cn';

// Define interfaces based on ReconShipManagerImpl
interface ReconShip {
  id: string;
  name: string;
  type: 'AC27G' | 'PathFinder' | 'VoidSeeker' | 'recon' | 'mining' | 'war';
  status: 'idle' | 'scanning' | 'investigating' | 'returning';
  targetSector?: string;
  experience: number;
  specialization: 'mapping' | 'anomaly' | 'resource';
  efficiency: number;
  position: Position;
  formationId?: string;
  formationRole?: 'leader' | 'support' | 'scout';
  coordinationBonus?: number;
  capabilities: {
    scanning: number;
    stealth: number;
    combat: number;
    stealthActive?: boolean;
    speed: number;
    range: number;
  };
  currentTask?: {
    type: string;
    target: string;
    progress: number;
  };
  stealthActive?: boolean;
}

interface Formation {
  id: string;
  name: string;
  type: string;
  shipIds: string[];
  leaderId: string;
  position: Position;
  status: 'idle' | 'scanning' | 'moving' | 'combat';
  effectiveness?: number;
}

interface ReconShipCoordinationProps {
  ships: ReconShip[];
  sectors: Sector[];
  formations?: Formation[];
  onCreateFormation: (name: string, type: string, shipIds: string[], leaderId: string) => void;
  onDisbandFormation: (formationId: string) => void;
  _onAddShipToFormation: (shipId: string, formationId: string) => void;
  _onRemoveShipFromFormation: (shipId: string, formationId: string) => void;
  onStartCoordinatedScan: (sectorId: string, shipIds: string[]) => void;
  onShareTask?: (
    sourceShipId: string,
    targetShipId: string,
    taskType: 'explore' | 'investigate' | 'evade'
  ) => void;
  onAutoDistributeTasks: (sectorIds: string[], prioritizeFormations: boolean) => void;
  className?: string;
}

export const ReconShipCoordination: React.FC<ReconShipCoordinationProps> = ({
  ships,
  sectors,
  formations = [],
  onCreateFormation,
  onDisbandFormation,
  _onAddShipToFormation,
  _onRemoveShipFromFormation,
  onStartCoordinatedScan,
  onShareTask,
  onAutoDistributeTasks,
  className,
}) => {
  // State for UI
  const [activeTab, setActiveTab] = useState<'formations' | 'coordination' | 'auto'>('formations');
  const [selectedFormationId, setSelectedFormationId] = useState<string>('');
  const [selectedSectorId, setSelectedSectorId] = useState<string>('');
  const [showCreateFormation, setShowCreateFormation] = useState(false);
  const [formationName, setFormationName] = useState('');
  const [formationType, setFormationType] = useState<string>('exploration');
  const [selectedShipIds, setSelectedShipIds] = useState<string[]>([]);
  const [selectedLeaderId, setSelectedLeaderId] = useState<string>('');
  const [_prioritizeFormations, _setPrioritizeFormations] = useState(true);

  // Derived state
  const availableShips = useMemo(() => {
    return ships.filter(ship => ship.status === 'idle' && !ship.formationId);
  }, [ships]);

  const _formationShips = useMemo(() => {
    if (!selectedFormationId) return [];
    const formation = formations.find(f => f.id === selectedFormationId);
    if (!formation) return [];
    return ships.filter(ship => formation.shipIds.includes(ship.id));
  }, [ships, formations, selectedFormationId]);

  const _selectedFormation = useMemo(() => {
    return formations.find(f => f.id === selectedFormationId) || null;
  }, [formations, selectedFormationId]);

  // Reset selected leader when selected ships change
  useEffect(() => {
    if (selectedShipIds.length > 0 && !selectedShipIds.includes(selectedLeaderId)) {
      setSelectedLeaderId(selectedShipIds[0]);
    } else if (selectedShipIds.length === 0) {
      setSelectedLeaderId('');
    }
  }, [selectedShipIds, selectedLeaderId]);

  // Handle form submission for creating a new formation with standardized events
  const handleCreateFormation = useCallback(
    (name: string, type: string, shipIds: string[], leaderId: string) => {
      const event: StandardizedEvent = {
        type: EventType.MODULE_UPDATED,
        moduleId: 'recon-coordination',
        moduleType: 'exploration',
        timestamp: Date.now(),
        data: { name, type, shipIds, leaderId, action: 'create_formation' },
      };
      moduleEventBus.emit(event);
      onCreateFormation(name, type, shipIds, leaderId);
    },
    [onCreateFormation]
  );

  // Handle starting a coordinated scan with standardized events
  const handleStartCoordinatedScan = useCallback(
    (sectorId: string, shipIds: string[]) => {
      const event: StandardizedEvent = {
        type: EventType.EXPLORATION_SCAN_STARTED,
        moduleId: 'recon-coordination',
        moduleType: 'exploration',
        timestamp: Date.now(),
        data: { sectorId, shipIds },
      };
      moduleEventBus.emit(event);
      onStartCoordinatedScan(sectorId, shipIds);
    },
    [onStartCoordinatedScan]
  );

  // Handle auto-distribution of tasks with standardized events
  const handleAutoDistributeTasks = useCallback(
    (sectorIds: string[], prioritizeFormations: boolean) => {
      const event: StandardizedEvent = {
        type: EventType.EXPLORATION_TASK_ASSIGNED,
        moduleId: 'recon-coordination',
        moduleType: 'exploration',
        timestamp: Date.now(),
        data: { sectorIds, prioritizeFormations },
      };
      moduleEventBus.emit(event);
      onAutoDistributeTasks(sectorIds, prioritizeFormations);
    },
    [onAutoDistributeTasks]
  );

  // Handle task sharing with standardized events
  const handleShareTask = useCallback(
    (sourceShipId: string, targetShipId: string, taskType: 'explore' | 'investigate' | 'evade') => {
      if (onShareTask) {
        const event: StandardizedEvent = {
          type: EventType.EXPLORATION_TASK_ASSIGNED,
          moduleId: 'recon-coordination',
          moduleType: 'exploration',
          timestamp: Date.now(),
          data: { sourceShipId, targetShipId, taskType },
        };
        moduleEventBus.emit(event);
        onShareTask(sourceShipId, targetShipId, taskType);
      }
    },
    [onShareTask]
  );

  // Cleanup subscriptions on unmount
  useEffect(() => {
    const cleanup = () => {
      // Any cleanup needed for event subscriptions
    };

    return cleanup;
  }, []);

  // Add a button or UI element to use the handleShareTask function
  const _renderTaskSharingControls = () => {
    if (activeTab !== 'coordination' || !selectedShipIds.length) return null;

    return (
      <div className="mt-4 rounded border border-gray-200 p-3">
        <h4 className="mb-2 text-sm font-semibold">Share Tasks</h4>
        <div className="space-y-2">
          {selectedShipIds.map(shipId => {
            const ship = ships.find(s => s.id === shipId);
            if (!ship) return null;

            return (
              <div key={`share-${shipId}`} className="flex items-center justify-between">
                <span className="text-sm">{ship.name}</span>
                <div className="flex space-x-1">
                  <button
                    className="rounded bg-blue-100 px-2 py-1 text-xs text-blue-700 hover:bg-blue-200"
                    onClick={() =>
                      handleShareTask(
                        shipId,
                        selectedShipIds.find(id => id !== shipId) || '',
                        'explore'
                      )
                    }
                    disabled={selectedShipIds.length < 2}
                  >
                    Explore
                  </button>
                  <button
                    className="rounded bg-purple-100 px-2 py-1 text-xs text-purple-700 hover:bg-purple-200"
                    onClick={() =>
                      handleShareTask(
                        shipId,
                        selectedShipIds.find(id => id !== shipId) || '',
                        'investigate'
                      )
                    }
                    disabled={selectedShipIds.length < 2}
                  >
                    Investigate
                  </button>
                  <button
                    className="rounded bg-red-100 px-2 py-1 text-xs text-red-700 hover:bg-red-200"
                    onClick={() =>
                      handleShareTask(
                        shipId,
                        selectedShipIds.find(id => id !== shipId) || '',
                        'evade'
                      )
                    }
                    disabled={selectedShipIds.length < 2}
                  >
                    Evade
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  const getUnexploredSectors = useCallback(() => {
    return sectors.filter(sector => sector.explorationStatus === ExplorationStatus.UNDISCOVERED);
  }, [sectors]);

  const _getHighPrioritySectors = useCallback(() => {
    return sectors.filter(
      sector =>
        sector.explorationStatus !== ExplorationStatus.FULLY_EXPLORED && sector.resources.length > 0
    );
  }, [sectors]);

  const getAvailableReconShips = useCallback(() => {
    return ships.filter(
      ship =>
        ship.type === 'recon' &&
        ship.status === 'idle' &&
        !formations.some(f => f.shipIds.includes(ship.id))
    );
  }, [ships, formations]);

  const getFormationEffectiveness = useCallback(
    (formation: Formation) => {
      const formationShips = ships.filter(ship => formation.shipIds.includes(ship.id));
      return formationShips.reduce((total, ship) => {
        const baseEffectiveness = ship.capabilities.speed + ship.capabilities.range;
        const stealthBonus = ship.capabilities.stealth
          ? ship.capabilities.stealth * (ship.stealthActive ? 2 : 1)
          : 0;
        return total + baseEffectiveness + stealthBonus;
      }, 0);
    },
    [ships]
  );

  const getFormationShips = useCallback(
    (formation: Formation) => {
      return ships.filter(ship => formation.shipIds.includes(ship.id));
    },
    [ships]
  );

  const getFormationLeader = useCallback(
    (formation: Formation) => {
      return ships.find(ship => ship.id === formation.leaderId);
    },
    [ships]
  );

  const getFormationStatus = useCallback(
    (formation: Formation) => {
      const formationShips = getFormationShips(formation);
      if (formationShips.every(ship => ship.status === 'scanning')) {
        return 'scanning';
      }
      if (formationShips.some(ship => ship.status === 'investigating')) {
        return 'investigating';
      }
      return 'idle';
    },
    [getFormationShips]
  );

  const _getFormationsByType = useCallback(
    (type: string) => {
      return formations.filter(f => f.type === type);
    },
    [formations]
  );

  const _getShipsByType = useCallback(
    (type: 'recon' | 'mining' | 'war') => {
      return ships.filter(ship => ship.type === type);
    },
    [ships]
  );

  const _getShipsByStatus = useCallback(
    (status: 'idle' | 'scanning' | 'investigating' | 'returning') => {
      return ships.filter(ship => ship.status === status);
    },
    [ships]
  );

  const renderFormationList = () => {
    return (
      <div className="space-y-4">
        {formations.map(formation => {
          const leader = getFormationLeader(formation);
          const effectiveness = getFormationEffectiveness(formation);
          const status = getFormationStatus(formation);

          return (
            <div key={formation.id} className="rounded border p-4">
              <div className="flex items-center justify-between">
                <div>
                  <h4 className="font-medium">{formation.name}</h4>
                  <p className="text-sm text-gray-500">Type: {formation.type}</p>
                  <p className="text-sm text-gray-500">Status: {status}</p>
                  <p className="text-sm text-gray-500">Effectiveness: {effectiveness.toFixed(1)}</p>
                </div>
                <div className="flex space-x-2">
                  <button
                    className="rounded bg-red-500 px-2 py-1 text-white hover:bg-red-600"
                    onClick={() => onDisbandFormation(formation.id)}
                  >
                    Disband
                  </button>
                </div>
              </div>
              <div className="mt-2">
                <h5 className="text-sm font-medium">Ships</h5>
                <div className="mt-1 space-y-1">
                  {getFormationShips(formation).map(ship => (
                    <div key={ship.id} className="flex items-center justify-between text-sm">
                      <span>{ship.name}</span>
                      <span className="text-gray-500">{ship.type}</span>
                      <span className="text-gray-500">
                        {ship.currentTask ? `Task: ${ship.currentTask}` : 'No task'}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          );
        })}
      </div>
    );
  };

  const handleAutoDistributeClick = () => {
    const unexploredSectorIds = getUnexploredSectors().map(sector => sector.id);
    handleAutoDistributeTasks(unexploredSectorIds, true);
  };

  const renderCoordinationTab = () => {
    return (
      <div>
        <div className="mb-4">
          <h3 className="text-lg font-medium">Coordinated Scanning</h3>
          <p className="text-sm text-gray-500">
            Select a formation and sector to begin coordinated scanning
          </p>
        </div>

        <div className="grid gap-4 md:grid-cols-2">
          <div>
            <h4 className="mb-2 font-medium">Select Formation</h4>
            <select
              className="w-full rounded border p-2"
              value={selectedShipIds.join(',')}
              onChange={e => setSelectedShipIds(e.target.value ? e.target.value.split(',') : [])}
            >
              <option value="">Select a formation...</option>
              {formations.map(formation => (
                <option key={formation.id} value={formation.shipIds.join(',')}>
                  {formation.name} ({formation.shipIds.length} ships)
                </option>
              ))}
            </select>
          </div>

          <div>
            <h4 className="mb-2 font-medium">Select Sector</h4>
            <select
              className="w-full rounded border p-2"
              value={selectedSectorId}
              onChange={e => setSelectedSectorId(e.target.value)}
            >
              <option value="">Select a sector...</option>
              {sectors.map(sector => (
                <option key={sector.id} value={sector.id}>
                  {sector.name} - {sector.explorationStatus}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="mt-4">
          <button
            className="flex items-center rounded bg-blue-500 px-4 py-2 text-white hover:bg-blue-600"
            onClick={() =>
              selectedSectorId &&
              selectedShipIds.length > 0 &&
              handleStartCoordinatedScan(selectedSectorId, selectedShipIds)
            }
            disabled={!selectedSectorId || selectedShipIds.length === 0}
          >
            Start Coordinated Scan
          </button>
        </div>
      </div>
    );
  };

  const renderAutoDistributionTab = () => {
    return (
      <div>
        <div className="mb-4">
          <h3 className="text-lg font-medium">Auto-Distribution</h3>
          <p className="text-sm text-gray-500">Automatically distribute tasks to available ships</p>
        </div>

        <div className="mb-4">
          <button
            className="flex items-center rounded bg-blue-500 px-4 py-2 text-white hover:bg-blue-600"
            onClick={handleAutoDistributeClick}
            disabled={getUnexploredSectors().length === 0 || getAvailableReconShips().length === 0}
          >
            Auto-Distribute Tasks
          </button>
        </div>
      </div>
    );
  };

  const renderShipDetails = (ship: ReconShip) => {
    return (
      <div key={ship.id} className="mb-4 rounded-lg bg-gray-800 p-4">
        <h4 className="mb-2 text-lg font-semibold text-white">{ship.name}</h4>
        <div className="grid grid-cols-2 gap-2 text-sm text-gray-300">
          <div>Type: {ship.type}</div>
          <div>Status: {ship.status}</div>
          <div>Scanning: {ship.capabilities?.scanning || 0}</div>
          <div>Stealth: {ship.capabilities?.stealth || 0}</div>
          <div>Combat: {ship.capabilities?.combat || 0}</div>
          <div>Stealth Active: {ship.capabilities?.stealthActive ? 'Yes' : 'No'}</div>
        </div>
      </div>
    );
  };

  const handleFormationSelect = (formationId: string) => {
    setSelectedFormationId(formationId === selectedFormationId ? '' : formationId);
  };

  return (
    <div className={cn('flex flex-col space-y-4', className)}>
      {/* Header */}
      <div className="border-b bg-gray-50 p-4 dark:bg-gray-800">
        <h2 className="flex items-center text-xl font-semibold">
          <Users className="mr-2" />
          Recon Ship Coordination
        </h2>
        <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
          Manage fleet formations and coordinate recon ship operations
        </p>
      </div>

      {/* Tabs */}
      <div className="flex border-b">
        <button
          className={`px-4 py-2 font-medium ${
            activeTab === 'formations'
              ? 'border-b-2 border-blue-500 text-blue-600'
              : 'text-gray-600 hover:text-gray-800'
          }`}
          onClick={() => setActiveTab('formations')}
        >
          <div className="flex items-center">
            <Users size={16} className="mr-2" />
            Formations
          </div>
        </button>
        <button
          className={`px-4 py-2 font-medium ${
            activeTab === 'coordination'
              ? 'border-b-2 border-blue-500 text-blue-600'
              : 'text-gray-600 hover:text-gray-800'
          }`}
          onClick={() => setActiveTab('coordination')}
        >
          <div className="flex items-center">
            <Radar size={16} className="mr-2" />
            Coordinated Scanning
          </div>
        </button>
        <button
          className={`px-4 py-2 font-medium ${
            activeTab === 'auto'
              ? 'border-b-2 border-blue-500 text-blue-600'
              : 'text-gray-600 hover:text-gray-800'
          }`}
          onClick={() => setActiveTab('auto')}
        >
          <div className="flex items-center">
            <Zap size={16} className="mr-2" />
            Auto-Distribution
          </div>
        </button>
      </div>

      {/* Content */}
      <div className="p-4">
        {/* Formations Tab */}
        {activeTab === 'formations' && (
          <div>
            {/* Formation List */}
            <div className="mb-4">
              <div className="mb-2 flex items-center justify-between">
                <h3 className="text-lg font-medium">Fleet Formations</h3>
                <button
                  className="flex items-center rounded bg-blue-500 px-2 py-1 text-white hover:bg-blue-600"
                  onClick={() => setShowCreateFormation(!showCreateFormation)}
                >
                  {showCreateFormation ? (
                    <>
                      <ChevronUp size={16} className="mr-1" />
                      Cancel
                    </>
                  ) : (
                    <>
                      <Plus size={16} className="mr-1" />
                      New Formation
                    </>
                  )}
                </button>
              </div>

              {/* Create Formation Form */}
              {showCreateFormation && (
                <div className="mb-4 rounded border bg-gray-50 p-4 dark:bg-gray-800">
                  <h4 className="mb-2 font-medium">Create New Formation</h4>

                  <div className="mb-3">
                    <label className="mb-1 block text-sm font-medium">Formation Name</label>
                    <input
                      type="text"
                      className="w-full rounded border p-2"
                      value={formationName}
                      onChange={e => setFormationName(e.target.value)}
                      placeholder="Enter formation name"
                    />
                  </div>

                  <div className="mb-3">
                    <label className="mb-1 block text-sm font-medium">Formation Type</label>
                    <select
                      className="w-full rounded border p-2"
                      value={formationType}
                      onChange={e => setFormationType(e.target.value)}
                    >
                      <option value="exploration">Exploration</option>
                      <option value="survey">Survey</option>
                      <option value="defensive">Defensive</option>
                    </select>
                    <p className="mt-1 text-xs text-gray-500">
                      {formationType === 'exploration'
                        ? 'Balanced formation for general exploration'
                        : formationType === 'survey'
                          ? 'Specialized formation for resource discovery'
                          : 'Defensive formation with enhanced threat detection'}
                    </p>
                  </div>

                  <div className="mb-3">
                    <label className="mb-1 block text-sm font-medium">Select Ships</label>
                    <div className="max-h-40 overflow-y-auto rounded border p-2">
                      {availableShips.length > 0 ? (
                        availableShips.map(ship => (
                          <div key={ship.id} className="mb-1 flex items-center last:mb-0">
                            <input
                              type="checkbox"
                              id={`ship-${ship.id}`}
                              checked={selectedShipIds.includes(ship.id)}
                              onChange={e => {
                                if (e.target.checked) {
                                  setSelectedShipIds([...selectedShipIds, ship.id]);
                                } else {
                                  setSelectedShipIds(selectedShipIds.filter(id => id !== ship.id));
                                }
                              }}
                              className="mr-2"
                            />
                            <label htmlFor={`ship-${ship.id}`} className="flex items-center">
                              <span className="font-medium">{ship.name}</span>
                              <span className="ml-2 text-xs text-gray-500">
                                ({ship.type} - {ship.specialization})
                              </span>
                            </label>
                          </div>
                        ))
                      ) : (
                        <div className="text-sm text-gray-500">No available ships</div>
                      )}
                    </div>
                  </div>

                  {selectedShipIds.length > 0 && (
                    <div className="mb-3">
                      <label className="mb-1 block text-sm font-medium">Formation Leader</label>
                      <select
                        className="w-full rounded border p-2"
                        value={selectedLeaderId}
                        onChange={e => setSelectedLeaderId(e.target.value)}
                      >
                        {selectedShipIds.map(shipId => {
                          const ship = ships.find(s => s.id === shipId);
                          return (
                            <option key={shipId} value={shipId}>
                              {ship?.name} ({ship?.specialization})
                            </option>
                          );
                        })}
                      </select>
                    </div>
                  )}

                  <div className="flex justify-end">
                    <button
                      className="flex items-center rounded bg-blue-500 px-3 py-1 text-white hover:bg-blue-600"
                      onClick={() =>
                        handleCreateFormation(
                          formationName,
                          formationType,
                          selectedShipIds,
                          selectedLeaderId
                        )
                      }
                      disabled={!formationName || selectedShipIds.length === 0 || !selectedLeaderId}
                    >
                      <Plus size={16} className="mr-1" />
                      Create Formation
                    </button>
                  </div>
                </div>
              )}

              {/* Formations List */}
              {renderFormationList()}
            </div>
          </div>
        )}

        {/* Coordinated Scanning Tab */}
        {activeTab === 'coordination' && renderCoordinationTab()}

        {/* Auto-Distribution Tab */}
        {activeTab === 'auto' && renderAutoDistributionTab()}
      </div>
    </div>
  );
};

import { Database, Droplet, Leaf, Loader, Search, Sparkles, Zap } from 'lucide-react';
import * as React from 'react';
import { useCallback, useEffect, useMemo, useState } from 'react';
import { ensureStringResourceType } from '../../utils/resources/ResourceTypeMigration';
import { ResourceType } from './../../types/resources/ResourceTypes';

// Interfaces
interface ResourceDiscovery {
  id: string;
  sectorId: string;
  sectorName: string;
  discoveryDate: number;
  processedDate?: number;
  status: 'discovered' | 'analyzing' | 'processed';
  confidence: number; // 0-1 scale
  rawData: RawResourceData[];
  processedData?: ResourceData[];
  scanQuality: number; // 0-1 scale
  notes?: string;
}

interface RawResourceData {
  signalStrength: number; // 0-1 scale
  signalType:
    | 'mineral'
    | ResourceType.ENERGY
    | ResourceType.GAS
    | 'organic'
    | ResourceType.EXOTIC
    | 'unknown';
  signalPattern: 'concentrated' | 'scattered' | 'veins' | 'unknown';
  signalDepth: number; // 0-1 scale (deeper = harder to access)
  coordinates: { x: number; y: number }; // Relative to sector center
}

interface ResourceData {
  type: ResourceType;
  name: string;
  amount: number; // 0-100 scale
  quality: number; // 0-1 scale
  accessibility: number; // 0-1 scale (how easy it is to extract)
  distribution: 'concentrated' | 'scattered' | 'veins';
  estimatedValue: number; // Credit value
  extractionDifficulty: number; // 0-10 scale
  coordinates: { x: number; y: number }; // Relative to sector center
}

interface Sector {
  id: string;
  name: string;
  status: 'unmapped' | 'mapped' | 'scanning' | 'analyzed';
  coordinates: { x: number; y: number };
  resourcePotential: number;
  habitabilityScore: number;
  lastScanned?: number;
}

interface ResourceDiscoverySystemProps {
  discoveries: ResourceDiscovery[];
  sectors: Sector[];
  onProcessDiscovery: (discoveryId: string, processedData: ResourceData[]) => void;
  onUpdateNotes: (discoveryId: string, notes: string) => void;
  className?: string;
  quality?: 'low' | 'medium' | 'high';
}

// Resource name generators by type - updated to use enum values
const resourceNames: Record<string, string[]> = {
  [ResourceType.MINERALS]: [
    'Iron Deposits',
    'Copper Veins',
    'Titanium Ore',
    'Platinum Clusters',
