} from './BaseChart';

// Define allowed positions for reference line labels
type ReferenceLinePosition =
  | 'top'
  | 'bottom'
  | 'left'
  | 'right'
  | 'insideTop'
  | 'insideBottom'
  | 'insideLeft'
  | 'insideRight'
  | 'insideTopRight'
  | 'insideTopLeft'
  | 'insideBottomRight'
  | 'insideBottomLeft'
  | 'center';

// Define the click event interface
interface PointClickEvent {
  payload: Record<string, unknown>;
  [key: string]: unknown;
}

export interface ScatterPlotProps extends BaseChartProps {
  /** Key for X-axis values */
  xAxisKey: string;

  /** Key for Y-axis values */
  yAxisKey: string;

  /** Optional key for Z-axis values (bubble size) */
  zAxisKey?: string;

  /** Label for X-axis */
  xAxisLabel?: string;

  /** Label for Y-axis */
  yAxisLabel?: string;

  /** Key for point name/label */
  nameKey?: string;

  /** Whether to show grid lines */
  showGrid?: boolean;

  /** Point fill color */
  color?: string;

  /** Size of the points */
  pointSize?: number;

  /** Minimum for Z-axis size range */
  zAxisSizeMin?: number;

  /** Maximum for Z-axis size range */
  zAxisSizeMax?: number;

  /** Optional quadrants to divide the chart */
  showQuadrants?: boolean;

  /** X-axis value for quadrant division */
  quadrantXValue?: number;

  /** Y-axis value for quadrant division */
  quadrantYValue?: number;

  /** Quadrant label configuration */
  quadrantLabels?: {
    topRight?: string;
    topLeft?: string;
    bottomRight?: string;
    bottomLeft?: string;
  };

  /** Whether to show the legends */
  showLegend?: boolean;

  /** Reference lines to display */
  referenceLines?: ReferenceLineType[];
}

/**
 * ScatterPlot component for visualizing correlations between variables
 */
export function ScatterPlot({
  data,
  xAxisKey,
  yAxisKey,
  zAxisKey,
  width = '100%',
  height = 400,
  title,
  color = '#8884d8',
  xAxisLabel,
  yAxisLabel,
  nameKey,
  customTooltip,
  theme = 'light',
  showGrid = true,
  pointSize = 10,
  zAxisSizeMin = 10,
  zAxisSizeMax = 80,
  showQuadrants = false,
  quadrantXValue,
  quadrantYValue,
  quadrantLabels = {
    topRight: 'Top Right',
    topLeft: 'Top Left',
    bottomRight: 'Bottom Right',
    bottomLeft: 'Bottom Left',
  },
  showLegend = true,
  referenceLines = [],
  className = '',
  animate = true,
  onElementClick,
  errorMessage,
}: ScatterPlotProps) {
  // Process data for the chart
  const processedData = useMemo(() => {
    if (!data || data.length === 0) return [];

    return data.map(item => {
      let x, y, z, name;

      // Handle DataPoint objects
      if ('properties' in item && 'metadata' in item) {
        const dataPoint = item as DataPoint;
        const properties = { ...dataPoint.properties };
        const metadata = dataPoint.metadata || {};

        // Try to find the required values in properties or metadata
        x = properties[xAxisKey] !== undefined ? properties[xAxisKey] : metadata[xAxisKey];
        y = properties[yAxisKey] !== undefined ? properties[yAxisKey] : metadata[yAxisKey];
        z = zAxisKey
          ? properties[zAxisKey] !== undefined
            ? properties[zAxisKey]
            : metadata[zAxisKey]
          : undefined;

        name = nameKey
          ? (properties[nameKey] !== undefined ? properties[nameKey] : metadata[nameKey]) ||
            dataPoint.name
          : dataPoint.name;

        // Ensure x and y values are numbers
        x = typeof x === 'number' ? x : parseFloat(String(x)) || 0;
        y = typeof y === 'number' ? y : parseFloat(String(y)) || 0;
        z = z && typeof z === 'number' ? z : z ? parseFloat(String(z)) || pointSize : pointSize;

        return {
          x,
          y,
          z,
          name,
          id: dataPoint.id,
          originalData: dataPoint,
        };
      }
      // Handle regular objects
      else {
        const record = item as Record<string, unknown>;

        x = record[xAxisKey];
        y = record[yAxisKey];
        z = zAxisKey ? record[zAxisKey] : undefined;

        name = nameKey ? record[nameKey] : undefined;

        // Ensure x and y values are numbers
        x = typeof x === 'number' ? x : parseFloat(String(x)) || 0;
        y = typeof y === 'number' ? y : parseFloat(String(y)) || 0;
        z = z && typeof z === 'number' ? z : z ? parseFloat(String(z)) || pointSize : pointSize;

        return {
          x,
          y,
          z,
          name: name || `(${x}, ${y})`,
          originalData: record,
        };
      }
    });
  }, [data, xAxisKey, yAxisKey, zAxisKey, nameKey, pointSize]);

  // If no data, show error
  if (!data || data.length === 0) {
    return (
      <BaseChart
        width={width}
        height={height}
        title={title}
        theme={theme}
        className={className}
        errorMessage={errorMessage || 'No data available'}
      >
        <RechartsScatterChart data={[]} />
      </BaseChart>
    );
  }

  // Calculate quadrant values if not provided
  const xValue =
    quadrantXValue !== undefined
      ? quadrantXValue
      : processedData.reduce((sum, item) => sum + item.x, 0) / processedData.length;

  const yValue =
    quadrantYValue !== undefined
      ? quadrantYValue
      : processedData.reduce((sum, item) => sum + item.y, 0) / processedData.length;

  // Handle click events
  const handlePointClick = (event: PointClickEvent) => {
    if (onElementClick && event && event.payload) {
      onElementClick(event.payload, 0);
    }
  };

  // Create the chart content
  const chartContent = (
    <RechartsScatterChart margin={{ top: 20, right: 20, bottom: 20, left: 20 }}>
      {showGrid && <CartesianGrid strokeDasharray="3 3" />}

      <XAxis
        type="number"
        dataKey="x"
        name={xAxisLabel || xAxisKey}
        label={
          xAxisLabel ? { value: xAxisLabel, position: 'insideBottom', offset: -10 } : undefined
        }
        tickFormatter={value => formatAxisTick(value, false)}
      />

      <YAxis
        type="number"
        dataKey="y"
        name={yAxisLabel || yAxisKey}
        label={yAxisLabel ? { value: yAxisLabel, angle: -90, position: 'insideLeft' } : undefined}
        tickFormatter={value => formatAxisTick(value, false)}
      />

      {zAxisKey && (
        <ZAxis type="number" dataKey="z" range={[zAxisSizeMin, zAxisSizeMax]} name={zAxisKey} />
      )}

      <Tooltip content={customTooltip || <DefaultTooltip />} cursor={{ strokeDasharray: '3 3' }} />

      {showLegend && <Legend />}

      {/* Reference lines for thresholds or important values */}
      {referenceLines.map((line, i) => (
        <ReferenceLine
          key={`ref-line-${i}`}
          x={line.axis === 'x' ? line.value : undefined}
          y={line.axis === 'y' ? line.value : undefined}
          stroke={line.color || '#ff7300'}
          label={
            line.label
              ? ({
                  value: line.label,
                  position: line.position || 'center',
                } as LabelProps)
              : undefined
          }
        />
      ))}

      {/* Quadrant reference lines */}
      {showQuadrants && (
        <>
          <ReferenceLine x={xValue} stroke="#666" strokeDasharray="3 3" />
          <ReferenceLine y={yValue} stroke="#666" strokeDasharray="3 3" />

          {/* Quadrant labels */}
          {quadrantLabels.topRight && (
            <ReferenceArea
              x1={xValue}
              x2="auto"
              y1={yValue}
              y2="auto"
              fillOpacity={0}
              label={{ value: quadrantLabels.topRight, position: 'insideTopRight' }}
            />
          )}

          {quadrantLabels.topLeft && (
            <ReferenceArea
              x1={0}
              x2={xValue}
              y1={yValue}
              y2="auto"
              fillOpacity={0}
              label={{ value: quadrantLabels.topLeft, position: 'insideTopLeft' }}
            />
          )}

          {quadrantLabels.bottomRight && (
            <ReferenceArea
              x1={xValue}
              x2="auto"
              y1={0}
              y2={yValue}
              fillOpacity={0}
              label={{ value: quadrantLabels.bottomRight, position: 'insideBottomRight' }}
            />
          )}

          {quadrantLabels.bottomLeft && (
            <ReferenceArea
              x1={0}
              x2={xValue}
              y1={0}
              y2={yValue}
              fillOpacity={0}
              label={{ value: quadrantLabels.bottomLeft, position: 'insideBottomLeft' }}
            />
          )}
        </>
      )}

      <Scatter
        name={`${xAxisLabel || xAxisKey} vs ${yAxisLabel || yAxisKey}`}
        data={processedData}
        fill={color}
        onClick={(e: PointClickEvent) => handlePointClick(e)}
        isAnimationActive={animate}
      />
    </RechartsScatterChart>
  );

  return (
    <BaseChart
      width={width}
      height={height}
      title={title}
      theme={theme}
      className={className}
      errorMessage={errorMessage}
    >
      {chartContent}
    </BaseChart>
  );
}

import { ComponentType, useEffect, useState } from 'react';
import { BaseChartProps } from './charts/BaseChart';

/**
 * Options for memory management behavior
 */
interface MemoryOptions {
  /** Show memory usage statistics */
  showMemoryStats?: boolean;

  /** Memory threshold in bytes */
  memoryThreshold?: number;

  /** Whether to log memory usage to console */
  enableLogging?: boolean;

  /** Auto cleanup level, controlling aggressiveness of cleanup */
  autoCleanupLevel?: 'none' | 'low' | 'medium' | 'high';

  /** Time in milliseconds for cache expiration */
  cacheExpirationTime?: number;
}

/**
 * Simple function to estimate data size
 */
function estimateSize(data: unknown): number {
  if (data === null || data === undefined) return 0;

  if (Array.isArray(data)) {
    return data.length * 1024; // Estimate 1KB per array item
  }

  if (typeof data === 'object') {
    return Object.keys(data).length * 1024; // Estimate 1KB per object property
  }

  return 1024; // Default size for primitives
}

/**
 * Higher-order component that adds memory management to visualization components
 */
export function withMemoryManagement<P extends BaseChartProps>(
  WrappedComponent: ComponentType<P>,
  options: MemoryOptions = {}
): ComponentType<P> {
  const ManagedComponent = (props: P) => {
    // Default options
    const {
      showMemoryStats = false,
      memoryThreshold = 50 * 1024 * 1024,
      enableLogging = false,
      autoCleanupLevel = 'medium',
      cacheExpirationTime = 5 * 60 * 1000,
    } = options;

    // Track memory usage
    const [memoryUsage, setMemoryUsage] = useState<number>(0);

    // Update memory usage when props change
    useEffect(() => {
      const size = estimateSize(props.data);
      setMemoryUsage(size);

      // Log memory usage if enabled
      if (enableLogging) {
        console.log(
          `[MemoryManager] Memory usage: ${Math.round(size / 1024 / 1024)}MB, Cleanup level: ${autoCleanupLevel}`
        );
      }
    }, [props.data, enableLogging, autoCleanupLevel]);

    // Calculate memory usage in MB for display
    const memoryMB = Math.round(memoryUsage / 1024 / 1024);

    // Check if memory usage exceeds threshold
    const isAboveThreshold = memoryUsage > memoryThreshold;

    return (
      <div style={{ position: 'relative', width: '100%', height: '100%' }}>
        <WrappedComponent {...props} />

        {showMemoryStats && (
          <div
            style={{
              position: 'absolute',
              bottom: 5,
              right: 5,
              fontSize: '0.75rem',
              opacity: 0.7,
              backgroundColor: isAboveThreshold ? 'rgba(255,200,200,0.8)' : 'rgba(200,200,200,0.8)',
              padding: '2px 5px',
              borderRadius: '3px',
            }}
          >
            Memory: {memoryMB}MB
          </div>
        )}
      </div>
    );
  };

  // Set display name for better debugging
  const wrappedName = WrappedComponent.displayName || WrappedComponent.name || 'Component';
  ManagedComponent.displayName = `WithMemory(${wrappedName})`;

  return ManagedComponent;
}

export default withMemoryManagement;

