import * as React from 'react';
import { useEffect, useRef, useState } from 'react';
import { FixedSizeList as List } from 'react-window';
import { useEventCategorySubscription } from '../../hooks/events/useEventSubscription';
import { EventBus } from '../../lib/events/EventBus';
import { ModuleEvent, moduleEventBus } from '../../lib/events/ModuleEventBus';
import { EventCategory, EventType } from '../../types/events/EventTypes';
import { ResourceType } from '../../types/resources/ResourceTypes';

// Resource-related event types to monitor
const RESOURCE_EVENT_TYPES = [
  EventType.RESOURCE_PRODUCED,
  EventType.RESOURCE_CONSUMED,
  EventType.RESOURCE_TRANSFERRED,
  EventType.RESOURCE_PRODUCTION_REGISTERED,
  EventType.RESOURCE_CONSUMPTION_REGISTERED,
];

interface ResourceEventData {
  resourceType: ResourceType;
  amount: number;
  source?: string;
  target?: string;
}

// Type guard for resource events
function isResourceEvent(event: ModuleEvent): event is ModuleEvent & { data: ResourceEventData } {
  if (!event.data) return false;
  const data = event.data as Partial<ResourceEventData>;
  return (
    'resourceType' in data &&
    'amount' in data &&
    typeof data.resourceType === 'string' &&
    typeof data.amount === 'number'
  );
}

interface ResourceEventLog {
  id: string;
  type: EventType;
  moduleId: string;
  timestamp: number;
  resourceType: ResourceType;
  amount: number;
  source?: string;
  target?: string;
}

/**
 * Component that monitors and displays resource-related events in real-time.
 * Uses the standardized event system with proper type safety and event filtering.
 * Uses virtualization for efficient rendering of large event logs.
 */
export const ResourceEventMonitor: React.FC = () => {
  const [eventLogs, setEventLogs] = useState<ResourceEventLog[]>([]);
  const [filter, setFilter] = useState<string>('');
  const listRef = useRef<List>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const [containerSize, setContainerSize] = useState({ width: 0, height: 0 });

  // Subscribe to all resource events using category subscription
  const { latestEvents, receivedCount } = useEventCategorySubscription(
    moduleEventBus as unknown as EventBus<ModuleEvent>,
    EventCategory.RESOURCE,
    (event: ModuleEvent) => {
      if (isResourceEvent(event)) {
        addEventToLog({
          id: `${event.type}-${event.moduleId}-${event.timestamp}`,
          type: event.type,
          moduleId: event.moduleId,
          timestamp: event.timestamp,
          resourceType: event.data.resourceType,
          amount: event.data.amount,
          source: event.data.source,
          target: event.data.target,
        });
      }
    },
    {
      trackLatest: true,
      filter: (event: ModuleEvent) => RESOURCE_EVENT_TYPES.includes(event.type as EventType),
    }
  );

  // Helper function to add events to the log
  const addEventToLog = (eventLog: ResourceEventLog) => {
    setEventLogs(prevLogs => {
      const newLogs = [eventLog, ...prevLogs];
      return newLogs.slice(0, 1000);
    });

    // Scroll to top when new events come in
    if (listRef.current) {
      listRef.current.scrollTo(0);
    }
  };

  // Clear logs
  const clearLogs = () => {
    setEventLogs([]);
  };

  // Filter logs based on user input
  const filteredLogs = filter
    ? eventLogs.filter(
        log =>
          log.type.toLowerCase().includes(filter.toLowerCase()) ||
          log.moduleId.toLowerCase().includes(filter.toLowerCase()) ||
          log.resourceType.toLowerCase().includes(filter.toLowerCase())
      )
    : eventLogs;

  // Measure container size for the virtualized list
  useEffect(() => {
    if (containerRef.current) {
      const resizeObserver = new ResizeObserver(entries => {
        for (const entry of entries) {
          const { width, height } = entry.contentRect;
          setContainerSize({ width, height });
        }
      });

      resizeObserver.observe(containerRef.current);

      // Initialize size
      setContainerSize({
        width: containerRef.current.clientWidth,
        height: containerRef.current.clientHeight,
      });

      return () => {
        resizeObserver.disconnect();
      };
    }
  }, []);

  // Row renderer for the virtualized list
  const Row = ({ index, style }: { index: number; style: React.CSSProperties }) => {
    const log = filteredLogs[index];
    return (
      <div style={style} className="flex border-b border-gray-700 hover:bg-gray-600">
        <div className="min-w-[100px] flex-1 px-4 py-2">
          {new Date(log.timestamp).toLocaleTimeString()}
        </div>
        <div className="min-w-[150px] flex-1 px-4 py-2">{log.type}</div>
        <div className="min-w-[120px] flex-1 px-4 py-2">{log.moduleId}</div>
        <div className="min-w-[100px] flex-1 px-4 py-2">{log.resourceType}</div>
        <div className="min-w-[80px] flex-1 px-4 py-2">{log.amount.toFixed(2)}</div>
      </div>
    );
  };

  // Table header for the virtualized list
  const TableHeader = () => (
    <div className="flex bg-gray-700 text-xs uppercase">
      <div className="min-w-[100px] flex-1 px-4 py-2">Time</div>
      <div className="min-w-[150px] flex-1 px-4 py-2">Event Type</div>
      <div className="min-w-[120px] flex-1 px-4 py-2">Module</div>
      <div className="min-w-[100px] flex-1 px-4 py-2">Resource</div>
      <div className="min-w-[80px] flex-1 px-4 py-2">Amount</div>
    </div>
  );

  return (
    <div className="rounded-lg bg-gray-800 p-4 shadow-lg">
      <div className="mb-4 flex items-center justify-between">
        <h2 className="text-xl font-semibold text-white">Resource Event Monitor</h2>
        <div className="flex space-x-2">
          <input
            type="text"
            placeholder="Filter events..."
            className="rounded border border-gray-600 bg-gray-700 px-3 py-1 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={filter}
            onChange={e => setFilter(e.target.value)}
          />
          <button
            onClick={clearLogs}
            className="rounded bg-red-600 px-3 py-1 text-white hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500"
          >
            Clear
          </button>
        </div>
      </div>

      <div ref={containerRef} className="h-96 w-full">
        {filteredLogs.length === 0 ? (
          <div className="flex h-full items-center justify-center py-8 text-center text-gray-400">
            No resource events recorded yet
          </div>
        ) : (
          <div className="text-left text-sm text-gray-300">
            <TableHeader />
            <List
              ref={listRef}
              className="text-left text-sm text-gray-300"
              height={containerSize.height - 30} // Subtract header height
              width={containerSize.width}
              itemCount={filteredLogs.length}
              itemSize={40} // Height of each row
            >
              {Row}
            </List>
          </div>
        )}
      </div>

      <div className="mt-2 text-xs text-gray-500">
        Displaying {filteredLogs.length} events (virtualized for performance)
      </div>
    </div>
  );
};

import * as React from "react";
import { useEffect, useState } from 'react';
import { z } from 'zod';
import {
  ConfigItem,
  ConfigValidationError,
  createConfigItem,
  createConfigManager,
  createFeatureFlag,
  FeatureFlag,
  FeatureStatus,
  useFeatureFlag,
  useTypedConfig,
} from '../../../types/config/TypeSafeConfig';

// Create schema definitions for our config items
const themeSchema = z.enum(['light', 'dark', 'system']);
const pageSizeSchema = z.number().int().min(5).max(100);
const apiEndpointSchema = z.string().url();
const cacheTTLSchema = z.number().int().min(0).max(86400);
const loggingLevelSchema = z.enum(['debug', 'info', 'warn', 'error']);
const notificationSchema = z.object({
  enabled: z.boolean(),
  sound: z.boolean().optional(),
  desktop: z.boolean().optional(),
  frequency: z.enum(['immediately', 'batched', 'daily']).optional(),
});

// Define our config items with schemas
const configItems = [
  createConfigItem('theme', themeSchema, 'system', {
    name: 'Theme',
    description: 'Application color theme',
    category: 'appearance',
    tags: ['ui', 'appearance'],
  }),
  createConfigItem('pageSize', pageSizeSchema, 20, {
    name: 'Page Size',
    description: 'Number of items to display per page',
    category: 'appearance',
    tags: ['ui', 'pagination'],
  }),
  createConfigItem('apiEndpoint', apiEndpointSchema, 'https://api.example.com/v1', {
    name: 'API Endpoint',
    description: 'Base URL for API requests',
    category: 'api',
    tags: ['api', 'connection'],
  }),
  createConfigItem('cacheTTL', cacheTTLSchema, 3600, {
    name: 'Cache TTL',
    description: 'Time to live for cached data in seconds',
    category: 'performance',
    tags: ['cache', 'performance'],
  }),
  createConfigItem('loggingLevel', loggingLevelSchema, 'info', {
    name: 'Logging Level',
    description: 'Minimum level for log messages',
    category: 'debugging',
    tags: ['logs', 'debugging'],
  }),
  createConfigItem(
    'notifications',
    notificationSchema,
    { enabled: true, sound: true, frequency: 'immediately' },
    {
      name: 'Notifications',
      description: 'Notification settings',
      category: 'notifications',
      tags: ['notifications', 'alerts'],
    }
  ),
];

// Define feature flags
const featureFlags = [
  createFeatureFlag('newDashboard', false, {
    name: 'New Dashboard',
    description: 'Enable the new dashboard interface',
    status: FeatureStatus.PREVIEW,
    targeting: {
      userRoles: ['admin', 'beta-tester'],
      percentageRollout: 20,
    },
  }),
  createFeatureFlag('advancedCharts', false, {
    name: 'Advanced Charts',
    description: 'Enable advanced chart visualizations',
    status: FeatureStatus.EXPERIMENTAL,
    targeting: {
      userRoles: ['admin', 'data-analyst'],
      environments: ['development', 'staging'],
    },
  }),
  createFeatureFlag('bulkOperations', true, {
    name: 'Bulk Operations',
    description: 'Enable bulk operations in list views',
    status: FeatureStatus.ENABLED,
  }),
  createFeatureFlag('aiSuggestions', false, {
    name: 'AI Suggestions',
    description: 'Enable AI-powered suggestions',
    status: FeatureStatus.BETA,
    targeting: {
      percentageRollout: 10,
      dateRange: {
        start: '2023-06-01',
        end: '2023-12-31',
      },
    },
  }),
  createFeatureFlag('legacyExport', true, {
    name: 'Legacy Export',
    description: 'Enable legacy export functionality',
    status: FeatureStatus.DEPRECATED,
  }),
];

// Define categories
const categories = [
  {
    id: 'appearance',
    name: 'Appearance',
    description: 'Visual appearance settings',
    items: [],
  },
  {
    id: 'api',
    name: 'API',
    description: 'API connection settings',
    items: [],
  },
  {
    id: 'performance',
    name: 'Performance',
    description: 'Performance optimization settings',
    items: [],
  },
  {
    id: 'debugging',
    name: 'Debugging',
    description: 'Debugging and logging settings',
    items: [],
  },
  {
    id: 'notifications',
    name: 'Notifications',
    description: 'Notification settings',
    items: [],
  },
];

// Create a shared config manager instance
const configManager = createConfigManager({
  validateOnAccess: true,
  logErrors: true,
  onValidationError: errors => {
    console.warn('Config validation errors:', errors);
  },
  onConfigChange: (key, newValue, oldValue) => {
    console.warn(`Config changed: ${key}`, { oldValue, newValue });
  },
});

// Initialize the config manager
categories.forEach(category => configManager.registerCategory(category));
configManager.registerConfigs(configItems);
featureFlags.forEach(flag => configManager.registerFeature(flag));

// User roles for demo
const userRoles = ['user', 'admin', 'beta-tester', 'data-analyst'];
const environments = ['development', 'staging', 'production'];

const TypeSafeConfigDemo: React.FC = () => {
  const [validationErrors, setValidationErrors] = useState<ConfigValidationError[]>([]);
  const [selectedRole, setSelectedRole] = useState<string>('user');
  const [selectedEnvironment, setSelectedEnvironment] = useState<string>('production');
  const [userId, setUserId] = useState<string>('user-123');
  const [configValues, setConfigValues] = useState<Record<string, unknown>>({});
  const [featureValues, setFeatureValues] = useState<Record<string, boolean>>({});
  const [selectedCategory, setSelectedCategory] = useState<string>('appearance');
  const [selectedConfig, setSelectedConfig] = useState<ConfigItem | null>(null);
  const [editValue, setEditValue] = useState<string>('');
  const [showExport, setShowExport] = useState(false);
  const [exportedConfig, setExportedConfig] = useState('');

  // Set user context whenever role, environment, or user ID changes
  useEffect(() => {
    configManager.setUserContext({
      role: selectedRole,
      environment: selectedEnvironment,
      id: userId,
    });

    // Update feature values
    setFeatureValues(configManager.exportFeatures());
  }, [selectedRole, selectedEnvironment, userId]);

  // Initial load of config values
  useEffect(() => {
    setConfigValues(configManager.exportConfig());
  }, []);

  // Handle config item selection
  const handleSelectConfig = (item: ConfigItem) => {
    setSelectedConfig(item);
    // Get current value and set as edit value
    const value = configManager.get(item.key);
    setEditValue(typeof value === 'object' ? JSON.stringify(value, null, 2) : String(value));
  };

  // Handle value change
  const handleSaveValue = () => {
    if (!selectedConfig) return;

    // Parse the value based on the schema type
    let parsedValue: unknown;
    try {
      if (selectedConfig.schema instanceof z.ZodObject) {
        parsedValue = JSON.parse(editValue);
      } else if (selectedConfig.schema instanceof z.ZodNumber) {
        parsedValue = Number(editValue);
      } else if (selectedConfig.schema instanceof z.ZodBoolean) {
        parsedValue = editValue === 'true';
      } else {
        parsedValue = editValue;
      }

      // Update the config
      const result = configManager.set(selectedConfig.key, parsedValue);
      if (!result.valid) {
        setValidationErrors(result.errors);
      } else {
        setValidationErrors([]);
        setConfigValues(configManager.exportConfig());
      }
    } catch (err) {
      setValidationErrors([
        {
          key: selectedConfig.key,
          message: 'Invalid format: ' + (err instanceof Error ? err.message : String(err)),
        },
      ]);
    }
  };

  // Export config
  const handleExport = () => {
    const config = {
      settings: configManager.exportConfig(),
      features: configManager.exportFeatures(),
    };
    setExportedConfig(JSON.stringify(config, null, 2));
    setShowExport(true);
  };

  // Import config
  const handleImport = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = e => {
      try {
        const content = e.target?.result as string;
        const config = JSON.parse(content);

        if (config.settings) {
          const result = configManager.importConfig(config.settings);
          if (!result.valid) {
            setValidationErrors(result.errors);
          } else {
            setValidationErrors([]);
            setConfigValues(configManager.exportConfig());
            setFeatureValues(configManager.exportFeatures());
          }
        }
      } catch (err) {
        setValidationErrors([
          {
            key: 'import',
            message: 'Invalid import file: ' + (err instanceof Error ? err.message : String(err)),
          },
        ]);
      }
    };
    reader.readAsText(file);
  };

  // Render feature flag status display
  const renderFeatureStatus = (flag: FeatureFlag) => {
    const isEnabled = featureValues[flag.key] || false;
    return (
      <div className={`feature-flag ${isEnabled ? 'enabled' : 'disabled'}`} key={flag.key}>
        <h4>{flag.name}</h4>
        <div className="feature-status">
          <span className={`status-badge ${flag.status.toLowerCase()}`}>{flag.status}</span>
          <span className={`enabled-badge ${isEnabled ? 'enabled' : 'disabled'}`}>
            {isEnabled ? 'ENABLED' : 'DISABLED'}
          </span>
        </div>
        <p>{flag.description}</p>
        {flag.targeting && (
          <div className="targeting-info">
            {flag.targeting.userRoles && (
              <div className="targeting-detail">
                <span>Roles:</span> {flag.targeting.userRoles.join(', ')}
              </div>
            )}
            {flag.targeting.environments && (
              <div className="targeting-detail">
                <span>Environments:</span> {flag.targeting.environments.join(', ')}
              </div>
            )}
            {flag.targeting.percentageRollout !== undefined && (
              <div className="targeting-detail">
                <span>Rollout:</span> {flag.targeting.percentageRollout}%
              </div>
            )}
          </div>
        )}
      </div>
    );
  };

  // Example usage of the type-safe hooks
  const theme = useTypedConfig<typeof themeSchema>(configManager, 'theme', 'system');
  const pageSize = useTypedConfig<typeof pageSizeSchema>(configManager, 'pageSize', 20);
  const newDashboardEnabled = useFeatureFlag(configManager, 'newDashboard');

  return (
    <div className={`config-demo theme-${theme}`}>
      <h1>Type-Safe Configuration Demo</h1>

      <div className="demo-layout">
        <aside className="sidebar">
          <div className="user-context">
            <h3>User Context</h3>
            <div className="form-group">
              <label htmlFor="user-id">User ID:</label>
              <input
                id="user-id"
                type="text"
                value={userId}
                onChange={e => setUserId(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label htmlFor="user-role">User Role:</label>
              <select
                id="user-role"
                value={selectedRole}
                onChange={e => setSelectedRole(e.target.value)}
              >
                {userRoles.map(role => (
                  <option key={role} value={role}>
                    {role}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="environment">Environment:</label>
              <select
                id="environment"
                value={selectedEnvironment}
                onChange={e => setSelectedEnvironment(e.target.value)}
              >
                {environments.map(env => (
                  <option key={env} value={env}>
                    {env}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="categories">
            <h3>Categories</h3>
            <ul>
              {categories.map(category => (
                <li
                  key={category.id}
                  className={selectedCategory === category.id ? 'active' : ''}
                  onClick={() => setSelectedCategory(category.id)}
                >
                  {category.name}
                </li>
              ))}
            </ul>
          </div>

          <div className="actions">
            <button onClick={handleExport}>Export Configuration</button>
            <div className="import-container">
              <label htmlFor="import-file" className="import-label">
                Import Configuration
              </label>
              <input
                id="import-file"
                type="file"
                accept=".json"
                onChange={handleImport}
                className="import-input"
              />
            </div>
          </div>
        </aside>

        <main className="main-content">
          <div className="config-section">
            <h2>Configuration</h2>

            <div className="config-items">
              {configItems
                .filter(item => item.category === selectedCategory)
                .map(item => (
                  <div
                    key={item.key}
                    className={`config-item ${selectedConfig?.key === item.key ? 'selected' : ''}`}
                    onClick={() => handleSelectConfig(item)}
                  >
                    <h4>{item.name}</h4>
                    <p>{item.description}</p>
                    <div className="config-value">
                      {typeof configValues[item.key] === 'object'
                        ? JSON.stringify(configValues[item.key])
                        : String(configValues[item.key])}
                    </div>
                    <div className="config-tags">
                      {item.tags?.map(tag => (
                        <span key={tag} className="tag">
                          {tag}
                        </span>
                      ))}
                    </div>
                  </div>
                ))}
            </div>
          </div>

          {selectedConfig && (
            <div className="config-editor">
              <h3>Edit {selectedConfig.name}</h3>

              <div className="editor-form">
                <div className="form-group">
                  <label htmlFor="config-value">Value:</label>
                  {selectedConfig.schema instanceof z.ZodObject ? (
                    <textarea
                      id="config-value"
                      value={editValue}
                      onChange={e => setEditValue(e.target.value)}
                      rows={10}
                    />
                  ) : selectedConfig.schema instanceof z.ZodEnum ? (
                    <select
                      id="config-value"
                      value={editValue}
                      onChange={e => setEditValue(e.target.value)}
                    >
                      {(selectedConfig.schema as any)._def.values.map((val: string) => (
                        <option key={val} value={val}>
                          {val}
                        </option>
                      ))}
                    </select>
                  ) : (
                    <input
                      id="config-value"
                      type={selectedConfig.schema instanceof z.ZodNumber ? 'number' : 'text'}
                      value={editValue}
                      onChange={e => setEditValue(e.target.value)}
                    />
                  )}
                </div>

                <div className="editor-actions">
                  <button onClick={handleSaveValue}>Save</button>
                  <button onClick={() => setSelectedConfig(null)}>Cancel</button>
                </div>
              </div>

              {validationErrors.length > 0 && (
                <div className="validation-errors">
                  <h4>Validation Errors</h4>
                  <ul>
                    {validationErrors.map((error, index) => (
                      <li key={index}>
                        {error.key}: {error.message}
                        {error.path && <span> (Path: {error.path.join('.')})</span>}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          )}

          <div className="feature-section">
            <h2>Feature Flags</h2>
            <div className="feature-grid">
              {featureFlags.map(flag => renderFeatureStatus(flag))}
            </div>
          </div>

          <div className="demo-output">
            <h2>Active Configuration Demo</h2>
            <div className="output-example">
              <div className="example-item">
                <h4>Theme Setting</h4>
                <div className="example-value">{theme}</div>
                <div className="example-code">
                  <pre>
                    useTypedConfig&lt;typeof themeSchema&gt;(configManager, 'theme', 'system')
                  </pre>
                </div>
              </div>

              <div className="example-item">
                <h4>Page Size Setting</h4>
                <div className="example-value">{pageSize}</div>
                <div className="example-code">
                  <pre>
                    useTypedConfig&lt;typeof pageSizeSchema&gt;(configManager, 'pageSize', 20)
                  </pre>
                </div>
              </div>

              <div className="example-item">
                <h4>New Dashboard Feature Flag</h4>
                <div className="example-value">{newDashboardEnabled ? 'Enabled' : 'Disabled'}</div>
                <div className="example-code">
                  <pre>useFeatureFlag(configManager, 'newDashboard')</pre>
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>

      {showExport && (
        <div className="modal-overlay">
          <div className="export-modal">
            <h3>Exported Configuration</h3>
            <pre className="export-content">{exportedConfig}</pre>
            <div className="modal-actions">
              <button
                onClick={() => {
                  navigator.clipboard.writeText(exportedConfig);
                  alert('Copied to clipboard!');
                }}
              >
                Copy to Clipboard
              </button>
              <button onClick={() => setShowExport(false)}>Close</button>
            </div>
          </div>
        </div>
      )}

      <style jsx>{`
        .config-demo {
          font-family:
            system-ui,
            -apple-system,
            BlinkMacSystemFont,
            'Segoe UI',
