import * as d3 from 'd3';
import * as React from "react";
import { useEffect, useRef } from 'react';
import {
  AnimationConfig,
  createTypedTimer,
  TypedAnimationSequence,
  typedInterpolators,
} from '../../../types/visualizations/D3AnimationTypes';
import { selectSvg } from '../../../types/visualizations/D3SelectionTypes';

interface Point {
  x: number;
  y: number;
  radius: number;
  color: string;
}

interface AnimationTypeSafetyDemoProps {
  width?: number;
  height?: number;
  animationConfig?: Partial<AnimationConfig>;
}

/**
 * Demo component showcasing the type-safe animation utilities
 *
 * This component demonstrates:
 * 1. Type-safe interpolation
 * 2. Type-safe transition configuration
 * 3. Type-safe timer usage
 * 4. Animation sequences with proper typing
 */
const AnimationTypeSafetyDemo: React.FC<AnimationTypeSafetyDemoProps> = ({
  width = 600,
  height = 400,
  animationConfig = {},
}) => {
  const svgRef = useRef<SVGSVGElement>(null);
  const config: AnimationConfig = {
    duration: 1500,
    easing: d3.easeCubicInOut,
    loop: true,
    loopDelay: 500,
    ...animationConfig,
  };

  // Setup point data for animation
  const pointsData: Point[] = [
    { x: 100, y: 100, radius: 20, color: '#E63946' },
    { x: 300, y: 150, radius: 30, color: '#457B9D' },
    { x: 500, y: 200, radius: 25, color: '#2A9D8F' },
    { x: 200, y: 250, radius: 35, color: '#F4A261' },
    { x: 400, y: 300, radius: 15, color: '#6D597A' },
  ];

  // Animation point data targets (for interpolation)
  const targetData: Point[] = [
    { x: 150, y: 200, radius: 35, color: '#E76F51' },
    { x: 250, y: 100, radius: 15, color: '#264653' },
    { x: 350, y: 300, radius: 40, color: '#1D3557' },
    { x: 450, y: 200, radius: 20, color: '#F1FAEE' },
    { x: 200, y: 150, radius: 30, color: '#E9C46A' },
  ];

  useEffect(() => {
    if (!svgRef.current) return;

    // Clear any existing elements
    d3.select(svgRef.current).selectAll('*').remove();

    // Create SVG container with proper typing
    const svg = selectSvg(`#animation-demo-svg`);

    // Add circles for each data point
    const circles = svg
      .selectAll<SVGCircleElement, Point>('circle')
      .data(pointsData)
      .enter()
      .append('circle')
      .attr('cx', d => d.x)
      .attr('cy', d => d.y)
      .attr('r', d => d.radius)
      .attr('fill', d => d.color);

    // Setup animation sequence using type-safe utilities
    const animationSequence = new TypedAnimationSequence({
      transitions: [
        {
          selection: circles,
          duration: config.duration,
          easing: config.easing,
          delay: (_, i) => i * 100, // Staggered delay based on index
        },
        {
          selection: circles,
          duration: config.duration,
          easing: d3.easeElasticOut,
        },
      ],
      sequenceDelay: 500,
      loop: config.loop,
    });

    // Create object interpolators for each data point
    const interpolators = pointsData.map((startPoint, index) => {
      return {
        position: typedInterpolators.object<Pick<Point, 'x' | 'y'>>(
          { x: startPoint.x, y: startPoint.y },
          { x: targetData[index].x, y: targetData[index].y }
        ),
        radius: typedInterpolators.number(startPoint.radius, targetData[index].radius),
        color: typedInterpolators.color(startPoint.color, targetData[index].color),
      };
    });

    // Create a type-safe timer for smooth animation
    const timer = createTypedTimer({
      callback: elapsed => {
        // Calculate progress based on elapsed time (ping-pong effect)
        const totalDuration = config.duration * 2;
        const normalizedTime = (elapsed % totalDuration) / config.duration;
        const t = normalizedTime <= 1 ? normalizedTime : 2 - normalizedTime;

        // Update each circle with interpolated values
        circles.each(function (d, i) {
          const point = interpolators[i];
          const interpolatedPosition = point.position(t);
          const interpolatedRadius = point.radius(t);
          const interpolatedColor = point.color(t);

          d3.select(this)
            .attr('cx', interpolatedPosition.x)
            .attr('cy', interpolatedPosition.y)
            .attr('r', interpolatedRadius)
            .attr('fill', interpolatedColor);
        });

        // Continue animation if we're looping
        return !config.loop && elapsed >= totalDuration;
      },
      duration: config.loop ? undefined : config.duration * 2,
    });

    // Add labels to show interpolation t value
    const label = svg
      .append('text')
      .attr('x', width / 2)
      .attr('y', 30)
      .attr('text-anchor', 'middle')
      .attr('font-size', '14px')
      .attr('fill', '#333');

    // Update label with current interpolation value
    createTypedTimer({
      callback: elapsed => {
        const totalDuration = config.duration * 2;
        const normalizedTime = (elapsed % totalDuration) / config.duration;
        const t = normalizedTime <= 1 ? normalizedTime : 2 - normalizedTime;

        label.text(`Interpolation t: ${t.toFixed(2)}`);
        return false;
      },
    });

    // Cleanup function
    return () => {
      timer.stop();
    };
  }, [width, height, config]);

  return (
    <div className="animation-type-safety-demo">
      <h3>Animation Type Safety Demo</h3>
      <svg
        id="animation-demo-svg"
        ref={svgRef}
        width={width}
        height={height}
        style={{
          border: '1px solid #ccc',
          borderRadius: '4px',
          background: '#f7f7f7',
        }}
      />
      <div className="demo-description">
        <p>This demo showcases the type-safe animation utilities:</p>
        <ul>
          <li>Strong typing for interpolators (position, radius, color)</li>
          <li>Type-safe timer configuration and transitions</li>
          <li>Animation sequences with proper event handling</li>
        </ul>
      </div>
    </div>
  );
};

export default AnimationTypeSafetyDemo;

import * as React from "react";
import { useState } from 'react';
import { useChartCoordination } from '../../../hooks/visualization/useChartCoordination';
import {
  BrushState,
  HighlightState,
  ViewportState,
} from '../../../lib/visualization/ChartCoordinationManager';

interface ChartCoordinationDemoProps {
  width?: number;
  height?: number;
  className?: string;
}

/**
 * ChartCoordinationDemo
 *
 * A demo component that shows how multiple charts can be coordinated for:
 * - Synchronized zooming/panning
 * - Linked brushing
 * - Synchronized highlighting
 * - Shared color scales
 */
export const ChartCoordinationDemo: React.FC<ChartCoordinationDemoProps> = ({
  width = 1200,
  height = 800,
  className = '',
}) => {
  // Sample data
  const data = React.useMemo(() => {
    const points = [];
    for (let i = 0; i < 100; i++) {
      points.push({
        id: `point-${i}`,
        x: Math.random() * 100,
        y: Math.random() * 100,
        category: Math.random() > 0.5 ? 'A' : 'B',
        value: Math.random() * 100,
      });
    }
    return points;
  }, []);

  // Chart states
  const [scatterViewport, setScatterViewport] = useState<ViewportState>({
    scale: 1,
    translateX: 0,
    translateY: 0,
  });
  const [scatterBrush, setScatterBrush] = useState<BrushState>({
    active: false,
    x1: 0,
    y1: 0,
    x2: 0,
    y2: 0,
  });
  const [scatterHighlight, setScatterHighlight] = useState<HighlightState>({
    active: false,
    dataIds: [],
  });

  const [barViewport, setBarViewport] = useState<ViewportState>({
    scale: 1,
    translateX: 0,
    translateY: 0,
  });
  const [barHighlight, setBarHighlight] = useState<HighlightState>({
    active: false,
    dataIds: [],
  });

  // Set up chart coordination
  const scatterCoordination = useChartCoordination({
    chartId: 'scatter-plot',
    groupId: 'demo-group',
    initialState: {
      viewport: scatterViewport,
      brush: scatterBrush,
      highlight: scatterHighlight,
    },
    onViewportChange: setScatterViewport,
    onBrushChange: setScatterBrush,
    onHighlightChange: setScatterHighlight,
  });

  const barCoordination = useChartCoordination({
    chartId: 'bar-chart',
    groupId: 'demo-group',
    initialState: {
      viewport: barViewport,
      highlight: barHighlight,
    },
    onViewportChange: setBarViewport,
    onHighlightChange: setBarHighlight,
  });

  // Handle interactions
  const handleScatterZoom = (scale: number, translateX: number, translateY: number) => {
    scatterCoordination.updateViewport({ scale, translateX, translateY });
  };

  const handleScatterBrush = (x1: number, y1: number, x2: number, y2: number) => {
    // Find points within brush
    const selectedPoints = data.filter(
      point =>
        point.x >= Math.min(x1, x2) &&
        point.x <= Math.max(x1, x2) &&
        point.y >= Math.min(y1, y2) &&
        point.y <= Math.max(y1, y2)
    );

    scatterCoordination.updateBrush({
      active: true,
      x1,
      y1,
      x2,
      y2,
    });

    scatterCoordination.updateHighlight({
      active: true,
      dataIds: selectedPoints.map(p => p.id),
    });
  };

  const handleBarHighlight = (category: string) => {
    const selectedPoints = data.filter(point => point.category === category);

    barCoordination.updateHighlight({
      active: true,
      dataIds: selectedPoints.map(p => p.id),
      category,
    });
  };

  return (
    <div className={`chart-coordination-demo ${className}`}>
      <div className="flex flex-col gap-4">
        <div className="flex gap-4">
          {/* Scatter Plot */}
          <div className="flex-1 rounded-lg border p-4 shadow-sm">
            <h3 className="mb-2 text-lg font-bold">Scatter Plot</h3>
            <div
              className="relative"
              style={{
                width: width / 2 - 32,
                height: height - 200,
                transform: `scale(${scatterViewport.scale}) translate(${scatterViewport.translateX}px, ${scatterViewport.translateY}px)`,
              }}
            >
              {/* Plot points */}
              {data.map(point => (
                <div
                  key={point.id}
                  className={`absolute h-3 w-3 rounded-full transition-opacity ${
                    scatterHighlight.active && !scatterHighlight.dataIds.includes(point.id)
                      ? 'opacity-20'
                      : 'opacity-100'
                  }`}
                  style={{
                    left: `${point.x}%`,
                    top: `${point.y}%`,
                    backgroundColor: point.category === 'A' ? '#3B82F6' : '#EF4444',
                    transform: 'translate(-50%, -50%)',
                  }}
                />
              ))}

              {/* Brush overlay */}
              {scatterBrush.active && (
                <div
                  className="absolute border-2 border-blue-500 bg-blue-500/10"
                  style={{
                    left: `${Math.min(scatterBrush.x1, scatterBrush.x2)}%`,
                    top: `${Math.min(scatterBrush.y1, scatterBrush.y2)}%`,
                    width: `${Math.abs(scatterBrush.x2 - scatterBrush.x1)}%`,
                    height: `${Math.abs(scatterBrush.y2 - scatterBrush.y1)}%`,
                  }}
                />
              )}
            </div>
          </div>

          {/* Bar Chart */}
          <div className="flex-1 rounded-lg border p-4 shadow-sm">
            <h3 className="mb-2 text-lg font-bold">Bar Chart</h3>
            <div
              className="relative"
              style={{
                width: width / 2 - 32,
                height: height - 200,
                transform: `scale(${barViewport.scale}) translate(${barViewport.translateX}px, ${barViewport.translateY}px)`,
              }}
            >
              {/* Category bars */}
              {['A', 'B'].map(category => {
                const categoryPoints = data.filter(p => p.category === category);
                const average =
                  categoryPoints.reduce((sum, p) => sum + p.value, 0) / categoryPoints.length;

                return (
                  <div
                    key={category}
                    className={`absolute bottom-0 w-32 transition-opacity ${
                      barHighlight.active && barHighlight.category !== category
                        ? 'opacity-20'
                        : 'opacity-100'
                    }`}
                    style={{
                      left: category === 'A' ? '30%' : '60%',
                      height: `${average}%`,
                      backgroundColor: category === 'A' ? '#3B82F6' : '#EF4444',
                    }}
                    onClick={() => handleBarHighlight(category)}
                  >
                    <div className="absolute -top-6 w-full text-center">{category}</div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>

        <div className="rounded-lg border p-4 shadow-sm">
          <h3 className="mb-2 text-lg font-bold">Controls</h3>
          <div className="flex gap-4">
            <div>
              <label className="block font-medium">Zoom</label>
              <input
                type="range"
                min="0.5"
                max="2"
                step="0.1"
                value={scatterViewport.scale}
                onChange={e => handleScatterZoom(parseFloat(e.target.value), 0, 0)}
                className="w-48"
              />
            </div>
            <button
              className="rounded bg-blue-500 px-4 py-2 text-white"
              onClick={() => {
                scatterCoordination.updateBrush({
                  active: false,
                  x1: 0,
                  y1: 0,
                  x2: 0,
                  y2: 0,
                });
                scatterCoordination.updateHighlight({
                  active: false,
                  dataIds: [],
                });
                barCoordination.updateHighlight({
                  active: false,
                  dataIds: [],
                });
              }}
            >
              Reset Selection
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChartCoordinationDemo;

import * as React from "react";
import { useEffect, useRef, useState } from 'react';
import { useComponentLifecycle } from '../../../hooks/ui/useComponentLifecycle';
import { useComponentRegistration } from '../../../hooks/ui/useComponentRegistration';
import { ParticleSystemManager } from '../../../managers/effects/ParticleSystemManager';
import { Position } from '../../../types/core/Position';
import { ResourceType } from "./../../../types/resources/ResourceTypes";

export interface DataPoint {
  id: string;
  position: Position;
  value: number;
  resourceType?: ResourceType;
  size?: number;
  opacity?: number;
}

interface DataTransitionConfig {
  sourceData: DataPoint[];
  targetData: DataPoint[];
  duration?: number;
  easing?: (t: number) => number;
  staggerDelay?: number;
  trailEffect?: boolean;
  blendMode?: 'normal' | 'additive';
  onTransitionComplete?: () => void;
  onTransitionProgress?: (progress: number) => void;
}

interface DataTransitionParticleSystemProps {
  width: number;
  height: number;
  quality?: 'low' | 'medium' | 'high';
  className?: string;
  sourceData: DataPoint[];
  targetData: DataPoint[];
  onTransitionComplete?: () => void;
  onTransitionProgress?: (progress: number) => void;
  duration?: number;
  easing?: (t: number) => number;
  staggerDelay?: number;
  trailEffect?: boolean;
  blendMode?: 'normal' | 'additive';
}

/**
 * DataTransitionParticleSystem
 *
 * A specialized particle system for animating data transitions in visualizations.
 * Extends the base ParticleSystemManager with data-specific transition features.
 */
export const DataTransitionParticleSystem: React.FC<DataTransitionParticleSystemProps> = ({
  width,
  height,
  quality = 'medium',
  className = '',
  sourceData,
  targetData,
  onTransitionComplete,
  onTransitionProgress,
  duration = 1000,
  easing = t => t,
  staggerDelay = 20,
  trailEffect = false,
  blendMode = 'additive',
}) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const particleSystemRef = useRef<ParticleSystemManager | null>(null);
  const [isInitialized, setIsInitialized] = useState(false);

  // Register with component registry
  useComponentRegistration({
    type: 'DataTransitionParticleSystem',
    eventSubscriptions: ['RESOURCE_UPDATED', 'RESOURCE_FLOW_UPDATED', 'RESOURCE_THRESHOLD_CHANGED'],
    updatePriority: 'high',
  });

  // Initialize particle system
  useEffect(() => {
    if (!canvasRef.current || isInitialized) return;

    particleSystemRef.current = ParticleSystemManager.getInstance();
    setIsInitialized(true);

    return () => {
      if (particleSystemRef.current) {
        particleSystemRef.current.cleanup();
      }
    };
  }, [isInitialized]);

  // Handle component lifecycle
  useComponentLifecycle({
    onMount: () => {
      console.warn('DataTransitionParticleSystem mounted');
    },
    onUnmount: () => {
      console.warn('DataTransitionParticleSystem unmounted');
    },
  });

  // Start transition when data changes
  useEffect(() => {
    if (!particleSystemRef.current || !isInitialized) return;

    // Create particle configuration
    const particleConfig = {
      maxParticles: Math.max(sourceData.length, targetData.length),
      spawnRate: 0,
      position: { x: 0, y: 0 },
      spread: 0,
      initialVelocity: {
        min: { x: 0, y: 0 },
        max: { x: 0, y: 0 },
      },
      acceleration: { x: 0, y: 0 },
      size: {
        min: 2,
        max: 8,
      },
      life: {
        min: duration,
        max: duration,
      },
      color: '#ffffff',
      blendMode: blendMode as 'normal' | 'additive',
      quality,
    };

    // Create transition particles
    sourceData.forEach((source, index) => {
      const target = targetData[index] || targetData[targetData.length - 1];
      const delay = index * staggerDelay;

      particleSystemRef.current?.createParticleSystem(`transition-${index}`, {
        ...particleConfig,
        position: source.position,
        color: getResourceColor(source.resourceType),
        size: {
          min: source.size || 2,
          max: target.size || 8,
        },
      });
    });

    // Start animation loop
    const startTime = performance.now();
    let animationFrame: number;

    const animate = () => {
      const currentTime = performance.now();
      const elapsed = currentTime - startTime;
      const progress = Math.min(1, elapsed / duration);

      // Update particle positions
      sourceData.forEach((source, index) => {
        const target = targetData[index] || targetData[targetData.length - 1];
        const particleProgress = easing(progress);

        const currentPosition = {
          x: source.position.x + (target.position.x - source.position.x) * particleProgress,
          y: source.position.y + (target.position.y - source.position.y) * particleProgress,
        };

        particleSystemRef.current?.update(1 / 60);
      });

      // Report progress
      onTransitionProgress?.(progress);

      // Continue animation or complete
      if (progress < 1) {
        animationFrame = requestAnimationFrame(animate);
      } else {
        onTransitionComplete?.();
      }
    };

    animationFrame = requestAnimationFrame(animate);

    // Cleanup function
    return () => {
      cancelAnimationFrame(animationFrame);
      sourceData.forEach((_, index) => {
        particleSystemRef.current?.removeSystem(`transition-${index}`);
      });
    };
  }, [
    sourceData,
    targetData,
    duration,
    easing,
    staggerDelay,
    trailEffect,
    blendMode,
    quality,
    onTransitionComplete,
    onTransitionProgress,
  ]);

  return (
    <div
      ref={containerRef}
      className={`data-transition-particle-system ${className}`}
      style={{ width, height, position: 'relative' }}
    >
      <canvas
        ref={canvasRef}
        width={width}
        height={height}
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          pointerEvents: 'none',
        }}
      />
    </div>
  );
};

// Helper function to get color for resource type
const getResourceColor = (resourceType?: ResourceType): string => {
  if (!resourceType) return '#ffffff';

  switch (resourceType) {
    case ResourceType.MINERALS:
      return '#4CAF50';
    case ResourceType.ENERGY:
      return '#FFC107';
    case ResourceType.PLASMA:
      return '#9C27B0';
    case ResourceType.GAS:
      return '#03A9F4';
    case ResourceType.RESEARCH:
      return '#3F51B5';
    default:
      return '#9E9E9E';
  }
};

export default DataTransitionParticleSystem;

