/**
 * Link type for D3 force simulation with proper typing
 * Extends SimulationLinkDatum for D3 compatibility
 */
interface FlowLink extends SimulationLinkDatum<FlowNode> {
  id: string;
  source: string | FlowNode;
  target: string | FlowNode;
  value: number;
  maxCapacity?: number;
  utilization?: number;
  flowType?: string;
  active: boolean;
  width?: number;
  color?: string;
}

/**
 * Props for FlowDiagram component
 */
interface FlowDiagramProps {
  /**
   * Flow data to visualize
   */
  data: FlowData;

  /**
   * Width of the diagram in pixels
   * @default 800
   */
  width?: number;

  /**
   * Height of the diagram in pixels
   * @default 600
   */
  height?: number;

  /**
   * Whether the diagram supports interaction (dragging, zooming)
   * @default true
   */
  interactive?: boolean;

  /**
   * Whether to animate transitions in the diagram
   * @default true
   */
  animated?: boolean;

  /**
   * Callback when a node is clicked
   */
  onNodeClick?: (nodeId: string, nodeData: FlowDataNode) => void;

  /**
   * Callback when a link is clicked
   */
  onLinkClick?: (linkId: string, linkData: FlowDataLink) => void;

  /**
   * CSS class name for additional styling
   */
  className?: string;
}

/**
 * Convert data nodes to D3-compatible nodes with proper typing
 */
const convertNodesToD3Format = (nodes: FlowDataNode[]): FlowNode[] => {
  return nodes.map(node => {
    // Determine radius based on node value
    const radius = Math.max(15, Math.min(40, 15 + (node.value / 100) * 25));

    // Determine color based on node type
    let color: string;
    switch (node.type) {
      case 'source':
        color = '#06b6d4'; // cyan
        break;
      case 'process':
        color = '#a855f7'; // purple
        break;
      case 'destination':
        color = '#10b981'; // green
        break;
      default:
        color = '#94a3b8'; // slate
    }

    // Create a properly typed node with no type assertions
    const d3Node: FlowNode = {
      id: node.id,
      name: node.name,
      type: node.type,
      value: node.value,
      capacity: node.capacity,
      efficiency: node.efficiency,
      description: node.description,
      radius,
      color,
      // Store original data for reference
      data: node,
    };

    return d3Node;
  });
};

/**
 * Convert data links to D3-compatible links with proper typing
 */
const convertLinksToD3Format = (
  links: FlowDataLink[],
  nodeMap: Map<string, FlowNode>
): FlowLink[] => {
  return links.map(link => {
    // Determine line width based on value and maxCapacity
    const width = Math.max(1, Math.min(8, 1 + (link.value / 100) * 7));

    // Determine color based on active state and utilization
    let color: string;
    if (!link.active) {
      color = '#94a3b8'; // slate (inactive)
    } else if (link.utilization && link.utilization > 0.8) {
      color = '#ef4444'; // red (high utilization)
    } else if (link.utilization && link.utilization > 0.5) {
      color = '#f59e0b'; // amber (medium utilization)
    } else {
      color = '#3b82f6'; // blue (low utilization)
    }

    // Create a properly typed link with no type assertions
    const d3Link: FlowLink = {
      id: link.id,
      source: link.source,
      target: link.target,
      value: link.value,
      maxCapacity: link.maxCapacity,
      utilization: link.utilization,
      flowType: link.flowType,
      active: link.active,
      width,
      color,
    };

    return d3Link;
  });
};

/**
 * Find a node by ID in an array of nodes
 * Type-safe helper function
 */
const findNodeById = (nodes: FlowNode[], id: string): FlowNode | undefined => {
  return nodes.find(node => node.id === id);
};

/**
 * Check if a node/link is a string or an object
 * Type guard function
 */
const isNodeReference = (obj: string | FlowNode): obj is FlowNode => {
  return typeof obj !== 'string' && obj.id !== undefined;
};

/**
 * Component for visualizing flow data with type-safe D3 integration
 */
const FlowDiagram: React.FC<FlowDiagramProps> = ({
  data,
  width = 800,
  height = 600,
  interactive = true,
  animated = true,
  onNodeClick,
  onLinkClick,
  className = '',
}) => {
  const svgRef = useRef<SVGSVGElement | null>(null);
  const simulationRef = useRef<d3.Simulation<FlowNode, FlowLink> | null>(null);

  // State for tracking hover and selected nodes/links
  const [selectedNodeId, setSelectedNodeId] = useState<string | null>(null);
  const [selectedLinkId, setSelectedLinkId] = useState<string | null>(null);

  /**
   * Prepare visualization data with type safety
   */
  const prepareVisualizationData = useCallback(() => {
    try {
      // Define validation schemas
      const flowDataSchema: Schema = {
        name: 'FlowData',
        description: 'Schema for flow data',
        properties: {
          nodes: {
            type: 'array',
            required: true,
          },
          links: {
            type: 'array',
            required: true,
          },
        },
      };

      // Create validation functions
      const validation = createD3ForceValidation<FlowData, FlowNode, FlowLink>(
        // Node transform function
        inputData => convertNodesToD3Format(inputData.nodes),
        // Link transform function
        inputData =>
          convertLinksToD3Format(
            inputData.links,
            new Map(convertNodesToD3Format(inputData.nodes).map(node => [node.id, node]))
          ),
        // Custom node schema properties
        {
          properties: {
            id: {
              type: 'string',
              required: true,
              validate: value => (value as string).length > 0 || 'Node ID cannot be empty',
            },
            type: {
              type: 'string',
              required: true,
              enum: ['source', 'process', 'destination'],
            },
          },
        },
        // Custom link schema properties
        {
          properties: {
            id: {
              type: 'string',
              required: true,
            },
            active: {
              type: 'boolean',
              required: true,
            },
          },
        },
        // Validation options
        {
          throwOnError: false,
          logErrors: true,
          errorPrefix: 'FlowDiagram Validation Error',
        }
      );

      // Validate and transform data
      const nodeResult: ValidationTransformResult<FlowNode[]> = validation.validateNodes(data);
      const linkResult: ValidationTransformResult<FlowLink[]> = validation.validateLinks(data);

      // Handle validation errors
      if (!nodeResult.valid || !linkResult.valid) {
        console.warn(
          'Flow data validation failed:',
          [...nodeResult.errors, ...linkResult.errors].join('\n')
        );
      }

      // Create a map for node lookups (using validated nodes)
      const nodeMap = new Map<string, FlowNode>();
      nodeResult.data.forEach(node => nodeMap.set(node.id, node));

      return {
        nodes: nodeResult.data,
        links: linkResult.data,
        nodeMap,
        valid: nodeResult.valid && linkResult.valid,
        errors: [...nodeResult.errors, ...linkResult.errors],
      };
    } catch (error) {
      console.error('Error preparing visualization data:', error);
      return {
        nodes: [],
        links: [],
        nodeMap: new Map(),
        valid: false,
        errors: ['Error preparing visualization data'],
      };
    }
  }, [data]);

  /**
   * Handle node click events with proper typing
   */
  const handleNodeClick = useCallback(
    (event: React.MouseEvent, node: FlowNode) => {
      setSelectedNodeId(prevId => (prevId === node.id ? null : node.id));

      if (onNodeClick && node.data) {
        onNodeClick(node.id, node.data);
      }
    },
    [onNodeClick]
  );

  /**
   * Handle link click events with proper typing
   */
  const handleLinkClick = useCallback(
    (event: React.MouseEvent, link: FlowLink) => {
      if (typeof link.source === 'string' || typeof link.target === 'string') {
        return; // Ignore links that don't have resolved nodes
      }

      setSelectedLinkId(prevId => (prevId === link.id ? null : link.id));

      // Find the original link data for the callback
      const linkData = data.links.find(l => l.id === link.id);
      if (onLinkClick && linkData) {
        onLinkClick(link.id, linkData);
      }
    },
    [data.links, onLinkClick]
  );

  /**
   * Create and update the visualization
   */
  useEffect(() => {
    if (!svgRef.current || !data) return;

    // Clean up previous simulation if it exists
    if (simulationRef.current) {
      simulationRef.current.stop();
    }

    // Get SVG element with proper typing
    const svg = d3.select<SVGSVGElement, unknown>(svgRef.current);

    // Clear previous content
    svg.selectAll('*').remove();

    // Prepare data with type safety
    const { nodes, links, nodeMap } = prepareVisualizationData();

    // Create container group for zooming
    const container = svg.append('g').attr('class', 'container');

    // Add zoom behavior if interactive
    if (interactive) {
      const zoom = d3
        .zoom<SVGSVGElement, unknown>()
        .scaleExtent([0.1, 4])
        .on('zoom', event => {
          container.attr('transform', event.transform);
        });

      svg.call(zoom);
    }

    // Create arrow markers for links
    const defs = container.append('defs');

    // Create a basic arrow marker
    defs
      .append('marker')
      .attr('id', 'arrowhead')
      .attr('viewBox', '-10 -5 10 10')
      .attr('refX', 0)
      .attr('refY', 0)
      .attr('orient', 'auto')
      .attr('markerWidth', 8)
      .attr('markerHeight', 8)
      .append('path')
      .attr('d', 'M-10,-5L0,0L-10,5')
      .attr('fill', '#777');

    // Create colored arrow markers
    ['active', 'inactive', 'high', 'medium', 'low'].forEach(type => {
      let color: string;
      switch (type) {
        case 'active':
          color = '#3b82f6';
          break;
        case 'inactive':
          color = '#94a3b8';
          break;
        case 'high':
          color = '#ef4444';
          break;
        case 'medium':
          color = '#f59e0b';
          break;
        case 'low':
          color = '#10b981';
          break;
        default:
          color = '#777';
      }

      defs
        .append('marker')
        .attr('id', `arrowhead-${type}`)
        .attr('viewBox', '-10 -5 10 10')
        .attr('refX', 0)
        .attr('refY', 0)
        .attr('orient', 'auto')
        .attr('markerWidth', 8)
        .attr('markerHeight', 8)
        .append('path')
        .attr('d', 'M-10,-5L0,0L-10,5')
        .attr('fill', color);
    });

    // Create D3 force simulation with proper typing
    const simulation = d3
      .forceSimulation<FlowNode, FlowLink>(nodes)
      .force(
        'link',
        d3
          .forceLink<FlowNode, FlowLink>(links)
          .id(d => d.id)
          .distance(100)
      )
      .force('charge', d3.forceManyBody().strength(-200))
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force(
        'collision',
        d3.forceCollide<FlowNode>().radius(d => (d.radius || 20) + 10)
      );

    // Store simulation reference for cleanup
    simulationRef.current = simulation;

    // Create links with proper typing
    const link = container
      .append('g')
      .attr('class', 'links')
      .selectAll<SVGLineElement, FlowLink>('line')
      .data(links)
      .enter()
      .append('line')
      .attr('class', d => `link ${d.active ? 'active' : 'inactive'}`)
      .attr('stroke', d => d.color || '#999')
      .attr('stroke-width', d => d.width || 1)
      .attr('marker-end', d => {
        // Determine marker based on link properties
        if (!d.active) return 'url(#arrowhead-inactive)';
        if (d.utilization && d.utilization > 0.8) return 'url(#arrowhead-high)';
        if (d.utilization && d.utilization > 0.5) return 'url(#arrowhead-medium)';
        return 'url(#arrowhead-active)';
      })
      .style('cursor', onLinkClick ? 'pointer' : 'default')
      .on('click', function (event, d) {
        if (onLinkClick) {
          handleLinkClick(event, d);
        }
      });

    // Create nodes with proper typing
    const node = container
      .append('g')
      .attr('class', 'nodes')
      .selectAll<SVGGElement, FlowNode>('g')
      .data(nodes)
      .enter()
      .append('g')
      .attr('class', 'node')
      .style('cursor', onNodeClick ? 'pointer' : 'default')
      .call(
        d3
          .drag<SVGGElement, FlowNode>()
          .on('start', dragStarted)
          .on('drag', dragged)
          .on('end', dragEnded)
      )
      .on('click', function (event, d) {
        if (onNodeClick) {
          handleNodeClick(event, d);
        }
      });

    // Add circles to nodes
    node
      .append('circle')
      .attr('r', d => d.radius || 20)
      .attr('fill', d => d.color || '#999')
      .attr('stroke', '#fff')
      .attr('stroke-width', 1.5);

    // Add node labels
    node
      .append('text')
      .attr('dy', '.3em')
      .attr('text-anchor', 'middle')
      .attr('font-size', '10px')
      .attr('fill', '#fff')
      .text(d => d.name);

    // Add node value labels
    node
      .append('text')
      .attr('dy', '1.6em')
      .attr('text-anchor', 'middle')
      .attr('font-size', '9px')
      .attr('fill', '#fff')
      .text(d => `${d.value}${d.capacity ? `/${d.capacity}` : ''}`);

    // Add hover effects with type-safe accessors
    node
      .on('mouseover', function (event, d) {
        d3.select(this)
          .select('circle')
          .transition()
          .duration(200)
          .attr('r', (d.radius || 20) * 1.1);
      })
      .on('mouseout', function (event, d) {
        d3.select(this)
          .select('circle')
          .transition()
          .duration(200)
          .attr('r', d.radius || 20);
      });

    // Add flow effects to links
    if (animated) {
      link
        .filter(d => d.active)
        .each(function (d) {
          const element = d3.select(this);

          // Add animated dash array for active links
          element
            .attr('stroke-dasharray', '5,5')
            .style('animation', `flowAnimation ${5000 / (d.value || 1)}ms linear infinite`);
        });
    }

    // Update positions on simulation tick with proper typing
    simulation.on('tick', () => {
      // Update links with safe accessors to prevent type errors
      link
        .attr('x1', d => {
          const source = typeof d.source === 'string' ? findNodeById(nodes, d.source) : d.source;
          return source ? d3Accessors.getX(source) : 0;
        })
        .attr('y1', d => {
          const source = typeof d.source === 'string' ? findNodeById(nodes, d.source) : d.source;
          return source ? d3Accessors.getY(source) : 0;
        })
        .attr('x2', d => {
          const target = typeof d.target === 'string' ? findNodeById(nodes, d.target) : d.target;
          return target ? d3Accessors.getX(target) : 0;
        })
        .attr('y2', d => {
          const target = typeof d.target === 'string' ? findNodeById(nodes, d.target) : d.target;
          return target ? d3Accessors.getY(target) : 0;
        });

      // Update nodes with safe transforms
      node.attr('transform', d => {
        const x = d3Accessors.getX(d);
        const y = d3Accessors.getY(d);
        return `translate(${x}, ${y})`;
      });
    });

    // Type-safe drag functions
    function dragStarted(event: d3.D3DragEvent<SVGGElement, FlowNode, FlowNode>, d: FlowNode) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d3Accessors.getX(d);
      d.fy = d3Accessors.getY(d);
    }

    function dragged(event: d3.D3DragEvent<SVGGElement, FlowNode, FlowNode>, d: FlowNode) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnded(event: d3.D3DragEvent<SVGGElement, FlowNode, FlowNode>, d: FlowNode) {
      if (!event.active) simulation.alphaTarget(0);
      if (!interactive) {
        d.fx = null;
        d.fy = null;
      }
    }

    // Cleanup function
    return () => {
      if (simulationRef.current) {
        simulationRef.current.stop();
      }
    };
  }, [
    data,
    width,
    height,
    interactive,
    animated,
    prepareVisualizationData,
    handleNodeClick,
    handleLinkClick,
    onNodeClick,
    onLinkClick,
  ]);

  return (
    <div className={`flow-diagram-container ${className}`}>
      {/* Add CSS for animation */}
      {animated && (
        <style>
          {`
            @keyframes flowAnimation {
              from {
                stroke-dashoffset: 20;
              }
              to {
                stroke-dashoffset: 0;
              }
            }
          `}
        </style>
      )}
      <svg ref={svgRef} width={width} height={height} className="flow-diagram-svg" />
    </div>
  );
};

export default FlowDiagram;

import * as React from "react";
import { useMemo, useRef } from 'react';
import {
  DataVisualizationShaderType,
  ShaderUniform,
} from '../../../lib/optimization/WebGLShaderManager';
import {
  DataHighlightVisualization,
  DataHighlightVisualizationProps,
} from './DataHighlightVisualization';

/**
 * Kernel density estimation method
 */
export enum KernelType {
  GAUSSIAN = 'gaussian',
  EPANECHNIKOV = 'epanechnikov',
  UNIFORM = 'uniform',
  TRIANGULAR = 'triangular',
  COSINE = 'cosine',
}

/**
 * Enhanced props for heat map density visualization
 */
export interface HeatMapDensityVisualizationProps
  extends Omit<DataHighlightVisualizationProps, 'visualizationType'> {
  /**
   * Bandwidth for kernel density estimation (affects smoothness)
   * Higher values create smoother heatmaps with less detail
   * Lower values create more detailed heatmaps but may introduce noise
   */
  bandwidth?: number;

  /**
   * Kernel type for density estimation
   */
  kernelType?: KernelType;

  /**
   * Number of interpolation steps between data points
   * Higher values create smoother gradients but reduce performance
   */
  interpolationSteps?: number;

  /**
   * Range of the radius around each point where the heat spreads
   * As a percentage of the visualization dimensions
   */
  heatRadius?: number;

  /**
   * Whether to use logarithmic scale for intensity
   * Useful for datasets with high variance
   */
  useLogScale?: boolean;

  /**
   * Contour levels to show on the heatmap
   * Values between 0 and 1 representing intensity thresholds
   */
  contourLevels?: number[];

  /**
   * Whether to show a grid overlay
   */
  showGrid?: boolean;

  /**
   * Grid cell size as a fraction of the visualization dimensions
   */
  gridSize?: number;
}

/**
 * HeatMapDensityVisualization Component
 *
 * A specialized component for rendering density-based heat maps using WebGL shaders.
 * Provides advanced configuration options for kernel density estimation and heat map rendering.
 */
export const HeatMapDensityVisualization: React.FC<HeatMapDensityVisualizationProps> = ({
  data,
  width,
  height,
  colors = ['#000080', '#0000ff', '#00ffff', '#ffff00', '#ff0000'],
  bandwidth = 0.1,
  kernelType = KernelType.GAUSSIAN,
  interpolationSteps = 32,
  heatRadius = 0.15,
  useLogScale = false,
  contourLevels = [],
  showGrid = false,
  gridSize = 0.05,
  ...restProps
}) => {
  // Canvas ref for custom pre-rendering if needed
  const canvasRef = useRef<HTMLCanvasElement>(null);

  // Process data for density visualization
  const processedData = useMemo(() => {
    // For basic usage, we can just return the data
    // In a more advanced implementation, we might preprocess the data
    // to optimize for density visualization
    return data;
  }, [data]);

  // Prepare custom shader uniforms for the heat map visualization
  const customUniforms = useMemo(() => {
    const uniforms: Record<string, ShaderUniform> = {
      u_bandwidth: {
        type: 'float',
        value: bandwidth,
      },
      u_heatRadius: {
        type: 'float',
        value: heatRadius,
      },
      u_interpolationSteps: {
        type: 'float',
        value: interpolationSteps,
      },
      u_useLogScale: {
        type: 'int',
        value: useLogScale ? 1 : 0,
      },
      u_kernelType: {
        type: 'int',
        value: Object.values(KernelType).indexOf(kernelType),
      },
      u_showGrid: {
        type: 'int',
        value: showGrid ? 1 : 0,
      },
      u_gridSize: {
        type: 'float',
        value: gridSize,
      },
    };

    // Add contour levels if provided
    if (contourLevels.length > 0) {
      const levels = new Float32Array(Math.min(contourLevels.length, 10));
      contourLevels.slice(0, 10).forEach((level, i) => {
        levels[i] = level;
      });

      uniforms.u_contourLevels = {
        type: 'float',
        value: levels,
      };

      uniforms.u_contourLevelCount = {
        type: 'int',
        value: contourLevels.length,
      };
    }

    return uniforms;
  }, [
    bandwidth,
    heatRadius,
    interpolationSteps,
    useLogScale,
    kernelType,
    contourLevels,
    showGrid,
    gridSize,
  ]);

  // Custom fragment shader code for enhanced heat map rendering
  const getCustomFragmentShader = (): string => {
    return `
      precision mediump float;

      uniform vec3 u_colors[5];
      uniform int u_colorCount;
      uniform float u_intensity;
      uniform float u_time;
      uniform vec2 u_highlightRange;
      uniform float u_bandwidth;
      uniform float u_heatRadius;
      uniform float u_interpolationSteps;
      uniform int u_useLogScale;
      uniform int u_kernelType;
      uniform int u_contourLevelCount;
      uniform float u_contourLevels[10];
      uniform int u_showGrid;
      uniform float u_gridSize;

      varying float v_data;
      varying vec2 v_position;
      varying float v_time;

      // Helper function to interpolate colors
      vec3 getColor(float value) {
        if (u_colorCount == 1) return u_colors[0];

        float indexFloat = value * float(u_colorCount - 1);
        int index = int(floor(indexFloat));
        float t = fract(indexFloat);

        if (index >= u_colorCount - 1) {
          return u_colors[u_colorCount - 1];
        }

        return mix(u_colors[index], u_colors[index + 1], t);
      }

      // Kernel functions for density estimation
      float gaussianKernel(float distance, float bandwidth) {
        float x = distance / bandwidth;
        return exp(-0.5 * x * x);
      }

      float epanechnikovKernel(float distance, float bandwidth) {
        float x = distance / bandwidth;
        if (abs(x) <= 1.0) {
          return 0.75 * (1.0 - x * x);
        }
        return 0.0;
      }

      float uniformKernel(float distance, float bandwidth) {
        return distance <= bandwidth ? 1.0 : 0.0;
      }

      float triangularKernel(float distance, float bandwidth) {
        float x = distance / bandwidth;
        return abs(x) <= 1.0 ? 1.0 - abs(x) : 0.0;
      }

      float cosineKernel(float distance, float bandwidth) {
        float x = distance / bandwidth;
        return abs(x) <= 1.0 ? (cos(x * 3.14159) + 1.0) * 0.5 : 0.0;
      }

      // Apply kernel based on type
      float applyKernel(float distance, float bandwidth) {
        if (u_kernelType == 0) {
          return gaussianKernel(distance, bandwidth);
        } else if (u_kernelType == 1) {
          return epanechnikovKernel(distance, bandwidth);
        } else if (u_kernelType == 2) {
          return uniformKernel(distance, bandwidth);
        } else if (u_kernelType == 3) {
          return triangularKernel(distance, bandwidth);
        } else if (u_kernelType == 4) {
          return cosineKernel(distance, bandwidth);
        }
        return gaussianKernel(distance, bandwidth);
      }

      // Draw grid lines
      float drawGrid(vec2 position, float cellSize) {
        vec2 grid = fract(position / cellSize);
        float line = step(0.98, grid.x) + step(0.98, grid.y);
        return min(line, 1.0) * 0.2;
      }

      void main() {
        // Get base color from data value
        float dataValue = v_data;

        // Apply log scale if enabled
        if (u_useLogScale == 1 && dataValue > 0.0) {
          dataValue = log(1.0 + dataValue * 9.0) / log(10.0);
        }

        // Get color based on data value
        vec3 color = getColor(dataValue);
        float alpha = u_intensity;

        // Adjust based on distance from center point
        float dist = length(gl_PointCoord - vec2(0.5));
        float heatValue = applyKernel(dist, u_bandwidth);

        // Fade out at edges
        alpha *= heatValue;

        // Add contour lines if specified
        if (u_contourLevelCount > 0) {
          for (int i = 0; i < 10; i++) {
            if (i >= u_contourLevelCount) break;

            float level = u_contourLevels[i];
            float contourWidth = 0.02;
            if (abs(dataValue - level) < contourWidth) {
              color = mix(color, vec3(1.0), 0.5);
              alpha = mix(alpha, 1.0, 0.5);
            }
          }
        }

        // Apply highlight effect if in range
        if (dataValue >= u_highlightRange.x && dataValue <= u_highlightRange.y) {
          // Pulse effect
          float pulse = 0.5 + 0.5 * sin(u_time * 3.0);

          // Brighten color and add glow
          color = mix(color, vec3(1.0), pulse * 0.3);
          alpha = mix(alpha, 1.0, pulse * 0.4);
        }

        // Apply grid overlay if enabled
        if (u_showGrid == 1) {
          float gridOverlay = drawGrid(v_position, u_gridSize);
          color = mix(color, vec3(1.0), gridOverlay);
        }

        gl_FragColor = vec4(color, alpha);
      }
    `;
  };

  // Re-render whenever custom parameters change
  const customShaderConfig = useMemo(
    () => ({
      customUniforms,
      fragmentShader: getCustomFragmentShader(),
    }),
    [customUniforms]
  );

  return (
    <DataHighlightVisualization
      data={processedData}
      width={width}
      height={height}
      visualizationType={DataVisualizationShaderType.HEATMAP}
      colors={colors}
      shaderConfig={customShaderConfig}
      {...restProps}
    />
  );
};

