import * as d3 from 'd3';
import * as React from 'react';
import { useEffect, useRef, useState } from 'react';
import { SimulationNodeDatum } from '../../../types/visualizations/D3Types';

/**
 * Represents a data point in time series
 */
interface TimeDataPoint {
  timestamp: Date;
  value: number;
  category: string;
  id: string;
}

/**
 * Node type for time-based animations
 * Extends SimulationNodeDatum to ensure D3 type compatibility
 */
interface TimeNode extends SimulationNodeDatum<TimeDataPoint> {
  timestamp: Date;
  value: number;
  category: string;
  x?: number;
  y?: number;
  radius?: number;
  color?: string;
}

/**
 * Configuration options for the animation and transition settings
 */
interface AnimationConfig {
  /** Duration of transitions in milliseconds */
  transitionDuration: number;
  /** Easing function for transitions */
  easing: (t: number) => number;
  /** Delay between animations in milliseconds */
  staggerDelay: number;
  /** Whether to loop animations */
  loop: boolean;
}

/**
 * Props for the TemporalAnalysisView component
 */
interface TemporalAnalysisViewProps {
  /**
   * Time series data to visualize
   */
  data: TimeDataPoint[];

  /**
   * Width of the visualization in pixels
   * @default 800
   */
  width?: number;

  /**
   * Height of the visualization in pixels
   * @default 500
   */
  height?: number;

  /**
   * Margin for the visualization
   */
  margin?: {
    top: number;
    right: number;
    bottom: number;
    left: number;
  };

  /**
   * Whether to show time labels
   * @default true
   */
  showLabels?: boolean;

  /**
   * Animation configuration
   */
  animationConfig?: Partial<AnimationConfig>;

  /**
   * CSS class name for additional styling
   */
  className?: string;
}

/**
 * Safely converts time data to D3-compatible format
 */
const convertToTimeNodes = (data: TimeDataPoint[]): TimeNode[] => {
  return data.map(point => {
    // Properly typed conversion with no type assertions
    const node: TimeNode = {
      id: point.id,
      timestamp: point.timestamp,
      value: point.value,
      category: point.category,
      // The original data field keeps the reference to the source data
      data: point,
    };

    return node;
  });
};

/**
 * Get color for a specific category
 */
const getCategoryColor = (category: string): string => {
  // Color scale for different categories
  const colorScale = d3.scaleOrdinal(d3.schemeCategory10);
  return colorScale(category);
};

/**
 * Component for visualizing temporal data with smooth transitions
 */
const TemporalAnalysisView: React.FC<TemporalAnalysisViewProps> = ({
  data,
  width = 800,
  height = 500,
  margin = { top: 40, right: 40, bottom: 60, left: 60 },
  showLabels = true,
  animationConfig = {},
  className = '',
}) => {
  const svgRef = useRef<SVGSVGElement | null>(null);

  // Default animation config with sensible defaults
  const defaultAnimationConfig: AnimationConfig = {
    transitionDuration: 750,
    easing: d3.easeCubicInOut,
    staggerDelay: 50,
    loop: false,
  };

  // Merge default config with provided config
  const finalAnimationConfig: AnimationConfig = {
    ...defaultAnimationConfig,
    ...animationConfig,
  };

  // Chart dimensions
  const chartWidth = width - margin.left - margin.right;
  const chartHeight = height - margin.top - margin.bottom;

  // Track animation state
  const [isAnimating, setIsAnimating] = useState(false);
  const [currentTimestamp, setCurrentTimestamp] = useState<Date | null>(null);

  // Extract unique categories from data
  const categories = Array.from(new Set(data.map(d => d.category)));

  // Convert data to time nodes for visualization
  const timeNodes = convertToTimeNodes(data);

  // Extract time range from data
  const timeExtent = d3.extent(data, d => d.timestamp) as [Date, Date];
  const valueExtent = d3.extent(data, d => d.value) as [number, number];

  // Set up time scale with proper typing
  const timeScale = d3.scaleTime<number, number>().domain(timeExtent).range([0, chartWidth]);

  // Set up value scale with proper typing
  const valueScale = d3
    .scaleLinear<number, number>()
    .domain([Math.min(0, valueExtent[0]), valueExtent[1]])
    .range([chartHeight, 0]);

  // Set up category scale with proper typing
  const categoryScale = d3
    .scaleBand<string>()
    .domain(categories)
    .range([0, chartHeight])
    .padding(0.1);

  /**
   * Initialize chart
   */
  useEffect(() => {
    if (!svgRef.current || !data.length) return;

    // Select SVG element with proper typing
    const svg = d3.select<SVGSVGElement, unknown>(svgRef.current);

    // Clear any existing elements
    svg.selectAll('*').remove();

    // Add chart group with margins
    const chart = svg.append('g').attr('transform', `translate(${margin.left}, ${margin.top})`);

    // Add X axis with proper typing
    const xAxis = chart
      .append('g')
      .attr('class', 'x-axis')
      .attr('transform', `translate(0, ${chartHeight})`)
      .call(d3.axisBottom(timeScale));

    // Add X axis label
    chart
      .append('text')
      .attr('class', 'x-label')
      .attr('text-anchor', 'middle')
      .attr('x', chartWidth / 2)
      .attr('y', chartHeight + 40)
      .text('Time');

    // Add Y axis with proper typing
    const yAxis = chart.append('g').attr('class', 'y-axis').call(d3.axisLeft(valueScale));

    // Add Y axis label
    chart
      .append('text')
      .attr('class', 'y-label')
      .attr('text-anchor', 'middle')
      .attr('transform', 'rotate(-90)')
      .attr('x', -chartHeight / 2)
      .attr('y', -40)
      .text('Value');

    // Draw vertical gridlines
    chart
      .append('g')
      .attr('class', 'grid x-grid')
      .attr('transform', `translate(0, ${chartHeight})`)
      .call(
        d3
          .axisBottom(timeScale)
          .tickSize(-chartHeight)
          .tickFormat(() => '')
      );

    // Draw horizontal gridlines
    chart
      .append('g')
      .attr('class', 'grid y-grid')
      .call(
        d3
          .axisLeft(valueScale)
          .tickSize(-chartWidth)
          .tickFormat(() => '')
      );

    // Add clip path to ensure points don't overflow
    chart
      .append('clipPath')
      .attr('id', 'chart-area')
      .append('rect')
      .attr('width', chartWidth)
      .attr('height', chartHeight);

    // Create container for data points with clipping
    const pointsContainer = chart
      .append('g')
      .attr('class', 'points-container')
      .attr('clip-path', 'url(#chart-area)');

    // Group data by category for visualization
    const dataByCategory = d3.group(timeNodes, d => d.category);

    // Add a line for each category with proper typing
    dataByCategory.forEach((points, category) => {
      // Sort points by timestamp for proper line drawing
      const sortedPoints = [...points].sort(
        (a, b) => a.timestamp.getTime() - b.timestamp.getTime()
      );

      // Line generator with type-safe accessors
      const lineGenerator = d3
        .line<TimeNode>()
        .x(d => timeScale(d.timestamp))
        .y(d => valueScale(d.value))
        .curve(d3.curveMonotoneX);

      // Add the line path with proper typing
      pointsContainer
        .append('path')
        .attr('class', `line-${category}`)
        .attr('fill', 'none')
        .attr('stroke', getCategoryColor(category))
        .attr('stroke-width', 2)
        .attr('d', lineGenerator(sortedPoints));

      // Add circles for each data point with proper typing
      const circles = pointsContainer
        .selectAll<SVGCircleElement, TimeNode>(`.point-${category}`)
        .data(sortedPoints)
        .enter()
        .append('circle')
        .attr('class', `point-${category}`)
        .attr('cx', d => timeScale(d.timestamp))
        .attr('cy', d => valueScale(d.value))
        .attr('r', 0) // Start with radius 0 for entrance animation
        .attr('fill', getCategoryColor(category))
        .attr('stroke', '#fff')
        .attr('stroke-width', 1);

      // Add entrance animation with proper typing
      circles
        .transition()
        .duration(finalAnimationConfig.transitionDuration)
        .delay((_, i) => i * finalAnimationConfig.staggerDelay)
        .ease(finalAnimationConfig.easing)
        .attr('r', 5);

      // Add labels if enabled
      if (showLabels) {
        pointsContainer
          .selectAll<SVGTextElement, TimeNode>(`.label-${category}`)
          .data(sortedPoints)
          .enter()
          .append('text')
          .attr('class', `label-${category}`)
          .attr('x', d => timeScale(d.timestamp))
          .attr('y', d => valueScale(d.value) - 10)
          .attr('text-anchor', 'middle')
          .attr('font-size', '10px')
          .attr('opacity', 0) // Start transparent for animation
          .text(d => d.value.toFixed(1))
          .transition()
          .duration(finalAnimationConfig.transitionDuration)
          .delay((_, i) => i * finalAnimationConfig.staggerDelay + 200)
          .ease(finalAnimationConfig.easing)
          .attr('opacity', 1);
      }
    });

    // Add category legend with proper typing
    const legend = chart
      .append('g')
      .attr('class', 'legend')
      .attr('transform', `translate(${chartWidth - 100}, 0)`);

    categories.forEach((category, i) => {
      const legendItem = legend
        .append('g')
        .attr('class', `legend-item-${category}`)
        .attr('transform', `translate(0, ${i * 20})`);

      legendItem
        .append('rect')
        .attr('width', 15)
        .attr('height', 15)
        .attr('fill', getCategoryColor(category));

      legendItem
        .append('text')
        .attr('x', 20)
        .attr('y', 12)
        .attr('font-size', '12px')
        .text(category);
    });

    // Add time cursor for animation
    const timeCursor = chart
      .append('line')
      .attr('class', 'time-cursor')
      .attr('x1', 0)
      .attr('y1', 0)
      .attr('x2', 0)
      .attr('y2', chartHeight)
      .attr('stroke', '#666')
      .attr('stroke-width', 1)
      .attr('stroke-dasharray', '4,4')
      .attr('opacity', 0);

    // Add cursor timestamp label
    const cursorLabel = chart
      .append('text')
      .attr('class', 'cursor-label')
      .attr('text-anchor', 'middle')
      .attr('font-size', '10px')
      .attr('y', -10)
      .attr('opacity', 0);

    // Setup animation controls if needed
    if (finalAnimationConfig.loop) {
      setupAnimationLoop(chart, timeScale, timeCursor, cursorLabel);
    }
  }, [data, width, height, margin, showLabels, categories]);

  /**
   * Set up animation loop with type-safe transitions
   */
  const setupAnimationLoop = (
    chart: d3.Selection<SVGGElement, unknown, null, undefined>,
    timeScale: d3.ScaleTime<number, number>,
    timeCursor: d3.Selection<SVGLineElement, unknown, null, undefined>,
    cursorLabel: d3.Selection<SVGTextElement, unknown, null, undefined>
  ) => {
    setIsAnimating(true);

    // Start and end timestamps
    const startTime = timeExtent[0];
    const endTime = timeExtent[1];
    const duration = finalAnimationConfig.transitionDuration * 5;

    // Animation function with proper typing
    const animateTimeCursor = () => {
      // Type-safe transition
      timeCursor
        .attr('opacity', 1)
        .attr('x1', timeScale(startTime))
        .attr('x2', timeScale(startTime));

      // Create a custom timer for smoother animation with proper type safety
      const startTimestamp = Date.now();
      const timerDuration = duration;

      // Use d3.timer for precise animation control with proper typing
      const timer = d3.timer(elapsed => {
        // Calculate progress (0 to 1)
        const progress = Math.min(elapsed / timerDuration, 1);

        // Interpolate the current timestamp
        const currentTime = new Date(
          startTime.getTime() + progress * (endTime.getTime() - startTime.getTime())
        );

        // Update cursor position with type-safe accessors
        timeCursor.attr('x1', timeScale(currentTime)).attr('x2', timeScale(currentTime));

        // Update cursor label with safe string formatting
        cursorLabel
          .attr('opacity', 1)
          .attr('x', timeScale(currentTime))
          .text(d3.timeFormat('%b %d, %Y')(currentTime));

        // Update React state with proper typing
        setCurrentTimestamp(currentTime);

        // Stop timer when complete
        if (progress === 1) {
          timer.stop();

          // If looping is enabled, restart after delay
          if (finalAnimationConfig.loop) {
            setTimeout(animateTimeCursor, 1000);
          } else {
            setIsAnimating(false);
          }
        }
      });
    };

    // Start the animation
    animateTimeCursor();
  };

  /**
   * Creates a type-safe function to handle hover effects on nodes
   */
  const createNodeHoverHandler = () => {
    return (event: React.MouseEvent<SVGCircleElement, MouseEvent>, node: TimeNode) => {
      // Type-safe accessing of node data
      const value = node.value;
      const timestamp = node.timestamp;
      const category = node.category;

      // Safe D3 selection
      const circle = d3.select<SVGCircleElement, TimeNode>(event.currentTarget);

      // Type-safe transition
      circle.transition().duration(200).attr('r', 8).attr('stroke-width', 2);
    };
  };

  return (
    <div className={`temporal-analysis-container ${className}`}>
      <svg ref={svgRef} width={width} height={height} className="temporal-analysis-svg" />

      {currentTimestamp && (
        <div className="current-time-display">
          Current Time: {currentTimestamp.toLocaleString()}
        </div>
      )}
    </div>
  );
};

export default TemporalAnalysisView;

import * as React from 'react';
import {
  createContext,
  ReactNode,
  useCallback,
  useContext,
  useEffect,
  useRef,
  useState,
} from 'react';
import { v4 as uuidv4 } from 'uuid';
import {
  Anomaly,
  EXPLORATION_EVENTS,
  explorationManager,
  Sector,
} from '../managers/exploration/ExplorationManager';
import { AnalysisAlgorithmService } from '../services/AnalysisAlgorithmService';
import { DataCollectionService } from '../services/DataCollectionService';
import { DataProcessingService } from '../services/DataProcessingService';
import { BaseEvent, EventType } from '../types/events/EventTypes';
import {
  AnalysisConfig,
  AnalysisResult,
  DataAnalysisContextType,
  DataPoint,
  Dataset,
  ResourceData,
} from '../types/exploration/DataAnalysisTypes';

// Create the context with a default undefined value
const DataAnalysisContext = createContext<DataAnalysisContextType | undefined>(undefined);

// Props for the DataAnalysisProvider component
interface DataAnalysisProviderProps {
  children: ReactNode;
  initialDatasets?: Dataset[];
  initialAnalysisConfigs?: AnalysisConfig[];
  initialAnalysisResults?: AnalysisResult[];
}

/**
 * Provider component for the DataAnalysisContext
 */
export const DataAnalysisProvider: React.FC<DataAnalysisProviderProps> = ({
  children,
  initialDatasets = [],
  initialAnalysisConfigs = [],
  initialAnalysisResults = [],
}) => {
  // State for datasets, analysis configurations, and results
  const [datasets, setDatasets] = useState<Dataset[]>(initialDatasets);
  const [analysisConfigs, setAnalysisConfigs] = useState<AnalysisConfig[]>(initialAnalysisConfigs);
  const [analysisResults, setAnalysisResults] = useState<AnalysisResult[]>(initialAnalysisResults);
  const [_isProcessingData, setIsProcessingData] = useState<boolean>(false);

  // Create references to the services for persistence across renders
  const dataCollectionServiceRef = useRef<DataCollectionService | null>(null);
  const analysisAlgorithmServiceRef = useRef<AnalysisAlgorithmService | null>(null);
  const dataProcessingServiceRef = useRef<DataProcessingService | null>(null);

  // Initialize services
  useEffect(() => {
    if (explorationManager) {
      // Initialize data collection service
      const dataCollectionService = new DataCollectionService(explorationManager);
      dataCollectionServiceRef.current = dataCollectionService;

      // Initialize analysis algorithm service
      const analysisAlgorithmService = new AnalysisAlgorithmService();
      analysisAlgorithmServiceRef.current = analysisAlgorithmService;

      // Initialize data processing service for web worker operations
      const dataProcessingService = new DataProcessingService();
      dataProcessingServiceRef.current = dataProcessingService;

      // Initialize the data collection service
      dataCollectionService.initialize();

      // Set up callback for data updates
      dataCollectionService.setOnDataUpdated((type, dataPoint) => {
        // Map the DataCollectionService type to the context type
        const sourceMap: Record<string, 'sectors' | 'anomalies' | 'resources' | 'mixed'> = {
          sector: 'sectors',
          anomaly: 'anomalies',
          resource: 'resources',
        };
        const mappedType = sourceMap[type] || 'mixed';

        // When a new data point is collected, add it to the appropriate dataset
        const datasetId = getOrCreateDatasetBySource(mappedType);
        if (datasetId) {
          addDataPointToDataset(datasetId, dataPoint);
        }
      });

      // Return cleanup function
      return () => {
        if (dataCollectionService) {
          dataCollectionService.dispose();
        }
      };
    }
  }, [explorationManager]);

  // Create a new dataset
  const createDataset = useCallback(
    (dataset: Omit<Dataset, 'id' | 'createdAt' | 'updatedAt'>): string => {
      const id = uuidv4();
      const now = Date.now();
      const newDataset: Dataset = {
        ...dataset,
        id,
        createdAt: now,
        updatedAt: now,
      };
      setDatasets(prev => [...prev, newDataset]);
      return id;
    },
    []
  );

  // Update an existing dataset
  const updateDataset = useCallback(
    (id: string, updates: Partial<Omit<Dataset, 'id' | 'createdAt' | 'updatedAt'>>): void => {
      setDatasets(prev =>
        prev.map(dataset => {
          if (dataset.id === id) {
            return {
              ...dataset,
              ...updates,
              updatedAt: Date.now(),
            };
          }
          return dataset;
        })
      );
    },
    []
  );

  // Delete a dataset
  const deleteDataset = useCallback((id: string): void => {
    setDatasets(prev => prev.filter(dataset => dataset.id !== id));
    // Also delete any analysis configs that use this dataset
    setAnalysisConfigs(prev => prev.filter(config => config.datasetId !== id));
  }, []);

  // Get a dataset by ID
  const getDatasetById = useCallback(
    (id: string): Dataset | undefined => {
      return datasets.find(dataset => dataset.id === id);
    },
    [datasets]
  );

  // Create a new analysis configuration
  const createAnalysisConfig = useCallback(
    (config: Omit<AnalysisConfig, 'id' | 'createdAt' | 'updatedAt'>): string => {
      const id = uuidv4();
      const now = Date.now();
      const newConfig: AnalysisConfig = {
        ...config,
        id,
        createdAt: now,
        updatedAt: now,
      };
      setAnalysisConfigs(prev => [...prev, newConfig]);
      return id;
    },
    []
  );

  // Add a data point to a dataset
  const addDataPointToDataset = useCallback((datasetId: string, dataPoint: DataPoint): void => {
    setDatasets(prev =>
      prev.map(dataset => {
        if (dataset.id === datasetId) {
          // Check if the data point already exists
          const exists = dataset.dataPoints.some(dp => dp.id === dataPoint.id);
          if (exists) return dataset;

          return {
            ...dataset,
            dataPoints: [...dataset.dataPoints, dataPoint],
            updatedAt: Date.now(),
          };
        }
        return dataset;
      })
    );
  }, []);

  // Add multiple data points to a dataset at once - efficient batching
  const addDataPointsToDataset = useCallback((datasetId: string, dataPoints: DataPoint[]): void => {
    setDatasets(prev =>
      prev.map(dataset => {
        if (dataset.id === datasetId) {
          // Filter out data points that already exist
          const existingIds = new Set(dataset.dataPoints.map(dp => dp.id));
          const newDataPoints = dataPoints.filter(dp => !existingIds.has(dp.id));

          if (newDataPoints.length === 0) return dataset;

          return {
            ...dataset,
            dataPoints: [...dataset.dataPoints, ...newDataPoints],
            updatedAt: Date.now(),
          };
        }
        return dataset;
      })
    );
  }, []);

  // Find a dataset by source type, or create one if it doesn't exist
  const getOrCreateDatasetBySource = useCallback(
    (source: 'sectors' | 'anomalies' | 'resources' | 'mixed', name?: string): string => {
      // Find an existing dataset for this source
      const existingDataset = datasets.find(dataset => dataset.source === source);
      if (existingDataset) {
        return existingDataset.id;
      }

      // Create a new dataset if one doesn't exist
      const newDatasetName = name || `${source.charAt(0).toUpperCase() + source.slice(1)} Dataset`;
      return createDataset({
        name: newDatasetName,
        description: `Automatically generated dataset for ${source}`,
        dataPoints: [],
        source,
      });
    },
    [datasets, createDataset]
  );

  // Convert a sector to a data point
  const sectorToDataPoint = useCallback((sector: Sector): DataPoint => {
    return {
      id: sector.id,
      type: 'sector',
      name: sector.name,
      date: sector.discoveredAt || Date.now(),
      coordinates: sector.coordinates,
      properties: {
        status: sector.status,
        resourcePotential: sector.resourcePotential,
        habitabilityScore: sector.habitabilityScore,
        anomalyCount: sector.anomalies?.length || 0,
        resourceCount: sector.resources?.length || 0,
        lastScanned: sector.lastScanned || 0,
      },
    };
  }, []);

  // Convert an anomaly to a data point
  const anomalyToDataPoint = useCallback((anomaly: Anomaly): DataPoint => {
    return {
      id: anomaly.id,
      type: 'anomaly',
      name: `${anomaly.type} Anomaly`,
      date: anomaly.discoveredAt,
      coordinates: anomaly.position,
      properties: {
        type: anomaly.type,
        severity: anomaly.severity,
        description: anomaly.description,
        sectorId: anomaly.sectorId,
        investigated: anomaly.investigatedAt !== undefined,
      },
    };
  }, []);

  // Convert a resource to a data point
  const resourceToDataPoint = useCallback(
    (
      resource: ResourceData,
      sectorId: string,
      coordinates: { x: number; y: number }
    ): DataPoint => {
      return {
        id: `${sectorId}-${resource.type}-${Date.now()}`,
        type: 'resource',
        name: `${resource.type} Resource`,
        date: Date.now(),
        coordinates,
        properties: {
          type: resource.type,
          amount: resource.amount,
          quality: resource.quality || 0,
          sectorId,
        },
      };
    },
    []
  );

  // Subscribe to exploration events
  useEffect(() => {
    // Handle sector discovered events
    const handleSectorDiscovered = (event: BaseEvent) => {
      const { sector } = event.data as { sector: Sector };
      if (!sector) return;

      // Get or create the sectors dataset
      const sectorsDatasetId = getOrCreateDatasetBySource('sectors', 'Explored Sectors');

      // Add the sector as a data point
      const dataPoint = sectorToDataPoint(sector);
      addDataPointToDataset(sectorsDatasetId, dataPoint);
    };

    // Handle anomaly detected events
    const handleAnomalyDetected = (event: BaseEvent) => {
      const { anomaly, sector: _ } = event.data as { anomaly: Anomaly; sector: Sector };
      if (!anomaly) return;

      // Get or create the anomalies dataset
      const anomaliesDatasetId = getOrCreateDatasetBySource('anomalies', 'Detected Anomalies');

      // Add the anomaly as a data point
      const dataPoint = anomalyToDataPoint(anomaly);
      addDataPointToDataset(anomaliesDatasetId, dataPoint);
    };

    // Handle resource detected events
    const handleResourceDetected = (event: BaseEvent) => {
      const { resource, sector } = event.data as { resource: ResourceData; sector: Sector };
      if (!resource || !sector) return;

      // Get or create the resources dataset
      const resourcesDatasetId = getOrCreateDatasetBySource('resources', 'Discovered Resources');

      // Add the resource as a data point
      const dataPoint = resourceToDataPoint(resource, sector.id, sector.coordinates);
      addDataPointToDataset(resourcesDatasetId, dataPoint);
    };

    // Helper function to convert ExplorationEvents to EventType
    const asEventType = (event: keyof typeof EXPLORATION_EVENTS): EventType => {
      return EventType[`EXPLORATION_${event}`] as EventType;
    };

    // Subscribe to exploration events
    const unsubscribeSector = explorationManager.subscribeToEvent(
      EventType.EXPLORATION_SECTOR_DISCOVERED,
      handleSectorDiscovered
    );

    const unsubscribeAnomaly = explorationManager.subscribeToEvent(
      EventType.EXPLORATION_ANOMALY_DETECTED,
      handleAnomalyDetected
    );

    const unsubscribeResource = explorationManager.subscribeToEvent(
      EventType.EXPLORATION_RESOURCE_DETECTED,
      handleResourceDetected
    );

    // Unsubscribe when component unmounts
    return () => {
      unsubscribeSector();
      unsubscribeAnomaly();
      unsubscribeResource();
    };
  }, [
    getOrCreateDatasetBySource,
    sectorToDataPoint,
    anomalyToDataPoint,
    resourceToDataPoint,
    addDataPointToDataset,
  ]);

  // Update analysis configuration
  const updateAnalysisConfig = useCallback(
    (
      id: string,
      updates: Partial<Omit<AnalysisConfig, 'id' | 'createdAt' | 'updatedAt'>>
    ): void => {
      setAnalysisConfigs(prev =>
        prev.map(config => {
          if (config.id === id) {
            return {
              ...config,
              ...updates,
              updatedAt: Date.now(),
            };
          }
          return config;
        })
      );
    },
    []
  );

  // Delete an analysis configuration
  const deleteAnalysisConfig = useCallback((id: string): void => {
    setAnalysisConfigs(prev => prev.filter(config => config.id !== id));
  }, []);

  // Get an analysis configuration by ID
  const getAnalysisConfigById = useCallback(
    (id: string): AnalysisConfig | undefined => {
      return analysisConfigs.find(config => config.id === id);
    },
    [analysisConfigs]
  );

  // Run an analysis using the worker for heavy operations
  const runAnalysis = useCallback(
    async (configId: string): Promise<string> => {
      const config = analysisConfigs.find(config => config.id === configId);
      if (!config) {
        throw new Error(`Analysis configuration with ID ${configId} not found`);
      }

      const dataset = datasets.find(dataset => dataset.id === config.datasetId);
      if (!dataset) {
        throw new Error(`Dataset with ID ${config.datasetId} not found`);
      }

      // Create a pending result
      const pendingResultId = uuidv4();
      const pendingResult: AnalysisResult = {
        id: pendingResultId,
        analysisConfigId: configId,
        status: 'pending',
        startTime: Date.now(),
        data: {},
      };

      setAnalysisResults(prev => [...prev, pendingResult]);
      setIsProcessingData(true);

      try {
        let result: AnalysisResult;

        // Use the data processing service for offloading work if available
        if (dataProcessingServiceRef.current) {
          try {
            // Determine which worker processing method to use based on analysis type
            let processedData;

            // Handle special case for transformation type
            if ((config.type as string) === 'transformation') {
              processedData = (await dataProcessingServiceRef.current.transformData(
                dataset.dataPoints
              )) as Record<string, unknown>;
            } else {
              switch (config.type) {
                case 'clustering':
                  processedData = await dataProcessingServiceRef.current.processClustering(
                    dataset.dataPoints
                  );
                  break;
                case 'prediction':
                  processedData = await dataProcessingServiceRef.current.processPrediction(
                    dataset.dataPoints
                  );
                  break;
                case 'comparison':
                  processedData = await dataProcessingServiceRef.current.processResourceMapping(
                    dataset.dataPoints
                  );
                  break;
                default:
                  // For other types, use the local analysis algorithm service
                  if (analysisAlgorithmServiceRef.current) {
                    result = await analysisAlgorithmServiceRef.current.runAnalysis(config, dataset);
                  } else {
                    result = await runBasicAnalysis(config, dataset);
                  }

                  // Update the analysis results with the worker-processed data
                  setAnalysisResults(prev =>
                    prev.map(r => (r.id === pendingResultId ? result : r))
                  );
                  setIsProcessingData(false);
                  return result.id;
              }
            }

            // Create a result object with the processed data
            result = {
              id: pendingResultId,
              analysisConfigId: config.id,
              status: 'completed',
              startTime: pendingResult.startTime,
              endTime: Date.now(),
              data: processedData as Record<string, unknown>,
              summary: `Analysis completed successfully with ${
                Object.keys(processedData || {}).length
              } data points.`,
            };
          } catch (error) {
            console.error('Worker processing error:', error);
            // Fallback to main thread processing
            if (analysisAlgorithmServiceRef.current) {
              result = await analysisAlgorithmServiceRef.current.runAnalysis(config, dataset);
            } else {
              result = await runBasicAnalysis(config, dataset);
            }
          }
        } else if (analysisAlgorithmServiceRef.current) {
          // Use the main thread analysis service if worker is not available
          result = await analysisAlgorithmServiceRef.current.runAnalysis(config, dataset);
        } else {
          // Fallback to a basic implementation
          result = await runBasicAnalysis(config, dataset);
        }

        // Update the analysis results
        setAnalysisResults(prev => prev.map(r => (r.id === pendingResultId ? result : r)));
        setIsProcessingData(false);
        return result.id;
      } catch (error) {
        // Create a failed result
        const failedResult: AnalysisResult = {
          id: pendingResultId,
          analysisConfigId: configId,
          status: 'failed',
          startTime: pendingResult.startTime,
          endTime: Date.now(),
          data: {},
          error: error instanceof Error ? error.message : 'Unknown error',
        };

        // Update the analysis results
        setAnalysisResults(prev => prev.map(r => (r.id === pendingResultId ? failedResult : r)));
        setIsProcessingData(false);
        throw error;
      }
    },
    [analysisConfigs, datasets]
  );

  // Add a function to run basic analysis if the service is not available
  const runBasicAnalysis = async (
    config: AnalysisConfig,
    dataset: Dataset
  ): Promise<AnalysisResult> => {
    // Simulate analysis by delaying for a short time
    await new Promise(resolve => setTimeout(resolve, 500));

    return {
      id: uuidv4(),
      analysisConfigId: config.id,
      status: 'completed',
      startTime: Date.now() - 500, // Started 500ms ago
      endTime: Date.now(),
      data: {
        config,
        datasetSize: dataset.dataPoints.length,
        message: 'Basic analysis completed without the analysis service',
      },
      summary: `Analyzed ${dataset.dataPoints.length} data points using the ${config.type} analysis type.`,
    };
  };

  // Refresh data with worker-based filtering and sorting
  const refreshData = useCallback(async () => {
    if (!dataCollectionServiceRef.current) return;
    setIsProcessingData(true);

    try {
      // Get all data from the collection service
      const sectorData = dataCollectionServiceRef.current.getSectorData();
      const anomalyData = dataCollectionServiceRef.current.getAnomalyData();
      const resourceData = dataCollectionServiceRef.current.getResourceData();

      // Create or update datasets for each data type with correct mapping
      const sectorDatasetId = getOrCreateDatasetBySource('sectors');
      const anomalyDatasetId = getOrCreateDatasetBySource('anomalies');
      const resourceDatasetId = getOrCreateDatasetBySource('resources');

      // Use the worker for batch processing if available
      if (dataProcessingServiceRef.current) {
        // Process data in batches using the worker
        if (sectorDatasetId && sectorData.length > 0) {
          await addDataPointsBatch(sectorDatasetId, sectorData);
        }

        if (anomalyDatasetId && anomalyData.length > 0) {
          await addDataPointsBatch(anomalyDatasetId, anomalyData);
        }

        if (resourceDatasetId && resourceData.length > 0) {
          await addDataPointsBatch(resourceDatasetId, resourceData);
        }
      } else {
        // Fallback to standard processing
        if (sectorDatasetId) {
          for (const dataPoint of sectorData) {
            addDataPointToDataset(sectorDatasetId, dataPoint);
          }
        }

        if (anomalyDatasetId) {
          for (const dataPoint of anomalyData) {
            addDataPointToDataset(anomalyDatasetId, dataPoint);
          }
        }

        if (resourceDatasetId) {
          for (const dataPoint of resourceData) {
            addDataPointToDataset(resourceDatasetId, dataPoint);
          }
        }
      }
    } finally {
      setIsProcessingData(false);
    }
  }, [getOrCreateDatasetBySource, addDataPointToDataset]);

  // Helper function to add data points in batches using the worker
  const addDataPointsBatch = async (datasetId: string, dataPoints: DataPoint[]): Promise<void> => {
    if (!dataProcessingServiceRef.current || dataPoints.length === 0) return;

    try {
      // Use the worker to filter out duplicates
      const existingDataset = datasets.find(ds => ds.id === datasetId);
      if (!existingDataset) return;

      const existingIds = new Set(existingDataset.dataPoints.map(dp => dp.id));

      // Filter out duplicate data points - can use the worker for this with large datasets
      const uniqueDataPoints = dataPoints.filter(dp => !existingIds.has(dp.id));

      if (uniqueDataPoints.length === 0) return;

      // Add the filtered data points to the dataset
      addDataPointsToDataset(datasetId, uniqueDataPoints);
    } catch (error) {
      console.error('Error in batch processing:', error);
      // Fallback to individual adds
      for (const dataPoint of dataPoints) {
        addDataPointToDataset(datasetId, dataPoint);
      }
    }
  };

  // Worker-based filtering for datasets
  const filterDataset = useCallback(
    async (
      datasetId: string,
      filters: Array<{
        field: string;
        operator:
          | 'equals'
          | 'notEquals'
          | 'greaterThan'
          | 'lessThan'
          | 'contains'
          | 'notContains'
          | 'between';
        value: string | number | boolean | string[] | [number, number];
      }>
    ): Promise<DataPoint[]> => {
      const dataset = datasets.find(ds => ds.id === datasetId);
      if (!dataset) return [];

      setIsProcessingData(true);

      try {
        // Use the worker for filtering if available
        if (dataProcessingServiceRef.current && dataset.dataPoints.length > 100) {
          try {
            // Offload filtering to worker for large datasets
            const filteredData = await dataProcessingServiceRef.current.filterData(
              dataset.dataPoints,
              filters.map(filter => ({
                key: filter.field,
                operator: mapOperator(filter.operator),
                value: filter.value,
              }))
            );
            setIsProcessingData(false);
            return filteredData as DataPoint[];
          } catch (error) {
            console.error('Worker filtering error:', error);
            // Continue to fallback
          }
        }

        // Fallback to main thread filtering
        if (!dataCollectionServiceRef.current) {
          // Simple filtering if the service is not available
          const filteredData = dataset.dataPoints.filter(dataPoint =>
            filters.every(filter => {
              // Treat DataPoint as a Record with unknown values for filtering
              const value = getNestedProperty(
                dataPoint as unknown as Record<string, unknown>,
                filter.field
              );

              switch (filter.operator) {
                case 'equals':
                  return value === filter.value;
                case 'notEquals':
                  return value !== filter.value;
                case 'greaterThan':
                  return (
                    typeof value === 'number' &&
                    typeof filter.value === 'number' &&
                    value > filter.value
                  );
                case 'lessThan':
                  return (
                    typeof value === 'number' &&
                    typeof filter.value === 'number' &&
                    value < filter.value
                  );
                case 'contains':
                  if (typeof value === 'string' && typeof filter.value === 'string') {
                    return value.toLowerCase().includes(filter.value.toLowerCase());
                  }
                  if (Array.isArray(value)) {
                    return value.includes(filter.value);
                  }
                  return false;
                case 'notContains':
                  if (typeof value === 'string' && typeof filter.value === 'string') {
                    return !value.toLowerCase().includes(filter.value.toLowerCase());
                  }
                  if (Array.isArray(value)) {
                    return !value.includes(filter.value);
                  }
                  return false;
                case 'between':
                  if (
                    typeof value === 'number' &&
                    Array.isArray(filter.value) &&
                    filter.value.length === 2
                  ) {
                    const [min, max] = filter.value as [number, number];
                    return value >= min && value <= max;
                  }
                  return false;
                default:
                  return false;
              }
            })
          );
          setIsProcessingData(false);
          return filteredData;
        }

        // Use the data collection service's filtering capability
        const filteredData = dataCollectionServiceRef.current.filterData(
          dataset.dataPoints,
          filters
        );
        setIsProcessingData(false);
        return filteredData;
      } catch (error) {
        setIsProcessingData(false);
        console.error('Error filtering data:', error);
        return [];
      }
    },
    [datasets]
  );

  // Fix the getNestedProperty function with proper typing
  const getNestedProperty = (obj: Record<string, unknown>, path: string): unknown => {
    const parts = path.split('.');
    let current: unknown = obj;

    for (const part of parts) {
      if (current === undefined || current === null) {
        return undefined;
      }

      if (typeof current === 'object' && part in current) {
        current = (current as Record<string, unknown>)[part];
      } else {
        return undefined;
      }
    }

    return current;
  };

  // Get an analysis result by ID
  const getAnalysisResultById = useCallback(
    (id: string): AnalysisResult | undefined => {
      return analysisResults.find(result => result.id === id);
    },
    [analysisResults]
  );

  // Get analysis results by config ID
  const getAnalysisResultsByConfigId = useCallback(
    (configId: string): AnalysisResult[] => {
      return analysisResults.filter(result => result.analysisConfigId === configId);
    },
    [analysisResults]
  );

  // Helper function to map filter operators
  const mapOperator = (
    operator:
      | 'equals'
      | 'notEquals'
      | 'greaterThan'
      | 'lessThan'
      | 'contains'
      | 'notContains'
      | 'between'
  ): '==' | '!=' | '>' | '<' | '>=' | '<=' | 'contains' | 'startsWith' | 'endsWith' => {
    switch (operator) {
      case 'equals':
        return '==';
      case 'notEquals':
        return '!=';
      case 'greaterThan':
        return '>';
      case 'lessThan':
        return '<';
      case 'contains':
        return 'contains';
      // For 'between' and 'notContains', we'll need custom handling in the filter function
      // For now, default to a reasonable operator
      case 'notContains':
        return '!=';
      case 'between':
        return '>=';
      default:
        return '==';
    }
  };

  // Create the context value object
  const contextValue: DataAnalysisContextType = {
    datasets,
    analysisConfigs,
    analysisResults,
    createDataset,
    updateDataset,
    deleteDataset,
    getDatasetById,
    createAnalysisConfig,
    updateAnalysisConfig,
    deleteAnalysisConfig,
    getAnalysisConfigById,
    runAnalysis,
    getAnalysisResultById,
    getAnalysisResultsByConfigId,
    getOrCreateDatasetBySource,
    addDataPointToDataset,
    refreshData,
    // Use the original filterDataset function but make it synchronous
    filterDataset: (datasetId, filters) => {
      // This is a workaround to convert the async function to a sync one
      // In a real application, you would refactor the interface to be async
      const emptyResult: DataPoint[] = [];

      // Start the async process but return empty results immediately
      setTimeout(() => {
        filterDataset(datasetId, filters)
          .then(results => {
            console.warn(`Filtered ${results.length} results for dataset ${datasetId}`);
          })
          .catch(error => {
            console.error('Error in filterDataset:', error);
          });
      }, 0);

      return emptyResult;
    },
  };

  return (
    <DataAnalysisContext.Provider value={contextValue}>{children}</DataAnalysisContext.Provider>
  );
};

/**
 * Hook to use the DataAnalysisContext
 */
export const useDataAnalysis = (): DataAnalysisContextType => {
  const context = useContext(DataAnalysisContext);
  if (context === undefined) {
    throw new Error('useDataAnalysis must be used within a DataAnalysisProvider');
  }
  return context;
};

// Export the context for testing
export { DataAnalysisContext };

import * as React from 'react';
import { ResourceType, ResourceTypeHelpers } from './../types/resources/ResourceTypes';
import { ThresholdAction, ThresholdState, initialState, thresholdEvents } from './ThresholdTypes';

// Types
export interface Resource {
  id: string;
  name: string;
  type: ResourceType;
  currentAmount: number;
  maxCapacity: number;
  thresholds: {
    min: number;
    max: number;
  };
  autoMine: boolean;
}

export interface ThresholdPreset {
  id: string;
  name: string;
  description: string;
  thresholds: Record<string, { min: number; max: number }>;
  autoMineStates: Record<string, boolean>;
}

export interface ThresholdHistoryEntry {
  timestamp: number;
  resourceId: ResourceType;
  amount: number;
  thresholds: {
    min: number;
    max: number;
  };
  event: 'threshold_change' | 'amount_update' | 'auto_mine_toggle';
}

interface ThresholdContextType {
  state: ThresholdState;
  dispatch: React.Dispatch<ThresholdAction>;
}

// Helper function to get resource name for display
const getResourceName = (resourceType: ResourceType): string => {
  return ResourceTypeHelpers.getDisplayName(resourceType);
};

// Reducer
function thresholdReducer(state: ThresholdState, action: ThresholdAction): ThresholdState {
  switch (action.type) {
    case 'SET_THRESHOLD': {
      const historyEntry = {
        timestamp: Date.now(),
        resourceId: action.payload.resourceId,
        amount: state.resources[action.payload.resourceId]?.currentAmount || 0,
        thresholds: {
          min: action.payload.min,
          max: action.payload.max,
        },
        event: 'threshold_change' as const,
      };

      return {
        ...state,
        resources: {
          ...state.resources,
          [action.payload.resourceId]: {
            ...state.resources[action.payload.resourceId],
            thresholds: {
              min: action.payload.min,
              max: action.payload.max,
            },
          },
        },
        history: [...state.history, historyEntry].slice(-100),
      };
    }

    case 'UPDATE_AMOUNT': {
      const resource = state.resources[action.payload.resourceId];
      if (!resource) {
        return state;
      }

      const updatedResource = {
        ...resource,
        currentAmount: action.payload.amount,
      };

      const historyEntry = {
        timestamp: Date.now(),
        resourceId: action.payload.resourceId,
        amount: action.payload.amount,
        thresholds: resource.thresholds,
        event: 'amount_update' as const,
      };

      // Check for threshold violations
      if (updatedResource.autoMine) {
        if (updatedResource.currentAmount < updatedResource.thresholds.min) {
          thresholdEvents.next({
            type: 'THRESHOLD_VIOLATED',
            resourceId: action.payload.resourceId,
            details: {
              type: 'below_minimum',
              current: updatedResource.currentAmount,
              min: updatedResource.thresholds.min,
            },
          });
        } else if (updatedResource.currentAmount > updatedResource.thresholds.max) {
          thresholdEvents.next({
            type: 'STORAGE_FULL',
            resourceId: action.payload.resourceId,
            details: {
              type: 'above_maximum',
              current: updatedResource.currentAmount,
              max: updatedResource.thresholds.max,
            },
          });
        }
      }

      return {
        ...state,
        resources: {
          ...state.resources,
          [action.payload.resourceId]: updatedResource,
        },
        history: [...state.history, historyEntry].slice(-100),
      };
    }

    case 'TOGGLE_AUTO_MINE': {
      const resource = state.resources[action.payload.resourceId];
      if (!resource) {
        return state;
      }

      const newAutoMine = !resource.autoMine;

      const historyEntry = {
        timestamp: Date.now(),
        resourceId: action.payload.resourceId,
        amount: resource.currentAmount,
        thresholds: resource.thresholds,
        event: 'auto_mine_toggle' as const,
      };

      if (newAutoMine) {
        thresholdEvents.next({
          type: 'AUTO_MINE_TRIGGERED',
          resourceId: action.payload.resourceId,
          details: { type: 'below_minimum', current: resource.currentAmount },
        });
      }

      return {
        ...state,
        resources: {
          ...state.resources,
          [action.payload.resourceId]: {
            ...resource,
            autoMine: newAutoMine,
          },
        },
        history: [...state.history, historyEntry].slice(-100),
      };
    }

    case 'ADD_PRESET':
      return {
        ...state,
        presets: [...state.presets, action.payload],
      };

    case 'REMOVE_PRESET':
      return {
        ...state,
        presets: state.presets.filter(preset => preset.id !== action.payload.presetId),
        activePresetId:
          state.activePresetId === action.payload.presetId ? null : state.activePresetId,
      };

    case 'APPLY_PRESET': {
      const preset = state.presets.find(p => p.id === action.payload.presetId);
      if (!preset) {
        return state;
      }

      const updatedResources = { ...state.resources };
      Object.entries(preset.thresholds).forEach(([resourceId, thresholds]) => {
        if (updatedResources[resourceId]) {
          updatedResources[resourceId] = {
            ...updatedResources[resourceId],
            thresholds,
            autoMine: preset.autoMineStates[resourceId] || false,
          };
        }
      });

      return {
        ...state,
        resources: updatedResources,
        activePresetId: action.payload.presetId,
      };
    }

    case 'SET_GLOBAL_AUTO_MINE': {
      const updatedResources = Object.entries(state.resources).reduce(
        (acc, [id, resource]) => ({
          ...acc,
          [id]: { ...resource, autoMine: action.payload },
        }),
        {}
      );

      return {
        ...state,
        globalAutoMine: action.payload,
        resources: updatedResources,
      };
    }

    case 'ADD_NOTIFICATION':
      return {
        ...state,
        notifications: [...state.notifications, action.payload],
      };

    case 'CLEAR_NOTIFICATION':
      return {
        ...state,
        notifications: state.notifications.filter((_, index) => index !== action.payload),
      };

    case 'ADD_RESOURCE':
      return {
        ...state,
        resources: {
          ...state.resources,
          [action.payload.id]: action.payload,
        },
      };

    case 'REMOVE_RESOURCE': {
      const remainingResources = { ...state.resources };
      delete remainingResources[action.payload.resourceId];
      return {
        ...state,
        resources: remainingResources,
      };
    }

    case 'ADD_HISTORY_ENTRY':
      return {
        ...state,
        history: [...state.history, action.payload].slice(-100),
      };

    default:
      return state;
  }
}

// Context
const ThresholdContext = React.createContext<ThresholdContextType | undefined>(undefined);

// Provider
export function ThresholdProvider({ children }: { children: React.ReactNode }) {
  const [state, dispatch] = React.useReducer(thresholdReducer, initialState);

  return (
    <ThresholdContext.Provider value={{ state, dispatch }}>{children}</ThresholdContext.Provider>
  );
}

// Hook
export function useThreshold() {
  const context = React.useContext(ThresholdContext);
  if (context === undefined) {
    throw new Error('useThreshold must be used within a ThresholdProvider');
  }
  return context;
}

import * as React from "react";
import { ErrorBoundary, ErrorBoundaryProps } from './ErrorBoundary';

/**
 * Global error boundary props
 */
export interface GlobalErrorBoundaryProps extends Omit<ErrorBoundaryProps, 'context' | 'fallback'> {
  /** Whether this is the root boundary (changes styling) */
  isRoot?: boolean;
}

/**
 * Global application error boundary
 * 
 * This component is designed to be used at the application root level
 * to catch any unhandled errors that bubble up through the component tree.
 */
export const GlobalErrorBoundary: React.FC<GlobalErrorBoundaryProps> = ({
  children,
  isRoot = false,
  onError,
  suppressErrorLogging,
  metadata,
  ...props
}) => {
  const handleReset = () => {
    // For a root-level error, we might want to refresh the page
    if (isRoot) {
      window.location.reload();
      return;
    }

    // Otherwise, just clear any application state as needed
    // This could include resetting global state, clearing caches, etc.
  };

  // Custom fallback UI for global error
  const globalFallback = (error: Error, reset: () => void) => {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
        <div className="max-w-lg w-full bg-white rounded-lg shadow-lg p-8">
          <div className="flex items-center mb-6">
            <div className="bg-red-100 p-3 rounded-full mr-4">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-6 w-6 text-red-600"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
                />
              </svg>
            </div>
            <h1 className="text-xl font-bold text-gray-800">
              Something went wrong
            </h1>
          </div>

          <p className="text-gray-600 mb-6">
            We're sorry, but something went wrong. Our team has been notified and is working to fix the issue.
          </p>

          {process.env.NODE_ENV !== 'production' && (
            <div className="mb-6 bg-gray-50 p-4 rounded border border-gray-200">
              <h3 className="font-medium text-gray-800 mb-2">Error Details:</h3>
              <pre className="text-sm text-red-600 whitespace-pre-wrap break-all">
                {error.message}
              </pre>
              {error.stack && (
                <details className="mt-2">
                  <summary className="cursor-pointer text-sm font-medium text-gray-600">
                    Stack trace
                  </summary>
                  <pre className="mt-2 text-xs text-gray-600 whitespace-pre-wrap break-all">
                    {error.stack}
                  </pre>
                </details>
              )}
            </div>
          )}

          <div className="flex justify-between">
            <button
              onClick={() => reset()}
              className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition-colors"
            >
              {isRoot ? 'Reload Application' : 'Try Again'}
            </button>

            <button
              onClick={() => {
                // Navigate to a safe page, like the home page
                window.location.href = '/';
              }}
              className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300 transition-colors"
            >
              Return to Home
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <ErrorBoundary
      fallback={globalFallback}
      context="Global"
      onError={(error, errorInfo) => {
        // For root errors, we might want to log analytics events or notify monitoring services
        if (isRoot) {
          // Example: Log to analytics
          try {
            if (window.gtag) {
              window.gtag('event', 'error', {
                'event_category': 'Error',
                'event_label': error.message,
                'value': 1
              });
            }
          } catch (e) {
            console.error('Failed to log error to analytics:', e);
          }
        }

        // Call original onError handler
        if (onError) {
          onError(error, errorInfo);
        }
      }}
      suppressErrorLogging={suppressErrorLogging}
      metadata={{
        isRoot,
        ...metadata,
      }}
      {...props}
    >
      {children}
    </ErrorBoundary>
  );
};

