import * as React from "react";
import { ComponentType, ErrorInfo, ReactNode } from 'react';
import { ErrorBoundary, ErrorBoundaryProps } from '../ErrorBoundary';

/**
 * Props for the fallback component created by createTypedErrorBoundary
 */
export interface TypedErrorFallbackProps {
  /** The error that occurred */
  error: Error;
  /** Component name for error reporting */
  componentName: string;
  /** (...args: unknown[]) => unknown to reset the error boundary */
  resetError: () => void;
  /** Additional error context information */
  errorContext?: Record<string, unknown>;
}

/**
 * Default fallback component for typed error boundaries
 */
export const TypedErrorFallback: React.FC<TypedErrorFallbackProps> = ({
  error,
  componentName,
  resetError,
  errorContext,
}) => (
  <div className="p-4 rounded border border-red-300 bg-red-50 text-red-800">
    <h3 className="font-semibold mb-2">Error in {componentName}</h3>
    <p className="mb-2">An error occurred while rendering the {componentName} component:</p>
    <pre className="p-2 bg-red-100 rounded mb-3 text-sm overflow-auto">{error.message}</pre>
    {errorContext && (
      <div className="mb-3">
        <h4 className="font-medium mb-1">Error Context:</h4>
        <pre className="p-2 bg-red-100 rounded text-xs overflow-auto">
          {JSON.stringify(errorContext, null, 2)}
        </pre>
      </div>
    )}
    <button
      onClick={resetError}
      className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
    >
      Reset Component
    </button>
  </div>
);

/**
 * Create a typed error boundary for a specific component type
 * 
 * This function wraps a component with an error boundary that is specifically
 * typed for that component's props, providing a clean way to create "safe"
 * versions of components.
 * 
 * @param Component The React component to wrap with an error boundary
 * @param componentName Name of the component for error reporting
 * @param FallbackComponent Custom fallback component
 * @param onError Custom error handler function
 * @returns A wrapped component with an error boundary
 */
export function createTypedErrorBoundary<P extends object>(
  Component: ComponentType<P>,
  componentName: string,
  FallbackComponent?: React.ComponentType<TypedErrorFallbackProps>,
  onError?: (error: Error, errorInfo: ErrorInfo, componentName: string) => void
): React.FC<P & { errorContext?: Record<string, unknown>; resetKeys?: unknown[] }> {
  return (props: P & { errorContext?: Record<string, unknown>; resetKeys?: unknown[] }) => {
    const { errorContext, resetKeys, ...componentProps } = props;

    // Custom error handler that includes the component name
    const handleError = (error: Error, errorInfo: ErrorInfo) => {
      console.error(`Error in ${componentName}:`, error);
      console.error('Component Stack:', errorInfo.componentStack);

      if (onError) {
        onError(error, errorInfo, componentName);
      }
    };

    // Custom fallback UI that includes the component name
    const fallback = (error: Error, reset: () => void) => {
      const FallbackUI = FallbackComponent || TypedErrorFallback;

      return (
        <FallbackUI
          error={error}
          componentName={componentName}
          resetError={reset}
          errorContext={errorContext}
        />
      );
    };

    return (
      <ErrorBoundary
        fallback={fallback}
        onError={handleError}
        context={componentName}
        resetKeys={resetKeys}
        metadata={{
          componentName,
          ...errorContext,
        }}
      >
        <Component {...(componentProps as P)} />
      </ErrorBoundary>
    );
  };
}

/**
 * Options for the withErrorBoundary HOC
 */
export interface WithErrorBoundaryOptions extends Omit<ErrorBoundaryProps, 'children'> {
  /** Component name for error reporting */
  componentName?: string;
}

/**
 * Higher-order component that wraps a component with an error boundary
 * 
 * This is a more flexible alternative to createTypedErrorBoundary that allows
 * passing any ErrorBoundary props.
 * 
 * @param Component The component to wrap
 * @param options Options for the error boundary
 * @returns A wrapped component with an error boundary
 */
export function withErrorBoundary<P extends object>(
  Component: ComponentType<P>,
  options: WithErrorBoundaryOptions = {}
): React.FC<P> {
  const { componentName = Component.displayName || Component.name || 'UnknownComponent', ...errorBoundaryProps } = options;

  // Set a display name for the wrapped component
  const wrappedComponentName = `WithErrorBoundary(${componentName})`;

  const WrappedComponent: React.FC<P> = (props) => {
    return (
      <ErrorBoundary 
        context={componentName}
        {...errorBoundaryProps}
      >
        <Component {...props} />
      </ErrorBoundary>
    );
  };

  // Set display name for better debugging
  WrappedComponent.displayName = wrappedComponentName;

  return WrappedComponent;
}

/**
 * Create safer versions of components by wrapping them with error boundaries
 * 
 * This utility function takes a record of components and wraps each one with
 * an error boundary, creating a new record of "safe" components.
 * 
 * @param components Record of components to wrap
 * @param getOptions (...args: unknown[]) => unknown to generate options for each component
 * @returns Record of wrapped components
 */
export function createSafeComponents<T extends Record<string, ComponentType<any>>>(
  components: T,
  getOptions?: (key: string, Component: ComponentType<any>) => WithErrorBoundaryOptions
): { [K in keyof T]: React.FC<React.ComponentProps<T[K]>> } {
  const safeComponents = {} as { [K in keyof T]: React.FC<React.ComponentProps<T[K]>> };

  for (const key in components) {
    const Component = components[key];
    const options = getOptions ? getOptions(key, Component) : { componentName: key as string };

    safeComponents[key] = withErrorBoundary(Component, options);
  }

  return safeComponents;
}

/**
 * Create specialized versions of error boundaries
 * 
 * This utility function creates a specialized error boundary component
 * for specific contexts or use cases.
 * 
 * @param defaultProps Default props to apply to the error boundary
 * @returns A specialized error boundary component
 */
export function createSpecializedErrorBoundary(
  defaultProps: Omit<ErrorBoundaryProps, 'children'>
): React.FC<ErrorBoundaryProps> {
  const SpecializedErrorBoundary: React.FC<ErrorBoundaryProps> = (props) => {
    return (
      <ErrorBoundary
        {...defaultProps}
        {...props}
        metadata={{
          ...defaultProps.metadata,
          ...props.metadata,
        }}
      />
    );
  };

  return SpecializedErrorBoundary;
}

import { useEffect, useState } from 'react';
import { BehaviorEvents, behaviorTreeManager } from '../../managers/ai/BehaviorTreeManager';
import { getCombatManager } from '../../managers/ManagerRegistry';
import { CombatUnitStatus } from '../../types/combat/CombatTypes';
import { CombatUnitDamageEvent, CombatUnitStatusEvent } from '../../types/events/CombatEvents';
import { FactionId } from '../../types/ships/FactionTypes';
import { WeaponCategory, WeaponStatus } from '../../types/weapons/WeaponTypes';

// Define formation interface to replace any type
interface UnitFormation {
  type: 'offensive' | 'defensive' | 'balanced';
  spacing: number;
  facing: number;
}

export function useCombatAI(unitId: string, factionId: FactionId) {
  const [status, setStatus] = useState<CombatUnitStatus>({
    main: 'active',
    effects: [],
  });
  const [performance, setPerformance] = useState({
    successRate: 0,
    damageDealt: 0,
    damageTaken: 0,
    killCount: 0,
  });

  useEffect(() => {
    const combatManager = getCombatManager();

    const handleNodeExecuted = (event: BehaviorEvents['nodeExecuted']) => {
      // Update performance metrics based on node execution
      setPerformance(prev => ({
        ...prev,
        successRate: prev.successRate * 0.9 + (event.success ? 0.1 : 0),
      }));
    };

    const handleActionStarted = (event: BehaviorEvents['actionStarted']) => {
      if (event.unitId === unitId) {
        // Update status based on action type
        setStatus(prev => ({
          ...prev,
          secondary: 'charging',
        }));
      }
    };

    const handleTreeCompleted = (event: BehaviorEvents['treeCompleted']) => {
      if (event.unitId === unitId) {
        // Update performance metrics
        setPerformance(prev => ({
          ...prev,
          successRate: prev.successRate * 0.9 + (event.success ? 0.1 : 0),
        }));
      }
    };

    // Set up subscriptions
    const unsubscribeNodeExecuted = behaviorTreeManager.on('nodeExecuted', handleNodeExecuted);
    const unsubscribeActionStarted = behaviorTreeManager.on('actionStarted', handleActionStarted);
    const unsubscribeTreeCompleted = behaviorTreeManager.on('treeCompleted', handleTreeCompleted);

    // Subscribe to combat events for performance tracking
    const unsubscribeDamaged = combatManager.on(
      'combat:unit-damaged',
      (event: CombatUnitDamageEvent) => {
        if (event.unitId === unitId) {
          setPerformance(prev => ({
            ...prev,
            damageTaken: prev.damageTaken + event.damageAmount,
          }));
        } else if (event.damageSource === unitId) {
          setPerformance(prev => ({
            ...prev,
            damageDealt: prev.damageDealt + event.damageAmount,
          }));
        }
      }
    );

    const unsubscribeStatusChanged = combatManager.on(
      'combat:unit-status-changed',
      (event: CombatUnitStatusEvent) => {
        if (event.unitId === unitId) {
          // Convert string status to CombatUnitStatus object
          setStatus({
            main: event.status === 'destroyed' ? 'destroyed' : 'active',
            effects: [],
          });
        }
      }
    );

    // Update behavior tree periodically
    const updateInterval = setInterval(() => {
      const unit = combatManager.getUnitStatus?.(unitId);
      if (!unit) return;

      const convertStatus = (status: string): CombatUnitStatus => ({
        main: status === 'destroyed' ? 'destroyed' : status === 'disabled' ? 'disabled' : 'active',
        effects: [],
      });

      const nearbyEnemies = (
        combatManager
          .getUnitsInRange?.(unit.position, 500)
          ?.filter(other => other.faction !== factionId) || []
      ).map(unit => ({
        ...unit,
        velocity: { x: 0, y: 0 }, // Add default velocity for units
        status: convertStatus(unit.status),
        weapons: unit.weapons.map(weapon => ({
          id: weapon.id,
          type: weapon.type as WeaponCategory,
          damage: weapon.damage,
          range: weapon.range,
          cooldown: weapon.cooldown,
          status: weapon.status as WeaponStatus,
        })),
        stats: {
          ...unit.stats,
          armor: 0, // Add default armor value
        },
      }));

      const nearbyAllies = (
        combatManager
          .getUnitsInRange?.(unit.position, 500)
          ?.filter(other => other.faction === factionId && other.id !== unitId) || []
      ).map(unit => ({
        ...unit,
        velocity: { x: 0, y: 0 }, // Add default velocity for units
        status: convertStatus(unit.status),
        weapons: unit.weapons.map(weapon => ({
          id: weapon.id,
          type: weapon.type as WeaponCategory,
          damage: weapon.damage,
          range: weapon.range,
          cooldown: weapon.cooldown,
          status: weapon.status as WeaponStatus,
        })),
        stats: {
          ...unit.stats,
          armor: 0, // Add default armor value
        },
      }));

      // Update behavior tree context
      behaviorTreeManager.updateContext?.(unitId, {
        unit: {
          ...unit,
          velocity: { x: 0, y: 0 }, // Add default velocity
          status: convertStatus(unit.status),
          weapons: unit.weapons.map(weapon => ({
            id: weapon.id,
            type: weapon.type as WeaponCategory,
            damage: weapon.damage,
            range: weapon.range,
            cooldown: weapon.cooldown,
            status: weapon.status as WeaponStatus,
          })),
          stats: {
            ...unit.stats,
            armor: 0, // Add default armor value
          },
          target: unit.target?.id,
        },
        factionId,
        fleetStrength: nearbyAllies.reduce((sum, ally) => sum + ally.stats.health, 0),
        threatLevel: nearbyEnemies.reduce((sum, enemy) => sum + enemy.stats.health, 0),
        nearbyEnemies,
        nearbyAllies,
        currentFormation: {
          type: 'balanced',
          spacing: 100,
          facing: 0,
        },
        lastAction: undefined,
        cooldowns: {},
      });

      // Evaluate behavior tree
      const treeId = `${factionId}-combat`;
      const success = behaviorTreeManager.evaluateTree?.(unitId, treeId) || false;

      setPerformance(prev => ({
        ...prev,
        successRate: prev.successRate * 0.9 + (success ? 0.1 : 0),
      }));
    }, 1000);

    return () => {
      clearInterval(updateInterval);
      if (typeof unsubscribeNodeExecuted === 'function') unsubscribeNodeExecuted();
      if (typeof unsubscribeActionStarted === 'function') unsubscribeActionStarted();
      if (typeof unsubscribeTreeCompleted === 'function') unsubscribeTreeCompleted();
      if (typeof unsubscribeDamaged === 'function') unsubscribeDamaged();
      if (typeof unsubscribeStatusChanged === 'function') unsubscribeStatusChanged();
    };
  }, [unitId, factionId]);

  return {
    status,
    performance,
  };
}

import { useCallback, useEffect, useState } from 'react';
import { getCombatManager } from '../../managers/ManagerRegistry';
import { FleetFormation } from '../../types/combat/CombatTypes';
import { Position } from '../../types/core/GameTypes';
import { CombatUnitStatus } from '../../types/events/CombatEvents';

/**
 * Enhanced combat system hook for managing fleet formations and tactics
 */
export function useCombatSystem() {
  const [threatLevel, setThreatLevel] = useState(0);
  const [activeUnits, setActiveUnits] = useState(0);
  const [isActive, setIsActive] = useState(false);
  const [activeFleets, setActiveFleets] = useState<string[]>([]);
  const [formations, setFormations] = useState<Record<string, FleetFormation>>({});

  useEffect(() => {
    const combatManager = getCombatManager();

    // Subscribe to combat events
    const unsubscribeUnitSpawned = combatManager.subscribe('combat:unit-spawned', event => {
      setActiveUnits(prev => prev + 1);
      // Update threat level based on unit type and faction
      if (event.faction !== 'player') {
        setThreatLevel(prev => prev + getThreatValueForUnit(event.unitType));
      }
      setIsActive(true);
    });

    const unsubscribeUnitDestroyed = combatManager.subscribe('combat:unit-destroyed', event => {
      setActiveUnits(prev => prev - 1);
      // Check if combat is still active
      if (activeUnits <= 1) {
        setIsActive(false);
      }
    });

    // Initial state setup
    const currentUnits = combatManager.getAllUnits();
    setActiveUnits(currentUnits.length);
    setIsActive(currentUnits.length > 0);

    return () => {
      unsubscribeUnitSpawned();
      unsubscribeUnitDestroyed();
    };
  }, [activeUnits]);

  /**
   * Update formation configuration for a fleet
   */
  const updateFleetFormation = useCallback((fleetId: string, formation: FleetFormation) => {
    const combatManager = getCombatManager();
    // Get all units in the fleet
    const fleetUnits = combatManager.getAllUnits().filter(unit => unit.faction === fleetId);

    // Update each unit's position based on formation
    fleetUnits.forEach((unit, index) => {
      const basePosition = unit.position;
      const offset = calculateFormationOffset(formation.type, index, fleetUnits.length);
      const newPosition = {
        x: basePosition.x + offset.x,
        y: basePosition.y + offset.y,
      };
      combatManager.moveUnit(unit.id, newPosition);
    });

    setFormations(prev => ({
      ...prev,
      [fleetId]: formation,
    }));
  }, []);

  /**
   * Update tactical behavior for a fleet
   */
  const updateFleetTactic = useCallback(
    (fleetId: string, tactic: 'flank' | 'charge' | 'kite' | 'hold') => {
      const combatManager = getCombatManager();
      const fleetUnits = combatManager.getAllUnits().filter(unit => unit.faction === fleetId);

      // Update each unit's status based on tactic
      fleetUnits.forEach(unit => {
        const status = getTacticStatus(tactic);
        combatManager.changeUnitStatus(unit.id, status);
      });

      setActiveFleets(prev => {
        if (!prev.includes(fleetId)) {
          return [...prev, fleetId];
        }
        return prev;
      });
    },
    []
  );

  /**
   * Get current formations for all fleets
   */
  const getFleetFormations = useCallback(() => {
    return formations;
  }, [formations]);

  /**
   * Get current tactics for all fleets
   */
  const getFleetTactics = useCallback(() => {
    const combatManager = getCombatManager();
    const units = combatManager.getAllUnits();
    const tactics: Record<string, string> = {};

    units.forEach(unit => {
      if (unit.faction && !tactics[unit.faction]) {
        tactics[unit.faction] = getStatusTactic(unit.status);
      }
    });

    return tactics;
  }, []);

  return {
    threatLevel,
    activeUnits,
    isActive,
    activeFleets,
    formations,
    updateFleetFormation,
    updateFleetTactic,
    getFleetFormations,
    getFleetTactics,
  };
}

// Helper function to calculate threat value based on unit type
function getThreatValueForUnit(unitType: string): number {
  const threatValues: Record<string, number> = {
    spitflare: 1,
    starSchooner: 2,
    orionFrigate: 3,
    harbringerGalleon: 4,
    midwayCarrier: 5,
    motherEarthRevenge: 6,
  };
  return threatValues[unitType] || 1;
}

// Helper function to calculate formation offset based on formation type
function calculateFormationOffset(
  formationType: 'offensive' | 'defensive' | 'balanced',
  index: number,
  totalUnits: number
): Position {
  const spacing = 100; // Default spacing

  switch (formationType) {
    case 'offensive': {
      // Wedge formation
      return {
        x: index * spacing * Math.cos(Math.PI / 4),
        y: index * spacing * Math.sin(Math.PI / 4),
      };
    }
    case 'defensive': {
      // Circle formation
      const angle = (2 * Math.PI * index) / totalUnits;
      return {
        x: spacing * Math.cos(angle),
        y: spacing * Math.sin(angle),
      };
    }
    case 'balanced': {
      // Line formation
      return {
        x: index * spacing,
        y: 0,
      };
    }
  }
}

// Helper function to convert tactic to status
function getTacticStatus(tactic: string): CombatUnitStatus {
  switch (tactic) {
    case 'flank':
      return 'moving';
    case 'charge':
      return 'attacking';
    case 'kite':
      return 'retreating';
    case 'hold':
      return 'defending';
    default:
      return 'idle';
  }
}

// Helper function to convert status to tactic
function getStatusTactic(status: CombatUnitStatus): string {
  switch (status) {
    case 'moving':
      return 'flank';
    case 'attacking':
      return 'charge';
    case 'retreating':
      return 'kite';
    case 'defending':
      return 'hold';
    default:
      return 'hold';
  }
}

export function useUnitCombat(unitId: string) {
  const [unitStatus, setUnitStatus] = useState(() => getCombatManager().getUnitStatus(unitId));

  useEffect(() => {
    const interval = setInterval(() => {
      const status = getCombatManager().getUnitStatus(unitId);
      setUnitStatus(status);
    }, 250); // More frequent updates for combat units

    return () => clearInterval(interval);
  }, [unitId]);

  return {
    status: unitStatus?.status || 'idle',
    health: unitStatus?.stats?.health || 0,
    shield: unitStatus?.stats?.shield || 0,
    target: unitStatus?.target,
    position: unitStatus?.position,
  };
}

import { useState, useEffect, useCallback } from 'react';
import { errorLoggingService, ErrorType } from '../../services/ErrorLoggingService';

/**
 * Options for data fetching hook
 */
export interface DataFetchOptions {
  /** Whether to fetch immediately on mount */
  fetchOnMount?: boolean;
  /** Custom condition to trigger fetch */
  enabled?: boolean;
  /** Refetch interval in milliseconds */
  refetchInterval?: number;
  /** Maximum number of retries on error */
  maxRetries?: number;
  /** Whether to reset data when refetching */
  resetOnFetch?: boolean;
  /** Cache key for the request */
  cacheKey?: string;
  /** Cache time in milliseconds */
  cacheTime?: number;
}

/**
 * Return type for data fetching hook
 */
export interface DataFetchResult<T> {
  /** The fetched data */
  data: T | null;
  /** Whether data is currently being fetched */
  isLoading: boolean;
  /** Any error that occurred during fetching */
  error: Error | null;
  /** (...args: unknown[]) => unknown to manually trigger a fetch */
  fetch: () => Promise<void>;
  /** (...args: unknown[]) => unknown to reset the hook state */
  reset: () => void;
  /** Last time data was fetched successfully */
  lastFetched: number | null;
  /** Number of retries attempted */
  retryCount: number;
}

/**
 * Creates a reusable data fetching hook with standardized loading, error, and data states
 * @param fetchFn The function that fetches data
 * @param defaultOptions Default options for the hook
 * @returns A React hook that manages data fetching
 */
export function createDataFetchHook<T, P extends any[] = []>(
  fetchFn: (...args: P) => Promise<T>,
  defaultOptions: DataFetchOptions = {}
) {
  return (...args: P): DataFetchResult<T> => {
    const [data, setData] = useState<T | null>(null);
    const [isLoading, setIsLoading] = useState<boolean>(defaultOptions.fetchOnMount !== false);
    const [error, setError] = useState<Error | null>(null);
    const [lastFetched, setLastFetched] = useState<number | null>(null);
    const [retryCount, setRetryCount] = useState<number>(0);

    // Determine if fetch is enabled
    const enabled = defaultOptions.enabled !== false;

    // Memoized fetch function
    const fetch = useCallback(async () => {
      if (!enabled) return;

      try {
        if (defaultOptions.resetOnFetch) {
          setData(null);
        }

        setIsLoading(true);
        setError(null);

        const result = await fetchFn(...args);

        setData(result);
        setLastFetched(Date.now());
        setRetryCount(0);
      } catch (err) {
        setError(err instanceof Error ? err : new Error(String(err)));

        // Log error to the error service
        errorLoggingService.logError(
          err instanceof Error ? err : new Error(String(err)),
          ErrorType.NETWORK,
          undefined,
          { hook: 'dataFetch', args: JSON.stringify(args) }
        );

        // Retry logic
        if (defaultOptions.maxRetries && retryCount < defaultOptions.maxRetries) {
          setRetryCount(current => current + 1);

          // Exponential backoff
          const backoffTime = Math.min(1000 * Math.pow(2, retryCount), 30000);

          setTimeout(() => {
            fetch();
          }, backoffTime);
        }
      } finally {
        setIsLoading(false);
      }
    }, [enabled, ...args]);

    // Reset function
    const reset = useCallback(() => {
      setData(null);
      setIsLoading(false);
      setError(null);
      setLastFetched(null);
      setRetryCount(0);
    }, []);

    // Handle automatic fetching on mount
    useEffect(() => {
      let mounted = true;
      let intervalId: NodeJS.Timeout | null = null;

      if (defaultOptions.fetchOnMount !== false && enabled) {
        fetch();
      }

      // Set up refetch interval if specified
      if (defaultOptions.refetchInterval && enabled) {
        intervalId = setInterval(() => {
          if (mounted) {
            fetch();
          }
        }, defaultOptions.refetchInterval);
      }

      return () => {
        mounted = false;
        if (intervalId) {
          clearInterval(intervalId);
        }
      };
    }, [fetch, enabled]);

    return {
      data,
      isLoading,
      error,
      fetch,
      reset,
      lastFetched,
      retryCount
    };
  };
}

/**
 * Example usage:
 * 
 * ```typescript
 * // Define the hook
 * const useUserData = createDataFetchHook(
 *   (userId: string) => fetch(`/api/users/${userId}`).then(res => res.json()),
 *   { fetchOnMount: true }
 * );
 * 
 * // Use in component
 * function UserProfile({ userId }) {
 *   const { data, isLoading, error, fetch } = useUserData(userId);
 *   
 *   if (isLoading) return <Loading />;
 *   if (error) return <Error message={error.message} />;
 *   
 *   return (
 *     <div>
 *       <h1>{data.name}</h1>
 *       <button onClick={fetch}>Refresh</button>
 *     </div>
 *   );
 * }
 * ```
 */

import { useState, useMemo, useCallback, useEffect } from 'react';

/**
 * Action creator type
 */
export type ActionCreator<TState, TPayload = any> = (
  state: TState,
  payload: TPayload
) => Partial<TState>;

/**
 * Bound action type
 */
export type BoundAction<TPayload = any> = (payload: TPayload) => void;

/**
 * State hook options
 */
export interface StateHookOptions<TState> {
  /** (...args: unknown[]) => unknown to run on state initialization */
  onInit?: (state: TState) => void;
  /** (...args: unknown[]) => unknown to run on state cleanup */
  onCleanup?: (state: TState) => void;
  /** Whether to persist state in localStorage */
  persist?: boolean;
  /** Key to use for localStorage persistence */
  persistKey?: string;
  /** Custom state equality function */
  areEqual?: (prev: TState, next: TState) => boolean;
  /** Initial state override */
  initialStateOverride?: TState | (() => TState);
}

/**
 * Creates a reusable state management hook with actions
 * @param initialState The initial state or a function that returns the initial state
 * @param actions An object of action creators
 * @param options Options for the hook
 * @returns A hook that provides state and bound actions
 */
export function createStateHook<
  TState extends Record<string, unknown>,
  TActions extends Record<string, ActionCreator<TState, unknown>>
>(
  initialState: TState | (() => TState),
  actions: TActions,
  options: StateHookOptions<TState> = {}
) {
  // Type for the returned actions object
  type BoundActions = {
    [K in keyof TActions]: BoundAction<Parameters<TActions[K]>[1]>;
  };

  return () => {
    // Initialize state, handling both function and object initial states
    const getInitialState = useCallback(() => {
      // Check if we should use override
      if (options.initialStateOverride !== undefined) {
        return typeof options.initialStateOverride === 'function'
          ? (options.initialStateOverride as () => TState)()
          : options.initialStateOverride;
      }

      // Otherwise use the default initial state
      const defaultState = typeof initialState === 'function'
        ? (initialState as () => TState)()
        : initialState;

      // If persistence is enabled, try to load from localStorage
      if (options.persist && options.persistKey) {
        try {
          const savedState = localStorage.getItem(options.persistKey);
          if (savedState) {
            return { ...defaultState, ...JSON.parse(savedState) };
          }
        } catch (error) {
          console.error('Failed to load persisted state:', error);
        }
      }

      return defaultState;
    }, []);

    // Initialize state
    const [state, setState] = useState<TState>(getInitialState);

    // Create memoized bound actions
    const boundActions = useMemo(() => {
      const result = {} as BoundActions;

      for (const [key, actionCreator] of Object.entries(actions)) {
        result[key as keyof TActions] = (payload) => {
          setState((currentState) => {
            const updates = actionCreator(currentState, payload);

            // If updates is empty, return the current state
            if (!updates || Object.keys(updates).length === 0) {
              return currentState;
            }

            const newState = { ...currentState, ...updates };

            // If custom equality function is provided, check if state actually changed
            if (options.areEqual && options.areEqual(currentState, newState)) {
              return currentState;
            }

            // Persist state if enabled
            if (options.persist && options.persistKey) {
              try {
                localStorage.setItem(options.persistKey, JSON.stringify(newState));
              } catch (error) {
                console.error('Failed to persist state:', error);
              }
            }

            return newState;
          });
        };
      }

      return result;
    }, []);

    // Reset action
    const reset = useCallback(() => {
      setState(getInitialState());

      // Clear persisted state if applicable
      if (options.persist && options.persistKey) {
        try {
          localStorage.removeItem(options.persistKey);
        } catch (error) {
          console.error('Failed to clear persisted state:', error);
        }
      }
    }, [getInitialState]);

    // Add reset to bound actions
    const actionsWithReset = useMemo(() => ({
      ...boundActions,
      reset
    }), [boundActions, reset]);

    // Run initialization and cleanup
    useEffect(() => {
      if (options.onInit) {
        options.onInit(state);
      }

      return () => {
        if (options.onCleanup) {
          options.onCleanup(state);
        }
      };
    }, []);

    return [state, actionsWithReset] as const;
  };
}

/**
 * Example usage:
 * 
 * ```typescript
 * // Define the state type
 * interface CounterState {
 *   count: number;
 *   lastUpdated: number | null;
 * }
 * 
 * // Define action creators
 * const counterActions = {
 *   increment: (state: CounterState, step: number = 1) => ({
 *     count: state.count + step,
 *     lastUpdated: Date.now()
 *   }),
 *   decrement: (state: CounterState, step: number = 1) => ({
 *     count: state.count - step,
 *     lastUpdated: Date.now()
 *   }),
 *   reset: (state: CounterState) => ({
 *     count: 0,
 *     lastUpdated: Date.now()
 *   })
 * };
 * 
 * // Create the hook
 * const useCounter = createStateHook<CounterState, typeof counterActions>(
 *   { count: 0, lastUpdated: null },
 *   counterActions,
 *   { persist: true, persistKey: 'app-counter' }
 * );
 * 
 * // Use in component
 * function Counter() {
 *   const [state, actions] = useCounter();
 *   
 *   return (
 *     <div>
 *       <p>Count: {state.count}</p>
 *       <button onClick={() => actions.increment()}>Increment</button>
 *       <button onClick={() => actions.decrement()}>Decrement</button>
 *       <button onClick={actions.reset}>Reset</button>
 *     </div>
 *   );
 * }
 * ```
 */

/**
 * useSessionPerformance
 *
 * A React hook for tracking component performance and user interactions.
 * Provides access to the SessionPerformanceTracker for anonymous telemetry.
 */

import { useEffect, useRef } from 'react';
import {
  SessionPerformanceTracker,
  TelemetryOptions,
  UserInteractionData,
} from '../../services/telemetry/SessionPerformanceTracker';

// Create a singleton instance of the tracker to be shared across the application
let globalTracker: SessionPerformanceTracker | null = null;

/**
 * Initialize the global performance tracker
 */
export function initializeSessionPerformanceTracker(options?: Partial<TelemetryOptions>): void {
  if (!globalTracker) {
    globalTracker = new SessionPerformanceTracker(options);
  }
}

/**
 * React hook for tracking component performance
 */
export default function useSessionPerformance(componentId: string) {
  const trackerRef = useRef<SessionPerformanceTracker | null>(null);

  // Initialize the tracker on the first render
  useEffect(() => {
    // Create global instance if it doesn't exist yet
    if (!globalTracker) {
      initializeSessionPerformanceTracker();
    }

    trackerRef.current = globalTracker;

    // Start timing the component load
    if (trackerRef.current) {
      trackerRef.current.startComponentLoadTimer(componentId);
    }

    // When component mounts, record completion time
    const tracker = trackerRef.current;
    if (tracker) {
      // Use queueMicrotask to ensure we measure after initial render
      queueMicrotask(() => {
        tracker.endComponentLoadTimer(componentId);
      });
    }

    return () => {
      // Optionally track unmount time or other component lifecycle events
    };
  }, [componentId]);

  /**
   * Track a custom user interaction within the component
   */
  const trackInteraction = (
    interactionType: 'click' | 'hover' | 'scroll' | 'keypress' | 'custom',
    details: Partial<UserInteractionData> = {}
  ) => {
    if (!trackerRef.current) return;

    trackerRef.current.trackUserInteraction({
      interactionType,
      targetComponent: componentId,
      timestamp: Date.now(),
      responseTime: 0, // Will be populated with measured time
      successful: true,
      ...details,
    });
  };

  /**
   * Create a performance-tracked event handler
   */
  const withPerformanceTracking = <T extends (...args: unknown[]) => any>(
    handler: T,
    interactionType: 'click' | 'hover' | 'scroll' | 'keypress' | 'custom' = 'click'
  ): ((...args: Parameters<T>) => ReturnType<T>) => {
    return (...args: Parameters<T>) => {
      const startTime = performance.now();

      try {
        const result = handler(...args);

        // For promise-returning handlers, track completion when promise resolves
        if (result instanceof Promise) {
          result
            .then(() => {
              if (trackerRef.current) {
                trackerRef.current.trackUserInteraction({
                  interactionType,
                  targetComponent: componentId,
                  timestamp: Date.now(),
                  responseTime: performance.now() - startTime,
                  successful: true,
                });
              }
            })
            .catch(() => {
              if (trackerRef.current) {
                trackerRef.current.trackUserInteraction({
                  interactionType,
                  targetComponent: componentId,
                  timestamp: Date.now(),
                  responseTime: performance.now() - startTime,
                  successful: false,
                });
              }
            });
        } else {
          // For synchronous handlers, track completion immediately
          if (trackerRef.current) {
            trackerRef.current.trackUserInteraction({
              interactionType,
              targetComponent: componentId,
              timestamp: Date.now(),
              responseTime: performance.now() - startTime,
              successful: true,
            });
          }
        }

        return result;
      } catch (error) {
        // Track failed interactions
        if (trackerRef.current) {
          trackerRef.current.trackUserInteraction({
            interactionType,
            targetComponent: componentId,
            timestamp: Date.now(),
            responseTime: performance.now() - startTime,
            successful: false,
          });
        }
        throw error;
      }
    };
  };

  return {
    trackInteraction,
    withPerformanceTracking,
  };
}

import { ResourceType } from "./../../types/resources/ResourceTypes";
import { useEffect, useRef } from 'react';
import { componentRegistryService } from '../../services/ComponentRegistryService';
import { useComponentProfiler } from './useComponentProfiler';

/**
 * Options for component registration
 */
export interface ComponentRegistrationOptions {
  /**
   * Type of the component (e.g., 'ResourceDisplay', 'ModuleCard')
   */
  type: ResourceType;

  /**
   * Event types this component is interested in receiving
   */
  eventSubscriptions: string[];

  /**
   * Update priority for this component
   * - high: Component updates are critical for game functionality
   * - medium: Component updates are important but not critical
   * - low: Component updates can be delayed if necessary
   */
  updatePriority?: 'high' | 'medium' | 'low';
}

/**
 * Hook that registers a React component with the ComponentRegistryService
 *
 * This hook:
 * 1. Generates a unique ID for the component
 * 2. Registers the component with the registry
 * 3. Sets up profiling to measure render times
 * 4. Automatically cleans up on unmount
 *
 * @param options Component registration options
 * @returns The generated component ID
 *
 * @example
 * function ResourceDisplay({ resourceType }) {
 *   // Register component with the system
 *   useComponentRegistration({
 *     type: 'ResourceDisplay',
 *     eventSubscriptions: ['RESOURCE_PRODUCED', 'RESOURCE_CONSUMED'],
 *     updatePriority: 'high'
 *   });
 *
 *   // Component implementation...
 * }
 */
export function useComponentRegistration(options: ComponentRegistrationOptions): string {
  const componentId = useRef<string>('');
  const profiler = useComponentProfiler(options.type);

  useEffect(() => {
    // Register component with registry
    const id = componentRegistryService.registerComponent({
      type: options.type,
      eventSubscriptions: options.eventSubscriptions,
      updatePriority: options.updatePriority || 'medium',
    });

    componentId.current = id;

    // Track render with profiler
    if (profiler) {
      const renderTime = profiler.metrics.lastRenderTime;
      if (renderTime !== undefined) {
        componentRegistryService.trackRender(id);
      }
    }

    // Return cleanup function
    return () => {
      componentRegistryService.unregisterComponent(id);
    };
  }, [options.type, options.updatePriority, profiler]);

  return componentId.current;
}

/**
 * Hook that registers a component and provides a method to manually update its metrics
 *
 * This is useful for components that need to manually control when metrics are updated,
 * such as those with complex rendering logic or conditional renders.
 *
 * @param options Component registration options
 * @returns An object containing the component ID and an update function
 */
export function useComponentRegistrationWithManualUpdates(options: ComponentRegistrationOptions): {
  componentId: string;
  updateMetrics: (renderTime: number) => void;
} {
  const componentId = useComponentRegistration(options);

  // Function to manually update metrics
  const updateMetrics = (renderTime: number) => {
    componentRegistryService.trackRender(componentId);
  };

  return { componentId, updateMetrics };
}

import { useCallback, useEffect, useState } from 'react';
import { workerService } from '../services/WorkerService';
import { useService } from './services/useService';

interface UseWorkerOptions {
  type: string;
  priority?: 'HIGH' | 'MEDIUM' | 'LOW';
  onProgress?: (progress: number) => void;
}

interface UseWorkerResult<T> {
  execute: (data: unknown) => Promise<T>;
  cancel: () => void;
  isRunning: boolean;
  progress: number;
  error: Error | null;
}

export function useWorker<T>({
  type,
  priority = 'MEDIUM',
  onProgress,
}: UseWorkerOptions): UseWorkerResult<T> {
  const { service } = useService<typeof workerService>('worker');
  const [currentTaskId, setCurrentTaskId] = useState<string | null>(null);
  const [isRunning, setIsRunning] = useState(false);
  const [progress, setProgress] = useState(0);
  const [error, setError] = useState<Error | null>(null);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (currentTaskId) {
        service?.cancelTask(currentTaskId);
      }
    };
  }, [currentTaskId, service]);

  const execute = useCallback(
    async (data: unknown): Promise<T> => {
      if (!service) {
        throw new Error('Worker service not available');
      }

      setIsRunning(true);
      setProgress(0);
      setError(null);

      try {
        const result = await service.submitTask<T>(type, data, priority);
        return result;
      } catch (err) {
        const error = err instanceof Error ? err : new Error(String(err));
        setError(error);
        throw error;
      } finally {
        setIsRunning(false);
        setCurrentTaskId(null);
      }
    },
    [service, type, priority]
  );

  const cancel = useCallback(() => {
    if (currentTaskId && service) {
      service.cancelTask(currentTaskId);
      setIsRunning(false);
      setCurrentTaskId(null);
    }
  }, [currentTaskId, service]);

  return {
    execute,
    cancel,
    isRunning,
    progress,
    error,
  };
}

/**
 * BaseTypedEventEmitter.ts
 *
 * This file provides a base implementation of the ITypedEventEmitter interface
 * that manager classes can extend to gain typed event emitting capabilities.
 */

import { ITypedEventEmitter } from '../../types/events/EventEmitterInterface';
import { TypedEventEmitter } from './EventBus';

/**
 * Base class for all typed event emitters in the system
 * Manager classes should extend this class to gain typed event emitting capabilities
 */
export class BaseTypedEventEmitter<T extends Record<string, unknown>>
  extends TypedEventEmitter<T>
  implements ITypedEventEmitter<T>
{
  /**
   * Creates a new BaseTypedEventEmitter
   */
  constructor() {
    super();
  }

  /**
   * Emit an event
   *
   * @param event The event name (key of T)
   * @param data The data to pass to the event handlers
   */
  public emit<K extends keyof T>(event: K, data: T[K]): void {
    // Call the protected emit method from the parent class
    super.emit(event, data);
  }

  /**
   * Remove all event listeners
   *
   * @param event Optional event name to remove listeners for. If not provided, all listeners are removed.
   */
  public removeAllListeners<K extends keyof T>(event?: K): void {
    if (event) {
      // Clear listeners for a specific event
      const handlers = this.getHandlers(event);
      if (handlers) {
        handlers.forEach(handler => {
          this.off(event, handler);
        });
      }
    } else {
      // Clear all listeners
      this.clearAllListeners();
    }
  }

  /**
   * Get all handlers for a specific event
   * This is a helper method used by removeAllListeners
   *
   * @param event The event name
   * @returns Array of handlers for the event
   */
  private getHandlers<K extends keyof T>(event: K): Array<(data: T[K]) => void> {
    // This is a simplified implementation
    // In a real implementation, we would track all handlers
    return [];
  }

  /**
   * Subscribe to an event (alias for on)
   *
   * @param event The event name (key of T)
   * @param callback The function to call when the event is emitted
   * @returns A function to unsubscribe the handler
   */
  public subscribe<K extends keyof T>(event: K, callback: (data: T[K]) => void): () => void {
    return this.on(event, callback);
  }

  /**
   * Unsubscribe from an event (alias for off)
   *
   * @param event The event name (key of T)
   * @param callback The handler function to remove
   */
  public unsubscribe<K extends keyof T>(event: K, callback: (data: T[K]) => void): void {
    this.off(event, callback);
  }
}

import { ResourceType } from "./../../types/resources/ResourceTypes";
import { ModuleType } from '../../types/buildings/ModuleTypes';

/**
 * Represents all possible event types that can be emitted by modules in the system.
 *
 * These event types are categorized by their functional area:
 * - Lifecycle events: Monitor the creation, attachment, and activation of modules
 * - Attachment events: Track the process of attaching modules to buildings or other modules
 * - Resource events: Monitor resource production, consumption, and transfers
 * - Automation events: Track automation rule execution and cycles
 * - Status events: Report changes in module status and errors
 * - Mission events: Track mission progress and completions
 * - Sub-module events: Monitor the lifecycle of sub-modules
 * - Combat events: Track combat-related updates
 * - Tech events: Monitor technology unlocks and updates
 *
 * @typedef {string} ModuleEventType
 */
export type ModuleEventType =
  // Lifecycle events
  | 'MODULE_CREATED'
  | 'MODULE_ATTACHED'
  | 'MODULE_DETACHED'
  | 'MODULE_UPGRADED'
  | 'MODULE_ACTIVATED'
  | 'MODULE_DEACTIVATED'
  | 'MODULE_UPDATED'
  // Attachment events
  | 'ATTACHMENT_STARTED'
  | 'ATTACHMENT_CANCELLED'
  | 'ATTACHMENT_COMPLETED'
  | 'ATTACHMENT_PREVIEW_SHOWN'
  // Resource events
  | 'RESOURCE_PRODUCED'
  | 'RESOURCE_CONSUMED'
  | 'RESOURCE_TRANSFERRED'
  | 'RESOURCE_PRODUCTION_REGISTERED'
  | 'RESOURCE_PRODUCTION_UNREGISTERED'
  | 'RESOURCE_CONSUMPTION_REGISTERED'
  | 'RESOURCE_CONSUMPTION_UNREGISTERED'
  | 'RESOURCE_FLOW_REGISTERED'
  | 'RESOURCE_FLOW_UNREGISTERED'
  | 'RESOURCE_SHORTAGE'
  | 'RESOURCE_UPDATED'
  | 'RESOURCE_DISCOVERED'
  // Automation events
  | 'AUTOMATION_STARTED'
  | 'AUTOMATION_STOPPED'
  | 'AUTOMATION_CYCLE_COMPLETE'
  // Status events
  | 'STATUS_CHANGED'
  | 'ERROR_OCCURRED'
  // Mission events
  | 'MISSION_STARTED'
  | 'MISSION_COMPLETED'
  | 'MISSION_FAILED'
  | 'MISSION_PROGRESS_UPDATED'
  | 'MISSION_REWARD_CLAIMED'
  // Sub-module events
  | 'SUB_MODULE_CREATED'
  | 'SUB_MODULE_ATTACHED'
  | 'SUB_MODULE_DETACHED'
  | 'SUB_MODULE_UPGRADED'
  | 'SUB_MODULE_ACTIVATED'
  | 'SUB_MODULE_DEACTIVATED'
  | 'SUB_MODULE_EFFECT_APPLIED'
  | 'SUB_MODULE_EFFECT_REMOVED'
  // Combat events
  | 'COMBAT_UPDATED'
  // Tech events
  | 'TECH_UNLOCKED'
  | 'TECH_UPDATED';

/**
 * Represents an event emitted by a module in the system.
 *
 * The ModuleEvent interface defines the structure of events that flow through the
 * event system. Each event must include a type, moduleId, moduleType, and timestamp.
 * Additional data can be included in the optional data property.
 *
 * @interface ModuleEvent
 * @property {ModuleEventType} type - The type of event, indicating what action or state change occurred
 * @property {string} moduleId - Unique identifier of the module that emitted the event
 * @property {ModuleType} moduleType - Type of the module that emitted the event (e.g., 'production', 'storage')
 * @property {number} timestamp - Unix timestamp when the event occurred (milliseconds since epoch)
 * @property {Record<string, unknown>} [data] - Optional additional data relevant to the event
 *
 * @example
 * // Example of a module created event
 * const event: ModuleEvent = {
 *   type: 'MODULE_CREATED',
 *   moduleId: 'mining-module-1',
 *   moduleType: 'production',
 *   timestamp: Date.now(),
 *   data: {
 *     location: { x: 100, y: 200 },
 *     createdBy: 'player'
 *   }
 * };
 */
export interface ModuleEvent {
  type: ModuleEventType;
  moduleId: string;
  moduleType: ModuleType;
  timestamp: number;
  data?: Record<string, unknown>;
}

/**
 * Function signature for event listeners that handle module events.
 *
 * @callback ModuleEventListener
 * @param {ModuleEvent} event - The event object containing information about what occurred
 * @returns {void}
 */
type ModuleEventListener = (event: ModuleEvent) => void;

/**
 * Event bus that facilitates module communication through events.
 *
 * The ModuleEventBus provides publish-subscribe functionality for module events,
 * allowing modules to communicate with each other without direct dependencies.
 * It manages event listeners, dispatches events to appropriate listeners,
 * and maintains a history of events for analysis and debugging.
 *
 * @class ModuleEventBus
 */
export class ModuleEventBus {
  /**
   * Map of event types to sets of event listeners.
   * @private
   */
  private listeners: Map<ModuleEventType, Set<ModuleEventListener>>;

  /**
   * Array containing event history, with the most recent events at the end.
   * @private
   */
  private history: ModuleEvent[];

  /**
   * Maximum number of events to keep in history.
   * @private
   */
  private maxHistorySize: number;

  /**
   * Creates a new ModuleEventBus instance.
   *
   * @param {number} [maxHistorySize=1000] - Maximum number of events to keep in history
   */
  constructor(maxHistorySize = 1000) {
    this.listeners = new Map();
    this.history = [];
    this.maxHistorySize = maxHistorySize;
  }

  /**
   * Subscribes to a specific type of module event.
   *
   * This method registers a listener function to be called whenever an event
   * of the specified type is emitted. It returns an unsubscribe function that
   * can be called to remove the listener.
   *
   * @param {ModuleEventType} type - The type of event to subscribe to
   * @param {ModuleEventListener} listener - The function to call when events of this type occur
   * @returns {Function} An unsubscribe function that, when called, removes the listener
   *
   * @example
   * // Subscribe to MODULE_CREATED events
   * const unsubscribe = moduleEventBus.subscribe('MODULE_CREATED', (event) => {
   *   console.warn(`New module created: ${event.moduleId}`);
   * });
   *
   * // Later, to stop receiving events
   * unsubscribe();
   */
  subscribe(type: ModuleEventType, listener: ModuleEventListener): () => void {
    if (!this.listeners.has(type)) {
      this.listeners.set(type, new Set());
    }

    this.listeners.get(type)!.add(listener);

    // Return unsubscribe function
    return () => {
      const typeListeners = this.listeners.get(type);
      if (typeListeners) {
        typeListeners.delete(listener);
        if (typeListeners.size === 0) {
          this.listeners.delete(type);
        }
      }
    };
  }

  /**
   * Emits an event to all subscribed listeners.
   *
   * This method adds the event to the event history and notifies all listeners
   * subscribed to the event's type. If an error occurs in a listener, it is
   * caught and logged, allowing other listeners to continue receiving the event.
   *
   * @param {ModuleEvent} event - The event to emit
   * @returns {void}
   *
   * @example
   * // Emit a MODULE_ACTIVATED event
   * moduleEventBus.emit({
   *   type: 'MODULE_ACTIVATED',
   *   moduleId: 'mining-module-1',
   *   moduleType: 'production',
   *   timestamp: Date.now(),
   *   data: { activatedBy: 'player' }
   * });
   */
  emit(event: ModuleEvent): void {
    // Add to history
    this.history.push(event);
    if (this.history.length > this.maxHistorySize) {
      this.history.shift();
    }

    // Notify listeners
    const typeListeners = this.listeners.get(event.type);
    if (typeListeners) {
      typeListeners.forEach(listener => {
        try {
          listener(event);
        } catch (error) {
          console.error('Error in module event listener:', error);
        }
      });
    }
  }

  /**
   * Retrieves the complete event history.
   *
   * Returns a copy of the entire event history array, preserving the original
   * history in the event bus. Events are ordered from oldest (index 0) to newest.
   *
   * @returns {ModuleEvent[]} Array containing all events in the history
   *
   * @example
   * // Get complete event history and count events by type
   * const history = moduleEventBus.getHistory();
   * const eventCounts = history.reduce((counts, event) => {
   *   counts[event.type] = (counts[event.type] || 0) + 1;
   *   return counts;
   * }, {});
   * console.warn('Event counts:', eventCounts);
   */
  getHistory(): ModuleEvent[] {
    return [...this.history];
  }

  /**
   * Retrieves event history for a specific module.
   *
   * Filters the event history to return only events related to the specified module ID.
   * Returns a copy of the filtered events, ordered from oldest to newest.
   *
   * @param {string} moduleId - ID of the module to get history for
   * @returns {ModuleEvent[]} Array containing events for the specified module
   *
   * @example
   * // Get history for a specific module and analyze its lifecycle
   * const moduleHistory = moduleEventBus.getModuleHistory('mining-module-1');
   * const creationEvent = moduleHistory.find(event => event.type === 'MODULE_CREATED');
   * if (creationEvent) {
   *   console.warn(`Module created at: ${new Date(creationEvent.timestamp).toLocaleString()}`);
   * }
   */
  getModuleHistory(moduleId: string): ModuleEvent[] {
    return this.history.filter(event => event.moduleId === moduleId);
  }

  /**
   * Retrieves event history for a specific event type.
   *
   * Filters the event history to return only events of the specified type.
   * Returns a copy of the filtered events, ordered from oldest to newest.
   *
   * @param {ModuleEventType} type - Type of events to retrieve
   * @returns {ModuleEvent[]} Array containing events of the specified type
   *
   * @example
   * // Get all resource shortage events and identify affected resources
   * const shortages = moduleEventBus.getEventTypeHistory('RESOURCE_SHORTAGE');
   * const affectedResources = shortages.map(event => event.data?.resourceType).filter(Boolean);
   * console.warn('Resources with shortages:', affectedResources);
   */
  getEventTypeHistory(type: ModuleEventType): ModuleEvent[] {
    return this.history.filter(event => event.type === type);
  }

  /**
   * Clears the entire event history.
   *
   * This method removes all events from the history array. This can be useful
   * for managing memory usage or resetting the state for testing.
   *
   * @returns {void}
   *
   * @example
   * // Clear event history at the start of a new game session
   * moduleEventBus.clearHistory();
   */
  clearHistory(): void {
    this.history = [];
  }
}

/**
 * Singleton instance of the ModuleEventBus.
 *
 * This exported instance should be used throughout the application to ensure
 * that all modules communicate through the same event bus.
 *
 * @type {ModuleEventBus}
 *
 * @example
 * import { moduleEventBus } from '../../lib/modules/ModuleEvents';
 *
 * // Subscribe to events
 * moduleEventBus.subscribe('MODULE_ACTIVATED', handleModuleActivated);
 *
 * // Emit events
 * moduleEventBus.emit({
 *   type: 'MODULE_ACTIVATED',
 *   moduleId: 'mining-module-1',
 *   moduleType: 'production',
 *   timestamp: Date.now()
 * });
 */
export const moduleEventBus = new ModuleEventBus();

import { Position } from '../../types/core/Position';

/**
 * Represents a WebGL shader uniform
 */
export interface ShaderUniform {
  type: 'float' | 'vec2' | 'vec3' | 'vec4' | 'int' | 'bool' | 'sampler2D';
  value: number | number[] | boolean | WebGLTexture | null;
}

/**
 * Supported shader types for data visualization
 */
export enum DataVisualizationShaderType {
  HEATMAP = 'heatmap',
  CONTOUR = 'contour',
  POINT_CLUSTER = 'pointCluster',
  HIGHLIGHT = 'highlight',
  DENSITY = 'density',
  FLOW = 'flow',
  TRANSITION = 'transition',
  CUSTOM = 'custom',
}

/**
 * Configuration for data visualization shaders
 */
export interface DataVisualizationShaderConfig {
  type: DataVisualizationShaderType;
  colors: string[];
  intensity?: number;
  resolution?: [number, number];
  animate?: boolean;
  animationSpeed?: number;
  highlightRange?: [number, number];
  dataRange?: [number, number];
  dataPoints?: Float32Array;
  customUniforms?: Record<string, ShaderUniform>;
  customVertexShader?: string;
  customFragmentShader?: string;
}

/**
 * WebGL Shader Manager for data visualization effects
 *
 * This utility manages WebGL shaders for data visualization, including
 * heatmaps, contour plots, point clustering, and data highlighting effects.
 */
export class WebGLShaderManager {
  private gl: WebGLRenderingContext | null = null;
  private canvas: HTMLCanvasElement | null = null;
  private programs: Map<string, WebGLProgram> = new Map();
  private shaders: Map<string, WebGLShader> = new Map();
  private textures: Map<string, WebGLTexture> = new Map();
  private framebuffers: Map<string, WebGLFramebuffer> = new Map();
  private animationFrame: number | null = null;
  private _lastTimestamp: number = 0;

  /**
   * Initialize the WebGL context
   */
  public initialize(canvas: HTMLCanvasElement): boolean {
    try {
      this.canvas = canvas;
      this.gl = canvas.getContext('webgl', {
        alpha: true,
        premultipliedAlpha: false,
        antialias: true,
        powerPreference: 'high-performance',
      });

      if (!this.gl) {
        console.error('[WebGLShaderManager] WebGL not supported');
        return false;
      }

      this.initializeDefaultShaders();
      console.warn('[WebGLShaderManager] Initialized successfully');
      return true;
    } catch (error) {
      console.error('[WebGLShaderManager] Initialization failed', error);
      return false;
    }
  }

  /**
   * Initialize default shaders for each visualization type
   */
  private initializeDefaultShaders(): void {
    // Set up standard shaders for each visualization type
    Object.values(DataVisualizationShaderType).forEach(type => {
      if (typeof type === 'string') {
        // Create a default shader with a predefined color array
        this.createDataVisualizationShader({
          type: type as DataVisualizationShaderType,
          colors: [
            '#3366cc', // Blue
            '#cc6633', // Orange
            '#33cc66', // Green
            '#cc33cc', // Purple
          ],
        });
      }
    });
  }

  /**
   * Convert hex color string to RGB array
   * @param hex Hex color string (e.g. "#ff0000" or "#f00")
   * @returns Array of RGB values [r, g, b] in range 0-1
   */
  private hexToRgb(hex: string): [number, number, number] {
    // Remove # if present
    hex = hex.replace(/^#/, '');

    // Handle shorthand hex (e.g. #f00 -> #ff0000)
    if (hex.length === 3) {
      hex = hex
        .split('')
        .map(c => c + c)
        .join('');
    }

    // Parse the hex values
    const r = parseInt(hex.substring(0, 2), 16) / 255;
    const g = parseInt(hex.substring(2, 4), 16) / 255;
    const b = parseInt(hex.substring(4, 6), 16) / 255;

    return [r, g, b];
  }

  /**
   * Create a shader
   */
  private createShader(type: number, source: string): WebGLShader | undefined {
    if (!this.gl) return undefined;

    const shader = this.gl.createShader(type);
    if (!shader) return undefined;

    this.gl.shaderSource(shader, source);
    this.gl.compileShader(shader);

    if (!this.gl.getShaderParameter(shader, this.gl.COMPILE_STATUS)) {
      console.error('An error occurred compiling the shaders:', this.gl.getShaderInfoLog(shader));
      this.gl.deleteShader(shader);
      return undefined;
    }

    return shader;
  }

  /**
   * Create a shader program
   */
  private createShaderProgram(
    name: string,
    vertexShaderSource: string,
    fragmentShaderSource: string
  ): WebGLProgram | undefined {
    if (!this.gl) return undefined;

    const vertexShader = this.createShader(this.gl.VERTEX_SHADER, vertexShaderSource);
    const fragmentShader = this.createShader(this.gl.FRAGMENT_SHADER, fragmentShaderSource);

    if (!vertexShader || !fragmentShader) return undefined;

    const program = this.gl.createProgram();
    if (!program) return undefined;

    this.gl.attachShader(program, vertexShader);
    this.gl.attachShader(program, fragmentShader);
    this.gl.linkProgram(program);

    if (!this.gl.getProgramParameter(program, this.gl.LINK_STATUS)) {
      console.error('Unable to initialize the shader program:', this.gl.getProgramInfoLog(program));
      return undefined;
    }

    this.programs.set(name, program);
    return program;
  }

  /**
   * Render data visualization with WebGL shader
   */
  public renderDataVisualization(
    config: DataVisualizationShaderConfig,
    data: Float32Array,
    positions: Position[],
    width: number,
    height: number
  ): void {
    if (!this.gl || !this.canvas) {
      console.error('[WebGLShaderManager] WebGL context not initialized');
      return;
    }

    // Update canvas size if needed
    if (this.canvas.width !== width || this.canvas.height !== height) {
      this.canvas.width = width;
      this.canvas.height = height;
      this.gl.viewport(0, 0, width, height);
    }

    // Get appropriate shader program
    const programName = `data_${config.type}`;
    let program = this.programs.get(programName);

    // Create program if not exists
    if (!program) {
      program = this.createDataVisualizationShader(config);
      if (!program) {
        console.error(`[WebGLShaderManager] Failed to create shader for ${config.type}`);
        return;
      }
    }

    // Set up rendering
    this.gl.useProgram(program);

    // Clear canvas
    this.gl.clearColor(0, 0, 0, 0);
    this.gl.clear(this.gl.COLOR_BUFFER_BIT);

    // Prepare data
    this.setupDataVisualizationBuffers(program, config, data, positions);

    // Perform drawing
    this.gl.drawArrays(this.gl.POINTS, 0, positions.length);
  }

  /**
   * Create data visualization shader program
   */
  private createDataVisualizationShader(
    config: DataVisualizationShaderConfig
  ): WebGLProgram | undefined {
    const vertexShader = this.getDataVisualizationVertexShader(config);
    const fragmentShader = this.getDataVisualizationFragmentShader(config);

    return this.createShaderProgram(`data_${config.type}`, vertexShader, fragmentShader);
  }

  /**
   * Set up data visualization buffers and uniforms
   */
  private setupDataVisualizationBuffers(
    program: WebGLProgram,
    config: DataVisualizationShaderConfig,
    data: Float32Array,
    positions: Position[]
  ): void {
    if (!this.gl) return;

    // Create position buffer
    const positionBuffer = this.gl.createBuffer();
    this.gl.bindBuffer(this.gl.ARRAY_BUFFER, positionBuffer);

    // Convert positions to flat array
    const positionArray = new Float32Array(positions.length * 2);
    positions.forEach((pos, index) => {
      positionArray[index * 2] = pos.x;
      positionArray[index * 2 + 1] = pos.y;
    });

    this.gl.bufferData(this.gl.ARRAY_BUFFER, positionArray, this.gl.STATIC_DRAW);

    // Get attribute location
    const positionLocation = this.gl.getAttribLocation(program, 'a_position');
    this.gl.enableVertexAttribArray(positionLocation);
    this.gl.vertexAttribPointer(positionLocation, 2, this.gl.FLOAT, false, 0, 0);

    // Create data buffer
    const dataBuffer = this.gl.createBuffer();
    this.gl.bindBuffer(this.gl.ARRAY_BUFFER, dataBuffer);
    this.gl.bufferData(this.gl.ARRAY_BUFFER, data, this.gl.STATIC_DRAW);

    // Get attribute location
    const dataLocation = this.gl.getAttribLocation(program, 'a_data');
    this.gl.enableVertexAttribArray(dataLocation);
    this.gl.vertexAttribPointer(dataLocation, 1, this.gl.FLOAT, false, 0, 0);

    // Set uniforms
    this.setDataVisualizationUniforms(program, config);
  }

  /**
   * Set data visualization uniforms
   */
  private setDataVisualizationUniforms(
    program: WebGLProgram,
    config: DataVisualizationShaderConfig
  ): void {
    if (!this.gl) return;

    // Resolution
    const resolution = config.resolution || [this.canvas?.width || 800, this.canvas?.height || 600];
    const resolutionLocation = this.gl.getUniformLocation(program, 'u_resolution');
    this.gl.uniform2f(resolutionLocation, resolution[0], resolution[1]);

    // Colors
    const colorCount = Math.min(config.colors.length, 5);
    for (let i = 0; i < colorCount; i++) {
      const color = this.hexToRgb(config.colors[i]);
      const colorLocation = this.gl.getUniformLocation(program, `u_colors[${i}]`);
      this.gl.uniform3f(colorLocation, color[0], color[1], color[2]);
    }

    // Color count
    const colorCountLocation = this.gl.getUniformLocation(program, 'u_colorCount');
    this.gl.uniform1i(colorCountLocation, colorCount);

    // Intensity
    const intensityLocation = this.gl.getUniformLocation(program, 'u_intensity');
    this.gl.uniform1f(intensityLocation, config.intensity || 1.0);

    // Time (for animations)
    const timeLocation = this.gl.getUniformLocation(program, 'u_time');
    this.gl.uniform1f(timeLocation, performance.now() / 1000.0);

    // Animation speed
    const speedLocation = this.gl.getUniformLocation(program, 'u_speed');
    this.gl.uniform1f(speedLocation, config.animationSpeed || 1.0);

    // Data range
    const dataRange = config.dataRange || [0, 1];
    const dataRangeLocation = this.gl.getUniformLocation(program, 'u_dataRange');
    this.gl.uniform2f(dataRangeLocation, dataRange[0], dataRange[1]);

    // Highlight range
    const highlightRange = config.highlightRange || [0.7, 1.0];
    const highlightRangeLocation = this.gl.getUniformLocation(program, 'u_highlightRange');
    if (highlightRangeLocation !== null) {
      this.gl.uniform2f(highlightRangeLocation, highlightRange[0], highlightRange[1]);
    }

    // Custom uniforms
    if (config.customUniforms) {
      Object.entries(config.customUniforms).forEach(([name, uniform]) => {
        if (!this.gl) return;
        const location = this.gl.getUniformLocation(program, name);
        if (location) {
          this.setUniform(location, uniform);
        }
      });
    }
  }

  /**
   * Set uniform value
   */
  private setUniform(location: WebGLUniformLocation | null, uniform: ShaderUniform): void {
    const gl = this.gl;
    if (!gl || !location) return;

    switch (uniform.type) {
      case 'float':
        gl.uniform1f(location, uniform.value as number);
        break;
      case 'vec2':
        gl.uniform2fv(location, uniform.value as number[]);
        break;
      case 'vec3':
        gl.uniform3fv(location, uniform.value as number[]);
        break;
      case 'vec4':
        gl.uniform4fv(location, uniform.value as number[]);
        break;
      case 'int':
        gl.uniform1i(location, uniform.value as number);
        break;
      case 'bool':
        gl.uniform1i(location, uniform.value ? 1 : 0);
        break;
      case 'sampler2D':
        if (uniform.value instanceof WebGLTexture) {
          gl.uniform1i(location, 0);
          gl.activeTexture(gl.TEXTURE0);
          gl.bindTexture(gl.TEXTURE_2D, uniform.value);
        }
        break;
    }
  }

  /**
   * Get vertex shader source for data visualization
   */
  private getDataVisualizationVertexShader(config: DataVisualizationShaderConfig): string {
    // Use custom vertex shader if provided
    if (config.type === DataVisualizationShaderType.CUSTOM && config.customVertexShader) {
      return config.customVertexShader;
    }

    // Default vertex shader
    return `
      attribute vec2 a_position;
      attribute float a_data;

      uniform vec2 u_resolution;
      uniform float u_time;
      uniform float u_speed;
      uniform vec2 u_dataRange;
      uniform vec2 u_highlightRange;

      varying float v_data;
      varying vec2 v_position;
      varying float v_time;

      void main() {
        // Normalize data
        v_data = (a_data - u_dataRange.x) / (u_dataRange.y - u_dataRange.x);
        v_position = a_position;
        v_time = u_time * u_speed;

        // Set point size based on data
        float highlightFactor = 1.0;

        // Highlight range
        if (v_data >= u_highlightRange.x && v_data <= u_highlightRange.y) {
          // Pulsing effect for highlighted data
          highlightFactor = 1.0 + 0.3 * sin(u_time * 3.0);
        }

        // Calculate point size 
        float baseSize = 1.0;
        ${this.getPointSizeShaderCode(config)}

        // Position conversion
        vec2 clipSpace = (a_position / u_resolution) * 2.0 - 1.0;
        gl_Position = vec4(clipSpace * vec2(1, -1), 0, 1);
      }
    `;
  }

  /**
   * Get fragment shader source for data visualization
   */
  private getDataVisualizationFragmentShader(config: DataVisualizationShaderConfig): string {
    // Use custom fragment shader if provided
    if (config.type === DataVisualizationShaderType.CUSTOM && config.customFragmentShader) {
      return config.customFragmentShader;
    }

    // Default fragment shader
    return `
      precision mediump float;

      uniform vec3 u_colors[5];
      uniform int u_colorCount;
      uniform float u_intensity;
      uniform float u_time;
      uniform vec2 u_highlightRange;

      varying float v_data;
      varying vec2 v_position;
      varying float v_time;

      // Helper function to interpolate colors
      vec3 getColor(float value) {
        if (u_colorCount == 1) return u_colors[0];

        float indexFloat = value * float(u_colorCount - 1);
        int index = int(floor(indexFloat));
        float t = fract(indexFloat);

        if (index >= u_colorCount - 1) {
          return u_colors[u_colorCount - 1];
        }

        return mix(u_colors[index], u_colors[index + 1], t);
      }

      void main() {
        // Get base color from data value
        vec3 color = getColor(v_data);
        float alpha = u_intensity;

        ${this.getFragmentShaderEffects(config)}

        // Apply highlight effect if in range
        if (v_data >= u_highlightRange.x && v_data <= u_highlightRange.y) {
          // Pulse effect
          float pulse = 0.5 + 0.5 * sin(u_time * 3.0);

          // Brighten color 
          color = mix(color, vec3(1.0), pulse * 0.3);

          // Increase opacity
          alpha = mix(alpha, 1.0, pulse * 0.5);

          // Add glow based on distance from center of point
          float dist = length(gl_PointCoord - vec2(0.5));
          float glow = smoothstep(0.5, 0.0, dist);
          color += glow * pulse * 0.3;
        }

        gl_FragColor = vec4(color, alpha);
      }
    `;
  }

  /**
   * Get point size shader code based on visualization type
   */
  private getPointSizeShaderCode(config: DataVisualizationShaderConfig): string {
    let code = '';

    switch (config.type) {
      case DataVisualizationShaderType.CUSTOM:
        code = `gl_PointSize = max(baseSize, 8.0) * highlightFactor;`;
        break;

      case DataVisualizationShaderType.HEATMAP:
        code = `gl_PointSize = max(baseSize, 10.0) * highlightFactor;`;
        break;

      case DataVisualizationShaderType.CONTOUR:
        code = `
          // For contour, size based on data thresholds
          float thresholdFactor = abs(fract(v_data * 10.0) - 0.5) * 2.0;
          gl_PointSize = mix(baseSize, 12.0, thresholdFactor) * highlightFactor;
        `;
        break;

      case DataVisualizationShaderType.POINT_CLUSTER:
        code = `
          // For clustering, vary size by data value
          gl_PointSize = mix(baseSize, 15.0, v_data) * highlightFactor;
        `;
        break;

      case DataVisualizationShaderType.HIGHLIGHT:
        code = `
          // For highlighting, emphasize highlighted ranges
          float inHighlight = (v_data >= u_highlightRange.x && v_data <= u_highlightRange.y) ? 1.0 : 0.0;
          gl_PointSize = mix(baseSize, 20.0, inHighlight) * highlightFactor;
        `;
        break;

      case DataVisualizationShaderType.DENSITY:
        code = `
          // For density maps, size consistent
          gl_PointSize = 8.0 * highlightFactor;
        `;
        break;

      case DataVisualizationShaderType.FLOW:
        code = `
          // For flow visualization, animate size with time
          float flowPulse = 0.7 + 0.3 * sin(v_time + v_data * 10.0);
          gl_PointSize = mix(baseSize, 12.0, v_data) * flowPulse * highlightFactor;
        `;
        break;

      case DataVisualizationShaderType.TRANSITION:
        code = `
          float dist = length(gl_PointCoord - vec2(0.5));
          float transitionFactor = 0.5 + 0.5 * sin(v_time * 2.0 + v_data * 5.0);

          alpha *= smoothstep(0.5, 0.0, dist) * mix(0.5, 1.0, transitionFactor);
          color = mix(color, color * vec3(1.2, 1.1, 0.9), transitionFactor);

          if (transitionFactor > 0.7) {
            float glow = smoothstep(0.5, 0.0, dist) * (transitionFactor - 0.7) * 3.0;
            color += glow * vec3(1.0, 0.9, 0.7);
          }
        `;
        break;

      default:
        code = `gl_PointSize = max(baseSize, 8.0) * highlightFactor;`;
    }

    return code;
  }

  /**
   * Get fragment shader effects based on visualization type
   */
  private getFragmentShaderEffects(config: DataVisualizationShaderConfig): string {
    let effects = '';

    switch (config.type) {
      case DataVisualizationShaderType.CUSTOM:
        effects = '';
        break;

      case DataVisualizationShaderType.HEATMAP:
        effects = `
          float dist = length(gl_PointCoord - vec2(0.5));
          float fadeEdge = smoothstep(0.5, 0.35, dist);
          alpha *= fadeEdge;
        `;
        break;

      case DataVisualizationShaderType.CONTOUR:
        effects = `
          float contourBands = abs(fract(v_data * 10.0) - 0.5) * 2.0;
          float isContour = step(0.8, contourBands);
          alpha *= mix(0.3, 1.0, isContour);

          float dist = length(gl_PointCoord - vec2(0.5));
          alpha *= smoothstep(0.5, 0.0, dist);
        `;
        break;

      case DataVisualizationShaderType.POINT_CLUSTER:
        effects = `
          float dist = length(gl_PointCoord - vec2(0.5));
          float center = smoothstep(0.5, 0.1, dist);
          alpha *= center;

          if (v_data > 0.7) {
            float glow = smoothstep(0.5, 0.0, dist) * 0.5;
            color += glow * vec3(1.0, 0.8, 0.2);
          }
        `;
        break;

      case DataVisualizationShaderType.HIGHLIGHT:
        effects = `
          float dist = length(gl_PointCoord - vec2(0.5));
          alpha *= smoothstep(0.5, 0.0, dist);

          if (v_data >= u_highlightRange.x && v_data <= u_highlightRange.y) {
            float glow = smoothstep(1.0, 0.0, dist * 2.0) * 0.7;
            color += glow * vec3(1.0, 0.9, 0.5);
          }
        `;
        break;

      case DataVisualizationShaderType.DENSITY:
        effects = `
          float dist = length(gl_PointCoord - vec2(0.5));
          alpha *= smoothstep(0.5, 0.0, dist) * v_data;

          color *= 0.8 + 0.2 * v_data;
        `;
        break;

      case DataVisualizationShaderType.FLOW:
        effects = `
          float dist = length(gl_PointCoord - vec2(0.5));

          vec2 dir = normalize(gl_PointCoord - vec2(0.5));
          float dirFactor = 0.5 + 0.5 * dot(dir, vec2(cos(v_time), sin(v_time)));

          alpha *= smoothstep(0.5, 0.0, dist) * dirFactor;
          color *= 0.8 + 0.2 * dirFactor;

          float streak = smoothstep(0.9, 0.0, abs(dot(dir, vec2(cos(v_time), sin(v_time)))));
          color += streak * 0.2 * vec3(1.0, 0.9, 0.7);
        `;
        break;

      case DataVisualizationShaderType.TRANSITION:
        effects = `
          float dist = length(gl_PointCoord - vec2(0.5));
          float transitionFactor = 0.5 + 0.5 * sin(v_time * 2.0 + v_data * 5.0);

          alpha *= smoothstep(0.5, 0.0, dist) * mix(0.5, 1.0, transitionFactor);
          color = mix(color, color * vec3(1.2, 1.1, 0.9), transitionFactor);

          if (transitionFactor > 0.7) {
            float glow = smoothstep(0.5, 0.0, dist) * (transitionFactor - 0.7) * 3.0;
            color += glow * vec3(1.0, 0.9, 0.7);
          }
        `;
        break;

      default:
        effects = '';
    }

    return effects;
  }

  /**
   * Start animation loop for continuous rendering
   * @param renderCallback (...args: unknown[]) => unknown to call on each animation frame
   */
  public startAnimationLoop(renderCallback: () => void): void {
    // Stop any existing animation loop
    this.stopAnimationLoop();

    // Animation frame handler
    const animate = (timestamp: number) => {
      // Calculate delta time
      const deltaTime = this._lastTimestamp ? timestamp - this._lastTimestamp : 0;
      this._lastTimestamp = timestamp;

      // Call render callback
      renderCallback();

      // Request next frame
      this.animationFrame = requestAnimationFrame(animate);
    };

    // Start the animation loop
    this.animationFrame = requestAnimationFrame(animate);
  }

  /**
   * Stop the animation loop
   */
  public stopAnimationLoop(): void {
    if (this.animationFrame !== null) {
      cancelAnimationFrame(this.animationFrame);
      this.animationFrame = null;
    }
  }

  /**
   * Dispose of WebGL resources
   */
  public dispose(): void {
    // Stop any running animation
    this.stopAnimationLoop();

    // Clean up WebGL resources
    if (this.gl) {
      // Delete shaders
      this.shaders.forEach(shader => {
        this.gl?.deleteShader(shader);
      });
      this.shaders.clear();

      // Delete programs
      this.programs.forEach(program => {
        this.gl?.deleteProgram(program);
      });
      this.programs.clear();

      // Delete textures
      this.textures.forEach(texture => {
        this.gl?.deleteTexture(texture);
      });
      this.textures.clear();

      // Delete framebuffers
      this.framebuffers.forEach(framebuffer => {
        this.gl?.deleteFramebuffer(framebuffer);
      });
      this.framebuffers.clear();

      // Reset context
      this.gl = null;
      this.canvas = null;
    }

    console.warn('[WebGLShaderManager] Resources disposed');
  }
}

import { Singleton } from '../patterns/Singleton';
import { ErrorType } from '../../services/ErrorLoggingService';

/**
 * Interface for service metadata
 */
export interface ServiceMetadata {
  name: string;
  version: string;
  status: 'initializing' | 'ready' | 'error' | 'disposed';
  lastError?: {
    type: ErrorType;
    message: string;
    timestamp: number;
  };
  metrics?: Record<string, number>;
}

/**
 * Base interface that all services should implement
 */
export interface BaseService {
  /**
   * Initialize the service with optional dependencies
   */
  initialize(dependencies?: Record<string, unknown>): Promise<void>;

  /**
   * Dispose of any resources used by the service
   */
  dispose(): Promise<void>;

  /**
   * Get metadata about the service's current state
   */
  getMetadata(): ServiceMetadata;

  /**
   * Check if the service is ready to handle requests
   */
  isReady(): boolean;

  /**
   * Handle errors that occur within the service
   */
  handleError(error: Error, context?: Record<string, unknown>): void;
}

/**
 * Abstract base class that provides common service functionality
 * Extends the Singleton pattern to ensure only one instance exists
 */
export abstract class AbstractBaseService extends Singleton<AbstractBaseService> implements BaseService {
  protected metadata: ServiceMetadata;

  protected constructor(name: string, version: string) {
    super();
    this.metadata = {
      name,
      version,
      status: 'initializing',
    };
  }

  async initialize(dependencies?: Record<string, unknown>): Promise<void> {
    try {
      await this.onInitialize(dependencies);
      this.metadata.status = 'ready';
    } catch (error) {
      this.metadata.status = 'error';
      this.handleError(error as Error);
      throw error;
    }
  }

  async dispose(): Promise<void> {
    try {
      await this.onDispose();
      this.metadata.status = 'disposed';
    } catch (error) {
      this.handleError(error as Error);
      throw error;
    }
  }

  getMetadata(): ServiceMetadata {
    return { ...this.metadata };
  }

  isReady(): boolean {
    return this.metadata.status === 'ready';
  }

  handleError(error: Error, context?: Record<string, unknown>): void {
    this.metadata.lastError = {
      type: ErrorType.UNKNOWN,
      message: error.message,
      timestamp: Date.now(),
    };
    // Subclasses should override this to provide custom error handling
  }

  protected abstract onInitialize(dependencies?: Record<string, unknown>): Promise<void>;
  protected abstract onDispose(): Promise<void>;
}

import { Position } from '../../types/core/Position';

/**
 * Particle animation easing functions
 */
export enum EasingType {
  LINEAR = 'linear',
  EASE_IN = 'easeIn',
  EASE_OUT = 'easeOut',
  EASE_IN_OUT = 'easeInOut',
  BOUNCE = 'bounce',
  ELASTIC = 'elastic',
  BACK = 'back',
}

/**
 * Easing function type
 */
export type EasingFunction = (progress: number) => number;

/**
 * Path type for particle movement
 */
export enum ParticlePath {
  LINEAR = 'linear',
  CURVED = 'curved',
  SPIRAL = 'spiral',
  BEZIER = 'bezier',
  WAVE = 'wave',
  RANDOM = 'random',
}

/**
 * Particle blend mode
 */
export enum ParticleBlendMode {
  NORMAL = 'normal',
  ADD = 'add',
  MULTIPLY = 'multiply',
  SCREEN = 'screen',
}

/**
 * Basic particle properties
 */
export interface Particle {
  id: string;
  position: Position;
  prevPosition?: Position;
  targetPosition?: Position;
  startPosition?: Position;
  velocity: { x: number; y: number };
  acceleration: { x: number; y: number };
  size: number;
  startSize?: number;
  targetSize?: number;
  color: string;
  startColor?: string;
  targetColor?: string;
  opacity: number;
  startOpacity?: number;
  targetOpacity?: number;
  rotation: number;
  startRotation?: number;
  targetRotation?: number;
  life: number;
  maxLife: number;
  active: boolean;
  path?: ParticlePath;
  pathParams?: Record<string, number>;
  easing?: EasingFunction | EasingType;
  blendMode?: ParticleBlendMode;
  group?: string;
  data?: Record<string, unknown>;
}

/**
 * Particle emitter configuration
 */
export interface ParticleEmitterConfig {
  position: Position;
  rate: number;
  burstCount?: number;
  emitRadius?: number;
  direction?: number; // Angle in radians
  spread?: number; // Angle in radians
  minLife?: number;
  maxLife?: number;
  minSize?: number;
  maxSize?: number;
  minVelocity?: number;
  maxVelocity?: number;
  colors?: string[];
  minOpacity?: number;
  maxOpacity?: number;
  gravity?: { x: number; y: number };
  path?: ParticlePath;
  pathParams?: Record<string, number>;
  easing?: EasingFunction | EasingType;
  blendMode?: ParticleBlendMode;
  group?: string;
}

/**
 * Transition configuration for moving particles between data states
 */
export interface ParticleTransitionConfig {
  /**
   * Optional transition ID
   */
  id?: string;

  /**
   * Source data points with positions
   */
  sourceData?: DataPoint[];

  /**
   * Target data points with positions
   */
  targetData?: DataPoint[];

  /**
   * Transition duration in milliseconds
   */
  duration: number;

  /**
   * Easing function or type for the transition
   */
  easing?: EasingFunction | EasingType;

  /**
   * Path type for particle movement
   */
  path?: ParticlePath;

  /**
   * Additional path parameters
   */
  pathParams?: Record<string, number>;

  /**
   * Delay between individual particle transitions in milliseconds
   */
  staggerDelay?: number;

  /**
   * Whether to transition colors
   */
  transitionColors?: boolean;

  /**
   * Whether to transition sizes
   */
  transitionSizes?: boolean;

  /**
   * Whether to transition opacity
   */
  transitionOpacity?: boolean;

  /**
   * Whether to reverse the transition
   */
  reverse?: boolean;

  /**
   * Callback when transition is complete
   */
  onComplete?: () => void;

  /**
   * Callback when transition is updated
   */
  onUpdate?: (progress: number) => void;
}

/**
 * Data point for visualization
 */
export interface DataPoint {
  id?: string;
  x: number;
  y: number;
  value: number;
  size?: number;
  color?: string;
  opacity?: number;
  group?: string;
  active?: boolean;
  [key: string]: unknown;
}

/**
 * Manages a particle system for animated data transitions
 */
export class ParticleSystem {
  private particles: Map<string, Particle> = new Map();
  private emitters: Map<string, ParticleEmitterConfig> = new Map();
  private lastFrameTime: number = 0;
  private animationFrame: number | null = null;
  private transitionConfigs: Map<string, ParticleTransitionConfig> = new Map();
  private transitionTimers: Map<string, number> = new Map();
  private transitionProgress: Map<string, number> = new Map();

  /**
   * Create a new particle system
   */
  constructor() {
    this.lastFrameTime = performance.now();
  }

  /**
   * Add a particle to the system
   */
  public addParticle(particle: Omit<Particle, 'id'>): string {
    const id = `particle-${Math.random().toString(36).substring(2, 9)}`;
    this.particles.set(id, {
      ...particle,
      id,
    });
    return id;
  }

  /**
   * Remove a particle from the system
   */
  public removeParticle(id: string): boolean {
    return this.particles.delete(id);
  }

  /**
   * Add a particle emitter
   */
  public addEmitter(config: ParticleEmitterConfig): string {
    const id = `emitter-${Math.random().toString(36).substring(2, 9)}`;
    this.emitters.set(id, config);
    return id;
  }

  /**
   * Remove a particle emitter
   */
  public removeEmitter(id: string): boolean {
    return this.emitters.delete(id);
  }

  /**
   * Start the animation loop
   */
  public start(): void {
    if (this.animationFrame !== null) {
      return;
    }

    this.lastFrameTime = performance.now();
    this.animationLoop();
  }

  /**
   * Stop the animation loop
   */
  public stop(): void {
    if (this.animationFrame !== null) {
      cancelAnimationFrame(this.animationFrame);
      this.animationFrame = null;
    }
  }

  /**
   * Clear all particles
   */
  public clear(): void {
    this.particles.clear();
  }

  /**
   * Set up a transition between data states
   */
  public setupTransition(id: string, config: ParticleTransitionConfig): void {
    this.transitionConfigs.set(id, config);
    this.transitionProgress.set(id, 0);

    // Create particles for transition if needed
    if (config.sourceData && config.targetData) {
      this.createParticlesForTransition(id, config);
    }
  }

  /**
   * Start a transition between data states
   */
  public startTransition(id: string): void {
    const config = this.transitionConfigs.get(id);
    if (!config) {
      console.warn(`No transition config found with id: ${id}`);
      return;
    }

    // Start the transition timer
    const startTime = performance.now();
    this.transitionTimers.set(id, startTime);

    // Ensure animation is running
    this.start();
  }

  /**
   * Stop a transition
   */
  public stopTransition(id: string): void {
    this.transitionTimers.delete(id);
  }

  /**
   * Create particles for a data transition
   */
  private createParticlesForTransition(
    transitionId: string,
    config: ParticleTransitionConfig
  ): void {
    if (!config.sourceData || !config.targetData) {
      return;
    }

    // Clear any existing particles for this transition
    this.particles.forEach((particle, id) => {
      if (particle.group === transitionId) {
        this.particles.delete(id);
      }
    });

    // Determine how to map source to target
    const isEqualSize = config.sourceData.length === config.targetData.length;

    if (isEqualSize) {
      // Direct mapping when source and target have same number of points
      config.sourceData.forEach((sourcePoint, index) => {
        const targetPoint = config.targetData![index];
        this.createTransitionParticle(sourcePoint, targetPoint, transitionId, config);
      });
    } else if (config.sourceData.length < config.targetData.length) {
      // Source has fewer points, need to generate additional particles
      config.sourceData.forEach((sourcePoint, index) => {
        const targetPoint = config.targetData![index % config.targetData!.length];
        this.createTransitionParticle(sourcePoint, targetPoint, transitionId, config);
      });

      // Create additional particles starting from appropriate source points
      for (let i = config.sourceData.length; i < config.targetData.length; i++) {
        const sourceIndex = i % config.sourceData.length;
        const sourcePoint = config.sourceData[sourceIndex];
        const targetPoint = config.targetData[i];
        this.createTransitionParticle(sourcePoint, targetPoint, transitionId, config);
      }
    } else {
      // Target has fewer points, some source particles will converge
      config.sourceData.forEach((sourcePoint, index) => {
        const targetPoint = config.targetData![index % config.targetData!.length];
        this.createTransitionParticle(sourcePoint, targetPoint, transitionId, config);
      });
    }
  }

  /**
   * Create a single transition particle
   */
  private createTransitionParticle(
    sourcePoint: DataPoint,
    targetPoint: DataPoint,
    transitionId: string,
    config: ParticleTransitionConfig
  ): string {
    const sourceColor = sourcePoint.color || '#ffffff';
    const targetColor = targetPoint.color || '#ffffff';
    const sourceSize = sourcePoint.size || 10;
    const targetSize = targetPoint.size || 10;
    const sourceOpacity = sourcePoint.opacity !== undefined ? sourcePoint.opacity : 1;
    const targetOpacity = targetPoint.opacity !== undefined ? targetPoint.opacity : 1;

    return this.addParticle({
      position: { x: sourcePoint.x, y: sourcePoint.y },
      startPosition: { x: sourcePoint.x, y: sourcePoint.y },
      targetPosition: { x: targetPoint.x, y: targetPoint.y },
      velocity: { x: 0, y: 0 },
      acceleration: { x: 0, y: 0 },
      size: sourceSize,
      startSize: sourceSize,
      targetSize: targetSize,
      color: sourceColor,
      startColor: sourceColor,
      targetColor: targetColor,
      opacity: sourceOpacity,
      startOpacity: sourceOpacity,
      targetOpacity: targetOpacity,
      rotation: 0,
      life: 1,
      maxLife: 1,
      active: true,
      path: config.path || ParticlePath.LINEAR,
      easing: config.easing || this.getEasingFunction(EasingType.EASE_IN_OUT),
      pathParams: config.pathParams,
      group: transitionId,
      data: {
        sourcePoint,
        targetPoint,
        startTime: performance.now(),
        staggerDelay: config.staggerDelay || 0,
        transitionStarted: false,
      },
    });
  }

  /**
   * Main animation loop
   */
  private animationLoop(): void {
    const currentTime = performance.now();
    const deltaTime = (currentTime - this.lastFrameTime) / 1000; // in seconds
    this.lastFrameTime = currentTime;

    // Update transitions
    this.updateTransitions(currentTime);

    // Update emitters
    this.updateEmitters(deltaTime);

    // Update particles
    this.updateParticles(deltaTime);

    // Schedule next frame
    this.animationFrame = requestAnimationFrame(() => this.animationLoop());
  }

  /**
   * Update particle transitions
   */
  private updateTransitions(currentTime: number): void {
    this.transitionTimers.forEach((startTime, id) => {
      const config = this.transitionConfigs.get(id);
      if (!config) return;

      // Calculate progress
      const elapsedTime = currentTime - startTime;
      const progress = Math.min(1, elapsedTime / config.duration);

      // Store progress
      this.transitionProgress.set(id, progress);

      // Call update callback
      config.onUpdate?.(progress);

      // Check if transition is complete
      if (progress >= 1) {
        this.transitionTimers.delete(id);
        config.onComplete?.();
      }
    });
  }

  /**
   * Update particle emitters
   */
  private updateEmitters(deltaTime: number): void {
    this.emitters.forEach((config, id) => {
      // Calculate number of particles to emit
      const emitCount = config.burstCount || Math.floor(config.rate * deltaTime);

      // Emit particles
      for (let i = 0; i < emitCount; i++) {
        this.emitParticle(config);
      }
    });
  }

  /**
   * Emit a single particle from an emitter
   */
  private emitParticle(config: ParticleEmitterConfig): string {
    // Randomize position within emit radius
    const angle = Math.random() * Math.PI * 2;
    const radius = Math.random() * (config.emitRadius || 0);
    const position = {
      x: config.position.x + Math.cos(angle) * radius,
      y: config.position.y + Math.sin(angle) * radius,
    };

    // Randomize velocity
    const direction = (config.direction || 0) + (Math.random() - 0.5) * (config.spread || 0);
    const speed =
      config.minVelocity !== undefined && config.maxVelocity !== undefined
        ? config.minVelocity + Math.random() * (config.maxVelocity - config.minVelocity)
        : 50;

    const velocity = {
      x: Math.cos(direction) * speed,
      y: Math.sin(direction) * speed,
    };

    // Randomize life
    const life =
      config.minLife !== undefined && config.maxLife !== undefined
        ? config.minLife + Math.random() * (config.maxLife - config.minLife)
        : 1;

    // Randomize size
    const size =
      config.minSize !== undefined && config.maxSize !== undefined
        ? config.minSize + Math.random() * (config.maxSize - config.minSize)
        : 10;

    // Randomize color
    const color =
      config.colors && config.colors.length > 0
        ? config.colors[Math.floor(Math.random() * config.colors.length)]
        : '#ffffff';

    // Randomize opacity
    const opacity =
      config.minOpacity !== undefined && config.maxOpacity !== undefined
        ? config.minOpacity + Math.random() * (config.maxOpacity - config.minOpacity)
        : 1;

    // Create particle
    return this.addParticle({
      position,
      velocity,
      acceleration: config.gravity || { x: 0, y: 0 },
      size,
      color,
      opacity,
      rotation: Math.random() * Math.PI * 2,
      life,
      maxLife: life,
      active: true,
      path: config.path,
      pathParams: config.pathParams,
      easing: config.easing,
      blendMode: config.blendMode,
      group: config.group,
    });
  }

  /**
   * Update all particles
   */
  private updateParticles(deltaTime: number): void {
    this.particles.forEach((particle, id) => {
      if (!particle.active) return;

      // Handle transition particles
      if (particle.group && this.transitionTimers.has(particle.group)) {
        this.updateTransitionParticle(particle, this.transitionProgress.get(particle.group) || 0);
      } else {
        // Handle regular particles
        this.updateRegularParticle(particle, deltaTime);
      }

      // Remove dead particles
      if (particle.life <= 0) {
        this.particles.delete(id);
      }
    });
  }

  /**
   * Update a transition particle
   */
  private updateTransitionParticle(particle: Particle, transitionProgress: number): void {
    if (!particle.startPosition || !particle.targetPosition) return;

    const data = particle.data as Record<string, unknown>;
    const staggerDelay = (data.staggerDelay as number) || 0;

    // Handle staggered start
    if (!data.transitionStarted) {
      const elapsedSinceStart = performance.now() - (data.startTime as number);
      if (elapsedSinceStart < staggerDelay) {
        return;
      }
      data.transitionStarted = true;
    }

    // Apply easing
    const easedProgress = this.applyEasing(
      transitionProgress,
      particle.easing || this.getEasingFunction(EasingType.LINEAR)
    );

    // Update position based on path type
    this.updateParticlePosition(particle, easedProgress);

    // Update size
    if (particle.startSize !== undefined && particle.targetSize !== undefined) {
      particle.size =
        particle.startSize + (particle.targetSize - particle.startSize) * easedProgress;
    }

    // Update opacity
    if (particle.startOpacity !== undefined && particle.targetOpacity !== undefined) {
      particle.opacity =
        particle.startOpacity + (particle.targetOpacity - particle.startOpacity) * easedProgress;
    }

    // Update color
    if (particle.startColor && particle.targetColor) {
      particle.color = this.interpolateColor(
        particle.startColor,
        particle.targetColor,
        easedProgress
      );
    }

    // Store previous position for trail effects
    particle.prevPosition = { ...particle.position };
  }

  /**
   * Update a regular particle
   */
  private updateRegularParticle(particle: Particle, deltaTime: number): void {
    // Update velocity
    particle.velocity.x += particle.acceleration.x * deltaTime;
    particle.velocity.y += particle.acceleration.y * deltaTime;

    // Store previous position for trail effects
    particle.prevPosition = { ...particle.position };

    // Update position
    particle.position.x += particle.velocity.x * deltaTime;
    particle.position.y += particle.velocity.y * deltaTime;

    // Update life
    particle.life -= deltaTime;

    // Update opacity based on life
    const lifeRatio = Math.max(0, particle.life / particle.maxLife);
    particle.opacity = lifeRatio;
  }

  /**
   * Update particle position based on path type
   */
  private updateParticlePosition(particle: Particle, progress: number): void {
    const start = particle.startPosition || { x: 0, y: 0 };
    const end = particle.targetPosition || { x: 0, y: 0 };
    let position: Position;

    switch (particle.path || ParticlePath.LINEAR) {
      case ParticlePath.CURVED:
        position = this.calculateCurvedPath(start, end, progress);
        break;

      case ParticlePath.SPIRAL:
        position = this.calculateSpiralPath(start, end, progress, particle.pathParams?.turns);
        break;

      case ParticlePath.BEZIER:
        position = this.calculateBezierPath(start, end, progress);
        break;

      case ParticlePath.WAVE:
        position = this.calculateWavePath(
          start,
          end,
          progress,
          particle.pathParams?.amplitude,
          particle.pathParams?.frequency
        );
        break;

      case ParticlePath.RANDOM:
        position = this.calculateRandomPath(
          start,
          end,
          progress,
          particle,
          particle.pathParams?.jitter
        );
        break;

      case ParticlePath.LINEAR:
      default:
        position = {
          x: start.x + (end.x - start.x) * progress,
          y: start.y + (end.y - start.y) * progress,
        };
    }

    particle.position = position;
  }

  /**
   * Calculate curved path position
   */
  private calculateCurvedPath(start: Position, end: Position, progress: number): Position {
    const controlX = (start.x + end.x) / 2;
    const controlY = Math.min(start.y, end.y) - Math.abs(end.x - start.x) * 0.2;

    const t = progress;
    const invT = 1 - t;

    return {
      x: invT * invT * start.x + 2 * invT * t * controlX + t * t * end.x,
      y: invT * invT * start.y + 2 * invT * t * controlY + t * t * end.y,
    };
  }

  /**
   * Calculate spiral path position
   */
  private calculateSpiralPath(
    start: Position,
    end: Position,
    progress: number,
    turns = 2
  ): Position {
    const angle = progress * turns * Math.PI * 2;
    const radius =
      (1 - progress) * Math.min(Math.abs(end.x - start.x), Math.abs(end.y - start.y)) * 0.2;

    return {
      x: start.x + (end.x - start.x) * progress + Math.cos(angle) * radius,
      y: start.y + (end.y - start.y) * progress + Math.sin(angle) * radius,
    };
  }

  /**
   * Calculate bezier path position
   */
  private calculateBezierPath(start: Position, end: Position, progress: number): Position {
    const cp1x = start.x + (end.x - start.x) * 0.3;
    const cp1y = start.y - Math.abs(end.y - start.y) * 0.3;
    const cp2x = start.x + (end.x - start.x) * 0.7;
    const cp2y = end.y + Math.abs(end.y - start.y) * 0.3;

    const t1 = progress;
    const t2 = t1 * t1;
    const t3 = t2 * t1;
    const invT1 = 1 - t1;
    const invT2 = invT1 * invT1;
    const invT3 = invT2 * invT1;

    return {
      x: invT3 * start.x + 3 * invT2 * t1 * cp1x + 3 * invT1 * t2 * cp2x + t3 * end.x,
      y: invT3 * start.y + 3 * invT2 * t1 * cp1y + 3 * invT1 * t2 * cp2y + t3 * end.y,
    };
  }

  /**
   * Calculate wave path position
   */
  private calculateWavePath(
    start: Position,
    end: Position,
    progress: number,
    amplitude?: number,
    frequency = 3
  ): Position {
    const actualAmplitude =
      amplitude || Math.min(Math.abs(end.x - start.x), Math.abs(end.y - start.y)) * 0.1;
    const waviness = Math.sin(progress * Math.PI * frequency) * actualAmplitude;

    // Calculate the normal vector to the path
    const dx = end.x - start.x;
    const dy = end.y - start.y;
    const length = Math.sqrt(dx * dx + dy * dy);
    const normalX = -dy / length;
    const normalY = dx / length;

    return {
      x: start.x + (end.x - start.x) * progress + normalX * waviness,
      y: start.y + (end.y - start.y) * progress + normalY * waviness,
    };
  }

  /**
   * Calculate random path position
   */
  private calculateRandomPath(
    start: Position,
    end: Position,
    progress: number,
    particle: Particle,
    jitter = 0.1
  ): Position {
    // Get or create random seeds
    let seeds = (particle.data?.randomSeeds as number[]) || [];
    if (!seeds.length) {
      seeds = Array.from({ length: 10 }, () => Math.random());
      (particle.data as Record<string, unknown>).randomSeeds = seeds;
    }

    const jitterSize = Math.min(Math.abs(end.x - start.x), Math.abs(end.y - start.y)) * jitter;

    // Use seeds and progress to generate controlled randomness
    const index = Math.floor(progress * 10);
    const subProgress = (progress * 10) % 1;
    const seed1 = seeds[index % seeds.length];
    const seed2 = seeds[(index + 1) % seeds.length];

    const randomX =
      (seed1 * 2 - 1) * jitterSize * (1 - subProgress) + (seed2 * 2 - 1) * jitterSize * subProgress;
    const randomY =
      (seeds[(index + 2) % seeds.length] * 2 - 1) * jitterSize * (1 - subProgress) +
      (seeds[(index + 3) % seeds.length] * 2 - 1) * jitterSize * subProgress;

    return {
      x: start.x + (end.x - start.x) * progress + randomX,
      y: start.y + (end.y - start.y) * progress + randomY,
    };
  }

  /**
   * Bounce easing function
   */
  private bounceEasing(t: number): number {
    const a = 7.5625;
    const b = 2.75;

    if (t < 1 / b) {
      return a * t * t;
    } else if (t < 2 / b) {
      t -= 1.5 / b;
      return a * t * t + 0.75;
    } else if (t < 2.5 / b) {
      t -= 2.25 / b;
      return a * t * t + 0.9375;
    } else {
      t -= 2.625 / b;
      return a * t * t + 0.984375;
    }
  }

  /**
   * Elastic easing function
   */
  private elasticEasing(t: number): number {
    return t === 0
      ? 0
      : t === 1
        ? 1
        : Math.pow(2, -10 * t) * Math.sin(((t * 10 - 0.75) * Math.PI) / 1.5) + 1;
  }

  /**
   * Back easing function
   */
  private backEasing(t: number): number {
    const overshoot = 1.70158;
    return t * t * ((overshoot + 1) * t - overshoot);
  }

  /**
   * Get easing function by type
   */
  private getEasingFunction(type: EasingType): EasingFunction {
    switch (type) {
      case EasingType.LINEAR:
        return (t: number) => t;

      case EasingType.EASE_IN:
        return (t: number) => t * t;

      case EasingType.EASE_OUT:
        return (t: number) => t * (2 - t);

      case EasingType.EASE_IN_OUT:
        return (t: number) => (t < 0.5 ? 2 * t * t : -1 + (4 - 2 * t) * t);

      case EasingType.BOUNCE:
        return this.bounceEasing.bind(this);

      case EasingType.ELASTIC:
        return this.elasticEasing.bind(this);

      case EasingType.BACK:
        return this.backEasing.bind(this);

      default:
        return (t: number) => t;
    }
  }

  /**
   * Apply easing function to progress
   */
  private applyEasing(progress: number, easing: EasingFunction | EasingType): number {
    if (typeof easing === 'function') {
      return easing(progress);
    }
    return this.getEasingFunction(easing)(progress);
  }

  /**
   * Interpolate between two colors
   */
  private interpolateColor(color1: string, color2: string, progress: number): string {
    // Parse colors
    const parseColor = (color: string): [number, number, number] => {
      // Handle hex colors
      if (color.startsWith('#')) {
        const hex = color.substring(1);
        if (hex.length === 3) {
          return [
            parseInt(hex[0] + hex[0], 16),
            parseInt(hex[1] + hex[1], 16),
            parseInt(hex[2] + hex[2], 16),
          ];
        } else {
          return [
            parseInt(hex.substring(0, 2), 16),
            parseInt(hex.substring(2, 4), 16),
            parseInt(hex.substring(4, 6), 16),
          ];
        }
      }

      // Handle rgb colors
      if (color.startsWith('rgb')) {
        const match = color.match(/rgb\((\d+),\s*(\d+),\s*(\d+)\)/);
        if (match) {
          return [parseInt(match[1]), parseInt(match[2]), parseInt(match[3])];
        }
      }

      // Default
      return [255, 255, 255];
    };

    const [r1, g1, b1] = parseColor(color1);
    const [r2, g2, b2] = parseColor(color2);

    // Interpolate
    const r = Math.round(r1 + (r2 - r1) * progress);
    const g = Math.round(g1 + (g2 - g1) * progress);
    const b = Math.round(b1 + (b2 - b1) * progress);

    // Convert back to hex
    return `#${r.toString(16).padStart(2, '0')}${g.toString(16).padStart(2, '0')}${b.toString(16).padStart(2, '0')}`;
  }

  /**
   * Get all particles
   */
  public getParticles(): Particle[] {
    return Array.from(this.particles.values());
  }

  /**
   * Get particles by group
   */
  public getParticlesByGroup(group: string): Particle[] {
    return Array.from(this.particles.values()).filter(p => p.group === group);
  }

  /**
   * Check if a transition is running
   */
  public isTransitionRunning(id: string): boolean {
    return this.transitionTimers.has(id);
  }

  /**
   * Get transition progress
   */
  public getTransitionProgress(id: string): number {
    return this.transitionProgress.get(id) || 0;
  }

  /**
   * Clear everything
   */
  public dispose(): void {
    this.stop();
    this.particles.clear();
    this.emitters.clear();
    this.transitionConfigs.clear();
    this.transitionTimers.clear();
    this.transitionProgress.clear();
  }
}

import { ModuleEvent, moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import {
  getSystemCommunication,
  MessagePriority,
  SystemId,
} from '../../utils/events/EventCommunication';
import { EventPriorityQueue } from '../../utils/events/EventFiltering';
import {
  AutomationAction,
  AutomationCondition,
  AutomationManager,
  AutomationRule,
  ResourceConditionValue,
} from '../game/AutomationManager';
import { gameLoopManager, UpdatePriority } from '../game/GameLoopManager';
import { ResourceType } from './../../types/resources/ResourceTypes';

/**
 * Global routine type
 */
export type GlobalRoutineType =
  | 'system-maintenance'
  | 'resource-balancing'
  | 'performance-optimization'
  | 'emergency-response'
  | 'scheduled-task'
  | 'custom';

/**
 * Global routine interface
 */
export interface GlobalRoutine {
  id: string;
  name: string;
  type: GlobalRoutineType;
  description: string;
  enabled: boolean;
  priority: MessagePriority;
  interval: number;
  lastRun?: number;
  conditions: AutomationCondition[];
  actions: AutomationAction[];
  systems: SystemId[];
  tags: string[];
}

/**
 * Global automation manager
 * Extends the module-specific automation with system-wide routines
 */
export class GlobalAutomationManager {
  private _automationManager: AutomationManager;
  private routines: Map<string, GlobalRoutine>;
  private activeRoutines: Map<string, boolean>;
  private routineQueue: EventPriorityQueue<GlobalRoutine & { executionTime: number }>;
  private systemCommunications: Map<SystemId, ReturnType<typeof getSystemCommunication>>;
  private isInitialized: boolean = false;

  constructor(_automationManager: AutomationManager) {
    this._automationManager = _automationManager;
    this.routines = new Map();
    this.activeRoutines = new Map();
    this.systemCommunications = new Map();

    // Create a priority queue for routine execution
    this.routineQueue = new EventPriorityQueue(routine => {
      return this.executeRoutine(routine);
    });
  }

  /**
   * Initialize the global automation manager
   */
  public initialize(): void {
    if (this.isInitialized) {
      return;
    }

    console.warn('Initializing Global Automation Manager...');

    // Log the automation manager status
    if (this._automationManager) {
      console.warn(
        `[GlobalAutomationManager] Using automation manager for condition checking and rule management`
      );
    } else {
      console.warn(
        '[GlobalAutomationManager] No automation manager provided, some features may be limited'
      );
    }

    // Initialize system communications
    this.initializeSystemCommunications();

    // Register with game loop for regular updates
    gameLoopManager.registerUpdate(
      'global-automation-manager',
      this.update.bind(this),
      UpdatePriority.NORMAL
    );

    // Subscribe to relevant events
    moduleEventBus.subscribe('ERROR_OCCURRED' as ModuleEventType, this.handleErrorEvent);
    moduleEventBus.subscribe('RESOURCE_SHORTAGE' as ModuleEventType, this.handleResourceShortage);
    moduleEventBus.subscribe('STATUS_CHANGED' as ModuleEventType, this.handleStatusChanged);

    this.isInitialized = true;

    // Emit initialization event
    moduleEventBus.emit({
      type: 'AUTOMATION_STARTED' as ModuleEventType,
      moduleId: 'global-automation',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        routineCount: this.routines.size,
        systems: Array.from(this.systemCommunications.keys()),
      },
    });
  }

  /**
   * Initialize system communications
   */
  private initializeSystemCommunications(): void {
    const systems: SystemId[] = [
      'resource-system',
      'module-system',
      'combat-system',
      'exploration-system',
      'mining-system',
      'tech-system',
      'ui-system',
      'game-loop',
      'event-system',
    ];

    systems.forEach(systemId => {
      const communication = getSystemCommunication(systemId);
      this.systemCommunications.set(systemId, communication);

      // Register message handler for automation requests
      communication.registerHandler('automation-request', message => {
        console.warn(`Received automation request from ${systemId}:`, message.payload);

        // Use type guard instead of type assertion
        const { payload } = message;
        if (payload && typeof payload === 'object') {
          const routineId = 'routineId' in payload ? String(payload.routineId) : undefined;
          const createRoutine =
            'createRoutine' in payload &&
            payload.createRoutine &&
            typeof payload.createRoutine === 'object'
              ? (payload.createRoutine as GlobalRoutine)
              : undefined;

          if (routineId) {
            const routine = this.routines.get(routineId);
            if (routine && routine.enabled) {
              this.scheduleRoutine(routine);
              return;
            }
          }

          if (createRoutine) {
            this.registerRoutine(createRoutine);
          }
        }
      });
    });
  }

  /**
   * Register a global routine
   */
  public registerRoutine(routine: GlobalRoutine): string {
    // Generate ID if not provided
    if (!routine.id) {
      routine.id = `routine-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    }

    this.routines.set(routine.id, routine);
    this.activeRoutines.set(routine.id, routine.enabled);

    // If routine is enabled, schedule it
    if (routine.enabled) {
      this.scheduleRoutine(routine);
    }

    // Notify systems about the new routine
    routine.systems.forEach(systemId => {
      const communication = this.systemCommunications.get(systemId);
      if (communication) {
        communication.sendMessage('broadcast', 'routine-registered', {
          routineId: routine.id,
          name: routine.name,
          type: routine.type,
        });
      }
    });

    return routine.id;
  }

  /**
   * Unregister a global routine
   */
  public unregisterRoutine(routineId: string): boolean {
    const routine = this.routines.get(routineId);
    if (!routine) {
      return false;
    }

    // Notify systems about routine removal
    routine.systems.forEach(systemId => {
      const communication = this.systemCommunications.get(systemId);
      if (communication) {
        communication.sendMessage(systemId, 'routine-unregistered', {
          routineId: routine.id,
        });
      }
    });

    this.routines.delete(routineId);
    this.activeRoutines.delete(routineId);
    return true;
  }

  /**
   * Enable a global routine
   */
  public enableRoutine(routineId: string): boolean {
    const routine = this.routines.get(routineId);
    if (!routine) {
      return false;
    }

    routine.enabled = true;
    this.activeRoutines.set(routineId, true);

    // Schedule the routine
    this.scheduleRoutine(routine);

    return true;
  }

  /**
   * Disable a global routine
   */
  public disableRoutine(routineId: string): boolean {
    const routine = this.routines.get(routineId);
    if (!routine) {
      return false;
    }

    routine.enabled = false;
    this.activeRoutines.set(routineId, false);
    return true;
  }

  /**
   * Schedule a routine for execution
   */
  private scheduleRoutine(routine: GlobalRoutine): void {
    // Add to the priority queue
    this.routineQueue.enqueue({
      ...routine,
      executionTime: Date.now() + (routine.interval || 0),
    });
  }

  /**
   * Execute a routine
   */
  private async executeRoutine(routine: GlobalRoutine & { executionTime: number }): Promise<void> {
    try {
      // Skip if routine is disabled
      if (!routine.enabled) {
        return;
      }

      // Skip if it's not time to run yet
      if (Date.now() < routine.executionTime) {
        // Re-queue for later execution
        this.scheduleRoutine(routine);
        return;
      }

      console.warn(`Executing routine: ${routine.name} (${routine.id})`);

      // Check conditions
      let conditionsMet = true;
      try {
        // Try to check conditions, but handle if the method is private
        // We'll implement our own simple condition checking if needed
        for (const condition of routine.conditions) {
          if (condition.type === 'RESOURCE_BELOW') {
            // Simple implementation for resource below condition
            const { target, value } = condition;
            if (target && value !== undefined) {
              const resourceAmount = this.getResourceAmount(target as ResourceType);
              // Extract numeric value for comparison
              const threshold =
                typeof value === 'number' ? value : (value as ResourceConditionValue).amount;
              if (resourceAmount > threshold) {
                conditionsMet = false;
                break;
              }
            }
          } else if (condition.type === 'RESOURCE_ABOVE') {
            // Simple implementation for resource above condition
            const { target, value } = condition;
            if (target && value !== undefined) {
              const resourceAmount = this.getResourceAmount(target as ResourceType);
              // Extract numeric value for comparison
              const threshold =
                typeof value === 'number' ? value : (value as ResourceConditionValue).amount;
              if (resourceAmount < threshold) {
                conditionsMet = false;
                break;
              }
            }
          }
          // Add more condition types as needed
        }
      } catch (error) {
        console.warn('Error checking conditions, using simple implementation:', error);
      }

      if (!conditionsMet) {
        console.warn(`Conditions not met for routine: ${routine.name}`);

        // Re-schedule for next interval
        this.scheduleRoutine({
          ...routine,
          lastRun: Date.now(),
        });
        return;
      }

      // Execute actions
      try {
        // Try to execute actions through the automation manager
        // If that fails, implement our own simple action execution
        for (const action of routine.actions) {
          await this.executeAction(action);
        }
      } catch (error) {
        console.warn('Error executing actions, using simple implementation:', error);
      }

      // Update last run time
      const updatedRoutine = {
        ...routine,
        lastRun: Date.now(),
      };
      this.routines.set(routine.id, updatedRoutine);

      // Emit routine completion event
      moduleEventBus.emit({
        type: 'AUTOMATION_CYCLE_COMPLETE' as ModuleEventType,
        moduleId: 'global-automation',
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        data: {
          routineId: routine.id,
          routineName: routine.name,
          routineType: routine.type,
        },
      });

      // Notify relevant systems
      routine.systems.forEach(systemId => {
        const communication = this.systemCommunications.get(systemId);
        if (communication) {
          communication.sendMessage(systemId, 'routine-executed', {
            routineId: routine.id,
            success: true,
            timestamp: Date.now(),
          });
        }
      });

      // Re-schedule for next interval
      this.scheduleRoutine(updatedRoutine);
    } catch (error) {
      console.error(`Error executing routine ${routine.id}:`, error);

      // Emit error event
      moduleEventBus.emit({
        type: 'ERROR_OCCURRED' as ModuleEventType,
        moduleId: 'global-automation',
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        data: {
          routineId: routine.id,
          error: error instanceof Error ? error.message : String(error),
        },
      });

      // Re-schedule for next interval
      this.scheduleRoutine({
        ...routine,
        lastRun: Date.now(),
      });
    }
  }

  /**
   * Get the current amount of a resource
   * @param resourceType The type of resource to check
   * @returns The current amount of the resource
   */
  private getResourceAmount(resourceType: ResourceType): number {
    // Implementation would get the actual resource amount from the game state
    // For now, return a mock value
    return 100;
  }

  /**
   * Helper method to execute a single action
   */
  private async executeAction(action: AutomationAction): Promise<void> {
    switch (action.type) {
      case 'ACTIVATE_MODULE':
        if (!action.target) {
          return;
        }
        // Emit an event to activate the module
        moduleEventBus.emit({
          type: 'MODULE_ACTIVATED' as ModuleEventType,
          moduleId: action.target,
          moduleType: 'resource-manager',
          timestamp: Date.now(),
          data: { source: 'automation' },
        });
        break;

      case 'DEACTIVATE_MODULE':
        if (!action.target) {
          return;
        }
        // Emit an event to deactivate the module
        moduleEventBus.emit({
          type: 'MODULE_DEACTIVATED' as ModuleEventType,
          moduleId: action.target,
          moduleType: 'resource-manager',
          timestamp: Date.now(),
          data: { source: 'automation' },
        });
        break;

      case 'TRANSFER_RESOURCES':
        if (!action.target || !action.value) {
          return;
        }
        // Emit an event to transfer resources
        moduleEventBus.emit({
          type: 'RESOURCE_TRANSFERRED' as ModuleEventType,
          moduleId: 'automation',
          moduleType: 'resource-manager',
          timestamp: Date.now(),
          data: this.convertActionValueToRecord(action.value),
        });
        break;

      case 'EMIT_EVENT': {
        if (!action.target || !action.value) {
          return;
        }

        // Define interface for EmitEventValue
        interface EmitEventValue {
          moduleId?: string;
          moduleType?: string;
          data?: Record<string, unknown>;
          [key: string]: unknown; // Add index signature to satisfy Record<string, unknown>
        }

        // Use type guard instead of type assertion
        const emitValue: EmitEventValue = {};

        if (typeof action.value === 'object' && action.value !== null) {
          // First cast to unknown to avoid type errors
          const value = action.value as unknown as Record<string, unknown>;

          if ('moduleId' in value && typeof value.moduleId === 'string') {
            emitValue.moduleId = value.moduleId;
          }

          if ('moduleType' in value && typeof value.moduleType === 'string') {
            emitValue.moduleType = value.moduleType as ModuleType;
          }

          if ('data' in value && typeof value.data === 'object' && value.data !== null) {
            emitValue.data = value.data as Record<string, unknown>;
          }
        }

        // Validate that action.target is a valid ModuleEventType
        const isValidEventType = (type: string): type is ModuleEventType => {
          return [
            'MODULE_CREATED',
            'MODULE_ATTACHED',
            'MODULE_DETACHED',
            'MODULE_UPGRADED',
            'MODULE_ACTIVATED',
            'MODULE_DEACTIVATED',
            'MODULE_UPDATED',
            'ATTACHMENT_STARTED',
            'ATTACHMENT_CANCELLED',
            'ATTACHMENT_COMPLETED',
            'ATTACHMENT_PREVIEW_SHOWN',
            'RESOURCE_PRODUCED',
            'RESOURCE_CONSUMED',
            'RESOURCE_TRANSFERRED',
            'RESOURCE_PRODUCTION_REGISTERED',
            'RESOURCE_PRODUCTION_UNREGISTERED',
            'RESOURCE_CONSUMPTION_REGISTERED',
            'RESOURCE_CONSUMPTION_UNREGISTERED',
            'RESOURCE_FLOW_REGISTERED',
            'RESOURCE_FLOW_UNREGISTERED',
            'RESOURCE_SHORTAGE',
            'RESOURCE_UPDATED',
            'AUTOMATION_STARTED',
            'AUTOMATION_STOPPED',
            'AUTOMATION_CYCLE_COMPLETE',
            'STATUS_CHANGED',
            'ERROR_OCCURRED',
            'MISSION_STARTED',
            'MISSION_COMPLETED',
            'MISSION_FAILED',
            'MISSION_PROGRESS_UPDATED',
            'MISSION_REWARD_CLAIMED',
            'SUB_MODULE_CREATED',
            'SUB_MODULE_ATTACHED',
            'SUB_MODULE_DETACHED',
            'SUB_MODULE_UPGRADED',
            'SUB_MODULE_ACTIVATED',
            'SUB_MODULE_DEACTIVATED',
            'SUB_MODULE_EFFECT_APPLIED',
            'SUB_MODULE_EFFECT_REMOVED',
            'COMBAT_UPDATED',
            'TECH_UNLOCKED',
            'TECH_UPDATED',
          ].includes(type as ModuleEventType);
        };

        if (isValidEventType(action.target)) {
          // Emit the specified event
          moduleEventBus.emit({
            type: action.target,
            moduleId: emitValue.moduleId || 'automation',
            moduleType: (emitValue.moduleType || 'resource-manager') as ModuleType,
            timestamp: Date.now(),
            data: emitValue.data || {},
          });
        } else {
          console.warn(`Invalid event type: ${action.target}`);
        }
        break;
      }

      default:
        console.warn(`Unsupported action type: ${action.type}`);
    }
  }

  // Add a helper method to convert action values to Record<string, unknown>
  private convertActionValueToRecord(value: unknown): Record<string, unknown> {
    if (value === null || value === undefined) {
      return {};
    }

    if (typeof value === 'object') {
      // If it's already an object, convert it to a Record<string, unknown>
      // First cast to unknown, then to Record<string, unknown> to avoid type errors
      return Object.entries(value as unknown as Record<string, unknown>).reduce(
        (acc, [key, val]) => {
          acc[key] = val;
          return acc;
        },
        {} as Record<string, unknown>
      );
    }

    // If it's a primitive value, wrap it in an object
    return { value };
  }

  /**
   * Update method called by the game loop
   */
  private update(_deltaTime: number, _elapsedTime: number): void {
    // Process any pending routines
    // The queue itself handles the execution
  }

  /**
   * Handle error events
   */
  private handleErrorEvent = (_event: ModuleEvent): void => {
    // Find emergency response routines
    const emergencyRoutines = Array.from(this.routines.values()).filter(
      routine =>
        routine.enabled &&
        routine.type === 'emergency-response' &&
        routine.tags.includes('error-handling')
    );

    // Schedule emergency routines immediately
    emergencyRoutines.forEach(routine => {
      this.routineQueue.enqueue({
        ...routine,
        executionTime: Date.now(), // Execute immediately
      });
    });
  };

  /**
   * Handle resource shortage events
   */
  private handleResourceShortage = (event: ModuleEvent): void => {
    // Find resource balancing routines
    const resourceRoutines = Array.from(this.routines.values()).filter(
      routine =>
        routine.enabled &&
        routine.type === 'resource-balancing' &&
        routine.tags.includes(
          event.data && typeof event.data === 'object' && 'resourceType' in event.data
            ? String(event.data.resourceType)
            : 'general'
        )
    );

    // Schedule resource routines with high priority
    resourceRoutines.forEach(routine => {
      this.routineQueue.enqueue({
        ...routine,
        executionTime: Date.now(), // Execute immediately
        priority: MessagePriority.HIGH, // Override with high priority
      });
    });
  };

  /**
   * Handle status changed events
   */
  private handleStatusChanged = (event: ModuleEvent): void => {
    // Find relevant routines based on status
    const statusRoutines = Array.from(this.routines.values()).filter(
      routine =>
        routine.enabled &&
        (routine.type === 'system-maintenance' || routine.type === 'performance-optimization') &&
        routine.tags.includes(
          event.data && typeof event.data === 'object' && 'status' in event.data
            ? String(event.data.status)
            : 'general'
        )
    );

    // Schedule status routines
    statusRoutines.forEach(routine => {
      this.routineQueue.enqueue({
        ...routine,
        executionTime: Date.now() + 1000, // Small delay to allow system to stabilize
      });
    });
  };

  /**
   * Get all routines
   */
  public getAllRoutines(): GlobalRoutine[] {
    return Array.from(this.routines.values());
  }

  /**
   * Get routines by type
   */
  public getRoutinesByType(type: GlobalRoutineType): GlobalRoutine[] {
    return Array.from(this.routines.values()).filter(routine => routine.type === type);
  }

  /**
   * Get routines by system
   */
  public getRoutinesBySystem(systemId: SystemId): GlobalRoutine[] {
    return Array.from(this.routines.values()).filter(routine => routine.systems.includes(systemId));
  }

  /**
   * Get routines by tag
   */
  public getRoutinesByTag(tag: string): GlobalRoutine[] {
    return Array.from(this.routines.values()).filter(routine => routine.tags.includes(tag));
  }

  /**
   * Get active routines
   */
  public getActiveRoutines(): GlobalRoutine[] {
    return Array.from(this.routines.values()).filter(routine => routine.enabled);
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    // Unregister from game loop
    gameLoopManager.unregisterUpdate('global-automation-manager');

    // Unsubscribe from events
    const unsubscribeError = moduleEventBus.subscribe(
      'ERROR_OCCURRED' as ModuleEventType,
      this.handleErrorEvent
    );
    const unsubscribeShortage = moduleEventBus.subscribe(
      'RESOURCE_SHORTAGE' as ModuleEventType,
      this.handleResourceShortage
    );
    const unsubscribeStatus = moduleEventBus.subscribe(
      'STATUS_CHANGED' as ModuleEventType,
      this.handleStatusChanged
    );

    if (typeof unsubscribeError === 'function') {
      unsubscribeError();
    }
    if (typeof unsubscribeShortage === 'function') {
      unsubscribeShortage();
    }
    if (typeof unsubscribeStatus === 'function') {
      unsubscribeStatus();
    }

    // Clear routines
    this.routines.clear();
    this.activeRoutines.clear();

    // Clear system communications
    this.systemCommunications.clear();

    this.isInitialized = false;
  }

  /**
   * Get the automation manager instance
   * This method is used for testing and debugging purposes
   */
  public getAutomationManager(): AutomationManager | null {
    return this._automationManager || null;
  }

  /**
   * Get a rule by ID
   */
  public getRule(ruleId: string): AutomationRule | undefined {
    // Delegate to AutomationManager
    return this._automationManager.getRule(ruleId);
  }

  /**
   * Update an existing rule
   */
  public updateRule(ruleId: string, rule: AutomationRule): void {
    // Delegate to AutomationManager
    this._automationManager.updateRule(ruleId, rule);

    // Emit event
    moduleEventBus.emit({
      type: 'STATUS_CHANGED', // Use a valid ModuleEventType
      moduleId: rule.moduleId,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { ruleId, rule, status: 'updated' },
    });
  }

  /**
   * Register a new rule
   */
  public registerRule(rule: AutomationRule): void {
    // Delegate to AutomationManager
    this._automationManager.registerRule(rule);

    // Emit event
    moduleEventBus.emit({
      type: 'AUTOMATION_STARTED', // Use a valid ModuleEventType
      moduleId: rule.moduleId,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { ruleId: rule.id, rule },
    });
  }
}

// Export singleton instance
export const globalAutomationManager = new GlobalAutomationManager(
  // We'll need to import the actual instance in the initialization file
  null as unknown as AutomationManager
);

import { ResourceType } from "./../../types/resources/ResourceTypes";
import { ModuleEvent, moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import { moduleManager } from './ModuleManager';

/**
 * Extended module status types beyond the basic 'active', 'constructing', 'inactive'
 */
export type ExtendedModuleStatus =
  // Basic statuses
  | 'active'
  | 'constructing'
  | 'inactive'
  // Performance statuses
  | 'optimized'
  | 'degraded'
  | 'overloaded'
  // Operational statuses
  | 'maintenance'
  | 'upgrading'
  | 'repairing'
  // Error statuses
  | 'error'
  | 'critical'
  | 'offline'
  // Special statuses
  | 'standby'
  | 'powersave'
  | 'boost';

/**
 * Status history entry
 */
export interface StatusHistoryEntry {
  status: ExtendedModuleStatus;
  timestamp: number;
  duration?: number;
  reason?: string;
}

/**
 * Module alert interface
 */
export interface ModuleAlert {
  level: 'info' | 'warning' | 'error' | 'critical';
  message: string;
  timestamp: number;
  acknowledged: boolean;
}

/**
 * Module status details
 */
export interface ModuleStatusDetails {
  currentStatus: ExtendedModuleStatus;
  previousStatus?: ExtendedModuleStatus;
  history: StatusHistoryEntry[];
  lastUpdated: number;
  metrics: {
    uptime: number;
    efficiency: number;
    reliability: number;
    performance: number;
  };
  alerts: ModuleAlert[];
}

/**
 * Module status manager
 * Manages the status tracking, history, and notifications for modules
 */
export class ModuleStatusManager {
  private moduleStatuses: Map<string, ModuleStatusDetails>;
  private statusUpdateInterval: number;
  private intervalId?: NodeJS.Timeout;

  constructor(statusUpdateInterval = 60000) {
    // Default to 1 minute updates
    this.moduleStatuses = new Map();
    this.statusUpdateInterval = statusUpdateInterval;

    // Subscribe to module events
    this.subscribeToEvents();

    // Start status update interval
    this.startStatusUpdates();
  }

  /**
   * Subscribe to module events
   */
  private subscribeToEvents(): void {
    // Module lifecycle events
    moduleEventBus.subscribe('MODULE_CREATED' as ModuleEventType, this.handleModuleCreated);
    moduleEventBus.subscribe('MODULE_ATTACHED' as ModuleEventType, this.handleModuleAttached);
    moduleEventBus.subscribe('MODULE_DETACHED' as ModuleEventType, this.handleModuleDetached);
    moduleEventBus.subscribe('MODULE_UPGRADED' as ModuleEventType, this.handleModuleUpgraded);
    moduleEventBus.subscribe('MODULE_ACTIVATED' as ModuleEventType, this.handleModuleActivated);
    moduleEventBus.subscribe('MODULE_DEACTIVATED' as ModuleEventType, this.handleModuleDeactivated);

    // Status events
    moduleEventBus.subscribe('STATUS_CHANGED' as ModuleEventType, this.handleStatusChanged);
    moduleEventBus.subscribe('ERROR_OCCURRED' as ModuleEventType, this.handleErrorOccurred);

    // Resource events that might affect status
    moduleEventBus.subscribe('RESOURCE_SHORTAGE' as ModuleEventType, this.handleResourceShortage);
  }

  /**
   * Start periodic status updates
   */
  private startStatusUpdates(): void {
    this.intervalId = setInterval(() => {
      this.updateAllModuleMetrics();
    }, this.statusUpdateInterval);
  }

  /**
   * Stop periodic status updates
   */
  public stopStatusUpdates(): void {
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = undefined;
    }
  }

  /**
   * Update metrics for all modules
   */
  private updateAllModuleMetrics(): void {
    const modules = Array.from(moduleManager.getActiveModules());

    for (const module of modules) {
      this.updateModuleMetrics(module.id);
    }
  }

  /**
   * Update metrics for a specific module
   */
  private updateModuleMetrics(moduleId: string): void {
    const statusDetails = this.moduleStatuses.get(moduleId);
    if (!statusDetails) {
      return;
    }

    const module = moduleManager.getModule(moduleId);
    if (!module) {
      return;
    }

    // Calculate uptime (time since first activation)
    const firstActivation = statusDetails.history.find(entry => entry.status === 'active');
    if (firstActivation) {
      const uptime = module.isActive
        ? Date.now() - firstActivation.timestamp
        : statusDetails.metrics.uptime;

      statusDetails.metrics.uptime = uptime;
    }

    // Calculate efficiency based on status
    let efficiency = 1.0;
    switch (statusDetails.currentStatus) {
      case 'optimized':
        efficiency = 1.2; // 20% boost
        break;
      case 'degraded':
        efficiency = 0.8; // 20% reduction
        break;
      case 'overloaded':
        efficiency = 0.6; // 40% reduction
        break;
      case 'maintenance':
      case 'repairing':
        efficiency = 0.5; // 50% reduction
        break;
      case 'powersave':
        efficiency = 0.7; // 30% reduction
        break;
      case 'boost':
        efficiency = 1.5; // 50% boost
        break;
      case 'error':
      case 'critical':
      case 'offline':
        efficiency = 0; // No efficiency
        break;
      default:
        efficiency = 1.0; // Normal efficiency
    }
    statusDetails.metrics.efficiency = efficiency;

    // Calculate reliability (percentage of time without errors)
    const errorEntries = statusDetails.history.filter(entry =>
      ['error', 'critical', 'offline'].includes(entry.status)
    );
    const totalTime = Date.now() - statusDetails.history[0].timestamp;
    let errorTime = 0;

    for (const entry of errorEntries) {
      errorTime += entry.duration || 0;
    }

    statusDetails.metrics.reliability = Math.max(0, Math.min(1, 1 - errorTime / totalTime));

    // Calculate performance based on level and status
    let performance = module.level / 10; // Base performance from level (0.1 to 1.0)

    // Adjust based on status
    switch (statusDetails.currentStatus) {
      case 'optimized':
        performance *= 1.2; // 20% boost
        break;
      case 'boost':
        performance *= 1.5; // 50% boost
        break;
      case 'degraded':
        performance *= 0.8; // 20% reduction
        break;
      case 'overloaded':
        performance *= 0.7; // 30% reduction
        break;
      default:
        // No adjustment
        break;
    }

    statusDetails.metrics.performance = Math.min(1, performance);

    // Update last updated timestamp
    statusDetails.lastUpdated = Date.now();

    // Emit metrics updated event
    moduleEventBus.emit({
      type: 'STATUS_CHANGED' as ModuleEventType,
      moduleId,
      moduleType: module.type,
      timestamp: Date.now(),
      data: {
        status: statusDetails.currentStatus,
        metrics: statusDetails.metrics,
      },
    });
  }

  /**
   * Initialize status tracking for a module
   */
  public initializeModuleStatus(moduleId: string): void {
    const module = moduleManager.getModule(moduleId);
    if (!module) {
      console.error(`[ModuleStatusManager] Module ${moduleId} not found`);
      return;
    }

    // Create initial status details
    const statusDetails: ModuleStatusDetails = {
      currentStatus: module.status as ExtendedModuleStatus,
      history: [
        {
          status: module.status as ExtendedModuleStatus,
          timestamp: Date.now(),
        },
      ],
      lastUpdated: Date.now(),
      metrics: {
        uptime: 0,
        efficiency: 1.0,
        reliability: 1.0,
        performance: module.level / 10,
      },
      alerts: [],
    };

    this.moduleStatuses.set(moduleId, statusDetails);
  }

  /**
   * Update the status of a module
   */
  public updateModuleStatus(
    moduleId: string,
    status: ExtendedModuleStatus,
    reason?: string
  ): boolean {
    const module = moduleManager.getModule(moduleId);
    if (!module) {
      console.error(`[ModuleStatusManager] Module ${moduleId} not found`);
      return false;
    }

    let statusDetails = this.moduleStatuses.get(moduleId);

    // Initialize status tracking if not already done
    if (!statusDetails) {
      this.initializeModuleStatus(moduleId);
      statusDetails = this.moduleStatuses.get(moduleId);
      if (!statusDetails) {
        return false;
      }
    }

    // Update previous status entry with duration
    const previousEntry = statusDetails.history[statusDetails.history.length - 1];
    if (previousEntry) {
      previousEntry.duration = Date.now() - previousEntry.timestamp;
    }

    // Add new status entry
    const newEntry: StatusHistoryEntry = {
      status,
      timestamp: Date.now(),
      reason,
    };

    statusDetails.history.push(newEntry);

    // Update current and previous status
    statusDetails.previousStatus = statusDetails.currentStatus;
    statusDetails.currentStatus = status;
    statusDetails.lastUpdated = Date.now();

    // Update module's basic status if it's one of the core statuses
    if (['active', 'constructing', 'inactive'].includes(status)) {
      module.status = status as 'active' | 'constructing' | 'inactive';

      // Update module's active state
      if (status === 'active') {
        module.isActive = true;
      } else if (status === 'inactive') {
        module.isActive = false;
      }
    }

    // Emit status changed event
    moduleEventBus.emit({
      type: 'STATUS_CHANGED' as ModuleEventType,
      moduleId,
      moduleType: module.type,
      timestamp: Date.now(),
      data: {
        status,
        previousStatus: statusDetails.previousStatus,
        reason,
      },
    });

    // Create alert for critical statuses
    if (['error', 'critical', 'offline'].includes(status)) {
      this.addAlert(
        moduleId,
        'error',
        `Module entered ${status} state${reason ? ': ' + reason : ''}`
      );
    } else if (['degraded', 'overloaded'].includes(status)) {
      this.addAlert(
        moduleId,
        'warning',
        `Module performance degraded${reason ? ': ' + reason : ''}`
      );
    } else if (['optimized', 'boost'].includes(status)) {
      this.addAlert(moduleId, 'info', `Module performance enhanced${reason ? ': ' + reason : ''}`);
    }

    return true;
  }

  /**
   * Add an alert for a module
   */
  public addAlert(
    moduleId: string,
    level: 'info' | 'warning' | 'error' | 'critical',
    message: string
  ): void {
    const statusDetails = this.moduleStatuses.get(moduleId);
    if (!statusDetails) {
      return;
    }

    const alert = {
      level,
      message,
      timestamp: Date.now(),
      acknowledged: false,
    };

    statusDetails.alerts.push(alert);

    // Emit alert event
    const module = moduleManager.getModule(moduleId);
    if (module) {
      moduleEventBus.emit({
        type: 'ERROR_OCCURRED' as ModuleEventType,
        moduleId,
        moduleType: module.type,
        timestamp: Date.now(),
        data: { alert },
      });
    }
  }

  /**
   * Acknowledge an alert
   */
  public acknowledgeAlert(moduleId: string, alertIndex: number): boolean {
    const statusDetails = this.moduleStatuses.get(moduleId);
    if (!statusDetails || alertIndex >= statusDetails.alerts.length) {
      return false;
    }

    statusDetails.alerts[alertIndex].acknowledged = true;
    return true;
  }

  /**
   * Get status details for a module
   */
  public getModuleStatusDetails(moduleId: string): ModuleStatusDetails | undefined {
    return this.moduleStatuses.get(moduleId);
  }

  /**
   * Get current status for a module
   */
  public getModuleStatus(moduleId: string): ExtendedModuleStatus | undefined {
    return this.moduleStatuses.get(moduleId)?.currentStatus;
  }

  /**
   * Get status history for a module
   */
  public getModuleStatusHistory(moduleId: string): StatusHistoryEntry[] {
    return this.moduleStatuses.get(moduleId)?.history || [];
  }

  /**
   * Get alerts for a module
   */
  public getModuleAlerts(moduleId: string, onlyUnacknowledged = false): ModuleAlert[] {
    const alerts = this.moduleStatuses.get(moduleId)?.alerts || [];
    return onlyUnacknowledged ? alerts.filter(alert => !alert.acknowledged) : alerts;
  }

  /**
   * Get modules with a specific status
   */
  public getModulesByStatus(status: ExtendedModuleStatus): string[] {
    const moduleIds: string[] = [];

    this.moduleStatuses.forEach((details, moduleId) => {
      if (details.currentStatus === status) {
        moduleIds.push(moduleId);
      }
    });

    return moduleIds;
  }

  /**
   * Get modules with alerts
   */
  public getModulesWithAlerts(level?: 'info' | 'warning' | 'error' | 'critical'): string[] {
    const moduleIds: string[] = [];

    this.moduleStatuses.forEach((details, moduleId) => {
      const hasAlerts = level
        ? details.alerts.some(alert => alert.level === level && !alert.acknowledged)
        : details.alerts.some(alert => !alert.acknowledged);

      if (hasAlerts) {
        moduleIds.push(moduleId);
      }
    });

    return moduleIds;
  }

  /**
   * Handle module created event
   */
  private handleModuleCreated = (event: ModuleEvent): void => {
    this.initializeModuleStatus(event.moduleId);
  };

  /**
   * Handle module attached event
   */
  private handleModuleAttached = (event: ModuleEvent): void => {
    const { moduleId } = event;
    this.updateModuleStatus(moduleId, 'inactive', 'Module attached');
  };

  /**
   * Handle module detached event
   */
  private handleModuleDetached = (event: ModuleEvent): void => {
    const { moduleId } = event;
    // We don't remove the status history, just mark it as detached
    this.updateModuleStatus(moduleId, 'inactive', 'Module detached');
  };

  /**
   * Handle module upgraded event
   */
  private handleModuleUpgraded = (event: ModuleEvent): void => {
    const newLevel = event.data?.newLevel as number | undefined;
    this.updateModuleStatus(event.moduleId, 'upgrading', `Upgrading to level ${newLevel}`);

    // After a short delay, return to active status
    setTimeout(() => {
      this.updateModuleStatus(event.moduleId, 'active', 'Upgrade completed');
    }, 5000);
  };

  /**
   * Handle module activated event
   */
  private handleModuleActivated = (event: ModuleEvent): void => {
    const { moduleId } = event;
    this.updateModuleStatus(moduleId, 'active', 'Module activated');
  };

  /**
   * Handle module deactivated event
   */
  private handleModuleDeactivated = (event: ModuleEvent): void => {
    const { moduleId } = event;
    this.updateModuleStatus(moduleId, 'inactive', 'Module deactivated');
  };

  /**
   * Handle status changed event
   */
  private handleStatusChanged = (event: ModuleEvent): void => {
    // Only handle events from other sources to avoid loops
    if (event.data && event.data.source !== 'ModuleStatusManager') {
      const { moduleId } = event;
      const status = event.data?.status as ExtendedModuleStatus | undefined;
      const reason = event.data?.reason as string | undefined;

      if (status && status !== this.getModuleStatus(moduleId)) {
        this.updateModuleStatus(moduleId, status, reason);
      }
    }
  };

  /**
   * Handle error occurred event
   */
  private handleErrorOccurred = (event: ModuleEvent): void => {
    const { moduleId } = event;
    const level = event.data?.level as 'info' | 'warning' | 'error' | 'critical' | undefined;
    const message = event.data?.message as string | undefined;

    // Add alert
    if (message) {
      this.addAlert(moduleId, level || 'error', message);
    }

    // Update status for serious errors
    if (level === 'critical') {
      this.updateModuleStatus(moduleId, 'critical', message || 'Critical error occurred');
    } else if (level === 'error') {
      this.updateModuleStatus(moduleId, 'error', message || 'Error occurred');
    }
  };

  /**
   * Handle resource shortage event
   */
  private handleResourceShortage = (event: ModuleEvent): void => {
    const { moduleId } = event;
    const resourceType = event.data?.resourceType as string | undefined;
    const amount = event.data?.amount as number | undefined;
    const required = event.data?.required as number | undefined;

    // Add alert
    this.addAlert(
      moduleId,
      'warning',
      `Resource shortage: ${resourceType} (${amount}/${required})`
    );

    // Update status if module is active
    const currentStatus = this.getModuleStatus(moduleId);
    if (currentStatus === 'active') {
      this.updateModuleStatus(moduleId, 'degraded', `Resource shortage: ${resourceType}`);
    }
  };

  /**
   * Clean up resources
   */
  public cleanup(): void {
    // Stop interval
    this.stopStatusUpdates();

    // Unsubscribe from events
    const unsubscribeCreated = moduleEventBus.subscribe(
      'MODULE_CREATED' as ModuleEventType,
      this.handleModuleCreated
    );
    const unsubscribeAttached = moduleEventBus.subscribe(
      'MODULE_ATTACHED' as ModuleEventType,
      this.handleModuleAttached
    );
    const unsubscribeDetached = moduleEventBus.subscribe(
      'MODULE_DETACHED' as ModuleEventType,
      this.handleModuleDetached
    );
    const unsubscribeUpgraded = moduleEventBus.subscribe(
      'MODULE_UPGRADED' as ModuleEventType,
      this.handleModuleUpgraded
    );
    const unsubscribeActivated = moduleEventBus.subscribe(
      'MODULE_ACTIVATED' as ModuleEventType,
      this.handleModuleActivated
    );
    const unsubscribeDeactivated = moduleEventBus.subscribe(
      'MODULE_DEACTIVATED' as ModuleEventType,
      this.handleModuleDeactivated
    );
    const unsubscribeStatusChanged = moduleEventBus.subscribe(
      'STATUS_CHANGED' as ModuleEventType,
      this.handleStatusChanged
    );
    const unsubscribeErrorOccurred = moduleEventBus.subscribe(
      'ERROR_OCCURRED' as ModuleEventType,
      this.handleErrorOccurred
    );
    const unsubscribeResourceShortage = moduleEventBus.subscribe(
      'RESOURCE_SHORTAGE' as ModuleEventType,
      this.handleResourceShortage
    );

    if (typeof unsubscribeCreated === 'function') {
      unsubscribeCreated();
    }
    if (typeof unsubscribeAttached === 'function') {
      unsubscribeAttached();
    }
    if (typeof unsubscribeDetached === 'function') {
      unsubscribeDetached();
    }
    if (typeof unsubscribeUpgraded === 'function') {
      unsubscribeUpgraded();
    }
    if (typeof unsubscribeActivated === 'function') {
      unsubscribeActivated();
    }
    if (typeof unsubscribeDeactivated === 'function') {
      unsubscribeDeactivated();
    }
    if (typeof unsubscribeStatusChanged === 'function') {
      unsubscribeStatusChanged();
    }
    if (typeof unsubscribeErrorOccurred === 'function') {
      unsubscribeErrorOccurred();
    }
    if (typeof unsubscribeResourceShortage === 'function') {
      unsubscribeResourceShortage();
    }

    // Clear data
    this.moduleStatuses.clear();
  }
}

// Export singleton instance
export const moduleStatusManager = new ModuleStatusManager();

/**
 * MultitabPerformanceTestPage
 *
 * A page component that combines the MultitabPerformanceLauncher and MultitabPerformanceResults
 * components to provide a complete multi-tab performance testing solution.
 */

import * as React from "react";
import { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import MultitabPerformanceLauncher from '../../components/performance/MultitabPerformanceLauncher';
import MultitabPerformanceResults from '../../components/performance/MultitabPerformanceResults';
import { MultitabPerformanceResult } from '../../tests/performance/MultitabPerformanceTestSuite';

type ResultSet = MultitabPerformanceResult[] | Record<string, MultitabPerformanceResult[]>;

/**
 * MultitabPerformanceTestPage component
 */
const MultitabPerformanceTestPage: React.FC = () => {
  const [searchParams] = useSearchParams();
  const [results, setResults] = useState<ResultSet | null>(null);
  const [isCoordinator, setIsCoordinator] = useState(true);
  const [report, setReport] = useState<string>('');

  // Check URL parameters to determine if this is a worker tab
  useEffect(() => {
    const isWorker = searchParams.get('worker') === 'true';
    setIsCoordinator(!isWorker);
  }, [searchParams]);

  /**
   * Handle test results from the performance launcher
   */
  const handleTestResults = (newResults: ResultSet) => {
    setResults(newResults);

    // Store results in localStorage for persistence
    try {
      localStorage.setItem('multitab_performance_results', JSON.stringify(newResults));
    } catch (e) {
      console.warn('Failed to store test results in localStorage:', e);
    }
  };

  /**
   * Handle generated reports
   */
  const handleReportGenerated = (generatedReport: string) => {
    setReport(generatedReport);
  };

  // Try to load previous results from localStorage
  useEffect(() => {
    try {
      const savedResults = localStorage.getItem('multitab_performance_results');
      if (savedResults) {
        setResults(JSON.parse(savedResults));
      }
    } catch (e) {
      console.warn('Failed to load previous test results:', e);
    }
  }, []);

  return (
    <div className="multitab-performance-test-page">
      <header>
        <h1>Multi-Tab Performance Testing</h1>
        <p className="subtitle">
          {isCoordinator
            ? 'Test application performance with multiple tabs open'
            : 'Worker tab - leave this open and return to the coordinator tab'}
        </p>
      </header>

      <div className="page-content">
        <section className="launcher-section">
          <MultitabPerformanceLauncher
            isCoordinator={isCoordinator}
            onTestResults={handleTestResults}
          />
        </section>

        {isCoordinator && results && (
          <section className="results-section">
            <MultitabPerformanceResults
              results={results}
              onReportGenerated={handleReportGenerated}
            />
          </section>
        )}

        {isCoordinator && (
          <section className="info-section">
            <h2>About Multi-Tab Performance Testing</h2>
            <p>
              Multi-tab performance testing evaluates how your application behaves when users have
              multiple instances open simultaneously in different browser tabs. This is crucial for
              modern web applications where users often work with multiple tabs.
            </p>

            <h3>Why Test Multi-Tab Performance?</h3>
            <ul>
              <li>
                <strong>Resource Contention:</strong> Multiple tabs can compete for limited browser
                resources, causing performance degradation.
              </li>
              <li>
                <strong>Shared Storage:</strong> Tabs may access the same localStorage, IndexedDB,
                or other shared storage, leading to potential conflicts.
              </li>
              <li>
                <strong>Memory Usage:</strong> Total memory consumption can grow dramatically with
                multiple tabs, potentially causing browser slowdowns or crashes.
              </li>
              <li>
                <strong>Background Processing:</strong> Tabs in the background may continue
                consuming resources, affecting the performance of the active tab.
              </li>
            </ul>

