import { eventSystem } from '../../lib/events/UnifiedEventSystem';
import { EventType } from '../../types/events/EventTypes';
import {
  ResourceState,
  ResourceTransfer,
  ResourceType as StringResourceType,
} from '../../types/resources/ResourceTypes';
import { ResourceType } from "./../../types/resources/ResourceTypes";
import {
  isStringResourceType,
  toStringResourceType,
} from '../../utils/resources/ResourceTypeConverter';
import { validateResourceTransfer } from '../../utils/resources/resourceValidation';
import { ResourceFlowWorkerUtil } from '../../utils/workers/ResourceFlowWorkerUtil';
import { ResourceSystem, ResourceSystemConfig } from '../ResourceSystem';

/**
 * Flow node types
 */
export type FlowNodeType = 'producer' | 'consumer' | 'storage' | 'converter';

/**
 * Flow priority configuration
 */
export interface FlowPriority {
  type: StringResourceType;
  priority: number;
  consumers: string[];
}

/**
 * Flow node
 */
export interface FlowNode {
  id: string;
  type: FlowNodeType;
  resources: StringResourceType[];
  priority: FlowPriority;
  active: boolean;
  efficiency?: number;
  converterConfig?: Record<string, unknown>; // Configuration for converters
  converterStatus?: Record<string, unknown>; // Status information for converters
  config?: Record<string, unknown>; // Generic config for additional properties
}

/**
 * Flow connection
 */
export interface FlowConnection {
  id: string;
  source: string;
  target: string;
  resourceType: StringResourceType;
  maxRate: number;
  currentRate: number;
  priority: FlowPriority;
  active: boolean;
}

/**
 * Resource flow
 */
export interface ResourceFlow {
  source: string;
  target: string;
  resourceType: StringResourceType;
  maxRate: number;
}

/**
 * Flow optimization result
 */
export interface FlowOptimizationResult {
  transfers: ResourceTransfer[];
  updatedConnections: FlowConnection[];
  bottlenecks: string[];
  underutilized: string[];
  performanceMetrics?: {
    executionTimeMs: number;
    nodesProcessed: number;
    connectionsProcessed: number;
    transfersGenerated: number;
  };
}

/**
 * ResourceFlowSubsystem
 *
 * Manages resource flow through the game systems
 * Responsible for:
 * - Tracking resource nodes (producers, consumers, storage, converters)
 * - Managing connections between nodes
 * - Optimizing resource distribution
 * - Processing resource conversions
 */
export class ResourceFlowSubsystem {
  // Flow network data structures
  private nodes: Map<string, FlowNode> = new Map();
  private connections: Map<string, FlowConnection> = new Map();
  private sourceConnections: Map<string, string[]> = new Map();
  private targetConnections: Map<string, string[]> = new Map();

  // Type-specific node registries
  private producerNodes: Map<string, FlowNode> = new Map();
  private consumerNodes: Map<string, FlowNode> = new Map();
  private storageNodes: Map<string, FlowNode> = new Map();
  private converterNodes: Map<string, FlowNode> = new Map();

  // Resource tracking
  private resourceStates: Map<StringResourceType, ResourceState> = new Map();
  private resourceProducers: Map<StringResourceType, string[]> = new Map();
  private resourceConsumers: Map<StringResourceType, string[]> = new Map();
  private resourceStorage: Map<StringResourceType, string[]> = new Map();

  // Processing state
  private transferHistory: ResourceTransfer[] = [];
  private lastOptimizationTime = 0;
  private isOptimizing = false;
  private lastOptimizationResult: FlowOptimizationResult | null = null;

  // Worker utility
  private workerUtil: ResourceFlowWorkerUtil | null = null;

  // Parent system reference
  private parentSystem: ResourceSystem;
  private config: ResourceSystemConfig;
  private isInitialized = false;

  constructor(parentSystem: ResourceSystem, config: ResourceSystemConfig) {
    this.parentSystem = parentSystem;
    this.config = config;

    // Initialize resource states
    this.initializeResourceStates();

    // Initialize Web Worker utility if enabled
    if (this.config.useWorkerOffloading) {
      try {
        this.workerUtil = new ResourceFlowWorkerUtil();
      } catch (error) {
        console.error('Failed to initialize ResourceFlowWorkerUtil:', error);
      }
    }
  }

  /**
   * Initialize the subsystem
   */
  public async initialize(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      // Subscribe to relevant events
      this.initializeEventSubscriptions();

      this.isInitialized = true;
    } catch (error) {
      console.error('Failed to initialize ResourceFlowSubsystem:', error);
      throw error;
    }
  }

  /**
   * Dispose of the subsystem
   */
  public async dispose(): Promise<void> {
    if (!this.isInitialized) {
      return;
    }

    try {
      // Clean up Web Worker
      if (this.workerUtil) {
        this.workerUtil.terminate();
        this.workerUtil = null;
      }

      // Clear data structures
      this.nodes.clear();
      this.connections.clear();
      this.sourceConnections.clear();
      this.targetConnections.clear();

      this.producerNodes.clear();
      this.consumerNodes.clear();
      this.storageNodes.clear();
      this.converterNodes.clear();

      this.resourceStates.clear();
      this.resourceProducers.clear();
      this.resourceConsumers.clear();
      this.resourceStorage.clear();

      this.transferHistory = [];

      this.isInitialized = false;
    } catch (error) {
      console.error('Failed to dispose ResourceFlowSubsystem:', error);
      throw error;
    }
  }

  /**
   * Initialize event subscriptions
   */
  private initializeEventSubscriptions(): void {
    // Subscribe to relevant events
    eventSystem.subscribe('MODULE_CREATED', this.handleModuleCreated);
    eventSystem.subscribe('MODULE_UPDATED', this.handleModuleUpdated);
    eventSystem.subscribe('MODULE_DESTROYED', this.handleModuleDestroyed);
    eventSystem.subscribe('MODULE_ENABLED', this.handleModuleStateChanged);
    eventSystem.subscribe('MODULE_DISABLED', this.handleModuleStateChanged);
  }

  /**
   * Initialize with default states for all resource types
   */
  private initializeResourceStates(): void {
    const defaultResourceTypes: StringResourceType[] = [
      ResourceType.ENERGY,
      ResourceType.MINERALS,
      ResourceType.POPULATION,
      ResourceType.RESEARCH,
      ResourceType.PLASMA,
      ResourceType.GAS,
      ResourceType.EXOTIC,
    ];

    // Initialize resource states for all resource types
    for (const type of defaultResourceTypes) {
      const resourceState: ResourceState = {
        current: 0,
        max: 1000,
        min: 0,
        production: 0,
        consumption: 0,
      };

      this.resourceStates.set(type, resourceState);
      this.resourceProducers.set(type, []);
      this.resourceConsumers.set(type, []);
      this.resourceStorage.set(type, []);
    }
  }

  /**
   * Registers a node in the resource flow network
   */
  public registerNode(node: FlowNode): boolean {
    if (!node.id || !node.resources || node.resources.length === 0) {
      console.warn('Invalid flow node:', node);
      return false;
    }

    // Add to main nodes map
    this.nodes.set(node.id, node);

    // Add to type-specific map
    switch (node.type) {
      case 'producer':
        this.producerNodes.set(node.id, node);
        break;
      case 'consumer':
        this.consumerNodes.set(node.id, node);
        break;
      case 'storage':
        this.storageNodes.set(node.id, node);
        break;
      case 'converter':
        this.converterNodes.set(node.id, node);
        break;
    }

    // Add to resource-specific tracking
    for (const resourceType of node.resources) {
      // Invalidate parent system cache
      this.invalidateCache(resourceType);

      // Add to the appropriate resource tracking maps
      switch (node.type) {
        case 'producer':
          this.addToArray(this.resourceProducers, resourceType, node.id);
          break;
        case 'consumer':
          this.addToArray(this.resourceConsumers, resourceType, node.id);
          break;
        case 'storage':
          this.addToArray(this.resourceStorage, resourceType, node.id);
          break;
      }
    }

    return true;
  }

  /**
   * Unregisters a node from the resource flow network
   */
  public unregisterNode(id: string): boolean {
    if (!this.nodes.has(id)) {
      return false;
    }

    // Get node before removing it
    const node = this.nodes.get(id);
    if (!node) return false;

    // Remove from type-specific map
    switch (node.type) {
      case 'producer':
        this.producerNodes.delete(id);
        break;
      case 'consumer':
        this.consumerNodes.delete(id);
        break;
      case 'storage':
        this.storageNodes.delete(id);
        break;
      case 'converter':
        this.converterNodes.delete(id);
        break;
    }

    // Remove from resource-specific tracking
    for (const resourceType of node.resources) {
      // Invalidate parent system cache
      this.invalidateCache(resourceType);

      // Remove from the appropriate resource tracking maps
      switch (node.type) {
        case 'producer':
          this.removeFromArray(this.resourceProducers, resourceType, id);
          break;
        case 'consumer':
          this.removeFromArray(this.resourceConsumers, resourceType, id);
          break;
        case 'storage':
          this.removeFromArray(this.resourceStorage, resourceType, id);
          break;
      }
    }

    // Remove all connections to/from this node
    const connectionEntries = Array.from(this.connections.entries());
    for (const [connectionId, connection] of connectionEntries) {
      if (connection.source === id || connection.target === id) {
        this.unregisterConnection(connectionId);
      }
    }

    // Remove from main nodes map
    this.nodes.delete(id);

    return true;
  }

  /**
   * Registers a connection between nodes in the resource flow network
   */
  public registerConnection(connection: FlowConnection): boolean {
    if (
      !connection.id ||
      !connection.source ||
      !connection.target ||
      !connection.resourceType ||
      connection.maxRate <= 0
    ) {
      console.warn('Invalid connection:', connection);
      return false;
    }

    // Ensure source and target nodes exist
    if (!this.nodes.has(connection.source)) {
      console.warn(`Source node ${connection.source} does not exist`);
      return false;
    }

    if (!this.nodes.has(connection.target)) {
      console.warn(`Target node ${connection.target} does not exist`);
      return false;
    }

    // Ensure source node has the resource type
    const sourceNode = this.nodes.get(connection.source);
    if (!sourceNode?.resources.includes(connection.resourceType)) {
      console.warn(
        `Source node ${connection.source} does not have resource type ${connection.resourceType}`
      );
      return false;
    }

    // Add to connections map
    this.connections.set(connection.id, connection);

    // Update source and target connection maps
    this.addToArray(this.sourceConnections, connection.source, connection.id);
    this.addToArray(this.targetConnections, connection.target, connection.id);

    // Invalidate cache for affected resource
    this.invalidateCache(connection.resourceType);

    return true;
  }

  /**
   * Unregisters a connection from the resource flow network
   */
  public unregisterConnection(id: string): boolean {
    const connection = this.connections.get(id);
    if (!connection) {
      return false;
    }

    // Remove from source and target connection maps
    this.removeFromArray(this.sourceConnections, connection.source, id);
    this.removeFromArray(this.targetConnections, connection.target, id);

    // Remove from connections map
    this.connections.delete(id);

    // Invalidate cache for affected resource
    this.invalidateCache(connection.resourceType);

    return true;
  }

  /**
   * Registers a resource flow between nodes
   */
  public registerResourceFlow(
    sourceId: string,
    targetId: string,
    resourceType: StringResourceType | ResourceType,
    rate: number
  ): boolean {
    // Ensure we're using string resource type for internal storage
    const stringType = isStringResourceType(resourceType)
      ? (resourceType as StringResourceType)
      : toStringResourceType(resourceType as ResourceType);

    // Check if source and target nodes exist
    const source = this.nodes.get(sourceId);
    const target = this.nodes.get(targetId);

    if (!source || !target) {
      console.error(`Cannot register flow: source or target node not found`);
      return false;
    }

    // Check if source produces this resource
    if (!source.resources.includes(stringType)) {
      console.error(`Source node ${sourceId} does not produce ${stringType}`);
      return false;
    }

    // Check if target accepts this resource
    if (!target.resources.includes(stringType)) {
      console.error(`Target node ${targetId} does not accept ${stringType}`);
      return false;
    }

    // Create a unique ID for the connection
    const connectionId = `${sourceId}-${targetId}-${stringType}`;

    // Create the connection
    const connection: FlowConnection = {
      id: connectionId,
      source: sourceId,
      target: targetId,
      resourceType: ResourceTypeType,
      maxRate: rate,
      currentRate: 0,
      priority: {
        type: stringType,
        priority: 1,
        consumers: [targetId],
      },
      active: true,
    };

    // Register the connection
    return this.registerConnection(connection);
  }

  /**
   * Updates the state of a resource in the network
   */
  public updateResourceState(type: StringResourceType | ResourceType, state: ResourceState): void {
    // Ensure we're using string resource type for internal storage
    const stringType = isStringResourceType(type)
      ? (type as StringResourceType)
      : toStringResourceType(type as ResourceType);

    this.resourceStates.set(stringType, state);
    this.invalidateCache(stringType);
  }

  /**
   * Gets the current state of a resource in the network
   */
  public getResourceState(type: StringResourceType | ResourceType): ResourceState | undefined {
    // Ensure we're using string resource type for internal storage
    const stringType = isStringResourceType(type)
      ? (type as StringResourceType)
      : toStringResourceType(type as ResourceType);

    return this.resourceStates.get(stringType);
  }

  /**
   * Invalidate cache for a resource type
   */
  private invalidateCache(type: StringResourceType): void {
    // Publish event for resource state change
    eventSystem.publish({
      type: EventType.RESOURCE_UPDATED,
      timestamp: Date.now(),
      data: {
        resourceType: type,
      },
    });
  }

  /**
   * Optimize resource flows across the network
   */
  public async optimizeFlows(): Promise<FlowOptimizationResult> {
    // Prevent concurrent optimization runs
    if (this.isOptimizing) {
      return (
        this.lastOptimizationResult || {
          transfers: [],
          updatedConnections: [],
          bottlenecks: [],
          underutilized: [],
          performanceMetrics: {
            executionTimeMs: 0,
            nodesProcessed: 0,
            connectionsProcessed: 0,
            transfersGenerated: 0,
          },
        }
      );
    }

    this.isOptimizing = true;
    const startTime = Date.now();

    try {
      // Get active nodes and connections
      const activeNodes = Array.from(this.nodes.values()).filter(node => node.active);
      const activeConnections = Array.from(this.connections.values()).filter(conn => conn.active);

      // Check if we should use Web Worker offloading
      if (
        this.config.useWorkerOffloading &&
        this.workerUtil &&
        activeNodes.length > this.config.batchSize
      ) {
        try {
          // Offload optimization to Web Worker
          const result = await this.workerUtil.optimizeFlows(
            activeNodes,
            activeConnections,
            Object.fromEntries(this.resourceStates)
          );

          // Apply the results from the worker
          this.applyOptimizationResults(result);

          // Add execution time to performance metrics
          result.performanceMetrics = result.performanceMetrics || {
            executionTimeMs: 0,
            nodesProcessed: activeNodes.length,
            connectionsProcessed: activeConnections.length,
            transfersGenerated: result.transfers.length,
          };

          result.performanceMetrics.executionTimeMs = Date.now() - startTime;

          this.lastOptimizationResult = result;
          this.lastOptimizationTime = Date.now();
          return result;
        } catch (error) {
          console.warn('Web Worker optimization failed, falling back to main thread:', error);
          // Fall back to main thread optimization
        }
      }

      // Categorize nodes by type
      const producers = activeNodes.filter(node => node.type === 'producer');
      const consumers = activeNodes.filter(node => node.type === 'consumer');
      const storages = activeNodes.filter(node => node.type === 'storage');
      const converters = activeNodes.filter(node => node.type === 'converter');

      // Process converters
      this.processConverters(converters, activeConnections);

      // Calculate resource balance
      const { availability, demand } = this.calculateResourceBalance(
        producers,
        consumers,
        storages,
        activeConnections
      );

      // Identify resource issues
      const { bottlenecks, underutilized } = this.identifyResourceIssues(availability, demand);

      // Optimize flow rates
      const { updatedConnections, transfers } = this.optimizeFlowRates(
        activeConnections,
        availability,
        demand
      );

      // Update connections with optimized rates
      for (const connection of updatedConnections) {
        this.connections.set(connection.id, connection);
      }

      // Create and store result
      const result: FlowOptimizationResult = {
        transfers,
        updatedConnections,
        bottlenecks,
        underutilized,
        performanceMetrics: {
          executionTimeMs: Date.now() - startTime,
          nodesProcessed: activeNodes.length,
          connectionsProcessed: activeConnections.length,
          transfersGenerated: transfers.length,
        },
      };

      this.lastOptimizationResult = result;
      this.lastOptimizationTime = Date.now();
      return result;
    } finally {
      this.isOptimizing = false;
    }
  }

  /**
   * Apply optimization results
   */
  private applyOptimizationResults(result: FlowOptimizationResult): void {
    // Update connections with optimized rates
    for (const connection of result.updatedConnections) {
      if (this.connections.has(connection.id)) {
        this.connections.set(connection.id, connection);
      }
    }

    // Process transfers
    for (const transfer of result.transfers) {
      if (validateResourceTransfer(transfer)) {
        this.addToTransferHistory(transfer);
      }
    }
  }

  /**
   * Process converters
   */
  private processConverters(converters: FlowNode[], activeConnections: FlowConnection[]): void {
    // Process each converter
    for (const converter of converters) {
      if (converter.config?.type === 'advanced') {
        this.processAdvancedConverter(converter, activeConnections);
      } else {
        // Basic converter processing
      }
    }
  }

  /**
   * Process an advanced converter
   */
  private processAdvancedConverter(converter: FlowNode, activeConnections: FlowConnection[]): void {
    // This would implement advanced converter logic
    // For now, we'll leave it as a placeholder
  }

  /**
   * Calculate resource balance between producers, consumers, and storage
   */
  private calculateResourceBalance(
    producers: FlowNode[],
    consumers: FlowNode[],
    storages: FlowNode[],
    activeConnections: FlowConnection[]
  ): {
    availability: Partial<Record<StringResourceType, number>>;
    demand: Partial<Record<StringResourceType, number>>;
  } {
    const availability: Partial<Record<StringResourceType, number>> = {};
    const demand: Partial<Record<StringResourceType, number>> = {};

    // Initialize with zero values for all resource types
    for (const type of this.resourceStates.keys()) {
      availability[type] = 0;
      demand[type] = 0;
    }

    // Calculate production capacity
    for (const producer of producers) {
      for (const resourceType of producer.resources) {
        // Find outgoing connections for this resource
        const outgoingConnections = activeConnections.filter(
          conn => conn.source === producer.id && conn.resourceType === resourceType
        );

        // Sum up max rates
        const totalMaxRate = outgoingConnections.reduce((sum, conn) => sum + conn.maxRate, 0);

        // Apply efficiency if available
        const effectiveRate = producer.efficiency
          ? totalMaxRate * producer.efficiency
          : totalMaxRate;

        availability[resourceType] = (availability[resourceType] || 0) + effectiveRate;
      }
    }

    // Calculate consumer demand
    for (const consumer of consumers) {
      for (const resourceType of consumer.resources) {
        // Find incoming connections for this resource
        const incomingConnections = activeConnections.filter(
          conn => conn.target === consumer.id && conn.resourceType === resourceType
        );

        // Sum up max rates
        const totalMaxRate = incomingConnections.reduce((sum, conn) => sum + conn.maxRate, 0);

        demand[resourceType] = (demand[resourceType] || 0) + totalMaxRate;
      }
    }

    // Factor in storage capacity
    for (const storage of storages) {
      for (const resourceType of storage.resources) {
        // Use resource state if available
        const resourceState = this.resourceStates.get(resourceType);
        if (!resourceState) {
          continue;
        }

        // If storage is near capacity, reduce availability
        if (resourceState.current > resourceState.max * 0.9) {
          availability[resourceType] = Math.max(
            0,
            (availability[resourceType] || 0) - (resourceState.max - resourceState.current)
          );
        }

        // If storage is near empty, increase demand
        if (resourceState.current < resourceState.max * 0.1) {
          demand[resourceType] = (demand[resourceType] || 0) + resourceState.max * 0.2;
        }
      }
    }

    return { availability, demand };
  }

  /**
   * Identify resource bottlenecks and underutilized resources
   */
  private identifyResourceIssues(
    availability: Partial<Record<StringResourceType, number>>,
    demand: Partial<Record<StringResourceType, number>>
  ): {
    bottlenecks: string[];
    underutilized: string[];
  } {
    const bottlenecks: string[] = [];
    const underutilized: string[] = [];

    for (const [type, availableAmount] of Object.entries(availability)) {
      const demandAmount = demand[type as StringResourceType] || 0;

      if (availableAmount < demandAmount * 0.9) {
        bottlenecks.push(type);
      } else if (availableAmount > demandAmount * 1.5) {
        underutilized.push(type);
      }
    }

    return { bottlenecks, underutilized };
  }

  /**
   * Optimize flow rates based on priorities
   */
  private optimizeFlowRates(
    activeConnections: FlowConnection[],
    availability: Partial<Record<StringResourceType, number>>,
    demand: Partial<Record<StringResourceType, number>>
  ): {
    updatedConnections: FlowConnection[];
    transfers: ResourceTransfer[];
  } {
    const updatedConnections: FlowConnection[] = [];
    const transfers: ResourceTransfer[] = [];
    const now = Date.now();

    // Sort connections by priority (high to low)
    const prioritizedConnections = [...activeConnections].sort(
      (a, b) => b.priority.priority - a.priority.priority
    );

    // Adjust flow rates
    for (const connection of prioritizedConnections) {
      const { resourceType } = connection;
      const availableForType = availability[resourceType] || 0;
      const demandForType = demand[resourceType] || 0;

      if (availableForType <= 0 || demandForType <= 0) {
        // No flow possible
        connection.currentRate = 0;
      } else if (availableForType >= demandForType) {
        // Full flow possible
        connection.currentRate = Math.min(connection.maxRate, demandForType);
      } else {
        // Partial flow based on ratio
        const ratio = availableForType / demandForType;
        connection.currentRate = connection.maxRate * ratio;
      }

      updatedConnections.push({ ...connection });

      // Generate transfer if flow is positive
      if (connection.currentRate > 0) {
        const transfer: ResourceTransfer = {
          type: connection.resourceType,
          source: connection.source,
          target: connection.target,
          amount: connection.currentRate,
          timestamp: now,
        };

        if (validateResourceTransfer(transfer)) {
          transfers.push(transfer);
          this.addToTransferHistory(transfer);
        }
      }
    }

    return { updatedConnections, transfers };
  }

  /**
   * Add a transfer to the history
   */
  private addToTransferHistory(transfer: ResourceTransfer): void {
    this.transferHistory.push(transfer);

    // Trim history if needed
    if (this.transferHistory.length > this.config.maxHistorySize) {
      this.transferHistory = this.transferHistory.slice(-this.config.maxHistorySize);
    }
  }

  /**
   * Get the transfer history
   */
  public getTransferHistory(): ResourceTransfer[] {
    return [...this.transferHistory];
  }

  /**
   * Get a specific node
   */
  public getNode(id: string): FlowNode | undefined {
    return this.nodes.get(id);
  }

  /**
   * Get all nodes
   */
  public getNodes(): FlowNode[] {
    return Array.from(this.nodes.values());
  }

  /**
   * Get a specific connection
   */
  public getConnection(id: string): FlowConnection | undefined {
    return this.connections.get(id);
  }

  /**
   * Get all connections
   */
  public getConnections(): FlowConnection[] {
    return Array.from(this.connections.values());
  }

  /**
   * Add an item to an array in a map
   */
  private addToArray<K, V>(map: Map<K, V[]>, key: K, value: V): void {
    const array = map.get(key) || [];
    if (!array.includes(value)) {
      array.push(value);
      map.set(key, array);
    }
  }

  /**
   * Remove an item from an array in a map
   */
  private removeFromArray<K, V>(map: Map<K, V[]>, key: K, value: V): void {
    const array = map.get(key);
    if (array) {
      const index = array.indexOf(value);
      if (index >= 0) {
        array.splice(index, 1);
        map.set(key, array);
      }
    }
  }

  // Module event handlers
  private handleModuleCreated = (event: unknown): void => {
    const { moduleId, moduleType, resources } = event;

    // Determine node type based on module type
    let nodeType: FlowNodeType = 'consumer';
    if (moduleType === 'producer' || moduleType === 'mining') {
      nodeType = 'producer';
    } else if (moduleType === 'storage') {
      nodeType = 'storage';
    } else if (moduleType === 'converter') {
      nodeType = 'converter';
    }

    // Create and register node
    const node: FlowNode = {
      id: moduleId,
      type: nodeType,
      resources: resources || [],
      priority: { type: resources?.[0] || ResourceType.ENERGY, priority: 1, consumers: [] },
      active: true,
    };

    this.registerNode(node);
  };

  private handleModuleUpdated = (event: unknown): void => {
    const { moduleId, changes } = event;

    // Get existing node
    const node = this.nodes.get(moduleId);
    if (!node) return;

    // Apply changes
    if (changes.resources) {
      node.resources = changes.resources;
    }

    if (changes.active !== undefined) {
      node.active = changes.active;
    }

    if (changes.efficiency !== undefined) {
      node.efficiency = changes.efficiency;
    }

    // Update node
    this.nodes.set(moduleId, node);

    // Invalidate cache for affected resources
    for (const resource of node.resources) {
      this.invalidateCache(resource);
    }
  };

  private handleModuleDestroyed = (event: unknown): void => {
    const { moduleId } = event;
    this.unregisterNode(moduleId);
  };

  private handleModuleStateChanged = (event: unknown): void => {
    const { moduleId, active } = event;

    // Get existing node
    const node = this.nodes.get(moduleId);
    if (!node) return;

    // Update active state
    node.active = active;

    // Update node
    this.nodes.set(moduleId, node);

    // Invalidate cache for affected resources
    for (const resource of node.resources) {
      this.invalidateCache(resource);
    }
  };
}

import { eventSystem } from '../../lib/events/UnifiedEventSystem';
import {
  ResourceState as StringResourceState,
  ResourceTransfer as StringResourceTransfer,
  ResourceType as StringResourceType,
} from '../../types/resources/ResourceTypes';
import { ResourceType } from "./../../types/resources/ResourceTypes";
import { ResourceType } from "./../../types/resources/ResourceTypes";
import { ResourceSystem, ResourceSystemConfig } from '../ResourceSystem';

/**
 * Storage container configuration
 */
export interface StorageContainerConfig {
  id: string;
  name: string;
  type: 'container' | 'pool' | 'storage';
  capacity: number;
  resourceTypes: StringResourceType[];
  priority: number;
  location?: string;
  efficiency?: number;
  accessSpeed?: number;
  upgradeLevel?: number;
  maxUpgradeLevel?: number;
}

/**
 * Storage container state
 */
export interface StorageContainerState {
  config: StorageContainerConfig;
  resources: Map<StringResourceType, StringResourceState>;
  totalStored: number;
  lastUpdated: number;
}

/**
 * Storage allocation
 */
export interface StorageAllocation {
  containerId: string;
  resourceType: StringResourceType;
  amount: number;
  percentage: number;
}

/**
 * ResourceStorageSubsystem
 *
 * Manages resource storage containers, pools, and allocation strategies
 */
export class ResourceStorageSubsystem {
  private containers: Map<string, StorageContainerState>;
  private resourcePriorities: Map<StringResourceType, number>;
  private transferHistory: StringResourceTransfer[];
  private parentSystem: ResourceSystem;
  private config: ResourceSystemConfig;
  private isInitialized = false;

  constructor(parentSystem: ResourceSystem, config: ResourceSystemConfig) {
    this.parentSystem = parentSystem;
    this.config = config;
    this.containers = new Map();
    this.resourcePriorities = new Map();
    this.transferHistory = [];

    // Set default resource priorities
    this.setDefaultResourcePriorities();
  }

  /**
   * Set default resource priorities
   */
  private setDefaultResourcePriorities(): void {
    // Higher number = higher priority
    this.resourcePriorities.set(ResourceType.ENERGY, 10);
    this.resourcePriorities.set(ResourceType.MINERALS, 8);
    this.resourcePriorities.set(ResourceType.POPULATION, 9);
    this.resourcePriorities.set(ResourceType.RESEARCH, 10);
    this.resourcePriorities.set(ResourceType.PLASMA, 10);
    this.resourcePriorities.set(ResourceType.GAS, 7);
    this.resourcePriorities.set(ResourceType.EXOTIC, 6);
  }

  /**
   * Initialize the subsystem
   */
  public async initialize(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      // Additional initialization logic can go here
      this.isInitialized = true;
    } catch (error) {
      console.error('Failed to initialize ResourceStorageSubsystem:', error);
      throw error;
    }
  }

  /**
   * Dispose of the subsystem
   */
  public async dispose(): Promise<void> {
    if (!this.isInitialized) {
      return;
    }

    try {
      // Cleanup resources
      this.containers.clear();
      this.resourcePriorities.clear();
      this.transferHistory = [];

      this.isInitialized = false;
    } catch (error) {
      console.error('Failed to dispose ResourceStorageSubsystem:', error);
      throw error;
    }
  }

  /**
   * Register a storage container
   */
  public registerContainer(config: StorageContainerConfig): boolean {
    if (!config.id || !config.resourceTypes || config.resourceTypes.length === 0) {
      console.error('Invalid storage container configuration:', config);
      return false;
    }

    // Initialize resource states
    const resources = new Map<StringResourceType, StringResourceState>();

    for (const type of config.resourceTypes) {
      resources.set(type, {
        current: 0,
        min: 0,
        max: config.capacity / config.resourceTypes.length, // Divide capacity equally by default
        production: 0,
        consumption: 0,
      });
    }

    this.containers.set(config.id, {
      config,
      resources,
      totalStored: 0,
      lastUpdated: Date.now(),
    });

    return true;
  }

  /**
   * Unregister a storage container
   */
  public unregisterContainer(id: string): boolean {
    if (!this.containers.has(id)) {
      return false;
    }

    this.containers.delete(id);
    return true;
  }

  /**
   * Get a storage container
   */
  public getContainer(id: string): StorageContainerState | undefined {
    return this.containers.get(id);
  }

  /**
   * Get all storage containers
   */
  public getAllContainers(): StorageContainerState[] {
    return Array.from(this.containers.values());
  }

  /**
   * Get containers by resource type
   */
  public getContainersByResourceType(type: StringResourceType): StorageContainerState[] {
    return Array.from(this.containers.values()).filter(container => container.resources.has(type));
  }

  /**
   * Store resource in a specific container
   */
  public storeResource(
    containerId: string,
    type: StringResourceType | ResourceType,
    amount: number
  ): number {
    // Convert to string resource type for internal use
    const stringType = ensureStringResourceType(type);

    const container = this.containers.get(containerId);
    if (!container || amount <= 0) {
      return 0;
    }

    // Check if container supports this resource type
    if (!container.config.resourceTypes.includes(stringType)) {
      console.warn(`Container ${containerId} does not support resource type ${stringType}`);
      return 0;
    }

    // Initialize resource state if it doesn't exist
    if (!container.resources.has(stringType)) {
      container.resources.set(stringType, {
        current: 0,
        max: container.config.capacity,
        min: 0,
        production: 0,
        consumption: 0,
      });
    }

    const resourceState = container.resources.get(stringType)!;
    const availableSpace = resourceState.max - resourceState.current;

    if (availableSpace <= 0) {
      // Handle overflow based on policy
      if (this.config.overflowPolicy === 'redistribute') {
        return this.redistributeResource(stringType, amount, containerId);
      }

      // Publish overflow event
      eventSystem.publish({
        type: 'RESOURCE_STORAGE_OVERFLOW',
        resourceType: ResourceTypeType,
        amount,
        containerId,
        timestamp: Date.now(),
      });
      return 0;
    }

    const amountToStore = Math.min(amount, availableSpace);

    resourceState.current += amountToStore;
    container.totalStored += amountToStore;
    container.lastUpdated = Date.now();

    // Update system resource state
    const systemState = this.parentSystem.getResourceState(stringType);
    if (systemState) {
      systemState.current += amountToStore;
      this.parentSystem.updateResourceState(stringType, systemState);
    }

    // Record transfer
    this.recordTransfer({
      type: stringType,
      source: 'external',
      target: containerId,
      amount: amountToStore,
      timestamp: Date.now(),
    });

    return amountToStore;
  }

  /**
   * Retrieve resource from a specific container
   */
  public retrieveResource(
    containerId: string,
    type: StringResourceType | ResourceType,
    amount: number
  ): number {
    // Convert to string resource type for internal use
    const stringType = ensureStringResourceType(type);

    const container = this.containers.get(containerId);
    if (!container || !container.resources.has(stringType) || amount <= 0) {
      return 0;
    }

    const resourceState = container.resources.get(stringType)!;

    if (resourceState.current <= 0) {
      return 0;
    }

    const amountToRetrieve = Math.min(amount, resourceState.current);

    resourceState.current -= amountToRetrieve;
    container.totalStored -= amountToRetrieve;
    container.lastUpdated = Date.now();

    // Update system resource state
    const systemState = this.parentSystem.getResourceState(stringType);
    if (systemState) {
      systemState.current -= amountToRetrieve;
      this.parentSystem.updateResourceState(stringType, systemState);
    }

    // Record transfer
    this.recordTransfer({
      type: stringType,
      source: containerId,
      target: 'external',
      amount: amountToRetrieve,
      timestamp: Date.now(),
    });

    return amountToRetrieve;
  }

  /**
   * Redistribute resource to other containers when primary is full
   */
  private redistributeResource(
    type: StringResourceType,
    amount: number,
    excludeContainerId: string
  ): number {
    let totalStored = 0;
    const availableContainers = Array.from(this.containers.values())
      .filter(
        container =>
          container.config.id !== excludeContainerId &&
          container.config.resourceTypes.includes(type)
      )
      .sort((a, b) => b.config.priority - a.config.priority);

    for (const container of availableContainers) {
      if (amount <= 0) break;

      const stored = this.storeResource(container.config.id, type, amount);
      totalStored += stored;
      amount -= stored;
    }

    return totalStored;
  }

  /**
   * Store resource in the best available container
   */
  public storeResourceOptimal(type: StringResourceType | ResourceType, amount: number): number {
    if (amount <= 0) {
      return 0;
    }

    // Get all containers that can store this resource type
    const availableContainers = this.getContainersByResourceType(type as StringResourceType);

    if (availableContainers.length === 0) {
      return 0;
    }

    // Calculate scores for each container based on the allocation strategy
    const containerScores = this.calculateContainerScores(
      availableContainers,
      type as StringResourceType
    );

    // Sort containers by score (highest first)
    const sortedContainers = [...containerScores].sort((a, b) => b.score - a.score);

    let remainingAmount = amount;
    let totalStored = 0;

    // Try to store in containers by score order
    for (const { containerId } of sortedContainers) {
      if (remainingAmount <= 0) {
        break;
      }

      const amountStored = this.storeResource(containerId, type, remainingAmount);
      totalStored += amountStored;
      remainingAmount -= amountStored;
    }

    // Handle overflow if needed
    if (remainingAmount > 0 && this.config.overflowPolicy !== 'reject') {
      // Emit overflow event
      eventSystem.publish({
        type: 'RESOURCE_STORAGE_OVERFLOW',
        resourceType: type,
        amount: remainingAmount,
        timestamp: Date.now(),
      });
    }

    // Check if rebalancing is needed
    if (this.config.autoRebalance) {
      this.checkAndRebalance(type as StringResourceType);
    }

    return totalStored;
  }

  /**
   * Retrieve resource from the best available container
   */
  public retrieveResourceOptimal(type: StringResourceType | ResourceType, amount: number): number {
    if (amount <= 0) {
      return 0;
    }

    // Get all containers that store this resource type
    const availableContainers = this.getContainersByResourceType(type as StringResourceType);

    if (availableContainers.length === 0) {
      return 0;
    }

    // Calculate scores for each container based on the allocation strategy
    // For retrieval, we want to prioritize containers with higher fill percentage
    const containerScores = this.calculateContainerScores(
      availableContainers,
      type as StringResourceType,
      true
    );

    // Sort containers by score (highest first)
    const sortedContainers = [...containerScores].sort((a, b) => b.score - a.score);

    let remainingAmount = amount;
    let totalRetrieved = 0;

    // Try to retrieve from containers by score order
    for (const { containerId } of sortedContainers) {
      if (remainingAmount <= 0) {
        break;
      }

      const amountRetrieved = this.retrieveResource(containerId, type, remainingAmount);
      totalRetrieved += amountRetrieved;
      remainingAmount -= amountRetrieved;
    }

    return totalRetrieved;
  }

  /**
   * Calculate container scores for allocation
   */
  private calculateContainerScores(
    containers: StorageContainerState[],
    resourceType: StringResourceType,
    forRetrieval = false
  ): Array<{ containerId: string; score: number }> {
    const weights = {
      containerPriority: 0.4,
      resourcePriority: 0.4,
      fillPercentage: 0.2,
    };

    const resourcePriorityValue = this.resourcePriorities.get(resourceType) || 5;

    return containers.map(container => {
      const resourceState = container.resources.get(resourceType)!;
      const fillRatio = resourceState.current / resourceState.max;

      // For storage, we prefer containers with lower fill percentage
      // For retrieval, we prefer containers with higher fill percentage
      const fillScore = forRetrieval ? fillRatio : 1 - fillRatio;

      // Calculate weighted score
      const score =
        container.config.priority * weights.containerPriority +
        resourcePriorityValue * weights.resourcePriority +
        fillScore * weights.fillPercentage;

      return {
        containerId: container.config.id,
        score,
      };
    });
  }

  /**
   * Redistribute overflow by expanding container capacity
   */
  public redistributeOverflow(
    type: StringResourceType | ResourceType,
    amount: number,
    sourceId?: string
  ): number {
    // Find containers that can store this resource type
    const relevantContainers = this.getContainersByResourceType(type as StringResourceType);

    if (relevantContainers.length === 0) {
      return 0;
    }

    // If sourceId is provided, filter out that container
    const eligibleContainers = sourceId
      ? relevantContainers.filter(c => c.config.id !== sourceId)
      : relevantContainers;

    if (eligibleContainers.length === 0) {
      // Sort by upgrade potential (containers with lower upgrade level first)
      const upgradableContainers = relevantContainers
        .filter(
          container =>
            container.config.upgradeLevel !== undefined &&
            container.config.maxUpgradeLevel !== undefined &&
            container.config.upgradeLevel < container.config.maxUpgradeLevel
        )
        .sort((a, b) => (a.config.upgradeLevel || 0) - (b.config.upgradeLevel || 0));

      if (upgradableContainers.length === 0) {
        return 0;
      }

      // Upgrade the first container
      const containerToUpgrade = upgradableContainers[0];

      // For each resource type in the container
      for (const [resType, resourceState] of containerToUpgrade.resources.entries()) {
        // Increase capacity by 20%
        const capacityIncrease = resourceState.max * 0.2;
        resourceState.max += capacityIncrease;
      }

      // Increment upgrade level
      if (containerToUpgrade.config.upgradeLevel !== undefined) {
        containerToUpgrade.config.upgradeLevel += 1;
      }

      // Try to store again if it's the target resource type
      if (sourceId) {
        return this.storeResource(containerToUpgrade.config.id, type, amount);
      } else {
        return this.storeResourceOptimal(type, amount);
      }
    } else {
      // Distribute among eligible containers
      let remainingAmount = amount;
      let totalStored = 0;

      // Calculate available space in all eligible containers
      for (const container of eligibleContainers) {
        if (remainingAmount <= 0) break;

        const resourceState = container.resources.get(type as StringResourceType)!;
        const availableSpace = resourceState.max - resourceState.current;

        if (availableSpace <= 0) continue;

        const amountToStore = Math.min(remainingAmount, availableSpace);
        const stored = this.storeResource(container.config.id, type, amountToStore);

        totalStored += stored;
        remainingAmount -= stored;
      }

      return totalStored;
    }
  }

  /**
   * Check if rebalancing is needed and perform it
   */
  private checkAndRebalance(type: StringResourceType): void {
    const containers = this.getContainersByResourceType(type);

    if (containers.length <= 1) {
      return;
    }

    // Calculate fill ratios
    const fillRatios = containers.map(container => {
      const resourceState = container.resources.get(type)!;
      return {
        id: container.config.id,
        fillRatio: resourceState.current / resourceState.max,
      };
    });

    // Find min and max fill ratios
    const minFill = Math.min(...fillRatios.map(r => r.fillRatio));
    const maxFill = Math.max(...fillRatios.map(r => r.fillRatio));

    // Check if imbalance exceeds threshold (default 0.2 or 20%)
    const threshold = 0.2;
    if (maxFill - minFill > threshold) {
      this.rebalanceContainers(type, containers);
    }
  }

  /**
   * Rebalance resources between containers
   */
  private rebalanceContainers(type: StringResourceType, containers: StorageContainerState[]): void {
    // Calculate target fill ratio (average)
    let totalCurrent = 0;
    let totalMax = 0;

    for (const container of containers) {
      const resourceState = container.resources.get(type)!;
      totalCurrent += resourceState.current;
      totalMax += resourceState.max;
    }

    const targetFillRatio = totalCurrent / totalMax;

    // Calculate transfers needed
    for (const container of containers) {
      const resourceState = container.resources.get(type)!;
      const currentFillRatio = resourceState.current / resourceState.max;

      if (Math.abs(currentFillRatio - targetFillRatio) < 0.05) {
        // Close enough, skip
        continue;
      }

      const targetAmount = resourceState.max * targetFillRatio;
      const difference = targetAmount - resourceState.current;

      if (difference > 0) {
        // Need to add resources
        // Find container with excess
        const sourceContainer = containers.find(c => {
          const rs = c.resources.get(type)!;
          return rs.current / rs.max > targetFillRatio + 0.05;
        });

        if (sourceContainer) {
          const sourceState = sourceContainer.resources.get(type)!;
          const availableToTransfer = sourceState.current - sourceState.max * targetFillRatio;
          const transferAmount = Math.min(difference, availableToTransfer);

          // Transfer resources
          this.transferBetweenContainers(
            sourceContainer.config.id,
            container.config.id,
            type,
            transferAmount
          );
        }
      }
    }
  }

  /**
   * Transfer resources between containers
   */
  public transferBetweenContainers(
    sourceId: string,
    targetId: string,
    type: StringResourceType | ResourceType,
    amount: number
  ): number {
    if (amount <= 0 || sourceId === targetId) {
      return 0;
    }

    // Retrieve from source
    const retrievedAmount = this.retrieveResource(sourceId, type, amount);

    if (retrievedAmount <= 0) {
      return 0;
    }

    // Store in target
    const storedAmount = this.storeResource(targetId, type, retrievedAmount);

    // If not all was stored, return remainder to source
    if (storedAmount < retrievedAmount) {
      const remainder = retrievedAmount - storedAmount;
      this.storeResource(sourceId, type, remainder);
    }

    // Record transfer
    this.recordTransfer({
      type: type as StringResourceType,
      source: sourceId,
      target: targetId,
      amount: storedAmount,
      timestamp: Date.now(),
    });

    return storedAmount;
  }

  /**
   * Record a transfer in history
   */
  private recordTransfer(transfer: StringResourceTransfer): void {
    this.transferHistory.push(transfer);

    // Trim history if needed
    if (this.transferHistory.length > this.config.maxHistorySize) {
      this.transferHistory = this.transferHistory.slice(-this.config.maxHistorySize);
    }

    // Publish transfer event
    eventSystem.publish({
      type: 'RESOURCE_TRANSFERRED',
      resourceType: transfer.type,
      source: transfer.source,
      target: transfer.target,
      amount: transfer.amount,
      timestamp: Date.now(),
    });
  }

  /**
   * Get transfer history
   */
  public getTransferHistory(): StringResourceTransfer[] {
    return [...this.transferHistory];
  }

  /**
   * Get total stored amount of a resource type
   */
  public getTotalStored(type: StringResourceType | ResourceType): number {
    let total = 0;

    for (const container of this.containers.values()) {
      const resourceState = container.resources.get(type as StringResourceType);
      if (resourceState) {
        total += resourceState.current;
      }
    }

    return total;
  }

  /**
   * Get total capacity for a resource type
   */
  public getTotalCapacity(type: StringResourceType | ResourceType): number {
    let total = 0;

    for (const container of this.containers.values()) {
      const resourceState = container.resources.get(type as StringResourceType);
      if (resourceState) {
        total += resourceState.max;
      }
    }

    return total;
  }

  /**
   * Set resource priority
   */
  public setResourcePriority(type: StringResourceType | ResourceType, priority: number): void {
    // Convert to string resource type for internal use
    const stringType = ensureStringResourceType(type);

    this.resourcePriorities.set(stringType, priority);
  }

  /**
   * Get resource priority
   */
  public getResourcePriority(type: StringResourceType | ResourceType): number {
    // Convert to string resource type for internal use
    const stringType = ensureStringResourceType(type);

    return this.resourcePriorities.get(stringType) || 5;
  }

  /**
   * Update container configuration
   */
  public updateContainerConfig(id: string, config: Partial<StorageContainerConfig>): boolean {
    const container = this.containers.get(id);
    if (!container) {
      return false;
    }

    // Update config
    container.config = {
      ...container.config,
      ...config,
    };

    return true;
  }

  /**
   * Get resource state for a specific container and resource type
   */
  public getContainerResourceState(
    containerId: string,
    type: StringResourceType | ResourceType
  ): StringResourceState | null {
    // Convert to string resource type for internal use
    const stringType = ensureStringResourceType(type);

    const container = this.containers.get(containerId);
    if (!container || !container.resources.has(stringType)) {
      return null;
    }

    return { ...container.resources.get(stringType)! };
  }

  /**
   * Get all resource states for a specific container
   */
  public getContainerResourceStates(
    containerId: string
  ): Map<StringResourceType, StringResourceState> | null {
    const container = this.containers.get(containerId);
    if (!container) {
      return null;
    }

    // Create a copy of the resource states
    const resourceStates = new Map<StringResourceType, StringResourceState>();
    for (const [type, state] of container.resources.entries()) {
      resourceStates.set(type, { ...state });
    }

    return resourceStates;
  }

  /**
   * Get all containers that store a specific resource type
   */
  public getContainersForResourceType(
    type: StringResourceType | ResourceType
  ): StorageContainerState[] {
    // Convert to string resource type for internal use
    const stringType = ensureStringResourceType(type);

    return Array.from(this.containers.values()).filter(container =>
      container.config.resourceTypes.includes(stringType)
    );
  }

  /**
   * Get total stored amount of a specific resource type across all containers
   */
  public getTotalStoredAmount(type: StringResourceType | ResourceType): number {
    // Convert to string resource type for internal use
    const stringType = ensureStringResourceType(type);

    let total = 0;
    for (const container of this.containers.values()) {
      const resourceState = container.resources.get(stringType);
      if (resourceState) {
        total += resourceState.current;
      }
    }
    return total;
  }

  /**
   * Get total storage capacity for a specific resource type across all containers
   */
  public getTotalStorageCapacity(type: StringResourceType | ResourceType): number {
    // Convert to string resource type for internal use
    const stringType = ensureStringResourceType(type);

    let total = 0;
    for (const container of this.containers.values()) {
      if (container.config.resourceTypes.includes(stringType)) {
        total += container.config.capacity;
      }
    }
    return total;
  }

  /**
   * Get available storage space for a specific resource type across all containers
   */
  public getAvailableStorageSpace(type: StringResourceType | ResourceType): number {
    // Convert to string resource type for internal use
    const stringType = ensureStringResourceType(type);

    let total = 0;
    for (const container of this.containers.values()) {
      if (container.config.resourceTypes.includes(stringType)) {
        const resourceState = container.resources.get(stringType);
        if (resourceState) {
          total += resourceState.max - resourceState.current;
        } else {
          total += container.config.capacity;
        }
      }
    }
    return total;
  }
}

/**
 * Network Degradation Test Suite Runner
 *
 * This script runs the network degradation test suite to evaluate application
 * performance under various network conditions. It provides command-line options
 * to run specific tests or all tests together.
 *
 * Usage:
 *   npx ts-node src/scripts/runNetworkTests.ts --all
 *   npx ts-node src/scripts/runNetworkTests.ts --api
 *   npx ts-node src/scripts/runNetworkTests.ts --resources
 *   npx ts-node src/scripts/runNetworkTests.ts --interactions
 */

import {
  NetworkDegradationTestResult,
  testApiPerformance,
  testResourceLoadingPerformance,
  testUserInteractionPerformance,
} from '../tests/performance/NetworkDegradationTestSuite';

// Parse command line arguments
const args = process.argv.slice(2);
const runAll = args.includes('--all');
const runApi = args.includes('--api') || runAll;
const runResources = args.includes('--resources') || runAll;
const runInteractions = args.includes('--interactions') || runAll;
const generateReport = args.includes('--report') || runAll;

// Define a function to format duration
function formatDuration(ms: number): string {
  if (ms < 1000) return `${ms.toFixed(0)}ms`;
  const seconds = ms / 1000;
  if (seconds < 60) return `${seconds.toFixed(1)}s`;
  const minutes = Math.floor(seconds / 60);
  const remainingSeconds = seconds % 60;
  return `${minutes}m ${remainingSeconds.toFixed(0)}s`;
}

// Function to print a result table
function printResultTable(results: NetworkDegradationTestResult[], title: string): void {
  console.warn(`\n${title}`);
  console.warn('-'.repeat(100));
  console.warn(
    '| Network Condition       | Avg Response Time | Max Response Time | Success Rate | TTI        |'
  );
  console.warn(
    '|-------------------------|-------------------|-------------------|--------------|------------|'
  );

  // Group results by network condition
  const groupedResults: Record<string, NetworkDegradationTestResult[]> = {};
  results.forEach(result => {
    const conditionName = result.networkCondition.name;
    groupedResults[conditionName] = groupedResults[conditionName] || [];
    groupedResults[conditionName].push(result);
  });

  // Sort network conditions by average response time (ascending)
  const sortedConditions = Object.entries(groupedResults)
    .map(([condition, results]) => {
      const avgResponseTime =
        results.reduce((sum, r) => sum + r.averageResponseTimeMs, 0) / results.length;

      return { condition, avgResponseTime };
    })
    .sort((a, b) => a.avgResponseTime - b.avgResponseTime);

  // Print each condition's results
  for (const { condition } of sortedConditions) {
    const condResults = groupedResults[condition];
    const avgResponseTime =
      condResults.reduce((sum, r) => sum + r.averageResponseTimeMs, 0) / condResults.length;

    const maxResponseTime = Math.max(...condResults.map(r => r.maxResponseTimeMs));

    const successCount = condResults.reduce((sum, r) => sum + r.successfulOperations, 0);

    const totalOps = successCount + condResults.reduce((sum, r) => sum + r.failedOperations, 0);

    const successRate = totalOps > 0 ? (successCount / totalOps) * 100 : 0;

    const avgTTI =
      condResults.reduce((sum, r) => sum + r.timeToInteractive, 0) / condResults.length;

    // Print the row
    console.warn(
      `| %-23s | %-17s | %-17s | %-12s | %-10s |`.replace(/%(-?\d+)s/g, (match, width) => {
        const paddingLength = parseInt(width.replace('-', ''));
        const text = [
          condition.padEnd(23),
          formatDuration(avgResponseTime).padEnd(17),
          formatDuration(maxResponseTime).padEnd(17),
          `${successRate.toFixed(1)}%`.padEnd(12),
          formatDuration(avgTTI).padEnd(10),
        ][0];
        return width.startsWith('-') ? text.padEnd(paddingLength) : text.padStart(paddingLength);
      })
    );
  }

  console.warn('-'.repeat(100));
}

// Function to generate a full HTML report
function generateHtmlReport(results: Record<string, NetworkDegradationTestResult[]>): void {
  // Implementation for generating a detailed HTML report
  // (This would be a more extensive implementation in the real system)
  console.warn('\nGenerating HTML report...');
  console.warn('HTML report generation not implemented in this demo script.');
}

async function main(): Promise<void> {
  console.warn('Network Degradation Test Suite Runner');
  console.warn('====================================\n');

  const startTime = Date.now();
  const results: Record<string, NetworkDegradationTestResult[]> = {};

  if (runApi) {
    console.warn('Running API performance tests...');
    results.api = await testApiPerformance();
    printResultTable(results.api, 'API Performance Results');
  }

  if (runResources) {
    console.warn('\nRunning resource loading performance tests...');
    results.resources = await testResourceLoadingPerformance();
    printResultTable(results.resources, 'Resource Loading Performance Results');
  }

  if (runInteractions) {
    console.warn('\nRunning user interaction performance tests...');
    results.interactions = await testUserInteractionPerformance();
    printResultTable(results.interactions, 'User Interaction Performance Results');
  }

  // Print overall time
  const totalTime = Date.now() - startTime;
  console.warn(`\nCompleted all tests in ${formatDuration(totalTime)}`);

  // Generate report if requested
  if (generateReport) {
    generateHtmlReport(results);
  }

  // Print usage instructions if no arguments provided
  if (!runAll && !runApi && !runResources && !runInteractions) {
    console.warn('\nUsage:');
    console.warn('  npx ts-node src/scripts/runNetworkTests.ts --all         Run all tests');
    console.warn(
      '  npx ts-node src/scripts/runNetworkTests.ts --api         Run API performance tests'
    );
    console.warn(
      '  npx ts-node src/scripts/runNetworkTests.ts --resources   Run resource loading tests'
    );
    console.warn(
      '  npx ts-node src/scripts/runNetworkTests.ts --interactions Run user interaction tests'
    );
    console.warn('  npx ts-node src/scripts/runNetworkTests.ts --report      Generate HTML report');
  }
}

// Run the main function
main().catch(error => {
  console.error('Error running network tests:', error);
  process.exit(1);
});

