/**
 * RecoveryService - Provides mechanisms for recovering from critical application failures
 *
 * This service handles:
 * - Saving application state snapshots
 * - Restoring previous application states
 * - Implementing different recovery strategies based on error type
 * - Providing graceful degradation options
 */

import { AbstractBaseService } from '../lib/services/BaseService';
import { ErrorType } from './ErrorLoggingService';

// Types of recovery strategies that can be applied
export enum RecoveryStrategy {
  RETRY = 'retry',
  ROLLBACK = 'rollback',
  RESET = 'reset',
  IGNORE = 'ignore',
}

// Structure for application state snapshot
export interface StateSnapshot {
  id: string;
  state: Record<string, unknown>;
  timestamp: number;
  metadata?: Record<string, unknown>;
}

// Configuration for recovery actions
export interface RecoveryConfig {
  maxSnapshots: number;
  autoSaveInterval: number;
  enableAutoRecover: boolean;
  defaultStrategy: RecoveryStrategy;
  strategyByErrorType: Partial<Record<ErrorType, RecoveryStrategy>>;
}

class RecoveryServiceImpl extends AbstractBaseService {
  private static instance: RecoveryServiceImpl;
  private snapshots: StateSnapshot[] = [];
  private config: RecoveryConfig = {
    maxSnapshots: 10,
    autoSaveInterval: 60000, // 1 minute
    enableAutoRecover: true,
    defaultStrategy: RecoveryStrategy.RETRY,
    strategyByErrorType: {
      [ErrorType.NETWORK]: RecoveryStrategy.RETRY,
      [ErrorType.RESOURCE]: RecoveryStrategy.ROLLBACK,
      [ErrorType.UNKNOWN]: RecoveryStrategy.RESET,
    },
  };

  private constructor() {
    super('RecoveryService', '1.0.0');
  }

  public static getInstance(): RecoveryServiceImpl {
    if (!RecoveryServiceImpl.instance) {
      RecoveryServiceImpl.instance = new RecoveryServiceImpl();
    }
    return RecoveryServiceImpl.instance;
  }

  protected async onInitialize(): Promise<void> {
    // Load any existing snapshots from localStorage
    try {
      const savedSnapshots = localStorage.getItem('recovery_snapshots');
      if (savedSnapshots) {
        this.snapshots = JSON.parse(savedSnapshots);
      }
    } catch (error) {
      this.handleError(error as Error);
    }
  }

  protected async onDispose(): Promise<void> {
    // Save snapshots to localStorage
    try {
      localStorage.setItem('recovery_snapshots', JSON.stringify(this.snapshots));
    } catch (error) {
      this.handleError(error as Error);
    }
  }

  public createSnapshot(
    state: Record<string, unknown>,
    metadata?: Record<string, unknown>
  ): string {
    const snapshot: StateSnapshot = {
      id: crypto.randomUUID(),
      state,
      timestamp: Date.now(),
      metadata,
    };

    this.snapshots.unshift(snapshot);

    // Trim snapshots if we exceed max size
    if (this.snapshots.length > this.config.maxSnapshots) {
      this.snapshots = this.snapshots.slice(0, this.config.maxSnapshots);
    }

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.total_snapshots = this.snapshots.length;
    metrics.latest_snapshot_timestamp = snapshot.timestamp;
    this.metadata.metrics = metrics;

    return snapshot.id;
  }

  public restoreSnapshot(snapshotId: string): Record<string, unknown> | null {
    const snapshot = this.snapshots.find(s => s.id === snapshotId);
    if (!snapshot) {
      return null;
    }

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.total_restores = (metrics.total_restores || 0) + 1;
    metrics.last_restore_timestamp = Date.now();
    this.metadata.metrics = metrics;

    return snapshot.state;
  }

  public getSnapshots(): StateSnapshot[] {
    return [...this.snapshots];
  }

  public clearSnapshots(): void {
    this.snapshots = [];
    this.metadata.metrics = {};
  }

  public getRecoveryStrategy(
    errorType: ErrorType = ErrorType.UNKNOWN,
    metadata?: Record<string, unknown>
  ): RecoveryStrategy {
    return this.config.strategyByErrorType[errorType] || this.config.defaultStrategy;
  }

  public override handleError(error: Error): void {
    // Update error metrics
    const metrics = this.metadata.metrics || {};
    metrics.total_errors = (metrics.total_errors || 0) + 1;
    metrics.last_error_timestamp = Date.now();
    this.metadata.metrics = metrics;

    // Log error in development
    if (process.env.NODE_ENV === 'development') {
      console.error('[RecoveryService] Error:', error);
    }
  }
}

// Export singleton instance
export const recoveryService = RecoveryServiceImpl.getInstance();

// Export default for easier imports
export default recoveryService;

import { AbstractBaseService } from '../lib/services/BaseService';
import { ErrorType, errorLoggingService } from './ErrorLoggingService';

export interface WorkerTask<T = unknown> {
  id: string;
  type: string;
  data: unknown;
  priority: number;
  cancelToken?: AbortController;
  progress?: number;
  result?: T;
  error?: Error;
  startTime?: number;
  endTime?: number;
}

export interface WorkerConfig {
  maxWorkers: number;
  taskTimeout: number;
  retryAttempts: number;
  priorityLevels: {
    HIGH: number;
    MEDIUM: number;
    LOW: number;
  };
}

class WorkerServiceImpl extends AbstractBaseService {
  private static instance: WorkerServiceImpl;
  private workers: Worker[] = [];
  private taskQueue: WorkerTask[] = [];
  private activeTasks: Map<string, WorkerTask> = new Map();
  private workerPool: Map<Worker, WorkerTask | null> = new Map();

  private config: WorkerConfig = {
    maxWorkers: navigator.hardwareConcurrency || 4,
    taskTimeout: 30000, // 30 seconds
    retryAttempts: 3,
    priorityLevels: {
      HIGH: 3,
      MEDIUM: 2,
      LOW: 1,
    },
  };

  private constructor() {
    super('WorkerService', '1.0.0');
  }

  public static getInstance(): WorkerServiceImpl {
    if (!WorkerServiceImpl.instance) {
      WorkerServiceImpl.instance = new WorkerServiceImpl();
    }
    return WorkerServiceImpl.instance;
  }

  protected async onInitialize(): Promise<void> {
    // Initialize worker pool
    for (let i = 0; i < this.config.maxWorkers; i++) {
      const worker = new Worker(new URL('../workers/worker.ts', import.meta.url), {
        type: 'module',
      });
      this.setupWorker(worker);
      this.workers.push(worker);
      this.workerPool.set(worker, null);
    }

    // Initialize metrics
    this.metadata.metrics = {
      total_tasks: 0,
      active_tasks: 0,
      completed_tasks: 0,
      failed_tasks: 0,
      average_task_time: 0,
    };
  }

  protected async onDispose(): Promise<void> {
    // Cancel all active tasks
    for (const task of this.activeTasks.values()) {
      this.cancelTask(task.id);
    }

    // Terminate all workers
    for (const worker of this.workers) {
      worker.terminate();
    }

    this.workers = [];
    this.taskQueue = [];
    this.activeTasks.clear();
    this.workerPool.clear();
  }

  private setupWorker(worker: Worker): void {
    worker.onmessage = (event: MessageEvent) => {
      const { taskId, type, data } = event.data;
      const task = this.activeTasks.get(taskId);

      if (!task) return;

      switch (type) {
        case 'progress':
          task.progress = data;
          break;
        case 'result':
          this.completeTask(taskId, data);
          break;
        case 'error':
          this.failTask(taskId, new Error(data));
          break;
      }
    };

    worker.onerror = (error: ErrorEvent) => {
      const task = Array.from(this.activeTasks.values()).find(
        task => this.workerPool.get(worker) === task
      );
      if (task) {
        this.failTask(task.id, error.error);
      }
    };
  }

  public async submitTask<T>(
    type: string,
    data: unknown,
    priority: keyof WorkerConfig['priorityLevels'] = 'MEDIUM'
  ): Promise<T> {
    const task: WorkerTask<T> = {
      id: crypto.randomUUID(),
      type,
      data,
      priority: this.config.priorityLevels[priority],
      cancelToken: new AbortController(),
    };

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.total_tasks = (metrics.total_tasks || 0) + 1;
    this.metadata.metrics = metrics;

    return new Promise((resolve, reject) => {
      // Add task to queue
      this.taskQueue.push(task);
      this.taskQueue.sort((a, b) => b.priority - a.priority);

      // Setup task timeout
      const timeoutId = setTimeout(() => {
        this.cancelTask(task.id);
        reject(new Error(`Task ${task.id} timed out after ${this.config.taskTimeout}ms`));
      }, this.config.taskTimeout);

      // Setup completion handlers
      const cleanup = () => {
        clearTimeout(timeoutId);
        this.taskQueue = this.taskQueue.filter(t => t.id !== task.id);
        this.activeTasks.delete(task.id);
      };

      const onComplete = (result: T) => {
        cleanup();
        resolve(result);
      };

      const onError = (error: Error) => {
        cleanup();
        reject(error);
      };

      // Store handlers with task
      Object.assign(task, { onComplete, onError });

      // Try to process task immediately
      this.processNextTask();
    });
  }

  public cancelTask(taskId: string): void {
    const task = this.activeTasks.get(taskId);
    if (!task) return;

    task.cancelToken?.abort();
    this.failTask(taskId, new Error('Task cancelled'));
  }

  private async processNextTask(): Promise<void> {
    // Find available worker
    const availableWorker = Array.from(this.workerPool.entries()).find(
      ([, task]) => task === null
    )?.[0];

    if (!availableWorker || this.taskQueue.length === 0) return;

    // Get highest priority task
    const task = this.taskQueue.shift()!;
    task.startTime = Date.now();

    // Assign task to worker
    this.activeTasks.set(task.id, task);
    this.workerPool.set(availableWorker, task);

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.active_tasks = this.activeTasks.size;
    this.metadata.metrics = metrics;

    // Send task to worker
    availableWorker.postMessage({
      taskId: task.id,
      type: task.type,
      data: task.data,
    });
  }

  private completeTask(taskId: string, result: unknown): void {
    const task = this.activeTasks.get(taskId);
    if (!task) return;

    task.endTime = Date.now();
    task.result = result;

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.completed_tasks = (metrics.completed_tasks || 0) + 1;
    metrics.active_tasks = this.activeTasks.size - 1;

    const taskTime = task.endTime - (task.startTime || 0);
    metrics.average_task_time = metrics.average_task_time
      ? (metrics.average_task_time + taskTime) / 2
      : taskTime;

    this.metadata.metrics = metrics;

    // Release worker
    const worker = Array.from(this.workerPool.entries()).find(([, t]) => t === task)?.[0];
    if (worker) {
      this.workerPool.set(worker, null);
    }

    // Call completion handler
    (task as any).onComplete?.(result);

    // Process next task
    this.processNextTask();
  }

  private failTask(taskId: string, error: Error): void {
    const task = this.activeTasks.get(taskId);
    if (!task) return;

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.failed_tasks = (metrics.failed_tasks || 0) + 1;
    metrics.active_tasks = this.activeTasks.size - 1;
    this.metadata.metrics = metrics;

    // Release worker
    const worker = Array.from(this.workerPool.entries()).find(([, t]) => t === task)?.[0];
    if (worker) {
      this.workerPool.set(worker, null);
    }

    // Call error handler
    (task as any).onError?.(error);

    // Process next task
    this.processNextTask();
  }

  public override handleError(error: Error): void {
    errorLoggingService.logError(error, ErrorType.RUNTIME, undefined, { service: 'WorkerService' });
  }
}

// Export singleton instance
export const workerService = WorkerServiceImpl.getInstance();

// Export default for easier imports
export default workerService;

/**
 * SessionPerformanceTracker
 *
 * A service that anonymously tracks performance metrics during user sessions.
 * Collects, aggregates, and reports performance data without storing personally
 * identifiable information.
 */

import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import { ModuleEvent } from '../../types/events/ModuleEventTypes';
import { ResourceType } from "./../../types/resources/ResourceTypes";
import { generateAnonymousId } from '../../utils/idGenerator';

/**
 * Session identification data
 */
export interface SessionMetadata {
  sessionId: string;
  deviceCategory: 'desktop' | 'tablet' | 'mobile' | 'unknown';
  browserCategory: string;
  viewportWidth: number;
  viewportHeight: number;
  startTimestamp: number;
  geographicRegion?: string;
  connectionType?: string;
  memoryEstimate?: number;
}

/**
 * Performance metrics collected during a session
 */
export interface SessionPerformanceData {
  sessionId: string;
  timestamp: number;
  metrics: {
    fps: number;
    memoryUsage: number;
    cpuUsage: number;
    resourceUtilization: Map<ResourceType, number>;
    renderTime: number;
    eventProcessingTime: number;
    interactionLatency: number;
    loadTimes: {
      [componentId: string]: number;
    };
    eventCounts: {
      [eventType: string]: number;
    };
  };
  userInteractions: UserInteractionData[];
  errors: ErrorData[];
}

/**
 * Data about user interactions with timestamps and performance metrics
 */
export interface UserInteractionData {
  interactionType: 'click' | 'hover' | 'scroll' | 'keypress' | 'custom';
  targetComponent?: string;
  timestamp: number;
  responseTime: number;
  successful: boolean;
}

/**
 * Error information for tracking performance-related errors
 */
export interface ErrorData {
  errorType: ResourceType;
  message: string;
  timestamp: number;
  componentId?: string;
  stackSummary?: string;
  affectedResource?: ResourceType;
}

/**
 * Options for telemetry collection
 */
export interface TelemetryOptions {
  collectionEnabled: boolean;
  samplingRate: number; // 0.0 to 1.0
  anonymousIdSeed?: string;
  geolocationEnabled: boolean;
  performanceDetailLevel: 'minimal' | 'standard' | 'detailed';
  transmitIntervalMs: number;
  maxBatchSize: number;
  errorSamplingRate: number; // 0.0 to 1.0
}

// Browser API interfaces to avoid 'any' usage
interface NavigatorExtended extends Navigator {
  deviceMemory?: number;
  connection?: {
    effectiveType?: string;
    type?: string;
  };
  getBattery?: () => Promise<{
    charging: boolean;
    level: number;
  }>;
}

interface PerformanceExtended extends Performance {
  memory?: {
    jsHeapSizeLimit: number;
    totalJSHeapSize: number;
    usedJSHeapSize: number;
  };
}

/**
 * Service that tracks anonymous session performance metrics
 */
export class SessionPerformanceTracker {
  private isEnabled: boolean;
  private sessionMetadata!: SessionMetadata; // Using definite assignment assertion
  private performanceData: SessionPerformanceData[] = [];
  private options: TelemetryOptions;
  private transmitInterval: number | null = null;
  private eventSubscription: (() => void) | null = null;
  private interactionObservers: Map<string, () => void> = new Map();
  private componentLoadTimers: Map<string, number> = new Map();
  private startTime: number;
  private lastTransmitTime: number;
  private accumulatedEventCounts: Record<string, number> = {};

  /**
   * Initialize the session performance tracker
   */
  constructor(options?: Partial<TelemetryOptions>) {
    this.startTime = performance.now();
    this.lastTransmitTime = this.startTime;

    // Set default options
    this.options = {
      collectionEnabled: true,
      samplingRate: 0.1, // Only track 10% of sessions by default
      geolocationEnabled: false,
      performanceDetailLevel: 'standard',
      transmitIntervalMs: 60000, // Transmit data every minute
      maxBatchSize: 50,
      errorSamplingRate: 1.0, // Track all errors
      ...options,
    };

    // Only enable tracking if it passes the sampling threshold
    const samplingValue = Math.random();
    this.isEnabled = this.options.collectionEnabled && samplingValue <= this.options.samplingRate;

    if (!this.isEnabled) {
      console.warn('[SessionPerformanceTracker] Telemetry disabled due to sampling');
      return;
    }

    // Create session metadata
    this.sessionMetadata = this.generateSessionMetadata();

    // Initialize performance data for the current session
    this.initializeCurrentSessionData();

    // Subscribe to system events
    this.subscribeToEvents();

    // Set up interaction tracking
    this.setupInteractionTracking();

    // Start transmission cycle
    this.startTransmissionCycle();

    console.warn(
      `[SessionPerformanceTracker] Initialized session: ${this.sessionMetadata.sessionId}`
    );
  }

  /**
   * Generate anonymous session metadata
   */
  private generateSessionMetadata(): SessionMetadata {
    // Create an anonymous ID that doesn't contain PII
    const sessionId = generateAnonymousId(this.options.anonymousIdSeed);

    // Detect device type from user agent and screen size
    const userAgent = navigator.userAgent.toLowerCase();
    const screenWidth = window.screen.width;
    const screenHeight = window.screen.height;

    let deviceCategory: 'desktop' | 'tablet' | 'mobile' | 'unknown' = 'unknown';
    if (/mobile|android|iphone|ipad|ipod|blackberry|iemobile|opera mini/.test(userAgent)) {
      deviceCategory = screenWidth >= 768 ? 'tablet' : 'mobile';
    } else {
      deviceCategory = 'desktop';
    }

    // Determine browser category without version info for anonymity
    let browserCategory = 'unknown';
    if (userAgent.includes('firefox')) {
      browserCategory = 'firefox';
    } else if (userAgent.includes('chrome')) {
      browserCategory = 'chrome';
    } else if (userAgent.includes('safari')) {
      browserCategory = 'safari';
    } else if (userAgent.includes('edge')) {
      browserCategory = 'edge';
    }

    // Get viewport size
    const viewportWidth = window.innerWidth;
    const viewportHeight = window.innerHeight;

    // Get optional connection information if available
    const nav = navigator as NavigatorExtended;
    let connectionType: string | undefined;
    if (nav.connection) {
      connectionType = nav.connection.effectiveType || nav.connection.type;
    }

    // Estimate memory if available
    let memoryEstimate: number | undefined;
    if (nav.deviceMemory) {
      memoryEstimate = nav.deviceMemory;
    } else if ((performance as PerformanceExtended).memory) {
      memoryEstimate = (performance as PerformanceExtended).memory!.jsHeapSizeLimit / 1048576; // Convert to MB
    }

    // Optional geographic region if enabled (coarse-grained only)
    let geographicRegion: string | undefined;
    if (this.options.geolocationEnabled) {
      // Only collect broad region data, not specific coordinates
      if (navigator.language) {
        // Just use language/region preference as a proxy
        geographicRegion = navigator.language.split('-')[1] || navigator.language;
      }
    }

    return {
      sessionId,
      deviceCategory,
      browserCategory,
      viewportWidth,
      viewportHeight,
      startTimestamp: Date.now(),
      connectionType,
      memoryEstimate,
      geographicRegion,
    };
  }

  /**
   * Initialize performance data structure for the current session
   */
  private initializeCurrentSessionData(): void {
    const currentData: SessionPerformanceData = {
      sessionId: this.sessionMetadata.sessionId,
      timestamp: Date.now(),
      metrics: {
        fps: 0,
        memoryUsage: 0,
        cpuUsage: 0,
        resourceUtilization: new Map(),
        renderTime: 0,
        eventProcessingTime: 0,
        interactionLatency: 0,
        loadTimes: {},
        eventCounts: {},
      },
      userInteractions: [],
      errors: [],
    };

    this.performanceData.push(currentData);
  }

  /**
   * Subscribe to system events for performance monitoring
   */
  private subscribeToEvents(): void {
    if (!this.isEnabled) return;

    this.eventSubscription = moduleEventBus.subscribe({
      topic: 'STATUS_CHANGED',
      callback: (event: ModuleEvent) => {
        this.trackEvent(event);

        // Track specific performance-related events
        if (event.moduleId === 'game-loop-manager') {
          if (event.data.type === 'performance_snapshot') {
            this.trackPerformanceSnapshot(event.data);
          }
        }

        // Track errors
        if (event.type === 'ERROR') {
          this.trackError({
            errorType: event.data.type || 'unknown',
            message: (event.data.message as string) || 'Unknown error',
            timestamp: event.timestamp,
            componentId: event.moduleId,
            affectedResource: event.data.resourceType as ResourceType,
          });
        }
      },
    });
  }

  /**
   * Set up tracking for user interactions
   */
  private setupInteractionTracking(): void {
    if (!this.isEnabled) return;

    // Track clicks
    const clickHandler = (e: MouseEvent) => {
      const target = e.target as HTMLElement;
      const componentId = target.getAttribute('data-component-id') || target.id || target.tagName;

      const startTime = performance.now();

      // Add a one-time callback to requestAnimationFrame to measure response time
      requestAnimationFrame(() => {
        const responseTime = performance.now() - startTime;

        this.trackUserInteraction({
          interactionType: 'click',
          targetComponent: componentId,
          timestamp: Date.now(),
          responseTime,
          successful: true,
        });
      });
    };

    document.addEventListener('click', clickHandler);
    this.interactionObservers.set('click', () => {
      document.removeEventListener('click', clickHandler);
    });

    // Track scrolling performance
    let lastScrollTime = 0;
    const scrollTimeouts: number[] = [];

    const scrollHandler = () => {
      const now = performance.now();
      const timeSinceLastScroll = now - lastScrollTime;
      lastScrollTime = now;

      // Only track if it's been a while since the last scroll
      // to avoid tracking every tiny scroll event
      if (timeSinceLastScroll > 500) {
        const scrollTimeout = window.setTimeout(() => {
          this.trackUserInteraction({
            interactionType: 'scroll',
            timestamp: Date.now(),
            responseTime: performance.now() - now,
            successful: true,
          });
        }, 100);

        scrollTimeouts.push(scrollTimeout);
      }
    };

    document.addEventListener('scroll', scrollHandler, { passive: true });
    this.interactionObservers.set('scroll', () => {
      document.removeEventListener('scroll', scrollHandler);
      scrollTimeouts.forEach(clearTimeout);
    });
  }

  /**
   * Start periodic transmission of telemetry data
   */
  private startTransmissionCycle(): void {
    if (!this.isEnabled) return;

    this.transmitInterval = window.setInterval(() => {
      this.transmitTelemetryData();
    }, this.options.transmitIntervalMs);
  }

  /**
   * Track a system event for telemetry
   */
  private trackEvent(event: ModuleEvent): void {
    if (!this.isEnabled) return;

    // Increment event count
    const eventType = `${event.type}:${event.data.type || 'unknown'}`;
    this.accumulatedEventCounts[eventType] = (this.accumulatedEventCounts[eventType] || 0) + 1;

    // For detailed level, track event processing time
    if (this.options.performanceDetailLevel === 'detailed') {
      const startTime = performance.now();

      // Measure how long the event takes to be processed
      // This is a simplification - in reality we would measure actual handler execution
      setTimeout(() => {
        const processingTime = performance.now() - startTime;

        // Update event processing time metric (rolling average)
        const current = this.getCurrentSessionData();
        current.metrics.eventProcessingTime =
          (current.metrics.eventProcessingTime + processingTime) / 2;
      }, 0);
    }
  }

  /**
   * Track a performance snapshot
   */
  private trackPerformanceSnapshot(data: Record<string, unknown>): void {
    if (!this.isEnabled) return;

    const current = this.getCurrentSessionData();

    // Update general metrics
    if (typeof data.fps === 'number') {
      current.metrics.fps = data.fps;
    }

    if (typeof data.memoryUsage === 'number') {
      current.metrics.memoryUsage = data.memoryUsage;
    }

    if (typeof data.cpuUsage === 'number') {
      current.metrics.cpuUsage = data.cpuUsage;
    }

    // Update resource utilization
    if (data.resourceUtilization && typeof data.resourceUtilization === 'object') {
      const utilization = data.resourceUtilization as Record<string, number>;
      for (const [resource, value] of Object.entries(utilization)) {
        current.metrics.resourceUtilization.set(resource as ResourceType, value);
      }
    }

    // Update render time
    if (typeof data.renderTime === 'number') {
      current.metrics.renderTime = data.renderTime;
    }
  }

  /**
   * Register the start of a component load
   */
  public startComponentLoadTimer(componentId: string): void {
    if (!this.isEnabled) return;

    this.componentLoadTimers.set(componentId, performance.now());
  }

  /**
   * Register the completion of a component load
   */
  public endComponentLoadTimer(componentId: string): void {
    if (!this.isEnabled) return;

    const startTime = this.componentLoadTimers.get(componentId);
    if (startTime === undefined) return;

    const loadTime = performance.now() - startTime;
    this.componentLoadTimers.delete(componentId);

    // Update load time metrics
    const current = this.getCurrentSessionData();
    current.metrics.loadTimes[componentId] = loadTime;
  }

  /**
   * Track a user interaction with the system
   */
  public trackUserInteraction(interaction: UserInteractionData): void {
    if (!this.isEnabled) return;

    const current = this.getCurrentSessionData();

    // Add to interactions array
    current.userInteractions.push(interaction);

    // Update average interaction latency metric
    const totalInteractions = current.userInteractions.length;
    const totalLatency = current.userInteractions.reduce(
      (sum, interaction) => sum + interaction.responseTime,
      0
    );

    current.metrics.interactionLatency = totalLatency / totalInteractions;
  }

  /**
   * Track an error that occurred
   */
  public trackError(error: ErrorData): void {
    if (!this.isEnabled) return;

    // Apply error sampling
    if (Math.random() > this.options.errorSamplingRate) {
      return;
    }

    const current = this.getCurrentSessionData();
    current.errors.push(error);
  }

  /**
   * Get the current session's data object
   */
  private getCurrentSessionData(): SessionPerformanceData {
    // If the last entry is older than 5 minutes, create a new entry
    const lastEntry = this.performanceData[this.performanceData.length - 1];
    const fiveMinutesAgo = Date.now() - 5 * 60 * 1000;

    if (lastEntry.timestamp < fiveMinutesAgo) {
      this.initializeCurrentSessionData();
    }

    return this.performanceData[this.performanceData.length - 1];
  }

  /**
   * Transmit collected telemetry data to the server
   */
  private transmitTelemetryData(): void {
    if (!this.isEnabled || this.performanceData.length === 0) return;

    // Update event counts in the current session data
    const current = this.getCurrentSessionData();
    current.metrics.eventCounts = { ...this.accumulatedEventCounts };

    // Reset accumulated counts after transferring
    this.accumulatedEventCounts = {};

    // Prepare data for transmission
    const dataToTransmit = {
      metadata: this.sessionMetadata,
      performanceData: this.performanceData.slice(0, this.options.maxBatchSize),
    };

    // In a real implementation, we would transmit this data to a telemetry server
    // For this implementation, we'll log it
    console.warn('[SessionPerformanceTracker] Transmitting telemetry data:', dataToTransmit);

    // After transmission, remove the transmitted entries except the current one
    if (this.performanceData.length > 1) {
      this.performanceData = this.performanceData.slice(this.options.maxBatchSize);
    }

    // Ensure we always have at least one entry
    if (this.performanceData.length === 0) {
      this.initializeCurrentSessionData();
    }

    this.lastTransmitTime = performance.now();
  }

  /**
   * Force an immediate transmission of telemetry data
   */
  public flushTelemetryData(): void {
    if (!this.isEnabled) return;

    this.transmitTelemetryData();
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    // Transmit any pending data
    if (this.isEnabled) {
      this.flushTelemetryData();
    }

    // Clear transmission interval
    if (this.transmitInterval !== null) {
      clearInterval(this.transmitInterval);
      this.transmitInterval = null;
    }

    // Remove event subscription
    if (this.eventSubscription) {
      this.eventSubscription();
      this.eventSubscription = null;
    }

    // Remove interaction observers
    for (const unsubscribe of this.interactionObservers.values()) {
      unsubscribe();
    }
    this.interactionObservers.clear();

    // Clear data
    this.performanceData = [];
    this.componentLoadTimers.clear();
    this.accumulatedEventCounts = {};

    console.warn(
      `[SessionPerformanceTracker] Cleaned up session: ${this.sessionMetadata.sessionId}`
    );
  }
}

import { ResourceType } from "./../../types/resources/ResourceTypes";
import { SessionPerformanceData, UserInteractionData } from './SessionPerformanceTracker';

/**
 * Interface for user behavior pattern
 */
export interface UserBehaviorPattern {
  patternType:
    | 'frequent_interaction'
    | 'rapid_sequence'
    | 'complex_operation'
    | 'sustained_activity'
    | 'custom';
  description: string;
  frequency: number;
  impactScore: number;
  relatedMetrics: string[];
  confidence: number;
}

/**
 * Interface for behavior-performance correlation result
 */
export interface BehaviorPerformanceCorrelation {
  behaviorMetric: string;
  performanceMetric: string;
  correlationCoefficient: number;
  significance: 'none' | 'weak' | 'moderate' | 'strong';
  sampleSize: number;
  description: string;
}

/**
 * Configuration options for behavior correlation analysis
 */
export interface BehaviorCorrelationConfig {
  minDataPoints: number;
  significanceThreshold: number;
  timeWindowMs: number;
  behaviorMetrics: string[];
  performanceMetrics: string[];
  groupSimilarInteractions: boolean;
}

/**
 * Service that analyzes correlations between user behavior and performance metrics
 */
export class UserBehaviorCorrelationAnalysis {
  private config: BehaviorCorrelationConfig;

  constructor(config?: Partial<BehaviorCorrelationConfig>) {
    // Default configuration
    this.config = {
      minDataPoints: 10,
      significanceThreshold: 0.3,
      timeWindowMs: 60000, // 1 minute
      behaviorMetrics: ['interactionFrequency', 'responseTime', 'interactionType'],
      performanceMetrics: ['fps', 'renderTime', 'memoryUsage', 'cpuUsage'],
      groupSimilarInteractions: true,
      ...config,
    };
  }

  /**
   * Analyze correlation between user behavior and performance metrics
   */
  public analyzeCorrelations(sessions: SessionPerformanceData[]): BehaviorPerformanceCorrelation[] {
    if (!sessions || sessions.length === 0) {
      return [];
    }

    const correlations: BehaviorPerformanceCorrelation[] = [];

    // Extract behavior metrics
    const behaviorMetrics = this.extractBehaviorMetrics(sessions);

    // Extract performance metrics
    const performanceMetrics = this.extractPerformanceMetrics(sessions);

    // Calculate correlations between behavior and performance metrics
    for (const behaviorMetric of Object.keys(behaviorMetrics)) {
      for (const performanceMetric of Object.keys(performanceMetrics)) {
        const behaviorValues = behaviorMetrics[behaviorMetric];
        const performanceValues = performanceMetrics[performanceMetric];

        // Find matching timestamps (within the time window)
        const pairedData: Array<[number, number]> = [];

        behaviorValues.forEach(behaviorPoint => {
          // Find performance points within the time window
          const matchingPoints = performanceValues.filter(
            perfPoint =>
              Math.abs(perfPoint.timestamp - behaviorPoint.timestamp) <=
              this.config.timeWindowMs / 2
          );

          if (matchingPoints.length > 0) {
            // Use the average if multiple matching points
            const avgPerformanceValue =
              matchingPoints.reduce((sum, point) => sum + point.value, 0) / matchingPoints.length;
            pairedData.push([behaviorPoint.value, avgPerformanceValue]);
          }
        });

        // Only calculate correlation if we have enough data points
        if (pairedData.length >= this.config.minDataPoints) {
          const correlation = this.calculatePearsonCorrelation(
            pairedData.map(pair => pair[0]),
            pairedData.map(pair => pair[1])
          );

          // Determine significance
          const significance = this.determineSignificance(correlation);

          // Only include significant correlations
          if (Math.abs(correlation) >= this.config.significanceThreshold) {
            correlations.push({
              behaviorMetric,
              performanceMetric,
              correlationCoefficient: correlation,
              significance,
              sampleSize: pairedData.length,
              description: this.generateCorrelationDescription(
                behaviorMetric,
                performanceMetric,
                correlation
              ),
            });
          }
        }
      }
    }

    return correlations;
  }

  /**
   * Identify user behavior patterns from session data
   */
  public identifyBehaviorPatterns(sessions: SessionPerformanceData[]): UserBehaviorPattern[] {
    if (!sessions || sessions.length === 0) {
      return [];
    }

    const patterns: UserBehaviorPattern[] = [];

    // Aggregate all interactions across sessions
    const allInteractions = sessions.flatMap(session => session.userInteractions);

    // No interactions to analyze
    if (allInteractions.length === 0) {
      return [];
    }

    // Group interactions by type
    const interactionsByType = this.groupInteractionsByType(allInteractions);

    // Analyze frequent interaction patterns
    this.analyzeFrequentInteractions(interactionsByType, patterns);

    // Analyze rapid sequence patterns
    this.analyzeRapidSequences(allInteractions, patterns);

    // Analyze complex operations (sequences of different interaction types)
    this.analyzeComplexOperations(allInteractions, patterns);

    // Analyze sustained activity
    this.analyzeSustainedActivity(sessions, patterns);

    return patterns;
  }

  /**
   * Extract behavior metrics from session data
   */
  private extractBehaviorMetrics(
    sessions: SessionPerformanceData[]
  ): Record<string, Array<{ timestamp: number; value: number }>> {
    const metrics: Record<string, Array<{ timestamp: number; value: number }>> = {};

    // Initialize metrics
    for (const metric of this.config.behaviorMetrics) {
      metrics[metric] = [];
    }

    sessions.forEach(session => {
      // Skip sessions with no interactions
      if (!session.userInteractions || session.userInteractions.length === 0) {
        return;
      }

      // Calculate interaction frequency (interactions per minute)
      if (this.config.behaviorMetrics.includes('interactionFrequency')) {
        const sessionDurationMs =
          Math.max(...session.userInteractions.map(i => i.timestamp)) -
          Math.min(...session.userInteractions.map(i => i.timestamp));

        if (sessionDurationMs > 0) {
          const frequency = (session.userInteractions.length / sessionDurationMs) * 60000;
          metrics['interactionFrequency'].push({
            timestamp: session.timestamp,
            value: frequency,
          });
        }
      }

      // Calculate average response time
      if (this.config.behaviorMetrics.includes('responseTime')) {
        const avgResponseTime =
          session.userInteractions.reduce((sum, interaction) => sum + interaction.responseTime, 0) /
          session.userInteractions.length;

        metrics['responseTime'].push({
          timestamp: session.timestamp,
          value: avgResponseTime,
        });
      }

      // Calculate interaction type frequencies
      if (this.config.behaviorMetrics.includes('interactionType')) {
        const typeCount: Record<string, number> = {};

        session.userInteractions.forEach(interaction => {
          typeCount[interaction.interactionType] =
            (typeCount[interaction.interactionType] || 0) + 1;
        });

        Object.entries(typeCount).forEach(([type, count]) => {
          metrics[`interactionType:${type}`] = metrics[`interactionType:${type}`] || [];
          metrics[`interactionType:${type}`].push({
            timestamp: session.timestamp,
            value: count,
          });
        });
      }
    });

    return metrics;
  }

  /**
   * Extract performance metrics from session data
   */
  private extractPerformanceMetrics(
    sessions: SessionPerformanceData[]
  ): Record<string, Array<{ timestamp: number; value: number }>> {
    const metrics: Record<string, Array<{ timestamp: number; value: number }>> = {};

    // Initialize metrics
    for (const metric of this.config.performanceMetrics) {
      metrics[metric] = [];
    }

    sessions.forEach(session => {
      // Extract standard performance metrics
      if (this.config.performanceMetrics.includes('fps')) {
        metrics['fps'].push({
          timestamp: session.timestamp,
          value: session.metrics.fps,
        });
      }

      if (this.config.performanceMetrics.includes('renderTime')) {
        metrics['renderTime'].push({
          timestamp: session.timestamp,
          value: session.metrics.renderTime,
        });
      }

      if (this.config.performanceMetrics.includes('memoryUsage')) {
        metrics['memoryUsage'].push({
          timestamp: session.timestamp,
          value: session.metrics.memoryUsage,
        });
      }

      if (this.config.performanceMetrics.includes('cpuUsage')) {
        metrics['cpuUsage'].push({
          timestamp: session.timestamp,
          value: session.metrics.cpuUsage,
        });
      }

      if (this.config.performanceMetrics.includes('interactionLatency')) {
        metrics['interactionLatency'].push({
          timestamp: session.timestamp,
          value: session.metrics.interactionLatency,
        });
      }

      // Extract resource utilization metrics
      session.metrics.resourceUtilization.forEach((value, resourceType) => {
        const metricName = `resourceUtilization:${resourceType}`;

        if (this.config.performanceMetrics.includes(metricName)) {
          metrics[metricName] = metrics[metricName] || [];
          metrics[metricName].push({
            timestamp: session.timestamp,
            value,
          });
        }
      });

      // Extract event processing time
      if (this.config.performanceMetrics.includes('eventProcessingTime')) {
        metrics['eventProcessingTime'].push({
          timestamp: session.timestamp,
          value: session.metrics.eventProcessingTime,
        });
      }
    });

    return metrics;
  }

  /**
   * Calculate Pearson correlation coefficient
   */
  private calculatePearsonCorrelation(xValues: number[], yValues: number[]): number {
    if (xValues.length !== yValues.length || xValues.length === 0) {
      return 0;
    }

    const n = xValues.length;

    // Calculate means
    const meanX = xValues.reduce((sum, x) => sum + x, 0) / n;
    const meanY = yValues.reduce((sum, y) => sum + y, 0) / n;

    // Calculate covariance and variances
    let covariance = 0;
    let varianceX = 0;
    let varianceY = 0;

    for (let i = 0; i < n; i++) {
      const diffX = xValues[i] - meanX;
      const diffY = yValues[i] - meanY;

      covariance += diffX * diffY;
      varianceX += diffX * diffX;
      varianceY += diffY * diffY;
    }

    // Avoid division by zero
    if (varianceX === 0 || varianceY === 0) {
      return 0;
    }

    return covariance / Math.sqrt(varianceX * varianceY);
  }

  /**
   * Determine the significance of a correlation coefficient
   */
  private determineSignificance(correlation: number): 'none' | 'weak' | 'moderate' | 'strong' {
    const absCorrelation = Math.abs(correlation);

    if (absCorrelation < 0.3) {
      return 'none';
    } else if (absCorrelation < 0.5) {
      return 'weak';
    } else if (absCorrelation < 0.7) {
      return 'moderate';
    } else {
      return 'strong';
    }
  }

  /**
   * Generate a description for a correlation
   */
  private generateCorrelationDescription(
    behaviorMetric: string,
    performanceMetric: string,
    correlation: number
  ): string {
    const direction = correlation > 0 ? 'positive' : 'negative';
    const strength = this.determineSignificance(correlation);

    let behaviorDescription = behaviorMetric;
    if (behaviorMetric.startsWith('interactionType:')) {
      behaviorDescription = `${behaviorMetric.split(':')[1]} interactions`;
    }

    let performanceDescription = performanceMetric;
    if (performanceMetric.startsWith('resourceUtilization:')) {
      performanceDescription = `${performanceMetric.split(':')[1]} utilization`;
    }

    let impactDescription = '';
    if (direction === 'positive') {
      impactDescription = `As ${behaviorDescription} increases, ${performanceDescription} tends to increase`;
    } else {
      impactDescription = `As ${behaviorDescription} increases, ${performanceDescription} tends to decrease`;
    }

    if (strength === 'none' || strength === 'weak') {
      return `${impactDescription}, but the relationship is ${strength} (${correlation.toFixed(2)}).`;
    } else {
      return `${impactDescription}. This shows a ${strength} ${direction} correlation (${correlation.toFixed(2)}).`;
    }
  }

  /**
   * Group interactions by their type
   */
  private groupInteractionsByType(
    interactions: UserInteractionData[]
  ): Record<string, UserInteractionData[]> {
    const grouped: Record<string, UserInteractionData[]> = {};

    interactions.forEach(interaction => {
      const type = interaction.interactionType;
      grouped[type] = grouped[type] || [];
      grouped[type].push(interaction);
    });

    return grouped;
  }

  /**
   * Analyze frequent interaction patterns
   */
  private analyzeFrequentInteractions(
    interactionsByType: Record<string, UserInteractionData[]>,
    patterns: UserBehaviorPattern[]
  ): void {
    const totalInteractions = Object.values(interactionsByType).flat().length;

    if (totalInteractions === 0) {
      return;
    }

    Object.entries(interactionsByType).forEach(([type, interactions]) => {
      const frequency = interactions.length / totalInteractions;

      if (frequency > 0.2) {
        // If the interaction type makes up more than 20% of all interactions
        patterns.push({
          patternType: 'frequent_interaction',
          description: `Frequent use of ${type} interactions (${Math.round(frequency * 100)}% of all interactions)`,
          frequency,
          impactScore: frequency * 0.8, // Impact score based on frequency
          relatedMetrics: ['interactionFrequency', `interactionType:${type}`],
          confidence: 0.7 + frequency * 0.3, // Higher confidence for more frequent patterns
        });
      }
    });
  }

  /**
   * Analyze rapid sequence patterns (multiple interactions in quick succession)
   */
  private analyzeRapidSequences(
    interactions: UserInteractionData[],
    patterns: UserBehaviorPattern[]
  ): void {
    if (interactions.length < 3) {
      return;
    }

    // Sort interactions by timestamp
    const sortedInteractions = [...interactions].sort((a, b) => a.timestamp - b.timestamp);

    // Look for rapid sequences (interactions within 1 second of each other)
    const rapidSequences: UserInteractionData[][] = [];
    let currentSequence: UserInteractionData[] = [sortedInteractions[0]];

    for (let i = 1; i < sortedInteractions.length; i++) {
      const current = sortedInteractions[i];
      const previous = sortedInteractions[i - 1];

      if (current.timestamp - previous.timestamp <= 1000) {
        currentSequence.push(current);
      } else {
        if (currentSequence.length >= 3) {
          rapidSequences.push([...currentSequence]);
        }
        currentSequence = [current];
      }
    }

    // Add the last sequence if it's rapid
    if (currentSequence.length >= 3) {
      rapidSequences.push(currentSequence);
    }

    // Analyze rapid sequences
    rapidSequences.forEach(sequence => {
      const types = new Set(sequence.map(i => i.interactionType));
      const avgResponseTime =
        sequence.reduce((sum, i) => sum + i.responseTime, 0) / sequence.length;

      patterns.push({
        patternType: 'rapid_sequence',
        description: `Rapid sequence of ${sequence.length} interactions (${Array.from(types).join(', ')})`,
        frequency: sequence.length / interactions.length,
        impactScore: 0.6 + (avgResponseTime > 100 ? 0.3 : 0), // Higher impact if response time is slow
        relatedMetrics: ['responseTime', 'interactionFrequency'],
        confidence: 0.6 + sequence.length / 20, // Higher confidence for longer sequences, max 0.9
      });
    });
  }

  /**
   * Analyze complex operations (sequences of different interaction types)
   */
  private analyzeComplexOperations(
    interactions: UserInteractionData[],
    patterns: UserBehaviorPattern[]
  ): void {
    if (interactions.length < 5) {
      return;
    }

    // Sort interactions by timestamp
    const sortedInteractions = [...interactions].sort((a, b) => a.timestamp - b.timestamp);

    // Look for complex operations (sequences of 5+ interactions with at least 3 different types within 5 seconds)
    const complexOperations: UserInteractionData[][] = [];
    let currentOperation: UserInteractionData[] = [sortedInteractions[0]];

    for (let i = 1; i < sortedInteractions.length; i++) {
      const current = sortedInteractions[i];
      const operationStart = currentOperation[0];

      if (current.timestamp - operationStart.timestamp <= 5000) {
        currentOperation.push(current);
      } else {
        const uniqueTypes = new Set(currentOperation.map(i => i.interactionType));
        if (currentOperation.length >= 5 && uniqueTypes.size >= 3) {
          complexOperations.push([...currentOperation]);
        }
        currentOperation = [current];
      }
    }

    // Add the last operation if it's complex
    const uniqueTypes = new Set(currentOperation.map(i => i.interactionType));
    if (currentOperation.length >= 5 && uniqueTypes.size >= 3) {
      complexOperations.push(currentOperation);
    }

    // Analyze complex operations
    complexOperations.forEach(operation => {
      const types = new Set(operation.map(i => i.interactionType));
      const typeCount = Array.from(types)
        .map(type => `${type} (${operation.filter(i => i.interactionType === type).length})`)
        .join(', ');

      patterns.push({
        patternType: 'complex_operation',
        description: `Complex operation with ${operation.length} interactions: ${typeCount}`,
        frequency: operation.length / interactions.length,
        impactScore: 0.7 + types.size / 10, // Higher impact for more diverse operations
        relatedMetrics: ['responseTime', 'eventProcessingTime', 'cpuUsage'],
        confidence: 0.7,
      });
    });
  }

  /**
   * Analyze sustained activity patterns
   */
  private analyzeSustainedActivity(
    sessions: SessionPerformanceData[],
    patterns: UserBehaviorPattern[]
  ): void {
    // Find sessions with continuous activity for extended periods
    sessions.forEach(session => {
      if (!session.userInteractions || session.userInteractions.length < 10) {
        return;
      }

      // Sort interactions by timestamp
      const sortedInteractions = [...session.userInteractions].sort(
        (a, b) => a.timestamp - b.timestamp
      );

      // Calculate session duration
      const sessionDuration =
        sortedInteractions[sortedInteractions.length - 1].timestamp -
        sortedInteractions[0].timestamp;

      // Calculate average time between interactions
      let totalGap = 0;
      for (let i = 1; i < sortedInteractions.length; i++) {
        totalGap += sortedInteractions[i].timestamp - sortedInteractions[i - 1].timestamp;
      }
      const avgGap = totalGap / (sortedInteractions.length - 1);

      // If session is longer than 5 minutes and has consistent activity
      if (sessionDuration > 300000 && avgGap < 30000) {
        patterns.push({
          patternType: 'sustained_activity',
          description: `Sustained activity for ${Math.round(sessionDuration / 60000)} minutes with ${sortedInteractions.length} interactions`,
          frequency: sortedInteractions.length / sessionDuration,
          impactScore: 0.8,
          relatedMetrics: ['memoryUsage', 'cpuUsage', 'fps'],
          confidence: 0.8,
        });
      }
    });
  }
}

