/**
 * Formats a resource amount for display
 * @param amount The resource amount
 * @param resourceType Optional resource type for specialized formatting
 * @returns Formatted string representation of the amount
 */
export function formatResourceAmount(amount: number, resourceType?: ResourceType): ResourceType {
  // For large numbers, use abbreviations
  if (amount >= 1_000_000) {
    return `${(amount / 1_000_000).toFixed(1)}M`;
  } else if (amount >= 1_000) {
    return `${(amount / 1_000).toFixed(1)}K`;
  } else if (Number.isInteger(amount)) {
    return amount.toString();
  } else {
    return amount.toFixed(1);
  }
}

/**
 * Calculates the production rate per minute
 * @param amountPerTick Amount produced per tick
 * @param ticksPerSecond Number of ticks per second
 * @returns Production rate per minute
 */
export function calculateProductionRatePerMinute(
  amountPerTick: number,
  ticksPerSecond: number
): number {
  return amountPerTick * ticksPerSecond * 60;
}

/**
 * Calculates time until a target amount is reached
 * @param currentAmount Current amount of the resource
 * @param targetAmount Target amount to reach
 * @param productionRate Production rate per second (positive for production, negative for consumption)
 * @returns Time in seconds until target is reached, or Infinity if impossible
 */
export function calculateTimeToTarget(
  currentAmount: number,
  targetAmount: number,
  productionRate: number
): number {
  if (productionRate === 0) {
    return Infinity;
  }

  const difference = targetAmount - currentAmount;

  // If we're already at or past the target
  if ((productionRate > 0 && difference <= 0) || (productionRate < 0 && difference >= 0)) {
    return 0;
  }

  // If we're consuming and trying to reach a higher amount, or
  // if we're producing and trying to reach a lower amount
  if ((productionRate < 0 && difference > 0) || (productionRate > 0 && difference < 0)) {
    return Infinity;
  }

  return Math.abs(difference / productionRate);
}

/**
 * Formats a time duration in seconds to a human-readable string
 * @param seconds Time in seconds
 * @returns Formatted time string
 */
export function formatTimeDuration(seconds: number): string {
  if (!isFinite(seconds)) {
    return 'Never';
  }

  if (seconds < 60) {
    return `${Math.ceil(seconds)}s`;
  } else if (seconds < 3600) {
    return `${Math.ceil(seconds / 60)}m`;
  } else if (seconds < 86400) {
    return `${Math.floor(seconds / 3600)}h ${Math.ceil((seconds % 3600) / 60)}m`;
  } else {
    return `${Math.floor(seconds / 86400)}d ${Math.floor((seconds % 86400) / 3600)}h`;
  }
}

/**
 * Type-Safe State Management
 * 
 * This module provides utilities for creating strongly typed reducers, actions,
 * and state transitions to enhance the type safety of application state management.
 */

import React, { Reducer, useReducer, useMemo, useState, useCallback, useContext } from 'react';

/**
 * Base Action interface that all action types should extend
 * Enforces inclusion of a type property for action discrimination
 */
export interface Action<T extends string = string> {
  type: T;
}

/**
 * Action with payload interface that extends the base Action
 * Provides proper typing for actions that include data
 */
export interface PayloadAction<T extends string, P> extends Action<T> {
  payload: P;
}

/**
 * Helper type to define a discriminated union of action types based on a record
 * Makes it easy to define all the possible action types for a reducer
 */
export type ActionUnion<T extends Record<string, (...args: unknown[]) => Action<string>>> = ReturnType<T[keyof T]>;

/**
 * Type for a function that creates a PayloadAction
 */
export type ActionCreator<T extends string, P> = (payload: P) => PayloadAction<T, P>;

/**
 * Type for a function that creates an Action without payload
 */
export type SimpleActionCreator<T extends string> = () => Action<T>;

/**
 * Creates an action creator for actions with payloads
 * 
 * @param type The action type string
 * @returns An action creator function that takes a payload and returns a properly typed action
 */
export function createAction<T extends string, P>(type: T): ActionCreator<T, P> {
  return (payload: P): PayloadAction<T, P> => ({
    type,
    payload
  });
}

/**
 * Creates an action creator for actions without payloads
 * 
 * @param type The action type string
 * @returns An action creator function that returns a properly typed action
 */
export function createSimpleAction<T extends string>(type: T): SimpleActionCreator<T> {
  return (): Action<T> => ({ type });
}

/**
 * Helper to create a record of action creators from a record of action types
 * 
 * @param actionMap Record of action types mapped to their payload types
 * @returns Record of action creators
 */
export function createActionCreators<
  T extends Record<string, unknown>,
  K extends keyof T = keyof T
>(actionMap: { [P in K]: T[P] extends undefined ? string : [string, T[P]] }) {
  const creators: Record<string, ActionCreator<string, unknown> | SimpleActionCreator<string>> = {};

  for (const key in actionMap) {
    const value = actionMap[key];
    if (typeof value === 'string') {
      creators[key] = createSimpleAction(value);
    } else {
      creators[key] = createAction<string, T[K]>(value[0]);
    }
  }

  return creators as {
    [P in K]: T[P] extends undefined
      ? SimpleActionCreator<string>
      : ActionCreator<string, T[P]>
  };
}

/**
 * Type-safe reducer builder that enforces action type discrimination
 * Ensures that action types are properly typed and matched in the reducer
 */
export class ReducerBuilder<S, A extends Action = Action> {
  private handlers: Partial<Record<A['type'], (state: S, action: unknown) => S>> = {};

  /**
   * Add a handler for a specific action type
   * 
   * @param type The action type to handle
   * @param handler The handler function for this action type
   * @returns The builder for chaining
   */
  addCase<T extends A['type'], AC extends Extract<A, { type: T }>>(
    type: T,
    handler: (state: S, action: AC) => S
  ): ReducerBuilder<S, A> {
    this.handlers[type] = handler as unknown;
    return this;
  }

  /**
   * Add a default handler for unmatched action types
   * 
   * @param handler The default handler function
   * @returns The builder for chaining
   */
  addDefaultCase(handler: (state: S) => S): ReducerBuilder<S, A> {
    this.handlers['DEFAULT'] = (state) => handler(state);
    return this;
  }

  /**
   * Build the reducer function
   * 
   * @returns A properly typed reducer function
   */
  build(): Reducer<S, A> {
    return (state: S, action: A) => {
      const handler = this.handlers[action.type] || this.handlers['DEFAULT'];
      return handler ? handler(state, action) : state;
    };
  }
}

/**
 * Creates a reducer builder for a specific state and action type
 * 
 * @param initialState The initial state (used for type inference)
 * @returns A reducer builder instance
 */
export function createReducer<S, A extends Action = Action>(
  initialState: S
): ReducerBuilder<S, A> {
  return new ReducerBuilder<S, A>();
}

/**
 * Hook to create a state slice with type-safe actions and reducer
 * 
 * @param reducer The reducer function
 * @param initialState The initial state
 * @param actions Object of action creators
 * @returns Tuple of [state, actions with dispatch bound]
 */
export function useTypedReducer<
  S,
  A extends Action,
  AC extends Record<string, (...args: unknown[]) => A>
>(
  reducer: Reducer<S, A>,
  initialState: S,
  actions: AC
): [S, { [K in keyof AC]: (...args: Parameters<AC[K]>) => void }] {
  const [state, dispatch] = useReducer(reducer, initialState);

  const boundActions = useMemo(() => {
    const result: Record<string, unknown> = {};
    for (const key in actions) {
      const actionCreator = actions[key];
      result[key] = (...args: unknown[]) => dispatch(actionCreator(...args));
    }
    return result as { [K in keyof AC]: (...args: Parameters<AC[K]>) => void };
  }, [actions, dispatch]);

  return [state, boundActions];
}

/**
 * Creates a type-safe state slice
 * Combines reducer creation and action binding in one utility
 * 
 * @param initialState The initial state
 * @param reducerMap Map of action type to reducer handlers
 * @param actionMap Map of action creators
 * @returns A hook to use this state slice
 */
export function createTypedStateSlice<
  S,
  AM extends Record<string, (...args: unknown[]) => Action>,
  A extends ReturnType<AM[keyof AM]>
>(
  initialState: S,
  reducerMap: Record<A['type'], (state: S, action: A) => S>,
  actionMap: AM
) {
  // Create the reducer
  const reducer: Reducer<S, A> = (state = initialState, action) => {
    const handler = reducerMap[action.type];
    return handler ? handler(state, action) : state;
  };

  // Return a hook that provides the state and bound actions
  return () => {
    const [state, dispatch] = useReducer(reducer, initialState);

    const boundActions = useMemo(() => {
      const result: Record<string, unknown> = {};
      for (const key in actionMap) {
        const actionCreator = actionMap[key];
        result[key] = (...args: unknown[]) => dispatch(actionCreator(...args));
      }
      return result as { [K in keyof AM]: (...args: Parameters<AM[K]>) => void };
    }, [dispatch]);

    return [state, boundActions] as const;
  };
}

/**
 * Type for a complex state transition function
 * Used for multi-step state transitions that involve side effects
 */
export type StateTransitionFn<S, R = void> = (
  getState: () => S,
  setState: (update: Partial<S> | ((prevState: S) => Partial<S>)) => void
) => Promise<R> | R;

/**
 * Hook to manage complex state transitions in a type-safe way
 * 
 * @param initialState The initial state
 * @returns A tuple of [state, setState, runTransition]
 */
export function useTypedTransitions<S>(initialState: S) {
  const [state, setState] = useState<S>(initialState);

  /**
   * Set state with type checking
   */
  const setTypedState = useCallback((update: Partial<S> | ((prevState: S) => Partial<S>)) => {
    setState(prev => {
      const newParts = typeof update === 'function' ? update(prev) : update;
      return { ...prev, ...newParts };
    });
  }, []);

  /**
   * Run a complex state transition with proper typing
   */
  const runTransition = useCallback(<R = void>(
    transitionFn: StateTransitionFn<S, R>
  ): Promise<R> => {
    const getState = () => state;
    return Promise.resolve(transitionFn(getState, setTypedState));
  }, [state, setTypedState]);

  return [state, setTypedState, runTransition] as const;
}

/**
 * Type-safe selector hook
 * 
 * @param state The state object
 * @param selector (...args: unknown[]) => unknown to select a portion of state
 * @returns The selected state portion
 */
export function useTypedSelector<S, R>(state: S, selector: (state: S) => R): R {
  return useMemo(() => selector(state), [state, selector]);
}

/**
 * Creates a type-safe async reducer
 * Handles loading, error, and success states for async operations
 * 
 * @param actionType Base action type for the async action
 * @param handler (...args: unknown[]) => unknown that processes the action payload
 * @returns An object with action creators and a reducer
 */
export function createAsyncReducer<S, P, R>(
  actionType: string,
  handler: (payload: P) => Promise<R>
) {
  // Define action types
  const PENDING = `${actionType}/pending`;
  const FULFILLED = `${actionType}/fulfilled`;
  const REJECTED = `${actionType}/rejected`;

  // Create action creators
  const actionCreators = {
    pending: createSimpleAction(PENDING),
    fulfilled: createAction<typeof FULFILLED, R>(FULFILLED),
    rejected: createAction<typeof REJECTED, Error>(REJECTED),
    trigger: (payload: P) => async (dispatch: (action: Action) => void) => {
      dispatch(actionCreators.pending());
      try {
        const result = await handler(payload);
        dispatch(actionCreators.fulfilled(result));
        return result;
      } catch (error) {
        dispatch(actionCreators.rejected(error instanceof Error ? error : new Error(String(error))));
        throw error;
      }
    }
  };

  // Create reducer
  type AsyncState = {
    loading: boolean;
    error: Error | null;
    data: R | null;
  };

  type AsyncReducerState = S & AsyncState;

  const reducer = createReducer<AsyncReducerState, Action>(
    { loading: false, error: null, data: null } as AsyncReducerState
  )
    .addCase(PENDING, (state) => ({
      ...state,
      loading: true,
      error: null
    }))
    .addCase(FULFILLED, (state, action) => ({
      ...state,
      loading: false,
      data: action.payload,
      error: null
    }))
    .addCase(REJECTED, (state, action) => ({
      ...state,
      loading: false,
      error: action.payload
    }))
    .build();

  return {
    actions: actionCreators,
    reducer
  };
}

/**
 * Type-safe context state utility
 * Combines the createReducer and useReducer patterns specifically for React contexts
 * 
 * @param reducer The reducer function
 * @param initialState The initial state
 * @returns A context provider and hooks to use the state
 */
export function createTypedContext<
  S,
  A extends Action = Action
>(
  reducer: Reducer<S, A>,
  initialState: S
) {
  const StateContext = React.createContext<S | undefined>(undefined);
  const DispatchContext = React.createContext<React.Dispatch<A> | undefined>(undefined);

  const Provider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [state, dispatch] = useReducer(reducer, initialState);

    return (
      React.createElement(StateContext.Provider, { value: state },
        React.createElement(DispatchContext.Provider, { value: dispatch }, children)
      )
    );
  };

  const useStateContext = () => {
    const context = useContext(StateContext);
    if (context === undefined) {
      throw new Error('useStateContext must be used within a Provider');
    }
    return context;
  };

  const useDispatchContext = () => {
    const context = useContext(DispatchContext);
    if (context === undefined) {
      throw new Error('useDispatchContext must be used within a Provider');
    }
    return context;
  };

  return {
    Provider,
    useStateContext,
    useDispatchContext
  };
}

/**
 * D3 Drag Types
 *
 * This module provides type-safe wrappers and utilities for D3 drag behaviors.
 * It ensures proper typing for drag events, subjects, and related operations
 * while maintaining compatibility with D3's drag behavior API.
 */

import * as d3 from 'd3';
import { SimulationNodeDatum } from './D3Types';

/**
 * Type-safe drag event structure that properly extends D3's drag event
 * with generic type parameters for subject, parent data, and container element
 */
export interface TypedDragEvent<
  Datum extends object,
  ParentDatum extends object = object,
  ContainerElement extends Element = Element,
> extends d3.D3DragEvent<ContainerElement, Datum, ParentDatum> {
  // Additional type-safe properties can be added here
}

/**
 * Type-safe drag behavior configuration
 */
export interface DragBehaviorConfig<
  Datum extends object,
  ParentDatum extends object = object,
  ContainerElement extends Element = Element,
> {
  /**
   * (...args: unknown[]) => unknown called when drag starts
   */
  onDragStart?: (event: TypedDragEvent<Datum, ParentDatum, ContainerElement>) => void;

  /**
   * (...args: unknown[]) => unknown called during dragging
   */
  onDrag?: (event: TypedDragEvent<Datum, ParentDatum, ContainerElement>) => void;

  /**
   * (...args: unknown[]) => unknown called when drag ends
   */
  onDragEnd?: (event: TypedDragEvent<Datum, ParentDatum, ContainerElement>) => void;

  /**
   * Container to which the drag behavior should listen for events
   * Defaults to the window if not specified
   */
  container?: ContainerElement | null;

  /**
   * Filter function to determine if a drag should start
   * Return true to allow the drag, false to prevent it
   */
  filter?: (event: Event, datum: Datum) => boolean;

  /**
   * Whether to enable drag events on touchscreen devices
   * Defaults to true
   */
  touchable?: boolean;
}

/**
 * Creates a type-safe D3 drag behavior
 *
 * @param config Configuration for the drag behavior
 * @returns A properly typed D3 drag behavior
 */
export function createTypedDragBehavior<
  Datum extends object,
  ParentDatum extends object = object,
  ContainerElement extends Element = Element,
>(
  config: DragBehaviorConfig<Datum, ParentDatum, ContainerElement> = {}
): d3.DragBehavior<ContainerElement, Datum, ParentDatum> {
  // Create the drag behavior
  const drag = d3.drag<ContainerElement, Datum, ParentDatum>();

  // Configure drag behavior based on provided config
  if (config.onDragStart) {
    drag.on('start', config.onDragStart);
  }

  if (config.onDrag) {
    drag.on('drag', config.onDrag);
  }

  if (config.onDragEnd) {
    drag.on('end', config.onDragEnd);
  }

  if (config.container) {
    drag.container(() => config.container as ContainerElement);
  }

  if (config.filter) {
    drag.filter((event: unknown, d: Datum) => config.filter!(event, d));
  }

  if (config.touchable !== undefined) {
    drag.touchable(config.touchable);
  }

  return drag;
}

/**
 * Creates a drag behavior specifically for simulation nodes
 * This is particularly useful for flow diagrams or network visualizations
 *
 * @param simulation The D3 force simulation
 * @param config Additional configuration for the drag behavior
 * @returns A properly typed D3 drag behavior for simulation nodes
 */
export function createSimulationDragBehavior<
  NodeDatum extends SimulationNodeDatum,
  ContainerElement extends Element = SVGElement,
>(
  simulation: d3.Simulation<NodeDatum, undefined>,
  config: Partial<DragBehaviorConfig<NodeDatum, object, ContainerElement>> = {}
): d3.DragBehavior<ContainerElement, NodeDatum, object> {
  // Standard drag behavior for force simulations
  const handleDragStart = (event: TypedDragEvent<NodeDatum, object, ContainerElement>) => {
    if (!event.active) simulation.alphaTarget(0.3).restart();
    // Fix the node position during drag
    event.subject.fx = event.subject.x;
    event.subject.fy = event.subject.y;

    // Call custom handler if provided
    if (config.onDragStart) {
      config.onDragStart(event);
    }
  };

  const handleDrag = (event: TypedDragEvent<NodeDatum, object, ContainerElement>) => {
    // Update the fixed position to follow the pointer
    event.subject.fx = event.x;
    event.subject.fy = event.y;

    // Call custom handler if provided
    if (config.onDrag) {
      config.onDrag(event);
    }
  };

  const handleDragEnd = (event: TypedDragEvent<NodeDatum, object, ContainerElement>) => {
    if (!event.active) simulation.alphaTarget(0);
    // Release the fixed position when drag ends (unless configured otherwise)
    event.subject.fx = null;
    event.subject.fy = null;

    // Call custom handler if provided
    if (config.onDragEnd) {
      config.onDragEnd(event);
    }
  };

  // Create the drag behavior with simulation-specific defaults
  return createTypedDragBehavior<NodeDatum, object, ContainerElement>({
    onDragStart: handleDragStart,
    onDrag: handleDrag,
    onDragEnd: handleDragEnd,
    ...config,
  });
}

/**
 * Creates a custom drag behavior for visualization elements that need special handling
 *
 * @param options Custom options for specialized drag behavior
 * @returns A properly typed D3 drag behavior
 */
export function createCustomDragBehavior<
  Datum extends object,
  ParentDatum extends object = object,
  ContainerElement extends Element = Element,
>(
  options: {
    /** Maintain position relative to container */
    constrainToContainer?: boolean;
    /** Snap to grid with specified size */
    snapToGrid?: number;
    /** Only allow horizontal movement */
    horizontalOnly?: boolean;
    /** Only allow vertical movement */
    verticalOnly?: boolean;
    /** Minimum allowed position */
    minPosition?: { x?: number; y?: number };
    /** Maximum allowed position */
    maxPosition?: { x?: number; y?: number };
  } & DragBehaviorConfig<Datum, ParentDatum, ContainerElement>
): d3.DragBehavior<ContainerElement, Datum, ParentDatum> {
  // Wrap the standard drag handlers with custom behavior
  const onDrag = (event: TypedDragEvent<Datum, ParentDatum, ContainerElement>) => {
    let x = event.x;
    let y = event.y;

    // Apply custom constraints
    if (options.snapToGrid) {
      const gridSize = options.snapToGrid;
      x = Math.round(x / gridSize) * gridSize;
      y = Math.round(y / gridSize) * gridSize;
    }

    if (options.horizontalOnly) {
      y = (event.subject as unknown).y;
    }

    if (options.verticalOnly) {
      x = (event.subject as unknown).x;
    }

    if (options.minPosition) {
      if (options.minPosition.x !== undefined) {
        x = Math.max(x, options.minPosition.x);
      }
      if (options.minPosition.y !== undefined) {
        y = Math.max(y, options.minPosition.y);
      }
    }

    if (options.maxPosition) {
      if (options.maxPosition.x !== undefined) {
        x = Math.min(x, options.maxPosition.x);
      }
      if (options.maxPosition.y !== undefined) {
        y = Math.min(y, options.maxPosition.y);
      }
    }

    // Update event coordinates with constrained values
    event.x = x;
    event.y = y;

    // Call the original drag handler if provided
    if (options.onDrag) {
      options.onDrag(event);
    }
  };

  // Create a drag behavior with the wrapped handler
  return createTypedDragBehavior<Datum, ParentDatum, ContainerElement>({
    ...options,
    onDrag: onDrag,
  });
}

/**
 * Helper for applying a typed drag behavior to a D3 selection
 *
 * @param selection The D3 selection to apply the drag behavior to
 * @param dragBehavior The typed drag behavior to apply
 * @returns The selection with drag behavior applied
 */
export function applyDragBehavior<
  GElement extends Element,
  Datum extends object,
  PElement extends Element = Element,
  PDatum extends object = object,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  dragBehavior: d3.DragBehavior<Element, Datum, PDatum>
): d3.Selection<GElement, Datum, PElement, PDatum> {
  return selection.call(dragBehavior as unknown);
}

/**
 * D3 Selection Types
 *
 * This module provides type-safe wrappers and utilities for D3 selections.
 * It ensures proper typing for selection operations, data binding, and DOM manipulations
 * while maintaining compatibility with D3's selection API.
 */

import * as d3 from 'd3';

/**
 * Type-safe selection creator for SVG elements
 *
 * @param selector CSS selector string
 * @returns A properly typed D3 selection
 */
export function selectSvg(
  selector: string
): d3.Selection<SVGSVGElement, unknown, HTMLElement, unknown> {
  return d3.select<SVGSVGElement, unknown>(selector);
}

/**
 * Type-safe selection creator for SVG group elements
 *
 * @param selector CSS selector string
 * @returns A properly typed D3 selection
 */
export function selectGroup(
  selector: string
): d3.Selection<SVGGElement, unknown, HTMLElement, unknown> {
