/**
 * Type-safe data binding for selections
 *
 * @param selection The D3 selection to bind data to
 * @param data Array of data items to bind
 * @param key Data join key function or string
 * @returns A properly typed update selection
 */
export function bindData<GElement extends Element, Datum, PElement extends Element, PDatum>(
  selection: d3.Selection<GElement, PDatum, PElement, unknown>,
  data: Datum[],
  key?: ((datum: Datum, index: number, groups: Datum[]) => string) | string
): d3.Selection<GElement, Datum, PElement, PDatum> {
  return selection.data(data, key as unknown);
}

/**
 * Type-safe join operation for selections
 *
 * @param selection The D3 selection to perform join on
 * @param elementType Tag name for new elements
 * @param enter (...args: unknown[]) => unknown to handle enter selection
 * @param update (...args: unknown[]) => unknown to handle update selection
 * @param exit (...args: unknown[]) => unknown to handle exit selection
 * @returns A merged selection
 */
export function joinElements<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
  NewElement extends Element,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  elementType: string,
  enter?: (
    selection: d3.Selection<d3.EnterElement, Datum, PElement, PDatum>
  ) => d3.Selection<NewElement, Datum, PElement, PDatum>,
  update?: (
    selection: d3.Selection<GElement, Datum, PElement, PDatum>
  ) => d3.Selection<GElement, Datum, PElement, PDatum>,
  exit?: (selection: d3.Selection<GElement, Datum, PElement, PDatum>) => void
): d3.Selection<NewElement | GElement, Datum, PElement, PDatum> {
  return selection.join(elementType, enter as unknown, update, exit) as d3.Selection<
    NewElement | GElement,
    Datum,
    PElement,
    PDatum
  >;
}

/**
 * Type-safe attribute setter for selections
 *
 * @param selection The D3 selection to set attributes on
 * @param attributes Object containing attribute name-value pairs
 * @returns The selection with attributes applied
 */
export function setAttributes<GElement extends Element, Datum, PElement extends Element, PDatum>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  attributes: Record<
    string,
    string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
  >
): d3.Selection<GElement, Datum, PElement, PDatum> {
  Object.entries(attributes).forEach(([key, value]) => {
    if (typeof value === 'function') {
      selection.attr(key, value as (d: Datum, i: number) => string);
    } else {
      selection.attr(key, value as string);
    }
  });

  return selection;
}

/**
 * Type-safe style setter for selections
 *
 * @param selection The D3 selection to set styles on
 * @param styles Object containing style name-value pairs
 * @returns The selection with styles applied
 */
export function setStyles<GElement extends Element, Datum, PElement extends Element, PDatum>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  styles: Record<
    string,
    string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
  >
): d3.Selection<GElement, Datum, PElement, PDatum> {
  Object.entries(styles).forEach(([key, value]) => {
    if (typeof value === 'function') {
      selection.style(key, value as (d: Datum, i: number) => string);
    } else {
      selection.style(key, value as string);
    }
  });

  return selection;
}

/**
 * Type-safe append operation for selections
 *
 * @param selection The D3 selection to append to
 * @param elementType Type of element to append
 * @returns A selection of the newly appended elements
 */
export function appendElement<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
  NewElement extends Element,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  elementType: string
): d3.Selection<NewElement, Datum, PElement, PDatum> {
  return selection.append<NewElement>(elementType);
}

/**
 * Type-safe event handler attachment for selections
 *
 * @param selection The D3 selection to attach event handlers to
 * @param eventType Type of event to listen for
 * @param listener Event listener function
 * @returns The selection with event handler attached
 */
export function addEventHandler<GElement extends Element, Datum, PElement extends Element, PDatum>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  eventType: string,
  listener: (event: Event, d: Datum, i: number, g: GElement[]) => void
): d3.Selection<GElement, Datum, PElement, PDatum> {
  return selection.on(eventType, listener);
}

/**
 * Type-safe transition creator for selections
 *
 * @param selection The D3 selection to create a transition on
 * @param name Optional name for the transition
 * @returns A properly typed transition
 */
export function createTransition<GElement extends Element, Datum, PElement extends Element, PDatum>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  name?: string
): d3.Transition<GElement, Datum, PElement, PDatum> {
  return name ? selection.transition(name) : selection.transition();
}

/**
 * Creates a typed builder for D3 selections to enable fluent chaining of operations
 *
 * @param selection The D3 selection to wrap
 * @returns A builder object with fluent methods
 */
export function createSelectionBuilder<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(selection: d3.Selection<GElement, Datum, PElement, PDatum>) {
  return {
    /**
     * The underlying D3 selection
     */
    selection,

    /**
     * Sets attributes on the selection
     */
    attr(
      key: string,
      value: string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
    ) {
      selection.attr(key, value as unknown);
      return this;
    },

    /**
     * Sets multiple attributes from an object
     */
    attrs(
      attributes: Record<
        string,
        string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
      >
    ) {
      return setAttributes(selection, attributes), this;
    },

    /**
     * Sets a style property on the selection
     */
    style(
      key: string,
      value: string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
    ) {
      selection.style(key, value as unknown);
      return this;
    },

    /**
     * Sets multiple style properties from an object
     */
    styles(
      styles: Record<
        string,
        string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
      >
    ) {
      return setStyles(selection, styles), this;
    },

    /**
     * Sets text content on the selection
     */
    text(value: string | ((d: Datum, i: number) => string)) {
      selection.text(value as unknown);
      return this;
    },

    /**
     * Sets HTML content on the selection
     */
    html(value: string | ((d: Datum, i: number) => string)) {
      selection.html(value as unknown);
      return this;
    },

    /**
     * Appends a new element to each element in the selection
     */
    append<NewElement extends Element>(elementType: string) {
      const newSelection = appendElement<GElement, Datum, PElement, PDatum, NewElement>(
        selection,
        elementType
      );
      return createSelectionBuilder(newSelection);
    },

    /**
     * Adds an event listener to the selection
     */
    on(eventType: string, listener: (event: Event, d: Datum, i: number, g: GElement[]) => void) {
      selection.on(eventType, listener);
      return this;
    },

    /**
     * Creates a transition on the selection
     */
    transition(name?: string) {
      const transition = createTransition(selection, name);
      return createTransitionBuilder(transition);
    },

    /**
     * Filters the selection
     */
    filter(filterFn: (d: Datum, i: number) => boolean) {
      const filtered = selection.filter(filterFn);
      return createSelectionBuilder(filtered);
    },

    /**
     * Binds new data to the selection
     */
    data<NewDatum>(
      data: NewDatum[],
      key?: ((datum: NewDatum, index: number, groups: NewDatum[]) => string) | string
    ) {
      const newSelection = bindData<GElement, NewDatum, PElement, PDatum>(
        selection as unknown,
        data,
        key
      );
      return createSelectionBuilder(newSelection);
    },

    /**
     * Joins data with elements
     */
    join<NewElement extends Element>(
      elementType: string,
      enter?: (
        selection: d3.Selection<d3.EnterElement, Datum, PElement, PDatum>
      ) => d3.Selection<NewElement, Datum, PElement, PDatum>,
      update?: (
        selection: d3.Selection<GElement, Datum, PElement, PDatum>
      ) => d3.Selection<GElement, Datum, PElement, PDatum>,
      exit?: (selection: d3.Selection<GElement, Datum, PElement, PDatum>) => void
    ) {
      const joined = joinElements<GElement, Datum, PElement, PDatum, NewElement>(
        selection,
        elementType,
        enter,
        update,
        exit
      );
      return createSelectionBuilder(joined);
    },

    /**
     * Calls a function with the selection
     */
    call(fn: (selection: d3.Selection<GElement, Datum, PElement, PDatum>) => void) {
      selection.call(fn);
      return this;
    },
  };
}

/**
 * Creates a typed builder for D3 transitions to enable fluent chaining of operations
 *
 * @param transition The D3 transition to wrap
 * @returns A builder object with fluent methods
 */
export function createTransitionBuilder<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(transition: d3.Transition<GElement, Datum, PElement, PDatum>) {
  return {
    /**
     * The underlying D3 transition
     */
    transition,

    /**
     * Sets the duration of the transition
     */
    duration(milliseconds: number) {
      transition.duration(milliseconds);
      return this;
    },

    /**
     * Sets the delay of the transition
     */
    delay(milliseconds: number | ((d: Datum, i: number) => number)) {
      transition.delay(milliseconds as unknown);
      return this;
    },

    /**
     * Sets the easing function of the transition
     */
    ease(easingFn: d3.EasingFn) {
      transition.ease(easingFn);
      return this;
    },

    /**
     * Sets an attribute with a transition
     */
    attr(
      key: string,
      value: string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
    ) {
      transition.attr(key, value as unknown);
      return this;
    },

    /**
     * Sets a style property with a transition
     */
    style(
      key: string,
      value: string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
    ) {
      transition.style(key, value as unknown);
      return this;
    },

    /**
     * Adds an event listener for transition events
     */
    on(eventType: 'start' | 'end' | 'interrupt', listener: (event: Event, d: Datum) => void) {
      transition.on(eventType, listener);
      return this;
    },

    /**
     * Calls a function with the transition
     */
    call(fn: (transition: d3.Transition<GElement, Datum, PElement, PDatum>) => void) {
      transition.call(fn);
      return this;
    },
  };
}

/**
 * Creates a type-safe selection for a new SVG element
 *
 * @param parent The parent element to append the SVG to
 * @param width Width of the SVG
 * @param height Height of the SVG
 * @returns A selection builder for the new SVG
 */
export function createSvg(
  parent: string | d3.Selection<HTMLElement, unknown, HTMLElement, unknown>,
  width: number,
  height: number
) {
  const parentSelection =
    typeof parent === 'string' ? d3.select<HTMLElement, unknown>(parent) : parent;

  const svg = parentSelection
    .append<SVGSVGElement>('svg')
    .attr('width', width)
    .attr('height', height)
    .attr('viewBox', `0 0 ${width} ${height}`)
    .style('overflow', 'visible');

  return createSelectionBuilder(svg);
}

/**
 * Creates a defs element for SVG definitions like markers, patterns, etc.
 *
 * @param svg The SVG element to append defs to
 * @returns A selection of the defs element
 */
export function createDefs(svg: d3.Selection<SVGSVGElement, unknown, HTMLElement, unknown>) {
  return svg.append<SVGDefsElement>('defs');
}

/**
 * Creates a marker definition for arrow heads etc.
 *
 * @param defs The defs element to append the marker to
 * @param id Unique ID for the marker
 * @param options Configuration options for the marker
 * @returns A selection of the marker element
 */
export function createMarker(
  defs: d3.Selection<SVGDefsElement, unknown, HTMLElement, unknown>,
  id: string,
  options: {
    width?: number;
    height?: number;
    refX?: number;
    refY?: number;
    viewBox?: string;
    orient?: string;
    path?: string;
    color?: string;
  } = {}
) {
  const {
    width = 10,
    height = 10,
    refX = 5,
    refY = 5,
    viewBox = '0 0 10 10',
    orient = 'auto',
    path = 'M0,0L10,5L0,10z',
    color = 'black',
  } = options;

  const marker = defs
    .append<SVGMarkerElement>('marker')
    .attr('id', id)
    .attr('markerWidth', width)
    .attr('markerHeight', height)
    .attr('refX', refX)
    .attr('refY', refY)
    .attr('viewBox', viewBox)
    .attr('orient', orient)
    .attr('markerUnits', 'userSpaceOnUse');

  marker.append<SVGPathElement>('path').attr('d', path).attr('fill', color);

  return marker;
}

/**
 * D3 Zoom Types
 *
 * This module provides type-safe wrappers and utilities for D3 zoom behaviors.
 * It ensures proper typing for zoom events, transformations, and related operations
 * while maintaining compatibility with D3's zoom behavior API.
 */

import * as d3 from 'd3';

/**
 * Type-safe zoom event structure that properly extends D3's zoom event
 * with generic type parameters for the container element
 */
export interface TypedZoomEvent<
  Element extends d3.BaseType,
  Datum,
  PElement extends d3.BaseType = d3.BaseType,
  PDatum = any,
> extends d3.D3ZoomEvent<Element, Datum> {
  // Additional type-safe properties can be added here
  sourceEvent: Event;
}

/**
 * Type-safe zoom behavior configuration
 */
export interface ZoomBehaviorConfig<Element extends d3.BaseType, Datum = any> {
  /**
   * (...args: unknown[]) => unknown called when zoom starts
   */
  onZoomStart?: (event: TypedZoomEvent<Element, Datum>) => void;

  /**
   * (...args: unknown[]) => unknown called during zooming
   */
  onZoom?: (event: TypedZoomEvent<Element, Datum>) => void;

  /**
   * (...args: unknown[]) => unknown called when zoom ends
   */
  onZoomEnd?: (event: TypedZoomEvent<Element, Datum>) => void;

  /**
   * Minimum zoom scale factor
   * Defaults to 0.1
   */
  scaleExtentMin?: number;

  /**
   * Maximum zoom scale factor
   * Defaults to 8
   */
  scaleExtentMax?: number;

  /**
   * Filter function to determine if a zoom should be allowed
   * Return true to allow the zoom, false to prevent it
   */
  filter?: (event: Event) => boolean;

  /**
   * Whether to enable wheel zooming
   * Defaults to true
   */
  wheelZoom?: boolean;

  /**
   * Whether to enable double-click to zoom
   * Defaults to true
   */
  dblClickZoom?: boolean;

  /**
   * Duration of transition for zoom events in milliseconds
   * Set to 0 to disable transitions
   * Defaults to 250ms
   */
  transitionDuration?: number;

  /**
   * Initial transform to apply
   */
  initialTransform?: d3.ZoomTransform;
}

/**
 * Creates a type-safe D3 zoom behavior
 *
 * @param config Configuration for the zoom behavior
 * @returns A properly typed D3 zoom behavior
 */
export function createTypedZoomBehavior<Element extends d3.BaseType, Datum = any>(
  config: ZoomBehaviorConfig<Element, Datum> = {}
): d3.ZoomBehavior<Element, Datum> {
  // Create the zoom behavior
  const zoom = d3.zoom<Element, Datum>();

  // Configure zoom behavior based on provided config
  if (config.onZoomStart) {
    zoom.on('start', config.onZoomStart);
  }

  if (config.onZoom) {
    zoom.on('zoom', config.onZoom);
  }

  if (config.onZoomEnd) {
    zoom.on('end', config.onZoomEnd);
  }

  // Set scale extent (min/max zoom level)
  const minScale = config.scaleExtentMin ?? 0.1;
  const maxScale = config.scaleExtentMax ?? 8;
  zoom.scaleExtent([minScale, maxScale]);

  // Apply filter if provided
  if (config.filter) {
    zoom.filter(config.filter);
  }

  // Configure wheel zoom
  if (config.wheelZoom !== undefined) {
    // If wheelZoom is false, we need to filter out wheel events
    if (!config.wheelZoom) {
      const originalFilter = zoom.filter();
      zoom.filter(function (event: unknown) {
        return event.type !== 'wheel' && originalFilter.call(this, event);
      });
    }
  }

  // Configure double-click zoom
  if (config.dblClickZoom !== undefined) {
    zoom.interpolate(config.dblClickZoom ? d3.interpolateZoom : null);
  }

  return zoom;
}

/**
 * Helper to apply a typed zoom behavior to a D3 selection
 *
 * @param selection The D3 selection to apply the zoom behavior to
 * @param zoomBehavior The typed zoom behavior to apply
 * @returns The selection with zoom behavior applied
 */
export function applyZoomBehavior<
  GElement extends d3.BaseType,
  Datum,
  PElement extends d3.BaseType,
  PDatum,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  zoomBehavior: d3.ZoomBehavior<GElement, Datum>
): d3.Selection<GElement, Datum, PElement, PDatum> {
  return selection.call(zoomBehavior);
}

/**
 * Creates a zoom behavior specifically for SVG visualizations
 * This is particularly useful for pan/zoom in diagrams or charts
 *
 * @param config Configuration for the SVG zoom behavior
 * @returns A properly typed D3 zoom behavior for SVG elements
 */
export function createSvgZoomBehavior<Element extends SVGElement = SVGSVGElement, Datum = any>(
  config: ZoomBehaviorConfig<Element, Datum> & {
    /**
     * Target element (usually a group) to transform during zoom
     * If not provided, zooming will apply to the element the zoom behavior is attached to
     */
    targetElement?: d3.Selection<SVGGElement, unknown, any, unknown>;

    /**
     * Whether to enable panning
     * Defaults to true
     */
    enablePan?: boolean;

    /**
     * Whether to constrain panning to prevent the content from leaving the viewport
     * Defaults to false
     */
    constrainPan?: boolean;

    /**
     * Extent of the viewable area [x, y, width, height]
     * Defaults to the element's viewport
     */
    extent?: [[number, number], [number, number]];
  } = {}
): d3.ZoomBehavior<Element, Datum> {
  // Create base zoom behavior
  const zoom = createTypedZoomBehavior<Element, Datum>(config);

  // Configure SVG-specific options
  if (config.enablePan === false) {
    // Disable panning by only allowing scaling transformations
    zoom.on('zoom', (event: unknown) => {
      const transform = event.transform;
      const newTransform = d3.zoomIdentity.scale(transform.k);

      if (config.targetElement) {
        config.targetElement.attr('transform', `scale(${transform.k})`);
      }

      // Call the original zoom handler if provided
      if (config.onZoom) {
        // Create a modified event with the new transform
        const modifiedEvent = Object.assign({}, event, { transform: newTransform });
        config.onZoom(modifiedEvent as TypedZoomEvent<Element, Datum>);
      }
    });
  } else if (config.targetElement) {
    // Apply normal pan/zoom transformation to target element
    zoom.on('zoom', (event: unknown) => {
      config.targetElement!.attr('transform', event.transform);

      // Call the original zoom handler if provided
      if (config.onZoom) {
        config.onZoom(event as TypedZoomEvent<Element, Datum>);
      }
    });
  }

  // Set extent if provided
  if (config.extent) {
    zoom.extent(config.extent);
  }

  // Add constraint handling for panning
  if (config.constrainPan) {
    const originalZoom = zoom.on('zoom');

    zoom.on('zoom', (event: unknown) => {
      // Constrain the transform to prevent content from leaving viewport
      const transform = event.transform;
      const constrainedTransform = constrainTransform(transform, config.extent);

      // Update the event transform with the constrained one
      event.transform = constrainedTransform;

      // Call the original zoom handler
      if (originalZoom) {
        originalZoom.call(this, event);
      }
    });
  }

  return zoom;
}

/**
 * Constrains a zoom transform to keep content within the viewport
 *
 * @param transform The transform to constrain
 * @param extent The extent of the viewable area
 * @returns A constrained transform
 */
function constrainTransform(
  transform: d3.ZoomTransform,
  extent?: [[number, number], [number, number]]
): d3.ZoomTransform {
  if (!extent) return transform;

  const [[x0, y0], [x1, y1]] = extent;
  const width = x1 - x0;
  const height = y1 - y0;

  // Calculate constraints based on scale and extent
  const maxX = width * (1 - transform.k);
  const maxY = height * (1 - transform.k);

  // Create a new transform with constrained values
  return d3.zoomIdentity
    .translate(Math.min(0, Math.max(transform.x, maxX)), Math.min(0, Math.max(transform.y, maxY)))
    .scale(transform.k);
}

/**
 * Creates a zoom behavior for panning and zooming a d3 simulation visualization
 *
 * @param config Configuration for the simulation visualization zoom behavior
 * @returns A properly typed D3 zoom behavior for simulation visualizations
 */
export function createSimulationZoomBehavior<
  Element extends SVGElement = SVGSVGElement,
  Datum = any,
>(
  config: ZoomBehaviorConfig<Element, Datum> & {
    /**
     * The container group that holds all simulation elements
     */
    container: d3.Selection<SVGGElement, unknown, any, unknown>;

    /**
     * (...args: unknown[]) => unknown to update the simulation view after zoom/pan
     */
    updateView?: (transform: d3.ZoomTransform) => void;
  }
): d3.ZoomBehavior<Element, Datum> {
  const { container, updateView, ...zoomConfig } = config;

  // Handler for zoom events
  const handleZoom = (event: TypedZoomEvent<Element, Datum>) => {
    // Apply transform to the container
    container.attr('transform', event.transform.toString());

    // Call custom update function if provided
    if (updateView) {
      updateView(event.transform);
    }

    // Call original handler if provided
    if (config.onZoom) {
      config.onZoom(event);
    }
  };

  // Create the zoom behavior
  return createTypedZoomBehavior<Element, Datum>({
    ...zoomConfig,
    onZoom: handleZoom,
  });
}

/**
 * Helper function to get the initial transform to fit content within a viewport
 *
 * @param width Width of the viewport
 * @param height Height of the viewport
 * @param contentWidth Width of the content to fit
 * @param contentHeight Height of the content to fit
 * @param padding Padding to add around the content (default: 20)
 * @returns ZoomTransform that will fit the content within the viewport
 */
export function getFitToViewportTransform(
  width: number,
  height: number,
  contentWidth: number,
  contentHeight: number,
  padding: number = 20
): d3.ZoomTransform {
  // Calculate scale to fit content
  const scaleX = (width - padding * 2) / contentWidth;
  const scaleY = (height - padding * 2) / contentHeight;
  const scale = Math.min(scaleX, scaleY, 1); // Don't scale up more than 1x

  // Calculate translation to center content
  const translateX = (width - contentWidth * scale) / 2;
  const translateY = (height - contentHeight * scale) / 2;

  return d3.zoomIdentity.translate(translateX, translateY).scale(scale);
}

/**
 * Chart Data Transformation Utilities
 * 
 * This module provides standardized utilities for transforming data
 * for visualization components. It consolidates common patterns for
 * extracting, processing, and formatting chart data.
 */

import { 
  ChartDataRecord,
  ClusterPoint,
  ForecastPoint,
  PredictionPoint,
  ResourceGridCell,
} from '../../types/exploration/AnalysisComponentTypes';
import { 
  AnalysisResult, 
  DataPoint,
  AnalysisType
} from '../../types/exploration/DataAnalysisTypes';
import { ResourceType } from "./../../types/resources/ResourceTypes";

//=============================================================================
// Type Guards
//=============================================================================

/**
 * Type guard to check if a value is a number
 */
export function isNumber(value: unknown): value is number {
  return typeof value === 'number' && !isNaN(value);
}

/**
 * Type guard to check if a value is a string
 */
export function isString(value: unknown): value is string {
  return typeof value === 'string';
}

/**
 * Type guard to check if a value is an array
 */
export function isArray<T = unknown>(value: unknown): value is Array<T> {
  return Array.isArray(value);
}

/**
 * Type guard to check if a value is an object
 */
export function isObject(value: unknown): value is Record<string, unknown> {
  return typeof value === 'object' && value !== null && !Array.isArray(value);
}

//=============================================================================
// Safe Data Extraction
//=============================================================================

/**
 * Safely extracts a number value from an object property
 * @param obj The object to extract from
 * @param key The property key
 * @param defaultValue Default value if property doesn't exist or isn't a number
 */
export function safelyExtractNumber(
  obj: Record<string, unknown> | null | undefined,
  key: string,
  defaultValue = 0
): number {
  if (!obj) return defaultValue;
  const value = obj[key];
  return isNumber(value) ? value : defaultValue;
}

/**
 * Safely extracts a string value from an object property
 * @param obj The object to extract from
 * @param key The property key
 * @param defaultValue Default value if property doesn't exist or isn't a string
 */
export function safelyExtractString(
  obj: Record<string, unknown> | null | undefined,
  key: string,
  defaultValue = ''
): string {
  if (!obj) return defaultValue;
  const value = obj[key];
  return isString(value) ? value : defaultValue;
}

/**
 * Safely extracts an array from an object property
 * @param obj The object to extract from
 * @param key The property key
 * @param defaultValue Default value if property doesn't exist or isn't an array
 */
export function safelyExtractArray<T = unknown>(
  obj: Record<string, unknown> | null | undefined,
  key: string,
  defaultValue: T[] = []
): T[] {
  if (!obj) return defaultValue;
  const value = obj[key];
  return isArray<T>(value) ? value : defaultValue;
}

/**
 * Safely extracts an object from an object property
 * @param obj The object to extract from
 * @param key The property key
 * @param defaultValue Default value if property doesn't exist or isn't an object
 */
export function safelyExtractObject<T extends Record<string, unknown>>(
  obj: Record<string, unknown> | null | undefined,
  key: string,
  defaultValue: T
): T {
  if (!obj) return defaultValue;
  const value = obj[key];
  return isObject(value) ? value as T : defaultValue;
}

/**
 * Safely extracts a property from a nested object structure
 * @param obj The object to extract from
 * @param path Path to the property using dot notation (e.g., 'properties.value')
 * @param defaultValue Default value if path doesn't exist
 */
export function safelyExtractPath<T>(
  obj: Record<string, unknown> | null | undefined,
  path: string,
  defaultValue: T
): T {
  if (!obj) return defaultValue;

  const keys = path.split('.');
  let current: unknown = obj;

  for (const key of keys) {
    if (current === null || current === undefined || typeof current !== 'object') {
      return defaultValue;
    }
    current = current[key];
  }

  return (current !== null && current !== undefined) ? current as T : defaultValue;
}

//=============================================================================
// Domain Calculations
//=============================================================================

/**
 * Calculates the domain (min/max) for a numeric data field
 * @param data Array of data objects
 * @param key Property key to extract
 * @param padding Optional padding percentage (0-1) to add to the domain
 */
export function calculateDomain(
  data: Record<string, unknown>[],
  key: string,
  padding = 0.05
): [number, number] {
  if (!data || data.length === 0) {
    return [0, 1];
  }

  let min = Infinity;
  let max = -Infinity;

  for (const item of data) {
    const value = safelyExtractNumber(item, key, NaN);
    if (!isNaN(value)) {
      min = Math.min(min, value);
      max = Math.max(max, value);
    }
  }

  // Handle edge cases
  if (!isFinite(min) || !isFinite(max) || min === max) {
    return min === max ? [min - 1, max + 1] : [0, 1];
  }

  // Apply padding
  const range = max - min;
  const paddingAmount = range * padding;
  return [min - paddingAmount, max + paddingAmount];
}

/**
 * Calculates multiple domains at once
 * @param data Array of data objects
 * @param keys Object mapping output keys to data property keys
 * @param padding Optional padding percentage
 */
export function calculateDomains(
  data: Record<string, unknown>[],
  keys: Record<string, string>,
  padding = 0.05
): Record<string, [number, number]> {
  const domains: Record<string, [number, number]> = {};

  for (const [outputKey, dataKey] of Object.entries(keys)) {
    domains[outputKey] = calculateDomain(data, dataKey, padding);
  }

  return domains;
}

//=============================================================================
// Color Mapping
//=============================================================================

/**
 * Creates a color scale function that maps numeric values to colors
 * @param domain Min/max value range
 * @param range Array of colors to interpolate between
 */
export function createColorScale(
  domain: [number, number],
  range: string[]
): (value: number) => string {
  return (value: number) => {
    // Normalize value to 0-1 range
    const normalizedValue = Math.max(0, Math.min(1, (value - domain[0]) / (domain[1] - domain[0])));

    // Map to color index
    const index = Math.min(range.length - 1, Math.floor(normalizedValue * range.length));
    return range[index];
  };
}

/**
 * Maps resource types to standard colors
 * @param resourceType Resource type to get color for
 * @param defaultColor Default color if resource type is not recognized
 */
export function getResourceTypeColor(
  resourceType: ResourceType | ResourceType,
  defaultColor = '#888888'
): string {
  const resourceTypeColors: Record<ResourceType, string> = {
    minerals: '#3D85C6', // Blue
    energy: '#F1C232',   // Yellow/gold
    population: '#6AA84F', // Green
    research: '#9FC5E8',  // Light blue
    plasma: '#D5A6BD',   // Purple
    gas: '#C27BA0',      // Pink
    exotic: '#CC0000',   // Red
  };

  return (resourceTypeColors as Record<string, string>)[resourceType] || defaultColor;
}

/**
 * Converts a hex color code to RGB components
 * @param hex Hex color code (e.g., '#FF0000')
 */
export function hexToRgb(hex: string): { r: number; g: number; b: number } {
  // Default to black if invalid hex
  const defaultRgb = { r: 0, g: 0, b: 0 };

  // Remove # if present
  const sanitizedHex = hex.replace(/^#/, '');

  // Handle different hex formats
  if (!/^[0-9A-Fa-f]{3}$|^[0-9A-Fa-f]{6}$/.test(sanitizedHex)) {
    return defaultRgb;
  }

  let r, g, b;

  if (sanitizedHex.length === 3) {
    // Expand shorthand form (e.g. "03F") to full form (e.g. "0033FF")
    r = parseInt(sanitizedHex.charAt(0) + sanitizedHex.charAt(0), 16);
    g = parseInt(sanitizedHex.charAt(1) + sanitizedHex.charAt(1), 16);
    b = parseInt(sanitizedHex.charAt(2) + sanitizedHex.charAt(2), 16);
  } else {
    r = parseInt(sanitizedHex.substring(0, 2), 16);
    g = parseInt(sanitizedHex.substring(2, 4), 16);
    b = parseInt(sanitizedHex.substring(4, 6), 16);
  }

  return { r, g, b };
}

//=============================================================================
// Data Format Transformations
//=============================================================================

/**
 * Transforms cluster data from analysis results
 * @param result Analysis result containing cluster data
 * @param allData All data points
 */
export function transformClusterData(
  result: AnalysisResult,
  allData: ChartDataRecord[]
): {
  clusters: unknown[];
  features: string[];
  clusterPoints: ClusterPoint[];
} {
  if (!isArray(result.data.clusters)) {
    return {
      clusters: [],
      features: [],
      clusterPoints: [],
    };
  }

  const clusters = result.data.clusters as unknown[];
  const features = safelyExtractArray<string>(result.data, 'features', []);
  const clusterPoints: ClusterPoint[] = [];

  clusters.forEach(cluster => {
    const clusterIndex = safelyExtractNumber(cluster, 'cluster', 0);
    const pointIds = safelyExtractArray<string>(cluster, 'pointIds', []);

    pointIds.forEach(pointId => {
      const originalPoint = allData.find(p => p.id === pointId);
      if (!originalPoint) return;

      const featureValues = features.map(feature => {
        const value = safelyExtractPath<unknown>(
          originalPoint, 
          feature, 
          safelyExtractPath<unknown>(originalPoint, `properties.${feature}`, null)
        );

        return isNumber(value) ? value : null;
      });

      clusterPoints.push({
        id: pointId,
        name: safelyExtractString(originalPoint, 'name', `Point-${pointId}`),
        type: safelyExtractString(originalPoint, 'type', 'unknown'),
        cluster: clusterIndex,
        features: featureValues,
        distanceToCentroid: 0, // Default value
        originalPoint: originalPoint as unknown as DataPoint,
      });
    });
  });

  return {
    clusters,
    features,
    clusterPoints,
  };
}

/**
 * Transforms prediction data from analysis results
 * @param result Analysis result containing prediction data
 */
export function transformPredictionData(
  result: AnalysisResult
): {
  predictions: PredictionPoint[];
  forecast: ForecastPoint[];
  model: string;
  targetVariable: string;
  features: string[];
  metrics: unknown;
  modelDetails: unknown;
} {
  // Extract data with type safety
  const predictions = safelyExtractArray<PredictionPoint>(result.data, 'predictions', []);
  const forecast = safelyExtractArray<ForecastPoint>(result.data, 'forecast', []);
  const features = safelyExtractArray<string>(result.data, 'features', []);
  const metrics = safelyExtractObject(result.data, 'metrics', { mse: 0, rmse: 0, mae: 0 });
  const modelDetails = safelyExtractObject(result.data, 'modelDetails', {});
  const model = safelyExtractString(result.data, 'model', 'unknown');
  const targetVariable = safelyExtractString(
    result.config?.parameters || {}, 
    'target', 
    ''
  );

  // Process model details based on model type
  let typedModelDetails: unknown;

  if (model === 'linear') {
    typedModelDetails = {
      coefficients: safelyExtractArray<number>(modelDetails, 'coefficients', [0]),
      weights: safelyExtractArray<number>(modelDetails, 'weights', undefined),
      featureImportance: safelyExtractArray(modelDetails, 'featureImportance', []),
    };
  } else if (model === 'neuralNetwork') {
    const architecture = safelyExtractObject(modelDetails, 'architecture', {
      inputSize: 0,
      hiddenUnits: 0,
      activation: 'relu',
    });

    const training = safelyExtractObject(modelDetails, 'training', {
      epochs: 0,
      learningRate: 0,
      batchSize: 0,
    });

    const normalization = safelyExtractObject(modelDetails, 'normalization', undefined);

    typedModelDetails = {
      architecture: {
        inputSize: safelyExtractNumber(architecture, 'inputSize', 0),
        hiddenUnits: safelyExtractNumber(architecture, 'hiddenUnits', 0),
        activation: safelyExtractString(architecture, 'activation', 'relu'),
      },
      training: {
        epochs: safelyExtractNumber(training, 'epochs', 0),
        learningRate: safelyExtractNumber(training, 'learningRate', 0),
        batchSize: safelyExtractNumber(training, 'batchSize', 0),
      },
      normalization: normalization ? {
        means: safelyExtractArray<number>(normalization, 'means', []),
        stdDevs: safelyExtractArray<number>(normalization, 'stdDevs', []),
      } : undefined,
    };
  } else {
    typedModelDetails = {
      coefficients: [0],
      featureImportance: [],
    };
  }

  return {
    predictions,
    forecast,
    model,
    targetVariable,
    features,
    metrics,
    modelDetails: typedModelDetails,
  };
}

/**
 * Transforms resource mapping data from analysis results
 * @param result Analysis result containing resource mapping data
 */
export function transformResourceMappingData(
  result: AnalysisResult
): {
  resourcePoints: DataPoint[];
  gridCells: ResourceGridCell[];
  resourceTypes: ResourceType[];
  valueMetric: string;
  regionSize: number;
  xRange: [number, number];
  yRange: [number, number];
  density: Record<string, number>;
  insights: string[];
  summary: string;
} {
  // Safely extract all data with proper type checking
  const resourcePoints = safelyExtractArray<DataPoint>(result.data, 'resourcePoints', []);
  const gridCells = safelyExtractArray<ResourceGridCell>(result.data, 'gridCells', []);
  const resourceTypes = safelyExtractArray<ResourceType>(result.data, 'resourceTypes', []);

  const valueMetric = safelyExtractString(
    result.data, 
    'valueMetric', 
    'amount'
  ) as 'amount' | 'quality' | 'accessibility' | 'estimatedValue';

  const regionSize = safelyExtractNumber(result.data, 'regionSize', 1);

  // Ensure ranges are properly formatted
  let xRange: [number, number] = [0, 0];
  let yRange: [number, number] = [0, 0];

  if (
    isArray(result.data.xRange) &&
    result.data.xRange.length === 2 &&
    isNumber(result.data.xRange[0]) &&
    isNumber(result.data.xRange[1])
  ) {
    xRange = result.data.xRange as [number, number];
  }

  if (
    isArray(result.data.yRange) &&
    result.data.yRange.length === 2 &&
    isNumber(result.data.yRange[0]) &&
    isNumber(result.data.yRange[1])
  ) {
    yRange = result.data.yRange as [number, number];
  }

  // Extract remaining data
  const density = safelyExtractObject(result.data, 'density', {});
  const insights = safelyExtractArray<string>(result, 'insights', []);
  const summary = safelyExtractString(result, 'summary', '');

  return {
    resourcePoints,
    gridCells,
    resourceTypes,
    valueMetric,
    regionSize,
    xRange,
    yRange,
    density,
    insights,
    summary,
  };
}

/**
 * Transforms raw data points into scatter plot format
 * @param dataPoints Array of data points with coordinates
 * @param valueMetric Metric to use for point value
 */
export function transformToScatterFormat(
  dataPoints: DataPoint[],
  valueMetric: string = 'amount'
): Array<{
  id: string;
  name: string;
  x: number;
  y: number;
  value: number;
  type: string;
  coordinates: { x: number; y: number };
}> {
  if (!isArray(dataPoints) || dataPoints.length === 0) {
    return [];
  }

  return dataPoints.map(point => {
    // Safely extract properties
    const properties = safelyExtractObject(point, 'properties', {});
    const resourceType = safelyExtractString(properties, 'resourceType', 
      safelyExtractString(properties, 'type', 'unknown')
    );

    const value = safelyExtractNumber(properties, valueMetric, 
      safelyExtractNumber(properties, 'amount', 1)
    );

    const coordinates = safelyExtractObject(point, 'coordinates', { x: 0, y: 0 });

    return {
      id: safelyExtractString(point, 'id', `point-${Math.random().toString(36).substr(2, 9)}`),
      name: safelyExtractString(point, 'name', `Resource ${point.id || 'Unknown'}`),
      x: safelyExtractNumber(coordinates, 'x', 0),
      y: safelyExtractNumber(coordinates, 'y', 0),
      value,
      type: resourceType,
      coordinates: {
        x: safelyExtractNumber(coordinates, 'x', 0),
        y: safelyExtractNumber(coordinates, 'y', 0),
      },
    };
  });
}

/**
 * Transforms resource grid cells into heat map format
 * @param gridCells Array of grid cells
 * @param valueMetric Metric to use for cell value
 * @param selectedResourceType Type of resource to filter by (or 'all')
 */
export function transformToHeatMapFormat(
  gridCells: ResourceGridCell[],
  valueMetric: string = 'amount',
  selectedResourceType: string = 'all'
): Array<{
  x: number;
  y: number;
  value: number;
  resources: Array<{
    type: ResourceType;
    amount: number;
    quality?: number;
    accessibility?: number;
    estimatedValue?: number;
  }>;
}> {
  if (!isArray(gridCells) || gridCells.length === 0) {
    return [];
  }

  return gridCells.map(cell => {
    let value = 0;

    if (selectedResourceType === 'all') {
      value = safelyExtractNumber(cell, 'totalValue', 0);
    } else {
      const resources = safelyExtractArray(cell, 'resources', []);
      const resourceData = resources.find(r => 
        safelyExtractString(r, 'type', '') === selectedResourceType
      );

      if (resourceData) {
        value = safelyExtractNumber(resourceData, valueMetric, 
          safelyExtractNumber(resourceData, 'amount', 0)
        );
      }
    }

    return {
      ...cell,
      value,
    };
  });
}

//=============================================================================
// Filtering utilities
//=============================================================================

/**
 * Type-safe conversion of filter values
 * @param value The value to convert
 * @param operator The filter operator determining the expected type
 */
export function convertFilterValue(
  value: string,
  operator: string
): string | number | boolean | [number, number] {
  // Numeric operators
  if (['greaterThan', 'lessThan', 'equals', 'notEquals'].includes(operator)) {
    // Try to convert to number first
    const num = parseFloat(value);
    if (!isNaN(num)) {
      return num;
    }

    // Handle boolean values
    if (value === 'true' || value === 'false') {
      return value === 'true';
    }
  }

  // Between operator (expects a range)
  if (operator === 'between' && value.includes(',')) {
    const [minStr, maxStr] = value.split(',');
    const min = parseFloat(minStr.trim());
    const max = parseFloat(maxStr.trim());
    if (!isNaN(min) && !isNaN(max)) {
      return [min, max];
    }
  }

  // Default to string
  return value;
}

/**
 * Formats a filter value for display
 * @param value The filter value
 */
export function formatFilterValue(value: string | number | boolean | [number, number]): string {
  if (Array.isArray(value)) {
    return `${value[0]} to ${value[1]}`;
  }
  return String(value);
}

/**
 * Applies filters to a dataset
 * @param data Array of data objects
 * @param filters Array of filter objects
 */
export function applyFilters(
  data: Array<Record<string, unknown>>,
  filters: Array<{
    field: string;
    operator: string;
    value: string | number | boolean | [number, number];
  }>
): Array<Record<string, unknown>> {
  if (!filters || filters.length === 0) {
    return data;
  }

  return data.filter(item => {
    // Apply all filters (AND logic)
    return filters.every(filter => {
      const { field, operator, value } = filter;

      // Extract the field value, supporting dot notation for nested properties
      const fieldValue = safelyExtractPath(item, field, null);

      // Skip invalid values
      if (fieldValue === null || fieldValue === undefined) {
        return false;
      }

      // Apply appropriate comparison based on operator
      switch (operator) {
        case 'equals':
          return fieldValue === value;

        case 'notEquals':
          return fieldValue !== value;

        case 'greaterThan':
          return isNumber(fieldValue) && isNumber(value) && fieldValue > value;

        case 'lessThan':
          return isNumber(fieldValue) && isNumber(value) && fieldValue < value;

        case 'contains':
          return isString(fieldValue) && isString(value) && 
            fieldValue.toLowerCase().includes(value.toLowerCase());

        case 'notContains':
          return isString(fieldValue) && isString(value) && 
            !fieldValue.toLowerCase().includes(value.toLowerCase());

        case 'between':
          return isNumber(fieldValue) && Array.isArray(value) && 
            fieldValue >= value[0] && fieldValue <= value[1];

        default:
          return true;
      }
    });
  });
}

//=============================================================================
// Pagination utilities
//=============================================================================

/**
 * Creates a paginated subset of data
 * @param data The full dataset
 * @param pageSize Number of items per page
 * @param currentPage Current page number (0-based)
 */
export function paginateData<T>(
  data: T[],
  pageSize: number,
  currentPage: number
): {
  items: T[];
  totalItems: number;
  totalPages: number;
  currentPage: number;
  hasNextPage: boolean;
  hasPreviousPage: boolean;
} {
  if (!Array.isArray(data)) {
    return {
      items: [],
      totalItems: 0,
      totalPages: 0,
      currentPage: 0,
      hasNextPage: false,
      hasPreviousPage: false,
    };
  }

  const totalItems = data.length;
  const totalPages = Math.max(1, Math.ceil(totalItems / pageSize));
  const safePage = Math.max(0, Math.min(currentPage, totalPages - 1));

  const startIndex = safePage * pageSize;
  const endIndex = Math.min(startIndex + pageSize, totalItems);

  return {
    items: data.slice(startIndex, endIndex),
    totalItems,
    totalPages,
    currentPage: safePage,
    hasNextPage: safePage < totalPages - 1,
    hasPreviousPage: safePage > 0,
  };
}

