/**
 * Scientific Data Transformation Utilities
 * 
 * This module provides specialized utilities for scientific data transformations
 * used in advanced analysis visualizations like prediction models, clustering, and
 * statistical analysis.
 */

import { safelyExtractArray, safelyExtractNumber, safelyExtractObject } from './chartTransforms';

//=============================================================================
// Time Series Transformations
//=============================================================================

/**
 * Transforms time series data for visualization
 * @param timePoints Array of time points
 * @param actualValues Array of actual values
 * @param predictedValues Array of predicted values (optional)
 * @param forecastValues Array of forecast values (optional)
 */
export function transformTimeSeriesData(
  timePoints: number[] | string[],
  actualValues: number[],
  predictedValues?: number[],
  forecastValues?: number[]
): Array<{
  time: number | string;
  actual?: number;
  predicted?: number;
  forecast?: number;
}> {
  // Create time series data points
  const timeSeriesData: Array<{
    time: number | string;
    actual?: number;
    predicted?: number;
    forecast?: number;
  }> = [];

  // Add actual and predicted values
  const maxActualLength = Math.min(timePoints.length, actualValues.length);
  for (let i = 0; i < maxActualLength; i++) {
    const point: {
      time: number | string;
      actual?: number;
      predicted?: number;
    } = {
      time: timePoints[i],
      actual: actualValues[i],
    };

    if (predictedValues && i < predictedValues.length) {
      point.predicted = predictedValues[i];
    }

    timeSeriesData.push(point);
  }

  // Add forecast values
  if (forecastValues && forecastValues.length > 0) {
    // Calculate the start index for forecast values
    const forecastStartIndex = timeSeriesData.length;
    const forecastTimes = timePoints.slice(forecastStartIndex);

    // Add available forecast times, or generate them if needed
    for (let i = 0; i < forecastValues.length; i++) {
      const time = i < forecastTimes.length 
        ? forecastTimes[i] 
        : generateNextTimePoint(timePoints, forecastStartIndex + i);

      timeSeriesData.push({
        time,
        forecast: forecastValues[i],
      });
    }
  }

  return timeSeriesData;
}

/**
 * Generates a next time point for forecasting based on pattern detection
 * @param existingTimePoints Array of existing time points
 * @param index Index of the new time point
 */
function generateNextTimePoint(
  existingTimePoints: Array<number | string>,
  index: number
): number | string {
  if (existingTimePoints.length < 2) {
    return index; // Default to index if not enough points to detect pattern
  }

  // Check if time points are numeric and follow a pattern
  if (typeof existingTimePoints[0] === 'number' && typeof existingTimePoints[1] === 'number') {
    const firstPoint = existingTimePoints[0] as number;
    const lastPoint = existingTimePoints[existingTimePoints.length - 1] as number;
    const interval = (lastPoint - firstPoint) / (existingTimePoints.length - 1);
    return lastPoint + interval * (index - existingTimePoints.length + 1);
  }

  // Handle date string pattern (assuming ISO format for simplicity)
  if (typeof existingTimePoints[0] === 'string' && typeof existingTimePoints[1] === 'string') {
    try {
      const firstDate = new Date(existingTimePoints[0]);
      const lastDate = new Date(existingTimePoints[existingTimePoints.length - 1]);

      // Calculate interval in milliseconds
      const interval = (lastDate.getTime() - firstDate.getTime()) / (existingTimePoints.length - 1);

      // Calculate next date
      const nextDate = new Date(lastDate.getTime() + interval * (index - existingTimePoints.length + 1));
      return nextDate.toISOString().split('T')[0]; // Return as YYYY-MM-DD
    } catch (e) {
      // If date parsing fails, return string representation of index
      return `Point ${index}`;
    }
  }

  // Default fallback
  return `Point ${index}`;
}

/**
 * Calculates residuals (actual - predicted)
 * @param actualValues Array of actual values
 * @param predictedValues Array of predicted values
 */
export function calculateResiduals(
  actualValues: number[],
  predictedValues: number[]
): number[] {
  // Calculate residuals (actual - predicted)
  const residuals: number[] = [];
  const minLength = Math.min(actualValues.length, predictedValues.length);

  for (let i = 0; i < minLength; i++) {
    residuals.push(actualValues[i] - predictedValues[i]);
  }

  return residuals;
}

//=============================================================================
// Statistical Transformations
//=============================================================================

/**
 * Calculates correlation coefficients between variables
 * @param data Array of data objects
 * @param variables Array of variable names to correlate
 */
export function calculateCorrelationMatrix(
  data: Array<Record<string, unknown>>,
  variables: string[]
): Array<Array<number>> {
  if (!data || data.length === 0 || !variables || variables.length === 0) {
    return [];
  }

  // Extract numeric values for each variable
  const extractedData: Record<string, number[]> = {};

  // Initialize arrays for each variable
  variables.forEach(variable => {
    extractedData[variable] = [];
  });

  // Extract values from data
  data.forEach(item => {
    variables.forEach(variable => {
      const value = safelyExtractNumber(item, variable, NaN);
      if (!isNaN(value)) {
        extractedData[variable].push(value);
      }
    });
  });

  // Create correlation matrix
  const correlationMatrix: number[][] = [];

  // Calculate correlation for each pair of variables
  for (let i = 0; i < variables.length; i++) {
    correlationMatrix[i] = [];
    for (let j = 0; j < variables.length; j++) {
      // Diagonal values are always 1 (self-correlation)
      if (i === j) {
        correlationMatrix[i][j] = 1;
        continue;
      }

      // Get values for variables
      const variable1Values = extractedData[variables[i]];
      const variable2Values = extractedData[variables[j]];

      // Calculate correlation
      const correlation = calculatePearsonCorrelation(variable1Values, variable2Values);
      correlationMatrix[i][j] = correlation;
    }
  }

  return correlationMatrix;
}

/**
 * Calculates Pearson correlation coefficient between two variables
 * @param values1 Array of values for first variable
 * @param values2 Array of values for second variable
 */
function calculatePearsonCorrelation(values1: number[], values2: number[]): number {
  // Calculate valid data points (where both values exist)
  const validPairs: Array<[number, number]> = [];

  const minLength = Math.min(values1.length, values2.length);
  for (let i = 0; i < minLength; i++) {
    if (!isNaN(values1[i]) && !isNaN(values2[i])) {
      validPairs.push([values1[i], values2[i]]);
    }
  }

  // Handle insufficient data
  if (validPairs.length < 2) {
    return NaN;
  }

  // Calculate means
  let sum1 = 0;
  let sum2 = 0;
  for (const [val1, val2] of validPairs) {
    sum1 += val1;
    sum2 += val2;
  }
  const mean1 = sum1 / validPairs.length;
  const mean2 = sum2 / validPairs.length;

  // Calculate correlation coefficient
  let numerator = 0;
  let denominator1 = 0;
  let denominator2 = 0;

  for (const [val1, val2] of validPairs) {
    const diff1 = val1 - mean1;
    const diff2 = val2 - mean2;
    numerator += diff1 * diff2;
    denominator1 += diff1 * diff1;
    denominator2 += diff2 * diff2;
  }

  // Avoid division by zero
  if (denominator1 === 0 || denominator2 === 0) {
    return 0;
  }

  return numerator / Math.sqrt(denominator1 * denominator2);
}

/**
 * Calculates descriptive statistics for a variable
 * @param data Array of data objects
 * @param variable Variable name to analyze
 */
export function calculateStatistics(
  data: Array<Record<string, unknown>>,
  variable: string
): {
  min: number;
  max: number;
  mean: number;
  median: number;
  standardDeviation: number;
  count: number;
  missing: number;
} {
  if (!data || data.length === 0) {
    return {
      min: NaN,
      max: NaN,
      mean: NaN,
      median: NaN,
      standardDeviation: NaN,
      count: 0,
      missing: 0,
    };
  }

  // Extract values and filter out invalid ones
  const values: number[] = [];
  let missing = 0;

  data.forEach(item => {
    const value = safelyExtractNumber(item, variable, NaN);
    if (!isNaN(value)) {
      values.push(value);
    } else {
      missing++;
    }
  });

  // Handle no valid values
  if (values.length === 0) {
    return {
      min: NaN,
      max: NaN,
      mean: NaN,
      median: NaN,
      standardDeviation: NaN,
      count: 0,
      missing,
    };
  }

  // Sort values for min, max, and median calculations
  values.sort((a, b) => a - b);

  const min = values[0];
  const max = values[values.length - 1];

  // Calculate mean
  const sum = values.reduce((acc, val) => acc + val, 0);
  const mean = sum / values.length;

  // Calculate median
  let median: number;
  const middle = Math.floor(values.length / 2);
  if (values.length % 2 === 0) {
    median = (values[middle - 1] + values[middle]) / 2;
  } else {
    median = values[middle];
  }

  // Calculate standard deviation
  const squaredDiffs = values.map(val => Math.pow(val - mean, 2));
  const variance = squaredDiffs.reduce((acc, val) => acc + val, 0) / values.length;
  const standardDeviation = Math.sqrt(variance);

  return {
    min,
    max,
    mean,
    median,
    standardDeviation,
    count: values.length,
    missing,
  };
}

//=============================================================================
// Feature Importance and Model Transformations
//=============================================================================

/**
 * Extracts feature importance from model details
 * @param modelDetails Model details object
 * @param features Array of feature names
 */
export function extractFeatureImportance(
  modelDetails: Record<string, unknown>,
  features: string[]
): Array<{ feature: string; importance: number }> {
  // Try to extract explicit feature importance
  const explicitImportance = safelyExtractArray<{ feature: string; importance: number }>(
    modelDetails,
    'featureImportance',
    []
  );

  if (explicitImportance.length > 0) {
    return explicitImportance;
  }

  // Try to extract coefficients for linear models
  const coefficients = safelyExtractArray<number>(modelDetails, 'coefficients', []);

  if (coefficients.length > 0 && features.length > 0) {
    // Create feature importance from coefficients
    // Use absolute values as importance measure
    return features.map((feature, index) => {
      const coefficient = index < coefficients.length ? Math.abs(coefficients[index]) : 0;
      return {
        feature,
        importance: coefficient,
      };
    }).sort((a, b) => b.importance - a.importance);
  }

  // Try to extract weights for other models
  const weights = safelyExtractArray<number>(modelDetails, 'weights', []);

  if (weights.length > 0 && features.length > 0) {
    // Create feature importance from weights
    return features.map((feature, index) => {
      const weight = index < weights.length ? Math.abs(weights[index]) : 0;
      return {
        feature,
        importance: weight,
      };
    }).sort((a, b) => b.importance - a.importance);
  }

  // Return empty array if no importance data found
  return [];
}

/**
 * Type guard to check if model details are from a linear regression model
 * @param modelDetails Model details object
 */
export function isLinearRegressionModel(
  modelDetails: Record<string, unknown>
): boolean {
  // Check for coefficients which are typical for linear models
  const coefficients = safelyExtractArray(modelDetails, 'coefficients', undefined);
  return Array.isArray(coefficients) && coefficients.length > 0;
}

/**
 * Type guard to check if model details are from a neural network model
 * @param modelDetails Model details object
 */
export function isNeuralNetworkModel(
  modelDetails: Record<string, unknown>
): boolean {
  // Check for architecture which is typical for neural networks
  const architecture = safelyExtractObject(modelDetails, 'architecture', undefined);
  return !!architecture && typeof architecture === 'object';
}

//=============================================================================
// Clustering Transformations
//=============================================================================

/**
 * Calculates cluster centroids from cluster points
 * @param clusterPoints Array of cluster points
 * @param features Array of feature names
 */
export function calculateClusterCentroids(
  clusterPoints: Array<{
    cluster: number;
    features: Array<number | null>;
  }>,
  features: string[]
): Array<{
  cluster: number;
  centroid: number[];
  size: number;
}> {
  if (!clusterPoints || clusterPoints.length === 0 || !features || features.length === 0) {
    return [];
  }

  // Group points by cluster
  const clusters: Record<number, Array<Array<number | null>>> = {};

  clusterPoints.forEach(point => {
    const { cluster, features: pointFeatures } = point;

    if (!clusters[cluster]) {
      clusters[cluster] = [];
    }

    clusters[cluster].push(pointFeatures);
  });

  // Calculate centroids
  const centroids: Array<{
    cluster: number;
    centroid: number[];
    size: number;
  }> = [];

  for (const [clusterStr, points] of Object.entries(clusters)) {
    const cluster = parseInt(clusterStr, 10);
    const numFeatures = features.length;
    const validCounts: number[] = Array(numFeatures).fill(0);
    const sums: number[] = Array(numFeatures).fill(0);

    // Sum values by feature
    points.forEach(pointFeatures => {
      pointFeatures.forEach((value, i) => {
        if (value !== null && !isNaN(value)) {
          sums[i] += value;
          validCounts[i]++;
        }
      });
    });

    // Calculate averages
    const centroid = sums.map((sum, i) => 
      validCounts[i] > 0 ? sum / validCounts[i] : 0
    );

    centroids.push({
      cluster,
      centroid,
      size: points.length,
    });
  }

  return centroids;
}

/**
 * Calculates distances between points and their cluster centroids
 * @param clusterPoints Array of cluster points
 * @param centroids Array of cluster centroids
 */
export function calculateDistancesToCentroids(
  clusterPoints: Array<{
    cluster: number;
    features: Array<number | null>;
  }>,
  centroids: Array<{
    cluster: number;
    centroid: number[];
  }>
): Array<{
  pointIndex: number;
  distance: number;
}> {
  if (!clusterPoints || clusterPoints.length === 0 || !centroids || centroids.length === 0) {
    return [];
  }

  // Map centroids by cluster for quick lookup
  const centroidMap = new Map<number, number[]>();
  centroids.forEach(({ cluster, centroid }) => {
    centroidMap.set(cluster, centroid);
  });

  // Calculate distances
  return clusterPoints
    .map((point, index) => {
      const centroid = centroidMap.get(point.cluster);

      // Skip if centroid not found
      if (!centroid) {
        return {
          pointIndex: index,
          distance: Infinity,
        };
      }

      // Calculate Euclidean distance
      let sum = 0;
      let validDimensions = 0;

      point.features.forEach((value, i) => {
        if (value !== null && !isNaN(value) && i < centroid.length) {
          sum += Math.pow(value - centroid[i], 2);
          validDimensions++;
        }
      });

      // Handle case where no valid dimensions exist
      if (validDimensions === 0) {
        return {
          pointIndex: index,
          distance: Infinity,
        };
      }

      // Normalize by dimensionality for fair comparison
      const distance = Math.sqrt(sum) / Math.sqrt(validDimensions);

      return {
        pointIndex: index,
        distance,
      };
    })
    .filter(item => isFinite(item.distance))
    .sort((a, b) => b.distance - a.distance); // Sort descending (most distant first)
}

import { ResourceType } from "./../../types/resources/ResourceTypes";
import * as React from "react";
import { createContext, ReactNode, useContext, useEffect, useRef, useState } from 'react';
import { ModuleEvent, moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';

/**
 * Interface defining the context type for the EventDispatcher.
 * Provides methods for subscribing to events, emitting events, accessing event history,
 * filtering events, and accessing the latest events by type.
 *
 * @interface EventDispatcherContextType
 * @property {Function} subscribe - Function to subscribe to specific event types
 * @property {Function} emit - Function to emit events to the event bus
 * @property {Function} getHistory - Function to get the complete event history
 * @property {Function} getModuleHistory - Function to get events for a specific module
 * @property {Function} getEventTypeHistory - Function to get events of a specific type
 * @property {Function} clearHistory - Function to clear the event history
 * @property {Function} getFilteredEvents - Function to get events matching a filter function
 * @property {Map<ModuleEventType, ModuleEvent>} latestEvents - Map of the most recent event of each type
 */
interface EventDispatcherContextType {
  // Event subscription
  subscribe: (type: ModuleEventType, listener: (event: ModuleEvent) => void) => () => void;

  // Event emission
  emit: (event: ModuleEvent) => void;

  // Event history
  getHistory: () => ModuleEvent[];
  getModuleHistory: (moduleId: string) => ModuleEvent[];
  getEventTypeHistory: (type: ModuleEventType) => ModuleEvent[];
  clearHistory: () => void;

  // Event filtering
  getFilteredEvents: (filter: (event: ModuleEvent) => boolean) => ModuleEvent[];

  // Latest events by type
  latestEvents: Map<ModuleEventType, ModuleEvent>;
}

/**
 * Props for the EventDispatcherProvider component.
 *
 * @interface EventDispatcherProviderProps
 * @property {ReactNode} children - Child components that will have access to the event context
 * @property {number} [__maxHistorySize=1000] - Maximum number of events to keep in history
 */
interface EventDispatcherProviderProps {
  children: ReactNode;
  /**
   * Maximum number of events to keep in history
   * Controls the maximum size of the event history to prevent excessive memory usage
   */
  __maxHistorySize?: number;
}

/**
 * React Context for the EventDispatcher.
 * Initially set to null and properly initialized in the EventDispatcherProvider.
 */
const EventDispatcherContext = createContext<EventDispatcherContextType | null>(null);

/**
 * Provider component for the event dispatcher system.
 *
 * This component sets up a React context that provides access to the event system
 * throughout the component tree. It handles:
 * - Tracking the latest events by type
 * - Managing history size limits
 * - Subscribing to events from the underlying moduleEventBus
 * - Providing methods to interact with the event system
 *
 * All components that need to work with events should be descendants of this provider.
 *
 * @component
 * @example
 * // In your application's root component
 * import { EventDispatcherProvider } from '../utils/events/EventDispatcher';
 *
 * const App = () => {
 *   return (
 *     <EventDispatcherProvider>
 *       <YourComponents />
 *     </EventDispatcherProvider>
 *   );
 * };
 */
export const EventDispatcherProvider: React.FC<EventDispatcherProviderProps> = ({
  children,
  __maxHistorySize = 1000,
}) => {
  // Store the latest event of each type
  const [latestEvents, setLatestEvents] = useState<Map<ModuleEventType, ModuleEvent>>(new Map());

  // Reference to track the current max history size
  const maxHistorySizeRef = useRef<number>(__maxHistorySize);

  // Update the ref when the prop changes
  useEffect(() => {
    maxHistorySizeRef.current = __maxHistorySize;
    console.warn(`[EventDispatcher] Setting max history size to ${__maxHistorySize}`);

    // Update the moduleEventBus maxHistorySize if possible
    // Note: This is a workaround since we can't directly access the private property
    try {
      // @ts-expect-error - Accessing private property for configuration
      if (moduleEventBus.maxHistorySize !== undefined) {
        // @ts-expect-error - Accessing private property for configuration
        moduleEventBus.maxHistorySize = __maxHistorySize;
        console.warn(
          `[EventDispatcher] Updated moduleEventBus history size to ${__maxHistorySize}`
        );
      }
    } catch (error) {
      console.warn('[EventDispatcher] Could not update moduleEventBus history size:', error);
    }
  }, [__maxHistorySize]);

  // Subscribe to all module events
  useEffect(() => {
    // Create a handler for all events
    const handleEvent = (event: ModuleEvent) => {
      setLatestEvents(prev => {
        const newMap = new Map(prev);
        newMap.set(event.type, event);
        return newMap;
      });
    };

    // Get all possible event types
    const eventTypes: ModuleEventType[] = [
      'MODULE_CREATED',
      'MODULE_ATTACHED',
      'MODULE_DETACHED',
      'MODULE_UPGRADED',
      'MODULE_ACTIVATED',
      'MODULE_DEACTIVATED',
      'ATTACHMENT_STARTED',
      'ATTACHMENT_CANCELLED',
      'ATTACHMENT_COMPLETED',
      'ATTACHMENT_PREVIEW_SHOWN',
      'RESOURCE_PRODUCED',
      'RESOURCE_CONSUMED',
      'RESOURCE_TRANSFERRED',
      'RESOURCE_PRODUCTION_REGISTERED',
      'RESOURCE_PRODUCTION_UNREGISTERED',
      'RESOURCE_CONSUMPTION_REGISTERED',
      'RESOURCE_CONSUMPTION_UNREGISTERED',
      'RESOURCE_FLOW_REGISTERED',
      'RESOURCE_FLOW_UNREGISTERED',
      'RESOURCE_SHORTAGE',
      'AUTOMATION_STARTED',
      'AUTOMATION_STOPPED',
      'AUTOMATION_CYCLE_COMPLETE',
      'STATUS_CHANGED',
      'ERROR_OCCURRED',
      'MISSION_STARTED',
      'MISSION_COMPLETED',
      'MISSION_FAILED',
      'MISSION_PROGRESS_UPDATED',
      'MISSION_REWARD_CLAIMED',
      'SUB_MODULE_CREATED',
      'SUB_MODULE_ATTACHED',
      'SUB_MODULE_DETACHED',
      'SUB_MODULE_UPGRADED',
      'SUB_MODULE_ACTIVATED',
      'SUB_MODULE_DEACTIVATED',
      'SUB_MODULE_EFFECT_APPLIED',
      'SUB_MODULE_EFFECT_REMOVED',
    ];

    // Subscribe to all event types
    const unsubscribers = eventTypes.map(type => moduleEventBus.subscribe(type, handleEvent));

    // Cleanup subscriptions
    return () => {
      unsubscribers.forEach(unsubscribe => {
        if (typeof unsubscribe === 'function') {
          unsubscribe();
        }
      });
    };
  }, []);

  // Context value
  const value: EventDispatcherContextType = {
    // Event subscription - delegate to moduleEventBus
    subscribe: (type, listener) => moduleEventBus.subscribe(type, listener),

    // Event emission - delegate to moduleEventBus
    emit: event => moduleEventBus.emit(event),

    // Event history - delegate to moduleEventBus with size limit
    getHistory: () => {
      const history = moduleEventBus.getHistory();
      // Apply our own size limit if the history is too large
      return history.length > maxHistorySizeRef.current
        ? history.slice(history.length - maxHistorySizeRef.current)
        : history;
    },
    getModuleHistory: moduleId => {
      const history = moduleEventBus.getModuleHistory(moduleId);
      // Apply our own size limit if the history is too large
      return history.length > maxHistorySizeRef.current
        ? history.slice(history.length - maxHistorySizeRef.current)
        : history;
    },
    getEventTypeHistory: type => {
      const history = moduleEventBus.getEventTypeHistory(type);
      // Apply our own size limit if the history is too large
      return history.length > maxHistorySizeRef.current
        ? history.slice(history.length - maxHistorySizeRef.current)
        : history;
    },
    clearHistory: () => moduleEventBus.clearHistory(),

    // Event filtering with size limit
    getFilteredEvents: filter => {
      const history = moduleEventBus.getHistory();
      const filtered = history.filter(filter);
      // Apply our own size limit if the filtered history is too large
      return filtered.length > maxHistorySizeRef.current
        ? filtered.slice(filtered.length - maxHistorySizeRef.current)
        : filtered;
    },

    // Latest events by type
    latestEvents,
  };

  return (
    <EventDispatcherContext.Provider value={value}>{children}</EventDispatcherContext.Provider>
  );
};

/**
 * Hook to access the event dispatcher context.
 *
 * Provides access to all event operations such as subscribing to events,
 * emitting events, accessing event history, and viewing the latest events.
 *
 * @returns {EventDispatcherContextType} The event dispatcher context
 * @throws {Error} If used outside of an EventDispatcherProvider
 *
 * @example
 * const MyComponent = () => {
 *   const { emit, getHistory } = useEventDispatcher();
 *
 *   const handleButtonClick = () => {
 *     emit({
 *       type: 'MODULE_ACTIVATED',
 *       moduleId: 'module-1',
 *       moduleType: 'production',
 *       timestamp: Date.now()
 *     });
 *   };
 *
 *   return (
 *     <div>
 *       <button onClick={handleButtonClick}>Activate Module</button>
 *       <div>Total Events: {getHistory().length}</div>
 *     </div>
 *   );
 * };
 */
export const useEventDispatcher = (): EventDispatcherContextType => {
  const context = useContext(EventDispatcherContext);

  if (!context) {
    throw new Error('useEventDispatcher must be used within an EventDispatcherProvider');
  }

  return context;
};

/**
 * Hook to subscribe to a specific event type.
 *
 * This hook simplifies the process of subscribing to events by automatically
 * handling subscription and cleanup. The callback will be called whenever
 * an event of the specified type is emitted.
 *
 * @template T - The specific event type to subscribe to
 * @param {T} eventType - The event type to subscribe to
 * @param {Function} callback - Function to call when an event of this type occurs
 * @param {React.DependencyList} [deps=[]] - Additional dependencies for the effect
 *
 * @example
 * // Subscribe to MODULE_CREATED events
 * const ModuleTracker = () => {
 *   const [moduleCount, setModuleCount] = useState(0);
 *
 *   useEventSubscription('MODULE_CREATED', (event) => {
 *     setModuleCount(prev => prev + 1);
 *     console.warn(`New module created: ${event.moduleId}`);
 *   });
 *
 *   return <div>Total modules created: {moduleCount}</div>;
 * };
 */
export const useEventSubscription = <T extends ModuleEventType>(
  eventType: T,
  callback: (event: ModuleEvent) => void,
  deps: React.DependencyList = []
): void => {
  const { subscribe } = useEventDispatcher();

  useEffect(() => {
    return subscribe(eventType, callback);
  }, [eventType, callback, subscribe, ...deps]);
};

/**
 * Hook to get the latest event of a specific type.
 *
 * Retrieves the most recent event of the specified type that has been
 * emitted in the system. Returns undefined if no event of that type
 * has been emitted yet.
 *
 * @template T - The specific event type to query
 * @param {T} eventType - The event type to get the latest event for
 * @returns {ModuleEvent | undefined} The latest event of the specified type, or undefined if none exists
 *
 * @example
 * // Display information about the most recent error
 * const ErrorDisplay = () => {
 *   const latestError = useLatestEvent('ERROR_OCCURRED');
 *
 *   if (!latestError) {
 *     return <div>No errors reported</div>;
 *   }
 *
 *   return (
 *     <div className="error-panel">
 *       <h3>Latest Error</h3>
 *       <p>Module: {latestError.moduleId}</p>
 *       <p>Time: {new Date(latestError.timestamp).toLocaleString()}</p>
 *       <p>Message: {latestError.data?.message || 'Unknown error'}</p>
 *     </div>
 *   );
 * };
 */
export const useLatestEvent = <T extends ModuleEventType>(
  eventType: T
): ModuleEvent | undefined => {
  const { latestEvents } = useEventDispatcher();
  return latestEvents.get(eventType);
};

/**
 * Hook to get events that match a filter function.
 *
 * Allows for complex event filtering beyond just module ID or event type.
 * Automatically updates when new events are emitted that match the filter.
 *
 * @param {Function} filter - Filter function that returns true for events to include
 * @param {React.DependencyList} [deps=[]] - Additional dependencies for the effect
 * @returns {ModuleEvent[]} Array of events that match the filter
 *
 * @example
 * // Display all resource shortage events from the last hour
 * const ResourceShortageMonitor = () => {
 *   const oneHourAgo = Date.now() - 3600000;
 *
 *   const recentShortages = useFilteredEvents(
 *     (event) => (
 *       event.type === 'RESOURCE_SHORTAGE' &&
 *       event.timestamp > oneHourAgo
 *     ),
 *     [oneHourAgo] // Update when oneHourAgo changes
 *   );
 *
 *   return (
 *     <div>
 *       <h3>Recent Resource Shortages: {recentShortages.length}</h3>
 *       <ul>
 *         {recentShortages.map(event => (
 *           <li key={event.timestamp}>
 *             {event.data?.resourceType}: {new Date(event.timestamp).toLocaleTimeString()}
 *           </li>
 *         ))}
 *       </ul>
 *     </div>
 *   );
 * };
 */
export const useFilteredEvents = (
  filter: (event: ModuleEvent) => boolean,
  deps: React.DependencyList = []
): ModuleEvent[] => {
  const { getFilteredEvents } = useEventDispatcher();
  const [filteredEvents, setFilteredEvents] = useState<ModuleEvent[]>([]);

  useEffect(() => {
    setFilteredEvents(getFilteredEvents(filter));
  }, [getFilteredEvents, filter, ...deps]);

  return filteredEvents;
};

/**
 * D3 Accessor Benchmark Utility
 *
 * This utility provides benchmarking tools to compare the performance of
 * type-safe accessors versus direct property access in D3 visualizations.
 */

import { d3Accessors, SimulationNodeDatum } from '../../types/visualizations/D3Types';

/**
 * Test case configuration
 */
export interface BenchmarkTestCase {
  /** Name of the test case */
  name: string;
  /** Description of what is being tested */
  description: string;
  /** Function to execute for the test */
  testFn: () => void;
  /** Number of iterations to run */
  iterations?: number;
}

/**
 * Benchmark result
 */
export interface BenchmarkResult {
  /** Name of the test case */
  name: string;
  /** Description of what was tested */
  description: string;
  /** Average execution time in milliseconds */
  averageTimeMs: number;
  /** Median execution time in milliseconds */
  medianTimeMs: number;
  /** Total execution time in milliseconds */
  totalTimeMs: number;
  /** Number of iterations run */
  iterations: number;
  /** Individual execution times */
  executionTimes: number[];
  /** Timestamp when the benchmark was run */
  timestamp: Date;
}

/**
 * Benchmark comparison result
 */
export interface BenchmarkComparison {
  /** Baseline test case name */
  baseline: string;
  /** Comparison test case name */
  comparison: string;
  /** Performance difference as a percentage (positive means slower, negative means faster) */
  percentageDifference: number;
  /** Absolute time difference in milliseconds */
  absoluteDifferenceMs: number;
  /** Whether the comparison is significantly different (>5% difference) */
  isSignificant: boolean;
}

/**
 * Run a single benchmark test
 *
 * @param testCase The test case to run
 * @returns Benchmark result
 */
export function runBenchmark(testCase: BenchmarkTestCase): BenchmarkResult {
  const iterations = testCase.iterations || 1000;
  const executionTimes: number[] = [];

  // Run test iterations
  for (let i = 0; i < iterations; i++) {
    const startTime = performance.now();
    testCase.testFn();
    const endTime = performance.now();
    executionTimes.push(endTime - startTime);
  }

  // Calculate statistics
  const totalTimeMs = executionTimes.reduce((sum, time) => sum + time, 0);
  const averageTimeMs = totalTimeMs / iterations;

  // Calculate median (sort first)
  const sortedTimes = [...executionTimes].sort((a, b) => a - b);
  const medianTimeMs =
    iterations % 2 === 0
      ? (sortedTimes[iterations / 2 - 1] + sortedTimes[iterations / 2]) / 2
      : sortedTimes[Math.floor(iterations / 2)];

  return {
    name: testCase.name,
    description: testCase.description,
    averageTimeMs,
    medianTimeMs,
    totalTimeMs,
    iterations,
    executionTimes,
    timestamp: new Date(),
  };
}

/**
 * Compare two benchmark results
 *
 * @param baseline Baseline benchmark result
 * @param comparison Comparison benchmark result
 * @returns Benchmark comparison
 */
export function compareBenchmarks(
  baseline: BenchmarkResult,
  comparison: BenchmarkResult
): BenchmarkComparison {
  const absoluteDifferenceMs = comparison.averageTimeMs - baseline.averageTimeMs;
  const percentageDifference = (absoluteDifferenceMs / baseline.averageTimeMs) * 100;

  return {
    baseline: baseline.name,
    comparison: comparison.name,
    percentageDifference,
    absoluteDifferenceMs,
    isSignificant: Math.abs(percentageDifference) > 5,
  };
}

/**
 * Format benchmark results for display
 *
 * @param result Benchmark result
 * @returns Formatted string
 */
export function formatBenchmarkResult(result: BenchmarkResult): string {
  return `
Benchmark: ${result.name}
Description: ${result.description}
Iterations: ${result.iterations}
Average Time: ${result.averageTimeMs.toFixed(6)} ms
Median Time: ${result.medianTimeMs.toFixed(6)} ms
Total Time: ${result.totalTimeMs.toFixed(2)} ms
  `.trim();
}

/**
 * Format benchmark comparison for display
 *
 * @param comparison Benchmark comparison
 * @returns Formatted string
 */
export function formatBenchmarkComparison(comparison: BenchmarkComparison): string {
  const differenceDescription =
    comparison.percentageDifference > 0
      ? `${comparison.percentageDifference.toFixed(2)}% slower`
      : `${Math.abs(comparison.percentageDifference).toFixed(2)}% faster`;

  const significance = comparison.isSignificant
    ? 'Significant difference'
    : 'No significant difference';

  return `
Comparison: ${comparison.comparison} vs ${comparison.baseline}
Difference: ${differenceDescription} (${Math.abs(comparison.absoluteDifferenceMs).toFixed(6)} ms)
Assessment: ${significance}
  `.trim();
}

// Simulate typical D3 simulation data
function generateTestNodes(count: number): SimulationNodeDatum[] {
  const nodes: SimulationNodeDatum[] = [];

  for (let i = 0; i < count; i++) {
    nodes.push({
      id: `node-${i}`,
      x: Math.random() * 100,
      y: Math.random() * 100,
      vx: Math.random() * 2 - 1,
      vy: Math.random() * 2 - 1,
    });
  }

  return nodes;
}

/**
 * Run a benchmark comparing type-safe accessors vs direct property access
 *
 * @param nodeCount Number of nodes to use in the test
 * @param iterations Number of iterations to run
 * @returns Benchmark comparison
 */
export function benchmarkAccessors(
  nodeCount: number = 1000,
  iterations: number = 10000
): BenchmarkComparison {
  const nodes = generateTestNodes(nodeCount);

  // Test case for direct property access
  const directAccessTest: BenchmarkTestCase = {
    name: 'Direct Property Access',
    description: `Access x/y coordinates directly on ${nodeCount} nodes`,
    iterations,
    testFn: () => {
      // Simulate typical D3 simulation tick function with direct access
      for (let i = 0; i < nodes.length; i++) {
        const node = nodes[i];
        // Direct property access
        const x = node.x || 0;
        const y = node.y || 0;
        // Do something with coordinates (simulate transform application)
        const transform = `translate(${x}, ${y})`;
      }
    },
  };

  // Test case for type-safe accessor functions
  const safeAccessTest: BenchmarkTestCase = {
    name: 'Type-Safe Accessor Functions',
    description: `Access x/y coordinates via d3Accessors on ${nodeCount} nodes`,
    iterations,
    testFn: () => {
      // Simulate typical D3 simulation tick function with safe accessors
      for (let i = 0; i < nodes.length; i++) {
        const node = nodes[i];
        // Safe accessor functions
        const x = d3Accessors.getX(node);
        const y = d3Accessors.getY(node);
        // Do something with coordinates (simulate transform application)
        const transform = `translate(${x}, ${y})`;
      }
    },
  };

  // Run both benchmarks
  const directResult = runBenchmark(directAccessTest);
  const safeResult = runBenchmark(safeAccessTest);

  // Compare results
  return compareBenchmarks(directResult, safeResult);
}

/**
 * Run a comprehensive set of benchmarks for D3 simulations
 */
export function runComprehensiveBenchmarks(): {
  results: BenchmarkResult[];
  comparisons: BenchmarkComparison[];
} {
  const results: BenchmarkResult[] = [];
  const comparisons: BenchmarkComparison[] = [];

  // Test scenario 1: Basic coordinate access (small dataset)
  const nodes100 = generateTestNodes(100);

  // Direct access test (small)
  const directSmall = runBenchmark({
    name: 'Direct Access (100 nodes)',
    description: 'Direct property access on 100 nodes',
    iterations: 10000,
    testFn: () => {
      for (const node of nodes100) {
        const x = node.x || 0;
        const y = node.y || 0;
        const transform = `translate(${x}, ${y})`;
      }
    },
  });
  results.push(directSmall);

  // Safe access test (small)
  const safeSmall = runBenchmark({
    name: 'Safe Access (100 nodes)',
    description: 'Type-safe accessor functions on 100 nodes',
    iterations: 10000,
    testFn: () => {
      for (const node of nodes100) {
        const x = d3Accessors.getX(node);
        const y = d3Accessors.getY(node);
        const transform = `translate(${x}, ${y})`;
      }
    },
  });
  results.push(safeSmall);

  // Compare small dataset
  comparisons.push(compareBenchmarks(directSmall, safeSmall));

  // Test scenario 2: Basic coordinate access (large dataset)
  const nodes10000 = generateTestNodes(10000);

  // Direct access test (large)
  const directLarge = runBenchmark({
    name: 'Direct Access (10000 nodes)',
    description: 'Direct property access on 10000 nodes',
    iterations: 100,
    testFn: () => {
      for (const node of nodes10000) {
        const x = node.x || 0;
        const y = node.y || 0;
        const transform = `translate(${x}, ${y})`;
      }
    },
  });
  results.push(directLarge);

  // Safe access test (large)
  const safeLarge = runBenchmark({
    name: 'Safe Access (10000 nodes)',
    description: 'Type-safe accessor functions on 10000 nodes',
    iterations: 100,
    testFn: () => {
      for (const node of nodes10000) {
        const x = d3Accessors.getX(node);
        const y = d3Accessors.getY(node);
        const transform = `translate(${x}, ${y})`;
      }
    },
  });
  results.push(safeLarge);

  // Compare large dataset
  comparisons.push(compareBenchmarks(directLarge, safeLarge));

  // Test scenario 3: Simulation tick function (realistic scenario)
  const simulationNodes = generateTestNodes(500);

  // Direct access simulation test
  const directSim = runBenchmark({
    name: 'Direct Access Simulation',
    description: 'Simulation tick function with direct property access',
    iterations: 1000,
    testFn: () => {
      // Simulate a D3 force simulation tick
      for (let i = 0; i < simulationNodes.length; i++) {
        const d = simulationNodes[i];
        // Direct property access with manual null checking
        const x = d.x !== undefined ? d.x : 0;
        const y = d.y !== undefined ? d.y : 0;

        // Apply force simulation logic (simplified)
        const vx = d.vx || 0;
        const vy = d.vy || 0;
        d.x = x + vx * 0.1;
        d.y = y + vy * 0.1;

        // Constrain to bounds
        if (d.x! < 0) d.x = 0;
        if (d.x! > 500) d.x = 500;
        if (d.y! < 0) d.y = 0;
        if (d.y! > 500) d.y = 500;
      }
    },
  });
  results.push(directSim);

  // Safe access simulation test
  const safeSim = runBenchmark({
    name: 'Safe Access Simulation',
    description: 'Simulation tick function with type-safe accessors',
    iterations: 1000,
    testFn: () => {
      // Simulate a D3 force simulation tick
      for (let i = 0; i < simulationNodes.length; i++) {
        const d = simulationNodes[i];
        // Safe accessor functions
        const x = d3Accessors.getX(d);
        const y = d3Accessors.getY(d);

        // Apply force simulation logic (simplified)
        const vx = d.vx || 0;
        const vy = d.vy || 0;
        d.x = x + vx * 0.1;
        d.y = y + vy * 0.1;

        // Constrain to bounds (type-safe version still needs non-null assertion for assignment)
        if (d.x! < 0) d.x = 0;
        if (d.x! > 500) d.x = 500;
        if (d.y! < 0) d.y = 0;
        if (d.y! > 500) d.y = 500;
      }
    },
  });
  results.push(safeSim);

  // Compare simulation tests
  comparisons.push(compareBenchmarks(directSim, safeSim));

  return { results, comparisons };
}

/**
 * Generate a comprehensive benchmark report
 */
export function generateBenchmarkReport(): string {
  const { results, comparisons } = runComprehensiveBenchmarks();

  let report = `
# D3 Accessor Performance Benchmark Report
Generated: ${new Date().toISOString()}

## Summary
This report compares the performance of type-safe accessors versus direct property access
in D3 visualizations across different scenarios and data sizes.

## Results
`;

  // Add individual results
  results.forEach(result => {
    report += `\n### ${result.name}\n`;
    report += formatBenchmarkResult(result) + '\n';
  });

  // Add comparisons
  report += `\n## Comparisons\n`;
  comparisons.forEach(comparison => {
    report += `\n${formatBenchmarkComparison(comparison)}\n`;
  });

  // Add recommendations
  report += `
## Recommendations
`;

  // Determine if type-safe accessors have a significant performance impact
  const hasSignificantImpact = comparisons.some(
    comp => comp.isSignificant && comp.percentageDifference > 0
  );

  if (hasSignificantImpact) {
    report += `
- Type-safe accessors show a measurable performance impact in some scenarios
- Consider implementing optimizations such as memoization for performance-critical sections
- Evaluate usage patterns to determine where direct access might be necessary
`;
  } else {
    report += `
- Type-safe accessors show minimal performance impact across tested scenarios
- Continue using type-safe accessors for improved code safety and maintainability
- No need for extensive optimization at this time
`;
  }

  return report;
}

