/**
 * Converts a string resource type to an enum resource type
 * @param resourceType The string resource type
 * @returns The enum ResourceType
 */
export function toEnumResourceType(resourceType: StringResourceType): ResourceType {
  if (!(resourceType in STRING_TO_ENUM_MAP)) {
    console.warn(`Unknown resource type: ${resourceType}, defaulting to MINERALS`);
    return ResourceType.MINERALS;
  }
  return STRING_TO_ENUM_MAP[resourceType];
}

/**
 * Converts an enum resource type to a string resource type
 * @param resourceType The enum resource type
 * @returns The string resource type
 */
export function toStringResourceType(resourceType: ResourceType): StringResourceType | string {
  if (!(resourceType in ENUM_TO_STRING_MAP)) {
    console.warn(`Unknown resource type: ${resourceType}, defaulting to minerals`);
    return ResourceType.MINERALS;
  }
  return ENUM_TO_STRING_MAP[resourceType];
}

/**
 * Checks if a value is a valid string resource type
 * @param value The value to check
 * @returns Whether the value is a valid string resource type
 */
export function isStringResourceType(value: unknown): value is StringResourceType {
  return typeof value === 'string' && value in STRING_TO_ENUM_MAP;
}

/**
 * Checks if a value is a valid enum resource type
 * @param value The value to check
 * @returns Whether the value is a valid enum resource type
 */
export function isEnumResourceType(value: unknown): value is ResourceType {
  return typeof value === 'string' && Object.values(ResourceType).includes(value as ResourceType);
}

/**
 * Ensures a value is an enum resource type
 * @param resourceType Either a string resource type or an enum resource type
 * @returns The enum ResourceType
 */
export function ensureEnumResourceType(
  resourceType: StringResourceType | ResourceType
): ResourceType {
  // If it's already an enum type, return it
  if (Object.values(ResourceType).includes(resourceType as ResourceType)) {
    return resourceType as ResourceType;
  }

  // Otherwise, convert it
  return toEnumResourceType(resourceType as StringResourceType);
}

/**
 * Ensures a value is a string resource type
 * @param value The value to ensure is a string resource type
 * @returns The string resource type
 * @throws Error if the value cannot be converted to a string resource type
 */
export function ensureStringResourceType(value: unknown): StringResourceType | string {
  if (isStringResourceType(value)) {
    return value;
  }

  if (isEnumResourceType(value)) {
    return toStringResourceType(value);
  }

  console.warn(`Unknown resource type: ${value}, defaulting to minerals`);
  return ResourceType.MINERALS;
}

/**
 * Converts an array of items with resource types from string to enum
 * @param array The array to convert
 * @param converter The conversion function
 * @returns The converted array
 */
export function convertArrayResourceTypes<T>(array: T[], converter: (item: T) => T): T[] {
  return array.map(converter);
}

/**
 * Converts a record with string resource type keys to enum resource type keys
 * @param record The record to convert
 * @returns The converted record
 */
export function convertRecordResourceTypes<T>(
  record: Record<StringResourceType, T>
): Record<ResourceType, T> {
  const result: Record<ResourceType, T> = {} as Record<ResourceType, T>;
  for (const [key, value] of Object.entries(record)) {
    if (isStringResourceType(key)) {
      result[toEnumResourceType(key)] = value;
    }
  }
  return result;
}

/**
 * Converts a map with string resource type keys to enum resource type keys
 * @param map The map to convert
 * @returns The converted map
 */
export function convertMapResourceTypes<T>(map: Map<StringResourceType, T>): Map<ResourceType, T> {
  const result = new Map<ResourceType, T>();
  // Use Array.from to avoid iterator issues
  Array.from(map.entries()).forEach(([key, value]) => {
    result.set(toEnumResourceType(key), value);
  });
  return result;
}

/**
 * Analyzes a file for resource type migration needs
 * @param fileContent The file content to analyze
 * @returns Analysis results
 */
export function analyzeMigrationNeeds(fileContent: string): {
  stringResourceTypes: number;
  enumResourceTypes: number;
  mixedUsage: boolean;
} {
  const stringMatches = (
    fileContent.match(
      /['"]minerals['"]|['"]energy['"]|['"]population['"]|['"]research['"]|['"]plasma['"]|['"]gas['"]|['"]exotic['"]/g
    ) || []
  ).length;
  const enumMatches = (
    fileContent.match(
      /ResourceType\.MINERALS|ResourceType\.ENERGY|ResourceType\.POPULATION|ResourceType\.RESEARCH|ResourceType\.PLASMA|ResourceType\.GAS|ResourceType\.EXOTIC/g
    ) || []
  ).length;

  return {
    stringResourceTypes: stringMatches,
    enumResourceTypes: enumMatches,
    mixedUsage: stringMatches > 0 && enumMatches > 0,
  };
}

/**
 * Applies migration to a file content
 * @param fileContent The file content to migrate
 * @returns The migrated file content
 */
export function applyMigration(fileContent: string): string {
  let result = fileContent;

  // Replace string literals with enum values
  result = result.replace(/['"]minerals['"]/g, 'ResourceType.MINERALS');
  result = result.replace(/['"]energy['"]/g, 'ResourceType.ENERGY');
  result = result.replace(/['"]population['"]/g, 'ResourceType.POPULATION');
  result = result.replace(/['"]research['"]/g, 'ResourceType.RESEARCH');
  result = result.replace(/['"]plasma['"]/g, 'ResourceType.PLASMA');
  result = result.replace(/['"]gas['"]/g, 'ResourceType.GAS');
  result = result.replace(/['"]exotic['"]/g, 'ResourceType.EXOTIC');

  return result;
}

/**
 * Checks if a file needs migration
 * @param fileContent The file content to check
 * @returns Whether the file needs migration
 */
export function needsMigration(fileContent: string): boolean {
  const { stringResourceTypes, enumResourceTypes } = analyzeMigrationNeeds(fileContent);
  return stringResourceTypes > 0 && enumResourceTypes === 0;
}

/**
 * Creates a compatibility layer for functions that need to work with both string and enum resource types
 * @param original The original function
 * @param parameterIndices The indices of parameters to convert
 * @param convertToEnum Whether to convert to enum (true) or string (false)
 * @returns The wrapped function
 */
export function createResourceTypeCompatibilityLayer<T extends (...args: unknown[]) => unknown>(
  original: T,
  parameterIndices: number[],
  convertToEnum: boolean
): T {
  return ((...args: unknown[]) => {
    const newArgs = [...args];
    for (const index of parameterIndices) {
      if (index >= args.length) continue;

      if (convertToEnum) {
        newArgs[index] = ensureEnumResourceType(args[index] as StringResourceType | ResourceType);
      } else {
        newArgs[index] = ensureStringResourceType(args[index]);
      }
    }
    return original(...newArgs);
  }) as T;
}

/**
 * Migrates object keys from string resource types to enum resource types
 * @param obj The object to migrate
 * @returns The migrated object
 */
export function migrateObjectKeys<T>(obj: Record<StringResourceType, T>): Record<ResourceType, T> {
  const result: Partial<Record<ResourceType, T>> = {};

  for (const [key, value] of Object.entries(obj)) {
    if (isStringResourceType(key)) {
      result[toEnumResourceType(key as StringResourceType)] = value;
    }
  }

  return result as Record<ResourceType, T>;
}

/**
 * Migrates resource types in an array of objects
 * @param arr The array to migrate
 * @param propertyName The property name containing the resource type
 * @returns The migrated array
 */
export function migrateArrayResourceTypes<T extends Record<string, unknown>>(
  arr: T[],
  propertyName: string = 'type'
): T[] {
  return arr.map(item => {
    if (
      propertyName in item &&
      typeof item[propertyName] === 'string' &&
      isStringResourceType(item[propertyName] as string)
    ) {
      return {
        ...item,
        [propertyName]: toEnumResourceType(item[propertyName] as StringResourceType),
      };
    }
    return item;
  });
}

import {
  AreaEffect,
  DamageEffect,
  StatusEffect,
  WeaponEffect,
  WeaponEffectType,
} from '../../effects/types_effects/WeaponEffects';
import { Effect } from '../../types/core/GameTypes';
import { ResourceType } from '../../types/resources/ResourceTypes';
import { WeaponCategory, WeaponSystem } from '../../types/weapons/WeaponTypes';

interface WeaponLike {
  id: string;
  type: WeaponCategory | string;
  damage?: number;
  cooldown: number;
  displayName?: string;
}

/**
 * Common ship ability interface for standardizing ship special abilities
 *
 * This interface will be used in future implementations to:
 * 1. Standardize the structure of ship special abilities across different ship classes
 * 2. Enable ability sharing and inheritance between related ship types
 * 3. Support the upcoming ship ability customization system
 * 4. Provide consistent ability parameters for UI display and tooltips
 * 5. Facilitate ability cooldown and activation state tracking
 *
 * Properties:
 * - id: Unique identifier for the ability
 * - name: Display name of the ability
 * - type: Category of ability (offensive, defensive, utility, etc.)
 * - cooldown: Time in seconds before the ability can be used again
 * - damage: Base damage value for offensive abilities
 */

interface CommonShipAbility {
  id?: string;
  name: string;
  type?: string;
  cooldown: number;
  damage?: number;
}

/**
 * Creates a weapon-like object from parameters
 */
export function createWeaponLike(params: {
  id: string;
  type: WeaponCategory | string;
  damage?: number;
  cooldown: number;
  displayName?: string;
}): WeaponLike {
  return params;
}

/**
 * Converts a HangarWeaponSystem to WeaponLike
 */
export function convertToWeaponLike(weapon: WeaponSystem & { name?: string }): WeaponLike {
  return {
    id: weapon.id,
    type: weapon.type,
    damage: weapon.damage,
    cooldown: weapon.cooldown,
    displayName: 'name' in weapon ? weapon.name : undefined,
  };
}

/**
 * Creates a base weapon effect
 */
export function createBaseWeaponEffect(params: {
  id: string;
  type: 'damage' | 'area' | 'status';
  magnitude: number;
  duration: number;
  strength: number;
  name?: string;
  description?: string;
}): WeaponEffect {
  return {
    id: params.id,
    type: params.type,
    magnitude: params.magnitude,
    duration: params.duration,
    strength: params.strength,
    name: params.name || params.id,
    description: params.description || `${params.type} effect with magnitude ${params.magnitude}`,
  };
}

/**
 * Creates a damage effect with the specified parameters
 */
export function createDamageEffect(params: {
  id: string;
  magnitude: number;
  duration: number;
  strength: number;
  damageType: 'physical' | ResourceType.ENERGY | 'explosive';
  penetration: number;
  name?: string;
  description?: string;
}): DamageEffect {
  const baseEffect = createBaseWeaponEffect({
    id: params.id,
    type: 'damage',
    magnitude: params.magnitude,
    duration: params.duration,
    strength: params.strength,
    name: params.name,
    description: params.description,
  });

  return {
    ...baseEffect,
    type: 'damage' as const,
    damageType: params.damageType,
    penetration: params.penetration,
  };
}

/**
 * Creates an area effect with the specified parameters
 */
export function createAreaEffect(params: {
  id: string;
  magnitude: number;
  duration: number;
  strength: number;
  radius: number;
  falloff: number;
  name?: string;
  description?: string;
}): AreaEffect {
  const baseEffect = createBaseWeaponEffect({
    id: params.id,
    type: 'area',
    magnitude: params.magnitude,
    duration: params.duration,
    strength: params.strength,
    name: params.name,
    description: params.description,
  });

  return {
    ...baseEffect,
    type: 'area' as const,
    radius: params.radius,
    falloff: params.falloff,
  };
}

/**
 * Creates a status effect with the specified parameters
 */
export function createStatusEffect(params: {
  id: string;
  magnitude: number;
  duration: number;
  strength: number;
  statusType: 'burn' | 'emp' | 'slow' | 'stun';
  name?: string;
  description?: string;
}): StatusEffect {
  const baseEffect = createBaseWeaponEffect({
    id: params.id,
    type: 'status',
    magnitude: params.magnitude,
    duration: params.duration,
    strength: params.strength,
    name: params.name,
    description: params.description,
  });

  return {
    ...baseEffect,
    type: 'status' as const,
    statusType: params.statusType,
  };
}

/**
 * Creates a weapon effect from a weapon system or ability
 */
export function createWeaponEffect(source: WeaponLike): WeaponEffectType {
  return createDamageEffect({
    id: `${source.id}-effect`,
    magnitude: source.damage || 0,
    duration: source.cooldown,
    strength: source.damage || 0,
    damageType: 'physical',
    penetration: 0,
    name: source.displayName || `${source.type} Effect`,
    description: `Effect from ${source.displayName || source.type}`,
  });
}

/**
 * Creates a weapon effect with custom parameters
 */
export function createCustomWeaponEffect(params: {
  id: string;
  type: 'damage' | 'area' | 'status';
  magnitude: number;
  duration: number;
  strength: number;
  damageType?: 'physical' | ResourceType.ENERGY | 'explosive';
  penetration?: number;
  radius?: number;
  falloff?: number;
  statusType?: 'burn' | 'emp' | 'slow' | 'stun';
  name?: string;
  description?: string;
}): WeaponEffectType {
  switch (params.type) {
    case 'damage':
      return createDamageEffect({
        id: params.id,
        magnitude: params.magnitude,
        duration: params.duration,
        strength: params.strength,
        damageType: params.damageType || 'physical',
        penetration: params.penetration || 0,
        name: params.name,
        description: params.description,
      });
    case 'area':
      return createAreaEffect({
        id: params.id,
        magnitude: params.magnitude,
        duration: params.duration,
        strength: params.strength,
        radius: params.radius || 0,
        falloff: params.falloff || 0,
        name: params.name,
        description: params.description,
      });
    case 'status':
      return createStatusEffect({
        id: params.id,
        magnitude: params.magnitude,
        duration: params.duration,
        strength: params.strength,
        statusType: params.statusType || 'stun',
        name: params.name,
        description: params.description,
      });
  }
}

/**
 * Creates a weapon effect from a weapon system with scaling
 */
export function createScaledWeaponEffect(
  weapon: WeaponSystem & { name?: string },
  scale: number = 1
): WeaponEffectType {
  return createDamageEffect({
    id: `${weapon.id}-scaled-effect`,
    magnitude: weapon.damage * scale,
    duration: weapon.cooldown,
    strength: weapon.damage * scale,
    damageType: 'physical',
    penetration: 0,
    name: weapon.name || `Scaled ${weapon.type}`,
    description: `Scaled effect (${scale}x) from ${weapon.name || weapon.type}`,
  });
}

/**
 * Creates a combined weapon effect from multiple weapons
 */
export function createCombinedWeaponEffect(weapons: WeaponSystem[]): WeaponEffectType {
  if (weapons.length === 0) {
    throw new Error('Cannot create combined effect from empty weapons array');
  }

  // Calculate combined damage and cooldown
  const totalDamage = weapons.reduce((sum, weapon) => sum + weapon.damage, 0);
  const avgCooldown = weapons.reduce((sum, weapon) => sum + weapon.cooldown, 0) / weapons.length;

  // Create a combined weapon-like object
  const combinedWeapon: WeaponLike = {
    id: `combined-${weapons[0].id}`,
    type: weapons[0].type,
    damage: totalDamage,
    cooldown: avgCooldown,
    displayName: `Combined ${weapons[0].type}`,
  };

  // Create effect from the combined weapon
  return createWeaponEffect(combinedWeapon);
}

/**
 * Checks if an object is a valid Effect
 */
export function isValidEffect(effect: unknown): effect is Effect {
  if (!effect || typeof effect !== 'object') {
    return false;
  }

  const effectObj = effect as Partial<Effect>;
  return (
    typeof effectObj.id === 'string' &&
    typeof effectObj.type === 'string' &&
    typeof effectObj.magnitude === 'number' &&
    typeof effectObj.duration === 'number'
  );
}

/**
 * Validates and normalizes an effect
 */
export function validateEffect(effect: Partial<Effect>): Effect {
  if (!effect.id) {
    throw new Error('Effect must have an id');
  }

  if (!effect.type) {
    throw new Error('Effect must have a type');
  }

  return {
    id: effect.id,
    type: effect.type,
    magnitude: effect.magnitude || 0,
    duration: effect.duration || 0,
  };
}

/**
 * Combines multiple effects into a single effect
 */
export function combineEffects(effects: Effect[]): Effect {
  if (effects.length === 0) {
    throw new Error('Cannot combine empty effects array');
  }

  // Use the first effect as a base
  const baseEffect = effects[0];

  // Combine magnitudes and take the longest duration
  const combinedMagnitude = effects.reduce((sum, effect) => sum + effect.magnitude, 0);
  const maxDuration = Math.max(...effects.map(effect => effect.duration));

  // Create a new effect with combined values
  return {
    id: `combined-${baseEffect.id}`,
    type: baseEffect.type,
    magnitude: combinedMagnitude,
    duration: maxDuration,
  };
}

/**
 * Scales an effect by a factor
 */
export function scaleEffect(effect: Effect, factor: number): Effect {
  // Create a new effect with scaled values
  return {
    id: `scaled-${effect.id}`,
    type: effect.type,
    magnitude: effect.magnitude * factor,
    duration: effect.duration,
  };
}

/**
 * Creates a chain of effects that trigger sequentially
 */
export function createEffectChain(effects: Effect[]): Effect[] {
  if (effects.length <= 1) {
    return effects;
  }

  // Create a new array of effects with sequential triggers
  return effects.map((effect, index) => {
    if (index === 0) {
      return effect;
    }

    // Each subsequent effect is triggered by the previous one
    return {
      ...effect,
      id: `chain-${index}-${effect.id}`,
      trigger: {
        type: 'effect-complete',
        effectId: effects[index - 1].id,
      },
    };
  });
}

import * as React from "react";
import { useMemo, useState, useEffect, useRef } from 'react';
import { CanvasRenderer } from './renderers/CanvasRenderer';
import { SVGRenderer } from './renderers/SVGRenderer';
import { WebGLRenderer } from './renderers/WebGLRenderer';
import { errorLoggingService, ErrorType } from '../services/ErrorLoggingService';

/**
 * Common chart data point interface
 */
export interface ChartDataPoint {
  x: number | string | Date;
  y: number;
  [key: string]: unknown;
}

/**
 * Chart data interface
 */
export interface ChartData {
  datasets: {
    label: string;
    data: ChartDataPoint[];
    color?: string;
    [key: string]: unknown;
  }[];
  annotations?: {
    type: 'line' | 'box' | 'point';
    position: { x?: number | string | Date; y?: number };
    color?: string;
    label?: string;
    [key: string]: unknown;
  }[];
}

/**
 * Chart axes configuration
 */
export interface ChartAxes {
  x: {
    type: 'linear' | 'time' | 'category' | 'log';
    label?: string;
    min?: number | string | Date;
    max?: number | string | Date;
    tickCount?: number;
    tickFormat?: (value: unknown) => string;
    grid?: boolean;
  };
  y: {
    type: 'linear' | 'log';
    label?: string;
    min?: number;
    max?: number;
    tickCount?: number;
    tickFormat?: (value: number) => string;
    grid?: boolean;
  };
}

/**
 * Chart legend configuration
 */
export interface ChartLegend {
  visible: boolean;
  position: 'top' | 'right' | 'bottom' | 'left';
  align?: 'start' | 'center' | 'end';
}

/**
 * Chart tooltip configuration
 */
export interface ChartTooltip {
  enabled: boolean;
  mode: 'point' | 'nearest' | 'dataset';
  intersect?: boolean;
  format?: (point: ChartDataPoint, dataset: ChartData['datasets'][0]) => string;
}

/**
 * Chart animation configuration
 */
export interface ChartAnimation {
  enabled: boolean;
  duration?: number;
  easing?: 'linear' | 'easeInOut' | 'easeIn' | 'easeOut';
}

/**
 * Chart options interface
 */
export interface ChartOptions {
  width?: number | string;
  height?: number | string;
  renderer?: 'canvas' | 'svg' | 'webgl';
  responsive?: boolean;
  maintainAspectRatio?: boolean;
  axes?: ChartAxes;
  legend?: ChartLegend;
  tooltip?: ChartTooltip;
  animation?: ChartAnimation;
  backgroundColor?: string;
  padding?: {
    top?: number;
    right?: number;
    bottom?: number;
    left?: number;
  };
  enablePanning?: boolean;
  enableZooming?: boolean;
  theme?: 'light' | 'dark' | 'auto';
  memoryOptimized?: boolean;
  renderOptimization?: boolean;
  optimizationThreshold?: number;
}

/**
 * Default chart options
 */
const DEFAULT_CHART_OPTIONS: ChartOptions = {
  width: '100%',
  height: 300,
  renderer: 'canvas',
  responsive: true,
  maintainAspectRatio: true,
  axes: {
    x: {
      type: 'linear',
      grid: true,
    },
    y: {
      type: 'linear',
      grid: true,
    }
  },
  legend: {
    visible: true,
    position: 'top',
    align: 'center',
  },
  tooltip: {
    enabled: true,
    mode: 'nearest',
    intersect: true,
  },
  animation: {
    enabled: true,
    duration: 300,
    easing: 'easeInOut',
  },
  backgroundColor: 'transparent',
  padding: {
    top: 10,
    right: 10,
    bottom: 20,
    left: 30,
  },
  enablePanning: false,
  enableZooming: false,
  theme: 'light',
  memoryOptimized: false,
  renderOptimization: true,
  optimizationThreshold: 1000,
};

/**
 * Chart renderer interface
 */
export interface ChartRenderer {
  render: (
    container: HTMLElement,
    data: ChartData,
    options: ChartOptions,
    type: ChartType
  ) => void;
  update: (
    container: HTMLElement,
    data: ChartData,
    options: ChartOptions,
    type: ChartType
  ) => void;
  destroy: () => void;
  getStatus: () => { isInitialized: boolean; lastRenderTime?: number };
}

/**
 * Chart type
 */
export type ChartType = 'line' | 'bar' | 'scatter' | 'area' | 'pie' | 'radar' | 'heatmap';

/**
 * Chart component props
 */
export interface ChartProps {
  data: ChartData;
  options?: Partial<ChartOptions>;
  type: ChartType;
  className?: string;
  onRender?: (renderer: ChartRenderer) => void;
  onError?: (error: Error) => void;
}

/**
 * Chart component that uses a strategy pattern for different rendering methods
 */
export const Chart: React.FC<ChartProps> = ({
  data,
  options = {},
  type,
  className = '',
  onRender,
  onError,
}) => {
  // Merge options with defaults
  const mergedOptions: ChartOptions = useMemo(() => ({
    ...DEFAULT_CHART_OPTIONS,
    ...options,
  }), [options]);

  // Container ref for rendering
  const containerRef = useRef<HTMLDivElement>(null);

  // Chart renderer ref
  const rendererRef = useRef<ChartRenderer | null>(null);

  // Error state
  const [error, setError] = useState<Error | null>(null);

  // Auto-select renderer based on data size and device capabilities if not specified
  const renderer = useMemo(() => {
    // Count total data points
    const totalDataPoints = data.datasets.reduce((acc, dataset) => acc + dataset.data.length, 0);

    // If renderer is explicitly set, use that
    if (mergedOptions.renderer) {
      return mergedOptions.renderer;
    }

    // Auto-select based on data size and optimization settings
    if (mergedOptions.memoryOptimized || totalDataPoints > (mergedOptions.optimizationThreshold || 1000)) {
      // Check if WebGL is available
      try {
        const canvas = document.createElement('canvas');
        const gl = canvas.getContext('webgl') || canvas.getContext('experimental-webgl');
        if (gl) {
          return 'webgl';
        }
      } catch (e) {
        // WebGL not available, fall back to canvas
      }

      return 'canvas';
    }

    // For smaller datasets, SVG provides better interactivity
    return 'svg';
  }, [mergedOptions.renderer, mergedOptions.memoryOptimized, mergedOptions.optimizationThreshold, data]);

  // Initialize renderer
  useEffect(() => {
    if (!containerRef.current) return;

    try {
      // Destroy previous renderer if it exists
      if (rendererRef.current) {
        rendererRef.current.destroy();
        rendererRef.current = null;
      }

      // Create new renderer based on selected strategy
      switch (renderer) {
        case 'canvas':
          rendererRef.current = new CanvasRenderer();
          break;
        case 'webgl':
          rendererRef.current = new WebGLRenderer();
          break;
        case 'svg':
        default:
          rendererRef.current = new SVGRenderer();
          break;
      }

      // Initial render
      rendererRef.current.render(containerRef.current, data, mergedOptions, type);

      // Call onRender callback if provided
      if (onRender) {
        onRender(rendererRef.current);
      }

      // Clear any previous errors
      if (error) {
        setError(null);
      }
    } catch (err) {
      const error = err instanceof Error ? err : new Error(String(err));

      // Log error
      errorLoggingService.logError(error, ErrorType.RUNTIME, undefined, {
        component: 'Chart',
        renderer,
        chartType: type,
        dataSize: data.datasets.reduce((acc, dataset) => acc + dataset.data.length, 0),
      });

      // Set error state
      setError(error);

      // Call onError callback if provided
      if (onError) {
        onError(error);
      }
    }

    // Cleanup
    return () => {
      if (rendererRef.current) {
        rendererRef.current.destroy();
        rendererRef.current = null;
      }
    };
  }, [renderer, type]);

  // Update chart when data or options change
  useEffect(() => {
    if (!containerRef.current || !rendererRef.current) return;

    try {
      rendererRef.current.update(containerRef.current, data, mergedOptions, type);

      // Clear any previous errors
      if (error) {
        setError(null);
      }
    } catch (err) {
      const error = err instanceof Error ? err : new Error(String(err));

      // Log error
      errorLoggingService.logError(error, ErrorType.RUNTIME, undefined, {
        component: 'Chart',
        renderer,
        chartType: type,
        dataSize: data.datasets.reduce((acc, dataset) => acc + dataset.data.length, 0),
        action: 'update',
      });

      // Set error state
      setError(error);

      // Call onError callback if provided
      if (onError) {
        onError(error);
      }
    }
  }, [data, mergedOptions]);

  // Handle container resize
  useEffect(() => {
    if (!containerRef.current || !rendererRef.current || !mergedOptions.responsive) return;

    const resizeObserver = new ResizeObserver(() => {
      if (containerRef.current && rendererRef.current) {
        try {
          rendererRef.current.update(containerRef.current, data, mergedOptions, type);
        } catch (err) {
          console.error('Error resizing chart:', err);
        }
      }
    });

    resizeObserver.observe(containerRef.current);

    return () => {
      resizeObserver.disconnect();
    };
  }, [mergedOptions.responsive]);

  // Fallback UI for errors
  if (error) {
    return (
      <div 
        className={`chart-error ${className}`}
        style={{
          width: mergedOptions.width,
          height: mergedOptions.height,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          border: '1px solid #ff6b6b',
          borderRadius: '4px',
          padding: '1rem',
          backgroundColor: '#fff1f1',
          color: '#d63031',
        }}
      >
        <h4>Chart Error</h4>
        <p style={{ fontSize: '0.875rem' }}>{error.message}</p>
        <button 
          onClick={() => {
            setError(null);
            if (containerRef.current && rendererRef.current) {
              try {
                rendererRef.current.render(containerRef.current, data, mergedOptions, type);
              } catch (err) {
                setError(err instanceof Error ? err : new Error(String(err)));
              }
            }
          }}
          style={{
            marginTop: '0.5rem',
            padding: '0.25rem 0.75rem',
            border: '1px solid #d63031',
            borderRadius: '4px',
            backgroundColor: 'white',
            color: '#d63031',
            cursor: 'pointer',
          }}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div
      ref={containerRef}
      className={`chart-container ${className}`}
      style={{
        width: mergedOptions.width,
        height: mergedOptions.height,
        position: 'relative',
      }}
      data-chart-type={type}
      data-chart-renderer={renderer}
    />
  );
};

export default Chart;
