    border: 'border-cyan-700/30',
    fill: 'bg-cyan-500',
  },
  population: {
    base: 'text-green-400',
    bg: 'bg-green-900/20',
    border: 'border-green-700/30',
    fill: 'bg-green-500',
  },
  research: {
    base: 'text-purple-400',
    bg: 'bg-purple-900/20',
    border: 'border-purple-700/30',
    fill: 'bg-purple-500',
  },
};

function ResourceDisplay({ type, value, rate, capacity, thresholds }: ResourceDisplayProps) {
  const Icon = resourceIcons[type];
  const colors = resourceColors[type];
  const percentage = capacity ? (value / capacity) * 100 : 100;
  const isLow = thresholds && value <= thresholds.low;
  const isCritical = thresholds && value <= thresholds.critical;

  return (
    <div className={`p-3 ${colors.bg} rounded-lg border ${colors.border}`}>
      <div className="mb-2 flex items-center space-x-3">
        <div className={`rounded-lg p-1.5 ${colors.bg}`}>
          <Icon className={`h-4 w-4 ${colors.base}`} />
        </div>
        <div className="flex-1">
          <div className="text-sm font-medium capitalize text-gray-300">{type}</div>
          <div className={`text-lg font-bold ${colors.base}`}>
            {value.toLocaleString()}
            {capacity && ` / ${capacity.toLocaleString()}`}
          </div>
        </div>
        <AnimatePresence>
          {rate !== 0 && (
            <motion.div
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: 10 }}
              className={`text-sm ${rate > 0 ? 'text-green-400' : 'text-red-400'}`}
            >
              {rate > 0 ? '+' : ''}
              {rate}/s
            </motion.div>
          )}
        </AnimatePresence>
      </div>

      {/* Progress Bar */}
      <div className="h-1.5 overflow-hidden rounded-full bg-gray-800">
        <motion.div
          className={`h-full ${colors.fill} transition-all duration-500`}
          initial={{ width: 0 }}
          animate={{ width: `${percentage}%` }}
          style={{
            opacity: isCritical ? 0.5 : isLow ? 0.7 : 1,
          }}
        />
      </div>

      {/* Warning Indicator */}
      <AnimatePresence>
        {(isLow || isCritical) && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            className={`mt-2 text-xs ${isCritical ? 'text-red-400' : 'text-yellow-400'}`}
          >
            {isCritical ? 'Critical' : 'Low'} {type} levels
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export function ResourceVisualization() {
  const resources = useResources();
  const [resourceRates, setResourceRates] = useState({
    minerals: 0,
    energy: 0,
    population: 0,
    research: 0,
  });

  // Calculate resource rates
  useEffect(() => {
    const interval = setInterval(() => {
      setResourceRates(prev => ({
        minerals: Math.random() * 10 - 2, // Example rate calculation
        energy: Math.random() * 15 - 5,
        population: Math.random() * 2 - 0.5,
        research: Math.random() * 5,
      }));
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="grid grid-cols-2 gap-4">
      <ResourceDisplay
        type="minerals"
        value={resources.minerals}
        rate={resourceRates.minerals}
        capacity={10000}
        thresholds={{ low: 1000, critical: 500 }}
      />
      <ResourceDisplay
        type="energy"
        value={resources.energy}
        rate={resourceRates.energy}
        capacity={8000}
        thresholds={{ low: 800, critical: 400 }}
      />
      <ResourceDisplay
        type="population"
        value={resources.population}
        rate={resourceRates.population}
        thresholds={{ low: 50, critical: 25 }}
      />
      <ResourceDisplay type="research" value={resources.research} rate={resourceRates.research} />
    </div>
  );
}

import {
  AlertTriangle,
  Bell,
  ChevronRight,
  Crown,
  Database,
  Flag,
  HelpCircle,
  Layers,
  Map,
  Rocket,
  Search,
  Star,
  Zap,
  ZoomIn,
  ZoomOut,
} from 'lucide-react';
import React, { useEffect, useRef, useState } from 'react';
import { GameLayout } from './GameLayout';

interface EmpireDetails {
  name: string;
  bannerColor: string;
}

interface Asset {
  id: string;
  type: 'colony' | 'dysonSphere' | 'starStation' | 'tradeHub';
  name: string;
  position: { x: number; y: number };
  population?: number;
  resources?: string[];
  tradeConnections?: { target: string; volume: number }[];
  status: 'active' | 'constructing' | 'damaged';
  developmentLevel: number;
  lastUpdate?: number;
}

interface ViewState {
  zoom: number;
  focusedAsset: string | null;
  position: { x: number; y: number };
  activeOverlays: string[];
  searchQuery: string;
  notifications: {
    id: string;
    type: 'info' | 'warning' | 'success';
    message: string;
    timestamp: number;
  }[];
  dayNightCycle: number;
}

export function SprawlView({ empire }: { empire: EmpireDetails }) {
  const [view, setView] = useState<ViewState>({
    zoom: 1,
    focusedAsset: null,
    position: { x: 0, y: 0 },
    activeOverlays: ['trade', 'resources'],
    searchQuery: '',
    notifications: [],
    dayNightCycle: 0,
  });

  const [assets, setAssets] = useState<Asset[]>([
    {
      id: 'alpha-colony',
      type: 'colony',
      name: 'Alpha Prime',
      position: { x: 0, y: 0 },
      population: 10000,
      resources: ['Iron', 'Titanium'],
      tradeConnections: [
        { target: 'beta-station', volume: 0.8 },
        { target: 'gamma-sphere', volume: 0.5 },
      ],
      status: 'active',
      developmentLevel: 0.8,
      lastUpdate: Date.now(),
    },
    {
      id: 'beta-station',
      type: 'starStation',
      name: 'Beta Station',
      position: { x: 150, y: -100 },
      population: 5000,
      resources: ['Helium-3'],
      tradeConnections: [{ target: 'alpha-colony', volume: 0.8 }],
      status: 'active',
      developmentLevel: 0.6,
      lastUpdate: Date.now(),
    },
    {
      id: 'gamma-sphere',
      type: 'dysonSphere',
      name: 'Gamma Sphere',
      position: { x: -120, y: 80 },
      tradeConnections: [
        { target: 'alpha-colony', volume: 0.5 },
        { target: 'delta-hub', volume: 0.3 },
      ],
      status: 'constructing',
      developmentLevel: 0.4,
      lastUpdate: Date.now(),
    },
    {
      id: 'delta-hub',
      type: 'tradeHub',
      name: 'Delta Trading Post',
      position: { x: 80, y: 120 },
      population: 2000,
      tradeConnections: [{ target: 'gamma-sphere', volume: 0.3 }],
      status: 'active',
      developmentLevel: 0.3,
      lastUpdate: Date.now(),
    },
  ]);

  // Day/Night cycle effect
  useEffect(() => {
    const interval = setInterval(() => {
      setView(prev => ({
        ...prev,
        dayNightCycle: (prev.dayNightCycle + 0.01) % 1,
      }));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  // Simulate real-time updates
  useEffect(() => {
    const interval = setInterval(() => {
      setAssets(currentAssets => {
        return currentAssets.map(asset => {
          if (asset.status === 'active' && Math.random() > 0.8) {
            const populationChange = Math.floor(Math.random() * 100) - 30;
            const newPopulation = asset.population ? asset.population + populationChange : 0;

            if (populationChange > 0) {
              addNotification(
                'success',
                `${asset.name}: Population increased by ${populationChange}`,
                asset.id
              );
            }

            return {
              ...asset,
              population: Math.max(0, newPopulation),
              lastUpdate: Date.now(),
            };
          }
          return asset;
        });
      });
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const addNotification = (
    type: 'info' | 'warning' | 'success',
    message: string,
    assetId: string
  ) => {
    const notification = {
      id: `${assetId}-${Date.now()}`,
      type,
      message,
      timestamp: Date.now(),
    };

    setView(prev => ({
      ...prev,
      notifications: [...prev.notifications, notification],
    }));

    // Remove notification after 5 seconds
    setTimeout(() => {
      setView(prev => ({
        ...prev,
        notifications: prev.notifications.filter(n => n.id !== notification.id),
      }));
    }, 5000);
  };

  const containerRef = useRef<HTMLDivElement>(null);
  const isDragging = useRef(false);
  const lastPosition = useRef({ x: 0, y: 0 });

  const handleMouseDown = (e: React.MouseEvent) => {
    isDragging.current = true;
    lastPosition.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging.current) {
      return;
    }

    const deltaX = e.clientX - lastPosition.current.x;
    const deltaY = e.clientY - lastPosition.current.y;

    setView(prev => ({
      ...prev,
      position: {
        x: prev.position.x + deltaX,
        y: prev.position.y + deltaY,
      },
    }));

    lastPosition.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseUp = () => {
    isDragging.current = false;
  };

  const handleZoom = (delta: number) => {
    setView(prev => ({
      ...prev,
      zoom: Math.max(0.5, Math.min(2, prev.zoom + delta)),
    }));
  };

  const focusAsset = (assetName: string) => {
    setView(prev => ({
      ...prev,
      focusedAsset: assetName,
      zoom: 1.5,
    }));
  };

  const getAssetIcon = (type: Asset['type']) => {
    switch (type) {
      case 'colony':
        return Crown;
      case 'dysonSphere':
        return Star;
      case 'starStation':
        return Rocket;
      case 'tradeHub':
        return Database;
      default:
        return Crown;
    }
  };

  const getAssetColor = (asset: Asset) => {
    if (asset.status === 'constructing') {
      return 'yellow';
    }
    if (asset.status === 'damaged') {
      return 'red';
    }
    switch (asset.type) {
      case 'colony':
        return 'cyan';
      case 'dysonSphere':
        return 'orange';
      case 'starStation':
        return 'purple';
      case 'tradeHub':
        return 'emerald';
      default:
        return 'blue';
    }
  };

  const filteredAssets = assets.filter(asset =>
    view.searchQuery ? asset.name.toLowerCase().includes(view.searchQuery.toLowerCase()) : true
  );

  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        setView(prev => ({
          ...prev,
          focusedAsset: null,
          zoom: 1,
        }));
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, []);

  const onToggleSprawlView = () => {
    // Implement the logic to toggle the sprawl view
    console.log('Sprawl view toggled');
  };

  return (
    <GameLayout empireName={empire.name} bannerColor={empire.bannerColor}>
      <div className="absolute inset-0 overflow-hidden bg-gray-900">
        {/* Multi-layered Starfield Background */}
        <div
          className="absolute inset-0"
          style={{
            backgroundColor: `rgba(0, 0, 0, ${0.3 + Math.sin(view.dayNightCycle * Math.PI * 2) * 0.2})`,
          }}
        >
          <div
            className="absolute inset-0 scale-100 transform bg-[url('https://images.unsplash.com/photo-1506318137071-a8e063b4bec0?q=80&w=3000')] bg-cover opacity-20"
            style={{ transition: 'transform 120s linear' }}
          />
          <div
            className="absolute inset-0 scale-150 transform bg-[url('https://images.unsplash.com/photo-1419242902214-272b3f66ee7a?q=80&w=2013')] bg-cover opacity-10"
            style={{ transition: 'transform 180s linear' }}
          />

          {/* Aurora Effect */}
          <div
            className="absolute inset-0 bg-gradient-to-b from-indigo-500/5 via-transparent to-transparent"
            style={{
              transform: `translateY(${Math.sin(view.dayNightCycle * Math.PI * 2) * 20}px)`,
              opacity: 0.3 + Math.sin(view.dayNightCycle * Math.PI * 2) * 0.2,
            }}
          />
        </div>

        {/* Controls */}
        <div className="absolute left-6 right-6 top-6 z-10 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="relative">
              <input
                type="text"
                placeholder="Search assets..."
                className="w-64 rounded-lg border border-gray-700 bg-gray-800/90 px-4 py-2 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-cyan-500"
                value={view.searchQuery}
                onChange={e => setView(prev => ({ ...prev, searchQuery: e.target.value }))}
              />
              <Search className="absolute right-3 top-2.5 h-5 w-5 text-gray-400" />
            </div>

            {/* Overlay Toggles */}
            <div className="flex space-x-2">
              <button
                onClick={() =>
                  setView(prev => ({
                    ...prev,
                    activeOverlays: prev.activeOverlays.includes('trade')
                      ? prev.activeOverlays.filter(o => o !== 'trade')
                      : [...prev.activeOverlays, 'trade'],
                  }))
                }
                className={`flex items-center space-x-2 rounded-lg px-3 py-2 transition-colors ${
                  view.activeOverlays.includes('trade')
                    ? 'bg-indigo-600 text-white'
                    : 'bg-gray-800/90 text-gray-400 hover:bg-gray-700/90'
                }`}
              >
                <Database className="h-4 w-4" />
                <span>Trade Routes</span>
              </button>

              <button
                onClick={() =>
                  setView(prev => ({
                    ...prev,
                    activeOverlays: prev.activeOverlays.includes('resources')
                      ? prev.activeOverlays.filter(o => o !== 'resources')
                      : [...prev.activeOverlays, 'resources'],
                  }))
                }
                className={`flex items-center space-x-2 rounded-lg px-3 py-2 transition-colors ${
                  view.activeOverlays.includes('resources')
                    ? 'bg-indigo-600 text-white'
                    : 'bg-gray-800/90 text-gray-400 hover:bg-gray-700/90'
                }`}
              >
                <Layers className="h-4 w-4" />
                <span>Resources</span>
              </button>
            </div>
          </div>

          <div className="flex space-x-2">
            <button
              onClick={() => handleZoom(0.1)}
              className="rounded-lg bg-gray-800/90 p-2 backdrop-blur-sm transition-colors hover:bg-gray-700/90"
            >
              <ZoomIn className="h-5 w-5 text-cyan-400" />
            </button>
            <button
              onClick={() => handleZoom(-0.1)}
              className="rounded-lg bg-gray-800/90 p-2 backdrop-blur-sm transition-colors hover:bg-gray-700/90"
            >
              <ZoomOut className="h-5 w-5 text-cyan-400" />
            </button>
            <button className="group relative rounded-lg bg-gray-800/90 p-2 backdrop-blur-sm transition-colors hover:bg-gray-700/90">
              <HelpCircle className="h-5 w-5 text-cyan-400" />
              <div className="absolute right-0 top-full mt-2 hidden w-64 rounded-lg border border-gray-700 bg-gray-800/95 p-3 group-hover:block">
                <h4 className="mb-2 font-medium text-white">Navigation Help</h4>
                <ul className="space-y-1 text-sm text-gray-300">
                  <li className="flex items-center">
                    <ZoomIn className="mr-2 h-4 w-4" /> Zoom in view
                  </li>
                  <li className="flex items-center">
                    <ZoomOut className="mr-2 h-4 w-4" /> Zoom out view
                  </li>
                  <li className="flex items-center">
                    <Flag className="mr-2 h-4 w-4" /> Mark territory
                  </li>
                  <li className="flex items-center">
                    <ChevronRight className="mr-2 h-4 w-4" /> Navigate sectors
                  </li>
                </ul>
              </div>
            </button>
          </div>
        </div>

        {/* Map Area */}
        <div
          ref={containerRef}
          className="absolute inset-0 cursor-move"
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
        >
          <div
            className="absolute inset-0 transition-transform duration-300 ease-out"
            style={{
              transform: `translate(${view.position.x}px, ${view.position.y}px) scale(${view.zoom})`,
            }}
          >
            {/* Trade Routes */}
            {view.activeOverlays.includes('trade') && (
              <svg className="pointer-events-none absolute inset-0">
                {assets.map(asset =>
                  asset.tradeConnections?.map(connection => {
                    const target = assets.find(a => a.id === connection.target);
                    if (!target) {
                      return null;
                    }

                    const startX = asset.position.x + window.innerWidth / 2;
                    const startY = asset.position.y + window.innerHeight / 2;
                    const endX = target.position.x + window.innerWidth / 2;
                    const endY = target.position.y + window.innerHeight / 2;

                    return (
                      <g key={`${asset.id}-${target.id}`}>
                        <line
                          x1={startX}
                          y1={startY}
                          x2={endX}
                          y2={endY}
                          stroke="rgba(99, 102, 241, 0.3)"
                          strokeWidth={2 + connection.volume * 4}
                          strokeDasharray="4 4"
                        >
                          <animate
                            attributeName="stroke-dashoffset"
                            from="0"
                            to="8"
                            dur={`${1 / connection.volume}s`}
                            repeatCount="indefinite"
                          />
                        </line>
                        {/* Trade Volume Indicator */}
                        <circle
                          cx={(startX + endX) / 2}
                          cy={(startY + endY) / 2}
                          r={4 + connection.volume * 4}
                          fill="rgba(99, 102, 241, 0.2)"
                          className="animate-pulse"
                        />
                      </g>
                    );
                  })
                )}
              </svg>
            )}

            {/* Assets */}
            {filteredAssets.map(asset => {
              const Icon = getAssetIcon(asset.type);
              const color = getAssetColor(asset);

              return (
                <div
                  key={asset.id}
                  className="absolute"
                  style={{
                    left: `calc(50% + ${asset.position.x}px)`,
                    top: `calc(50% + ${asset.position.y}px)`,
                    transform: 'translate(-50%, -50%)',
                  }}
                >
                  <button onClick={() => focusAsset(asset.id)} className="group relative">
                    <div
                      className={`h-24 w-24 rounded-full bg-${color}-500/20 relative animate-pulse`}
                      style={{
                        boxShadow: `0 0 ${20 + asset.developmentLevel * 30}px ${asset.developmentLevel * 20}px rgba(${
                          color === 'cyan'
                            ? '34, 211, 238'
                            : color === 'orange'
                              ? '234, 88, 12'
                              : color === 'purple'
                                ? '147, 51, 234'
                                : color === 'emerald'
                                  ? '16, 185, 129'
                                  : '59, 130, 246'
                        }, ${0.1 + asset.developmentLevel * 0.2})`,
                      }}
                    >
                      <div
                        className={`absolute left-1/2 top-1/2 h-16 w-16 -translate-x-1/2 -translate-y-1/2 rounded-full bg-${color}-400/30`}
                        style={{
                          transform: `translate(-50%, -50%) scale(${0.8 + Math.sin(Date.now() / 1000) * 0.1})`,
                        }}
                      >
                        <div
                          className={`absolute left-1/2 top-1/2 h-8 w-8 -translate-x-1/2 -translate-y-1/2 rounded-full bg-${color}-300/40 animate-spin-slow flex items-center justify-center`}
                        >
                          <Icon className={`h-5 w-5 text-${color}-200`} />
                        </div>
                      </div>

                      {/* Development Level Rings */}
                      {Array.from({
                        length: Math.ceil(asset.developmentLevel * 5),
                      }).map((_, i) => (
                        <div
                          key={i}
                          className="absolute inset-0 rounded-full border"
                          style={{
                            borderColor: `rgba(${
                              color === 'cyan'
                                ? '34, 211, 238'
                                : color === 'orange'
                                  ? '234, 88, 12'
                                  : color === 'purple'
                                    ? '147, 51, 234'
                                    : color === 'emerald'
                                      ? '16, 185, 129'
                                      : '59, 130, 246'
                            }, ${0.1 + (i / 5) * 0.2})`,
                            transform: `scale(${1.2 + i * 0.2}) rotate(${i * 30}deg)`,
                            animation: `spin ${10 + i * 5}s linear infinite`,
                          }}
                        />
                      ))}
                    </div>

                    {/* Asset Label */}
                    <div className="absolute left-1/2 top-full mt-2 -translate-x-1/2 text-center">
                      <div className={`text-${color}-200 font-medium`}>{asset.name}</div>
                      {asset.population && (
                        <div className={`text-${color}-300/70 text-sm`}>
                          Pop: {asset.population.toLocaleString()}
                        </div>
                      )}
                      {asset.status === 'constructing' && (
                        <div className="flex items-center justify-center text-sm text-yellow-300/70">
                          <Zap className="mr-1 h-4 w-4" />
                          Power Required
                        </div>
                      )}
                    </div>

                    {/* Hover Tooltip */}
                    <div className="pointer-events-none absolute bottom-full left-1/2 mb-2 -translate-x-1/2 opacity-0 transition-opacity group-hover:opacity-100">
                      <div className="whitespace-nowrap rounded-lg border border-gray-700 bg-gray-800/95 px-3 py-2 backdrop-blur-sm">
                        {asset.resources && view.activeOverlays.includes('resources') && (
                          <div className="flex items-center gap-2 text-sm">
                            {asset.resources.map(resource => (
                              <span key={resource} className="text-gray-300">
                                {resource}
                              </span>
                            ))}
                          </div>
                        )}
                      </div>
                    </div>
                  </button>
                </div>
              );
            })}
          </div>
        </div>

        {/* Notifications */}
        <div className="fixed bottom-6 left-6 space-y-2">
          {view.notifications.map(notification => (
            <div
              key={notification.id}
              className={`flex items-center space-x-2 rounded-lg px-4 py-2 backdrop-blur-sm transition-all ${
                notification.type === 'success'
                  ? 'bg-green-900/80 text-green-200'
                  : notification.type === 'warning'
                    ? 'bg-yellow-900/80 text-yellow-200'
                    : 'bg-blue-900/80 text-blue-200'
              }`}
              style={{
                animation: 'slideIn 0.3s ease-out',
              }}
            >
              {notification.type === 'success' && <Bell className="h-4 w-4" />}
              {notification.type === 'warning' && <AlertTriangle className="h-4 w-4" />}
              {notification.type === 'info' && <Database className="h-4 w-4" />}
              <span>{notification.message}</span>
            </div>
          ))}
        </div>

        <button
          onClick={onToggleSprawlView}
          className="group w-full rounded-lg border border-indigo-700/50 bg-gradient-to-r from-indigo-950/80 to-indigo-900/80 px-4 py-3 text-left backdrop-blur-sm transition-all hover:bg-indigo-800/30"
        >
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <Map className="h-5 w-5 text-indigo-400" />
              <span className="font-medium text-indigo-100">{empire.name} Map</span>
            </div>
            <span className="text-sm text-indigo-400 opacity-60 transition-opacity group-hover:opacity-100">
              Press S
            </span>
          </div>
        </button>
      </div>
    </GameLayout>
  );
}

import React, { useEffect, useState } from 'react';
import {
  GlobalAutomationManager,
  GlobalRoutine,
} from '../../../managers/automation/GlobalAutomationManager';
import '../../../styles/automation.css';

// Define the routine type enum to match what's in GlobalAutomationManager
enum RoutineType {
  RESOURCE_BALANCING = 'RESOURCE_BALANCING',
  PERFORMANCE_OPTIMIZATION = 'PERFORMANCE_OPTIMIZATION',
  EMERGENCY_RESPONSE = 'EMERGENCY_RESPONSE',
  SYSTEM_MAINTENANCE = 'SYSTEM_MAINTENANCE',
}

// Define SystemId type to match what's expected
type SystemId = string;

interface AutomationVisualizationProps {
  automationManager?: GlobalAutomationManager;
  className?: string;
}

export const AutomationVisualization: React.FC<AutomationVisualizationProps> = ({
  automationManager,
  className = '',
}) => {
  const [routines, setRoutines] = useState<GlobalRoutine[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [filter, setFilter] = useState<{
    type: string;
    system: string;
    status: string;
    search: string;
  }>({
    type: 'all',
    system: 'all',
    status: 'all',
    search: '',
  });

  // Get all available types and systems for filtering
  const routineTypes = Object.values(RoutineType);
  const systems = [
    ...new Set(
      routines.map(routine =>
        Array.isArray(routine.systems) && routine.systems.length > 0
          ? routine.systems[0]
          : 'unknown'
      )
    ),
  ];

  useEffect(() => {
    if (!automationManager) {
      return;
    }

    // Initial load
    loadRoutines();

    // Subscribe to automation events
    const unsubscribe = subscribeToAutomationEvents();

    return () => {
      unsubscribe();
    };
  }, [automationManager]);

  const loadRoutines = () => {
    if (!automationManager) {
      return;
    }

    setLoading(true);
    try {
      const allRoutines = automationManager.getAllRoutines();
      setRoutines(allRoutines);
    } catch (error) {
      console.error('Failed to load automation routines:', error);
    } finally {
      setLoading(false);
    }
  };

  const subscribeToAutomationEvents = () => {
    if (!automationManager) {
      return () => {};
    }

    // This would be implemented with the event system
    // For now, we'll just return an empty function
    return () => {};
  };

  const handleFilterChange = (event: React.ChangeEvent<HTMLSelectElement | HTMLInputElement>) => {
    const { name, value } = event.target;
    setFilter(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleToggleRoutine = (routineId: string) => {
    if (!automationManager) {
      return;
    }

    const routine = routines.find(r => r.id === routineId);
    if (!routine) {
      return;
    }

    try {
      if (routine.enabled) {
        automationManager.disableRoutine(routineId);
      } else {
        automationManager.enableRoutine(routineId);
      }

      // Update local state
      setRoutines(prev => prev.map(r => (r.id === routineId ? { ...r, enabled: !r.enabled } : r)));
    } catch (error) {
      console.error(`Failed to toggle routine ${routineId}:`, error);
    }
  };

  const handleRemoveRoutine = (routineId: string) => {
    if (!automationManager) {
      return;
    }

    try {
      automationManager.unregisterRoutine(routineId);

      // Update local state
      setRoutines(prev => prev.filter(r => r.id !== routineId));
    } catch (error) {
      console.error(`Failed to remove routine ${routineId}:`, error);
    }
  };

  const handleRunRoutine = (routineId: string) => {
    if (!automationManager) {
      return;
    }

    try {
      // Find the routine first
      const routine = routines.find(r => r.id === routineId);
      if (!routine) {
        return;
      }

      // Manually trigger the routine execution
      // Note: Since executeRoutine is private, we'll just update the UI
      // In a real implementation, we would need a public method to execute a routine
      console.log(`Executing routine: ${routineId}`);

      // Update local state to reflect the routine was run
      const now = new Date().getTime(); // Use number instead of string for lastRun
      setRoutines(prev => prev.map(r => (r.id === routineId ? { ...r, lastRun: now } : r)));
    } catch (error) {
      console.error(`Failed to run routine ${routineId}:`, error);
    }
  };

  // Filter routines based on current filter settings
  const filteredRoutines = routines.filter(routine => {
    const matchesType = filter.type === 'all' || routine.type === filter.type;
    const matchesSystem =
      filter.system === 'all' ||
      (Array.isArray(routine.systems) &&
        routine.systems.some(sys => sys === (filter.system as SystemId)));
    const matchesStatus =
      filter.status === 'all' ||
      (filter.status === 'active' && routine.enabled) ||
      (filter.status === 'inactive' && !routine.enabled);
    const matchesSearch =
      !filter.search ||
      routine.name.toLowerCase().includes(filter.search.toLowerCase()) ||
      routine.description.toLowerCase().includes(filter.search.toLowerCase()) ||
      (Array.isArray(routine.tags) &&
        routine.tags.some(tag => tag.toLowerCase().includes(filter.search.toLowerCase())));

    return matchesType && matchesSystem && matchesStatus && matchesSearch;
  });

  // Get routine type icon based on type
  const getRoutineTypeIcon = (type: string) => {
    switch (type) {
      case RoutineType.RESOURCE_BALANCING:
        return '';
      case RoutineType.PERFORMANCE_OPTIMIZATION:
        return '';
      case RoutineType.EMERGENCY_RESPONSE:
        return '';
      case RoutineType.SYSTEM_MAINTENANCE:
        return '';
      default:
        return '';
    }
  };

  // Format relative time
  const formatRelativeTime = (timestamp: number | undefined) => {
    if (!timestamp) {
      return 'Never';
    }

    const now = new Date().getTime();
    const diffMs = now - timestamp;
    const diffSec = Math.floor(diffMs / 1000);

    if (diffSec < 60) {
      return `${diffSec}s ago`;
    }
    if (diffSec < 3600) {
      return `${Math.floor(diffSec / 60)}m ago`;
    }
    if (diffSec < 86400) {
      return `${Math.floor(diffSec / 3600)}h ago`;
    }
    return `${Math.floor(diffSec / 86400)}d ago`;
  };

  // Get priority label and color
  const getPriorityLabel = (priority: number) => {
    if (priority >= 90) {
      return { label: 'Critical', color: '#f44336' };
    }
    if (priority >= 70) {
      return { label: 'High', color: '#ff9800' };
    }
    if (priority >= 40) {
      return { label: 'Medium', color: '#2196f3' };
    }
    if (priority >= 10) {
      return { label: 'Low', color: '#4caf50' };
    }
    return { label: 'Background', color: '#9e9e9e' };
  };

  if (loading) {
    return (
      <div className={`automation-visualization automation-visualization--loading ${className}`}>
        <div className="automation-visualization__loading-spinner">
          {/* Spinner component would go here */}
          <div>Loading...</div>
        </div>
        <div className="automation-visualization__loading-text">Loading automation routines...</div>
      </div>
    );
  }

  return (
    <div className={`automation-visualization ${className}`}>
      <div className="automation-visualization__header">
        <h2 className="automation-visualization__title">
          <span style={{ marginRight: '8px' }}></span>
          Automation Routines
        </h2>
        <div className="automation-visualization__stats">
          <div className="automation-visualization__stat">
            <span className="automation-visualization__stat-label">Total:</span>
            <span className="automation-visualization__stat-value">{routines.length}</span>
          </div>
          <div className="automation-visualization__stat">
            <span className="automation-visualization__stat-label">Active:</span>
            <span className="automation-visualization__stat-value">
              {routines.filter(r => r.enabled).length}
            </span>
          </div>
        </div>
      </div>

      <div className="automation-visualization__filters">
        <div className="automation-visualization__filter">
          <label className="automation-visualization__filter-label">Type:</label>
          <select
            className="automation-visualization__filter-select"
            name="type"
            value={filter.type}
            onChange={handleFilterChange}
          >
            <option value="all">All Types</option>
            {routineTypes.map(type => (
              <option key={type} value={type}>
                {type.replace(/_/g, ' ')}
              </option>
            ))}
          </select>
        </div>

        <div className="automation-visualization__filter">
          <label className="automation-visualization__filter-label">System:</label>
          <select
            className="automation-visualization__filter-select"
            name="system"
            value={filter.system}
            onChange={handleFilterChange}
          >
            <option value="all">All Systems</option>
            {systems.map(system => (
              <option key={system} value={system}>
                {system}
              </option>
            ))}
          </select>
        </div>

        <div className="automation-visualization__filter">
          <label className="automation-visualization__filter-label">Status:</label>
          <select
            className="automation-visualization__filter-select"
            name="status"
            value={filter.status}
            onChange={handleFilterChange}
          >
            <option value="all">All Statuses</option>
            <option value="active">Active</option>
            <option value="inactive">Inactive</option>
          </select>
        </div>

        <input
          type="text"
          className="automation-visualization__search"
          name="search"
          value={filter.search}
          onChange={handleFilterChange}
          placeholder="Search routines..."
        />
      </div>

      <div className="automation-visualization__routines">
        {filteredRoutines.length === 0 ? (
          <div className="automation-visualization__empty">
            <div>No automation routines found</div>
            <div>Try adjusting your filters or create a new routine</div>
          </div>
        ) : (
          <div className="automation-visualization__routines-list">
            {filteredRoutines.map(routine => {
              const priorityInfo = getPriorityLabel(routine.priority);
              const systemName =
                Array.isArray(routine.systems) && routine.systems.length > 0
                  ? routine.systems[0]
                  : 'unknown';

              return (
                <div
                  key={routine.id}
                  className={`automation-visualization__routine ${
                    routine.enabled
                      ? 'automation-visualization__routine--active'
                      : 'automation-visualization__routine--inactive'
                  }`}
                >
                  <div className="automation-visualization__routine-header">
                    <div className="automation-visualization__routine-type">
                      {getRoutineTypeIcon(routine.type)}
                    </div>
                    <div className="automation-visualization__routine-name">{routine.name}</div>
                    <div
                      className="automation-visualization__routine-priority"
                      style={{ backgroundColor: priorityInfo.color }}
                    >
                      {priorityInfo.label}
                    </div>
                  </div>

                  <div className="automation-visualization__routine-description">
                    {routine.description}
                  </div>

                  <div className="automation-visualization__routine-meta">
                    <div className="automation-visualization__routine-systems">
                      System: {systemName}
                    </div>
                    {routine.interval && (
                      <div className="automation-visualization__routine-interval">
                        Interval: {routine.interval}ms
                      </div>
                    )}
                    <div className="automation-visualization__routine-last-run">
                      Last run: {formatRelativeTime(routine.lastRun)}
                    </div>
                  </div>

                  {Array.isArray(routine.tags) && routine.tags.length > 0 && (
                    <div className="automation-visualization__routine-tags">
                      {routine.tags.map((tag, index) => (
                        <div
                          key={`${routine.id}-tag-${index}`}
                          className="automation-visualization__routine-tag"
                        >
                          {tag}
                        </div>
                      ))}
                    </div>
                  )}

                  <div className="automation-visualization__routine-controls">
                    <button
                      className="automation-visualization__routine-control automation-visualization__routine-control--run"
                      onClick={() => handleRunRoutine(routine.id)}
                      title="Run now"
                    >

                    </button>
                    <button
                      className="automation-visualization__routine-control automation-visualization__routine-control--toggle"
                      onClick={() => handleToggleRoutine(routine.id)}
                      title={routine.enabled ? 'Disable' : 'Enable'}
                    >
                      {routine.enabled ? '' : ''}
                    </button>
                    <button
                      className="automation-visualization__routine-control automation-visualization__routine-control--remove"
                      onClick={() => handleRemoveRoutine(routine.id)}
                      title="Remove"
                    >

                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>

      <div className="automation-visualization__create">
        <button className="automation-visualization__create-button">+ Create New Routine</button>
      </div>
    </div>
  );
};

export default AutomationVisualization;

import { Crosshair } from 'lucide-react';
import {
  CombatWeaponStats,
  WEAPON_COLORS,
  WeaponEffect,
  WeaponSystemProps,
  WeaponUpgrade,
} from '../../types/weapons/WeaponTypes';
import {
  WeaponEffectsDisplay,
  WeaponHeader,
  WeaponStatsDisplay,
  WeaponUpgradeDisplay,
} from './WeaponComponents';

export function WeaponSystem({
  weapon,
  availableUpgrades,
  resources,
  onFire,
  onUpgrade,
  onToggleEffect,
}: WeaponSystemProps) {
  const color = WEAPON_COLORS[weapon.config.category];

  const handleUpgrade = (upgradeId: string, upgrade: WeaponUpgrade) => {
    if (onUpgrade) {
      const updatedStats: CombatWeaponStats = {
        ...weapon.state.currentStats,
        ...upgrade.stats,
      };
      console.debug(`[WeaponSystem] Applying upgrade ${upgradeId}, new stats:`, updatedStats);
      onUpgrade(upgradeId);
    }
  };

  const mapToWeaponEffect = (effect: WeaponEffect): WeaponEffect => ({
    ...effect,
    active: effect.active ?? true,
    cooldown: effect.cooldown ?? 0,
  });

  return (
    <div className={`bg-${color}-900/20 border border-${color}-700/30 rounded-lg p-6`}>
      <WeaponHeader
        name={weapon.config.name}
        tier={weapon.config.tier}
        type={weapon.config.category}
        status={weapon.state.status}
        color={color}
      />

      {/* Main Stats */}
      <div className="mb-6 grid grid-cols-2 gap-4">
        <div>
          <div className="mb-1 text-sm text-gray-400">Damage</div>
          <div className="flex items-baseline space-x-1">
            <span className="text-2xl font-bold text-white">
              {weapon.state.currentStats.damage}
            </span>
            <span className="text-sm text-gray-400">DPS</span>
          </div>
        </div>
        <div>
          <div className="mb-1 text-sm text-gray-400">Range</div>
          <div className="flex items-baseline space-x-1">
            <span className="text-2xl font-bold text-white">{weapon.state.currentStats.range}</span>
            <span className="text-sm text-gray-400">ly</span>
          </div>
        </div>
      </div>

      <WeaponStatsDisplay
        stats={weapon.state.currentStats}
        color={color}
        showAmmo={weapon.state.currentAmmo !== undefined}
        currentAmmo={weapon.state.currentAmmo}
        maxAmmo={weapon.state.maxAmmo}
      />

      <WeaponEffectsDisplay
        effects={weapon.state.effects.map(mapToWeaponEffect)}
        color={color}
        onToggle={onToggleEffect}
      />

      {/* Available Upgrades */}
      {availableUpgrades && availableUpgrades.length > 0 && resources && (
        <div className="mb-6 space-y-3">
          <h4 className="text-sm font-medium text-gray-300">Available Upgrades</h4>
          {availableUpgrades.map((upgrade: WeaponUpgrade) => (
            <WeaponUpgradeDisplay
              key={upgrade.id}
              upgrade={upgrade}
              currentStats={weapon.state.currentStats}
              resources={resources}
              onUpgrade={id => handleUpgrade(id, upgrade)}
            />
          ))}
        </div>
      )}

      {/* Fire Control */}
      <button
        onClick={() => onFire?.(weapon.config.id)}
        disabled={weapon.state.status !== 'ready'}
        className={`flex w-full items-center justify-center space-x-2 rounded-lg px-4 py-3 ${
          weapon.state.status === 'ready'
            ? `bg-${color}-600 hover:bg-${color}-700 text-white`
            : 'cursor-not-allowed bg-gray-700 text-gray-500'
        }`}
      >
        <Crosshair className="h-5 w-5" />
        <span>Fire Weapon</span>
      </button>
    </div>
  );
}

