// Visual Effect Class
export class WeaponEffectVisual extends VisualEffect {
  protected override config: WeaponEffectConfig;
  private trailPoints: Position[] = [];
  private impactParticles: Position[] = [];
  private impactStartTime: number = 0;

  constructor(config: WeaponEffectConfig) {
    super(config);
    this.config = config;
  }

  protected getEffectType(): string {
    return `weapon-${this.config.type}`;
  }

  protected onStart(): void {
    // Initialize trail points
    this.trailPoints = [this.config.position];

    // Debug logging
    console.debug(`[WeaponEffect] Started ${this.config.type} effect`);
  }

  protected onUpdate(progress: number): void {
    // Update trail points
    if (this.config.target) {
      const currentPoint = {
        x: this.config.position.x + (this.config.target.x - this.config.position.x) * progress,
        y: this.config.position.y + (this.config.target.y - this.config.position.y) * progress,
      };

      this.trailPoints.push(currentPoint);

      // Keep only recent points
      while (this.trailPoints.length > 10) {
        this.trailPoints.shift();
      }

      // Create impact when projectile reaches target
      if (progress >= 1 && !this.impactStartTime) {
        this.impactStartTime = Date.now();
        this.createImpactParticles();
      }
    }
  }

  protected onComplete(): void {
    this.trailPoints = [];
    this.impactParticles = [];
    this.impactStartTime = 0;

    console.debug(`[WeaponEffect] Completed ${this.config.type} effect`);
  }

  protected onReset(): void {
    this.trailPoints = [];
    this.impactParticles = [];
    this.impactStartTime = 0;
  }

  protected updateRendering(batcher: RenderBatcher): void {
    if (!this.batchId) {
      return;
    }

    // Render trail
    this.renderTrail(batcher);

    // Render impact
    if (this.impactStartTime) {
      this.renderImpact(batcher);
    }
  }

  private renderTrail(batcher: RenderBatcher): void {
    // Different trail rendering based on weapon type
    switch (this.config.type) {
      case 'machineGun':
        this.renderProjectileTrail(batcher);
        break;
      case 'gaussCannon':
      case 'railGun':
        this.renderBeamTrail(batcher);
        break;
      case 'mgss':
        this.renderEnergyTrail(batcher);
        break;
      case 'rockets':
        this.renderRocketTrail(batcher);
        break;
      case 'harmonicCannon':
        this.renderHarmonicTrail(batcher);
        break;
      case 'temporalCannon':
        this.renderTemporalTrail(batcher);
        break;
      case 'quantumCannon':
        this.renderQuantumTrail(batcher);
        break;
    }
  }

  private renderProjectileTrail(batcher: RenderBatcher): void {
    // Simple projectile with small trail
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;
      batcher.addItem(this.batchId!, {
        id: `${this.id}-trail-${index}`,
        position: point,
        size: { width: 4, height: 4 },
        rotation: 0,
        opacity,
        color: this.config.color || '#ffff00',
        shader: 'additive',
      });
    });
  }

  private renderBeamTrail(batcher: RenderBatcher): void {
    // Solid beam with glow
    if (this.trailPoints.length < 2) {
      return;
    }

    const start = this.trailPoints[0];
    const end = this.trailPoints[this.trailPoints.length - 1];
    const angle = Math.atan2(end.y - start.y, end.x - start.x);
    const length = Math.sqrt(Math.pow(end.x - start.x, 2) + Math.pow(end.y - start.y, 2));

    // Core beam
    batcher.addItem(this.batchId!, {
      id: `${this.id}-beam`,
      position: {
        x: (start.x + end.x) / 2,
        y: (start.y + end.y) / 2,
      },
      size: { width: length, height: 4 },
      rotation: angle,
      opacity: this.config.opacity || 1,
      color: this.config.color || '#00ffff',
      shader: 'additive',
    });

    // Glow effect
    batcher.addItem(this.batchId!, {
      id: `${this.id}-glow`,
      position: {
        x: (start.x + end.x) / 2,
        y: (start.y + end.y) / 2,
      },
      size: { width: length, height: 12 },
      rotation: angle,
      opacity: (this.config.opacity || 1) * 0.5,
      color: this.config.color || '#00ffff',
      shader: 'additive',
    });
  }

  private renderEnergyTrail(batcher: RenderBatcher): void {
    // Pulsing energy trail
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;
      const pulse = Math.sin(Date.now() / 100 + index) * 0.3 + 0.7;

      batcher.addItem(this.batchId!, {
        id: `${this.id}-trail-${index}`,
        position: point,
        size: { width: 6, height: 6 },
        rotation: 0,
        opacity: opacity * pulse,
        color: this.config.color || '#ff00ff',
        shader: 'additive',
      });
    });
  }

  private renderRocketTrail(batcher: RenderBatcher): void {
    // Rocket with smoke trail
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;

      // Smoke
      batcher.addItem(this.batchId!, {
        id: `${this.id}-smoke-${index}`,
        position: point,
        size: { width: 8, height: 8 },
        rotation: Math.random() * Math.PI * 2,
        opacity: opacity * 0.3,
        color: '#888888',
        shader: 'normal',
      });

      // Fire
      batcher.addItem(this.batchId!, {
        id: `${this.id}-fire-${index}`,
        position: point,
        size: { width: 6, height: 6 },
        rotation: 0,
        opacity: opacity,
        color: this.config.color || '#ff4400',
        shader: 'additive',
      });
    });
  }

  private renderHarmonicTrail(batcher: RenderBatcher): void {
    // Harmonic wave pattern
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;
      const wave = Math.sin(Date.now() / 200 + index);
      const offset = wave * 10;

      batcher.addItem(this.batchId!, {
        id: `${this.id}-trail-${index}`,
        position: {
          x: point.x + offset,
          y: point.y + offset,
        },
        size: { width: 8, height: 8 },
        rotation: wave * Math.PI,
        opacity: opacity,
        color: this.config.color || '#00ff88',
        shader: 'additive',
      });
    });
  }

  private renderTemporalTrail(batcher: RenderBatcher): void {
    // Time distortion effect
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;
      const time = Date.now() / 1000;
      const distortion = Math.sin(time * 2 + index);

      for (let i = 0; i < 3; i++) {
        const offset = distortion * (i + 1) * 5;
        batcher.addItem(this.batchId!, {
          id: `${this.id}-trail-${index}-${i}`,
          position: {
            x: point.x + offset,
            y: point.y + offset,
          },
          size: { width: 6 - i * 2, height: 6 - i * 2 },
          rotation: time + (i * Math.PI) / 3,
          opacity: opacity * (1 - i * 0.2),
          color: this.config.color || '#8800ff',
          shader: 'additive',
        });
      }
    });
  }

  private renderQuantumTrail(batcher: RenderBatcher): void {
    // Quantum tunneling effect
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;
      const time = Date.now() / 1000;

      // Phase shift effect
      for (let i = 0; i < 4; i++) {
        const phase = (time + (i * Math.PI) / 2) % (Math.PI * 2);
        const shift = Math.sin(phase) * 10;

        batcher.addItem(this.batchId!, {
          id: `${this.id}-trail-${index}-${i}`,
          position: {
            x: point.x + Math.cos(phase) * shift,
            y: point.y + Math.sin(phase) * shift,
          },
          size: { width: 5, height: 5 },
          rotation: phase,
          opacity: opacity * Math.abs(Math.sin(phase)),
          color: this.config.color || '#0088ff',
          shader: 'additive',
        });
      }
    });
  }

  private renderImpact(batcher: RenderBatcher): void {
    const impactProgress = Math.min(
      1,
      (Date.now() - this.impactStartTime) / (this.config.duration || 1000)
    );

    // Update impact particles
    this.impactParticles.forEach((particle, index) => {
      const particleProgress = impactProgress * (1 + index * 0.1);
      if (particleProgress >= 1) {
        return;
      }

      batcher.addItem(this.batchId!, {
        id: `${this.id}-impact-${index}`,
        position: particle,
        size: {
          width: (this.config.impactSize || 20) * (1 - particleProgress),
          height: (this.config.impactSize || 20) * (1 - particleProgress),
        },
        rotation: (index * Math.PI) / 4,
        opacity: 1 - particleProgress,
        color: this.config.color || '#ffffff',
        shader: 'additive',
      });
    });
  }

  private createImpactParticles(): void {
    if (!this.config.target) {
      return;
    }

    // Create particles in a circular pattern
    const particleCount = 8;
    for (let i = 0; i < particleCount; i++) {
      const angle = (i / particleCount) * Math.PI * 2;
      const distance = (this.config.impactSize || 20) / 2;

      this.impactParticles.push({
        x: this.config.target.x + Math.cos(angle) * distance,
        y: this.config.target.y + Math.sin(angle) * distance,
      });
    }
  }
}

import { useEffect, useState } from 'react';
import { behaviorTreeManager } from '../../managers/ai/BehaviorTreeManager';
import { combatManager } from '../../managers/combat/combatManager';
import { CombatUnit } from '../../types/combat/CombatTypes';
import { FactionId } from '../../types/ships/FactionTypes';
import { convertToCombatTypesUnit } from '../../utils/typeConversions';

interface CombatAIState {
  isActive: boolean;
  currentBehavior: string;
  lastAction: string;
  performance: {
    successRate: number;
    actionsPerMinute: number;
    damageDealt: number;
    damageTaken: number;
  };
}

// Define formation interface to replace any type
interface UnitFormation {
  type: 'offensive' | 'defensive' | 'balanced';
  spacing: number;
  facing: number;
}

// Extend CombatUnit with faction property
interface ExtendedCombatUnit extends CombatUnit {
  faction: FactionId;
  formation?: UnitFormation;
}

export function useCombatAI(unitId: string, factionId: FactionId) {
  const [state, setState] = useState<CombatAIState>({
    isActive: false,
    currentBehavior: 'idle',
    lastAction: '',
    performance: {
      successRate: 0,
      actionsPerMinute: 0,
      damageDealt: 0,
      damageTaken: 0,
    },
  });

  useEffect(() => {
    let actionCount = 0;
    let successCount = 0;
    const startTime = Date.now();

    const handleNodeExecuted = ({ nodeId, success }: { nodeId: string; success: boolean }) => {
      successCount += success ? 1 : 0;
      const totalExecutions = successCount + (actionCount - successCount);
      const successRate = totalExecutions > 0 ? successCount / totalExecutions : 0;

      setState(prev => ({
        ...prev,
        performance: {
          ...prev.performance,
          successRate,
        },
      }));
    };

    const handleActionStarted = ({ actionType }: { actionType: string }) => {
      actionCount++;
      const elapsedMinutes = (Date.now() - startTime) / 60000;

      setState(prev => ({
        ...prev,
        lastAction: actionType,
        performance: {
          ...prev.performance,
          actionsPerMinute: actionCount / elapsedMinutes,
        },
      }));
    };

    behaviorTreeManager.on('nodeExecuted', handleNodeExecuted);
    behaviorTreeManager.on('actionStarted', handleActionStarted);

    // Update AI context periodically
    const updateInterval = setInterval(() => {
      const managerUnit = combatManager.getUnitStatus(unitId);
      if (!managerUnit) {
        return;
      }

      // Convert to CombatTypes.CombatUnit
      const unit = convertToCombatTypesUnit(managerUnit) as ExtendedCombatUnit;

      const nearbyUnits = combatManager
        .getUnitsInRange(unit.position, 500)
        .map(convertToCombatTypesUnit) as ExtendedCombatUnit[];
      const nearbyEnemies = nearbyUnits.filter(u => u.faction !== factionId);
      const nearbyAllies = nearbyUnits.filter(u => u.faction === factionId);

      // Calculate fleet strength and threat level
      const fleetStrength = calculateFleetStrength(unit, nearbyAllies);
      const threatLevel = calculateThreatLevel(nearbyEnemies);

      // Update behavior tree context
      behaviorTreeManager.updateContext(unitId, {
        unit,
        factionId,
        fleetStrength,
        threatLevel,
        nearbyEnemies,
        nearbyAllies,
        currentFormation: unit.formation || {
          type: 'balanced',
          spacing: 100,
          facing: 0,
        },
        cooldowns: {},
      });

      // Evaluate behavior tree
      const treeId = `${factionId}-combat`;
      const success = behaviorTreeManager.evaluateTree(unitId, treeId);

      setState(prev => ({
        ...prev,
        isActive: true,
        currentBehavior: success ? 'executing' : 'idle',
      }));
    }, 250); // Fast updates for combat

    return () => {
      clearInterval(updateInterval);
      behaviorTreeManager.off('nodeExecuted', handleNodeExecuted);
      behaviorTreeManager.off('actionStarted', handleActionStarted);
    };
  }, [unitId, factionId]);

  return state;
}

function calculateFleetStrength(unit: CombatUnit, allies: CombatUnit[]): number {
  const unitStrength =
    (unit.stats.health / unit.stats.maxHealth) * (unit.stats.shield / unit.stats.maxShield);
  const allyStrength = allies.reduce((sum, ally) => {
    return (
      sum + (ally.stats.health / ally.stats.maxHealth) * (ally.stats.shield / ally.stats.maxShield)
    );
  }, 0);

  return (unitStrength + allyStrength) / (1 + allies.length);
}

function calculateThreatLevel(enemies: CombatUnit[]): number {
  if (enemies.length === 0) {
    return 0;
  }

  return (
    enemies.reduce((sum, enemy) => {
      const baseStrength =
        (enemy.stats.health / enemy.stats.maxHealth) * (enemy.stats.shield / enemy.stats.maxShield);
      const weaponStrength = enemy.weapons.reduce((total, w) => total + w.damage, 0) / 100;
      return sum + baseStrength * (1 + weaponStrength);
    }, 0) / enemies.length
  );
}

import { useCallback, useEffect, useState } from 'react';
import { moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import {
  ExtendedModuleStatus,
  ModuleStatusDetails,
  moduleStatusManager,
} from '../../managers/module/ModuleStatusManager';

/**
 * Hook for tracking and managing module status
 */
export function useModuleStatus(moduleId?: string) {
  const [statusDetails, setStatusDetails] = useState<ModuleStatusDetails | null>(null);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Load status details
  useEffect(() => {
    if (!moduleId) {
      setStatusDetails(null);
      setIsLoading(false);
      return;
    }

    try {
      const details = moduleStatusManager.getModuleStatusDetails(moduleId);

      if (details) {
        setStatusDetails(details);
      } else {
        // Initialize status tracking if not already done
        moduleStatusManager.initializeModuleStatus(moduleId);
        const newDetails = moduleStatusManager.getModuleStatusDetails(moduleId);
        setStatusDetails(newDetails || null);
      }

      setIsLoading(false);
    } catch (err) {
      setError(`Error loading module status: ${err}`);
      setIsLoading(false);
    }
  }, [moduleId]);

  // Subscribe to status events
  useEffect(() => {
    if (!moduleId) {
      return;
    }

    const handleStatusChanged = (event: any) => {
      if (event.moduleId === moduleId) {
        const details = moduleStatusManager.getModuleStatusDetails(moduleId);
        setStatusDetails(details || null);
      }
    };

    const handleErrorOccurred = (event: any) => {
      if (event.moduleId === moduleId) {
        const details = moduleStatusManager.getModuleStatusDetails(moduleId);
        setStatusDetails(details || null);
      }
    };

    // Subscribe to events
    const unsubscribeStatus = moduleEventBus.subscribe(
      'STATUS_CHANGED' as ModuleEventType,
      handleStatusChanged
    );
    const unsubscribeError = moduleEventBus.subscribe(
      'ERROR_OCCURRED' as ModuleEventType,
      handleErrorOccurred
    );

    return () => {
      if (typeof unsubscribeStatus === 'function') {
        unsubscribeStatus();
      }
      if (typeof unsubscribeError === 'function') {
        unsubscribeError();
      }
    };
  }, [moduleId]);

  // Update module status
  const updateStatus = useCallback(
    (status: ExtendedModuleStatus, reason?: string) => {
      if (!moduleId) {
        return false;
      }
      return moduleStatusManager.updateModuleStatus(moduleId, status, reason);
    },
    [moduleId]
  );

  // Add an alert
  const addAlert = useCallback(
    (level: 'info' | 'warning' | 'error' | 'critical', message: string) => {
      if (!moduleId) {
        return;
      }
      moduleStatusManager.addAlert(moduleId, level, message);
    },
    [moduleId]
  );

  // Acknowledge an alert
  const acknowledgeAlert = useCallback(
    (alertIndex: number) => {
      if (!moduleId) {
        return false;
      }
      return moduleStatusManager.acknowledgeAlert(moduleId, alertIndex);
    },
    [moduleId]
  );

  // Get status color
  const getStatusColor = useCallback((status?: ExtendedModuleStatus): string => {
    if (!status) {
      return 'gray';
    }

    switch (status) {
      case 'active':
        return 'green';
      case 'constructing':
        return 'yellow';
      case 'inactive':
        return 'gray';
      case 'optimized':
        return 'blue';
      case 'boost':
        return 'purple';
      case 'degraded':
        return 'orange';
      case 'overloaded':
        return 'orange';
      case 'maintenance':
      case 'upgrading':
      case 'repairing':
        return 'yellow';
      case 'error':
        return 'red';
      case 'critical':
        return 'darkred';
      case 'offline':
        return 'black';
      case 'standby':
        return 'lightblue';
      case 'powersave':
        return 'teal';
      default:
        return 'gray';
    }
  }, []);

  // Get alert color
  const getAlertColor = useCallback((level: 'info' | 'warning' | 'error' | 'critical'): string => {
    switch (level) {
      case 'info':
        return 'blue';
      case 'warning':
        return 'orange';
      case 'error':
        return 'red';
      case 'critical':
        return 'darkred';
      default:
        return 'gray';
    }
  }, []);

  // Format uptime
  const formatUptime = useCallback((uptime: number): string => {
    const seconds = Math.floor(uptime / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    if (days > 0) {
      return `${days}d ${hours % 24}h`;
    }

    if (hours > 0) {
      return `${hours}h ${minutes % 60}m`;
    }

    if (minutes > 0) {
      return `${minutes}m ${seconds % 60}s`;
    }

    return `${seconds}s`;
  }, []);

  return {
    // State
    statusDetails,
    isLoading,
    error,

    // Current status
    currentStatus: statusDetails?.currentStatus,
    previousStatus: statusDetails?.previousStatus,
    history: statusDetails?.history || [],
    metrics: statusDetails?.metrics,
    alerts: statusDetails?.alerts || [],

    // Actions
    updateStatus,
    addAlert,
    acknowledgeAlert,

    // Utilities
    getStatusColor,
    getAlertColor,
    formatUptime,
  };
}

/**
 * Hook for tracking modules with specific status or alerts
 */
export function useModulesWithStatus(
  status?: ExtendedModuleStatus,
  alertLevel?: 'info' | 'warning' | 'error' | 'critical'
) {
  const [moduleIds, setModuleIds] = useState<string[]>([]);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Load modules with status or alerts
  useEffect(() => {
    try {
      let ids: string[] = [];

      if (status) {
        ids = moduleStatusManager.getModulesByStatus(status);
      } else if (alertLevel) {
        ids = moduleStatusManager.getModulesWithAlerts(alertLevel);
      } else {
        // Get all modules with any alerts
        ids = moduleStatusManager.getModulesWithAlerts();
      }

      setModuleIds(ids);
      setIsLoading(false);
    } catch (err) {
      setError(`Error loading modules: ${err}`);
      setIsLoading(false);
    }
  }, [status, alertLevel]);

  // Subscribe to status events
  useEffect(() => {
    const handleStatusChanged = () => {
      try {
        let ids: string[] = [];

        if (status) {
          ids = moduleStatusManager.getModulesByStatus(status);
        } else if (alertLevel) {
          ids = moduleStatusManager.getModulesWithAlerts(alertLevel);
        } else {
          // Get all modules with any alerts
          ids = moduleStatusManager.getModulesWithAlerts();
        }

        setModuleIds(ids);
      } catch (err) {
        setError(`Error updating modules: ${err}`);
      }
    };

    const handleErrorOccurred = () => {
      if (alertLevel || !status) {
        try {
          const ids = alertLevel
            ? moduleStatusManager.getModulesWithAlerts(alertLevel)
            : moduleStatusManager.getModulesWithAlerts();

          setModuleIds(ids);
        } catch (err) {
          setError(`Error updating modules: ${err}`);
        }
      }
    };

    // Subscribe to events
    const unsubscribeStatus = moduleEventBus.subscribe(
      'STATUS_CHANGED' as ModuleEventType,
      handleStatusChanged
    );
    const unsubscribeError = moduleEventBus.subscribe(
      'ERROR_OCCURRED' as ModuleEventType,
      handleErrorOccurred
    );

    return () => {
      if (typeof unsubscribeStatus === 'function') {
        unsubscribeStatus();
      }
      if (typeof unsubscribeError === 'function') {
        unsubscribeError();
      }
    };
  }, [status, alertLevel]);

  return {
    moduleIds,
    isLoading,
    error,
  };
}

import { useCallback, useEffect, useState } from 'react';
import { ModuleEvent, moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import { moduleManager } from '../../managers/module/ModuleManager';
import {
  ModuleUpgradeEffect,
  moduleUpgradeManager,
  ModuleUpgradeStatus,
} from '../../managers/module/ModuleUpgradeManager';

/**
 * Interface for module event data
 */
interface ModuleEventData {
  moduleId: string;
  [key: string]: unknown;
}

/**
 * Hook for managing module upgrades
 * @param moduleId The ID of the module to manage upgrades for
 */
export function useModuleUpgrade(moduleId?: string) {
  const [upgradeStatus, setUpgradeStatus] = useState<ModuleUpgradeStatus | null>(null);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Load upgrade status
  useEffect(() => {
    if (!moduleId) {
      setUpgradeStatus(null);
      setIsLoading(false);
      return;
    }

    try {
      const status = moduleUpgradeManager.getUpgradeStatus(moduleId);
      setUpgradeStatus(status || null);
      setIsLoading(false);
    } catch (err) {
      setError(`Error loading upgrade status: ${err}`);
      setIsLoading(false);
    }
  }, [moduleId]);

  // Subscribe to module events
  useEffect(() => {
    if (!moduleId) {
      return;
    }

    const handleModuleUpgraded = (event: ModuleEvent) => {
      if (event.moduleId === moduleId) {
        try {
          const status = moduleUpgradeManager.getUpgradeStatus(moduleId);
          setUpgradeStatus(status || null);
        } catch (err) {
          setError(`Error updating upgrade status: ${err}`);
        }
      }
    };

    const handleUpgradeStarted = (event: ModuleEvent) => {
      if (event.moduleId === moduleId) {
        try {
          const status = moduleUpgradeManager.getUpgradeStatus(moduleId);
          setUpgradeStatus(status || null);
        } catch (err) {
          setError(`Error updating upgrade status: ${err}`);
        }
      }
    };

    const handleUpgradeCancelled = (event: ModuleEvent) => {
      if (event.moduleId === moduleId) {
        try {
          const status = moduleUpgradeManager.getUpgradeStatus(moduleId);
          setUpgradeStatus(status || null);
        } catch (err) {
          setError(`Error updating upgrade status: ${err}`);
        }
      }
    };

    // Subscribe to events
    const unsubscribeUpgraded = moduleEventBus.subscribe(
      'MODULE_UPGRADED' as ModuleEventType,
      handleModuleUpgraded
    );
    const unsubscribeStarted = moduleEventBus.subscribe(
      'MODULE_UPGRADE_STARTED' as ModuleEventType,
      handleUpgradeStarted
    );
    const unsubscribeCancelled = moduleEventBus.subscribe(
      'MODULE_UPGRADE_CANCELLED' as ModuleEventType,
      handleUpgradeCancelled
    );

    // Set up progress update interval for active upgrades
    const progressInterval = setInterval(() => {
      if (upgradeStatus?.upgradeProgress !== undefined) {
        try {
          const status = moduleUpgradeManager.getUpgradeStatus(moduleId);
          setUpgradeStatus(status || null);
        } catch (err) {
          // Silently ignore errors during progress updates
        }
      }
    }, 1000); // Update every second

    return () => {
      // Unsubscribe from events
      if (typeof unsubscribeUpgraded === 'function') {
        unsubscribeUpgraded();
      }
      if (typeof unsubscribeStarted === 'function') {
        unsubscribeStarted();
      }
      if (typeof unsubscribeCancelled === 'function') {
        unsubscribeCancelled();
      }

      // Clear interval
      clearInterval(progressInterval);
    };
  }, [moduleId, upgradeStatus?.upgradeProgress]);

  // Start upgrade
  const startUpgrade = useCallback(() => {
    if (!moduleId) {
      return false;
    }
    return moduleUpgradeManager.startUpgrade(moduleId);
  }, [moduleId]);

  // Cancel upgrade
  const cancelUpgrade = useCallback(() => {
    if (!moduleId) {
      return false;
    }
    return moduleUpgradeManager.cancelUpgrade(moduleId);
  }, [moduleId]);

  // Format time remaining
  const formatTimeRemaining = useCallback((milliseconds: number): string => {
    const seconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);

    if (hours > 0) {
      return `${hours}h ${minutes % 60}m`;
    }

    if (minutes > 0) {
      return `${minutes}m ${seconds % 60}s`;
    }

    return `${seconds}s`;
  }, []);

  // Get effect description
  const getEffectDescription = useCallback((effect: ModuleUpgradeEffect): string => {
    const sign = effect.value >= 0 ? '+' : '';
    const valueStr = effect.isPercentage ? `${sign}${effect.value}%` : `${sign}${effect.value}`;

    return `${effect.description} (${valueStr})`;
  }, []);

  return {
    // State
    upgradeStatus,
    isLoading,
    error,

    // Current status
    currentLevel: upgradeStatus?.currentLevel,
    maxLevel: upgradeStatus?.maxLevel,
    nextLevel: upgradeStatus?.nextLevel,
    upgradeAvailable: upgradeStatus?.upgradeAvailable,
    requirementsMet: upgradeStatus?.requirementsMet,
    missingRequirements: upgradeStatus?.missingRequirements,
    upgradeProgress: upgradeStatus?.upgradeProgress,
    estimatedTimeRemaining: upgradeStatus?.estimatedTimeRemaining,
    effects: upgradeStatus?.effects,

    // Actions
    startUpgrade,
    cancelUpgrade,

    // Utilities
    formatTimeRemaining,
    getEffectDescription,
  };
}

/**
 * Hook for tracking modules with available upgrades
 */
export function useModulesWithAvailableUpgrades() {
  const [moduleIds, setModuleIds] = useState<string[]>([]);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Load modules with available upgrades
  useEffect(() => {
    setIsLoading(true);
    setError(null);

    try {
      // Get all modules
      const modules = moduleManager.getActiveModules();

      // Filter modules with available upgrades
      const availableUpgrades = modules.filter(module => {
        const status = moduleUpgradeManager.getUpgradeStatus(module.id);
        return status?.upgradeAvailable && status?.requirementsMet;
      });

      setModuleIds(availableUpgrades.map(module => module.id));
      setIsLoading(false);
    } catch (err) {
      setError(`Error loading modules with available upgrades: ${err}`);
      setIsLoading(false);
    }
  }, []);

  // Subscribe to module events
  useEffect(() => {
    const handleModuleUpgraded = () => {
      try {
        // Get all modules
        const modules = moduleManager.getActiveModules();

        // Filter modules with available upgrades
        const availableUpgrades = modules.filter(module => {
          const status = moduleUpgradeManager.getUpgradeStatus(module.id);
          return status?.upgradeAvailable && status?.requirementsMet;
        });

        setModuleIds(availableUpgrades.map(module => module.id));
      } catch (err) {
        setError(`Error updating modules with available upgrades: ${err}`);
      }
    };

    const handleResourceChanged = () => {
      // Resource changes might affect upgrade availability
      handleModuleUpgraded();
    };

    // Subscribe to events
    const unsubscribeUpgraded = moduleEventBus.subscribe(
      'MODULE_UPGRADED' as ModuleEventType,
      handleModuleUpgraded
    );
    const unsubscribeResourceChanged = moduleEventBus.subscribe(
      'RESOURCE_CHANGED' as ModuleEventType,
      handleResourceChanged
    );

    return () => {
      // Unsubscribe from events
      if (typeof unsubscribeUpgraded === 'function') {
        unsubscribeUpgraded();
      }
      if (typeof unsubscribeResourceChanged === 'function') {
        unsubscribeResourceChanged();
      }
    };
  }, []);

  return {
    moduleIds,
    isLoading,
    error,
  };
}

import { useCallback, useEffect, useMemo, useState } from 'react';
import { resourceManager } from '../../managers/game/ResourceManager';
import {
  ResourceIntegration,
  createResourceIntegration,
} from '../../managers/resource/ResourceIntegration';
import { ResourceState, ResourceType } from '../../types/resources/ResourceTypes';

// Singleton instance of the resource integration
let resourceIntegrationInstance: ResourceIntegration | null = null;

/**
 * Initialize the resource integration if it hasn't been initialized yet
 */
function getResourceIntegration(): ResourceIntegration {
  if (!resourceIntegrationInstance) {
    resourceIntegrationInstance = createResourceIntegration(resourceManager);
  }

  if (!resourceIntegrationInstance) {
    throw new Error('Failed to create resource integration instance');
  }

  return resourceIntegrationInstance;
}

/**
 * Hook for accessing the resource management system
 */
export function useResourceManagement() {
  const [resourceStates, setResourceStates] = useState<Map<ResourceType, ResourceState>>(new Map());
  const [isInitialized, setIsInitialized] = useState(false);

  // Get or create the resource integration
  const integration = useMemo(() => getResourceIntegration(), []);

  // Initialize the resource states
  useEffect(() => {
    // Get all resource types
    const resourceTypes = Array.from(resourceManager['resources'].keys()) as ResourceType[];

    // Create a map of resource states
    const states = new Map<ResourceType, ResourceState>();
    resourceTypes.forEach(type => {
      const state = resourceManager.getResourceState(type);
      if (state) {
        states.set(type, state);
      }
    });

    setResourceStates(states);
    setIsInitialized(true);
  }, []);

  // Update the resource integration on each frame
  useEffect(() => {
    if (!isInitialized) {
      return;
    }

    const frameId = requestAnimationFrame(time => {
      integration.update(1000 / 60); // Assume 60 FPS
    });

    return () => {
      cancelAnimationFrame(frameId);
    };
  }, [integration, isInitialized]);

  // Clean up the resource integration when the component unmounts
  useEffect(() => {
    return () => {
      if (resourceIntegrationInstance) {
        resourceIntegrationInstance.cleanup();
        resourceIntegrationInstance = null;
      }
    };
  }, []);

  // Get a resource state
  const getResourceState = useCallback(
    (type: ResourceType): ResourceState | undefined => {
      return resourceStates.get(type);
    },
    [resourceStates]
  );

  // Get all resource states
  const getAllResourceStates = useCallback((): Map<ResourceType, ResourceState> => {
    return resourceStates;
  }, [resourceStates]);

  // Get resource amount
  const getResourceAmount = useCallback(
    (type: ResourceType): number => {
      return resourceStates.get(type)?.current || 0;
    },
    [resourceStates]
  );

  // Check if a resource is available
  const hasResource = useCallback(
    (type: ResourceType, amount: number): boolean => {
      const state = resourceStates.get(type);
      return state ? state.current >= amount : false;
    },
    [resourceStates]
  );

  // Check if multiple resources are available
  const hasResources = useCallback(
    (resources: Array<{ type: ResourceType; amount: number }>): boolean => {
      return resources.every(({ type, amount }) => hasResource(type, amount));
    },
    [hasResource]
  );

  // Consume a resource
  const consumeResource = useCallback(
    (type: ResourceType, amount: number): boolean => {
      if (!hasResource(type, amount)) {
        return false;
      }

      resourceManager.removeResource(type, amount);
      return true;
    },
    [hasResource]
  );

  // Consume multiple resources
  const consumeResources = useCallback(
    (resources: Array<{ type: ResourceType; amount: number }>): boolean => {
      if (!hasResources(resources)) {
        return false;
      }

      resources.forEach(({ type, amount }) => {
        resourceManager.removeResource(type, amount);
      });

      return true;
    },
    [hasResources]
  );

  // Add a resource
  const addResource = useCallback((type: ResourceType, amount: number): void => {
    resourceManager.addResource(type, amount);
  }, []);

  // Add multiple resources
  const addResources = useCallback(
    (resources: Array<{ type: ResourceType; amount: number }>): void => {
      resources.forEach(({ type, amount }) => {
        resourceManager.addResource(type, amount);
      });
    },
    []
  );

  // Get resource production rate
  const getProductionRate = useCallback(
    (type: ResourceType): number => {
      return resourceStates.get(type)?.production || 0;
    },
    [resourceStates]
  );

  // Get resource consumption rate
  const getConsumptionRate = useCallback(
    (type: ResourceType): number => {
      return resourceStates.get(type)?.consumption || 0;
    },
    [resourceStates]
  );

  // Set resource production rate
  const setProductionRate = useCallback((type: ResourceType, rate: number): void => {
    resourceManager.setResourceProduction(type, rate);
  }, []);

  // Set resource consumption rate
  const setConsumptionRate = useCallback((type: ResourceType, rate: number): void => {
    resourceManager.setResourceConsumption(type, rate);
  }, []);

  // Get resource capacity
  const getResourceCapacity = useCallback(
    (type: ResourceType): number => {
      return resourceStates.get(type)?.max || 0;
    },
    [resourceStates]
  );

  // Get resource percentage
  const getResourcePercentage = useCallback(
    (type: ResourceType): number => {
      const state = resourceStates.get(type);
      if (!state || state.max === 0) {
        return 0;
      }
      return (state.current / state.max) * 100;
    },
    [resourceStates]
  );

  return {
    // Resource state
    getResourceState,
    getAllResourceStates,
    getResourceAmount,
    hasResource,
    hasResources,

    // Resource modification
    consumeResource,
    consumeResources,
    addResource,
    addResources,

    // Resource rates
    getProductionRate,
    getConsumptionRate,
    setProductionRate,
    setConsumptionRate,

    // Resource capacity
    getResourceCapacity,
    getResourcePercentage,

    // Initialization state
    isInitialized,
  };
}

import { useCallback, useEffect, useMemo, useState } from 'react';
import {
  ResourceTotals,
  SerializedResource,
  SerializedResourceState,
  SerializedThreshold,
  isSerializedResourceState,
  serializeResourceMap,
  validateResourceState,
} from '../../types/resources/ResourceSerializationTypes';
import {
  ResourceAlert,
  ResourceState,
  ResourceThreshold,
  ResourceTransfer,
  ResourceType,
} from '../../types/resources/ResourceTypes';

/**
 * Resource tracking options
 */
export interface ResourceTrackingOptions {
  types?: ResourceType[];
  updateInterval?: number;
  historyLimit?: number;
  enableAlerts?: boolean;
  enableThresholds?: boolean;
}

/**
 * Resource tracking state
 */
export interface ResourceTrackingState {
  resources: Map<ResourceType, ResourceState>;
  history: ResourceTransfer[];
  alerts: ResourceAlert[];
  lastUpdated: number;
  thresholds: Map<ResourceType, ResourceThreshold[]>;
}

/**
 * Resource tracking result
 */
export interface ResourceTrackingResult {
  // Resource states
  resources: Map<ResourceType, ResourceState>;
  resourceList: Array<{ type: ResourceType; state: ResourceState }>;
  getResource: (type: ResourceType) => ResourceState | undefined;

  // Resource history
  history: ResourceTransfer[];
  getHistoryByType: (type: ResourceType) => ResourceTransfer[];
  clearHistory: () => void;

  // Resource alerts
  alerts: ResourceAlert[];
  getAlertsByType: (type: ResourceType) => ResourceAlert[];
  clearAlerts: () => void;
  dismissAlert: (id: string) => void;

  // Resource thresholds
  setThreshold: (type: ResourceType, threshold: ResourceThreshold) => void;
  removeThreshold: (type: ResourceType) => void;

  // Resource updates
  updateResource: (type: ResourceType, update: Partial<ResourceState>) => void;
  incrementResource: (type: ResourceType, amount: number) => void;
  decrementResource: (type: ResourceType, amount: number) => void;
  transferResource: (transfer: ResourceTransfer) => boolean;

  // Utility functions
  getTotalResources: () => number;
  getResourcePercentage: (type: ResourceType) => number;
  getResourcesAboveThreshold: (percentage: number) => ResourceType[];
  getResourcesBelowThreshold: (percentage: number) => ResourceType[];

  // Metadata
  lastUpdated: number;
  isLoading: boolean;
  error: Error | null;
}

/**
 * Hook for tracking resources globally
 */
export function useResourceTracking(options: ResourceTrackingOptions = {}): ResourceTrackingResult {
  // Default options
  const {
    types = ['minerals', 'energy', 'population', 'research', 'plasma', 'gas', 'exotic'],
    updateInterval = 1000,
    historyLimit = 100,
    enableAlerts = true,
    enableThresholds = true,
  } = options;

  // State
  const [state, setState] = useState<ResourceTrackingState>({
    resources: new Map(),
    history: [],
    alerts: [],
    lastUpdated: Date.now(),
    thresholds: new Map(),
  });

  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<Error | null>(null);

  // Initialize resources
  useEffect(() => {
    const initialResources = new Map<ResourceType, ResourceState>();
    const initialThresholds = new Map<ResourceType, ResourceThreshold[]>();

    // Initialize with default values
    for (const type of types) {
      initialResources.set(type, {
        current: 0,
        min: 0,
        max: 100,
        production: 0,
        consumption: 0,
      });
      initialThresholds.set(type, []);
    }

    // Try to load from storage
    try {
      const savedResources = localStorage.getItem('resources');
      if (savedResources) {
        const parsed = JSON.parse(savedResources);

        // Validate the parsed data
        if (isSerializedResourceState(parsed) && validateResourceState(parsed)) {
          // Convert serialized resources to Map with proper type conversion
          const resourceMap = new Map<ResourceType, ResourceState>();

          // Process each resource entry with proper conversion
          for (const [key, value] of Object.entries(parsed.resources)) {
            if (types.includes(key as ResourceType)) {
              const serializedResource = value as SerializedResource;
              resourceMap.set(key as ResourceType, {
                current: serializedResource.current,
                min: 0, // Default value
                max: serializedResource.capacity || 100, // Use capacity or default
                production: serializedResource.production,
                consumption: serializedResource.consumption,
              });
            }
          }

          // Convert serialized thresholds to Map
          const thresholdMap = new Map<ResourceType, ResourceThreshold[]>();
          for (const [key, thresholds] of Object.entries(parsed.thresholds)) {
            if (types.includes(key as ResourceType)) {
              thresholdMap.set(key as ResourceType, thresholds as ResourceThreshold[]);
            }
          }

          setState(prev => ({
            ...prev,
            resources: resourceMap,
            thresholds: thresholdMap,
            alerts: parsed.alerts || [],
            lastUpdated: parsed.timestamp || Date.now(),
          }));
        } else {
          console.warn('Invalid resource data in localStorage, using defaults');
          setState(prev => ({
            ...prev,
            resources: initialResources,
            thresholds: initialThresholds,
            lastUpdated: Date.now(),
          }));
        }
      } else {
        setState(prev => ({
          ...prev,
          resources: initialResources,
          thresholds: initialThresholds,
          lastUpdated: Date.now(),
        }));
      }

      setIsLoading(false);
    } catch (err) {
      setError(err instanceof Error ? err : new Error('Failed to load resources'));
      setIsLoading(false);
    }
  }, [types]);

  // Save resources to storage
  useEffect(() => {
    if (isLoading) {
      return;
    }

    try {
      // Serialize the state
      const serializedState: SerializedResourceState = {
        resources: serializeResourceMap(state.resources) as Record<
          ResourceType,
          SerializedResource
        >,
        thresholds: serializeResourceMap(state.thresholds) as Record<string, SerializedThreshold[]>,
        alerts: state.alerts,
        timestamp: Date.now(),
      };

      localStorage.setItem('resources', JSON.stringify(serializedState));
    } catch (err) {
      console.error('Failed to save resources:', err);
    }
  }, [state, isLoading]);

  // Check thresholds
  useEffect(() => {
    if (!enableThresholds || isLoading) {
      return;
    }

    const checkThresholds = () => {
      const newAlerts: ResourceAlert[] = [];

      // Convert Map entries to array to avoid MapIterator error
      const thresholdEntries = Array.from(state.thresholds.entries());
      for (const [type, thresholdList] of thresholdEntries) {
        const resourceState = state.resources.get(type);
        if (!resourceState) {
          continue;
        }

        for (const threshold of thresholdList) {
          // Check min threshold
          if (threshold.min !== undefined && resourceState.current < threshold.min) {
            newAlerts.push({
              id: `${type}-min-${Date.now()}`,
              type,
              threshold,
              message: `${type} is below minimum threshold (${resourceState.current} < ${threshold.min})`,
              severity: 'critical',
            });
          }

          // Check max threshold
          if (threshold.max !== undefined && resourceState.current > threshold.max) {
            newAlerts.push({
              id: `${type}-max-${Date.now()}`,
              type,
              threshold,
              message: `${type} is above maximum threshold (${resourceState.current} > ${threshold.max})`,
              severity: 'medium',
            });
          }

          // Check target threshold
          if (threshold.target !== undefined) {
            const deviation = Math.abs(resourceState.current - threshold.target);
            const maxDeviation = threshold.target * 0.2; // 20% deviation

            if (deviation > maxDeviation) {
              newAlerts.push({
                id: `${type}-target-${Date.now()}`,
                type,
                threshold,
                message: `${type} is deviating from target (${resourceState.current} vs ${threshold.target})`,
                severity: 'low',
              });
            }
          }
        }
      }

      if (newAlerts.length > 0) {
        setState(prev => ({
          ...prev,
          alerts: [...prev.alerts, ...newAlerts],
        }));
      }
    };

    // Check thresholds initially
    checkThresholds();

    // Set up interval for checking thresholds
    const intervalId = setInterval(checkThresholds, updateInterval);

    return () => {
      clearInterval(intervalId);
    };
  }, [enableThresholds, isLoading, state.resources, state.thresholds, updateInterval]);

  // Update resource
  const updateResource = useCallback((type: ResourceType, update: Partial<ResourceState>) => {
    setState(prev => {
      const resources = new Map(prev.resources);
      const current = resources.get(type) || {
        current: 0,
        min: 0,
        max: 100,
        production: 0,
        consumption: 0,
      };

      resources.set(type, {
        ...current,
        ...update,
      });

      return {
        ...prev,
        resources,
        lastUpdated: Date.now(),
      };
    });
  }, []);

  // Increment resource
  const incrementResource = useCallback(
    (type: ResourceType, amount: number) => {
      if (amount <= 0) {
        return;
      }

      setState(prev => {
        const resources = new Map(prev.resources);
        const current = resources.get(type) || {
          current: 0,
          min: 0,
          max: 100,
          production: 0,
          consumption: 0,
        };

        const newValue = Math.min(current.current + amount, current.max);

        resources.set(type, {
          ...current,
          current: newValue,
        });

        // Add to history
        const transfer: ResourceTransfer = {
          type,
          source: 'production',
          target: 'storage',
          amount,
          timestamp: Date.now(),
        };

        const history = [transfer, ...prev.history].slice(0, historyLimit);

        return {
          ...prev,
          resources,
          history,
          lastUpdated: Date.now(),
        };
      });
    },
    [historyLimit]
  );

  // Decrement resource
  const decrementResource = useCallback(
    (type: ResourceType, amount: number) => {
      if (amount <= 0) {
        return;
      }

      setState(prev => {
        const resources = new Map(prev.resources);
        const current = resources.get(type) || {
          current: 0,
          min: 0,
          max: 100,
          production: 0,
          consumption: 0,
        };

        const newValue = Math.max(current.current - amount, current.min);

        resources.set(type, {
          ...current,
          current: newValue,
        });

        // Add to history
        const transfer: ResourceTransfer = {
          type,
          source: 'storage',
          target: 'consumption',
          amount,
          timestamp: Date.now(),
        };

        const history = [transfer, ...prev.history].slice(0, historyLimit);

        return {
          ...prev,
          resources,
          history,
          lastUpdated: Date.now(),
        };
      });
    },
    [historyLimit]
  );

  // Transfer resource
  const transferResource = useCallback(
    (transfer: ResourceTransfer): boolean => {
      if (transfer.amount <= 0) {
        return false;
      }

      let success = false;

      setState(prev => {
        const resources = new Map(prev.resources);

        // Get source resource
        const sourceResource = resources.get(transfer.type);
        if (!sourceResource || sourceResource.current < transfer.amount) {
          return prev; // Not enough resources
        }

        // Update source
        resources.set(transfer.type, {
          ...sourceResource,
          current: sourceResource.current - transfer.amount,
        });

        // Add to history
        const history = [transfer, ...prev.history].slice(0, historyLimit);

        success = true;

        return {
          ...prev,
          resources,
          history,
          lastUpdated: Date.now(),
        };
      });

      return success;
    },
    [historyLimit]
  );

  // Set threshold
  const setThreshold = useCallback(
    (type: ResourceType, threshold: ResourceThreshold) => {
      const thresholds = new Map(state.thresholds);
      const currentThresholds = thresholds.get(type) || [];
      thresholds.set(type, [...currentThresholds, threshold]);
      setState(prev => ({ ...prev, thresholds }));
    },
    [state.thresholds]
  );

  // Remove threshold
  const removeThreshold = useCallback(
    (type: ResourceType) => {
      const thresholds = new Map(state.thresholds);
      thresholds.set(type, []);
      setState(prev => ({ ...prev, thresholds }));
    },
    [state.thresholds]
  );

  // Get resource
  const getResource = useCallback(
    (type: ResourceType) => {
      return state.resources.get(type);
    },
    [state.resources]
  );

  // Get history by type
  const getHistoryByType = useCallback(
    (type: ResourceType) => {
      return state.history.filter(item => item.type === type);
    },
    [state.history]
  );

  // Clear history
  const clearHistory = useCallback(() => {
    setState(prev => ({
      ...prev,
      history: [],
    }));
  }, []);

  // Get alerts by type
  const getAlertsByType = useCallback(
    (type: ResourceType) => {
      return state.alerts.filter(alert => alert.type === type);
    },
    [state.alerts]
  );

  // Clear alerts
  const clearAlerts = useCallback(() => {
    setState(prev => ({
      ...prev,
      alerts: [],
    }));
  }, []);

  // Dismiss alert
  const dismissAlert = useCallback((id: string) => {
    setState(prev => ({
      ...prev,
      alerts: prev.alerts.filter(alert => alert.id !== id),
    }));
  }, []);

  // Get total resources
  const getTotalResources = useCallback(() => {
    let total = 0;

    // Convert Map entries to array to avoid MapIterator error
    const resourceValues = Array.from(state.resources.values());
    for (const resource of resourceValues) {
      total += resource.current;
    }

    return total;
  }, [state.resources]);

  // Get resource percentage
  const getResourcePercentage = useCallback(
    (type: ResourceType) => {
      const resource = state.resources.get(type);
      if (!resource) {
        return 0;
      }

      return (resource.current / resource.max) * 100;
    },
    [state.resources]
  );

  // Get resources above threshold
  const getResourcesAboveThreshold = useCallback(
    (percentage: number) => {
      const result: ResourceType[] = [];

      // Convert Map entries to array to avoid MapIterator error
      const resourceEntries = Array.from(state.resources.entries());
      for (const [type, resource] of resourceEntries) {
        const resourcePercentage = (resource.current / resource.max) * 100;

        if (resourcePercentage >= percentage) {
          result.push(type);
        }
      }

      return result;
    },
    [state.resources]
  );

  // Get resources below threshold
  const getResourcesBelowThreshold = useCallback(
    (percentage: number) => {
      const result: ResourceType[] = [];

      // Convert Map entries to array to avoid MapIterator error
      const resourceEntries = Array.from(state.resources.entries());
      for (const [type, resource] of resourceEntries) {
        const resourcePercentage = (resource.current / resource.max) * 100;

        if (resourcePercentage <= percentage) {
          result.push(type);
        }
      }

      return result;
    },
    [state.resources]
  );

  // Create resource list
  const resourceList = useMemo(() => {
    return Array.from(state.resources.entries()).map(([type, state]) => ({
      type,
      state,
    }));
  }, [state.resources]);

  // Calculate resource totals
  const calculateTotals = useCallback(
    (resources: Map<ResourceType, ResourceState>): ResourceTotals => {
      const totals: ResourceTotals = {
        production: 0,
        consumption: 0,
        net: 0,
        amounts: {} as Record<ResourceType, number>,
        capacities: {} as Record<ResourceType, number>,
        rates: {} as Record<ResourceType, number>,
      };

      // Convert Map entries to array to avoid MapIterator error
      const resourceEntries = Array.from(resources.entries());
      for (const [type, resource] of resourceEntries) {
        totals.production += resource.production;
        totals.consumption += resource.consumption;

        if (totals.amounts) {
          totals.amounts[type] = resource.current;
        }

        if (totals.capacities) {
          totals.capacities[type] = resource.max;
        }

        if (totals.rates) {
          totals.rates[type] = resource.production - resource.consumption;
        }
      }

      totals.net = totals.production - totals.consumption;
      return totals;
    },
    []
  );

  // Calculate resource percentages
  const calculatePercentages = useCallback(
    (resources: Map<ResourceType, ResourceState>): Record<ResourceType, number> => {
      const percentages: Record<ResourceType, number> = {} as Record<ResourceType, number>;

      // Convert Map entries to array to avoid MapIterator error
      const resourceEntries = Array.from(resources.entries());
      for (const [type, resource] of resourceEntries) {
        if (resource.max > 0) {
          percentages[type] = (resource.current / resource.max) * 100;
        } else {
          percentages[type] = 0;
        }
      }

      return percentages;
    },
    []
  );

  return {
    // Resource states
    resources: state.resources,
    resourceList,
    getResource,

    // Resource history
    history: state.history,
    getHistoryByType,
    clearHistory,

    // Resource alerts
    alerts: state.alerts,
    getAlertsByType,
    clearAlerts,
    dismissAlert,

    // Resource thresholds
    setThreshold,
    removeThreshold,

    // Resource updates
    updateResource,
    incrementResource,
    decrementResource,
    transferResource,

    // Utility functions
    getTotalResources,
    getResourcePercentage,
    getResourcesAboveThreshold,
    getResourcesBelowThreshold,

    // Metadata
    lastUpdated: state.lastUpdated,
    isLoading,
    error,
  };
}

