import { Subject } from 'rxjs';
import { thresholdEvents } from '../../contexts/ThresholdTypes';
import { AutomationCondition } from '../../managers/game/AutomationManager';
import { MiningShipManagerImpl } from '../../managers/mining/MiningShipManagerImpl';
import { moduleManager } from '../../managers/module/ModuleManager';

// Create a subject to handle condition state
const conditionState = new Subject<{
  resourceId: string;
  currentAmount: number;
  thresholds: { min: number; max: number };
}>();

/**
 * Extended condition type with runtime state
 */
interface RuntimeCondition extends AutomationCondition {
  lastChecked?: number;
}

export class ConditionChecker {
  private lastCheckedTimes: Map<string, number> = new Map();
  private miningManager?: MiningShipManagerImpl;

  constructor(miningManager?: MiningShipManagerImpl) {
    this.miningManager = miningManager;

    // Subscribe to threshold events
    thresholdEvents.subscribe(event => {
      if (event.type === 'THRESHOLD_VIOLATED' || event.type === 'STORAGE_FULL') {
        conditionState.next({
          resourceId: event.resourceId,
          currentAmount: event.details.current,
          thresholds: {
            min: event.details.min || 0,
            max: event.details.max || Infinity,
          },
        });
      }
    });
  }

  private getConditionKey(condition: AutomationCondition): string {
    return `${condition.type}-${condition.target || ''}-${condition.value || ''}-${condition.operator || ''}`;
  }

  /**
   * Checks if a condition is met
   */
  public async checkCondition(condition: AutomationCondition): Promise<boolean> {
    switch (condition.type) {
      case 'RESOURCE_ABOVE':
      case 'RESOURCE_BELOW':
        return new Promise(resolve => {
          const subscription = conditionState.subscribe(state => {
            if (state.resourceId === condition.target) {
              const threshold = Number(condition.value);
              const result =
                condition.type === 'RESOURCE_ABOVE'
                  ? state.currentAmount > threshold
                  : state.currentAmount < threshold;
              subscription.unsubscribe();
              resolve(result);
            }
          });

          // Timeout after 1 second
          setTimeout(() => {
            subscription.unsubscribe();
            resolve(false);
          }, 1000);
        });

      case 'MODULE_ACTIVE':
      case 'MODULE_INACTIVE':
        return this.checkModuleCondition(condition);

      case 'TIME_ELAPSED':
        return this.checkTimeCondition(condition);

      case 'EVENT_OCCURRED':
        return this.checkEventCondition(condition);

      case 'STATUS_EQUALS':
        return this.checkStatusCondition(condition);

      default:
        console.warn(`Unknown condition type: ${condition.type}`);
        return false;
    }
  }

  /**
   * Checks a module-based condition
   */
  private checkModuleCondition(condition: AutomationCondition): boolean {
    if (!condition.target) {
      return false;
    }

    const module = moduleManager.getModule(condition.target);
    if (!module) {
      return false;
    }

    if (condition.type === 'MODULE_ACTIVE') {
      return module.isActive;
    } else {
      return !module.isActive;
    }
  }

  /**
   * Checks a time-based condition
   */
  private checkTimeCondition(condition: AutomationCondition): boolean {
    if (!condition.value) {
      return false;
    }

    const now = Date.now();
    const key = this.getConditionKey(condition);
    const lastChecked = this.lastCheckedTimes.get(key) || 0;
    const elapsed = now - lastChecked;

    // Update last checked time
    this.lastCheckedTimes.set(key, now);

    if (condition.operator === 'greater') {
      return elapsed > condition.value;
    } else if (condition.operator === 'less') {
      return elapsed < condition.value;
    }

    return elapsed >= Number(condition.value);
  }

  /**
   * Checks an event-based condition
   */
  private checkEventCondition(condition: AutomationCondition): boolean {
    // TODO: Implement event checking using moduleEventBus
    return false;
  }

  /**
   * Checks a status-based condition
   */
  private checkStatusCondition(condition: AutomationCondition): boolean {
    if (!condition.target || !condition.value) {
      return false;
    }

    const module = moduleManager.getModule(condition.target);
    if (!module) {
      return false;
    }

    // Get module status from metadata
    const status = (module as any).metadata?.status;
    if (!status) {
      return false;
    }

    switch (condition.operator) {
      case 'equals':
        return status === condition.value;
      case 'not_equals':
        return status !== condition.value;
      case 'contains':
        return status.includes(String(condition.value));
      default:
        return false;
    }
  }

  /**
   * Checks if all conditions in a set are met
   */
  public async checkConditions(conditions: AutomationCondition[]): Promise<boolean> {
    try {
      const results = await Promise.all(
        conditions.map(condition => this.checkCondition(condition))
      );
      return results.every(result => result);
    } catch (error) {
      console.error('Error checking conditions:', error);
      return false;
    }
  }

  /**
   * Resets a condition's last checked time
   */
  public resetCondition(condition: AutomationCondition): void {
    const key = this.getConditionKey(condition);
    this.lastCheckedTimes.delete(key);
  }
}

import { ModuleType } from '../../types/buildings/ModuleTypes';

/**
 * Module event types
 */
export type ModuleEventType =
  // Lifecycle events
  | 'MODULE_CREATED'
  | 'MODULE_ATTACHED'
  | 'MODULE_DETACHED'
  | 'MODULE_UPGRADED'
  | 'MODULE_ACTIVATED'
  | 'MODULE_DEACTIVATED'
  | 'MODULE_UPDATED'
  // Attachment events
  | 'ATTACHMENT_STARTED'
  | 'ATTACHMENT_CANCELLED'
  | 'ATTACHMENT_COMPLETED'
  | 'ATTACHMENT_PREVIEW_SHOWN'
  // Resource events
  | 'RESOURCE_PRODUCED'
  | 'RESOURCE_CONSUMED'
  | 'RESOURCE_TRANSFERRED'
  | 'RESOURCE_PRODUCTION_REGISTERED'
  | 'RESOURCE_PRODUCTION_UNREGISTERED'
  | 'RESOURCE_CONSUMPTION_REGISTERED'
  | 'RESOURCE_CONSUMPTION_UNREGISTERED'
  | 'RESOURCE_FLOW_REGISTERED'
  | 'RESOURCE_FLOW_UNREGISTERED'
  | 'RESOURCE_SHORTAGE'
  | 'RESOURCE_UPDATED'
  // Automation events
  | 'AUTOMATION_STARTED'
  | 'AUTOMATION_STOPPED'
  | 'AUTOMATION_CYCLE_COMPLETE'
  // Status events
  | 'STATUS_CHANGED'
  | 'ERROR_OCCURRED'
  // Mission events
  | 'MISSION_STARTED'
  | 'MISSION_COMPLETED'
  | 'MISSION_FAILED'
  | 'MISSION_PROGRESS_UPDATED'
  | 'MISSION_REWARD_CLAIMED'
  // Sub-module events
  | 'SUB_MODULE_CREATED'
  | 'SUB_MODULE_ATTACHED'
  | 'SUB_MODULE_DETACHED'
  | 'SUB_MODULE_UPGRADED'
  | 'SUB_MODULE_ACTIVATED'
  | 'SUB_MODULE_DEACTIVATED'
  | 'SUB_MODULE_EFFECT_APPLIED'
  | 'SUB_MODULE_EFFECT_REMOVED'
  // Combat events
  | 'COMBAT_UPDATED'
  // Tech events
  | 'TECH_UNLOCKED'
  | 'TECH_UPDATED';

/**
 * Module event interface
 */
export interface ModuleEvent {
  type: ModuleEventType;
  moduleId: string;
  moduleType: ModuleType;
  timestamp: number;
  data?: any;
}

/**
 * Event listener type
 */
type ModuleEventListener = (event: ModuleEvent) => void;

/**
 * Module event bus for handling module communication
 */
export class ModuleEventBus {
  private listeners: Map<ModuleEventType, Set<ModuleEventListener>>;
  private history: ModuleEvent[];
  private maxHistorySize: number;

  constructor(maxHistorySize = 1000) {
    this.listeners = new Map();
    this.history = [];
    this.maxHistorySize = maxHistorySize;
  }

  /**
   * Subscribe to module events
   */
  subscribe(type: ModuleEventType, listener: ModuleEventListener): () => void {
    if (!this.listeners.has(type)) {
      this.listeners.set(type, new Set());
    }

    this.listeners.get(type)!.add(listener);

    // Return unsubscribe function
    return () => {
      const typeListeners = this.listeners.get(type);
      if (typeListeners) {
        typeListeners.delete(listener);
        if (typeListeners.size === 0) {
          this.listeners.delete(type);
        }
      }
    };
  }

  /**
   * Emit a module event
   */
  emit(event: ModuleEvent): void {
    // Add to history
    this.history.push(event);
    if (this.history.length > this.maxHistorySize) {
      this.history.shift();
    }

    // Notify listeners
    const typeListeners = this.listeners.get(event.type);
    if (typeListeners) {
      typeListeners.forEach(listener => {
        try {
          listener(event);
        } catch (error) {
          console.error('Error in module event listener:', error);
        }
      });
    }
  }

  /**
   * Get event history
   */
  getHistory(): ModuleEvent[] {
    return [...this.history];
  }

  /**
   * Get event history for a specific module
   */
  getModuleHistory(moduleId: string): ModuleEvent[] {
    return this.history.filter(event => event.moduleId === moduleId);
  }

  /**
   * Get event history for a specific type
   */
  getEventTypeHistory(type: ModuleEventType): ModuleEvent[] {
    return this.history.filter(event => event.type === type);
  }

  /**
   * Clear event history
   */
  clearHistory(): void {
    this.history = [];
  }
}

// Export singleton instance
export const moduleEventBus = new ModuleEventBus();

import { EventEmitter } from '../utils/EventEmitter';

export interface PooledEntity {
  reset(): void;
}

interface PoolEvents<T extends PooledEntity> {
  entityActivated: { entity: T };
  entityDeactivated: { entity: T };
  poolExpanded: { newSize: number };
}

/**
 * Generic entity pool for efficient object reuse
 */
export class EntityPool<T extends PooledEntity> extends EventEmitter<PoolEvents<T>> {
  private available: T[];
  private inUse: Set<T>;
  private factory: () => T;
  private maxSize: number;
  private expandSize: number;

  constructor(
    factory: () => T,
    initialSize: number,
    maxSize: number = 1000,
    expandSize: number = 50
  ) {
    super();
    this.factory = factory;
    this.maxSize = maxSize;
    this.expandSize = expandSize;
    this.available = [];
    this.inUse = new Set();

    // Pre-allocate initial pool
    for (let i = 0; i < initialSize; i++) {
      this.available.push(factory());
    }

    // Debug logging
    console.debug(`[EntityPool] Initialized with ${initialSize} entities (max: ${maxSize})`);
  }

  /**
   * Get an inactive entity from the pool
   */
  public acquire(): T | undefined {
    let entity: T;
    if (this.available.length > 0) {
      entity = this.available.pop()!;
    } else {
      entity = this.factory();
    }

    entity.reset();
    this.inUse.add(entity);
    this.emit('entityActivated', { entity });
    return entity;
  }

  /**
   * Return an entity to the pool
   */
  public release(entity: T): void {
    if (this.inUse.has(entity)) {
      this.inUse.delete(entity);
      entity.reset();
      this.available.push(entity);
      this.emit('entityDeactivated', { entity });
    }
  }

  /**
   * Get all active entities
   */
  public getActiveEntities(): T[] {
    return Array.from(this.inUse);
  }

  /**
   * Get total number of entities in pool
   */
  public getTotalCount(): number {
    return this.available.length + this.inUse.size;
  }

  /**
   * Get number of active entities
   */
  public getActiveCount(): number {
    return this.inUse.size;
  }

  /**
   * Get number of available entities
   */
  public getAvailableCount(): number {
    return this.available.length;
  }

  /**
   * Clear all entities from pool
   */
  public clear(): void {
    this.available = [];
    this.inUse.clear();
    this.emit('entityDeactivated', { entity: null });
    console.debug('[EntityPool] Pool cleared');
  }
}

