import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import { Position } from '../../types/core/GameTypes';
import { CommonShipCapabilities } from '../../types/ships/CommonShipTypes';
import { EventEmitter } from '../../utils/EventEmitter';
import { combatManager } from '../combat/combatManager';

interface Anomaly {
  id: string;
  type: 'artifact' | 'signal' | 'phenomenon';
  severity: 'low' | 'medium' | 'high';
  description: string;
  investigated: boolean;
}

interface Threat {
  id: string;
  position: Position;
  severity: number;
  type: string;
}

interface ReconShipEvents {
  shipRegistered: { shipId: string };
  shipUnregistered: { shipId: string };
  taskAssigned: { shipId: string; task: ExplorationTask };
  taskCompleted: { shipId: string; task: ExplorationTask };
  techBonusesUpdated: {
    shipId: string;
    bonuses: {
      scanSpeed: number;
      stealthEfficiency: number;
      detectionRange: number;
    };
  };
  stealthToggled: { shipId: string; active: boolean };
  shipStatusUpdated: { shipId: string; status: ReconShip['status'] };
  threatDetected: { shipId: string; threatLevel: number; position: Position };
  anomalyDiscovered: { shipId: string; anomaly: Anomaly; position: Position };
}

interface ReconShip {
  id: string;
  name: string;
  type: 'AC27G' | 'PathFinder' | 'VoidSeeker';
  status: 'idle' | 'scanning' | 'investigating' | 'returning';
  targetSector?: string;
  experience: number;
  specialization: 'mapping' | 'anomaly' | 'resource';
  efficiency: number;
  position: Position;
  capabilities: CommonShipCapabilities;
  stealth: {
    active: boolean;
    level: number;
    cooldown: number;
  };
  sensors: {
    range: number;
    accuracy: number;
    anomalyDetection: number;
  };
  discoveries: {
    mappedSectors: number;
    anomaliesFound: number;
    resourcesLocated: number;
  };
  techBonuses?: {
    scanSpeed: number;
    stealthEfficiency: number;
    detectionRange: number;
  };
}

interface ExplorationTask {
  id: string;
  type: 'explore' | 'investigate' | 'evade';
  target: {
    id: string;
    position: Position;
  };
  priority: number;
  assignedAt: number;
  specialization: 'mapping' | 'anomaly' | 'resource';
  status: 'queued' | 'in-progress' | 'completed' | 'failed';
  progress?: number;
  threatLevel?: number;
}

export class ReconShipManagerImpl extends EventEmitter<ReconShipEvents> {
  private ships: Map<string, ReconShip> = new Map();
  private tasks: Map<string, ExplorationTask> = new Map();
  private sectors: Map<
    string,
    {
      id: string;
      position: Position;
      explored: boolean;
      anomalies: Anomaly[];
      resources: number;
    }
  > = new Map();

  public registerShip(ship: ReconShip): void {
    if (ship.capabilities.canScan) {
      this.ships.set(ship.id, ship);

      // Emit events
      this.emit('shipRegistered', { shipId: ship.id });
      moduleEventBus.emit({
        type: 'MODULE_ACTIVATED',
        moduleId: ship.id,
        moduleType: 'radar' as ModuleType,
        timestamp: Date.now(),
        data: { ship },
      });
    }
  }

  public unregisterShip(shipId: string): void {
    if (this.ships.has(shipId)) {
      this.ships.delete(shipId);
      this.tasks.delete(shipId);

      // Emit events
      this.emit('shipUnregistered', { shipId });
      moduleEventBus.emit({
        type: 'MODULE_DEACTIVATED',
        moduleId: shipId,
        moduleType: 'radar' as ModuleType,
        timestamp: Date.now(),
      });
    }
  }

  public assignExplorationTask(
    shipId: string,
    sectorId: string,
    position: Position,
    specialization: 'mapping' | 'anomaly' | 'resource'
  ): void {
    const ship = this.ships.get(shipId);
    if (!ship || !ship.capabilities.canScan) {
      return;
    }

    // Check for threats before assigning task
    const threats = this.getNearbyThreats(position, ship.sensors.range);
    const threatLevel = this.calculateThreatLevel(threats, ship);

    if (threatLevel > 0.7) {
      // High threat - initiate evasion
      this.assignEvasionTask(shipId, position, threats);
      return;
    }

    const task: ExplorationTask = {
      id: `explore-${sectorId}`,
      type: threatLevel > 0.3 ? 'investigate' : 'explore',
      target: {
        id: sectorId,
        position,
      },
      priority: this.getPriorityForSpecialization(specialization),
      assignedAt: Date.now(),
      specialization,
      status: 'queued',
      threatLevel,
    };

    this.tasks.set(shipId, task);
    this.updateShipStatus(shipId, 'scanning');

    // Emit events
    this.emit('taskAssigned', { shipId, task });
    moduleEventBus.emit({
      type: 'AUTOMATION_STARTED',
      moduleId: shipId,
      moduleType: 'radar' as ModuleType,
      timestamp: Date.now(),
      data: { task },
    });
  }

  private assignEvasionTask(shipId: string, position: Position, threats: Threat[]): void {
    const ship = this.ships.get(shipId);
    if (!ship) return;

    // Calculate safe position away from threats
    const safePosition = this.calculateSafePosition(position, threats);

    const task: ExplorationTask = {
      id: `evade-${Date.now()}`,
      type: 'evade',
      target: {
        id: 'safe-zone',
        position: safePosition,
      },
      priority: 10, // Highest priority for evasion
      assignedAt: Date.now(),
      specialization: 'mapping',
      status: 'queued',
      threatLevel: 1,
    };

    this.tasks.set(shipId, task);
    this.updateShipStatus(shipId, 'returning');
    this.toggleShipStealth(shipId, true);

    this.emit('taskAssigned', { shipId, task });
  }

  private calculateSafePosition(position: Position, threats: Threat[]): Position {
    // Calculate vector away from threats
    const escapeVector = threats.reduce(
      (vec, threat) => {
        const dx = position.x - threat.position.x;
        const dy = position.y - threat.position.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        return {
          x: vec.x + (dx / distance) * threat.severity,
          y: vec.y + (dy / distance) * threat.severity,
        };
      },
      { x: 0, y: 0 }
    );

    // Normalize and scale
    const magnitude = Math.sqrt(escapeVector.x * escapeVector.x + escapeVector.y * escapeVector.y);
    const safeDistance = 500; // Base safe distance

    return {
      x: position.x + (escapeVector.x / magnitude) * safeDistance,
      y: position.y + (escapeVector.y / magnitude) * safeDistance,
    };
  }

  public completeTask(shipId: string): void {
    const task = this.tasks.get(shipId);
    const ship = this.ships.get(shipId);

    if (task && ship) {
      // Update sector data
      const sector = this.sectors.get(task.target.id);
      if (sector) {
        sector.explored = true;
        ship.discoveries.mappedSectors++;

        // Enhanced specialization handling
        if (task.specialization === 'anomaly') {
          const anomalies = this.generateAnomalies(ship.sensors.anomalyDetection);
          sector.anomalies = anomalies;
          ship.discoveries.anomaliesFound += anomalies.length;

          // Emit anomaly discoveries
          anomalies.forEach(anomaly => {
            this.emit('anomalyDiscovered', {
              shipId,
              anomaly,
              position: task.target.position,
            });
          });

          // Emit mission event
          moduleEventBus.emit({
            type: 'MISSION_COMPLETED',
            moduleId: shipId,
            moduleType: 'radar' as ModuleType,
            timestamp: Date.now(),
            data: {
              type: 'anomaly',
              sector: task.target.id,
              importance: anomalies.some(a => a.severity === 'high')
                ? 'high'
                : anomalies.some(a => a.severity === 'medium')
                  ? 'medium'
                  : 'low',
              description: `Discovered ${anomalies.length} anomalies in ${task.target.id}`,
              xpGained: this.calculateExperienceGain(task, ship),
              anomalyDetails: anomalies.map(a => ({
                type: a.type,
                severity: a.severity,
                investigated: a.investigated,
              })),
            },
          });
        } else if (task.specialization === 'resource') {
          sector.resources = Math.floor(Math.random() * 5) + 1;
          ship.discoveries.resourcesLocated += sector.resources;

          // Emit mission event
          moduleEventBus.emit({
            type: 'MISSION_COMPLETED',
            moduleId: shipId,
            moduleType: 'radar' as ModuleType,
            timestamp: Date.now(),
            data: {
              type: 'discovery',
              sector: task.target.id,
              importance: sector.resources > 3 ? 'high' : sector.resources > 1 ? 'medium' : 'low',
              description: `Located ${sector.resources} resource deposits in ${task.target.id}`,
              xpGained: this.calculateExperienceGain(task, ship),
              resourcesFound: [{ type: 'Unknown', amount: sector.resources }],
            },
          });
        }
      }

      // Enhanced experience calculation
      const experienceGained = this.calculateExperienceGain(task, ship);
      ship.experience += experienceGained;

      // Apply tech bonuses based on experience
      this.updateTechBonuses(ship);

      this.tasks.delete(shipId);
      this.updateShipStatus(shipId, 'returning');

      // Emit events
      this.emit('taskCompleted', { shipId, task });
      moduleEventBus.emit({
        type: 'AUTOMATION_CYCLE_COMPLETE',
        moduleId: shipId,
        moduleType: 'radar' as ModuleType,
        timestamp: Date.now(),
        data: {
          task,
          discoveries: ship.discoveries,
          experienceGained,
          heatMapValue: this.calculateHeatMapValue(sector),
        },
      });
    }
  }

  private calculateHeatMapValue(sector: any): number {
    if (!sector) return 0;

    let heatValue = 0;

    // Resource potential contribution (40%)
    heatValue += (sector.resources || 0) * 0.4;

    // Anomaly contribution (30%)
    if (sector.anomalies) {
      const anomalyHeat = sector.anomalies.reduce((sum: number, anomaly: Anomaly) => {
        return (
          sum + (anomaly.severity === 'high' ? 0.3 : anomaly.severity === 'medium' ? 0.2 : 0.1)
        );
      }, 0);
      heatValue += anomalyHeat;
    }

    // Habitability contribution (30%)
    if (sector.habitabilityScore) {
      heatValue += sector.habitabilityScore * 0.3;
    }

    // Age decay
    if (sector.lastScanned) {
      const hoursSinceLastScan = (Date.now() - sector.lastScanned) / (1000 * 60 * 60);
      const ageFactor = Math.max(0, 1 - hoursSinceLastScan / 168); // 168 hours = 1 week
      heatValue *= ageFactor;
    }

    return Math.min(1, heatValue);
  }

  private generateAnomalies(detectionSkill: number): Anomaly[] {
    const count = Math.floor(Math.random() * 3 * detectionSkill) + 1;
    const anomalies: Anomaly[] = [];
    const anomalyTypes = ['artifact', 'signal', 'phenomenon'] as const;
    const severityLevels = ['high', 'medium', 'low'] as const;

    for (let i = 0; i < count; i++) {
      const type = anomalyTypes[Math.floor(Math.random() * anomalyTypes.length)];
      const severity = Math.random() < 0.2 ? 'high' : Math.random() < 0.5 ? 'medium' : 'low';

      anomalies.push({
        id: `anomaly-${Date.now()}-${i}`,
        type,
        severity,
        description: this.generateAnomalyDescription(type, severity),
        investigated: false,
      });
    }

    return anomalies;
  }

  private generateAnomalyDescription(
    type: 'artifact' | 'signal' | 'phenomenon',
    severity: 'low' | 'medium' | 'high'
  ): string {
    const descriptions = {
      artifact: {
        high: 'Ancient technological marvel of immense power',
        medium: 'Mysterious alien artifacts with unknown purpose',
        low: 'Scattered remnants of past civilizations',
      },
      signal: {
        high: 'Powerful energy signature of unknown origin',
        medium: 'Recurring patterns in subspace frequencies',
        low: 'Faint echoes of distant transmissions',
      },
      phenomenon: {
        high: 'Dangerous spatial anomaly requiring immediate attention',
        medium: 'Unusual gravitational fluctuations detected',
        low: 'Minor disturbances in local space-time',
      },
    } as const;

    return descriptions[type][severity];
  }

  private calculateExperienceGain(task: ExplorationTask, ship: ReconShip): number {
    const baseXP = 100;
    const timeFactor = (Date.now() - task.assignedAt) / 1000 / 60; // Minutes

    // Specialization bonus
    const specializationBonus = task.specialization === ship.specialization ? 1.5 : 1.2;

    // Threat bonus
    const threatBonus = task.threatLevel ? 1 + task.threatLevel : 1;

    // Efficiency bonus
    const efficiencyBonus = ship.efficiency;

    // Discovery bonus
    const discoveryBonus =
      ship.discoveries.anomaliesFound * 0.1 + ship.discoveries.resourcesLocated * 0.05;

    return Math.floor(
      baseXP *
        timeFactor *
        specializationBonus *
        threatBonus *
        efficiencyBonus *
        (1 + discoveryBonus)
    );
  }

  private updateTechBonuses(ship: ReconShip): void {
    const experienceLevel = Math.floor(ship.experience / 1000);
    const bonuses = {
      scanSpeed: 1 + experienceLevel * 0.1,
      stealthEfficiency: 1 + experienceLevel * 0.15,
      detectionRange: 1 + experienceLevel * 0.05,
    };

    this.emit('techBonusesUpdated', { shipId: ship.id, bonuses });
  }

  public updateShipTechBonuses(
    shipId: string,
    bonuses: { scanSpeed: number; stealthEfficiency: number; detectionRange: number }
  ): void {
    const ship = this.ships.get(shipId);
    if (ship) {
      ship.techBonuses = bonuses;
      this.emit('techBonusesUpdated', { shipId, bonuses });
    }
  }

  public toggleShipStealth(shipId: string, active: boolean): void {
    const ship = this.ships.get(shipId);
    if (ship && ship.stealth.cooldown <= 0) {
      ship.stealth.active = active;
      this.emit('stealthToggled', { shipId, active: ship.stealth.active });
    }
  }

  private updateShipStatus(
    shipId: string,
    status: 'idle' | 'scanning' | 'investigating' | 'returning'
  ): void {
    const ship = this.ships.get(shipId);
    if (ship) {
      ship.status = status;

      // Emit events
      this.emit('shipStatusUpdated', { shipId, status });
      moduleEventBus.emit({
        type: 'STATUS_CHANGED',
        moduleId: shipId,
        moduleType: 'radar' as ModuleType,
        timestamp: Date.now(),
        data: { status },
      });
    }
  }

  private getPriorityForSpecialization(specialization: 'mapping' | 'anomaly' | 'resource'): number {
    switch (specialization) {
      case 'anomaly':
        return 3;
      case 'resource':
        return 2;
      case 'mapping':
        return 1;
      default:
        return 0;
    }
  }

  // Add method to get ship efficiency with tech bonuses
  public getShipEfficiency(shipId: string): number {
    const ship = this.ships.get(shipId);
    if (!ship) {
      return 0;
    }

    const baseEfficiency = ship.efficiency;
    const techBonus = ship.techBonuses?.scanSpeed || 1;

    return baseEfficiency * techBonus;
  }

  // Update method for periodic tasks
  public update(deltaTime: number): void {
    this.ships.forEach(ship => {
      // Update stealth cooldown
      if (ship.stealth.cooldown > 0) {
        ship.stealth.cooldown = Math.max(0, ship.stealth.cooldown - deltaTime);
      }

      // Apply tech bonuses to sensor range
      if (ship.techBonuses) {
        ship.sensors.range *= ship.techBonuses.detectionRange;
      }

      // Handle automatic stealth activation near threats
      if (this.isNearThreat(ship) && !ship.stealth.active && ship.stealth.cooldown <= 0) {
        this.toggleShipStealth(ship.id, true);
      }
    });
  }

  private isNearThreat(ship: ReconShip): boolean {
    // Get threats within ship's sensor range
    const threats = combatManager.getThreatsInRange(ship.position, ship.sensors.range);

    if (threats.length === 0) {
      return false;
    }

    // Calculate threat level based on proximity and severity
    const threatLevel = threats.reduce((total, threat) => {
      const distance = Math.sqrt(
        Math.pow(threat.position.x - ship.position.x, 2) +
          Math.pow(threat.position.y - ship.position.y, 2)
      );

      // Scale threat by distance and severity
      const distanceFactor = 1 - Math.min(distance / ship.sensors.range, 1);
      const severityFactor =
        threat.severity === 'high' ? 1 : threat.severity === 'medium' ? 0.6 : 0.3;

      // Apply sensor accuracy to detection reliability
      const detectionChance = ship.sensors.accuracy * (ship.techBonuses?.detectionRange || 1);

      return total + distanceFactor * severityFactor * detectionChance;
    }, 0);

    // Return true if threat level exceeds threshold
    // Scale threshold based on ship's stealth capabilities
    const stealthFactor = (ship.stealth.level / 100) * (ship.techBonuses?.stealthEfficiency || 1);
    const baseThreshold = 0.3; // Base threshold for threat response

    return threatLevel > baseThreshold * (1 + stealthFactor);
  }

  private getNearbyThreats(position: Position, range: number): Threat[] {
    return combatManager.getThreatsInRange(position, range).map(threat => ({
      id: threat.id,
      position: threat.position,
      severity: threat.severity === 'high' ? 1 : threat.severity === 'medium' ? 0.6 : 0.3,
      type: threat.type,
    }));
  }

  private calculateThreatLevel(threats: Threat[], ship: ReconShip): number {
    if (threats.length === 0) return 0;

    return threats.reduce((total, threat) => {
      const distance = Math.sqrt(
        Math.pow(threat.position.x - ship.position.x, 2) +
          Math.pow(threat.position.y - ship.position.y, 2)
      );

      const distanceFactor = 1 - Math.min(distance / ship.sensors.range, 1);
      const detectionChance = ship.sensors.accuracy * (ship.techBonuses?.detectionRange || 1);

      return total + distanceFactor * threat.severity * detectionChance;
    }, 0);
  }
}

import { ModuleEventType, moduleEventBus } from '../../lib/modules/ModuleEvents';

/**
 * Update priority levels
 */
export enum UpdatePriority {
  CRITICAL = 0, // Run every frame, no matter what (physics, core game state)
  HIGH = 1, // Run most frames, can be skipped occasionally (AI, combat)
  NORMAL = 2, // Run regularly, can be skipped more often (resource updates)
  LOW = 3, // Run occasionally (visual effects, non-critical updates)
  BACKGROUND = 4, // Run when there's spare time (analytics, cleanup)
}

/**
 * Update callback type
 */
export type UpdateCallback = (deltaTime: number, elapsedTime: number) => void;

/**
 * Update registration interface
 */
export interface UpdateRegistration {
  id: string;
  callback: UpdateCallback;
  priority: UpdatePriority;
  interval?: number; // Optional interval in ms (for fixed timestep updates)
  lastUpdate?: number; // Last time this was updated
}

/**
 * Game loop statistics
 */
export interface GameLoopStats {
  fps: number;
  frameTime: number;
  updateTime: number;
  renderTime: number;
  idleTime: number;
  elapsedTime: number;
  frameCount: number;
  skippedFrames: number;
  priorityStats: Record<
    UpdatePriority,
    {
      count: number;
      totalTime: number;
      averageTime: number;
    }
  >;
}

/**
 * Game loop manager configuration
 */
export interface GameLoopConfig {
  targetFPS: number;
  maxDeltaTime: number;
  priorityThrottling: boolean;
  fixedTimestep: boolean;
  throttlePriorities: UpdatePriority[];
  statsInterval: number;
  enableStats: boolean;
}

/**
 * Game loop manager for centralized timing and updates
 */
export class GameLoopManager {
  private updates: Map<string, UpdateRegistration> = new Map();
  private running: boolean = false;
  private lastFrameTime: number = 0;
  private frameCount: number = 0;
  private skippedFrames: number = 0;
  private elapsedTime: number = 0;
  private animationFrameId: number | null = null;
  private statsInterval: number | null = null;
  private stats: GameLoopStats;
  private config: GameLoopConfig;

  constructor(config: Partial<GameLoopConfig> = {}) {
    // Default configuration
    this.config = {
      targetFPS: 60,
      maxDeltaTime: 1000 / 30, // Cap at 30 FPS equivalent
      priorityThrottling: true,
      fixedTimestep: false,
      throttlePriorities: [UpdatePriority.LOW, UpdatePriority.BACKGROUND],
      statsInterval: 1000, // 1 second
      enableStats: true,
      ...config,
    };

    // Initialize stats
    this.stats = {
      fps: 0,
      frameTime: 0,
      updateTime: 0,
      renderTime: 0,
      idleTime: 0,
      elapsedTime: 0,
      frameCount: 0,
      skippedFrames: 0,
      priorityStats: {
        [UpdatePriority.CRITICAL]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.HIGH]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.NORMAL]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.LOW]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.BACKGROUND]: { count: 0, totalTime: 0, averageTime: 0 },
      },
    };
  }

  /**
   * Start the game loop
   */
  public start(): void {
    if (this.running) {
      return;
    }

    this.running = true;
    this.lastFrameTime = performance.now();
    this.frameCount = 0;
    this.skippedFrames = 0;
    this.elapsedTime = 0;

    // Start the game loop
    this.animationFrameId = requestAnimationFrame(this.gameLoop);

    // Start stats reporting if enabled
    if (this.config.enableStats) {
      this.statsInterval = window.setInterval(() => {
        this.reportStats();
      }, this.config.statsInterval);
    }

    // Emit game loop started event
    moduleEventBus.emit({
      type: 'GAME_LOOP_STARTED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { config: this.config },
    });
  }

  /**
   * Stop the game loop
   */
  public stop(): void {
    if (!this.running) {
      return;
    }

    this.running = false;

    // Stop the animation frame
    if (this.animationFrameId !== null) {
      cancelAnimationFrame(this.animationFrameId);
      this.animationFrameId = null;
    }

    // Stop stats reporting
    if (this.statsInterval !== null) {
      clearInterval(this.statsInterval);
      this.statsInterval = null;
    }

    // Emit game loop stopped event
    moduleEventBus.emit({
      type: 'GAME_LOOP_STOPPED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { stats: this.stats },
    });
  }

  /**
   * Register an update callback
   */
  public registerUpdate(
    id: string,
    callback: UpdateCallback,
    priority: UpdatePriority = UpdatePriority.NORMAL,
    interval?: number
  ): void {
    this.updates.set(id, {
      id,
      callback,
      priority,
      interval,
      lastUpdate: performance.now(),
    });

    // Emit update registered event
    moduleEventBus.emit({
      type: 'GAME_LOOP_UPDATE_REGISTERED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { id, priority, interval },
    });
  }

  /**
   * Unregister an update callback
   */
  public unregisterUpdate(id: string): void {
    if (this.updates.has(id)) {
      this.updates.delete(id);

      // Emit update unregistered event
      moduleEventBus.emit({
        type: 'GAME_LOOP_UPDATE_UNREGISTERED' as ModuleEventType,
        moduleId: 'game-loop-manager',
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        data: { id },
      });
    }
  }

  /**
   * Get the current game loop stats
   */
  public getStats(): GameLoopStats {
    return { ...this.stats };
  }

  /**
   * Update the game loop configuration
   */
  public updateConfig(config: Partial<GameLoopConfig>): void {
    this.config = {
      ...this.config,
      ...config,
    };

    // Emit config updated event
    moduleEventBus.emit({
      type: 'GAME_LOOP_CONFIG_UPDATED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { config: this.config },
    });
  }

  /**
   * The main game loop
   */
  private gameLoop = (timestamp: number): void => {
    if (!this.running) {
      return;
    }

    // Schedule the next frame
    this.animationFrameId = requestAnimationFrame(this.gameLoop);

    // Calculate delta time
    const now = timestamp;
    let deltaTime = now - this.lastFrameTime;
    this.lastFrameTime = now;

    // Cap delta time to prevent spiral of death
    if (deltaTime > this.config.maxDeltaTime) {
      deltaTime = this.config.maxDeltaTime;
      this.skippedFrames++;
    }

    // Update elapsed time
    this.elapsedTime += deltaTime;
    this.frameCount++;

    // Update frame time stats
    this.stats.frameTime = deltaTime;

    // Start update timing
    const updateStartTime = performance.now();

    // Process updates by priority
    this.processUpdates(deltaTime, this.elapsedTime);

    // End update timing
    const updateEndTime = performance.now();
    this.stats.updateTime = updateEndTime - updateStartTime;

    // Calculate idle time (time left in the frame)
    const targetFrameTime = 1000 / this.config.targetFPS;
    const currentFrameTime = performance.now() - now;
    this.stats.idleTime = Math.max(0, targetFrameTime - currentFrameTime);

    // Update FPS stats
    this.stats.fps = 1000 / deltaTime;
    this.stats.elapsedTime = this.elapsedTime;
    this.stats.frameCount = this.frameCount;
    this.stats.skippedFrames = this.skippedFrames;
  };

  /**
   * Process all registered updates
   */
  private processUpdates(deltaTime: number, elapsedTime: number): void {
    // Group updates by priority
    const priorityGroups: Map<UpdatePriority, UpdateRegistration[]> = new Map();

    for (const update of this.updates.values()) {
      if (!priorityGroups.has(update.priority)) {
        priorityGroups.set(update.priority, []);
      }
      priorityGroups.get(update.priority)!.push(update);
    }

    // Process each priority group
    for (
      let priority = UpdatePriority.CRITICAL;
      priority <= UpdatePriority.BACKGROUND;
      priority++
    ) {
      const updates = priorityGroups.get(priority) || [];

      // Skip throttled priorities if enabled
      if (
        this.config.priorityThrottling &&
        this.config.throttlePriorities.includes(priority) &&
        this.frameCount % (priority + 1) !== 0
      ) {
        continue;
      }

      // Process all updates in this priority group
      for (const update of updates) {
        // Skip if interval is set and not enough time has passed
        if (update.interval && update.lastUpdate) {
          const timeSinceLastUpdate = elapsedTime - update.lastUpdate;
          if (timeSinceLastUpdate < update.interval) {
            continue;
          }
        }

        // Update the last update time
        update.lastUpdate = elapsedTime;

        // Measure update time
        const startTime = performance.now();

        try {
          // Call the update callback
          update.callback(deltaTime, elapsedTime);
        } catch (error) {
          console.error(`Error in update ${update.id}:`, error);

          // Emit error event
          moduleEventBus.emit({
            type: 'ERROR_OCCURRED' as ModuleEventType,
            moduleId: 'game-loop-manager',
            moduleType: 'resource-manager',
            timestamp: Date.now(),
            data: {
              error,
              updateId: update.id,
              priority: update.priority,
            },
          });
        }

        // Update stats
        const endTime = performance.now();
        const updateTime = endTime - startTime;

        this.stats.priorityStats[priority].count++;
        this.stats.priorityStats[priority].totalTime += updateTime;
        this.stats.priorityStats[priority].averageTime =
          this.stats.priorityStats[priority].totalTime / this.stats.priorityStats[priority].count;
      }
    }
  }

  /**
   * Report game loop stats
   */
  private reportStats(): void {
    // Calculate average FPS
    const avgFps = this.frameCount / (this.config.statsInterval / 1000);

    // Reset frame count for next interval
    this.frameCount = 0;

    // Emit stats event
    moduleEventBus.emit({
      type: 'GAME_LOOP_STATS' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        ...this.stats,
        avgFps,
      },
    });

    // Log stats if in development
    if (process.env.NODE_ENV === 'development') {
      console.debug('[GameLoop] Stats:', {
        fps: avgFps.toFixed(2),
        updateTime: this.stats.updateTime.toFixed(2) + 'ms',
        idleTime: this.stats.idleTime.toFixed(2) + 'ms',
        skippedFrames: this.stats.skippedFrames,
      });
    }
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.stop();
    this.updates.clear();
  }
}

// Export singleton instance
export const gameLoopManager = new GameLoopManager();

import { v4 as uuidv4 } from 'uuid';
import { EntityPool } from '../../lib/optimization/EntityPool';
import { Position } from '../../types/core/GameTypes';
import { EventEmitter } from '../../utils/EventEmitter';

interface Particle {
  id: string;
  active: boolean;
  position: Position;
  velocity: Position;
  acceleration: Position;
  size: number;
  color: string;
  opacity: number;
  life: number;
  maxLife: number;
  reset(): void;
}

interface ParticleSystemEvents {
  particleSpawned: { particle: Particle };
  particleUpdated: { particle: Particle };
  particleDied: { particle: Particle };
  systemUpdated: { activeCount: number };
}

/**
 * Manages particle systems with efficient pooling
 */
export class ParticleSystemManager extends EventEmitter<ParticleSystemEvents> {
  private particlePool: EntityPool<Particle>;
  private systems: Map<string, ParticleSystem> = new Map();
  private lastUpdate: number = 0;

  constructor(initialPoolSize: number = 1000) {
    super();
    this.particlePool = new EntityPool<Particle>(() => this.createParticle(), initialPoolSize);

    // Debug logging
    console.debug(`[ParticleSystemManager] Initialized with pool size ${initialPoolSize}`);
  }

  /**
   * Create a new particle
   */
  private createParticle(): Particle {
    return {
      id: uuidv4(),
      active: false,
      position: { x: 0, y: 0 },
      velocity: { x: 0, y: 0 },
      acceleration: { x: 0, y: 0 },
      size: 1,
      color: '#ffffff',
      opacity: 1,
      life: 1,
      maxLife: 1,
      reset(): void {
        this.active = false;
        this.position = { x: 0, y: 0 };
        this.velocity = { x: 0, y: 0 };
        this.acceleration = { x: 0, y: 0 };
        this.size = 1;
        this.color = '#ffffff';
        this.opacity = 1;
        this.life = 1;
        this.maxLife = 1;
      },
    };
  }

  /**
   * Create a new particle system
   */
  public createSystem(config: ParticleSystemConfig): string {
    const id = uuidv4();
    this.systems.set(id, new ParticleSystem(this.particlePool, config));
    return id;
  }

  /**
   * Remove a particle system
   */
  public removeSystem(id: string): void {
    const system = this.systems.get(id);
    if (system) {
      system.cleanup();
      this.systems.delete(id);
    }
  }

  /**
   * Update all particle systems
   */
  public update(timestamp: number): void {
    const deltaTime = this.lastUpdate ? (timestamp - this.lastUpdate) / 1000 : 0;
    this.lastUpdate = timestamp;

    let totalActiveParticles = 0;

    this.systems.forEach(system => {
      system.update(deltaTime);
      totalActiveParticles += system.getActiveParticleCount();
    });

    this.emit('systemUpdated', { activeCount: totalActiveParticles });
  }

  /**
   * Clean up all systems
   */
  public cleanup(): void {
    this.systems.forEach(system => system.cleanup());
    this.systems.clear();
    this.particlePool.clear();
  }
}

interface ParticleSystemConfig {
  maxParticles: number;
  spawnRate: number;
  position: Position;
  spread: number;
  initialVelocity: {
    min: Position;
    max: Position;
  };
  acceleration: Position;
  size: {
    min: number;
    max: number;
  };
  life: {
    min: number;
    max: number;
  };
  color: string | string[];
  blendMode?: 'normal' | 'additive';
}

/**
 * Individual particle system instance
 */
class ParticleSystem {
  private pool: EntityPool<Particle>;
  private config: ParticleSystemConfig;
  private particles: Set<Particle> = new Set();
  private timeSinceLastSpawn: number = 0;

  constructor(pool: EntityPool<Particle>, config: ParticleSystemConfig) {
    this.pool = pool;
    this.config = config;
  }

  /**
   * Spawn a new particle
   */
  private spawnParticle(): void {
    if (this.particles.size >= this.config.maxParticles) {
      return;
    }

    const particle = this.pool.acquire();
    if (!particle) {
      return;
    }

    // Initialize particle properties
    const angle = Math.random() * Math.PI * 2;
    const distance = Math.random() * this.config.spread;

    particle.position = {
      x: this.config.position.x + Math.cos(angle) * distance,
      y: this.config.position.y + Math.sin(angle) * distance,
    };

    particle.velocity = {
      x:
        this.config.initialVelocity.min.x +
        Math.random() * (this.config.initialVelocity.max.x - this.config.initialVelocity.min.x),
      y:
        this.config.initialVelocity.min.y +
        Math.random() * (this.config.initialVelocity.max.y - this.config.initialVelocity.min.y),
    };

    particle.acceleration = { ...this.config.acceleration };
    particle.size =
      this.config.size.min + Math.random() * (this.config.size.max - this.config.size.min);

    particle.life =
      this.config.life.min + Math.random() * (this.config.life.max - this.config.life.min);
    particle.maxLife = particle.life;

    if (Array.isArray(this.config.color)) {
      const colorIndex = Math.floor(Math.random() * this.config.color.length);
      particle.color = this.config.color[colorIndex];
    } else {
      particle.color = this.config.color;
    }

    this.particles.add(particle);
  }

  /**
   * Update particle system
   */
  public update(deltaTime: number): void {
    // Spawn new particles
    this.timeSinceLastSpawn += deltaTime;
    const spawnInterval = 1 / this.config.spawnRate;

    while (this.timeSinceLastSpawn >= spawnInterval) {
      this.spawnParticle();
      this.timeSinceLastSpawn -= spawnInterval;
    }

    // Update existing particles
    this.particles.forEach(particle => {
      // Update life
      particle.life -= deltaTime;
      if (particle.life <= 0) {
        this.particles.delete(particle);
        this.pool.release(particle);
        return;
      }

      // Update physics
      particle.velocity.x += particle.acceleration.x * deltaTime;
      particle.velocity.y += particle.acceleration.y * deltaTime;
      particle.position.x += particle.velocity.x * deltaTime;
      particle.position.y += particle.velocity.y * deltaTime;

      // Update opacity based on life
      particle.opacity = particle.life / particle.maxLife;
    });
  }

  /**
   * Get number of active particles
   */
  public getActiveParticleCount(): number {
    return this.particles.size;
  }

  /**
   * Clean up particle system
   */
  public cleanup(): void {
    this.particles.forEach(particle => {
      this.pool.release(particle);
    });
    this.particles.clear();
  }
}

import { v4 as uuidv4 } from 'uuid';
import { OFFICER_TRAITS, SQUAD_CONFIG, TRAINING_CONFIG } from '../../config/OfficerConfig';
import { ModuleEvent, moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import { techTreeManager } from '../../managers/game/techTreeManager';
import type { ModuleType } from '../../types/buildings/ModuleTypes';
import type {
  OfficerManager as IOfficerManager,
  Officer,
  OfficerEvents,
  OfficerRole,
  OfficerSkills,
  OfficerSpecialization,
  OfficerTier,
  Squad,
  TrainingProgram,
} from '../../types/officers/OfficerTypes';
import { EventEmitter } from '../../utils/EventEmitter';

type TechNodeUnlockedEvent = {
  nodeId: string;
  node: {
    type: string;
    tier: number;
  };
};

/**
 * Implementation of the Officer Manager
 * Handles officer hiring, training, assignment, squad management, and progression
 */
export class OfficerManager extends EventEmitter<OfficerEvents> implements IOfficerManager {
  private officers: Map<string, Officer> = new Map();
  private squads: Map<string, Squad> = new Map();
  private trainingPrograms: Map<string, TrainingProgram> = new Map();
  private currentTier: OfficerTier = 1;
  private moduleId: string = 'academy'; // Default module ID for academy

  constructor() {
    super();
    this.setupEventListeners();
  }

  /**
   * Set up event listeners for tech tree unlocks and other relevant events
   */
  private setupEventListeners(): void {
    techTreeManager.on('nodeUnlocked', (event: TechNodeUnlockedEvent) => {
      if (event.node.type === 'academy') {
        this.handleAcademyUpgrade(event.node.tier as OfficerTier);
      }
    });

    moduleEventBus.subscribe('MODULE_ACTIVATED', (event: ModuleEvent) => {
      if (event.moduleType === 'academy') {
        this.moduleId = event.moduleId;
        this.emit('academyActivated', { moduleId: event.moduleId });
      }
    });
  }

  /**
   * Handle academy tier upgrades
   */
  private handleAcademyUpgrade(tier: OfficerTier): void {
    if (tier > this.currentTier) {
      this.currentTier = tier;
      this.emit('tierUpgraded', { tier });
      moduleEventBus.emit({
        type: 'MODULE_UPGRADED',
        moduleId: this.moduleId,
        moduleType: 'academy',
        timestamp: Date.now(),
        data: { tier },
      });
    }
  }

  /**
   * Generate base stats for a new officer based on role and specialization
   */
  private generateBaseStats(
    role: OfficerRole,
    specialization: OfficerSpecialization
  ): OfficerSkills {
    const baseStats: OfficerSkills = {
      combat: 1,
      leadership: 1,
      technical: 1,
    };

    // Adjust based on role
    if (role === 'Squad Leader') {
      baseStats.leadership += 2;
    }

    // Adjust based on specialization
    switch (specialization) {
      case 'War':
        baseStats.combat += 2;
        break;
      case 'Recon':
        baseStats.technical += 1;
        baseStats.combat += 1;
        break;
      case 'Mining':
        baseStats.technical += 2;
        break;
    }

    return baseStats;
  }

  /**
   * Generate random traits for a new officer
   */
  private generateTraits(): string[] {
    const numTraits = Math.floor(Math.random() * 2) + 1; // 1-2 traits
    const availableTraits = [...OFFICER_TRAITS];
    const selectedTraits: string[] = [];

    for (let i = 0; i < numTraits; i++) {
      if (availableTraits.length === 0) {
        break;
      }
      const index = Math.floor(Math.random() * availableTraits.length);
      selectedTraits.push(availableTraits[index].id);
      availableTraits.splice(index, 1);
    }

    return selectedTraits;
  }

  /**
   * Apply trait effects to an officer's stats
   */
  private applyTraitEffects(officer: Officer): void {
    officer.traits.forEach(traitId => {
      const trait = OFFICER_TRAITS.find(t => t.id === traitId);
      if (trait?.effects.skills) {
        Object.entries(trait.effects.skills).forEach(([skill, value]) => {
          officer.skills[skill as keyof typeof officer.skills] += value;
        });
      }
    });
  }

  /**
   * Calculate training duration based on officer and program
   */
  private calculateTrainingDuration(
    officer: Officer,
    specialization: OfficerSpecialization
  ): number {
    let duration = TRAINING_CONFIG.baseTime;

    // Apply level modifier
    duration *= 1 - (officer.level - 1) * TRAINING_CONFIG.levelModifier;

    // Apply specialization modifier
    if (officer.specialization === specialization) {
      duration *= 1 - TRAINING_CONFIG.specializationModifier;
    }

    // Apply trait modifiers
    officer.traits.forEach(traitId => {
      const trait = OFFICER_TRAITS.find(t => t.id === traitId);
      if (trait?.effects.bonuses?.trainingSpeed) {
        duration *= 1 - trait.effects.bonuses.trainingSpeed;
      }
    });

    return Math.max(duration, TRAINING_CONFIG.baseTime * 0.5); // Minimum 50% of base time
  }

  /**
   * Calculate XP multiplier based on officer traits and current activity
   */
  private calculateXpMultiplier(officer: Officer, activity?: string): number {
    let multiplier = TRAINING_CONFIG.xpMultiplier;

    // Apply trait bonuses
    officer.traits.forEach(traitId => {
      const trait = OFFICER_TRAITS.find(t => t.id === traitId);
      if (trait?.effects.bonuses?.xpGain) {
        multiplier *= 1 + trait.effects.bonuses.xpGain;
      }
    });

    // Apply activity-specific bonuses
    if (activity === 'training') {
      multiplier *= 1.5; // 50% bonus XP during training
    } else if (activity === 'combat') {
      multiplier *= 2.0; // 100% bonus XP during combat
    }

    return multiplier;
  }

  /**
   * Calculate next level XP requirement using a progressive scale
   */
  private calculateNextLevelXp(currentLevel: number): number {
    return Math.floor(100 * Math.pow(1.5, currentLevel - 1));
  }

  /**
   * Improve officer skills based on level up and specialization
   */
  private improveSkills(officer: Officer): void {
    // Base skill improvements
    const improvements: OfficerSkills = {
      combat: 0,
      leadership: 0,
      technical: 0,
    };

    // Specialization-based improvements
    switch (officer.specialization) {
      case 'War':
        improvements.combat += 2;
        improvements.leadership += 1;
        break;
      case 'Recon':
        improvements.technical += 1;
        improvements.combat += 1;
        improvements.leadership += 1;
        break;
      case 'Mining':
        improvements.technical += 2;
        improvements.leadership += 1;
        break;
    }

    // Role-based improvements
    if (officer.role === 'Squad Leader') {
      improvements.leadership += 1;
    }

    // Apply improvements
    Object.entries(improvements).forEach(([skill, value]) => {
      officer.skills[skill as keyof OfficerSkills] += value;
    });

    // Update stats to match skills
    officer.stats = { ...officer.skills };
  }

  /**
   * Hire a new officer
   */
  public hireOfficer(role: OfficerRole, specialization: OfficerSpecialization): Officer {
    const id = uuidv4();
    const baseStats = this.generateBaseStats(role, specialization);
    const traits = this.generateTraits();

    const officer: Officer = {
      id,
      name: `Officer-${id.substring(0, 4)}`, // Temporary name generation
      portrait: '', // TODO: Implement portrait generation
      level: 1,
      xp: 0,
      nextLevelXp: 100,
      role,
      status: 'available',
      specialization,
      skills: { ...baseStats },
      traits,
      stats: { ...baseStats },
    };

    this.applyTraitEffects(officer);
    this.officers.set(id, officer);
    this.emit('officerHired', { officer });
    this.emitModuleEvent('MODULE_CREATED', { officer });
    return officer;
  }

  /**
   * Start training an officer
   */
  public startTraining(officerId: string, specialization: OfficerSpecialization): void {
    const officer = this.officers.get(officerId);
    if (!officer || officer.status !== 'available') {
      return;
    }

    const duration = this.calculateTrainingDuration(officer, specialization);
    const program: TrainingProgram = {
      id: uuidv4(),
      officerId,
      specialization,
      progress: 0,
      startTime: Date.now(),
      duration,
      bonuses: {
        xpMultiplier: this.calculateXpMultiplier(officer, 'training'),
        skillGainRate: TRAINING_CONFIG.skillGainRate,
      },
    };

    officer.status = 'training';
    officer.trainingProgress = 0;
    this.trainingPrograms.set(program.id, program);
    this.officers.set(officerId, officer);

    this.emit('trainingStarted', {
      officerId,
      program,
    });
    this.emitModuleEvent('AUTOMATION_STARTED', { officerId, program });
  }

  /**
   * Assign an officer to a task or location
   */
  public assignOfficer(officerId: string, assignmentId: string): void {
    const officer = this.officers.get(officerId);
    if (!officer || officer.status !== 'available') {
      return;
    }

    officer.status = 'assigned';
    officer.assignedTo = assignmentId;
    this.officers.set(officerId, officer);

    this.emit('officerAssigned', {
      officerId,
      assignmentId,
    });
    this.emitModuleEvent('STATUS_CHANGED', { officerId, assignmentId, status: 'assigned' });
  }

  /**
   * Create a new squad
   */
  public createSquad(name: string, specialization: OfficerSpecialization): Squad {
    const squad: Squad = {
      id: uuidv4(),
      name,
      members: [],
      specialization,
      bonuses: {
        combat: 0,
        efficiency: 0,
        survival: 0,
      },
    };

    this.squads.set(squad.id, squad);
    this.emit('squadCreated', { squad });
    this.emitModuleEvent('MODULE_CREATED', { squad });
    return squad;
  }

  /**
   * Assign an officer to a squad
   */
  public assignToSquad(officerId: string, squadId: string): void {
    const officer = this.officers.get(officerId);
    const squad = this.squads.get(squadId);

    if (!officer || !squad || officer.status !== 'available') {
      return;
    }

    // Check squad size limit
    if (squad.members.length >= SQUAD_CONFIG.maxSize) {
      return;
    }

    // If officer is Squad Leader and squad has no leader
    if (officer.role === 'Squad Leader' && !squad.leader) {
      squad.leader = officer;
    }

    squad.members.push(officer);
    officer.status = 'assigned';
    officer.assignedTo = squadId;

    this.updateSquadBonuses(squad);
    this.emit('squadUpdated', { squadId, officer: officerId });
    this.emitModuleEvent('STATUS_CHANGED', { squadId, officer: officerId });
  }

  /**
   * Update squad bonuses based on member skills and traits
   */
  private updateSquadBonuses(squad: Squad): void {
    const baseBonuses = {
      combat: 0,
      efficiency: 0,
      survival: 0,
    };

    // Add leader bonuses if present
    if (squad.leader) {
      const leaderBonus = this.calculateLeaderBonus(squad.leader);
      baseBonuses.combat += leaderBonus;
      baseBonuses.efficiency += leaderBonus;
      baseBonuses.survival += leaderBonus;
    }

    // Add member bonuses
    squad.members.forEach(member => {
      if (member !== squad.leader) {
        const memberBonus = this.calculateMemberBonus(member);
        baseBonuses.combat += memberBonus;
        baseBonuses.efficiency += memberBonus;
        baseBonuses.survival += memberBonus;
      }
    });

    // Apply multipliers
    squad.bonuses = {
      combat: baseBonuses.combat * SQUAD_CONFIG.bonusMultipliers.combat,
      efficiency: baseBonuses.efficiency * SQUAD_CONFIG.bonusMultipliers.efficiency,
      survival: baseBonuses.survival * SQUAD_CONFIG.bonusMultipliers.survival,
    };

    // Update squad in storage
    this.squads.set(squad.id, squad);
    this.emitModuleEvent('STATUS_CHANGED', { squadId: squad.id, bonuses: squad.bonuses });
  }

  /**
   * Calculate leadership bonus for squad leader
   */
  private calculateLeaderBonus(officer: Officer): number {
    let bonus = officer.skills.leadership * SQUAD_CONFIG.leadershipBonus;

    // Apply trait bonuses
    officer.traits.forEach(traitId => {
      const trait = OFFICER_TRAITS.find(t => t.id === traitId);
      if (trait?.effects.bonuses?.squadBonus) {
        bonus *= 1 + trait.effects.bonuses.squadBonus;
      }
    });

    return bonus;
  }

  /**
   * Calculate bonus contribution from squad member
   */
  private calculateMemberBonus(officer: Officer): number {
    let bonus = 0;

    // Add skill contributions
    switch (officer.specialization) {
      case 'War':
        bonus += officer.skills.combat * 0.5;
        break;
      case 'Recon':
        bonus += (officer.skills.combat + officer.skills.technical) * 0.25;
        break;
      case 'Mining':
        bonus += officer.skills.technical * 0.5;
        break;
    }

    // Apply trait bonuses
    officer.traits.forEach(traitId => {
      const trait = OFFICER_TRAITS.find(t => t.id === traitId);
      if (trait?.effects.bonuses?.squadBonus) {
        bonus *= 1 + trait.effects.bonuses.squadBonus;
      }
    });

    return bonus;
  }

  /**
   * Add experience to an officer and handle level ups
   */
  public addExperience(officerId: string, amount: number, activity?: string): void {
    const officer = this.officers.get(officerId);
    if (!officer) {
      return;
    }

    const multiplier = this.calculateXpMultiplier(officer, activity);
    const adjustedAmount = Math.floor(amount * multiplier);

    officer.xp += adjustedAmount;

    // Handle level ups
    while (officer.xp >= officer.nextLevelXp) {
      officer.xp -= officer.nextLevelXp;
      officer.level += 1;
      officer.nextLevelXp = this.calculateNextLevelXp(officer.level);
      this.improveSkills(officer);

      // Emit level up event
      this.emit('officerLeveledUp', {
        officerId,
        newLevel: officer.level,
        skills: officer.skills,
      });
    }

    // Update officer in storage
    this.officers.set(officerId, officer);

    // Emit experience gained event
    this.emit('experienceGained', {
      officerId,
      amount: adjustedAmount,
      newTotal: officer.xp,
      nextLevel: officer.nextLevelXp,
    });
    this.emitModuleEvent('STATUS_CHANGED', {
      officerId,
      amount: adjustedAmount,
      newTotal: officer.xp,
      nextLevel: officer.nextLevelXp,
    });
  }

  /**
   * Update training programs and apply experience
   */
  public update(deltaTime: number): void {
    // Update training programs
    this.trainingPrograms.forEach((program, programId) => {
      const officer = this.officers.get(program.officerId);
      if (!officer) {
        this.trainingPrograms.delete(programId);
        return;
      }

      program.progress += deltaTime;
      if (program.progress >= program.duration) {
        this.completeTraining(programId);
      } else {
        // Update training progress
        officer.trainingProgress = program.progress / program.duration;
        this.officers.set(officer.id, officer);
      }
    });

    // Update squad bonuses
    this.squads.forEach(squad => {
      this.updateSquadBonuses(squad);
    });
  }

  /**
   * Complete a training program
   */
  private completeTraining(programId: string): void {
    const program = this.trainingPrograms.get(programId);
    if (!program) {
      return;
    }

    const officer = this.officers.get(program.officerId);
    if (!officer) {
      this.trainingPrograms.delete(programId);
      return;
    }

    // Apply training results
    officer.status = 'available';
    officer.trainingProgress = undefined;

    // Improve skills based on training
    const skillImprovement = Math.floor(
      (program.duration * program.bonuses.skillGainRate) / TRAINING_CONFIG.baseTime
    );

    switch (program.specialization) {
      case 'War':
        officer.skills.combat += skillImprovement;
        break;
      case 'Recon':
        officer.skills.combat += Math.floor(skillImprovement / 2);
        officer.skills.technical += Math.floor(skillImprovement / 2);
        break;
      case 'Mining':
        officer.skills.technical += skillImprovement;
        break;
    }

    // Update stats
    officer.stats = { ...officer.skills };

    // Add experience
    this.addExperience(officer.id, 100, 'training');

    // Update storage
    this.officers.set(officer.id, officer);
    this.trainingPrograms.delete(programId);

    // Emit completion event
    this.emit('trainingCompleted', {
      officerId: officer.id,
      specialization: program.specialization,
      skills: officer.skills,
    });
    this.emitModuleEvent('AUTOMATION_CYCLE_COMPLETE', {
      officerId: officer.id,
      specialization: program.specialization,
      skills: officer.skills,
    });
  }

  /**
   * Get an officer by ID
   */
  public getOfficer(id: string): Officer | undefined {
    return this.officers.get(id);
  }

  /**
   * Get a squad by ID
   */
  public getSquad(id: string): Squad | undefined {
    return this.squads.get(id);
  }

  /**
   * Get all available officers
   */
  public getAvailableOfficers(): Officer[] {
    return Array.from(this.officers.values()).filter(officer => officer.status === 'available');
  }

  /**
   * Get all squads of a specific specialization
   */
  public getSquadsBySpecialization(specialization: OfficerSpecialization): Squad[] {
    return Array.from(this.squads.values()).filter(
      squad => squad.specialization === specialization
    );
  }

  /**
   * Get current academy tier
   */
  public getCurrentTier(): OfficerTier {
    return this.currentTier;
  }

  private emitModuleEvent(type: ModuleEventType, data: any): void {
    moduleEventBus.emit({
      type,
      moduleId: 'officer-manager',
      moduleType: 'academy' as ModuleType,
      timestamp: Date.now(),
      data,
    });
  }
}

import {
  ResourceFlow,
  ResourcePriority,
  ResourceState,
  ResourceTransfer,
  ResourceType,
} from '../../types/resources/ResourceTypes';
import {
  validateResourceFlow,
  validateResourceTransfer,
} from '../../utils/resources/resourceValidation';

/**
 * Flow node type
 */
export type FlowNodeType = 'producer' | 'consumer' | 'storage' | 'converter';

/**
 * Flow node interface
 */
export interface FlowNode {
  id: string;
  type: FlowNodeType;
  resources: ResourceType[];
  priority: ResourcePriority;
  capacity?: number;
  efficiency?: number;
  active: boolean;
}

/**
 * Flow connection interface
 */
export interface FlowConnection {
  id: string;
  source: string;
  target: string;
  resourceType: ResourceType;
  maxRate: number;
  currentRate: number;
  priority: ResourcePriority;
  active: boolean;
}

/**
 * Flow network interface
 */
export interface FlowNetwork {
  nodes: Map<string, FlowNode>;
  connections: Map<string, FlowConnection>;
  resourceStates: Map<ResourceType, ResourceState>;
}

/**
 * Flow optimization result
 */
export interface FlowOptimizationResult {
  transfers: ResourceTransfer[];
  updatedConnections: FlowConnection[];
  bottlenecks: string[];
  underutilized: string[];
}

/**
 * Resource Flow Manager
 * Manages and optimizes resource flows between producers, consumers, and storage
 */
export class ResourceFlowManager {
  private network: FlowNetwork;
  private lastOptimization: number;
  private optimizationInterval: number;
  private transferHistory: ResourceTransfer[];
  private maxHistorySize: number;

  constructor(optimizationInterval = 5000) {
    this.network = {
      nodes: new Map<string, FlowNode>(),
      connections: new Map<string, FlowConnection>(),
      resourceStates: new Map<ResourceType, ResourceState>(),
    };
    this.lastOptimization = 0;
    this.optimizationInterval = optimizationInterval;
    this.transferHistory = [];
    this.maxHistorySize = 100;
  }

  /**
   * Register a flow node
   */
  public registerNode(node: FlowNode): boolean {
    if (!node.id || !node.resources || node.resources.length === 0) {
      console.error('Invalid flow node:', node);
      return false;
    }

    this.network.nodes.set(node.id, node);
    return true;
  }

  /**
   * Unregister a flow node
   */
  public unregisterNode(id: string): boolean {
    if (!this.network.nodes.has(id)) {
      return false;
    }

    // Remove all connections to/from this node
    // Convert Map entries to array to avoid MapIterator error
    const connectionEntries = Array.from(this.network.connections.entries());
    for (const [connectionId, connection] of connectionEntries) {
      if (connection.source === id || connection.target === id) {
        this.network.connections.delete(connectionId);
      }
    }

    this.network.nodes.delete(id);
    return true;
  }

  /**
   * Register a flow connection
   */
  public registerConnection(connection: FlowConnection): boolean {
    if (!connection.id || !connection.source || !connection.target || !connection.resourceType) {
      console.error('Invalid flow connection:', connection);
      return false;
    }

    // Verify source and target nodes exist
    if (!this.network.nodes.has(connection.source) || !this.network.nodes.has(connection.target)) {
      console.error('Source or target node does not exist:', connection);
      return false;
    }

    // Verify source node can provide the resource
    const sourceNode = this.network.nodes.get(connection.source);
    if (!sourceNode?.resources.includes(connection.resourceType)) {
      console.error('Source node cannot provide resource:', connection);
      return false;
    }

    // Verify target node can accept the resource
    const targetNode = this.network.nodes.get(connection.target);
    if (!targetNode?.resources.includes(connection.resourceType)) {
      console.error('Target node cannot accept resource:', connection);
      return false;
    }

    this.network.connections.set(connection.id, connection);
    return true;
  }

  /**
   * Unregister a flow connection
   */
  public unregisterConnection(id: string): boolean {
    if (!this.network.connections.has(id)) {
      return false;
    }

    this.network.connections.delete(id);
    return true;
  }

  /**
   * Update resource state
   */
  public updateResourceState(type: ResourceType, state: ResourceState): void {
    this.network.resourceStates.set(type, state);
  }

  /**
   * Get resource state
   */
  public getResourceState(type: ResourceType): ResourceState | undefined {
    return this.network.resourceStates.get(type);
  }

  /**
   * Optimize resource flows
   */
  public optimizeFlows(): FlowOptimizationResult {
    const now = Date.now();

    // Skip optimization if not enough time has passed
    if (now - this.lastOptimization < this.optimizationInterval) {
      return {
        transfers: [],
        updatedConnections: [],
        bottlenecks: [],
        underutilized: [],
      };
    }

    this.lastOptimization = now;

    // Get active nodes and connections
    const activeNodes = Array.from(this.network.nodes.values()).filter(node => node.active);
    const activeConnections = Array.from(this.network.connections.values()).filter(
      conn => conn.active
    );

    // Group nodes by type
    const producers = activeNodes.filter(node => node.type === 'producer');
    const consumers = activeNodes.filter(node => node.type === 'consumer');
    const storages = activeNodes.filter(node => node.type === 'storage');
    const converters = activeNodes.filter(node => node.type === 'converter');

    // Calculate resource availability and demand
    const availability: Partial<Record<ResourceType, number>> = {};
    const demand: Partial<Record<ResourceType, number>> = {};

    // Initialize with current resource states
    // Convert Map entries to array to avoid MapIterator error
    const resourceStateEntries = Array.from(this.network.resourceStates.entries());
    for (const [type, state] of resourceStateEntries) {
      availability[type] = 0;
      demand[type] = 0;
    }

    // Calculate production capacity
    for (const producer of producers) {
      for (const resourceType of producer.resources) {
        // Find outgoing connections for this resource
        const outgoingConnections = activeConnections.filter(
          conn => conn.source === producer.id && conn.resourceType === resourceType
        );

        // Sum up max rates
        const totalMaxRate = outgoingConnections.reduce((sum, conn) => sum + conn.maxRate, 0);

        // Apply efficiency if available
        const effectiveRate = producer.efficiency
          ? totalMaxRate * producer.efficiency
          : totalMaxRate;

        availability[resourceType] = (availability[resourceType] || 0) + effectiveRate;
      }
    }

    // Calculate consumption needs
    for (const consumer of consumers) {
      for (const resourceType of consumer.resources) {
        // Find incoming connections for this resource
        const incomingConnections = activeConnections.filter(
          conn => conn.target === consumer.id && conn.resourceType === resourceType
        );

        // Sum up max rates
        const totalMaxRate = incomingConnections.reduce((sum, conn) => sum + conn.maxRate, 0);

        demand[resourceType] = (demand[resourceType] || 0) + totalMaxRate;
      }
    }

    // Adjust for storage capacity
    for (const storage of storages) {
      for (const resourceType of storage.resources) {
        const resourceState = this.network.resourceStates.get(resourceType);
        if (!resourceState) {
          continue;
        }

        // If storage is near capacity, reduce availability
        if (resourceState.current > resourceState.max * 0.9) {
          availability[resourceType] = Math.max(
            0,
            (availability[resourceType] || 0) - (resourceState.max - resourceState.current)
          );
        }

        // If storage is near empty, increase demand
        if (resourceState.current < resourceState.max * 0.1) {
          demand[resourceType] = (demand[resourceType] || 0) + resourceState.max * 0.2;
        }
      }
    }

    // Identify bottlenecks and underutilized resources
    const bottlenecks: string[] = [];
    const underutilized: string[] = [];

    for (const [type, availableAmount] of Object.entries(availability)) {
      const demandAmount = demand[type as ResourceType] || 0;

      if (availableAmount < demandAmount * 0.9) {
        bottlenecks.push(type);
      } else if (availableAmount > demandAmount * 1.5) {
        underutilized.push(type);
      }
    }

    // Optimize flow rates based on priorities
    const updatedConnections: FlowConnection[] = [];

    // Sort connections by priority (high to low)
    const prioritizedConnections = [...activeConnections].sort(
      (a, b) => Number(b.priority) - Number(a.priority)
    );

    // Adjust flow rates
    for (const connection of prioritizedConnections) {
      const { resourceType } = connection;
      const availableForType = availability[resourceType] || 0;
      const demandForType = demand[resourceType] || 0;

      if (availableForType <= 0 || demandForType <= 0) {
        // No flow possible
        connection.currentRate = 0;
      } else if (availableForType >= demandForType) {
        // Full flow possible
        connection.currentRate = Math.min(connection.maxRate, demandForType);
      } else {
        // Partial flow based on ratio
        const ratio = availableForType / demandForType;
        connection.currentRate = connection.maxRate * ratio;
      }

      updatedConnections.push({ ...connection });

      // Update the actual connection in the network
      this.network.connections.set(connection.id, connection);
    }

    // Generate transfer instructions
    const transfers: ResourceTransfer[] = [];

    for (const connection of updatedConnections) {
      if (connection.currentRate <= 0) {
        continue;
      }

      const transfer: ResourceTransfer = {
        type: connection.resourceType,
        source: connection.source,
        target: connection.target,
        amount: connection.currentRate,
        timestamp: now,
      };

      if (validateResourceTransfer(transfer)) {
        transfers.push(transfer);

        // Add to history
        this.addToTransferHistory(transfer);
      }
    }

    return {
      transfers,
      updatedConnections,
      bottlenecks,
      underutilized,
    };
  }

  /**
   * Add a transfer to history
   */
  private addToTransferHistory(transfer: ResourceTransfer): void {
    this.transferHistory.push(transfer);

    // Trim history if needed
    if (this.transferHistory.length > this.maxHistorySize) {
      this.transferHistory = this.transferHistory.slice(-this.maxHistorySize);
    }
  }

  /**
   * Get transfer history
   */
  public getTransferHistory(): ResourceTransfer[] {
    return [...this.transferHistory];
  }

  /**
   * Get transfer history for a specific resource type
   */
  public getTransferHistoryByType(type: ResourceType): ResourceTransfer[] {
    return this.transferHistory.filter(transfer => transfer.type === type);
  }

  /**
   * Get all nodes
   */
  public getNodes(): FlowNode[] {
    return Array.from(this.network.nodes.values());
  }

  /**
   * Get all connections
   */
  public getConnections(): FlowConnection[] {
    return Array.from(this.network.connections.values());
  }

  /**
   * Get node by ID
   */
  public getNode(id: string): FlowNode | undefined {
    return this.network.nodes.get(id);
  }

  /**
   * Get connection by ID
   */
  public getConnection(id: string): FlowConnection | undefined {
    return this.network.connections.get(id);
  }

  /**
   * Create a resource flow
   */
  public createFlow(flow: ResourceFlow): boolean {
    if (!validateResourceFlow(flow)) {
      console.error('Invalid resource flow:', flow);
      return false;
    }

    // Extract resource type and other properties from flow
    const resourceType = flow.resources[0]?.type;
    const rate = flow.resources[0]?.amount || 0;
    const interval = flow.resources[0]?.interval || 1000;
    // Create a proper ResourcePriority object
    const priority: ResourcePriority = {
      type: resourceType,
      priority: 1,
      consumers: [],
    };

    if (!resourceType) {
      console.error('Flow must have at least one resource');
      return false;
    }

    // Create nodes if they don't exist
    if (!this.network.nodes.has(flow.source)) {
      this.registerNode({
        id: flow.source,
        type: 'producer',
        resources: [resourceType],
        priority: priority,
        active: true,
      });
    }

    if (!this.network.nodes.has(flow.target)) {
      this.registerNode({
        id: flow.target,
        type: 'consumer',
        resources: [resourceType],
        priority: priority,
        active: true,
      });
    }

    // Create connection
    const connectionId = `${flow.source}-${flow.target}-${resourceType}`;

    return this.registerConnection({
      id: connectionId,
      source: flow.source,
      target: flow.target,
      resourceType: resourceType,
      maxRate: rate,
      currentRate: 0,
      priority: priority,
      active: true,
    });
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.network.nodes.clear();
    this.network.connections.clear();
    this.network.resourceStates.clear();
    this.transferHistory = [];
  }
}

import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import { ResourceType } from '../../types/resources/ResourceTypes';

export interface PerformanceMetrics {
  timestamp: number;
  resourceType: ResourceType;
  productionRate: number;
  consumptionRate: number;
  transferRate: number;
  utilizationRate: number;
  efficiency: number;
}

export interface ResourcePerformanceSnapshot {
  metrics: Map<ResourceType, PerformanceMetrics>;
  systemLoad: number;
  bottlenecks: ResourceType[];
  recommendations: string[];
}

/**
 * Monitors and analyzes resource management performance
 */
export class ResourcePerformanceMonitor {
  private metricsHistory: Map<ResourceType, PerformanceMetrics[]>;
  private snapshotInterval: number;
  private maxHistoryLength: number;
  private lastSnapshotTime: number;

  constructor(snapshotInterval = 5000, maxHistoryLength = 100) {
    this.metricsHistory = new Map();
    this.snapshotInterval = snapshotInterval;
    this.maxHistoryLength = maxHistoryLength;
    this.lastSnapshotTime = Date.now();

    // Start monitoring
    this.startMonitoring();
  }

  /**
   * Starts the performance monitoring
   */
  private startMonitoring(): void {
    setInterval(() => this.takeSnapshot(), this.snapshotInterval);
    console.debug('[ResourcePerformanceMonitor] Started monitoring');
  }

  /**
   * Records performance metrics for a resource
   */
  recordMetrics(
    type: ResourceType,
    productionRate: number,
    consumptionRate: number,
    transferRate: number,
    utilizationRate: number
  ): void {
    const metrics: PerformanceMetrics = {
      timestamp: Date.now(),
      resourceType: type,
      productionRate,
      consumptionRate,
      transferRate,
      utilizationRate,
      efficiency: this.calculateEfficiency(productionRate, consumptionRate, utilizationRate),
    };

    if (!this.metricsHistory.has(type)) {
      this.metricsHistory.set(type, []);
    }

    const history = this.metricsHistory.get(type)!;
    history.push(metrics);

    // Trim history if needed
    if (history.length > this.maxHistoryLength) {
      history.shift();
    }
  }

  /**
   * Calculates resource efficiency
   */
  private calculateEfficiency(
    production: number,
    consumption: number,
    utilization: number
  ): number {
    // Efficiency formula: balance between production matching consumption
    // and good utilization (not too full, not too empty)
    const productionBalance = consumption > 0 ? Math.min(production / consumption, 1.5) : 1.0;
    const utilizationScore = 1 - Math.abs(0.5 - utilization);
    return (productionBalance + utilizationScore) / 2;
  }

  /**
   * Takes a performance snapshot
   */
  private takeSnapshot(): void {
    const snapshot = this.generateSnapshot();
    this.lastSnapshotTime = Date.now();

    // Emit performance snapshot
    moduleEventBus.emit({
      type: 'STATUS_CHANGED',
      moduleId: 'resource-performance-monitor',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        type: 'performance_snapshot',
        snapshot,
      },
    });

    // Log performance insights
    this.logPerformanceInsights(snapshot);
  }

  /**
   * Generates a performance snapshot
   */
  private generateSnapshot(): ResourcePerformanceSnapshot {
    const metrics = new Map<ResourceType, PerformanceMetrics>();
    const bottlenecks: ResourceType[] = [];
    const recommendations: string[] = [];

    // Calculate current metrics for each resource
    for (const [type, history] of this.metricsHistory) {
      if (history.length === 0) {
        continue;
      }

      const latest = history[history.length - 1];
      metrics.set(type, latest);

      // Identify bottlenecks
      if (latest.efficiency < 0.6) {
        bottlenecks.push(type);

        // Generate recommendations
        if (latest.productionRate < latest.consumptionRate) {
          recommendations.push(
            `Increase production rate for ${type} (current: ${latest.productionRate.toFixed(2)}, needed: ${latest.consumptionRate.toFixed(2)})`
          );
        } else if (latest.utilizationRate > 0.9) {
          recommendations.push(
            `Consider increasing storage capacity for ${type} (utilization: ${(latest.utilizationRate * 100).toFixed(1)}%)`
          );
        } else if (latest.utilizationRate < 0.1) {
          recommendations.push(
            `Optimize consumption or reduce production of ${type} (utilization: ${(latest.utilizationRate * 100).toFixed(1)}%)`
          );
        }
      }
    }

    // Calculate system load (average utilization across all resources)
    const systemLoad =
      Array.from(metrics.values()).reduce((sum, m) => sum + m.utilizationRate, 0) /
      Math.max(metrics.size, 1);

    return {
      metrics,
      systemLoad,
      bottlenecks,
      recommendations,
    };
  }

  /**
   * Logs performance insights
   */
  private logPerformanceInsights(snapshot: ResourcePerformanceSnapshot): void {
    console.debug(`[ResourcePerformanceMonitor] Performance Snapshot:
      System Load: ${(snapshot.systemLoad * 100).toFixed(1)}%
      Bottlenecks: ${snapshot.bottlenecks.join(', ') || 'None'}

      Recommendations:
      ${snapshot.recommendations.map(r => `- ${r}`).join('\n      ') || '- No recommendations'}`);
  }

  /**
   * Gets performance history for a resource
   */
  getResourceHistory(type: ResourceType): PerformanceMetrics[] {
    return [...(this.metricsHistory.get(type) || [])];
  }

  /**
   * Gets the latest snapshot
   */
  getLatestSnapshot(): ResourcePerformanceSnapshot {
    return this.generateSnapshot();
  }

  /**
   * Cleans up the monitor
   */
  cleanup(): void {
    this.metricsHistory.clear();
    console.debug('[ResourcePerformanceMonitor] Cleaned up performance monitor');
  }

  /**
   * Get all metrics history
   */
  public getAllMetricsHistory(): Record<ResourceType, PerformanceMetrics[]> {
    const result: Record<ResourceType, PerformanceMetrics[]> = {} as Record<
      ResourceType,
      PerformanceMetrics[]
    >;

    for (const [type, metrics] of Array.from(this.metricsHistory)) {
      result[type] = [...metrics];
    }

    return result;
  }
}

// Export singleton instance
export const resourcePerformanceMonitor = new ResourcePerformanceMonitor();

import {
  ResourceContainer,
  ResourcePool,
  ResourceState,
  ResourceType,
} from '../../types/resources/ResourceTypes';
import { isResourcePool } from '../../utils/resources/resourceValidation';

/**
 * Pool allocation strategy
 */
export type PoolAllocationStrategy = 'equal' | 'priority' | 'demand-based' | 'custom';

/**
 * Pool distribution rule
 */
export interface PoolDistributionRule {
  id: string;
  poolId: string;
  targetIds: string[];
  resourceType: ResourceType;
  percentage: number;
  minAmount?: number;
  maxAmount?: number;
  priority: number;
  condition?: (state: ResourceState) => boolean;
  enabled?: boolean;
  sourceId?: string;
  amount?: number;
}

/**
 * Pool allocation result
 */
export interface PoolAllocationResult {
  poolId: string;
  resourceType: ResourceType;
  allocations: Array<{
    targetId: string;
    amount: number;
    percentage: number;
  }>;
  timestamp: number;
}

/**
 * Resource Pool Manager
 * Manages resource pools, distribution, and allocation
 */
export class ResourcePoolManager {
  private pools: Map<string, ResourcePool>;
  private containers: Map<string, ResourceContainer>;
  private distributionRules: Map<string, PoolDistributionRule>;
  private resourceStates: Map<ResourceType, ResourceState>;
  private allocationHistory: PoolAllocationResult[];
  private allocationStrategy: PoolAllocationStrategy;
  private maxHistorySize: number;
  private lastDistribution: number;
  private distributionInterval: number;

  constructor(
    allocationStrategy: PoolAllocationStrategy = 'priority',
    distributionInterval = 5000,
    maxHistorySize = 100
  ) {
    this.pools = new Map();
    this.containers = new Map();
    this.distributionRules = new Map();
    this.resourceStates = new Map();
    this.allocationHistory = [];
    this.allocationStrategy = allocationStrategy;
    this.maxHistorySize = maxHistorySize;
    this.lastDistribution = 0;
    this.distributionInterval = distributionInterval;
  }

  /**
   * Update resource state
   */
  public updateResourceState(type: ResourceType, state: ResourceState): void {
    this.resourceStates.set(type, state);
  }

  /**
   * Register a resource pool
   */
  public registerPool(pool: ResourcePool): boolean {
    if (!isResourcePool(pool)) {
      console.error('Invalid resource pool:', pool);
      return false;
    }

    this.pools.set(pool.id, pool);
    return true;
  }

  /**
   * Unregister a resource pool
   */
  public unregisterPool(id: string): boolean {
    if (!this.pools.has(id)) {
      return false;
    }

    // Remove all distribution rules for this pool
    for (const [ruleId, rule] of this.distributionRules.entries()) {
      if (rule.poolId === id) {
        this.distributionRules.delete(ruleId);
      }
    }

    this.pools.delete(id);
    return true;
  }

  /**
   * Register a container
   */
  public registerContainer(container: ResourceContainer): boolean {
    if (!container.id) {
      console.error('Invalid container:', container);
      return false;
    }

    this.containers.set(container.id, container);
    return true;
  }

  /**
   * Unregister a container
   */
  public unregisterContainer(id: string): boolean {
    if (!this.containers.has(id)) {
      return false;
    }

    this.containers.delete(id);
    return true;
  }

  /**
   * Register a distribution rule
   */
  public registerDistributionRule(rule: PoolDistributionRule): boolean {
    if (!rule.id || !rule.poolId || !rule.targetIds || rule.targetIds.length === 0) {
      console.error('Invalid distribution rule:', rule);
      return false;
    }

    // Verify pool exists
    if (!this.pools.has(rule.poolId)) {
      console.error(`Pool ${rule.poolId} does not exist`);
      return false;
    }

    // Verify targets exist
    for (const targetId of rule.targetIds) {
      if (!this.containers.has(targetId)) {
        console.error(`Target container ${targetId} does not exist`);
        return false;
      }
    }

    this.distributionRules.set(rule.id, rule);
    return true;
  }

  /**
   * Unregister a distribution rule
   */
  public unregisterDistributionRule(id: string): boolean {
    if (!this.distributionRules.has(id)) {
      return false;
    }

    this.distributionRules.delete(id);
    return true;
  }

  /**
   * Set allocation strategy
   */
  public setAllocationStrategy(strategy: PoolAllocationStrategy): void {
    this.allocationStrategy = strategy;
  }

  /**
   * Get allocation strategy
   */
  public getAllocationStrategy(): PoolAllocationStrategy {
    return this.allocationStrategy;
  }

  /**
   * Add resource to pool
   */
  public addToPool(poolId: string, type: ResourceType, amount: number): boolean {
    const pool = this.pools.get(poolId);
    if (!pool) {
      console.error(`Pool ${poolId} does not exist`);
      return false;
    }

    // Initialize resources map if it doesn't exist
    if (!pool.resources) {
      pool.resources = new Map<ResourceType, number>();
    }

    // Add resource to pool
    const currentAmount = pool.resources.get(type) || 0;
    pool.resources.set(type, currentAmount + amount);

    return true;
  }

  /**
   * Remove resource from pool
   */
  public removeFromPool(poolId: string, type: ResourceType, amount: number): boolean {
    const pool = this.pools.get(poolId);
    if (!pool || !pool.resources) {
      console.error(`Pool ${poolId} does not exist or has no resources`);
      return false;
    }

    const currentAmount = pool.resources.get(type) || 0;
    if (currentAmount < amount) {
      console.error(`Insufficient ${type} in pool ${poolId}`);
      return false;
    }

    pool.resources.set(type, currentAmount - amount);
    return true;
  }

  /**
   * Transfer between pools
   */
  public transferBetweenPools(
    sourcePoolId: string,
    targetPoolId: string,
    type: ResourceType,
    amount: number
  ): boolean {
    // Remove from source pool
    if (!this.removeFromPool(sourcePoolId, type, amount)) {
      return false;
    }

    // Add to target pool
    if (!this.addToPool(targetPoolId, type, amount)) {
      // Rollback source pool removal
      this.addToPool(sourcePoolId, type, amount);
      return false;
    }

    return true;
  }

  /**
   * Distribute resources from pools to containers
   */
  public distributeResources(now = Date.now()): PoolAllocationResult[] {
    // Skip if not enough time has passed
    if (now - this.lastDistribution < this.distributionInterval) {
      return [];
    }

    this.lastDistribution = now;
    const results: PoolAllocationResult[] = [];

    // Sort rules by priority (high to low)
    const sortedRules = Array.from(this.distributionRules.values()).sort(
      (a, b) => b.priority - a.priority
    );

    // Process each rule
    for (const rule of sortedRules) {
      // Skip disabled rules
      if (rule.enabled === false) {
        continue;
      }

      const pool = this.pools.get(rule.poolId);
      if (!pool || !pool.resources) {
        continue;
      }

      const resourceAmount = pool.resources.get(rule.resourceType) || 0;
      if (resourceAmount <= 0) {
        continue;
      }

      // Check condition if provided
      if (rule.condition) {
        const resourceState = this.resourceStates.get(rule.resourceType);
        if (!resourceState || !rule.condition(resourceState)) {
          continue;
        }
      }

      // Calculate amount to distribute
      let amountToDistribute = resourceAmount * (rule.percentage / 100);

      // Apply min/max constraints
      if (rule.minAmount !== undefined && amountToDistribute < rule.minAmount) {
        amountToDistribute = Math.min(rule.minAmount, resourceAmount);
      }

      if (rule.maxAmount !== undefined && amountToDistribute > rule.maxAmount) {
        amountToDistribute = rule.maxAmount;
      }

      // Skip if amount is too small
      if (amountToDistribute <= 0) {
        continue;
      }

      // Allocate resources based on strategy
      const allocations = this.allocateResources(
        rule.targetIds,
        rule.resourceType,
        amountToDistribute
      );

      // Apply allocations
      let totalAllocated = 0;
      for (const allocation of allocations) {
        const container = this.containers.get(allocation.targetId);
        if (!container) {
          continue;
        }

        // Initialize resources map if it doesn't exist
        if (!container.resources) {
          container.resources = new Map<ResourceType, number>();
        }

        // Add resource to container
        const currentAmount = container.resources.get(rule.resourceType) || 0;
        container.resources.set(rule.resourceType, currentAmount + allocation.amount);
        totalAllocated += allocation.amount;
      }

      // Remove allocated amount from pool
      if (totalAllocated > 0) {
        pool.resources.set(rule.resourceType, resourceAmount - totalAllocated);

        // Record allocation
        const result: PoolAllocationResult = {
          poolId: rule.poolId,
          resourceType: rule.resourceType,
          allocations,
          timestamp: now,
        };

        results.push(result);
        this.allocationHistory.push(result);

        // Trim history if needed
        if (this.allocationHistory.length > this.maxHistorySize) {
          this.allocationHistory = this.allocationHistory.slice(-this.maxHistorySize);
        }
      }
    }

    return results;
  }

  /**
   * Allocate resources based on strategy
   */
  private allocateResources(
    targetIds: string[],
    resourceType: ResourceType,
    amount: number
  ): Array<{ targetId: string; amount: number; percentage: number }> {
    if (targetIds.length === 0 || amount <= 0) {
      return [];
    }

    switch (this.allocationStrategy) {
      case 'equal':
        return this.allocateEqual(targetIds, amount);
      case 'priority':
        return this.allocatePriority(targetIds, resourceType, amount);
      case 'demand-based':
        return this.allocateDemandBased(targetIds, resourceType, amount);
      case 'custom':
        return this.allocateCustom(targetIds, resourceType, amount);
      default:
        return this.allocateEqual(targetIds, amount);
    }
  }

  /**
   * Allocate resources equally
   */
  private allocateEqual(
    targetIds: string[],
    amount: number
  ): Array<{ targetId: string; amount: number; percentage: number }> {
    const equalAmount = amount / targetIds.length;
    const percentage = 100 / targetIds.length;

    return targetIds.map(targetId => ({
      targetId,
      amount: equalAmount,
      percentage,
    }));
  }

  /**
   * Allocate resources based on priority
   */
  private allocatePriority(
    targetIds: string[],
    resourceType: ResourceType,
    amount: number
  ): Array<{ targetId: string; amount: number; percentage: number }> {
    // Get containers with priority
    const containersWithPriority = targetIds
      .map(id => {
        const container = this.containers.get(id);
        return {
          id,
          priority:
            container && 'priority' in container ? ((container as any).priority as number) : 1,
        };
      })
      .sort((a, b) => (b.priority as number) - (a.priority as number));

    // Calculate total priority
    const totalPriority = containersWithPriority.reduce(
      (sum, container) => sum + (container.priority as number),
      0
    );

    // Allocate based on priority
    return containersWithPriority.map(container => {
      const containerPercentage = ((container.priority as number) / totalPriority) * 100;
      const containerAmount = ((container.priority as number) / totalPriority) * amount;

      return {
        targetId: container.id,
        amount: containerAmount,
        percentage: containerPercentage,
      };
    });
  }

  /**
   * Allocate resources based on demand
   */
  private allocateDemandBased(
    targetIds: string[],
    resourceType: ResourceType,
    amount: number
  ): Array<{ targetId: string; amount: number; percentage: number }> {
    // Calculate demand for each container
    const containerDemands = targetIds.map(id => {
      const container = this.containers.get(id);
      if (!container) {
        return { id, demand: 0 };
      }

      // Calculate demand based on capacity and current amount
      let demand = 0;
      if (container.capacity && container.resources) {
        const currentAmount = container.resources.get(resourceType) || 0;
        demand = Math.max(0, container.capacity - currentAmount);
      }

      return { id, demand };
    });

    // Calculate total demand
    const totalDemand = containerDemands.reduce((sum, container) => sum + container.demand, 0);

    // If no demand, allocate equally
    if (totalDemand <= 0) {
      return this.allocateEqual(targetIds, amount);
    }

    // Allocate based on demand
    return containerDemands.map(container => {
      const containerPercentage = (container.demand / totalDemand) * 100;
      const containerAmount = (container.demand / totalDemand) * amount;

      return {
        targetId: container.id,
        amount: containerAmount,
        percentage: containerPercentage,
      };
    });
  }

  /**
   * Allocate resources based on custom logic
   */
  private allocateCustom(
    targetIds: string[],
    resourceType: ResourceType,
    amount: number
  ): Array<{ targetId: string; amount: number; percentage: number }> {
    // This is a placeholder for custom allocation logic
    // In a real implementation, this would use more complex rules

    // For now, we'll use a combination of priority and demand
    const priorityAllocations = this.allocatePriority(targetIds, resourceType, amount);
    const demandAllocations = this.allocateDemandBased(targetIds, resourceType, amount);

    // Combine allocations (50% priority, 50% demand)
    return targetIds.map(id => {
      const priorityAllocation = priorityAllocations.find(a => a.targetId === id);
      const demandAllocation = demandAllocations.find(a => a.targetId === id);

      const combinedAmount =
        (priorityAllocation?.amount || 0) * 0.5 + (demandAllocation?.amount || 0) * 0.5;

      const combinedPercentage =
        (priorityAllocation?.percentage || 0) * 0.5 + (demandAllocation?.percentage || 0) * 0.5;

      return {
        targetId: id,
        amount: combinedAmount,
        percentage: combinedPercentage,
      };
    });
  }

  /**
   * Get pool by ID
   */
  public getPool(id: string): ResourcePool | undefined {
    return this.pools.get(id);
  }

  /**
   * Get all pools
   */
  public getAllPools(): ResourcePool[] {
    return Array.from(this.pools.values());
  }

  /**
   * Get pools by type
   */
  public getPoolsByType(type: ResourceType): ResourcePool[] {
    return Array.from(this.pools.values()).filter(pool => pool.type === type);
  }

  /**
   * Get distribution rules by pool ID
   */
  public getDistributionRulesByPool(poolId: string): PoolDistributionRule[] {
    return Array.from(this.distributionRules.values()).filter(rule => rule.poolId === poolId);
  }

  /**
   * Get allocation history
   */
  public getAllocationHistory(): PoolAllocationResult[] {
    return [...this.allocationHistory];
  }

  /**
   * Get allocation history by pool ID
   */
  public getAllocationHistoryByPool(poolId: string): PoolAllocationResult[] {
    return this.allocationHistory.filter(result => result.poolId === poolId);
  }

  /**
   * Get allocation history by resource type
   */
  public getAllocationHistoryByType(type: ResourceType): PoolAllocationResult[] {
    return this.allocationHistory.filter(result => result.resourceType === type);
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.pools.clear();
    this.containers.clear();
    this.distributionRules.clear();
    this.resourceStates.clear();
    this.allocationHistory = [];
  }

  /**
   * Transfer resources directly between containers
   */
  public transferDirectly(
    sourceId: string,
    targetId: string,
    resourceType: ResourceType,
    amount: number
  ): boolean {
    const sourceContainer = this.containers.get(sourceId);
    const targetContainer = this.containers.get(targetId);

    if (!sourceContainer || !targetContainer) {
      console.error(`Source or target container not found: ${sourceId}, ${targetId}`);
      return false;
    }

    // Initialize resources maps if they don't exist
    if (!sourceContainer.resources) {
      sourceContainer.resources = new Map<ResourceType, number>();
    }

    if (!targetContainer.resources) {
      targetContainer.resources = new Map<ResourceType, number>();
    }

    // Check if source has enough resources
    const sourceAmount = sourceContainer.resources.get(resourceType) || 0;
    if (sourceAmount < amount) {
      console.error(`Insufficient ${resourceType} in source container ${sourceId}`);
      return false;
    }

    // Transfer resources
    const targetAmount = targetContainer.resources.get(resourceType) || 0;

    // Update source and target containers
    sourceContainer.resources.set(resourceType, sourceAmount - amount);
    targetContainer.resources.set(resourceType, targetAmount + amount);

    return true;
  }
}

import { ResourceState, ResourceTransfer, ResourceType } from '../../types/resources/ResourceTypes';

/**
 * Storage allocation strategy
 */
export type StorageAllocationStrategy = 'balanced' | 'prioritized' | 'dedicated';

/**
 * Storage container configuration
 */
export interface StorageContainerConfig {
  id: string;
  name: string;
  type: 'container' | 'pool' | 'storage';
  capacity: number;
  resourceTypes: ResourceType[];
  priority: number;
  location?: string;
  efficiency?: number;
  accessSpeed?: number;
  upgradeLevel?: number;
  maxUpgradeLevel?: number;
}

/**
 * Storage container state
 */
export interface StorageContainerState {
  config: StorageContainerConfig;
  resources: Map<ResourceType, ResourceState>;
  totalStored: number;
  lastUpdated: number;
}

/**
 * Storage allocation
 */
export interface StorageAllocation {
  containerId: string;
  resourceType: ResourceType;
  amount: number;
  percentage: number;
}

/**
 * Storage overflow policy
 */
export type StorageOverflowPolicy = 'reject' | 'redistribute' | 'convert' | 'discard';

/**
 * Storage manager configuration
 */
export interface StorageManagerConfig {
  defaultAllocationStrategy: StorageAllocationStrategy;
  overflowPolicy: StorageOverflowPolicy;
  autoRebalance: boolean;
  rebalanceThreshold: number;
  priorityWeights: {
    containerPriority: number;
    resourcePriority: number;
    fillPercentage: number;
  };
}

/**
 * Resource Storage Manager
 * Manages resource storage containers, pools, and allocation strategies
 */
export class ResourceStorageManager {
  private containers: Map<string, StorageContainerState>;
  private resourcePriorities: Map<ResourceType, number>;
  private config: StorageManagerConfig;
  private transferHistory: ResourceTransfer[];
  private maxHistorySize: number;

  constructor(config?: Partial<StorageManagerConfig>) {
    this.containers = new Map();
    this.resourcePriorities = new Map();
    this.transferHistory = [];
    this.maxHistorySize = 100;

    // Default configuration
    this.config = {
      defaultAllocationStrategy: 'balanced',
      overflowPolicy: 'redistribute',
      autoRebalance: true,
      rebalanceThreshold: 0.2, // 20% imbalance triggers rebalance
      priorityWeights: {
        containerPriority: 0.4,
        resourcePriority: 0.4,
        fillPercentage: 0.2,
      },
      ...config,
    };

    // Set default resource priorities
    this.setDefaultResourcePriorities();
  }

  /**
   * Set default resource priorities
   */
  private setDefaultResourcePriorities(): void {
    // Higher number = higher priority
    this.resourcePriorities.set('energy', 10);
    this.resourcePriorities.set('minerals', 8);
    // Replace non-existent resource types with valid ones
    this.resourcePriorities.set('population', 9);
    this.resourcePriorities.set('research', 10);
    this.resourcePriorities.set('plasma', 10);
    this.resourcePriorities.set('gas', 7);
    this.resourcePriorities.set('exotic', 6);
    this.resourcePriorities.set('research', 5);
    this.resourcePriorities.set('population', 10);
  }

  /**
   * Register a storage container
   */
  public registerContainer(config: StorageContainerConfig): boolean {
    if (!config.id || !config.resourceTypes || config.resourceTypes.length === 0) {
      console.error('Invalid storage container configuration:', config);
      return false;
    }

    // Initialize resource states
    const resources = new Map<ResourceType, ResourceState>();

    for (const type of config.resourceTypes) {
      resources.set(type, {
        current: 0,
        min: 0,
        max: config.capacity / config.resourceTypes.length, // Divide capacity equally by default
        production: 0,
        consumption: 0,
      });
    }

    this.containers.set(config.id, {
      config,
      resources,
      totalStored: 0,
      lastUpdated: Date.now(),
    });

    return true;
  }

  /**
   * Unregister a storage container
   */
  public unregisterContainer(id: string): boolean {
    if (!this.containers.has(id)) {
      return false;
    }

    this.containers.delete(id);
    return true;
  }

  /**
   * Get a storage container
   */
  public getContainer(id: string): StorageContainerState | undefined {
    return this.containers.get(id);
  }

  /**
   * Get all storage containers
   */
  public getAllContainers(): StorageContainerState[] {
    return Array.from(this.containers.values());
  }

  /**
   * Get containers by resource type
   */
  public getContainersByResourceType(type: ResourceType): StorageContainerState[] {
    return Array.from(this.containers.values()).filter(container => container.resources.has(type));
  }

  /**
   * Store resource in a specific container
   */
  public storeResource(containerId: string, type: ResourceType, amount: number): number {
    const container = this.containers.get(containerId);
    if (!container || !container.resources.has(type) || amount <= 0) {
      return 0;
    }

    const resourceState = container.resources.get(type)!;
    const availableSpace = resourceState.max - resourceState.current;

    if (availableSpace <= 0) {
      return 0;
    }

    const amountToStore = Math.min(amount, availableSpace);

    resourceState.current += amountToStore;

    container.totalStored += amountToStore;
    container.lastUpdated = Date.now();

    // Record transfer
    this.recordTransfer({
      type,
      source: 'external',
      target: containerId,
      amount: amountToStore,
      timestamp: Date.now(),
    });

    return amountToStore;
  }

  /**
   * Retrieve resource from a specific container
   */
  public retrieveResource(containerId: string, type: ResourceType, amount: number): number {
    const container = this.containers.get(containerId);
    if (!container || !container.resources.has(type) || amount <= 0) {
      return 0;
    }

    const resourceState = container.resources.get(type)!;

    if (resourceState.current <= 0) {
      return 0;
    }

    const amountToRetrieve = Math.min(amount, resourceState.current);

    resourceState.current -= amountToRetrieve;

    container.totalStored -= amountToRetrieve;
    container.lastUpdated = Date.now();

    // Record transfer
    this.recordTransfer({
      type,
      source: containerId,
      target: 'external',
      amount: amountToRetrieve,
      timestamp: Date.now(),
    });

    return amountToRetrieve;
  }

  /**
   * Store resource in the best available container
   */
  public storeResourceOptimal(type: ResourceType, amount: number): number {
    if (amount <= 0) {
      return 0;
    }

    // Get all containers that can store this resource type
    const availableContainers = this.getContainersByResourceType(type);

    if (availableContainers.length === 0) {
      return 0;
    }

    // Calculate scores for each container based on the allocation strategy
    const containerScores = this.calculateContainerScores(availableContainers, type);

    // Sort containers by score (highest first)
    const sortedContainers = [...containerScores].sort((a, b) => b.score - a.score);

    let remainingAmount = amount;
    let totalStored = 0;

    // Try to store in containers by score order
    for (const { containerId, score: _ } of sortedContainers) {
      if (remainingAmount <= 0) {
        break;
      }

      const amountStored = this.storeResource(containerId, type, remainingAmount);
      totalStored += amountStored;
      remainingAmount -= amountStored;
    }

    // Handle overflow if needed
    if (remainingAmount > 0 && this.config.overflowPolicy !== 'reject') {
      totalStored += this.handleOverflow(type, remainingAmount);
    }

    // Check if rebalancing is needed
    if (this.config.autoRebalance) {
      this.checkAndRebalance(type);
    }

    return totalStored;
  }

  /**
   * Retrieve resource from the best available container
   */
  public retrieveResourceOptimal(type: ResourceType, amount: number): number {
    if (amount <= 0) {
      return 0;
    }

    // Get all containers that store this resource type
    const availableContainers = this.getContainersByResourceType(type);

    if (availableContainers.length === 0) {
      return 0;
    }

    // Calculate scores for each container based on the allocation strategy
    // For retrieval, we want to prioritize containers with higher fill percentage
    const containerScores = this.calculateContainerScores(availableContainers, type, true);

    // Sort containers by score (highest first)
    const sortedContainers = [...containerScores].sort((a, b) => b.score - a.score);

    let remainingAmount = amount;
    let totalRetrieved = 0;

    // Try to retrieve from containers by score order
    for (const { containerId, score: _ } of sortedContainers) {
      if (remainingAmount <= 0) {
        break;
      }

      const amountRetrieved = this.retrieveResource(containerId, type, remainingAmount);
      totalRetrieved += amountRetrieved;
      remainingAmount -= amountRetrieved;
    }

    return totalRetrieved;
  }

  /**
   * Calculate container scores for allocation
   */
  private calculateContainerScores(
    containers: StorageContainerState[],
    resourceType: ResourceType,
    forRetrieval = false
  ): Array<{ containerId: string; score: number }> {
    const { containerPriority, resourcePriority, fillPercentage } = this.config.priorityWeights;
    const resourcePriorityValue = this.resourcePriorities.get(resourceType) || 5;

    return containers.map(container => {
      const resourceState = container.resources.get(resourceType)!;
      const fillRatio = resourceState.current / resourceState.max;

      // For storage, we prefer containers with lower fill percentage
      // For retrieval, we prefer containers with higher fill percentage
      const fillScore = forRetrieval ? fillRatio : 1 - fillRatio;

      // Calculate weighted score
      const score =
        container.config.priority * containerPriority +
        resourcePriorityValue * resourcePriority +
        fillScore * fillPercentage;

      return {
        containerId: container.config.id,
        score,
      };
    });
  }

  /**
   * Handle overflow based on policy
   */
  private handleOverflow(type: ResourceType, amount: number): number {
    switch (this.config.overflowPolicy) {
      case 'redistribute':
        return this.redistributeOverflow(type, amount);

      case 'convert':
        return this.convertOverflow(type, amount);

      case 'discard':
        // Just discard the overflow
        return 0;

      default:
        return 0;
    }
  }

  /**
   * Redistribute overflow by expanding container capacity
   */
  private redistributeOverflow(type: ResourceType, amount: number): number {
    // Find containers that can store this resource type
    const relevantContainers = this.getContainersByResourceType(type);

    if (relevantContainers.length === 0) {
      return 0;
    }

    // Sort by upgrade potential (containers with lower upgrade level first)
    const upgradableContainers = relevantContainers
      .filter(
        container =>
          container.config.upgradeLevel !== undefined &&
          container.config.maxUpgradeLevel !== undefined &&
          container.config.upgradeLevel < container.config.maxUpgradeLevel
      )
      .sort((a, b) => (a.config.upgradeLevel || 0) - (b.config.upgradeLevel || 0));

    if (upgradableContainers.length === 0) {
      return 0;
    }

    // Upgrade the first container
    const containerToUpgrade = upgradableContainers[0];
    const resourceState = containerToUpgrade.resources.get(type)!;

    // Increase capacity by 20%
    const capacityIncrease = resourceState.max * 0.2;
    resourceState.max += capacityIncrease;

    // Increment upgrade level
    if (containerToUpgrade.config.upgradeLevel !== undefined) {
      containerToUpgrade.config.upgradeLevel += 1;
    }

    // Try to store again
    return this.storeResource(containerToUpgrade.config.id, type, amount);
  }

  /**
   * Convert overflow to another resource type
   */
  private convertOverflow(type: ResourceType, amount: number): number {
    // This is a placeholder for resource conversion logic
    // In a real implementation, this would convert the resource to another type
    // based on conversion rules

    // For now, we'll just return 0 (no storage)
    return 0;
  }

  /**
   * Check if rebalancing is needed and perform it
   */
  private checkAndRebalance(type: ResourceType): void {
    const containers = this.getContainersByResourceType(type);

    if (containers.length <= 1) {
      return;
    }

    // Calculate fill ratios
    const fillRatios = containers.map(container => {
      const resourceState = container.resources.get(type)!;
      return {
        id: container.config.id,
        fillRatio: resourceState.current / resourceState.max,
      };
    });

    // Find min and max fill ratios
    const minFill = Math.min(...fillRatios.map(r => r.fillRatio));
    const maxFill = Math.max(...fillRatios.map(r => r.fillRatio));

    // Check if imbalance exceeds threshold
    if (maxFill - minFill > this.config.rebalanceThreshold) {
      this.rebalanceContainers(type, containers);
    }
  }

  /**
   * Rebalance resources between containers
   */
  private rebalanceContainers(type: ResourceType, containers: StorageContainerState[]): void {
    // Calculate target fill ratio (average)
    let totalCurrent = 0;
    let totalMax = 0;

    for (const container of containers) {
      const resourceState = container.resources.get(type)!;
      totalCurrent += resourceState.current;
      totalMax += resourceState.max;
    }

    const targetFillRatio = totalCurrent / totalMax;

    // Calculate transfers needed
    for (const container of containers) {
      const resourceState = container.resources.get(type)!;
      const currentFillRatio = resourceState.current / resourceState.max;

      if (Math.abs(currentFillRatio - targetFillRatio) < 0.05) {
        // Close enough, skip
        continue;
      }

      const targetAmount = resourceState.max * targetFillRatio;
      const difference = targetAmount - resourceState.current;

      if (difference > 0) {
        // Need to add resources
        // Find container with excess
        const sourceContainer = containers.find(c => {
          const rs = c.resources.get(type)!;
          return rs.current / rs.max > targetFillRatio + 0.05;
        });

        if (sourceContainer) {
          const sourceState = sourceContainer.resources.get(type)!;
          const availableToTransfer = sourceState.current - sourceState.max * targetFillRatio;
          const transferAmount = Math.min(difference, availableToTransfer);

          // Transfer resources
          this.transferBetweenContainers(
            sourceContainer.config.id,
            container.config.id,
            type,
            transferAmount
          );
        }
      }
    }
  }

  /**
   * Transfer resources between containers
   */
  public transferBetweenContainers(
    sourceId: string,
    targetId: string,
    type: ResourceType,
    amount: number
  ): number {
    if (amount <= 0 || sourceId === targetId) {
      return 0;
    }

    // Retrieve from source
    const retrievedAmount = this.retrieveResource(sourceId, type, amount);

    if (retrievedAmount <= 0) {
      return 0;
    }

    // Store in target
    const storedAmount = this.storeResource(targetId, type, retrievedAmount);

    // If not all was stored, return remainder to source
    if (storedAmount < retrievedAmount) {
      const remainder = retrievedAmount - storedAmount;
      this.storeResource(sourceId, type, remainder);
    }

    // Record transfer
    this.recordTransfer({
      type,
      source: sourceId,
      target: targetId,
      amount: storedAmount,
      timestamp: Date.now(),
    });

    return storedAmount;
  }

  /**
   * Record a transfer in history
   */
  private recordTransfer(transfer: ResourceTransfer): void {
    this.transferHistory.push(transfer);

    // Trim history if needed
    if (this.transferHistory.length > this.maxHistorySize) {
      this.transferHistory = this.transferHistory.slice(-this.maxHistorySize);
    }
  }

  /**
   * Get transfer history
   */
  public getTransferHistory(): ResourceTransfer[] {
    return [...this.transferHistory];
  }

  /**
   * Get transfer history for a specific resource type
   */
  public getTransferHistoryByType(type: ResourceType): ResourceTransfer[] {
    return this.transferHistory.filter(transfer => transfer.type === type);
  }

  /**
   * Get total stored amount of a resource type
   */
  public getTotalStored(type: ResourceType): number {
    let total = 0;

    for (const container of this.containers.values()) {
      const resourceState = container.resources.get(type);
      if (resourceState) {
        total += resourceState.current;
      }
    }

    return total;
  }

  /**
   * Get total capacity for a resource type
   */
  public getTotalCapacity(type: ResourceType): number {
    let total = 0;

    for (const container of this.containers.values()) {
      const resourceState = container.resources.get(type);
      if (resourceState) {
        total += resourceState.max;
      }
    }

    return total;
  }

  /**
   * Set resource priority
   */
  public setResourcePriority(type: ResourceType, priority: number): void {
    this.resourcePriorities.set(type, priority);
  }

  /**
   * Get resource priority
   */
  public getResourcePriority(type: ResourceType): number {
    return this.resourcePriorities.get(type) || 5;
  }

  /**
   * Update container configuration
   */
  public updateContainerConfig(id: string, config: Partial<StorageContainerConfig>): boolean {
    const container = this.containers.get(id);
    if (!container) {
      return false;
    }

    // Update config
    container.config = {
      ...container.config,
      ...config,
    };

    return true;
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.containers.clear();
    this.resourcePriorities.clear();
    this.transferHistory = [];
  }
}

import { ModuleEvent, moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import {
  ResourceAlert,
  ResourceState,
  ResourceThreshold,
  ResourceType,
} from '../../types/resources/ResourceTypes';
import { validateResourceThreshold } from '../../utils/resources/resourceValidation';

// Map our custom event types to the standard ModuleEventType
const RESOURCE_UPDATE: ModuleEventType = 'RESOURCE_UPDATED';
const RESOURCE_THRESHOLD_TRIGGERED: ModuleEventType = 'STATUS_CHANGED';
const RESOURCE_THRESHOLD_RESOLVED: ModuleEventType = 'STATUS_CHANGED';
const RESOURCE_PRODUCTION_ADJUST: ModuleEventType = 'RESOURCE_PRODUCTION_REGISTERED';
const RESOURCE_CONSUMPTION_ADJUST: ModuleEventType = 'RESOURCE_CONSUMPTION_REGISTERED';
const RESOURCE_TRANSFER_REQUEST: ModuleEventType = 'RESOURCE_TRANSFERRED';
const NOTIFICATION_CREATE: ModuleEventType = 'STATUS_CHANGED';
const RESOURCE_ALERT_CREATED: ModuleEventType = 'STATUS_CHANGED';
const RESOURCE_ALERT_CLEARED: ModuleEventType = 'STATUS_CHANGED';

// Resource manager module ID and type
const RESOURCE_MANAGER_ID = 'resource-threshold-manager';
const RESOURCE_MANAGER_TYPE: ModuleType = 'resource-manager';

/**
 * Threshold action types
 */
export type ThresholdActionType = 'production' | 'consumption' | 'transfer' | 'notification';

/**
 * Threshold action
 */
export interface ThresholdAction {
  type: ThresholdActionType;
  target: string;
  amount?: number;
  message?: string;
  priority?: number;
}

/**
 * Threshold configuration
 */
export interface ThresholdConfig {
  id: string;
  threshold: ResourceThreshold;
  actions: ThresholdAction[];
  enabled: boolean;
  autoResolve?: boolean;
}

/**
 * Threshold status
 */
export type ThresholdStatus = 'inactive' | 'warning' | 'critical' | 'resolved';

/**
 * Threshold state
 */
export interface ThresholdState {
  config: ThresholdConfig;
  status: ThresholdStatus;
  lastTriggered?: number;
  lastResolved?: number;
  actionsTaken: number;
}

/**
 * Resource Threshold Manager
 * Manages resource thresholds and triggers actions when thresholds are crossed
 */
export class ResourceThresholdManager {
  private thresholds: Map<string, ThresholdConfig>;
  private thresholdStates: Map<string, ThresholdState>;
  private activeAlerts: Map<string, ResourceAlert>;
  private resourceStates: Map<ResourceType, ResourceState>;
  private checkInterval: number;
  private intervalId: NodeJS.Timeout | null;
  private lastCheck: number;

  constructor(checkInterval = 1000) {
    this.thresholds = new Map();
    this.thresholdStates = new Map();
    this.activeAlerts = new Map();
    this.resourceStates = new Map();
    this.checkInterval = checkInterval;
    this.intervalId = null;
    this.lastCheck = Date.now();

    // Register event listeners
    moduleEventBus.subscribe(RESOURCE_UPDATE, this.handleResourceUpdate);
  }

  /**
   * Start threshold monitoring
   */
  public start(): void {
    if (this.intervalId) {
      return;
    }

    this.intervalId = setInterval(() => {
      this.checkThresholds();
    }, this.checkInterval);
  }

  /**
   * Stop threshold monitoring
   */
  public stop(): void {
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = null;
    }
  }

  /**
   * Register a threshold configuration
   */
  public registerThreshold(config: ThresholdConfig): boolean {
    if (!config.id || !validateResourceThreshold(config.threshold)) {
      console.error('Invalid threshold configuration:', config);
      return false;
    }

    this.thresholds.set(config.id, config);
    this.thresholdStates.set(config.id, {
      config,
      status: 'inactive',
      actionsTaken: 0,
    });

    return true;
  }

  /**
   * Unregister a threshold configuration
   */
  public unregisterThreshold(id: string): boolean {
    if (!this.thresholds.has(id)) {
      return false;
    }

    this.thresholds.delete(id);
    this.thresholdStates.delete(id);
    return true;
  }

  /**
   * Enable a threshold
   */
  public enableThreshold(id: string): boolean {
    const config = this.thresholds.get(id);
    if (!config) {
      return false;
    }

    config.enabled = true;
    return true;
  }

  /**
   * Disable a threshold
   */
  public disableThreshold(id: string): boolean {
    const config = this.thresholds.get(id);
    if (!config) {
      return false;
    }

    config.enabled = false;
    return true;
  }

  /**
   * Update resource state
   */
  private handleResourceUpdate = (event: ModuleEvent): void => {
    if (event.data && event.data.type && event.data.state) {
      this.resourceStates.set(event.data.type, event.data.state);
    }
  };

  /**
   * Check all thresholds
   */
  private checkThresholds(): void {
    const now = Date.now();
    const deltaTime = now - this.lastCheck;
    this.lastCheck = now;

    // Convert Map entries to array to avoid MapIterator error
    const thresholdEntries = Array.from(this.thresholds.entries());

    for (let i = 0; i < thresholdEntries.length; i++) {
      const [id, config] = thresholdEntries[i];

      if (!config.enabled) {
        continue;
      }

      const state = this.thresholdStates.get(id);
      if (!state) {
        continue;
      }

      const resourceState = this.resourceStates.get(config.threshold.type);
      if (!resourceState) {
        continue;
      }

      const isTriggered = this.isThresholdTriggered(config.threshold, resourceState);

      if (isTriggered && state.status === 'inactive') {
        // Threshold triggered
        state.status = this.getThresholdSeverity(config.threshold, resourceState);
        state.lastTriggered = now;
        this.executeThresholdActions(config, state.status);
        state.actionsTaken++;

        // Create alert
        this.createAlert(config, state.status);

        // Emit event
        moduleEventBus.emit({
          type: RESOURCE_THRESHOLD_TRIGGERED,
          moduleId: RESOURCE_MANAGER_ID,
          moduleType: RESOURCE_MANAGER_TYPE,
          timestamp: now,
          data: {
            id,
            type: config.threshold.type,
            status: state.status,
            timestamp: now,
            severity: state.status === 'critical' ? 'high' : 'medium',
          },
        });
      } else if (!isTriggered && state.status !== 'inactive') {
        // Threshold resolved
        state.status = 'resolved';
        state.lastResolved = now;

        // Clear alert if auto-resolve is enabled
        if (config.autoResolve) {
          this.clearAlert(id);
        }

        // Emit event
        moduleEventBus.emit({
          type: RESOURCE_THRESHOLD_RESOLVED,
          moduleId: RESOURCE_MANAGER_ID,
          moduleType: RESOURCE_MANAGER_TYPE,
          timestamp: now,
          data: {
            id,
            type: config.threshold.type,
            timestamp: now,
            severity: 'info',
          },
        });

        // Reset to inactive after a short delay
        setTimeout(() => {
          if (state.status === 'resolved') {
            state.status = 'inactive';
          }
        }, 5000);
      }
    }
  }

  /**
   * Check if a threshold is triggered
   */
  private isThresholdTriggered(threshold: ResourceThreshold, state: ResourceState): boolean {
    if (threshold.min !== undefined && state.current < threshold.min) {
      return true;
    }

    if (threshold.max !== undefined && state.current > threshold.max) {
      return true;
    }

    if (threshold.target !== undefined) {
      const deviation = Math.abs(state.current - threshold.target);
      const maxDeviation = threshold.target * 0.1; // 10% deviation

      if (deviation > maxDeviation) {
        return true;
      }
    }

    return false;
  }

  /**
   * Get threshold severity based on how far the value is from the threshold
   */
  private getThresholdSeverity(
    threshold: ResourceThreshold,
    state: ResourceState
  ): ThresholdStatus {
    if (threshold.min !== undefined && state.current < threshold.min) {
      const ratio = state.current / threshold.min;

      if (ratio < 0.5) {
        return 'critical';
      } else {
        return 'warning';
      }
    }

    if (threshold.max !== undefined && state.current > threshold.max) {
      const ratio = state.current / threshold.max;

      if (ratio > 1.5) {
        return 'critical';
      } else {
        return 'warning';
      }
    }

    if (threshold.target !== undefined) {
      const deviation = Math.abs(state.current - threshold.target);
      const deviationRatio = deviation / threshold.target;

      if (deviationRatio > 0.25) {
        return 'critical';
      } else {
        return 'warning';
      }
    }

    return 'warning';
  }

  /**
   * Execute threshold actions
   */
  private executeThresholdActions(config: ThresholdConfig, status: ThresholdStatus): void {
    const now = Date.now();

    for (const action of config.actions) {
      switch (action.type) {
        case 'production':
          moduleEventBus.emit({
            type: RESOURCE_PRODUCTION_ADJUST,
            moduleId: RESOURCE_MANAGER_ID,
            moduleType: RESOURCE_MANAGER_TYPE,
            timestamp: now,
            data: {
              type: config.threshold.type,
              target: action.target,
              amount: action.amount || 0,
              priority: action.priority || 1,
              severity: 'info',
            },
          });
          break;

        case 'consumption':
          moduleEventBus.emit({
            type: RESOURCE_CONSUMPTION_ADJUST,
            moduleId: RESOURCE_MANAGER_ID,
            moduleType: RESOURCE_MANAGER_TYPE,
            timestamp: now,
            data: {
              type: config.threshold.type,
              target: action.target,
              amount: action.amount || 0,
              priority: action.priority || 1,
              severity: 'info',
            },
          });
          break;

        case 'transfer':
          moduleEventBus.emit({
            type: RESOURCE_TRANSFER_REQUEST,
            moduleId: RESOURCE_MANAGER_ID,
            moduleType: RESOURCE_MANAGER_TYPE,
            timestamp: now,
            data: {
              type: config.threshold.type,
              target: action.target,
              amount: action.amount || 0,
              priority: action.priority || 1,
              severity: 'info',
            },
          });
          break;

        case 'notification':
          moduleEventBus.emit({
            type: NOTIFICATION_CREATE,
            moduleId: RESOURCE_MANAGER_ID,
            moduleType: RESOURCE_MANAGER_TYPE,
            timestamp: now,
            data: {
              type: 'resource',
              message: action.message || `Resource ${config.threshold.type} threshold triggered`,
              severity: status === 'critical' ? 'high' : 'medium',
              timestamp: now,
            },
          });
          break;
      }
    }
  }

  /**
   * Create a resource alert
   */
  private createAlert(config: ThresholdConfig, status: ThresholdStatus): void {
    const now = Date.now();
    const alert: ResourceAlert = {
      id: config.id,
      type: config.threshold.type,
      threshold: config.threshold,
      message: `Resource ${config.threshold.type} threshold triggered`,
      severity: status === 'critical' ? 'critical' : 'medium',
      autoResolve: config.autoResolve,
      actions: config.actions
        .filter(action => action.type !== 'notification') // Filter out notification actions
        .map(action => ({
          type: action.type as 'production' | 'consumption' | 'transfer',
          target: action.target,
          amount: action.amount || 0,
        })),
    };

    this.activeAlerts.set(config.id, alert);

    moduleEventBus.emit({
      type: RESOURCE_ALERT_CREATED,
      moduleId: RESOURCE_MANAGER_ID,
      moduleType: RESOURCE_MANAGER_TYPE,
      timestamp: now,
      data: {
        ...alert,
        severity: status === 'critical' ? 'high' : 'medium',
      },
    });
  }

  /**
   * Clear a resource alert
   */
  private clearAlert(id: string): void {
    const now = Date.now();
    const alert = this.activeAlerts.get(id);
    if (!alert) {
      return;
    }

    this.activeAlerts.delete(id);

    moduleEventBus.emit({
      type: RESOURCE_ALERT_CLEARED,
      moduleId: RESOURCE_MANAGER_ID,
      moduleType: RESOURCE_MANAGER_TYPE,
      timestamp: now,
      data: {
        id,
        type: alert.type,
        timestamp: now,
        severity: 'info',
      },
    });
  }

  /**
   * Get all active alerts
   */
  public getActiveAlerts(): ResourceAlert[] {
    return Array.from(this.activeAlerts.values());
  }

  /**
   * Get all threshold configurations
   */
  public getThresholdConfigs(): ThresholdConfig[] {
    return Array.from(this.thresholds.values());
  }

  /**
   * Get all threshold states
   */
  public getThresholdStates(): ThresholdState[] {
    return Array.from(this.thresholdStates.values());
  }

  /**
   * Get a specific threshold state
   */
  public getThresholdState(id: string): ThresholdState | undefined {
    return this.thresholdStates.get(id);
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.stop();
    // Use the unsubscribe function returned by subscribe
    moduleEventBus.subscribe(RESOURCE_UPDATE, this.handleResourceUpdate)();
    this.thresholds.clear();
    this.thresholdStates.clear();
    this.activeAlerts.clear();
    this.resourceStates.clear();
  }
}

