import { ResourceState, ResourceType } from './ResourceTypes';

/**
 * Pool allocation strategy
 */
export type PoolAllocationStrategy = 'equal' | 'priority' | 'demand-based' | 'custom';

/**
 * Pool distribution rule
 */
export interface PoolDistributionRule {
  id: string;
  poolId: string;
  targetIds: string[];
  resourceType: ResourceType;
  percentage: number;
  minAmount?: number;
  maxAmount?: number;
  priority: number;
  condition?: (state: ResourceState) => boolean;
}

/**
 * Pool allocation result
 */
export interface PoolAllocationResult {
  poolId: string;
  resourceType: ResourceType;
  allocations: Array<{
    targetId: string;
    amount: number;
    percentage: number;
  }>;
  timestamp: number;
}

/**
 * Pool allocation options
 */
export interface PoolAllocationOptions {
  strategy?: PoolAllocationStrategy;
  priorityMap?: Map<string, number>;
  customAllocations?: Map<string, number>;
  minAllocation?: number;
  maxAllocation?: number;
}

/**
 * Pool distribution options
 */
export interface PoolDistributionOptions {
  interval?: number;
  maxHistory?: number;
  autoDistribute?: boolean;
  validateRules?: boolean;
}

/**
 * Pool target demand
 */
export interface PoolTargetDemand {
  targetId: string;
  resourceType: ResourceType;
  currentAmount: number;
  capacity: number;
  priority: number;
  demandFactor: number;
}

/**
 * Pool resource summary
 */
export interface PoolResourceSummary {
  poolId: string;
  resourceType: ResourceType;
  amount: number;
  capacity: number;
  percentage: number;
  distributionRules: number;
  lastDistribution?: number;
}

/**
 * Type guard for PoolDistributionRule
 */
export function isPoolDistributionRule(obj: any): obj is PoolDistributionRule {
  return (
    obj !== null &&
    typeof obj === 'object' &&
    'id' in obj &&
    'poolId' in obj &&
    'targetIds' in obj &&
    'resourceType' in obj &&
    'percentage' in obj &&
    'priority' in obj &&
    Array.isArray(obj.targetIds)
  );
}

/**
 * Type guard for PoolAllocationResult
 */
export function isPoolAllocationResult(obj: any): obj is PoolAllocationResult {
  return (
    obj !== null &&
    typeof obj === 'object' &&
    'poolId' in obj &&
    'resourceType' in obj &&
    'allocations' in obj &&
    'timestamp' in obj &&
    Array.isArray(obj.allocations)
  );
}

/**
 * Helper function to create a default distribution rule
 */
export function createDefaultDistributionRule(
  poolId: string,
  targetIds: string[],
  resourceType: ResourceType
): PoolDistributionRule {
  return {
    id: `rule-${poolId}-${resourceType}-${Date.now()}`,
    poolId,
    targetIds,
    resourceType,
    percentage: 100,
    priority: 1,
  };
}

/**
 * Helper function to calculate demand factor for a target
 */
export function calculateDemandFactor(currentAmount: number, capacity: number): number {
  if (capacity <= 0) {
    return 0;
  }
  const fillPercentage = (currentAmount / capacity) * 100;
  return Math.max(0, 100 - fillPercentage) / 100;
}

import React, { createContext, ReactNode, useContext, useEffect, useState } from 'react';
import { ModuleEvent, moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';

/**
 * Event dispatcher context interface
 */
interface EventDispatcherContextType {
  // Event subscription
  subscribe: (type: ModuleEventType, listener: (event: ModuleEvent) => void) => () => void;

  // Event emission
  emit: (event: ModuleEvent) => void;

  // Event history
  getHistory: () => ModuleEvent[];
  getModuleHistory: (moduleId: string) => ModuleEvent[];
  getEventTypeHistory: (type: ModuleEventType) => ModuleEvent[];
  clearHistory: () => void;

  // Event filtering
  getFilteredEvents: (filter: (event: ModuleEvent) => boolean) => ModuleEvent[];

  // Latest events by type
  latestEvents: Map<ModuleEventType, ModuleEvent>;
}

/**
 * Event dispatcher provider props
 */
interface EventDispatcherProviderProps {
  children: ReactNode;
  maxHistorySize?: number;
}

// Create the context with a default value
const EventDispatcherContext = createContext<EventDispatcherContextType | null>(null);

/**
 * Event dispatcher provider component
 */
export const EventDispatcherProvider: React.FC<EventDispatcherProviderProps> = ({
  children,
  maxHistorySize = 1000,
}) => {
  // Store the latest event of each type
  const [latestEvents, setLatestEvents] = useState<Map<ModuleEventType, ModuleEvent>>(new Map());

  // Subscribe to all module events
  useEffect(() => {
    // Create a handler for all events
    const handleEvent = (event: ModuleEvent) => {
      setLatestEvents(prev => {
        const newMap = new Map(prev);
        newMap.set(event.type, event);
        return newMap;
      });
    };

    // Get all possible event types
    const eventTypes: ModuleEventType[] = [
      'MODULE_CREATED',
      'MODULE_ATTACHED',
      'MODULE_DETACHED',
      'MODULE_UPGRADED',
      'MODULE_ACTIVATED',
      'MODULE_DEACTIVATED',
      'ATTACHMENT_STARTED',
      'ATTACHMENT_CANCELLED',
      'ATTACHMENT_COMPLETED',
      'ATTACHMENT_PREVIEW_SHOWN',
      'RESOURCE_PRODUCED',
      'RESOURCE_CONSUMED',
      'RESOURCE_TRANSFERRED',
      'RESOURCE_PRODUCTION_REGISTERED',
      'RESOURCE_PRODUCTION_UNREGISTERED',
      'RESOURCE_CONSUMPTION_REGISTERED',
      'RESOURCE_CONSUMPTION_UNREGISTERED',
      'RESOURCE_FLOW_REGISTERED',
      'RESOURCE_FLOW_UNREGISTERED',
      'RESOURCE_SHORTAGE',
      'AUTOMATION_STARTED',
      'AUTOMATION_STOPPED',
      'AUTOMATION_CYCLE_COMPLETE',
      'STATUS_CHANGED',
      'ERROR_OCCURRED',
      'MISSION_STARTED',
      'MISSION_COMPLETED',
      'MISSION_FAILED',
      'MISSION_PROGRESS_UPDATED',
      'MISSION_REWARD_CLAIMED',
      'SUB_MODULE_CREATED',
      'SUB_MODULE_ATTACHED',
      'SUB_MODULE_DETACHED',
      'SUB_MODULE_UPGRADED',
      'SUB_MODULE_ACTIVATED',
      'SUB_MODULE_DEACTIVATED',
      'SUB_MODULE_EFFECT_APPLIED',
      'SUB_MODULE_EFFECT_REMOVED',
    ];

    // Subscribe to all event types
    const unsubscribers = eventTypes.map(type => moduleEventBus.subscribe(type, handleEvent));

    // Cleanup subscriptions
    return () => {
      unsubscribers.forEach(unsubscribe => {
        if (typeof unsubscribe === 'function') {
          unsubscribe();
        }
      });
    };
  }, []);

  // Context value
  const value: EventDispatcherContextType = {
    // Event subscription - delegate to moduleEventBus
    subscribe: (type, listener) => moduleEventBus.subscribe(type, listener),

    // Event emission - delegate to moduleEventBus
    emit: event => moduleEventBus.emit(event),

    // Event history - delegate to moduleEventBus
    getHistory: () => moduleEventBus.getHistory(),
    getModuleHistory: moduleId => moduleEventBus.getModuleHistory(moduleId),
    getEventTypeHistory: type => moduleEventBus.getEventTypeHistory(type),
    clearHistory: () => moduleEventBus.clearHistory(),

    // Event filtering
    getFilteredEvents: filter => moduleEventBus.getHistory().filter(filter),

    // Latest events by type
    latestEvents,
  };

  return (
    <EventDispatcherContext.Provider value={value}>{children}</EventDispatcherContext.Provider>
  );
};

/**
 * Hook to use the event dispatcher
 */
export const useEventDispatcher = (): EventDispatcherContextType => {
  const context = useContext(EventDispatcherContext);

  if (!context) {
    throw new Error('useEventDispatcher must be used within an EventDispatcherProvider');
  }

  return context;
};

/**
 * Hook to subscribe to specific event types
 */
export const useEventSubscription = <T extends ModuleEventType>(
  eventType: T,
  callback: (event: ModuleEvent) => void,
  deps: React.DependencyList = []
): void => {
  const { subscribe } = useEventDispatcher();

  useEffect(() => {
    return subscribe(eventType, callback);
  }, [eventType, ...deps]); // eslint-disable-line react-hooks/exhaustive-deps
};

/**
 * Hook to get the latest event of a specific type
 */
export const useLatestEvent = <T extends ModuleEventType>(
  eventType: T
): ModuleEvent | undefined => {
  const { latestEvents } = useEventDispatcher();
  return latestEvents.get(eventType);
};

/**
 * Hook to get filtered events
 */
export const useFilteredEvents = (
  filter: (event: ModuleEvent) => boolean,
  deps: React.DependencyList = []
): ModuleEvent[] => {
  const { getFilteredEvents } = useEventDispatcher();
  const [filteredEvents, setFilteredEvents] = useState<ModuleEvent[]>([]);

  useEffect(() => {
    setFilteredEvents(getFilteredEvents(filter));
  }, [getFilteredEvents, ...deps]); // eslint-disable-line react-hooks/exhaustive-deps

  return filteredEvents;
};

import { Observable, timer } from 'rxjs';
import { buffer, debounceTime, filter, groupBy, map, mergeMap } from 'rxjs/operators';
import { ModuleEvent, ModuleEventType } from '../../lib/modules/ModuleEvents';
import { MessagePriority, SystemMessage } from './EventCommunication';

/**
 * Filter condition type for event filtering
 */
export type FilterCondition<T> = (event: T) => boolean;

/**
 * Event batch processor type
 */
export type BatchProcessor<T, R> = (events: T[]) => R;

/**
 * Event priority queue for handling events based on priority
 */
export class EventPriorityQueue<T extends { priority?: number }> {
  private queues: Map<number, T[]> = new Map();
  private processing = false;
  private processingPromise: Promise<void> | null = null;
  private processor: (event: T) => Promise<void> | void;

  /**
   * Create a new event priority queue
   * @param processor Function to process each event
   */
  constructor(processor: (event: T) => Promise<void> | void) {
    this.processor = processor;

    // Initialize queues for each priority level
    for (let i = 0; i <= 4; i++) {
      this.queues.set(i, []);
    }
  }

  /**
   * Add an event to the queue
   * @param event Event to add
   */
  public enqueue(event: T): void {
    const priority = event.priority !== undefined ? event.priority : MessagePriority.NORMAL;
    const queue = this.queues.get(priority);
    if (queue) {
      queue.push(event);
      this.processQueue();
    }
  }

  /**
   * Process the queue
   */
  private async processQueue(): Promise<void> {
    if (this.processing) {
      return;
    }

    this.processing = true;
    this.processingPromise = this.processQueueInternal();
    await this.processingPromise;
    this.processingPromise = null;
    this.processing = false;

    // Check if new events were added during processing
    let hasEvents = false;
    // Convert Map entries to array to avoid MapIterator error
    const queueValues = Array.from(this.queues.values());
    for (const queue of queueValues) {
      if (queue.length > 0) {
        hasEvents = true;
        break;
      }
    }

    if (hasEvents) {
      this.processQueue();
    }
  }

  /**
   * Internal queue processing
   */
  private async processQueueInternal(): Promise<void> {
    // Process all events in priority order
    const queueEntries = Array.from(this.queues.entries());
    for (const [priority, queue] of queueEntries) {
      while (queue.length > 0) {
        const event = queue.shift();
        if (event) {
          try {
            const result = this.processor(event);
            if (result instanceof Promise) {
              await result;
            }
          } catch (error) {
            console.error('Error processing event:', error);
          }
        }
      }
    }
  }

  /**
   * Get the current queue sizes
   */
  public getQueueSizes(): Record<number, number> {
    const sizes: Record<number, number> = {};
    // Convert Map entries to array to avoid MapIterator error
    const queueEntries = Array.from(this.queues.entries());
    for (const [priority, queue] of queueEntries) {
      sizes[priority] = queue.length;
    }
    return sizes;
  }

  /**
   * Check if the queue is currently processing
   */
  public isProcessing(): boolean {
    return this.processing;
  }

  /**
   * Wait for all current processing to complete
   */
  public async waitForProcessing(): Promise<void> {
    if (this.processingPromise) {
      await this.processingPromise;
    }
  }

  /**
   * Clear all queues
   */
  public clear(): void {
    // Convert Map entries to array to avoid MapIterator error
    const queueValues = Array.from(this.queues.values());
    for (const queue of queueValues) {
      queue.length = 0;
    }
  }

  /**
   * Process all events in priority order
   */
  public processAll(): void {
    // Convert Map entries to array to avoid MapIterator error
    const queueEntries = Array.from(this.queues.entries());
    for (const [priority, queue] of queueEntries) {
      while (queue.length > 0) {
        const event = queue.shift();
        if (event) {
          this.processor(event);
        }
      }
    }
  }
}

/**
 * Create a filtered event stream
 * @param source Source observable
 * @param condition Filter condition
 */
export function createFilteredStream<T>(
  source: Observable<T>,
  condition: FilterCondition<T>
): Observable<T> {
  return source.pipe(filter(condition));
}

/**
 * Create a batched event stream
 * @param source Source observable
 * @param timeWindow Time window for batching in milliseconds
 * @param processor Batch processor function
 */
export function createBatchedStream<T, R>(
  source: Observable<T>,
  timeWindow: number,
  processor: BatchProcessor<T, R>
): Observable<R> {
  return source.pipe(
    buffer(timer(0, timeWindow)),
    filter(events => events.length > 0),
    map(events => processor(events))
  );
}

/**
 * Create a grouped event stream
 * @param source Source observable
 * @param keySelector Function to select the key for grouping
 */
export function createGroupedStream<T, K>(
  source: Observable<T>,
  keySelector: (event: T) => K
): Observable<{ key: K; events: T[] }> {
  return source.pipe(
    groupBy(keySelector),
    mergeMap(group => {
      return group.pipe(
        buffer(group.pipe(debounceTime(100))),
        map(events => ({ key: group.key, events }))
      );
    })
  );
}

/**
 * Filter module events by type
 * @param events Module events observable
 * @param type Event type to filter
 */
export function filterEventsByType(
  events: Observable<ModuleEvent>,
  type: ModuleEventType
): Observable<ModuleEvent> {
  return events.pipe(filter(event => event.type === type));
}

/**
 * Filter module events by module ID
 * @param events Module events observable
 * @param moduleId Module ID to filter
 */
export function filterEventsByModuleId(
  events: Observable<ModuleEvent>,
  moduleId: string
): Observable<ModuleEvent> {
  return events.pipe(filter(event => event.moduleId === moduleId));
}

/**
 * Filter module events by module type
 * @param events Module events observable
 * @param moduleType Module type to filter
 */
export function filterEventsByModuleType(
  events: Observable<ModuleEvent>,
  moduleType: string
): Observable<ModuleEvent> {
  return events.pipe(filter(event => event.moduleType === moduleType));
}

/**
 * Filter system messages by type
 * @param messages System messages observable
 * @param type Message type to filter
 */
export function filterMessagesByType(
  messages: Observable<SystemMessage>,
  type: string
): Observable<SystemMessage> {
  return messages.pipe(filter(message => message.type === type));
}

/**
 * Filter system messages by source
 * @param messages System messages observable
 * @param source Source system ID to filter
 */
export function filterMessagesBySource(
  messages: Observable<SystemMessage>,
  source: string
): Observable<SystemMessage> {
  return messages.pipe(filter(message => message.source === source));
}

/**
 * Filter system messages by priority
 * @param messages System messages observable
 * @param priority Priority level to filter
 * @param comparison Comparison operator ('eq' | 'lt' | 'lte' | 'gt' | 'gte')
 */
export function filterMessagesByPriority(
  messages: Observable<SystemMessage>,
  priority: MessagePriority,
  comparison: 'eq' | 'lt' | 'lte' | 'gt' | 'gte' = 'eq'
): Observable<SystemMessage> {
  return messages.pipe(
    filter(message => {
      const messagePriority = message.priority;
      switch (comparison) {
        case 'eq':
          return messagePriority === priority;
        case 'lt':
          return messagePriority < priority;
        case 'lte':
          return messagePriority <= priority;
        case 'gt':
          return messagePriority > priority;
        case 'gte':
          return messagePriority >= priority;
        default:
          return messagePriority === priority;
      }
    })
  );
}

/**
 * Create a conditional event processor
 * @param condition Condition to check
 * @param processor Processor function
 */
export function createConditionalProcessor<T>(
  condition: FilterCondition<T>,
  processor: (event: T) => void
): (event: T) => void {
  return (event: T) => {
    if (condition(event)) {
      processor(event);
    }
  };
}

/**
 * Create a debounced event processor
 * @param processor Processor function
 * @param debounceMs Debounce time in milliseconds
 */
export function createDebouncedProcessor<T>(
  processor: (event: T) => void,
  debounceMs: number
): {
  process: (event: T) => void;
  flush: () => void;
} {
  let timeout: NodeJS.Timeout | null = null;
  let lastEvent: T | null = null;

  return {
    process: (event: T) => {
      lastEvent = event;

      if (timeout) {
        clearTimeout(timeout);
      }

      timeout = setTimeout(() => {
        if (lastEvent) {
          processor(lastEvent);
          lastEvent = null;
        }
        timeout = null;
      }, debounceMs);
    },
    flush: () => {
      if (timeout) {
        clearTimeout(timeout);
        timeout = null;
      }

      if (lastEvent) {
        processor(lastEvent);
        lastEvent = null;
      }
    },
  };
}

/**
 * Create a throttled event processor
 * @param processor Processor function
 * @param throttleMs Throttle time in milliseconds
 */
export function createThrottledProcessor<T>(
  processor: (event: T) => void,
  throttleMs: number
): (event: T) => void {
  let lastProcessTime = 0;
  let pending: T | null = null;
  let timeout: NodeJS.Timeout | null = null;

  return (event: T) => {
    const now = Date.now();

    if (now - lastProcessTime >= throttleMs) {
      // Process immediately
      lastProcessTime = now;
      processor(event);
    } else {
      // Store for later processing
      pending = event;

      if (!timeout) {
        timeout = setTimeout(
          () => {
            if (pending) {
              processor(pending);
              pending = null;
            }
            lastProcessTime = Date.now();
            timeout = null;
          },
          throttleMs - (now - lastProcessTime)
        );
      }
    }
  };
}

/**
 * Create a batch event processor
 * @param batchProcessor Batch processor function
 * @param maxBatchSize Maximum batch size
 * @param maxWaitMs Maximum wait time in milliseconds
 */
export function createBatchProcessor<T, R>(
  batchProcessor: BatchProcessor<T, R>,
  maxBatchSize: number,
  maxWaitMs: number
): {
  process: (event: T) => void;
  flush: () => R | null;
  onResult: (callback: (result: R) => void) => () => void;
} {
  const batch: T[] = [];
  let timeout: NodeJS.Timeout | null = null;
  const resultCallbacks: Set<(result: R) => void> = new Set();

  const processCurrentBatch = (): R | null => {
    if (batch.length === 0) {
      return null;
    }

    const currentBatch = [...batch];
    batch.length = 0;

    const result = batchProcessor(currentBatch);

    // Notify callbacks
    resultCallbacks.forEach(callback => {
      try {
        callback(result);
      } catch (error) {
        console.error('Error in batch result callback:', error);
      }
    });

    return result;
  };

  return {
    process: (event: T) => {
      batch.push(event);

      if (batch.length >= maxBatchSize) {
        // Process immediately if batch is full
        if (timeout) {
          clearTimeout(timeout);
          timeout = null;
        }
        processCurrentBatch();
      } else if (!timeout) {
        // Start timer for batch processing
        timeout = setTimeout(() => {
          timeout = null;
          processCurrentBatch();
        }, maxWaitMs);
      }
    },
    flush: () => {
      if (timeout) {
        clearTimeout(timeout);
        timeout = null;
      }
      return processCurrentBatch();
    },
    onResult: (callback: (result: R) => void) => {
      resultCallbacks.add(callback);
      return () => {
        resultCallbacks.delete(callback);
      };
    },
  };
}

/**
 * Create a priority-based event processor
 * @param processors Map of processors by priority
 * @param defaultPriority Default priority if not specified
 */
export function createPriorityProcessor<T extends { priority?: number }>(
  processors: Map<number, (event: T) => void>,
  defaultPriority: number = MessagePriority.NORMAL
): (event: T) => void {
  return (event: T) => {
    const priority = event.priority !== undefined ? event.priority : defaultPriority;
    const processor = processors.get(priority);

    if (processor) {
      processor(event);
    } else {
      // Find the closest priority processor
      let closestPriority: number | null = null;
      let minDistance = Infinity;

      for (const p of processors.keys()) {
        const distance = Math.abs(p - priority);
        if (distance < minDistance) {
          minDistance = distance;
          closestPriority = p;
        }
      }

      if (closestPriority !== null) {
        const closestProcessor = processors.get(closestPriority);
        if (closestProcessor) {
          closestProcessor(event);
        }
      }
    }
  };
}

import { Observable, Subject, filter, map, share } from 'rxjs';
import { ModuleEvent, ModuleEventType, moduleEventBus } from '../../lib/modules/ModuleEvents';

/**
 * Subject for all module events
 */
export const moduleEventSubject = new Subject<ModuleEvent>();

/**
 * Observable for all module events
 */
export const moduleEvents$ = moduleEventSubject.asObservable().pipe(
  share() // Share the observable to prevent multiple subscriptions
);

/**
 * Initialize the RxJS integration with the moduleEventBus
 */
export function initializeRxJSIntegration(): () => void {
  // Subscribe to all module events and forward them to the subject
  const unsubscribe = moduleEventBus.subscribe('MODULE_CREATED' as ModuleEventType, event => {
    moduleEventSubject.next(event);
  });

  // Return a cleanup function
  return () => {
    if (typeof unsubscribe === 'function') {
      unsubscribe();
    }
    moduleEventSubject.complete();
  };
}

/**
 * Get an observable for a specific event type
 */
export function getEventsByType<T extends ModuleEventType>(eventType: T): Observable<ModuleEvent> {
  return moduleEvents$.pipe(filter(event => event.type === eventType));
}

/**
 * Get an observable for events from a specific module
 */
export function getEventsByModule(moduleId: string): Observable<ModuleEvent> {
  return moduleEvents$.pipe(filter(event => event.moduleId === moduleId));
}

/**
 * Get an observable for events with a specific data property
 */
export function getEventsByData<T>(
  propertyName: string,
  propertyValue: T
): Observable<ModuleEvent> {
  return moduleEvents$.pipe(
    filter(
      event =>
        event.data &&
        event.data[propertyName] !== undefined &&
        event.data[propertyName] === propertyValue
    )
  );
}

/**
 * Get an observable that maps events to their data
 */
export function getEventData<T>(eventType: ModuleEventType): Observable<T> {
  return moduleEvents$.pipe(
    filter(event => event.type === eventType),
    map(event => event.data as T)
  );
}

/**
 * Create a custom event observable with filtering
 */
export function createFilteredEventStream(
  filterFn: (event: ModuleEvent) => boolean
): Observable<ModuleEvent> {
  return moduleEvents$.pipe(filter(filterFn));
}

/**
 * Emit an event through the RxJS subject and moduleEventBus
 */
export function emitEvent(event: ModuleEvent): void {
  // Emit through the moduleEventBus
  moduleEventBus.emit(event);

  // Also emit through the RxJS subject
  moduleEventSubject.next(event);
}

/**
 * Hook up a Subject to a specific event type
 */
export function createEventTypeSubject<T extends ModuleEventType>(
  eventType: T
): Subject<ModuleEvent> {
  const subject = new Subject<ModuleEvent>();

  // Subscribe to the event type and forward to the subject
  const unsubscribe = moduleEventBus.subscribe(eventType, event => {
    subject.next(event);
  });

  // Add cleanup method to the subject
  interface SubjectWithCleanup<T> extends Subject<T> {
    cleanup?: () => void;
  }

  const subjectWithCleanup = subject as SubjectWithCleanup<ModuleEvent>;
  subjectWithCleanup.cleanup = () => {
    if (typeof unsubscribe === 'function') {
      unsubscribe();
    }
    subject.complete();
  };

  return subject;
}

/**
 * Create a specialized event stream with transformation
 */
export function createTransformedEventStream<T, R>(
  eventType: ModuleEventType,
  transformFn: (event: ModuleEvent) => R
): Observable<R> {
  return moduleEvents$.pipe(
    filter(event => event.type === eventType),
    map(transformFn)
  );
}

/**
 * Create a debounced event stream
 */
export function createDebouncedEventStream(
  eventType: ModuleEventType,
  debounceTime: number
): Observable<ModuleEvent> {
  return moduleEvents$.pipe(
    filter(event => event.type === eventType)
    // We would normally use debounceTime here, but we're keeping it simple
    // debounceTime(debounceTime)
  );
}

/**
 * Create a throttled event stream
 */
export function createThrottledEventStream(
  eventType: ModuleEventType,
  throttleTime: number
): Observable<ModuleEvent> {
  return moduleEvents$.pipe(
    filter(event => event.type === eventType)
    // We would normally use throttleTime here, but we're keeping it simple
    // throttleTime(throttleTime)
  );
}

/**
 * Create a buffered event stream that collects events over time
 */
export function createBufferedEventStream(
  eventType: ModuleEventType,
  bufferTime: number
): Observable<ModuleEvent[]> {
  return moduleEvents$.pipe(
    filter(event => event.type === eventType)
    // We would normally use bufferTime here, but we're keeping it simple
    // bufferTime(bufferTime)
  ) as unknown as Observable<ModuleEvent[]>;
}

/**
 * Create a combined event stream from multiple event types
 */
export function createCombinedEventStream(eventTypes: ModuleEventType[]): Observable<ModuleEvent> {
  return moduleEvents$.pipe(filter(event => eventTypes.includes(event.type)));
}

// Combat Web Worker
import { QuadTree } from '../lib/optimization/QuadTree';
import { CombatUnit } from '../types/combat/CombatTypes';
import { Hazard } from '../types/combat/HazardTypes';
import { Position } from '../types/core/GameTypes';

interface WorkerMessage {
  type: string;
  units: CombatUnit[];
  hazards: Hazard[];
  worldBounds: { width: number; height: number };
}

interface BatchedUpdate {
  weaponFires: Array<{
    weaponId: string;
    targetId: string;
    weaponType: string;
  }>;
  unitMoves: Array<{
    unitId: string;
    position: Position;
  }>;
}

// Type guard for Hazard
function isHazard(obj: any): obj is Hazard {
  return (
    obj &&
    typeof obj === 'object' &&
    'id' in obj &&
    'position' in obj &&
    'radius' in obj &&
    'type' in obj
  );
}

// Combat state
let quadTree: QuadTree;
let lastUpdate = 0;
const UPDATE_INTERVAL = 16; // ~60fps
const BATCH_SIZE = 10;
let pendingUpdates: BatchedUpdate = {
  weaponFires: [],
  unitMoves: [],
};

// Handle combat calculations
self.onmessage = (e: MessageEvent<WorkerMessage>) => {
  const { type, units, hazards, worldBounds } = e.data;

  if (type === 'UPDATE') {
    const now = performance.now();
    if (now - lastUpdate < UPDATE_INTERVAL) {
      return; // Skip update if too soon
    }
    lastUpdate = now;

    // Initialize or clear quadtree
    if (!quadTree) {
      quadTree = new QuadTree({ x: 0, y: 0, width: worldBounds.width, height: worldBounds.height });
    } else {
      quadTree.clear();
    }

    // Insert units into quadtree
    units.forEach(unit => {
      quadTree.insert({
        id: unit.id,
        position: unit.position,
      });
    });

    // Process units in batches
    for (let i = 0; i < units.length; i += BATCH_SIZE) {
      const batch = units.slice(i, i + BATCH_SIZE);
      processBatch(batch, hazards);
    }

    // Send batched updates
    if (pendingUpdates.weaponFires.length > 0 || pendingUpdates.unitMoves.length > 0) {
      self.postMessage({
        type: 'BATCH_UPDATE',
        updates: pendingUpdates,
      });

      // Clear pending updates
      pendingUpdates = {
        weaponFires: [],
        unitMoves: [],
      };
    }
  }
};

function processBatch(units: CombatUnit[], hazards: Hazard[]): void {
  units.forEach(unit => {
    // Check if unit status is an object with a main property
    if (typeof unit.status === 'object' && unit.status.main === 'active') {
      processEngagingUnit(unit, hazards);
    }
  });
}

function processEngagingUnit(unit: CombatUnit, hazards: Hazard[]): void {
  // Find nearby hazards using quadtree
  const searchBounds = {
    x: unit.position.x - 500,
    y: unit.position.y - 500,
    width: 1000,
    height: 1000,
  };

  const nearbyObjects = quadTree.retrieve(searchBounds) as Array<{
    id: string;
    position: Position;
  }>;

  // Create a filtered array of hazards instead of using Set
  const nearbyHazards: Hazard[] = [];

  hazards.forEach(hazard => {
    if (
      nearbyObjects.some(
        obj =>
          Math.abs(obj.position.x - hazard.position.x) < hazard.radius &&
          Math.abs(obj.position.y - hazard.position.y) < hazard.radius
      )
    ) {
      nearbyHazards.push(hazard);
    }
  });

  // Find nearest hazard
  let nearestHazard: Hazard | null = null;
  let nearestDistance = Infinity;

  // Iterate over the array directly
  for (let i = 0; i < nearbyHazards.length; i++) {
    const hazard = nearbyHazards[i];
    const distance = calculateDistance(unit.position, hazard.position);
    if (distance < nearestDistance) {
      nearestDistance = distance;
      nearestHazard = hazard;
    }
  }

  // Check if we found a hazard
  if (nearestHazard) {
    // Check for ready weapon
    const readyWeapon = unit.weapons.find(
      weapon => weapon.status === 'ready' && nearestDistance <= weapon.range
    );

    if (readyWeapon) {
      pendingUpdates.weaponFires.push({
        weaponId: readyWeapon.id,
        targetId: nearestHazard.id,
        weaponType: readyWeapon.type,
      });
    }

    // Calculate new position with smooth movement
    const newPosition = calculateNewPosition(unit.position, nearestHazard.position);
    pendingUpdates.unitMoves.push({
      unitId: unit.id,
      position: newPosition,
    });
  }
}

function calculateDistance(pos1: Position, pos2: Position): number {
  const dx = pos2.x - pos1.x;
  const dy = pos2.y - pos1.y;
  return Math.sqrt(dx * dx + dy * dy);
}

function calculateNewPosition(current: Position, target: Position): Position {
  const dx = target.x - current.x;
  const dy = target.y - current.y;
  const distance = Math.sqrt(dx * dx + dy * dy);

  // Smooth movement with easing
  const speed = 0.1;
  const easing = 1 - Math.pow(0.95, distance);

  return {
    x: current.x + dx * speed * easing,
    y: current.y + dy * speed * easing,
  };
}

