export function SprawlView({ empire }: { empire: EmpireDetails }) {
  const [view, setView] = useState<ViewState>({
    zoom: 1,
    focusedAsset: null,
    position: { x: 0, y: 0 },
    activeOverlays: ['trade', 'resources'],
    searchQuery: '',
    notifications: [],
    dayNightCycle: 0,
  });

  const [assets, setAssets] = useState<Asset[]>([
    {
      id: 'alpha-colony',
      type: 'colony',
      name: 'Alpha Prime',
      position: { x: 0, y: 0 },
      population: 10000,
      resources: ['Iron', 'Titanium'],
      tradeConnections: [
        { target: 'beta-station', volume: 0.8 },
        { target: 'gamma-sphere', volume: 0.5 },
      ],
      status: 'active',
      developmentLevel: 0.8,
      lastUpdate: Date.now(),
    },
    {
      id: 'beta-station',
      type: 'starStation',
      name: 'Beta Station',
      position: { x: 150, y: -100 },
      population: 5000,
      resources: ['Helium-3'],
      tradeConnections: [{ target: 'alpha-colony', volume: 0.8 }],
      status: 'active',
      developmentLevel: 0.6,
      lastUpdate: Date.now(),
    },
    {
      id: 'gamma-sphere',
      type: 'dysonSphere',
      name: 'Gamma Sphere',
      position: { x: -120, y: 80 },
      tradeConnections: [
        { target: 'alpha-colony', volume: 0.5 },
        { target: 'delta-hub', volume: 0.3 },
      ],
      status: 'constructing',
      developmentLevel: 0.4,
      lastUpdate: Date.now(),
    },
    {
      id: 'delta-hub',
      type: 'tradeHub',
      name: 'Delta Trading Post',
      position: { x: 80, y: 120 },
      population: 2000,
      tradeConnections: [{ target: 'gamma-sphere', volume: 0.3 }],
      status: 'active',
      developmentLevel: 0.3,
      lastUpdate: Date.now(),
    },
  ]);

  // Day/Night cycle effect
  useEffect(() => {
    const interval = setInterval(() => {
      setView(prev => ({
        ...prev,
        dayNightCycle: (prev.dayNightCycle + 0.01) % 1,
      }));
    }, 100);
    return () => clearInterval(interval);
  }, []);

  // Simulate real-time updates
  useEffect(() => {
    const interval = setInterval(() => {
      setAssets(currentAssets => {
        return currentAssets.map(asset => {
          if (asset.status === 'active' && Math.random() > 0.8) {
            const populationChange = Math.floor(Math.random() * 100) - 30;
            const newPopulation = asset.population ? asset.population + populationChange : 0;

            if (populationChange > 0) {
              addNotification(
                'success',
                `${asset.name}: Population increased by ${populationChange}`,
                asset.id
              );
            }

            return {
              ...asset,
              population: Math.max(0, newPopulation),
              lastUpdate: Date.now(),
            };
          }
          return asset;
        });
      });
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const addNotification = (
    type: 'info' | 'warning' | 'success',
    message: string,
    assetId: string
  ) => {
    const notification = {
      id: `${assetId}-${Date.now()}`,
      type,
      message,
      timestamp: Date.now(),
    };

    setView(prev => ({
      ...prev,
      notifications: [...prev.notifications, notification],
    }));

    // Remove notification after 5 seconds
    setTimeout(() => {
      setView(prev => ({
        ...prev,
        notifications: prev.notifications.filter(n => n.id !== notification.id),
      }));
    }, 5000);
  };

  const containerRef = useRef<HTMLDivElement>(null);
  const isDragging = useRef(false);
  const lastPosition = useRef({ x: 0, y: 0 });

  const handleMouseDown = (e: React.MouseEvent) => {
    isDragging.current = true;
    lastPosition.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isDragging.current) {
      return;
    }

    const deltaX = e.clientX - lastPosition.current.x;
    const deltaY = e.clientY - lastPosition.current.y;

    setView(prev => ({
      ...prev,
      position: {
        x: prev.position.x + deltaX,
        y: prev.position.y + deltaY,
      },
    }));

    lastPosition.current = { x: e.clientX, y: e.clientY };
  };

  const handleMouseUp = () => {
    isDragging.current = false;
  };

  const handleZoom = (delta: number) => {
    setView(prev => ({
      ...prev,
      zoom: Math.max(0.5, Math.min(2, prev.zoom + delta)),
    }));
  };

  const focusAsset = (assetName: string) => {
    setView(prev => ({
      ...prev,
      focusedAsset: assetName,
      zoom: 1.5,
    }));
  };

  const getAssetIcon = (type: Asset['type']) => {
    switch (type) {
      case 'colony':
        return Crown;
      case 'dysonSphere':
        return Star;
      case 'starStation':
        return Rocket;
      case 'tradeHub':
        return Database;
      default:
        return Crown;
    }
  };

  const getAssetColor = (asset: Asset) => {
    if (asset.status === 'constructing') {
      return 'yellow';
    }
    if (asset.status === 'damaged') {
      return 'red';
    }
    switch (asset.type) {
      case 'colony':
        return 'cyan';
      case 'dysonSphere':
        return 'orange';
      case 'starStation':
        return 'purple';
      case 'tradeHub':
        return 'emerald';
      default:
        return 'blue';
    }
  };

