import React, { createContext, ReactNode, useContext, useEffect, useReducer } from 'react';
import { useGame } from '../contexts/GameContext';
import { moduleManager } from '../managers/module/ModuleManager';
import { BaseModule, ModularBuilding, ModuleType } from '../types/buildings/ModuleTypes';
import { Position } from '../types/core/GameTypes';

// State interface
interface ModuleState {
  activeModules: BaseModule[];
  buildings: ModularBuilding[];
  selectedModuleId?: string;
  selectedBuildingId?: string;
}

// Action types
type ModuleAction =
  | { type: 'CREATE_MODULE'; moduleType: ModuleType; position: Position }
  | { type: 'ATTACH_MODULE'; moduleId: string; buildingId: string; attachmentPointId: string }
  | { type: 'UPGRADE_MODULE'; moduleId: string }
  | { type: 'SET_MODULE_ACTIVE'; moduleId: string; active: boolean }
  | { type: 'SELECT_MODULE'; moduleId: string }
  | { type: 'SELECT_BUILDING'; buildingId: string }
  | { type: 'REGISTER_BUILDING'; building: ModularBuilding }
  | { type: 'UPDATE_ACTIVE_MODULES'; modules: BaseModule[] };

// Initial state
const initialState: ModuleState = {
  activeModules: [],
  buildings: [],
  selectedModuleId: undefined,
  selectedBuildingId: undefined,
};

// Reducer
function moduleReducer(state: ModuleState, action: ModuleAction): ModuleState {
  switch (action.type) {
    case 'CREATE_MODULE': {
      const module = moduleManager.createModule(action.moduleType, action.position);
      return {
        ...state,
        activeModules: moduleManager.getActiveModules(),
      };
    }

    case 'ATTACH_MODULE': {
      moduleManager.attachModule(action.moduleId, action.buildingId, action.attachmentPointId);
      return {
        ...state,
        buildings: Array.from(state.buildings),
      };
    }

    case 'UPGRADE_MODULE': {
      moduleManager.upgradeModule(action.moduleId);
      return {
        ...state,
        activeModules: moduleManager.getActiveModules(),
      };
    }

    case 'SET_MODULE_ACTIVE': {
      moduleManager.setModuleActive(action.moduleId, action.active);
      return {
        ...state,
        activeModules: moduleManager.getActiveModules(),
      };
    }

    case 'SELECT_MODULE':
      return {
        ...state,
        selectedModuleId: action.moduleId,
      };

    case 'SELECT_BUILDING':
      return {
        ...state,
        selectedBuildingId: action.buildingId,
      };

    case 'REGISTER_BUILDING': {
      moduleManager.registerBuilding(action.building);
      return {
        ...state,
        buildings: [...state.buildings, action.building],
      };
    }

    case 'UPDATE_ACTIVE_MODULES':
      return {
        ...state,
        activeModules: action.modules,
      };

    default:
      return state;
  }
}

// Context
interface ModuleContextType {
  state: ModuleState;
  dispatch: React.Dispatch<ModuleAction>;
}

const ModuleContext = createContext<ModuleContextType | undefined>(undefined);

// Provider
interface ModuleProviderProps {
  children: ReactNode;
}

export function ModuleProvider({ children }: ModuleProviderProps) {
  const [state, dispatch] = useReducer(moduleReducer, initialState);

  // Initialize state with default buildings
  useEffect(() => {
    const buildings = moduleManager.getBuildings();
    if (buildings.length > 0) {
      buildings.forEach(building => {
        dispatch({
          type: 'REGISTER_BUILDING',
          building,
        });
      });
    }
  }, []);

  return <ModuleContext.Provider value={{ state, dispatch }}>{children}</ModuleContext.Provider>;
}

// Hook
export function useModules() {
  const context = useContext(ModuleContext);
  if (context === undefined) {
    throw new Error('useModules must be used within a ModuleProvider');
  }
  return context;
}

// Helper hooks
export function useSelectedModule() {
  const { state } = useModules();
  return state.selectedModuleId ? moduleManager.getModule(state.selectedModuleId) : undefined;
}

export function useSelectedBuilding() {
  const { state } = useModules();
  return state.selectedBuildingId ? moduleManager.getBuilding(state.selectedBuildingId) : undefined;
}

export function useModulesByType(type: ModuleType) {
  return moduleManager.getModulesByType(type);
}

export function useBuildingModules(buildingId: string) {
  return moduleManager.getBuildingModules(buildingId);
}

// Helper functions
export function canBuildModule(
  moduleType: ModuleType,
  cost: { minerals?: number; energy?: number }
) {
  const { state } = useGame();
  const { state: moduleState } = useModules();

  // Check resources
  const hasResources =
    (cost.minerals || 0) <= state.resources.minerals &&
    (cost.energy || 0) <= state.resources.energy;

  if (!hasResources) {
    return false;
  }

  // Find a suitable building and attachment point
  for (const building of moduleState.buildings) {
    for (const point of building.attachmentPoints) {
      if (point.allowedTypes.includes(moduleType) && !point.currentModule) {
        return true;
      }
    }
  }

  return false;
}

export function buildModule(moduleType: ModuleType, cost: { minerals?: number; energy?: number }) {
  const { dispatch, state } = useModules();

  // Find a suitable building and attachment point
  let targetBuilding: ModularBuilding | undefined;
  let targetPoint: string | undefined;

  for (const building of state.buildings) {
    for (const point of building.attachmentPoints) {
      if (point.allowedTypes.includes(moduleType) && !point.currentModule) {
        targetBuilding = building;
        targetPoint = point.id;
        break;
      }
    }
    if (targetBuilding) {
      break;
    }
  }

  if (!targetBuilding || !targetPoint) {
    console.error('No suitable attachment point found for module:', moduleType);
    return;
  }

  // Create and attach the module
  const position = targetBuilding.attachmentPoints.find(p => p.id === targetPoint)?.position || {
    x: 0,
    y: 0,
  };

  dispatch({
    type: 'CREATE_MODULE',
    moduleType,
    position,
  });

  // Get the newly created module's ID (it will be the last one created)
  const newModule = moduleManager.getModulesByType(moduleType).pop();
  if (!newModule) {
    console.error('Failed to create module:', moduleType);
    return;
  }

  // Attach the module
  dispatch({
    type: 'ATTACH_MODULE',
    moduleId: newModule.id,
    buildingId: targetBuilding.id,
    attachmentPointId: targetPoint,
  });

  // Activate the module
  dispatch({
    type: 'SET_MODULE_ACTIVE',
    moduleId: newModule.id,
    active: true,
  });
}

import { RenderBatcher } from '../../lib/optimization/RenderBatcher';
import { Position } from '../../types/core/Position';
import { VisualEffect, VisualEffectConfig } from './VisualEffect';

interface EngineTrailConfig extends VisualEffectConfig {
  engineSize: number;
  enginePower: number;
  velocity: Position;
}

/**
 * Visual effect for ship engine trails
 */
export class EngineTrailEffect extends VisualEffect {
  protected override config: EngineTrailConfig;
  private trailPoints: Array<{
    position: Position;
    size: number;
    opacity: number;
    rotation: number;
    time: number;
  }> = [];
  private lastEmitTime: number = 0;
  private emitInterval: number = 50; // ms

  constructor(config: EngineTrailConfig) {
    super(config);
    this.config = config;
  }

  protected getEffectType(): string {
    return 'engine-trail';
  }

  protected onStart(): void {
    this.lastEmitTime = Date.now();
    console.debug(`[EngineTrailEffect] Started with power: ${this.config.enginePower}`);
  }

  protected onUpdate(progress: number): void {
    const now = Date.now();

    // Emit new particles
    if (now - this.lastEmitTime >= this.emitInterval) {
      this.emitParticles();
      this.lastEmitTime = now;
    }

    // Update existing particles
    this.updateParticles();
  }

  protected onComplete(): void {
    this.trailPoints = [];
    console.debug('[EngineTrailEffect] Completed');
  }

  protected onReset(): void {
    this.trailPoints = [];
    this.lastEmitTime = 0;
  }

  protected updateRendering(batcher: RenderBatcher): void {
    if (!this.batchId) {
      return;
    }

    // Render core engine glow
    this.renderEngineCore(batcher);

    // Render trail particles
    this.renderTrail(batcher);
  }

  private emitParticles(): void {
    const baseSize = this.config.engineSize;
    const particleCount = Math.ceil(this.config.enginePower * 3);

    for (let i = 0; i < particleCount; i++) {
      const angle = Math.random() * Math.PI * 2;
      const spread = this.config.engineSize * 0.3;
      const offset = {
        x: Math.cos(angle) * spread,
        y: Math.sin(angle) * spread,
      };

      this.trailPoints.push({
        position: {
          x: this.config.position.x + offset.x,
          y: this.config.position.y + offset.y,
        },
        size: baseSize * (0.5 + Math.random() * 0.5),
        opacity: 0.8 + Math.random() * 0.2,
        rotation: angle,
        time: Date.now(),
      });
    }

    // Limit total particles
    while (this.trailPoints.length > 100) {
      this.trailPoints.shift();
    }
  }

  private updateParticles(): void {
    const now = Date.now();
    const deltaTime = 1 / 60; // Assume 60fps for physics

    this.trailPoints = this.trailPoints.filter(point => {
      // Update position based on velocity
      point.position.x -= this.config.velocity.x * deltaTime;
      point.position.y -= this.config.velocity.y * deltaTime;

      // Add some turbulence
      const age = (now - point.time) / 1000;
      const turbulence = Math.sin(age * 10 + point.rotation) * 2;
      point.position.x += Math.cos(point.rotation) * turbulence * deltaTime;
      point.position.y += Math.sin(point.rotation) * turbulence * deltaTime;

      // Fade out based on age
      point.opacity = Math.max(0, point.opacity - deltaTime);

      // Keep particle if still visible
      return point.opacity > 0;
    });
  }

  private renderEngineCore(batcher: RenderBatcher): void {
    const time = Date.now() / 1000;
    const pulse = Math.sin(time * 10) * 0.2 + 0.8;
    const coreSize = this.config.engineSize * this.config.enginePower;

    // Core glow
    batcher.addItem(this.batchId!, {
      id: `${this.id}-core`,
      position: this.config.position,
      size: { width: coreSize * 2, height: coreSize * 2 },
      rotation: 0,
      opacity: 0.8 * pulse,
      color: this.config.color || '#00ffff',
      shader: 'additive',
    });

    // Inner core
    batcher.addItem(this.batchId!, {
      id: `${this.id}-inner`,
      position: this.config.position,
      size: { width: coreSize, height: coreSize },
      rotation: time * 2,
      opacity: 1,
      color: '#ffffff',
      shader: 'additive',
    });
  }

  private renderTrail(batcher: RenderBatcher): void {
    this.trailPoints.forEach((point, index) => {
      const time = Date.now() / 1000;
      const flicker = Math.sin(time * 20 + index) * 0.2 + 0.8;

      // Trail particle
      batcher.addItem(this.batchId!, {
        id: `${this.id}-trail-${index}`,
        position: point.position,
        size: { width: point.size, height: point.size },
        rotation: point.rotation + time,
        opacity: point.opacity * flicker,
        color: this.config.color || '#00ffff',
        shader: 'additive',
      });

      // Particle glow
      batcher.addItem(this.batchId!, {
        id: `${this.id}-glow-${index}`,
        position: point.position,
        size: { width: point.size * 2, height: point.size * 2 },
        rotation: -point.rotation + time,
        opacity: point.opacity * 0.5 * flicker,
        color: this.config.color || '#00ffff',
        shader: 'additive',
      });
    });
  }
}

import { useEffect, useState } from 'react';
import { Position } from '../../types/core/GameTypes';

interface FormationTransitionEffectProps {
  sourcePositions: Position[];
  targetPositions: Position[];
  duration: number;
  easingFunction: 'linear' | 'easeInOut' | 'easeIn' | 'easeOut';
  quality: 'low' | 'medium' | 'high';
  pattern: 'offensive' | 'defensive' | 'balanced';
  onComplete?: () => void;
}

interface ParticleProps {
  x: number;
  y: number;
  size: number;
  opacity: number;
  color: string;
}

export function FormationTransitionEffect({
  sourcePositions,
  targetPositions,
  duration,
  easingFunction,
  quality,
  pattern,
  onComplete,
}: FormationTransitionEffectProps) {
  const [progress, setProgress] = useState(0);
  const [particles, setParticles] = useState<ParticleProps[]>([]);

  // Quality-based settings
  const particleCount = quality === 'high' ? 16 : quality === 'medium' ? 8 : 4;
  const trailLength = quality === 'high' ? 8 : quality === 'medium' ? 4 : 2;
  const glowIntensity = quality === 'high' ? '12px' : quality === 'medium' ? '8px' : '4px';

  // Pattern-based colors
  const getPatternColor = () => {
    switch (pattern) {
      case 'offensive':
        return 'rgb(239, 68, 68)'; // red-500
      case 'defensive':
        return 'rgb(59, 130, 246)'; // blue-500
      case 'balanced':
        return 'rgb(168, 85, 247)'; // purple-500
    }
  };

  // Easing functions
  const getEasedProgress = (p: number) => {
    switch (easingFunction) {
      case 'easeIn':
        return p * p;
      case 'easeOut':
        return 1 - (1 - p) * (1 - p);
      case 'easeInOut':
        return p < 0.5 ? 2 * p * p : 1 - Math.pow(-2 * p + 2, 2) / 2;
      default:
        return p;
    }
  };

  useEffect(() => {
    const startTime = Date.now();
    const color = getPatternColor();

    const updateFrame = () => {
      const elapsed = Date.now() - startTime;
      const rawProgress = Math.min(1, elapsed / duration);
      const easedProgress = getEasedProgress(rawProgress);
      setProgress(easedProgress);

      // Update particle effects
      const newParticles: ParticleProps[] = [];

      sourcePositions.forEach((source, index) => {
        const target = targetPositions[index];
        if (!target) {
          return;
        }

        // Calculate current position
        const currentX = source.x + (target.x - source.x) * easedProgress;
        const currentY = source.y + (target.y - source.y) * easedProgress;

        // Add trail particles
        for (let i = 0; i < trailLength; i++) {
          const trailProgress = Math.max(0, easedProgress - (i / trailLength) * 0.2);
          const trailX = source.x + (target.x - source.x) * trailProgress;
          const trailY = source.y + (target.y - source.y) * trailProgress;

          newParticles.push({
            x: trailX,
            y: trailY,
            size: 4 * (1 - i / trailLength),
            opacity: 0.3 * (1 - i / trailLength),
            color,
          });
        }
      });

      setParticles(newParticles);

      if (rawProgress < 1) {
        requestAnimationFrame(updateFrame);
      } else {
        onComplete?.();
      }
    };

    const frameId = requestAnimationFrame(updateFrame);
    return () => cancelAnimationFrame(frameId);
  }, [sourcePositions, targetPositions, duration, easingFunction, quality, pattern, onComplete]);

  return (
    <div className="pointer-events-none absolute inset-0">
      {/* Formation Lines */}
      <svg className="absolute inset-0">
        {sourcePositions.map((source, index) => {
          const target = targetPositions[index];
          if (!target) {
            return null;
          }

          const currentX = source.x + (target.x - source.x) * progress;
          const currentY = source.y + (target.y - source.y) * progress;

          return (
            <line
              key={index}
              x1={source.x}
              y1={source.y}
              x2={currentX}
              y2={currentY}
              stroke={getPatternColor()}
              strokeWidth="2"
              strokeDasharray="4 4"
              className="opacity-30"
            />
          );
        })}
      </svg>

      {/* Particles */}
      {particles.map((particle, index) => (
        <div
          key={`particle-${index}`}
          className="absolute rounded-full transition-all duration-100"
          style={{
            left: particle.x,
            top: particle.y,
            width: particle.size,
            height: particle.size,
            backgroundColor: particle.color,
            opacity: particle.opacity,
            transform: 'translate(-50%, -50%)',
            filter: `blur(${glowIntensity})`,
          }}
        />
      ))}
    </div>
  );
}

import { RenderBatcher } from '../../lib/optimization/RenderBatcher';
import { Position } from '../../types/core/Position';
import { VisualEffect, VisualEffectConfig } from './VisualEffect';

interface ShieldImpactConfig extends VisualEffectConfig {
  radius: number;
  damage: number;
  shieldStrength: number;
}

/**
 * Visual effect for shield impacts
 */
export class ShieldImpactEffect extends VisualEffect {
  protected override config: ShieldImpactConfig;
  private ripplePoints: Position[] = [];
  private hexagonPoints: Position[] = [];
  private crackPoints: Position[] = [];

  constructor(config: ShieldImpactConfig) {
    super(config);
    this.config = config;
  }

  protected getEffectType(): string {
    return 'shield-impact';
  }

  protected onStart(): void {
    // Create initial points
    this.createRipplePoints();
    this.createHexagonPoints();
    this.createCrackPoints();

    // Debug logging
    console.debug(`[ShieldImpactEffect] Started effect with damage: ${this.config.damage}`);
  }

  protected onUpdate(progress: number): void {
    // No additional update needed as points are transformed during rendering
  }

  protected onComplete(): void {
    this.ripplePoints = [];
    this.hexagonPoints = [];
    this.crackPoints = [];

    console.debug('[ShieldImpactEffect] Completed effect');
  }

  protected onReset(): void {
    this.ripplePoints = [];
    this.hexagonPoints = [];
    this.crackPoints = [];
  }

  protected updateRendering(batcher: RenderBatcher): void {
    if (!this.batchId) {
      return;
    }

    // Render shield components
    this.renderRipple(batcher);
    this.renderHexGrid(batcher);
    this.renderCracks(batcher);
  }

  private createRipplePoints(): void {
    const segments = 32;
    for (let i = 0; i < segments; i++) {
      const angle = (i / segments) * Math.PI * 2;
      this.ripplePoints.push({
        x: Math.cos(angle) * this.config.radius,
        y: Math.sin(angle) * this.config.radius,
      });
    }
  }

  private createHexagonPoints(): void {
    // Create hexagonal grid pattern
    const hexRadius = 20;
    const rows = Math.ceil(this.config.radius / (hexRadius * 1.5));
    const cols = Math.ceil(this.config.radius / (hexRadius * Math.sqrt(3)));

    for (let row = -rows; row <= rows; row++) {
      for (let col = -cols; col <= cols; col++) {
        const x = col * hexRadius * Math.sqrt(3);
        const y = row * hexRadius * 1.5 + (col % 2) * hexRadius * 0.75;

        // Only add points within shield radius
        if (Math.sqrt(x * x + y * y) <= this.config.radius) {
          this.hexagonPoints.push({ x, y });
        }
      }
    }
  }

  private createCrackPoints(): void {
    if (this.config.damage <= 0) {
      return;
    }

    // Create crack pattern based on damage
    const crackCount = Math.ceil((this.config.damage / this.config.shieldStrength) * 5);
    const angleStep = (Math.PI * 2) / crackCount;

    for (let i = 0; i < crackCount; i++) {
      const baseAngle = angleStep * i;
      let currentPoint = {
        x: Math.cos(baseAngle) * this.config.radius * 0.2,
        y: Math.sin(baseAngle) * this.config.radius * 0.2,
      };

      // Create branching cracks
      for (let j = 0; j < 5; j++) {
        const angle = baseAngle + ((Math.random() - 0.5) * Math.PI) / 4;
        const length = Math.random() * this.config.radius * 0.3;

        const endPoint = {
          x: currentPoint.x + Math.cos(angle) * length,
          y: currentPoint.y + Math.sin(angle) * length,
        };

        this.crackPoints.push(currentPoint, endPoint);
        currentPoint = endPoint;
      }
    }
  }

  private renderRipple(batcher: RenderBatcher): void {
    const time = Date.now() / 1000;
    const baseRadius = this.config.radius;
    const rippleStrength = Math.max(0, 1 - this.progress * 2);

    // Render expanding ripple
    this.ripplePoints.forEach((point, index) => {
      const angle = (index / this.ripplePoints.length) * Math.PI * 2;
      const rippleOffset = Math.sin(time * 5 + angle * 3) * 10 * rippleStrength;
      const radius = baseRadius + rippleOffset;

      const position = {
        x: this.config.position.x + Math.cos(angle) * radius,
        y: this.config.position.y + Math.sin(angle) * radius,
      };

      batcher.addItem(this.batchId!, {
        id: `${this.id}-ripple-${index}`,
        position,
        size: { width: 4, height: 4 },
        rotation: angle,
        opacity: rippleStrength * 0.5,
        color: this.config.color || '#00ffff',
        shader: 'additive',
      });
    });
  }

  private renderHexGrid(batcher: RenderBatcher): void {
    const time = Date.now() / 1000;
    const hexOpacity = Math.max(0, 1 - this.progress * 3);

    this.hexagonPoints.forEach((point, index) => {
      const distance = Math.sqrt(point.x * point.x + point.y * point.y);
      const distanceRatio = distance / this.config.radius;
      const pulse = Math.sin(time * 3 + distanceRatio * 5) * 0.3 + 0.7;

      batcher.addItem(this.batchId!, {
        id: `${this.id}-hex-${index}`,
        position: {
          x: this.config.position.x + point.x,
          y: this.config.position.y + point.y,
        },
        size: { width: 30, height: 30 },
        rotation: time + index,
        opacity: hexOpacity * pulse * (1 - distanceRatio),
        color: this.config.color || '#00ffff',
        shader: 'additive',
      });
    });
  }

  private renderCracks(batcher: RenderBatcher): void {
    if (this.crackPoints.length === 0) {
      return;
    }

    const crackOpacity = Math.max(0, 1 - this.progress * 4);
    const time = Date.now() / 1000;

    for (let i = 0; i < this.crackPoints.length; i += 2) {
      const start = this.crackPoints[i];
      const end = this.crackPoints[i + 1];

      const centerX = (start.x + end.x) / 2;
      const centerY = (start.y + end.y) / 2;
      const length = Math.sqrt(Math.pow(end.x - start.x, 2) + Math.pow(end.y - start.y, 2));
      const angle = Math.atan2(end.y - start.y, end.x - start.x);

      // Render crack line
      batcher.addItem(this.batchId!, {
        id: `${this.id}-crack-${i}`,
        position: {
          x: this.config.position.x + centerX,
          y: this.config.position.y + centerY,
        },
        size: { width: length, height: 2 },
        rotation: angle,
        opacity: crackOpacity * (0.8 + Math.sin(time * 10 + i) * 0.2),
        color: this.config.color || '#00ffff',
        shader: 'additive',
      });

      // Render glow
      batcher.addItem(this.batchId!, {
        id: `${this.id}-crack-glow-${i}`,
        position: {
          x: this.config.position.x + centerX,
          y: this.config.position.y + centerY,
        },
        size: { width: length, height: 6 },
        rotation: angle,
        opacity: crackOpacity * 0.5 * (0.8 + Math.sin(time * 10 + i) * 0.2),
        color: this.config.color || '#00ffff',
        shader: 'additive',
      });
    }
  }
}

import { RenderBatcher } from '../../lib/optimization/RenderBatcher';
import { Position } from '../../types/core/Position';

export interface VisualEffectConfig {
  id?: string;
  position: Position;
  color?: string;
  duration?: number;
}

export abstract class VisualEffect {
  protected id: string;
  protected config: VisualEffectConfig;
  protected progress: number = 0;
  protected batchId: string | null = null;
  protected startTime: number;
  protected isComplete: boolean = false;

  constructor(config: VisualEffectConfig) {
    this.id = config.id || Math.random().toString(36).substring(7);
    this.config = config;
    this.startTime = Date.now();
  }

  public start(): void {
    this.onStart();
    console.debug(`[${this.getEffectType()}] Started effect ${this.id}`);
  }

  public update(): void {
    if (this.isComplete) return;

    const elapsed = Date.now() - this.startTime;
    const duration = this.config.duration || 1000;
    this.progress = Math.min(elapsed / duration, 1);

    this.onUpdate(this.progress);

    if (this.progress >= 1) {
      this.complete();
    }
  }

  public render(batcher: RenderBatcher): void {
    if (!this.batchId) {
      this.batchId = batcher.createBatch(this.getEffectType());
    }
    this.updateRendering(batcher);
  }

  public complete(): void {
    if (this.isComplete) return;
    this.isComplete = true;
    this.onComplete();
    console.debug(`[${this.getEffectType()}] Completed effect ${this.id}`);
  }

  public reset(): void {
    this.progress = 0;
    this.startTime = Date.now();
    this.isComplete = false;
    this.onReset();
  }

  public isFinished(): boolean {
    return this.isComplete;
  }

  protected abstract getEffectType(): string;
  protected abstract onStart(): void;
  protected abstract onUpdate(progress: number): void;
  protected abstract updateRendering(batcher: RenderBatcher): void;
  protected abstract onComplete(): void;
  protected abstract onReset(): void;
}

import { useSpring } from '@react-spring/three';
import { Trail } from '@react-three/drei';
import { Canvas, useFrame } from '@react-three/fiber';
import { useRef } from 'react';
import * as THREE from 'three';
import { RenderBatcher } from '../../lib/optimization/RenderBatcher';
import { Position } from '../../types/core/Position';
import { WeaponCategory } from '../../types/weapons/WeaponTypes';
import { VisualEffect, VisualEffectConfig } from './VisualEffect';

// Props and Config Types
interface WeaponEffectProps {
  type:
    | 'machineGun'
    | 'railGun'
    | 'gaussCannon'
    | 'rockets'
    | 'mgss'
    | 'pointDefense'
    | 'plasmaCannon'
    | 'beamWeapon'
    | 'pulseWeapon'
    | 'disruptor'
    | 'ionCannon';
  color: string;
  position: { x: number; y: number };
  rotation: number;
  firing: boolean;
}

interface WeaponEffectConfig extends VisualEffectConfig {
  type: WeaponCategory;
  target?: Position;
  damage?: number;
  impactSize?: number;
  opacity?: number;
  color?: string;
  duration?: number;
}

// React Components
function WeaponBeam({ type, color, firing }: Omit<WeaponEffectProps, 'position' | 'rotation'>) {
  const materialRef = useRef<THREE.ShaderMaterial>(null);
  const timeRef = useRef(0);

  const { intensity } = useSpring({
    intensity: firing ? 1 : 0,
    config: { tension: 280, friction: 60 },
  });

  // Create shader material directly
  const shader = {
    uniforms: {
      time: { value: 0 },
      color: { value: new THREE.Color(color) },
      intensity: { value: 0 },
      weaponType: { value: 0 },
    },
    vertexShader: `
      varying vec2 vUv;
      varying vec3 vPosition;
      varying vec3 vNormal;

      void main() {
        vUv = uv;
        vPosition = position;
        vNormal = normalize(normalMatrix * normal);
        gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
      }
    `,
    fragmentShader: `
      uniform float time;
      uniform vec3 color;
      uniform float intensity;
      uniform int weaponType;

      varying vec2 vUv;
      varying vec3 vPosition;
      varying vec3 vNormal;

      float noise(vec2 p) {
        return fract(sin(dot(p.xy, vec2(12.9898,78.233))) * 43758.5453123);
      }

      float fbm(vec2 p) {
        float v = 0.0;
        float a = 0.5;
        vec2 shift = vec2(100.0);
        mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.5));
        for (int i = 0; i < 5; ++i) {
          v += a * noise(p);
          p = rot * p * 2.0 + shift;
          a *= 0.5;
        }
        return v;
      }

      void main() {
        vec2 uv = vUv;
        float alpha = 0.0;
        vec3 finalColor = color;

        // Edge glow
        float fresnel = pow(1.0 - abs(dot(vNormal, vec3(0.0, 0.0, 1.0))), 3.0);

        if (weaponType == 0) { // Machine Gun
          float bulletPattern = smoothstep(0.48, 0.52, abs(uv.x - 0.5));
          float trailFade = smoothstep(0.0, 1.0, uv.y);
          float energyPulse = sin(uv.y * 30.0 - time * 15.0) * 0.5 + 0.5;
          alpha = (1.0 - bulletPattern) * (1.0 - trailFade) * intensity;
          alpha *= mix(0.8, 1.0, energyPulse);
          finalColor = mix(color, vec3(1.0), fresnel * 0.5);
        }
        else if (weaponType == 1) { // Rail Gun
          float beam = smoothstep(0.45, 0.55, abs(uv.x - 0.5));
          float energyPulse = sin(uv.y * 20.0 - time * 10.0) * 0.5 + 0.5;
          float distortion = fbm(uv * 5.0 + time * 0.5);
          alpha = (1.0 - beam) * (energyPulse + distortion * 0.5) * intensity;
          finalColor = mix(color, vec3(1.0), fresnel * 0.7 + energyPulse * 0.3);
        }
        else if (weaponType == 2) { // Gauss Cannon
          float core = smoothstep(0.4, 0.6, abs(uv.x - 0.5));
          float plasma = fbm(uv * 8.0 + time * 2.0);
          float rings = sin(uv.y * 50.0 + time * 5.0) * 0.5 + 0.5;
          alpha = (1.0 - core) * (plasma + rings * 0.3) * intensity;
          finalColor = mix(color, vec3(1.0), fresnel * 0.8 + plasma * 0.4);
        }
        else if (weaponType == 3) { // Rockets
          float rocketCore = smoothstep(0.45, 0.55, abs(uv.x - 0.5));
          float exhaust = fbm(vec2(uv.x * 5.0, uv.y * 2.0 - time * 3.0));
          float sparkles = noise(uv * 20.0 + time * 4.0);
          alpha = (1.0 - rocketCore) * (exhaust + sparkles * 0.2) * intensity;
          finalColor = mix(color, vec3(1.0), fresnel * 0.6 + exhaust * 0.3);
        }
        else if (weaponType == 4) { // MGSS
          float beamCore = smoothstep(0.47, 0.53, abs(uv.x - 0.5));
          float swirl = sin(uv.y * 40.0 + time * 8.0 + uv.x * 5.0) * 0.5 + 0.5;
          float energyField = fbm(uv * 6.0 + time * 1.5);
          alpha = (1.0 - beamCore) * (swirl + energyField * 0.4) * intensity;
          finalColor = mix(color, vec3(1.0), fresnel * 0.9 + swirl * 0.3);
        }
        else if (weaponType == 5) { // Point Defense
          float pointDefense = smoothstep(0.45, 0.55, abs(uv.x - 0.5));
          float energyPulse = sin(uv.y * 30.0 - time * 15.0) * 0.5 + 0.5;
          alpha = (1.0 - pointDefense) * (energyPulse + 0.5) * intensity;
          finalColor = mix(color, vec3(1.0), fresnel * 0.5);
        }
        else if (weaponType == 6) { // Plasma Cannon
          float plasmaCore = smoothstep(0.45, 0.55, abs(uv.x - 0.5));
          float plasmaField = fbm(uv * 10.0 + time * 3.0);
          float energyRings = sin(uv.y * 60.0 + time * 8.0) * 0.5 + 0.5;
          alpha = (1.0 - plasmaCore) * (plasmaField + energyRings * 0.4) * intensity;
          finalColor = mix(color, vec3(1.0), fresnel * 0.9 + plasmaField * 0.5);
        }
        else if (weaponType == 7) { // Beam Weapon
          float beamCore = smoothstep(0.48, 0.52, abs(uv.x - 0.5));
          float energyField = fbm(uv * 12.0 + time * 4.0);
          float beamPulse = sin(uv.y * 80.0 - time * 20.0) * 0.5 + 0.5;
          alpha = (1.0 - beamCore) * (energyField + beamPulse * 0.6) * intensity;
          finalColor = mix(color, vec3(1.0), fresnel * 0.95 + beamPulse * 0.4);
        }
        else if (weaponType == 8) { // Pulse Weapon
          float pulseCore = smoothstep(0.46, 0.54, abs(uv.x - 0.5));
          float pulseWave = sin(uv.y * 40.0 - time * 12.0) * 0.5 + 0.5;
          float energyField = fbm(uv * 8.0 + time * 2.0);
          alpha = (1.0 - pulseCore) * (pulseWave + energyField * 0.3) * intensity;
          finalColor = mix(color, vec3(1.0), fresnel * 0.8 + pulseWave * 0.5);
        }
        else if (weaponType == 9) { // Disruptor
          float disruptorCore = smoothstep(0.47, 0.53, abs(uv.x - 0.5));
          float disruption = fbm(uv * 15.0 + time * 5.0);
          float chaosField = noise(uv * 25.0 + time * 6.0);
          alpha = (1.0 - disruptorCore) * (disruption + chaosField * 0.4) * intensity;
          finalColor = mix(color, vec3(1.0), fresnel * 0.7 + disruption * 0.6);
        }
        else if (weaponType == 10) { // Ion Cannon
          float ionCore = smoothstep(0.46, 0.54, abs(uv.x - 0.5));
          float ionField = fbm(uv * 10.0 + time * 3.5);
          float ionPulse = sin(uv.y * 70.0 - time * 15.0) * 0.5 + 0.5;
          alpha = (1.0 - ionCore) * (ionField + ionPulse * 0.5) * intensity;
          finalColor = mix(color, vec3(1.0), fresnel * 0.85 + ionPulse * 0.45);
        }

        // Add global glow
        alpha += fresnel * 0.3 * intensity;

        gl_FragColor = vec4(finalColor, alpha);
      }
    `,
  };

  useFrame((_state, delta) => {
    if (materialRef.current) {
      timeRef.current += delta;
      materialRef.current.uniforms.time.value = timeRef.current;
      materialRef.current.uniforms.intensity.value = intensity.get();
      materialRef.current.uniforms.weaponType.value = typeMap[type];
    }
  });

  const typeMap = {
    machineGun: 0,
    railGun: 1,
    gaussCannon: 2,
    rockets: 3,
    mgss: 4,
    pointDefense: 5,
    plasmaCannon: 6,
    beamWeapon: 7,
    pulseWeapon: 8,
    disruptor: 9,
    ionCannon: 10,
  };

  return (
    <mesh>
      <planeGeometry args={[0.2, 1, 32, 32]} />
      <shaderMaterial
        ref={materialRef}
        transparent
        depthWrite={false}
        blending={THREE.AdditiveBlending}
        uniforms={shader.uniforms}
        vertexShader={shader.vertexShader}
        fragmentShader={shader.fragmentShader}
      />
      {type !== 'machineGun' && (
        <Trail width={0.2} length={5} color={color} attenuation={(t: number) => t * t} />
      )}
    </mesh>
  );
}

export function WeaponEffectComponent({
  type,
  color,
  position,
  rotation,
  firing,
}: WeaponEffectProps) {
  return (
    <div
      className="absolute"
      style={{
        left: position.x,
        top: position.y,
        width: '100px',
        height: '200px',
        transform: `rotate(${rotation}deg)`,
      }}
    >
      <Canvas camera={{ position: [0, 0, 2], fov: 75 }} style={{ background: 'transparent' }}>
        <WeaponBeam type={type} color={color} firing={firing} />
      </Canvas>

      {/* Enhanced Glow Effect */}
      {firing && (
        <>
          <div
            className="pointer-events-none absolute inset-0"
            style={{
              background: `radial-gradient(circle at 50% 0%, ${color}66 0%, ${color}00 70%)`,
              filter: 'blur(8px)',
              opacity: 0.8,
              animation: 'pulse 1.5s ease-in-out infinite',
            }}
          />
          <div
            className="pointer-events-none absolute inset-0"
            style={{
              background: `radial-gradient(circle at 50% 0%, ${color}33 0%, ${color}00 100%)`,
              filter: 'blur(16px)',
              opacity: 0.6,
              animation: 'pulse 2s ease-in-out infinite reverse',
            }}
          />
        </>
      )}
    </div>
  );
}

// Visual Effect Class
export class WeaponEffectVisual extends VisualEffect {
  protected override config: WeaponEffectConfig;
  private trailPoints: Position[] = [];
  private impactParticles: Position[] = [];
  private impactStartTime: number = 0;

  constructor(config: WeaponEffectConfig) {
    super(config);
    this.config = config;
  }

  protected getEffectType(): string {
    return `weapon-${this.config.type}`;
  }

  protected onStart(): void {
    // Initialize trail points
    this.trailPoints = [this.config.position];

    // Debug logging
    console.debug(`[WeaponEffect] Started ${this.config.type} effect`);
  }

  protected onUpdate(progress: number): void {
    // Update trail points
    if (this.config.target) {
      const currentPoint = {
        x: this.config.position.x + (this.config.target.x - this.config.position.x) * progress,
        y: this.config.position.y + (this.config.target.y - this.config.position.y) * progress,
      };

      this.trailPoints.push(currentPoint);

      // Keep only recent points
      while (this.trailPoints.length > 10) {
        this.trailPoints.shift();
      }

      // Create impact when projectile reaches target
      if (progress >= 1 && !this.impactStartTime) {
        this.impactStartTime = Date.now();
        this.createImpactParticles();
      }
    }
  }

  protected onComplete(): void {
    this.trailPoints = [];
    this.impactParticles = [];
    this.impactStartTime = 0;

    console.debug(`[WeaponEffect] Completed ${this.config.type} effect`);
  }

  protected onReset(): void {
    this.trailPoints = [];
    this.impactParticles = [];
    this.impactStartTime = 0;
  }

  protected updateRendering(batcher: RenderBatcher): void {
    if (!this.batchId) {
      return;
    }

    // Render trail
    this.renderTrail(batcher);

    // Render impact
    if (this.impactStartTime) {
      this.renderImpact(batcher);
    }
  }

  private renderTrail(batcher: RenderBatcher): void {
    // Different trail rendering based on weapon type
    switch (this.config.type) {
      case 'machineGun':
        this.renderProjectileTrail(batcher);
        break;
      case 'gaussCannon':
      case 'railGun':
        this.renderBeamTrail(batcher);
        break;
      case 'mgss':
        this.renderEnergyTrail(batcher);
        break;
      case 'rockets':
        this.renderRocketTrail(batcher);
        break;
      case 'harmonicCannon':
        this.renderHarmonicTrail(batcher);
        break;
      case 'temporalCannon':
        this.renderTemporalTrail(batcher);
        break;
      case 'quantumCannon':
        this.renderQuantumTrail(batcher);
        break;
    }
  }

  private renderProjectileTrail(batcher: RenderBatcher): void {
    // Simple projectile with small trail
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;
      batcher.addItem(this.batchId!, {
        id: `${this.id}-trail-${index}`,
        position: point,
        size: { width: 4, height: 4 },
        rotation: 0,
        opacity,
        color: this.config.color || '#ffff00',
        shader: 'additive',
      });
    });
  }

  private renderBeamTrail(batcher: RenderBatcher): void {
    // Solid beam with glow
    if (this.trailPoints.length < 2) {
      return;
    }

    const start = this.trailPoints[0];
    const end = this.trailPoints[this.trailPoints.length - 1];
    const angle = Math.atan2(end.y - start.y, end.x - start.x);
    const length = Math.sqrt(Math.pow(end.x - start.x, 2) + Math.pow(end.y - start.y, 2));

    // Core beam
    batcher.addItem(this.batchId!, {
      id: `${this.id}-beam`,
      position: {
        x: (start.x + end.x) / 2,
        y: (start.y + end.y) / 2,
      },
      size: { width: length, height: 4 },
      rotation: angle,
      opacity: this.config.opacity || 1,
      color: this.config.color || '#00ffff',
      shader: 'additive',
    });

    // Glow effect
    batcher.addItem(this.batchId!, {
      id: `${this.id}-glow`,
      position: {
        x: (start.x + end.x) / 2,
        y: (start.y + end.y) / 2,
      },
      size: { width: length, height: 12 },
      rotation: angle,
      opacity: (this.config.opacity || 1) * 0.5,
      color: this.config.color || '#00ffff',
      shader: 'additive',
    });
  }

  private renderEnergyTrail(batcher: RenderBatcher): void {
    // Pulsing energy trail
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;
      const pulse = Math.sin(Date.now() / 100 + index) * 0.3 + 0.7;

      batcher.addItem(this.batchId!, {
        id: `${this.id}-trail-${index}`,
        position: point,
        size: { width: 6, height: 6 },
        rotation: 0,
        opacity: opacity * pulse,
        color: this.config.color || '#ff00ff',
        shader: 'additive',
      });
    });
  }

  private renderRocketTrail(batcher: RenderBatcher): void {
    // Rocket with smoke trail
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;

      // Smoke
      batcher.addItem(this.batchId!, {
        id: `${this.id}-smoke-${index}`,
        position: point,
        size: { width: 8, height: 8 },
        rotation: Math.random() * Math.PI * 2,
        opacity: opacity * 0.3,
        color: '#888888',
        shader: 'normal',
      });

      // Fire
      batcher.addItem(this.batchId!, {
        id: `${this.id}-fire-${index}`,
        position: point,
        size: { width: 6, height: 6 },
        rotation: 0,
        opacity: opacity,
        color: this.config.color || '#ff4400',
        shader: 'additive',
      });
    });
  }

  private renderHarmonicTrail(batcher: RenderBatcher): void {
    // Harmonic wave pattern
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;
      const wave = Math.sin(Date.now() / 200 + index);
      const offset = wave * 10;

      batcher.addItem(this.batchId!, {
        id: `${this.id}-trail-${index}`,
        position: {
          x: point.x + offset,
          y: point.y + offset,
        },
        size: { width: 8, height: 8 },
        rotation: wave * Math.PI,
        opacity: opacity,
        color: this.config.color || '#00ff88',
        shader: 'additive',
      });
    });
  }

  private renderTemporalTrail(batcher: RenderBatcher): void {
    // Time distortion effect
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;
      const time = Date.now() / 1000;
      const distortion = Math.sin(time * 2 + index);

      for (let i = 0; i < 3; i++) {
        const offset = distortion * (i + 1) * 5;
        batcher.addItem(this.batchId!, {
          id: `${this.id}-trail-${index}-${i}`,
          position: {
            x: point.x + offset,
            y: point.y + offset,
          },
          size: { width: 6 - i * 2, height: 6 - i * 2 },
          rotation: time + (i * Math.PI) / 3,
          opacity: opacity * (1 - i * 0.2),
          color: this.config.color || '#8800ff',
          shader: 'additive',
        });
      }
    });
  }

  private renderQuantumTrail(batcher: RenderBatcher): void {
    // Quantum tunneling effect
    this.trailPoints.forEach((point, index) => {
      const opacity = index / this.trailPoints.length;
      const time = Date.now() / 1000;

      // Phase shift effect
      for (let i = 0; i < 4; i++) {
        const phase = (time + (i * Math.PI) / 2) % (Math.PI * 2);
        const shift = Math.sin(phase) * 10;

        batcher.addItem(this.batchId!, {
          id: `${this.id}-trail-${index}-${i}`,
          position: {
            x: point.x + Math.cos(phase) * shift,
            y: point.y + Math.sin(phase) * shift,
          },
          size: { width: 5, height: 5 },
          rotation: phase,
          opacity: opacity * Math.abs(Math.sin(phase)),
          color: this.config.color || '#0088ff',
          shader: 'additive',
        });
      }
    });
  }

  private renderImpact(batcher: RenderBatcher): void {
    const impactProgress = Math.min(
      1,
      (Date.now() - this.impactStartTime) / (this.config.duration || 1000)
    );

    // Update impact particles
    this.impactParticles.forEach((particle, index) => {
      const particleProgress = impactProgress * (1 + index * 0.1);
      if (particleProgress >= 1) {
        return;
      }

      batcher.addItem(this.batchId!, {
        id: `${this.id}-impact-${index}`,
        position: particle,
        size: {
          width: (this.config.impactSize || 20) * (1 - particleProgress),
          height: (this.config.impactSize || 20) * (1 - particleProgress),
        },
        rotation: (index * Math.PI) / 4,
        opacity: 1 - particleProgress,
        color: this.config.color || '#ffffff',
        shader: 'additive',
      });
    });
  }

  private createImpactParticles(): void {
    if (!this.config.target) {
      return;
    }

    // Create particles in a circular pattern
    const particleCount = 8;
    for (let i = 0; i < particleCount; i++) {
      const angle = (i / particleCount) * Math.PI * 2;
      const distance = (this.config.impactSize || 20) / 2;

      this.impactParticles.push({
        x: this.config.target.x + Math.cos(angle) * distance,
        y: this.config.target.y + Math.sin(angle) * distance,
      });
    }
  }
}

import { useCallback, useEffect, useState } from 'react';
import { moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import {
  globalAutomationManager,
  GlobalRoutine,
  GlobalRoutineType,
} from '../../managers/automation/GlobalAutomationManager';
import { AutomationAction, AutomationCondition } from '../../managers/game/AutomationManager';
import { MessagePriority, SystemId } from '../../utils/events/EventCommunication';

/**
 * Hook for using the global automation system
 */
export function useGlobalAutomation() {
  const [routines, setRoutines] = useState<GlobalRoutine[]>([]);
  const [activeRoutines, setActiveRoutines] = useState<GlobalRoutine[]>([]);
  const [isInitialized, setIsInitialized] = useState<boolean>(false);
  const [lastUpdate, setLastUpdate] = useState<number>(Date.now());

  // Initialize and load routines
  useEffect(() => {
    // Initialize the global automation manager if not already initialized
    if (!isInitialized) {
      globalAutomationManager.initialize();
      setIsInitialized(true);
    }

    // Load all routines
    const allRoutines = globalAutomationManager.getAllRoutines();
    setRoutines(allRoutines);

    // Load active routines
    const active = globalAutomationManager.getActiveRoutines();
    setActiveRoutines(active);
  }, [isInitialized]);

  // Subscribe to automation events
  useEffect(() => {
    const handleAutomationEvent = (event: any) => {
      if (
        event.type === 'AUTOMATION_STARTED' ||
        event.type === 'AUTOMATION_STOPPED' ||
        event.type === 'AUTOMATION_CYCLE_COMPLETE'
      ) {
        // Refresh routines
        const allRoutines = globalAutomationManager.getAllRoutines();
        setRoutines(allRoutines);

        // Refresh active routines
        const active = globalAutomationManager.getActiveRoutines();
        setActiveRoutines(active);

        // Update timestamp
        setLastUpdate(Date.now());
      }
    };

    // Subscribe to automation events
    const unsubscribeStarted = moduleEventBus.subscribe(
      'AUTOMATION_STARTED' as ModuleEventType,
      handleAutomationEvent
    );
    const unsubscribeStopped = moduleEventBus.subscribe(
      'AUTOMATION_STOPPED' as ModuleEventType,
      handleAutomationEvent
    );
    const unsubscribeComplete = moduleEventBus.subscribe(
      'AUTOMATION_CYCLE_COMPLETE' as ModuleEventType,
      handleAutomationEvent
    );

    // Cleanup subscriptions
    return () => {
      if (typeof unsubscribeStarted === 'function') {
        unsubscribeStarted();
      }
      if (typeof unsubscribeStopped === 'function') {
        unsubscribeStopped();
      }
      if (typeof unsubscribeComplete === 'function') {
        unsubscribeComplete();
      }
    };
  }, []);

  /**
   * Create a new global routine
   */
  const createRoutine = useCallback(
    (
      name: string,
      type: GlobalRoutineType,
      description: string,
      conditions: AutomationCondition[],
      actions: AutomationAction[],
      systems: SystemId[],
      options?: {
        priority?: MessagePriority;
        interval?: number;
        enabled?: boolean;
        tags?: string[];
      }
    ): string => {
      const routine: GlobalRoutine = {
        id: `routine-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
        name,
        type,
        description,
        conditions,
        actions,
        systems,
        priority: options?.priority || MessagePriority.NORMAL,
        interval: options?.interval || 60000, // Default to 1 minute
        enabled: options?.enabled !== undefined ? options.enabled : true,
        tags: options?.tags || [],
      };

      const routineId = globalAutomationManager.registerRoutine(routine);

      // Refresh routines
      const allRoutines = globalAutomationManager.getAllRoutines();
      setRoutines(allRoutines);

      // Refresh active routines
      const active = globalAutomationManager.getActiveRoutines();
      setActiveRoutines(active);

      return routineId;
    },
    []
  );

  /**
   * Enable a routine
   */
  const enableRoutine = useCallback((routineId: string): boolean => {
    const result = globalAutomationManager.enableRoutine(routineId);

    if (result) {
      // Refresh routines
      const allRoutines = globalAutomationManager.getAllRoutines();
      setRoutines(allRoutines);

      // Refresh active routines
      const active = globalAutomationManager.getActiveRoutines();
      setActiveRoutines(active);
    }

    return result;
  }, []);

  /**
   * Disable a routine
   */
  const disableRoutine = useCallback((routineId: string): boolean => {
    const result = globalAutomationManager.disableRoutine(routineId);

    if (result) {
      // Refresh routines
      const allRoutines = globalAutomationManager.getAllRoutines();
      setRoutines(allRoutines);

      // Refresh active routines
      const active = globalAutomationManager.getActiveRoutines();
      setActiveRoutines(active);
    }

    return result;
  }, []);

  /**
   * Remove a routine
   */
  const removeRoutine = useCallback((routineId: string): boolean => {
    const result = globalAutomationManager.unregisterRoutine(routineId);

    if (result) {
      // Refresh routines
      const allRoutines = globalAutomationManager.getAllRoutines();
      setRoutines(allRoutines);

      // Refresh active routines
      const active = globalAutomationManager.getActiveRoutines();
      setActiveRoutines(active);
    }

    return result;
  }, []);

  /**
   * Get routines by type
   */
  const getRoutinesByType = useCallback((type: GlobalRoutineType): GlobalRoutine[] => {
    return globalAutomationManager.getRoutinesByType(type);
  }, []);

  /**
   * Get routines by system
   */
  const getRoutinesBySystem = useCallback((systemId: SystemId): GlobalRoutine[] => {
    return globalAutomationManager.getRoutinesBySystem(systemId);
  }, []);

  /**
   * Get routines by tag
   */
  const getRoutinesByTag = useCallback((tag: string): GlobalRoutine[] => {
    return globalAutomationManager.getRoutinesByTag(tag);
  }, []);

  /**
   * Create a system maintenance routine
   */
  const createMaintenanceRoutine = useCallback(
    (
      name: string,
      description: string,
      systems: SystemId[],
      actions: AutomationAction[],
      options?: {
        conditions?: AutomationCondition[];
        interval?: number;
        priority?: MessagePriority;
        enabled?: boolean;
        tags?: string[];
      }
    ): string => {
      return createRoutine(
        name,
        'system-maintenance',
        description,
        options?.conditions || [],
        actions,
        systems,
        {
          interval: options?.interval || 3600000, // Default to 1 hour
          priority: options?.priority || MessagePriority.LOW,
          enabled: options?.enabled,
          tags: options?.tags || ['maintenance'],
        }
      );
    },
    [createRoutine]
  );

  /**
   * Create a resource balancing routine
   */
  const createResourceBalancingRoutine = useCallback(
    (
      name: string,
      description: string,
      systems: SystemId[],
      conditions: AutomationCondition[],
      actions: AutomationAction[],
      options?: {
        interval?: number;
        priority?: MessagePriority;
        enabled?: boolean;
        tags?: string[];
      }
    ): string => {
      return createRoutine(name, 'resource-balancing', description, conditions, actions, systems, {
        interval: options?.interval || 300000, // Default to 5 minutes
        priority: options?.priority || MessagePriority.NORMAL,
        enabled: options?.enabled,
        tags: options?.tags || ['resource'],
      });
    },
    [createRoutine]
  );

  /**
   * Create a performance optimization routine
   */
  const createPerformanceOptimizationRoutine = useCallback(
    (
      name: string,
      description: string,
      systems: SystemId[],
      actions: AutomationAction[],
      options?: {
        conditions?: AutomationCondition[];
        interval?: number;
        priority?: MessagePriority;
        enabled?: boolean;
        tags?: string[];
      }
    ): string => {
      return createRoutine(
        name,
        'performance-optimization',
        description,
        options?.conditions || [],
        actions,
        systems,
        {
          interval: options?.interval || 1800000, // Default to 30 minutes
          priority: options?.priority || MessagePriority.LOW,
          enabled: options?.enabled,
          tags: options?.tags || ['performance'],
        }
      );
    },
    [createRoutine]
  );

  /**
   * Create an emergency response routine
   */
  const createEmergencyResponseRoutine = useCallback(
    (
      name: string,
      description: string,
      systems: SystemId[],
      conditions: AutomationCondition[],
      actions: AutomationAction[],
      options?: {
        priority?: MessagePriority;
        enabled?: boolean;
        tags?: string[];
      }
    ): string => {
      return createRoutine(name, 'emergency-response', description, conditions, actions, systems, {
        interval: 0, // Emergency routines are triggered by events, not time
        priority: options?.priority || MessagePriority.CRITICAL,
        enabled: options?.enabled !== undefined ? options.enabled : true,
        tags: options?.tags || ['emergency'],
      });
    },
    [createRoutine]
  );

  /**
   * Create a scheduled task routine
   */
  const createScheduledTaskRoutine = useCallback(
    (
      name: string,
      description: string,
      systems: SystemId[],
      actions: AutomationAction[],
      interval: number,
      options?: {
        conditions?: AutomationCondition[];
        priority?: MessagePriority;
        enabled?: boolean;
        tags?: string[];
      }
    ): string => {
      return createRoutine(
        name,
        'scheduled-task',
        description,
        options?.conditions || [],
        actions,
        systems,
        {
          interval,
          priority: options?.priority || MessagePriority.NORMAL,
          enabled: options?.enabled,
          tags: options?.tags || ['scheduled'],
        }
      );
    },
    [createRoutine]
  );

  return {
    // State
    routines,
    activeRoutines,
    isInitialized,
    lastUpdate,

    // Basic operations
    createRoutine,
    enableRoutine,
    disableRoutine,
    removeRoutine,

    // Queries
    getRoutinesByType,
    getRoutinesBySystem,
    getRoutinesByTag,

    // Specialized routine creators
    createMaintenanceRoutine,
    createResourceBalancingRoutine,
    createPerformanceOptimizationRoutine,
    createEmergencyResponseRoutine,
    createScheduledTaskRoutine,
  };
}

import { useEffect, useState } from 'react';
import { behaviorTreeManager } from '../../managers/ai/BehaviorTreeManager';
import { combatManager } from '../../managers/combat/combatManager';
import { CombatUnit } from '../../types/combat/CombatTypes';
import { FactionId } from '../../types/ships/FactionTypes';
import { convertToCombatTypesUnit } from '../../utils/typeConversions';

interface CombatAIState {
  isActive: boolean;
  currentBehavior: string;
  lastAction: string;
  performance: {
    successRate: number;
    actionsPerMinute: number;
    damageDealt: number;
    damageTaken: number;
  };
}

export function useCombatAI(unitId: string, factionId: FactionId) {
  const [state, setState] = useState<CombatAIState>({
    isActive: false,
    currentBehavior: 'idle',
    lastAction: '',
    performance: {
      successRate: 0,
      actionsPerMinute: 0,
      damageDealt: 0,
      damageTaken: 0,
    },
  });

  useEffect(() => {
    let actionCount = 0;
    let successCount = 0;
    const startTime = Date.now();

    const handleNodeExecuted = ({ nodeId, success }: { nodeId: string; success: boolean }) => {
      successCount += success ? 1 : 0;
      const totalExecutions = successCount + (actionCount - successCount);
      const successRate = totalExecutions > 0 ? successCount / totalExecutions : 0;

      setState(prev => ({
        ...prev,
        performance: {
          ...prev.performance,
          successRate,
        },
      }));
    };

    const handleActionStarted = ({ actionType }: { actionType: string }) => {
      actionCount++;
      const elapsedMinutes = (Date.now() - startTime) / 60000;

      setState(prev => ({
        ...prev,
        lastAction: actionType,
        performance: {
          ...prev.performance,
          actionsPerMinute: actionCount / elapsedMinutes,
        },
      }));
    };

    behaviorTreeManager.on('nodeExecuted', handleNodeExecuted);
    behaviorTreeManager.on('actionStarted', handleActionStarted);

    // Update AI context periodically
    const updateInterval = setInterval(() => {
      const managerUnit = combatManager.getUnitStatus(unitId);
      if (!managerUnit) {
        return;
      }

      // Convert to CombatTypes.CombatUnit
      const unit = convertToCombatTypesUnit(managerUnit);

      const nearbyUnits = combatManager
        .getUnitsInRange(unit.position, 500)
        .map(convertToCombatTypesUnit);
      const nearbyEnemies = nearbyUnits.filter(u => (u as any).faction !== factionId);
      const nearbyAllies = nearbyUnits.filter(u => (u as any).faction === factionId);

      // Calculate fleet strength and threat level
      const fleetStrength = calculateFleetStrength(unit, nearbyAllies);
      const threatLevel = calculateThreatLevel(nearbyEnemies);

      // Update behavior tree context
      behaviorTreeManager.updateContext(unitId, {
        unit,
        factionId,
        fleetStrength,
        threatLevel,
        nearbyEnemies,
        nearbyAllies,
        currentFormation: (unit as any).formation || {
          type: 'balanced',
          spacing: 100,
          facing: 0,
        },
        cooldowns: {},
      });

      // Evaluate behavior tree
      const treeId = `${factionId}-combat`;
      const success = behaviorTreeManager.evaluateTree(unitId, treeId);

      setState(prev => ({
        ...prev,
        isActive: true,
        currentBehavior: success ? 'executing' : 'idle',
      }));
    }, 250); // Fast updates for combat

    return () => {
      clearInterval(updateInterval);
      behaviorTreeManager.off('nodeExecuted', handleNodeExecuted);
      behaviorTreeManager.off('actionStarted', handleActionStarted);
    };
  }, [unitId, factionId]);

  return state;
}

function calculateFleetStrength(unit: CombatUnit, allies: CombatUnit[]): number {
  const unitStrength =
    (unit.stats.health / unit.stats.maxHealth) * (unit.stats.shield / unit.stats.maxShield);
  const allyStrength = allies.reduce((sum, ally) => {
    return (
      sum + (ally.stats.health / ally.stats.maxHealth) * (ally.stats.shield / ally.stats.maxShield)
    );
  }, 0);

  return (unitStrength + allyStrength) / (1 + allies.length);
}

function calculateThreatLevel(enemies: CombatUnit[]): number {
  if (enemies.length === 0) {
    return 0;
  }

  return (
    enemies.reduce((sum, enemy) => {
      const baseStrength =
        (enemy.stats.health / enemy.stats.maxHealth) * (enemy.stats.shield / enemy.stats.maxShield);
      const weaponStrength = enemy.weapons.reduce((total, w) => total + w.damage, 0) / 100;
      return sum + baseStrength * (1 + weaponStrength);
    }, 0) / enemies.length
  );
}

import { useCallback, useEffect, useState } from 'react';
import { SHIP_STATS as CONFIG_SHIP_STATS } from '../../config/ships';
import { ModuleEvent, moduleEventBus } from '../../lib/modules/ModuleEvents';
import { EventEmitter } from '../../lib/utils/EventEmitter';
import { AsteroidFieldManager } from '../../managers/game/AsteroidFieldManager';
import { CombatUnit, CombatUnitStatus } from '../../types/combat/CombatTypes';
import { Position } from '../../types/core/GameTypes';
import { ResourceType } from '../../types/resources/ResourceTypes';
import { CommonShipStats, ShipStatus as CommonShipStatus } from '../../types/ships/CommonShipTypes';
import { FactionFleet, FactionShip, FactionShipClass } from '../../types/ships/FactionShipTypes';
import { FactionBehaviorType, FactionId, FactionState } from '../../types/ships/FactionTypes';
import {
  WeaponCategory,
  WeaponConfig,
  WeaponInstance,
  WeaponMount,
  WeaponMountPosition,
  WeaponMountSize,
  WeaponSystem,
} from '../../types/weapons/WeaponTypes';
import { getDistance } from '../../utils/geometry';
import {
  convertToFactionCombatUnit,
  convertWeaponSystemToMount,
  isFactionCombatUnit,
} from '../../utils/typeConversions';

// Define weapon system interface for faction combat
export interface FactionWeaponSystem extends WeaponSystem {
  mountInfo?: {
    size: WeaponMountSize;
    position: WeaponMountPosition;
    rotation: number;
    allowedCategories: WeaponCategory[];
  };
}

// Convert weapon system to weapon instance
function convertToWeaponInstance(weapon: WeaponSystem): WeaponInstance {
  const config: WeaponConfig = {
    id: weapon.id,
    name: weapon.type,
    category: weapon.type,
    tier: 1,
    baseStats: {
      damage: weapon.damage,
      range: weapon.range,
      accuracy: 0.8,
      rateOfFire: 1,
      energyCost: 10,
      cooldown: weapon.cooldown,
      effects: [],
    },
    visualAsset: 'default_weapon',
    mountRequirements: {
      size: 'medium' as WeaponMountSize,
      power: 100,
    },
  };

  const state = {
    status: weapon.status,
    currentStats: {
      damage: weapon.damage,
      range: weapon.range,
      accuracy: 0.8,
      rateOfFire: 1,
      energyCost: 10,
      cooldown: weapon.cooldown,
      effects: [],
    },
    effects: [],
  };

  return { config, state };
}

// Convert combat unit weapons to weapon mounts
function convertToWeaponMounts(weapons: WeaponSystem[]): WeaponMount[] {
  return weapons.map((weapon, index) => convertWeaponSystemToMount(weapon, index));
}

// Define FactionCombatWeapon interface
export interface FactionCombatWeapon extends WeaponSystem {
  upgrades?: {
    name: string;
    description: string;
    unlocked: boolean;
  }[];
}

// Create FactionCombatUnit interface extending CombatUnit
export interface FactionCombatUnit
  extends Omit<CombatUnit, 'status' | 'faction' | 'weapons' | 'stats'> {
  faction: FactionId;
  class: FactionShipClass;
  tactics: FactionBehaviorType;
  specialAbility?: {
    name: string;
    description: string;
    cooldown: number;
    active: boolean;
  };
  weaponMounts: WeaponMount[];
  weapons: FactionCombatWeapon[];
  formation: {
    type: 'offensive' | 'defensive' | 'balanced';
    spacing: number;
    facing: number;
    position: number;
  };
  stats: {
    health: number;
    maxHealth: number;
    shield: number;
    maxShield: number;
    armor: number;
    speed: number;
    turnRate: number;
    accuracy: number;
    evasion: number;
    criticalChance: number;
    criticalDamage: number;
    armorPenetration: number;
    shieldPenetration: number;
    experience: number;
    level: number;
  };
  status: {
    main: 'active' | 'disabled' | 'destroyed';
    secondary?: 'charging' | 'cooling' | 'repairing' | 'boosting';
    effects: string[];
  };
  experience: {
    current: number;
    total: number;
    level: number;
    skills: string[];
  };
}

// Keep only one implementation of hasStatus
function hasStatus(
  unit: CombatUnit | FactionCombatUnit,
  status: CombatUnitStatus['main'] | CombatUnitStatus['secondary'] | string
): boolean {
  if (typeof status === 'string') {
    return unit.status.effects.includes(status);
  }
  return unit.status.main === status || unit.status.secondary === status;
}

// Helper function to check if array is FactionCombatUnit[]
function isFactionCombatUnitArray(
  units: CombatUnit[] | FactionCombatUnit[]
): units is FactionCombatUnit[] {
  return units.length > 0 && isFactionCombatUnit(units[0]);
}

// Helper function to convert CombatUnit to FactionCombatUnit
function convertUnitsToFaction(
  units: CombatUnit[],
  defaultFaction: FactionId
): FactionCombatUnit[] {
  return units.map(unit => {
    const baseStats = getShipBehaviorStats(unit.type as unknown as ShipClass);

    const factionUnit: FactionCombatUnit = {
      id: unit.id,
      type: unit.type,
      position: unit.position,
      rotation: unit.rotation,
      velocity: unit.velocity,
      faction: defaultFaction,
      class: unit.type as FactionShipClass,
      tactics: {
        formation: 'balanced',
        behavior: 'aggressive',
        target: undefined,
      },
      weaponMounts: unit.weapons.map((weapon, index) => ({
        id: `mount-${index}`,
        size: 'medium' as const,
        position: index % 2 === 0 ? ('front' as const) : ('side' as const),
        rotation: 0,
        allowedCategories: [weapon.type as WeaponCategory],
      })),
      weapons: unit.weapons.map(w => ({
        ...w,
        upgrades: [],
      })),
      formation: {
        type: 'balanced',
        spacing: 100,
        facing: 0,
        position: 0,
      },
      stats: {
        ...unit.stats,
        accuracy: 0.8,
        evasion: 0.2,
        criticalChance: 0.1,
        criticalDamage: 1.5,
        armorPenetration: 0,
        shieldPenetration: 0,
        experience: 0,
        level: 1,
      },
      status: unit.status,
      experience: {
        current: 0,
        total: 0,
        level: 1,
        skills: [],
      },
    };

    return factionUnit;
  });
}

// Update calculateFleetStrength to handle type conversion
function calculateFleetStrength(units: CombatUnit[] | FactionCombatUnit[]): number {
  const factionUnits = isFactionCombatUnitArray(units)
    ? units
    : convertUnitsToFaction(units, 'neutral');
  return factionUnits.reduce((total, unit) => {
    const weaponDamage = unit.weapons.reduce((sum, weapon) => sum + weapon.damage, 0);
    return total + weaponDamage;
  }, 0);
}

