import { ModuleEvent, moduleEventBus } from '../../lib/modules/ModuleEvents';
import { EventEmitter } from '../../lib/utils/EventEmitter';
import { combatManager } from '../../managers/combat/combatManager';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import { Position } from '../../types/core/GameTypes';
import { CommonShipCapabilities } from '../../types/ships/CommonShipTypes';
import { WeaponConfig, WeaponState } from '../../types/weapons/WeaponTypes';

interface WarShip {
  id: string;
  name: string;
  type:
    | 'spitflare'
    | 'starSchooner'
    | 'orionFrigate'
    | 'harbringerGalleon'
    | 'midwayCarrier'
    | 'motherEarthRevenge';
  tier: 1 | 2 | 3;
  status: 'idle' | 'patrolling' | 'engaging' | 'returning' | 'damaged' | 'retreating' | 'disabled';
  position: Position;
  health: number;
  maxHealth: number;
  shield: number;
  maxShield: number;
  energy: number;
  maxEnergy: number;
  capabilities: CommonShipCapabilities;
  weapons: {
    id: string;
    config: WeaponConfig;
    state: WeaponState;
  }[];
  specialAbilities?: {
    name: string;
    description: string;
    cooldown: number;
    active: boolean;
    effectiveness?: number;
  }[];
  techBonuses?: {
    weaponEfficiency: number;
    shieldRegeneration: number;
    energyEfficiency: number;
  };
  combatStats: {
    damageDealt: number;
    damageReceived: number;
    killCount: number;
    assistCount: number;
  };
}

interface CombatTask {
  id: string;
  type: 'combat';
  target: {
    id: string;
    position: Position;
  };
  priority: number;
  assignedAt: number;
  status: 'queued' | 'in-progress' | 'completed' | 'failed';
  formation?: {
    type: 'offensive' | 'defensive' | 'balanced';
    spacing: number;
    facing: number;
  };
}

export class WarShipManagerImpl extends EventEmitter {
  private ships: Map<string, WarShip> = new Map();
  private tasks: Map<string, CombatTask> = new Map();
  private formations: Map<
    string,
    {
      type: 'offensive' | 'defensive' | 'balanced';
      ships: string[];
      leader?: string;
      spacing: number;
      facing: number;
    }
  > = new Map();

  constructor() {
    super();
    this.initializeAutomationHandlers();
  }

  private initializeAutomationHandlers(): void {
    moduleEventBus.subscribe('AUTOMATION_STARTED', (event: ModuleEvent) => {
      if (event.moduleType === 'hangar' && event.data?.type) {
        const ship = this.ships.get(event.moduleId);
        if (ship) {
          switch (event.data.type) {
            case 'formation':
              this.handleFormationChange(ship, event.data);
              break;
            case 'engagement':
              this.handleEngagement(ship, event.data);
              break;
            case 'repair':
              this.handleRepair(ship);
              break;
            case 'shield':
              this.handleShieldBoost(ship);
              break;
            case 'attack':
              this.handleAttack(ship, event.data);
              break;
            case 'retreat':
              this.handleRetreat(ship);
              break;
          }
        }
      }
    });
  }

  private handleFormationChange(ship: WarShip, data: any): void {
    const { formation } = data;
    if (formation) {
      const formationId = `formation-${Date.now()}`;
      this.formations.set(formationId, {
        type: formation.type,
        ships: [ship.id],
        spacing: formation.spacing,
        facing: formation.facing,
      });
    }
  }

  private handleEngagement(ship: WarShip, data: any): void {
    const { targetId } = data;
    if (targetId) {
      const task: CombatTask = {
        id: `combat-${targetId}`,
        type: 'combat',
        target: {
          id: targetId,
          position: { x: 0, y: 0 }, // Position will be updated in the update loop
        },
        priority: this.getPriorityForShipType(ship.type),
        assignedAt: Date.now(),
        status: 'in-progress',
      };
      this.tasks.set(ship.id, task);
      this.updateShipStatus(ship.id, 'engaging');
    }
  }

  private handleRepair(ship: WarShip): void {
    if (ship.health < ship.maxHealth) {
      ship.health = Math.min(ship.maxHealth, ship.health + ship.maxHealth * 0.2);
      if (ship.health > ship.maxHealth * 0.3) {
        this.updateShipStatus(ship.id, 'idle');
      }
    }
  }

  private handleShieldBoost(ship: WarShip): void {
    if (ship.shield < ship.maxShield) {
      ship.shield = Math.min(ship.maxShield, ship.shield + ship.maxShield * 0.3);
    }
  }

  private handleAttack(ship: WarShip, data: any): void {
    const { targetId } = data;
    if (targetId) {
      const readyWeapon = ship.weapons.find(w => w.state.status === 'ready');
      if (readyWeapon) {
        readyWeapon.state.status = 'cooling';
        readyWeapon.state.currentStats.cooldown = Date.now();
        ship.energy -=
          readyWeapon.config.baseStats.energyCost * (ship.techBonuses?.energyEfficiency || 1);

        // Update combat stats
        ship.combatStats.damageDealt +=
          readyWeapon.config.baseStats.damage * (ship.techBonuses?.weaponEfficiency || 1);
      }
    }
  }

  private handleRetreat(ship: WarShip): void {
    this.updateShipStatus(ship.id, 'retreating');
    const task = this.tasks.get(ship.id);
    if (task) {
      task.status = 'failed';
      this.tasks.delete(ship.id);
    }
  }

  public registerShip(ship: WarShip): void {
    if (ship.capabilities.canJump) {
      this.ships.set(ship.id, ship);

      // Emit events
      this.emit('shipRegistered', { shipId: ship.id });
      moduleEventBus.emit({
        type: 'MODULE_ACTIVATED',
        moduleId: ship.id,
        moduleType: 'war' as ModuleType,
        timestamp: Date.now(),
        data: { ship },
      });
    }
  }

  public unregisterShip(shipId: string): void {
    if (this.ships.has(shipId)) {
      // Remove from formations
      this.formations.forEach(formation => {
        const index = formation.ships.indexOf(shipId);
        if (index !== -1) {
          formation.ships.splice(index, 1);
          if (formation.leader === shipId) {
            formation.leader = formation.ships[0];
          }
        }
      });

      this.ships.delete(shipId);
      this.tasks.delete(shipId);

      // Emit events
      this.emit('shipUnregistered', { shipId });
      moduleEventBus.emit({
        type: 'MODULE_DEACTIVATED',
        moduleId: shipId,
        moduleType: 'war' as ModuleType,
        timestamp: Date.now(),
      });
    }
  }

  public assignCombatTask(
    shipId: string,
    targetId: string,
    position: Position,
    formation?: {
      type: 'offensive' | 'defensive' | 'balanced';
      spacing: number;
      facing: number;
    }
  ): void {
    const ship = this.ships.get(shipId);
    if (!ship || ship.status === 'disabled') {
      return;
    }

    const task: CombatTask = {
      id: `combat-${targetId}`,
      type: 'combat',
      target: {
        id: targetId,
        position,
      },
      priority: this.getPriorityForShipType(ship.type),
      assignedAt: Date.now(),
      status: 'queued',
      formation,
    };

    this.tasks.set(shipId, task);
    this.updateShipStatus(shipId, 'engaging');

    // Emit events
    this.emit('taskAssigned', { shipId, task });
    moduleEventBus.emit({
      type: 'AUTOMATION_STARTED',
      moduleId: shipId,
      moduleType: 'war' as ModuleType,
      timestamp: Date.now(),
      data: { task },
    });
  }

  public completeTask(shipId: string): void {
    const task = this.tasks.get(shipId);
    const ship = this.ships.get(shipId);

    if (task && ship) {
      this.tasks.delete(shipId);
      this.updateShipStatus(shipId, 'returning');

      // Emit events
      this.emit('taskCompleted', { shipId, task });
      moduleEventBus.emit({
        type: 'AUTOMATION_CYCLE_COMPLETE',
        moduleId: shipId,
        moduleType: 'war' as ModuleType,
        timestamp: Date.now(),
        data: { task, combatStats: ship.combatStats },
      });
    }
  }

  public updateShipTechBonuses(
    shipId: string,
    bonuses: { weaponEfficiency: number; shieldRegeneration: number; energyEfficiency: number }
  ): void {
    const ship = this.ships.get(shipId);
    if (ship) {
      ship.techBonuses = bonuses;
      this.emit('techBonusesUpdated', { shipId, bonuses });
    }
  }

  public createFormation(
    type: 'offensive' | 'defensive' | 'balanced',
    shipIds: string[],
    spacing: number = 100
  ): string {
    const formationId = `formation-${Date.now()}`;
    const validShips = shipIds.filter(id => this.ships.has(id));

    if (validShips.length > 0) {
      this.formations.set(formationId, {
        type,
        ships: validShips,
        leader: validShips[0],
        spacing,
        facing: 0,
      });

      // Update tasks with formation info
      validShips.forEach(shipId => {
        const task = this.tasks.get(shipId);
        if (task) {
          task.formation = {
            type,
            spacing,
            facing: 0,
          };
        }
      });

      this.emit('formationCreated', { formationId, type, ships: validShips });
    }

    return formationId;
  }

  private updateShipStatus(shipId: string, status: WarShip['status']): void {
    const ship = this.ships.get(shipId);
    if (ship) {
      ship.status = status;

      // Emit events
      this.emit('shipStatusUpdated', { shipId, status });
      moduleEventBus.emit({
        type: 'STATUS_CHANGED',
        moduleId: shipId,
        moduleType: 'war' as ModuleType,
        timestamp: Date.now(),
        data: { status },
      });
    }
  }

  private getPriorityForShipType(type: WarShip['type']): number {
    switch (type) {
      case 'motherEarthRevenge':
        return 5;
      case 'midwayCarrier':
        return 4;
      case 'harbringerGalleon':
        return 3;
      case 'orionFrigate':
        return 2;
      case 'starSchooner':
      case 'spitflare':
        return 1;
      default:
        return 0;
    }
  }

  // Update method for periodic tasks
  public update(deltaTime: number): void {
    // Update formations
    this.formations.forEach(formation => {
      if (formation.ships.length > 0) {
        const leader = this.ships.get(formation.leader!);
        if (leader) {
          // Update formation facing based on leader's target
          const task = this.tasks.get(leader.id);
          if (task?.target) {
            const dx = task.target.position.x - leader.position.x;
            const dy = task.target.position.y - leader.position.y;
            formation.facing = Math.atan2(dy, dx);
          }

          // Update formation positions
          formation.ships.forEach((shipId, index) => {
            if (shipId !== formation.leader) {
              const ship = this.ships.get(shipId);
              if (ship) {
                // Calculate formation position
                const angle = formation.facing + index * (Math.PI / 4);
                const targetPos = {
                  x: leader.position.x + Math.cos(angle) * formation.spacing,
                  y: leader.position.y + Math.sin(angle) * formation.spacing,
                };

                // Move ship towards formation position
                combatManager.moveUnit(shipId, targetPos);
              }
            }
          });
        }
      }
    });

    // Update ships
    this.ships.forEach(ship => {
      // Update weapon cooldowns
      ship.weapons.forEach(weapon => {
        if (weapon.state.status === 'cooling') {
          const timeSinceFired = Date.now() - (weapon.state.currentStats.cooldown || 0);
          if (timeSinceFired >= weapon.config.baseStats.cooldown) {
            weapon.state.status = 'ready';
          }
        }
      });

      // Handle combat tasks
      const task = this.tasks.get(ship.id);
      if (task?.status === 'in-progress') {
        const target = combatManager
          .getUnitsInRange(ship.position, ship.weapons[0].config.baseStats.range)
          .find(unit => unit.id === task.target.id);

        if (target) {
          // Find ready weapon in range
          const readyWeapon = ship.weapons.find(weapon => {
            const distance = Math.sqrt(
              Math.pow(target.position.x - ship.position.x, 2) +
                Math.pow(target.position.y - ship.position.y, 2)
            );
            return weapon.state.status === 'ready' && distance <= weapon.config.baseStats.range;
          });

          if (readyWeapon) {
            // Fire weapon
            readyWeapon.state.status = 'cooling';
            readyWeapon.state.currentStats.cooldown = Date.now();
            ship.energy -=
              readyWeapon.config.baseStats.energyCost * (ship.techBonuses?.energyEfficiency || 1);

            // Update combat stats
            ship.combatStats.damageDealt +=
              readyWeapon.config.baseStats.damage * (ship.techBonuses?.weaponEfficiency || 1);
          }
        }
      }

      // Regenerate shields
      if (ship.shield < ship.maxShield) {
        ship.shield = Math.min(
          ship.maxShield,
          ship.shield + deltaTime * 0.1 * (ship.techBonuses?.shieldRegeneration || 1)
        );
      }

      // Check for critical damage
      if (ship.health < ship.maxHealth * 0.3 && ship.status !== 'retreating') {
        this.updateShipStatus(ship.id, 'retreating');
      }
    });
  }
}

import { ModuleEvent, moduleEventBus } from '../../lib/modules/ModuleEvents';
import { factionManager } from '../../managers/factions/factionManager';

export interface Fleet {
  units: CombatUnit[];
}

export interface CombatManager {
  getFleetStatus: (fleetId: string) => Fleet | undefined;
  getUnitsInRange: (position: { x: number; y: number }, range: number) => CombatUnit[];
  getThreatsInRange: (position: { x: number; y: number }, range: number) => Threat[];
  moveUnit: (unitId: string, position: { x: number; y: number }) => void;
  removeUnit: (unitId: string) => void;
}

export interface Threat {
  id: string;
  position: { x: number; y: number };
  severity: 'low' | 'medium' | 'high';
  type: string;
}

export interface CombatUnit {
  id: string;
  faction: string;
  type:
    | 'spitflare'
    | 'starSchooner'
    | 'orionFrigate'
    | 'harbringerGalleon'
    | 'midwayCarrier'
    | 'motherEarthRevenge';
  tier: 1 | 2 | 3;
  position: { x: number; y: number };
  status: 'idle' | 'patrolling' | 'engaging' | 'returning' | 'damaged' | 'retreating' | 'disabled';
  health: number;
  maxHealth: number;
  shield: number;
  maxShield: number;
  target?: string;
  weapons: {
    id: string;
    type: 'machineGun' | 'gaussCannon' | 'railGun' | 'mgss' | 'rockets';
    range: number;
    damage: number;
    cooldown: number;
    status: 'ready' | 'charging' | 'cooling';
    lastFired?: number;
  }[];
  specialAbilities?: {
    name: string;
    description: string;
    cooldown: number;
    active: boolean;
  }[];
}

interface CombatZone {
  id: string;
  position: { x: number; y: number };
  radius: number;
  units: CombatUnit[];
  threatLevel: number;
}

class CombatManagerImpl implements CombatManager {
  private combatZones: Map<string, CombatZone> = new Map();
  private units: Map<string, CombatUnit> = new Map();
  private autoDispatchEnabled: boolean = true;
  private threats: Map<string, Threat> = new Map();

  // Combat Thresholds
  private readonly ENGAGEMENT_RANGE = 500; // Units within this range will engage
  private readonly RETREAT_HEALTH_THRESHOLD = 0.3; // Units retreat at 30% health
  private readonly REINFORCEMENT_THRESHOLD = 0.5; // Call reinforcements at 50% fleet strength
  private readonly MAX_UNITS_PER_ZONE = 10; // Maximum units in a combat zone

  constructor() {
    this.startCombatLoop();
    this.initializeAutomationHandlers();
  }

  private initializeAutomationHandlers(): void {
    moduleEventBus.subscribe('AUTOMATION_STARTED', (event: ModuleEvent) => {
      if (event.moduleType === 'hangar') {
        switch (event.data?.type) {
          case 'formation':
            this.handleFormationUpdate(event.data);
            break;
          case 'engagement':
            this.handleEngagement(event.data);
            break;
          case 'repair':
            this.handleDamageControl(event.data);
            break;
          case 'shield':
            this.handleShieldBoost(event.data);
            break;
          case 'attack':
            this.handleWeaponFire(event.data);
            break;
          case 'retreat':
            this.handleRetreat(event.data);
            break;
        }
      }
    });
  }

  private handleFormationUpdate(data: any): void {
    const { position, units } = data;
    if (position && units) {
      units.forEach((unitId: string) => {
        const unit = this.units.get(unitId);
        if (unit) {
          // Update unit position in formation
          this.moveUnit(unitId, {
            x: position.x + (Math.random() - 0.5) * 50, // Add some variation
            y: position.y + (Math.random() - 0.5) * 50,
          });
        }
      });
    }
  }

  private handleEngagement(data: any): void {
    const { targetId, units } = data;
    if (targetId && units) {
      units.forEach((unitId: string) => {
        const unit = this.units.get(unitId);
        if (unit && unit.status !== 'disabled') {
          unit.status = 'engaging';
          unit.target = targetId;
        }
      });
    }
  }

  private handleDamageControl(data: any): void {
    const { unitId } = data;
    if (unitId) {
      const unit = this.units.get(unitId);
      if (unit) {
        // Apply repair effect
        unit.health = Math.min(unit.maxHealth, unit.health + unit.maxHealth * 0.2);
        if (unit.health > unit.maxHealth * this.RETREAT_HEALTH_THRESHOLD) {
          unit.status = 'engaging';
        }
      }
    }
  }

  private handleShieldBoost(data: any): void {
    const { unitId } = data;
    if (unitId) {
      const unit = this.units.get(unitId);
      if (unit) {
        // Boost shields
        unit.shield = Math.min(unit.maxShield, unit.shield + unit.maxShield * 0.3);
      }
    }
  }

  private handleWeaponFire(data: any): void {
    const { unitId, targetId } = data;
    if (unitId && targetId) {
      const unit = this.units.get(unitId);
      const target = this.units.get(targetId);
      if (unit && target) {
        // Find ready weapon
        const weapon = unit.weapons.find(w => w.status === 'ready');
        if (weapon) {
          weapon.status = 'cooling';
          weapon.lastFired = Date.now();
          // Apply damage
          if (target.shield > 0) {
            const shieldDamage = Math.min(target.shield, weapon.damage * 0.7);
            target.shield -= shieldDamage;
            target.health -= (weapon.damage - shieldDamage) * 0.3;
          } else {
            target.health -= weapon.damage;
          }
          // Check for disabled state
          if (target.health <= 0) {
            target.status = 'disabled';
            target.target = undefined;
          }
        }
      }
    }
  }

  private handleRetreat(data: any): void {
    const { unitId } = data;
    if (unitId) {
      const unit = this.units.get(unitId);
      if (unit) {
        unit.status = 'retreating';
        unit.target = undefined;
      }
    }
  }

  private startCombatLoop() {
    setInterval(() => {
      this.updateCombatZones();
      this.processAutoDispatch();
      this.updateUnitBehaviors();
    }, 1000);
  }

  private updateCombatZones() {
    this.combatZones.forEach(zone => {
      // Update threat level based on hostile units
      zone.threatLevel = this.calculateZoneThreatLevel(zone);

      // Merge nearby zones if they overlap
      this.combatZones.forEach(otherZone => {
        if (zone.id !== otherZone.id && this.zonesOverlap(zone, otherZone)) {
          this.mergeZones(zone, otherZone);
        }
      });

      // Remove empty zones
      if (zone.units.length === 0) {
        this.combatZones.delete(zone.id);
      }
    });
  }

  private calculateZoneThreatLevel(zone: CombatZone): number {
    let threat = 0;
    const hostileUnits = zone.units.filter(
      unit => factionManager.getFactionBehavior(unit.faction)?.isHostile
    );

    hostileUnits.forEach(unit => {
      threat +=
        (unit.health / unit.maxHealth) * (unit.weapons.reduce((sum, w) => sum + w.damage, 0) / 100);
    });

    return Math.min(1, threat / this.MAX_UNITS_PER_ZONE);
  }

  private zonesOverlap(zone1: CombatZone, zone2: CombatZone): boolean {
    const dx = zone1.position.x - zone2.position.x;
    const dy = zone1.position.y - zone2.position.y;
    const distance = Math.sqrt(dx * dx + dy * dy);
    return distance < zone1.radius + zone2.radius;
  }

  private mergeZones(zone1: CombatZone, zone2: CombatZone) {
    // Create new zone encompassing both
    const newZone: CombatZone = {
      id: `merged-${Date.now()}`,
      position: {
        x: (zone1.position.x + zone2.position.x) / 2,
        y: (zone1.position.y + zone2.position.y) / 2,
      },
      radius: Math.max(
        zone1.radius,
        zone2.radius,
        this.getDistance(zone1.position, zone2.position) / 2
      ),
      units: [...zone1.units, ...zone2.units],
      threatLevel: Math.max(zone1.threatLevel, zone2.threatLevel),
    };

    this.combatZones.delete(zone1.id);
    this.combatZones.delete(zone2.id);
    this.combatZones.set(newZone.id, newZone);
  }

  private processAutoDispatch() {
    if (!this.autoDispatchEnabled) {
      return;
    }

    this.combatZones.forEach(zone => {
      if (zone.threatLevel > this.REINFORCEMENT_THRESHOLD) {
        const nearbyAllies = this.findNearbyAllies(zone);
        nearbyAllies.forEach(ally => {
          if (this.shouldDispatchUnit(ally, zone)) {
            this.dispatchUnitToZone(ally, zone);
          }
        });
      }
    });
  }

  private findNearbyAllies(zone: CombatZone): CombatUnit[] {
    return Array.from(this.units.values()).filter(unit => {
      if (unit.status !== 'idle') {
        return false;
      }
      const distance = this.getDistance(unit.position, zone.position);
      return distance <= this.ENGAGEMENT_RANGE;
    });
  }

  private shouldDispatchUnit(unit: CombatUnit, zone: CombatZone): boolean {
    // Check if unit is available and healthy
    if (unit.status !== 'idle' || unit.health < unit.maxHealth * 0.8) {
      return false;
    }

    // Check if zone needs reinforcements
    const alliedUnits = zone.units.filter(u => u.faction === unit.faction);
    if (alliedUnits.length >= this.MAX_UNITS_PER_ZONE / 2) {
      return false;
    }

    return true;
  }

  private dispatchUnitToZone(unit: CombatUnit, zone: CombatZone) {
    unit.status = 'engaging';
    zone.units.push(unit);

    // Find suitable target
    const target = this.findBestTarget(unit, zone);
    if (target) {
      unit.target = target.id;
    }
  }

  private findBestTarget(unit: CombatUnit, zone: CombatZone): CombatUnit | null {
    const hostileUnits = zone.units.filter(
      u => factionManager.getFactionBehavior(u.faction)?.isHostile
    );

    if (hostileUnits.length === 0) {
      return null;
    }

    // Prioritize targets based on threat and distance
    return hostileUnits.reduce(
      (best, current) => {
        if (!best) {
          return current;
        }

        const bestScore = this.calculateTargetScore(unit, best);
        const currentScore = this.calculateTargetScore(unit, current);

        return currentScore > bestScore ? current : best;
      },
      null as CombatUnit | null
    );
  }

  private calculateTargetScore(unit: CombatUnit, target: CombatUnit): number {
    const distance = this.getDistance(unit.position, target.position);
    const healthFactor = target.health / target.maxHealth;
    const threatFactor = target.weapons.reduce((sum, w) => sum + w.damage, 0) / 100;

    return (threatFactor * 0.4 + (1 - healthFactor) * 0.3) / (distance * 0.3);
  }

  private updateUnitBehaviors() {
    this.units.forEach(unit => {
      if (unit.status === 'disabled') {
        return;
      }

      // Check for retreat conditions
      if (this.shouldRetreat(unit)) {
        this.initiateRetreat(unit);
        return;
      }

      // Update combat behavior
      if (unit.status === 'engaging' && unit.target) {
        const target = this.units.get(unit.target);
        if (target) {
          this.updateCombat(unit, target);
        } else {
          unit.target = undefined;
        }
      }
    });
  }

  private shouldRetreat(unit: CombatUnit): boolean {
    // Check health threshold
    if (unit.health < unit.maxHealth * this.RETREAT_HEALTH_THRESHOLD) {
      return true;
    }

    // Check if outnumbered significantly
    const zone = this.findUnitZone(unit);
    if (zone) {
      const allies = zone.units.filter(u => u.faction === unit.faction).length;
      const enemies = zone.units.filter(u => u.faction !== unit.faction).length;
      if (enemies > allies * 2) {
        return true;
      }
    }

    return false;
  }

  private initiateRetreat(unit: CombatUnit) {
    unit.status = 'retreating';
    unit.target = undefined;

    // Remove from combat zone
    const zone = this.findUnitZone(unit);
    if (zone) {
      zone.units = zone.units.filter(u => u.id !== unit.id);
    }
  }

  private updateCombat(unit: CombatUnit, target: CombatUnit) {
    // Check if weapons are in range and ready
    unit.weapons.forEach(weapon => {
      const distance = this.getDistance(unit.position, target.position);
      if (distance <= weapon.range && this.canFireWeapon(weapon)) {
        this.fireWeapon(unit, target, weapon);
      }
    });
  }

  private canFireWeapon(weapon: CombatUnit['weapons'][0]): boolean {
    const now = Date.now();
    return (
      weapon.status === 'ready' &&
      (!weapon.lastFired || now - weapon.lastFired >= weapon.cooldown * 1000)
    );
  }

  private updateWeaponStatus(weapon: CombatUnit['weapons'][0]): void {
    if (this.canFireWeapon(weapon)) {
      weapon.status = 'charging';
      weapon.lastFired = Date.now();
    }
  }

  private fireWeapon(unit: CombatUnit, target: CombatUnit, weapon: CombatUnit['weapons'][0]) {
    // Skip if unit is disabled
    if (unit.status === 'disabled') {
      return;
    }

    weapon.lastFired = Date.now();
    this.updateWeaponStatus(weapon);

    // Calculate damage considering shields
    let { damage } = weapon;
    if (target.shield > 0) {
      const shieldDamage = Math.min(target.shield, damage * 0.7);
      target.shield -= shieldDamage;
      damage -= shieldDamage;
    }

    target.health = Math.max(0, target.health - damage);

    // Check if target is disabled
    if (target.health === 0) {
      target.status = 'disabled';
      target.target = undefined;
    }
  }

  private findUnitZone(unit: CombatUnit): CombatZone | null {
    for (const zone of this.combatZones.values()) {
      if (zone.units.some(u => u.id === unit.id)) {
        return zone;
      }
    }
    return null;
  }

  private getDistance(pos1: { x: number; y: number }, pos2: { x: number; y: number }): number {
    const dx = pos1.x - pos2.x;
    const dy = pos1.y - pos2.y;
    return Math.sqrt(dx * dx + dy * dy);
  }

  // Public API
  public addUnit(unit: CombatUnit) {
    this.units.set(unit.id, unit);
  }

  public removeUnit(unitId: string) {
    this.units.delete(unitId);
  }

  public createCombatZone(position: { x: number; y: number }): string {
    const zone: CombatZone = {
      id: `zone-${Date.now()}`,
      position,
      radius: 100,
      units: [],
      threatLevel: 0,
    };
    this.combatZones.set(zone.id, zone);
    return zone.id;
  }

  public setAutoDispatch(enabled: boolean) {
    this.autoDispatchEnabled = enabled;
  }

  public getZoneStatus(zoneId: string) {
    return this.combatZones.get(zoneId);
  }

  public getUnitStatus(unitId: string) {
    return this.units.get(unitId);
  }

  getFleetStatus(fleetId: string) {
    const fleetUnits = Array.from(this.units.values()).filter(unit => unit.faction === fleetId);
    return fleetUnits.length > 0 ? { units: fleetUnits } : undefined;
  }

  getUnitsInRange(position: { x: number; y: number }, range: number): CombatUnit[] {
    return Array.from(this.units.values()).filter(unit => {
      const dx = unit.position.x - position.x;
      const dy = unit.position.y - position.y;
      return Math.sqrt(dx * dx + dy * dy) <= range;
    });
  }

  getThreatsInRange(position: { x: number; y: number }, range: number): Threat[] {
    return Array.from(this.threats.values()).filter(threat => {
      const dx = threat.position.x - position.x;
      const dy = threat.position.y - position.y;
      return Math.sqrt(dx * dx + dy * dy) <= range;
    });
  }

  moveUnit(unitId: string, position: { x: number; y: number }): void {
    const unit = this.units.get(unitId);
    if (unit) {
      unit.position = position;
    }
  }
}

export const combatManager = new CombatManagerImpl();

import {
  AdditiveBlending,
  BufferAttribute,
  BufferGeometry,
  Color,
  PerspectiveCamera,
  Points,
  Scene,
  ShaderMaterial,
  WebGLRenderer,
} from 'three';
import { EntityPool, PooledEntity } from '../../lib/optimization/EntityPool';
import { Position } from '../../types/core/GameTypes';

interface Particle extends PooledEntity {
  id: string;
  active: boolean;
  position: Position;
  velocity: Position;
  acceleration: Position;
  size: number;
  color: string;
  opacity: number;
  life: number;
  maxLife: number;
  reset(): void;
}

interface ParticleSystemConfig {
  maxParticles: number;
  spawnRate: number;
  position: Position;
  spread: number;
  initialVelocity: {
    min: Position;
    max: Position;
  };
  acceleration: Position;
  size: {
    min: number;
    max: number;
  };
  life: {
    min: number;
    max: number;
  };
  color: string | string[];
  blendMode?: 'normal' | 'additive';
  quality: 'low' | 'medium' | 'high';
}

export class ParticleSystemManager {
  private static instance: ParticleSystemManager;
  private particlePools: Map<string, Map<number, EntityPool<Particle>>>;
  private systems: Map<string, ParticleSystem>;
  private renderer: WebGLRenderer;
  private scene: Scene;
  private camera: PerspectiveCamera;
  private frameCount: number = 0;
  private readonly FRAME_SKIP_THRESHOLD = 2;

  private constructor() {
    this.particlePools = new Map();
    this.systems = new Map();
    this.renderer = new WebGLRenderer({ antialias: true });
    this.scene = new Scene();
    this.camera = new PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    this.camera.position.z = 5;
    this.initializeRenderer();

    // Start cleanup interval
    setInterval(() => this.cleanupInactiveSystems(), 5000);
  }

  public static getInstance(): ParticleSystemManager {
    if (!ParticleSystemManager.instance) {
      ParticleSystemManager.instance = new ParticleSystemManager();
    }
    return ParticleSystemManager.instance;
  }

  private initializeRenderer(): void {
    this.renderer.setSize(window.innerWidth, window.innerHeight);
    this.renderer.setPixelRatio(window.devicePixelRatio);
  }

  private getPoolSizeForQuality(quality: 'low' | 'medium' | 'high'): number {
    switch (quality) {
      case 'low':
        return 100;
      case 'medium':
        return 500;
      case 'high':
        return 1000;
      default:
        return 500;
    }
  }

  private getOrCreatePool(
    systemId: string,
    particleSize: number,
    quality: 'low' | 'medium' | 'high'
  ): EntityPool<Particle> {
    if (!this.particlePools.has(systemId)) {
      this.particlePools.set(systemId, new Map());
    }

    const sizePools = this.particlePools.get(systemId)!;
    if (!sizePools.has(particleSize)) {
      const poolSize = this.getPoolSizeForQuality(quality);
      sizePools.set(particleSize, new EntityPool<Particle>(() => this.createParticle(), poolSize));
    }

    return sizePools.get(particleSize)!;
  }

  private createParticle(): Particle {
    return {
      id: `particle-${Date.now()}-${Math.random()}`,
      active: false,
      position: { x: 0, y: 0 },
      velocity: { x: 0, y: 0 },
      acceleration: { x: 0, y: 0 },
      size: 1,
      color: '#ffffff',
      opacity: 1,
      life: 1,
      maxLife: 1,
      reset(): void {
        this.active = false;
        this.position = { x: 0, y: 0 };
        this.velocity = { x: 0, y: 0 };
        this.acceleration = { x: 0, y: 0 };
        this.opacity = 1;
        this.life = this.maxLife;
      },
    };
  }

  public createParticleSystem(id: string, config: ParticleSystemConfig): void {
    const pool = this.getOrCreatePool(id, config.size.max, config.quality);
    const geometry = new BufferGeometry();
    const material = this.createParticleMaterial(config);
    const points = new Points(geometry, material);

    const system = new ParticleSystem(pool, config, points, this.scene);
    this.systems.set(id, system);
    this.scene.add(points);
  }

  private createParticleMaterial(config: ParticleSystemConfig): ShaderMaterial {
    return new ShaderMaterial({
      uniforms: {
        time: { value: 0 },
        color: { value: new Color(config.color as string) },
      },
      vertexShader: `
        attribute float size;
        attribute float opacity;
        attribute vec3 color;
        varying float vOpacity;
        varying vec3 vColor;

        void main() {
          vOpacity = opacity;
          vColor = color;
          vec4 mvPosition = modelViewMatrix * vec4(position, 1.0);
          gl_PointSize = size * (300.0 / -mvPosition.z);
          gl_Position = projectionMatrix * mvPosition;
        }
      `,
      fragmentShader: `
        varying float vOpacity;
        varying vec3 vColor;

        void main() {
          vec2 xy = gl_PointCoord.xy - vec2(0.5);
          float r = length(xy);
          if (r > 0.5) discard;

          float glow = exp(-r * 3.0);
          gl_FragColor = vec4(vColor, vOpacity * glow);
        }
      `,
      transparent: true,
      blending: config.blendMode === 'additive' ? AdditiveBlending : undefined,
      depthWrite: false,
    });
  }

  public update(deltaTime: number): void {
    this.frameCount++;

    // Skip frames for low priority systems when under performance pressure
    const shouldUpdate = this.frameCount % this.FRAME_SKIP_THRESHOLD === 0;
    if (!shouldUpdate) {
      return;
    }

    this.systems.forEach(system => system.update(deltaTime));
    this.renderer.render(this.scene, this.camera);
  }

  private cleanupInactiveSystems(): void {
    const now = Date.now();
    this.systems.forEach((system, id) => {
      if (!system.hasActiveParticles() && now - system.getLastUpdateTime() > 5000) {
        this.removeSystem(id);
      }
    });
  }

  public removeSystem(id: string): void {
    const system = this.systems.get(id);
    if (system) {
      system.cleanup();
      this.systems.delete(id);

      // Clean up size-based pools
      const sizePools = this.particlePools.get(id);
      if (sizePools) {
        sizePools.forEach(pool => pool.clear());
        this.particlePools.delete(id);
      }
    }
  }

  public cleanup(): void {
    this.systems.forEach(system => system.cleanup());
    this.systems.clear();
    this.particlePools.forEach(sizePools => {
      sizePools.forEach(pool => pool.clear());
    });
    this.particlePools.clear();
    this.renderer.dispose();
    this.scene.clear();
  }
}

class ParticleSystem {
  private pool: EntityPool<Particle>;
  private config: ParticleSystemConfig;
  private points: Points;
  private scene: Scene;
  private particles: Set<Particle>;
  private lastSpawnTime: number;
  private lastUpdateTime: number;

  constructor(
    pool: EntityPool<Particle>,
    config: ParticleSystemConfig,
    points: Points,
    scene: Scene
  ) {
    this.pool = pool;
    this.config = config;
    this.points = points;
    this.scene = scene;
    this.particles = new Set();
    this.lastSpawnTime = 0;
    this.lastUpdateTime = Date.now();
  }

  public update(deltaTime: number): void {
    this.lastUpdateTime = Date.now();
    this.spawnParticles(deltaTime);
    this.updateParticles(deltaTime);
    this.updateGeometry();
  }

  private spawnParticles(deltaTime: number): void {
    const now = performance.now();
    const spawnInterval = 1000 / this.config.spawnRate;

    while (
      now - this.lastSpawnTime >= spawnInterval &&
      this.particles.size < this.config.maxParticles
    ) {
      this.spawnParticle();
      this.lastSpawnTime += spawnInterval;
    }
  }

  private spawnParticle(): void {
    const particle = this.pool.acquire();
    if (!particle) {
      return;
    }

    particle.active = true;
    particle.position = { ...this.config.position };
    particle.velocity = {
      x:
        this.config.initialVelocity.min.x +
        Math.random() * (this.config.initialVelocity.max.x - this.config.initialVelocity.min.x),
      y:
        this.config.initialVelocity.min.y +
        Math.random() * (this.config.initialVelocity.max.y - this.config.initialVelocity.min.y),
    };
    particle.acceleration = { ...this.config.acceleration };
    particle.size =
      this.config.size.min + Math.random() * (this.config.size.max - this.config.size.min);
    particle.life =
      this.config.life.min + Math.random() * (this.config.life.max - this.config.life.min);
    particle.maxLife = particle.life;

    this.particles.add(particle);
  }

  private updateParticles(deltaTime: number): void {
    this.particles.forEach(particle => {
      particle.life -= deltaTime;
      if (particle.life <= 0) {
        this.particles.delete(particle);
        this.pool.release(particle);
        return;
      }

      particle.velocity.x += particle.acceleration.x * deltaTime;
      particle.velocity.y += particle.acceleration.y * deltaTime;
      particle.position.x += particle.velocity.x * deltaTime;
      particle.position.y += particle.velocity.y * deltaTime;
      particle.opacity = particle.life / particle.maxLife;
    });
  }

  private updateGeometry(): void {
    const positions = new Float32Array(this.particles.size * 3);
    const sizes = new Float32Array(this.particles.size);
    const opacities = new Float32Array(this.particles.size);
    const colors = new Float32Array(this.particles.size * 3);

    let i = 0;
    this.particles.forEach(particle => {
      positions[i * 3] = particle.position.x;
      positions[i * 3 + 1] = particle.position.y;
      positions[i * 3 + 2] = 0;

      sizes[i] = particle.size;
      opacities[i] = particle.opacity;

      const color = new Color(particle.color);
      colors[i * 3] = color.r;
      colors[i * 3 + 1] = color.g;
      colors[i * 3 + 2] = color.b;

      i++;
    });

    const geometry = this.points.geometry as BufferGeometry;
    geometry.setAttribute('position', new BufferAttribute(positions, 3));
    geometry.setAttribute('size', new BufferAttribute(sizes, 1));
    geometry.setAttribute('opacity', new BufferAttribute(opacities, 1));
    geometry.setAttribute('color', new BufferAttribute(colors, 3));
  }

  public hasActiveParticles(): boolean {
    return this.particles.size > 0;
  }

  public getLastUpdateTime(): number {
    return this.lastUpdateTime;
  }

  public cleanup(): void {
    this.particles.forEach(particle => {
      this.pool.release(particle);
    });
    this.particles.clear();
    this.scene.remove(this.points);
    this.points.geometry.dispose();
    (this.points.material as ShaderMaterial).dispose();
  }
}

// Export singleton instance
export const particleSystemManager = ParticleSystemManager.getInstance();

import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import { Position } from '../../types/core/GameTypes';
import { CommonShipCapabilities } from '../../types/ships/CommonShipTypes';
import { EventEmitter } from '../../utils/EventEmitter';
import { combatManager } from '../combat/combatManager';

interface Anomaly {
  id: string;
  type: 'artifact' | 'signal' | 'phenomenon';
  severity: 'low' | 'medium' | 'high';
  description: string;
  investigated: boolean;
}

interface Threat {
  id: string;
  position: Position;
  severity: number;
  type: string;
}

interface ReconShipEvents {
  shipRegistered: { shipId: string };
  shipUnregistered: { shipId: string };
  taskAssigned: { shipId: string; task: ExplorationTask };
  taskCompleted: { shipId: string; task: ExplorationTask };
  techBonusesUpdated: {
    shipId: string;
    bonuses: {
      scanSpeed: number;
      stealthEfficiency: number;
      detectionRange: number;
    };
  };
  stealthToggled: { shipId: string; active: boolean };
  shipStatusUpdated: { shipId: string; status: ReconShip['status'] };
  threatDetected: { shipId: string; threatLevel: number; position: Position };
  anomalyDiscovered: { shipId: string; anomaly: Anomaly; position: Position };
}

interface ReconShip {
  id: string;
  name: string;
  type: 'AC27G' | 'PathFinder' | 'VoidSeeker';
  status: 'idle' | 'scanning' | 'investigating' | 'returning';
  targetSector?: string;
  experience: number;
  specialization: 'mapping' | 'anomaly' | 'resource';
  efficiency: number;
  position: Position;
  capabilities: CommonShipCapabilities;
  stealth: {
    active: boolean;
    level: number;
    cooldown: number;
  };
  sensors: {
    range: number;
    accuracy: number;
    anomalyDetection: number;
  };
  discoveries: {
    mappedSectors: number;
    anomaliesFound: number;
    resourcesLocated: number;
  };
  techBonuses?: {
    scanSpeed: number;
    stealthEfficiency: number;
    detectionRange: number;
  };
}

interface ExplorationTask {
  id: string;
  type: 'explore' | 'investigate' | 'evade';
  target: {
    id: string;
    position: Position;
  };
  priority: number;
  assignedAt: number;
  specialization: 'mapping' | 'anomaly' | 'resource';
  status: 'queued' | 'in-progress' | 'completed' | 'failed';
  progress?: number;
  threatLevel?: number;
}

export class ReconShipManagerImpl extends EventEmitter<ReconShipEvents> {
  private ships: Map<string, ReconShip> = new Map();
  private tasks: Map<string, ExplorationTask> = new Map();
  private sectors: Map<
    string,
    {
      id: string;
      position: Position;
      explored: boolean;
      anomalies: Anomaly[];
      resources: number;
    }
  > = new Map();

  public registerShip(ship: ReconShip): void {
    if (ship.capabilities.canScan) {
      this.ships.set(ship.id, ship);

      // Emit events
      this.emit('shipRegistered', { shipId: ship.id });
      moduleEventBus.emit({
        type: 'MODULE_ACTIVATED',
        moduleId: ship.id,
        moduleType: 'radar' as ModuleType,
        timestamp: Date.now(),
        data: { ship },
      });
    }
  }

  public unregisterShip(shipId: string): void {
    if (this.ships.has(shipId)) {
      this.ships.delete(shipId);
      this.tasks.delete(shipId);

      // Emit events
      this.emit('shipUnregistered', { shipId });
      moduleEventBus.emit({
        type: 'MODULE_DEACTIVATED',
        moduleId: shipId,
        moduleType: 'radar' as ModuleType,
        timestamp: Date.now(),
      });
    }
  }

  public assignExplorationTask(
    shipId: string,
    sectorId: string,
    position: Position,
    specialization: 'mapping' | 'anomaly' | 'resource'
  ): void {
    const ship = this.ships.get(shipId);
    if (!ship || !ship.capabilities.canScan) {
      return;
    }

    // Check for threats before assigning task
    const threats = this.getNearbyThreats(position, ship.sensors.range);
    const threatLevel = this.calculateThreatLevel(threats, ship);

    if (threatLevel > 0.7) {
      // High threat - initiate evasion
      this.assignEvasionTask(shipId, position, threats);
      return;
    }

    const task: ExplorationTask = {
      id: `explore-${sectorId}`,
      type: threatLevel > 0.3 ? 'investigate' : 'explore',
      target: {
        id: sectorId,
        position,
      },
      priority: this.getPriorityForSpecialization(specialization),
      assignedAt: Date.now(),
      specialization,
      status: 'queued',
      threatLevel,
    };

    this.tasks.set(shipId, task);
    this.updateShipStatus(shipId, 'scanning');

    // Emit events
    this.emit('taskAssigned', { shipId, task });
    moduleEventBus.emit({
      type: 'AUTOMATION_STARTED',
      moduleId: shipId,
      moduleType: 'radar' as ModuleType,
      timestamp: Date.now(),
      data: { task },
    });
  }

  private assignEvasionTask(shipId: string, position: Position, threats: Threat[]): void {
    const ship = this.ships.get(shipId);
    if (!ship) return;

    // Calculate safe position away from threats
    const safePosition = this.calculateSafePosition(position, threats);

    const task: ExplorationTask = {
      id: `evade-${Date.now()}`,
      type: 'evade',
      target: {
        id: 'safe-zone',
        position: safePosition,
      },
      priority: 10, // Highest priority for evasion
      assignedAt: Date.now(),
      specialization: 'mapping',
      status: 'queued',
      threatLevel: 1,
    };

    this.tasks.set(shipId, task);
    this.updateShipStatus(shipId, 'returning');
    this.toggleShipStealth(shipId, true);

    this.emit('taskAssigned', { shipId, task });
  }

  private calculateSafePosition(position: Position, threats: Threat[]): Position {
    // Calculate vector away from threats
    const escapeVector = threats.reduce(
      (vec, threat) => {
        const dx = position.x - threat.position.x;
        const dy = position.y - threat.position.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        return {
          x: vec.x + (dx / distance) * threat.severity,
          y: vec.y + (dy / distance) * threat.severity,
        };
      },
      { x: 0, y: 0 }
    );

    // Normalize and scale
    const magnitude = Math.sqrt(escapeVector.x * escapeVector.x + escapeVector.y * escapeVector.y);
    const safeDistance = 500; // Base safe distance

    return {
      x: position.x + (escapeVector.x / magnitude) * safeDistance,
      y: position.y + (escapeVector.y / magnitude) * safeDistance,
    };
  }

  public completeTask(shipId: string): void {
    const task = this.tasks.get(shipId);
    const ship = this.ships.get(shipId);

    if (task && ship) {
      // Update sector data
      const sector = this.sectors.get(task.target.id);
      if (sector) {
        sector.explored = true;
        ship.discoveries.mappedSectors++;

        // Enhanced specialization handling
        if (task.specialization === 'anomaly') {
          const anomalies = this.generateAnomalies(ship.sensors.anomalyDetection);
          sector.anomalies = anomalies;
          ship.discoveries.anomaliesFound += anomalies.length;

          // Emit anomaly discoveries
          anomalies.forEach(anomaly => {
            this.emit('anomalyDiscovered', {
              shipId,
              anomaly,
              position: task.target.position,
            });
          });

          // Emit mission event
          moduleEventBus.emit({
            type: 'MISSION_COMPLETED',
            moduleId: shipId,
            moduleType: 'radar' as ModuleType,
            timestamp: Date.now(),
            data: {
              type: 'anomaly',
              sector: task.target.id,
              importance: anomalies.some(a => a.severity === 'high')
                ? 'high'
                : anomalies.some(a => a.severity === 'medium')
                  ? 'medium'
                  : 'low',
              description: `Discovered ${anomalies.length} anomalies in ${task.target.id}`,
              xpGained: this.calculateExperienceGain(task, ship),
              anomalyDetails: anomalies.map(a => ({
                type: a.type,
                severity: a.severity,
                investigated: a.investigated,
              })),
            },
          });
        } else if (task.specialization === 'resource') {
          sector.resources = Math.floor(Math.random() * 5) + 1;
          ship.discoveries.resourcesLocated += sector.resources;

          // Emit mission event
          moduleEventBus.emit({
            type: 'MISSION_COMPLETED',
            moduleId: shipId,
            moduleType: 'radar' as ModuleType,
            timestamp: Date.now(),
            data: {
              type: 'discovery',
              sector: task.target.id,
              importance: sector.resources > 3 ? 'high' : sector.resources > 1 ? 'medium' : 'low',
              description: `Located ${sector.resources} resource deposits in ${task.target.id}`,
              xpGained: this.calculateExperienceGain(task, ship),
              resourcesFound: [{ type: 'Unknown', amount: sector.resources }],
            },
          });
        }
      }

      // Enhanced experience calculation
      const experienceGained = this.calculateExperienceGain(task, ship);
      ship.experience += experienceGained;

      // Apply tech bonuses based on experience
      this.updateTechBonuses(ship);

      this.tasks.delete(shipId);
      this.updateShipStatus(shipId, 'returning');

      // Emit events
      this.emit('taskCompleted', { shipId, task });
      moduleEventBus.emit({
        type: 'AUTOMATION_CYCLE_COMPLETE',
        moduleId: shipId,
        moduleType: 'radar' as ModuleType,
        timestamp: Date.now(),
        data: {
          task,
          discoveries: ship.discoveries,
          experienceGained,
          heatMapValue: this.calculateHeatMapValue(sector),
        },
      });
    }
  }

  private calculateHeatMapValue(sector: any): number {
    if (!sector) return 0;

    let heatValue = 0;

    // Resource potential contribution (40%)
    heatValue += (sector.resources || 0) * 0.4;

    // Anomaly contribution (30%)
    if (sector.anomalies) {
      const anomalyHeat = sector.anomalies.reduce((sum: number, anomaly: Anomaly) => {
        return (
          sum + (anomaly.severity === 'high' ? 0.3 : anomaly.severity === 'medium' ? 0.2 : 0.1)
        );
      }, 0);
      heatValue += anomalyHeat;
    }

    // Habitability contribution (30%)
    if (sector.habitabilityScore) {
      heatValue += sector.habitabilityScore * 0.3;
    }

    // Age decay
    if (sector.lastScanned) {
      const hoursSinceLastScan = (Date.now() - sector.lastScanned) / (1000 * 60 * 60);
      const ageFactor = Math.max(0, 1 - hoursSinceLastScan / 168); // 168 hours = 1 week
      heatValue *= ageFactor;
    }

    return Math.min(1, heatValue);
  }

  private generateAnomalies(detectionSkill: number): Anomaly[] {
    const count = Math.floor(Math.random() * 3 * detectionSkill) + 1;
    const anomalies: Anomaly[] = [];
    const anomalyTypes = ['artifact', 'signal', 'phenomenon'] as const;
    const severityLevels = ['high', 'medium', 'low'] as const;

    for (let i = 0; i < count; i++) {
      const type = anomalyTypes[Math.floor(Math.random() * anomalyTypes.length)];
      const severity = Math.random() < 0.2 ? 'high' : Math.random() < 0.5 ? 'medium' : 'low';

      anomalies.push({
        id: `anomaly-${Date.now()}-${i}`,
        type,
        severity,
        description: this.generateAnomalyDescription(type, severity),
        investigated: false,
      });
    }

    return anomalies;
  }

  private generateAnomalyDescription(
    type: 'artifact' | 'signal' | 'phenomenon',
    severity: 'low' | 'medium' | 'high'
  ): string {
    const descriptions = {
      artifact: {
        high: 'Ancient technological marvel of immense power',
        medium: 'Mysterious alien artifacts with unknown purpose',
        low: 'Scattered remnants of past civilizations',
      },
      signal: {
        high: 'Powerful energy signature of unknown origin',
        medium: 'Recurring patterns in subspace frequencies',
        low: 'Faint echoes of distant transmissions',
      },
      phenomenon: {
        high: 'Dangerous spatial anomaly requiring immediate attention',
        medium: 'Unusual gravitational fluctuations detected',
        low: 'Minor disturbances in local space-time',
      },
    } as const;

    return descriptions[type][severity];
  }

  private calculateExperienceGain(task: ExplorationTask, ship: ReconShip): number {
    const baseXP = 100;
    const timeFactor = (Date.now() - task.assignedAt) / 1000 / 60; // Minutes

    // Specialization bonus
    const specializationBonus = task.specialization === ship.specialization ? 1.5 : 1.2;

    // Threat bonus
    const threatBonus = task.threatLevel ? 1 + task.threatLevel : 1;

    // Efficiency bonus
    const efficiencyBonus = ship.efficiency;

    // Discovery bonus
    const discoveryBonus =
      ship.discoveries.anomaliesFound * 0.1 + ship.discoveries.resourcesLocated * 0.05;

    return Math.floor(
      baseXP *
        timeFactor *
        specializationBonus *
        threatBonus *
        efficiencyBonus *
        (1 + discoveryBonus)
    );
  }

  private updateTechBonuses(ship: ReconShip): void {
    const experienceLevel = Math.floor(ship.experience / 1000);
    const bonuses = {
      scanSpeed: 1 + experienceLevel * 0.1,
      stealthEfficiency: 1 + experienceLevel * 0.15,
      detectionRange: 1 + experienceLevel * 0.05,
    };

    this.emit('techBonusesUpdated', { shipId: ship.id, bonuses });
  }

  public updateShipTechBonuses(
    shipId: string,
    bonuses: { scanSpeed: number; stealthEfficiency: number; detectionRange: number }
  ): void {
    const ship = this.ships.get(shipId);
    if (ship) {
      ship.techBonuses = bonuses;
      this.emit('techBonusesUpdated', { shipId, bonuses });
    }
  }

  public toggleShipStealth(shipId: string, active: boolean): void {
    const ship = this.ships.get(shipId);
    if (ship && ship.stealth.cooldown <= 0) {
      ship.stealth.active = active;
      this.emit('stealthToggled', { shipId, active: ship.stealth.active });
    }
  }

  private updateShipStatus(
    shipId: string,
    status: 'idle' | 'scanning' | 'investigating' | 'returning'
  ): void {
    const ship = this.ships.get(shipId);
    if (ship) {
      ship.status = status;

      // Emit events
      this.emit('shipStatusUpdated', { shipId, status });
      moduleEventBus.emit({
        type: 'STATUS_CHANGED',
        moduleId: shipId,
        moduleType: 'radar' as ModuleType,
        timestamp: Date.now(),
        data: { status },
      });
    }
  }

  private getPriorityForSpecialization(specialization: 'mapping' | 'anomaly' | 'resource'): number {
    switch (specialization) {
      case 'anomaly':
        return 3;
      case 'resource':
        return 2;
      case 'mapping':
        return 1;
      default:
        return 0;
    }
  }

  // Add method to get ship efficiency with tech bonuses
  public getShipEfficiency(shipId: string): number {
    const ship = this.ships.get(shipId);
    if (!ship) {
      return 0;
    }

    const baseEfficiency = ship.efficiency;
    const techBonus = ship.techBonuses?.scanSpeed || 1;

    return baseEfficiency * techBonus;
  }

  // Update method for periodic tasks
  public update(deltaTime: number): void {
    this.ships.forEach(ship => {
      // Update stealth cooldown
      if (ship.stealth.cooldown > 0) {
        ship.stealth.cooldown = Math.max(0, ship.stealth.cooldown - deltaTime);
      }

      // Apply tech bonuses to sensor range
      if (ship.techBonuses) {
        ship.sensors.range *= ship.techBonuses.detectionRange;
      }

      // Handle automatic stealth activation near threats
      if (this.isNearThreat(ship) && !ship.stealth.active && ship.stealth.cooldown <= 0) {
        this.toggleShipStealth(ship.id, true);
      }
    });
  }

  private isNearThreat(ship: ReconShip): boolean {
    // Get threats within ship's sensor range
    const threats = combatManager.getThreatsInRange(ship.position, ship.sensors.range);

    if (threats.length === 0) {
      return false;
    }

    // Calculate threat level based on proximity and severity
    const threatLevel = threats.reduce((total, threat) => {
      const distance = Math.sqrt(
        Math.pow(threat.position.x - ship.position.x, 2) +
          Math.pow(threat.position.y - ship.position.y, 2)
      );

      // Scale threat by distance and severity
      const distanceFactor = 1 - Math.min(distance / ship.sensors.range, 1);
      const severityFactor =
        threat.severity === 'high' ? 1 : threat.severity === 'medium' ? 0.6 : 0.3;

      // Apply sensor accuracy to detection reliability
      const detectionChance = ship.sensors.accuracy * (ship.techBonuses?.detectionRange || 1);

      return total + distanceFactor * severityFactor * detectionChance;
    }, 0);

    // Return true if threat level exceeds threshold
    // Scale threshold based on ship's stealth capabilities
    const stealthFactor = (ship.stealth.level / 100) * (ship.techBonuses?.stealthEfficiency || 1);
    const baseThreshold = 0.3; // Base threshold for threat response

    return threatLevel > baseThreshold * (1 + stealthFactor);
  }

  private getNearbyThreats(position: Position, range: number): Threat[] {
    return combatManager.getThreatsInRange(position, range).map(threat => ({
      id: threat.id,
      position: threat.position,
      severity: threat.severity === 'high' ? 1 : threat.severity === 'medium' ? 0.6 : 0.3,
      type: threat.type,
    }));
  }

  private calculateThreatLevel(threats: Threat[], ship: ReconShip): number {
    if (threats.length === 0) return 0;

    return threats.reduce((total, threat) => {
      const distance = Math.sqrt(
        Math.pow(threat.position.x - ship.position.x, 2) +
          Math.pow(threat.position.y - ship.position.y, 2)
      );

      const distanceFactor = 1 - Math.min(distance / ship.sensors.range, 1);
      const detectionChance = ship.sensors.accuracy * (ship.techBonuses?.detectionRange || 1);

      return total + distanceFactor * threat.severity * detectionChance;
    }, 0);
  }
}

import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import { EventEmitter } from '../../lib/utils/EventEmitter';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import { Position } from '../../types/core/GameTypes';
import { ResourceType } from '../../types/resources/ResourceTypes';

interface Hazard {
  id: string;
  type: 'asteroids' | 'debris' | 'radiation' | 'anomaly';
  position: Position;
  radius: number;
  severity: 'low' | 'medium' | 'high';
  effect: {
    type: 'damage' | 'slow' | 'shield' | 'weapon';
    value: number;
  };
  movement?: {
    speed: number;
    direction: number;
  };
  particles?: number;
}

interface AsteroidField {
  id: string;
  position: Position;
  radius: number;
  density: number;
  resources: Map<ResourceType, number>;
  hazards: Array<Hazard>;
  status: 'active' | 'depleted';
  createdAt: number;
  lastUpdated: number;
}

interface AsteroidFieldState {
  fields: Map<string, AsteroidField>;
  activeHazards: Set<string>;
  resourceNodes: Map<string, { fieldId: string; type: ResourceType; amount: number }>;
}

interface AsteroidFieldEvents {
  fieldGenerated: { fieldId: string; position: Position };
  fieldDepleted: { fieldId: string };
  resourceDiscovered: { fieldId: string; resourceType: ResourceType; amount: number };
  hazardCreated: { fieldId: string; hazard: Hazard };
  hazardRemoved: { fieldId: string; hazardId: string };
  resourceNodeRegistered: {
    nodeId: string;
    fieldId: string;
    type: ResourceType;
    position: Position;
  };
  resourceExtracted: { nodeId: string; type: ResourceType; amount: number; remaining: number };
  shipHazardCollision: { shipId: string; hazardId: string; effect: Hazard['effect'] };
  shipPositionUpdated: { shipId: string; position: Position; inField: boolean };
}

export class AsteroidFieldManager extends EventEmitter<AsteroidFieldEvents> {
  private state: AsteroidFieldState = {
    fields: new Map(),
    activeHazards: new Set(),
    resourceNodes: new Map(),
  };
  private updateInterval: NodeJS.Timeout | null = null;

  constructor() {
    super();
    this.setupEventListeners();
    this.startUpdateLoop();

    // Debug logging
    console.debug('[AsteroidFieldManager] Initialized');
  }

  private setupEventListeners(): void {
    moduleEventBus.subscribe(
      'MODULE_ACTIVATED',
      (event: { moduleType: string; moduleId: string }) => {
        if (event.moduleType === 'asteroidField') {
          this.handleModuleActivation(event.moduleId);
        }
      }
    );

    moduleEventBus.subscribe(
      'MODULE_DEACTIVATED',
      (event: { moduleType: string; moduleId: string }) => {
        if (event.moduleType === 'asteroidField') {
          this.handleModuleDeactivation(event.moduleId);
        }
      }
    );
  }

  private startUpdateLoop(): void {
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
    }

    this.updateInterval = setInterval(() => {
      this.updateFields();
    }, 1000); // Update every second
  }

  private updateFields(): void {
    const now = Date.now();
    this.state.fields.forEach((field, fieldId) => {
      if (field.status === 'depleted') {
        return;
      }

      // Update hazards
      field.hazards = field.hazards.filter(hazard => {
        const isActive = this.state.activeHazards.has(hazard.id);
        if (!isActive) {
          this.emit('hazardRemoved', { fieldId, hazardId: hazard.id });
        }
        return isActive;
      });

      // Generate new hazards if needed
      if (field.hazards.length < Math.ceil(field.density * 5)) {
        const hazard = this.generateHazard(field);
        field.hazards.push(hazard);
        this.state.activeHazards.add(hazard.id);
        this.emit('hazardCreated', { fieldId, hazard });
      }

      // Update resources
      let totalResources = 0;
      field.resources.forEach((amount, type) => {
        totalResources += amount;
      });

      if (totalResources <= 0) {
        field.status = 'depleted';
        this.emit('fieldDepleted', { fieldId });
      }

      field.lastUpdated = now;
    });
  }

  private generateHazard(field: AsteroidField): Hazard {
    const angle = Math.random() * Math.PI * 2;
    const distance = Math.random() * field.radius;
    const position = {
      x: field.position.x + Math.cos(angle) * distance,
      y: field.position.y + Math.sin(angle) * distance,
    };

    return {
      id: `hazard-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      type: 'asteroids',
      position,
      radius: 20 + Math.random() * 30,
      severity: Math.random() > 0.7 ? 'high' : Math.random() > 0.4 ? 'medium' : 'low',
      effect: {
        type: 'damage',
        value: 10 + Math.random() * 20,
      },
      movement: {
        speed: 0.5 + Math.random(),
        direction: Math.random() * Math.PI * 2,
      },
    };
  }

  public generateField(position: Position, radius: number, density: number): string {
    const fieldId = `field-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    const field: AsteroidField = {
      id: fieldId,
      position,
      radius,
      density: Math.max(0.1, Math.min(1, density)),
      resources: new Map(),
      hazards: [],
      status: 'active',
      createdAt: Date.now(),
      lastUpdated: Date.now(),
    };

    // Generate initial resources
    const resourceTypes: ResourceType[] = ['minerals', 'gas', 'exotic'];
    resourceTypes.forEach(type => {
      const amount = Math.floor(1000 + Math.random() * 4000 * density);
      field.resources.set(type, amount);

      const nodeId = `node-${fieldId}-${type}`;
      this.state.resourceNodes.set(nodeId, {
        fieldId,
        type,
        amount,
      });

      this.emit('resourceDiscovered', {
        fieldId,
        resourceType: type,
        amount,
      });
    });

    // Generate initial hazards
    for (let i = 0; i < Math.ceil(density * 5); i++) {
      const hazard = this.generateHazard(field);
      field.hazards.push(hazard);
      this.state.activeHazards.add(hazard.id);
      this.emit('hazardCreated', { fieldId, hazard });
    }

    this.state.fields.set(fieldId, field);
    this.emit('fieldGenerated', { fieldId, position });

    // Debug logging
    console.debug(
      `[AsteroidFieldManager] Generated field ${fieldId} at (${position.x}, ${position.y}) with density ${density}`
    );

    return fieldId;
  }

  public getField(fieldId: string): AsteroidField | undefined {
    return this.state.fields.get(fieldId);
  }

  public getAllFields(): AsteroidField[] {
    return Array.from(this.state.fields.values());
  }

  public getActiveFields(): AsteroidField[] {
    return Array.from(this.state.fields.values()).filter(field => field.status === 'active');
  }

  public getResourceNodes(): Map<string, { fieldId: string; type: ResourceType; amount: number }> {
    return new Map(this.state.resourceNodes);
  }

  public extractResource(nodeId: string, amount: number): boolean {
    const node = this.state.resourceNodes.get(nodeId);
    if (!node || amount <= 0) {
      return false;
    }

    const field = this.state.fields.get(node.fieldId);
    if (!field || field.status === 'depleted') {
      return false;
    }

    const currentAmount = field.resources.get(node.type) || 0;
    if (currentAmount < amount) {
      return false;
    }

    field.resources.set(node.type, currentAmount - amount);
    node.amount = currentAmount - amount;

    // Debug logging
    console.debug(`[AsteroidFieldManager] Extracted ${amount} ${node.type} from node ${nodeId}`);

    return true;
  }

  private handleModuleActivation(moduleId: string): void {
    console.debug(`[AsteroidFieldManager] Module ${moduleId} activated`);
  }

  private handleModuleDeactivation(moduleId: string): void {
    console.debug(`[AsteroidFieldManager] Module ${moduleId} deactivated`);
  }

  public cleanup(): void {
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
      this.updateInterval = null;
    }

    this.state.fields.clear();
    this.state.activeHazards.clear();
    this.state.resourceNodes.clear();

    // Debug logging
    console.debug('[AsteroidFieldManager] Cleanup completed');
  }

  public registerResourceNode(fieldId: string, type: ResourceType): string {
    const field = this.state.fields.get(fieldId);
    if (!field) {
      return '';
    }

    const nodeId = `node-${fieldId}-${type}-${Date.now()}`;
    const amount = field.resources.get(type) || 0;

    this.state.resourceNodes.set(nodeId, {
      fieldId,
      type,
      amount,
    });

    // Register with mining system
    moduleEventBus.emit({
      type: 'MODULE_ACTIVATED',
      moduleId: nodeId,
      moduleType: 'mineral' as ModuleType,
      timestamp: Date.now(),
      data: {
        id: nodeId,
        type,
        position: field.position,
        amount,
        thresholds: {
          min: Math.floor(amount * 0.2),
          max: amount,
        },
      },
    });

    this.emit('resourceNodeRegistered', {
      nodeId,
      fieldId,
      type,
      position: field.position,
    });

    console.debug(
      `[AsteroidFieldManager] Registered resource node ${nodeId} of type ${type} in field ${fieldId}`
    );
    return nodeId;
  }

  public handleMiningTask(nodeId: string, shipId: string, amount: number): boolean {
    const node = this.state.resourceNodes.get(nodeId);
    if (!node) {
      console.debug(`[AsteroidFieldManager] Mining task failed: Node ${nodeId} not found`);
      return false;
    }

    const field = this.state.fields.get(node.fieldId);
    if (!field || field.status === 'depleted') {
      console.debug(
        `[AsteroidFieldManager] Mining task failed: Field ${node.fieldId} not found or depleted`
      );
      return false;
    }

    const extracted = this.extractResource(nodeId, amount);
    if (!extracted) {
      console.debug(
        `[AsteroidFieldManager] Mining task failed: Could not extract ${amount} resources from node ${nodeId}`
      );
      return false;
    }

    const remaining = field.resources.get(node.type) || 0;
    this.emit('resourceExtracted', {
      nodeId,
      type: node.type,
      amount,
      remaining,
    });

    // Update mining system
    moduleEventBus.emit({
      type: 'RESOURCE_TRANSFERRED',
      moduleId: nodeId,
      moduleType: 'mineral' as ModuleType,
      timestamp: Date.now(),
      data: {
        shipId,
        amount,
        remaining,
        resourceType: node.type,
      },
    });

    console.debug(
      `[AsteroidFieldManager] Mining task completed: Extracted ${amount} ${node.type} from node ${nodeId}, ${remaining} remaining`
    );
    return true;
  }

  public getNodeThresholds(nodeId: string): { min: number; max: number } | null {
    const node = this.state.resourceNodes.get(nodeId);
    if (!node) {
      return null;
    }

    const field = this.state.fields.get(node.fieldId);
    if (!field) {
      return null;
    }

    const amount = field.resources.get(node.type) || 0;
    return {
      min: Math.floor(amount * 0.2),
      max: amount,
    };
  }

  public handleShipMovement(shipId: string, position: Position): void {
    let inField = false;
    let nearestHazard: Hazard | null = null;
    let nearestDistance = Infinity;

    // Convert Map entries to array to avoid MapIterator error
    const fieldValues = Array.from(this.state.fields.values());
    for (const field of fieldValues) {
      // Calculate distance to field center
      const distanceToField = Math.sqrt(
        Math.pow(position.x - field.position.x, 2) + Math.pow(position.y - field.position.y, 2)
      );

      if (distanceToField <= field.radius) {
        inField = true;

        // Find nearest hazard in field
        for (const hazard of field.hazards) {
          const distanceToHazard = Math.sqrt(
            Math.pow(position.x - hazard.position.x, 2) +
              Math.pow(position.y - hazard.position.y, 2)
          );

          if (distanceToHazard < nearestDistance) {
            nearestDistance = distanceToHazard;
            nearestHazard = hazard;
          }
        }
      }
    }

    // Emit position update
    this.emit('shipPositionUpdated', { shipId, position, inField });

    // Handle hazard collision if ship is near a hazard
    if (nearestHazard && nearestDistance <= nearestHazard.radius) {
      this.handleHazardCollision(shipId, nearestHazard);
    }

    console.debug(
      `[AsteroidFieldManager] Ship ${shipId} position updated: (${position.x}, ${position.y}), in field: ${inField}`
    );
  }

  public calculateAvoidanceVector(shipId: string, position: Position): Position | null {
    let nearestHazard: Hazard | null = null;
    let nearestDistance = Infinity;

    // Find nearest hazard across all fields
    // Convert Map entries to array to avoid MapIterator error
    const fieldValues = Array.from(this.state.fields.values());
    for (const field of fieldValues) {
      for (const hazard of field.hazards) {
        const distance = Math.sqrt(
          Math.pow(position.x - hazard.position.x, 2) + Math.pow(position.y - hazard.position.y, 2)
        );

        if (distance < nearestDistance) {
          nearestDistance = distance;
          nearestHazard = hazard;
        }
      }
    }

    // If no hazard is nearby or distance is safe, return null
    if (!nearestHazard || nearestDistance > nearestHazard.radius * 1.5) {
      return null;
    }

    // Calculate avoidance vector
    const dx = position.x - nearestHazard.position.x;
    const dy = position.y - nearestHazard.position.y;
    const distance = Math.sqrt(dx * dx + dy * dy);

    // Return position that moves away from hazard
    return {
      x: position.x + (dx / distance) * 50, // Move 50 units away
      y: position.y + (dy / distance) * 50,
    };
  }

  private handleHazardCollision(shipId: string, hazard: Hazard): void {
    this.emit('shipHazardCollision', {
      shipId,
      hazardId: hazard.id,
      effect: hazard.effect,
    });

    // Update hazard state if needed
    if (hazard.type === 'asteroids') {
      // Asteroids might break apart or move after collision
      const newPosition = {
        x: hazard.position.x + (Math.random() - 0.5) * 20,
        y: hazard.position.y + (Math.random() - 0.5) * 20,
      };

      hazard.position = newPosition;

      // Sometimes create smaller hazards after collision
      if (Math.random() < 0.3 && hazard.radius > 20) {
        const newHazard: Hazard = {
          id: `${hazard.id}_fragment_${Date.now()}`,
          type: hazard.type,
          severity: hazard.severity,
          radius: hazard.radius * 0.5,
          position: newPosition,
          effect: hazard.effect,
          movement: hazard.movement,
          particles: hazard.particles,
        };

        // Add to parent field
        // Convert Map entries to array to avoid MapIterator error
        const fieldEntries = Array.from(this.state.fields.entries());
        for (const [fieldId, field] of fieldEntries) {
          if (field.hazards.some(h => h.id === hazard.id)) {
            field.hazards.push(newHazard);
            this.state.activeHazards.add(newHazard.id);
            this.emit('hazardCreated', { fieldId, hazard: newHazard });
            break;
          }
        }
      }
    }

    console.debug(
      `[AsteroidFieldManager] Ship ${shipId} collided with hazard ${hazard.id} of type ${hazard.type}`
    );
  }

  public getShipBonuses(
    shipId: string,
    position: Position
  ): {
    speedMultiplier: number;
    hazardResistance: number;
    miningEfficiency: number;
  } {
    const bonuses = {
      speedMultiplier: 1.0,
      hazardResistance: 1.0,
      miningEfficiency: 1.0,
    };

    // Check if ship is in any asteroid field
    // Convert Map entries to array to avoid MapIterator error
    const fieldValues = Array.from(this.state.fields.values());
    for (const field of fieldValues) {
      const distanceToField = Math.sqrt(
        Math.pow(position.x - field.position.x, 2) + Math.pow(position.y - field.position.y, 2)
      );

      if (distanceToField <= field.radius) {
        // Apply field density effects
        bonuses.speedMultiplier *= Math.max(0.5, 1 - field.density * 0.5);
        bonuses.hazardResistance *= Math.max(0.6, 1 - field.density * 0.4);
        bonuses.miningEfficiency *= 1 + field.density * 0.3;

        // Log the bonuses
        console.debug(`[AsteroidFieldManager] Ship ${shipId} field bonuses:
          Speed: ${bonuses.speedMultiplier.toFixed(2)}x
          Hazard Resistance: ${bonuses.hazardResistance.toFixed(2)}x
          Mining Efficiency: ${bonuses.miningEfficiency.toFixed(2)}x`);

        break; // Only apply bonuses from one field
      }
    }

    return bonuses;
  }
}

import { ModuleEventType, moduleEventBus } from '../../lib/modules/ModuleEvents';

/**
 * Update priority levels
 */
export enum UpdatePriority {
  CRITICAL = 0, // Run every frame, no matter what (physics, core game state)
  HIGH = 1, // Run most frames, can be skipped occasionally (AI, combat)
  NORMAL = 2, // Run regularly, can be skipped more often (resource updates)
  LOW = 3, // Run occasionally (visual effects, non-critical updates)
  BACKGROUND = 4, // Run when there's spare time (analytics, cleanup)
}

/**
 * Update callback type
 */
export type UpdateCallback = (deltaTime: number, elapsedTime: number) => void;

/**
 * Update registration interface
 */
export interface UpdateRegistration {
  id: string;
  callback: UpdateCallback;
  priority: UpdatePriority;
  interval?: number; // Optional interval in ms (for fixed timestep updates)
  lastUpdate?: number; // Last time this was updated
}

/**
 * Game loop statistics
 */
export interface GameLoopStats {
  fps: number;
  frameTime: number;
  updateTime: number;
  renderTime: number;
  idleTime: number;
  elapsedTime: number;
  frameCount: number;
  skippedFrames: number;
  priorityStats: Record<
    UpdatePriority,
    {
      count: number;
      totalTime: number;
      averageTime: number;
    }
  >;
}

/**
 * Game loop manager configuration
 */
export interface GameLoopConfig {
  targetFPS: number;
  maxDeltaTime: number;
  priorityThrottling: boolean;
  fixedTimestep: boolean;
  throttlePriorities: UpdatePriority[];
  statsInterval: number;
  enableStats: boolean;
}

/**
 * Game loop manager for centralized timing and updates
 */
export class GameLoopManager {
  private updates: Map<string, UpdateRegistration> = new Map();
  private running: boolean = false;
  private lastFrameTime: number = 0;
  private frameCount: number = 0;
  private skippedFrames: number = 0;
  private elapsedTime: number = 0;
  private animationFrameId: number | null = null;
  private statsInterval: number | null = null;
  private stats: GameLoopStats;
  private config: GameLoopConfig;

  constructor(config: Partial<GameLoopConfig> = {}) {
    // Default configuration
    this.config = {
      targetFPS: 60,
      maxDeltaTime: 1000 / 30, // Cap at 30 FPS equivalent
      priorityThrottling: true,
      fixedTimestep: false,
      throttlePriorities: [UpdatePriority.LOW, UpdatePriority.BACKGROUND],
      statsInterval: 1000, // 1 second
      enableStats: true,
      ...config,
    };

    // Initialize stats
    this.stats = {
      fps: 0,
      frameTime: 0,
      updateTime: 0,
      renderTime: 0,
      idleTime: 0,
      elapsedTime: 0,
      frameCount: 0,
      skippedFrames: 0,
      priorityStats: {
        [UpdatePriority.CRITICAL]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.HIGH]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.NORMAL]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.LOW]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.BACKGROUND]: { count: 0, totalTime: 0, averageTime: 0 },
      },
    };
  }

  /**
   * Start the game loop
   */
  public start(): void {
    if (this.running) {
      return;
    }

    this.running = true;
    this.lastFrameTime = performance.now();
    this.frameCount = 0;
    this.skippedFrames = 0;
    this.elapsedTime = 0;

    // Start the game loop
    this.animationFrameId = requestAnimationFrame(this.gameLoop);

    // Start stats reporting if enabled
    if (this.config.enableStats) {
      this.statsInterval = window.setInterval(() => {
        this.reportStats();
      }, this.config.statsInterval);
    }

    // Emit game loop started event
    moduleEventBus.emit({
      type: 'GAME_LOOP_STARTED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { config: this.config },
    });
  }

  /**
   * Stop the game loop
   */
  public stop(): void {
    if (!this.running) {
      return;
    }

    this.running = false;

    // Stop the animation frame
    if (this.animationFrameId !== null) {
      cancelAnimationFrame(this.animationFrameId);
      this.animationFrameId = null;
    }

    // Stop stats reporting
    if (this.statsInterval !== null) {
      clearInterval(this.statsInterval);
      this.statsInterval = null;
    }

    // Emit game loop stopped event
    moduleEventBus.emit({
      type: 'GAME_LOOP_STOPPED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { stats: this.stats },
    });
  }

  /**
   * Register an update callback
   */
  public registerUpdate(
    id: string,
    callback: UpdateCallback,
    priority: UpdatePriority = UpdatePriority.NORMAL,
    interval?: number
  ): void {
    this.updates.set(id, {
      id,
      callback,
      priority,
      interval,
      lastUpdate: performance.now(),
    });

    // Emit update registered event
    moduleEventBus.emit({
      type: 'GAME_LOOP_UPDATE_REGISTERED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { id, priority, interval },
    });
  }

  /**
   * Unregister an update callback
   */
  public unregisterUpdate(id: string): void {
    if (this.updates.has(id)) {
      this.updates.delete(id);

      // Emit update unregistered event
      moduleEventBus.emit({
        type: 'GAME_LOOP_UPDATE_UNREGISTERED' as ModuleEventType,
        moduleId: 'game-loop-manager',
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        data: { id },
      });
    }
  }

  /**
   * Get the current game loop stats
   */
  public getStats(): GameLoopStats {
    return { ...this.stats };
  }

  /**
   * Update the game loop configuration
   */
  public updateConfig(config: Partial<GameLoopConfig>): void {
    this.config = {
      ...this.config,
      ...config,
    };

    // Emit config updated event
    moduleEventBus.emit({
      type: 'GAME_LOOP_CONFIG_UPDATED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { config: this.config },
    });
  }

  /**
   * The main game loop
   */
  private gameLoop = (timestamp: number): void => {
    if (!this.running) {
      return;
    }

    // Schedule the next frame
    this.animationFrameId = requestAnimationFrame(this.gameLoop);

    // Calculate delta time
    const now = timestamp;
    let deltaTime = now - this.lastFrameTime;
    this.lastFrameTime = now;

    // Cap delta time to prevent spiral of death
    if (deltaTime > this.config.maxDeltaTime) {
      deltaTime = this.config.maxDeltaTime;
      this.skippedFrames++;
    }

    // Update elapsed time
    this.elapsedTime += deltaTime;
    this.frameCount++;

    // Update frame time stats
    this.stats.frameTime = deltaTime;

    // Start update timing
    const updateStartTime = performance.now();

    // Process updates by priority
    this.processUpdates(deltaTime, this.elapsedTime);

    // End update timing
    const updateEndTime = performance.now();
    this.stats.updateTime = updateEndTime - updateStartTime;

    // Calculate idle time (time left in the frame)
    const targetFrameTime = 1000 / this.config.targetFPS;
    const currentFrameTime = performance.now() - now;
    this.stats.idleTime = Math.max(0, targetFrameTime - currentFrameTime);

    // Update FPS stats
    this.stats.fps = 1000 / deltaTime;
    this.stats.elapsedTime = this.elapsedTime;
    this.stats.frameCount = this.frameCount;
    this.stats.skippedFrames = this.skippedFrames;
  };

  /**
   * Process all registered updates
   */
  private processUpdates(deltaTime: number, elapsedTime: number): void {
    // Group updates by priority
    const priorityGroups: Map<UpdatePriority, UpdateRegistration[]> = new Map();

    for (const update of this.updates.values()) {
      if (!priorityGroups.has(update.priority)) {
        priorityGroups.set(update.priority, []);
      }
      priorityGroups.get(update.priority)!.push(update);
    }

    // Process each priority group
    for (
      let priority = UpdatePriority.CRITICAL;
      priority <= UpdatePriority.BACKGROUND;
      priority++
    ) {
      const updates = priorityGroups.get(priority) || [];

      // Skip throttled priorities if enabled
      if (
        this.config.priorityThrottling &&
        this.config.throttlePriorities.includes(priority) &&
        this.frameCount % (priority + 1) !== 0
      ) {
        continue;
      }

      // Process all updates in this priority group
      for (const update of updates) {
        // Skip if interval is set and not enough time has passed
        if (update.interval && update.lastUpdate) {
          const timeSinceLastUpdate = elapsedTime - update.lastUpdate;
          if (timeSinceLastUpdate < update.interval) {
            continue;
          }
        }

        // Update the last update time
        update.lastUpdate = elapsedTime;

        // Measure update time
        const startTime = performance.now();

        try {
          // Call the update callback
          update.callback(deltaTime, elapsedTime);
        } catch (error) {
          console.error(`Error in update ${update.id}:`, error);

          // Emit error event
          moduleEventBus.emit({
            type: 'ERROR_OCCURRED' as ModuleEventType,
            moduleId: 'game-loop-manager',
            moduleType: 'resource-manager',
            timestamp: Date.now(),
            data: {
              error,
              updateId: update.id,
              priority: update.priority,
            },
          });
        }

        // Update stats
        const endTime = performance.now();
        const updateTime = endTime - startTime;

        this.stats.priorityStats[priority].count++;
        this.stats.priorityStats[priority].totalTime += updateTime;
        this.stats.priorityStats[priority].averageTime =
          this.stats.priorityStats[priority].totalTime / this.stats.priorityStats[priority].count;
      }
    }
  }

  /**
   * Report game loop stats
   */
  private reportStats(): void {
    // Calculate average FPS
    const avgFps = this.frameCount / (this.config.statsInterval / 1000);

    // Reset frame count for next interval
    this.frameCount = 0;

    // Emit stats event
    moduleEventBus.emit({
      type: 'GAME_LOOP_STATS' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        ...this.stats,
        avgFps,
      },
    });

    // Log stats if in development
    if (process.env.NODE_ENV === 'development') {
      console.debug('[GameLoop] Stats:', {
        fps: avgFps.toFixed(2),
        updateTime: this.stats.updateTime.toFixed(2) + 'ms',
        idleTime: this.stats.idleTime.toFixed(2) + 'ms',
        skippedFrames: this.stats.skippedFrames,
      });
    }
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.stop();
    this.updates.clear();
  }
}

// Export singleton instance
export const gameLoopManager = new GameLoopManager();

import { v4 as uuidv4 } from 'uuid';
import { EntityPool } from '../../lib/optimization/EntityPool';
import { Position } from '../../types/core/GameTypes';
import { EventEmitter } from '../../utils/EventEmitter';

interface Particle {
  id: string;
  active: boolean;
  position: Position;
  velocity: Position;
  acceleration: Position;
  size: number;
  color: string;
  opacity: number;
  life: number;
  maxLife: number;
  reset(): void;
}

interface ParticleSystemEvents {
  particleSpawned: { particle: Particle };
  particleUpdated: { particle: Particle };
  particleDied: { particle: Particle };
  systemUpdated: { activeCount: number };
}

/**
 * Manages particle systems with efficient pooling
 */
export class ParticleSystemManager extends EventEmitter<ParticleSystemEvents> {
  private particlePool: EntityPool<Particle>;
  private systems: Map<string, ParticleSystem> = new Map();
  private lastUpdate: number = 0;

  constructor(initialPoolSize: number = 1000) {
    super();
    this.particlePool = new EntityPool<Particle>(() => this.createParticle(), initialPoolSize);

    // Debug logging
    console.debug(`[ParticleSystemManager] Initialized with pool size ${initialPoolSize}`);
  }

  /**
   * Create a new particle
   */
  private createParticle(): Particle {
    return {
      id: uuidv4(),
      active: false,
      position: { x: 0, y: 0 },
      velocity: { x: 0, y: 0 },
      acceleration: { x: 0, y: 0 },
      size: 1,
      color: '#ffffff',
      opacity: 1,
      life: 1,
      maxLife: 1,
      reset(): void {
        this.active = false;
        this.position = { x: 0, y: 0 };
        this.velocity = { x: 0, y: 0 };
        this.acceleration = { x: 0, y: 0 };
        this.size = 1;
        this.color = '#ffffff';
        this.opacity = 1;
        this.life = 1;
        this.maxLife = 1;
      },
    };
  }

  /**
   * Create a new particle system
   */
  public createSystem(config: ParticleSystemConfig): string {
    const id = uuidv4();
    this.systems.set(id, new ParticleSystem(this.particlePool, config));
    return id;
  }

  /**
   * Remove a particle system
   */
  public removeSystem(id: string): void {
    const system = this.systems.get(id);
    if (system) {
      system.cleanup();
      this.systems.delete(id);
    }
  }

  /**
   * Update all particle systems
   */
  public update(timestamp: number): void {
    const deltaTime = this.lastUpdate ? (timestamp - this.lastUpdate) / 1000 : 0;
    this.lastUpdate = timestamp;

    let totalActiveParticles = 0;

    this.systems.forEach(system => {
      system.update(deltaTime);
      totalActiveParticles += system.getActiveParticleCount();
    });

    this.emit('systemUpdated', { activeCount: totalActiveParticles });
  }

  /**
   * Clean up all systems
   */
  public cleanup(): void {
    this.systems.forEach(system => system.cleanup());
    this.systems.clear();
    this.particlePool.clear();
  }
}

interface ParticleSystemConfig {
  maxParticles: number;
  spawnRate: number;
  position: Position;
  spread: number;
  initialVelocity: {
    min: Position;
    max: Position;
  };
  acceleration: Position;
  size: {
    min: number;
    max: number;
  };
  life: {
    min: number;
    max: number;
  };
  color: string | string[];
  blendMode?: 'normal' | 'additive';
}

/**
 * Individual particle system instance
 */
class ParticleSystem {
  private pool: EntityPool<Particle>;
  private config: ParticleSystemConfig;
  private particles: Set<Particle> = new Set();
  private timeSinceLastSpawn: number = 0;

  constructor(pool: EntityPool<Particle>, config: ParticleSystemConfig) {
    this.pool = pool;
    this.config = config;
  }

  /**
   * Spawn a new particle
   */
  private spawnParticle(): void {
    if (this.particles.size >= this.config.maxParticles) {
      return;
    }

    const particle = this.pool.acquire();
    if (!particle) {
      return;
    }

    // Initialize particle properties
    const angle = Math.random() * Math.PI * 2;
    const distance = Math.random() * this.config.spread;

    particle.position = {
      x: this.config.position.x + Math.cos(angle) * distance,
      y: this.config.position.y + Math.sin(angle) * distance,
    };

    particle.velocity = {
      x:
        this.config.initialVelocity.min.x +
        Math.random() * (this.config.initialVelocity.max.x - this.config.initialVelocity.min.x),
      y:
        this.config.initialVelocity.min.y +
        Math.random() * (this.config.initialVelocity.max.y - this.config.initialVelocity.min.y),
    };

    particle.acceleration = { ...this.config.acceleration };
    particle.size =
      this.config.size.min + Math.random() * (this.config.size.max - this.config.size.min);

    particle.life =
      this.config.life.min + Math.random() * (this.config.life.max - this.config.life.min);
    particle.maxLife = particle.life;

    if (Array.isArray(this.config.color)) {
      const colorIndex = Math.floor(Math.random() * this.config.color.length);
      particle.color = this.config.color[colorIndex];
    } else {
      particle.color = this.config.color;
    }

    this.particles.add(particle);
  }

  /**
   * Update particle system
   */
  public update(deltaTime: number): void {
    // Spawn new particles
    this.timeSinceLastSpawn += deltaTime;
    const spawnInterval = 1 / this.config.spawnRate;

    while (this.timeSinceLastSpawn >= spawnInterval) {
      this.spawnParticle();
      this.timeSinceLastSpawn -= spawnInterval;
    }

    // Update existing particles
    this.particles.forEach(particle => {
      // Update life
      particle.life -= deltaTime;
      if (particle.life <= 0) {
        this.particles.delete(particle);
        this.pool.release(particle);
        return;
      }

      // Update physics
      particle.velocity.x += particle.acceleration.x * deltaTime;
      particle.velocity.y += particle.acceleration.y * deltaTime;
      particle.position.x += particle.velocity.x * deltaTime;
      particle.position.y += particle.velocity.y * deltaTime;

      // Update opacity based on life
      particle.opacity = particle.life / particle.maxLife;
    });
  }

  /**
   * Get number of active particles
   */
  public getActiveParticleCount(): number {
    return this.particles.size;
  }

  /**
   * Clean up particle system
   */
  public cleanup(): void {
    this.particles.forEach(particle => {
      this.pool.release(particle);
    });
    this.particles.clear();
  }
}

import { v4 as uuidv4 } from 'uuid';
import {
  Salvage,
  SalvageCapability,
  SalvageDropConfig,
  SalvageManager,
} from '../../types/combat/SalvageTypes';
import { ShipType } from '../../types/ships/CommonShipTypes';

const DEFAULT_DROP_CONFIG: SalvageDropConfig = {
  baseDropRate: 0.5,
  rarityWeights: {
    common: 0.7,
    rare: 0.25,
    epic: 0.05,
  },
  typeWeights: {
    resources: 0.6,
    components: 0.3,
    technology: 0.1,
  },
};

class SalvageManagerImpl implements SalvageManager {
  private activeSalvage: Salvage[] = [];
  private dropConfig: SalvageDropConfig;
  private techTree: { unlockedNodes: Set<string> };

  constructor(dropConfig = DEFAULT_DROP_CONFIG, techTree: { unlockedNodes: Set<string> }) {
    this.dropConfig = dropConfig;
    this.techTree = techTree;
  }

  checkSalvageCapability(ship: ShipType): SalvageCapability {
    // Mining ships can always salvage
    if (ship.type.includes('mining')) {
      return { canSalvage: true, reason: 'mining-ship' };
    }

    // Recon ships can always salvage
    if (ship.type.includes('recon')) {
      return { canSalvage: true, reason: 'recon-ship' };
    }

    // War ships can salvage if they have the cutting laser upgrade
    if (this.techTree.unlockedNodes.has('cutting-laser')) {
      return { canSalvage: true, reason: 'cutting-laser' };
    }

    return { canSalvage: false, reason: 'none' };
  }

  generateSalvageDrops(position: { x: number; y: number }, tier: number): Salvage[] {
    if (Math.random() > this.dropConfig.baseDropRate) {
      return [];
    }

    const numDrops = Math.floor(Math.random() * 3) + 1; // 1-3 drops
    const drops: Salvage[] = [];

    for (let i = 0; i < numDrops; i++) {
      // Determine rarity
      const rarityRoll = Math.random();
      let rarity: Salvage['rarity'];
      if (rarityRoll < this.dropConfig.rarityWeights.epic) {
        rarity = 'epic';
      } else if (
        rarityRoll <
        this.dropConfig.rarityWeights.epic + this.dropConfig.rarityWeights.rare
      ) {
        rarity = 'rare';
      } else {
        rarity = 'common';
      }

      // Determine type
      const typeRoll = Math.random();
      let type: Salvage['type'];
      if (typeRoll < this.dropConfig.typeWeights.technology) {
        type = 'technology';
      } else if (
        typeRoll <
        this.dropConfig.typeWeights.technology + this.dropConfig.typeWeights.components
      ) {
        type = 'components';
      } else {
        type = 'resources';
      }

      // Generate salvage item
      const salvage: Salvage = {
        id: uuidv4(),
        type,
        name: this.generateSalvageName(type, rarity, tier),
        amount: this.calculateAmount(type, rarity, tier),
        rarity,
        position: {
          x: position.x + (Math.random() - 0.5) * 50, // Spread drops slightly
          y: position.y + (Math.random() - 0.5) * 50,
        },
        collected: false,
      };

      drops.push(salvage);
    }

    this.activeSalvage.push(...drops);
    return drops;
  }

  collectSalvage(salvageId: string, _shipId: string): void {
    const salvageIndex = this.activeSalvage.findIndex(s => s.id === salvageId);
    if (salvageIndex >= 0) {
      this.activeSalvage[salvageIndex].collected = true;
    }
  }

  getActiveSalvage(): Salvage[] {
    return this.activeSalvage.filter(s => !s.collected);
  }

  private generateSalvageName(
    type: Salvage['type'],
    rarity: Salvage['rarity'],
    tier: number
  ): string {
    const prefix = rarity === 'epic' ? 'Ancient' : rarity === 'rare' ? 'Advanced' : 'Standard';

    switch (type) {
      case 'resources':
        return `${prefix} ${tier === 3 ? 'Dark Matter' : tier === 2 ? 'Plasma' : 'Metal'} Crystal`;
      case 'components':
        return `${prefix} ${tier === 3 ? 'Quantum' : tier === 2 ? 'Enhanced' : 'Basic'} Components`;
      case 'technology':
        return `${prefix} ${tier === 3 ? 'Forbidden' : tier === 2 ? 'Experimental' : 'Prototype'} Tech`;
    }
  }

  private calculateAmount(type: Salvage['type'], rarity: Salvage['rarity'], tier: number): number {
    const baseAmount = type === 'resources' ? 100 : type === 'components' ? 50 : 25;
    const rarityMultiplier = rarity === 'epic' ? 3 : rarity === 'rare' ? 2 : 1;
    const tierMultiplier = tier;

    return Math.floor(baseAmount * rarityMultiplier * tierMultiplier);
  }
}

export const salvageManager = new SalvageManagerImpl(DEFAULT_DROP_CONFIG, {
  unlockedNodes: new Set(),
});

import { moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import { Position } from '../../types/core/GameTypes';
import { ResourceType } from '../../types/resources/ResourceTypes';
import { FlowNodeType, ResourceFlowManager } from '../resource/ResourceFlowManager';
import { ResourceThresholdManager, ThresholdConfig } from '../resource/ResourceThresholdManager';
import { MiningShipManagerImpl } from './MiningShipManagerImpl';

/**
 * MiningResourceIntegration
 *
 * Integrates the MiningShipManager with the resource management system.
 * Connects mining operations with resource thresholds and flow optimization.
 */
export class MiningResourceIntegration {
  private miningManager: MiningShipManagerImpl;
  private thresholdManager: ResourceThresholdManager;
  private flowManager: ResourceFlowManager;
  private initialized: boolean = false;
  private miningNodes: Map<
    string,
    {
      id: string;
      type: ResourceType;
      position: Position;
      efficiency: number;
    }
  > = new Map();

  constructor(
    miningManager: MiningShipManagerImpl,
    thresholdManager: ResourceThresholdManager,
    flowManager: ResourceFlowManager
  ) {
    this.miningManager = miningManager;
    this.thresholdManager = thresholdManager;
    this.flowManager = flowManager;
  }

  /**
   * Initialize the integration
   */
  public initialize(): void {
    if (this.initialized) {
      return;
    }

    // Subscribe to mining events
    this.subscribeToMiningEvents();

    // Register mining nodes in the flow manager
    this.registerMiningNodes();

    // Create thresholds for mining operations
    this.createMiningThresholds();

    this.initialized = true;
    console.debug('[MiningResourceIntegration] Mining resource integration initialized');
  }

  /**
   * Subscribe to mining events
   */
  private subscribeToMiningEvents(): void {
    // Listen for mining ship registration
    this.miningManager.on('shipRegistered', (data: { shipId: string }) => {
      console.debug(`[MiningResourceIntegration] Mining ship registered: ${data.shipId}`);

      // Get the ship from the mining manager
      const ship = (this.miningManager as any).ships.get(data.shipId);
      if (!ship) {
        return;
      }

      // Register the ship as a producer node in the flow manager
      this.flowManager.registerNode({
        id: `mining-ship-${data.shipId}`,
        type: 'producer' as FlowNodeType,
        resources: ['minerals', 'gas', 'plasma', 'exotic'] as ResourceType[],
        priority: { type: 'minerals' as ResourceType, priority: 5, consumers: [] },
        efficiency: ship.efficiency || 1.0,
        active: ship.status === 'mining',
      });
    });

    // Listen for mining ship unregistration
    this.miningManager.on('shipUnregistered', (data: { shipId: string }) => {
      console.debug(`[MiningResourceIntegration] Mining ship unregistered: ${data.shipId}`);

      // Unregister the ship from the flow manager
      this.flowManager.unregisterNode(`mining-ship-${data.shipId}`);
    });

    // Listen for mining task completion
    moduleEventBus.subscribe('MODULE_ACTIVATED' as ModuleEventType, event => {
      if (event.moduleType !== 'mineral') {
        return;
      }

      const ship = event.data?.ship;
      if (!ship) {
        return;
      }

      // Update the ship's status in the flow manager
      const node = this.flowManager.getNode(`mining-ship-${ship.id}`);
      if (node) {
        node.active = ship.status === 'mining';
      }
    });

    // Listen for resource extraction events
    moduleEventBus.subscribe('RESOURCE_PRODUCED' as ModuleEventType, event => {
      if (!event.moduleId.startsWith('mining-ship-')) {
        return;
      }

      const { resourceType, newAmount, oldAmount, delta } = event.data;
      if (!resourceType || !delta) {
        return;
      }

      // Record the extraction in the flow manager
      const shipId = event.moduleId.replace('mining-ship-', '');
      const transfer = {
        type: resourceType as ResourceType,
        source: `mining-ship-${shipId}`,
        target: `storage-${resourceType}`,
        amount: delta,
        timestamp: event.timestamp,
      };

      // Add to transfer history
      this.addTransferToHistory(transfer);
    });
  }

  /**
   * Add a transfer to the history
   */
  private addTransferToHistory(transfer: any): void {
    // We can't directly access the private addToTransferHistory method,
    // so we'll emit an event that the ResourceIntegration will handle
    moduleEventBus.emit({
      type: 'RESOURCE_TRANSFERRED' as ModuleEventType,
      moduleId: transfer.source,
      moduleType: 'mineral',
      timestamp: Date.now(),
      data: {
        resourceType: transfer.type,
        amount: transfer.amount,
        source: transfer.source,
        target: transfer.target,
      },
    });
  }

  /**
   * Register mining nodes in the flow manager
   */
  private registerMiningNodes(): void {
    // Get all resource nodes from the mining manager
    const { resourceNodes } = this.miningManager as any;
    if (!resourceNodes) {
      return;
    }

    // Register each node in the flow manager
    resourceNodes.forEach((node: any) => {
      this.miningNodes.set(node.id, {
        id: node.id,
        type: node.type,
        position: node.position,
        efficiency: 1.0,
      });

      // Register the node in the flow manager
      this.flowManager.registerNode({
        id: `mining-node-${node.id}`,
        type: 'producer' as FlowNodeType,
        resources: [node.type],
        priority: { type: node.type, priority: 3, consumers: [] },
        efficiency: 1.0,
        active: true,
      });

      // Create a connection to the storage
      this.flowManager.registerConnection({
        id: `mining-connection-${node.id}`,
        source: `mining-node-${node.id}`,
        target: `storage-${node.type}`,
        resourceType: node.type,
        maxRate: 10, // Default extraction rate
        currentRate: 0,
        priority: { type: node.type, priority: 3, consumers: [] },
        active: true,
      });
    });
  }

  /**
   * Create thresholds for mining operations
   */
  private createMiningThresholds(): void {
    // Get all resource nodes from the mining manager
    const { resourceNodes } = this.miningManager as any;
    if (!resourceNodes) {
      return;
    }

    // Create a set of unique resource types
    const resourceTypes = new Set<ResourceType>();
    resourceNodes.forEach((node: any) => {
      resourceTypes.add(node.type);
    });

    // Create thresholds for each resource type
    resourceTypes.forEach(type => {
      // Create a threshold config
      const config: ThresholdConfig = {
        id: `mining-threshold-${type}`,
        threshold: {
          type,
          min: 100, // Default minimum threshold
          target: 500, // Default target threshold
        },
        actions: [
          {
            type: 'notification',
            target: 'mining-manager',
            message: `Low ${type} levels, prioritizing mining`,
          },
        ],
        enabled: true,
      };

      this.thresholdManager.registerThreshold(config);
    });
  }

  /**
   * Register a new mining node
   */
  public registerMiningNode(
    id: string,
    type: ResourceType,
    position: Position,
    efficiency: number = 1.0
  ): void {
    // Store the node
    this.miningNodes.set(id, {
      id,
      type,
      position,
      efficiency,
    });

    // Register the node in the flow manager
    this.flowManager.registerNode({
      id: `mining-node-${id}`,
      type: 'producer' as FlowNodeType,
      resources: [type],
      priority: { type, priority: 3, consumers: [] },
      efficiency,
      active: true,
    });

    // Create a connection to the storage
    this.flowManager.registerConnection({
      id: `mining-connection-${id}`,
      source: `mining-node-${id}`,
      target: `storage-${type}`,
      resourceType: type,
      maxRate: 10 * efficiency, // Extraction rate based on efficiency
      currentRate: 0,
      priority: { type, priority: 3, consumers: [] },
      active: true,
    });

    // Update thresholds if needed
    const existingThreshold = this.thresholdManager
      .getThresholdConfigs()
      .find(config => config.threshold.type === type);

    if (!existingThreshold) {
      // Create a new threshold
      const config: ThresholdConfig = {
        id: `mining-threshold-${type}`,
        threshold: {
          type,
          min: 100,
          target: 500,
        },
        actions: [
          {
            type: 'notification',
            target: 'mining-manager',
            message: `Low ${type} levels, prioritizing mining`,
          },
        ],
        enabled: true,
      };

      this.thresholdManager.registerThreshold(config);
    }
  }

  /**
   * Unregister a mining node
   */
  public unregisterMiningNode(id: string): void {
    // Remove the node
    this.miningNodes.delete(id);

    // Unregister the node from the flow manager
    this.flowManager.unregisterNode(`mining-node-${id}`);

    // Unregister the connection
    this.flowManager.unregisterConnection(`mining-connection-${id}`);
  }

  /**
   * Update mining node efficiency
   */
  public updateMiningNodeEfficiency(id: string, efficiency: number): void {
    // Update the node
    const node = this.miningNodes.get(id);
    if (!node) {
      return;
    }

    node.efficiency = efficiency;

    // Update the node in the flow manager
    const flowNode = this.flowManager.getNode(`mining-node-${id}`);
    if (flowNode) {
      flowNode.efficiency = efficiency;
    }

    // Update the connection
    const connection = this.flowManager.getConnection(`mining-connection-${id}`);
    if (connection) {
      connection.maxRate = 10 * efficiency;
    }
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    if (!this.initialized) {
      return;
    }

    // Unregister all mining nodes
    this.miningNodes.forEach((node, id) => {
      this.flowManager.unregisterNode(`mining-node-${id}`);
      this.flowManager.unregisterConnection(`mining-connection-${id}`);
    });

    // Unregister all mining ships
    const { ships } = this.miningManager as any;
    if (ships) {
      ships.forEach((_: any, shipId: string) => {
        this.flowManager.unregisterNode(`mining-ship-${shipId}`);
      });
    }

    this.initialized = false;
  }
}

/**
 * Factory function to create and initialize the mining resource integration
 */
export function createMiningResourceIntegration(
  miningManager: MiningShipManagerImpl,
  thresholdManager: ResourceThresholdManager,
  flowManager: ResourceFlowManager
): MiningResourceIntegration {
  // Create the integration
  const integration = new MiningResourceIntegration(miningManager, thresholdManager, flowManager);

  // Initialize the integration
  integration.initialize();

  return integration;
}

import { EventEmitter } from 'events';
import { thresholdEvents } from '../../contexts/ThresholdTypes';
import { shipBehaviorManager } from '../../lib/ai/shipBehavior';
import { shipMovementManager } from '../../lib/ai/shipMovement';
import { Position } from '../../types/core/GameTypes';

interface MiningShip {
  id: string;
  name: string;
  type: 'rockBreaker' | 'voidDredger';
  status: 'idle' | 'mining' | 'returning' | 'maintenance';
  capacity: number;
  currentLoad: number;
  targetNode?: string;
  efficiency: number;
}

interface MiningTask {
  id: string;
  shipId: string;
  nodeId: string;
  resourceType: string;
  priority: number;
  status: 'queued' | 'in-progress' | 'completed' | 'failed';
  startTime?: number;
  endTime?: number;
}

/**
 * Manages automated mining operations
 */
class MiningShipManagerImpl extends EventEmitter {
  private ships: Map<string, MiningShip>;
  private tasks: Map<string, MiningTask>;
  private nodeAssignments: Map<string, string>; // nodeId -> shipId

  constructor() {
    super();
    this.ships = new Map();
    this.tasks = new Map();
    this.nodeAssignments = new Map();

    // Listen for threshold events
    thresholdEvents.subscribe(event => {
      if (event.type === 'THRESHOLD_VIOLATED') {
        this.handleThresholdViolation(event.resourceId, event.details);
      }
    });
  }

  /**
   * Registers a mining ship
   */
  registerShip(ship: MiningShip): void {
    this.ships.set(ship.id, ship);

    // Register with behavior system
    shipBehaviorManager.registerShip({
      id: ship.id,
      type: ship.type,
      category: 'mining',
      capabilities: {
        canMine: true,
        canSalvage: false,
        canScan: false,
        canJump: false,
      },
      position: { x: 0, y: 0 }, // Initial position
      stats: {
        health: 100,
        shield: 100,
        speed: 100,
        maneuverability: 1,
        cargo: ship.capacity,
      },
    });
  }

  /**
   * Unregisters a mining ship
   */
  unregisterShip(shipId: string): void {
    this.ships.delete(shipId);
    shipBehaviorManager.unregisterShip(shipId);

    // Clean up any tasks
    Array.from(this.tasks.values())
      .filter(task => task.shipId === shipId)
      .forEach(task => this.tasks.delete(task.id));

    // Clean up node assignments
    Array.from(this.nodeAssignments.entries())
      .filter(([_, assignedShipId]) => assignedShipId === shipId)
      .forEach(([nodeId]) => this.nodeAssignments.delete(nodeId));
  }

  /**
   * Handles threshold violations by dispatching mining ships
   */
  private handleThresholdViolation(
    resourceId: string,
    details: { type: 'below_minimum' | 'above_maximum'; current: number }
  ): void {
    if (details.type === 'below_minimum') {
      // Find available mining ship
      const availableShip = Array.from(this.ships.values()).find(
        ship => ship.status === 'idle' && ship.currentLoad === 0
      );

      if (availableShip) {
        this.dispatchShipToResource(availableShip.id, resourceId);
      }
    } else if (details.type === 'above_maximum') {
      // Recall any ships mining this resource
      const assignedShipId = this.nodeAssignments.get(resourceId);
      if (assignedShipId) {
        this.recallShip(assignedShipId);
      }
    }
  }

  /**
   * Dispatches a ship to mine a resource
   */
  private dispatchShipToResource(shipId: string, resourceId: string): void {
    const ship = this.ships.get(shipId);
    if (!ship) {
      return;
    }

    // Create mining task
    const task: MiningTask = {
      id: `mining-${Date.now()}`,
      shipId,
      nodeId: resourceId,
      resourceType: resourceId.split('-')[0], // e.g., "iron" from "iron-belt-1"
      priority: 1,
      status: 'queued',
    };

    this.tasks.set(task.id, task);
    this.nodeAssignments.set(resourceId, shipId);

    // Update ship status
    ship.status = 'mining';
    ship.targetNode = resourceId;

    // Assign task to behavior system
    shipBehaviorManager.assignTask({
      id: task.id,
      type: 'mine',
      target: {
        id: resourceId,
        position: this.getResourcePosition(resourceId),
      },
      priority: task.priority,
      assignedAt: Date.now(),
    });
  }

  /**
   * Recalls a ship from its current task
   */
  private recallShip(shipId: string): void {
    const ship = this.ships.get(shipId);
    if (!ship) {
      return;
    }

    // Clear current task
    Array.from(this.tasks.values())
      .filter(task => task.shipId === shipId && task.status === 'in-progress')
      .forEach(task => {
        task.status = 'completed';
        task.endTime = Date.now();
        this.nodeAssignments.delete(task.nodeId);
      });

    // Update ship status
    ship.status = 'returning';
    ship.targetNode = undefined;

    // Move ship back to base
    shipMovementManager.moveToPosition(shipId, { x: 0, y: 0 }); // Base position
  }

  /**
   * Gets the position of a resource node
   */
  private getResourcePosition(resourceId: string): Position {
    // Use resourceId to seed the random position for consistency
    const seed = parseInt(resourceId.split('-').pop() || '0', 10);
    return {
      x: (seed * 17) % 1000,
      y: (seed * 23) % 1000,
    };
  }

  /**
   * Updates mining progress
   */
  update(deltaTime: number): void {
    this.ships.forEach(ship => {
      if (ship.status === 'mining' && ship.targetNode) {
        // Update mining progress
        const task = Array.from(this.tasks.values()).find(
          t => t.shipId === ship.id && t.status === 'in-progress'
        );

        if (task) {
          // Simulate resource collection
          ship.currentLoad += ship.efficiency * deltaTime;

          // Check if cargo is full
          if (ship.currentLoad >= ship.capacity) {
            this.recallShip(ship.id);
          }
        }
      }
    });
  }
}

// Export singleton instance
export const miningShipManager = new MiningShipManagerImpl();

import { moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import {
  BaseModule,
  ModularBuilding,
  ModuleAttachmentPoint,
  ModuleType,
} from '../../types/buildings/ModuleTypes';
import { moduleManager } from './ModuleManager';

/**
 * Attachment visualization options
 */
export interface AttachmentVisualizationOptions {
  showValidPoints: boolean;
  highlightIncompatible: boolean;
  showAttachmentPreview: boolean;
  animateAttachment: boolean;
  attachmentAnimationDuration: number;
}

/**
 * Attachment result
 */
export interface AttachmentResult {
  success: boolean;
  moduleId?: string;
  buildingId?: string;
  attachmentPointId?: string;
  error?: string;
}

/**
 * Module Attachment Manager
 * Handles the attachment of modules to buildings, including validation, visualization, and event handling
 */
export class ModuleAttachmentManager {
  private visualizationOptions: AttachmentVisualizationOptions;
  private previewModule: BaseModule | null = null;
  private validAttachmentPoints: Map<string, ModuleAttachmentPoint[]> = new Map();
  private incompatibleAttachmentPoints: Map<string, ModuleAttachmentPoint[]> = new Map();

  constructor(options?: Partial<AttachmentVisualizationOptions>) {
    this.visualizationOptions = {
      showValidPoints: true,
      highlightIncompatible: true,
      showAttachmentPreview: true,
      animateAttachment: true,
      attachmentAnimationDuration: 500,
      ...options,
    };

    // Subscribe to module events
    moduleEventBus.subscribe('MODULE_ATTACHED' as ModuleEventType, this.handleModuleAttached);
    moduleEventBus.subscribe('MODULE_DETACHED' as ModuleEventType, this.handleModuleDetached);
  }

  /**
   * Handle module attached event
   */
  private handleModuleAttached = (event: any): void => {
    const { moduleId, buildingId, attachmentPointId } = event.data;
    console.debug(
      `[ModuleAttachmentManager] Module ${moduleId} attached to building ${buildingId} at point ${attachmentPointId}`
    );

    // Clear any previews or highlights for this building
    this.clearAttachmentVisualization(buildingId);
  };

  /**
   * Handle module detached event
   */
  private handleModuleDetached = (event: any): void => {
    const { moduleId, buildingId, attachmentPointId } = event.data;
    console.debug(
      `[ModuleAttachmentManager] Module ${moduleId} detached from building ${buildingId} at point ${attachmentPointId}`
    );
  };

  /**
   * Start attachment process for a module
   */
  public startAttachment(moduleType: ModuleType, buildingId: string): void {
    const building = moduleManager.getBuilding(buildingId);
    if (!building) {
      console.error(`[ModuleAttachmentManager] Building ${buildingId} not found`);
      return;
    }

    // Create a temporary preview module
    this.previewModule = {
      id: `preview-${moduleType}-${Date.now()}`,
      name: moduleType,
      type: moduleType,
      position: { x: 0, y: 0 },
      isActive: false,
      level: 1,
      status: 'inactive',
    };

    // Find valid attachment points
    this.findValidAttachmentPoints(this.previewModule, building);

    // Show visualization if enabled
    if (this.visualizationOptions.showValidPoints) {
      this.showAttachmentVisualization(buildingId);
    }

    // Emit event
    moduleEventBus.emit({
      type: 'ATTACHMENT_STARTED' as ModuleEventType,
      moduleId: this.previewModule.id,
      moduleType: moduleType,
      timestamp: Date.now(),
      data: {
        buildingId,
        validPoints: this.validAttachmentPoints.get(buildingId)?.map(p => p.id) || [],
        incompatiblePoints: this.incompatibleAttachmentPoints.get(buildingId)?.map(p => p.id) || [],
      },
    });
  }

  /**
   * Cancel attachment process
   */
  public cancelAttachment(buildingId: string): void {
    this.previewModule = null;
    this.clearAttachmentVisualization(buildingId);

    // Emit event
    moduleEventBus.emit({
      type: 'ATTACHMENT_CANCELLED' as ModuleEventType,
      moduleId: 'none',
      moduleType: 'radar' as ModuleType, // Using a default module type
      timestamp: Date.now(),
      data: { buildingId },
    });
  }

  /**
   * Complete attachment process
   */
  public completeAttachment(
    moduleType: ModuleType,
    buildingId: string,
    attachmentPointId: string
  ): AttachmentResult {
    const building = moduleManager.getBuilding(buildingId);
    if (!building) {
      return { success: false, error: `Building ${buildingId} not found` };
    }

    const attachmentPoint = building.attachmentPoints.find(p => p.id === attachmentPointId);
    if (!attachmentPoint) {
      return { success: false, error: `Attachment point ${attachmentPointId} not found` };
    }

    // Check if attachment point is valid for this module type
    if (!attachmentPoint.allowedTypes.includes(moduleType)) {
      return {
        success: false,
        error: `Module type ${moduleType} not allowed at attachment point ${attachmentPointId}`,
      };
    }

    // Check if attachment point is already occupied
    if (attachmentPoint.currentModule) {
      return {
        success: false,
        error: `Attachment point ${attachmentPointId} already has a module`,
      };
    }

    // Create the actual module
    const { position } = attachmentPoint;
    const module = moduleManager.createModule(moduleType, position);

    // Attach the module
    const attached = moduleManager.attachModule(module.id, buildingId, attachmentPointId);
    if (!attached) {
      return {
        success: false,
        error: 'Failed to attach module',
      };
    }

    // Clear visualization
    this.previewModule = null;
    this.clearAttachmentVisualization(buildingId);

    return {
      success: true,
      moduleId: module.id,
      buildingId,
      attachmentPointId,
    };
  }

  /**
   * Find valid attachment points for a module
   */
  private findValidAttachmentPoints(module: BaseModule, building: ModularBuilding): void {
    const validPoints: ModuleAttachmentPoint[] = [];
    const incompatiblePoints: ModuleAttachmentPoint[] = [];

    for (const point of building.attachmentPoints) {
      // Skip points that already have modules
      if (point.currentModule) {
        continue;
      }

      // Check if module type is allowed at this point
      if (point.allowedTypes.includes(module.type)) {
        validPoints.push(point);
      } else {
        incompatiblePoints.push(point);
      }
    }

    this.validAttachmentPoints.set(building.id, validPoints);
    this.incompatibleAttachmentPoints.set(building.id, incompatiblePoints);
  }

  /**
   * Show attachment visualization
   */
  private showAttachmentVisualization(buildingId: string): void {
    // This would be implemented with actual UI visualization code
    // For now, we'll just log the information
    const validPoints = this.validAttachmentPoints.get(buildingId) || [];
    const incompatiblePoints = this.incompatibleAttachmentPoints.get(buildingId) || [];

    console.debug(
      `[ModuleAttachmentManager] Showing attachment visualization for building ${buildingId}`
    );
    console.debug(`Valid attachment points: ${validPoints.map(p => p.id).join(', ')}`);

    if (this.visualizationOptions.highlightIncompatible) {
      console.debug(
        `Incompatible attachment points: ${incompatiblePoints.map(p => p.id).join(', ')}`
      );
    }
  }

  /**
   * Clear attachment visualization
   */
  private clearAttachmentVisualization(buildingId: string): void {
    // This would be implemented with actual UI visualization code
    console.debug(
      `[ModuleAttachmentManager] Clearing attachment visualization for building ${buildingId}`
    );

    this.validAttachmentPoints.delete(buildingId);
    this.incompatibleAttachmentPoints.delete(buildingId);
  }

  /**
   * Show attachment preview at a specific point
   */
  public showAttachmentPreview(buildingId: string, attachmentPointId: string): void {
    if (!this.previewModule || !this.visualizationOptions.showAttachmentPreview) {
      return;
    }

    const building = moduleManager.getBuilding(buildingId);
    if (!building) {
      return;
    }

    const attachmentPoint = building.attachmentPoints.find(p => p.id === attachmentPointId);
    if (!attachmentPoint) {
      return;
    }

    // This would be implemented with actual UI preview code
    console.debug(
      `[ModuleAttachmentManager] Showing attachment preview for module ${this.previewModule.type} at point ${attachmentPointId}`
    );
  }

  /**
   * Get valid attachment points for a building
   */
  public getValidAttachmentPoints(buildingId: string): ModuleAttachmentPoint[] {
    return this.validAttachmentPoints.get(buildingId) || [];
  }

  /**
   * Get incompatible attachment points for a building
   */
  public getIncompatibleAttachmentPoints(buildingId: string): ModuleAttachmentPoint[] {
    return this.incompatibleAttachmentPoints.get(buildingId) || [];
  }

  /**
   * Check if a module can be attached to a specific point
   */
  public canAttachToPoint(
    moduleType: ModuleType,
    buildingId: string,
    attachmentPointId: string
  ): boolean {
    const building = moduleManager.getBuilding(buildingId);
    if (!building) {
      return false;
    }

    const attachmentPoint = building.attachmentPoints.find(p => p.id === attachmentPointId);
    if (!attachmentPoint) {
      return false;
    }

    // Check if attachment point already has a module
    if (attachmentPoint.currentModule) {
      return false;
    }

    // Check if module type is allowed at this point
    return attachmentPoint.allowedTypes.includes(moduleType);
  }

  /**
   * Detach a module from a building
   */
  public detachModule(moduleId: string, buildingId: string): AttachmentResult {
    const building = moduleManager.getBuilding(buildingId);
    if (!building) {
      return { success: false, error: `Building ${buildingId} not found` };
    }

    // Find the attachment point that has this module
    const attachmentPoint = building.attachmentPoints.find(
      p => p.currentModule && p.currentModule.id === moduleId
    );

    if (!attachmentPoint) {
      return {
        success: false,
        error: `Module ${moduleId} not found in building ${buildingId}`,
      };
    }

    // Remove the module from the attachment point
    if (attachmentPoint.currentModule) {
      const moduleType = attachmentPoint.currentModule.type;
      attachmentPoint.currentModule = undefined;

      // Remove the module from the building's modules array
      building.modules = building.modules.filter(m => m.id !== moduleId);

      // Emit detachment event
      moduleEventBus.emit({
        type: 'MODULE_DETACHED' as ModuleEventType,
        moduleId,
        moduleType,
        timestamp: Date.now(),
        data: {
          buildingId,
          attachmentPointId: attachmentPoint.id,
        },
      });

      return {
        success: true,
        moduleId,
        buildingId,
        attachmentPointId: attachmentPoint.id,
      };
    }

    return {
      success: false,
      error: `Module ${moduleId} not found in building ${buildingId}`,
    };
  }

  /**
   * Get visualization options
   */
  public getVisualizationOptions(): AttachmentVisualizationOptions {
    return { ...this.visualizationOptions };
  }

  /**
   * Update visualization options
   */
  public updateVisualizationOptions(options: Partial<AttachmentVisualizationOptions>): void {
    this.visualizationOptions = {
      ...this.visualizationOptions,
      ...options,
    };
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    // Unsubscribe from events
    const unsubscribeAttached = moduleEventBus.subscribe(
      'MODULE_ATTACHED' as ModuleEventType,
      this.handleModuleAttached
    );
    const unsubscribeDetached = moduleEventBus.subscribe(
      'MODULE_DETACHED' as ModuleEventType,
      this.handleModuleDetached
    );

    // Call the unsubscribe functions
    if (typeof unsubscribeAttached === 'function') {
      unsubscribeAttached();
    }

    if (typeof unsubscribeDetached === 'function') {
      unsubscribeDetached();
    }

    // Clear data
    this.previewModule = null;
    this.validAttachmentPoints.clear();
    this.incompatibleAttachmentPoints.clear();
  }
}

// Export singleton instance
export const moduleAttachmentManager = new ModuleAttachmentManager();

import { moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import { ResourceType } from '../../types/resources/ResourceTypes';
import { resourceManager } from '../game/ResourceManager';
import { moduleManager } from './ModuleManager';
import { moduleStatusManager } from './ModuleStatusManager';

/**
 * Upgrade path for a module
 * Defines the progression of a module through levels
 */
export interface ModuleUpgradePath {
  moduleType: ModuleType;
  levels: ModuleUpgradeLevel[];
}

/**
 * Upgrade level definition
 * Contains requirements and effects for a specific level
 */
export interface ModuleUpgradeLevel {
  level: number;
  name: string;
  description: string;
  requirements: ModuleUpgradeRequirements;
  effects: ModuleUpgradeEffect[];
  visualChanges?: ModuleVisualChange[];
}

/**
 * Requirements for upgrading a module
 */
export interface ModuleUpgradeRequirements {
  minLevel: number;
  resourceCosts: {
    type: string;
    amount: number;
  }[];
  techRequirements?: string[];
  moduleRequirements?: {
    type: ModuleType;
    level: number;
  }[];
  buildingLevel?: number;
}

/**
 * Effect of upgrading a module
 */
export interface ModuleUpgradeEffect {
  type: 'stat' | 'resource' | 'ability' | 'special';
  target: string;
  value: number;
  isPercentage: boolean;
  description: string;
}

/**
 * Visual change for a module upgrade
 */
export interface ModuleVisualChange {
  type: 'color' | 'size' | 'shape' | 'texture' | 'effect';
  description: string;
  value: string;
}

/**
 * Upgrade status for a module
 */
export interface ModuleUpgradeStatus {
  moduleId: string;
  moduleType: ModuleType;
  currentLevel: number;
  maxLevel: number;
  nextLevel?: ModuleUpgradeLevel;
  upgradeAvailable: boolean;
  requirementsMet: boolean;
  missingRequirements: string[];
  upgradeProgress?: number;
  estimatedTimeRemaining?: number;
  effects: ModuleUpgradeEffect[];
}

/**
 * Module upgrade manager
 * Manages upgrade paths, requirements, and effects for modules
 */
export class ModuleUpgradeManager {
  private upgradePaths: Map<ModuleType, ModuleUpgradePath>;
  private activeUpgrades: Map<
    string,
    {
      startTime: number;
      duration: number;
      targetLevel: number;
      timer: NodeJS.Timeout;
    }
  >;

  constructor() {
    this.upgradePaths = new Map();
    this.activeUpgrades = new Map();

    // Subscribe to events
    this.subscribeToEvents();
  }

  /**
   * Subscribe to module events
   */
  private subscribeToEvents(): void {
    moduleEventBus.subscribe('MODULE_CREATED' as ModuleEventType, this.handleModuleCreated);
    moduleEventBus.subscribe('MODULE_UPGRADED' as ModuleEventType, this.handleModuleUpgraded);
  }

  /**
   * Register an upgrade path for a module type
   */
  public registerUpgradePath(path: ModuleUpgradePath): void {
    this.upgradePaths.set(path.moduleType, path);
  }

  /**
   * Get the upgrade path for a module type
   */
  public getUpgradePath(moduleType: ModuleType): ModuleUpgradePath | undefined {
    return this.upgradePaths.get(moduleType);
  }

  /**
   * Get the upgrade level for a module
   */
  public getUpgradeLevel(moduleType: ModuleType, level: number): ModuleUpgradeLevel | undefined {
    const path = this.upgradePaths.get(moduleType);
    if (!path) {
      return undefined;
    }

    return path.levels.find(l => l.level === level);
  }

  /**
   * Get the next upgrade level for a module
   */
  public getNextUpgradeLevel(moduleId: string): ModuleUpgradeLevel | undefined {
    const module = moduleManager.getModule(moduleId);
    if (!module) {
      return undefined;
    }

    const path = this.upgradePaths.get(module.type);
    if (!path) {
      return undefined;
    }

    return path.levels.find(l => l.level === module.level + 1);
  }

  /**
   * Check if a module can be upgraded
   */
  public canUpgrade(moduleId: string): boolean {
    const module = moduleManager.getModule(moduleId);
    if (!module) {
      return false;
    }

    // Check if module is already being upgraded
    if (this.activeUpgrades.has(moduleId)) {
      return false;
    }

    // Check if module is active
    if (!module.isActive) {
      return false;
    }

    // Get next upgrade level
    const nextLevel = this.getNextUpgradeLevel(moduleId);
    if (!nextLevel) {
      return false;
    }

    // Check requirements
    return this.checkUpgradeRequirements(moduleId, nextLevel);
  }

  /**
   * Check if a module meets the requirements for an upgrade
   */
  public checkUpgradeRequirements(moduleId: string, upgradeLevel: ModuleUpgradeLevel): boolean {
    const module = moduleManager.getModule(moduleId);
    if (!module) {
      return false;
    }

    const { requirements } = upgradeLevel;

    // Check minimum level requirement
    if (module.level < requirements.minLevel) {
      return false;
    }

    // Check resource costs
    for (const cost of requirements.resourceCosts) {
      const available = resourceManager.getResourceAmount(cost.type as ResourceType);
      if (available < cost.amount) {
        return false;
      }
    }

    // Check tech requirements
    if (requirements.techRequirements) {
      // TODO: Implement tech requirement checking
      // This would require integration with a tech tree system
    }

    // Check module requirements
    if (requirements.moduleRequirements) {
      for (const req of requirements.moduleRequirements) {
        const modules = moduleManager.getModulesByType(req.type);
        const hasRequiredModule = modules.some(m => m.level >= req.level);
        if (!hasRequiredModule) {
          return false;
        }
      }
    }

    // Check building level
    if (requirements.buildingLevel) {
      // Find the building this module is attached to
      let attachedBuilding = undefined;
      for (const building of moduleManager.getBuildings()) {
        if (building.modules.some(m => m.id === moduleId)) {
          attachedBuilding = building;
          break;
        }
      }

      if (!attachedBuilding || attachedBuilding.level < requirements.buildingLevel) {
        return false;
      }
    }

    return true;
  }

  /**
   * Get missing requirements for a module upgrade
   */
  public getMissingRequirements(moduleId: string): string[] {
    const module = moduleManager.getModule(moduleId);
    if (!module) {
      return ['Module not found'];
    }

    const nextLevel = this.getNextUpgradeLevel(moduleId);
    if (!nextLevel) {
      return ['No upgrade available'];
    }

    const { requirements } = nextLevel;
    const missingRequirements: string[] = [];

    // Check minimum level requirement
    if (module.level < requirements.minLevel) {
      missingRequirements.push(
        `Module level ${module.level} is below required level ${requirements.minLevel}`
      );
    }

    // Check resource costs
    for (const cost of requirements.resourceCosts) {
      const available = resourceManager.getResourceAmount(cost.type as ResourceType);
      if (available < cost.amount) {
        missingRequirements.push(`Insufficient ${cost.type}: ${available}/${cost.amount}`);
      }
    }

    // Check tech requirements
    if (requirements.techRequirements) {
      // TODO: Implement tech requirement checking
      // This would require integration with a tech tree system
    }

    // Check module requirements
    if (requirements.moduleRequirements) {
      for (const req of requirements.moduleRequirements) {
        const modules = moduleManager.getModulesByType(req.type);
        const hasRequiredModule = modules.some(m => m.level >= req.level);
        if (!hasRequiredModule) {
          missingRequirements.push(`Requires ${req.type} module at level ${req.level}`);
        }
      }
    }

    // Check building level
    if (requirements.buildingLevel) {
      // Find the building this module is attached to
      let attachedBuilding = undefined;
      for (const building of moduleManager.getBuildings()) {
        if (building.modules.some(m => m.id === moduleId)) {
          attachedBuilding = building;
          break;
        }
      }

      if (!attachedBuilding) {
        missingRequirements.push(`Module not attached to a building`);
      } else if (attachedBuilding.level < requirements.buildingLevel) {
        missingRequirements.push(
          `Building level ${attachedBuilding.level} is below required level ${requirements.buildingLevel}`
        );
      }
    }

    return missingRequirements;
  }

  /**
   * Get upgrade status for a module
   */
  public getUpgradeStatus(moduleId: string): ModuleUpgradeStatus | undefined {
    const module = moduleManager.getModule(moduleId);
    if (!module) {
      return undefined;
    }

    const path = this.upgradePaths.get(module.type);
    if (!path) {
      return undefined;
    }

    const nextLevel = this.getNextUpgradeLevel(moduleId);
    const maxLevel =
      path.levels.length > 0 ? path.levels[path.levels.length - 1].level : module.level;

    // Get active upgrade info
    const activeUpgrade = this.activeUpgrades.get(moduleId);
    let upgradeProgress: number | undefined;
    let estimatedTimeRemaining: number | undefined;

    if (activeUpgrade) {
      const elapsed = Date.now() - activeUpgrade.startTime;
      upgradeProgress = Math.min(1, elapsed / activeUpgrade.duration);
      estimatedTimeRemaining = Math.max(0, activeUpgrade.duration - elapsed);
    }

    // Get upgrade effects
    const effects: ModuleUpgradeEffect[] = [];
    if (nextLevel) {
      effects.push(...nextLevel.effects);
    }

    // Check requirements
    const requirementsMet = nextLevel ? this.checkUpgradeRequirements(moduleId, nextLevel) : false;
    const missingRequirements = nextLevel
      ? this.getMissingRequirements(moduleId)
      : ['No upgrade available'];

    return {
      moduleId,
      moduleType: module.type,
      currentLevel: module.level,
      maxLevel,
      nextLevel,
      upgradeAvailable: !!nextLevel && module.level < maxLevel,
      requirementsMet,
      missingRequirements,
      upgradeProgress,
      estimatedTimeRemaining,
      effects,
    };
  }

  /**
   * Start upgrading a module
   */
  public startUpgrade(moduleId: string): boolean {
    // Check if module can be upgraded
    if (!this.canUpgrade(moduleId)) {
      return false;
    }

    const module = moduleManager.getModule(moduleId);
    if (!module) {
      return false;
    }

    const nextLevel = this.getNextUpgradeLevel(moduleId);
    if (!nextLevel) {
      return false;
    }

    // Consume resources
    for (const cost of nextLevel.requirements.resourceCosts) {
      resourceManager.removeResource(cost.type as ResourceType, cost.amount);
    }

    // Calculate upgrade time (1 minute per level)
    const baseUpgradeTime = 60000; // 1 minute
    const upgradeTime = baseUpgradeTime * nextLevel.level;

    // Update module status
    moduleStatusManager.updateModuleStatus(
      moduleId,
      'upgrading',
      `Upgrading to level ${nextLevel.level}`
    );

    // Start upgrade timer
    const timer = setTimeout(() => {
      this.completeUpgrade(moduleId, nextLevel.level);
    }, upgradeTime);

    // Store upgrade info
    this.activeUpgrades.set(moduleId, {
      startTime: Date.now(),
      duration: upgradeTime,
      targetLevel: nextLevel.level,
      timer,
    });

    // Emit upgrade started event
    moduleEventBus.emit({
      type: 'MODULE_UPGRADE_STARTED' as ModuleEventType,
      moduleId,
      moduleType: module.type,
      timestamp: Date.now(),
      data: {
        currentLevel: module.level,
        targetLevel: nextLevel.level,
        duration: upgradeTime,
        requirements: nextLevel.requirements,
        effects: nextLevel.effects,
      },
    });

    return true;
  }

  /**
   * Cancel an active module upgrade
   */
  public cancelUpgrade(moduleId: string): boolean {
    const activeUpgrade = this.activeUpgrades.get(moduleId);
    if (!activeUpgrade) {
      return false;
    }

    // Clear timer
    clearTimeout(activeUpgrade.timer);

    // Remove from active upgrades
    this.activeUpgrades.delete(moduleId);

    // Update module status
    moduleStatusManager.updateModuleStatus(moduleId, 'active', 'Upgrade cancelled');

    // Emit upgrade cancelled event
    const module = moduleManager.getModule(moduleId);
    if (module) {
      moduleEventBus.emit({
        type: 'MODULE_UPGRADE_CANCELLED' as ModuleEventType,
        moduleId,
        moduleType: module.type,
        timestamp: Date.now(),
        data: {
          currentLevel: module.level,
          targetLevel: activeUpgrade.targetLevel,
        },
      });
    }

    return true;
  }

  /**
   * Complete a module upgrade
   */
  private completeUpgrade(moduleId: string, targetLevel: number): void {
    const activeUpgrade = this.activeUpgrades.get(moduleId);
    if (!activeUpgrade) {
      return;
    }

    // Remove from active upgrades
    this.activeUpgrades.delete(moduleId);

    // Get module
    const module = moduleManager.getModule(moduleId);
    if (!module) {
      return;
    }

    // Get upgrade level
    const upgradeLevel = this.getUpgradeLevel(module.type, targetLevel);
    if (!upgradeLevel) {
      return;
    }

    // Update module level
    const oldLevel = module.level;
    module.level = targetLevel;

    // Update module status
    moduleStatusManager.updateModuleStatus(moduleId, 'active', `Upgraded to level ${targetLevel}`);

    // Apply upgrade effects
    this.applyUpgradeEffects(moduleId, upgradeLevel);

    // Emit upgrade completed event
    moduleEventBus.emit({
      type: 'MODULE_UPGRADED' as ModuleEventType,
      moduleId,
      moduleType: module.type,
      timestamp: Date.now(),
      data: {
        oldLevel,
        newLevel: targetLevel,
        effects: upgradeLevel.effects,
        visualChanges: upgradeLevel.visualChanges,
      },
    });
  }

  /**
   * Apply upgrade effects to a module
   */
  private applyUpgradeEffects(moduleId: string, upgradeLevel: ModuleUpgradeLevel): void {
    // This would apply the effects of the upgrade to the module
    // For now, we'll just log the effects
    console.log(
      `[ModuleUpgradeManager] Applying effects to module ${moduleId}:`,
      upgradeLevel.effects
    );

    // In a real implementation, this would modify the module's stats, abilities, etc.
    // based on the effects defined in the upgrade level
  }

  /**
   * Handle module created event
   */
  private handleModuleCreated = (event: any): void => {
    // Nothing to do here for now
  };

  /**
   * Handle module upgraded event
   */
  private handleModuleUpgraded = (event: any): void => {
    // Nothing to do here for now
  };

  /**
   * Clean up resources
   */
  public cleanup(): void {
    // Clear all active upgrade timers
    for (const [moduleId, upgrade] of Array.from(this.activeUpgrades.entries())) {
      clearTimeout(upgrade.timer);

      // Update module status
      moduleStatusManager.updateModuleStatus(
        moduleId,
        'active',
        'Upgrade cancelled due to cleanup'
      );
    }

    // Clear active upgrades
    this.activeUpgrades.clear();

    // Unsubscribe from events
    const unsubscribeCreated = moduleEventBus.subscribe(
      'MODULE_CREATED' as ModuleEventType,
      this.handleModuleCreated
    );
    const unsubscribeUpgraded = moduleEventBus.subscribe(
      'MODULE_UPGRADED' as ModuleEventType,
      this.handleModuleUpgraded
    );

    if (typeof unsubscribeCreated === 'function') {
      unsubscribeCreated();
    }
    if (typeof unsubscribeUpgraded === 'function') {
      unsubscribeUpgraded();
    }
  }
}

// Export singleton instance
export const moduleUpgradeManager = new ModuleUpgradeManager();

import { v4 as uuidv4 } from 'uuid';
import { OFFICER_TRAITS, SQUAD_CONFIG, TRAINING_CONFIG } from '../../config/OfficerConfig';
import { ModuleEvent, moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import { techTreeManager } from '../../managers/game/techTreeManager';
import type { ModuleType } from '../../types/buildings/ModuleTypes';
import type {
  OfficerManager as IOfficerManager,
  Officer,
  OfficerEvents,
  OfficerRole,
  OfficerSkills,
  OfficerSpecialization,
  OfficerTier,
  Squad,
  TrainingProgram,
} from '../../types/officers/OfficerTypes';
import { EventEmitter } from '../../utils/EventEmitter';

type TechNodeUnlockedEvent = {
  nodeId: string;
  node: {
    type: string;
    tier: number;
  };
};

/**
 * Implementation of the Officer Manager
 * Handles officer hiring, training, assignment, squad management, and progression
 */
export class OfficerManager extends EventEmitter<OfficerEvents> implements IOfficerManager {
  private officers: Map<string, Officer> = new Map();
  private squads: Map<string, Squad> = new Map();
  private trainingPrograms: Map<string, TrainingProgram> = new Map();
  private currentTier: OfficerTier = 1;
  private moduleId: string = 'academy'; // Default module ID for academy

  constructor() {
    super();
    this.setupEventListeners();
  }

  /**
   * Set up event listeners for tech tree unlocks and other relevant events
   */
  private setupEventListeners(): void {
    techTreeManager.on('nodeUnlocked', (event: TechNodeUnlockedEvent) => {
      if (event.node.type === 'academy') {
        this.handleAcademyUpgrade(event.node.tier as OfficerTier);
      }
    });

    moduleEventBus.subscribe('MODULE_ACTIVATED', (event: ModuleEvent) => {
      if (event.moduleType === 'academy') {
        this.moduleId = event.moduleId;
        this.emit('academyActivated', { moduleId: event.moduleId });
      }
    });
  }

  /**
   * Handle academy tier upgrades
   */
  private handleAcademyUpgrade(tier: OfficerTier): void {
    if (tier > this.currentTier) {
      this.currentTier = tier;
      this.emit('tierUpgraded', { tier });
      moduleEventBus.emit({
        type: 'MODULE_UPGRADED',
        moduleId: this.moduleId,
        moduleType: 'academy',
        timestamp: Date.now(),
        data: { tier },
      });
    }
  }

  /**
   * Generate base stats for a new officer based on role and specialization
   */
  private generateBaseStats(
    role: OfficerRole,
    specialization: OfficerSpecialization
  ): OfficerSkills {
    const baseStats: OfficerSkills = {
      combat: 1,
      leadership: 1,
      technical: 1,
    };

    // Adjust based on role
    if (role === 'Squad Leader') {
      baseStats.leadership += 2;
    }

    // Adjust based on specialization
    switch (specialization) {
      case 'War':
        baseStats.combat += 2;
        break;
      case 'Recon':
        baseStats.technical += 1;
        baseStats.combat += 1;
        break;
      case 'Mining':
        baseStats.technical += 2;
        break;
    }

    return baseStats;
  }

  /**
   * Generate random traits for a new officer
   */
  private generateTraits(): string[] {
    const numTraits = Math.floor(Math.random() * 2) + 1; // 1-2 traits
    const availableTraits = [...OFFICER_TRAITS];
    const selectedTraits: string[] = [];

    for (let i = 0; i < numTraits; i++) {
      if (availableTraits.length === 0) {
        break;
      }
      const index = Math.floor(Math.random() * availableTraits.length);
      selectedTraits.push(availableTraits[index].id);
      availableTraits.splice(index, 1);
    }

    return selectedTraits;
  }

  /**
   * Apply trait effects to an officer's stats
   */
  private applyTraitEffects(officer: Officer): void {
    officer.traits.forEach(traitId => {
      const trait = OFFICER_TRAITS.find(t => t.id === traitId);
      if (trait?.effects.skills) {
        Object.entries(trait.effects.skills).forEach(([skill, value]) => {
          officer.skills[skill as keyof typeof officer.skills] += value;
        });
      }
    });
  }

  /**
   * Calculate training duration based on officer and program
   */
  private calculateTrainingDuration(
    officer: Officer,
    specialization: OfficerSpecialization
  ): number {
    let duration = TRAINING_CONFIG.baseTime;

    // Apply level modifier
    duration *= 1 - (officer.level - 1) * TRAINING_CONFIG.levelModifier;

    // Apply specialization modifier
    if (officer.specialization === specialization) {
      duration *= 1 - TRAINING_CONFIG.specializationModifier;
    }

    // Apply trait modifiers
    officer.traits.forEach(traitId => {
      const trait = OFFICER_TRAITS.find(t => t.id === traitId);
      if (trait?.effects.bonuses?.trainingSpeed) {
        duration *= 1 - trait.effects.bonuses.trainingSpeed;
      }
    });

    return Math.max(duration, TRAINING_CONFIG.baseTime * 0.5); // Minimum 50% of base time
  }

  /**
   * Calculate XP multiplier based on officer traits and current activity
   */
  private calculateXpMultiplier(officer: Officer, activity?: string): number {
    let multiplier = TRAINING_CONFIG.xpMultiplier;

    // Apply trait bonuses
    officer.traits.forEach(traitId => {
      const trait = OFFICER_TRAITS.find(t => t.id === traitId);
      if (trait?.effects.bonuses?.xpGain) {
        multiplier *= 1 + trait.effects.bonuses.xpGain;
      }
    });

    // Apply activity-specific bonuses
    if (activity === 'training') {
      multiplier *= 1.5; // 50% bonus XP during training
    } else if (activity === 'combat') {
      multiplier *= 2.0; // 100% bonus XP during combat
    }

    return multiplier;
  }

  /**
   * Calculate next level XP requirement using a progressive scale
   */
  private calculateNextLevelXp(currentLevel: number): number {
    return Math.floor(100 * Math.pow(1.5, currentLevel - 1));
  }

  /**
   * Improve officer skills based on level up and specialization
   */
  private improveSkills(officer: Officer): void {
    // Base skill improvements
    const improvements: OfficerSkills = {
      combat: 0,
      leadership: 0,
      technical: 0,
    };

    // Specialization-based improvements
    switch (officer.specialization) {
      case 'War':
        improvements.combat += 2;
        improvements.leadership += 1;
        break;
      case 'Recon':
        improvements.technical += 1;
        improvements.combat += 1;
        improvements.leadership += 1;
        break;
      case 'Mining':
        improvements.technical += 2;
        improvements.leadership += 1;
        break;
    }

    // Role-based improvements
    if (officer.role === 'Squad Leader') {
      improvements.leadership += 1;
    }

    // Apply improvements
    Object.entries(improvements).forEach(([skill, value]) => {
      officer.skills[skill as keyof OfficerSkills] += value;
    });

    // Update stats to match skills
    officer.stats = { ...officer.skills };
  }

  /**
   * Hire a new officer
   */
  public hireOfficer(role: OfficerRole, specialization: OfficerSpecialization): Officer {
    const id = uuidv4();
    const baseStats = this.generateBaseStats(role, specialization);
    const traits = this.generateTraits();

    const officer: Officer = {
      id,
      name: `Officer-${id.substring(0, 4)}`, // Temporary name generation
      portrait: '', // TODO: Implement portrait generation
      level: 1,
      xp: 0,
      nextLevelXp: 100,
      role,
      status: 'available',
      specialization,
      skills: { ...baseStats },
      traits,
      stats: { ...baseStats },
    };

    this.applyTraitEffects(officer);
    this.officers.set(id, officer);
    this.emit('officerHired', { officer });
    this.emitModuleEvent('MODULE_CREATED', { officer });
    return officer;
  }

  /**
   * Start training an officer
   */
  public startTraining(officerId: string, specialization: OfficerSpecialization): void {
    const officer = this.officers.get(officerId);
    if (!officer || officer.status !== 'available') {
      return;
    }

    const duration = this.calculateTrainingDuration(officer, specialization);
    const program: TrainingProgram = {
      id: uuidv4(),
      officerId,
      specialization,
      progress: 0,
      startTime: Date.now(),
      duration,
      bonuses: {
        xpMultiplier: this.calculateXpMultiplier(officer, 'training'),
        skillGainRate: TRAINING_CONFIG.skillGainRate,
      },
    };

    officer.status = 'training';
    officer.trainingProgress = 0;
    this.trainingPrograms.set(program.id, program);
    this.officers.set(officerId, officer);

    this.emit('trainingStarted', {
      officerId,
      program,
    });
    this.emitModuleEvent('AUTOMATION_STARTED', { officerId, program });
  }

  /**
   * Assign an officer to a task or location
   */
  public assignOfficer(officerId: string, assignmentId: string): void {
    const officer = this.officers.get(officerId);
    if (!officer || officer.status !== 'available') {
      return;
    }

    officer.status = 'assigned';
    officer.assignedTo = assignmentId;
    this.officers.set(officerId, officer);

    this.emit('officerAssigned', {
      officerId,
      assignmentId,
    });
    this.emitModuleEvent('STATUS_CHANGED', { officerId, assignmentId, status: 'assigned' });
  }

  /**
   * Create a new squad
   */
  public createSquad(name: string, specialization: OfficerSpecialization): Squad {
    const squad: Squad = {
      id: uuidv4(),
      name,
      members: [],
      specialization,
      bonuses: {
        combat: 0,
        efficiency: 0,
        survival: 0,
      },
    };

    this.squads.set(squad.id, squad);
    this.emit('squadCreated', { squad });
    this.emitModuleEvent('MODULE_CREATED', { squad });
    return squad;
  }

  /**
   * Assign an officer to a squad
   */
  public assignToSquad(officerId: string, squadId: string): void {
    const officer = this.officers.get(officerId);
    const squad = this.squads.get(squadId);

    if (!officer || !squad || officer.status !== 'available') {
      return;
    }

    // Check squad size limit
    if (squad.members.length >= SQUAD_CONFIG.maxSize) {
      return;
    }

    // If officer is Squad Leader and squad has no leader
    if (officer.role === 'Squad Leader' && !squad.leader) {
      squad.leader = officer;
    }

    squad.members.push(officer);
    officer.status = 'assigned';
    officer.assignedTo = squadId;

    this.updateSquadBonuses(squad);
    this.emit('squadUpdated', { squadId, officer: officerId });
    this.emitModuleEvent('STATUS_CHANGED', { squadId, officer: officerId });
  }

  /**
   * Update squad bonuses based on member skills and traits
   */
  private updateSquadBonuses(squad: Squad): void {
    const baseBonuses = {
      combat: 0,
      efficiency: 0,
      survival: 0,
    };

    // Add leader bonuses if present
    if (squad.leader) {
      const leaderBonus = this.calculateLeaderBonus(squad.leader);
      baseBonuses.combat += leaderBonus;
      baseBonuses.efficiency += leaderBonus;
      baseBonuses.survival += leaderBonus;
    }

    // Add member bonuses
    squad.members.forEach(member => {
      if (member !== squad.leader) {
        const memberBonus = this.calculateMemberBonus(member);
        baseBonuses.combat += memberBonus;
        baseBonuses.efficiency += memberBonus;
        baseBonuses.survival += memberBonus;
      }
    });

    // Apply multipliers
    squad.bonuses = {
      combat: baseBonuses.combat * SQUAD_CONFIG.bonusMultipliers.combat,
      efficiency: baseBonuses.efficiency * SQUAD_CONFIG.bonusMultipliers.efficiency,
      survival: baseBonuses.survival * SQUAD_CONFIG.bonusMultipliers.survival,
    };

    // Update squad in storage
    this.squads.set(squad.id, squad);
    this.emitModuleEvent('STATUS_CHANGED', { squadId: squad.id, bonuses: squad.bonuses });
  }

  /**
   * Calculate leadership bonus for squad leader
   */
  private calculateLeaderBonus(officer: Officer): number {
    let bonus = officer.skills.leadership * SQUAD_CONFIG.leadershipBonus;

    // Apply trait bonuses
    officer.traits.forEach(traitId => {
      const trait = OFFICER_TRAITS.find(t => t.id === traitId);
      if (trait?.effects.bonuses?.squadBonus) {
        bonus *= 1 + trait.effects.bonuses.squadBonus;
      }
    });

    return bonus;
  }

  /**
   * Calculate bonus contribution from squad member
   */
  private calculateMemberBonus(officer: Officer): number {
    let bonus = 0;

    // Add skill contributions
    switch (officer.specialization) {
      case 'War':
        bonus += officer.skills.combat * 0.5;
        break;
      case 'Recon':
        bonus += (officer.skills.combat + officer.skills.technical) * 0.25;
        break;
      case 'Mining':
        bonus += officer.skills.technical * 0.5;
        break;
    }

    // Apply trait bonuses
    officer.traits.forEach(traitId => {
      const trait = OFFICER_TRAITS.find(t => t.id === traitId);
      if (trait?.effects.bonuses?.squadBonus) {
        bonus *= 1 + trait.effects.bonuses.squadBonus;
      }
    });

    return bonus;
  }

  /**
   * Add experience to an officer and handle level ups
   */
  public addExperience(officerId: string, amount: number, activity?: string): void {
    const officer = this.officers.get(officerId);
    if (!officer) {
      return;
    }

    const multiplier = this.calculateXpMultiplier(officer, activity);
    const adjustedAmount = Math.floor(amount * multiplier);

    officer.xp += adjustedAmount;

    // Handle level ups
    while (officer.xp >= officer.nextLevelXp) {
      officer.xp -= officer.nextLevelXp;
      officer.level += 1;
      officer.nextLevelXp = this.calculateNextLevelXp(officer.level);
      this.improveSkills(officer);

      // Emit level up event
      this.emit('officerLeveledUp', {
        officerId,
        newLevel: officer.level,
        skills: officer.skills,
      });
    }

    // Update officer in storage
    this.officers.set(officerId, officer);

    // Emit experience gained event
    this.emit('experienceGained', {
      officerId,
      amount: adjustedAmount,
      newTotal: officer.xp,
      nextLevel: officer.nextLevelXp,
    });
    this.emitModuleEvent('STATUS_CHANGED', {
      officerId,
      amount: adjustedAmount,
      newTotal: officer.xp,
      nextLevel: officer.nextLevelXp,
    });
  }

  /**
   * Update training programs and apply experience
   */
  public update(deltaTime: number): void {
    // Update training programs
    this.trainingPrograms.forEach((program, programId) => {
      const officer = this.officers.get(program.officerId);
      if (!officer) {
        this.trainingPrograms.delete(programId);
        return;
      }

      program.progress += deltaTime;
      if (program.progress >= program.duration) {
        this.completeTraining(programId);
      } else {
        // Update training progress
        officer.trainingProgress = program.progress / program.duration;
        this.officers.set(officer.id, officer);
      }
    });

    // Update squad bonuses
    this.squads.forEach(squad => {
      this.updateSquadBonuses(squad);
    });
  }

  /**
   * Complete a training program
   */
  private completeTraining(programId: string): void {
    const program = this.trainingPrograms.get(programId);
    if (!program) {
      return;
    }

    const officer = this.officers.get(program.officerId);
    if (!officer) {
      this.trainingPrograms.delete(programId);
      return;
    }

    // Apply training results
    officer.status = 'available';
    officer.trainingProgress = undefined;

    // Improve skills based on training
    const skillImprovement = Math.floor(
      (program.duration * program.bonuses.skillGainRate) / TRAINING_CONFIG.baseTime
    );

    switch (program.specialization) {
      case 'War':
        officer.skills.combat += skillImprovement;
        break;
      case 'Recon':
        officer.skills.combat += Math.floor(skillImprovement / 2);
        officer.skills.technical += Math.floor(skillImprovement / 2);
        break;
      case 'Mining':
        officer.skills.technical += skillImprovement;
        break;
    }

    // Update stats
    officer.stats = { ...officer.skills };

    // Add experience
    this.addExperience(officer.id, 100, 'training');

    // Update storage
    this.officers.set(officer.id, officer);
    this.trainingPrograms.delete(programId);

    // Emit completion event
    this.emit('trainingCompleted', {
      officerId: officer.id,
      specialization: program.specialization,
      skills: officer.skills,
    });
    this.emitModuleEvent('AUTOMATION_CYCLE_COMPLETE', {
      officerId: officer.id,
      specialization: program.specialization,
      skills: officer.skills,
    });
  }

  /**
   * Get an officer by ID
   */
  public getOfficer(id: string): Officer | undefined {
    return this.officers.get(id);
  }

  /**
   * Get a squad by ID
   */
  public getSquad(id: string): Squad | undefined {
    return this.squads.get(id);
  }

  /**
   * Get all available officers
   */
  public getAvailableOfficers(): Officer[] {
    return Array.from(this.officers.values()).filter(officer => officer.status === 'available');
  }

  /**
   * Get all squads of a specific specialization
   */
  public getSquadsBySpecialization(specialization: OfficerSpecialization): Squad[] {
    return Array.from(this.squads.values()).filter(
      squad => squad.specialization === specialization
    );
  }

  /**
   * Get current academy tier
   */
  public getCurrentTier(): OfficerTier {
    return this.currentTier;
  }

  private emitModuleEvent(type: ModuleEventType, data: any): void {
    moduleEventBus.emit({
      type,
      moduleId: 'officer-manager',
      moduleType: 'academy' as ModuleType,
      timestamp: Date.now(),
      data,
    });
  }
}

import { v4 as uuidv4 } from 'uuid';
import { SHIP_BLUEPRINTS, ShipBlueprint } from '../../config/ShipBlueprints';
import { ModuleEvent, moduleEventBus } from '../../lib/modules/ModuleEvents';
