  /**
   * Set up maintenance timer for a bay
   */
  private setupBayMaintenance(bay: ShipHangarBay): void {
    // Clear existing timer if any
    const existingTimer = this.bayMaintenanceTimers.get(bay.id);
    if (existingTimer) {
      clearTimeout(existingTimer.timer);
    }

    // Set up new maintenance timer
    const maintenanceInterval = 300000; // 5 minutes
    const timer = setInterval(() => {
      this.performBayMaintenance(bay.id);
    }, maintenanceInterval);

    this.bayMaintenanceTimers.set(bay.id, {
      timer,
      lastMaintenance: bay.lastMaintenance,
      efficiency: bay.efficiency,
    });
  }

  /**
   * Perform maintenance on a bay
   */
  private performBayMaintenance(bayId: string): void {
    const bay = this.state.bays.find(b => b.id === bayId);
    if (!bay) {
      return;
    }

    // Calculate maintenance cost based on bay tier and ship count
    const baseCosts = bay.maintenanceCost;
    const shipCount = bay.ships.length;
    const actualCosts = baseCosts.map(cost => ({
      type: cost.type,
      amount: Math.ceil(cost.amount * (1 + (shipCount / bay.capacity) * 0.5)),
    }));

    // Check if we can afford maintenance
    const canAfford = actualCosts.every(
      cost => this.resourceManager.getResourceAmount(cost.type) >= cost.amount
    );

    if (canAfford) {
      // Consume resources
      actualCosts.forEach(cost => {
        this.resourceManager.removeResource(cost.type, cost.amount);
      });

      // Update bay efficiency
      bay.efficiency = Math.min(1.0, bay.efficiency + 0.1);
      bay.lastMaintenance = Date.now();

      // Update timer info
      const timerInfo = this.bayMaintenanceTimers.get(bayId);
      if (timerInfo) {
        timerInfo.lastMaintenance = bay.lastMaintenance;
        timerInfo.efficiency = bay.efficiency;
      }

      this.emit('bayMaintained', {
        bayId,
        newEfficiency: bay.efficiency,
        maintenanceCost: actualCosts,
      });
    } else {
      // Decrease efficiency due to missed maintenance
      bay.efficiency = Math.max(0.5, bay.efficiency - 0.1);

      // Update timer info
      const timerInfo = this.bayMaintenanceTimers.get(bayId);
      if (timerInfo) {
        timerInfo.efficiency = bay.efficiency;
      }

      this.emit('bayMaintenanceFailed', {
        bayId,
        newEfficiency: bay.efficiency,
        requiredResources: actualCosts,
      });
    }
  }

  /**
   * Get bay efficiency bonus
   */
  private getBayEfficiencyBonus(bay: ShipHangarBay): number {
    // Base multiplier from tier
    const tierMultiplier = 1 + (bay.tier - 1) * 0.1; // 10% per tier

    // Efficiency factor
    const efficiencyFactor = bay.efficiency;

    // Capacity utilization bonus
    const utilizationBonus =
      bay.ships.length > 0 ? Math.min(1.2, 1 + (bay.ships.length / bay.capacity) * 0.2) : 1.0;

    return tierMultiplier * efficiencyFactor * utilizationBonus;
  }

  /**
   * Assign an officer to a ship
   */
  public assignOfficer(shipId: string, officerId: string): void {
    // Find the ship
    let targetShip: CommonShip | undefined;

    for (const bay of this.state.bays) {
      const ship = bay.ships.find(s => s.id === shipId);
      if (ship) {
        targetShip = ship;
        break;
      }
    }

    if (!targetShip) {
      throw new Error('Ship not found');
    }

    // Get the officer
    const officer = this.officerManager.getOfficer(officerId);
    if (!officer) {
      throw new Error('Officer not found');
    }

    // Check if officer is available
    if (officer.status !== 'available') {
      throw new Error('Officer is not available');
    }

    // Calculate bonuses based on officer skills and specialization
    const bonuses = this.calculateOfficerBonuses(officer, targetShip);

    // Assign officer
    this.assignedOfficers.set(shipId, officerId);
    this.officerManager.assignOfficer(officerId, shipId);

    // Apply bonuses to ship
    this.applyOfficerBonuses(targetShip, bonuses);

    // Emit event
    this.emit('officerAssigned', {
      shipId,
      officerId,
      bonuses,
    });
  }

  /**
   * Unassign an officer from a ship
   */
  public unassignOfficer(shipId: string): void {
    const officerId = this.assignedOfficers.get(shipId);
    if (!officerId) {
      return;
    }

    // Find the ship
    let targetShip: CommonShip | undefined;

    for (const bay of this.state.bays) {
      const ship = bay.ships.find(s => s.id === shipId);
      if (ship) {
        targetShip = ship;
        break;
      }
    }

    if (!targetShip) {
      return;
    }

    // Remove bonuses
    this.removeOfficerBonuses(targetShip);

    // Unassign officer
    this.assignedOfficers.delete(shipId);
    this.officerManager.assignOfficer(officerId, null as unknown as string);

    // Emit event
    this.emit('officerUnassigned', {
      shipId,
      officerId,
    });
  }

  /**
   * Calculate bonuses provided by an officer
   */
  private calculateOfficerBonuses(
    officer: Officer,
    ship: CommonShip
  ): {
    buildSpeed?: number;
    resourceEfficiency?: number;
    combatEffectiveness?: number;
  } {
    const bonuses = {
      buildSpeed: 0,
      resourceEfficiency: 0,
      combatEffectiveness: 0,
    };

    // Base bonuses from level
    const levelBonus = (officer.level - 1) * 0.05; // 5% per level

    // Specialization bonuses
    switch (officer.specialization) {
      case 'War':
        if (ship.category === 'war') {
          bonuses.combatEffectiveness = 0.2 + levelBonus; // 20% base + level bonus
          bonuses.buildSpeed = 0.1 + levelBonus; // 10% base + level bonus
        }
        break;
      case 'Mining':
        bonuses.resourceEfficiency = 0.2 + levelBonus; // 20% base + level bonus
        break;
      case 'Recon':
        bonuses.buildSpeed = 0.15 + levelBonus; // 15% base + level bonus
        bonuses.resourceEfficiency = 0.1 + levelBonus; // 10% base + level bonus
        break;
    }

    // Skill bonuses
    bonuses.combatEffectiveness += officer.skills.combat * 0.01; // 1% per combat skill
    bonuses.resourceEfficiency += officer.skills.technical * 0.01; // 1% per technical skill
    bonuses.buildSpeed += officer.skills.leadership * 0.01; // 1% per leadership skill

    return bonuses;
  }

  /**
   * Apply officer bonuses to a ship
   */
  private applyOfficerBonuses(
    ship: CommonShip,
    bonuses: {
      buildSpeed?: number;
      resourceEfficiency?: number;
      combatEffectiveness?: number;
    }
  ): void {
    if (bonuses.combatEffectiveness) {
      // Apply combat bonuses
      ship.stats.weapons?.forEach(mount => {
        if (mount.currentWeapon) {
          const stats = mount.currentWeapon.state.currentStats;
          stats.damage *= 1 + bonuses.combatEffectiveness!;
          stats.accuracy *= 1 + bonuses.combatEffectiveness! * 0.5;
        }
      });
    }

    // Store bonuses for build speed and resource efficiency
    ship.officerBonuses = bonuses;
  }

  /**
   * Remove officer bonuses from a ship
   */
  private removeOfficerBonuses(ship: CommonShip): void {
    if (!ship.officerBonuses) {
      return;
    }

    if (ship.officerBonuses.combatEffectiveness) {
      // Remove combat bonuses
      ship.stats.weapons?.forEach(mount => {
        if (mount.currentWeapon) {
          const stats = mount.currentWeapon.state.currentStats;
          stats.damage /= 1 + ship.officerBonuses!.combatEffectiveness!;
          stats.accuracy /= 1 + ship.officerBonuses!.combatEffectiveness! * 0.5;
        }
      });
    }

    // Clear stored bonuses
    delete ship.officerBonuses;
  }

  // Update cleanup method
  public cleanup(): void {
    // Clear all repair timers
    this.activeRepairs.forEach((repair, shipId) => {
      clearTimeout(repair.timer);
      // Update ship status
      const ship = this.getDockedShips().find(s => s.id === shipId);
      if (ship) {
        ship.status = 'damaged'; // Revert to damaged state if repair was interrupted
      }
    });
    this.activeRepairs.clear();

    // Clear all upgrade timers
    this.activeUpgrades.forEach((upgrade, shipId) => {
      clearTimeout(upgrade.timer);
      // Revert ship status and refund resources for interrupted upgrades
      const ship = this.getDockedShips().find(s => s.id === shipId);
      if (ship) {
        ship.status = 'ready';
        // Refund 75% of remaining upgrade costs
        const remainingProgress = 1 - (Date.now() - upgrade.startTime) / upgrade.duration;
        upgrade.resourceCost.forEach(cost => {
          const refundAmount = Math.floor(cost.amount * remainingProgress * 0.75);
          this.resourceManager.addResource(cost.type, refundAmount);
        });
      }
    });
    this.activeUpgrades.clear();

    // Clear all ability timers
    this.activeAbilities.forEach((active, key) => {
      clearTimeout(active.timer);
      // Parse shipId and ability name from compound key
      const [shipId, abilityName] = key.split('-');
      const ship = this.getDockedShips().find(s => s.id === shipId);
      if (ship) {
        const ability = ship.abilities.find(a => a.name === abilityName);
        if (ability) {
          ability.active = false;
          ability.effect.active = false;
        }
      }
    });
    this.activeAbilities.clear();

    // Clear all cooldown timers
    this.abilityCooldowns.forEach((cooldown, key) => {
      clearTimeout(cooldown.timer);
      // Parse shipId and ability name from compound key
      const [shipId, abilityName] = key.split('-');
      const ship = this.getDockedShips().find(s => s.id === shipId);
      if (ship) {
        const ability = ship.abilities.find(a => a.name === abilityName);
        if (ability) {
          ability.effect.cooldown = 0; // Reset cooldown
        }
      }
    });
    this.abilityCooldowns.clear();

    // Clear all weapon states
    this.state.bays.forEach(bay => {
      bay.ships.forEach(ship => {
        ship.stats.weapons.forEach(mount => {
          if (mount.currentWeapon) {
            mount.currentWeapon.state.status = 'ready';
            mount.currentWeapon.state.effects = [];
          }
        });
      });
    });

    // Clear all bay maintenance timers
    this.bayMaintenanceTimers.forEach((info, bayId) => {
      clearTimeout(info.timer);
      // Update bay maintenance state
      const bay = this.state.bays.find(b => b.id === bayId);
      if (bay) {
        bay.lastMaintenance = info.lastMaintenance;
        bay.efficiency = info.efficiency;
        // Emit maintenance failed event since cleanup interrupts maintenance
        this.emit('bayMaintenanceFailed', {
          bayId,
          newEfficiency: bay.efficiency,
          requiredResources: bay.maintenanceCost,
        });
      }
    });
    this.bayMaintenanceTimers.clear();

    // Clean up officer assignments and remove bonuses
    for (const [shipId, officerId] of this.assignedOfficers) {
      const ship = this.getDockedShips().find(s => s.id === shipId);
      if (ship) {
        this.removeOfficerBonuses(ship); // Remove any active bonuses
        this.officerManager.assignOfficer(officerId, null as unknown as string); // Free up the officer
      }
      this.unassignOfficer(shipId);
    }
    this.assignedOfficers.clear();
  }

  // Add effect handling methods
  private applyShipEffect(ship: ShipWithState, effect: ShipEffect): void {
    // Initialize ship state if needed
    if (!ship.state) {
      ship.state = { activeEffects: [], effectHistory: [] };
    }

    // Apply effect modifiers
    switch (effect.type) {
      case 'buff':
        if (effect.name.includes('damage')) {
          ship.stats.weapons?.forEach(mount => {
            if (mount.currentWeapon) {
              mount.currentWeapon.state.currentStats.damage *= 1 + effect.magnitude;
            }
          });
        }
        break;
      case 'debuff':
        if (effect.name.includes('shield')) {
          ship.stats.shield *= 1 - effect.magnitude;
        }
        break;
      case 'status':
        // Just track status effects
        break;
    }

    // Add to active effects
    ship.state.activeEffects.push(effect);
    ship.state.effectHistory.push({
      effect,
      appliedAt: Date.now(),
    });

    // Set up effect expiration
    if (effect.duration > 0) {
      setTimeout(() => {
        this.removeShipEffect(ship, effect);
      }, effect.duration * 1000);
    }
  }

  private removeShipEffect(ship: ShipWithState, effect: ShipEffect): void {
    if (!ship.state) {
      return;
    }

    // Remove effect modifiers
    switch (effect.type) {
      case 'buff':
        if (effect.name.includes('damage')) {
          ship.stats.weapons?.forEach(mount => {
            if (mount.currentWeapon) {
              mount.currentWeapon.state.currentStats.damage /= 1 + effect.magnitude;
            }
          });
        }
        break;
      case 'debuff':
        if (effect.name.includes('shield')) {
          ship.stats.shield /= 1 - effect.magnitude;
        }
        break;
    }

    // Remove from active effects
    ship.state.activeEffects = ship.state.activeEffects.filter(e => e !== effect);

    // Update history
    const historyEntry = ship.state.effectHistory.find(h => h.effect === effect && !h.removedAt);
    if (historyEntry) {
      historyEntry.removedAt = Date.now();
    }
  }

  public hasOfficerMeetingRequirements(minLevel: number, specialization: string): boolean {
    // Check all assigned officers for one that meets requirements
    for (const [_, officerId] of this.assignedOfficers) {
      const officer = this.officerManager.getOfficer(officerId);
      if (officer && officer.level >= minLevel && officer.specialization === specialization) {
        return true;
      }
    }
    return false;
  }
}

import { moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import {
  BaseModule,
  ModuleType,
  SubModule,
  SubModuleConfig,
  SubModuleEffect,
  SubModuleRequirements,
  SubModuleType,
} from '../../types/buildings/ModuleTypes';
import { ResourceType } from '../../types/resources/ResourceTypes';
import { resourceManager } from '../game/ResourceManager';
import { moduleManager } from './ModuleManager';

/**
 * Sub-module attachment result
 */
export interface SubModuleAttachmentResult {
  success: boolean;
  subModuleId?: string;
  parentModuleId?: string;
  error?: string;
}

/**
 * Sub-module effect application result
 */
export interface EffectApplicationResult {
  success: boolean;
  effectType: string;
  target: string;
  value: number;
  error?: string;
}

/**
 * SubModuleManager
 * Manages the creation, attachment, activation, and effects of sub-modules
 */
export class SubModuleManager {
  private subModules: Map<string, SubModule>;
  private configs: Map<SubModuleType, SubModuleConfig>;
  private effectHandlers: Map<
    string,
    (effect: SubModuleEffect, moduleId: string) => EffectApplicationResult
  >;

  constructor() {
    this.subModules = new Map();
    this.configs = new Map();
    this.effectHandlers = new Map();

    // Register default effect handlers
    this.registerDefaultEffectHandlers();

    // Subscribe to module events
    moduleEventBus.subscribe('MODULE_UPGRADED' as ModuleEventType, this.handleModuleUpgraded);
    moduleEventBus.subscribe('MODULE_ACTIVATED' as ModuleEventType, this.handleModuleActivated);
    moduleEventBus.subscribe('MODULE_DEACTIVATED' as ModuleEventType, this.handleModuleDeactivated);
  }

  /**
   * Register default effect handlers
   */
  private registerDefaultEffectHandlers(): void {
    // Stat boost effect handler
    this.registerEffectHandler('stat_boost', (effect, moduleId) => {
      const module = moduleManager.getModule(moduleId);
      if (!module) {
        return {
          success: false,
          effectType: effect.type,
          target: effect.target,
          value: effect.value,
          error: `Module ${moduleId} not found`,
        };
      }

      // Apply stat boost (this would be implemented in the actual module)
      console.debug(
        `[SubModuleManager] Applied stat boost to ${moduleId}: ${effect.target} ${effect.isPercentage ? '+' : ''}${effect.value}${effect.isPercentage ? '%' : ''}`
      );

      return {
        success: true,
        effectType: effect.type,
        target: effect.target,
        value: effect.value,
      };
    });

    // Resource boost effect handler
    this.registerEffectHandler('resource_boost', (effect, moduleId) => {
      const module = moduleManager.getModule(moduleId);
      if (!module) {
        return {
          success: false,
          effectType: effect.type,
          target: effect.target,
          value: effect.value,
          error: `Module ${moduleId} not found`,
        };
      }

      // Apply resource boost
      console.debug(
        `[SubModuleManager] Applied resource boost to ${moduleId}: ${effect.target} ${effect.isPercentage ? '+' : ''}${effect.value}${effect.isPercentage ? '%' : ''}`
      );

      return {
        success: true,
        effectType: effect.type,
        target: effect.target,
        value: effect.value,
      };
    });

    // Unlock ability effect handler
    this.registerEffectHandler('unlock_ability', (effect, moduleId) => {
      const module = moduleManager.getModule(moduleId);
      if (!module) {
        return {
          success: false,
          effectType: effect.type,
          target: effect.target,
          value: effect.value,
          error: `Module ${moduleId} not found`,
        };
      }

      // Unlock ability
      console.debug(`[SubModuleManager] Unlocked ability for ${moduleId}: ${effect.target}`);

      return {
        success: true,
        effectType: effect.type,
        target: effect.target,
        value: effect.value,
      };
    });

    // Reduce cost effect handler
    this.registerEffectHandler('reduce_cost', (effect, moduleId) => {
      const module = moduleManager.getModule(moduleId);
      if (!module) {
        return {
          success: false,
          effectType: effect.type,
          target: effect.target,
          value: effect.value,
          error: `Module ${moduleId} not found`,
        };
      }

      // Reduce cost
      console.debug(
        `[SubModuleManager] Applied cost reduction to ${moduleId}: ${effect.target} ${effect.isPercentage ? '-' : ''}${effect.value}${effect.isPercentage ? '%' : ''}`
      );

      return {
        success: true,
        effectType: effect.type,
        target: effect.target,
        value: effect.value,
      };
    });

    // Automation effect handler
    this.registerEffectHandler('automation', (effect, moduleId) => {
      const module = moduleManager.getModule(moduleId);
      if (!module) {
        return {
          success: false,
          effectType: effect.type,
          target: effect.target,
          value: effect.value,
          error: `Module ${moduleId} not found`,
        };
      }

      // Apply automation
      console.debug(`[SubModuleManager] Applied automation to ${moduleId}: ${effect.target}`);

      return {
        success: true,
        effectType: effect.type,
        target: effect.target,
        value: effect.value,
      };
    });

    // Special effect handler
    this.registerEffectHandler('special', (effect, moduleId) => {
      const module = moduleManager.getModule(moduleId);
      if (!module) {
        return {
          success: false,
          effectType: effect.type,
          target: effect.target,
          value: effect.value,
          error: `Module ${moduleId} not found`,
        };
      }

      // Apply special effect
      console.debug(`[SubModuleManager] Applied special effect to ${moduleId}: ${effect.target}`);

      return {
        success: true,
        effectType: effect.type,
        target: effect.target,
        value: effect.value,
      };
    });
  }

  /**
   * Register a sub-module configuration
   */
  public registerSubModuleConfig(config: SubModuleConfig): void {
    this.configs.set(config.type, config);
  }

  /**
   * Register a custom effect handler
   */
  public registerEffectHandler(
    effectType: string,
    handler: (effect: SubModuleEffect, moduleId: string) => EffectApplicationResult
  ): void {
    this.effectHandlers.set(effectType, handler);
  }

  /**
   * Create a new sub-module
   */
  public createSubModule(type: SubModuleType, parentModuleId: string): SubModule | null {
    const config = this.configs.get(type);
    if (!config) {
      console.error(`[SubModuleManager] No configuration found for sub-module type: ${type}`);
      return null;
    }

    const parentModule = moduleManager.getModule(parentModuleId);
    if (!parentModule) {
      console.error(`[SubModuleManager] Parent module not found: ${parentModuleId}`);
      return null;
    }

    // Check if parent module supports sub-modules
    const parentConfig = (moduleManager as any).configs.get(parentModule.type);
    if (!parentConfig || !parentConfig.subModuleSupport) {
      console.error(
        `[SubModuleManager] Parent module does not support sub-modules: ${parentModuleId}`
      );
      return null;
    }

    // Check if parent module allows this sub-module type
    if (!parentConfig.subModuleSupport.allowedTypes.includes(type)) {
      console.error(
        `[SubModuleManager] Sub-module type ${type} not allowed for parent module ${parentModuleId}`
      );
      return null;
    }

    // Check if parent module has reached max sub-modules
    const currentSubModules = parentModule.subModules || [];
    if (currentSubModules.length >= parentConfig.subModuleSupport.maxSubModules) {
      console.error(
        `[SubModuleManager] Parent module ${parentModuleId} has reached max sub-modules`
      );
      return null;
    }

    // Check requirements
    if (!this.checkRequirements(config.requirements, parentModule)) {
      console.error(
        `[SubModuleManager] Requirements not met for sub-module ${type} on parent module ${parentModuleId}`
      );
      return null;
    }

    // Create the sub-module
    const subModule: SubModule = {
      id: `${type}-${Date.now()}`,
      name: config.name,
      type,
      parentModuleId,
      isActive: false,
      level: 1,
      status: 'constructing',
      progress: 0,
      effects: [...config.effects],
      requirements: config.requirements,
    };

    // Store the sub-module
    this.subModules.set(subModule.id, subModule);

    // Add the sub-module to the parent module
    if (!parentModule.subModules) {
      parentModule.subModules = [];
    }
    parentModule.subModules.push(subModule);

    // Emit creation event
    moduleEventBus.emit({
      type: 'SUB_MODULE_CREATED' as ModuleEventType,
      moduleId: parentModuleId,
      moduleType: parentModule.type,
      timestamp: Date.now(),
      data: { subModuleId: subModule.id, subModuleType: subModule.type },
    });

    return subModule;
  }

  /**
   * Check if requirements are met for a sub-module
   */
  private checkRequirements(
    requirements: SubModuleRequirements,
    parentModule: BaseModule
  ): boolean {
    // Check parent module level
    if (parentModule.level < requirements.parentModuleLevel) {
      return false;
    }

    // Check parent module type
    if (!requirements.parentModuleTypes.includes(parentModule.type)) {
      return false;
    }

    // Check resource costs
    for (const cost of requirements.resourceCosts) {
      if (resourceManager.getResourceAmount(cost.type as ResourceType) < cost.amount) {
        return false;
      }
    }

    // Check incompatibilities
    if (requirements.incompatibleWith && parentModule.subModules) {
      for (const subModule of parentModule.subModules) {
        if (requirements.incompatibleWith.includes(subModule.type)) {
          return false;
        }
      }
    }

    return true;
  }

  /**
   * Attach a sub-module to a parent module
   */
  public attachSubModule(subModuleId: string, parentModuleId: string): SubModuleAttachmentResult {
    const subModule = this.subModules.get(subModuleId);
    if (!subModule) {
      return {
        success: false,
        error: `Sub-module ${subModuleId} not found`,
      };
    }

    const parentModule = moduleManager.getModule(parentModuleId);
    if (!parentModule) {
      return {
        success: false,
        error: `Parent module ${parentModuleId} not found`,
      };
    }

    // Check if parent module supports sub-modules
    const parentConfig = (moduleManager as any).configs.get(parentModule.type);
    if (!parentConfig || !parentConfig.subModuleSupport) {
      return {
        success: false,
        error: `Parent module does not support sub-modules: ${parentModuleId}`,
      };
    }

    // Check if parent module allows this sub-module type
    if (!parentConfig.subModuleSupport.allowedTypes.includes(subModule.type)) {
      return {
        success: false,
        error: `Sub-module type ${subModule.type} not allowed for parent module ${parentModuleId}`,
      };
    }

    // Check if parent module has reached max sub-modules
    const currentSubModules = parentModule.subModules || [];
    if (currentSubModules.length >= parentConfig.subModuleSupport.maxSubModules) {
      return {
        success: false,
        error: `Parent module ${parentModuleId} has reached max sub-modules`,
      };
    }

    // Update sub-module parent
    subModule.parentModuleId = parentModuleId;

    // Add the sub-module to the parent module
    if (!parentModule.subModules) {
      parentModule.subModules = [];
    }
    parentModule.subModules.push(subModule);

    // Emit attachment event
    moduleEventBus.emit({
      type: 'SUB_MODULE_ATTACHED' as ModuleEventType,
      moduleId: parentModuleId,
      moduleType: parentModule.type,
      timestamp: Date.now(),
      data: { subModuleId, subModuleType: subModule.type },
    });

    return {
      success: true,
      subModuleId,
      parentModuleId,
    };
  }

  /**
   * Detach a sub-module from its parent module
   */
  public detachSubModule(subModuleId: string): SubModuleAttachmentResult {
    const subModule = this.subModules.get(subModuleId);
    if (!subModule) {
      return {
        success: false,
        error: `Sub-module ${subModuleId} not found`,
      };
    }

    const { parentModuleId } = subModule;
    const parentModule = moduleManager.getModule(parentModuleId);
    if (!parentModule) {
      return {
        success: false,
        error: `Parent module ${parentModuleId} not found`,
      };
    }

    // Remove the sub-module from the parent module
    if (parentModule.subModules) {
      parentModule.subModules = parentModule.subModules.filter(sm => sm.id !== subModuleId);
    }

    // Emit detachment event
    moduleEventBus.emit({
      type: 'SUB_MODULE_DETACHED' as ModuleEventType,
      moduleId: parentModuleId,
      moduleType: parentModule.type,
      timestamp: Date.now(),
      data: { subModuleId, subModuleType: subModule.type },
    });

    return {
      success: true,
      subModuleId,
      parentModuleId,
    };
  }

  /**
   * Activate a sub-module
   */
  public activateSubModule(subModuleId: string): boolean {
    const subModule = this.subModules.get(subModuleId);
    if (!subModule) {
      console.error(`[SubModuleManager] Sub-module ${subModuleId} not found`);
      return false;
    }

    // Check if parent module is active
    const parentModule = moduleManager.getModule(subModule.parentModuleId);
    if (!parentModule || !parentModule.isActive) {
      console.error(`[SubModuleManager] Parent module ${subModule.parentModuleId} is not active`);
      return false;
    }

    // Check if sub-module is already active
    if (subModule.isActive) {
      return true;
    }

    // Activate the sub-module
    subModule.isActive = true;
    subModule.status = 'active';

    // Apply effects
    this.applySubModuleEffects(subModule);

    // Emit activation event
    moduleEventBus.emit({
      type: 'SUB_MODULE_ACTIVATED' as ModuleEventType,
      moduleId: subModule.parentModuleId,
      moduleType: parentModule.type,
      timestamp: Date.now(),
      data: { subModuleId, subModuleType: subModule.type },
    });

    return true;
  }

  /**
   * Deactivate a sub-module
   */
  public deactivateSubModule(subModuleId: string): boolean {
    const subModule = this.subModules.get(subModuleId);
    if (!subModule) {
      console.error(`[SubModuleManager] Sub-module ${subModuleId} not found`);
      return false;
    }

    // Check if sub-module is already inactive
    if (!subModule.isActive) {
      return true;
    }

    // Deactivate the sub-module
    subModule.isActive = false;
    subModule.status = 'inactive';

    // Remove effects
    this.removeSubModuleEffects(subModule);

    // Emit deactivation event
    moduleEventBus.emit({
      type: 'SUB_MODULE_DEACTIVATED' as ModuleEventType,
      moduleId: subModule.parentModuleId,
      moduleType: moduleManager.getModule(subModule.parentModuleId)?.type as ModuleType,
      timestamp: Date.now(),
      data: { subModuleId, subModuleType: subModule.type },
    });

    return true;
  }

  /**
   * Apply sub-module effects
   */
  private applySubModuleEffects(subModule: SubModule): EffectApplicationResult[] {
    const results: EffectApplicationResult[] = [];

    for (const effect of subModule.effects) {
      const handler = this.effectHandlers.get(effect.type);
      if (handler) {
        const result = handler(effect, subModule.parentModuleId);
        results.push(result);
      } else {
        results.push({
          success: false,
          effectType: effect.type,
          target: effect.target,
          value: effect.value,
          error: `No handler found for effect type: ${effect.type}`,
        });
      }
    }

    return results;
  }

  /**
   * Remove sub-module effects
   */
  private removeSubModuleEffects(subModule: SubModule): void {
    // This would be implemented to reverse the effects
    // For now, we'll just log the removal
    console.debug(`[SubModuleManager] Removed effects for sub-module ${subModule.id}`);
  }

  /**
   * Upgrade a sub-module
   */
  public upgradeSubModule(subModuleId: string): boolean {
    const subModule = this.subModules.get(subModuleId);
    if (!subModule) {
      console.error(`[SubModuleManager] Sub-module ${subModuleId} not found`);
      return false;
    }

    // Check if sub-module is active
    if (!subModule.isActive) {
      console.error(`[SubModuleManager] Sub-module ${subModuleId} is not active`);
      return false;
    }

    // Get the config
    const config = this.configs.get(subModule.type);
    if (!config) {
      console.error(
        `[SubModuleManager] No configuration found for sub-module type: ${subModule.type}`
      );
      return false;
    }

    // Check resource costs (scaled by level)
    const levelMultiplier = Math.pow(1.5, subModule.level);
    for (const cost of config.requirements.resourceCosts) {
      const scaledAmount = Math.ceil(cost.amount * levelMultiplier);
      if (resourceManager.getResourceAmount(cost.type as ResourceType) < scaledAmount) {
        console.error(
          `[SubModuleManager] Insufficient resources for upgrade: ${cost.type} ${scaledAmount}`
        );
        return false;
      }

      // Consume resources
      resourceManager.removeResource(cost.type as ResourceType, scaledAmount);
    }

    // Remove current effects
    this.removeSubModuleEffects(subModule);

    // Upgrade the sub-module
    subModule.level++;

    // Scale effects based on level
    for (const effect of subModule.effects) {
      if (effect.isPercentage) {
        effect.value = Math.round(effect.value * (1 + 0.1 * (subModule.level - 1)));
      } else {
        effect.value = Math.round(effect.value * (1 + 0.2 * (subModule.level - 1)));
      }
    }

    // Apply new effects
    this.applySubModuleEffects(subModule);

    // Emit upgrade event
    moduleEventBus.emit({
      type: 'SUB_MODULE_UPGRADED' as ModuleEventType,
      moduleId: subModule.parentModuleId,
      moduleType: moduleManager.getModule(subModule.parentModuleId)?.type as ModuleType,
      timestamp: Date.now(),
      data: { subModuleId, subModuleType: subModule.type, newLevel: subModule.level },
    });

    return true;
  }

  /**
   * Get a sub-module by ID
   */
  public getSubModule(subModuleId: string): SubModule | undefined {
    return this.subModules.get(subModuleId);
  }

  /**
   * Get all sub-modules for a parent module
   */
  public getSubModulesForParent(parentModuleId: string): SubModule[] {
    return Array.from(this.subModules.values()).filter(sm => sm.parentModuleId === parentModuleId);
  }

  /**
   * Get all sub-modules of a specific type
   */
  public getSubModulesByType(type: SubModuleType): SubModule[] {
    return Array.from(this.subModules.values()).filter(sm => sm.type === type);
  }

  /**
   * Get all active sub-modules
   */
  public getActiveSubModules(): SubModule[] {
    return Array.from(this.subModules.values()).filter(sm => sm.isActive);
  }

  /**
   * Handle module upgraded event
   */
  private handleModuleUpgraded = (event: any): void => {
    const { moduleId, newLevel } = event.data;

    // Check if module has sub-modules
    const module = moduleManager.getModule(moduleId);
    if (!module || !module.subModules || module.subModules.length === 0) {
      return;
    }

    // Update sub-modules based on parent module upgrade
    for (const subModule of module.subModules) {
      if (subModule.isActive) {
        // Reapply effects with potentially new values
        this.removeSubModuleEffects(subModule);
        this.applySubModuleEffects(subModule);
      }
    }
  };

  /**
   * Handle module activated event
   */
  private handleModuleActivated = (event: any): void => {
    const { moduleId } = event.data;

    // Check if module has sub-modules
    const module = moduleManager.getModule(moduleId);
    if (!module || !module.subModules || module.subModules.length === 0) {
      return;
    }

    // Activate sub-modules that should be active
    for (const subModule of module.subModules) {
      if (subModule.status === 'active' && !subModule.isActive) {
        this.activateSubModule(subModule.id);
      }
    }
  };

  /**
   * Handle module deactivated event
   */
  private handleModuleDeactivated = (event: any): void => {
    const { moduleId } = event.data;

    // Check if module has sub-modules
    const module = moduleManager.getModule(moduleId);
    if (!module || !module.subModules || module.subModules.length === 0) {
      return;
    }

    // Deactivate all sub-modules
    for (const subModule of module.subModules) {
      if (subModule.isActive) {
        this.deactivateSubModule(subModule.id);
      }
    }
  };

  /**
   * Clean up resources
   */
  public cleanup(): void {
    // Unsubscribe from events
    const unsubscribeUpgraded = moduleEventBus.subscribe(
      'MODULE_UPGRADED' as ModuleEventType,
      this.handleModuleUpgraded
    );
    const unsubscribeActivated = moduleEventBus.subscribe(
      'MODULE_ACTIVATED' as ModuleEventType,
      this.handleModuleActivated
    );
    const unsubscribeDeactivated = moduleEventBus.subscribe(
      'MODULE_DEACTIVATED' as ModuleEventType,
      this.handleModuleDeactivated
    );

    if (typeof unsubscribeUpgraded === 'function') {
      unsubscribeUpgraded();
    }
    if (typeof unsubscribeActivated === 'function') {
      unsubscribeActivated();
    }
    if (typeof unsubscribeDeactivated === 'function') {
      unsubscribeDeactivated();
    }

    // Clear data
    this.subModules.clear();
    this.configs.clear();
    this.effectHandlers.clear();
  }
}

// Export singleton instance
export const subModuleManager = new SubModuleManager();

import {
  ResourceExchangeRate,
  ResourceState,
  ResourceType,
} from '../../types/resources/ResourceTypes';

/**
 * Exchange transaction
 */
export interface ExchangeTransaction {
  id: string;
  fromType: ResourceType;
  toType: ResourceType;
  fromAmount: number;
  toAmount: number;
  rate: number;
  timestamp: number;
  source?: string;
  target?: string;
}

/**
 * Market condition
 */
export type MarketCondition = 'stable' | 'volatile' | 'bullish' | 'bearish';

/**
 * Exchange rate modifier
 */
export interface ExchangeRateModifier {
  id: string;
  description: string;
  affectedTypes: ResourceType[];
  multiplier: number;
  expiresAt?: number;
  active?: boolean;
  sourceType?: ResourceType;
  targetType?: ResourceType;
}

/**
 * Extended resource exchange rate with additional properties
 */
export interface ExtendedRate extends ResourceExchangeRate {
  sourceType?: ResourceType;
  targetType?: ResourceType;
}

/**
 * Exchange path step
 */
export interface ExchangePathStep {
  sourceType: ResourceType;
  targetType: ResourceType;
  rate: number;
  inputAmount: number;
  outputAmount: number;
}

/**
 * Exchange path
 */
export interface ExchangePath {
  steps: ExchangePathStep[];
  totalRate: number;
  inputAmount: number;
  outputAmount: number;
}

/**
 * Resource Exchange Manager
 * Manages resource exchange rates, conversions, and market dynamics
 */
export class ResourceExchangeManager {
  private baseRates: Map<string, ResourceExchangeRate>;
  private currentRates: Map<string, ResourceExchangeRate>;
  private modifiers: Map<string, ExchangeRateModifier>;
  private transactions: ExchangeTransaction[];
  private resourceStates: Map<ResourceType, ResourceState>;
  private marketCondition: MarketCondition;
  private lastMarketUpdate: number;
  private marketUpdateInterval: number;
  private maxTransactionHistory: number;

  constructor(marketUpdateInterval = 60000, maxTransactionHistory = 100) {
    this.baseRates = new Map();
    this.currentRates = new Map();
    this.modifiers = new Map();
    this.transactions = [];
    this.resourceStates = new Map();
    this.marketCondition = 'stable';
    this.lastMarketUpdate = Date.now();
    this.marketUpdateInterval = marketUpdateInterval;
    this.maxTransactionHistory = maxTransactionHistory;

    // Initialize with default exchange rates
    this.initializeDefaultRates();
  }

  /**
   * Initialize default exchange rates
   */
  private initializeDefaultRates(): void {
    // Define base exchange rates between resources
    this.registerExchangeRate({
      fromType: 'minerals',
      toType: 'energy',
      rate: 0.5, // 2 minerals = 1 energy
      minAmount: 10,
      maxAmount: 1000,
      cooldown: 5000,
    });

    this.registerExchangeRate({
      fromType: 'energy',
      toType: 'minerals',
      rate: 1.8, // 1 energy = 1.8 minerals
      minAmount: 5,
      maxAmount: 500,
      cooldown: 5000,
    });

    this.registerExchangeRate({
      fromType: 'minerals',
      toType: 'research',
      rate: 0.2, // 5 minerals = 1 research
      minAmount: 20,
      maxAmount: 2000,
      cooldown: 10000,
    });

    this.registerExchangeRate({
      fromType: 'energy',
      toType: 'research',
      rate: 0.3, // 3.33 energy = 1 research
      minAmount: 10,
      maxAmount: 1000,
      cooldown: 10000,
    });

    this.registerExchangeRate({
      fromType: 'gas',
      toType: 'energy',
      rate: 2.0, // 1 gas = 2 energy
      minAmount: 5,
      maxAmount: 500,
      cooldown: 5000,
    });

    this.registerExchangeRate({
      fromType: 'plasma',
      toType: 'energy',
      rate: 3.0, // 1 plasma = 3 energy
      minAmount: 5,
      maxAmount: 300,
      cooldown: 8000,
    });

    this.registerExchangeRate({
      fromType: 'exotic',
      toType: 'research',
      rate: 5.0, // 1 exotic = 5 research
      minAmount: 1,
      maxAmount: 100,
      cooldown: 15000,
    });
  }

  /**
   * Update resource state
   */
  public updateResourceState(type: ResourceType, state: ResourceState): void {
    this.resourceStates.set(type, state);
  }

  /**
   * Register an exchange rate
   */
  public registerExchangeRate(rate: ResourceExchangeRate): boolean {
    if (!rate.fromType || !rate.toType || rate.rate <= 0) {
      console.error('Invalid exchange rate:', rate);
      return false;
    }

    const rateKey = this.getRateKey(rate.fromType, rate.toType);
    this.baseRates.set(rateKey, { ...rate });
    this.currentRates.set(rateKey, { ...rate });
    return true;
  }

  /**
   * Unregister an exchange rate
   */
  public unregisterExchangeRate(fromType: ResourceType, toType: ResourceType): boolean {
    const rateKey = this.getRateKey(fromType, toType);
    if (!this.baseRates.has(rateKey)) {
      return false;
    }

    this.baseRates.delete(rateKey);
    this.currentRates.delete(rateKey);
    return true;
  }

  /**
   * Get exchange rate key
   */
  private getRateKey(fromType: ResourceType, toType: ResourceType): string {
    return `${fromType}-${toType}`;
  }

  /**
   * Get current exchange rate
   */
  public getExchangeRate(
    fromType: ResourceType,
    toType: ResourceType
  ): ResourceExchangeRate | undefined {
    const rateKey = this.getRateKey(fromType, toType);
    return this.currentRates.get(rateKey);
  }

  /**
   * Calculate exchange amount
   */
  public calculateExchangeAmount(
    fromType: ResourceType,
    toType: ResourceType,
    amount: number
  ): number {
    const rate = this.getExchangeRate(fromType, toType);
    if (!rate) {
      return 0;
    }

    // Check if amount is within limits
    if (rate.minAmount && amount < rate.minAmount) {
      console.warn(`Exchange amount ${amount} is below minimum ${rate.minAmount}`);
      return 0;
    }

    if (rate.maxAmount && amount > rate.maxAmount) {
      console.warn(`Exchange amount ${amount} is above maximum ${rate.maxAmount}`);
      amount = rate.maxAmount;
    }

    return amount * rate.rate;
  }

  /**
   * Execute resource exchange
   */
  public executeExchange(
    fromType: ResourceType,
    toType: ResourceType,
    amount: number,
    source?: string,
    target?: string
  ): ExchangeTransaction | null {
    // Get exchange rate
    const rate = this.getExchangeRate(fromType, toType);
    if (!rate) {
      console.error(`No exchange rate defined for ${fromType} to ${toType}`);
      return null;
    }

    // Check if amount is within limits
    if (rate.minAmount && amount < rate.minAmount) {
      console.error(`Exchange amount ${amount} is below minimum ${rate.minAmount}`);
      return null;
    }

    if (rate.maxAmount && amount > rate.maxAmount) {
      console.warn(
        `Exchange amount ${amount} is above maximum ${rate.maxAmount}, capping at maximum`
      );
      amount = rate.maxAmount;
    }

    // Check if source has enough resources
    const sourceState = this.resourceStates.get(fromType);
    if (!sourceState || sourceState.current < amount) {
      console.error(`Insufficient ${fromType} resources for exchange`);
      return null;
    }

    // Calculate exchange amount
    const exchangeAmount = this.calculateExchangeAmount(fromType, toType, amount);
    if (exchangeAmount <= 0) {
      console.error(`Invalid exchange amount: ${exchangeAmount}`);
      return null;
    }

    // Update resource states
    sourceState.current -= amount;
    this.resourceStates.set(fromType, sourceState);

    const targetState = this.resourceStates.get(toType);
    if (targetState) {
      targetState.current += exchangeAmount;
      this.resourceStates.set(toType, targetState);
    }

    // Record transaction
    const transaction: ExchangeTransaction = {
      id: `exchange-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      fromType,
      toType,
      fromAmount: amount,
      toAmount: exchangeAmount,
      rate: rate.rate,
      timestamp: Date.now(),
      source,
      target,
    };

    this.transactions.push(transaction);

    // Trim transaction history if needed
    if (this.transactions.length > this.maxTransactionHistory) {
      this.transactions = this.transactions.slice(-this.maxTransactionHistory);
    }

    return transaction;
  }

  /**
   * Register a rate modifier
   */
  public registerRateModifier(modifier: ExchangeRateModifier): boolean {
    if (!modifier.id || !modifier.affectedTypes || modifier.affectedTypes.length === 0) {
      console.error('Invalid rate modifier:', modifier);
      return false;
    }

    this.modifiers.set(modifier.id, modifier);
    this.updateCurrentRates();
    return true;
  }

  /**
   * Unregister a rate modifier
   */
  public unregisterRateModifier(id: string): boolean {
    if (!this.modifiers.has(id)) {
      return false;
    }

    this.modifiers.delete(id);
    this.updateCurrentRates();
    return true;
  }

  /**
   * Update current rates based on modifiers and market conditions
   */
  private updateCurrentRates(): void {
    // Reset current rates to base rates
    this.currentRates = new Map(
      Array.from(this.baseRates.entries()).map(([key, rate]) => [key, { ...rate }])
    );

    // Apply market condition modifier
    const marketModifier = this.getMarketConditionModifier();

    // Apply all active modifiers
    const now = Date.now();
    for (const [id, modifier] of this.modifiers.entries()) {
      // Skip expired modifiers
      if (modifier.expiresAt && modifier.expiresAt < now) {
        this.modifiers.delete(id);
        continue;
      }

      // Apply modifier to affected rates
      for (const [rateKey, rate] of this.currentRates.entries()) {
        const [fromType, toType] = rateKey.split('-') as [ResourceType, ResourceType];

        if (modifier.affectedTypes.includes(fromType) || modifier.affectedTypes.includes(toType)) {
          rate.rate *= modifier.multiplier;
        }
      }
    }

    // Apply market condition modifier to all rates
    for (const rate of this.currentRates.values()) {
      rate.rate *= marketModifier;
    }
  }

  /**
   * Get market condition modifier
   */
  private getMarketConditionModifier(): number {
    switch (this.marketCondition) {
      case 'stable':
        return 1.0;
      case 'volatile':
        // Random fluctuation between 0.8 and 1.2
        return 0.8 + Math.random() * 0.4;
      case 'bullish':
        // Increased rates (1.1 to 1.3)
        return 1.1 + Math.random() * 0.2;
      case 'bearish':
        // Decreased rates (0.7 to 0.9)
        return 0.7 + Math.random() * 0.2;
      default:
        return 1.0;
    }
  }

  /**
   * Update market conditions
   */
  public updateMarketConditions(): void {
    const now = Date.now();

    // Only update at specified intervals
    if (now - this.lastMarketUpdate < this.marketUpdateInterval) {
      return;
    }

    this.lastMarketUpdate = now;

    // Randomly change market condition
    const rand = Math.random();
    if (rand < 0.6) {
      // 60% chance to remain stable
      this.marketCondition = 'stable';
    } else if (rand < 0.75) {
      // 15% chance to become volatile
      this.marketCondition = 'volatile';
    } else if (rand < 0.9) {
      // 15% chance to become bullish
      this.marketCondition = 'bullish';
    } else {
      // 10% chance to become bearish
      this.marketCondition = 'bearish';
    }

    // Update rates based on new market condition
    this.updateCurrentRates();

    console.debug(`[ResourceExchangeManager] Market condition updated to: ${this.marketCondition}`);
  }

  /**
   * Get current market condition
   */
  public getMarketCondition(): MarketCondition {
    return this.marketCondition;
  }

  /**
   * Set market condition (for testing or events)
   */
  public setMarketCondition(condition: MarketCondition): void {
    this.marketCondition = condition;
    this.updateCurrentRates();
  }

  /**
   * Get all exchange rates
   */
  public getAllExchangeRates(): ResourceExchangeRate[] {
    return Array.from(this.currentRates.values());
  }

  /**
   * Get transaction history
   */
  public getTransactionHistory(): ExchangeTransaction[] {
    return [...this.transactions];
  }

  /**
   * Get transaction history for a specific resource type
   */
  public getTransactionHistoryByType(type: ResourceType): ExchangeTransaction[] {
    return this.transactions.filter(
      transaction => transaction.fromType === type || transaction.toType === type
    );
  }

  /**
   * Calculate optimal exchange path
   * Uses a simple version of Dijkstra's algorithm to find the best conversion path
   */
  public calculateOptimalExchangePath(
    fromType: ResourceType,
    toType: ResourceType,
    amount: number
  ): { path: ResourceType[]; rate: number; amount: number } | null {
    // Direct exchange
    const directRate = this.getExchangeRate(fromType, toType);
    if (directRate) {
      return {
        path: [fromType, toType],
        rate: directRate.rate,
        amount: amount * directRate.rate,
      };
    }

    // All resource types
    const allTypes = Array.from(
      new Set(Array.from(this.currentRates.values()).flatMap(rate => [rate.fromType, rate.toType]))
    );

    // Initialize distances
    const distances = new Map<ResourceType, number>();
    const previous = new Map<ResourceType, ResourceType | null>();
    const visited = new Set<ResourceType>();

    for (const type of allTypes) {
      distances.set(type, type === fromType ? 1 : 0);
      previous.set(type, null);
    }

    while (visited.size < allTypes.length) {
      // Find unvisited node with highest distance
      let current: ResourceType | null = null;
      let maxDistance = 0;

      for (const type of allTypes) {
        if (!visited.has(type)) {
          const distance = distances.get(type) || 0;
          if (distance > maxDistance) {
            maxDistance = distance;
            current = type;
          }
        }
      }

      if (!current || maxDistance === 0) {
        break;
      }

      visited.add(current);

      // If we reached the target, we're done
      if (current === toType) {
        break;
      }

      // Check all neighbors
      for (const [rateKey, rate] of this.currentRates.entries()) {
        if (rate.fromType === current) {
          const neighbor = rate.toType;
          const distance = (distances.get(current) || 0) * rate.rate;

          if (distance > (distances.get(neighbor) || 0)) {
            distances.set(neighbor, distance);
            previous.set(neighbor, current);
          }
        }
      }
    }

    // If we couldn't reach the target, return null
    if (!(distances.get(toType) || 0)) {
      return null;
    }

    // Reconstruct path
    const path: ResourceType[] = [];
    let current: ResourceType | null = toType;

    while (current) {
      path.unshift(current);
      current = previous.get(current) || null;
    }

    return {
      path,
      rate: distances.get(toType) || 0,
      amount: amount * (distances.get(toType) || 0),
    };
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.baseRates.clear();
    this.currentRates.clear();
    this.modifiers.clear();
    this.transactions = [];
    this.resourceStates.clear();
  }

  /**
   * Calculate exchange rates based on current modifiers
   */
  private calculateRates(): void {
    // Apply modifiers to base rates
    // Convert Map entries to array to avoid MapIterator error
    const modifierEntries = Array.from(this.modifiers.entries());
    for (const [id, modifier] of modifierEntries) {
      // Check if modifier has active property and it's false
      if (modifier.active === false) {
        continue;
      }

      // Convert Map entries to array to avoid MapIterator error
      const rateEntries = Array.from(this.currentRates.entries());
      for (const [rateKey, rate] of rateEntries) {
        // Cast to extended type for compatibility
        const extendedRate = rate as ExtendedRate;
        const extendedModifier = modifier as ExchangeRateModifier;

        // Check if modifier has sourceType and if it matches the rate's sourceType
        if (
          extendedModifier.sourceType &&
          extendedRate.sourceType !== extendedModifier.sourceType
        ) {
          continue;
        }

        // Check if modifier has targetType and if it matches the rate's targetType
        if (
          extendedModifier.targetType &&
          extendedRate.targetType !== extendedModifier.targetType
        ) {
          continue;
        }

        // Apply modifier
        const newRate = { ...rate };
        newRate.rate *= modifier.multiplier;
        this.currentRates.set(rateKey, newRate);
      }
    }
  }

  /**
   * Find optimal exchange path
   */
  private findOptimalPath(
    sourceType: ResourceType,
    targetType: ResourceType,
    amount: number
  ): ExchangePath | null {
    // Direct exchange
    const directKey = this.getRateKey(sourceType, targetType);
    const directRate = this.currentRates.get(directKey);

    if (directRate) {
      return {
        steps: [
          {
            sourceType,
            targetType,
            rate: directRate.rate,
            inputAmount: amount,
            outputAmount: amount * directRate.rate,
          },
        ],
        totalRate: directRate.rate,
        inputAmount: amount,
        outputAmount: amount * directRate.rate,
      };
    }

    // Try to find a path with one intermediate step
    let bestPath: ExchangePath | null = null;
    let bestRate = 0;

    // Convert Map entries to array to avoid MapIterator error
    const rateEntries = Array.from(this.currentRates.entries());
    for (const [rateKey, rate] of rateEntries) {
      // Cast to extended type for compatibility
      const extendedRate = rate as ExtendedRate;

      if (extendedRate.sourceType !== sourceType) {
        continue;
      }

      const intermediateType = extendedRate.targetType;
      if (!intermediateType) {
        continue;
      }

      const secondKey = this.getRateKey(intermediateType, targetType);
      const secondRate = this.currentRates.get(secondKey);

      if (!secondRate) {
        continue;
      }

      const totalRate = extendedRate.rate * secondRate.rate;
      if (totalRate > bestRate) {
        bestRate = totalRate;
        const intermediateAmount = amount * extendedRate.rate;
        bestPath = {
          steps: [
            {
              sourceType,
              targetType: intermediateType,
              rate: extendedRate.rate,
              inputAmount: amount,
              outputAmount: intermediateAmount,
            },
            {
              sourceType: intermediateType,
              targetType,
              rate: secondRate.rate,
              inputAmount: intermediateAmount,
              outputAmount: intermediateAmount * secondRate.rate,
            },
          ],
          totalRate,
          inputAmount: amount,
          outputAmount: amount * totalRate,
        };
      }
    }

    return bestPath;
  }
}

import {
  ResourceFlow,
  ResourcePriority,
  ResourceState,
  ResourceTransfer,
  ResourceType,
} from '../../types/resources/ResourceTypes';
import {
  validateResourceFlow,
  validateResourceTransfer,
} from '../../utils/resources/resourceValidation';

/**
 * Flow node type
 */
export type FlowNodeType = 'producer' | 'consumer' | 'storage' | 'converter';

/**
 * Flow node interface
 */
export interface FlowNode {
  id: string;
  type: FlowNodeType;
  resources: ResourceType[];
  priority: ResourcePriority;
  capacity?: number;
  efficiency?: number;
  active: boolean;
}

/**
 * Flow connection interface
 */
export interface FlowConnection {
  id: string;
  source: string;
  target: string;
  resourceType: ResourceType;
  maxRate: number;
  currentRate: number;
  priority: ResourcePriority;
  active: boolean;
}

/**
 * Flow network interface
 */
export interface FlowNetwork {
  nodes: Map<string, FlowNode>;
  connections: Map<string, FlowConnection>;
  resourceStates: Map<ResourceType, ResourceState>;
}

/**
 * Flow optimization result
 */
export interface FlowOptimizationResult {
  transfers: ResourceTransfer[];
  updatedConnections: FlowConnection[];
  bottlenecks: string[];
  underutilized: string[];
}

/**
 * Resource Flow Manager
 * Manages and optimizes resource flows between producers, consumers, and storage
 */
export class ResourceFlowManager {
  private network: FlowNetwork;
  private lastOptimization: number;
  private optimizationInterval: number;
  private transferHistory: ResourceTransfer[];
  private maxHistorySize: number;

  constructor(optimizationInterval = 5000) {
    this.network = {
      nodes: new Map<string, FlowNode>(),
      connections: new Map<string, FlowConnection>(),
      resourceStates: new Map<ResourceType, ResourceState>(),
    };
    this.lastOptimization = 0;
    this.optimizationInterval = optimizationInterval;
    this.transferHistory = [];
    this.maxHistorySize = 100;
  }

  /**
   * Register a flow node
   */
  public registerNode(node: FlowNode): boolean {
    if (!node.id || !node.resources || node.resources.length === 0) {
      console.error('Invalid flow node:', node);
      return false;
    }

    this.network.nodes.set(node.id, node);
    return true;
  }

  /**
   * Unregister a flow node
   */
  public unregisterNode(id: string): boolean {
    if (!this.network.nodes.has(id)) {
      return false;
    }

    // Remove all connections to/from this node
    // Convert Map entries to array to avoid MapIterator error
    const connectionEntries = Array.from(this.network.connections.entries());
    for (const [connectionId, connection] of connectionEntries) {
      if (connection.source === id || connection.target === id) {
        this.network.connections.delete(connectionId);
      }
    }

    this.network.nodes.delete(id);
    return true;
  }

  /**
   * Register a flow connection
   */
  public registerConnection(connection: FlowConnection): boolean {
    if (!connection.id || !connection.source || !connection.target || !connection.resourceType) {
      console.error('Invalid flow connection:', connection);
      return false;
    }

    // Verify source and target nodes exist
    if (!this.network.nodes.has(connection.source) || !this.network.nodes.has(connection.target)) {
      console.error('Source or target node does not exist:', connection);
      return false;
    }

    // Verify source node can provide the resource
    const sourceNode = this.network.nodes.get(connection.source);
    if (!sourceNode?.resources.includes(connection.resourceType)) {
      console.error('Source node cannot provide resource:', connection);
      return false;
    }

    // Verify target node can accept the resource
    const targetNode = this.network.nodes.get(connection.target);
    if (!targetNode?.resources.includes(connection.resourceType)) {
      console.error('Target node cannot accept resource:', connection);
      return false;
    }

    this.network.connections.set(connection.id, connection);
    return true;
  }

  /**
   * Unregister a flow connection
   */
  public unregisterConnection(id: string): boolean {
    if (!this.network.connections.has(id)) {
      return false;
    }

    this.network.connections.delete(id);
    return true;
  }

  /**
   * Update resource state
   */
  public updateResourceState(type: ResourceType, state: ResourceState): void {
    this.network.resourceStates.set(type, state);
  }

  /**
   * Get resource state
   */
  public getResourceState(type: ResourceType): ResourceState | undefined {
    return this.network.resourceStates.get(type);
  }

  /**
   * Optimize resource flows
   */
  public optimizeFlows(): FlowOptimizationResult {
    const now = Date.now();

    // Skip optimization if not enough time has passed
    if (now - this.lastOptimization < this.optimizationInterval) {
      return {
        transfers: [],
        updatedConnections: [],
        bottlenecks: [],
        underutilized: [],
      };
    }

    this.lastOptimization = now;

    // Get active nodes and connections
    const activeNodes = Array.from(this.network.nodes.values()).filter(node => node.active);
    const activeConnections = Array.from(this.network.connections.values()).filter(
      conn => conn.active
    );

    // Group nodes by type
    const producers = activeNodes.filter(node => node.type === 'producer');
    const consumers = activeNodes.filter(node => node.type === 'consumer');
    const storages = activeNodes.filter(node => node.type === 'storage');
    const converters = activeNodes.filter(node => node.type === 'converter');

    // Calculate resource availability and demand
    const availability: Partial<Record<ResourceType, number>> = {};
    const demand: Partial<Record<ResourceType, number>> = {};

    // Initialize with current resource states
    // Convert Map entries to array to avoid MapIterator error
    const resourceStateEntries = Array.from(this.network.resourceStates.entries());
    for (const [type, state] of resourceStateEntries) {
      availability[type] = 0;
      demand[type] = 0;
    }

    // Calculate production capacity
    for (const producer of producers) {
      for (const resourceType of producer.resources) {
        // Find outgoing connections for this resource
        const outgoingConnections = activeConnections.filter(
          conn => conn.source === producer.id && conn.resourceType === resourceType
        );

        // Sum up max rates
        const totalMaxRate = outgoingConnections.reduce((sum, conn) => sum + conn.maxRate, 0);

        // Apply efficiency if available
        const effectiveRate = producer.efficiency
          ? totalMaxRate * producer.efficiency
          : totalMaxRate;

        availability[resourceType] = (availability[resourceType] || 0) + effectiveRate;
      }
    }

    // Calculate consumption needs
    for (const consumer of consumers) {
      for (const resourceType of consumer.resources) {
        // Find incoming connections for this resource
        const incomingConnections = activeConnections.filter(
          conn => conn.target === consumer.id && conn.resourceType === resourceType
        );

        // Sum up max rates
        const totalMaxRate = incomingConnections.reduce((sum, conn) => sum + conn.maxRate, 0);

        demand[resourceType] = (demand[resourceType] || 0) + totalMaxRate;
      }
    }

    // Adjust for storage capacity
    for (const storage of storages) {
      for (const resourceType of storage.resources) {
        const resourceState = this.network.resourceStates.get(resourceType);
        if (!resourceState) {
          continue;
        }

        // If storage is near capacity, reduce availability
        if (resourceState.current > resourceState.max * 0.9) {
          availability[resourceType] = Math.max(
            0,
            (availability[resourceType] || 0) - (resourceState.max - resourceState.current)
          );
        }

        // If storage is near empty, increase demand
        if (resourceState.current < resourceState.max * 0.1) {
          demand[resourceType] = (demand[resourceType] || 0) + resourceState.max * 0.2;
        }
      }
    }

    // Identify bottlenecks and underutilized resources
    const bottlenecks: string[] = [];
    const underutilized: string[] = [];

    for (const [type, availableAmount] of Object.entries(availability)) {
      const demandAmount = demand[type as ResourceType] || 0;

      if (availableAmount < demandAmount * 0.9) {
        bottlenecks.push(type);
      } else if (availableAmount > demandAmount * 1.5) {
        underutilized.push(type);
      }
    }

    // Optimize flow rates based on priorities
    const updatedConnections: FlowConnection[] = [];

    // Sort connections by priority (high to low)
    const prioritizedConnections = [...activeConnections].sort(
      (a, b) => Number(b.priority) - Number(a.priority)
    );

    // Adjust flow rates
    for (const connection of prioritizedConnections) {
      const { resourceType } = connection;
      const availableForType = availability[resourceType] || 0;
      const demandForType = demand[resourceType] || 0;

      if (availableForType <= 0 || demandForType <= 0) {
        // No flow possible
        connection.currentRate = 0;
      } else if (availableForType >= demandForType) {
        // Full flow possible
        connection.currentRate = Math.min(connection.maxRate, demandForType);
      } else {
        // Partial flow based on ratio
        const ratio = availableForType / demandForType;
        connection.currentRate = connection.maxRate * ratio;
      }

      updatedConnections.push({ ...connection });

      // Update the actual connection in the network
      this.network.connections.set(connection.id, connection);
    }

    // Generate transfer instructions
    const transfers: ResourceTransfer[] = [];

    for (const connection of updatedConnections) {
      if (connection.currentRate <= 0) {
        continue;
      }

      const transfer: ResourceTransfer = {
        type: connection.resourceType,
        source: connection.source,
        target: connection.target,
        amount: connection.currentRate,
        timestamp: now,
      };

      if (validateResourceTransfer(transfer)) {
        transfers.push(transfer);

        // Add to history
        this.addToTransferHistory(transfer);
      }
    }

    return {
      transfers,
      updatedConnections,
      bottlenecks,
      underutilized,
    };
  }

  /**
   * Add a transfer to history
   */
  private addToTransferHistory(transfer: ResourceTransfer): void {
    this.transferHistory.push(transfer);

    // Trim history if needed
    if (this.transferHistory.length > this.maxHistorySize) {
      this.transferHistory = this.transferHistory.slice(-this.maxHistorySize);
    }
  }

  /**
   * Get transfer history
   */
  public getTransferHistory(): ResourceTransfer[] {
    return [...this.transferHistory];
  }

  /**
   * Get transfer history for a specific resource type
   */
  public getTransferHistoryByType(type: ResourceType): ResourceTransfer[] {
    return this.transferHistory.filter(transfer => transfer.type === type);
  }

  /**
   * Get all nodes
   */
  public getNodes(): FlowNode[] {
    return Array.from(this.network.nodes.values());
  }

  /**
   * Get all connections
   */
  public getConnections(): FlowConnection[] {
    return Array.from(this.network.connections.values());
  }

  /**
   * Get node by ID
   */
  public getNode(id: string): FlowNode | undefined {
    return this.network.nodes.get(id);
  }

  /**
   * Get connection by ID
   */
  public getConnection(id: string): FlowConnection | undefined {
    return this.network.connections.get(id);
  }

  /**
   * Create a resource flow
   */
  public createFlow(flow: ResourceFlow): boolean {
    if (!validateResourceFlow(flow)) {
      console.error('Invalid resource flow:', flow);
      return false;
    }

    // Extract resource type and other properties from flow
    const resourceType = flow.resources[0]?.type;
    const rate = flow.resources[0]?.amount || 0;
    const interval = flow.resources[0]?.interval || 1000;
    // Create a proper ResourcePriority object
    const priority: ResourcePriority = {
      type: resourceType,
      priority: 1,
      consumers: [],
    };

    if (!resourceType) {
      console.error('Flow must have at least one resource');
      return false;
    }

    // Create nodes if they don't exist
    if (!this.network.nodes.has(flow.source)) {
      this.registerNode({
        id: flow.source,
        type: 'producer',
        resources: [resourceType],
        priority: priority,
        active: true,
      });
    }

    if (!this.network.nodes.has(flow.target)) {
      this.registerNode({
        id: flow.target,
        type: 'consumer',
        resources: [resourceType],
        priority: priority,
        active: true,
      });
    }

    // Create connection
    const connectionId = `${flow.source}-${flow.target}-${resourceType}`;

    return this.registerConnection({
      id: connectionId,
      source: flow.source,
      target: flow.target,
      resourceType: resourceType,
      maxRate: rate,
      currentRate: 0,
      priority: priority,
      active: true,
    });
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.network.nodes.clear();
    this.network.connections.clear();
    this.network.resourceStates.clear();
    this.transferHistory = [];
  }
}

import { moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import {
  ResourcePriority,
  ResourceState,
  ResourceThreshold,
  ResourceTransfer,
  ResourceType,
} from '../../types/resources/ResourceTypes';
import { ResourceManager } from '../game/ResourceManager';
import { ResourceCostManager } from './ResourceCostManager';
import { ResourceExchangeManager } from './ResourceExchangeManager';
import { FlowNodeType, ResourceFlowManager } from './ResourceFlowManager';
import { ResourcePoolManager } from './ResourcePoolManager';
import { ResourceStorageManager, StorageContainerConfig } from './ResourceStorageManager';
import { ResourceThresholdManager, ThresholdConfig } from './ResourceThresholdManager';

/**
 * ResourceIntegration
 *
 * Integrates the new resource management system with existing game systems.
 * Acts as a bridge between the legacy ResourceManager and the new specialized managers.
 */
export class ResourceIntegration {
  private resourceManager: ResourceManager;
  private thresholdManager: ResourceThresholdManager;
  private flowManager: ResourceFlowManager;
  private storageManager: ResourceStorageManager;
  private costManager: ResourceCostManager;
  private exchangeManager: ResourceExchangeManager;
  private poolManager: ResourcePoolManager;
  private initialized: boolean = false;
  private transferHistory: ResourceTransfer[] = [];

  constructor(
    resourceManager: ResourceManager,
    thresholdManager: ResourceThresholdManager,
    flowManager: ResourceFlowManager,
    storageManager: ResourceStorageManager,
    costManager: ResourceCostManager,
    exchangeManager: ResourceExchangeManager,
    poolManager: ResourcePoolManager
  ) {
    this.resourceManager = resourceManager;
    this.thresholdManager = thresholdManager;
    this.flowManager = flowManager;
    this.storageManager = storageManager;
    this.costManager = costManager;
    this.exchangeManager = exchangeManager;
    this.poolManager = poolManager;
  }

  /**
   * Initialize the integration
   */
  public initialize(): void {
    if (this.initialized) {
      return;
    }

    // Subscribe to resource events from the legacy system
    this.subscribeToLegacyEvents();

    // Initialize threshold monitoring
    this.initializeThresholds();

    // Initialize storage containers
    this.initializeStorage();

    // Initialize resource flows
    this.initializeFlows();

    // Start the threshold monitoring
    this.thresholdManager.start();

    this.initialized = true;
    console.debug('[ResourceIntegration] Resource management system integrated');
  }

  /**
   * Subscribe to events from the legacy resource manager
   */
  private subscribeToLegacyEvents(): void {
    // Subscribe to resource update events
    moduleEventBus.subscribe('RESOURCE_PRODUCED' as ModuleEventType, event => {
      const { resourceType, newAmount, oldAmount } = event.data;

      // Update resource state in our new system
      const resourceState = this.resourceManager.getResourceState(resourceType);
      if (resourceState) {
        this.updateResourceState(resourceType, resourceState);
      }
    });

    moduleEventBus.subscribe('RESOURCE_CONSUMED' as ModuleEventType, event => {
      const { resourceType, newAmount, oldAmount } = event.data;

      // Update resource state in our new system
      const resourceState = this.resourceManager.getResourceState(resourceType);
      if (resourceState) {
        this.updateResourceState(resourceType, resourceState);
      }
    });

    moduleEventBus.subscribe('RESOURCE_TRANSFERRED' as ModuleEventType, event => {
      const { resourceType, amount, source, target } = event.data;

      // Record the transfer in our local history
      const transfer: ResourceTransfer = {
        type: resourceType,
        amount,
        source,
        target,
        timestamp: event.timestamp,
      };

      this.transferHistory.push(transfer);

      // Keep history size manageable
      if (this.transferHistory.length > 100) {
        this.transferHistory.shift();
      }
    });

    // Subscribe to threshold events
    moduleEventBus.subscribe('RESOURCE_SHORTAGE' as ModuleEventType, event => {
      const { resourceType, currentAmount, requiredAmount } = event.data;

      // Create an alert in our threshold manager
      const threshold: ResourceThreshold = {
        type: resourceType,
        min: requiredAmount,
      };

      // Find existing threshold config or create a new one
      const existingConfig = this.thresholdManager
        .getThresholdConfigs()
        .find(config => config.threshold.type === resourceType);

      if (existingConfig) {
        // Update the threshold
        existingConfig.threshold.min = Math.max(existingConfig.threshold.min || 0, requiredAmount);
      } else {
        // Create a new threshold config
        const config: ThresholdConfig = {
          id: `shortage-${resourceType}-${Date.now()}`,
          threshold,
          actions: [
            {
              type: 'notification',
              target: 'system',
              message: `Resource shortage: ${resourceType}`,
            },
          ],
          enabled: true,
          autoResolve: true,
        };

        this.thresholdManager.registerThreshold(config);
      }
    });
  }

  /**
   * Initialize thresholds based on the legacy resource manager
   */
  private initializeThresholds(): void {
    // Get all resource types
    const resourceTypes = Array.from(this.resourceManager['resources'].keys()) as ResourceType[];

    // Create thresholds for each resource type
    resourceTypes.forEach(type => {
      const resourceState = this.resourceManager.getResourceState(type);
      if (!resourceState) {
        return;
      }

      // Create a threshold config
      const config: ThresholdConfig = {
        id: `resource-${type}`,
        threshold: {
          type,
          min: resourceState.min,
          max: resourceState.max,
          target: (resourceState.min + resourceState.max) / 2,
        },
        actions: [
          {
            type: 'notification',
            target: 'system',
            message: `${type} threshold triggered`,
          },
        ],
        enabled: true,
      };

      this.thresholdManager.registerThreshold(config);
    });
  }

  /**
   * Initialize storage containers based on the legacy resource manager
   */
  private initializeStorage(): void {
    // Get all resource types
    const resourceTypes = Array.from(this.resourceManager['resources'].keys()) as ResourceType[];

    // Create a main storage container for each resource type
    resourceTypes.forEach(type => {
      const resourceState = this.resourceManager.getResourceState(type);
      if (!resourceState) {
        return;
      }

      // Create a storage container config
      const config: StorageContainerConfig = {
        id: `main-storage-${type}`,
        name: `Main ${type} Storage`,
        type: 'storage',
        capacity: resourceState.max,
        resourceTypes: [type],
        priority: 10, // High priority for main storage
      };

      this.storageManager.registerContainer(config);

      // Initialize with current amount
      this.storageManager.storeResource(config.id, type, resourceState.current);
    });
  }

  /**
   * Initialize resource flows based on the legacy resource manager
   */
  private initializeFlows(): void {
    // Get all resource types
    const resourceTypes = Array.from(this.resourceManager['resources'].keys()) as ResourceType[];

    // Create producer and consumer nodes for each resource type
    resourceTypes.forEach(type => {
      const resourceState = this.resourceManager.getResourceState(type);
      if (!resourceState) {
        return;
      }

      // Create a resource priority
      const resourcePriority: ResourcePriority = {
        type,
        priority: 1,
        consumers: [],
      };

      // Create producer node
      this.flowManager.registerNode({
        id: `producer-${type}`,
        type: 'producer' as FlowNodeType,
        resources: [type],
        priority: resourcePriority,
        active: true,
      });

      // Create consumer node
      this.flowManager.registerNode({
        id: `consumer-${type}`,
        type: 'consumer' as FlowNodeType,
        resources: [type],
        priority: resourcePriority,
        active: true,
      });

      // Create storage node
      this.flowManager.registerNode({
        id: `storage-${type}`,
        type: 'storage' as FlowNodeType,
        resources: [type],
        priority: resourcePriority,
        capacity: resourceState.max,
        active: true,
      });

      // Create connections
      this.flowManager.registerConnection({
        id: `production-${type}`,
        source: `producer-${type}`,
        target: `storage-${type}`,
        resourceType: type,
        maxRate: resourceState.production,
        currentRate: 0,
        priority: resourcePriority,
        active: true,
      });

      this.flowManager.registerConnection({
        id: `consumption-${type}`,
        source: `storage-${type}`,
        target: `consumer-${type}`,
        resourceType: type,
        maxRate: resourceState.consumption,
        currentRate: 0,
        priority: resourcePriority,
        active: true,
      });

      // Update resource state in flow manager
      this.flowManager.updateResourceState(type, resourceState);
    });
  }

  /**
   * Update resource state in all managers
   */
  private updateResourceState(type: ResourceType, state: ResourceState): void {
    // Update in threshold manager via event
    moduleEventBus.emit({
      type: 'resource:update' as ModuleEventType,
      moduleId: 'resource-integration',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        type,
        state,
      },
    });

    // Update in flow manager
    this.flowManager.updateResourceState(type, state);

    // Update in cost manager
    this.costManager.updateResourceState(type, state);

    // Update in exchange manager
    this.exchangeManager.updateResourceState(type, state);
  }

  /**
   * Update method to be called on game loop
   */
  public update(deltaTime: number): void {
    if (!this.initialized) {
      return;
    }

    // Optimize resource flows
    const flowResult = this.flowManager.optimizeFlows();

    // Apply transfers to the legacy resource manager
    flowResult.transfers.forEach(transfer => {
      // Only apply transfers to/from storage
      if (transfer.source.startsWith('storage-') || transfer.target.startsWith('storage-')) {
        // Extract the resource type from the node ID
        const sourceType = transfer.source.replace('storage-', '') as ResourceType;
        const targetType = transfer.target.replace('storage-', '') as ResourceType;

        // If transferring from storage to consumer, remove from legacy manager
        if (transfer.source.startsWith('storage-') && transfer.target.startsWith('consumer-')) {
          this.resourceManager.removeResource(sourceType, transfer.amount);
        }

        // If transferring from producer to storage, add to legacy manager
        if (transfer.source.startsWith('producer-') && transfer.target.startsWith('storage-')) {
          this.resourceManager.addResource(targetType, transfer.amount);
        }
      }
    });
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    if (!this.initialized) {
      return;
    }

    // Stop threshold monitoring
    this.thresholdManager.stop();

    // Clean up all managers
    this.thresholdManager.cleanup();
    this.flowManager.cleanup();
    this.storageManager.cleanup();
    this.costManager.cleanup();
    this.exchangeManager.cleanup();
    this.poolManager.cleanup();

    this.initialized = false;
  }
}

/**
 * Factory function to create and initialize the resource integration
 */
export function createResourceIntegration(resourceManager: ResourceManager): ResourceIntegration {
  // Create all the resource managers
  const thresholdManager = new ResourceThresholdManager();
  const flowManager = new ResourceFlowManager();
  const storageManager = new ResourceStorageManager();
  const costManager = new ResourceCostManager();
  const exchangeManager = new ResourceExchangeManager();
  const poolManager = new ResourcePoolManager();

  // Create the integration
  const integration = new ResourceIntegration(
    resourceManager,
    thresholdManager,
    flowManager,
    storageManager,
    costManager,
    exchangeManager,
    poolManager
  );

  // Initialize the integration
  integration.initialize();

  return integration;
}

import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import { ResourceType } from '../../types/resources/ResourceTypes';

export interface PerformanceMetrics {
  timestamp: number;
  resourceType: ResourceType;
  productionRate: number;
  consumptionRate: number;
  transferRate: number;
  utilizationRate: number;
  efficiency: number;
}

export interface ResourcePerformanceSnapshot {
  metrics: Map<ResourceType, PerformanceMetrics>;
  systemLoad: number;
  bottlenecks: ResourceType[];
  recommendations: string[];
}

/**
 * Monitors and analyzes resource management performance
 */
export class ResourcePerformanceMonitor {
  private metricsHistory: Map<ResourceType, PerformanceMetrics[]>;
  private snapshotInterval: number;
  private maxHistoryLength: number;
  private lastSnapshotTime: number;

  constructor(snapshotInterval = 5000, maxHistoryLength = 100) {
    this.metricsHistory = new Map();
    this.snapshotInterval = snapshotInterval;
    this.maxHistoryLength = maxHistoryLength;
    this.lastSnapshotTime = Date.now();

    // Start monitoring
    this.startMonitoring();
  }

  /**
   * Starts the performance monitoring
   */
  private startMonitoring(): void {
    setInterval(() => this.takeSnapshot(), this.snapshotInterval);
    console.debug('[ResourcePerformanceMonitor] Started monitoring');
  }

  /**
   * Records performance metrics for a resource
   */
  recordMetrics(
    type: ResourceType,
    productionRate: number,
    consumptionRate: number,
    transferRate: number,
    utilizationRate: number
  ): void {
    const metrics: PerformanceMetrics = {
      timestamp: Date.now(),
      resourceType: type,
      productionRate,
      consumptionRate,
      transferRate,
      utilizationRate,
      efficiency: this.calculateEfficiency(productionRate, consumptionRate, utilizationRate),
    };

    if (!this.metricsHistory.has(type)) {
      this.metricsHistory.set(type, []);
    }

    const history = this.metricsHistory.get(type)!;
    history.push(metrics);

    // Trim history if needed
    if (history.length > this.maxHistoryLength) {
      history.shift();
    }
  }

  /**
   * Calculates resource efficiency
   */
  private calculateEfficiency(
    production: number,
    consumption: number,
    utilization: number
  ): number {
    // Efficiency formula: balance between production matching consumption
    // and good utilization (not too full, not too empty)
    const productionBalance = consumption > 0 ? Math.min(production / consumption, 1.5) : 1.0;
    const utilizationScore = 1 - Math.abs(0.5 - utilization);
    return (productionBalance + utilizationScore) / 2;
  }

  /**
   * Takes a performance snapshot
   */
  private takeSnapshot(): void {
    const snapshot = this.generateSnapshot();
    this.lastSnapshotTime = Date.now();

    // Emit performance snapshot
    moduleEventBus.emit({
      type: 'STATUS_CHANGED',
      moduleId: 'resource-performance-monitor',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        type: 'performance_snapshot',
        snapshot,
      },
    });

    // Log performance insights
    this.logPerformanceInsights(snapshot);
  }

  /**
   * Generates a performance snapshot
   */
  private generateSnapshot(): ResourcePerformanceSnapshot {
    const metrics = new Map<ResourceType, PerformanceMetrics>();
    const bottlenecks: ResourceType[] = [];
    const recommendations: string[] = [];

    // Calculate current metrics for each resource
    for (const [type, history] of this.metricsHistory) {
      if (history.length === 0) {
        continue;
      }

      const latest = history[history.length - 1];
      metrics.set(type, latest);

      // Identify bottlenecks
      if (latest.efficiency < 0.6) {
        bottlenecks.push(type);

        // Generate recommendations
        if (latest.productionRate < latest.consumptionRate) {
          recommendations.push(
            `Increase production rate for ${type} (current: ${latest.productionRate.toFixed(2)}, needed: ${latest.consumptionRate.toFixed(2)})`
          );
        } else if (latest.utilizationRate > 0.9) {
          recommendations.push(
            `Consider increasing storage capacity for ${type} (utilization: ${(latest.utilizationRate * 100).toFixed(1)}%)`
          );
        } else if (latest.utilizationRate < 0.1) {
          recommendations.push(
            `Optimize consumption or reduce production of ${type} (utilization: ${(latest.utilizationRate * 100).toFixed(1)}%)`
          );
        }
      }
    }

    // Calculate system load (average utilization across all resources)
    const systemLoad =
      Array.from(metrics.values()).reduce((sum, m) => sum + m.utilizationRate, 0) /
      Math.max(metrics.size, 1);

    return {
      metrics,
      systemLoad,
      bottlenecks,
      recommendations,
    };
  }

  /**
   * Logs performance insights
   */
  private logPerformanceInsights(snapshot: ResourcePerformanceSnapshot): void {
    console.debug(`[ResourcePerformanceMonitor] Performance Snapshot:
      System Load: ${(snapshot.systemLoad * 100).toFixed(1)}%
      Bottlenecks: ${snapshot.bottlenecks.join(', ') || 'None'}

      Recommendations:
      ${snapshot.recommendations.map(r => `- ${r}`).join('\n      ') || '- No recommendations'}`);
  }

  /**
   * Gets performance history for a resource
   */
  getResourceHistory(type: ResourceType): PerformanceMetrics[] {
    return [...(this.metricsHistory.get(type) || [])];
  }

  /**
   * Gets the latest snapshot
   */
  getLatestSnapshot(): ResourcePerformanceSnapshot {
    return this.generateSnapshot();
  }

  /**
   * Cleans up the monitor
   */
  cleanup(): void {
    this.metricsHistory.clear();
    console.debug('[ResourcePerformanceMonitor] Cleaned up performance monitor');
  }

  /**
   * Get all metrics history
   */
  public getAllMetricsHistory(): Record<ResourceType, PerformanceMetrics[]> {
    const result: Record<ResourceType, PerformanceMetrics[]> = {} as Record<
      ResourceType,
      PerformanceMetrics[]
    >;

    for (const [type, metrics] of Array.from(this.metricsHistory)) {
      result[type] = [...metrics];
    }

    return result;
  }
}

// Export singleton instance
export const resourcePerformanceMonitor = new ResourcePerformanceMonitor();

import {
  ResourceContainer,
  ResourcePool,
  ResourceState,
  ResourceType,
} from '../../types/resources/ResourceTypes';
import { isResourcePool } from '../../utils/resources/resourceValidation';

/**
 * Pool allocation strategy
 */
export type PoolAllocationStrategy = 'equal' | 'priority' | 'demand-based' | 'custom';

/**
 * Pool distribution rule
 */
export interface PoolDistributionRule {
  id: string;
  poolId: string;
  targetIds: string[];
  resourceType: ResourceType;
  percentage: number;
  minAmount?: number;
  maxAmount?: number;
  priority: number;
  condition?: (state: ResourceState) => boolean;
  enabled?: boolean;
  sourceId?: string;
  amount?: number;
}

/**
 * Pool allocation result
 */
export interface PoolAllocationResult {
  poolId: string;
  resourceType: ResourceType;
  allocations: Array<{
    targetId: string;
    amount: number;
    percentage: number;
  }>;
  timestamp: number;
}

/**
 * Resource Pool Manager
 * Manages resource pools, distribution, and allocation
 */
export class ResourcePoolManager {
  private pools: Map<string, ResourcePool>;
  private containers: Map<string, ResourceContainer>;
  private distributionRules: Map<string, PoolDistributionRule>;
  private resourceStates: Map<ResourceType, ResourceState>;
  private allocationHistory: PoolAllocationResult[];
  private allocationStrategy: PoolAllocationStrategy;
  private maxHistorySize: number;
  private lastDistribution: number;
  private distributionInterval: number;

  constructor(
    allocationStrategy: PoolAllocationStrategy = 'priority',
    distributionInterval = 5000,
    maxHistorySize = 100
  ) {
    this.pools = new Map();
    this.containers = new Map();
    this.distributionRules = new Map();
    this.resourceStates = new Map();
    this.allocationHistory = [];
    this.allocationStrategy = allocationStrategy;
    this.maxHistorySize = maxHistorySize;
    this.lastDistribution = 0;
    this.distributionInterval = distributionInterval;
  }

  /**
   * Update resource state
   */
  public updateResourceState(type: ResourceType, state: ResourceState): void {
    this.resourceStates.set(type, state);
  }

  /**
   * Register a resource pool
   */
  public registerPool(pool: ResourcePool): boolean {
    if (!isResourcePool(pool)) {
      console.error('Invalid resource pool:', pool);
      return false;
    }

    this.pools.set(pool.id, pool);
    return true;
  }

  /**
   * Unregister a resource pool
   */
  public unregisterPool(id: string): boolean {
    if (!this.pools.has(id)) {
      return false;
    }

    // Remove all distribution rules for this pool
    for (const [ruleId, rule] of this.distributionRules.entries()) {
      if (rule.poolId === id) {
        this.distributionRules.delete(ruleId);
      }
    }

    this.pools.delete(id);
    return true;
  }

  /**
   * Register a container
   */
  public registerContainer(container: ResourceContainer): boolean {
    if (!container.id) {
      console.error('Invalid container:', container);
      return false;
    }

    this.containers.set(container.id, container);
    return true;
  }

  /**
   * Unregister a container
   */
  public unregisterContainer(id: string): boolean {
    if (!this.containers.has(id)) {
      return false;
    }

    this.containers.delete(id);
    return true;
  }

  /**
   * Register a distribution rule
   */
  public registerDistributionRule(rule: PoolDistributionRule): boolean {
    if (!rule.id || !rule.poolId || !rule.targetIds || rule.targetIds.length === 0) {
      console.error('Invalid distribution rule:', rule);
      return false;
    }

    // Verify pool exists
    if (!this.pools.has(rule.poolId)) {
      console.error(`Pool ${rule.poolId} does not exist`);
      return false;
    }

    // Verify targets exist
    for (const targetId of rule.targetIds) {
      if (!this.containers.has(targetId)) {
        console.error(`Target container ${targetId} does not exist`);
        return false;
      }
    }

    this.distributionRules.set(rule.id, rule);
    return true;
  }

  /**
   * Unregister a distribution rule
   */
  public unregisterDistributionRule(id: string): boolean {
    if (!this.distributionRules.has(id)) {
      return false;
    }

    this.distributionRules.delete(id);
    return true;
  }

  /**
   * Set allocation strategy
   */
  public setAllocationStrategy(strategy: PoolAllocationStrategy): void {
    this.allocationStrategy = strategy;
  }

  /**
   * Get allocation strategy
   */
  public getAllocationStrategy(): PoolAllocationStrategy {
    return this.allocationStrategy;
  }

  /**
   * Add resource to pool
   */
  public addToPool(poolId: string, type: ResourceType, amount: number): boolean {
    const pool = this.pools.get(poolId);
    if (!pool) {
      console.error(`Pool ${poolId} does not exist`);
      return false;
    }

    // Initialize resources map if it doesn't exist
    if (!pool.resources) {
      pool.resources = new Map<ResourceType, number>();
    }

    // Add resource to pool
    const currentAmount = pool.resources.get(type) || 0;
    pool.resources.set(type, currentAmount + amount);

    return true;
  }

  /**
   * Remove resource from pool
   */
  public removeFromPool(poolId: string, type: ResourceType, amount: number): boolean {
    const pool = this.pools.get(poolId);
    if (!pool || !pool.resources) {
      console.error(`Pool ${poolId} does not exist or has no resources`);
      return false;
    }

    const currentAmount = pool.resources.get(type) || 0;
    if (currentAmount < amount) {
      console.error(`Insufficient ${type} in pool ${poolId}`);
      return false;
    }

    pool.resources.set(type, currentAmount - amount);
    return true;
  }

  /**
   * Transfer between pools
   */
  public transferBetweenPools(
    sourcePoolId: string,
    targetPoolId: string,
    type: ResourceType,
    amount: number
  ): boolean {
    // Remove from source pool
    if (!this.removeFromPool(sourcePoolId, type, amount)) {
      return false;
    }

    // Add to target pool
    if (!this.addToPool(targetPoolId, type, amount)) {
      // Rollback source pool removal
      this.addToPool(sourcePoolId, type, amount);
      return false;
    }

    return true;
  }

  /**
   * Distribute resources from pools to containers
   */
  public distributeResources(now = Date.now()): PoolAllocationResult[] {
    // Skip if not enough time has passed
    if (now - this.lastDistribution < this.distributionInterval) {
      return [];
    }

    this.lastDistribution = now;
    const results: PoolAllocationResult[] = [];

    // Sort rules by priority (high to low)
    const sortedRules = Array.from(this.distributionRules.values()).sort(
      (a, b) => b.priority - a.priority
    );

    // Process each rule
    for (const rule of sortedRules) {
      // Skip disabled rules
      if (rule.enabled === false) {
        continue;
      }

      const pool = this.pools.get(rule.poolId);
      if (!pool || !pool.resources) {
        continue;
      }

      const resourceAmount = pool.resources.get(rule.resourceType) || 0;
      if (resourceAmount <= 0) {
        continue;
      }

      // Check condition if provided
      if (rule.condition) {
        const resourceState = this.resourceStates.get(rule.resourceType);
        if (!resourceState || !rule.condition(resourceState)) {
          continue;
        }
      }

      // Calculate amount to distribute
      let amountToDistribute = resourceAmount * (rule.percentage / 100);

      // Apply min/max constraints
      if (rule.minAmount !== undefined && amountToDistribute < rule.minAmount) {
        amountToDistribute = Math.min(rule.minAmount, resourceAmount);
      }

      if (rule.maxAmount !== undefined && amountToDistribute > rule.maxAmount) {
        amountToDistribute = rule.maxAmount;
      }

      // Skip if amount is too small
      if (amountToDistribute <= 0) {
        continue;
      }

      // Allocate resources based on strategy
      const allocations = this.allocateResources(
        rule.targetIds,
        rule.resourceType,
        amountToDistribute
      );

      // Apply allocations
      let totalAllocated = 0;
      for (const allocation of allocations) {
        const container = this.containers.get(allocation.targetId);
        if (!container) {
          continue;
        }

        // Initialize resources map if it doesn't exist
        if (!container.resources) {
          container.resources = new Map<ResourceType, number>();
        }

        // Add resource to container
        const currentAmount = container.resources.get(rule.resourceType) || 0;
        container.resources.set(rule.resourceType, currentAmount + allocation.amount);
        totalAllocated += allocation.amount;
      }

      // Remove allocated amount from pool
      if (totalAllocated > 0) {
        pool.resources.set(rule.resourceType, resourceAmount - totalAllocated);

        // Record allocation
        const result: PoolAllocationResult = {
          poolId: rule.poolId,
          resourceType: rule.resourceType,
          allocations,
          timestamp: now,
        };

        results.push(result);
        this.allocationHistory.push(result);

        // Trim history if needed
        if (this.allocationHistory.length > this.maxHistorySize) {
          this.allocationHistory = this.allocationHistory.slice(-this.maxHistorySize);
        }
      }
    }

    return results;
  }

  /**
   * Allocate resources based on strategy
   */
  private allocateResources(
    targetIds: string[],
    resourceType: ResourceType,
    amount: number
  ): Array<{ targetId: string; amount: number; percentage: number }> {
    if (targetIds.length === 0 || amount <= 0) {
      return [];
    }

    switch (this.allocationStrategy) {
      case 'equal':
        return this.allocateEqual(targetIds, amount);
      case 'priority':
        return this.allocatePriority(targetIds, resourceType, amount);
      case 'demand-based':
        return this.allocateDemandBased(targetIds, resourceType, amount);
      case 'custom':
        return this.allocateCustom(targetIds, resourceType, amount);
      default:
        return this.allocateEqual(targetIds, amount);
    }
  }

  /**
   * Allocate resources equally
   */
  private allocateEqual(
    targetIds: string[],
    amount: number
  ): Array<{ targetId: string; amount: number; percentage: number }> {
    const equalAmount = amount / targetIds.length;
    const percentage = 100 / targetIds.length;

    return targetIds.map(targetId => ({
      targetId,
      amount: equalAmount,
      percentage,
    }));
  }

  /**
   * Allocate resources based on priority
   */
  private allocatePriority(
    targetIds: string[],
    resourceType: ResourceType,
    amount: number
  ): Array<{ targetId: string; amount: number; percentage: number }> {
    // Get containers with priority
    const containersWithPriority = targetIds
      .map(id => {
        const container = this.containers.get(id);
        return {
          id,
          priority:
            container && 'priority' in container ? ((container as any).priority as number) : 1,
        };
      })
      .sort((a, b) => (b.priority as number) - (a.priority as number));

    // Calculate total priority
    const totalPriority = containersWithPriority.reduce(
      (sum, container) => sum + (container.priority as number),
      0
    );

    // Allocate based on priority
    return containersWithPriority.map(container => {
      const containerPercentage = ((container.priority as number) / totalPriority) * 100;
      const containerAmount = ((container.priority as number) / totalPriority) * amount;

      return {
        targetId: container.id,
        amount: containerAmount,
        percentage: containerPercentage,
      };
    });
  }

  /**
   * Allocate resources based on demand
   */
  private allocateDemandBased(
    targetIds: string[],
    resourceType: ResourceType,
    amount: number
  ): Array<{ targetId: string; amount: number; percentage: number }> {
    // Calculate demand for each container
    const containerDemands = targetIds.map(id => {
      const container = this.containers.get(id);
      if (!container) {
        return { id, demand: 0 };
      }

      // Calculate demand based on capacity and current amount
      let demand = 0;
      if (container.capacity && container.resources) {
        const currentAmount = container.resources.get(resourceType) || 0;
        demand = Math.max(0, container.capacity - currentAmount);
      }

      return { id, demand };
    });

    // Calculate total demand
    const totalDemand = containerDemands.reduce((sum, container) => sum + container.demand, 0);

    // If no demand, allocate equally
    if (totalDemand <= 0) {
      return this.allocateEqual(targetIds, amount);
    }

    // Allocate based on demand
    return containerDemands.map(container => {
      const containerPercentage = (container.demand / totalDemand) * 100;
      const containerAmount = (container.demand / totalDemand) * amount;

      return {
        targetId: container.id,
        amount: containerAmount,
        percentage: containerPercentage,
      };
    });
  }

  /**
   * Allocate resources based on custom logic
   */
  private allocateCustom(
    targetIds: string[],
    resourceType: ResourceType,
    amount: number
  ): Array<{ targetId: string; amount: number; percentage: number }> {
    // This is a placeholder for custom allocation logic
    // In a real implementation, this would use more complex rules

    // For now, we'll use a combination of priority and demand
    const priorityAllocations = this.allocatePriority(targetIds, resourceType, amount);
    const demandAllocations = this.allocateDemandBased(targetIds, resourceType, amount);

    // Combine allocations (50% priority, 50% demand)
    return targetIds.map(id => {
      const priorityAllocation = priorityAllocations.find(a => a.targetId === id);
      const demandAllocation = demandAllocations.find(a => a.targetId === id);

      const combinedAmount =
        (priorityAllocation?.amount || 0) * 0.5 + (demandAllocation?.amount || 0) * 0.5;

      const combinedPercentage =
        (priorityAllocation?.percentage || 0) * 0.5 + (demandAllocation?.percentage || 0) * 0.5;

      return {
        targetId: id,
        amount: combinedAmount,
        percentage: combinedPercentage,
      };
    });
  }

  /**
   * Get pool by ID
   */
  public getPool(id: string): ResourcePool | undefined {
    return this.pools.get(id);
  }

  /**
   * Get all pools
   */
  public getAllPools(): ResourcePool[] {
    return Array.from(this.pools.values());
  }

  /**
   * Get pools by type
   */
  public getPoolsByType(type: ResourceType): ResourcePool[] {
    return Array.from(this.pools.values()).filter(pool => pool.type === type);
  }

  /**
   * Get distribution rules by pool ID
   */
  public getDistributionRulesByPool(poolId: string): PoolDistributionRule[] {
    return Array.from(this.distributionRules.values()).filter(rule => rule.poolId === poolId);
  }

  /**
   * Get allocation history
   */
  public getAllocationHistory(): PoolAllocationResult[] {
    return [...this.allocationHistory];
  }

  /**
   * Get allocation history by pool ID
   */
  public getAllocationHistoryByPool(poolId: string): PoolAllocationResult[] {
    return this.allocationHistory.filter(result => result.poolId === poolId);
  }

  /**
   * Get allocation history by resource type
   */
  public getAllocationHistoryByType(type: ResourceType): PoolAllocationResult[] {
    return this.allocationHistory.filter(result => result.resourceType === type);
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.pools.clear();
    this.containers.clear();
    this.distributionRules.clear();
    this.resourceStates.clear();
    this.allocationHistory = [];
  }

  /**
   * Transfer resources directly between containers
   */
  public transferDirectly(
    sourceId: string,
    targetId: string,
    resourceType: ResourceType,
    amount: number
  ): boolean {
    const sourceContainer = this.containers.get(sourceId);
    const targetContainer = this.containers.get(targetId);

    if (!sourceContainer || !targetContainer) {
      console.error(`Source or target container not found: ${sourceId}, ${targetId}`);
      return false;
    }

    // Initialize resources maps if they don't exist
    if (!sourceContainer.resources) {
      sourceContainer.resources = new Map<ResourceType, number>();
    }

    if (!targetContainer.resources) {
      targetContainer.resources = new Map<ResourceType, number>();
    }

    // Check if source has enough resources
    const sourceAmount = sourceContainer.resources.get(resourceType) || 0;
    if (sourceAmount < amount) {
      console.error(`Insufficient ${resourceType} in source container ${sourceId}`);
      return false;
    }

    // Transfer resources
    const targetAmount = targetContainer.resources.get(resourceType) || 0;

    // Update source and target containers
    sourceContainer.resources.set(resourceType, sourceAmount - amount);
    targetContainer.resources.set(resourceType, targetAmount + amount);

    return true;
  }
}

import { ResourceState, ResourceTransfer, ResourceType } from '../../types/resources/ResourceTypes';

/**
 * Storage allocation strategy
 */
export type StorageAllocationStrategy = 'balanced' | 'prioritized' | 'dedicated';

/**
 * Storage container configuration
 */
export interface StorageContainerConfig {
  id: string;
  name: string;
  type: 'container' | 'pool' | 'storage';
  capacity: number;
  resourceTypes: ResourceType[];
  priority: number;
  location?: string;
  efficiency?: number;
  accessSpeed?: number;
  upgradeLevel?: number;
  maxUpgradeLevel?: number;
}

/**
 * Storage container state
 */
export interface StorageContainerState {
  config: StorageContainerConfig;
  resources: Map<ResourceType, ResourceState>;
  totalStored: number;
  lastUpdated: number;
}

/**
 * Storage allocation
 */
export interface StorageAllocation {
  containerId: string;
  resourceType: ResourceType;
  amount: number;
  percentage: number;
}

/**
 * Storage overflow policy
 */
export type StorageOverflowPolicy = 'reject' | 'redistribute' | 'convert' | 'discard';

/**
 * Storage manager configuration
 */
export interface StorageManagerConfig {
  defaultAllocationStrategy: StorageAllocationStrategy;
  overflowPolicy: StorageOverflowPolicy;
  autoRebalance: boolean;
  rebalanceThreshold: number;
  priorityWeights: {
    containerPriority: number;
    resourcePriority: number;
    fillPercentage: number;
  };
}

/**
 * Resource Storage Manager
 * Manages resource storage containers, pools, and allocation strategies
 */
export class ResourceStorageManager {
  private containers: Map<string, StorageContainerState>;
  private resourcePriorities: Map<ResourceType, number>;
  private config: StorageManagerConfig;
  private transferHistory: ResourceTransfer[];
  private maxHistorySize: number;

  constructor(config?: Partial<StorageManagerConfig>) {
    this.containers = new Map();
    this.resourcePriorities = new Map();
    this.transferHistory = [];
    this.maxHistorySize = 100;

    // Default configuration
    this.config = {
      defaultAllocationStrategy: 'balanced',
      overflowPolicy: 'redistribute',
      autoRebalance: true,
      rebalanceThreshold: 0.2, // 20% imbalance triggers rebalance
      priorityWeights: {
        containerPriority: 0.4,
        resourcePriority: 0.4,
        fillPercentage: 0.2,
      },
      ...config,
    };

    // Set default resource priorities
    this.setDefaultResourcePriorities();
  }

  /**
   * Set default resource priorities
   */
  private setDefaultResourcePriorities(): void {
    // Higher number = higher priority
    this.resourcePriorities.set('energy', 10);
    this.resourcePriorities.set('minerals', 8);
    // Replace non-existent resource types with valid ones
    this.resourcePriorities.set('population', 9);
    this.resourcePriorities.set('research', 10);
    this.resourcePriorities.set('plasma', 10);
    this.resourcePriorities.set('gas', 7);
    this.resourcePriorities.set('exotic', 6);
    this.resourcePriorities.set('research', 5);
    this.resourcePriorities.set('population', 10);
  }

  /**
   * Register a storage container
   */
  public registerContainer(config: StorageContainerConfig): boolean {
    if (!config.id || !config.resourceTypes || config.resourceTypes.length === 0) {
      console.error('Invalid storage container configuration:', config);
      return false;
    }

    // Initialize resource states
    const resources = new Map<ResourceType, ResourceState>();

    for (const type of config.resourceTypes) {
      resources.set(type, {
        current: 0,
        min: 0,
        max: config.capacity / config.resourceTypes.length, // Divide capacity equally by default
        production: 0,
        consumption: 0,
      });
    }

    this.containers.set(config.id, {
      config,
      resources,
      totalStored: 0,
      lastUpdated: Date.now(),
    });

    return true;
  }

  /**
   * Unregister a storage container
   */
  public unregisterContainer(id: string): boolean {
    if (!this.containers.has(id)) {
      return false;
    }

    this.containers.delete(id);
    return true;
  }

  /**
   * Get a storage container
   */
  public getContainer(id: string): StorageContainerState | undefined {
    return this.containers.get(id);
  }

  /**
   * Get all storage containers
   */
  public getAllContainers(): StorageContainerState[] {
    return Array.from(this.containers.values());
  }

  /**
   * Get containers by resource type
   */
  public getContainersByResourceType(type: ResourceType): StorageContainerState[] {
    return Array.from(this.containers.values()).filter(container => container.resources.has(type));
  }

  /**
   * Store resource in a specific container
   */
  public storeResource(containerId: string, type: ResourceType, amount: number): number {
    const container = this.containers.get(containerId);
    if (!container || !container.resources.has(type) || amount <= 0) {
      return 0;
    }

    const resourceState = container.resources.get(type)!;
    const availableSpace = resourceState.max - resourceState.current;

    if (availableSpace <= 0) {
      return 0;
    }

    const amountToStore = Math.min(amount, availableSpace);

    resourceState.current += amountToStore;

    container.totalStored += amountToStore;
    container.lastUpdated = Date.now();

    // Record transfer
    this.recordTransfer({
      type,
      source: 'external',
      target: containerId,
      amount: amountToStore,
      timestamp: Date.now(),
    });

    return amountToStore;
  }

  /**
   * Retrieve resource from a specific container
   */
  public retrieveResource(containerId: string, type: ResourceType, amount: number): number {
    const container = this.containers.get(containerId);
    if (!container || !container.resources.has(type) || amount <= 0) {
      return 0;
    }

    const resourceState = container.resources.get(type)!;

    if (resourceState.current <= 0) {
      return 0;
    }

    const amountToRetrieve = Math.min(amount, resourceState.current);

    resourceState.current -= amountToRetrieve;

    container.totalStored -= amountToRetrieve;
    container.lastUpdated = Date.now();

    // Record transfer
    this.recordTransfer({
      type,
      source: containerId,
      target: 'external',
      amount: amountToRetrieve,
      timestamp: Date.now(),
    });

    return amountToRetrieve;
  }

  /**
   * Store resource in the best available container
   */
  public storeResourceOptimal(type: ResourceType, amount: number): number {
    if (amount <= 0) {
      return 0;
    }

    // Get all containers that can store this resource type
    const availableContainers = this.getContainersByResourceType(type);

    if (availableContainers.length === 0) {
      return 0;
    }

    // Calculate scores for each container based on the allocation strategy
    const containerScores = this.calculateContainerScores(availableContainers, type);

    // Sort containers by score (highest first)
    const sortedContainers = [...containerScores].sort((a, b) => b.score - a.score);

    let remainingAmount = amount;
    let totalStored = 0;

    // Try to store in containers by score order
    for (const { containerId, score } of sortedContainers) {
      if (remainingAmount <= 0) {
        break;
      }

      const amountStored = this.storeResource(containerId, type, remainingAmount);
      totalStored += amountStored;
      remainingAmount -= amountStored;
    }

    // Handle overflow if needed
    if (remainingAmount > 0 && this.config.overflowPolicy !== 'reject') {
      totalStored += this.handleOverflow(type, remainingAmount);
    }

    // Check if rebalancing is needed
    if (this.config.autoRebalance) {
      this.checkAndRebalance(type);
    }

    return totalStored;
  }

  /**
   * Retrieve resource from the best available container
   */
  public retrieveResourceOptimal(type: ResourceType, amount: number): number {
    if (amount <= 0) {
      return 0;
    }

    // Get all containers that store this resource type
    const availableContainers = this.getContainersByResourceType(type);

    if (availableContainers.length === 0) {
      return 0;
    }

    // Calculate scores for each container based on the allocation strategy
    // For retrieval, we want to prioritize containers with higher fill percentage
    const containerScores = this.calculateContainerScores(availableContainers, type, true);

    // Sort containers by score (highest first)
    const sortedContainers = [...containerScores].sort((a, b) => b.score - a.score);

    let remainingAmount = amount;
    let totalRetrieved = 0;

    // Try to retrieve from containers by score order
    for (const { containerId, score } of sortedContainers) {
      if (remainingAmount <= 0) {
        break;
      }

      const amountRetrieved = this.retrieveResource(containerId, type, remainingAmount);
      totalRetrieved += amountRetrieved;
      remainingAmount -= amountRetrieved;
    }

    return totalRetrieved;
  }

  /**
   * Calculate container scores for allocation
   */
  private calculateContainerScores(
    containers: StorageContainerState[],
    resourceType: ResourceType,
    forRetrieval = false
  ): Array<{ containerId: string; score: number }> {
    const { containerPriority, resourcePriority, fillPercentage } = this.config.priorityWeights;
    const resourcePriorityValue = this.resourcePriorities.get(resourceType) || 5;

    return containers.map(container => {
      const resourceState = container.resources.get(resourceType)!;
      const fillRatio = resourceState.current / resourceState.max;

      // For storage, we prefer containers with lower fill percentage
      // For retrieval, we prefer containers with higher fill percentage
      const fillScore = forRetrieval ? fillRatio : 1 - fillRatio;

      // Calculate weighted score
      const score =
        container.config.priority * containerPriority +
        resourcePriorityValue * resourcePriority +
        fillScore * fillPercentage;

      return {
        containerId: container.config.id,
        score,
      };
    });
  }

  /**
   * Handle overflow based on policy
   */
  private handleOverflow(type: ResourceType, amount: number): number {
    switch (this.config.overflowPolicy) {
      case 'redistribute':
        return this.redistributeOverflow(type, amount);

      case 'convert':
        return this.convertOverflow(type, amount);

      case 'discard':
        // Just discard the overflow
        return 0;

      default:
        return 0;
    }
  }

  /**
   * Redistribute overflow by expanding container capacity
   */
  private redistributeOverflow(type: ResourceType, amount: number): number {
    // Find containers that can store this resource type
    const relevantContainers = this.getContainersByResourceType(type);

    if (relevantContainers.length === 0) {
      return 0;
    }

    // Sort by upgrade potential (containers with lower upgrade level first)
    const upgradableContainers = relevantContainers
      .filter(
        container =>
          container.config.upgradeLevel !== undefined &&
          container.config.maxUpgradeLevel !== undefined &&
          container.config.upgradeLevel < container.config.maxUpgradeLevel
      )
      .sort((a, b) => (a.config.upgradeLevel || 0) - (b.config.upgradeLevel || 0));

    if (upgradableContainers.length === 0) {
      return 0;
    }

    // Upgrade the first container
    const containerToUpgrade = upgradableContainers[0];
    const resourceState = containerToUpgrade.resources.get(type)!;

    // Increase capacity by 20%
    const capacityIncrease = resourceState.max * 0.2;
    resourceState.max += capacityIncrease;

    // Increment upgrade level
    if (containerToUpgrade.config.upgradeLevel !== undefined) {
      containerToUpgrade.config.upgradeLevel += 1;
    }

    // Try to store again
    return this.storeResource(containerToUpgrade.config.id, type, amount);
  }

  /**
   * Convert overflow to another resource type
   */
  private convertOverflow(type: ResourceType, amount: number): number {
    // This is a placeholder for resource conversion logic
    // In a real implementation, this would convert the resource to another type
    // based on conversion rules

    // For now, we'll just return 0 (no storage)
    return 0;
  }

  /**
   * Check if rebalancing is needed and perform it
   */
  private checkAndRebalance(type: ResourceType): void {
    const containers = this.getContainersByResourceType(type);

    if (containers.length <= 1) {
      return;
    }

    // Calculate fill ratios
    const fillRatios = containers.map(container => {
      const resourceState = container.resources.get(type)!;
      return {
        id: container.config.id,
        fillRatio: resourceState.current / resourceState.max,
      };
    });

    // Find min and max fill ratios
    const minFill = Math.min(...fillRatios.map(r => r.fillRatio));
    const maxFill = Math.max(...fillRatios.map(r => r.fillRatio));

    // Check if imbalance exceeds threshold
    if (maxFill - minFill > this.config.rebalanceThreshold) {
      this.rebalanceContainers(type, containers);
    }
  }

  /**
   * Rebalance resources between containers
   */
  private rebalanceContainers(type: ResourceType, containers: StorageContainerState[]): void {
    // Calculate target fill ratio (average)
    let totalCurrent = 0;
    let totalMax = 0;

    for (const container of containers) {
      const resourceState = container.resources.get(type)!;
      totalCurrent += resourceState.current;
      totalMax += resourceState.max;
    }

    const targetFillRatio = totalCurrent / totalMax;

    // Calculate transfers needed
    for (const container of containers) {
      const resourceState = container.resources.get(type)!;
      const currentFillRatio = resourceState.current / resourceState.max;

      if (Math.abs(currentFillRatio - targetFillRatio) < 0.05) {
        // Close enough, skip
        continue;
      }

      const targetAmount = resourceState.max * targetFillRatio;
      const difference = targetAmount - resourceState.current;

      if (difference > 0) {
        // Need to add resources
        // Find container with excess
        const sourceContainer = containers.find(c => {
          const rs = c.resources.get(type)!;
          return rs.current / rs.max > targetFillRatio + 0.05;
        });

        if (sourceContainer) {
          const sourceState = sourceContainer.resources.get(type)!;
          const availableToTransfer = sourceState.current - sourceState.max * targetFillRatio;
          const transferAmount = Math.min(difference, availableToTransfer);

          // Transfer resources
          this.transferBetweenContainers(
            sourceContainer.config.id,
            container.config.id,
            type,
            transferAmount
          );
        }
      }
    }
  }

  /**
   * Transfer resources between containers
   */
  public transferBetweenContainers(
    sourceId: string,
    targetId: string,
    type: ResourceType,
    amount: number
  ): number {
    if (amount <= 0 || sourceId === targetId) {
      return 0;
    }

    // Retrieve from source
    const retrievedAmount = this.retrieveResource(sourceId, type, amount);

    if (retrievedAmount <= 0) {
      return 0;
    }

    // Store in target
    const storedAmount = this.storeResource(targetId, type, retrievedAmount);

    // If not all was stored, return remainder to source
    if (storedAmount < retrievedAmount) {
      const remainder = retrievedAmount - storedAmount;
      this.storeResource(sourceId, type, remainder);
    }

    // Record transfer
    this.recordTransfer({
      type,
      source: sourceId,
      target: targetId,
      amount: storedAmount,
      timestamp: Date.now(),
    });

    return storedAmount;
  }

  /**
   * Record a transfer in history
   */
  private recordTransfer(transfer: ResourceTransfer): void {
    this.transferHistory.push(transfer);

    // Trim history if needed
    if (this.transferHistory.length > this.maxHistorySize) {
      this.transferHistory = this.transferHistory.slice(-this.maxHistorySize);
    }
  }

  /**
   * Get transfer history
   */
  public getTransferHistory(): ResourceTransfer[] {
    return [...this.transferHistory];
  }

  /**
   * Get transfer history for a specific resource type
   */
  public getTransferHistoryByType(type: ResourceType): ResourceTransfer[] {
    return this.transferHistory.filter(transfer => transfer.type === type);
  }

  /**
   * Get total stored amount of a resource type
   */
  public getTotalStored(type: ResourceType): number {
    let total = 0;

    for (const container of this.containers.values()) {
      const resourceState = container.resources.get(type);
      if (resourceState) {
        total += resourceState.current;
      }
    }

    return total;
  }

  /**
   * Get total capacity for a resource type
   */
  public getTotalCapacity(type: ResourceType): number {
    let total = 0;

    for (const container of this.containers.values()) {
      const resourceState = container.resources.get(type);
      if (resourceState) {
        total += resourceState.max;
      }
    }

    return total;
  }

  /**
   * Set resource priority
   */
  public setResourcePriority(type: ResourceType, priority: number): void {
    this.resourcePriorities.set(type, priority);
  }

  /**
   * Get resource priority
   */
  public getResourcePriority(type: ResourceType): number {
    return this.resourcePriorities.get(type) || 5;
  }

  /**
   * Update container configuration
   */
  public updateContainerConfig(id: string, config: Partial<StorageContainerConfig>): boolean {
    const container = this.containers.get(id);
    if (!container) {
      return false;
    }

    // Update config
    container.config = {
      ...container.config,
      ...config,
    };

    return true;
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.containers.clear();
    this.resourcePriorities.clear();
    this.transferHistory = [];
  }
}

import { ModuleEvent, moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import {
  ResourceAlert,
  ResourceState,
  ResourceThreshold,
  ResourceType,
} from '../../types/resources/ResourceTypes';
import { validateResourceThreshold } from '../../utils/resources/resourceValidation';

// Map our custom event types to the standard ModuleEventType
const RESOURCE_UPDATE: ModuleEventType = 'RESOURCE_UPDATED';
const RESOURCE_THRESHOLD_TRIGGERED: ModuleEventType = 'STATUS_CHANGED';
const RESOURCE_THRESHOLD_RESOLVED: ModuleEventType = 'STATUS_CHANGED';
const RESOURCE_PRODUCTION_ADJUST: ModuleEventType = 'RESOURCE_PRODUCTION_REGISTERED';
const RESOURCE_CONSUMPTION_ADJUST: ModuleEventType = 'RESOURCE_CONSUMPTION_REGISTERED';
const RESOURCE_TRANSFER_REQUEST: ModuleEventType = 'RESOURCE_TRANSFERRED';
const NOTIFICATION_CREATE: ModuleEventType = 'STATUS_CHANGED';
const RESOURCE_ALERT_CREATED: ModuleEventType = 'STATUS_CHANGED';
const RESOURCE_ALERT_CLEARED: ModuleEventType = 'STATUS_CHANGED';

// Resource manager module ID and type
const RESOURCE_MANAGER_ID = 'resource-threshold-manager';
const RESOURCE_MANAGER_TYPE: ModuleType = 'resource-manager';

/**
 * Threshold action types
 */
export type ThresholdActionType = 'production' | 'consumption' | 'transfer' | 'notification';

/**
 * Threshold action
 */
export interface ThresholdAction {
  type: ThresholdActionType;
  target: string;
  amount?: number;
  message?: string;
  priority?: number;
}

/**
 * Threshold configuration
 */
export interface ThresholdConfig {
  id: string;
  threshold: ResourceThreshold;
  actions: ThresholdAction[];
  enabled: boolean;
  autoResolve?: boolean;
}

/**
 * Threshold status
 */
export type ThresholdStatus = 'inactive' | 'warning' | 'critical' | 'resolved';

/**
 * Threshold state
 */
export interface ThresholdState {
  config: ThresholdConfig;
  status: ThresholdStatus;
  lastTriggered?: number;
  lastResolved?: number;
  actionsTaken: number;
}

/**
 * Resource Threshold Manager
 * Manages resource thresholds and triggers actions when thresholds are crossed
 */
export class ResourceThresholdManager {
  private thresholds: Map<string, ThresholdConfig>;
  private thresholdStates: Map<string, ThresholdState>;
  private activeAlerts: Map<string, ResourceAlert>;
  private resourceStates: Map<ResourceType, ResourceState>;
  private checkInterval: number;
  private intervalId: NodeJS.Timeout | null;
  private lastCheck: number;

  constructor(checkInterval = 1000) {
    this.thresholds = new Map();
    this.thresholdStates = new Map();
    this.activeAlerts = new Map();
    this.resourceStates = new Map();
    this.checkInterval = checkInterval;
    this.intervalId = null;
    this.lastCheck = Date.now();

    // Register event listeners
    moduleEventBus.subscribe(RESOURCE_UPDATE, this.handleResourceUpdate);
  }

  /**
   * Start threshold monitoring
   */
  public start(): void {
    if (this.intervalId) {
      return;
    }

    this.intervalId = setInterval(() => {
      this.checkThresholds();
    }, this.checkInterval);
  }

  /**
   * Stop threshold monitoring
   */
  public stop(): void {
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = null;
    }
  }

  /**
   * Register a threshold configuration
   */
  public registerThreshold(config: ThresholdConfig): boolean {
    if (!config.id || !validateResourceThreshold(config.threshold)) {
      console.error('Invalid threshold configuration:', config);
      return false;
    }

    this.thresholds.set(config.id, config);
    this.thresholdStates.set(config.id, {
      config,
      status: 'inactive',
      actionsTaken: 0,
    });

    return true;
  }

  /**
   * Unregister a threshold configuration
   */
  public unregisterThreshold(id: string): boolean {
    if (!this.thresholds.has(id)) {
      return false;
    }

    this.thresholds.delete(id);
    this.thresholdStates.delete(id);
    return true;
  }

  /**
   * Enable a threshold
   */
  public enableThreshold(id: string): boolean {
    const config = this.thresholds.get(id);
    if (!config) {
      return false;
    }

    config.enabled = true;
    return true;
  }

  /**
   * Disable a threshold
   */
  public disableThreshold(id: string): boolean {
    const config = this.thresholds.get(id);
    if (!config) {
      return false;
    }

    config.enabled = false;
    return true;
  }

  /**
   * Update resource state
   */
  private handleResourceUpdate = (event: ModuleEvent): void => {
    if (event.data && event.data.type && event.data.state) {
      this.resourceStates.set(event.data.type, event.data.state);
    }
  };

  /**
   * Check all thresholds
   */
  private checkThresholds(): void {
    const now = Date.now();
    const deltaTime = now - this.lastCheck;
    this.lastCheck = now;

    // Convert Map entries to array to avoid MapIterator error
    const thresholdEntries = Array.from(this.thresholds.entries());

    for (let i = 0; i < thresholdEntries.length; i++) {
      const [id, config] = thresholdEntries[i];

      if (!config.enabled) {
        continue;
      }

      const state = this.thresholdStates.get(id);
      if (!state) {
        continue;
      }

      const resourceState = this.resourceStates.get(config.threshold.type);
      if (!resourceState) {
        continue;
      }

      const isTriggered = this.isThresholdTriggered(config.threshold, resourceState);

      if (isTriggered && state.status === 'inactive') {
        // Threshold triggered
        state.status = this.getThresholdSeverity(config.threshold, resourceState);
        state.lastTriggered = now;
        this.executeThresholdActions(config, state.status);
        state.actionsTaken++;

        // Create alert
        this.createAlert(config, state.status);

        // Emit event
        moduleEventBus.emit({
          type: RESOURCE_THRESHOLD_TRIGGERED,
          moduleId: RESOURCE_MANAGER_ID,
          moduleType: RESOURCE_MANAGER_TYPE,
          timestamp: now,
          data: {
            id,
            type: config.threshold.type,
            status: state.status,
            timestamp: now,
            severity: state.status === 'critical' ? 'high' : 'medium',
          },
        });
      } else if (!isTriggered && state.status !== 'inactive') {
        // Threshold resolved
        state.status = 'resolved';
        state.lastResolved = now;

        // Clear alert if auto-resolve is enabled
        if (config.autoResolve) {
          this.clearAlert(id);
        }

        // Emit event
        moduleEventBus.emit({
          type: RESOURCE_THRESHOLD_RESOLVED,
          moduleId: RESOURCE_MANAGER_ID,
          moduleType: RESOURCE_MANAGER_TYPE,
          timestamp: now,
          data: {
            id,
            type: config.threshold.type,
            timestamp: now,
            severity: 'info',
          },
        });

        // Reset to inactive after a short delay
        setTimeout(() => {
          if (state.status === 'resolved') {
            state.status = 'inactive';
          }
        }, 5000);
      }
    }
  }

  /**
   * Check if a threshold is triggered
   */
  private isThresholdTriggered(threshold: ResourceThreshold, state: ResourceState): boolean {
    if (threshold.min !== undefined && state.current < threshold.min) {
      return true;
    }

    if (threshold.max !== undefined && state.current > threshold.max) {
      return true;
    }

    if (threshold.target !== undefined) {
      const deviation = Math.abs(state.current - threshold.target);
      const maxDeviation = threshold.target * 0.1; // 10% deviation

      if (deviation > maxDeviation) {
        return true;
      }
    }

    return false;
  }

  /**
   * Get threshold severity based on how far the value is from the threshold
   */
  private getThresholdSeverity(
    threshold: ResourceThreshold,
    state: ResourceState
  ): ThresholdStatus {
    if (threshold.min !== undefined && state.current < threshold.min) {
      const ratio = state.current / threshold.min;

      if (ratio < 0.5) {
        return 'critical';
      } else {
        return 'warning';
      }
    }

    if (threshold.max !== undefined && state.current > threshold.max) {
      const ratio = state.current / threshold.max;

      if (ratio > 1.5) {
        return 'critical';
      } else {
        return 'warning';
      }
    }

    if (threshold.target !== undefined) {
      const deviation = Math.abs(state.current - threshold.target);
      const deviationRatio = deviation / threshold.target;

      if (deviationRatio > 0.25) {
        return 'critical';
      } else {
        return 'warning';
      }
    }

    return 'warning';
  }

  /**
   * Execute threshold actions
   */
  private executeThresholdActions(config: ThresholdConfig, status: ThresholdStatus): void {
    const now = Date.now();

    for (const action of config.actions) {
      switch (action.type) {
        case 'production':
          moduleEventBus.emit({
            type: RESOURCE_PRODUCTION_ADJUST,
            moduleId: RESOURCE_MANAGER_ID,
            moduleType: RESOURCE_MANAGER_TYPE,
            timestamp: now,
            data: {
              type: config.threshold.type,
              target: action.target,
              amount: action.amount || 0,
              priority: action.priority || 1,
              severity: 'info',
            },
          });
          break;

        case 'consumption':
          moduleEventBus.emit({
            type: RESOURCE_CONSUMPTION_ADJUST,
            moduleId: RESOURCE_MANAGER_ID,
            moduleType: RESOURCE_MANAGER_TYPE,
            timestamp: now,
            data: {
              type: config.threshold.type,
              target: action.target,
              amount: action.amount || 0,
              priority: action.priority || 1,
              severity: 'info',
            },
          });
          break;

        case 'transfer':
          moduleEventBus.emit({
            type: RESOURCE_TRANSFER_REQUEST,
            moduleId: RESOURCE_MANAGER_ID,
            moduleType: RESOURCE_MANAGER_TYPE,
            timestamp: now,
            data: {
              type: config.threshold.type,
              target: action.target,
              amount: action.amount || 0,
              priority: action.priority || 1,
              severity: 'info',
            },
          });
          break;

        case 'notification':
          moduleEventBus.emit({
            type: NOTIFICATION_CREATE,
            moduleId: RESOURCE_MANAGER_ID,
            moduleType: RESOURCE_MANAGER_TYPE,
            timestamp: now,
            data: {
              type: 'resource',
              message: action.message || `Resource ${config.threshold.type} threshold triggered`,
              severity: status === 'critical' ? 'high' : 'medium',
              timestamp: now,
            },
          });
          break;
      }
    }
  }

  /**
   * Create a resource alert
   */
  private createAlert(config: ThresholdConfig, status: ThresholdStatus): void {
    const now = Date.now();
    const alert: ResourceAlert = {
      id: config.id,
      type: config.threshold.type,
      threshold: config.threshold,
      message: `Resource ${config.threshold.type} threshold triggered`,
      severity: status === 'critical' ? 'critical' : 'medium',
      autoResolve: config.autoResolve,
      actions: config.actions
        .filter(action => action.type !== 'notification') // Filter out notification actions
        .map(action => ({
          type: action.type as 'production' | 'consumption' | 'transfer',
          target: action.target,
          amount: action.amount || 0,
        })),
    };

    this.activeAlerts.set(config.id, alert);

    moduleEventBus.emit({
      type: RESOURCE_ALERT_CREATED,
      moduleId: RESOURCE_MANAGER_ID,
      moduleType: RESOURCE_MANAGER_TYPE,
      timestamp: now,
      data: {
        ...alert,
        severity: status === 'critical' ? 'high' : 'medium',
      },
    });
  }

  /**
   * Clear a resource alert
   */
  private clearAlert(id: string): void {
    const now = Date.now();
    const alert = this.activeAlerts.get(id);
    if (!alert) {
      return;
    }

    this.activeAlerts.delete(id);

    moduleEventBus.emit({
      type: RESOURCE_ALERT_CLEARED,
      moduleId: RESOURCE_MANAGER_ID,
      moduleType: RESOURCE_MANAGER_TYPE,
      timestamp: now,
      data: {
        id,
        type: alert.type,
        timestamp: now,
        severity: 'info',
      },
    });
  }

  /**
   * Get all active alerts
   */
  public getActiveAlerts(): ResourceAlert[] {
    return Array.from(this.activeAlerts.values());
  }

  /**
   * Get all threshold configurations
   */
  public getThresholdConfigs(): ThresholdConfig[] {
    return Array.from(this.thresholds.values());
  }

  /**
   * Get all threshold states
   */
  public getThresholdStates(): ThresholdState[] {
    return Array.from(this.thresholdStates.values());
  }

  /**
   * Get a specific threshold state
   */
  public getThresholdState(id: string): ThresholdState | undefined {
    return this.thresholdStates.get(id);
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.stop();
    // Use the unsubscribe function returned by subscribe
    moduleEventBus.subscribe(RESOURCE_UPDATE, this.handleResourceUpdate)();
    this.thresholds.clear();
    this.thresholdStates.clear();
    this.activeAlerts.clear();
    this.resourceStates.clear();
  }
}

import { Position } from '../../types/core/GameTypes';
import { WeaponCategory, WeaponVariant } from '../../types/weapons/WeaponTypes';
import { EventEmitter } from '../../utils/EventEmitter';
import { effectLifecycleManager } from '../effects/EffectLifecycleManager';
import { particleSystemManager } from '../effects/ParticleSystemManager';

interface WeaponEffectEvents {
  effectStarted: { weaponId: string; effectType: string };
  effectEnded: { weaponId: string; effectType: string };
  qualityChanged: { weaponId: string; quality: 'low' | 'medium' | 'high' };
}

interface WeaponEffectConfig {
  duration: number;
  particleCount: number;
  color: string;
  size: number;
  spread: number;
  speed: number;
  pattern: 'beam' | 'projectile' | 'explosion' | 'continuous';
  quality?: 'low' | 'medium' | 'high';
}

export class WeaponEffectManager extends EventEmitter<WeaponEffectEvents> {
  private static instance: WeaponEffectManager;
  private activeEffects: Map<string, Set<string>>;
  private effectConfigs: Map<WeaponCategory, Map<WeaponVariant, WeaponEffectConfig>>;
  private qualitySettings: Map<string, 'low' | 'medium' | 'high'>;

  private constructor() {
    super();
    this.activeEffects = new Map();
    this.effectConfigs = new Map();
    this.qualitySettings = new Map();
    this.initializeEffectConfigs();
  }

  public static getInstance(): WeaponEffectManager {
    if (!WeaponEffectManager.instance) {
      WeaponEffectManager.instance = new WeaponEffectManager();
    }
    return WeaponEffectManager.instance;
  }

  private initializeEffectConfigs(): void {
    // Machine Gun effects
    this.setEffectConfig('machineGun', 'basic', {
      duration: 200,
      particleCount: 10,
      color: '#ffaa00',
      size: 2,
      spread: 0.1,
      speed: 2,
      pattern: 'projectile',
    });

    // Plasma Cannon effects
    this.setEffectConfig('plasmaCannon', 'basic', {
      duration: 1000,
      particleCount: 30,
      color: '#00ffff',
      size: 4,
      spread: 0.2,
      speed: 1,
      pattern: 'beam',
    });

    // Beam Weapon effects
    this.setEffectConfig('beamWeapon', 'basic', {
      duration: 2000,
      particleCount: 50,
      color: '#ff00ff',
      size: 3,
      spread: 0.05,
      speed: 3,
      pattern: 'continuous',
    });
  }

  private setEffectConfig(
    category: WeaponCategory,
    variant: WeaponVariant,
    config: WeaponEffectConfig
  ): void {
    if (!this.effectConfigs.has(category)) {
      this.effectConfigs.set(category, new Map());
    }
    this.effectConfigs.get(category)!.set(variant, config);
  }

  public createWeaponEffect(
    weaponId: string,
    category: WeaponCategory,
    variant: WeaponVariant,
    position: Position,
    direction: number
  ): string[] {
    const config = this.effectConfigs.get(category)?.get(variant);
    if (!config) {
      return [];
    }

    const quality = this.qualitySettings.get(weaponId) || 'medium';
    const particleCount = this.getQualityAdjustedParticleCount(config.particleCount, quality);

    const systemIds: string[] = [];

    // Create main weapon effect
    const mainEffectId = this.createMainEffect(weaponId, position, direction, config, quality);
    if (mainEffectId) {
      systemIds.push(mainEffectId);
    }

    // Create additional effects based on pattern
    switch (config.pattern) {
      case 'beam':
        systemIds.push(...this.createBeamEffect(position, direction, config, quality));
        break;
      case 'explosion':
        systemIds.push(...this.createExplosionEffect(position, config, quality));
        break;
      case 'continuous':
        systemIds.push(...this.createContinuousEffect(position, direction, config, quality));
        break;
    }

    // Register with effect lifecycle manager
    const effectId = effectLifecycleManager.registerEffect(
      `weapon-${category}-${variant}`,
      position,
      config.duration,
      systemIds,
      () => this.cleanupWeaponEffect(weaponId, systemIds)
    );

    // Track active effects
    if (!this.activeEffects.has(weaponId)) {
      this.activeEffects.set(weaponId, new Set());
    }
    this.activeEffects.get(weaponId)!.add(effectId);

    this.emit('effectStarted', { weaponId, effectType: `${category}-${variant}` });
    return systemIds;
  }

  private getQualityAdjustedParticleCount(
    baseCount: number,
    quality: 'low' | 'medium' | 'high'
  ): number {
    switch (quality) {
      case 'low':
        return Math.floor(baseCount * 0.5);
      case 'high':
        return Math.floor(baseCount * 1.5);
      default:
        return baseCount;
    }
  }

  private createMainEffect(
    weaponId: string,
    position: Position,
    direction: number,
    config: WeaponEffectConfig,
    quality: 'low' | 'medium' | 'high'
  ): string {
    return `${weaponId}-main-${Date.now()}`;
  }

  private createBeamEffect(
    position: Position,
    direction: number,
    config: WeaponEffectConfig,
    quality: 'low' | 'medium' | 'high'
  ): string[] {
    return [`beam-${Date.now()}`];
  }

  private createExplosionEffect(
    position: Position,
    config: WeaponEffectConfig,
    quality: 'low' | 'medium' | 'high'
  ): string[] {
    return [`explosion-${Date.now()}`];
  }

  private createContinuousEffect(
    position: Position,
    direction: number,
    config: WeaponEffectConfig,
    quality: 'low' | 'medium' | 'high'
  ): string[] {
    return [`continuous-${Date.now()}`];
  }

  private cleanupWeaponEffect(weaponId: string, systemIds: string[]): void {
    // Remove particle systems
    systemIds.forEach(id => {
      particleSystemManager.removeSystem(id);
    });

    // Update active effects tracking
    const effects = this.activeEffects.get(weaponId);
    if (effects) {
      effects.forEach(effectId => {
        this.emit('effectEnded', { weaponId, effectType: effectId });
      });
      effects.clear();
    }
  }

  public setQuality(weaponId: string, quality: 'low' | 'medium' | 'high'): void {
    this.qualitySettings.set(weaponId, quality);
    this.emit('qualityChanged', { weaponId, quality });
  }

  public getActiveEffects(weaponId: string): string[] {
    return Array.from(this.activeEffects.get(weaponId) || []);
  }

  public cleanup(): void {
    // Clean up all active effects
    for (const [weaponId, effects] of this.activeEffects) {
      effects.forEach(effectId => {
        effectLifecycleManager.cleanupEffectsByType(`weapon-${effectId}`);
      });
      effects.clear();
    }
    this.activeEffects.clear();
    this.qualitySettings.clear();
  }
}

// Export singleton instance
export const weaponEffectManager = WeaponEffectManager.getInstance();

import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import {
  CombatWeaponStats,
  WeaponCategory,
  WeaponInstance,
