/**
 * Officer specializations
 */
export type OfficerSpecialization = 'War' | 'Recon' | 'Mining';

/**
 * Officer status
 */
export type OfficerStatus = 'available' | 'training' | 'assigned';

/**
 * Officer tier
 */
export type OfficerTier = 1 | 2 | 3;

/**
 * Officer skills
 */
export interface OfficerSkills {
  combat: number;
  leadership: number;
  technical: number;
}

