// ------------------------------------------------------------

/**
 * Base officer interface
 */
export interface Officer {
  id: string;
  name: string;
  portrait: string;
  level: number;
  xp: number;
  nextLevelXp: number;
  role: OfficerRole;
  status: OfficerStatus;
  specialization: OfficerSpecialization;
  skills: OfficerSkills;
