import { ResourceState, ResourceType } from './ResourceTypes';

/**
 * Pool allocation strategy
 */
export type PoolAllocationStrategy = 'equal' | 'priority' | 'demand-based' | 'custom';

/**
 * Pool distribution rule
 */
export interface PoolDistributionRule {
  id: string;
  poolId: string;
  targetIds: string[];
  resourceType: ResourceType;
  percentage: number;
  minAmount?: number;
  maxAmount?: number;
  priority: number;
  condition?: (state: ResourceState) => boolean;
}

/**
 * Pool allocation result
 */
export interface PoolAllocationResult {
  poolId: string;
  resourceType: ResourceType;
  allocations: Array<{
    targetId: string;
    amount: number;
    percentage: number;
  }>;
  timestamp: number;
}

/**
 * Pool allocation options
 */
export interface PoolAllocationOptions {
  strategy?: PoolAllocationStrategy;
  priorityMap?: Map<string, number>;
  customAllocations?: Map<string, number>;
  minAllocation?: number;
  maxAllocation?: number;
}

/**
 * Pool distribution options
 */
export interface PoolDistributionOptions {
  interval?: number;
  maxHistory?: number;
  autoDistribute?: boolean;
  validateRules?: boolean;
}

/**
 * Pool target demand
 */
export interface PoolTargetDemand {
  targetId: string;
  resourceType: ResourceType;
  currentAmount: number;
  capacity: number;
  priority: number;
  demandFactor: number;
}

/**
 * Pool resource summary
 */
export interface PoolResourceSummary {
  poolId: string;
  resourceType: ResourceType;
  amount: number;
  capacity: number;
  percentage: number;
  distributionRules: number;
  lastDistribution?: number;
}

/**
 * Type guard for PoolDistributionRule
 */
export function isPoolDistributionRule(obj: any): obj is PoolDistributionRule {
  return (
    obj !== null &&
    typeof obj === 'object' &&
    'id' in obj &&
    'poolId' in obj &&
    'targetIds' in obj &&
    'resourceType' in obj &&
    'percentage' in obj &&
    'priority' in obj &&
    Array.isArray(obj.targetIds)
  );
}

/**
 * Type guard for PoolAllocationResult
 */
export function isPoolAllocationResult(obj: any): obj is PoolAllocationResult {
  return (
    obj !== null &&
    typeof obj === 'object' &&
    'poolId' in obj &&
    'resourceType' in obj &&
    'allocations' in obj &&
    'timestamp' in obj &&
    Array.isArray(obj.allocations)
  );
}

/**
 * Helper function to create a default distribution rule
 */
export function createDefaultDistributionRule(
  poolId: string,
  targetIds: string[],
  resourceType: ResourceType
): PoolDistributionRule {
  return {
    id: `rule-${poolId}-${resourceType}-${Date.now()}`,
    poolId,
    targetIds,
    resourceType,
    percentage: 100,
    priority: 1,
  };
}

/**
 * Helper function to calculate demand factor for a target
 */
export function calculateDemandFactor(currentAmount: number, capacity: number): number {
  if (capacity <= 0) {
    return 0;
  }
  const fillPercentage = (currentAmount / capacity) * 100;
  return Math.max(0, 100 - fillPercentage) / 100;
}

import { ResourceAlert, ResourceType } from './ResourceTypes';

/**
 * Interface for serialized resource data
 * Used for localStorage persistence
 */
export interface SerializedResource {
  current: number;
  capacity: number;
  production: number;
  consumption: number;
  history?: Array<{
    timestamp: number;
    amount: number;
    type: 'production' | 'consumption' | 'transfer';
  }>;
}

/**
 * Interface for serialized threshold data
 * Used for localStorage persistence
 */
export interface SerializedThreshold {
  min?: number;
  max?: number;
  target?: number;
  alert?: boolean;
}

/**
 * Interface for serialized resource state
 * Used for localStorage persistence
 */
export interface SerializedResourceState {
  resources: Record<ResourceType, SerializedResource>;
  thresholds: Record<string, SerializedThreshold[]>;
  alerts: ResourceAlert[];
  timestamp?: number;
}

/**
 * Interface for resource totals
 * Used for summary calculations
 */
export interface ResourceTotals {
  production: number;
  consumption: number;
  net: number;
  amounts?: Record<ResourceType, number>;
  capacities?: Record<ResourceType, number>;
  rates?: Record<ResourceType, number>;
}

/**
 * Type guard for SerializedResource
 */
export function isSerializedResource(obj: any): obj is SerializedResource {
  return (
    obj !== null &&
    typeof obj === 'object' &&
    'current' in obj &&
    'capacity' in obj &&
    'production' in obj &&
    'consumption' in obj
  );
}

/**
 * Type guard for SerializedResourceState
 */
export function isSerializedResourceState(obj: any): obj is SerializedResourceState {
  return (
    obj !== null &&
    typeof obj === 'object' &&
    'resources' in obj &&
    'thresholds' in obj &&
    'alerts' in obj
  );
}

/**
 * Helper function to convert Map to Record for serialization
 */
export function serializeResourceMap(map: Map<ResourceType, any>): Record<ResourceType, any> {
  const record: Record<ResourceType, any> = {} as Record<ResourceType, any>;

  // Convert Map entries to array to avoid MapIterator error
  Array.from(map.entries()).forEach(([key, value]) => {
    record[key] = value;
  });

  return record;
}

/**
 * Helper function to convert Record to Map for deserialization
 */
export function deserializeResourceMap<T>(record: Record<ResourceType, T>): Map<ResourceType, T> {
  const map = new Map<ResourceType, T>();

  Object.entries(record).forEach(([key, value]) => {
    map.set(key as ResourceType, value);
  });

  return map;
}

/**
 * Helper function to provide default values for a resource
 */
export function getResourceWithDefaults(resource: Partial<SerializedResource>): SerializedResource {
  return {
    current: resource.current ?? 0,
    capacity: resource.capacity ?? 100,
    production: resource.production ?? 0,
    consumption: resource.consumption ?? 0,
    history: resource.history ?? [],
  };
}

/**
 * Helper function to validate a serialized resource state
 */
export function validateResourceState(state: SerializedResourceState): boolean {
  if (!state.resources || typeof state.resources !== 'object') return false;
  if (!state.thresholds || typeof state.thresholds !== 'object') return false;
  if (!Array.isArray(state.alerts)) return false;

  // Check each resource
  for (const key in state.resources) {
    const resource = state.resources[key as ResourceType];
    if (!isSerializedResource(resource)) return false;
  }

  return true;
}

/**
 * Generic event emitter implementation
 */
export class EventEmitter<T extends Record<string, any>> {
  private listeners: Map<keyof T, Array<(data: T[keyof T]) => void>> = new Map();

  /**
   * Subscribe to an event
   */
  public on<K extends keyof T>(event: K, callback: (data: T[K]) => void): void {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, []);
    }
    this.listeners.get(event)?.push(callback as (data: T[keyof T]) => void);
  }

  /**
   * Unsubscribe from an event
   */
  public off<K extends keyof T>(event: K, callback: (data: T[K]) => void): void {
    const listeners = this.listeners.get(event);
    if (listeners) {
      const index = listeners.indexOf(callback as (data: T[keyof T]) => void);
      if (index !== -1) {
        listeners.splice(index, 1);
      }
    }
  }

  /**
   * Emit an event with data
   */
  public emit<K extends keyof T>(event: K, data: T[K]): void {
    const listeners = this.listeners.get(event);
    if (listeners) {
      listeners.forEach(callback => callback(data as T[keyof T]));
    }
  }

  /**
   * Subscribe to an event (alias for on)
   */
  public subscribe<K extends keyof T>(event: K, callback: (data: T[K]) => void): void {
    this.on(event, callback);
  }

  /**
   * Unsubscribe from an event (alias for off)
   */
  public unsubscribe<K extends keyof T>(event: K, callback: (data: T[K]) => void): void {
    this.off(event, callback);
  }

  /**
   * Remove all listeners for an event
   */
  public removeAllListeners<K extends keyof T>(event?: K): void {
    if (event) {
      this.listeners.delete(event);
    } else {
      this.listeners.clear();
    }
  }
}

import { Observable, Subject } from 'rxjs';
import { filter, map } from 'rxjs/operators';
import { ModuleEvent, ModuleEventType, moduleEventBus } from '../../lib/modules/ModuleEvents';

/**
 * System identifier type
 */
export type SystemId =
  | 'resource-system'
  | 'module-system'
  | 'combat-system'
  | 'exploration-system'
  | 'mining-system'
  | 'tech-system'
  | 'ui-system'
  | 'game-loop'
  | 'event-system';

/**
 * Message priority levels
 */
export enum MessagePriority {
  CRITICAL = 0,
  HIGH = 1,
  NORMAL = 2,
  LOW = 3,
  BACKGROUND = 4,
}

/**
 * System message interface
 */
export interface SystemMessage {
  id: string;
  source: SystemId;
  target: SystemId | 'broadcast';
  type: string;
  priority: MessagePriority;
  timestamp: number;
  payload: any;
  requiresAck?: boolean;
  correlationId?: string;
}

/**
 * Message acknowledgment interface
 */
export interface MessageAcknowledgment {
  messageId: string;
  source: SystemId;
  target: SystemId;
  timestamp: number;
  success: boolean;
  error?: string;
  correlationId?: string;
}

/**
 * Message handler type
 */
export type MessageHandler = (message: SystemMessage) => void | Promise<void>;

/**
 * Event communication system
 */
export class EventCommunication {
  private handlers: Map<SystemId, Map<string, Set<MessageHandler>>> = new Map();
  private messageSubject: Subject<SystemMessage> = new Subject();
  private ackSubject: Subject<MessageAcknowledgment> = new Subject();
  private systemId: SystemId;
  private pendingAcks: Map<
    string,
    {
      message: SystemMessage;
      timeout: NodeJS.Timeout;
      resolve: (ack: MessageAcknowledgment) => void;
      reject: (error: Error) => void;
    }
  > = new Map();

  constructor(systemId: SystemId) {
    this.systemId = systemId;
    this.initializeEventBusIntegration();
  }

  /**
   * Initialize integration with the module event bus
   */
  private initializeEventBusIntegration(): void {
    // Subscribe to system communication events
    moduleEventBus.subscribe('SYSTEM_MESSAGE' as ModuleEventType, (event: ModuleEvent) => {
      if (event.data && event.data.message) {
        const message = event.data.message as SystemMessage;

        // Process the message if it's targeted at this system or is a broadcast
        if (message.target === this.systemId || message.target === 'broadcast') {
          this.processIncomingMessage(message);
        }
      }
    });

    // Subscribe to acknowledgment events
    moduleEventBus.subscribe('SYSTEM_MESSAGE_ACK' as ModuleEventType, (event: ModuleEvent) => {
      if (event.data && event.data.ack) {
        const ack = event.data.ack as MessageAcknowledgment;

        // Process the acknowledgment if it's targeted at this system
        if (ack.target === this.systemId) {
          this.processAcknowledgment(ack);
        }
      }
    });
  }

  /**
   * Process an incoming message
   */
  private processIncomingMessage(message: SystemMessage): void {
    // Forward to the message subject
    this.messageSubject.next(message);

    // Get handlers for this message type
    const systemHandlers = this.handlers.get(this.systemId);
    if (!systemHandlers) {
      // If no handlers and message requires acknowledgment, send negative ack
      if (message.requiresAck) {
        this.sendAcknowledgment(
          message.id,
          message.source,
          false,
          'No handlers registered for this system'
        );
      }
      return;
    }

    const typeHandlers = systemHandlers.get(message.type);
    if (!typeHandlers || typeHandlers.size === 0) {
      // If no handlers for this type and message requires acknowledgment, send negative ack
      if (message.requiresAck) {
        this.sendAcknowledgment(
          message.id,
          message.source,
          false,
          `No handlers registered for message type: ${message.type}`
        );
      }
      return;
    }

    // Call all handlers
    const handlerPromises: Promise<void>[] = [];
    const errors: Error[] = [];

    typeHandlers.forEach(handler => {
      try {
        const result = handler(message);
        if (result instanceof Promise) {
          handlerPromises.push(
            result.catch(error => {
              errors.push(error);
            })
          );
        }
      } catch (error) {
        errors.push(error as Error);
      }
    });

    // If message requires acknowledgment, wait for all handlers to complete
    if (message.requiresAck) {
      if (handlerPromises.length > 0) {
        // Wait for all promises to resolve
        Promise.all(handlerPromises).then(() => {
          this.sendAcknowledgment(
            message.id,
            message.source,
            errors.length === 0,
            errors.length > 0 ? errors.map(e => e.message).join(', ') : undefined
          );
        });
      } else {
        // Send acknowledgment immediately
        this.sendAcknowledgment(
          message.id,
          message.source,
          errors.length === 0,
          errors.length > 0 ? errors.map(e => e.message).join(', ') : undefined
        );
      }
    }
  }

  /**
   * Process an acknowledgment
   */
  private processAcknowledgment(ack: MessageAcknowledgment): void {
    // Forward to the ack subject
    this.ackSubject.next(ack);

    // Resolve pending promise if exists
    const pending = this.pendingAcks.get(ack.messageId);
    if (pending) {
      clearTimeout(pending.timeout);
      this.pendingAcks.delete(ack.messageId);

      if (ack.success) {
        pending.resolve(ack);
      } else {
        pending.reject(new Error(ack.error || 'Unknown error'));
      }
    }
  }

  /**
   * Send an acknowledgment
   */
  private sendAcknowledgment(
    messageId: string,
    target: SystemId,
    success: boolean,
    error?: string
  ): void {
    const ack: MessageAcknowledgment = {
      messageId,
      source: this.systemId,
      target,
      timestamp: Date.now(),
      success,
      error,
    };

    // Emit through the module event bus
    moduleEventBus.emit({
      type: 'SYSTEM_MESSAGE_ACK' as ModuleEventType,
      moduleId: `system-${this.systemId}`,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { ack },
    });
  }

  /**
   * Register a message handler
   */
  public registerHandler(type: string, handler: MessageHandler): () => void {
    // Create system handlers map if it doesn't exist
    if (!this.handlers.has(this.systemId)) {
      this.handlers.set(this.systemId, new Map());
    }

    // Create type handlers set if it doesn't exist
    const systemHandlers = this.handlers.get(this.systemId)!;
    if (!systemHandlers.has(type)) {
      systemHandlers.set(type, new Set());
    }

    // Add the handler
    const typeHandlers = systemHandlers.get(type)!;
    typeHandlers.add(handler);

    // Return unregister function
    return () => {
      const systemHandlers = this.handlers.get(this.systemId);
      if (systemHandlers) {
        const typeHandlers = systemHandlers.get(type);
        if (typeHandlers) {
          typeHandlers.delete(handler);
          if (typeHandlers.size === 0) {
            systemHandlers.delete(type);
          }
        }
        if (systemHandlers.size === 0) {
          this.handlers.delete(this.systemId);
        }
      }
    };
  }

  /**
   * Send a message to another system
   */
  public sendMessage(
    target: SystemId | 'broadcast',
    type: string,
    payload: any,
    options: {
      priority?: MessagePriority;
      requiresAck?: boolean;
      timeout?: number;
      correlationId?: string;
    } = {}
  ): Promise<MessageAcknowledgment> | void {
    const {
      priority = MessagePriority.NORMAL,
      requiresAck = false,
      timeout = 5000,
      correlationId,
    } = options;

    const messageId = `${this.systemId}-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    const message: SystemMessage = {
      id: messageId,
      source: this.systemId,
      target,
      type,
      priority,
      timestamp: Date.now(),
      payload,
      requiresAck,
      correlationId,
    };

    // Emit through the module event bus
    moduleEventBus.emit({
      type: 'SYSTEM_MESSAGE' as ModuleEventType,
      moduleId: `system-${this.systemId}`,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { message },
    });

    // If acknowledgment is required, return a promise
    if (requiresAck) {
      return new Promise<MessageAcknowledgment>((resolve, reject) => {
        // Set timeout for acknowledgment
        const timeoutId = setTimeout(() => {
          this.pendingAcks.delete(messageId);
          reject(new Error(`Acknowledgment timeout for message ${messageId}`));
        }, timeout);

        // Store pending acknowledgment
        this.pendingAcks.set(messageId, {
          message,
          timeout: timeoutId,
          resolve,
          reject,
        });
      });
    }
  }

  /**
   * Get an observable of messages for a specific type
   */
  public getMessages(type?: string): Observable<SystemMessage> {
    return this.messageSubject
      .asObservable()
      .pipe(filter(message => !type || message.type === type));
  }

  /**
   * Get an observable of acknowledgments
   */
  public getAcknowledgments(correlationId?: string): Observable<MessageAcknowledgment> {
    return this.ackSubject
      .asObservable()
      .pipe(filter(ack => !correlationId || ack.correlationId === correlationId));
  }

  /**
   * Get an observable of message payloads for a specific type
   */
  public getMessagePayloads<T>(type: string): Observable<T> {
    return this.getMessages(type).pipe(map(message => message.payload as T));
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    // Clear all pending acknowledgments
    this.pendingAcks.forEach(pending => {
      clearTimeout(pending.timeout);
      pending.reject(new Error('System communication cleanup'));
    });
    this.pendingAcks.clear();

    // Clear all handlers
    this.handlers.clear();

    // Complete subjects
    this.messageSubject.complete();
    this.ackSubject.complete();
  }
}

// Map of system communication instances
const systemCommunications: Map<SystemId, EventCommunication> = new Map();

/**
 * Get or create a system communication instance
 */
export function getSystemCommunication(systemId: SystemId): EventCommunication {
  if (!systemCommunications.has(systemId)) {
    systemCommunications.set(systemId, new EventCommunication(systemId));
  }
  return systemCommunications.get(systemId)!;
}

/**
 * Clean up all system communications
 */
export function cleanupAllSystemCommunications(): void {
  systemCommunications.forEach(comm => comm.cleanup());
  systemCommunications.clear();
}

import React, { createContext, ReactNode, useContext, useEffect, useState } from 'react';
import { ModuleEvent, moduleEventBus, ModuleEventType } from '../../lib/modules/ModuleEvents';

/**
 * Event dispatcher context interface
 */
interface EventDispatcherContextType {
  // Event subscription
  subscribe: (type: ModuleEventType, listener: (event: ModuleEvent) => void) => () => void;

  // Event emission
  emit: (event: ModuleEvent) => void;

  // Event history
  getHistory: () => ModuleEvent[];
  getModuleHistory: (moduleId: string) => ModuleEvent[];
  getEventTypeHistory: (type: ModuleEventType) => ModuleEvent[];
  clearHistory: () => void;

  // Event filtering
  getFilteredEvents: (filter: (event: ModuleEvent) => boolean) => ModuleEvent[];

  // Latest events by type
  latestEvents: Map<ModuleEventType, ModuleEvent>;
}

/**
 * Event dispatcher provider props
 */
interface EventDispatcherProviderProps {
  children: ReactNode;
  maxHistorySize?: number;
}

// Create the context with a default value
const EventDispatcherContext = createContext<EventDispatcherContextType | null>(null);

/**
 * Event dispatcher provider component
 */
export const EventDispatcherProvider: React.FC<EventDispatcherProviderProps> = ({
  children,
  maxHistorySize = 1000,
}) => {
  // Store the latest event of each type
  const [latestEvents, setLatestEvents] = useState<Map<ModuleEventType, ModuleEvent>>(new Map());

  // Subscribe to all module events
  useEffect(() => {
    // Create a handler for all events
    const handleEvent = (event: ModuleEvent) => {
      setLatestEvents(prev => {
        const newMap = new Map(prev);
        newMap.set(event.type, event);
        return newMap;
      });
    };

    // Get all possible event types
    const eventTypes: ModuleEventType[] = [
      'MODULE_CREATED',
      'MODULE_ATTACHED',
      'MODULE_DETACHED',
      'MODULE_UPGRADED',
      'MODULE_ACTIVATED',
      'MODULE_DEACTIVATED',
      'ATTACHMENT_STARTED',
      'ATTACHMENT_CANCELLED',
      'ATTACHMENT_COMPLETED',
      'ATTACHMENT_PREVIEW_SHOWN',
      'RESOURCE_PRODUCED',
      'RESOURCE_CONSUMED',
      'RESOURCE_TRANSFERRED',
      'RESOURCE_PRODUCTION_REGISTERED',
      'RESOURCE_PRODUCTION_UNREGISTERED',
      'RESOURCE_CONSUMPTION_REGISTERED',
      'RESOURCE_CONSUMPTION_UNREGISTERED',
      'RESOURCE_FLOW_REGISTERED',
      'RESOURCE_FLOW_UNREGISTERED',
      'RESOURCE_SHORTAGE',
      'AUTOMATION_STARTED',
      'AUTOMATION_STOPPED',
      'AUTOMATION_CYCLE_COMPLETE',
      'STATUS_CHANGED',
      'ERROR_OCCURRED',
      'MISSION_STARTED',
      'MISSION_COMPLETED',
      'MISSION_FAILED',
      'MISSION_PROGRESS_UPDATED',
      'MISSION_REWARD_CLAIMED',
      'SUB_MODULE_CREATED',
      'SUB_MODULE_ATTACHED',
      'SUB_MODULE_DETACHED',
      'SUB_MODULE_UPGRADED',
      'SUB_MODULE_ACTIVATED',
      'SUB_MODULE_DEACTIVATED',
      'SUB_MODULE_EFFECT_APPLIED',
      'SUB_MODULE_EFFECT_REMOVED',
    ];

    // Subscribe to all event types
    const unsubscribers = eventTypes.map(type => moduleEventBus.subscribe(type, handleEvent));

    // Cleanup subscriptions
    return () => {
      unsubscribers.forEach(unsubscribe => {
        if (typeof unsubscribe === 'function') {
          unsubscribe();
        }
      });
    };
  }, []);

  // Context value
  const value: EventDispatcherContextType = {
    // Event subscription - delegate to moduleEventBus
    subscribe: (type, listener) => moduleEventBus.subscribe(type, listener),

    // Event emission - delegate to moduleEventBus
    emit: event => moduleEventBus.emit(event),

    // Event history - delegate to moduleEventBus
    getHistory: () => moduleEventBus.getHistory(),
    getModuleHistory: moduleId => moduleEventBus.getModuleHistory(moduleId),
    getEventTypeHistory: type => moduleEventBus.getEventTypeHistory(type),
    clearHistory: () => moduleEventBus.clearHistory(),

    // Event filtering
    getFilteredEvents: filter => moduleEventBus.getHistory().filter(filter),

    // Latest events by type
    latestEvents,
  };

  return (
    <EventDispatcherContext.Provider value={value}>{children}</EventDispatcherContext.Provider>
  );
};

/**
 * Hook to use the event dispatcher
 */
export const useEventDispatcher = (): EventDispatcherContextType => {
  const context = useContext(EventDispatcherContext);

  if (!context) {
    throw new Error('useEventDispatcher must be used within an EventDispatcherProvider');
  }

  return context;
};

/**
 * Hook to subscribe to specific event types
 */
export const useEventSubscription = <T extends ModuleEventType>(
  eventType: T,
  callback: (event: ModuleEvent) => void,
  deps: React.DependencyList = []
): void => {
  const { subscribe } = useEventDispatcher();

  useEffect(() => {
    return subscribe(eventType, callback);
  }, [eventType, ...deps]); // eslint-disable-line react-hooks/exhaustive-deps
};

/**
 * Hook to get the latest event of a specific type
 */
export const useLatestEvent = <T extends ModuleEventType>(
  eventType: T
): ModuleEvent | undefined => {
  const { latestEvents } = useEventDispatcher();
  return latestEvents.get(eventType);
};

/**
 * Hook to get filtered events
 */
export const useFilteredEvents = (
  filter: (event: ModuleEvent) => boolean,
  deps: React.DependencyList = []
): ModuleEvent[] => {
  const { getFilteredEvents } = useEventDispatcher();
  const [filteredEvents, setFilteredEvents] = useState<ModuleEvent[]>([]);

  useEffect(() => {
    setFilteredEvents(getFilteredEvents(filter));
  }, [getFilteredEvents, ...deps]); // eslint-disable-line react-hooks/exhaustive-deps

  return filteredEvents;
};

import { Observable, timer } from 'rxjs';
import { buffer, debounceTime, filter, groupBy, map, mergeMap } from 'rxjs/operators';
import { ModuleEvent, ModuleEventType } from '../../lib/modules/ModuleEvents';
import { MessagePriority, SystemMessage } from './EventCommunication';

/**
 * Filter condition type for event filtering
 */
export type FilterCondition<T> = (event: T) => boolean;

/**
 * Event batch processor type
 */
export type BatchProcessor<T, R> = (events: T[]) => R;

/**
 * Event priority queue for handling events based on priority
 */
export class EventPriorityQueue<T extends { priority?: number }> {
  private queues: Map<number, T[]> = new Map();
  private processing = false;
  private processingPromise: Promise<void> | null = null;
  private processor: (event: T) => Promise<void> | void;

  /**
   * Create a new event priority queue
   * @param processor Function to process each event
   */
  constructor(processor: (event: T) => Promise<void> | void) {
    this.processor = processor;

    // Initialize queues for each priority level
    for (let i = 0; i <= 4; i++) {
      this.queues.set(i, []);
    }
  }

  /**
   * Add an event to the queue
   * @param event Event to add
   */
  public enqueue(event: T): void {
    const priority = event.priority !== undefined ? event.priority : MessagePriority.NORMAL;
    const queue = this.queues.get(priority);
    if (queue) {
      queue.push(event);
      this.processQueue();
    }
  }

  /**
   * Process the queue
   */
  private async processQueue(): Promise<void> {
    if (this.processing) {
      return;
    }

    this.processing = true;
    this.processingPromise = this.processQueueInternal();
    await this.processingPromise;
    this.processingPromise = null;
    this.processing = false;

    // Check if new events were added during processing
    let hasEvents = false;
    // Convert Map entries to array to avoid MapIterator error
    const queueValues = Array.from(this.queues.values());
    for (const queue of queueValues) {
      if (queue.length > 0) {
        hasEvents = true;
        break;
      }
    }

    if (hasEvents) {
      this.processQueue();
    }
  }

  /**
   * Internal queue processing
   */
  private async processQueueInternal(): Promise<void> {
    // Process all events in priority order
    const queueEntries = Array.from(this.queues.entries());
    for (const [priority, queue] of queueEntries) {
      while (queue.length > 0) {
        const event = queue.shift();
        if (event) {
          try {
            const result = this.processor(event);
            if (result instanceof Promise) {
              await result;
            }
          } catch (error) {
            console.error('Error processing event:', error);
          }
        }
      }
    }
  }

  /**
   * Get the current queue sizes
   */
  public getQueueSizes(): Record<number, number> {
    const sizes: Record<number, number> = {};
    // Convert Map entries to array to avoid MapIterator error
    const queueEntries = Array.from(this.queues.entries());
    for (const [priority, queue] of queueEntries) {
      sizes[priority] = queue.length;
    }
    return sizes;
  }

  /**
   * Check if the queue is currently processing
   */
  public isProcessing(): boolean {
    return this.processing;
  }

  /**
   * Wait for all current processing to complete
   */
  public async waitForProcessing(): Promise<void> {
    if (this.processingPromise) {
      await this.processingPromise;
    }
  }

  /**
   * Clear all queues
   */
  public clear(): void {
    // Convert Map entries to array to avoid MapIterator error
    const queueValues = Array.from(this.queues.values());
    for (const queue of queueValues) {
      queue.length = 0;
    }
  }

  /**
   * Process all events in priority order
   */
  public processAll(): void {
    // Convert Map entries to array to avoid MapIterator error
    const queueEntries = Array.from(this.queues.entries());
    for (const [priority, queue] of queueEntries) {
      while (queue.length > 0) {
        const event = queue.shift();
        if (event) {
          this.processor(event);
        }
      }
    }
  }
}

/**
 * Create a filtered event stream
 * @param source Source observable
 * @param condition Filter condition
 */
export function createFilteredStream<T>(
  source: Observable<T>,
  condition: FilterCondition<T>
): Observable<T> {
  return source.pipe(filter(condition));
}

/**
 * Create a batched event stream
 * @param source Source observable
 * @param timeWindow Time window for batching in milliseconds
 * @param processor Batch processor function
 */
export function createBatchedStream<T, R>(
  source: Observable<T>,
  timeWindow: number,
  processor: BatchProcessor<T, R>
): Observable<R> {
  return source.pipe(
    buffer(timer(0, timeWindow)),
    filter(events => events.length > 0),
    map(events => processor(events))
  );
}

/**
 * Create a grouped event stream
 * @param source Source observable
 * @param keySelector Function to select the key for grouping
 */
export function createGroupedStream<T, K>(
  source: Observable<T>,
  keySelector: (event: T) => K
): Observable<{ key: K; events: T[] }> {
  return source.pipe(
    groupBy(keySelector),
    mergeMap(group => {
      return group.pipe(
        buffer(group.pipe(debounceTime(100))),
        map(events => ({ key: group.key, events }))
      );
    })
  );
}

/**
 * Filter module events by type
 * @param events Module events observable
 * @param type Event type to filter
 */
export function filterEventsByType(
  events: Observable<ModuleEvent>,
  type: ModuleEventType
): Observable<ModuleEvent> {
  return events.pipe(filter(event => event.type === type));
}

/**
 * Filter module events by module ID
 * @param events Module events observable
 * @param moduleId Module ID to filter
 */
export function filterEventsByModuleId(
  events: Observable<ModuleEvent>,
  moduleId: string
): Observable<ModuleEvent> {
  return events.pipe(filter(event => event.moduleId === moduleId));
}

/**
 * Filter module events by module type
 * @param events Module events observable
 * @param moduleType Module type to filter
 */
export function filterEventsByModuleType(
  events: Observable<ModuleEvent>,
  moduleType: string
): Observable<ModuleEvent> {
  return events.pipe(filter(event => event.moduleType === moduleType));
}

/**
 * Filter system messages by type
 * @param messages System messages observable
 * @param type Message type to filter
 */
export function filterMessagesByType(
  messages: Observable<SystemMessage>,
  type: string
): Observable<SystemMessage> {
  return messages.pipe(filter(message => message.type === type));
}

/**
 * Filter system messages by source
 * @param messages System messages observable
 * @param source Source system ID to filter
 */
export function filterMessagesBySource(
  messages: Observable<SystemMessage>,
  source: string
): Observable<SystemMessage> {
  return messages.pipe(filter(message => message.source === source));
}

/**
 * Filter system messages by priority
 * @param messages System messages observable
 * @param priority Priority level to filter
 * @param comparison Comparison operator ('eq' | 'lt' | 'lte' | 'gt' | 'gte')
 */
export function filterMessagesByPriority(
  messages: Observable<SystemMessage>,
  priority: MessagePriority,
  comparison: 'eq' | 'lt' | 'lte' | 'gt' | 'gte' = 'eq'
): Observable<SystemMessage> {
  return messages.pipe(
    filter(message => {
      const messagePriority = message.priority;
      switch (comparison) {
        case 'eq':
          return messagePriority === priority;
        case 'lt':
          return messagePriority < priority;
        case 'lte':
          return messagePriority <= priority;
        case 'gt':
          return messagePriority > priority;
        case 'gte':
          return messagePriority >= priority;
        default:
          return messagePriority === priority;
      }
    })
  );
}

/**
 * Create a conditional event processor
 * @param condition Condition to check
 * @param processor Processor function
 */
export function createConditionalProcessor<T>(
  condition: FilterCondition<T>,
  processor: (event: T) => void
): (event: T) => void {
  return (event: T) => {
    if (condition(event)) {
      processor(event);
    }
  };
}

/**
 * Create a debounced event processor
 * @param processor Processor function
 * @param debounceMs Debounce time in milliseconds
 */
export function createDebouncedProcessor<T>(
  processor: (event: T) => void,
  debounceMs: number
): {
  process: (event: T) => void;
  flush: () => void;
} {
  let timeout: NodeJS.Timeout | null = null;
  let lastEvent: T | null = null;

  return {
    process: (event: T) => {
      lastEvent = event;

      if (timeout) {
        clearTimeout(timeout);
      }

      timeout = setTimeout(() => {
        if (lastEvent) {
          processor(lastEvent);
          lastEvent = null;
        }
        timeout = null;
      }, debounceMs);
    },
    flush: () => {
      if (timeout) {
        clearTimeout(timeout);
        timeout = null;
      }

      if (lastEvent) {
        processor(lastEvent);
        lastEvent = null;
      }
    },
  };
}

/**
 * Create a throttled event processor
 * @param processor Processor function
 * @param throttleMs Throttle time in milliseconds
 */
export function createThrottledProcessor<T>(
  processor: (event: T) => void,
  throttleMs: number
): (event: T) => void {
  let lastProcessTime = 0;
  let pending: T | null = null;
  let timeout: NodeJS.Timeout | null = null;

  return (event: T) => {
    const now = Date.now();

    if (now - lastProcessTime >= throttleMs) {
      // Process immediately
      lastProcessTime = now;
      processor(event);
    } else {
      // Store for later processing
      pending = event;

      if (!timeout) {
        timeout = setTimeout(
          () => {
            if (pending) {
              processor(pending);
              pending = null;
            }
            lastProcessTime = Date.now();
            timeout = null;
          },
          throttleMs - (now - lastProcessTime)
        );
      }
    }
  };
}

/**
 * Create a batch event processor
 * @param batchProcessor Batch processor function
 * @param maxBatchSize Maximum batch size
 * @param maxWaitMs Maximum wait time in milliseconds
 */
export function createBatchProcessor<T, R>(
  batchProcessor: BatchProcessor<T, R>,
  maxBatchSize: number,
  maxWaitMs: number
): {
  process: (event: T) => void;
  flush: () => R | null;
  onResult: (callback: (result: R) => void) => () => void;
} {
  const batch: T[] = [];
  let timeout: NodeJS.Timeout | null = null;
  const resultCallbacks: Set<(result: R) => void> = new Set();

  const processCurrentBatch = (): R | null => {
    if (batch.length === 0) {
      return null;
    }

    const currentBatch = [...batch];
    batch.length = 0;

    const result = batchProcessor(currentBatch);

    // Notify callbacks
    resultCallbacks.forEach(callback => {
      try {
        callback(result);
      } catch (error) {
        console.error('Error in batch result callback:', error);
      }
    });

    return result;
  };

  return {
    process: (event: T) => {
      batch.push(event);

      if (batch.length >= maxBatchSize) {
        // Process immediately if batch is full
        if (timeout) {
          clearTimeout(timeout);
          timeout = null;
        }
        processCurrentBatch();
      } else if (!timeout) {
        // Start timer for batch processing
        timeout = setTimeout(() => {
          timeout = null;
          processCurrentBatch();
        }, maxWaitMs);
      }
    },
    flush: () => {
      if (timeout) {
        clearTimeout(timeout);
        timeout = null;
      }
      return processCurrentBatch();
    },
    onResult: (callback: (result: R) => void) => {
      resultCallbacks.add(callback);
      return () => {
        resultCallbacks.delete(callback);
      };
    },
  };
}

/**
 * Create a priority-based event processor
 * @param processors Map of processors by priority
 * @param defaultPriority Default priority if not specified
 */
export function createPriorityProcessor<T extends { priority?: number }>(
  processors: Map<number, (event: T) => void>,
  defaultPriority: number = MessagePriority.NORMAL
): (event: T) => void {
  return (event: T) => {
    const priority = event.priority !== undefined ? event.priority : defaultPriority;
    const processor = processors.get(priority);

    if (processor) {
      processor(event);
    } else {
      // Find the closest priority processor
      let closestPriority: number | null = null;
      let minDistance = Infinity;

      for (const p of processors.keys()) {
        const distance = Math.abs(p - priority);
        if (distance < minDistance) {
          minDistance = distance;
          closestPriority = p;
        }
      }

      if (closestPriority !== null) {
        const closestProcessor = processors.get(closestPriority);
        if (closestProcessor) {
          closestProcessor(event);
        }
      }
    }
  };
}

import { Observable, Subject, filter, map, share } from 'rxjs';
import { ModuleEvent, ModuleEventType, moduleEventBus } from '../../lib/modules/ModuleEvents';

/**
 * Subject for all module events
 */
export const moduleEventSubject = new Subject<ModuleEvent>();

/**
 * Observable for all module events
 */
export const moduleEvents$ = moduleEventSubject.asObservable().pipe(
  share() // Share the observable to prevent multiple subscriptions
);

/**
 * Initialize the RxJS integration with the moduleEventBus
 */
export function initializeRxJSIntegration(): () => void {
  // Subscribe to all module events and forward them to the subject
  const unsubscribe = moduleEventBus.subscribe('MODULE_CREATED' as ModuleEventType, event => {
    moduleEventSubject.next(event);
  });

  // Return a cleanup function
  return () => {
    if (typeof unsubscribe === 'function') {
      unsubscribe();
    }
    moduleEventSubject.complete();
  };
}

/**
 * Get an observable for a specific event type
 */
export function getEventsByType<T extends ModuleEventType>(eventType: T): Observable<ModuleEvent> {
  return moduleEvents$.pipe(filter(event => event.type === eventType));
}

/**
 * Get an observable for events from a specific module
 */
export function getEventsByModule(moduleId: string): Observable<ModuleEvent> {
  return moduleEvents$.pipe(filter(event => event.moduleId === moduleId));
}

/**
 * Get an observable for events with a specific data property
 */
export function getEventsByData<T>(
  propertyName: string,
  propertyValue: T
): Observable<ModuleEvent> {
  return moduleEvents$.pipe(
    filter(
      event =>
        event.data &&
        event.data[propertyName] !== undefined &&
        event.data[propertyName] === propertyValue
    )
  );
}

/**
 * Get an observable that maps events to their data
 */
export function getEventData<T>(eventType: ModuleEventType): Observable<T> {
  return moduleEvents$.pipe(
    filter(event => event.type === eventType),
    map(event => event.data as T)
  );
}

/**
 * Create a custom event observable with filtering
 */
export function createFilteredEventStream(
  filterFn: (event: ModuleEvent) => boolean
): Observable<ModuleEvent> {
  return moduleEvents$.pipe(filter(filterFn));
}

/**
 * Emit an event through the RxJS subject and moduleEventBus
 */
export function emitEvent(event: ModuleEvent): void {
  // Emit through the moduleEventBus
  moduleEventBus.emit(event);

  // Also emit through the RxJS subject
  moduleEventSubject.next(event);
}

/**
 * Hook up a Subject to a specific event type
 */
export function createEventTypeSubject<T extends ModuleEventType>(
  eventType: T
): Subject<ModuleEvent> {
  const subject = new Subject<ModuleEvent>();

  // Subscribe to the event type and forward to the subject
  const unsubscribe = moduleEventBus.subscribe(eventType, event => {
    subject.next(event);
  });

  // Add cleanup method to the subject
  (subject as any).cleanup = () => {
    if (typeof unsubscribe === 'function') {
      unsubscribe();
    }
    subject.complete();
  };

  return subject;
}

/**
 * Create a specialized event stream with transformation
 */
export function createTransformedEventStream<T, R>(
  eventType: ModuleEventType,
  transformFn: (event: ModuleEvent) => R
): Observable<R> {
  return moduleEvents$.pipe(
    filter(event => event.type === eventType),
    map(transformFn)
  );
}

/**
 * Create a debounced event stream
 */
export function createDebouncedEventStream(
  eventType: ModuleEventType,
  debounceTime: number
): Observable<ModuleEvent> {
  return moduleEvents$.pipe(
    filter(event => event.type === eventType)
    // We would normally use debounceTime here, but we're keeping it simple
    // debounceTime(debounceTime)
  );
}

/**
 * Create a throttled event stream
 */
export function createThrottledEventStream(
  eventType: ModuleEventType,
  throttleTime: number
): Observable<ModuleEvent> {
  return moduleEvents$.pipe(
    filter(event => event.type === eventType)
    // We would normally use throttleTime here, but we're keeping it simple
    // throttleTime(throttleTime)
  );
}

/**
 * Create a buffered event stream that collects events over time
 */
export function createBufferedEventStream(
  eventType: ModuleEventType,
  bufferTime: number
): Observable<ModuleEvent[]> {
  return moduleEvents$.pipe(
    filter(event => event.type === eventType)
    // We would normally use bufferTime here, but we're keeping it simple
    // bufferTime(bufferTime)
  ) as unknown as Observable<ModuleEvent[]>;
}

/**
 * Create a combined event stream from multiple event types
 */
export function createCombinedEventStream(eventTypes: ModuleEventType[]): Observable<ModuleEvent> {
  return moduleEvents$.pipe(filter(event => eventTypes.includes(event.type)));
}

import {
  AdvancedResource,
  BasicResource,
  ResourceConsumption,
  ResourceContainer,
  ResourceCost,
  ResourceFlow,
  ResourcePool,
  ResourceProduction,
  ResourceState,
  ResourceStorage,
  ResourceThreshold,
  ResourceTransfer,
  ResourceType,
  SpecialResource,
} from '../../types/resources/ResourceTypes';

/**
 * Type guard for ResourceType
 */
export function isResourceType(value: any): value is ResourceType {
  const validTypes: ResourceType[] = [
    'minerals',
    'energy',
    'population',
    'research',
    'plasma',
    'gas',
    'exotic',
  ];
  return typeof value === 'string' && validTypes.includes(value as ResourceType);
}

/**
 * Validates a ResourceState object
 */
export function validateResourceState(state: ResourceState): boolean {
  if (typeof state !== 'object' || state === null) {
    return false;
  }

  return (
    typeof state.current === 'number' &&
    typeof state.max === 'number' &&
    typeof state.min === 'number' &&
    typeof state.production === 'number' &&
    typeof state.consumption === 'number' &&
    state.min <= state.current &&
    state.current <= state.max
  );
}

/**
 * Validates a ResourceTransfer object
 */
export function validateResourceTransfer(transfer: ResourceTransfer): boolean {
  if (typeof transfer !== 'object' || transfer === null) {
    return false;
  }

  return (
    isResourceType(transfer.type) &&
    typeof transfer.amount === 'number' &&
    transfer.amount > 0 &&
    typeof transfer.source === 'string' &&
    typeof transfer.target === 'string' &&
    typeof transfer.timestamp === 'number'
  );
}

/**
 * Validates a ResourceThreshold object
 */
export function validateResourceThreshold(threshold: ResourceThreshold): boolean {
  if (typeof threshold !== 'object' || threshold === null) {
    return false;
  }

  // Type must be valid
  if (!isResourceType(threshold.type)) {
    return false;
  }

  // At least one threshold value must be defined
  if (
    threshold.min === undefined &&
    threshold.max === undefined &&
    threshold.target === undefined
  ) {
    return false;
  }

  // Validate threshold values if defined
  if (threshold.min !== undefined && typeof threshold.min !== 'number') {
    return false;
  }
  if (threshold.max !== undefined && typeof threshold.max !== 'number') {
    return false;
  }
  if (threshold.target !== undefined && typeof threshold.target !== 'number') {
    return false;
  }

  // Ensure min <= target <= max if all are defined
  if (
    threshold.min !== undefined &&
    threshold.max !== undefined &&
    threshold.target !== undefined &&
    !(threshold.min <= threshold.target && threshold.target <= threshold.max)
  ) {
    return false;
  }

  // Ensure min <= max if both are defined
  if (threshold.min !== undefined && threshold.max !== undefined && threshold.min > threshold.max) {
    return false;
  }

  return true;
}

/**
 * Validates a ResourceProduction object
 */
export function validateResourceProduction(production: ResourceProduction): boolean {
  if (typeof production !== 'object' || production === null) {
    return false;
  }

  // Basic properties validation
  const basicValid =
    isResourceType(production.type) &&
    typeof production.amount === 'number' &&
    production.amount >= 0 &&
    typeof production.interval === 'number' &&
    production.interval > 0;

  if (!basicValid) {
    return false;
  }

  // Validate conditions if defined
  if (production.conditions) {
    if (!Array.isArray(production.conditions)) {
      return false;
    }

    for (const condition of production.conditions) {
      if (!validateResourceThreshold(condition)) {
        return false;
      }
    }
  }

  return true;
}

/**
 * Validates a ResourceConsumption object
 */
export function validateResourceConsumption(consumption: ResourceConsumption): boolean {
  if (typeof consumption !== 'object' || consumption === null) {
    return false;
  }

  // Basic properties validation
  const basicValid =
    isResourceType(consumption.type) &&
    typeof consumption.amount === 'number' &&
    consumption.amount >= 0 &&
    typeof consumption.interval === 'number' &&
    consumption.interval > 0 &&
    typeof consumption.required === 'boolean';

  if (!basicValid) {
    return false;
  }

  // Validate conditions if defined
  if (consumption.conditions) {
    if (!Array.isArray(consumption.conditions)) {
      return false;
    }

    for (const condition of consumption.conditions) {
      if (!validateResourceThreshold(condition)) {
        return false;
      }
    }
  }

  return true;
}

/**
 * Validates a ResourceFlow object
 */
export function validateResourceFlow(flow: ResourceFlow): boolean {
  if (typeof flow !== 'object' || flow === null) {
    return false;
  }

  // Basic properties validation
  const basicValid =
    typeof flow.source === 'string' &&
    typeof flow.target === 'string' &&
    Array.isArray(flow.resources) &&
    flow.resources.length > 0;

  if (!basicValid) {
    return false;
  }

  // Validate resources
  for (const resource of flow.resources) {
    if (
      !isResourceType(resource.type) ||
      typeof resource.amount !== 'number' ||
      resource.amount <= 0 ||
      typeof resource.interval !== 'number' ||
      resource.interval <= 0
    ) {
      return false;
    }
  }

  // Validate conditions if defined
  if (flow.conditions) {
    if (!Array.isArray(flow.conditions)) {
      return false;
    }

    for (const condition of flow.conditions) {
      if (!validateResourceThreshold(condition)) {
        return false;
      }
    }
  }

  return true;
}

/**
 * Validates a ResourceCost object
 */
export function validateResourceCost(cost: ResourceCost): boolean {
  if (typeof cost !== 'object' || cost === null) {
    return false;
  }

  return isResourceType(cost.type) && typeof cost.amount === 'number' && cost.amount >= 0;
}

/**
 * Validates an array of ResourceCost objects
 */
export function validateResourceCosts(costs: ResourceCost[]): boolean {
  if (!Array.isArray(costs)) {
    return false;
  }

  for (const cost of costs) {
    if (!validateResourceCost(cost)) {
      return false;
    }
  }

  return true;
}

/**
 * Type guard for BasicResource
 */
export function isBasicResource(resource: any): resource is BasicResource {
  if (typeof resource !== 'object' || resource === null) {
    return false;
  }

  return (
    'id' in resource &&
    'name' in resource &&
    'type' in resource &&
    'category' in resource &&
    resource.category === 'basic'
  );
}

/**
 * Type guard for AdvancedResource
 */
export function isAdvancedResource(resource: any): resource is AdvancedResource {
  if (typeof resource !== 'object' || resource === null) {
    return false;
  }

  return (
    'id' in resource &&
    'name' in resource &&
    'type' in resource &&
    'category' in resource &&
    'components' in resource &&
    resource.category === 'advanced' &&
    Array.isArray(resource.components)
  );
}

/**
 * Type guard for SpecialResource
 */
export function isSpecialResource(resource: any): resource is SpecialResource {
  if (typeof resource !== 'object' || resource === null) {
    return false;
  }

  return (
    'id' in resource &&
    'name' in resource &&
    'type' in resource &&
    'category' in resource &&
    'rarity' in resource &&
    resource.category === 'special'
  );
}

/**
 * Type guard for ResourceContainer
 */
export function isResourceContainer(container: any): container is ResourceContainer {
  if (typeof container !== 'object' || container === null) {
    return false;
  }

  return (
    'id' in container &&
    'name' in container &&
    'capacity' in container &&
    typeof container.capacity === 'number' &&
    container.capacity > 0
  );
}

/**
 * Type guard for ResourcePool
 */
export function isResourcePool(pool: any): pool is ResourcePool {
  if (!isResourceContainer(pool)) {
    return false;
  }

  return (
    'type' in pool &&
    'poolType' in pool &&
    (pool.poolType === 'global' || pool.poolType === 'module')
  );
}

/**
 * Type guard for ResourceStorage
 */
export function isResourceStorage(storage: any): storage is ResourceStorage {
  if (!isResourceContainer(storage)) {
    return false;
  }

  return (
    'type' in storage &&
    'storageType' in storage &&
    (storage.storageType === 'basic' || storage.storageType === 'advanced') &&
    'efficiency' in storage &&
    typeof storage.efficiency === 'number' &&
    storage.efficiency > 0 &&
    storage.efficiency <= 1
  );
}

import { CombatUnit, FactionCombatUnit } from '../types/combat/CombatTypes';
import { FactionShipClass } from '../types/ships/FactionShipTypes';
import { FactionId } from '../types/ships/FactionTypes';
import { WeaponMount, WeaponSystem } from '../types/weapons/WeaponTypes';
import {
  WeaponSystemType,
  convertSystemTypeToWeaponCategory,
  convertWeaponCategoryToSystemType,
} from './weapons/weaponTypeConversions';

export function convertWeaponMountToSystem(mount: WeaponMount): WeaponSystem {
  if (!mount.currentWeapon) {
    return {
      id: mount.id,
      type: convertWeaponCategoryToSystemType(mount.allowedCategories[0] || 'machineGun'),
      damage: 0,
      range: 0,
      cooldown: 0,
      status: 'ready',
    };
  }

  const weapon = mount.currentWeapon;
  return {
    id: weapon.config.id,
    type: convertWeaponCategoryToSystemType(weapon.config.category),
    damage: weapon.state.currentStats.damage,
    range: weapon.state.currentStats.range,
    cooldown: weapon.state.currentStats.cooldown,
    status: weapon.state.status as 'ready' | 'charging' | 'cooling',
    upgrades: weapon.config.requirements?.tech.map(tech => ({
      name: tech,
      description: `Requires ${tech}`,
      unlocked: false,
    })),
  };
}

export function convertWeaponSystemToMount(weapon: WeaponSystem, index: number): WeaponMount {
  const category = convertSystemTypeToWeaponCategory(weapon.type as WeaponSystemType);
  return {
    id: `mount-${index}`,
    size: 'medium',
    position: index % 2 === 0 ? 'front' : 'side',
    rotation: 0,
    allowedCategories: [category],
    currentWeapon: {
      config: {
        id: weapon.id,
        name: weapon.type,
        category,
        tier: 1,
        baseStats: {
          damage: weapon.damage,
          range: weapon.range,
          cooldown: weapon.cooldown,
          accuracy: 0.8,
          rateOfFire: 1,
          energyCost: 1,
          effects: [],
        },
        visualAsset: `weapons/${weapon.type}.png`,
        mountRequirements: {
          size: 'medium',
          power: 1,
        },
        requirements: weapon.upgrades
          ? {
              tech: weapon.upgrades.map(upgrade => upgrade.name),
              resources: [],
            }
          : undefined,
      },
      state: {
        currentStats: {
          damage: weapon.damage,
          range: weapon.range,
          cooldown: weapon.cooldown,
          accuracy: 0.8,
          rateOfFire: 1,
          energyCost: 1,
          effects: [],
        },
        status: weapon.status,
        effects: [],
      },
    },
  };
}

export function convertToFactionCombatUnit(
  unit: CombatUnit,
  factionId: FactionId,
  shipClass: FactionShipClass
): FactionCombatUnit {
  const weaponMounts = unit.weapons.map((weapon, index) =>
    convertWeaponSystemToMount(weapon, index)
  );
  const weaponSystems = weaponMounts.map(convertWeaponMountToSystem);

  // Get formation data with safe defaults
  const formationType = (unit as any).formation?.type ?? 'balanced';
  const formationSpacing = (unit as any).formation?.spacing ?? 100;
  const formationFacing = (unit as any).formation?.facing ?? 0;

  return {
    ...unit,
    faction: factionId,
    class: shipClass,
    tactics: {
      formation: 'standard',
      behavior: 'balanced',
      target: undefined,
    },
    weaponMounts,
    weapons: weaponSystems,
    formation: {
      type: formationType,
      spacing: formationSpacing,
      facing: formationFacing,
      position: 0,
    },
    stats: {
      health: unit.stats?.health || 100,
      maxHealth: unit.stats?.maxHealth || 100,
      shield: unit.stats?.shield || 50,
      maxShield: unit.stats?.maxShield || 50,
      armor: unit.stats?.armor || 0,
      speed: unit.stats?.speed || 5,
      turnRate: unit.stats?.turnRate || 1,
      accuracy: 0.8,
      evasion: 0.2,
      criticalChance: 0.1,
      criticalDamage: 1.5,
      armorPenetration: 0,
      shieldPenetration: 0,
      experience: 0,
      level: 1,
    },
    status: {
      main: unit.status?.main || 'active',
      secondary: unit.status?.secondary,
      effects: unit.status?.effects || [],
    },
    experience: {
      current: 0,
      total: 0,
      level: 1,
      skills: [],
    },
  };
}

export function convertToBaseCombatUnit(unit: FactionCombatUnit): CombatUnit {
  return {
    id: unit.id,
    type: unit.type,
    position: unit.position,
    rotation: unit.rotation || 0,
    velocity: unit.velocity || { x: 0, y: 0 },
    status: unit.status,
    weapons: unit.weapons,
    stats: {
      health: unit.stats.health,
      maxHealth: unit.stats.maxHealth,
      shield: unit.stats.shield,
      maxShield: unit.stats.maxShield,
      armor: unit.stats.armor,
      speed: unit.stats.speed,
      turnRate: unit.stats.turnRate,
    },
  };
}

// Type guard functions
export function isFactionCombatUnit(
  unit: CombatUnit | FactionCombatUnit
): unit is FactionCombatUnit {
  return 'class' in unit && 'tactics' in unit && 'weaponMounts' in unit;
}

export function isBaseCombatUnit(unit: CombatUnit | FactionCombatUnit): unit is CombatUnit {
  return !isFactionCombatUnit(unit);
}

/**
 * Converts a CombatUnit from combatManager.ts to a CombatUnit from CombatTypes.ts
 */
export function convertToCombatTypesUnit(
  unit: any
): import('../types/combat/CombatTypes').CombatUnit {
  // Create a CombatUnit that matches the interface in CombatTypes.ts
  return {
    id: unit.id,
    type: unit.type,
    position: unit.position,
    rotation: 0, // Default value if not present
    velocity: { x: 0, y: 0 }, // Default value if not present
    status: {
      main: convertStatusToMain(unit.status),
      secondary: undefined,
      effects: [],
    },
    weapons: unit.weapons.map((w: any) => ({
      id: w.id,
      type: w.type,
      damage: w.damage,
      cooldown: w.cooldown,
      range: w.range,
      state: {
        status: w.status,
        lastFired: w.lastFired || 0,
        effects: [],
      },
    })),
    stats: {
      health: unit.health || 0,
      maxHealth: unit.maxHealth || 0,
      shield: unit.shield || 0,
      maxShield: unit.maxShield || 0,
      armor: unit.armor || 0,
      speed: unit.speed || 0,
      turnRate: unit.turnRate || 0,
    },
  };
}

/**
 * Converts a CombatUnit from CombatTypes.ts to a CombatUnit from combatManager.ts
 */
export function convertToManagerUnit(unit: import('../types/combat/CombatTypes').CombatUnit): any {
  // Create a CombatUnit that matches the interface in combatManager.ts
  return {
    id: unit.id,
    faction: 'unknown', // Default value if not present
    type: unit.type,
    tier: 1, // Default value if not present
    position: unit.position,
    status: convertMainToStatus(unit.status.main),
    health: unit.stats.health,
    maxHealth: unit.stats.maxHealth,
    shield: unit.stats.shield,
    maxShield: unit.stats.maxShield,
    target: undefined, // Default value if not present
    weapons: unit.weapons.map(w => {
      // Extract weapon properties safely
      const weaponId = w.id;
      const weaponType = w.type;
      const weaponRange = w.range || 0;
      const weaponDamage = w.damage;
      const weaponCooldown = w.cooldown;

      // Access state properties safely using optional chaining and nullish coalescing
      const weaponStatus = (w as any).state?.status || 'ready';
      const weaponLastFired = (w as any).state?.lastFired || 0;

      return {
        id: weaponId,
        type: weaponType,
        range: weaponRange,
        damage: weaponDamage,
        cooldown: weaponCooldown,
        status: weaponStatus,
        lastFired: weaponLastFired,
      };
    }),
  };
}

/**
 * Converts a status string from combatManager.ts to a main status in CombatTypes.ts
 */
function convertStatusToMain(status: string): 'active' | 'disabled' | 'destroyed' {
  if (status === 'disabled') {
    return 'disabled';
  }
  if (status === 'destroyed') {
    return 'destroyed';
  }
  return 'active'; // Default for all other statuses
}

/**
 * Converts a main status from CombatTypes.ts to a status string in combatManager.ts
 */
function convertMainToStatus(main: 'active' | 'disabled' | 'destroyed'): string {
  if (main === 'disabled') {
    return 'disabled';
  }
  if (main === 'destroyed') {
    return 'destroyed';
  }
  return 'idle'; // Default for 'active'
}

