#!/usr/bin/env node

import { execSync } from 'child_process';
import fs from 'fs';
import path from 'path';
import readline from 'readline';
import { fileURLToPath } from 'url';

/**
 * Advanced ESLint & Prettier issue fixer by rule name
 * Usage: node fix-eslint-by-rule.js [rule-name] [directory] [options]
 * Example: node fix-eslint-by-rule.js no-unused-vars src/components
 *
 * Options:
 *   --auto      Automatically fix top issue without prompting
 *   --batch=N   Fix N issues at once (batched fixing to avoid overwhelming changes)
 *   --dry-run   Show what would be fixed without making changes
 *   --prettier  Fix Prettier formatting issues
 *   --strict    Display all issues, including those in ignored files
 *   --threshold=N Only fix rules with at least N occurrences
 */

// Get __dirname equivalent in ESM
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Configuration
const CONFIG = {
  eslintCmd: 'npx eslint',
  prettierCmd: 'npx prettier',
  defaultFilePatterns: ['js', 'jsx', 'ts', 'tsx', 'vue'],
  ignoreDirs: ['node_modules', 'dist', 'build', '.git', 'coverage'],
  topIssuesToShow: 15, // Number of top issues to display
  batchSize: 10, // Default number of files to fix in one batch
  fixableRuleHints: {
    // Provide hints for common rules
    'no-unused-vars': 'Remove unused variables',
    'no-console': 'Remove console.log statements',
    'prefer-const': 'Convert let to const where possible',
    indent: 'Fix indentation issues',
    quotes: 'Fix quote style issues (single vs double)',
    semi: 'Fix missing or extra semicolons',
    eqeqeq: 'Replace == with === and != with !==',
    'no-multiple-empty-lines': 'Remove extra blank lines',
    'prettier/prettier': 'Fix formatting to match Prettier configuration',
  },
  recommendedOrder: [
    // Recommended order for fixing rules
    'prettier/prettier', // Fix formatting first
    'no-unused-imports', // Then remove unused imports
    'no-unused-vars', // Then remove unused variables
    'eqeqeq', // Then fix equality operators
    'prefer-const', // Then convert let to const
  ],
  // Helper messages to guide the user
  helpMessages: {
    prettierConfig:
      'No .prettierrc found. Create one with: echo \'{"singleQuote": true, "trailingComma": "es5", "printWidth": 100}\' > .prettierrc',
    eslintConfig: 'No ESLint configuration found. Create one with: npm init @eslint/config',
  },
  logFile: 'eslint-progress.log',
};

// Process command line arguments
const args = process.argv.slice(2);
const ruleName = args.find(arg => !arg.startsWith('--') && arg.indexOf('=') === -1);
const directory =
  args.find(arg => !arg.startsWith('--') && arg !== ruleName && arg.indexOf('=') === -1) || '.';
const options = {
  auto: args.includes('--auto'),
  dryRun: args.includes('--dry-run'),
  prettier: args.includes('--prettier'),
  strict: args.includes('--strict'),
  batchSize: CONFIG.batchSize,
  threshold: 1,
};

// Parse options with values
args.forEach(arg => {
  if (arg.startsWith('--batch=')) {
    options.batchSize = parseInt(arg.split('=')[1], 10) || CONFIG.batchSize;
  }
  if (arg.startsWith('--threshold=')) {
    options.threshold = parseInt(arg.split('=')[1], 10) || 1;
  }
});

// Help function
function showHelp() {
  console.log('Advanced ESLint & Prettier Issue Fixer by Rule');
  console.log('---------------------------------------------');
  console.log('Usage: node fix-eslint-by-rule.js [rule-name] [directory] [options]');
  console.log('\nOptions:');
  console.log('  --auto          Automatically fix the top issue without prompting');
  console.log('  --batch=N       Fix N files at once (default: 10)');
  console.log('  --dry-run       Show what would be fixed without making changes');
  console.log('  --prettier      Fix Prettier formatting issues');
  console.log('  --strict        Include issues in files that would normally be ignored');
  console.log('  --threshold=N   Only fix rules with at least N occurrences');
  console.log('  --help          Show this help message');
  console.log('\nExamples:');
  console.log('  node fix-eslint-by-rule.js              # Show top issues');
  console.log('  node fix-eslint-by-rule.js no-unused-vars src # Fix no-unused-vars in src');
  console.log('  node fix-eslint-by-rule.js --auto       # Auto-fix top issue');
  console.log('  node fix-eslint-by-rule.js --prettier   # Fix formatting issues');
  process.exit(0);
}

if (args.includes('--help') || args.includes('-h')) {
  showHelp();
}

// Check for ESLint and Prettier configurations
function checkConfigurations() {
  // Check for ESLint config
  const eslintConfigFiles = [
    '.eslintrc.js',
    '.eslintrc.cjs',
    '.eslintrc.yaml',
    '.eslintrc.yml',
    '.eslintrc.json',
    '.eslintrc',
    'eslint.config.js',
  ];
  const hasEslintConfig = eslintConfigFiles.some(configFile =>
    fs.existsSync(path.join(process.cwd(), configFile)),
  );

  // Check for Prettier config
  const prettierConfigFiles = [
    '.prettierrc',
    '.prettierrc.js',
    '.prettierrc.cjs',
    '.prettierrc.yaml',
    '.prettierrc.yml',
    '.prettierrc.json',
    '.prettierrc.toml',
    'prettier.config.js',
  ];
  const hasPrettierConfig = prettierConfigFiles.some(configFile =>
    fs.existsSync(path.join(process.cwd(), configFile)),
  );

  if (!hasEslintConfig) {
    console.warn('  Warning: No ESLint configuration found.');
    console.warn(`   ${CONFIG.helpMessages.eslintConfig}`);
  }

  if (options.prettier && !hasPrettierConfig) {
    console.warn('  Warning: No Prettier configuration found.');
    console.warn(`   ${CONFIG.helpMessages.prettierConfig}`);
  }

  return { hasEslintConfig, hasPrettierConfig };
}

// Safe execution function with error handling
function safeExec(command, { silent = false, json = false } = {}) {
  try {
    const output = execSync(command, {
      encoding: 'utf8',
      stdio: silent ? 'pipe' : ['pipe', 'pipe', 'pipe'],
    });

    if (json) {
      try {
        return JSON.parse(output);
      } catch (err) {
        console.warn('Warning: Could not parse command output as JSON.');
        return { parseError: true, output };
      }
    }

    return output;
  } catch (error) {
    // For ESLint, an error code is expected when issues are found
    if (error.stdout && json) {
      try {
        return JSON.parse(error.stdout);
      } catch (parseErr) {
        return { parseError: true, output: error.stdout };
      }
    } else if (error.stdout) {
      return error.stdout;
    }
    throw error;
  }
}

// Function to build file glob patterns
function buildFileGlobs(patterns = CONFIG.defaultFilePatterns, baseDir = '.') {
  return patterns.map(ext => `"${baseDir}/**/*.${ext}"`).join(' ');
}

// Create a readline interface for user input
const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout,
});

// Function to prompt user for confirmation
function confirm(question) {
  return new Promise(resolve => {
    rl.question(`${question} (y/n) `, answer => {
      resolve(answer.toLowerCase().startsWith('y'));
    });
  });
}

// Function to prompt user to select an option
async function promptSelect(message, options) {
  console.log(message);
  options.forEach((option, index) => {
    console.log(`${index + 1}. ${option}`);
  });

  return new Promise(resolve => {
    rl.question('Enter number: ', answer => {
      const index = parseInt(answer, 10) - 1;
      if (index >= 0 && index < options.length) {
        resolve(index);
      } else {
        console.log('Invalid selection. Please try again.');
        resolve(promptSelect(message, options));
      }
    });
  });
}

// Function to track progress
function trackProgress(fixedCount, ruleName) {
  if (fixedCount > 0) {
    try {
      const date = new Date().toISOString().split('T')[0];
      const progressEntry = `${date}: Fixed ${fixedCount} issues of rule "${ruleName}"`;

      if (fs.existsSync(CONFIG.logFile)) {
        fs.appendFileSync(CONFIG.logFile, `${progressEntry}\n`, 'utf8');
      }

      console.log('\n Progress entry added to eslint-progress.log');
    } catch (error) {
      console.warn('Could not update progress log:', error.message);
    }
  }
}

// Function to fix Prettier issues
async function fixPrettierIssues(dir) {
  console.log('\n Checking for Prettier formatting issues...');

  const fileGlobs = buildFileGlobs(CONFIG.defaultFilePatterns, dir);
  const checkCmd = `${CONFIG.prettierCmd} --check ${fileGlobs} --loglevel=error`;

  try {
    safeExec(checkCmd);
    console.log(' No Prettier formatting issues found.');
    return 0;
  } catch (error) {
    // Prettier found formatting issues
    console.log('Found Prettier formatting issues.');

    if (options.dryRun) {
      console.log('(Dry run mode - no changes will be made)');
      return 0;
    }

    if (!options.auto) {
      const shouldFix = await confirm('Do you want to fix all formatting issues?');
      if (!shouldFix) {
        return 0;
      }
    }

    const fixCmd = `${CONFIG.prettierCmd} --write ${fileGlobs}`;
    console.log(`\n Fixing formatting issues...`);

    try {
      execSync(fixCmd, { stdio: 'inherit' });
      console.log('\n Fixed Prettier formatting issues!');
      return 1; // We don't know the exact count, so return 1 for positive progress
    } catch (error) {
      console.error('Error fixing Prettier issues:', error.message);
      return 0;
    }
  }
}

// Main function to find and fix issues
async function main() {
  // Check for configurations
  const { hasEslintConfig, hasPrettierConfig } = checkConfigurations();

  // Handle Prettier fixing
  if (options.prettier || ruleName === 'prettier' || ruleName === 'prettier/prettier') {
    const fixedCount = await fixPrettierIssues(directory);
    if (fixedCount > 0) {
      trackProgress(fixedCount, 'prettier/prettier');
    }

    if (!ruleName || ruleName === 'prettier' || ruleName === 'prettier/prettier') {
      rl.close();
      return;
    }
  }

  // If no rule specified, find top issues
  if (!ruleName) {
    console.log(' Analyzing linting issues...');

    // Add --no-ignore in strict mode
    const strictFlag = options.strict ? '--no-ignore' : '';

    // Build the ESLint command
    const fileGlobs = buildFileGlobs(CONFIG.defaultFilePatterns, directory);
    const eslintCmd = `${CONFIG.eslintCmd} ${fileGlobs} ${strictFlag} --format json`;

    try {
      // Get ESLint report
      const eslintData = safeExec(eslintCmd, { silent: true, json: true });

      if (eslintData.parseError) {
        throw new Error('Could not parse ESLint output.');
      }

      // Count by rule
      const ruleStats = {};
      const filesByRule = {};

      eslintData.forEach(file => {
        if (file.messages && file.messages.length > 0) {
          file.messages.forEach(msg => {
            const rule = msg.ruleId || 'unknown';
            if (!rule) {
              return;
            }

            if (!ruleStats[rule]) {
              ruleStats[rule] = 0;
              filesByRule[rule] = new Set();
            }

            ruleStats[rule]++;
            filesByRule[rule].add(file.filePath);
          });
        }
      });

      // Sort and display top issues
      const sortedRules = Object.entries(ruleStats)
        .sort((a, b) => b[1] - a[1])
        .filter(([_, count]) => count >= options.threshold)
        .slice(0, CONFIG.topIssuesToShow);

      if (sortedRules.length === 0) {
        console.log('\n No linting issues found matching your criteria.');
        rl.close();
        return;
      }

      console.log('\n Top linting issues:');
      console.log('------------------');

      // Order rules by recommended fixing order if possible
      const orderedRules = [...sortedRules].sort((a, b) => {
        const indexA = CONFIG.recommendedOrder.indexOf(a[0]);
        const indexB = CONFIG.recommendedOrder.indexOf(b[0]);

        // If both are in the recommended order list, sort by that order
        if (indexA !== -1 && indexB !== -1) {
          return indexA - indexB;
        }
        // If only one is in the list, prioritize it
        if (indexA !== -1) {
          return -1;
        }
        if (indexB !== -1) {
          return 1;
        }
        // Otherwise keep original order (by count)
        return 0;
      });

      orderedRules.forEach(([rule, count], i) => {
        const fileCount = filesByRule[rule] ? filesByRule[rule].size : 0;
        const hint = CONFIG.fixableRuleHints[rule] || '';
        const hintText = hint ? ` (${hint})` : '';
        const isFixable = rule !== 'unknown' && rule !== null;
        const symbol = isFixable ? '' : '';

        console.log(
          `${i + 1}. ${symbol} ${rule}: ${count} issues in ${fileCount} files${hintText}`,
        );
      });

      // Auto-fix mode
      if (options.auto && orderedRules.length > 0) {
        const [topRule, _] = orderedRules[0];
        if (topRule && topRule !== 'unknown') {
          console.log(`\n Auto-fixing top issue: ${topRule}`);
          await fixIssuesByRule(topRule, directory);
        }
        rl.close();
        return;
      }

      // Interactive mode
      console.log('\n Options:');
      console.log('1. Fix an issue by number');
      console.log('2. Fix all issues');
      console.log('3. Exit');

      rl.question('\nEnter your choice (1-3): ', async choice => {
        if (choice === '1') {
          rl.question('\nEnter issue number to fix: ', async ruleNumber => {
            const index = parseInt(ruleNumber, 10) - 1;
            if (index >= 0 && index < orderedRules.length) {
              const [selectedRule, _] = orderedRules[index];
              await fixIssuesByRule(selectedRule, directory);
            } else {
              console.log(' Invalid rule number.');
            }
            rl.close();
          });
        } else if (choice === '2') {
          const shouldFix = await confirm(
            '\nAre you sure you want to fix ALL issues? This might make a lot of changes.',
          );
          if (shouldFix) {
            for (const [rule, _] of orderedRules) {
              if (rule !== 'unknown' && rule !== null) {
                await fixIssuesByRule(rule, directory);
              }
            }
          }
          rl.close();
        } else {
          rl.close();
        }
      });
    } catch (error) {
      console.error(' Error analyzing ESLint issues:', error.message);
      rl.close();
    }
  } else {
    // Fix the specified rule
    await fixIssuesByRule(ruleName, directory);
    rl.close();
  }
}

// Function to fix issues by rule
async function fixIssuesByRule(rule, dir) {
  console.log(`\n Checking for "${rule}" issues in ${dir}...`);

  try {
    const strictFlag = options.strict ? '--no-ignore' : '';
    const fileGlobs = buildFileGlobs(CONFIG.defaultFilePatterns, dir);

    // First, check how many issues of this type exist
    const checkCmd = `${CONFIG.eslintCmd} ${fileGlobs} ${strictFlag} --format json`;
    const beforeData = safeExec(checkCmd, { silent: true, json: true });

    if (beforeData.parseError) {
      throw new Error('Could not parse ESLint output.');
    }

    // Count issues of this type and gather affected files
    let beforeCount = 0;
    const affectedFiles = new Set();

    beforeData.forEach(file => {
      if (file.messages) {
        const matchingMessages = file.messages.filter(msg => msg.ruleId === rule);
        if (matchingMessages.length > 0) {
          beforeCount += matchingMessages.length;
          affectedFiles.add(file.filePath);
        }
      }
    });

    if (beforeCount === 0) {
      console.log(` No issues found for rule "${rule}".`);
      return 0;
    }

    console.log(
      ` Found ${beforeCount} issues for rule "${rule}" in ${affectedFiles.size} files.`,
    );

    if (options.dryRun) {
      console.log(' Dry run mode - no changes will be made.');
      console.log('The following files would be modified:');
      Array.from(affectedFiles)
        .slice(0, 10)
        .forEach(file => {
          console.log(`- ${path.relative(process.cwd(), file)}`);
        });
      if (affectedFiles.size > 10) {
        console.log(`... and ${affectedFiles.size - 10} more files`);
      }
      return 0;
    }

    const shouldFix = options.auto || (await confirm(`Do you want to fix "${rule}" issues?`));
    if (!shouldFix) {
      return 0;
    }

    // Special handling for Prettier
    if (rule === 'prettier/prettier' || rule === 'prettier') {
      return await fixPrettierIssues(dir);
    }

    // Fix in batches if there are many files
    const affectedFilesArray = Array.from(affectedFiles);
    const batchCount = Math.ceil(affectedFilesArray.length / options.batchSize);
    let fixedCount = 0;

    if (batchCount > 1) {
      console.log(` Fixing in ${batchCount} batches of ${options.batchSize} files...`);
    }

    for (let i = 0; i < batchCount; i++) {
      const batchFiles = affectedFilesArray
        .slice(i * options.batchSize, (i + 1) * options.batchSize)
        .map(f => `"${f}"`)
        .join(' ');

      if (batchCount > 1) {
        console.log(`\n Batch ${i + 1}/${batchCount}...`);
      }

      // Run the fix command
      const fixCmd = `${CONFIG.eslintCmd} ${batchFiles} --fix --rule "${rule}:error"`;
      execSync(fixCmd, { stdio: 'inherit' });

      fixedCount += beforeCount; // This is approximate since we don't recount per batch
    }

    // Check how many issues remain
    const afterData = safeExec(checkCmd, { silent: true, json: true });
    let afterCount = 0;

    afterData.forEach(file => {
      if (file.messages) {
        afterCount += file.messages.filter(msg => msg.ruleId === rule).length;
      }
    });

    const actualFixedCount = beforeCount - afterCount;

    console.log(`\n Results for "${rule}":`);
    console.log(`- Fixed: ${actualFixedCount} issues`);
    console.log(`- Remaining: ${afterCount} issues`);

    if (actualFixedCount > 0) {
      trackProgress(actualFixedCount, rule);
    }

    return actualFixedCount;
  } catch (error) {
    console.error(` Error fixing "${rule}" issues:`, error.message);
    return 0;
  }
}

// Run the main function
main().catch(error => {
  console.error(' Unexpected error:', error);
  rl.close();
  process.exit(1);
});

