#!/usr/bin/env node

import { execSync } from 'child_process';
import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

/**
 * Adds a new progress entry to eslint-progress.log
 * Run this daily or after fixing batches of issues
 * Enhanced version with automatic ignoring of node_modules and filtering options
 */

// Get __dirname equivalent in ESM
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Configuration (you can adjust these)
const CONFIG = {
  logFile: 'eslint-progress.log',
  filePatterns: ['js', 'ts', 'tsx'], // Removed 'jsx' since it's causing issues
  ignoreDirs: ['node_modules', 'dist', 'build', '.git'],
  includeDetails: true, // Set to true to include top issues in the log
  topIssuesCount: 5, // Number of top issues to display
  checkPrettier: true, // Whether to check Prettier formatting
};

// Process command line arguments
const args = process.argv.slice(2);
const showHelp = args.includes('--help') || args.includes('-h');
const includeDetails = args.includes('--details') ? true : CONFIG.includeDetails;
const checkPrettier = args.includes('--no-prettier') ? false : CONFIG.checkPrettier;
const silentMode = args.includes('--silent');

if (showHelp) {
  console.log('ESLint & Prettier Progress Tracker');
  console.log('Usage: ./track-eslint-progress.js [options]');
  console.log('');
  console.log('Options:');
  console.log('  --help, -h     Show this help message');
  console.log('  --details      Include details about top issues in the log');
  console.log("  --no-details   Don't include details about issues in the log");
  console.log('  --no-prettier  Skip Prettier check');
  console.log("  --silent       Don't show the progress chart");
  process.exit(0);
}

// Get current date
const date = new Date().toISOString().split('T')[0];
const dateEntry = `===== ${date} =====`;

// Helper function to execute commands safely
function safeExec(command) {
  try {
    return execSync(command, { encoding: 'utf8' });
  } catch (error) {
    // If the command fails but outputs something, return that output
    if (error.stdout) {
      return error.stdout;
    }
    throw error;
  }
}

// Run ESLint check and parse results
try {
  // Ensure the log file exists
  if (!fs.existsSync(CONFIG.logFile)) {
    fs.writeFileSync(CONFIG.logFile, '', 'utf8');
  }

  let logContent = `${dateEntry}\n`;
  let totalIssues = 0;
  let eslintErrors = 0;
  let eslintWarnings = 0;
  let prettierIssues = 0;

  // Build the ESLint command with file patterns and ignores
  const fileGlobs = CONFIG.filePatterns.map(ext => `"**/*.${ext}"`).join(' ');
  const eslintCmd = `npx eslint ${fileGlobs} --format json`;

  console.log('Running ESLint check...');
  const eslintOutput = safeExec(eslintCmd);

  // Parse ESLint results
  let eslintData = [];
  try {
    eslintData = JSON.parse(eslintOutput);
  } catch (e) {
    console.warn(
      'Warning: Could not parse ESLint output as JSON. ESLint may not be properly configured.',
    );
    console.warn('Proceeding with zero ESLint issues...');
  }

  // Count errors and warnings
  const issuesByRule = {};

  eslintData.forEach(file => {
    if (file.messages && file.messages.length > 0) {
      file.messages.forEach(msg => {
        if (msg.severity === 2) {
          eslintErrors++;
        } else if (msg.severity === 1) {
          eslintWarnings++;
        }

        // Track issues by rule
        const ruleId = msg.ruleId || 'unknown';
        if (!issuesByRule[ruleId]) {
          issuesByRule[ruleId] = 0;
        }
        issuesByRule[ruleId]++;
      });
    }
  });

  // Check Prettier formatting issues if enabled
  if (checkPrettier) {
    console.log('Running Prettier check...');
    try {
      const prettierCmd = `npx prettier --check ${fileGlobs} | wc -l`;
      const prettierOutput = safeExec(prettierCmd).trim();

      // Parse Prettier output - this is an approximation as Prettier doesn't have a strict format
      const prettierCount = parseInt(prettierOutput, 10);
      if (!isNaN(prettierCount) && prettierCount > 0) {
        // Usually Prettier prints a header line, so we subtract 1
        prettierIssues = Math.max(0, prettierCount - 1);
      }
    } catch (error) {
      console.warn('Warning: Could not run Prettier check. Prettier may not be installed.');
      console.warn('Proceeding with zero Prettier issues...');
    }
  }

  // Calculate totals
  totalIssues = eslintErrors + eslintWarnings + prettierIssues;

  // Format the progress entry
  const eslintProgressEntry = `${eslintErrors + eslintWarnings} ESLint problems (${eslintErrors} errors, ${eslintWarnings} warnings)`;
  const prettierProgressEntry = checkPrettier ? `${prettierIssues} Prettier formatting issues` : '';
  const progressEntry = `${totalIssues} problems (${eslintErrors} errors, ${eslintWarnings} warnings${checkPrettier ? `, ${prettierIssues} formatting` : ''})`;

  logContent += `${progressEntry}\n`;

  // Add details about top issues if enabled
  if (includeDetails && Object.keys(issuesByRule).length > 0) {
    logContent += '\nTop issues by rule:\n';

    // Sort issues by count (descending)
    const sortedIssues = Object.entries(issuesByRule)
      .sort((a, b) => b[1] - a[1])
      .slice(0, CONFIG.topIssuesCount);

    sortedIssues.forEach(([rule, count]) => {
      logContent += `- ${rule}: ${count} occurrences\n`;
    });

    // Add a suggestion for fixing the top issue
    if (sortedIssues.length > 0) {
      const [topRule, topCount] = sortedIssues[0];
      if (topRule && topRule !== 'unknown') {
        logContent += `\nSuggestion: Focus on fixing "${topRule}" issues (${topCount} occurrences)\n`;

        if (topRule.startsWith('prettier/') || topRule === 'prettier') {
          logContent += `Try running: npx prettier --write ${fileGlobs}\n`;
        } else {
          logContent += `Try running: npx eslint --fix . --rule "${topRule}: error"\n`;
        }
      }
    }
  }

  logContent += '\n';

  // Write to log file
  fs.appendFileSync(CONFIG.logFile, logContent, 'utf8');

  console.log('Progress entry added to eslint-progress.log');
  console.log(eslintProgressEntry);
  if (checkPrettier && prettierProgressEntry) {
    console.log(prettierProgressEntry);
  }
  console.log(`Total: ${totalIssues} issues`);

  // Generate chart
  if (!silentMode) {
    console.log('\nGenerating progress chart...');

    // Check if chart script exists in the same directory
    const chartScript = path.join(__dirname, 'chart-lint-progress.js');
    if (fs.existsSync(chartScript)) {
      // We can't directly require in ESM, so we'll run it as a separate process
      execSync(`node ${chartScript}`, { stdio: 'inherit' });
    } else {
      console.log(
        '\nTo generate a chart, please save the chart-lint-progress.js file in the same directory.',
      );
    }
  }
} catch (error) {
  console.error('Error tracking ESLint progress:', error.message);
  process.exit(1);
}

