import React, { lazy, Suspense, useEffect } from 'react';
import { SystemIntegration } from './components/core/SystemIntegration';
import { ThresholdIntegration } from './components/core/ThresholdIntegration';
import { GameStateMonitor } from './components/debug/GameStateMonitor';
import { TooltipProvider } from './components/ui/TooltipProvider';
import { defaultColony, defaultMothership } from './config/buildings/defaultBuildings';
import { defaultModuleConfigs } from './config/modules/defaultModuleConfigs';
import { GameActionType, GameProvider, useGameDispatch } from './contexts/GameContext';
import { ModuleActionType, ModuleProvider, useModuleDispatch } from './contexts/ModuleContext';
import { ResourceRatesProvider } from './contexts/ResourceRatesContext';
import { ThresholdProvider } from './contexts/ThresholdContext';
import { assetManager } from './managers/game/assetManager';
import { ResourceManager } from './managers/game/ResourceManager';
import { TechNode, techTreeManager } from './managers/game/techTreeManager';
import { moduleManager } from './managers/module/ModuleManager';
import { OfficerManager } from './managers/module/OfficerManager';
import { ShipHangarManager } from './managers/module/ShipHangarManager';
import { ModuleType } from './types/buildings/ModuleTypes';
import { ModuleStatus } from './types/modules/ModuleTypes';

// Import the GlobalErrorBoundary component
import { GlobalErrorBoundary } from './components/ui/GlobalErrorBoundary';
// Import error services
import { IntegrationErrorHandler } from './components/core/IntegrationErrorHandler';
import { ResourceVisualization } from './components/ui/ResourceVisualization';
import { useComponentProfiler } from './hooks/ui/useComponentProfiler';
import { useProfilingOverlay } from './hooks/ui/useProfilingOverlay';
import { errorLoggingService, ErrorSeverity, ErrorType } from './services/ErrorLoggingService';
import { eventPropagationService } from './services/EventPropagationService';
import { recoveryService } from './services/RecoveryService';
import { BaseEvent } from './types/events/EventTypes';

// Lazy load components that aren't needed on initial render
const GameLayout = lazy(() =>
  import('./components/ui/GameLayout').then(module => ({ default: module.GameLayout }))
);

// Loading component
const LoadingComponent = () => (
  <div className="flex h-screen w-full flex-col items-center justify-center bg-gray-900 text-white">
    <div className="mb-4 h-12 w-12 animate-spin rounded-full border-4 border-blue-500 border-t-transparent"></div>
    <h2 className="mb-2 text-xl font-medium">Loading Game Components...</h2>
    <p className="text-gray-400">Preparing your galactic adventure</p>
  </div>
);

// Initial tech tree setup
const initialTechs: TechNode[] = [
  {
    id: 'basic-ship-hangar',
    name: 'Basic Ship Hangar',
    description: 'Enables basic ship construction',
    type: 'hangar',
    tier: 1 as const,
    requirements: [],
    unlocked: true,
    category: 'infrastructure',
  },
  {
    id: 'basic-weapons',
    name: 'Basic Weapons',
    description: 'Enables basic weapon systems',
    type: 'weapons',
    tier: 1 as const,
    requirements: ['basic-ship-hangar'],
    unlocked: false,
    category: 'weapons',
  },
  {
    id: 'basic-sensors',
    name: 'Basic Sensors',
    description: 'Enables basic scanning capabilities',
    type: 'recon',
    tier: 1 as const,
    requirements: ['basic-ship-hangar'],
    unlocked: false,
    category: 'reconFleet',
  },
];

interface ResourceEvent extends BaseEvent {
  moduleId: string;
  data: {
    resources: {
      current: number;
      [key: string]: unknown;
    };
  };
}

interface ThresholdEvent extends BaseEvent {
  resourceId: string;
  details: {
    type: 'below_minimum' | 'above_maximum';
    current: number;
    min?: number;
    max?: number;
  };
}

// GameInitializer component to handle game initialization
const GameInitializer = ({ children }: { children: React.ReactNode }) => {
  const dispatch = useGameDispatch();
  const [isInitialized, setIsInitialized] = React.useState(false);
  const moduleDispatch = useModuleDispatch();
  const [resourceManagerInstance] = React.useState(() => new ResourceManager());

  useEffect(() => {
    const initializeGame = async () => {
      if (!isInitialized) {
        console.warn('Starting game initialization...');
        try {
          // Initialize resource manager
          console.warn('Initializing resource manager...');
          // ResourceManager is already initialized via useState

          // Register module configurations
          console.warn('Registering module configurations...');
          if (defaultModuleConfigs) {
            Object.values(defaultModuleConfigs).forEach(config => {
              if (config) {
                console.warn(`Registering module config: ${config.type}`);
                moduleManager.registerModuleConfig(config);
              }
            });
          } else {
            console.warn('defaultModuleConfigs is null or undefined');
          }

          // Register default buildings
          console.warn('Registering default buildings...');
          if (defaultMothership) {
            console.warn(`Registering mothership: ${defaultMothership.id}`);
            moduleManager.registerBuilding(defaultMothership);

            // Also register the building with the ModuleContext
            moduleDispatch({
              type: ModuleActionType.ADD_MODULE,
              payload: {
                module: {
                  ...defaultMothership,
                  name: 'Mothership',
                  position: { x: 0, y: 0 },
                  isActive: true,
                  status: ModuleStatus.ACTIVE,
                  type: 'resource-manager' as ModuleType,
                },
              },
            });
          }

          if (defaultColony) {
            console.warn(`Registering colony: ${defaultColony.id}`);
            moduleManager.registerBuilding(defaultColony);

            // Also register the building with the ModuleContext
            moduleDispatch({
              type: ModuleActionType.ADD_MODULE,
              payload: {
                module: {
                  ...defaultColony,
                  name: 'Colony',
                  position: { x: 0, y: 0 },
                  isActive: true,
                  status: ModuleStatus.ACTIVE,
                  type: 'resource-manager' as ModuleType,
                },
              },
            });
          }

          // Initialize asset manager
          console.warn('Initializing asset manager...');
          await assetManager.initialize();

          // Register initial technologies
          console.warn('Registering initial technologies...');
          if (initialTechs) {
            initialTechs.forEach(tech => {
              if (tech) {
                console.warn(`Registering tech: ${tech.id}`);
                techTreeManager.registerNode(tech);
              }
            });
          } else {
            console.warn('initialTechs is null or undefined');
          }

          // Add initial resources
          console.warn('Adding initial resources...');
          dispatch({
            type: GameActionType.UPDATE_RESOURCES,
            payload: {
              minerals: 2000, // Increased initial resources to allow for early module building
              energy: 2000,
              research: 0,
              population: 100,
            },
          });

          // Update systems count
          console.warn('Updating system counts...');
          dispatch({
            type: GameActionType.UPDATE_SYSTEMS,
            payload: {
              total: 1,
              colonized: 1,
              explored: 1,
            },
          });

          // Initialize the officer manager
          console.warn('Initializing officer manager...');
          const officerManager = new OfficerManager();

          // Initialize the ship hangar manager
          console.warn('Initializing ship hangar manager...');
          const shipHangarManager = new ShipHangarManager(resourceManagerInstance, officerManager);

          // Register the ship hangar manager with the global window object for development access
          if (process.env.NODE_ENV === 'development') {
            // Make manager available for debugging
            (
              window as Window & typeof globalThis & { shipHangarManager: ShipHangarManager }
            ).shipHangarManager = shipHangarManager;
          }

          // Initialize event propagation service
          console.warn('Initializing event propagation service...');

          // Register event mappings
          eventPropagationService.subscribe({
            eventType: 'RESOURCE_UPDATED',
            priority: 1,
            callback: (eventData: unknown) => {
              const event = eventData as ResourceEvent;
              const resources = event.data.resources;
              const current = resources.current;

              return {
                resourceId: event.moduleId,
                details: {
                  current,
                  type: 'below_minimum',
                },
                timestamp: Date.now(),
              };
            },
          });

          // Register threshold to module mappings
          eventPropagationService.subscribe({
            eventType: 'THRESHOLD_VIOLATED',
            priority: 1,
            callback: (eventData: unknown) => {
              const event = eventData as ThresholdEvent;
              return {
                moduleId: 'threshold-service',
                moduleType: 'resource' as ModuleType,
                timestamp: Date.now(),
                data: {
                  resourceType: event.resourceId,
                  thresholdType: event.details.type === 'below_minimum' ? 'min' : 'max',
                  current: event.details.current,
                  threshold:
                    event.details.type === 'below_minimum' ? event.details.min : event.details.max,
                },
              };
            },
          });

          // Initialize the service
          eventPropagationService.initialize();

          // Set initialization flag
          console.warn('Game initialization complete!');

          // Log the current state of the module manager
          console.warn('Module manager state:', {
            buildings: moduleManager.getBuildings(),
            modules: moduleManager.getActiveModules(),
            configs: 'Module configurations registered',
          });

          setIsInitialized(true);
        } catch (error) {
          console.error('Error during game initialization:', error);
          errorLoggingService.logError(
            error instanceof Error ? error : new Error(String(error)),
            ErrorType.INITIALIZATION,
            ErrorSeverity.HIGH,
            {
              action: 'initialization',
            }
          );

          // Attempt recovery
          const snapshot = {
            gameState: 'error',
            error: error instanceof Error ? error.message : String(error),
          };
          recoveryService.createSnapshot(snapshot, { reason: 'Error during initialization' });
        }
      }
    };

    initializeGame();
  }, [dispatch, isInitialized, moduleDispatch]);

  if (!isInitialized) {
    return (
      <div className="flex h-screen w-full flex-col items-center justify-center bg-gray-900 text-white">
        <div className="mb-4 h-12 w-12 animate-spin rounded-full border-4 border-blue-500 border-t-transparent"></div>
        <h2 className="mb-2 text-xl font-medium">Initializing Game Systems...</h2>
        <p className="text-gray-400">Preparing galaxy for exploration</p>
      </div>
    );
  }

  return (
    <IntegrationErrorHandler componentName="SystemIntegration">
      <SystemIntegration resourceManager={resourceManagerInstance}>
        <IntegrationErrorHandler componentName="ThresholdIntegration">
          <ThresholdIntegration resourceManager={resourceManagerInstance}>
            {children}
          </ThresholdIntegration>
        </IntegrationErrorHandler>
      </SystemIntegration>
    </IntegrationErrorHandler>
  );
};

// Handler for global errors
const handleGlobalError = (error: Error, errorInfo: React.ErrorInfo) => {
  // Log the error using our error logging service
  errorLoggingService.logError(error, ErrorType.RUNTIME, ErrorSeverity.HIGH, {
    componentName: 'GlobalErrorBoundary',
    errorInfo,
  });

  // Log to console for development purposes
  console.error('Global error caught:', error, errorInfo);
};

// A wrapper for the GameLayout component to provide the required props
const GameLayoutWrapper = () => {
  return (
    <GameLayout empireName="Stellar Dominion" bannerColor="#4FD1C5">
      <div className="min-h-screen bg-gray-900">
        <ResourceVisualization />
      </div>
    </GameLayout>
  );
};

export default function App() {
  // Enable app-level profiling
  const profiler = useComponentProfiler('App', {
    enabled: true,
    logToConsole: false,
    trackPropChanges: true,
  });

  // Show profiling overlay in development
  useProfilingOverlay({
    enabledByDefault: process.env.NODE_ENV === 'development',
    enableInProduction: false,
    toggleKey: 'p',
    persistState: true,
  });

  // Make the ResourceManager accessible in development
  const [resourceManager] = React.useState(() => new ResourceManager());

  if (process.env.NODE_ENV === 'development') {
    // Make ResourceManager available for debugging
    (window as Window & typeof globalThis & { resourceManager: ResourceManager }).resourceManager =
      resourceManager;
  }

  // Development mode debug tools
  const showDebugTools = process.env.NODE_ENV === 'development';

  return (
    <div className="app-container">
      <GlobalErrorBoundary onError={handleGlobalError}>
        <GameProvider>
          <ModuleProvider>
            <ResourceRatesProvider>
              <ThresholdProvider>
                <TooltipProvider>
                  <GameInitializer>
                    <Suspense fallback={<LoadingComponent />}>
                      <GameLayoutWrapper />
                      {showDebugTools && <GameStateMonitor expanded={false} />}
                    </Suspense>
                  </GameInitializer>
                </TooltipProvider>
              </ThresholdProvider>
            </ResourceRatesProvider>
          </ModuleProvider>
        </GameProvider>
      </GlobalErrorBoundary>
    </div>
  );
}

import {
  AlertTriangle,
  Filter,
  Flag,
  History,
  Map,
  Radar,
  Rocket,
  Search,
  Target,
  ZoomIn,
  ZoomOut,
} from 'lucide-react';
import React, { memo, useCallback, useEffect, useMemo, useRef, useState } from 'react';
import { ContextMenuItem, useContextMenu } from '../../../../components/ui/ContextMenu';
import { Draggable, DragItem, DropTarget } from '../../../../components/ui/DragAndDrop';
import { useTooltipContext } from '../../../../components/ui/tooltip-context';
import { explorationRules } from '../../../../config/automation/explorationRules';
import {
  ReconShipManagerImpl,
  ShipEvent,
} from '../../../../managers/exploration/ReconShipManagerImpl';
import { automationManager } from '../../../../managers/game/AutomationManager';
import { Position } from '../../../../types/core/GameTypes';
import { ResourceTransfer } from '../MiningHub/ResourceTransfer';
import { ExplorationControls } from './ExplorationControls';
import { ExplorationTutorial } from './ExplorationTutorial';
import { MissionLog } from './MissionLog';
import { ReconShipStatus } from './ReconShipStatus';

interface Sector {
  id: string;
  name: string;
  status: 'unmapped' | 'mapped' | 'scanning';
  coordinates: { x: number; y: number };
  resourcePotential: number;
  habitabilityScore: number;
  anomalies: Anomaly[];
  lastScanned?: number;
  resources?: Array<{
    type: string;
    amount: number;
  }>;
}

interface Anomaly {
  id: string;
  type: 'artifact' | 'signal' | 'phenomenon';
  severity: 'low' | 'medium' | 'high';
  description: string;
  investigated: boolean;
}

interface ReconShip {
  id: string;
  name: string;
  status: 'idle' | 'scanning' | 'investigating' | 'returning';
  targetSector?: string;
  experience: number;
  specialization: 'mapping' | 'anomaly' | 'resource';
  efficiency: number;
  lastUpdate?: number;
}

interface MapOffset {
  x: number;
  y: number;
}

interface AdvancedFilters {
  minResourcePotential: number;
  minHabitabilityScore: number;
  hasAnomalies: boolean;
  anomalySeverity: 'any' | 'low' | 'medium' | 'high';
  lastScannedWithin: number; // hours
  resourceTypes: string[];
}

// Mock data for demonstration
const mockSectors: Sector[] = [
  {
    id: 'alpha-sector',
    name: 'Alpha Sector',
    status: 'mapped',
    coordinates: { x: 0, y: 0 },
    resourcePotential: 0.8,
    habitabilityScore: 0.6,
    anomalies: [
      {
        id: 'ancient-ruins',
        type: 'artifact',
        severity: 'high',
        description: 'Ancient ruins of unknown origin',
        investigated: false,
      },
    ],
    lastScanned: Date.now() - 3600000,
  },
  {
    id: 'beta-sector',
    name: 'Beta Sector',
    status: 'scanning',
    coordinates: { x: 200, y: -150 },
    resourcePotential: 0.5,
    habitabilityScore: 0.3,
    anomalies: [],
    lastScanned: Date.now(),
  },
  {
    id: 'gamma-sector',
    name: 'Gamma Sector',
    status: 'unmapped',
    coordinates: { x: -180, y: 120 },
    resourcePotential: 0.4,
    habitabilityScore: 0.7,
    anomalies: [],
  },
];

const mockShips: ReconShip[] = [
  {
    id: 'recon-1',
    name: 'Pathfinder Alpha',
    status: 'scanning',
    targetSector: 'beta-sector',
    experience: 1250,
    specialization: 'mapping',
    efficiency: 0.9,
  },
  {
    id: 'recon-2',
    name: 'Signal Hunter Beta',
    status: 'investigating',
    targetSector: 'alpha-sector',
    experience: 800,
    specialization: 'anomaly',
    efficiency: 0.85,
  },
];

// Mock transfer data for exploration discoveries
const mockExplorationTransfers = [
  {
    id: 'discovery-1',
    sourceId: 'alpha-sector',
    targetId: 'storage',
    resourceType: 'Dark Matter',
    amount: 100,
    progress: 0.5,
  },
];

type FilterType = 'all' | 'unmapped' | 'anomalies';

// Memoized Sector Component
const SectorComponent = memo(
  ({
    sector,
    isSelected,
    showHeatMap,
    onSelect,
    onHover,
    getSectorHeat,
    ships,
    onShipAssign,
  }: {
    sector: Sector;
    isSelected: boolean;
    showHeatMap: boolean;
    onSelect: (sector: Sector) => void;
    onHover: (show: boolean, sector: Sector) => void;
    getSectorHeat: (sector: Sector) => number;
    ships: ReconShip[];
    onShipAssign: (shipId: string, sectorId: string) => void;
  }) => {
    const scanningShip = ships.find(ship => ship.targetSector === sector.id);
    const heatValue = getSectorHeat(sector);

    // Context menu for sector actions
    const getSectorMenuItems = (): ContextMenuItem[] => {
      const assignedShip = ships.find(ship => ship.targetSector === sector.id);
      return [
        {
          id: 'info',
          label: 'View Details',
          icon: <Map className="h-4 w-4" />,
          action: () => onSelect(sector),
        },
        {
          id: 'assign-ship',
          label: assignedShip ? 'Reassign Ship' : 'Assign Ship',
          icon: <Rocket className="h-4 w-4" />,
          action: () => {}, // No-op action for parent menu
          children: ships
            .filter(ship => ship.status === 'idle' || ship.targetSector === sector.id)
            .map(ship => ({
              id: ship.id,
              label: ship.name,
              icon: <Target className="h-4 w-4" />,
              action: () => onShipAssign(ship.id, sector.id),
            })),
        },
        {
          id: 'mark-priority',
          label: 'Mark as Priority',
          icon: <Flag className="h-4 w-4" />,
          action: () => {
            // Handle priority marking
            console.warn(`Marking ${sector.name} as priority`);
          },
        },
      ];
    };

    const { handleContextMenu, ContextMenuComponent } = useContextMenu({
      items: getSectorMenuItems(),
    });

    return (
      <div
        className="absolute"
        style={{
          left: `calc(50% + ${sector.coordinates.x}px)`,
          top: `calc(50% + ${sector.coordinates.y}px)`,
          transform: 'translate(-50%, -50%)',
        }}
      >
        <DropTarget
          accept={['ship']}
          onDrop={(item: DragItem) => {
            if (item.type === 'ship' && typeof item.data.id === 'string') {
              const shipId = item.data.id;
              onShipAssign(shipId, sector.id);
            }
          }}
          className="group relative"
        >
          <div
            onContextMenu={handleContextMenu}
            onClick={() => onSelect(sector)}
            onMouseEnter={() => onHover(true, sector)}
            onMouseLeave={() => onHover(false, sector)}
          >
            {/* Sector Visualization */}
            <div
              className={`h-24 w-24 rounded-lg transition-all duration-300 ${
                sector.status === 'unmapped'
                  ? 'bg-gray-800/50'
                  : sector.status === 'scanning'
                    ? 'animate-pulse bg-teal-900/50'
                    : 'bg-teal-800/30'
              } relative ${
                isSelected ? 'ring-2 ring-teal-400 ring-offset-2 ring-offset-gray-900' : ''
              }`}
            >
              {/* Heat Map Overlay */}
              {showHeatMap && sector.status !== 'unmapped' && (
                <div
                  className="absolute inset-0 rounded-lg mix-blend-overlay"
                  style={{
                    background: `rgba(${Math.round(heatValue * 255)}, ${Math.round(heatValue * 100)}, 0, ${heatValue * 0.8})`,
                  }}
                />
              )}

              {/* Resource Potential Indicator */}
              {sector.status !== 'unmapped' && (
                <div
                  className="absolute inset-2 rounded border-2 border-teal-500/30 transition-all"
                  style={{
                    clipPath: `polygon(0 ${100 - sector.resourcePotential * 100}%, 100% ${100 - sector.resourcePotential * 100}%, 0%, 0 100%)`,
                  }}
                />
              )}

              {/* Habitability Score Ring */}
              {sector.status !== 'unmapped' && (
                <div
                  className="absolute inset-0 rounded-lg border-4 border-teal-400/20 transition-all"
                  style={{
                    clipPath: `polygon(0 0, ${sector.habitabilityScore * 100}% 0, ${sector.habitabilityScore * 100}% 100%, 0 100%)`,
                  }}
                />
              )}

              {/* Anomaly Indicators */}
              {sector.anomalies.length > 0 && (
                <div className="mb-3">
                  <div className="mb-2 text-xs font-medium text-gray-300">Detected Anomalies</div>
                  <div className="space-y-1">
                    {sector.anomalies.map(anomaly => (
                      <div
                        key={anomaly.id}
                        className={`rounded px-2 py-1 text-xs ${
                          anomaly.severity === 'high'
                            ? 'bg-red-900/50 text-red-400'
                            : anomaly.severity === 'medium'
                              ? 'bg-yellow-900/50 text-yellow-400'
                              : 'bg-blue-900/50 text-blue-400'
                        }`}
                      >
                        {anomaly.type.charAt(0).toUpperCase() + anomaly.type.slice(1)}
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Scanning Ship Indicator */}
              {scanningShip && (
                <div className="absolute -right-2 -top-2">
                  <Rocket className="h-5 w-5 animate-pulse text-teal-400" />
                </div>
              )}
            </div>

            {/* Sector Label */}
            <div className="absolute left-1/2 top-full mt-2 -translate-x-1/2 text-center">
              <div className="font-medium text-teal-200">{sector.name}</div>
              {sector.status !== 'unmapped' && (
                <div className="text-sm text-teal-300/70">
                  {sector.status === 'scanning' ? 'Scanning in Progress' : 'Mapped'}
                </div>
              )}
            </div>
          </div>
        </DropTarget>
        {ContextMenuComponent}
      </div>
    );
  },
  (prevProps, nextProps) => {
    // Custom comparison function for memo
    return (
      prevProps.sector === nextProps.sector &&
      prevProps.isSelected === nextProps.isSelected &&
      prevProps.showHeatMap === nextProps.showHeatMap &&
      prevProps.ships.length === nextProps.ships.length
    );
  }
);

// Ship Marker Component with drag-and-drop
const ShipMarker = memo(({ ship, targetSector }: { ship: ReconShip; targetSector: Sector }) => {
  return (
    <div
      className="absolute transition-all duration-300"
      style={{
        left: `calc(50% + ${targetSector.coordinates.x}px)`,
        top: `calc(50% + ${targetSector.coordinates.y}px)`,
        transform: 'translate(-50%, -50%)',
      }}
    >
      <Draggable
        item={{
          id: ship.id,
          type: 'ship',
          data: ship,
        }}
      >
        <div className="rounded-lg border border-teal-500/30 bg-teal-900/80 p-2 backdrop-blur-sm">
          <div className="flex items-center space-x-2">
            <Rocket className="h-4 w-4 text-teal-400" />
            <span className="text-xs font-medium text-teal-200">{ship.name}</span>
          </div>
          <div className="mt-1 text-xs text-teal-400/70">
            {ship.status.charAt(0).toUpperCase() + ship.status.slice(1)}
          </div>
        </div>
      </Draggable>
    </div>
  );
});

// Define the ExplorationTask interface to match the one in ReconShipManagerImpl.ts
interface ExplorationTask {
  id: string;
  type: 'explore' | 'investigate' | 'evade';
  target: {
    id: string;
    position: Position;
  };
  priority: number;
  assignedAt: number;
  specialization: 'mapping' | 'anomaly' | 'resource';
  status: 'queued' | 'in-progress' | 'completed' | 'failed';
  progress?: number;
  threatLevel?: number;
}

export function ExplorationHub() {
  const [selectedSector, setSelectedSector] = useState<Sector | null>(null);
  const [showTutorial, setShowTutorial] = useState(true);
  const [showMissionLog, setShowMissionLog] = useState(false);
  const [filter, setFilter] = useState<FilterType>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [zoom, setZoom] = useState(1);
  const [position, setPosition] = useState<{ x: number; y: number } | null>(null);
  const [showHeatMap, setShowHeatMap] = useState(false);
  const [showFilters, setShowFilters] = useState(false);
  const [advancedFilters, setAdvancedFilters] = useState<AdvancedFilters>({
    minResourcePotential: 0,
    minHabitabilityScore: 0,
    hasAnomalies: false,
    anomalySeverity: 'any',
    lastScannedWithin: 24,
    resourceTypes: [],
  });
  const [mapOffset, setMapOffset] = useState<MapOffset>({ x: 0, y: 0 });

  const isDragging = useRef(false);
  const lastPosition = useRef({ x: 0, y: 0 });
  const { showTooltip, hideTooltip } = useTooltipContext();

  // Add new state for real-time updates
  const [sectors, setSectors] = useState(mockSectors);
  const [ships, setShips] = useState(mockShips);
  const [transfers, setTransfers] = useState(mockExplorationTransfers);

  // Optimize update intervals with useRef
  const updateIntervals = useRef({
    ships: null as NodeJS.Timeout | null,
    sectors: null as NodeJS.Timeout | null,
    transfers: null as NodeJS.Timeout | null,
  });

  // Memoize complex calculations
  const activeShips = useMemo(() => {
    return ships.filter(ship => ship.status !== 'idle');
  }, [ships]);

  // Initialize ReconShipManager
  const reconManager = useMemo(() => new ReconShipManagerImpl(), []);

  // Register automation rules on mount
  useEffect(() => {
    // Register each exploration rule
    explorationRules.forEach(rule => {
      automationManager.registerRule(rule);
    });

    // Cleanup on unmount
    return () => {
      explorationRules.forEach(rule => {
        automationManager.removeRule(rule.id);
      });
    };
  }, []);

  // Optimize real-time updates with separate intervals
  useEffect(() => {
    // Ship position updates (more frequent)
    updateIntervals.current.ships = setInterval(() => {
      setShips(prevShips =>
        prevShips.map(ship => {
          if (ship.status === 'idle' || !ship.targetSector) return ship;

          const targetSector = sectors.find(s => s.id === ship.targetSector);
          if (!targetSector) return ship;

          // Calculate progress based on efficiency and time
          const progress = Math.min(
            1,
            (Date.now() - (ship.lastUpdate || Date.now())) / (10000 / ship.efficiency)
          );

          // Update ship status based on progress
          if (progress >= 1) {
            return {
              ...ship,
              status: ship.status === 'scanning' ? 'investigating' : 'returning',
              lastUpdate: Date.now(),
            };
          }

          return {
            ...ship,
            lastUpdate: Date.now(),
          };
        })
      );
    }, 1000);

    // Sector updates (less frequent)
    updateIntervals.current.sectors = setInterval(() => {
      setSectors(prevSectors =>
        prevSectors.map(sector => {
          const scanningShip = ships.find(
            ship => ship.targetSector === sector.id && ship.status === 'scanning'
          );

          if (scanningShip) {
            // Update sector data based on ship's scan
            return {
              ...sector,
              status: 'scanning',
              lastScanned: Date.now(),
            };
          }

          if (sector.status === 'scanning' && !scanningShip) {
            // Complete the scan
            return {
              ...sector,
              status: 'mapped',
              lastScanned: Date.now(),
            };
          }

          return sector;
        })
      );
    }, 2000);

    // Transfer updates (least frequent)
    updateIntervals.current.transfers = setInterval(() => {
      setTransfers(prevTransfers =>
        prevTransfers.map(transfer => {
          if (transfer.progress >= 1) return transfer;

          return {
            ...transfer,
            progress: Math.min(1, transfer.progress + 0.1),
          };
        })
      );
    }, 3000);

    // Store current intervals for cleanup
    const currentIntervals = { ...updateIntervals.current };

    return () => {
      Object.values(currentIntervals).forEach(interval => {
        if (interval) clearInterval(interval);
      });
    };
  }, [sectors, ships]);

  // Enhanced sector filtering
  const filteredSectors = useMemo(() => {
    return sectors.filter(sector => {
      // Basic text search
      if (searchQuery && !sector.name.toLowerCase().includes(searchQuery.toLowerCase())) {
        return false;
      }

      // Basic type filter
      if (filter === 'unmapped' && sector.status !== 'unmapped') {
        return false;
      }
      if (filter === 'anomalies' && sector.anomalies.length === 0) {
        return false;
      }

      // Advanced filters
      if (sector.resourcePotential < advancedFilters.minResourcePotential) {
        return false;
      }
      if (sector.habitabilityScore < advancedFilters.minHabitabilityScore) {
        return false;
      }
      if (advancedFilters.hasAnomalies && sector.anomalies.length === 0) {
        return false;
      }
      if (advancedFilters.anomalySeverity !== 'any') {
        const hasMatchingSeverity = sector.anomalies.some(
          a => a.severity === advancedFilters.anomalySeverity
        );
        if (!hasMatchingSeverity) {
          return false;
        }
      }
      if (advancedFilters.lastScannedWithin > 0 && sector.lastScanned) {
        const hoursSinceLastScan = (Date.now() - sector.lastScanned) / (1000 * 60 * 60);
        if (hoursSinceLastScan > advancedFilters.lastScannedWithin) {
          return false;
        }
      }
      if (
        advancedFilters.resourceTypes.length > 0 &&
        !sector.resources?.some(r => advancedFilters.resourceTypes.includes(r.type))
      ) {
        return false;
      }

      return true;
    });
  }, [sectors, searchQuery, filter, advancedFilters]);

  // Enhanced heat map calculation
  const getSectorHeat = useCallback((sector: Sector) => {
    let heatValue = 0;

    // Base heat from resource potential
    heatValue += sector.resourcePotential * 0.4;

    // Heat from habitability
    heatValue += sector.habitabilityScore * 0.3;

    // Heat from anomalies
    const anomalyHeat = sector.anomalies.reduce((sum, anomaly) => {
      const severityValue =
        anomaly.severity === 'high' ? 0.3 : anomaly.severity === 'medium' ? 0.2 : 0.1;
      return sum + severityValue;
    }, 0);
    heatValue += anomalyHeat;

    // Reduce heat for older scans
    if (sector.lastScanned) {
      const hoursSinceLastScan = (Date.now() - sector.lastScanned) / (1000 * 60 * 60);
      const ageFactor = Math.max(0, 1 - hoursSinceLastScan / 168); // 168 hours = 1 week
      heatValue *= ageFactor;
    }

    return Math.min(1, heatValue);
  }, []);

  // Memoize handlers
  const handleSectorSelect = useCallback((sector: Sector) => {
    setSelectedSector(sector);
  }, []);

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    isDragging.current = true;
    lastPosition.current = { x: e.clientX, y: e.clientY };
    const startX = e.clientX;
    const startY = e.clientY;
    setPosition({ x: startX, y: startY });
  }, []);

  const handleMouseMove = useCallback(
    (e: React.MouseEvent) => {
      // Implementation for map panning
      if (position && isDragging.current) {
        const dx = e.clientX - position.x;
        const dy = e.clientY - position.y;
        setPosition({ x: e.clientX, y: e.clientY });
        // Update map position based on dx and dy
        setMapOffset((prev: MapOffset) => ({
          x: prev.x + dx,
          y: prev.y + dy,
        }));
      }
    },
    [position]
  );

  const handleMouseUp = useCallback(() => {
    isDragging.current = false;
    setPosition(null);
  }, []);

  const handleZoom = useCallback((delta: number) => {
    setZoom(prev => Math.max(0.5, Math.min(2, prev + delta)));
  }, []);

  // Enhanced sector hover tooltip
  const handleSectorHover = useCallback(
    (show: boolean, sector: Sector) => {
      if (show) {
        showTooltip(
          <div className="max-w-xs rounded-lg border border-gray-700 bg-gray-800/95 p-4 shadow-xl">
            <div className="mb-3 flex items-center justify-between">
              <div className="font-medium text-white">{sector.name}</div>
              <div
                className={`rounded px-2 py-0.5 text-xs ${
                  sector.status === 'unmapped'
                    ? 'bg-gray-700 text-gray-400'
                    : sector.status === 'scanning'
                      ? 'bg-teal-900/50 text-teal-400'
                      : 'bg-teal-800/30 text-teal-300'
                }`}
              >
                {sector.status.charAt(0).toUpperCase() + sector.status.slice(1)}
              </div>
            </div>

            {sector.status !== 'unmapped' && (
              <>
                {/* Resource and Habitability Bars */}
                <div className="mb-3 space-y-2">
                  <div>
                    <div className="mb-1 flex justify-between text-xs">
                      <span className="text-gray-400">Resources</span>
                      <span className="text-teal-400">
                        {Math.round(sector.resourcePotential * 100)}%
                      </span>
                    </div>
                    <div className="h-1.5 overflow-hidden rounded-full bg-gray-700">
                      <div
                        className="h-full rounded-full bg-teal-500"
                        style={{ width: `${sector.resourcePotential * 100}%` }}
                      />
                    </div>
                  </div>

                  <div>
                    <div className="mb-1 flex justify-between text-xs">
                      <span className="text-gray-400">Habitability</span>
                      <span className="text-teal-400">
                        {Math.round(sector.habitabilityScore * 100)}%
                      </span>
                    </div>
                    <div className="h-1.5 overflow-hidden rounded-full bg-gray-700">
                      <div
                        className="h-full rounded-full bg-teal-500"
                        style={{ width: `${sector.habitabilityScore * 100}%` }}
                      />
                    </div>
                  </div>
                </div>

                {/* Anomalies */}
                {sector.anomalies.length > 0 && (
                  <div className="mb-3">
                    <div className="mb-2 text-xs font-medium text-gray-300">Detected Anomalies</div>
                    <div className="space-y-1">
                      {sector.anomalies.map(anomaly => (
                        <div
                          key={anomaly.id}
                          className={`rounded px-2 py-1 text-xs ${
                            anomaly.severity === 'high'
                              ? 'bg-red-900/50 text-red-400'
                              : anomaly.severity === 'medium'
                                ? 'bg-yellow-900/50 text-yellow-400'
                                : 'bg-blue-900/50 text-blue-400'
                          }`}
                        >
                          {anomaly.type.charAt(0).toUpperCase() + anomaly.type.slice(1)}
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Last Scanned */}
                {sector.lastScanned && (
                  <div className="text-xs text-gray-500">
                    Last Scanned: {new Date(sector.lastScanned).toLocaleString()}
                  </div>
                )}
              </>
            )}
          </div>
        );
      } else {
        hideTooltip();
      }
    },
    [showTooltip, hideTooltip]
  );

  // Handle ship assignment
  const handleShipAssign = useCallback(
    (shipId: string, sectorId: string) => {
      const sector = sectors.find(s => s.id === sectorId);
      if (!sector) return;

      reconManager.assignExplorationTask(
        shipId,
        sectorId,
        sector.coordinates,
        'mapping' // Default to mapping, can be updated based on sector type
      );

      setShips(prevShips =>
        prevShips.map(ship =>
          ship.id === shipId
            ? {
                ...ship,
                status: 'scanning',
                targetSector: sectorId,
                lastUpdate: Date.now(),
              }
            : ship
        )
      );
    },
    [sectors, reconManager]
  );

  // Register ships with ReconShipManager
  useEffect(() => {
    ships.forEach(ship => {
      reconManager.registerShip({
        ...ship,
        type: 'AC27G',
        position: { x: 0, y: 0 }, // Initial position
        capabilities: {
          canScan: true,
          canSalvage: false,
          canMine: false,
          canJump: true,
        },
        stealth: {
          active: false,
          level: 1,
          cooldown: 0,
        },
        sensors: {
          range: 100,
          accuracy: 0.8,
          anomalyDetection: 0.7,
        },
        discoveries: {
          mappedSectors: 0,
          anomaliesFound: 0,
          resourcesLocated: 0,
        },
      });
    });

    // Cleanup on unmount
    return () => {
      ships.forEach(ship => reconManager.unregisterShip(ship.id));
    };
  }, [ships, reconManager]);

  // Listen for ReconShipManager events
  useEffect(() => {
    const handleTaskCompleted = ({ shipId, task }: ShipEvent) => {
      if (!task) return; // Skip if no task is provided

      setShips(prevShips =>
        prevShips.map(ship =>
          ship.id === shipId
            ? {
                ...ship,
                status: 'returning',
                experience: ship.experience + 100, // Base XP gain
              }
            : ship
        )
      );

      // Update sector status
      setSectors(prevSectors =>
        prevSectors.map(sector =>
          sector.id === task.target.id
            ? {
                ...sector,
                status: 'mapped',
                lastScanned: Date.now(),
              }
            : sector
        )
      );
    };

    reconManager.on('taskCompleted', handleTaskCompleted);
    return () => {
      reconManager.off('taskCompleted', handleTaskCompleted);
    };
  }, [reconManager]);

  // Update ReconShipManager
  useEffect(() => {
    const interval = setInterval(() => {
      reconManager.update(1000 / 60); // Update at 60fps
    }, 1000 / 60);

    return () => clearInterval(interval);
  }, [reconManager]);

  return (
    <div className="fixed inset-4 flex overflow-hidden rounded-lg border border-gray-700 bg-gray-900/95 shadow-2xl backdrop-blur-md">
      {/* Left Panel - Exploration Map */}
      <div className="flex w-2/3 flex-col border-r border-gray-700 p-6">
        <div className="mb-6 flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Radar className="h-6 w-6 text-teal-400" />
            <h2 className="text-xl font-bold text-white">Exploration Hub</h2>
          </div>

          <div className="flex items-center space-x-4">
            <div className="relative">
              <input
                type="text"
                placeholder="Search sectors..."
                className="w-64 rounded-lg border border-gray-700 bg-gray-800/90 px-4 py-2 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-teal-500"
                value={searchQuery}
                onChange={e => setSearchQuery(e.target.value)}
              />
              <Search className="absolute right-3 top-2.5 h-5 w-5 text-gray-400" />
            </div>

            <div className="flex space-x-2">
              <button
                onClick={() => handleZoom(0.1)}
                className="rounded-lg bg-gray-800/90 p-2 backdrop-blur-sm transition-colors hover:bg-gray-700/90"
              >
                <ZoomIn className="h-5 w-5 text-teal-400" />
              </button>
              <button
                onClick={() => handleZoom(-0.1)}
                className="rounded-lg bg-gray-800/90 p-2 backdrop-blur-sm transition-colors hover:bg-gray-700/90"
              >
                <ZoomOut className="h-5 w-5 text-teal-400" />
              </button>
            </div>

            <button
              onClick={() => setShowMissionLog(true)}
              className="rounded-lg bg-gray-800/90 p-2 backdrop-blur-sm transition-colors hover:bg-gray-700/90"
            >
              <History className="h-5 w-5 text-teal-400" />
            </button>
          </div>
        </div>

        {/* Enhanced Filter Controls */}
        <div className="mb-6">
          <div className="mb-2 flex items-center justify-between">
            <div className="flex space-x-2">
              <button
                onClick={() => setFilter('all')}
                className={`flex items-center space-x-2 rounded-lg px-3 py-2 ${
                  filter === 'all'
                    ? 'bg-teal-600 text-white'
                    : 'bg-gray-800 text-gray-400 hover:bg-gray-700'
                }`}
              >
                <Map className="h-4 w-4" />
                <span>All Sectors</span>
              </button>
              <button
                onClick={() => setFilter('unmapped')}
                className={`flex items-center space-x-2 rounded-lg px-3 py-2 ${
                  filter === 'unmapped'
                    ? 'bg-teal-600 text-white'
                    : 'bg-gray-800 text-gray-400 hover:bg-gray-700'
                }`}
              >
                <Radar className="h-4 w-4" />
                <span>Unmapped</span>
              </button>
              <button
                onClick={() => setFilter('anomalies')}
                className={`flex items-center space-x-2 rounded-lg px-3 py-2 ${
                  filter === 'anomalies'
                    ? 'bg-teal-600 text-white'
                    : 'bg-gray-800 text-gray-400 hover:bg-gray-700'
                }`}
              >
                <AlertTriangle className="h-4 w-4" />
                <span>Anomalies</span>
              </button>
              <button
                onClick={() => setShowHeatMap(!showHeatMap)}
                className={`flex items-center space-x-2 rounded-lg px-3 py-2 ${
                  showHeatMap
                    ? 'bg-teal-600 text-white'
                    : 'bg-gray-800 text-gray-400 hover:bg-gray-700'
                }`}
              >
                <Map className="h-4 w-4" />
                <span>Heat Map</span>
              </button>
            </div>

            <button
              onClick={() => setShowFilters(!showFilters)}
              className={`flex items-center space-x-2 rounded-lg px-3 py-2 ${
                Object.values(advancedFilters).some(v =>
                  Array.isArray(v) ? v.length > 0 : v !== 0 && v !== false && v !== 'any'
                )
                  ? 'bg-teal-600 text-white'
                  : 'bg-gray-800 text-gray-400 hover:bg-gray-700'
              }`}
            >
              <Filter className="h-4 w-4" />
              <span>Advanced Filters</span>
            </button>
          </div>

          {/* Advanced Filters Panel */}
          {showFilters && (
            <div className="mt-4 rounded-lg bg-gray-800/50 p-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="mb-2 block text-sm text-gray-400">Min Resource Potential</label>
                  <input
                    type="range"
                    min="0"
                    max="1"
                    step="0.1"
                    value={advancedFilters.minResourcePotential}
                    onChange={e =>
                      setAdvancedFilters(prev => ({
                        ...prev,
                        minResourcePotential: parseFloat(e.target.value),
                      }))
                    }
                    className="w-full"
                  />
                  <div className="mt-1 text-sm text-teal-400">
                    {Math.round(advancedFilters.minResourcePotential * 100)}%
                  </div>
                </div>
                <div>
                  <label className="mb-2 block text-sm text-gray-400">Min Habitability Score</label>
                  <input
                    type="range"
                    min="0"
                    max="1"
                    step="0.1"
                    value={advancedFilters.minHabitabilityScore}
                    onChange={e =>
                      setAdvancedFilters(prev => ({
                        ...prev,
                        minHabitabilityScore: parseFloat(e.target.value),
                      }))
                    }
                    className="w-full"
                  />
                  <div className="mt-1 text-sm text-teal-400">
                    {Math.round(advancedFilters.minHabitabilityScore * 100)}%
                  </div>
                </div>
              </div>

              <div className="mt-4 grid grid-cols-2 gap-4">
                <div>
                  <label className="mb-2 block text-sm text-gray-400">Anomaly Settings</label>
                  <div className="space-y-2">
                    <label className="flex items-center">
                      <input
                        type="checkbox"
                        checked={advancedFilters.hasAnomalies}
                        onChange={e =>
                          setAdvancedFilters(prev => ({
                            ...prev,
                            hasAnomalies: e.target.checked,
                          }))
                        }
                        className="mr-2"
                      />
                      <span className="text-sm text-gray-300">Has Anomalies</span>
                    </label>
                    <select
                      value={advancedFilters.anomalySeverity}
                      onChange={e =>
                        setAdvancedFilters(prev => ({
                          ...prev,
                          anomalySeverity: e.target.value as 'any' | 'low' | 'medium' | 'high',
                        }))
                      }
                      className="w-full rounded bg-gray-700 px-2 py-1 text-sm text-white"
                    >
                      <option value="any">Any Severity</option>
                      <option value="low">Low</option>
                      <option value="medium">Medium</option>
                      <option value="high">High</option>
                    </select>
                  </div>
                </div>
                <div>
                  <label className="mb-2 block text-sm text-gray-400">Last Scanned Within</label>
                  <select
                    value={advancedFilters.lastScannedWithin}
                    onChange={e =>
                      setAdvancedFilters(prev => ({
                        ...prev,
                        lastScannedWithin: parseInt(e.target.value),
                      }))
                    }
                    className="w-full rounded bg-gray-700 px-2 py-1 text-sm text-white"
                  >
                    <option value={0}>Any Time</option>
                    <option value={24}>24 Hours</option>
                    <option value={72}>3 Days</option>
                    <option value={168}>1 Week</option>
                  </select>
                </div>
              </div>

              <div className="mt-4">
                <label className="mb-2 block text-sm text-gray-400">Resource Types</label>
                <div className="flex flex-wrap gap-2">
                  {['Dark Matter', 'Helium-3', 'Rare Metals', 'Common Ores'].map(type => (
                    <button
                      key={type}
                      onClick={() =>
                        setAdvancedFilters(prev => ({
                          ...prev,
                          resourceTypes: prev.resourceTypes.includes(type)
                            ? prev.resourceTypes.filter(t => t !== type)
                            : [...prev.resourceTypes, type],
                        }))
                      }
                      className={`rounded px-2 py-1 text-sm ${
                        advancedFilters.resourceTypes.includes(type)
                          ? 'bg-teal-600 text-white'
                          : 'bg-gray-700 text-gray-400'
                      }`}
                    >
                      {type}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Map Content */}
        <div
          className="relative flex-1 overflow-hidden"
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
        >
          <div
            className="absolute inset-0"
            style={{
              transform: `translate(${mapOffset.x}px, ${mapOffset.y}px) scale(${zoom})`,
              transformOrigin: 'center',
              transition: isDragging.current ? 'none' : 'transform 0.3s ease-out',
            }}
          >
            {filteredSectors.map(sector => (
              <SectorComponent
                key={sector.id}
                sector={sector}
                isSelected={selectedSector?.id === sector.id}
                showHeatMap={showHeatMap}
                onSelect={handleSectorSelect}
                onHover={handleSectorHover}
                getSectorHeat={getSectorHeat}
                ships={ships}
                onShipAssign={handleShipAssign}
              />
            ))}

            {/* Ship Markers */}
            {activeShips.map(ship => {
              const targetSector = sectors.find(s => s.id === ship.targetSector);
              if (!targetSector) {
                return null;
              }
              return <ShipMarker key={ship.id} ship={ship} targetSector={targetSector} />;
            })}

            {/* Resource Transfers */}
            <ResourceTransfer transfers={transfers} />
          </div>
        </div>
      </div>

      {/* Right Panel */}
      <div className="flex w-1/3 flex-col p-6">
        {selectedSector ? (
          <>
            <ExplorationControls sector={selectedSector} onClose={() => setSelectedSector(null)} />
            <div className="mt-6">
              <ReconShipStatus ships={activeShips} />
            </div>
          </>
        ) : (
          <div className="flex h-full items-center justify-center text-gray-400">
            Select a sector to view details
          </div>
        )}
      </div>

      {/* Modals */}
      {showMissionLog && <MissionLog onClose={() => setShowMissionLog(false)} />}
      {showTutorial && <ExplorationTutorial onClose={() => setShowTutorial(false)} />}
    </div>
  );
}

import { Pause, Play, SkipBack, SkipForward, X } from 'lucide-react';
import { useCallback, useEffect, useState } from 'react';
import { useGameState } from '../../../../contexts/GameContext';
import { GameEvent, GameEventType, Position } from '../../../../types/core/GameTypes';

interface MissionReplayProps {
  missionId: string;
  onClose: () => void;
}

interface EventPosition {
  x: number;
  y: number;
}

interface Mission {
  id: string;
  timestamp: number;
  description: string;
}

interface GameState {
  missions: {
    history: Mission[];
  };
  events: GameEvent[];
}

const selectGameState = (state: unknown): GameState => {
  if (
    typeof state === 'object' &&
    state !== null &&
    'missions' in state &&
    'events' in state &&
    typeof state.missions === 'object' &&
    state.missions !== null &&
    'history' in state.missions &&
    Array.isArray(state.missions.history) &&
    Array.isArray(state.events)
  ) {
    return state as GameState;
  }
  return {
    missions: {
      history: [],
    },
    events: [],
  };
};

export function MissionReplay({ missionId, onClose }: MissionReplayProps) {
  const gameState = useGameState(selectGameState);

  // Ensure state is available
  if (!gameState) {
    return null;
  }

  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [playbackSpeed, setPlaybackSpeed] = useState(1);
  const [currentEventIndex, setCurrentEventIndex] = useState<number | null>(null);

  // Find the mission and related events
  const mission = gameState.missions.history.find(m => m.id === missionId);
  const events = gameState.events.filter(
    e =>
      e.timestamp >= (mission?.timestamp || 0) && e.timestamp <= (mission?.timestamp || 0) + 3600000 // 1 hour window
  );

  // Calculate total duration
  const duration =
    events.length > 0 ? events[events.length - 1].timestamp - events[0].timestamp : 0;

  // Handle playback controls
  const togglePlayback = useCallback(() => {
    setIsPlaying(!isPlaying);
  }, [isPlaying]);

  const handleSeek = useCallback(
    (time: number) => {
      setCurrentTime(Math.max(0, Math.min(time, duration)));
    },
    [duration]
  );

  const handleSpeedChange = useCallback((speed: number) => {
    setPlaybackSpeed(speed);
  }, []);

  // Update time during playback
  useEffect(() => {
    if (!isPlaying) {
      return;
    }

    const interval = setInterval(() => {
      setCurrentTime(time => {
        const newTime = time + 100 * playbackSpeed;
        if (newTime >= duration) {
          setIsPlaying(false);
          return duration;
        }
        return newTime;
      });
    }, 100);

    return () => clearInterval(interval);
  }, [isPlaying, playbackSpeed, duration]);

  if (!mission) {
    return null;
  }

  // Get events up to current time
  const currentEvents = events.filter(
    (e: GameEvent) => e.timestamp <= events[0].timestamp + currentTime
  );

  // Render map with ship paths and events
  const renderEvents = (events: GameEvent[]) => {
    return events.map((event: GameEvent, index: number) => {
      // Calculate position based on event data or use random positioning as fallback
      const eventPosition: EventPosition = {
        x:
          typeof event.data === 'object' &&
          event.data !== null &&
          'position' in event.data &&
          typeof event.data.position === 'object' &&
          event.data.position !== null &&
          'x' in event.data.position
            ? (event.data.position as Position).x
            : Math.random() * 100,
        y:
          typeof event.data === 'object' &&
          event.data !== null &&
          'position' in event.data &&
          typeof event.data.position === 'object' &&
          event.data.position !== null &&
          'y' in event.data.position
            ? (event.data.position as Position).y
            : Math.random() * 100,
      };

      // Determine event marker appearance based on event type
      const getEventColor = (type: GameEventType) => {
        switch (type) {
          case 'exploration':
            return 'bg-teal-400';
          case 'combat':
            return 'bg-red-400';
          case 'trade':
            return 'bg-amber-400';
          case 'diplomacy':
            return 'bg-purple-400';
          default:
            return 'bg-teal-400';
        }
      };

      // Check if this is the current event being viewed
      const isCurrentEvent = index === currentEventIndex;

      return (
        <div
          key={`event-${index}`}
          className="absolute h-2 w-2 rounded-full"
          style={{
            left: `${eventPosition.x}%`,
            top: `${eventPosition.y}%`,
            backgroundColor: getEventColor(event.type),
          }}
          onClick={() => {
            // Set current event index to this event when clicked
            setCurrentEventIndex(index);
          }}
        />
      );
    });
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50">
      <div className="mx-4 w-full max-w-4xl rounded-lg border border-gray-700 bg-gray-900 p-6">
        <div className="mb-6 flex items-center justify-between">
          <h2 className="text-xl font-bold text-white">Mission Replay</h2>
          <button onClick={onClose} className="rounded-lg p-2 transition-colors hover:bg-gray-800">
            <X className="h-5 w-5 text-gray-400" />
          </button>
        </div>

        {/* Mission Details */}
        <div className="mb-6">
          <div className="mb-2 text-lg text-white">{mission.description}</div>
          <div className="text-sm text-gray-400">
            {new Date(mission.timestamp).toLocaleString()}
          </div>
        </div>

        {/* Replay Visualization */}
        <div className="relative mb-6 h-96 overflow-hidden rounded-lg bg-gray-800/50">
          {/* Map View */}
          <div className="absolute inset-0">{renderEvents(currentEvents)}</div>

          {/* Event Timeline */}
          <div className="absolute bottom-0 left-0 right-0 h-24 bg-gradient-to-t from-gray-900/90 to-transparent p-4">
            <div className="mb-4 flex items-center space-x-4">
              {currentEvents.map((event: GameEvent, index: number) => (
                <div
                  key={index}
                  className={`h-2 w-2 rounded-full ${
                    index === currentEventIndex ? 'bg-white' : 'bg-teal-400'
                  }`}
                  style={{
                    left: `${((event.timestamp - events[0].timestamp) / duration) * 100}%`,
                  }}
                  onClick={() => setCurrentEventIndex(index)}
                />
              ))}
            </div>

            {/* Current Event Details */}
            {currentEventIndex !== null && currentEvents[currentEventIndex] && (
              <div className="mb-2 text-sm text-white">
                <span className="font-bold">{currentEvents[currentEventIndex].type}</span> event at{' '}
                {new Date(currentEvents[currentEventIndex].timestamp).toLocaleTimeString()}
              </div>
            )}
          </div>
        </div>

        {/* Playback Controls */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => handleSeek(0)}
              className="rounded-lg p-2 transition-colors hover:bg-gray-800"
            >
              <SkipBack className="h-5 w-5 text-teal-400" />
            </button>
            <button
              onClick={togglePlayback}
              className="rounded-lg p-2 transition-colors hover:bg-gray-800"
            >
              {isPlaying ? (
                <Pause className="h-5 w-5 text-teal-400" />
              ) : (
                <Play className="h-5 w-5 text-teal-400" />
              )}
            </button>
            <button
              onClick={() => handleSeek(duration)}
              className="rounded-lg p-2 transition-colors hover:bg-gray-800"
            >
              <SkipForward className="h-5 w-5 text-teal-400" />
            </button>
          </div>

          {/* Playback Speed */}
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-400">Speed:</span>
            {[0.5, 1, 2, 4].map(speed => (
              <button
                key={speed}
                onClick={() => handleSpeedChange(speed)}
                className={`rounded px-2 py-1 text-sm ${
                  playbackSpeed === speed
                    ? 'bg-teal-600 text-white'
                    : 'bg-gray-800 text-gray-400 hover:bg-gray-700'
                }`}
              >
                {speed}x
              </button>
            ))}
          </div>

          {/* Progress */}
          <div className="text-sm text-gray-400">
            {Math.floor(currentTime / 1000)}s / {Math.floor(duration / 1000)}s
          </div>
        </div>
      </div>
    </div>
  );
}

