import React, { ReactElement } from 'react';
import { ResponsiveContainer, TooltipProps } from 'recharts';
import { DataPoint } from '../../../../types/exploration/DataAnalysisTypes';

/**
 * Common types for chart components
 */

// Type for Recharts TooltipProps
export type ChartTooltipProps = TooltipProps<number | string, string>;

// Define allowed positions for reference line labels
export type ReferenceLinePosition =
  | 'top'
  | 'bottom'
  | 'left'
  | 'right'
  | 'insideTop'
  | 'insideBottom'
  | 'insideLeft'
  | 'insideRight'
  | 'insideTopRight'
  | 'insideTopLeft'
  | 'insideBottomRight'
  | 'insideBottomLeft'
  | 'center';

// Define common reference line type
export interface ReferenceLine {
  value: number;
  label?: string;
  color?: string;
  position?: ReferenceLinePosition;
  axis: 'x' | 'y';
}

/**
 * Base interface for all chart properties
 */
export interface BaseChartProps {
  /**
   * Data to visualize - supports both DataPoint objects and regular records
   */
  data: DataPoint[] | Record<string, unknown>[];

  /**
   * Width of the chart - can be a number (pixels) or string (e.g., '100%')
   */
  width?: number | string;

  /**
   * Height of the chart in pixels
   */
  height?: number | string;

  /**
   * Title displayed above the chart
   */
  title?: string;

  /**
   * Subtitle displayed below the title
   */
  subtitle?: string;

  /**
   * Colors to use for the chart elements
   */
  colors?: string[];

  /**
   * Custom tooltip component to override the default tooltip
   */
  customTooltip?: React.FC<ChartTooltipProps>;

  /**
   * CSS class name for additional styling
   */
  className?: string;

  /**
   * Whether to animate the chart when data changes
   */
  animate?: boolean;

  /**
   * Custom theme for the chart (light or dark)
   */
  theme?: 'light' | 'dark';

  /**
   * Optional click handler for chart elements
   */
  onElementClick?: (data: Record<string, unknown>, index: number) => void;

  /**
   * Whether to show a loading indicator when data is empty
   */
  showLoadingState?: boolean;

  /**
   * Error message to display if there's an error with the chart data
   */
  errorMessage?: string;
}

/**
 * The default color palette for charts
 */
export const defaultColors = [
  '#4361ee', // Blue
  '#3a86ff', // Light blue
  '#4cc9f0', // Cyan
  '#4895ef', // Sky blue
  '#560bad', // Purple
  '#7209b7', // Dark purple
  '#f72585', // Pink
  '#b5179e', // Magenta
  '#3f37c9', // Indigo
  '#4cc9f0', // Teal
  '#4361ee', // Blue
  '#3a0ca3', // Dark blue
  '#7209b7', // Violet
  '#f72585', // Hot pink
  '#4cc9f0', // Sky
];

/**
 * Default custom tooltip that works for most chart types
 */
export const DefaultTooltip: React.FC<ChartTooltipProps> = ({ active, payload, label }) => {
  if (!active || !payload || payload.length === 0) {
    return null;
  }

  // Format the label (often a date)
  const formattedLabel =
    typeof label === 'number' && label > 1000000000 ? new Date(label).toLocaleString() : label;

  return (
    <div className="custom-tooltip rounded border border-gray-200 bg-white p-3 shadow-lg dark:border-gray-700 dark:bg-gray-800">
      <p className="font-semibold text-gray-700 dark:text-gray-200">{formattedLabel}</p>
      <ul className="mt-2">
        {payload.map((entry, index) => (
          <li
            key={`item-${index}`}
            className="flex items-center gap-2 text-sm"
            style={{ color: entry.color }}
          >
            <div className="h-3 w-3 rounded-full" style={{ backgroundColor: entry.color }}></div>
            <span className="capitalize">
              {entry.name}:{' '}
              {typeof entry.value === 'number' ? entry.value.toLocaleString() : String(entry.value)}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
};

/**
 * Base chart component that provides common functionality for all chart types
 */
export function BaseChart({
  width = '100%',
  height = 400,
  title,
  subtitle,
  className = '',
  theme = 'light',
  children,
  errorMessage,
  showLoadingState = true,
}: Omit<BaseChartProps, 'data'> & { children: ReactElement }) {
  // Apply theme-specific classes
  const themeClasses = theme === 'dark' ? 'bg-gray-900 text-gray-100' : 'bg-white text-gray-800';

  // Show error message if provided
  if (errorMessage) {
    return (
      <div className={`chart-container ${themeClasses} rounded-lg p-4 shadow-md ${className}`}>
        {title && <h3 className="chart-title mb-2 text-lg font-semibold">{title}</h3>}
        {subtitle && <p className="chart-subtitle mb-4 text-sm text-gray-500">{subtitle}</p>}
        <div className="flex h-64 items-center justify-center text-red-500">
          <div className="text-center">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="mx-auto mb-2 h-12 w-12 text-red-500"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
              />
            </svg>
            <p>{errorMessage}</p>
          </div>
        </div>
      </div>
    );
  }

  // Show loading state if required and there's no children
  if (showLoadingState && !React.Children.count(children)) {
    return (
      <div className={`chart-container ${themeClasses} rounded-lg p-4 shadow-md ${className}`}>
        {title && <h3 className="chart-title mb-2 text-lg font-semibold">{title}</h3>}
        {subtitle && <p className="chart-subtitle mb-4 text-sm text-gray-500">{subtitle}</p>}
        <div className="flex h-64 items-center justify-center">
          <div className="text-center">
            <div className="mx-auto mb-2 h-12 w-12 animate-spin rounded-full border-b-2 border-t-2 border-blue-500"></div>
            <p className="text-gray-500 dark:text-gray-400">Loading chart data...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`chart-container ${themeClasses} rounded-lg p-4 shadow-md ${className}`}>
      {title && <h3 className="chart-title mb-2 text-lg font-semibold">{title}</h3>}
      {subtitle && <p className="chart-subtitle mb-4 text-sm text-gray-500">{subtitle}</p>}
      <ResponsiveContainer width={width} height={height}>
        {children}
      </ResponsiveContainer>
    </div>
  );
}

/**
 * Process data points for chart visualization
 * Handles both DataPoint objects and record objects
 */
export function processChartData(
  data: DataPoint[] | Record<string, unknown>[],
  xAxisKey: string,
  yAxisKeys: string[] | string,
  dateFormat = false
) {
  if (!data || data.length === 0) return [];

  // Ensure yAxisKeys is an array
  const yKeys = Array.isArray(yAxisKeys) ? yAxisKeys : [yAxisKeys];

  return data.map(item => {
    // Handle DataPoint objects
    if ('properties' in item && 'metadata' in item) {
      const dataPoint = item as DataPoint;
      const properties = { ...dataPoint.properties };
      const metadata = dataPoint.metadata || {};

      // Create a new object with flattened structure for chart
      return {
        ...properties,
        ...metadata,
        // Include the id and date for reference
        id: dataPoint.id,
        date: dataPoint.date,
        // Format date if it's being used as the x-axis
        formattedDate:
          dateFormat && xAxisKey === 'date'
            ? new Date(dataPoint.date).toLocaleDateString()
            : undefined,
      };
    }

    // Handle record objects
    return {
      ...item,
      // Format date if it's a timestamp and dateFormat is true
      formattedDate:
        dateFormat && xAxisKey === 'date' && typeof item.date === 'number'
          ? new Date(item.date as number).toLocaleDateString()
          : undefined,
    };
  });
}

/**
 * Format tick values for the X axis
 */
export function formatAxisTick(value: number | string, dateFormat = false) {
  if (dateFormat && typeof value === 'number') {
    return new Date(value).toLocaleDateString(undefined, { month: 'short', day: 'numeric' });
  }

  if (typeof value === 'number') {
    return value.toLocaleString();
  }

  return String(value);
}

/**
 * Get color from a palette based on index
 */
export function getColor(index: number, colors: string[] = defaultColors) {
  return colors[index % colors.length];
}

/**
 * Format a label by capitalizing and adding spaces before capital letters
 */
export function formatLabel(key: string) {
  return key.charAt(0).toUpperCase() + key.slice(1).replace(/([A-Z])/g, ' $1');
}

import React, { useCallback, useMemo } from 'react';
import {
  BaseVisualizationProps,
  ChartDataRecord,
} from '../../../../types/exploration/AnalysisComponentTypes';
import { BaseChart, ChartTooltipProps } from './BaseChart';

// Cell data structure for the heat map
export interface HeatMapCell extends ChartDataRecord {
  x: number;
  y: number;
  xIndex: number;
  yIndex: number;
  value: number;
  originalX: number;
  originalY: number;
}

export interface HeatMapProps extends BaseVisualizationProps {
  /** Key for the value to visualize */
  valueKey: string;

  /** Size of each cell in pixels */
  cellSize?: number;

  /** Array of labels for X-axis */
  xLabels?: string[];

  /** Array of labels for Y-axis */
  yLabels?: string[];

  /** Whether to show values inside cells */
  showValues?: boolean;

  /** Custom function to format cell values */
  valueFormatter?: (value: number) => string;

  /** Custom tooltip for cells */
  cellTooltip?: boolean;

  /** Number of decimal places for displayed values */
  valueDecimals?: number;

  /** Minimum value for color scale (if undefined, uses min from data) */
  minValue?: number;

  /** Maximum value for color scale (if undefined, uses max from data) */
  maxValue?: number;

  /** Border style for cells */
  cellBorder?: {
    width?: number;
    color?: string;
    radius?: number;
  };

  /** Theme for the chart (light or dark) */
  theme?: 'light' | 'dark';

  /** Colors to use for the heat map gradient */
  colors?: string[];
}

/**
 * HeatMap component for visualizing density or intensity data across a grid
 */
export const HeatMap = React.memo(function HeatMap({
  data,
  valueKey,
  xKey = 'x',
  yKey = 'y',
  width = '100%',
  height = 400,
  title,
  colors = [
    '#0a2f5c', // Dark blue
    '#0e4c92', // Navy blue
    '#3373c4', // Medium blue
    '#5a9bd4', // Light blue
    '#7fc8f8', // Sky blue
    '#a3d8f4', // Pale blue
    '#c6e7f5', // Very pale blue
    '#e1f3fb', // Almost white blue
    '#feebe2', // Very pale red
    '#fcc5c0', // Pale red
    '#fa9fb5', // Light red
    '#f768a1', // Pink
    '#dd3497', // Medium pink
    '#ae017e', // Dark pink
    '#7a0177', // Very dark pink/purple
  ],
  cellSize = 40,
  xLabels,
  yLabels,
  showValues = true,
  valueFormatter = (value: number) => value.toFixed(1),
  cellTooltip = true,
  customTooltip,
  valueDecimals = 1,
  minValue,
  maxValue,
  showLegend = true,
  cellBorder = {
    width: 1,
    color: 'rgba(255,255,255,0.2)',
    radius: 0,
  },
  colorAccessor,
  theme = 'light',
  className = '',
  onElementClick,
  errorMessage,
  grid,
  animate,
}: HeatMapProps) {
  // Process data into a 2D grid format for the heatmap
  const { processedData, xValues, yValues, dataMinValue, dataMaxValue } = useMemo(() => {
    console.log('Processing heat map data'); // For debugging
    if (!data || data.length === 0) {
      return {
        processedData: [],
        xValues: [],
        yValues: [],
        dataMinValue: 0,
        dataMaxValue: 0,
      };
    }

    // Extract unique x and y coordinates
    const xCoords = new Set<number>();
    const yCoords = new Set<number>();

    // Keep track of min and max values
    let minVal = Infinity;
    let maxVal = -Infinity;

    // Map to hold data by coordinates
    const dataByCoords = new Map<string, number>();

    data.forEach(item => {
      let x: number | undefined;
      let y: number | undefined;

      // Get x and y coordinates from the data point
      if (typeof item[xKey] === 'number') {
        x = item[xKey] as number;
      } else if (xKey.includes('.')) {
        const parts = xKey.split('.');
        let current: unknown = item;
        for (const part of parts) {
          if (
            current &&
            typeof current === 'object' &&
            part in (current as Record<string, unknown>)
          ) {
            current = (current as Record<string, unknown>)[part];
          } else {
            current = undefined;
            break;
          }
        }
        if (typeof current === 'number') {
          x = current;
        }
      }

      if (typeof item[yKey] === 'number') {
        y = item[yKey] as number;
      } else if (yKey.includes('.')) {
        const parts = yKey.split('.');
        let current: unknown = item;
        for (const part of parts) {
          if (
            current &&
            typeof current === 'object' &&
            part in (current as Record<string, unknown>)
          ) {
            current = (current as Record<string, unknown>)[part];
          } else {
            current = undefined;
            break;
          }
        }
        if (typeof current === 'number') {
          y = current;
        }
      }

      // Get the value to visualize
      let value: number | undefined;
      if (typeof item[valueKey] === 'number') {
        value = item[valueKey] as number;
      } else if (valueKey.includes('.')) {
        const parts = valueKey.split('.');
        let current: unknown = item;
        for (const part of parts) {
          if (
            current &&
            typeof current === 'object' &&
            part in (current as Record<string, unknown>)
          ) {
            current = (current as Record<string, unknown>)[part];
          } else {
            current = undefined;
            break;
          }
        }
        if (typeof current === 'number') {
          value = current;
        }
      }

      if (x !== undefined && y !== undefined && value !== undefined) {
        xCoords.add(x);
        yCoords.add(y);
        const key = `${x},${y}`;
        dataByCoords.set(key, value);

        // Update min and max values
        minVal = Math.min(minVal, value);
        maxVal = Math.max(maxVal, value);
      }
    });

    // Sort coordinates
    const sortedXCoords = Array.from(xCoords).sort((a, b) => a - b);
    const sortedYCoords = Array.from(yCoords).sort((a, b) => a - b);

    // Create a 2D grid from the data
    const rows: HeatMapCell[] = [];

    sortedYCoords.forEach((y, yIndex) => {
      sortedXCoords.forEach((x, xIndex) => {
        const key = `${x},${y}`;
        const value = dataByCoords.get(key) || 0;

        rows.push({
          x: xIndex,
          y: yIndex,
          xIndex,
          yIndex,
          value,
          originalX: x,
          originalY: y,
        });
      });
    });

    return {
      processedData: rows,
      xValues: sortedXCoords,
      yValues: sortedYCoords,
      dataMinValue: minVal !== Infinity ? minVal : 0,
      dataMaxValue: maxVal !== -Infinity ? maxVal : 0,
    };
  }, [data, xKey, yKey, valueKey]);

  // Calculate effective min and max values
  const effectiveMinValue = useMemo(
    () => (minValue !== undefined ? minValue : dataMinValue),
    [minValue, dataMinValue]
  );

  const effectiveMaxValue = useMemo(
    () => (maxValue !== undefined ? maxValue : dataMaxValue),
    [maxValue, dataMaxValue]
  );

  const valueRange = useMemo(
    () => effectiveMaxValue - effectiveMinValue,
    [effectiveMaxValue, effectiveMinValue]
  );

  // Calculate cell dimensions
  const cellWidth = cellSize;
  const cellHeight = cellSize;

  const gridDimensions = useMemo(
    () => ({
      width: xValues.length * cellWidth,
      height: yValues.length * cellHeight,
    }),
    [xValues.length, yValues.length, cellWidth, cellHeight]
  );

  // Function to get color for a value
  const getColorForValue = useCallback(
    (value: number) => {
      if (colorAccessor && data && processedData) {
        // Find the original data point for this cell
        const cell = processedData.find(c => c.value === value);
        if (cell) {
          // Find the original data item
          const originalItem = data.find(
            item =>
              (item[xKey] === cell.originalX ||
                (typeof item[xKey] === 'object' &&
                  (item[xKey] as Record<string, unknown>).x === cell.originalX)) &&
              (item[yKey] === cell.originalY ||
                (typeof item[yKey] === 'object' &&
                  (item[yKey] as Record<string, unknown>).y === cell.originalY))
          );
          if (originalItem) {
            return colorAccessor(originalItem);
          }
        }
      }

      // Use gradient if no colorAccessor or original item not found
      if (valueRange === 0) return colors[Math.floor(colors.length / 2)];
      const normalizedValue = Math.max(0, Math.min(1, (value - effectiveMinValue) / valueRange));
      const colorIndex = Math.min(colors.length - 1, Math.floor(normalizedValue * colors.length));
      return colors[colorIndex];
    },
    [colorAccessor, data, processedData, xKey, yKey, valueRange, colors, effectiveMinValue]
  );

  // Handle click on a cell
  const handleCellClick = useCallback(
    (cell: HeatMapCell) => {
      if (onElementClick) {
        const originalItem = data.find(
          item =>
            (item[xKey] === cell.originalX ||
              (typeof item[xKey] === 'object' &&
                (item[xKey] as Record<string, unknown>).x === cell.originalX)) &&
            (item[yKey] === cell.originalY ||
              (typeof item[yKey] === 'object' &&
                (item[yKey] as Record<string, unknown>).y === cell.originalY))
        );

        if (originalItem) {
          onElementClick(originalItem);
        } else {
          onElementClick(cell as unknown as ChartDataRecord);
        }
      }
    },
    [onElementClick, data, xKey, yKey]
  );

  // Memoize the rendered cells to prevent unnecessary re-renders
  const renderedCells = useMemo(
    () =>
      processedData.map((cell, index) => {
        const cellColor = getColorForValue(cell.value);
        return (
          <div
            key={`${cell.x}-${cell.y}`}
            className="heat-map-cell"
            style={{
              position: 'absolute',
              left: cell.x * cellWidth,
              top: cell.y * cellHeight,
              width: cellWidth,
              height: cellHeight,
              backgroundColor: cellColor,
              border: `${cellBorder.width}px solid ${cellBorder.color}`,
              borderRadius: cellBorder.radius,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              cursor: onElementClick ? 'pointer' : 'default',
            }}
            onClick={() => handleCellClick(cell)}
            data-testid={`cell-${cell.x}-${cell.y}`}
          >
            {showValues && (
              <span
                style={{
                  color: theme === 'light' ? '#000000' : '#ffffff',
                  fontSize: '12px',
                  fontWeight: 'bold',
                  textShadow:
                    theme === 'light' ? '0 0 2px #ffffff' : '0 0 2px #000000, 0 0 3px #000000',
                }}
              >
                {valueFormatter(cell.value)}
              </span>
            )}
          </div>
        );
      }),
    [
      processedData,
      getColorForValue,
      cellWidth,
      cellHeight,
      cellBorder,
      handleCellClick,
      onElementClick,
      showValues,
      theme,
      valueFormatter,
    ]
  );

  // Memoize the x-axis labels
  const renderedXLabels = useMemo(
    () =>
      xLabels?.map((label, index) => (
        <div
          key={`x-label-${index}`}
          style={{
            position: 'absolute',
            left: index * cellWidth + cellWidth / 2,
            top: gridDimensions.height + 5,
            transform: 'translateX(-50%)',
            fontSize: '12px',
          }}
        >
          {label}
        </div>
      )),
    [xLabels, cellWidth, gridDimensions.height]
  );

  // Memoize the y-axis labels
  const renderedYLabels = useMemo(
    () =>
      yLabels?.map((label, index) => (
        <div
          key={`y-label-${index}`}
          style={{
            position: 'absolute',
            top: index * cellHeight + cellHeight / 2,
            left: -5,
            transform: 'translateX(-100%) translateY(-50%)',
            fontSize: '12px',
            textAlign: 'right',
          }}
        >
          {label}
        </div>
      )),
    [yLabels, cellHeight]
  );

  // Memoize the legend component
  const legend = useMemo(
    () =>
      showLegend ? (
        <div
          className="heat-map-legend"
          style={{
            position: 'absolute',
            bottom: -40,
            left: 0,
            right: 0,
            height: 20,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <div
            style={{
              display: 'flex',
              width: '80%',
              height: '10px',
              background: `linear-gradient(to right, ${colors.join(', ')})`,
              borderRadius: '2px',
              marginRight: '10px',
            }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between', width: '80%' }}>
            <span style={{ fontSize: '10px' }}>{effectiveMinValue.toFixed(valueDecimals)}</span>
            <span style={{ fontSize: '10px' }}>{effectiveMaxValue.toFixed(valueDecimals)}</span>
          </div>
        </div>
      ) : null,
    [showLegend, colors, effectiveMinValue, effectiveMaxValue, valueDecimals]
  );

  return (
    <BaseChart
      title={title}
      width={width}
      height={height}
      className={`heatmap-chart ${className}`}
      customTooltip={customTooltip as React.FC<ChartTooltipProps>}
      errorMessage={errorMessage}
    >
      {data.length === 0 ? (
        <div className="heat-map-no-data">No data available</div>
      ) : (
        <div
          className="heat-map-container"
          style={{
            position: 'relative',
            width: gridDimensions.width,
            height: gridDimensions.height,
            margin: 'auto',
          }}
        >
          {/* Render heat map cells */}
          {renderedCells}

          {/* X-axis labels */}
          {renderedXLabels}

          {/* Y-axis labels */}
          {renderedYLabels}

          {/* Legend */}
          {legend}
        </div>
      )}
    </BaseChart>
  );
});

export default HeatMap;

import { useMemo } from 'react';
import {
  CartesianGrid,
  LabelProps,
  Legend,
  Line,
  LineChart as RechartsLineChart,
  ReferenceLine,
  Tooltip,
  XAxis,
  YAxis,
} from 'recharts';
import {
  BaseChart,
  BaseChartProps,
  DefaultTooltip,
  formatAxisTick,
  getColor,
  processChartData,
  ReferenceLine as ReferenceLineType,
} from './BaseChart';

// Define allowed positions for reference line labels
type ReferenceLinePosition =
  | 'top'
  | 'bottom'
  | 'left'
  | 'right'
  | 'insideTop'
  | 'insideBottom'
  | 'insideLeft'
  | 'insideRight'
  | 'insideTopRight'
  | 'insideTopLeft'
  | 'insideBottomRight'
  | 'insideBottomLeft'
  | 'center';

// Define the dot click event interface
interface DotClickEvent {
  payload: Record<string, unknown>;
  index?: number;
  dataKey?: string;
  cx?: number;
  cy?: number;
  r?: number;
  [key: string]: unknown;
}

export interface LineChartProps extends BaseChartProps {
  /** Key for X-axis values */
  xAxisKey: string;

  /** Keys for Y-axis values (multiple series) */
  yAxisKeys: string[];

  /** Whether to apply date formatting to x-axis values */
  dateFormat?: boolean;

  /** Whether to connect null data points */
  connectNulls?: boolean;

  /** Curve type for the lines */
  curveType?: 'linear' | 'monotone' | 'step' | 'stepAfter' | 'stepBefore' | 'natural';

  /** Whether to show grid lines */
  showGrid?: boolean;

  /** Whether to fill area under lines */
  fillArea?: boolean;

  /** Stroke width for the lines */
  strokeWidth?: number;

  /** X-axis label */
  xAxisLabel?: string;

  /** Y-axis label */
  yAxisLabel?: string;

  /** Whether to show dots on data points */
  showDots?: boolean;

  /** Whether to show the legend */
  showLegend?: boolean;

  /** Reference lines to display (e.g., thresholds) */
  referenceLines?: ReferenceLineType[];
}

/**
 * LineChart component for visualizing time series or other xy data
 */
export function LineChart({
  data,
  xAxisKey,
  yAxisKeys,
  width = '100%',
  height = 400,
  title,
  colors = [],
  dateFormat = false,
  customTooltip,
  theme = 'light',
  connectNulls = true,
  curveType = 'monotone',
  showGrid = true,
  fillArea = false,
  strokeWidth = 2,
  xAxisLabel,
  yAxisLabel,
  showDots = true,
  showLegend = true,
  referenceLines = [],
  className = '',
  animate = true,
  onElementClick,
  errorMessage,
}: LineChartProps) {
  // Process data for the chart
  const processedData = useMemo(
    () => processChartData(data, xAxisKey, yAxisKeys, dateFormat),
    [data, xAxisKey, yAxisKeys, dateFormat]
  );

  // If no data, show error
  if (!data || data.length === 0) {
    return (
      <BaseChart
        width={width}
        height={height}
        title={title}
        theme={theme}
        className={className}
        errorMessage={errorMessage || 'No data available'}
      >
        <RechartsLineChart data={[]} />
      </BaseChart>
    );
  }

  // Handle chart click
  const handleChartClick = (chartData: Record<string, unknown> | undefined, index: number) => {
    if (onElementClick && chartData) {
      onElementClick(chartData, index);
    }
  };

  // Handle dot click event
  const handleDotClick = (event: DotClickEvent, index: number) => {
    if (onElementClick && event && event.payload) {
      onElementClick(event.payload, index);
    }
  };

  // Create the chart content
  const chartContent = (
    <RechartsLineChart
      data={processedData}
      margin={{ top: 20, right: 30, left: 20, bottom: 20 }}
      onClick={chartData => chartData && handleChartClick(chartData as Record<string, unknown>, 0)}
    >
      {showGrid && <CartesianGrid strokeDasharray="3 3" />}

      <XAxis
        dataKey={dateFormat && xAxisKey === 'date' ? 'formattedDate' : xAxisKey}
        tickFormatter={value => formatAxisTick(value, dateFormat)}
        label={
          xAxisLabel ? { value: xAxisLabel, position: 'insideBottom', offset: -10 } : undefined
        }
      />

      <YAxis
        label={yAxisLabel ? { value: yAxisLabel, angle: -90, position: 'insideLeft' } : undefined}
      />

      <Tooltip content={customTooltip || <DefaultTooltip />} />

      {showLegend && <Legend />}

      {/* Reference lines for thresholds or important values */}
      {referenceLines.map((line, i) => (
        <ReferenceLine
          key={`ref-line-${i}`}
          x={line.axis === 'x' ? line.value : undefined}
          y={line.axis === 'y' ? line.value : undefined}
          stroke={line.color || '#ff7300'}
          label={
            line.label
              ? ({
                  value: line.label,
                  position: line.position || 'center',
                } as LabelProps)
              : undefined
          }
        />
      ))}

      {/* Render each data series as a line */}
      {yAxisKeys.map((key, index) => (
        <Line
          key={key}
          type={curveType}
          dataKey={key}
          stroke={getColor(index, colors)}
          activeDot={
            showDots
              ? {
                  r: 6,
                  onClick: (e: DotClickEvent) => handleDotClick(e, index),
                }
              : false
          }
          dot={showDots}
          strokeWidth={strokeWidth}
          connectNulls={connectNulls}
          name={key.charAt(0).toUpperCase() + key.slice(1).replace(/([A-Z])/g, ' $1')}
          fill={fillArea ? getColor(index, colors) + '33' : undefined} // Add transparency for fill
          fillOpacity={fillArea ? 0.2 : 0}
          isAnimationActive={animate}
        />
      ))}
    </RechartsLineChart>
  );

  return (
    <BaseChart
      width={width}
      height={height}
      title={title}
      theme={theme}
      className={className}
      errorMessage={errorMessage}
    >
      {chartContent}
    </BaseChart>
  );
}

