/**
 * Type definitions for internal use in ResourceMappingVisualization
 */
interface ResourceChartPoint extends ChartDataRecord {
  id: string;
  name: string;
  x: number;
  y: number;
  value: number;
  type: string;
  coordinates: { x: number; y: number };
}

interface ResourceHeatMapCell extends ChartDataRecord {
  x: number;
  y: number;
  value: number;
  resources: Array<{
    type: ResourceType;
    amount: number;
    quality?: number;
    accessibility?: number;
    estimatedValue?: number;
  }>;
  totalValue: number;
  dominantResource?: ResourceType;
  dominantPercentage?: number;
  totalResourceCount: number;
  dominantType: string;
}

/**
 * Interface for the data structure passed to ResourceMappingVisualization
 */
interface ResourceMappingData {
  resourcePoints: DataPoint[];
  gridCells?: ResourceGridCell[];
  resourceTypes: ResourceType[];
  valueMetric: 'amount' | 'quality' | 'accessibility' | 'estimatedValue';
  regionSize: number;
  xRange: [number, number];
  yRange: [number, number];
  density?: Record<string, number>;
  insights?: string[];
  summary?: string;
}

interface ResourceMappingVisualizationProps {
  data: ResourceMappingData;
  width?: number | string;
  height?: number;
  title?: string;
}

// Color mapping for different resource types
const resourceTypeColors: Record<ResourceType, string> = {
  minerals: '#3D85C6', // Blue
  energy: '#F1C232', // Yellow/gold
  population: '#6AA84F', // Green
  research: '#9FC5E8', // Light blue
  plasma: '#D5A6BD', // Purple
  gas: '#C27BA0', // Pink
  exotic: '#CC0000', // Red
};

// Define a function to get color for a resource type
const getResourceColor = (resourceType: string): string => {
  return (resourceTypeColors as Record<string, string>)[resourceType] || '#999999';
};

/**
 * ResourceMappingVisualization Component
 * Displays resource distribution across a 2D map with various visualization options
 */
export const ResourceMappingVisualization: React.FC<ResourceMappingVisualizationProps> = ({
  data,
  width = '100%',
  height = 600,
  title = 'Resource Mapping Analysis',
}) => {
  // Component state
  const [activeTab, setActiveTab] = useState(0);
  const [selectedResourceType, setSelectedResourceType] = useState<string>('all');
  const [showResourceLabels, setShowResourceLabels] = useState(false);
  const [overlayMode, setOverlayMode] = useState(false);

  // Prepare the heat map data
  const heatMapData = useMemo(() => {
    if (!data.gridCells || data.gridCells.length === 0) return [];

    return data.gridCells.map((cell: ResourceGridCell) => {
      let value = 0;

      if (selectedResourceType === 'all') {
        value = cell.totalValue;
      } else {
        const resourceData = cell.resources.find(
          (r: { type: ResourceType }) => r.type === selectedResourceType
        );
        if (resourceData) {
          value =
            (resourceData[data.valueMetric as keyof typeof resourceData] as number) ||
            resourceData.amount;
        }
      }

      return {
        ...cell,
        value,
      };
    });
  }, [data.gridCells, selectedResourceType, data.valueMetric]);

  // Prepare scatter plot data
  const scatterData = useMemo(() => {
    if (!data.resourcePoints || data.resourcePoints.length === 0) return [];

    return data.resourcePoints.map((point: DataPoint) => {
      // Safely extract resource data from properties
      const properties = point.properties || {};
      const resourceType = properties.resourceType || properties.type || 'unknown';
      const value = properties[data.valueMetric] || properties.amount || 1;

      return {
        id: point.id,
        name: point.name || `Resource ${point.id}`,
        x: point.coordinates?.x || 0,
        y: point.coordinates?.y || 0,
        value,
        type: resourceType,
        coordinates: point.coordinates || { x: 0, y: 0 },
      };
    });
  }, [data.resourcePoints, data.valueMetric]);

  // Event handlers
  const handleTabChange = (_event: React.SyntheticEvent, newValue: number) => {
    setActiveTab(newValue);
  };

  const handleResourceTypeChange = (event: SelectChangeEvent<string>) => {
    setSelectedResourceType(event.target.value);
  };

  // Color function for scatter plot points
  const getPointColor = (point: ResourceChartPoint): string => {
    const resourceType = point.type as ResourceType;
    return resourceTypeColors[resourceType] || '#888888';
  };

  // Color function for heat map cells in overlay mode
  const getHeatMapCellColor = (cell: ResourceHeatMapCell): string => {
    const dominantType = cell.dominantType as ResourceType;
    return resourceTypeColors[dominantType] || '#888888';
  };

  // Tooltip for heatmap cells
  const renderCellTooltip: TooltipRenderer<ResourceHeatMapCell> = heatMapCell => {
    if (!heatMapCell) return null;

    const resources = heatMapCell.resources || [];
    const sortedResources = [...resources].sort((a, b) => {
      const aValue = (a[data.valueMetric as keyof typeof a] as number) || a.amount;
      const bValue = (b[data.valueMetric as keyof typeof b] as number) || b.amount;
      return bValue - aValue;
    });

    return (
      <Paper sx={{ p: 1.5, maxWidth: 300 }}>
        <Typography variant="subtitle2">
          Region ({heatMapCell.x}, {heatMapCell.y})
        </Typography>
        <Typography variant="body2">Total value: {heatMapCell.totalValue.toFixed(2)}</Typography>
        <Typography variant="body2">Resources: {heatMapCell.totalResourceCount}</Typography>
        {heatMapCell.dominantResource && typeof heatMapCell.dominantPercentage === 'number' && (
          <Typography variant="body2">
            Dominant: {heatMapCell.dominantResource} (
            {(heatMapCell.dominantPercentage * 100).toFixed(1)}%)
          </Typography>
        )}
        <div style={{ marginTop: 8 }}>
          {sortedResources.map((res, idx) => (
            <div key={idx} style={{ display: 'flex', alignItems: 'center', marginBottom: 4 }}>
              <div
                style={{
                  width: 12,
                  height: 12,
                  backgroundColor: getResourceColor(res.type),
                  marginRight: 8,
                }}
              />
              <Typography variant="body2">
                {res.type}: {res.amount.toFixed(1)}{' '}
                {res.quality ? `(Q: ${res.quality.toFixed(1)})` : ''}
              </Typography>
            </div>
          ))}
        </div>
      </Paper>
    );
  };

  // Tooltip for scatter plot points
  const renderPointTooltip: TooltipRenderer<ResourceChartPoint> = point => (
    <Paper sx={{ p: 1 }}>
      <Typography variant="subtitle2">{point.name}</Typography>
      <Typography variant="body2">Type: {point.type}</Typography>
      <Typography variant="body2">
        {data.valueMetric}: {point.value.toFixed(2)}
      </Typography>
      <Typography variant="body2">
        Location: ({point.coordinates.x.toFixed(1)}, {point.coordinates.y.toFixed(1)})
      </Typography>
    </Paper>
  );

  // Create tooltip components using adapter
  const PointTooltipComponent = useMemo(
    () => createTooltipComponent(renderPointTooltip),
    [renderPointTooltip]
  );

  // Create cell tooltip component using adapter
  const CellTooltipComponentFromRenderer = useMemo(
    () => createTooltipComponent(renderCellTooltip),
    [renderCellTooltip]
  );

  // Legend for resource types
  const renderLegend = () => (
    <Paper
      sx={{
        p: 2,
        mt: 2,
        backgroundColor: theme => theme.palette.background.paper,
      }}
    >
      {data.resourceTypes.map((type: ResourceType) => (
        <div key={type} style={{ display: 'flex', alignItems: 'center' }}>
          <div
            style={{
              width: '16px',
              height: '16px',
              backgroundColor: resourceTypeColors[type],
              marginRight: '8px',
              borderRadius: '50%',
            }}
          />
          <Typography variant="body2">{type}</Typography>
        </div>
      ))}
    </Paper>
  );

  // Insights display
  const renderInsights = () => {
    if (!data.insights || data.insights.length === 0) {
      return (
        <Paper sx={{ p: 2, mt: 2, backgroundColor: theme => theme.palette.background.paper }}>
          <Typography variant="body2">No insights available for this analysis.</Typography>
        </Paper>
      );
    }

    return (
      <Paper sx={{ p: 2, mt: 2, backgroundColor: theme => theme.palette.background.paper }}>
        <Typography variant="h6" sx={{ mb: 1 }}>
          Key Insights
        </Typography>
        <Box component="ul" sx={{ ml: 2, mt: 1 }}>
          {data.insights.map((insight: string, index: number) => (
            <Typography component="li" key={index} variant="body2" sx={{ mb: 1 }}>
              {insight}
            </Typography>
          ))}
        </Box>
      </Paper>
    );
  };

  // Heat map for resource distribution
  const renderHeatMap = () => {
    if (heatMapData.length === 0) {
      return (
        <div style={{ padding: '16px', textAlign: 'center' }}>
          <Typography variant="body1">
            No grid data available for heat map visualization.
          </Typography>
        </div>
      );
    }

    // Process colors for heat map
    const heatMapColors =
      overlayMode && selectedResourceType === 'all' ? Object.values(resourceTypeColors) : undefined;

    return (
      <div style={{ height: 'calc(100% - 120px)', minHeight: '400px' }}>
        <HeatMap
          data={heatMapData}
          xKey="x"
          yKey="y"
          valueKey="value"
          width="100%"
          height="100%"
          showValues={false}
          showLegend={true}
          cellTooltip={true}
          customTooltip={CellTooltipComponentFromRenderer}
          colors={heatMapColors}
          cellBorder={{
            width: 1,
            color: 'rgba(255,255,255,0.3)',
            radius: 0,
          }}
        />
      </div>
    );
  };

  // Scatter plot for individual resource points
  const renderScatterPlot = () => {
    // Use the pre-processed scatter data
    const filteredScatterData =
      selectedResourceType === 'all'
        ? scatterData
        : scatterData.filter(point => point.type === selectedResourceType);

    if (filteredScatterData.length === 0) {
      return (
        <div
          style={{
            width: '100%',
            height: 400,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <Typography variant="body1">No resources to display</Typography>
        </div>
      );
    }

    // Set up props for ScatterPlot
    const scatterPlotProps = {
      data: filteredScatterData,
      xAxisKey: 'x',
      yAxisKey: 'y',
      width: typeof width === 'number' ? width : 800,
      height: 400,
      sizeKey: 'value',
      colorAccessor: getPointColor as ColorAccessorFn<ChartDataRecord>,
      showLabels: true,
      labelKey: 'name',
      customTooltip: PointTooltipComponent,
    };

    return <ScatterPlot {...scatterPlotProps} />;
  };

  return (
    <BaseChart title={title} width={width} height={height}>
      <>
        <div style={{ width: '100%', marginBottom: '16px' }}>
          <Tabs
            value={activeTab}
            onChange={handleTabChange}
            variant="scrollable"
            scrollButtons="auto"
          >
            <Tab label="Heat Map" />
            <Tab label="Scatter Plot" />
            <Tab label="Insights" />
          </Tabs>
        </div>

        <div style={{ marginBottom: '16px', display: 'flex', alignItems: 'center' }}>
          <FormControl size="small" sx={{ minWidth: 150 }}>
            <Select
              value={selectedResourceType}
              onChange={handleResourceTypeChange}
              displayEmpty
              size="small"
            >
              <MenuItem value="all">All Resources</MenuItem>
              {data.resourceTypes.map((type: ResourceType) => (
                <MenuItem key={type} value={type}>
                  {type}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <FormControlLabel
            control={
              <Switch
                checked={overlayMode}
                onChange={e => setOverlayMode(e.target.checked)}
                size="small"
              />
            }
            label="Overlay Mode"
          />

          {activeTab === 1 && (
            <FormControlLabel
              control={
                <Switch
                  checked={showResourceLabels}
                  onChange={e => setShowResourceLabels(e.target.checked)}
                  size="small"
                />
              }
              label="Show Labels"
            />
          )}

          <Tooltip title="Overlay mode shows resource types with different colors. For heat maps, it colors cells by dominant resource type.">
            <IconButton size="small">
              <InfoIcon fontSize="small" />
            </IconButton>
          </Tooltip>
        </div>

        {overlayMode && selectedResourceType === 'all' && renderLegend()}

        {activeTab === 0 && renderHeatMap()}
        {activeTab === 1 && renderScatterPlot()}
        {activeTab === 2 && renderInsights()}
      </>
    </BaseChart>
  );
};

import { useMemo } from 'react';
import {
  CartesianGrid,
  LabelProps,
  Legend,
  ScatterChart as RechartsScatterChart,
  ReferenceArea,
  ReferenceLine,
  Scatter,
  Tooltip,
  XAxis,
  YAxis,
  ZAxis,
} from 'recharts';
import { DataPoint } from '../../../../types/exploration/DataAnalysisTypes';
import {
  BaseChart,
  BaseChartProps,
  DefaultTooltip,
  formatAxisTick,
  ReferenceLine as ReferenceLineType,
} from './BaseChart';

// Define allowed positions for reference line labels
type ReferenceLinePosition =
  | 'top'
  | 'bottom'
  | 'left'
  | 'right'
  | 'insideTop'
  | 'insideBottom'
  | 'insideLeft'
  | 'insideRight'
  | 'insideTopRight'
  | 'insideTopLeft'
  | 'insideBottomRight'
  | 'insideBottomLeft'
  | 'center';

// Define the click event interface
interface PointClickEvent {
  payload: Record<string, unknown>;
  [key: string]: unknown;
}

export interface ScatterPlotProps extends BaseChartProps {
  /** Key for X-axis values */
  xAxisKey: string;

  /** Key for Y-axis values */
  yAxisKey: string;

  /** Optional key for Z-axis values (bubble size) */
  zAxisKey?: string;

  /** Label for X-axis */
  xAxisLabel?: string;

  /** Label for Y-axis */
  yAxisLabel?: string;

  /** Key for point name/label */
  nameKey?: string;

  /** Whether to show grid lines */
  showGrid?: boolean;

  /** Point fill color */
  color?: string;

  /** Size of the points */
  pointSize?: number;

  /** Minimum for Z-axis size range */
  zAxisSizeMin?: number;

  /** Maximum for Z-axis size range */
  zAxisSizeMax?: number;

  /** Optional quadrants to divide the chart */
  showQuadrants?: boolean;

  /** X-axis value for quadrant division */
  quadrantXValue?: number;

  /** Y-axis value for quadrant division */
  quadrantYValue?: number;

  /** Quadrant label configuration */
  quadrantLabels?: {
    topRight?: string;
    topLeft?: string;
    bottomRight?: string;
    bottomLeft?: string;
  };

  /** Whether to show the legends */
  showLegend?: boolean;

  /** Reference lines to display */
  referenceLines?: ReferenceLineType[];
}

/**
 * ScatterPlot component for visualizing correlations between variables
 */
export function ScatterPlot({
  data,
  xAxisKey,
  yAxisKey,
  zAxisKey,
  width = '100%',
  height = 400,
  title,
  color = '#8884d8',
  xAxisLabel,
  yAxisLabel,
  nameKey,
  customTooltip,
  theme = 'light',
  showGrid = true,
  pointSize = 10,
  zAxisSizeMin = 10,
  zAxisSizeMax = 80,
  showQuadrants = false,
  quadrantXValue,
  quadrantYValue,
  quadrantLabels = {
    topRight: 'Top Right',
    topLeft: 'Top Left',
    bottomRight: 'Bottom Right',
    bottomLeft: 'Bottom Left',
  },
  showLegend = true,
  referenceLines = [],
  className = '',
  animate = true,
  onElementClick,
  errorMessage,
}: ScatterPlotProps) {
  // Process data for the chart
  const processedData = useMemo(() => {
    if (!data || data.length === 0) return [];

    return data.map(item => {
      let x, y, z, name;

      // Handle DataPoint objects
      if ('properties' in item && 'metadata' in item) {
        const dataPoint = item as DataPoint;
        const properties = { ...dataPoint.properties };
        const metadata = dataPoint.metadata || {};

        // Try to find the required values in properties or metadata
        x = properties[xAxisKey] !== undefined ? properties[xAxisKey] : metadata[xAxisKey];
        y = properties[yAxisKey] !== undefined ? properties[yAxisKey] : metadata[yAxisKey];
        z = zAxisKey
          ? properties[zAxisKey] !== undefined
            ? properties[zAxisKey]
            : metadata[zAxisKey]
          : undefined;

        name = nameKey
          ? (properties[nameKey] !== undefined ? properties[nameKey] : metadata[nameKey]) ||
            dataPoint.name
          : dataPoint.name;

        // Ensure x and y values are numbers
        x = typeof x === 'number' ? x : parseFloat(String(x)) || 0;
        y = typeof y === 'number' ? y : parseFloat(String(y)) || 0;
        z = z && typeof z === 'number' ? z : z ? parseFloat(String(z)) || pointSize : pointSize;

        return {
          x,
          y,
          z,
          name,
          id: dataPoint.id,
          originalData: dataPoint,
        };
      }
      // Handle regular objects
      else {
        const record = item as Record<string, unknown>;

        x = record[xAxisKey];
        y = record[yAxisKey];
        z = zAxisKey ? record[zAxisKey] : undefined;

        name = nameKey ? record[nameKey] : undefined;

        // Ensure x and y values are numbers
        x = typeof x === 'number' ? x : parseFloat(String(x)) || 0;
        y = typeof y === 'number' ? y : parseFloat(String(y)) || 0;
        z = z && typeof z === 'number' ? z : z ? parseFloat(String(z)) || pointSize : pointSize;

        return {
          x,
          y,
          z,
          name: name || `(${x}, ${y})`,
          originalData: record,
        };
      }
    });
  }, [data, xAxisKey, yAxisKey, zAxisKey, nameKey, pointSize]);

  // If no data, show error
  if (!data || data.length === 0) {
    return (
      <BaseChart
        width={width}
        height={height}
        title={title}
        theme={theme}
        className={className}
        errorMessage={errorMessage || 'No data available'}
      >
        <RechartsScatterChart data={[]} />
      </BaseChart>
    );
  }

  // Calculate quadrant values if not provided
  const xValue =
    quadrantXValue !== undefined
      ? quadrantXValue
      : processedData.reduce((sum, item) => sum + item.x, 0) / processedData.length;

  const yValue =
    quadrantYValue !== undefined
      ? quadrantYValue
      : processedData.reduce((sum, item) => sum + item.y, 0) / processedData.length;

  // Handle click events
  const handlePointClick = (event: PointClickEvent) => {
    if (onElementClick && event && event.payload) {
      onElementClick(event.payload, 0);
    }
  };

  // Create the chart content
  const chartContent = (
    <RechartsScatterChart margin={{ top: 20, right: 20, bottom: 20, left: 20 }}>
      {showGrid && <CartesianGrid strokeDasharray="3 3" />}

      <XAxis
        type="number"
        dataKey="x"
        name={xAxisLabel || xAxisKey}
        label={
          xAxisLabel ? { value: xAxisLabel, position: 'insideBottom', offset: -10 } : undefined
        }
        tickFormatter={value => formatAxisTick(value, false)}
      />

      <YAxis
        type="number"
        dataKey="y"
        name={yAxisLabel || yAxisKey}
        label={yAxisLabel ? { value: yAxisLabel, angle: -90, position: 'insideLeft' } : undefined}
        tickFormatter={value => formatAxisTick(value, false)}
      />

      {zAxisKey && (
        <ZAxis type="number" dataKey="z" range={[zAxisSizeMin, zAxisSizeMax]} name={zAxisKey} />
      )}

      <Tooltip content={customTooltip || <DefaultTooltip />} cursor={{ strokeDasharray: '3 3' }} />

      {showLegend && <Legend />}

      {/* Reference lines for thresholds or important values */}
      {referenceLines.map((line, i) => (
        <ReferenceLine
          key={`ref-line-${i}`}
          x={line.axis === 'x' ? line.value : undefined}
          y={line.axis === 'y' ? line.value : undefined}
          stroke={line.color || '#ff7300'}
          label={
            line.label
              ? ({
                  value: line.label,
                  position: line.position || 'center',
                } as LabelProps)
              : undefined
          }
        />
      ))}

      {/* Quadrant reference lines */}
      {showQuadrants && (
        <>
          <ReferenceLine x={xValue} stroke="#666" strokeDasharray="3 3" />
          <ReferenceLine y={yValue} stroke="#666" strokeDasharray="3 3" />

          {/* Quadrant labels */}
          {quadrantLabels.topRight && (
            <ReferenceArea
              x1={xValue}
              x2="auto"
              y1={yValue}
              y2="auto"
              fillOpacity={0}
              label={{ value: quadrantLabels.topRight, position: 'insideTopRight' }}
            />
          )}

          {quadrantLabels.topLeft && (
            <ReferenceArea
              x1={0}
              x2={xValue}
              y1={yValue}
              y2="auto"
              fillOpacity={0}
              label={{ value: quadrantLabels.topLeft, position: 'insideTopLeft' }}
            />
          )}

          {quadrantLabels.bottomRight && (
            <ReferenceArea
              x1={xValue}
              x2="auto"
              y1={0}
              y2={yValue}
              fillOpacity={0}
              label={{ value: quadrantLabels.bottomRight, position: 'insideBottomRight' }}
            />
          )}

          {quadrantLabels.bottomLeft && (
            <ReferenceArea
              x1={0}
              x2={xValue}
              y1={0}
              y2={yValue}
              fillOpacity={0}
              label={{ value: quadrantLabels.bottomLeft, position: 'insideBottomLeft' }}
            />
          )}
        </>
      )}

      <Scatter
        name={`${xAxisLabel || xAxisKey} vs ${yAxisLabel || yAxisKey}`}
        data={processedData}
        fill={color}
        onClick={(e: PointClickEvent) => handlePointClick(e)}
        isAnimationActive={animate}
      />
    </RechartsScatterChart>
  );

  return (
    <BaseChart
      width={width}
      height={height}
      title={title}
      theme={theme}
      className={className}
      errorMessage={errorMessage}
    >
      {chartContent}
    </BaseChart>
  );
}

import React, { useCallback, useMemo, useState } from 'react';
import {
  CartesianGrid,
  LabelProps,
  Legend,
  Line,
  LineChart as RechartsLineChart,
  ReferenceLine,
  ResponsiveContainer,
  Tooltip,
  XAxis,
  YAxis,
} from 'recharts';
import {
  ChartDataRecord,
  VisualizationValue,
} from '../../../../types/exploration/AnalysisComponentTypes';
import {
  BaseChart,
  BaseChartProps,
  DefaultTooltip,
  formatAxisTick,
  getColor,
  ReferenceLine as ReferenceLineType,
} from './BaseChart';

// Define allowed positions for reference line labels
type ReferenceLinePosition =
  | 'top'
  | 'bottom'
  | 'left'
  | 'right'
  | 'insideTop'
  | 'insideBottom'
  | 'insideLeft'
  | 'insideRight'
  | 'insideTopRight'
  | 'insideTopLeft'
  | 'insideBottomRight'
  | 'insideBottomLeft'
  | 'center';

// Define the dot click event interface
interface DotClickEvent {
  payload: Record<string, unknown>;
  index?: number;
  dataKey?: string;
  cx?: number;
  cy?: number;
  r?: number;
  [key: string]: unknown;
}

// Type for visualization records
type VisualizationRecord = Record<string, VisualizationValue>;

export interface VirtualizedLineChartProps extends BaseChartProps {
  /** Key for X-axis values */
  xAxisKey: string;

  /** Keys for Y-axis values (multiple series) */
  yAxisKeys: string[];

  /** Whether to apply date formatting to x-axis values */
  dateFormat?: boolean;

  /** Whether to connect null data points */
  connectNulls?: boolean;

  /** Curve type for the lines */
  curveType?: 'linear' | 'monotone' | 'step' | 'stepAfter' | 'stepBefore' | 'natural';

  /** Whether to show grid lines */
  showGrid?: boolean;

  /** Whether to fill area under lines */
  fillArea?: boolean;

  /** Stroke width for the lines */
  strokeWidth?: number;

  /** X-axis label */
  xAxisLabel?: string;

  /** Y-axis label */
  yAxisLabel?: string;

  /** Whether to show dots on data points */
  showDots?: boolean;

  /** Whether to show the legend */
  showLegend?: boolean;

  /** Reference lines to display (e.g., thresholds) */
  referenceLines?: ReferenceLineType[];

  /** Maximum number of data points to render before downsampling */
  maxDisplayedPoints?: number;

  /** Enables progressive loading of points as user zooms in */
  enableProgressiveLoading?: boolean;

  /** Subtitle to display additional information */
  subtitle?: string;
}

/**
 * A specialized line chart component that handles extremely large datasets efficiently
 * through downsampling, virtualization, and progressive loading.
 */
export const VirtualizedLineChart = React.memo(function VirtualizedLineChart({
  data,
  xAxisKey,
  yAxisKeys,
  width = '100%',
  height = 400,
  title,
  colors = [],
  dateFormat = false,
  customTooltip,
  theme = 'light',
  connectNulls = true,
  curveType = 'monotone',
  showGrid = true,
  fillArea = false,
  strokeWidth = 2,
  xAxisLabel,
  yAxisLabel,
  showDots = true,
  showLegend = true,
  referenceLines = [],
  className = '',
  animate = false, // Default to false for large datasets
  onElementClick,
  errorMessage,
  maxDisplayedPoints = 1000,
  enableProgressiveLoading = true,
  subtitle,
}: VirtualizedLineChartProps) {
  // State for tracking visible domain (for zooming/panning)
  const [visibleDomain, setVisibleDomain] = useState<{
    x?: [number, number];
    y?: [number, number];
  }>({});

  // State for tracking the detail level based on zoom
  const [detailLevel, setDetailLevel] = useState<number>(1); // 1 = most downsampled

  // Format the processed data for chart rendering
  const formatDataForRendering = useCallback(
    (processedData: ChartDataRecord[]) => {
      return processedData.map(item => {
        // Use a type that matches ChartDataRecord's index signature
        const result = {
          [xAxisKey]: item[xAxisKey],
        } as Record<string, VisualizationValue>;

        if (dateFormat && typeof item[xAxisKey] === 'number') {
          result.formattedDate = new Date(item[xAxisKey] as number).toLocaleDateString();
        }

        // Extract all y-axis values
        yAxisKeys.forEach(key => {
          if (
            typeof item[key] === 'number' ||
            typeof item[key] === 'string' ||
            typeof item[key] === 'boolean' ||
            item[key] === null ||
            item[key] === undefined
          ) {
            result[key] = item[key] as VisualizationValue;
          }
        });

        return result;
      });
    },
    [xAxisKey, yAxisKeys, dateFormat]
  );

  // Process data for visualization
  const processedData = useMemo(() => {
    if (!data || data.length === 0) {
      return [];
    }

    // Convert data to the expected format
    const processedData = data.map(item => {
      // Create a new object with the required structure
      const record: ChartDataRecord = {};

      // Copy all properties from the original item
      Object.entries(item).forEach(([key, value]) => {
        // Only copy properties that match the VisualizationValue type
        if (
          typeof value === 'number' ||
          typeof value === 'string' ||
          typeof value === 'boolean' ||
          value === null ||
          value === undefined
        ) {
          record[key] = value;
        }
      });

      return record;
    });

    // Calculate how many points to display based on available space
    const downsampleFactor = Math.ceil(processedData.length / maxDisplayedPoints);

    // No downsampling needed
    if (downsampleFactor === 1) {
      return formatDataForRendering(processedData);
    }

    // Apply intelligent downsampling (LTTB algorithm - Largest Triangle Three Buckets)
    // This preserves visual characteristics better than simple downsampling
    return downsampleLTTB(processedData, xAxisKey, yAxisKeys[0], maxDisplayedPoints);
  }, [
    data,
    xAxisKey,
    yAxisKeys,
    visibleDomain,
    detailLevel,
    maxDisplayedPoints,
    enableProgressiveLoading,
    formatDataForRendering,
  ]);

  // Largest Triangle Three Buckets (LTTB) downsampling algorithm
  const downsampleLTTB = useCallback(
    (
      data: ChartDataRecord[],
      xKey: string,
      yKey: string,
      targetPoints: number
    ): VisualizationRecord[] => {
      const dataLength = data.length;
      if (dataLength <= targetPoints) {
        return formatDataForRendering(data);
      }

      // Always include first and last points
      const sampled: ChartDataRecord[] = [data[0]];

      // The LTTB algorithm preserves visual characteristics by keeping points that would create the largest triangles
      const bucketSize = (dataLength - 2) / (targetPoints - 2);

      let a = 0;
      for (let i = 0; i < targetPoints - 2; i++) {
        const nextA = Math.floor((i + 1) * bucketSize) + 1;
        let avgX = 0;
        let avgY = 0;
        const bucketCount = Math.max(1, nextA - Math.floor(a + bucketSize));

        // Calculate the average of points in the next bucket
        for (let j = Math.floor(a + bucketSize); j < nextA; j++) {
          const pointIdx = Math.min(j, dataLength - 1);
          // Make sure we always treat these as numbers
          const x = Number(data[pointIdx][xKey] || 0);
          const y = Number(data[pointIdx][yKey] || 0);
          avgX += x / bucketCount;
          avgY += y / bucketCount;
        }

        // Calculate area of triangles and find max area point
        let maxArea = -1;
        let maxAreaIndex = 0;

        // Use the current point (a) and the average of points in the next bucket
        // to find the point in the current bucket that creates the largest triangle
        for (let j = Math.floor(a + 1); j < nextA; j++) {
          const pointIdx = Math.min(j, dataLength - 1);

          // Get x,y coords from data
          const x = Number(data[pointIdx][xKey] || 0);
          const y = Number(data[pointIdx][yKey] || 0);

          // aPoint = previous selected point
          const aX = Number(data[a][xKey] || 0);
          const aY = Number(data[a][yKey] || 0);

          // Calculate triangle area (using cross product)
          const area = Math.abs((aX - avgX) * (y - aY) - (aX - x) * (avgY - aY));

          if (area > maxArea) {
            maxArea = area;
            maxAreaIndex = pointIdx;
          }
        }

        // Add the point with max area
        sampled.push(data[maxAreaIndex]);
        a = maxAreaIndex; // This becomes the new 'a' point
      }

      // Always include the last point
      sampled.push(data[dataLength - 1]);

      return formatDataForRendering(sampled);
    },
    [formatDataForRendering]
  );

  // Handle zoom events
  const handleZoom = useCallback(
    (domain: { x?: [number, number]; y?: [number, number] }) => {
      setVisibleDomain(domain);

      // Adjust detail level based on zoom level
      if (enableProgressiveLoading && domain.x) {
        const [min, max] = domain.x;
        const visibleRange = max - min;
        const totalRange =
          data.length > 0
            ? (data[data.length - 1][xAxisKey] as number) - (data[0][xAxisKey] as number)
            : 1;

        // Calculate detail level based on zoom (1-10)
        const zoomRatio = totalRange / visibleRange;
        const newDetailLevel = Math.min(10, Math.max(1, Math.ceil(zoomRatio)));

        setDetailLevel(newDetailLevel);
      }
    },
    [data, xAxisKey, enableProgressiveLoading]
  );

  // If no data, show error
  if (!data || data.length === 0) {
    return (
      <BaseChart
        width={width}
        height={height}
        title={title}
        theme={theme}
        className={className}
        errorMessage={errorMessage || 'No data available'}
        subtitle={subtitle}
      >
        <RechartsLineChart data={[]} />
      </BaseChart>
    );
  }

  // Handle chart click
  const handleChartClick = (chartData: Record<string, unknown> | undefined, index: number) => {
    if (onElementClick && chartData) {
      onElementClick(chartData, index);
    }
  };

  // Handle dot click event
  const handleDotClick = (event: DotClickEvent, index: number) => {
    if (onElementClick && event && event.payload) {
      onElementClick(event.payload, index);
    }
  };

  // Create the chart content
  const chartContent = (
    <ResponsiveContainer width="100%" height="100%">
      <RechartsLineChart
        data={processedData}
        margin={{ top: 20, right: 30, left: 20, bottom: 20 }}
        onClick={chartData =>
          chartData && handleChartClick(chartData as Record<string, unknown>, 0)
        }
        onMouseDown={() => console.log('Starting zoom/pan')}
        onMouseUp={e => {
          // The recharts library doesn't export proper types for chart events
          const event = e as unknown as {
            xAxis?: Array<{ domain: [number, number] }>;
            yAxis?: Array<{ domain: [number, number] }>;
          };

          if (event && event.xAxis && event.yAxis && event.xAxis[0] && event.yAxis[0]) {
            const domain = {
              x: event.xAxis[0].domain,
              y: event.yAxis[0].domain,
            };
            handleZoom(domain);
          }
        }}
      >
        {showGrid && <CartesianGrid strokeDasharray="3 3" />}

        <XAxis
          dataKey={dateFormat && xAxisKey === 'date' ? 'formattedDate' : xAxisKey}
          tickFormatter={value => formatAxisTick(value, dateFormat)}
          label={
            xAxisLabel ? { value: xAxisLabel, position: 'insideBottom', offset: -10 } : undefined
          }
          domain={visibleDomain.x || ['dataMin', 'dataMax']}
        />

        <YAxis
          label={yAxisLabel ? { value: yAxisLabel, angle: -90, position: 'insideLeft' } : undefined}
          domain={visibleDomain.y || ['auto', 'auto']}
        />

        <Tooltip content={customTooltip || <DefaultTooltip />} />

        {showLegend && <Legend />}

        {/* Reference lines for thresholds or important values */}
        {referenceLines.map((line, i) => (
          <ReferenceLine
            key={`ref-line-${i}`}
            x={line.axis === 'x' ? line.value : undefined}
            y={line.axis === 'y' ? line.value : undefined}
            stroke={line.color || '#ff7300'}
            label={
              line.label
                ? ({
                    value: line.label,
                    position: (line.position as ReferenceLinePosition) || 'center',
                  } as LabelProps)
                : undefined
            }
          />
        ))}

        {/* Render each data series as a line */}
        {yAxisKeys.map((key, index) => (
          <Line
            key={key}
            type={curveType}
            dataKey={key}
            stroke={getColor(index, colors)}
            activeDot={
              showDots
                ? {
                    r: 6,
                    onClick: (e: DotClickEvent) => handleDotClick(e, index),
                  }
                : false
            }
            dot={showDots && processedData.length < 100} // Only show dots if we have a reasonable number of points
            strokeWidth={strokeWidth}
            connectNulls={connectNulls}
            name={key.charAt(0).toUpperCase() + key.slice(1).replace(/([A-Z])/g, ' $1')}
            fill={fillArea ? getColor(index, colors) + '33' : undefined} // Add transparency for fill
            fillOpacity={fillArea ? 0.2 : 0}
            isAnimationActive={animate && processedData.length < 100} // Only animate if we have a small dataset
          />
        ))}
      </RechartsLineChart>
    </ResponsiveContainer>
  );

  // Add info about downsampling if applicable
  const displaySubtitle =
    processedData.length < data.length
      ? `Showing ${processedData.length} of ${data.length} points (downsampled for performance)`
      : subtitle;

  return (
    <BaseChart
      width={width}
      height={height}
      title={title}
      theme={theme}
      className={`virtualized-line-chart ${className}`}
      errorMessage={errorMessage}
      subtitle={displaySubtitle}
    >
      {chartContent}
    </BaseChart>
  );
});

export default VirtualizedLineChart;

/**
 * LongSessionMemoryVisualizer Component
 *
 * A React component for visualizing long session memory tracking data.
 * Displays memory usage over time, detects potential memory leaks,
 * and provides insights for performance optimization.
 */

import * as React from 'react';
import { useEffect, useRef, useState } from 'react';
import {
  LongSessionMemoryTracker,
  MemorySnapshot,
  MemoryTrendAnalysis,
} from '../../utils/performance/longsession/LongSessionMemoryTracker';

interface LongSessionMemoryVisualizerProps {
  /** Instance of the memory tracker to visualize */
  memoryTracker?: LongSessionMemoryTracker;

  /** Memory snapshots to visualize (alternative to providing a tracker) */
  snapshots?: MemorySnapshot[];

  /** Memory trend analysis to display (alternative to providing a tracker) */
  analysis?: MemoryTrendAnalysis;

  /** Whether to auto-update the visualization */
  autoUpdate?: boolean;

  /** Update interval in milliseconds (if autoUpdate is true) */
  updateIntervalMs?: number;

  /** Width of the visualization */
  width?: number;

  /** Height of the visualization */
  height?: number;

  /** Whether to show detailed metrics */
  showDetailedMetrics?: boolean;

  /** Whether to show the memory chart */
  showMemoryChart?: boolean;

  /** Whether to show leak detection information */
  showLeakDetection?: boolean;

  /** Whether to display session markers */
  showSessionMarkers?: boolean;

  /** Callback when a leak is detected */
  onLeakDetected?: (analysis: MemoryTrendAnalysis) => void;
}

/**
 * Component for visualizing long session memory tracking data
 */
const LongSessionMemoryVisualizer: React.FC<LongSessionMemoryVisualizerProps> = ({
  memoryTracker,
  snapshots: propSnapshots,
  analysis: propAnalysis,
  autoUpdate = true,
  updateIntervalMs = 5000,
  width = 800,
  height = 400,
  showDetailedMetrics = true,
  showMemoryChart = true,
  showLeakDetection = true,
  showSessionMarkers = true,
  onLeakDetected,
}) => {
  // State for snapshots and analysis
  const [snapshots, setSnapshots] = useState<MemorySnapshot[]>([]);
  const [analysis, setAnalysis] = useState<MemoryTrendAnalysis | null>(null);
  const [sessionMarkers, setSessionMarkers] = useState<
    Array<{ timestamp: number; name: string; metadata?: Record<string, unknown> }>
  >([]);
  const [updateInterval, setUpdateInterval] = useState<number | null>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [selectedSnapshot, setSelectedSnapshot] = useState<MemorySnapshot | null>(null);
  const [timeRange, setTimeRange] = useState<'all' | 'hour' | 'day'>('all');

  /**
   * Convert time range string to milliseconds
   */
  const timeRangeToMs = (range: string): number => {
    switch (range) {
      case 'hour':
        return 60 * 60 * 1000;
      case 'day':
        return 24 * 60 * 60 * 1000;
      case 'week':
        return 7 * 24 * 60 * 60 * 1000;
      case 'month':
        return 30 * 24 * 60 * 60 * 1000;
      default:
        return 24 * 60 * 60 * 1000; // Default to a day
    }
  };

  // Set up auto-updating if enabled
  useEffect(() => {
    if (autoUpdate && memoryTracker) {
      const intervalId = window.setInterval(() => {
        updateVisualization();
      }, updateIntervalMs);

      setUpdateInterval(intervalId);

      return () => {
        if (updateInterval !== null) {
          clearInterval(updateInterval);
        }
      };
    }
  }, [autoUpdate, memoryTracker, updateIntervalMs]);

  // Initialize with data from props or tracker
  useEffect(() => {
    updateVisualization();
  }, [memoryTracker, propSnapshots, propAnalysis]);

  // Render chart whenever snapshots or canvas changes
  useEffect(() => {
    if (showMemoryChart) {
      renderMemoryChart();
    }
  }, [snapshots, canvasRef.current, showMemoryChart, timeRange, sessionMarkers]);

  // Check for leak detection
  useEffect(() => {
    if (analysis && analysis.suspectedLeak && onLeakDetected) {
      onLeakDetected(analysis);
    }
  }, [analysis, onLeakDetected]);

  /**
   * Update the visualization with current data
   */
  const updateVisualization = () => {
    if (memoryTracker) {
      // Get data from the tracker
      setSnapshots(memoryTracker.getSnapshots());
      setAnalysis(memoryTracker.getLatestAnalysis());
      setSessionMarkers(memoryTracker.getSessionMarkers());
    } else if (propSnapshots) {
      // Use data from props
      setSnapshots(propSnapshots);
      setAnalysis(propAnalysis || null);
      setSessionMarkers([]);
    }
  };

  /**
   * Render the memory usage chart
   */
  const renderMemoryChart = () => {
    const canvas = canvasRef.current;
    if (!canvas || snapshots.length < 2) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Filter snapshots based on time range
    let filteredSnapshots = [...snapshots];
    if (timeRange !== 'all') {
      const now = Date.now();
      const rangeMs = timeRangeToMs(timeRange);
      filteredSnapshots = snapshots.filter(s => s.timestamp >= now - rangeMs);
    }

    if (filteredSnapshots.length < 2) {
      // Not enough data to render
      ctx.font = '14px Arial';
      ctx.fillStyle = '#666';
      ctx.textAlign = 'center';
      ctx.fillText('Not enough data to display chart', canvas.width / 2, canvas.height / 2);
      return;
    }

    // Calculate chart dimensions
    const padding = { top: 30, right: 30, bottom: 50, left: 60 };
    const chartWidth = canvas.width - padding.left - padding.right;
    const chartHeight = canvas.height - padding.top - padding.bottom;

    // Calculate min/max values
    const memoryValues = filteredSnapshots.map(s => s.usedHeapSizeMB);
    const minMemory = Math.floor(Math.min(...memoryValues) * 0.9);
    const maxMemory = Math.ceil(Math.max(...memoryValues) * 1.1);

    // Calculate time range
    const startTime = filteredSnapshots[0].timestamp;
    const endTime = filteredSnapshots[filteredSnapshots.length - 1].timestamp;

    // Draw axes
    drawAxes(ctx, padding, chartWidth, chartHeight, minMemory, maxMemory, startTime, endTime);

    // Draw data points
    drawDataPoints(
      ctx,
      filteredSnapshots,
      padding,
      chartWidth,
      chartHeight,
      minMemory,
      maxMemory,
      startTime,
      endTime
    );

    // Draw trend line if we have analysis
    if (analysis) {
      drawTrendLine(
        ctx,
        padding,
        chartWidth,
        chartHeight,
        minMemory,
        maxMemory,
        startTime,
        endTime,
        analysis
      );
    }

    // Draw session markers if enabled
    if (showSessionMarkers && sessionMarkers.length > 0) {
      drawSessionMarkers(ctx, sessionMarkers, padding, chartWidth, chartHeight, startTime, endTime);
    }
  };

  /**
   * Draw chart axes
   */
  const drawAxes = (
    ctx: CanvasRenderingContext2D,
    padding: { top: number; right: number; bottom: number; left: number },
    chartWidth: number,
    chartHeight: number,
    minMemory: number,
    maxMemory: number,
    startTime: number,
    endTime: number
  ) => {
    ctx.strokeStyle = '#333';
    ctx.lineWidth = 1;
    ctx.beginPath();

    // Y-axis
    ctx.moveTo(padding.left, padding.top);
    ctx.lineTo(padding.left, padding.top + chartHeight);

    // X-axis
    ctx.moveTo(padding.left, padding.top + chartHeight);
    ctx.lineTo(padding.left + chartWidth, padding.top + chartHeight);

    ctx.stroke();

    // Y-axis labels
    ctx.textAlign = 'right';
    ctx.textBaseline = 'middle';
    ctx.fillStyle = '#333';

    const yTickCount = 5;
    for (let i = 0; i <= yTickCount; i++) {
      const value = minMemory + ((maxMemory - minMemory) * (yTickCount - i)) / yTickCount;
      const y = padding.top + (i * chartHeight) / yTickCount;

      ctx.beginPath();
      ctx.moveTo(padding.left - 5, y);
      ctx.lineTo(padding.left, y);
      ctx.stroke();

      ctx.fillText(`${value.toFixed(1)} MB`, padding.left - 10, y);
    }

    // X-axis labels
    ctx.textAlign = 'center';
    ctx.textBaseline = 'top';

    const xTickCount = 6;
    for (let i = 0; i <= xTickCount; i++) {
      const timestamp = startTime + ((endTime - startTime) * i) / xTickCount;
      const x = padding.left + (i * chartWidth) / xTickCount;

      ctx.beginPath();
      ctx.moveTo(x, padding.top + chartHeight);
      ctx.lineTo(x, padding.top + chartHeight + 5);
      ctx.stroke();

      // Format time label based on range
      let timeLabel;
      if (endTime - startTime > 24 * 60 * 60 * 1000) {
        // More than a day - show date
        timeLabel = new Date(timestamp).toLocaleDateString();
      } else if (endTime - startTime > 60 * 60 * 1000) {
        // More than an hour - show hours
        timeLabel = new Date(timestamp).toLocaleTimeString([], {
          hour: '2-digit',
          minute: '2-digit',
        });
      } else {
        // Less than an hour - show minutes:seconds
        const date = new Date(timestamp);
        timeLabel = `${date.getMinutes()}:${date.getSeconds().toString().padStart(2, '0')}`;
      }

      ctx.fillText(timeLabel, x, padding.top + chartHeight + 10);
    }

    // Chart title
    ctx.textAlign = 'center';
    ctx.textBaseline = 'top';
    ctx.font = 'bold 14px Arial';
    ctx.fillText('Memory Usage Over Time', padding.left + chartWidth / 2, 10);

    // Y-axis title
    ctx.save();
    ctx.translate(20, padding.top + chartHeight / 2);
    ctx.rotate(-Math.PI / 2);
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText('Memory Usage (MB)', 0, 0);
    ctx.restore();

    // X-axis title
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    const canvasElement = canvasRef.current;
    if (canvasElement) {
      ctx.fillText('Time', padding.left + chartWidth / 2, canvasElement.height - 15);
    }
  };

  /**
   * Draw memory data points
   */
  const drawDataPoints = (
    ctx: CanvasRenderingContext2D,
    snapshots: MemorySnapshot[],
    padding: { top: number; right: number; bottom: number; left: number },
    chartWidth: number,
    chartHeight: number,
    minMemory: number,
    maxMemory: number,
    startTime: number,
    endTime: number
  ) => {
    if (snapshots.length < 2) return;

    // Draw line connecting points
    ctx.beginPath();
    ctx.strokeStyle = '#4285f4';
    ctx.lineWidth = 2;

    snapshots.forEach((snapshot, index) => {
      const x =
        padding.left + ((snapshot.timestamp - startTime) / (endTime - startTime)) * chartWidth;
      const y =
        padding.top +
        chartHeight -
        ((snapshot.usedHeapSizeMB - minMemory) / (maxMemory - minMemory)) * chartHeight;

      if (index === 0) {
        ctx.moveTo(x, y);
      } else {
        ctx.lineTo(x, y);
      }
    });

    ctx.stroke();

    // Draw individual points
    snapshots.forEach(snapshot => {
      const x =
        padding.left + ((snapshot.timestamp - startTime) / (endTime - startTime)) * chartWidth;
      const y =
        padding.top +
        chartHeight -
        ((snapshot.usedHeapSizeMB - minMemory) / (maxMemory - minMemory)) * chartHeight;

      ctx.beginPath();
      ctx.fillStyle = '#4285f4';
      ctx.arc(x, y, 3, 0, 2 * Math.PI);
      ctx.fill();
    });
  };

  /**
   * Draw the trend line based on analysis
   */
  const drawTrendLine = (
    ctx: CanvasRenderingContext2D,
    padding: { top: number; right: number; bottom: number; left: number },
    chartWidth: number,
    chartHeight: number,
    minMemory: number,
    maxMemory: number,
    startTime: number,
    endTime: number,
    analysis: MemoryTrendAnalysis
  ) => {
    // Get first and last snapshot values based on analysis
    const firstSnapshot = snapshots[0];
    const lastSnapshot = snapshots[snapshots.length - 1];

    if (!firstSnapshot || !lastSnapshot) return;

    // Calculate trend line start and end points using startTime and endTime
    const timeRange = endTime - startTime;

    // Calculate x coordinates based on timestamps
    const x1 = padding.left + ((firstSnapshot.timestamp - startTime) / timeRange) * chartWidth;
    const x2 = padding.left + ((lastSnapshot.timestamp - startTime) / timeRange) * chartWidth;

    // Calculate trend line
    ctx.beginPath();
    ctx.strokeStyle = analysis.suspectedLeak ? '#d93025' : '#fbbc04';
    ctx.lineWidth = 2;
    ctx.setLineDash([5, 3]);
    ctx.moveTo(
      x1,
      padding.top +
        chartHeight -
        ((firstSnapshot.usedHeapSizeMB - minMemory) / (maxMemory - minMemory)) * chartHeight
    );
    ctx.lineTo(
      x2,
      padding.top +
        chartHeight -
        ((lastSnapshot.usedHeapSizeMB - minMemory) / (maxMemory - minMemory)) * chartHeight
    );
    ctx.stroke();
    ctx.setLineDash([]);

    // Add trend label
    ctx.textAlign = 'right';
    ctx.textBaseline = 'bottom';
    ctx.fillStyle = analysis.suspectedLeak ? '#d93025' : '#fbbc04';
    ctx.font = 'bold 12px Arial';

    let trendLabel = `Trend: ${analysis.growthRatePerMinute.toFixed(2)} MB/min`;
    if (analysis.suspectedLeak) {
      trendLabel += ` (Leak Severity: ${analysis.leakSeverity}/5)`;
    }

    ctx.fillText(trendLabel, padding.left + chartWidth - 10, padding.top + 15);
  };

  /**
   * Draw session markers on the chart
   */
  const drawSessionMarkers = (
    ctx: CanvasRenderingContext2D,
    markers: Array<{ timestamp: number; name: string; metadata?: Record<string, unknown> }>,
    padding: { top: number; right: number; bottom: number; left: number },
    chartWidth: number,
    chartHeight: number,
    startTime: number,
    endTime: number
  ) => {
    markers.forEach(marker => {
      // Only draw markers within the visible time range
      if (marker.timestamp < startTime || marker.timestamp > endTime) return;

      const x =
        padding.left + ((marker.timestamp - startTime) / (endTime - startTime)) * chartWidth;

      // Draw marker line
      ctx.beginPath();
      ctx.strokeStyle = getMarkerColor(marker.name);
      ctx.lineWidth = 1;
      ctx.setLineDash([2, 2]);
      ctx.moveTo(x, padding.top);
      ctx.lineTo(x, padding.top + chartHeight);
      ctx.stroke();
      ctx.setLineDash([]);

      // Draw marker label
      ctx.save();
      ctx.translate(x, padding.top + 5);
      ctx.rotate(Math.PI / 4);
      ctx.textAlign = 'left';
      ctx.textBaseline = 'top';
      ctx.fillStyle = getMarkerColor(marker.name);
      ctx.font = '10px Arial';
      ctx.fillText(formatMarkerName(marker.name), 0, 0);
      ctx.restore();
    });
  };

  /**
   * Get color for a marker based on its name
   */
  const getMarkerColor = (markerName: string): string => {
    switch (markerName) {
      case 'tracking_started':
        return '#34a853'; // Green
      case 'tracking_stopped':
        return '#fbbc04'; // Yellow
      case 'leak_detected':
        return '#d93025'; // Red
      default:
        return '#4285f4'; // Blue
    }
  };

  /**
   * Format marker name for display
   */
  const formatMarkerName = (name: string): string => {
    return name.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  /**
   * Draw a message when no data is available
   */
  const drawNoDataMessage = (ctx: CanvasRenderingContext2D) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillStyle = '#666';
    ctx.font = '14px Arial';
    ctx.fillText('Not enough data to display chart', canvas.width / 2, canvas.height / 2);
  };

  /**
   * Format a duration in milliseconds to a human-readable string
   */
  const formatDuration = (ms: number): string => {
    const seconds = Math.floor(ms / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    if (days > 0) {
      return `${days}d ${hours % 24}h`;
    } else if (hours > 0) {
      return `${hours}h ${minutes % 60}m`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds % 60}s`;
    } else {
      return `${seconds}s`;
    }
  };

  /**
   * Handle canvas click to select a data point
   */
  const handleCanvasClick = (e: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas || snapshots.length < 2) return;

    // Get click coordinates relative to canvas
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;

    // Calculate chart dimensions
    const padding = { top: 30, right: 30, bottom: 50, left: 60 };
    const chartWidth = canvas.width - padding.left - padding.right;

    // Only handle clicks within the chart area
    if (x < padding.left || x > padding.left + chartWidth) return;

    // Get time range
    const startTime = snapshots[0].timestamp;
    const endTime = snapshots[snapshots.length - 1].timestamp;

    // Convert x position to timestamp
    const clickTime = startTime + ((x - padding.left) / chartWidth) * (endTime - startTime);

    // Find closest snapshot
    let closestSnapshot = snapshots[0];
    let minTimeDiff = Math.abs(clickTime - closestSnapshot.timestamp);

    for (const snapshot of snapshots) {
      const timeDiff = Math.abs(clickTime - snapshot.timestamp);
      if (timeDiff < minTimeDiff) {
        minTimeDiff = timeDiff;
        closestSnapshot = snapshot;
      }
    }

    setSelectedSnapshot(closestSnapshot);
  };

  return (
    <div className="long-session-memory-visualizer" style={{ width, maxWidth: '100%' }}>
      {/* Controls */}
      <div className="controls">
        <div className="time-range-selector">
          <span>Time Range:</span>
          <button
            className={timeRange === 'all' ? 'active' : ''}
            onClick={() => setTimeRange('all')}
          >
            All
          </button>
          <button
            className={timeRange === 'day' ? 'active' : ''}
            onClick={() => setTimeRange('day')}
          >
            Day
          </button>
          <button
            className={timeRange === 'hour' ? 'active' : ''}
            onClick={() => setTimeRange('hour')}
          >
            Hour
          </button>
        </div>
      </div>

      {/* Memory usage chart */}
      {showMemoryChart && (
        <div className="memory-chart">
          <canvas
            ref={canvasRef}
            width={width}
            height={height}
            onClick={handleCanvasClick}
            style={{ cursor: 'pointer' }}
          ></canvas>
        </div>
      )}

      {/* Selected snapshot details */}
      {selectedSnapshot && (
        <div className="snapshot-details">
          <h4>Snapshot Details</h4>
          <p>Time: {new Date(selectedSnapshot.timestamp).toLocaleString()}</p>
          <p>Memory Usage: {selectedSnapshot.usedHeapSizeMB.toFixed(2)} MB</p>
          <p>Heap Size: {selectedSnapshot.totalHeapSizeMB.toFixed(2)} MB</p>
          {selectedSnapshot.domNodeCount && <p>DOM Nodes: {selectedSnapshot.domNodeCount}</p>}
        </div>
      )}

      {/* Memory analysis */}
      {showLeakDetection && analysis && (
        <div className={`memory-analysis ${analysis.suspectedLeak ? 'leak-detected' : ''}`}>
          <h4>Memory Analysis</h4>

          <div className="analysis-metrics">
            <div className="metric">
              <div className="metric-label">Growth Rate</div>
              <div className="metric-value">{analysis.growthRatePerMinute.toFixed(2)} MB/min</div>
            </div>

            <div className="metric">
              <div className="metric-label">Hourly Growth</div>
              <div className="metric-value">{analysis.growthRatePerHour.toFixed(2)} MB/hour</div>
            </div>

            <div className="metric">
              <div className="metric-label">Confidence</div>
              <div className="metric-value">{(analysis.confidence * 100).toFixed(0)}%</div>
            </div>

            {analysis.estimatedTimeToLimit < Number.POSITIVE_INFINITY && (
              <div className="metric">
                <div className="metric-label">Time to Limit</div>
                <div className="metric-value">{formatDuration(analysis.estimatedTimeToLimit)}</div>
              </div>
            )}
          </div>

          {analysis.suspectedLeak && (
            <div className="leak-warning">
              <h5>Memory Leak Detected!</h5>
              <p>Severity: {analysis.leakSeverity} / 5</p>
              <p>Memory is growing at a rate that indicates a probable leak.</p>
              {analysis.isAccelerating && (
                <p>
                  <strong>Warning:</strong> Growth rate is accelerating!
                </p>
              )}
            </div>
          )}
        </div>
      )}

      {/* Detailed metrics */}
      {showDetailedMetrics && snapshots.length > 0 && (
        <div className="detailed-metrics">
          <h4>Detailed Metrics</h4>

          <div className="metrics-grid">
            <div className="metric">
              <div className="metric-label">Session Duration</div>
              <div className="metric-value">
                {formatDuration(snapshots[snapshots.length - 1].timestamp - snapshots[0].timestamp)}
              </div>
            </div>

            <div className="metric">
              <div className="metric-label">Snapshots</div>
              <div className="metric-value">{snapshots.length}</div>
            </div>

            <div className="metric">
              <div className="metric-label">Current Memory</div>
              <div className="metric-value">
                {snapshots[snapshots.length - 1].usedHeapSizeMB.toFixed(2)} MB
              </div>
            </div>

            <div className="metric">
              <div className="metric-label">Memory Change</div>
              <div className="metric-value">
                {(
                  snapshots[snapshots.length - 1].usedHeapSizeMB - snapshots[0].usedHeapSizeMB
                ).toFixed(2)}{' '}
                MB
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Session markers list */}
      {showSessionMarkers && sessionMarkers.length > 0 && (
        <div className="session-markers">
          <h4>Session Events</h4>
          <ul>
            {sessionMarkers.map(
              (
                marker: { timestamp: number; name: string; metadata?: Record<string, unknown> },
                index: number
              ) => (
                <li key={index} className={`marker-${marker.name}`}>
                  <span className="marker-time">
                    {new Date(marker.timestamp).toLocaleTimeString()}
                  </span>
                  <span className="marker-name">{formatMarkerName(marker.name)}</span>
                  {marker.name === 'leak_detected' && marker.metadata && (
                    <span className="marker-details">
                      (Growth: {(marker.metadata.growthRatePerMinute as number).toFixed(2)} MB/min,
                      Severity: {String(marker.metadata.severity)})
                    </span>
                  )}
                </li>
              )
            )}
          </ul>
        </div>
      )}

