  // Define menu items with actions
  const getMenuItems = () => {
    return {
      mining: [
        {
          id: 'mineral-processing',
          name: 'Mineral Processing',
          description: 'Process raw minerals and manage resource extraction',
          moduleType: 'mineral' as ModuleType,
          cost: {
            minerals: 500,
            energy: 300,
