import React, { useState } from 'react';
import { useChartCoordination } from '../../../hooks/visualization/useChartCoordination';
import {
  BrushState,
  HighlightState,
  ViewportState,
} from '../../../lib/visualization/ChartCoordinationManager';

interface ChartCoordinationDemoProps {
  width?: number;
  height?: number;
  className?: string;
}

/**
 * ChartCoordinationDemo
 *
 * A demo component that shows how multiple charts can be coordinated for:
 * - Synchronized zooming/panning
 * - Linked brushing
 * - Synchronized highlighting
 * - Shared color scales
 */
export const ChartCoordinationDemo: React.FC<ChartCoordinationDemoProps> = ({
  width = 1200,
  height = 800,
  className = '',
}) => {
  // Sample data
  const data = React.useMemo(() => {
    const points = [];
    for (let i = 0; i < 100; i++) {
      points.push({
        id: `point-${i}`,
        x: Math.random() * 100,
        y: Math.random() * 100,
        category: Math.random() > 0.5 ? 'A' : 'B',
        value: Math.random() * 100,
      });
    }
    return points;
  }, []);

  // Chart states
  const [scatterViewport, setScatterViewport] = useState<ViewportState>({
    scale: 1,
    translateX: 0,
    translateY: 0,
  });
  const [scatterBrush, setScatterBrush] = useState<BrushState>({
    active: false,
    x1: 0,
    y1: 0,
    x2: 0,
    y2: 0,
  });
  const [scatterHighlight, setScatterHighlight] = useState<HighlightState>({
    active: false,
    dataIds: [],
  });

  const [barViewport, setBarViewport] = useState<ViewportState>({
    scale: 1,
    translateX: 0,
    translateY: 0,
  });
  const [barHighlight, setBarHighlight] = useState<HighlightState>({
    active: false,
    dataIds: [],
  });

  // Set up chart coordination
  const scatterCoordination = useChartCoordination({
    chartId: 'scatter-plot',
    groupId: 'demo-group',
    initialState: {
      viewport: scatterViewport,
      brush: scatterBrush,
      highlight: scatterHighlight,
    },
    onViewportChange: setScatterViewport,
    onBrushChange: setScatterBrush,
    onHighlightChange: setScatterHighlight,
  });

  const barCoordination = useChartCoordination({
    chartId: 'bar-chart',
    groupId: 'demo-group',
    initialState: {
      viewport: barViewport,
      highlight: barHighlight,
    },
    onViewportChange: setBarViewport,
    onHighlightChange: setBarHighlight,
  });

  // Handle interactions
  const handleScatterZoom = (scale: number, translateX: number, translateY: number) => {
    scatterCoordination.updateViewport({ scale, translateX, translateY });
  };

  const handleScatterBrush = (x1: number, y1: number, x2: number, y2: number) => {
    // Find points within brush
    const selectedPoints = data.filter(
      point =>
        point.x >= Math.min(x1, x2) &&
        point.x <= Math.max(x1, x2) &&
        point.y >= Math.min(y1, y2) &&
        point.y <= Math.max(y1, y2)
    );

    scatterCoordination.updateBrush({
      active: true,
      x1,
      y1,
      x2,
      y2,
    });

    scatterCoordination.updateHighlight({
      active: true,
      dataIds: selectedPoints.map(p => p.id),
    });
  };

  const handleBarHighlight = (category: string) => {
    const selectedPoints = data.filter(point => point.category === category);

    barCoordination.updateHighlight({
      active: true,
      dataIds: selectedPoints.map(p => p.id),
      category,
    });
  };

  return (
    <div className={`chart-coordination-demo ${className}`}>
      <div className="flex flex-col gap-4">
        <div className="flex gap-4">
          {/* Scatter Plot */}
          <div className="flex-1 rounded-lg border p-4 shadow-sm">
            <h3 className="mb-2 text-lg font-bold">Scatter Plot</h3>
            <div
              className="relative"
              style={{
                width: width / 2 - 32,
                height: height - 200,
                transform: `scale(${scatterViewport.scale}) translate(${scatterViewport.translateX}px, ${scatterViewport.translateY}px)`,
              }}
            >
              {/* Plot points */}
              {data.map(point => (
                <div
                  key={point.id}
                  className={`absolute h-3 w-3 rounded-full transition-opacity ${
                    scatterHighlight.active && !scatterHighlight.dataIds.includes(point.id)
                      ? 'opacity-20'
                      : 'opacity-100'
                  }`}
                  style={{
                    left: `${point.x}%`,
                    top: `${point.y}%`,
                    backgroundColor: point.category === 'A' ? '#3B82F6' : '#EF4444',
                    transform: 'translate(-50%, -50%)',
                  }}
                />
              ))}

              {/* Brush overlay */}
              {scatterBrush.active && (
                <div
                  className="absolute border-2 border-blue-500 bg-blue-500/10"
                  style={{
                    left: `${Math.min(scatterBrush.x1, scatterBrush.x2)}%`,
                    top: `${Math.min(scatterBrush.y1, scatterBrush.y2)}%`,
                    width: `${Math.abs(scatterBrush.x2 - scatterBrush.x1)}%`,
                    height: `${Math.abs(scatterBrush.y2 - scatterBrush.y1)}%`,
                  }}
                />
              )}
            </div>
          </div>

          {/* Bar Chart */}
          <div className="flex-1 rounded-lg border p-4 shadow-sm">
            <h3 className="mb-2 text-lg font-bold">Bar Chart</h3>
            <div
              className="relative"
              style={{
                width: width / 2 - 32,
                height: height - 200,
                transform: `scale(${barViewport.scale}) translate(${barViewport.translateX}px, ${barViewport.translateY}px)`,
              }}
            >
              {/* Category bars */}
              {['A', 'B'].map(category => {
                const categoryPoints = data.filter(p => p.category === category);
                const average =
                  categoryPoints.reduce((sum, p) => sum + p.value, 0) / categoryPoints.length;

                return (
                  <div
                    key={category}
                    className={`absolute bottom-0 w-32 transition-opacity ${
                      barHighlight.active && barHighlight.category !== category
                        ? 'opacity-20'
                        : 'opacity-100'
                    }`}
                    style={{
                      left: category === 'A' ? '30%' : '60%',
                      height: `${average}%`,
                      backgroundColor: category === 'A' ? '#3B82F6' : '#EF4444',
                    }}
                    onClick={() => handleBarHighlight(category)}
                  >
                    <div className="absolute -top-6 w-full text-center">{category}</div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>

        <div className="rounded-lg border p-4 shadow-sm">
          <h3 className="mb-2 text-lg font-bold">Controls</h3>
          <div className="flex gap-4">
            <div>
              <label className="block font-medium">Zoom</label>
              <input
                type="range"
                min="0.5"
                max="2"
                step="0.1"
                value={scatterViewport.scale}
                onChange={e => handleScatterZoom(parseFloat(e.target.value), 0, 0)}
                className="w-48"
              />
            </div>
            <button
              className="rounded bg-blue-500 px-4 py-2 text-white"
              onClick={() => {
                scatterCoordination.updateBrush({
                  active: false,
                  x1: 0,
                  y1: 0,
                  x2: 0,
                  y2: 0,
                });
                scatterCoordination.updateHighlight({
                  active: false,
                  dataIds: [],
                });
                barCoordination.updateHighlight({
                  active: false,
                  dataIds: [],
                });
              }}
            >
              Reset Selection
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChartCoordinationDemo;

import React, { useEffect, useMemo, useRef, useState } from 'react';
import { useComponentLifecycle } from '../../../hooks/ui/useComponentLifecycle';
import { useComponentRegistration } from '../../../hooks/ui/useComponentRegistration';
import {
  DataVisualizationShaderConfig,
  DataVisualizationShaderType,
  ShaderUniform,
  WebGLShaderManager,
} from '../../../lib/optimization/WebGLShaderManager';
import { Position } from '../../../types/core/Position';

export interface CustomShaderDefinition {
  vertexShader?: string;
  fragmentShader?: string;
  uniforms?: Record<string, ShaderUniform>;
}

export interface DataPoint {
  x: number;
  y: number;
  value: number;
  [key: string]: unknown;
}

export interface CustomShaderVisualizationProps {
  /**
   * Data points to visualize
   */
  data: DataPoint[];

  /**
   * Width of the visualization
   */
  width: number;

  /**
   * Height of the visualization
   */
  height: number;

  /**
   * Custom shader definition
   */
  shaderDefinition: CustomShaderDefinition;

  /**
   * Base visualization type to extend
   */
  baseVisualizationType?: DataVisualizationShaderType;

  /**
   * Whether to animate the visualization
   */
  animate?: boolean;

  /**
   * Animation speed (1.0 is default)
   */
  animationSpeed?: number;

  /**
   * Visual intensity (1.0 is default)
   */
  intensity?: number;

  /**
   * Optional CSS class name
   */
  className?: string;

  /**
   * Callback when a data point is clicked
   */
  onDataPointClick?: (dataPoint: DataPoint, index: number) => void;
}

/**
 * CustomShaderVisualization Component
 *
 * A component that allows users to define custom WebGL shaders for data visualization.
 * Extends the base WebGLShaderManager with user-defined shader effects.
 */
export const CustomShaderVisualization: React.FC<CustomShaderVisualizationProps> = ({
  data,
  width,
  height,
  shaderDefinition,
  baseVisualizationType = DataVisualizationShaderType.CUSTOM,
  animate = true,
  animationSpeed = 1.0,
  intensity = 1.0,
  className = '',
  onDataPointClick,
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const shaderManagerRef = useRef<WebGLShaderManager | null>(null);
  const [isInitialized, setIsInitialized] = useState(false);

  // Register with component registry
  useComponentRegistration({
    type: 'CustomShaderVisualization',
    eventSubscriptions: ['RESOURCE_UPDATED', 'RESOURCE_FLOW_UPDATED'],
    updatePriority: 'high',
  });

  // Prepare data for WebGL rendering
  const { positions, dataValues, dataRange } = useMemo(() => {
    const pos: Position[] = [];
    const values = new Float32Array(data.length);
    let min = Infinity;
    let max = -Infinity;

    data.forEach((point, index) => {
      pos.push({ x: point.x, y: point.y });
      values[index] = point.value;

      min = Math.min(min, point.value);
      max = Math.max(max, point.value);
    });

    return {
      positions: pos,
      dataValues: values,
      dataRange: [min, max] as [number, number],
    };
  }, [data]);

  // Initialize WebGL shader manager
  useEffect(() => {
    if (!canvasRef.current) return;

    const manager = new WebGLShaderManager();
    const success = manager.initialize(canvasRef.current);

    if (success) {
      shaderManagerRef.current = manager;
      setIsInitialized(true);
    }

    return () => {
      if (shaderManagerRef.current) {
        shaderManagerRef.current.dispose();
        shaderManagerRef.current = null;
      }
    };
  }, []);

  // Handle component lifecycle
  useComponentLifecycle({
    onMount: () => {
      console.log('CustomShaderVisualization mounted');
    },
    onUnmount: () => {
      console.log('CustomShaderVisualization unmounted');
    },
  });

  // Create shader configuration
  const shaderConfig = useMemo((): DataVisualizationShaderConfig => {
    return {
      type: baseVisualizationType,
      colors: ['#3366cc', '#dc3912', '#ff9900', '#109618', '#990099'],
      intensity,
      resolution: [width, height],
      animate,
      animationSpeed,
      dataRange,
      customUniforms: shaderDefinition.uniforms,
      customVertexShader: shaderDefinition.vertexShader,
      customFragmentShader: shaderDefinition.fragmentShader,
    };
  }, [
    baseVisualizationType,
    intensity,
    width,
    height,
    animate,
    animationSpeed,
    dataRange,
    shaderDefinition,
  ]);

  // Handle animation and rendering
  useEffect(() => {
    if (!isInitialized || !shaderManagerRef.current) return;

    const manager = shaderManagerRef.current;

    // Render function
    const renderFrame = () => {
      manager.renderDataVisualization(shaderConfig, dataValues, positions, width, height);
    };

    // Single render if not animating
    if (!animate) {
      renderFrame();
      return;
    }

    // Start animation loop
    manager.startAnimationLoop(renderFrame);

    return () => {
      manager.stopAnimationLoop();
    };
  }, [isInitialized, shaderConfig, dataValues, positions, width, height, animate]);

  // Handle canvas clicks
  const handleCanvasClick = (e: React.MouseEvent<HTMLCanvasElement>) => {
    if (!onDataPointClick || !containerRef.current) return;

    const rect = containerRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Find nearest data point
    let nearestIndex = -1;
    let nearestDistance = Infinity;

    data.forEach((point, index) => {
      const dx = point.x - x;
      const dy = point.y - y;
      const distance = Math.sqrt(dx * dx + dy * dy);

      if (distance < nearestDistance && distance < 20) {
        nearestDistance = distance;
        nearestIndex = index;
      }
    });

    if (nearestIndex !== -1) {
      onDataPointClick(data[nearestIndex], nearestIndex);
    }
  };

  return (
    <div
      ref={containerRef}
      className={`custom-shader-visualization ${className}`}
      style={{ width, height, position: 'relative' }}
    >
      <canvas
        ref={canvasRef}
        width={width}
        height={height}
        onClick={handleCanvasClick}
        style={{
          display: 'block',
          width: '100%',
          height: '100%',
        }}
      />
    </div>
  );
};

export default CustomShaderVisualization;

import React, { useCallback, useMemo, useState } from 'react';
import { DataVisualizationShaderType } from '../../../lib/optimization/WebGLShaderManager';
import {
  DataHighlightVisualization,
  DataPoint,
  DataVisualizationPresets,
} from './DataHighlightVisualization';

export interface DataHighlightDemoProps {
  /**
   * Width of the demo container
   */
  width?: number;

  /**
   * Height of the demo container
   */
  height?: number;

  /**
   * Optional data to use for visualization
   * If not provided, sample data will be generated
   */
  data?: DataPoint[];

  /**
   * Whether to show interactive controls
   */
  showControls?: boolean;

  /**
   * Default visualization type
   */
  defaultVisualizationType?: DataVisualizationShaderType;

  /**
   * Initial highlight range
   */
  initialHighlightRange?: [number, number];

  /**
   * Optional class name for the container
   */
  className?: string;
}

/**
 * DataHighlightDemo
 *
 * A demo component that showcases different WebGL-based data visualization techniques
 * with interactive controls for adjusting visualization parameters.
 */
export const DataHighlightDemo: React.FC<DataHighlightDemoProps> = ({
  width = 800,
  height = 600,
  data: providedData,
  showControls = true,
  defaultVisualizationType = DataVisualizationShaderType.HIGHLIGHT,
  initialHighlightRange = [0.7, 1.0],
  className = '',
}) => {
  // Generate sample data if none provided
  const sampleData = useMemo(() => {
    if (providedData) return providedData;

    // Generate sample data points in a grid pattern
    const data: DataPoint[] = [];
    const gridSize = 30;
    const stepX = width / gridSize;
    const stepY = height / gridSize;

    for (let x = 0; x < gridSize; x++) {
      for (let y = 0; y < gridSize; y++) {
        const posX = x * stepX + stepX / 2;
        const posY = y * stepY + stepY / 2;

        // Create various patterns based on position
        const centerX = width / 2;
        const centerY = height / 2;
        const distToCenter = Math.sqrt(Math.pow(posX - centerX, 2) + Math.pow(posY - centerY, 2));

        // Generate different patterns
        const radialValue = 1 - Math.min(1, distToCenter / (Math.min(width, height) / 2));
        const waveValue = Math.sin(x / 3) * Math.cos(y / 2) * 0.5 + 0.5;
        const gridValue = x % 5 === 0 || y % 5 === 0 ? 0.9 : 0.3;

        // Combine patterns
        const combinedValue = (radialValue * 0.5 + waveValue * 0.5) * (1 - gridValue * 0.3);

        data.push({
          x: posX,
          y: posY,
          value: combinedValue,
          pattern: {
            radial: radialValue,
            wave: waveValue,
            grid: gridValue,
          },
        });
      }
    }

    return data;
  }, [providedData, width, height]);

  // State for visualization parameters
  const [visualizationType, setVisualizationType] =
    useState<DataVisualizationShaderType>(defaultVisualizationType);
  const [highlightRange, setHighlightRange] = useState<[number, number]>(initialHighlightRange);
  const [intensity, setIntensity] = useState(1.0);
  const [animate, setAnimate] = useState(true);
  const [animationSpeed, setAnimationSpeed] = useState(1.0);
  const [showLegend, setShowLegend] = useState(true);
  const [selectedPoint, setSelectedPoint] = useState<DataPoint | null>(null);

  // Handle data point selection
  const handleDataPointClick = useCallback((dataPoint: DataPoint, index: number) => {
    console.log(`Selected data point at index ${index} with value ${dataPoint.value}`);
    setSelectedPoint(dataPoint);
  }, []);

  // Custom color schemes for different visualization types
  const colorSchemes = useMemo(
    () => ({
      [DataVisualizationShaderType.HEATMAP]: [
        '#000080',
        '#0000ff',
        '#00ffff',
        '#ffff00',
        '#ff0000',
      ],
      [DataVisualizationShaderType.CONTOUR]: [
        '#000000',
        '#333333',
        '#666666',
        '#999999',
        '#ffffff',
      ],
      [DataVisualizationShaderType.POINT_CLUSTER]: [
        '#003300',
        '#006600',
        '#009900',
        '#00cc00',
        '#00ff00',
      ],
      [DataVisualizationShaderType.HIGHLIGHT]: [
        '#333333',
        '#666666',
        '#999999',
        '#cccccc',
        '#ffffff',
      ],
      [DataVisualizationShaderType.DENSITY]: [
        '#000044',
        '#000088',
        '#0000ff',
        '#4444ff',
        '#8888ff',
      ],
      [DataVisualizationShaderType.FLOW]: ['#003366', '#0066cc', '#0099ff', '#66ccff', '#99ddff'],
      [DataVisualizationShaderType.TRANSITION]: [
        '#330033',
        '#660066',
        '#990099',
        '#cc00cc',
        '#ff00ff',
      ],
      [DataVisualizationShaderType.CUSTOM]: ['#444444', '#777777', '#aaaaaa', '#dddddd', '#ffffff'],
    }),
    []
  );

  // Apply the current preset
  const getVisualizationProps = useCallback(() => {
    const baseProps = {
      data: sampleData,
      width,
      height,
      visualizationType,
      highlightRange,
      intensity,
      animate,
      animationSpeed,
      showLegend,
      onDataPointClick: handleDataPointClick,
      colors: colorSchemes[visualizationType],
    };

    // Apply presets based on visualization type
    switch (visualizationType) {
      case DataVisualizationShaderType.HEATMAP:
        return DataVisualizationPresets.heatmap(baseProps);
      case DataVisualizationShaderType.DENSITY:
        return DataVisualizationPresets.density(baseProps);
      case DataVisualizationShaderType.HIGHLIGHT:
        return DataVisualizationPresets.highlight(baseProps);
      case DataVisualizationShaderType.FLOW:
        return DataVisualizationPresets.flow(baseProps);
      case DataVisualizationShaderType.CONTOUR:
        return DataVisualizationPresets.contour(baseProps);
      default:
        return baseProps;
    }
  }, [
    sampleData,
    width,
    height,
    visualizationType,
    highlightRange,
    intensity,
    animate,
    animationSpeed,
    showLegend,
    handleDataPointClick,
    colorSchemes,
  ]);

  // Handle range slider changes
  const handleHighlightMinChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = parseFloat(e.target.value);
    setHighlightRange(prev => [value, Math.max(value, prev[1])]);
  };

  const handleHighlightMaxChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = parseFloat(e.target.value);
    setHighlightRange(prev => [Math.min(value, prev[0]), value]);
  };

  return (
    <div className={`flex flex-col ${className}`}>
      <div className="relative">
        <DataHighlightVisualization {...getVisualizationProps()} />

        {/* Info panel for selected point */}
        {selectedPoint && (
          <div className="absolute left-2 top-2 rounded bg-white bg-opacity-90 p-2 text-sm shadow">
            <div className="mb-1 font-bold">Selected Point</div>
            <div>
              Position: ({selectedPoint.x.toFixed(0)}, {selectedPoint.y.toFixed(0)})
            </div>
            <div>Value: {selectedPoint.value.toFixed(3)}</div>
            <div
              className="mt-1 h-4 w-full rounded"
              style={{
                background: `linear-gradient(to right, ${colorSchemes[visualizationType].join(', ')})`,
              }}
            />
            <div className="flex justify-between text-xs">
              <span>0.0</span>
              <span>1.0</span>
            </div>
          </div>
        )}
      </div>

      {showControls && (
        <div className="mt-4 rounded-md bg-gray-100 p-4">
          <h3 className="mb-3 text-lg font-semibold">Visualization Controls</h3>

          <div className="grid grid-cols-1 gap-4 md:grid-cols-2">
            {/* Visualization Type */}
            <div>
              <label className="mb-1 block text-sm font-medium text-gray-700">
                Visualization Type
              </label>
              <select
                className="block w-full rounded-md border border-gray-300 bg-white px-3 py-2 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-indigo-500"
                value={visualizationType}
                onChange={e => setVisualizationType(e.target.value as DataVisualizationShaderType)}
              >
                {Object.values(DataVisualizationShaderType).map(type => (
                  <option key={type} value={type}>
                    {type.charAt(0).toUpperCase() + type.slice(1)}
                  </option>
                ))}
              </select>
            </div>

            {/* Intensity */}
            <div>
              <label className="mb-1 block text-sm font-medium text-gray-700">
                Intensity: {intensity.toFixed(1)}
              </label>
              <input
                type="range"
                min="0.1"
                max="2"
                step="0.1"
                value={intensity}
                onChange={e => setIntensity(parseFloat(e.target.value))}
                className="block w-full"
              />
            </div>

            {/* Highlight Range Min */}
            <div>
              <label className="mb-1 block text-sm font-medium text-gray-700">
                Highlight Min: {highlightRange[0].toFixed(2)}
              </label>
              <input
                type="range"
                min="0"
                max="1"
                step="0.01"
                value={highlightRange[0]}
                onChange={handleHighlightMinChange}
                className="block w-full"
              />
            </div>

            {/* Highlight Range Max */}
            <div>
              <label className="mb-1 block text-sm font-medium text-gray-700">
                Highlight Max: {highlightRange[1].toFixed(2)}
              </label>
              <input
                type="range"
                min="0"
                max="1"
                step="0.01"
                value={highlightRange[1]}
                onChange={handleHighlightMaxChange}
                className="block w-full"
              />
            </div>

            {/* Animation Controls */}
            <div>
              <div className="mb-2 flex items-center">
                <input
                  id="animate-toggle"
                  type="checkbox"
                  checked={animate}
                  onChange={e => setAnimate(e.target.checked)}
                  className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
                />
                <label htmlFor="animate-toggle" className="ml-2 block text-sm text-gray-700">
                  Enable Animation
                </label>
              </div>

              {animate && (
                <>
                  <label className="mb-1 block text-sm font-medium text-gray-700">
                    Animation Speed: {animationSpeed.toFixed(1)}x
                  </label>
                  <input
                    type="range"
                    min="0.1"
                    max="3"
                    step="0.1"
                    value={animationSpeed}
                    onChange={e => setAnimationSpeed(parseFloat(e.target.value))}
                    className="block w-full"
                  />
                </>
              )}
            </div>

            {/* Legend Toggle */}
            <div>
              <div className="flex items-center">
                <input
                  id="legend-toggle"
                  type="checkbox"
                  checked={showLegend}
                  onChange={e => setShowLegend(e.target.checked)}
                  className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
                />
                <label htmlFor="legend-toggle" className="ml-2 block text-sm text-gray-700">
                  Show Legend
                </label>
              </div>
            </div>
          </div>

          <div className="mt-4 rounded-md bg-blue-50 p-3 text-sm text-blue-800">
            <p className="font-medium">Tips:</p>
            <ul className="mt-1 list-inside list-disc">
              <li>Click on data points to see detailed information</li>
              <li>Try different visualization types for the same data</li>
              <li>Adjust the highlight range to emphasize different data ranges</li>
              <li>The heatmap visualization is best for showing density distribution</li>
              <li>Contour visualization highlights threshold boundaries</li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

import { useEffect, useRef, useState } from 'react';
import { useComponentLifecycle } from '../../../hooks/ui/useComponentLifecycle';
import { useComponentRegistration } from '../../../hooks/ui/useComponentRegistration';
import { ParticleSystemManager } from '../../../managers/effects/ParticleSystemManager';
import { Position } from '../../../types/core/Position';
import { ResourceType } from '../../../types/resources/StandardizedResourceTypes';

export interface DataPoint {
  id: string;
  position: Position;
  value: number;
  resourceType?: ResourceType;
  size?: number;
  opacity?: number;
}

interface DataTransitionConfig {
  sourceData: DataPoint[];
  targetData: DataPoint[];
  duration?: number;
  easing?: (t: number) => number;
  staggerDelay?: number;
  trailEffect?: boolean;
  blendMode?: 'normal' | 'additive';
  onTransitionComplete?: () => void;
  onTransitionProgress?: (progress: number) => void;
}

interface DataTransitionParticleSystemProps {
  width: number;
  height: number;
  quality?: 'low' | 'medium' | 'high';
  className?: string;
  sourceData: DataPoint[];
  targetData: DataPoint[];
  onTransitionComplete?: () => void;
  onTransitionProgress?: (progress: number) => void;
  duration?: number;
  easing?: (t: number) => number;
  staggerDelay?: number;
  trailEffect?: boolean;
  blendMode?: 'normal' | 'additive';
}

/**
 * DataTransitionParticleSystem
 *
 * A specialized particle system for animating data transitions in visualizations.
 * Extends the base ParticleSystemManager with data-specific transition features.
 */
export const DataTransitionParticleSystem: React.FC<DataTransitionParticleSystemProps> = ({
  width,
  height,
  quality = 'medium',
  className = '',
  sourceData,
  targetData,
  onTransitionComplete,
  onTransitionProgress,
  duration = 1000,
  easing = t => t,
  staggerDelay = 20,
  trailEffect = false,
  blendMode = 'additive',
}) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const particleSystemRef = useRef<ParticleSystemManager | null>(null);
  const [isInitialized, setIsInitialized] = useState(false);

  // Register with component registry
  useComponentRegistration({
    type: 'DataTransitionParticleSystem',
    eventSubscriptions: ['RESOURCE_UPDATED', 'RESOURCE_FLOW_UPDATED', 'RESOURCE_THRESHOLD_CHANGED'],
    updatePriority: 'high',
  });

  // Initialize particle system
  useEffect(() => {
    if (!canvasRef.current || isInitialized) return;

    particleSystemRef.current = ParticleSystemManager.getInstance();
    setIsInitialized(true);

    return () => {
      if (particleSystemRef.current) {
        particleSystemRef.current.cleanup();
      }
    };
  }, [isInitialized]);

  // Handle component lifecycle
  useComponentLifecycle({
    onMount: () => {
      console.log('DataTransitionParticleSystem mounted');
    },
    onUnmount: () => {
      console.log('DataTransitionParticleSystem unmounted');
    },
  });

  // Start transition when data changes
  useEffect(() => {
    if (!particleSystemRef.current || !isInitialized) return;

    // Create particle configuration
    const particleConfig = {
      maxParticles: Math.max(sourceData.length, targetData.length),
      spawnRate: 0,
      position: { x: 0, y: 0 },
      spread: 0,
      initialVelocity: {
        min: { x: 0, y: 0 },
        max: { x: 0, y: 0 },
      },
      acceleration: { x: 0, y: 0 },
      size: {
        min: 2,
        max: 8,
      },
      life: {
        min: duration,
        max: duration,
      },
      color: '#ffffff',
      blendMode: blendMode as 'normal' | 'additive',
      quality,
    };

    // Create transition particles
    sourceData.forEach((source, index) => {
      const target = targetData[index] || targetData[targetData.length - 1];
      const delay = index * staggerDelay;

      particleSystemRef.current?.createParticleSystem(`transition-${index}`, {
        ...particleConfig,
        position: source.position,
        color: getResourceColor(source.resourceType),
        size: {
          min: source.size || 2,
          max: target.size || 8,
        },
      });
    });

    // Start animation loop
    const startTime = performance.now();
    let animationFrame: number;

    const animate = () => {
      const currentTime = performance.now();
      const elapsed = currentTime - startTime;
      const progress = Math.min(1, elapsed / duration);

      // Update particle positions
      sourceData.forEach((source, index) => {
        const target = targetData[index] || targetData[targetData.length - 1];
        const particleProgress = easing(progress);

        const currentPosition = {
          x: source.position.x + (target.position.x - source.position.x) * particleProgress,
          y: source.position.y + (target.position.y - source.position.y) * particleProgress,
        };

        particleSystemRef.current?.update(1 / 60);
      });

      // Report progress
      onTransitionProgress?.(progress);

      // Continue animation or complete
      if (progress < 1) {
        animationFrame = requestAnimationFrame(animate);
      } else {
        onTransitionComplete?.();
      }
    };

    animationFrame = requestAnimationFrame(animate);

    // Cleanup function
    return () => {
      cancelAnimationFrame(animationFrame);
      sourceData.forEach((_, index) => {
        particleSystemRef.current?.removeSystem(`transition-${index}`);
      });
    };
  }, [
    sourceData,
    targetData,
    duration,
    easing,
    staggerDelay,
    trailEffect,
    blendMode,
    quality,
    onTransitionComplete,
    onTransitionProgress,
  ]);

  return (
    <div
      ref={containerRef}
      className={`data-transition-particle-system ${className}`}
      style={{ width, height, position: 'relative' }}
    >
      <canvas
        ref={canvasRef}
        width={width}
        height={height}
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          pointerEvents: 'none',
        }}
      />
    </div>
  );
};

// Helper function to get color for resource type
const getResourceColor = (resourceType?: ResourceType): string => {
  if (!resourceType) return '#ffffff';

  switch (resourceType) {
    case ResourceType.MINERALS:
      return '#4CAF50';
    case ResourceType.ENERGY:
      return '#FFC107';
    case ResourceType.PLASMA:
      return '#9C27B0';
    case ResourceType.GAS:
      return '#03A9F4';
    case ResourceType.RESEARCH:
      return '#3F51B5';
    default:
      return '#9E9E9E';
  }
};

export default DataTransitionParticleSystem;

