          const gridSize = 10;
          const stepX = width / gridSize;
          const stepY = height / gridSize;

          for (let x = 0; x < gridSize; x++) {
            for (let y = 0; y < gridSize; y++) {
              points.push({
                x: x * stepX + stepX / 2,
                y: y * stepY + stepY / 2,
                value: (x + y) / (gridSize * 2),
                color: `hsl(${((x + y) / (gridSize * 2)) * 360}, 80%, 50%)`,
                size: 8,
              });
