          for (let i = 0; i < count; i++) {
            const angle = (i / count) * Math.PI * 2;
            const x = centerX + Math.cos(angle) * radius;
            const y = centerY + Math.sin(angle) * radius;

            points.push({
              x,
              y,
              value: i / count,
              color: `hsl(${(i / count) * 360}, 80%, 50%)`,
              size: 8,
            });
          }

