import * as d3 from 'd3';
import React, { useEffect, useRef } from 'react';
import {
  AnimationConfig,
  createTypedTimer,
  TypedAnimationSequence,
  typedInterpolators,
} from '../../../types/visualizations/D3AnimationTypes';
import { selectSvg } from '../../../types/visualizations/D3SelectionTypes';

interface Point {
  x: number;
  y: number;
  radius: number;
  color: string;
}

interface AnimationTypeSafetyDemoProps {
  width?: number;
  height?: number;
  animationConfig?: Partial<AnimationConfig>;
}

/**
 * Demo component showcasing the type-safe animation utilities
 *
 * This component demonstrates:
 * 1. Type-safe interpolation
 * 2. Type-safe transition configuration
 * 3. Type-safe timer usage
 * 4. Animation sequences with proper typing
 */
const AnimationTypeSafetyDemo: React.FC<AnimationTypeSafetyDemoProps> = ({
  width = 600,
  height = 400,
  animationConfig = {},
}) => {
  const svgRef = useRef<SVGSVGElement>(null);
  const config: AnimationConfig = {
    duration: 1500,
    easing: d3.easeCubicInOut,
    loop: true,
    loopDelay: 500,
    ...animationConfig,
  };

  // Setup point data for animation
  const pointsData: Point[] = [
    { x: 100, y: 100, radius: 20, color: '#E63946' },
    { x: 300, y: 150, radius: 30, color: '#457B9D' },
    { x: 500, y: 200, radius: 25, color: '#2A9D8F' },
    { x: 200, y: 250, radius: 35, color: '#F4A261' },
    { x: 400, y: 300, radius: 15, color: '#6D597A' },
  ];

  // Animation point data targets (for interpolation)
  const targetData: Point[] = [
    { x: 150, y: 200, radius: 35, color: '#E76F51' },
    { x: 250, y: 100, radius: 15, color: '#264653' },
    { x: 350, y: 300, radius: 40, color: '#1D3557' },
    { x: 450, y: 200, radius: 20, color: '#F1FAEE' },
    { x: 200, y: 150, radius: 30, color: '#E9C46A' },
  ];

  useEffect(() => {
    if (!svgRef.current) return;

    // Clear any existing elements
    d3.select(svgRef.current).selectAll('*').remove();

    // Create SVG container with proper typing
    const svg = selectSvg(`#animation-demo-svg`);

    // Add circles for each data point
    const circles = svg
      .selectAll<SVGCircleElement, Point>('circle')
      .data(pointsData)
      .enter()
      .append('circle')
      .attr('cx', d => d.x)
      .attr('cy', d => d.y)
      .attr('r', d => d.radius)
      .attr('fill', d => d.color);

    // Setup animation sequence using type-safe utilities
    const animationSequence = new TypedAnimationSequence({
      transitions: [
        {
          selection: circles,
          duration: config.duration,
          easing: config.easing,
          delay: (_, i) => i * 100, // Staggered delay based on index
        },
        {
          selection: circles,
          duration: config.duration,
          easing: d3.easeElasticOut,
        },
      ],
      sequenceDelay: 500,
      loop: config.loop,
    });

    // Create object interpolators for each data point
    const interpolators = pointsData.map((startPoint, index) => {
      return {
        position: typedInterpolators.object<Pick<Point, 'x' | 'y'>>(
          { x: startPoint.x, y: startPoint.y },
          { x: targetData[index].x, y: targetData[index].y }
        ),
        radius: typedInterpolators.number(startPoint.radius, targetData[index].radius),
        color: typedInterpolators.color(startPoint.color, targetData[index].color),
      };
    });

    // Create a type-safe timer for smooth animation
    const timer = createTypedTimer({
      callback: elapsed => {
        // Calculate progress based on elapsed time (ping-pong effect)
        const totalDuration = config.duration * 2;
        const normalizedTime = (elapsed % totalDuration) / config.duration;
        const t = normalizedTime <= 1 ? normalizedTime : 2 - normalizedTime;

        // Update each circle with interpolated values
        circles.each(function (d, i) {
          const point = interpolators[i];
          const interpolatedPosition = point.position(t);
          const interpolatedRadius = point.radius(t);
          const interpolatedColor = point.color(t);

          d3.select(this)
            .attr('cx', interpolatedPosition.x)
            .attr('cy', interpolatedPosition.y)
            .attr('r', interpolatedRadius)
            .attr('fill', interpolatedColor);
        });

        // Continue animation if we're looping
        return !config.loop && elapsed >= totalDuration;
      },
      duration: config.loop ? undefined : config.duration * 2,
    });

    // Add labels to show interpolation t value
    const label = svg
      .append('text')
      .attr('x', width / 2)
      .attr('y', 30)
      .attr('text-anchor', 'middle')
      .attr('font-size', '14px')
      .attr('fill', '#333');

    // Update label with current interpolation value
    createTypedTimer({
      callback: elapsed => {
        const totalDuration = config.duration * 2;
        const normalizedTime = (elapsed % totalDuration) / config.duration;
        const t = normalizedTime <= 1 ? normalizedTime : 2 - normalizedTime;

        label.text(`Interpolation t: ${t.toFixed(2)}`);
        return false;
      },
    });

    // Cleanup function
    return () => {
      timer.stop();
    };
  }, [width, height, config]);

  return (
    <div className="animation-type-safety-demo">
      <h3>Animation Type Safety Demo</h3>
      <svg
        id="animation-demo-svg"
        ref={svgRef}
        width={width}
        height={height}
        style={{
          border: '1px solid #ccc',
          borderRadius: '4px',
          background: '#f7f7f7',
        }}
      />
      <div className="demo-description">
        <p>This demo showcases the type-safe animation utilities:</p>
        <ul>
          <li>Strong typing for interpolators (position, radius, color)</li>
          <li>Type-safe timer configuration and transitions</li>
          <li>Animation sequences with proper event handling</li>
        </ul>
      </div>
    </div>
  );
};

export default AnimationTypeSafetyDemo;

import * as d3 from 'd3';
import React, { useCallback, useEffect, useRef, useState } from 'react';
import {
  d3Accessors,
  SimulationLinkDatum,
  SimulationNodeDatum,
} from '../../../types/visualizations/D3Types';
import {
  createD3ForceValidation,
  ValidationTransformResult,
} from '../../../types/visualizations/D3ValidationHooks';
import { Schema } from '../../../types/visualizations/D3Validators';

/**
 * Represents a node in the flow diagram
 */
interface FlowDataNode {
  id: string;
  name: string;
  type: 'source' | 'process' | 'destination';
  value: number;
  capacity?: number;
  efficiency?: number;
  description?: string;
}

/**
 * Represents a connection between two nodes in the flow diagram
 */
interface FlowDataLink {
  id: string;
  source: string;
  target: string;
  value: number;
  maxCapacity?: number;
  utilization?: number;
  flowType?: string;
  active: boolean;
}

/**
 * Represents the entire flow data structure
 */
interface FlowData {
  nodes: FlowDataNode[];
  links: FlowDataLink[];
}

/**
 * Node type for D3 force simulation with proper typing
 * Extends SimulationNodeDatum to ensure D3 compatibility
 */
interface FlowNode extends SimulationNodeDatum<FlowDataNode> {
  id: string;
  name: string;
  type: 'source' | 'process' | 'destination';
  value: number;
  capacity?: number;
  efficiency?: number;
  description?: string;
  color?: string;
  radius?: number;
  // Original data reference
  data?: FlowDataNode;
}

/**
 * Link type for D3 force simulation with proper typing
 * Extends SimulationLinkDatum for D3 compatibility
 */
interface FlowLink extends SimulationLinkDatum<FlowNode> {
  id: string;
  source: string | FlowNode;
  target: string | FlowNode;
  value: number;
  maxCapacity?: number;
  utilization?: number;
  flowType?: string;
  active: boolean;
  width?: number;
  color?: string;
}

/**
 * Props for FlowDiagram component
 */
interface FlowDiagramProps {
  /**
   * Flow data to visualize
   */
  data: FlowData;

  /**
   * Width of the diagram in pixels
   * @default 800
   */
  width?: number;

  /**
   * Height of the diagram in pixels
   * @default 600
   */
  height?: number;

  /**
   * Whether the diagram supports interaction (dragging, zooming)
   * @default true
   */
  interactive?: boolean;

  /**
   * Whether to animate transitions in the diagram
   * @default true
   */
  animated?: boolean;

  /**
   * Callback when a node is clicked
   */
  onNodeClick?: (nodeId: string, nodeData: FlowDataNode) => void;

  /**
   * Callback when a link is clicked
   */
  onLinkClick?: (linkId: string, linkData: FlowDataLink) => void;

  /**
   * CSS class name for additional styling
   */
  className?: string;
}

/**
 * Convert data nodes to D3-compatible nodes with proper typing
 */
const convertNodesToD3Format = (nodes: FlowDataNode[]): FlowNode[] => {
  return nodes.map(node => {
    // Determine radius based on node value
    const radius = Math.max(15, Math.min(40, 15 + (node.value / 100) * 25));

    // Determine color based on node type
    let color: string;
    switch (node.type) {
      case 'source':
        color = '#06b6d4'; // cyan
        break;
      case 'process':
        color = '#a855f7'; // purple
        break;
      case 'destination':
        color = '#10b981'; // green
        break;
      default:
        color = '#94a3b8'; // slate
    }

    // Create a properly typed node with no type assertions
    const d3Node: FlowNode = {
      id: node.id,
      name: node.name,
      type: node.type,
      value: node.value,
      capacity: node.capacity,
      efficiency: node.efficiency,
      description: node.description,
      radius,
      color,
      // Store original data for reference
      data: node,
    };

    return d3Node;
  });
};

/**
 * Convert data links to D3-compatible links with proper typing
 */
const convertLinksToD3Format = (
  links: FlowDataLink[],
  nodeMap: Map<string, FlowNode>
): FlowLink[] => {
  return links.map(link => {
    // Determine line width based on value and maxCapacity
    const width = Math.max(1, Math.min(8, 1 + (link.value / 100) * 7));

    // Determine color based on active state and utilization
    let color: string;
    if (!link.active) {
      color = '#94a3b8'; // slate (inactive)
    } else if (link.utilization && link.utilization > 0.8) {
      color = '#ef4444'; // red (high utilization)
    } else if (link.utilization && link.utilization > 0.5) {
      color = '#f59e0b'; // amber (medium utilization)
    } else {
      color = '#3b82f6'; // blue (low utilization)
    }

    // Create a properly typed link with no type assertions
    const d3Link: FlowLink = {
      id: link.id,
      source: link.source,
      target: link.target,
      value: link.value,
      maxCapacity: link.maxCapacity,
      utilization: link.utilization,
      flowType: link.flowType,
      active: link.active,
      width,
      color,
    };

    return d3Link;
  });
};

/**
 * Find a node by ID in an array of nodes
 * Type-safe helper function
 */
const findNodeById = (nodes: FlowNode[], id: string): FlowNode | undefined => {
  return nodes.find(node => node.id === id);
};

/**
 * Check if a node/link is a string or an object
 * Type guard function
 */
const isNodeReference = (obj: string | FlowNode): obj is FlowNode => {
  return typeof obj !== 'string' && obj.id !== undefined;
};

/**
 * Component for visualizing flow data with type-safe D3 integration
 */
const FlowDiagram: React.FC<FlowDiagramProps> = ({
  data,
  width = 800,
  height = 600,
  interactive = true,
  animated = true,
  onNodeClick,
  onLinkClick,
  className = '',
}) => {
  const svgRef = useRef<SVGSVGElement | null>(null);
  const simulationRef = useRef<d3.Simulation<FlowNode, FlowLink> | null>(null);

  // State for tracking hover and selected nodes/links
  const [selectedNodeId, setSelectedNodeId] = useState<string | null>(null);
  const [selectedLinkId, setSelectedLinkId] = useState<string | null>(null);

  /**
   * Prepare visualization data with type safety
   */
  const prepareVisualizationData = useCallback(() => {
    try {
      // Define validation schemas
      const flowDataSchema: Schema = {
        name: 'FlowData',
        description: 'Schema for flow data',
        properties: {
          nodes: {
            type: 'array',
            required: true,
          },
          links: {
            type: 'array',
            required: true,
          },
        },
      };

      // Create validation functions
      const validation = createD3ForceValidation<FlowData, FlowNode, FlowLink>(
        // Node transform function
        inputData => convertNodesToD3Format(inputData.nodes),
        // Link transform function
        inputData =>
          convertLinksToD3Format(
            inputData.links,
            new Map(convertNodesToD3Format(inputData.nodes).map(node => [node.id, node]))
          ),
        // Custom node schema properties
        {
          properties: {
            id: {
              type: 'string',
              required: true,
              validate: value => (value as string).length > 0 || 'Node ID cannot be empty',
            },
            type: {
              type: 'string',
              required: true,
              enum: ['source', 'process', 'destination'],
            },
          },
        },
        // Custom link schema properties
        {
          properties: {
            id: {
              type: 'string',
              required: true,
            },
            active: {
              type: 'boolean',
              required: true,
            },
          },
        },
        // Validation options
        {
          throwOnError: false,
          logErrors: true,
          errorPrefix: 'FlowDiagram Validation Error',
        }
      );

      // Validate and transform data
      const nodeResult: ValidationTransformResult<FlowNode[]> = validation.validateNodes(data);
      const linkResult: ValidationTransformResult<FlowLink[]> = validation.validateLinks(data);

      // Handle validation errors
      if (!nodeResult.valid || !linkResult.valid) {
        console.warn(
          'Flow data validation failed:',
          [...nodeResult.errors, ...linkResult.errors].join('\n')
        );
      }

      // Create a map for node lookups (using validated nodes)
      const nodeMap = new Map<string, FlowNode>();
      nodeResult.data.forEach(node => nodeMap.set(node.id, node));

      return {
        nodes: nodeResult.data,
        links: linkResult.data,
        nodeMap,
        valid: nodeResult.valid && linkResult.valid,
        errors: [...nodeResult.errors, ...linkResult.errors],
      };
    } catch (error) {
      console.error('Error preparing visualization data:', error);
      return {
        nodes: [],
        links: [],
        nodeMap: new Map(),
        valid: false,
        errors: ['Error preparing visualization data'],
      };
    }
  }, [data]);

  /**
   * Handle node click events with proper typing
   */
  const handleNodeClick = useCallback(
    (event: React.MouseEvent, node: FlowNode) => {
      setSelectedNodeId(prevId => (prevId === node.id ? null : node.id));

      if (onNodeClick && node.data) {
        onNodeClick(node.id, node.data);
      }
    },
    [onNodeClick]
  );

  /**
   * Handle link click events with proper typing
   */
  const handleLinkClick = useCallback(
    (event: React.MouseEvent, link: FlowLink) => {
      if (typeof link.source === 'string' || typeof link.target === 'string') {
        return; // Ignore links that don't have resolved nodes
      }

      setSelectedLinkId(prevId => (prevId === link.id ? null : link.id));

      // Find the original link data for the callback
      const linkData = data.links.find(l => l.id === link.id);
      if (onLinkClick && linkData) {
        onLinkClick(link.id, linkData);
      }
    },
    [data.links, onLinkClick]
  );

  /**
   * Create and update the visualization
   */
  useEffect(() => {
    if (!svgRef.current || !data) return;

    // Clean up previous simulation if it exists
    if (simulationRef.current) {
      simulationRef.current.stop();
    }

    // Get SVG element with proper typing
    const svg = d3.select<SVGSVGElement, unknown>(svgRef.current);

    // Clear previous content
    svg.selectAll('*').remove();

    // Prepare data with type safety
    const { nodes, links, nodeMap } = prepareVisualizationData();

    // Create container group for zooming
    const container = svg.append('g').attr('class', 'container');

    // Add zoom behavior if interactive
    if (interactive) {
      const zoom = d3
        .zoom<SVGSVGElement, unknown>()
        .scaleExtent([0.1, 4])
        .on('zoom', event => {
          container.attr('transform', event.transform);
        });

      svg.call(zoom);
    }

    // Create arrow markers for links
    const defs = container.append('defs');

    // Create a basic arrow marker
    defs
      .append('marker')
      .attr('id', 'arrowhead')
      .attr('viewBox', '-10 -5 10 10')
      .attr('refX', 0)
      .attr('refY', 0)
      .attr('orient', 'auto')
      .attr('markerWidth', 8)
      .attr('markerHeight', 8)
      .append('path')
      .attr('d', 'M-10,-5L0,0L-10,5')
      .attr('fill', '#777');

    // Create colored arrow markers
    ['active', 'inactive', 'high', 'medium', 'low'].forEach(type => {
      let color: string;
      switch (type) {
        case 'active':
          color = '#3b82f6';
          break;
        case 'inactive':
          color = '#94a3b8';
          break;
        case 'high':
          color = '#ef4444';
          break;
        case 'medium':
          color = '#f59e0b';
          break;
        case 'low':
          color = '#10b981';
          break;
        default:
          color = '#777';
      }

      defs
        .append('marker')
        .attr('id', `arrowhead-${type}`)
        .attr('viewBox', '-10 -5 10 10')
        .attr('refX', 0)
        .attr('refY', 0)
        .attr('orient', 'auto')
        .attr('markerWidth', 8)
        .attr('markerHeight', 8)
        .append('path')
        .attr('d', 'M-10,-5L0,0L-10,5')
        .attr('fill', color);
    });

    // Create D3 force simulation with proper typing
    const simulation = d3
      .forceSimulation<FlowNode, FlowLink>(nodes)
      .force(
        'link',
        d3
          .forceLink<FlowNode, FlowLink>(links)
          .id(d => d.id)
          .distance(100)
      )
      .force('charge', d3.forceManyBody().strength(-200))
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force(
        'collision',
        d3.forceCollide<FlowNode>().radius(d => (d.radius || 20) + 10)
      );

    // Store simulation reference for cleanup
    simulationRef.current = simulation;

    // Create links with proper typing
    const link = container
      .append('g')
      .attr('class', 'links')
      .selectAll<SVGLineElement, FlowLink>('line')
      .data(links)
      .enter()
      .append('line')
      .attr('class', d => `link ${d.active ? 'active' : 'inactive'}`)
      .attr('stroke', d => d.color || '#999')
      .attr('stroke-width', d => d.width || 1)
      .attr('marker-end', d => {
        // Determine marker based on link properties
        if (!d.active) return 'url(#arrowhead-inactive)';
        if (d.utilization && d.utilization > 0.8) return 'url(#arrowhead-high)';
        if (d.utilization && d.utilization > 0.5) return 'url(#arrowhead-medium)';
        return 'url(#arrowhead-active)';
      })
      .style('cursor', onLinkClick ? 'pointer' : 'default')
      .on('click', function (event, d) {
        if (onLinkClick) {
          handleLinkClick(event, d);
        }
      });

    // Create nodes with proper typing
    const node = container
      .append('g')
      .attr('class', 'nodes')
      .selectAll<SVGGElement, FlowNode>('g')
      .data(nodes)
      .enter()
      .append('g')
      .attr('class', 'node')
      .style('cursor', onNodeClick ? 'pointer' : 'default')
      .call(
        d3
          .drag<SVGGElement, FlowNode>()
          .on('start', dragStarted)
          .on('drag', dragged)
          .on('end', dragEnded)
      )
      .on('click', function (event, d) {
        if (onNodeClick) {
          handleNodeClick(event, d);
        }
      });

    // Add circles to nodes
    node
      .append('circle')
      .attr('r', d => d.radius || 20)
      .attr('fill', d => d.color || '#999')
      .attr('stroke', '#fff')
      .attr('stroke-width', 1.5);

    // Add node labels
    node
      .append('text')
      .attr('dy', '.3em')
      .attr('text-anchor', 'middle')
      .attr('font-size', '10px')
      .attr('fill', '#fff')
      .text(d => d.name);

    // Add node value labels
    node
      .append('text')
      .attr('dy', '1.6em')
      .attr('text-anchor', 'middle')
      .attr('font-size', '9px')
      .attr('fill', '#fff')
      .text(d => `${d.value}${d.capacity ? `/${d.capacity}` : ''}`);

    // Add hover effects with type-safe accessors
    node
      .on('mouseover', function (event, d) {
        d3.select(this)
          .select('circle')
          .transition()
          .duration(200)
          .attr('r', (d.radius || 20) * 1.1);
      })
      .on('mouseout', function (event, d) {
        d3.select(this)
          .select('circle')
          .transition()
          .duration(200)
          .attr('r', d.radius || 20);
      });

    // Add flow effects to links
    if (animated) {
      link
        .filter(d => d.active)
        .each(function (d) {
          const element = d3.select(this);

          // Add animated dash array for active links
          element
            .attr('stroke-dasharray', '5,5')
            .style('animation', `flowAnimation ${5000 / (d.value || 1)}ms linear infinite`);
        });
    }

    // Update positions on simulation tick with proper typing
    simulation.on('tick', () => {
      // Update links with safe accessors to prevent type errors
      link
        .attr('x1', d => {
          const source = typeof d.source === 'string' ? findNodeById(nodes, d.source) : d.source;
          return source ? d3Accessors.getX(source) : 0;
        })
        .attr('y1', d => {
          const source = typeof d.source === 'string' ? findNodeById(nodes, d.source) : d.source;
          return source ? d3Accessors.getY(source) : 0;
        })
        .attr('x2', d => {
          const target = typeof d.target === 'string' ? findNodeById(nodes, d.target) : d.target;
          return target ? d3Accessors.getX(target) : 0;
        })
        .attr('y2', d => {
          const target = typeof d.target === 'string' ? findNodeById(nodes, d.target) : d.target;
          return target ? d3Accessors.getY(target) : 0;
        });

      // Update nodes with safe transforms
      node.attr('transform', d => {
        const x = d3Accessors.getX(d);
        const y = d3Accessors.getY(d);
        return `translate(${x}, ${y})`;
      });
    });

    // Type-safe drag functions
    function dragStarted(event: d3.D3DragEvent<SVGGElement, FlowNode, FlowNode>, d: FlowNode) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d3Accessors.getX(d);
      d.fy = d3Accessors.getY(d);
    }

    function dragged(event: d3.D3DragEvent<SVGGElement, FlowNode, FlowNode>, d: FlowNode) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnded(event: d3.D3DragEvent<SVGGElement, FlowNode, FlowNode>, d: FlowNode) {
      if (!event.active) simulation.alphaTarget(0);
      if (!interactive) {
        d.fx = null;
        d.fy = null;
      }
    }

    // Cleanup function
    return () => {
      if (simulationRef.current) {
        simulationRef.current.stop();
      }
    };
  }, [
    data,
    width,
    height,
    interactive,
    animated,
    prepareVisualizationData,
    handleNodeClick,
    handleLinkClick,
    onNodeClick,
    onLinkClick,
  ]);

  return (
    <div className={`flow-diagram-container ${className}`}>
      {/* Add CSS for animation */}
      {animated && (
        <style>
          {`
            @keyframes flowAnimation {
              from {
                stroke-dashoffset: 20;
              }
              to {
                stroke-dashoffset: 0;
              }
            }
          `}
        </style>
      )}
      <svg ref={svgRef} width={width} height={height} className="flow-diagram-svg" />
    </div>
  );
};

export default FlowDiagram;

import * as d3 from 'd3';
import React, { useEffect, useRef, useState } from 'react';
import { SimulationNodeDatum } from '../../../types/visualizations/D3Types';

/**
 * Represents a data point in time series
 */
interface TimeDataPoint {
  timestamp: Date;
  value: number;
  category: string;
  id: string;
}

/**
 * Node type for time-based animations
 * Extends SimulationNodeDatum to ensure D3 type compatibility
 */
interface TimeNode extends SimulationNodeDatum<TimeDataPoint> {
  timestamp: Date;
  value: number;
  category: string;
  x?: number;
  y?: number;
  radius?: number;
  color?: string;
}

/**
 * Configuration options for the animation and transition settings
 */
interface AnimationConfig {
  /** Duration of transitions in milliseconds */
  transitionDuration: number;
  /** Easing function for transitions */
  easingFunction: (t: number) => number;
  /** Delay between animations in milliseconds */
  staggerDelay: number;
  /** Whether to loop animations */
  loop: boolean;
}

/**
 * Props for the TemporalAnalysisView component
 */
interface TemporalAnalysisViewProps {
  /**
   * Time series data to visualize
   */
  data: TimeDataPoint[];

  /**
   * Width of the visualization in pixels
   * @default 800
   */
  width?: number;

  /**
   * Height of the visualization in pixels
   * @default 500
   */
  height?: number;

  /**
   * Margin for the visualization
   */
  margin?: {
    top: number;
    right: number;
    bottom: number;
    left: number;
  };

  /**
   * Whether to show time labels
   * @default true
   */
  showLabels?: boolean;

  /**
   * Animation configuration
   */
  animationConfig?: Partial<AnimationConfig>;

  /**
   * CSS class name for additional styling
   */
  className?: string;
}

/**
 * Safely converts time data to D3-compatible format
 */
const convertToTimeNodes = (data: TimeDataPoint[]): TimeNode[] => {
  return data.map(point => {
    // Properly typed conversion with no type assertions
    const node: TimeNode = {
      id: point.id,
      timestamp: point.timestamp,
      value: point.value,
      category: point.category,
      // The original data field keeps the reference to the source data
      data: point,
    };

    return node;
  });
};

/**
 * Get color for a specific category
 */
const getCategoryColor = (category: string): string => {
  // Color scale for different categories
  const colorScale = d3.scaleOrdinal(d3.schemeCategory10);
  return colorScale(category);
};

/**
 * Component for visualizing temporal data with smooth transitions
 */
const TemporalAnalysisView: React.FC<TemporalAnalysisViewProps> = ({
  data,
  width = 800,
  height = 500,
  margin = { top: 40, right: 40, bottom: 60, left: 60 },
  showLabels = true,
  animationConfig = {},
  className = '',
}) => {
  const svgRef = useRef<SVGSVGElement | null>(null);

  // Default animation config with sensible defaults
  const defaultAnimationConfig: AnimationConfig = {
    transitionDuration: 750,
    easingFunction: d3.easeCubicInOut,
    staggerDelay: 50,
    loop: false,
  };

  // Merge default config with provided config
  const finalAnimationConfig: AnimationConfig = {
    ...defaultAnimationConfig,
    ...animationConfig,
  };

  // Chart dimensions
  const chartWidth = width - margin.left - margin.right;
  const chartHeight = height - margin.top - margin.bottom;

  // Track animation state
  const [isAnimating, setIsAnimating] = useState(false);
  const [currentTimestamp, setCurrentTimestamp] = useState<Date | null>(null);

  // Extract unique categories from data
  const categories = Array.from(new Set(data.map(d => d.category)));

  // Convert data to time nodes for visualization
  const timeNodes = convertToTimeNodes(data);

  // Extract time range from data
  const timeExtent = d3.extent(data, d => d.timestamp) as [Date, Date];
  const valueExtent = d3.extent(data, d => d.value) as [number, number];

  // Set up time scale with proper typing
  const timeScale = d3.scaleTime<number, number>().domain(timeExtent).range([0, chartWidth]);

  // Set up value scale with proper typing
  const valueScale = d3
    .scaleLinear<number, number>()
    .domain([Math.min(0, valueExtent[0]), valueExtent[1]])
    .range([chartHeight, 0]);

  // Set up category scale with proper typing
  const categoryScale = d3
    .scaleBand<string>()
    .domain(categories)
    .range([0, chartHeight])
    .padding(0.1);

  /**
   * Initialize chart
   */
  useEffect(() => {
    if (!svgRef.current || !data.length) return;

    // Select SVG element with proper typing
    const svg = d3.select<SVGSVGElement, unknown>(svgRef.current);

    // Clear any existing elements
    svg.selectAll('*').remove();

    // Add chart group with margins
    const chart = svg.append('g').attr('transform', `translate(${margin.left}, ${margin.top})`);

    // Add X axis with proper typing
    const xAxis = chart
      .append('g')
      .attr('class', 'x-axis')
      .attr('transform', `translate(0, ${chartHeight})`)
      .call(d3.axisBottom(timeScale));

    // Add X axis label
    chart
      .append('text')
      .attr('class', 'x-label')
      .attr('text-anchor', 'middle')
      .attr('x', chartWidth / 2)
      .attr('y', chartHeight + 40)
      .text('Time');

    // Add Y axis with proper typing
    const yAxis = chart.append('g').attr('class', 'y-axis').call(d3.axisLeft(valueScale));

    // Add Y axis label
    chart
      .append('text')
      .attr('class', 'y-label')
      .attr('text-anchor', 'middle')
      .attr('transform', 'rotate(-90)')
      .attr('x', -chartHeight / 2)
      .attr('y', -40)
      .text('Value');

    // Draw vertical gridlines
    chart
      .append('g')
      .attr('class', 'grid x-grid')
      .attr('transform', `translate(0, ${chartHeight})`)
      .call(
        d3
          .axisBottom(timeScale)
          .tickSize(-chartHeight)
          .tickFormat(() => '')
      );

    // Draw horizontal gridlines
    chart
      .append('g')
      .attr('class', 'grid y-grid')
      .call(
        d3
          .axisLeft(valueScale)
          .tickSize(-chartWidth)
          .tickFormat(() => '')
      );

    // Add clip path to ensure points don't overflow
    chart
      .append('clipPath')
      .attr('id', 'chart-area')
      .append('rect')
      .attr('width', chartWidth)
      .attr('height', chartHeight);

    // Create container for data points with clipping
    const pointsContainer = chart
      .append('g')
      .attr('class', 'points-container')
      .attr('clip-path', 'url(#chart-area)');

    // Group data by category for visualization
    const dataByCategory = d3.group(timeNodes, d => d.category);

    // Add a line for each category with proper typing
    dataByCategory.forEach((points, category) => {
      // Sort points by timestamp for proper line drawing
      const sortedPoints = [...points].sort(
        (a, b) => a.timestamp.getTime() - b.timestamp.getTime()
      );

      // Line generator with type-safe accessors
      const lineGenerator = d3
        .line<TimeNode>()
        .x(d => timeScale(d.timestamp))
        .y(d => valueScale(d.value))
        .curve(d3.curveMonotoneX);

      // Add the line path with proper typing
      pointsContainer
        .append('path')
        .attr('class', `line-${category}`)
        .attr('fill', 'none')
        .attr('stroke', getCategoryColor(category))
        .attr('stroke-width', 2)
        .attr('d', lineGenerator(sortedPoints));

      // Add circles for each data point with proper typing
      const circles = pointsContainer
        .selectAll<SVGCircleElement, TimeNode>(`.point-${category}`)
        .data(sortedPoints)
        .enter()
        .append('circle')
        .attr('class', `point-${category}`)
        .attr('cx', d => timeScale(d.timestamp))
        .attr('cy', d => valueScale(d.value))
        .attr('r', 0) // Start with radius 0 for entrance animation
        .attr('fill', getCategoryColor(category))
        .attr('stroke', '#fff')
        .attr('stroke-width', 1);

      // Add entrance animation with proper typing
      circles
        .transition()
        .duration(finalAnimationConfig.transitionDuration)
        .delay((_, i) => i * finalAnimationConfig.staggerDelay)
        .ease(finalAnimationConfig.easingFunction)
        .attr('r', 5);

      // Add labels if enabled
      if (showLabels) {
        pointsContainer
          .selectAll<SVGTextElement, TimeNode>(`.label-${category}`)
          .data(sortedPoints)
          .enter()
          .append('text')
          .attr('class', `label-${category}`)
          .attr('x', d => timeScale(d.timestamp))
          .attr('y', d => valueScale(d.value) - 10)
          .attr('text-anchor', 'middle')
          .attr('font-size', '10px')
          .attr('opacity', 0) // Start transparent for animation
          .text(d => d.value.toFixed(1))
          .transition()
          .duration(finalAnimationConfig.transitionDuration)
          .delay((_, i) => i * finalAnimationConfig.staggerDelay + 200)
          .ease(finalAnimationConfig.easingFunction)
          .attr('opacity', 1);
      }
    });

    // Add category legend with proper typing
    const legend = chart
      .append('g')
      .attr('class', 'legend')
      .attr('transform', `translate(${chartWidth - 100}, 0)`);

    categories.forEach((category, i) => {
      const legendItem = legend
        .append('g')
        .attr('class', `legend-item-${category}`)
        .attr('transform', `translate(0, ${i * 20})`);

      legendItem
        .append('rect')
        .attr('width', 15)
        .attr('height', 15)
        .attr('fill', getCategoryColor(category));

      legendItem
        .append('text')
        .attr('x', 20)
        .attr('y', 12)
        .attr('font-size', '12px')
        .text(category);
    });

    // Add time cursor for animation
    const timeCursor = chart
      .append('line')
      .attr('class', 'time-cursor')
      .attr('x1', 0)
      .attr('y1', 0)
      .attr('x2', 0)
      .attr('y2', chartHeight)
      .attr('stroke', '#666')
      .attr('stroke-width', 1)
      .attr('stroke-dasharray', '4,4')
      .attr('opacity', 0);

    // Add cursor timestamp label
    const cursorLabel = chart
      .append('text')
      .attr('class', 'cursor-label')
      .attr('text-anchor', 'middle')
      .attr('font-size', '10px')
      .attr('y', -10)
      .attr('opacity', 0);

    // Setup animation controls if needed
    if (finalAnimationConfig.loop) {
      setupAnimationLoop(chart, timeScale, timeCursor, cursorLabel);
    }
  }, [data, width, height, margin, showLabels, categories]);

  /**
   * Set up animation loop with type-safe transitions
   */
  const setupAnimationLoop = (
    chart: d3.Selection<SVGGElement, unknown, null, undefined>,
    timeScale: d3.ScaleTime<number, number>,
    timeCursor: d3.Selection<SVGLineElement, unknown, null, undefined>,
    cursorLabel: d3.Selection<SVGTextElement, unknown, null, undefined>
  ) => {
    setIsAnimating(true);

    // Start and end timestamps
    const startTime = timeExtent[0];
    const endTime = timeExtent[1];
    const duration = finalAnimationConfig.transitionDuration * 5;

    // Animation function with proper typing
    const animateTimeCursor = () => {
      // Type-safe transition
      timeCursor
        .attr('opacity', 1)
        .attr('x1', timeScale(startTime))
        .attr('x2', timeScale(startTime));

      // Create a custom timer for smoother animation with proper type safety
      const startTimestamp = Date.now();
      const timerDuration = duration;

      // Use d3.timer for precise animation control with proper typing
      const timer = d3.timer(elapsed => {
        // Calculate progress (0 to 1)
        const progress = Math.min(elapsed / timerDuration, 1);

        // Interpolate the current timestamp
        const currentTime = new Date(
          startTime.getTime() + progress * (endTime.getTime() - startTime.getTime())
        );

        // Update cursor position with type-safe accessors
        timeCursor.attr('x1', timeScale(currentTime)).attr('x2', timeScale(currentTime));

        // Update cursor label with safe string formatting
        cursorLabel
          .attr('opacity', 1)
          .attr('x', timeScale(currentTime))
          .text(d3.timeFormat('%b %d, %Y')(currentTime));

        // Update React state with proper typing
        setCurrentTimestamp(currentTime);

        // Stop timer when complete
        if (progress === 1) {
          timer.stop();

          // If looping is enabled, restart after delay
          if (finalAnimationConfig.loop) {
            setTimeout(animateTimeCursor, 1000);
          } else {
            setIsAnimating(false);
          }
        }
      });
    };

    // Start the animation
    animateTimeCursor();
  };

  /**
   * Creates a type-safe function to handle hover effects on nodes
   */
  const createNodeHoverHandler = () => {
    return (event: React.MouseEvent<SVGCircleElement, MouseEvent>, node: TimeNode) => {
      // Type-safe accessing of node data
      const value = node.value;
      const timestamp = node.timestamp;
      const category = node.category;

      // Safe D3 selection
      const circle = d3.select<SVGCircleElement, TimeNode>(event.currentTarget);

      // Type-safe transition
      circle.transition().duration(200).attr('r', 8).attr('stroke-width', 2);
    };
  };

  return (
    <div className={`temporal-analysis-container ${className}`}>
      <svg ref={svgRef} width={width} height={height} className="temporal-analysis-svg" />

      {currentTimestamp && (
        <div className="current-time-display">
          Current Time: {currentTimestamp.toLocaleString()}
        </div>
      )}
    </div>
  );
};

export default TemporalAnalysisView;

