import * as React from 'react';
import { ResourceType, ResourceTypeHelpers } from '../types/resources/StandardizedResourceTypes';
import { ThresholdAction, ThresholdState, initialState, thresholdEvents } from './ThresholdTypes';

// Types
export interface Resource {
  id: string;
  name: string;
  type: ResourceType;
  currentAmount: number;
  maxCapacity: number;
  thresholds: {
    min: number;
    max: number;
  };
  autoMine: boolean;
}

export interface ThresholdPreset {
  id: string;
  name: string;
  description: string;
  thresholds: Record<string, { min: number; max: number }>;
  autoMineStates: Record<string, boolean>;
}

export interface ThresholdHistoryEntry {
  timestamp: number;
  resourceId: string;
  amount: number;
  thresholds: {
    min: number;
    max: number;
  };
  event: 'threshold_change' | 'amount_update' | 'auto_mine_toggle';
}

interface ThresholdContextType {
  state: ThresholdState;
  dispatch: React.Dispatch<ThresholdAction>;
}

// Helper function to get resource name for display
const getResourceName = (resourceType: ResourceType): string => {
  return ResourceTypeHelpers.getDisplayName(resourceType);
};

// Reducer
function thresholdReducer(state: ThresholdState, action: ThresholdAction): ThresholdState {
  switch (action.type) {
    case 'SET_THRESHOLD': {
      const historyEntry = {
        timestamp: Date.now(),
        resourceId: action.payload.resourceId,
        amount: state.resources[action.payload.resourceId]?.currentAmount || 0,
        thresholds: {
          min: action.payload.min,
          max: action.payload.max,
        },
        event: 'threshold_change' as const,
      };

      return {
        ...state,
        resources: {
          ...state.resources,
          [action.payload.resourceId]: {
            ...state.resources[action.payload.resourceId],
            thresholds: {
              min: action.payload.min,
              max: action.payload.max,
            },
          },
        },
        history: [...state.history, historyEntry].slice(-100),
      };
    }

    case 'UPDATE_AMOUNT': {
      const resource = state.resources[action.payload.resourceId];
      if (!resource) {
        return state;
      }

      const updatedResource = {
        ...resource,
        currentAmount: action.payload.amount,
      };

      const historyEntry = {
        timestamp: Date.now(),
        resourceId: action.payload.resourceId,
        amount: action.payload.amount,
        thresholds: resource.thresholds,
        event: 'amount_update' as const,
      };

      // Check for threshold violations
      if (updatedResource.autoMine) {
        if (updatedResource.currentAmount < updatedResource.thresholds.min) {
          thresholdEvents.next({
            type: 'THRESHOLD_VIOLATED',
            resourceId: action.payload.resourceId,
            details: {
              type: 'below_minimum',
              current: updatedResource.currentAmount,
              min: updatedResource.thresholds.min,
            },
          });
        } else if (updatedResource.currentAmount > updatedResource.thresholds.max) {
          thresholdEvents.next({
            type: 'STORAGE_FULL',
            resourceId: action.payload.resourceId,
            details: {
              type: 'above_maximum',
              current: updatedResource.currentAmount,
              max: updatedResource.thresholds.max,
            },
          });
        }
      }

      return {
        ...state,
        resources: {
          ...state.resources,
          [action.payload.resourceId]: updatedResource,
        },
        history: [...state.history, historyEntry].slice(-100),
      };
    }

    case 'TOGGLE_AUTO_MINE': {
      const resource = state.resources[action.payload.resourceId];
      if (!resource) {
        return state;
      }

      const newAutoMine = !resource.autoMine;

      const historyEntry = {
        timestamp: Date.now(),
        resourceId: action.payload.resourceId,
        amount: resource.currentAmount,
        thresholds: resource.thresholds,
        event: 'auto_mine_toggle' as const,
      };

      if (newAutoMine) {
        thresholdEvents.next({
          type: 'AUTO_MINE_TRIGGERED',
          resourceId: action.payload.resourceId,
          details: { type: 'below_minimum', current: resource.currentAmount },
        });
      }

      return {
        ...state,
        resources: {
          ...state.resources,
          [action.payload.resourceId]: {
            ...resource,
            autoMine: newAutoMine,
          },
        },
        history: [...state.history, historyEntry].slice(-100),
      };
    }

    case 'ADD_PRESET':
      return {
        ...state,
        presets: [...state.presets, action.payload],
      };

    case 'REMOVE_PRESET':
      return {
        ...state,
        presets: state.presets.filter(preset => preset.id !== action.payload.presetId),
        activePresetId:
          state.activePresetId === action.payload.presetId ? null : state.activePresetId,
      };

    case 'APPLY_PRESET': {
      const preset = state.presets.find(p => p.id === action.payload.presetId);
      if (!preset) {
        return state;
      }

      const updatedResources = { ...state.resources };
      Object.entries(preset.thresholds).forEach(([resourceId, thresholds]) => {
        if (updatedResources[resourceId]) {
          updatedResources[resourceId] = {
            ...updatedResources[resourceId],
            thresholds,
            autoMine: preset.autoMineStates[resourceId] || false,
          };
        }
      });

      return {
        ...state,
        resources: updatedResources,
        activePresetId: action.payload.presetId,
      };
    }

    case 'SET_GLOBAL_AUTO_MINE': {
      const updatedResources = Object.entries(state.resources).reduce(
        (acc, [id, resource]) => ({
          ...acc,
          [id]: { ...resource, autoMine: action.payload },
        }),
        {}
      );

      return {
        ...state,
        globalAutoMine: action.payload,
        resources: updatedResources,
      };
    }

    case 'ADD_NOTIFICATION':
      return {
        ...state,
        notifications: [...state.notifications, action.payload],
      };

    case 'CLEAR_NOTIFICATION':
      return {
        ...state,
        notifications: state.notifications.filter((_, index) => index !== action.payload),
      };

    case 'ADD_RESOURCE':
      return {
        ...state,
        resources: {
          ...state.resources,
          [action.payload.id]: action.payload,
        },
      };

    case 'REMOVE_RESOURCE': {
      const remainingResources = { ...state.resources };
      delete remainingResources[action.payload.resourceId];
      return {
        ...state,
        resources: remainingResources,
      };
    }

    case 'ADD_HISTORY_ENTRY':
      return {
        ...state,
        history: [...state.history, action.payload].slice(-100),
      };

    default:
      return state;
  }
}

// Context
const ThresholdContext = React.createContext<ThresholdContextType | undefined>(undefined);

// Provider
export function ThresholdProvider({ children }: { children: React.ReactNode }) {
  const [state, dispatch] = React.useReducer(thresholdReducer, initialState);

  return (
    <ThresholdContext.Provider value={{ state, dispatch }}>{children}</ThresholdContext.Provider>
  );
}

// Hook
export function useThreshold() {
  const context = React.useContext(ThresholdContext);
  if (context === undefined) {
    throw new Error('useThreshold must be used within a ThresholdProvider');
  }
  return context;
}

import React from 'react';
import { ErrorBoundary, ErrorBoundaryProps } from './ErrorBoundary';

/**
 * Global error boundary props
 */
export interface GlobalErrorBoundaryProps extends Omit<ErrorBoundaryProps, 'context' | 'fallback'> {
  /** Whether this is the root boundary (changes styling) */
  isRoot?: boolean;
}

/**
 * Global application error boundary
 * 
 * This component is designed to be used at the application root level
 * to catch any unhandled errors that bubble up through the component tree.
 */
export const GlobalErrorBoundary: React.FC<GlobalErrorBoundaryProps> = ({
  children,
  isRoot = false,
  onError,
  suppressErrorLogging,
  metadata,
  ...props
}) => {
  const handleReset = () => {
    // For a root-level error, we might want to refresh the page
    if (isRoot) {
      window.location.reload();
      return;
    }

    // Otherwise, just clear any application state as needed
    // This could include resetting global state, clearing caches, etc.
  };

  // Custom fallback UI for global error
  const globalFallback = (error: Error, reset: () => void) => {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
        <div className="max-w-lg w-full bg-white rounded-lg shadow-lg p-8">
          <div className="flex items-center mb-6">
            <div className="bg-red-100 p-3 rounded-full mr-4">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-6 w-6 text-red-600"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
                />
              </svg>
            </div>
            <h1 className="text-xl font-bold text-gray-800">
              Something went wrong
            </h1>
          </div>

          <p className="text-gray-600 mb-6">
            We're sorry, but something went wrong. Our team has been notified and is working to fix the issue.
          </p>

          {process.env.NODE_ENV !== 'production' && (
            <div className="mb-6 bg-gray-50 p-4 rounded border border-gray-200">
              <h3 className="font-medium text-gray-800 mb-2">Error Details:</h3>
              <pre className="text-sm text-red-600 whitespace-pre-wrap break-all">
                {error.message}
              </pre>
              {error.stack && (
                <details className="mt-2">
                  <summary className="cursor-pointer text-sm font-medium text-gray-600">
                    Stack trace
                  </summary>
                  <pre className="mt-2 text-xs text-gray-600 whitespace-pre-wrap break-all">
                    {error.stack}
                  </pre>
                </details>
              )}
            </div>
          )}

          <div className="flex justify-between">
            <button
              onClick={() => reset()}
              className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition-colors"
            >
              {isRoot ? 'Reload Application' : 'Try Again'}
            </button>

            <button
              onClick={() => {
                // Navigate to a safe page, like the home page
                window.location.href = '/';
              }}
              className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300 transition-colors"
            >
              Return to Home
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <ErrorBoundary
      fallback={globalFallback}
      context="Global"
      onError={(error, errorInfo) => {
        // For root errors, we might want to log analytics events or notify monitoring services
        if (isRoot) {
          // Example: Log to analytics
          try {
            if (window.gtag) {
              window.gtag('event', 'error', {
                'event_category': 'Error',
                'event_label': error.message,
                'value': 1
              });
            }
          } catch (e) {
            console.error('Failed to log error to analytics:', e);
          }
        }

        // Call original onError handler
        if (onError) {
          onError(error, errorInfo);
        }
      }}
      suppressErrorLogging={suppressErrorLogging}
      metadata={{
        isRoot,
        ...metadata,
      }}
      {...props}
    >
      {children}
    </ErrorBoundary>
  );
};

