/**
 * Props for the visualization error boundary
 */
export interface VisualizationErrorBoundaryProps extends Omit<ErrorBoundaryProps, 'context'> {
  /** Whether to show a compact error UI */
  compact?: boolean;
  /** The visualization type for better error reporting */
  visualizationType?: 'chart' | 'graph' | 'map' | 'diagram' | 'table' | 'other';
  /** Data size, useful for debugging performance issues */
  dataSize?: number;
  /** Additional visualization metadata */
  visualizationMetadata?: Record<string, unknown>;
}

/**
 * Specialized error boundary for visualization components
 * 
 * This error boundary is designed specifically for visualization components
 * like charts, graphs, maps, etc. It provides specialized error handling and
 * fallback UIs for visualization-specific errors.
 */
export const VisualizationErrorBoundary: React.FC<VisualizationErrorBoundaryProps> = ({
  children,
  fallback,
  compact = false,
  visualizationType = 'other',
  dataSize,
  visualizationMetadata,
  onError,
  suppressErrorLogging,
  metadata,
  ...props
}) => {
  // Combine visualization-specific metadata with general metadata
  const combinedMetadata = {
    visualizationType,
    ...(dataSize !== undefined && { dataSize }),
    ...visualizationMetadata,
    ...metadata,
  };

  // Custom fallback UI specific to visualizations
  const visualizationFallback = (error: Error, reset: () => void) => {
    // If a custom fallback is provided, use that
    if (fallback) {
      if (typeof fallback === 'function') {
        return fallback(error, reset);
      }
      return fallback;
    }

    // Otherwise, use a specialized fallback for visualizations
    if (compact) {
      return (
        <div className="p-3 bg-gray-100 border border-gray-300 rounded text-sm text-gray-700 flex flex-col items-center justify-center h-full">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6 text-gray-500 mb-2"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M11 3.055A9.001 9.001 0 1020.945 13H11V3.055z"
            />
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M20.488 9H15V3.512A9.025 9.025 0 0120.488 9z"
            />
          </svg>
          <p className="mb-2 text-center">Visualization Error</p>
          <button
            onClick={reset}
            className="text-xs px-2 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Reset
          </button>
        </div>
      );
    }

    return (
      <ErrorFallback
        error={error}
        resetErrorBoundary={reset}
        title="Visualization Error"
        showDetails={process.env.NODE_ENV !== 'production'}
        actionText="Show Default View"
        onAction={() => {
          // This could switch to a simpler view or load simplified data
          console.log('Switching to default view');
          reset();
        }}
      />
    );
  };

  return (
    <ErrorBoundary
      fallback={visualizationFallback}
      context="Visualization"
      onError={onError}
      suppressErrorLogging={suppressErrorLogging}
      metadata={combinedMetadata}
      {...props}
    >
      {children}
    </ErrorBoundary>
  );
};

export default VisualizationErrorBoundary;

import React, { ComponentType, ErrorInfo, ReactNode } from 'react';
import { ErrorBoundary, ErrorBoundaryProps } from '../ErrorBoundary';

/**
 * Props for the fallback component created by createTypedErrorBoundary
 */
export interface TypedErrorFallbackProps {
  /** The error that occurred */
  error: Error;
  /** Component name for error reporting */
  componentName: string;
  /** Function to reset the error boundary */
  resetError: () => void;
  /** Additional error context information */
  errorContext?: Record<string, unknown>;
}

/**
 * Default fallback component for typed error boundaries
 */
export const TypedErrorFallback: React.FC<TypedErrorFallbackProps> = ({
  error,
  componentName,
  resetError,
  errorContext,
}) => (
  <div className="p-4 rounded border border-red-300 bg-red-50 text-red-800">
    <h3 className="font-semibold mb-2">Error in {componentName}</h3>
    <p className="mb-2">An error occurred while rendering the {componentName} component:</p>
    <pre className="p-2 bg-red-100 rounded mb-3 text-sm overflow-auto">{error.message}</pre>
    {errorContext && (
      <div className="mb-3">
        <h4 className="font-medium mb-1">Error Context:</h4>
        <pre className="p-2 bg-red-100 rounded text-xs overflow-auto">
          {JSON.stringify(errorContext, null, 2)}
        </pre>
      </div>
    )}
    <button
      onClick={resetError}
      className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
    >
      Reset Component
    </button>
  </div>
);

/**
 * Create a typed error boundary for a specific component type
 * 
 * This function wraps a component with an error boundary that is specifically
 * typed for that component's props, providing a clean way to create "safe"
 * versions of components.
 * 
 * @param Component The React component to wrap with an error boundary
 * @param componentName Name of the component for error reporting
 * @param FallbackComponent Custom fallback component
 * @param onError Custom error handler function
 * @returns A wrapped component with an error boundary
 */
export function createTypedErrorBoundary<P extends object>(
  Component: ComponentType<P>,
  componentName: string,
  FallbackComponent?: React.ComponentType<TypedErrorFallbackProps>,
  onError?: (error: Error, errorInfo: ErrorInfo, componentName: string) => void
): React.FC<P & { errorContext?: Record<string, unknown>; resetKeys?: unknown[] }> {
  return (props: P & { errorContext?: Record<string, unknown>; resetKeys?: unknown[] }) => {
    const { errorContext, resetKeys, ...componentProps } = props;

    // Custom error handler that includes the component name
    const handleError = (error: Error, errorInfo: ErrorInfo) => {
      console.error(`Error in ${componentName}:`, error);
      console.error('Component Stack:', errorInfo.componentStack);

      if (onError) {
        onError(error, errorInfo, componentName);
      }
    };

    // Custom fallback UI that includes the component name
    const fallback = (error: Error, reset: () => void) => {
      const FallbackUI = FallbackComponent || TypedErrorFallback;

      return (
        <FallbackUI
          error={error}
          componentName={componentName}
          resetError={reset}
          errorContext={errorContext}
        />
      );
    };

    return (
      <ErrorBoundary
        fallback={fallback}
        onError={handleError}
        context={componentName}
        resetKeys={resetKeys}
        metadata={{
          componentName,
          ...errorContext,
        }}
      >
        <Component {...(componentProps as P)} />
      </ErrorBoundary>
    );
  };
}

/**
 * Options for the withErrorBoundary HOC
 */
export interface WithErrorBoundaryOptions extends Omit<ErrorBoundaryProps, 'children'> {
  /** Component name for error reporting */
  componentName?: string;
}

/**
 * Higher-order component that wraps a component with an error boundary
 * 
 * This is a more flexible alternative to createTypedErrorBoundary that allows
 * passing any ErrorBoundary props.
 * 
 * @param Component The component to wrap
 * @param options Options for the error boundary
 * @returns A wrapped component with an error boundary
 */
export function withErrorBoundary<P extends object>(
  Component: ComponentType<P>,
  options: WithErrorBoundaryOptions = {}
): React.FC<P> {
  const { componentName = Component.displayName || Component.name || 'UnknownComponent', ...errorBoundaryProps } = options;

  // Set a display name for the wrapped component
  const wrappedComponentName = `WithErrorBoundary(${componentName})`;

  const WrappedComponent: React.FC<P> = (props) => {
    return (
      <ErrorBoundary 
        context={componentName}
        {...errorBoundaryProps}
      >
        <Component {...props} />
      </ErrorBoundary>
    );
  };

  // Set display name for better debugging
  WrappedComponent.displayName = wrappedComponentName;

  return WrappedComponent;
}

/**
 * Create safer versions of components by wrapping them with error boundaries
 * 
 * This utility function takes a record of components and wraps each one with
 * an error boundary, creating a new record of "safe" components.
 * 
 * @param components Record of components to wrap
 * @param getOptions Function to generate options for each component
 * @returns Record of wrapped components
 */
export function createSafeComponents<T extends Record<string, ComponentType<any>>>(
  components: T,
  getOptions?: (key: string, Component: ComponentType<any>) => WithErrorBoundaryOptions
): { [K in keyof T]: React.FC<React.ComponentProps<T[K]>> } {
  const safeComponents = {} as { [K in keyof T]: React.FC<React.ComponentProps<T[K]>> };

  for (const key in components) {
    const Component = components[key];
    const options = getOptions ? getOptions(key, Component) : { componentName: key as string };

    safeComponents[key] = withErrorBoundary(Component, options);
  }

  return safeComponents;
}

/**
 * Create specialized versions of error boundaries
 * 
 * This utility function creates a specialized error boundary component
 * for specific contexts or use cases.
 * 
 * @param defaultProps Default props to apply to the error boundary
 * @returns A specialized error boundary component
 */
export function createSpecializedErrorBoundary(
  defaultProps: Omit<ErrorBoundaryProps, 'children'>
): React.FC<ErrorBoundaryProps> {
  const SpecializedErrorBoundary: React.FC<ErrorBoundaryProps> = (props) => {
    return (
      <ErrorBoundary
        {...defaultProps}
        {...props}
        metadata={{
          ...defaultProps.metadata,
          ...props.metadata,
        }}
      />
    );
  };

  return SpecializedErrorBoundary;
}

import { useState, useEffect, useCallback } from 'react';
import { errorLoggingService, ErrorType } from '../../services/ErrorLoggingService';

/**
 * Options for data fetching hook
 */
export interface DataFetchOptions {
  /** Whether to fetch immediately on mount */
  fetchOnMount?: boolean;
  /** Custom condition to trigger fetch */
  enabled?: boolean;
  /** Refetch interval in milliseconds */
  refetchInterval?: number;
  /** Maximum number of retries on error */
  maxRetries?: number;
  /** Whether to reset data when refetching */
  resetOnFetch?: boolean;
  /** Cache key for the request */
  cacheKey?: string;
  /** Cache time in milliseconds */
  cacheTime?: number;
}

/**
 * Return type for data fetching hook
 */
export interface DataFetchResult<T> {
  /** The fetched data */
  data: T | null;
  /** Whether data is currently being fetched */
  isLoading: boolean;
  /** Any error that occurred during fetching */
  error: Error | null;
  /** Function to manually trigger a fetch */
  fetch: () => Promise<void>;
  /** Function to reset the hook state */
  reset: () => void;
  /** Last time data was fetched successfully */
  lastFetched: number | null;
  /** Number of retries attempted */
  retryCount: number;
}

/**
 * Creates a reusable data fetching hook with standardized loading, error, and data states
 * @param fetchFn The function that fetches data
 * @param defaultOptions Default options for the hook
 * @returns A React hook that manages data fetching
 */
export function createDataFetchHook<T, P extends any[] = []>(
  fetchFn: (...args: P) => Promise<T>,
  defaultOptions: DataFetchOptions = {}
) {
  return (...args: P): DataFetchResult<T> => {
    const [data, setData] = useState<T | null>(null);
    const [isLoading, setIsLoading] = useState<boolean>(defaultOptions.fetchOnMount !== false);
    const [error, setError] = useState<Error | null>(null);
    const [lastFetched, setLastFetched] = useState<number | null>(null);
    const [retryCount, setRetryCount] = useState<number>(0);

    // Determine if fetch is enabled
    const enabled = defaultOptions.enabled !== false;

    // Memoized fetch function
    const fetch = useCallback(async () => {
      if (!enabled) return;

      try {
        if (defaultOptions.resetOnFetch) {
          setData(null);
        }

        setIsLoading(true);
        setError(null);

        const result = await fetchFn(...args);

        setData(result);
        setLastFetched(Date.now());
        setRetryCount(0);
      } catch (err) {
        setError(err instanceof Error ? err : new Error(String(err)));

        // Log error to the error service
        errorLoggingService.logError(
          err instanceof Error ? err : new Error(String(err)),
          ErrorType.NETWORK,
          undefined,
          { hook: 'dataFetch', args: JSON.stringify(args) }
        );

        // Retry logic
        if (defaultOptions.maxRetries && retryCount < defaultOptions.maxRetries) {
          setRetryCount(current => current + 1);

          // Exponential backoff
          const backoffTime = Math.min(1000 * Math.pow(2, retryCount), 30000);

          setTimeout(() => {
            fetch();
          }, backoffTime);
        }
      } finally {
        setIsLoading(false);
      }
    }, [enabled, ...args]);

    // Reset function
    const reset = useCallback(() => {
      setData(null);
      setIsLoading(false);
      setError(null);
      setLastFetched(null);
      setRetryCount(0);
    }, []);

    // Handle automatic fetching on mount
    useEffect(() => {
      let mounted = true;
      let intervalId: NodeJS.Timeout | null = null;

      if (defaultOptions.fetchOnMount !== false && enabled) {
        fetch();
      }

      // Set up refetch interval if specified
      if (defaultOptions.refetchInterval && enabled) {
        intervalId = setInterval(() => {
          if (mounted) {
            fetch();
          }
        }, defaultOptions.refetchInterval);
      }

      return () => {
        mounted = false;
        if (intervalId) {
          clearInterval(intervalId);
        }
      };
    }, [fetch, enabled]);

    return {
      data,
      isLoading,
      error,
      fetch,
      reset,
      lastFetched,
      retryCount
    };
  };
}

/**
 * Example usage:
 * 
 * ```typescript
 * // Define the hook
 * const useUserData = createDataFetchHook(
 *   (userId: string) => fetch(`/api/users/${userId}`).then(res => res.json()),
 *   { fetchOnMount: true }
 * );
 * 
 * // Use in component
 * function UserProfile({ userId }) {
 *   const { data, isLoading, error, fetch } = useUserData(userId);
 *   
 *   if (isLoading) return <Loading />;
 *   if (error) return <Error message={error.message} />;
 *   
 *   return (
 *     <div>
 *       <h1>{data.name}</h1>
 *       <button onClick={fetch}>Refresh</button>
 *     </div>
 *   );
 * }
 * ```
 */

import { useState, useMemo, useCallback, useEffect } from 'react';

/**
 * Action creator type
 */
export type ActionCreator<TState, TPayload = any> = (
  state: TState,
  payload: TPayload
) => Partial<TState>;

/**
 * Bound action type
 */
export type BoundAction<TPayload = any> = (payload: TPayload) => void;

/**
 * State hook options
 */
export interface StateHookOptions<TState> {
  /** Function to run on state initialization */
  onInit?: (state: TState) => void;
  /** Function to run on state cleanup */
  onCleanup?: (state: TState) => void;
  /** Whether to persist state in localStorage */
  persist?: boolean;
  /** Key to use for localStorage persistence */
  persistKey?: string;
  /** Custom state equality function */
  areEqual?: (prev: TState, next: TState) => boolean;
  /** Initial state override */
  initialStateOverride?: TState | (() => TState);
}

/**
 * Creates a reusable state management hook with actions
 * @param initialState The initial state or a function that returns the initial state
 * @param actions An object of action creators
 * @param options Options for the hook
 * @returns A hook that provides state and bound actions
 */
export function createStateHook<
  TState extends Record<string, any>,
  TActions extends Record<string, ActionCreator<TState, any>>
>(
  initialState: TState | (() => TState),
  actions: TActions,
  options: StateHookOptions<TState> = {}
) {
  // Type for the returned actions object
  type BoundActions = {
    [K in keyof TActions]: BoundAction<Parameters<TActions[K]>[1]>;
  };

  return () => {
    // Initialize state, handling both function and object initial states
    const getInitialState = useCallback(() => {
      // Check if we should use override
      if (options.initialStateOverride !== undefined) {
        return typeof options.initialStateOverride === 'function'
          ? (options.initialStateOverride as () => TState)()
          : options.initialStateOverride;
      }

      // Otherwise use the default initial state
      const defaultState = typeof initialState === 'function'
        ? (initialState as () => TState)()
        : initialState;

      // If persistence is enabled, try to load from localStorage
      if (options.persist && options.persistKey) {
        try {
          const savedState = localStorage.getItem(options.persistKey);
          if (savedState) {
            return { ...defaultState, ...JSON.parse(savedState) };
          }
        } catch (error) {
          console.error('Failed to load persisted state:', error);
        }
      }

      return defaultState;
    }, []);

    // Initialize state
    const [state, setState] = useState<TState>(getInitialState);

    // Create memoized bound actions
    const boundActions = useMemo(() => {
      const result = {} as BoundActions;

      for (const [key, actionCreator] of Object.entries(actions)) {
        result[key as keyof TActions] = (payload) => {
          setState((currentState) => {
            const updates = actionCreator(currentState, payload);

            // If updates is empty, return the current state
            if (!updates || Object.keys(updates).length === 0) {
              return currentState;
            }

            const newState = { ...currentState, ...updates };

            // If custom equality function is provided, check if state actually changed
            if (options.areEqual && options.areEqual(currentState, newState)) {
              return currentState;
            }

            // Persist state if enabled
            if (options.persist && options.persistKey) {
              try {
                localStorage.setItem(options.persistKey, JSON.stringify(newState));
              } catch (error) {
                console.error('Failed to persist state:', error);
              }
            }

            return newState;
          });
        };
      }

      return result;
    }, []);

    // Reset action
    const reset = useCallback(() => {
      setState(getInitialState());

      // Clear persisted state if applicable
      if (options.persist && options.persistKey) {
        try {
          localStorage.removeItem(options.persistKey);
        } catch (error) {
          console.error('Failed to clear persisted state:', error);
        }
      }
    }, [getInitialState]);

    // Add reset to bound actions
    const actionsWithReset = useMemo(() => ({
      ...boundActions,
      reset
    }), [boundActions, reset]);

    // Run initialization and cleanup
    useEffect(() => {
      if (options.onInit) {
        options.onInit(state);
      }

      return () => {
        if (options.onCleanup) {
          options.onCleanup(state);
        }
      };
    }, []);

    return [state, actionsWithReset] as const;
  };
}

/**
 * Example usage:
 * 
 * ```typescript
 * // Define the state type
 * interface CounterState {
 *   count: number;
 *   lastUpdated: number | null;
 * }
 * 
 * // Define action creators
 * const counterActions = {
 *   increment: (state: CounterState, step: number = 1) => ({
 *     count: state.count + step,
 *     lastUpdated: Date.now()
 *   }),
 *   decrement: (state: CounterState, step: number = 1) => ({
 *     count: state.count - step,
 *     lastUpdated: Date.now()
 *   }),
 *   reset: (state: CounterState) => ({
 *     count: 0,
 *     lastUpdated: Date.now()
 *   })
 * };
 * 
 * // Create the hook
 * const useCounter = createStateHook<CounterState, typeof counterActions>(
 *   { count: 0, lastUpdated: null },
 *   counterActions,
 *   { persist: true, persistKey: 'app-counter' }
 * );
 * 
 * // Use in component
 * function Counter() {
 *   const [state, actions] = useCounter();
 *   
 *   return (
 *     <div>
 *       <p>Count: {state.count}</p>
 *       <button onClick={() => actions.increment()}>Increment</button>
 *       <button onClick={() => actions.decrement()}>Decrement</button>
 *       <button onClick={actions.reset}>Reset</button>
 *     </div>
 *   );
 * }
 * ```
 */

/**
 * @file useGameState.ts
 * Provides standardized hooks for accessing GameContext with selector pattern.
 *
 * This implementation:
 * 1. Uses selector pattern for better performance
 * 2. Provides type safety for game state data
 * 3. Maintains the same functionality as the previous implementation
 * 4. Follows the standardized context access pattern
 * 5. Includes performance monitoring for selectors and computations
 */

import { useCallback, useEffect } from 'react';
import {
  GameActionType,
  selectGameTime,
  selectIsPaused,
  selectIsRunning,
  selectMissions,
  selectResourceRates,
  selectResources,
  selectSectors,
  selectShips,
  selectSystems,
  useGameDispatch,
  useGameSelector,
} from '../../contexts/GameContext';
import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import { gameManager } from '../../managers/game/gameManager';
import { GameEvent } from '../../types/core/GameTypes';
import {
  HookPerformanceConfig,
  defaultPerformanceConfig,
  measureComputationTime,
  measureSelectorTime,
  trackHookRender,
} from '../../utils/performance/hookPerformanceMonitor';

// Type guards
interface Resource {
  type: string;
  amount: number;
}

// Define interfaces for event data types
interface MissionCompletedEventData {
  type: 'discovery' | 'anomaly' | 'completion';
  description: string;
  sector: string;
  importance: 'low' | 'medium' | 'high';
  xpGained?: number;
  resourcesFound?: Array<{ type: string; amount: number }>;
  anomalyDetails?: {
    type: string;
    severity: string;
    investigated: boolean;
  };
}

interface SectorUpdateEventData {
  heatMapValue: number;
  task: {
    target: {
      id: string;
    };
  };
  resourcePotential: number;
  habitabilityScore: number;
}

// Type guard functions
function isMissionCompletedEventData(data: unknown): data is MissionCompletedEventData {
  if (!data || typeof data !== 'object') {
    return false;
  }

  const missionData = data as Record<string, unknown>;

  return (
    (missionData.type === 'discovery' ||
      missionData.type === 'anomaly' ||
      missionData.type === 'completion') &&
    typeof missionData.description === 'string' &&
    typeof missionData.sector === 'string' &&
    (missionData.importance === 'low' ||
      missionData.importance === 'medium' ||
      missionData.importance === 'high')
  );
}

function isSectorUpdateEventData(data: unknown): data is SectorUpdateEventData {
  if (!data || typeof data !== 'object') {
    return false;
  }

  const sectorData = data as Record<string, unknown>;

  // Use explicit boolean checks for each condition
  const hasHeatMapValue = typeof sectorData.heatMapValue === 'number';
  const hasTask = sectorData.task !== undefined && sectorData.task !== null;
  const isTaskObject = hasTask && typeof sectorData.task === 'object';
  const hasTarget = isTaskObject && 'target' in (sectorData.task as object);
  const isTargetObject =
    hasTarget && typeof (sectorData.task as Record<string, unknown>).target === 'object';
  const hasId =
    isTargetObject && 'id' in ((sectorData.task as Record<string, unknown>).target as object);
  const isIdString =
    hasId &&
    typeof ((sectorData.task as Record<string, unknown>).target as Record<string, unknown>).id ===
      'string';

  return hasHeatMapValue && isTaskObject && hasTarget && isTargetObject && hasId && isIdString;
}

// Performance monitoring configuration
const gameStatePerformanceConfig: HookPerformanceConfig = {
  ...defaultPerformanceConfig,
  hookName: 'useGameState',
};

/**
 * Hook to access game state with selector pattern for performance optimization
 *
 * @returns The game state with action methods
 */
export function useGameState() {
  // Track hook render
  trackHookRender(gameStatePerformanceConfig);

  // Use selectors for individual pieces of state to prevent unnecessary re-renders
  const isRunning = measureSelectorTime(
    'isRunning',
    () => useGameSelector(selectIsRunning),
    gameStatePerformanceConfig
  );

  const isPaused = measureSelectorTime(
    'isPaused',
    () => useGameSelector(selectIsPaused),
    gameStatePerformanceConfig
  );

  const gameTime = measureSelectorTime(
    'gameTime',
    () => useGameSelector(selectGameTime),
    gameStatePerformanceConfig
  );

  const resources = measureSelectorTime(
    'resources',
    () => useGameSelector(selectResources),
    gameStatePerformanceConfig
  );

  const resourceRates = measureSelectorTime(
    'resourceRates',
    () => useGameSelector(selectResourceRates),
    gameStatePerformanceConfig
  );

  const systems = measureSelectorTime(
    'systems',
    () => useGameSelector(selectSystems),
    gameStatePerformanceConfig
  );

  const missions = measureSelectorTime(
    'missions',
    () => useGameSelector(selectMissions),
    gameStatePerformanceConfig
  );

  const sectors = measureSelectorTime(
    'sectors',
    () => useGameSelector(selectSectors),
    gameStatePerformanceConfig
  );

  const ships = measureSelectorTime(
    'ships',
    () => useGameSelector(selectShips),
    gameStatePerformanceConfig
  );

  // Get dispatch function for actions
  const dispatch = useGameDispatch();

  // Action creators with standardized pattern
  const startGame = useCallback(
    () => dispatch({ type: GameActionType.START_GAME, payload: undefined }),
    [dispatch]
  );
  const pauseGame = useCallback(
    () => dispatch({ type: GameActionType.PAUSE_GAME, payload: undefined }),
    [dispatch]
  );
  const resumeGame = useCallback(
    () => dispatch({ type: GameActionType.RESUME_GAME, payload: undefined }),
    [dispatch]
  );
  const dispatchEvent = useCallback(
    (event: GameEvent) => {
      gameManager.dispatchEvent(event);
      dispatch({ type: GameActionType.ADD_EVENT, payload: event });
    },
    [dispatch]
  );

  useEffect(() => {
    // Sync game manager state with context
    const unsubscribe = gameManager.subscribe(gameTime => {
      // Update game time in context
      dispatch({
        type: GameActionType.UPDATE_GAME_TIME,
        payload: gameTime,
      });
    });

    // Listen for game events
    const unsubscribeEvents = gameManager.addEventListener('*', (event: GameEvent) => {
      dispatch({
        type: GameActionType.ADD_EVENT,
        payload: event,
      });
    });

    // Listen for mission events
    const unsubscribeMissionEvents = moduleEventBus.subscribe('MISSION_COMPLETED', event => {
      if (event.moduleType === 'radar' && event.data) {
        if (!isMissionCompletedEventData(event.data)) {
          console.warn('Invalid mission data format:', event.data);
          return;
        }

        const missionData = event.data;
        dispatch({
          type: GameActionType.ADD_MISSION,
          payload: {
            id: `mission-${Date.now()}`,
            type: missionData.type,
            timestamp: event.timestamp,
            description: missionData.description,
            sector: missionData.sector,
            importance: missionData.importance,
            xpGained: missionData.xpGained,
            resourcesFound: missionData.resourcesFound,
            anomalyDetails: missionData.anomalyDetails,
          },
        });

        // Update mission statistics
        dispatch({
          type: GameActionType.UPDATE_MISSION_STATS,
          payload: {
            totalXP: missions.statistics.totalXP + (missionData.xpGained || 0),
            discoveries:
              missions.statistics.discoveries + (missionData.type === 'discovery' ? 1 : 0),
            anomalies: missions.statistics.anomalies + (missionData.type === 'anomaly' ? 1 : 0),
            resourcesFound:
              missions.statistics.resourcesFound + (missionData.resourcesFound?.length || 0),
            highPriorityCompleted:
              missions.statistics.highPriorityCompleted +
              (missionData.importance === 'high' ? 1 : 0),
          },
        });
      }
    });

    // Listen for sector updates
    const unsubscribeSectorUpdates = moduleEventBus.subscribe(
      'AUTOMATION_CYCLE_COMPLETE',
      event => {
        if (event.moduleType === 'radar' && event.data) {
          if (!isSectorUpdateEventData(event.data)) {
            console.warn('Invalid sector update data format:', event.data);
            return;
          }

          const sectorData = event.data;
          dispatch({
            type: GameActionType.UPDATE_SECTOR,
            payload: {
              sectorId: sectorData.task.target.id,
              data: {
                status: 'mapped' as const,
                resourcePotential: sectorData.resourcePotential || 0,
                habitabilityScore: sectorData.habitabilityScore || 0,
                heatMapValue: sectorData.heatMapValue || 0,
                lastScanned: Date.now(),
              },
            },
          });
        }
      }
    );

    // Sync running state
    if (isRunning && !gameManager.isGameRunning()) {
      gameManager.start();
    }

    // Sync pause state
    if (isPaused !== gameManager.isGamePaused()) {
      if (isPaused) {
        gameManager.pause();
      } else {
        gameManager.resume();
      }
    }

    return () => {
      unsubscribe();
      unsubscribeEvents();
      unsubscribeMissionEvents();
      unsubscribeSectorUpdates();
    };
  }, [isRunning, isPaused, missions.statistics, dispatch]);

  // Return the game state with action methods - measure computation time for complex derived state
  return measureComputationTime(
    'returnStateObject',
    () => ({
      isRunning,
      isPaused,
      gameTime,
      resources,
      resourceRates,
      systems,
      missions,
      exploration: {
        sectors,
        ships,
      },
      startGame,
      pauseGame,
      resumeGame,
      dispatchEvent,
    }),
    gameStatePerformanceConfig
  );
}

