/**
 * useSessionPerformance
 *
 * A React hook for tracking component performance and user interactions.
 * Provides access to the SessionPerformanceTracker for anonymous telemetry.
 */

import { useEffect, useRef } from 'react';
import {
  SessionPerformanceTracker,
  TelemetryOptions,
  UserInteractionData,
} from '../../services/telemetry/SessionPerformanceTracker';

// Create a singleton instance of the tracker to be shared across the application
let globalTracker: SessionPerformanceTracker | null = null;

/**
 * Initialize the global performance tracker
 */
export function initializeSessionPerformanceTracker(options?: Partial<TelemetryOptions>): void {
  if (!globalTracker) {
    globalTracker = new SessionPerformanceTracker(options);
  }
}

/**
 * React hook for tracking component performance
 */
export default function useSessionPerformance(componentId: string) {
  const trackerRef = useRef<SessionPerformanceTracker | null>(null);

  // Initialize the tracker on the first render
  useEffect(() => {
    // Create global instance if it doesn't exist yet
    if (!globalTracker) {
      initializeSessionPerformanceTracker();
    }

    trackerRef.current = globalTracker;

    // Start timing the component load
    if (trackerRef.current) {
      trackerRef.current.startComponentLoadTimer(componentId);
    }

    // When component mounts, record completion time
    const tracker = trackerRef.current;
    if (tracker) {
      // Use queueMicrotask to ensure we measure after initial render
      queueMicrotask(() => {
        tracker.endComponentLoadTimer(componentId);
      });
    }

    return () => {
      // Optionally track unmount time or other component lifecycle events
    };
  }, [componentId]);

  /**
   * Track a custom user interaction within the component
   */
  const trackInteraction = (
    interactionType: 'click' | 'hover' | 'scroll' | 'keypress' | 'custom',
    details: Partial<UserInteractionData> = {}
  ) => {
    if (!trackerRef.current) return;

    trackerRef.current.trackUserInteraction({
      interactionType,
      targetComponent: componentId,
      timestamp: Date.now(),
      responseTime: 0, // Will be populated with measured time
      successful: true,
      ...details,
    });
  };

  /**
   * Create a performance-tracked event handler
   */
  const withPerformanceTracking = <T extends (...args: any[]) => any>(
    handler: T,
    interactionType: 'click' | 'hover' | 'scroll' | 'keypress' | 'custom' = 'click'
  ): ((...args: Parameters<T>) => ReturnType<T>) => {
    return (...args: Parameters<T>) => {
      const startTime = performance.now();

      try {
        const result = handler(...args);

        // For promise-returning handlers, track completion when promise resolves
        if (result instanceof Promise) {
          result
            .then(() => {
              if (trackerRef.current) {
                trackerRef.current.trackUserInteraction({
                  interactionType,
                  targetComponent: componentId,
                  timestamp: Date.now(),
                  responseTime: performance.now() - startTime,
                  successful: true,
                });
              }
            })
            .catch(() => {
              if (trackerRef.current) {
                trackerRef.current.trackUserInteraction({
                  interactionType,
                  targetComponent: componentId,
                  timestamp: Date.now(),
                  responseTime: performance.now() - startTime,
                  successful: false,
                });
              }
            });
        } else {
          // For synchronous handlers, track completion immediately
          if (trackerRef.current) {
            trackerRef.current.trackUserInteraction({
              interactionType,
              targetComponent: componentId,
              timestamp: Date.now(),
              responseTime: performance.now() - startTime,
              successful: true,
            });
          }
        }

        return result;
      } catch (error) {
        // Track failed interactions
        if (trackerRef.current) {
          trackerRef.current.trackUserInteraction({
            interactionType,
            targetComponent: componentId,
            timestamp: Date.now(),
            responseTime: performance.now() - startTime,
            successful: false,
          });
        }
        throw error;
      }
    };
  };

  return {
    trackInteraction,
    withPerformanceTracking,
  };
}

import { useEffect, useState } from 'react';
import { useEventSubscription } from '../../lib/events/UnifiedEventSystem';
import { resourceSystem, ResourceSystem } from '../../resource/ResourceSystem';
import { ResourceState, ResourceType, ResourceTransfer } from '../../types/resources/ResourceTypes';
import { StorageContainerConfig } from '../../resource/subsystems/ResourceStorageSubsystem';
import { FlowNode, FlowConnection } from '../../resource/subsystems/ResourceFlowSubsystem';
import { ResourceThreshold } from '../../resource/subsystems/ResourceThresholdSubsystem';

// Make sure ResourceSystem is initialized
resourceSystem.initialize().catch(error => {
  console.error('Failed to initialize ResourceSystem:', error);
});

/**
 * Hook to access the resource system
 * 
 * Provides methods to interact with the unified resource system
 * and subscribes to relevant resource events.
 */
export function useResourceSystem() {
  const [isReady, setIsReady] = useState(false);

  // Initialize the resource system when the hook is first used
  useEffect(() => {
    let isMounted = true;

    resourceSystem.initialize()
      .then(() => {
        if (isMounted) {
          setIsReady(true);
        }
      })
      .catch(error => {
        console.error('Failed to initialize ResourceSystem in hook:', error);
      });

    return () => {
      isMounted = false;
    };
  }, []);

  // Subscribe to resource events
  useEventSubscription('RESOURCE_THRESHOLD_REACHED', (event) => {
    console.log('Resource threshold reached:', event);
  });

  useEventSubscription('RESOURCE_TRANSFERRED', (event) => {
    console.log('Resource transferred:', event);
  });

  /**
   * Get a resource state
   */
  function getResourceState(type: ResourceType): ResourceState | undefined {
    return resourceSystem.getResourceState(type);
  }

  /**
   * Update a resource state
   */
  function updateResourceState(type: ResourceType, state: ResourceState): void {
    resourceSystem.updateResourceState(type, state);
  }

  /**
   * Get the current resource total
   */
  function getResourceTotal(type: ResourceType): number {
    return resourceSystem.getResourceTotal(type);
  }

  /**
   * Check if a resource exists
   */
  function hasResource(type: ResourceType): boolean {
    return resourceSystem.hasResource(type);
  }

  /**
   * Check if a resource has at least the specified amount
   */
  function hasResourceAmount(type: ResourceType, amount: number): boolean {
    return resourceSystem.hasResourceAmount(type, amount);
  }

  /**
   * Get available resource space
   */
  function getAvailableSpace(type: ResourceType): number {
    return resourceSystem.getAvailableSpace(type);
  }

  /**
   * Store a resource
   */
  function storeResource(type: ResourceType, amount: number, targetId?: string): number {
    return resourceSystem.storeResource(type, amount, targetId);
  }

  /**
   * Retrieve a resource
   */
  function retrieveResource(type: ResourceType, amount: number, sourceId?: string): number {
    return resourceSystem.retrieveResource(type, amount, sourceId);
  }

  /**
   * Transfer resources between entities
   */
  function transferResource(
    type: ResourceType,
    amount: number,
    sourceId: string,
    targetId: string
  ): number {
    return resourceSystem.transferResource(type, amount, sourceId, targetId);
  }

  /**
   * Register a resource flow
   */
  function registerResourceFlow(
    sourceId: string,
    targetId: string,
    type: ResourceType,
    rate: number
  ): boolean {
    return resourceSystem.registerResourceFlow(sourceId, targetId, type, rate);
  }

  /**
   * Convert resources from one type to another
   */
  function convertResources(
    inputType: ResourceType,
    inputAmount: number,
    outputType: ResourceType,
    outputAmount: number,
    sourceId: string
  ): boolean {
    return resourceSystem.convertResources(
      inputType,
      inputAmount,
      outputType,
      outputAmount,
      sourceId
    );
  }

  /**
   * Get all recent resource transfers
   */
  function getTransferHistory(): ResourceTransfer[] {
    return resourceSystem.getTransferHistory();
  }

  /**
   * Register a storage container
   */
  function registerContainer(config: StorageContainerConfig): boolean {
    return resourceSystem.getStorageSubsystem().registerContainer(config);
  }

  /**
   * Get a storage container
   */
  function getContainer(id: string) {
    return resourceSystem.getStorageSubsystem().getContainer(id);
  }

  /**
   * Register a flow node
   */
  function registerNode(node: FlowNode): boolean {
    return resourceSystem.getFlowSubsystem().registerNode(node);
  }

  /**
   * Get a flow node
   */
  function getNode(id: string) {
    return resourceSystem.getFlowSubsystem().getNode(id);
  }

  /**
   * Register a threshold
   */
  function registerThreshold(threshold: ResourceThreshold): boolean {
    return resourceSystem.getThresholdSubsystem().registerThreshold(threshold);
  }

  /**
   * Get a threshold
   */
  function getThreshold(id: string) {
    return resourceSystem.getThresholdSubsystem().getThreshold(id);
  }

  /**
   * Optimize resource flows
   */
  async function optimizeFlows() {
    return resourceSystem.getFlowSubsystem().optimizeFlows();
  }

  return {
    // Core functionality
    isReady,
    getResourceState,
    updateResourceState,
    getResourceTotal,
    hasResource,
    hasResourceAmount,
    getAvailableSpace,
    storeResource,
    retrieveResource,
    transferResource,
    registerResourceFlow,
    convertResources,
    getTransferHistory,

    // Storage subsystem
    registerContainer,
    getContainer,
    getAllContainers: () => resourceSystem.getStorageSubsystem().getAllContainers(),
    getContainersByResourceType: (type: ResourceType) => 
      resourceSystem.getStorageSubsystem().getContainersByResourceType(type),
    transferBetweenContainers: (sourceId: string, targetId: string, type: ResourceType, amount: number) =>
      resourceSystem.getStorageSubsystem().transferBetweenContainers(sourceId, targetId, type, amount),

    // Flow subsystem
    registerNode,
    getNode,
    getAllNodes: () => resourceSystem.getFlowSubsystem().getNodes(),
    registerConnection: (connection: FlowConnection) => 
      resourceSystem.getFlowSubsystem().registerConnection(connection),
    getConnection: (id: string) => resourceSystem.getFlowSubsystem().getConnection(id),
    getAllConnections: () => resourceSystem.getFlowSubsystem().getConnections(),
    optimizeFlows,

    // Threshold subsystem
    registerThreshold,
    getThreshold,
    getAllThresholds: () => resourceSystem.getThresholdSubsystem().getAllThresholds(),
    getThresholdsByResourceType: (type: ResourceType) => 
      resourceSystem.getThresholdSubsystem().getThresholdsByResourceType(type),
    enableThreshold: (id: string) => resourceSystem.getThresholdSubsystem().enableThreshold(id),
    disableThreshold: (id: string) => resourceSystem.getThresholdSubsystem().disableThreshold(id),

    // Direct access to subsystems (for advanced use cases)
    getStorageSubsystem: () => resourceSystem.getStorageSubsystem(),
    getFlowSubsystem: () => resourceSystem.getFlowSubsystem(),
    getTransferSubsystem: () => resourceSystem.getTransferSubsystem(),
    getThresholdSubsystem: () => resourceSystem.getThresholdSubsystem(),

    // Access to the raw resource system
    system: resourceSystem,
  };
}

export default useResourceSystem;

import { useEffect, useRef } from 'react';
import { componentRegistryService } from '../../services/ComponentRegistryService';
import { useComponentProfiler } from './useComponentProfiler';

/**
 * Options for component registration
 */
export interface ComponentRegistrationOptions {
  /**
   * Type of the component (e.g., 'ResourceDisplay', 'ModuleCard')
   */
  type: string;

  /**
   * Event types this component is interested in receiving
   */
  eventSubscriptions: string[];

  /**
   * Update priority for this component
   * - high: Component updates are critical for game functionality
   * - medium: Component updates are important but not critical
   * - low: Component updates can be delayed if necessary
   */
  updatePriority?: 'high' | 'medium' | 'low';
}

/**
 * Hook that registers a React component with the ComponentRegistryService
 *
 * This hook:
 * 1. Generates a unique ID for the component
 * 2. Registers the component with the registry
 * 3. Sets up profiling to measure render times
 * 4. Automatically cleans up on unmount
 *
 * @param options Component registration options
 * @returns The generated component ID
 *
 * @example
 * function ResourceDisplay({ resourceType }) {
 *   // Register component with the system
 *   useComponentRegistration({
 *     type: 'ResourceDisplay',
 *     eventSubscriptions: ['RESOURCE_PRODUCED', 'RESOURCE_CONSUMED'],
 *     updatePriority: 'high'
 *   });
 *
 *   // Component implementation...
 * }
 */
export function useComponentRegistration(options: ComponentRegistrationOptions): string {
  const componentId = useRef<string>('');
  const profiler = useComponentProfiler(options.type);

  useEffect(() => {
    // Register component with registry
    const id = componentRegistryService.registerComponent({
      type: options.type,
      eventSubscriptions: options.eventSubscriptions,
      updatePriority: options.updatePriority || 'medium',
    });

    componentId.current = id;

    // Track render with profiler
    if (profiler) {
      const renderTime = profiler.metrics.lastRenderTime;
      if (renderTime !== undefined) {
        componentRegistryService.trackRender(id);
      }
    }

    // Return cleanup function
    return () => {
      componentRegistryService.unregisterComponent(id);
    };
  }, [options.type, options.updatePriority, profiler]);

  return componentId.current;
}

/**
 * Hook that registers a component and provides a method to manually update its metrics
 *
 * This is useful for components that need to manually control when metrics are updated,
 * such as those with complex rendering logic or conditional renders.
 *
 * @param options Component registration options
 * @returns An object containing the component ID and an update function
 */
export function useComponentRegistrationWithManualUpdates(options: ComponentRegistrationOptions): {
  componentId: string;
  updateMetrics: (renderTime: number) => void;
} {
  const componentId = useComponentRegistration(options);

  // Function to manually update metrics
  const updateMetrics = (renderTime: number) => {
    componentRegistryService.trackRender(componentId);
  };

  return { componentId, updateMetrics };
}

import { useEffect, useRef, useState } from 'react';

/**
 * Options for the memory manager hook
 */
export interface MemoryManagerOptions {
  /**
   * The key to use for identifying this instance in logs and metrics
   */
  key: string;

  /**
   * Initial data size estimation in bytes
   * (if not provided, it will be estimated)
   */
  initialDataSizeEstimate?: number;

  /**
   * Whether to log memory usage statistics to console
   */
  enableLogging?: boolean;

  /**
   * Maximum memory size (in bytes) before triggering cleanup recommendations
   */
  memoryThreshold?: number;

  /**
   * Auto cleanup level, controlling aggressiveness of cleanup
   * - none: No automatic cleanup
   * - low: Cleanup only very large datasets when component is hidden
   * - medium: Cleanup large datasets when component is hidden and unused
   * - high: Aggressive cleanup of all data when not in view
   */
  autoCleanupLevel?: 'none' | 'low' | 'medium' | 'high';

  /**
   * Time in milliseconds for cache expiration
   */
  cacheExpirationTime?: number;
}

/**
 * Return type for the memory manager hook
 */
export interface MemoryManagerResult<T> {
  /**
   * Current memory usage estimation in bytes
   */
  memoryUsage: number;

  /**
   * Whether the memory usage is above the threshold
   */
  isAboveThreshold: boolean;

  /**
   * Function to manually trigger cleanup
   */
  cleanup: () => void;

  /**
   * Function to update the tracked data
   */
  updateData: (newData: T | null) => void;

  /**
   * Function to get cached data
   */
  getCachedData: (key: string) => any;

  /**
   * Function to cache data with expiration
   */
  cacheData: (key: string, data: any, expiresInMs?: number) => void;

  /**
   * Clear a specific cache entry
   */
  clearCacheEntry: (key: string) => void;

  /**
   * Clear all cache entries
   */
  clearAllCache: () => void;

  /**
   * Whether data is currently loaded in memory
   */
  isDataLoaded: boolean;
}

/**
 * Rough size estimation for JavaScript objects
 */
function estimateObjectSize(object: any): number {
  if (object === null || object === undefined) return 0;

  // Handle primitive types
  if (typeof object !== 'object') {
    // Strings - each character is 2 bytes in JavaScript
    if (typeof object === 'string') return object.length * 2;
    // Numbers - 8 bytes for number type
    if (typeof object === 'number') return 8;
    // Boolean - 4 bytes
    if (typeof object === 'boolean') return 4;
    return 0;
  }

  // Handle arrays
  if (Array.isArray(object)) {
    let size = 0;
    // Add sample-based estimation for large arrays to avoid performance issues
    if (object.length > 1000) {
      // Sample first 100 elements
      const sampleSize = 100;
      let sampleTotal = 0;
      for (let i = 0; i < sampleSize; i++) {
        sampleTotal += estimateObjectSize(object[i]);
      }
      // Estimate total based on average
      size = (sampleTotal / sampleSize) * object.length;
    } else {
      // For smaller arrays, calculate all elements
      for (let i = 0; i < object.length; i++) {
        size += estimateObjectSize(object[i]);
      }
    }
    return size;
  }

  // Handle regular objects
  let size = 0;
  for (const key in object) {
    if (Object.prototype.hasOwnProperty.call(object, key)) {
      // Size of property name
      size += key.length * 2;
      // Size of property value
      size += estimateObjectSize(object[key]);
    }
  }

  return size;
}

/**
 * Cache item with expiration
 */
interface CacheItem {
  data: any;
  expiry: number | null; // null means no expiration
}

/**
 * Hook for managing memory in components with large datasets
 *
 * This hook helps track memory usage, provides caching with expiration,
 * and helps with cleanup to prevent memory leaks.
 */
export function useMemoryManager<T = any>(
  initialData: T | null = null,
  options: MemoryManagerOptions
): MemoryManagerResult<T> {
  // Default options
  const {
    key,
    initialDataSizeEstimate,
    enableLogging = false,
    memoryThreshold = 50 * 1024 * 1024, // 50MB default threshold
    autoCleanupLevel = 'medium',
    cacheExpirationTime = 5 * 60 * 1000, // 5 minutes by default
  } = options;

  // Store the current data
  const dataRef = useRef<T | null>(initialData);

  // Store memory usage estimation
  const [memoryUsage, setMemoryUsage] = useState<number>(
    initialDataSizeEstimate || estimateObjectSize(initialData)
  );

  // Track if memory is above threshold
  const [isAboveThreshold, setIsAboveThreshold] = useState<boolean>(memoryUsage > memoryThreshold);

  // Track if data is currently loaded
  const [isDataLoaded, setIsDataLoaded] = useState<boolean>(!!initialData);

  // Cache storage
  const cacheRef = useRef<Map<string, CacheItem>>(new Map());

  // Visibility tracking
  const isVisibleRef = useRef<boolean>(true);

  // Track last user interaction with the component
  const lastInteractionRef = useRef<number>(Date.now());

  // Update memory usage estimation
  const updateMemoryUsage = () => {
    const estimatedSize = estimateObjectSize(dataRef.current);
    setMemoryUsage(estimatedSize);
    setIsAboveThreshold(estimatedSize > memoryThreshold);

    if (enableLogging) {
      console.log(
        `[MemoryManager:${key}] Memory usage: ${(estimatedSize / 1024 / 1024).toFixed(2)}MB`
      );
    }

    return estimatedSize;
  };

  // Update tracked data
  const updateData = (newData: T | null) => {
    dataRef.current = newData;
    setIsDataLoaded(!!newData);

    // Track interaction
    lastInteractionRef.current = Date.now();

    // Update memory usage
    updateMemoryUsage();
  };

  // Cache data with expiration
  const cacheData = (cacheKey: string, data: any, expiresInMs: number = cacheExpirationTime) => {
    const expiry = expiresInMs ? Date.now() + expiresInMs : null;
    cacheRef.current.set(cacheKey, { data, expiry });

    if (enableLogging) {
      console.log(
        `[MemoryManager:${key}] Cached data: ${cacheKey}, expires: ${expiry ? new Date(expiry).toLocaleString() : 'never'}`
      );
    }
  };

  // Get cached data
  const getCachedData = (cacheKey: string): any => {
    const item = cacheRef.current.get(cacheKey);

    if (!item) return null;

    // Check if item has expired
    if (item.expiry && Date.now() > item.expiry) {
      cacheRef.current.delete(cacheKey);
      if (enableLogging) {
        console.log(`[MemoryManager:${key}] Cache expired: ${cacheKey}`);
      }
      return null;
    }

    // Track interaction
    lastInteractionRef.current = Date.now();

    return item.data;
  };

  // Clear a specific cache entry
  const clearCacheEntry = (cacheKey: string) => {
    cacheRef.current.delete(cacheKey);
    if (enableLogging) {
      console.log(`[MemoryManager:${key}] Cache cleared: ${cacheKey}`);
    }
  };

  // Clear all cache entries
  const clearAllCache = () => {
    cacheRef.current.clear();
    if (enableLogging) {
      console.log(`[MemoryManager:${key}] All cache cleared`);
    }
  };

  // Function to clean up data
  const cleanup = () => {
    // Clear the data reference
    dataRef.current = null;
    setIsDataLoaded(false);

    // Clear expired cache entries
    const now = Date.now();
    for (const [cacheKey, item] of cacheRef.current.entries()) {
      if (item.expiry && now > item.expiry) {
        cacheRef.current.delete(cacheKey);
      }
    }

    // Update memory usage
    const newUsage = updateMemoryUsage();

    if (enableLogging) {
      console.log(
        `[MemoryManager:${key}] Cleanup performed. New memory usage: ${(newUsage / 1024 / 1024).toFixed(2)}MB`
      );
    }
  };

  // Set up visibility observer
  useEffect(() => {
    // Skip if autoCleanupLevel is none
    if (autoCleanupLevel === 'none') return;

    // Use IntersectionObserver to track visibility
    const observer = new IntersectionObserver(
      entries => {
        entries.forEach(entry => {
          isVisibleRef.current = entry.isIntersecting;

          // Perform cleanup when element goes out of view
          if (!entry.isIntersecting) {
            const timeSinceLastInteraction = Date.now() - lastInteractionRef.current;

            // Different cleanup strategies based on level
            if (
              autoCleanupLevel === 'high' ||
              (autoCleanupLevel === 'medium' && timeSinceLastInteraction > 60000) || // 1 minute
              (autoCleanupLevel === 'low' && timeSinceLastInteraction > 300000) // 5 minutes
            ) {
              if (enableLogging) {
                console.log(`[MemoryManager:${key}] Auto cleanup triggered (${autoCleanupLevel})`);
              }
              cleanup();
            }
          }
        });
      },
      { threshold: 0.1 } // 10% visibility threshold
    );

    // We need to find the parent element to observe
    // This assumes the component using this hook has an element with a specific data attribute
    const element = document.querySelector(`[data-memory-manager="${key}"]`);

    if (element) {
      observer.observe(element);
    } else if (enableLogging) {
      console.warn(`[MemoryManager:${key}] No element found with data-memory-manager="${key}"`);
    }

    // Cleanup function for the effect
    return () => {
      observer.disconnect();
      cleanup();
    };
  }, [key, autoCleanupLevel, enableLogging]);

  // Regular cache cleanup interval
  useEffect(() => {
    // Clean up expired cache items every minute
    const interval = setInterval(() => {
      const now = Date.now();
      let removedCount = 0;

      for (const [cacheKey, item] of cacheRef.current.entries()) {
        if (item.expiry && now > item.expiry) {
          cacheRef.current.delete(cacheKey);
          removedCount++;
        }
      }

      if (removedCount > 0 && enableLogging) {
        console.log(`[MemoryManager:${key}] Auto-cleared ${removedCount} expired cache items`);
      }
    }, 60000); // 1 minute interval

    return () => clearInterval(interval);
  }, [key, enableLogging]);

  // Cleanup when component unmounts
  useEffect(() => {
    return () => {
      if (enableLogging) {
        console.log(`[MemoryManager:${key}] Component unmounted, cleaning up resources`);
      }

      // Clear all data and cache
      dataRef.current = null;
      cacheRef.current.clear();
    };
  }, [key, enableLogging]);

  return {
    memoryUsage,
    isAboveThreshold,
    cleanup,
    updateData,
    getCachedData,
    cacheData,
    clearCacheEntry,
    clearAllCache,
    isDataLoaded,
  };
}

export default useMemoryManager;

import { useCallback, useEffect, useRef, useState } from 'react';
import { z } from 'zod';
import { ApiEndpoint, ApiError, ApiResponse, TypeSafeApiClient } from '../api/TypeSafeApiClient';

/**
 * State type for API requests
 */
export interface ApiRequestState<T> {
  data: T | null;
  isLoading: boolean;
  isError: boolean;
  error: ApiError | null;
  isValidationError: boolean;
  isSuccess: boolean;
}

/**
 * Options for API requests
 */
export interface UseApiOptions {
  /** Skip the request (do not execute automatically) */
  skip?: boolean;
  /** Enable request caching */
  enableCache?: boolean;
  /** Cache key (defaults to endpoint path) */
  cacheKey?: string;
  /** Cache time to live in milliseconds (default: 5 minutes) */
  cacheTTL?: number;
  /** Automatically retry failed requests */
  retry?: boolean;
  /** Maximum number of retry attempts */
  maxRetries?: number;
  /** Base delay between retries in milliseconds */
  retryDelay?: number;
  /** Callback when request completes successfully */
  onSuccess?: <T>(data: T) => void;
  /** Callback when request fails */
  onError?: (error: ApiError) => void;
}

// Simple in-memory cache implementation
interface CacheEntry<T> {
  data: T;
  timestamp: number;
  ttl: number;
}

const apiCache = new Map<string, CacheEntry<any>>();

/**
 * Hook for using the type-safe API client
 */
export function useTypedApi(apiClient: TypeSafeApiClient) {
  // Use a ref to track if the component is mounted
  const isMounted = useRef(true);

  // Clean up on unmount
  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  /**
   * Generic request function
   */
  const request = useCallback(
    async <
      RequestType,
      ResponseType,
      RequestSchema extends z.ZodType<RequestType>,
      ResponseSchema extends z.ZodType<ResponseType>,
    >(
      endpoint: ApiEndpoint<RequestType, ResponseType, RequestSchema, ResponseSchema>,
      requestData?: RequestType,
      options: UseApiOptions = {}
    ): Promise<ApiResponse<ResponseType>> => {
      // Get cache key if caching is enabled
      const cacheEnabled = options.enableCache ?? false;
      const cacheKey = options.cacheKey ?? `${endpoint.method}:${endpoint.path}`;

      // Check cache
      if (cacheEnabled && apiCache.has(cacheKey)) {
        const cachedEntry = apiCache.get(cacheKey) as CacheEntry<ApiResponse<ResponseType>>;
        const now = Date.now();
        if (now - cachedEntry.timestamp < cachedEntry.ttl) {
          return cachedEntry.data;
        } else {
          // Remove expired cache entry
          apiCache.delete(cacheKey);
        }
      }

      try {
        // Execute request
        const response = await apiClient.request(endpoint, requestData);

        // Cache the result if caching is enabled
        if (cacheEnabled) {
          apiCache.set(cacheKey, {
            data: response,
            timestamp: Date.now(),
            ttl: options.cacheTTL ?? 5 * 60 * 1000, // Default 5 minutes
          });
        }

        // Call success callback if provided
        if (options.onSuccess && response.isValid) {
          options.onSuccess(response.data);
        }

        return response;
      } catch (error) {
        // Handle retry logic
        if (options.retry && error instanceof ApiError) {
          const apiError = error as ApiError;
          const maxRetries = options.maxRetries ?? 3;
          const retryDelay = options.retryDelay ?? 1000;

          let retryAttempt = 0;
          let lastError = apiError;

          while (retryAttempt < maxRetries) {
            retryAttempt++;

            // Exponential backoff
            const delay = retryDelay * Math.pow(2, retryAttempt - 1);
            await new Promise(resolve => setTimeout(resolve, delay));

            try {
              const retryResponse = await apiClient.request(endpoint, requestData);

              // Cache the result if caching is enabled
              if (cacheEnabled) {
                apiCache.set(cacheKey, {
                  data: retryResponse,
                  timestamp: Date.now(),
                  ttl: options.cacheTTL ?? 5 * 60 * 1000,
                });
              }

              return retryResponse;
            } catch (retryError) {
              if (retryError instanceof ApiError) {
                lastError = retryError as ApiError;
              } else {
                throw retryError;
              }
            }
          }

          // If we've exhausted retries, throw the last error
          throw lastError;
        }

        // Call error callback if provided
        if (options.onError && error instanceof ApiError) {
          options.onError(error as ApiError);
        }

        throw error;
      }
    },
    [apiClient]
  );

  /**
   * Hook for query requests (GET)
   */
  const useQuery = useCallback(
    <ResponseType, ResponseSchema extends z.ZodType<ResponseType>>(
      path: string,
      responseSchema: ResponseSchema,
      options: UseApiOptions & {
        queryParams?: Record<string, string | number | boolean | undefined>;
        headers?: Record<string, string>;
        withCredentials?: boolean;
        dependencies?: any[];
      } = {}
    ) => {
      const [state, setState] = useState<ApiRequestState<ResponseType>>({
        data: null,
        isLoading: !options.skip,
        isError: false,
        error: null,
        isValidationError: false,
        isSuccess: false,
      });

      const { dependencies = [], skip = false, ...requestOptions } = options;

      const fetchData = useCallback(async () => {
        if (skip) return;

        setState(prev => ({ ...prev, isLoading: true }));

        try {
          const endpoint: ApiEndpoint<void, ResponseType, z.ZodType<void>, ResponseSchema> = {
            path,
            method: 'GET',
            requestSchema: z.void(),
            responseSchema,
            queryParams: options.queryParams,
            headers: options.headers,
            withCredentials: options.withCredentials,
          };

          const response = await request(endpoint, undefined, requestOptions);

          if (isMounted.current) {
            setState({
              data: response.data,
              isLoading: false,
              isError: false,
              error: null,
              isValidationError: !response.isValid,
              isSuccess: true,
            });
          }
        } catch (error) {
          if (isMounted.current) {
            setState({
              data: null,
              isLoading: false,
              isError: true,
              error: error instanceof ApiError ? error : null,
              isValidationError:
                error instanceof ApiError &&
                (error.type === 'RESPONSE_VALIDATION_ERROR' ||
                  error.type === 'REQUEST_VALIDATION_ERROR'),
              isSuccess: false,
            });
          }
        }
      }, [path, responseSchema, requestOptions, skip, ...dependencies]);

      useEffect(() => {
        fetchData();
      }, [fetchData]);

      return {
        ...state,
        refetch: fetchData,
      };
    },
    [request]
  );

  /**
   * Hook for mutation requests (POST, PUT, PATCH, DELETE)
   */
  const useMutation = useCallback(
    <
      RequestType,
      ResponseType,
      RequestSchema extends z.ZodType<RequestType>,
      ResponseSchema extends z.ZodType<ResponseType>,
    >(
      method: 'POST' | 'PUT' | 'PATCH' | 'DELETE',
      path: string,
      requestSchema: RequestSchema,
      responseSchema: ResponseSchema,
      options: UseApiOptions & {
        headers?: Record<string, string>;
        withCredentials?: boolean;
      } = {}
    ) => {
      const [state, setState] = useState<ApiRequestState<ResponseType>>({
        data: null,
        isLoading: false,
        isError: false,
        error: null,
        isValidationError: false,
        isSuccess: false,
      });

      const execute = useCallback(
        async (requestData: RequestType) => {
          setState(prev => ({ ...prev, isLoading: true }));

          try {
            const endpoint: ApiEndpoint<RequestType, ResponseType, RequestSchema, ResponseSchema> =
              {
                path,
                method,
                requestSchema,
                responseSchema,
                headers: options.headers,
                withCredentials: options.withCredentials,
              };

            const response = await request(endpoint, requestData, options);

            if (isMounted.current) {
              setState({
                data: response.data,
                isLoading: false,
                isError: false,
                error: null,
                isValidationError: !response.isValid,
                isSuccess: true,
              });
            }

            return response.data;
          } catch (error) {
            if (isMounted.current) {
              setState({
                data: null,
                isLoading: false,
                isError: true,
                error: error instanceof ApiError ? error : null,
                isValidationError:
                  error instanceof ApiError &&
                  (error.type === 'RESPONSE_VALIDATION_ERROR' ||
                    error.type === 'REQUEST_VALIDATION_ERROR'),
                isSuccess: false,
              });
            }

            throw error;
          }
        },
        [path, method, requestSchema, responseSchema, options]
      );

      return {
        ...state,
        execute,
      };
    },
    [request]
  );

  return {
    useQuery,
    useMutation,
    request,
  };
}

/**
 * Creates a POST mutation hook
 */
export function usePost<
  RequestType,
  ResponseType,
  RequestSchema extends z.ZodType<RequestType>,
  ResponseSchema extends z.ZodType<ResponseType>,
>(
  apiClient: TypeSafeApiClient,
  path: string,
  requestSchema: RequestSchema,
  responseSchema: ResponseSchema,
  options: UseApiOptions & {
    headers?: Record<string, string>;
    withCredentials?: boolean;
  } = {}
) {
  const { useMutation } = useTypedApi(apiClient);
  return useMutation<RequestType, ResponseType, RequestSchema, ResponseSchema>(
    'POST',
    path,
    requestSchema,
    responseSchema,
    options
  );
}

