import { useCallback, useEffect, useState } from 'react';
import { workerService } from '../services/WorkerService';
import { useService } from './services/useService';

interface UseWorkerOptions {
  type: string;
  priority?: 'HIGH' | 'MEDIUM' | 'LOW';
  onProgress?: (progress: number) => void;
}

interface UseWorkerResult<T> {
  execute: (data: unknown) => Promise<T>;
  cancel: () => void;
  isRunning: boolean;
  progress: number;
  error: Error | null;
}

export function useWorker<T>({
  type,
  priority = 'MEDIUM',
  onProgress,
}: UseWorkerOptions): UseWorkerResult<T> {
  const { service } = useService<typeof workerService>('worker');
  const [currentTaskId, setCurrentTaskId] = useState<string | null>(null);
  const [isRunning, setIsRunning] = useState(false);
  const [progress, setProgress] = useState(0);
  const [error, setError] = useState<Error | null>(null);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (currentTaskId) {
        service?.cancelTask(currentTaskId);
      }
    };
  }, [currentTaskId, service]);

  const execute = useCallback(
    async (data: unknown): Promise<T> => {
      if (!service) {
        throw new Error('Worker service not available');
      }

      setIsRunning(true);
      setProgress(0);
      setError(null);

      try {
        const result = await service.submitTask<T>(type, data, priority);
        return result;
      } catch (err) {
        const error = err instanceof Error ? err : new Error(String(err));
        setError(error);
        throw error;
      } finally {
        setIsRunning(false);
        setCurrentTaskId(null);
      }
    },
    [service, type, priority]
  );

  const cancel = useCallback(() => {
    if (currentTaskId && service) {
      service.cancelTask(currentTaskId);
      setIsRunning(false);
      setCurrentTaskId(null);
    }
  }, [currentTaskId, service]);

  return {
    execute,
    cancel,
    isRunning,
    progress,
    error,
  };
}

/**
 * ResourceConsumptionPredictor
 *
 * A machine learning model implementation for predicting resource consumption patterns.
 * This system analyzes historical resource usage data to predict future consumption
 * trends, allowing for optimized resource allocation and proactive performance tuning.
 */

import { PerformanceMetrics } from '../../managers/resource/ResourcePerformanceMonitor';
import { ResourceType } from '../../types/resources/ResourceTypes';

/**
 * Data point for model training and prediction
 */
export interface ConsumptionDataPoint {
  timestamp: number;
  resourceType: ResourceType;
  value: number;
  sessionDuration: number;
  userActions: number;
  systemLoad: number;
  devicePerformanceScore?: number;
}

/**
 * Prediction result with confidence metrics
 */
export interface ConsumptionPrediction {
  resourceType: ResourceType;
  predictedValue: number;
  confidence: number;
  timeframe: 'short' | 'medium' | 'long'; // prediction timeframe
  potentialSavings: number;
  timestamp: number;
}

/**
 * Trained model data structure
 */
interface ModelData {
  resourceType: ResourceType;
  coefficients: number[];
  intercept: number;
  confidenceScore: number;
  lastUpdated: number;
}

/**
 * Machine learning model for predicting resource consumption
 * Uses a linear regression approach with support for online learning
 */
export class ResourceConsumptionPredictor {
  private models: Map<ResourceType, ModelData> = new Map();
  private historicalData: Map<ResourceType, ConsumptionDataPoint[]> = new Map();
  private predictionCache: Map<ResourceType, ConsumptionPrediction> = new Map();

  // Configuration
  private maxHistoricalDataPoints = 1000;
  private predictionUpdateIntervalMs = 60000; // 1 minute
  private minDataPointsForTraining = 30;
  private lastPredictionTime: number = 0;

  constructor() {
    this.lastPredictionTime = Date.now();
  }

  /**
   * Initialize the predictor with existing metrics history if available
   */
  public initialize(metricsHistory?: Map<ResourceType, PerformanceMetrics[]>): void {
    if (metricsHistory) {
      for (const [resourceType, metrics] of metricsHistory.entries()) {
        const dataPoints: ConsumptionDataPoint[] = metrics.map(metric => ({
          timestamp: metric.timestamp,
          resourceType: metric.resourceType,
          value: metric.consumptionRate,
          sessionDuration: 0, // Will be calculated based on first entry
          userActions: 0, // Will need to be populated from event data
          systemLoad: 0, // Will be derived from overall metrics
        }));

        this.historicalData.set(resourceType, dataPoints);
      }

      // Initialize models for resources with sufficient data
      this.initializeModels();
    }

    console.log('[ResourceConsumptionPredictor] Initialized predictor');
  }

  /**
   * Add a new consumption data point for training
   */
  public addDataPoint(dataPoint: ConsumptionDataPoint): void {
    const { resourceType } = dataPoint;

    if (!this.historicalData.has(resourceType)) {
      this.historicalData.set(resourceType, []);
    }

    const dataPoints = this.historicalData.get(resourceType)!;
    dataPoints.push(dataPoint);

    // Limit the number of data points to prevent memory issues
    if (dataPoints.length > this.maxHistoricalDataPoints) {
      dataPoints.shift();
    }

    // Check if we need to update the model
    if (dataPoints.length >= this.minDataPointsForTraining) {
      this.trainModel(resourceType);
    }

    // Check if we need to update predictions
    if (Date.now() - this.lastPredictionTime > this.predictionUpdateIntervalMs) {
      this.updateAllPredictions();
      this.lastPredictionTime = Date.now();
    }
  }

  /**
   * Train models for all resource types that have sufficient data
   */
  private initializeModels(): void {
    for (const [resourceType, dataPoints] of this.historicalData.entries()) {
      if (dataPoints.length >= this.minDataPointsForTraining) {
        this.trainModel(resourceType);
      }
    }
  }

  /**
   * Train the model for a specific resource type
   * Implements multiple linear regression
   */
  private trainModel(resourceType: ResourceType): void {
    const dataPoints = this.historicalData.get(resourceType);
    if (!dataPoints || dataPoints.length < this.minDataPointsForTraining) {
      return;
    }

    // Extract features and target values
    const features: number[][] = dataPoints.map(dp => [
      dp.sessionDuration,
      dp.userActions,
      dp.systemLoad,
      dp.devicePerformanceScore || 1.0, // Default to 1.0 if not provided
      Math.sin(2 * Math.PI * (new Date(dp.timestamp).getHours() / 24)), // Time of day feature
      Math.cos(2 * Math.PI * (new Date(dp.timestamp).getHours() / 24)), // Time of day feature
    ]);

    const targets: number[] = dataPoints.map(dp => dp.value);

    // Implement linear regression using normal equation
    // X'X coefficients = X'y
    try {
      const coefficients = this.multipleLinearRegression(features, targets);

      // Calculate model confidence (R-squared)
      const predictions = features.map(feature =>
        this.predictWithCoefficients(feature, coefficients.slice(1), coefficients[0])
      );

      const confidenceScore = this.calculateConfidenceScore(targets, predictions);

      // Store the trained model
      this.models.set(resourceType, {
        resourceType,
        coefficients: coefficients.slice(1),
        intercept: coefficients[0],
        confidenceScore,
        lastUpdated: Date.now(),
      });

      console.log(
        `[ResourceConsumptionPredictor] Trained model for ${resourceType} with confidence: ${confidenceScore.toFixed(2)}`
      );
    } catch (error) {
      console.error(
        `[ResourceConsumptionPredictor] Error training model for ${resourceType}:`,
        error
      );
    }
  }

  /**
   * Implement multiple linear regression using normal equation
   */
  private multipleLinearRegression(features: number[][], targets: number[]): number[] {
    // Add a column of 1s for the intercept term
    const X = features.map(feature => [1, ...feature]);

    // Compute X^T * X
    const XtX = this.matrixMultiply(this.transpose(X), X);

    // Compute (X^T * X)^-1
    const XtXInv = this.invertMatrix(XtX);

    // Compute X^T * y
    const Xty = this.matrixVectorMultiply(this.transpose(X), targets);

    // Compute (X^T * X)^-1 * X^T * y
    return this.matrixVectorMultiply(XtXInv, Xty);
  }

  /**
   * Matrix multiplication
   */
  private matrixMultiply(a: number[][], b: number[][]): number[][] {
    const result: number[][] = [];

    for (let i = 0; i < a.length; i++) {
      result[i] = [];
      for (let j = 0; j < b[0].length; j++) {
        let sum = 0;
        for (let k = 0; k < a[0].length; k++) {
          sum += a[i][k] * b[k][j];
        }
        result[i][j] = sum;
      }
    }

    return result;
  }

  /**
   * Matrix-vector multiplication
   */
  private matrixVectorMultiply(a: number[][], b: number[]): number[] {
    const result: number[] = [];

    for (let i = 0; i < a.length; i++) {
      let sum = 0;
      for (let j = 0; j < b.length; j++) {
        sum += a[i][j] * b[j];
      }
      result[i] = sum;
    }

    return result;
  }

  /**
   * Matrix transpose
   */
  private transpose(matrix: number[][]): number[][] {
    const rows = matrix.length;
    const cols = matrix[0].length;
    const result: number[][] = [];

    for (let j = 0; j < cols; j++) {
      result[j] = [];
      for (let i = 0; i < rows; i++) {
        result[j][i] = matrix[i][j];
      }
    }

    return result;
  }

  /**
   * Matrix inversion using Gaussian elimination
   * This is a simplified approach and may not be numerically stable for all matrices
   */
  private invertMatrix(matrix: number[][]): number[][] {
    const n = matrix.length;

    // Create augmented matrix [A|I]
    const augmented: number[][] = [];
    for (let i = 0; i < n; i++) {
      augmented[i] = [...matrix[i]];
      for (let j = 0; j < n; j++) {
        augmented[i].push(i === j ? 1 : 0);
      }
    }

    // Gaussian elimination
    for (let i = 0; i < n; i++) {
      // Find the maximum element in the current column
      let maxRow = i;
      for (let j = i + 1; j < n; j++) {
        if (Math.abs(augmented[j][i]) > Math.abs(augmented[maxRow][i])) {
          maxRow = j;
        }
      }

      // Swap the maximum row with the current row
      [augmented[i], augmented[maxRow]] = [augmented[maxRow], augmented[i]];

      // Scale the current row to have a 1 on the diagonal
      const scale = augmented[i][i];
      if (Math.abs(scale) < 1e-10) {
        throw new Error('Matrix is singular or nearly singular');
      }

      for (let j = 0; j < 2 * n; j++) {
        augmented[i][j] /= scale;
      }

      // Eliminate other rows
      for (let j = 0; j < n; j++) {
        if (j !== i) {
          const factor = augmented[j][i];
          for (let k = 0; k < 2 * n; k++) {
            augmented[j][k] -= factor * augmented[i][k];
          }
        }
      }
    }

    // Extract the right side of the augmented matrix
    const result: number[][] = [];
    for (let i = 0; i < n; i++) {
      result[i] = augmented[i].slice(n);
    }

    return result;
  }

  /**
   * Calculate the confidence score (R-squared) of the model
   */
  private calculateConfidenceScore(actual: number[], predicted: number[]): number {
    const mean = actual.reduce((sum, value) => sum + value, 0) / actual.length;

    const totalSumOfSquares = actual.reduce((sum, value) => sum + Math.pow(value - mean, 2), 0);
    const residualSumOfSquares = actual.reduce(
      (sum, value, i) => sum + Math.pow(value - predicted[i], 2),
      0
    );

    return 1 - residualSumOfSquares / totalSumOfSquares;
  }

  /**
   * Predict consumption with model coefficients
   */
  private predictWithCoefficients(
    features: number[],
    coefficients: number[],
    intercept: number
  ): number {
    return intercept + features.reduce((sum, value, index) => sum + value * coefficients[index], 0);
  }

  /**
   * Generate consumption predictions for a resource type
   */
  public predict(
    resourceType: ResourceType,
    contextData: {
      sessionDuration: number;
      userActions: number;
      systemLoad: number;
      devicePerformanceScore?: number;
    }
  ): ConsumptionPrediction | null {
    const model = this.models.get(resourceType);
    if (!model) {
      return null;
    }

    // Create feature vector for prediction
    const features = [
      contextData.sessionDuration,
      contextData.userActions,
      contextData.systemLoad,
      contextData.devicePerformanceScore || 1.0,
      Math.sin(2 * Math.PI * (new Date().getHours() / 24)), // Time of day feature
      Math.cos(2 * Math.PI * (new Date().getHours() / 24)), // Time of day feature
    ];

    // Make prediction
    const predictedValue = this.predictWithCoefficients(
      features,
      model.coefficients,
      model.intercept
    );

    // Create prediction object
    const prediction: ConsumptionPrediction = {
      resourceType,
      predictedValue: Math.max(0, predictedValue), // Ensure non-negative
      confidence: model.confidenceScore,
      timeframe: 'short', // Default to short-term prediction
      potentialSavings: 0, // Will be calculated later
      timestamp: Date.now(),
    };

    // Cache prediction
    this.predictionCache.set(resourceType, prediction);

    return prediction;
  }

  /**
   * Update predictions for all resource types
   */
  private updateAllPredictions(): void {
    const now = Date.now();
    const contextData = this.generateContextData(now);

    for (const resourceType of this.models.keys()) {
      this.predict(resourceType, contextData);
    }
  }

  /**
   * Generate context data for prediction
   */
  private generateContextData(timestamp: number): {
    sessionDuration: number;
    userActions: number;
    systemLoad: number;
    devicePerformanceScore?: number;
  } {
    // In a real implementation, this data would come from system monitoring
    // For now, use placeholder values
    return {
      sessionDuration: (timestamp - this.lastPredictionTime) / 1000, // in seconds
      userActions: Math.round(Math.random() * 10), // placeholder
      systemLoad: 0.5 + Math.random() * 0.3, // placeholder
      devicePerformanceScore: 1.0, // placeholder
    };
  }

  /**
   * Get all cached predictions
   */
  public getAllPredictions(): Map<ResourceType, ConsumptionPrediction> {
    return new Map(this.predictionCache);
  }

  /**
   * Calculate potential resource savings based on predictions
   */
  public calculatePotentialSavings(resourceType: ResourceType, currentConsumption: number): number {
    const prediction = this.predictionCache.get(resourceType);
    if (!prediction) {
      return 0;
    }

    // If current consumption is higher than predicted, we can potentially save the difference
    return Math.max(0, currentConsumption - prediction.predictedValue);
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.historicalData.clear();
    this.models.clear();
    this.predictionCache.clear();
    console.log('[ResourceConsumptionPredictor] Cleaned up predictor');
  }
}

/**
 * @file BaseContext.tsx
 * Provides a standardized template for context providers in the application.
 *
 * This file implements:
 * 1. A consistent pattern for context creation and usage
 * 2. Built-in error handling and loading states
 * 3. Performance optimization through memoization and context selectors
 * 4. Standard connection pattern to manager services
 * 5. Standardized state update mechanisms
 */

import React, { createContext, useContext, useEffect, useMemo, useRef } from 'react';
import { BaseEvent, EventType } from '../../types/events/EventTypes';
import { EventBus } from '../events/EventBus';

/**
 * Base state interface with common properties for all contexts
 */
export interface BaseState {
  /**
   * Whether the context is currently loading data
   */
  isLoading: boolean;

  /**
   * Error message if an error occurred
   */
  error: string | null;

  /**
   * Last time the state was updated
   */
  lastUpdated: number;
}

/**
 * Base action interface for all context actions
 */
export interface BaseAction<T extends string = string> {
  /**
   * The type of action being performed
   */
  type: T;

  /**
   * The payload for the action
   */
  payload?: unknown;
}

/**
 * Special action type for internal event-based updates
 */
export interface UpdateFromEventAction<TState extends BaseState>
  extends BaseAction<'UPDATE_FROM_EVENT'> {
  payload: TState;
}

/**
 * Selector function signature for extracting specific parts of state
 */
export type Selector<TState extends BaseState, TSelected> = (state: TState) => TSelected;

/**
 * Type for manager method
 */
export type ManagerMethod<TManager, TArgs extends unknown[] = unknown[], TReturn = unknown> = (
  manager: TManager,
  ...args: TArgs
) => TReturn;

/**
 * Manager interaction configuration
 */
export interface ManagerConfig<TManager> {
  /**
   * The manager instance to interact with
   */
  manager: TManager;

  /**
   * Methods to call on the manager from the context
   */
  methods: Record<string, ManagerMethod<TManager>>;

  /**
   * Events to subscribe to from the manager (if it uses an event bus)
   */
  events?: {
    /**
     * The event bus to subscribe to
     */
    eventBus: EventBus<BaseEvent>;

    /**
     * Map of event types to state update handlers
     */
    subscriptions: Record<EventType, (event: BaseEvent, draft: unknown) => void>;
  };
}

/**
 * Event handler function type
 */
export type EventHandler<TAction extends BaseAction> = (
  event: BaseEvent,
  dispatch: React.Dispatch<TAction>
) => void;

/**
 * Options for creating a context
 */
export interface ContextOptions<TState extends BaseState, TAction extends BaseAction> {
  /**
   * The name of the context (used for debugging)
   */
  name: string;

  /**
   * The initial state of the context
   */
  initialState: TState;

  /**
   * The reducer function for the context
   */
  reducer: (state: TState, action: TAction) => TState;

  /**
   * Event subscription options
   */
  eventSubscriptions?: {
    /**
     * Event bus to subscribe to
     */
    eventBus: EventBus<BaseEvent>;

    /**
     * Event types to subscribe to and their handlers
     */
    subscriptions: Record<EventType, EventHandler<TAction>>;
  };

  /**
   * Performance monitoring configuration
   */
  performanceMonitoring?: {
    /**
     * Whether to enable performance monitoring
     */
    enabled: boolean;

    /**
     * Threshold in milliseconds for slow reducer warnings
     */
    reducerThreshold?: number;

    /**
     * Threshold in milliseconds for slow selector warnings
     */
    selectorThreshold?: number;
  };

  /**
   * Debug mode options
   */
  debug?: {
    /**
     * Whether to log state changes
     */
    logStateChanges?: boolean;

    /**
     * Whether to log actions
     */
    logActions?: boolean;

    /**
     * Whether to log rendering
     */
    logRendering?: boolean;
  };
}

/**
 * Context result with provider, hooks, and utilities
 */
export interface ContextResult<
  TState extends BaseState,
  TAction extends BaseAction,
  TManager = unknown,
> {
  /**
   * The name of the context
   */
  contextName: string;

  /**
   * The React context object
   */
  Context: React.Context<
    | {
        state: TState;
        dispatch: React.Dispatch<TAction>;
      }
    | undefined
  >;

  /**
   * The provider component for the context
   */
  Provider: React.FC<{
    children: React.ReactNode;
    manager?: TManager;
    initialState?: Partial<TState>;
  }>;

  /**
   * Hook to use the entire context state
   */
  useContextState: () => TState;

  /**
   * Hook to use the context dispatch function
   */
  useContextDispatch: () => React.Dispatch<TAction>;

  /**
   * Hook to select a specific part of the context state
   */
  useContextSelector: <TSelected>(selector: Selector<TState, TSelected>) => TSelected;

  /**
   * Connect the context to a manager
   */
  connectToManager: (config: ManagerConfig<TManager>) => void;
}

/**
 * Creates a standardized context with the given options
 */
export function createStandardContext<
  TState extends BaseState,
  TAction extends BaseAction,
  TManager = unknown,
>(options: ContextOptions<TState, TAction>): ContextResult<TState, TAction, TManager> {
  type ContextType =
    | {
        state: TState;
        dispatch: React.Dispatch<TAction>;
      }
    | undefined;

  // Create a special action creator for internal event-based updates
  const createUpdateFromEventAction = (payload: TState): TAction => {
    return {
      type: 'UPDATE_FROM_EVENT',
      payload,
    } as unknown as TAction;
  };

  // Create the context
  const Context = createContext<ContextType>(undefined);
  Context.displayName = `${options.name}Context`;

  // Reference to the manager connection config
  const managerConfigRef = { current: null as ManagerConfig<TManager> | null };

  // Provider component
  const Provider: React.FC<{
    children: React.ReactNode;
    manager?: TManager;
    initialState?: Partial<TState>;
  }> = ({ children, manager, initialState }) => {
    // Combine default initial state with override props
    const combinedInitialState = {
      ...options.initialState,
      ...initialState,
    } as TState;

    // State and reducer
    const [state, dispatch] = React.useReducer((state: TState, action: TAction): TState => {
      if (options.debug?.logActions) {
        console.log(`[${options.name}] Action:`, action);
      }

      const startTime = options.performanceMonitoring?.enabled ? performance.now() : 0;

      // Apply the reducer
      const newState = options.reducer(state, action);

      // Update lastUpdated timestamp
      newState.lastUpdated = Date.now();

      if (options.performanceMonitoring?.enabled) {
        const endTime = performance.now();
        const duration = endTime - startTime;

        if (duration > (options.performanceMonitoring.reducerThreshold || 5)) {
          console.warn(
            `[${options.name}] Slow reducer for action ${action.type}: ${duration.toFixed(2)}ms`
          );
        }
      }

      if (options.debug?.logStateChanges) {
        console.log(`[${options.name}] New state:`, newState);
      }

      return newState;
    }, combinedInitialState);

    // Manager connection effect
    useEffect(() => {
      if (manager && managerConfigRef.current) {
        const config = managerConfigRef.current;

        // Set up event subscriptions if provided
        if (config.events) {
          const { eventBus, subscriptions } = config.events;

          // Subscribe to events
          const unsubscribers = Object.entries(subscriptions).map(([eventType, handler]) => {
            return eventBus.subscribe(eventType as EventType, event => {
              // Use Immer-like pattern with a draft
              const draft = { ...state };
              handler(event, draft);

              // Dispatch an action to update the state
              dispatch(createUpdateFromEventAction(draft as TState));
            });
          });

          // Clean up subscriptions
          return () => {
            unsubscribers.forEach(unsubscribe => unsubscribe());
          };
        }
      }

      return undefined;
    }, [manager, state]);

    // Event subscriptions effect
    useEffect(() => {
      if (options.eventSubscriptions) {
        const { eventBus, subscriptions } = options.eventSubscriptions;

        // Subscribe to events
        const unsubscribers = Object.entries(subscriptions).map(([eventType, handler]) => {
          return eventBus.subscribe(eventType as EventType, event => {
            handler(event, dispatch);
          });
        });

        // Clean up subscriptions
        return () => {
          unsubscribers.forEach(unsubscribe => unsubscribe());
        };
      }

      return undefined;
    }, []);

    // Memoize context value to prevent unnecessary renders
    const contextValue = useMemo(() => {
      if (options.debug?.logRendering) {
        console.log(`[${options.name}] Rendering provider`);
      }
      return { state, dispatch };
    }, [state]);

    return <Context.Provider value={contextValue}>{children}</Context.Provider>;
  };

  // Hook to use the context
  const useContextState = (): TState => {
    const context = useContext(Context);
    if (!context) {
      throw new Error(`use${options.name} must be used within a ${options.name}Provider`);
    }
    return context.state;
  };

  // Hook to use the dispatch function
  const useContextDispatch = (): React.Dispatch<TAction> => {
    const context = useContext(Context);
    if (!context) {
      throw new Error(`use${options.name}Dispatch must be used within a ${options.name}Provider`);
    }
    return context.dispatch;
  };

  // Hook to select a specific part of the context state
  const useContextSelector = <TSelected,>(selector: Selector<TState, TSelected>): TSelected => {
    const context = useContext(Context);
    if (!context) {
      throw new Error(`use${options.name}Selector must be used within a ${options.name}Provider`);
    }

    // Ref for the previous selected value
    const prevSelectedRef = useRef<TSelected | undefined>(undefined);

    // Performance monitoring
    const startTime = options.performanceMonitoring?.enabled ? performance.now() : 0;

    // Apply the selector
    const selected = selector(context.state);

    // Performance monitoring
    if (options.performanceMonitoring?.enabled) {
      const endTime = performance.now();
      const duration = endTime - startTime;

      if (duration > (options.performanceMonitoring.selectorThreshold || 2)) {
        console.warn(`[${options.name}] Slow selector: ${duration.toFixed(2)}ms`);
      }
    }

    // Memoize the selected value
    const memoizedSelected = useMemo(() => {
      const prevSelected = prevSelectedRef.current;

      // If we have a previous value and it's equal to the current value, return the previous value
      if (prevSelected !== undefined && Object.is(prevSelected, selected)) {
        return prevSelected;
      }

      // Otherwise, update the ref and return the new value
      prevSelectedRef.current = selected;
      return selected;
    }, [selected]);

    return memoizedSelected;
  };

  // Function to connect the context to a manager
  const connectToManager = (config: ManagerConfig<TManager>): void => {
    managerConfigRef.current = config;
  };

  return {
    contextName: options.name,
    Context,
    Provider,
    useContextState,
    useContextDispatch,
    useContextSelector,
    connectToManager,
  };
}

/**
 * Creates action creators for a context
 */
export function createActions<
  TActionMap extends Record<string, (...args: unknown[]) => BaseAction>,
>(actions: TActionMap): TActionMap {
  return actions;
}

/**
 * Creates a selector for a specific part of the context state
 */
export function createSelector<TState extends BaseState, TSelected>(
  selector: Selector<TState, TSelected>
): Selector<TState, TSelected> {
  return selector;
}

/**
 * Creates a selector for a specific property of the context state
 */
export function createPropertySelector<TState extends BaseState, K extends keyof TState>(
  property: K
): Selector<TState, TState[K]> {
  return state => state[property];
}

/**
 * @file ModuleEventBus.ts
 * Standardized implementation of the ModuleEventBus using the new EventBus base class.
 *
 * This file maintains backward compatibility with the original ModuleEvents.ts
 * while leveraging the new standardized architecture.
 */

import { ModuleType } from '../../types/buildings/ModuleTypes';
import {
  BaseEvent,
  EventType,
  createEvent,
  stringToEventType,
} from '../../types/events/EventTypes';
import { EventBus, EventListener } from './EventBus';

/**
 * Legacy type definition for ModuleEvent
 * Kept for backward compatibility
 */
export interface ModuleEvent extends BaseEvent {
  moduleId: string;
  moduleType: ModuleType;
  timestamp: number;
  data?: Record<string, unknown>;
}

/**
 * Legacy type definition for ModuleEventType
 * Kept for backward compatibility
 */
export type ModuleEventType = string;

/**
 * Legacy type definition for ModuleEventListener
 * Kept for backward compatibility
 */
export type ModuleEventListener = EventListener<ModuleEvent>;

/**
 * ModuleEventBus extension of the base EventBus class
 * Provides backward compatibility with the original ModuleEventBus implementation
 */
export class ModuleEventBus extends EventBus<ModuleEvent> {
  /**
   * Subscribe to a module event type
   * This method maintains the original API for backward compatibility
   *
   * @param type The type of event to subscribe to (string version)
   * @param listener The function to call when events of this type occur
   * @returns A function that, when called, unsubscribes the listener
   */
  subscribe(type: ModuleEventType | '*', listener: ModuleEventListener): () => void {
    // Convert string type to enum if possible
    const eventType =
      type !== '*' ? stringToEventType(type) || (type as unknown as EventType) : '*';

    // Call the parent class subscribe method
    return super.subscribe(eventType, listener, {
      source: 'LegacyModuleEventBus',
    });
  }

  /**
   * Legacy method to maintain compatibility
   * @deprecated Use the function returned by subscribe instead
   */
  legacyUnsubscribe(type: ModuleEventType, listener: ModuleEventListener): void {
    console.warn(
      '[ModuleEventBus] unsubscribe method is deprecated. Use the returned function from subscribe instead.'
    );
    // This method can't be properly implemented with the new architecture
    // The proper approach is to use the function returned by subscribe
  }

  /**
   * Emit a module event
   * This method maintains the original API for backward compatibility
   *
   * @param event The module event to emit
   */
  emit(event: ModuleEvent): void {
    // Ensure event type is a valid EventType enum
    const eventType =
      stringToEventType(event.type.toString()) || (event.type as unknown as EventType);

    // Create a standardized event
    const standardizedEvent: ModuleEvent = {
      ...event,
      type: eventType,
    };

    // Call the parent class emit method
    super.emit(standardizedEvent);
  }

  /**
   * Create and emit a module event in one step
   *
   * @param type The type of event to emit
   * @param moduleId ID of the module emitting the event
   * @param moduleType Type of the module emitting the event
   * @param data Additional data for the event
   */
  emitEvent<T extends Record<string, unknown>>(
    type: EventType | string,
    moduleId: string,
    moduleType: ModuleType,
    data?: T
  ): void {
    const event = createEvent(type, moduleId, moduleType, data);
    this.emit(event as ModuleEvent);
  }

  /**
   * Get module history (maintains backward compatibility)
   *
   * @param moduleId The ID of the module to get history for
   * @returns An array of events for the specified module
   */
  getModuleHistory(moduleId: string): ModuleEvent[] {
    return super.getModuleHistory(moduleId);
  }

  /**
   * Get event type history (maintains backward compatibility)
   *
   * @param type The type of event to get history for
   * @returns An array of events of the specified type
   */
  getEventTypeHistory(type: ModuleEventType): ModuleEvent[] {
    const eventType = stringToEventType(type) || (type as unknown as EventType);
    return super.getEventHistory(eventType);
  }

  /**
   * Clear history (maintains backward compatibility)
   */
  clearHistory(): void {
    super.clearHistory();
  }
}

/**
 * Singleton instance of the ModuleEventBus
 * This maintains the same export pattern as the original ModuleEvents.ts
 */
export const moduleEventBus = new ModuleEventBus();

/**
 * Re-export types from EventTypes.ts for backward compatibility
 */
export type {
  EventCategory,
  EventType,
  BaseEvent as StandardizedModuleEvent,
} from '../../types/events/EventTypes';

import { useEffect, useState } from 'react';
import { ErrorType, errorLoggingService } from '../../services/ErrorLoggingService';
import { Singleton } from '../patterns/Singleton';

/**
 * Base event interface that all events should extend
 */
export interface BaseEvent {
  type: string;
  timestamp?: number;
  [key: string]: any;
}

/**
 * Event handler function type
 */
export type EventHandler<T extends BaseEvent> = (event: T) => void | Promise<void>;

/**
 * Event subscription options
 */
export interface SubscriptionOptions {
  priority?: number; // Higher priority handlers are called first (default: 0)
  once?: boolean; // If true, the handler is automatically unsubscribed after the first call
  filter?: (event: BaseEvent) => boolean; // Only call the handler if the filter returns true
}

/**
 * Subscription details tracking
 */
interface Subscription<T extends BaseEvent> {
  id: string;
  handler: EventHandler<T>;
  options: SubscriptionOptions;
  scope?: string; // Optional scope for grouping subscriptions
}

/**
 * Event publication options
 */
export interface PublishOptions {
  async?: boolean; // Whether to publish asynchronously
  timeout?: number; // Timeout for async handlers (in ms)
  errorMode?: 'continue' | 'throw'; // Whether to continue after a handler error
}

/**
 * Event batch entry for batched events
 */
interface EventBatchEntry<T extends BaseEvent> {
  event: T;
  options?: PublishOptions;
}

/**
 * A unified event system that provides a consistent API for event handling
 * throughout the application, with support for synchronous and asynchronous
 * events, priority-based handling, and event filtering.
 */
export class EventSystem extends Singleton<EventSystem> {
  private handlers = new Map<string, Map<string, Subscription<any>>>();
  private batchQueue: EventBatchEntry<any>[] = [];
  private isBatching = false;
  private isProcessingBatch = false;
  private subscriptionCounter = 0;

  // Default options for event publishing
  private defaultPublishOptions: PublishOptions = {
    async: false,
    timeout: 5000,
    errorMode: 'continue',
  };

  protected constructor() {
    super();
  }

  /**
   * Subscribe to an event
   * @param eventType The event type to subscribe to
   * @param handler The handler function to call when the event is published
   * @param options Optional subscription options
   * @param scope Optional scope for grouping subscriptions
   * @returns A function to unsubscribe
   */
  public subscribe<T extends BaseEvent>(
    eventType: string,
    handler: EventHandler<T>,
    options: SubscriptionOptions = {},
    scope?: string
  ): () => void {
    if (!this.handlers.has(eventType)) {
      this.handlers.set(eventType, new Map());
    }

    const subscriptionId = `${eventType}_${++this.subscriptionCounter}`;
    const subscription: Subscription<T> = {
      id: subscriptionId,
      handler,
      options,
      scope,
    };

    this.handlers.get(eventType)!.set(subscriptionId, subscription);

    // Return unsubscribe function
    return () => {
      const handlersForType = this.handlers.get(eventType);
      if (handlersForType) {
        handlersForType.delete(subscriptionId);
        if (handlersForType.size === 0) {
          this.handlers.delete(eventType);
        }
      }
    };
  }

  /**
   * Publish an event
   * @param event The event to publish
   * @param options Optional publishing options
   */
  public publish<T extends BaseEvent>(event: T, options?: PublishOptions): void {
    // Ensure event has a timestamp
    if (!event.timestamp) {
      event.timestamp = Date.now();
    }

    // If batching is active, add to batch queue and return
    if (this.isBatching) {
      this.batchQueue.push({ event, options });
      return;
    }

    // Merge options with defaults
    const mergedOptions = { ...this.defaultPublishOptions, ...options };

    if (mergedOptions.async) {
      this.publishAsync(event, mergedOptions).catch(error => {
        errorLoggingService.logError(
          error instanceof Error ? error : new Error(String(error)),
          ErrorType.RUNTIME,
          undefined,
          { context: 'EventSystem.publish', eventType: event.type }
        );
      });
    } else {
      this.publishSync(event, mergedOptions);
    }
  }

  /**
   * Publish an event asynchronously
   * @param event The event to publish
   * @param options Optional publishing options
   */
  public async publishAsync<T extends BaseEvent>(
    event: T,
    options: PublishOptions = {}
  ): Promise<void> {
    // Ensure event has a timestamp
    if (!event.timestamp) {
      event.timestamp = Date.now();
    }

    // Merge options with defaults
    const mergedOptions = { ...this.defaultPublishOptions, ...options, async: true };

    // Get handlers for this event type
    const handlersMap = this.handlers.get(event.type);
    if (!handlersMap || handlersMap.size === 0) {
      return;
    }

    try {
      // Convert to array and sort by priority
      const handlers = Array.from(handlersMap.values()).sort(
        (a, b) => (b.options.priority || 0) - (a.options.priority || 0)
      );

      // Create array of promises with timeout
      const promises = handlers
        .filter(subscription => this.shouldHandleEvent(subscription, event))
        .map(subscription => {
          // Handle once option
          if (subscription.options.once) {
            handlersMap.delete(subscription.id);
          }

          // Create promise with timeout
          return Promise.race([
            Promise.resolve().then(() => subscription.handler(event)),
            new Promise((_, reject) => {
              setTimeout(() => {
                reject(new Error(`Handler timeout for event ${event.type}`));
              }, mergedOptions.timeout);
            }),
          ]).catch(error => {
            if (mergedOptions.errorMode === 'throw') {
              throw error;
            } else {
              errorLoggingService.logError(
                error instanceof Error ? error : new Error(String(error)),
                ErrorType.RUNTIME,
                undefined,
                { context: 'EventSystem.publishAsync.handler', eventType: event.type }
              );
            }
          });
        });

      // Wait for all promises to resolve
      await Promise.all(promises);
    } catch (error) {
      if (mergedOptions.errorMode === 'throw') {
        throw error;
      } else {
        errorLoggingService.logError(
          error instanceof Error ? error : new Error(String(error)),
          ErrorType.RUNTIME,
          undefined,
          { context: 'EventSystem.publishAsync', eventType: event.type }
        );
      }
    }
  }

  /**
   * Publish an event synchronously
   * @param event The event to publish
   * @param options Optional publishing options
   */
  private publishSync<T extends BaseEvent>(event: T, options: PublishOptions): void {
    // Get handlers for this event type
    const handlersMap = this.handlers.get(event.type);
    if (!handlersMap || handlersMap.size === 0) {
      return;
    }

    try {
      // Convert to array and sort by priority
      const handlers = Array.from(handlersMap.values()).sort(
        (a, b) => (b.options.priority || 0) - (a.options.priority || 0)
      );

      // Call each handler
      for (const subscription of handlers) {
        if (this.shouldHandleEvent(subscription, event)) {
          try {
            // Handle once option
            if (subscription.options.once) {
              handlersMap.delete(subscription.id);
            }

            // Call handler
            subscription.handler(event);
          } catch (error) {
            if (options.errorMode === 'throw') {
              throw error;
            } else {
              errorLoggingService.logError(
                error instanceof Error ? error : new Error(String(error)),
                ErrorType.RUNTIME,
                undefined,
                { context: 'EventSystem.publishSync.handler', eventType: event.type }
              );
            }
          }
        }
      }
    } catch (error) {
      if (options.errorMode === 'throw') {
        throw error;
      } else {
        errorLoggingService.logError(
          error instanceof Error ? error : new Error(String(error)),
          ErrorType.RUNTIME,
          undefined,
          { context: 'EventSystem.publishSync', eventType: event.type }
        );
      }
    }
  }

  /**
   * Start a batched event publishing session
   * Multiple events can be added to the batch using publish(), and they will be
   * published together when endBatch() is called.
   */
  public startBatch(): void {
    this.isBatching = true;
    this.batchQueue = [];
  }

  /**
   * End a batched event publishing session and publish all events in the batch
   * @param async Whether to publish the batch asynchronously
   */
  public async endBatch(async = false): Promise<void> {
    if (!this.isBatching) {
      return;
    }

    this.isBatching = false;

    // If already processing a batch, just return (avoids recursive batch processing)
    if (this.isProcessingBatch) {
      return;
    }

    this.isProcessingBatch = true;

    try {
      if (async) {
        await Promise.all(
          this.batchQueue.map(entry => this.publishAsync(entry.event, entry.options))
        );
      } else {
        for (const entry of this.batchQueue) {
          this.publish(entry.event, entry.options);
        }
      }
    } finally {
      this.batchQueue = [];
      this.isProcessingBatch = false;
    }
  }

  /**
   * Clear all event subscriptions
   * @param eventType Optional event type to clear. If not provided, all subscriptions are cleared.
   * @param scope Optional scope to clear. If provided, only subscriptions in this scope are cleared.
   */
  public clearSubscriptions(eventType?: string, scope?: string): void {
    if (eventType) {
      if (scope) {
        // Clear subscriptions for a specific event type and scope
        const handlersMap = this.handlers.get(eventType);
        if (handlersMap) {
          for (const [id, subscription] of handlersMap.entries()) {
            if (subscription.scope === scope) {
              handlersMap.delete(id);
            }
          }
          if (handlersMap.size === 0) {
            this.handlers.delete(eventType);
          }
        }
      } else {
        // Clear all subscriptions for a specific event type
        this.handlers.delete(eventType);
      }
    } else if (scope) {
      // Clear all subscriptions for a specific scope
      for (const [eventType, handlersMap] of this.handlers.entries()) {
        for (const [id, subscription] of handlersMap.entries()) {
          if (subscription.scope === scope) {
            handlersMap.delete(id);
          }
        }
        if (handlersMap.size === 0) {
          this.handlers.delete(eventType);
        }
      }
    } else {
      // Clear all subscriptions
      this.handlers.clear();
    }
  }

  /**
   * Determine whether an event should be handled by a subscription
   * @param subscription The subscription to check
   * @param event The event to check
   * @returns True if the subscription should handle the event
   */
  private shouldHandleEvent<T extends BaseEvent>(subscription: Subscription<T>, event: T): boolean {
    return !subscription.options.filter || subscription.options.filter(event);
  }

  /**
   * Create a React hook that subscribes to an event
   * @param eventType The event type to subscribe to
   * @param options Optional subscription options
   * @returns The last received event, or null if no event has been received
   */
  public createHook<T extends BaseEvent>(
    eventType: string,
    options: SubscriptionOptions = {}
  ): () => T | null {
    return () => {
      const [lastEvent, setLastEvent] = useState<T | null>(null);

      useEffect(() => {
        const unsubscribe = this.subscribe<T>(
          eventType,
          event => {
            setLastEvent(event);
          },
          options
        );

        return unsubscribe;
      }, [eventType, JSON.stringify(options)]);

      return lastEvent;
    };
  }

  /**
   * Get the singleton instance of the EventSystem
   * @returns The singleton instance
   */

  public static getInstance(): EventSystem {
    if (!EventSystem.instance) {
      EventSystem.instance = new EventSystem();
    }
    return EventSystem.instance;
  }
}

// Export singleton instance
export const eventSystem = EventSystem.getInstance();

/**
 * React hook for subscribing to events
 * @param eventType The event type to subscribe to
 * @param handler The handler function to call when the event is published
 * @param options Optional subscription options
 */
export function useEventSubscription<T extends BaseEvent>(
  eventType: string,
  handler: EventHandler<T>,
  options: SubscriptionOptions = {}
): void {
  useEffect(() => {
    const unsubscribe = eventSystem.subscribe(eventType, handler, options);
    return unsubscribe;
  }, [eventType, handler]);
}

/**
 * React hook that returns the last received event of a given type
 * @param eventType The event type to listen for
 * @param options Optional subscription options
 * @returns The last received event, or null if no event has been received
 */
export function useLastEvent<T extends BaseEvent>(
  eventType: string,
  options: SubscriptionOptions = {}
): T | null {
  return eventSystem.createHook<T>(eventType, options)();
}

/**
 * Helper function to publish an event
 * @param event The event to publish
 * @param options Optional publishing options
 */
export function publishEvent<T extends BaseEvent>(event: T, options?: PublishOptions): void {
  eventSystem.publish(event, options);
}

/**
 * Helper function to publish an event asynchronously
 * @param event The event to publish
 * @param options Optional publishing options
 */
export async function publishEventAsync<T extends BaseEvent>(
  event: T,
  options?: PublishOptions
): Promise<void> {
  await eventSystem.publishAsync(event, options);
}

export default eventSystem;

/**
 * Generic Singleton implementation that can be extended by services and managers.
 * Provides a common pattern for singleton instances throughout the application.
 * 
 * @example
 * ```typescript
 * class MyService extends Singleton<MyService> {
 *   protected constructor() {
 *     super();
 *     // initialization code
 *   }
 *   
 *   public doSomething(): void {
 *     // implementation
 *   }
 * }
 * 
 * // Usage:
 * const service = MyService.getInstance();
 * service.doSomething();
 * ```
 */
export abstract class Singleton<T> {
  private static instances = new Map<string, any>();

  protected constructor() {
    // Protected constructor to prevent direct instantiation
  }

  /**
   * Gets the singleton instance of the class.
   * Creates a new instance if one doesn't exist yet.
   */
  public static getInstance<T extends Singleton<T>>(this: new () => T): T {
    const className = this.name;
    if (!Singleton.instances.has(className)) {
      Singleton.instances.set(className, new this());
    }
    return Singleton.instances.get(className) as T;
  }

  /**
   * Initializes the singleton instance.
   * This method should be overridden by subclasses that need initialization logic.
   */
  public async initialize?(): Promise<void>;

  /**
   * Disposes of resources used by the singleton instance.
   * This method should be overridden by subclasses that need cleanup logic.
   */
  public async dispose?(): Promise<void>;

  /**
   * Resets the singleton instance.
   * This is primarily useful for testing purposes.
   */
  public static resetInstance(className: string): void {
    if (Singleton.instances.has(className)) {
      const instance = Singleton.instances.get(className);
      if (instance && typeof instance.dispose === 'function') {
        instance.dispose();
      }
      Singleton.instances.delete(className);
    }
  }
}

import { Singleton } from '../patterns/Singleton';
import { ErrorType } from '../../services/ErrorLoggingService';

/**
 * Interface for service metadata
 */
export interface ServiceMetadata {
  name: string;
  version: string;
  status: 'initializing' | 'ready' | 'error' | 'disposed';
  lastError?: {
    type: ErrorType;
    message: string;
    timestamp: number;
  };
  metrics?: Record<string, number>;
}

/**
 * Base interface that all services should implement
 */
export interface BaseService {
  /**
   * Initialize the service with optional dependencies
   */
  initialize(dependencies?: Record<string, unknown>): Promise<void>;

  /**
   * Dispose of any resources used by the service
   */
  dispose(): Promise<void>;

  /**
   * Get metadata about the service's current state
   */
  getMetadata(): ServiceMetadata;

  /**
   * Check if the service is ready to handle requests
   */
  isReady(): boolean;

  /**
   * Handle errors that occur within the service
   */
  handleError(error: Error, context?: Record<string, unknown>): void;
}

/**
 * Abstract base class that provides common service functionality
 * Extends the Singleton pattern to ensure only one instance exists
 */
export abstract class AbstractBaseService extends Singleton<AbstractBaseService> implements BaseService {
  protected metadata: ServiceMetadata;

  protected constructor(name: string, version: string) {
    super();
    this.metadata = {
      name,
      version,
      status: 'initializing',
    };
  }

  async initialize(dependencies?: Record<string, unknown>): Promise<void> {
    try {
      await this.onInitialize(dependencies);
      this.metadata.status = 'ready';
    } catch (error) {
      this.metadata.status = 'error';
      this.handleError(error as Error);
      throw error;
    }
  }

  async dispose(): Promise<void> {
    try {
      await this.onDispose();
      this.metadata.status = 'disposed';
    } catch (error) {
      this.handleError(error as Error);
      throw error;
    }
  }

  getMetadata(): ServiceMetadata {
    return { ...this.metadata };
  }

  isReady(): boolean {
    return this.metadata.status === 'ready';
  }

  handleError(error: Error, context?: Record<string, unknown>): void {
    this.metadata.lastError = {
      type: ErrorType.UNKNOWN,
      message: error.message,
      timestamp: Date.now(),
    };
    // Subclasses should override this to provide custom error handling
  }

  protected abstract onInitialize(dependencies?: Record<string, unknown>): Promise<void>;
  protected abstract onDispose(): Promise<void>;
}

import { EventEmitter } from '../utils/EventEmitter';

export interface ViewportState {
  scale: number;
  translateX: number;
  translateY: number;
}

export interface BrushState {
  active: boolean;
  x1: number;
  y1: number;
  x2: number;
  y2: number;
}

export interface HighlightState {
  active: boolean;
  dataIds: string[];
  category?: string;
  value?: number;
}

export interface ColorScale {
  domain: [number, number];
  range: string[];
  type: 'linear' | 'ordinal';
}

export interface ChartState {
  id: string;
  viewport: ViewportState;
  brush: BrushState;
  highlight: HighlightState;
  colorScales: Record<string, ColorScale>;
}

export type ChartEventType =
  | 'viewport-change'
  | 'brush-change'
  | 'highlight-change'
  | 'color-scale-change';

export interface ChartEvent {
  type: ChartEventType;
  chartId: string;
  state: Partial<ChartState>;
  propagate?: boolean;
}

interface ChartEvents {
  [key: string]: ChartEvent;
  [key: `${string}:viewport-change`]: ChartEvent;
  [key: `${string}:brush-change`]: ChartEvent;
  [key: `${string}:highlight-change`]: ChartEvent;
  [key: `${string}:color-scale-change`]: ChartEvent;
}

/**
 * ChartCoordinationManager
 *
 * Manages synchronized interactions between multiple charts, including:
 * - Synchronized zooming/panning
 * - Linked brushing
 * - Synchronized highlighting
 * - Shared color scales
 */
export class ChartCoordinationManager {
  private static instance: ChartCoordinationManager;
  private eventEmitter: EventEmitter<ChartEvents>;
  private charts: Map<string, ChartState>;
  private linkedGroups: Map<string, Set<string>>;

  private constructor() {
    this.eventEmitter = new EventEmitter<ChartEvents>();
    this.charts = new Map();
    this.linkedGroups = new Map();
  }

  public static getInstance(): ChartCoordinationManager {
    if (!ChartCoordinationManager.instance) {
      ChartCoordinationManager.instance = new ChartCoordinationManager();
    }
    return ChartCoordinationManager.instance;
  }

  /**
   * Register a chart with the coordination manager
   */
  public registerChart(chartId: string, initialState?: Partial<ChartState>): void {
    const defaultState: ChartState = {
      id: chartId,
      viewport: { scale: 1, translateX: 0, translateY: 0 },
      brush: { active: false, x1: 0, y1: 0, x2: 0, y2: 0 },
      highlight: { active: false, dataIds: [] },
      colorScales: {},
    };

    this.charts.set(chartId, {
      ...defaultState,
      ...initialState,
    });
  }

  /**
   * Unregister a chart
   */
  public unregisterChart(chartId: string): void {
    this.charts.delete(chartId);
    // Remove from all linked groups
    this.linkedGroups.forEach(group => group.delete(chartId));
  }

  /**
   * Link charts together for synchronized interactions
   */
  public linkCharts(chartIds: string[], groupId: string): void {
    const group = this.linkedGroups.get(groupId) || new Set();
    chartIds.forEach(id => group.add(id));
    this.linkedGroups.set(groupId, group);
  }

  /**
   * Unlink charts
   */
  public unlinkCharts(chartIds: string[], groupId: string): void {
    const group = this.linkedGroups.get(groupId);
    if (group) {
      chartIds.forEach(id => group.delete(id));
      if (group.size === 0) {
        this.linkedGroups.delete(groupId);
      }
    }
  }

  /**
   * Update chart state and notify linked charts
   */
  public updateChartState(chartId: string, update: Partial<ChartState>, propagate = true): void {
    const chart = this.charts.get(chartId);
    if (!chart) return;

    // Update chart state
    this.charts.set(chartId, {
      ...chart,
      ...update,
    });

    if (propagate) {
      // Find all groups containing this chart
      this.linkedGroups.forEach((group, groupId) => {
        if (group.has(chartId)) {
          // Notify all other charts in the group
          group.forEach(linkedChartId => {
            if (linkedChartId !== chartId) {
              this.notifyChartUpdate(linkedChartId, update);
            }
          });
        }
      });
    }
  }

  /**
   * Subscribe to chart events
   */
  public subscribe(
    chartId: string,
    eventType: ChartEventType,
    callback: (event: ChartEvent) => void
  ): () => void {
    const eventName = `${chartId}:${eventType}` as keyof ChartEvents;
    const unsubscribe = (event: ChartEvent) => callback(event);
    this.eventEmitter.on(eventName, unsubscribe);
    return () => this.eventEmitter.off(eventName, unsubscribe);
  }

  /**
   * Emit a chart event
   */
  public emit(event: ChartEvent): void {
    const eventName = `${event.chartId}:${event.type}` as keyof ChartEvents;
    this.eventEmitter.emit(eventName, event);
  }

  /**
   * Get current state of a chart
   */
  public getChartState(chartId: string): ChartState | undefined {
    return this.charts.get(chartId);
  }

  /**
   * Get all charts in a linked group
   */
  public getLinkedCharts(groupId: string): string[] {
    const group = this.linkedGroups.get(groupId);
    return group ? Array.from(group) : [];
  }

  /**
   * Notify a chart of state updates
   */
  private notifyChartUpdate(chartId: string, update: Partial<ChartState>): void {
    // Determine event type based on what changed
    if (update.viewport) {
      this.emit({
        type: 'viewport-change',
        chartId,
        state: { viewport: update.viewport },
        propagate: false,
      });
    }
    if (update.brush) {
      this.emit({
        type: 'brush-change',
        chartId,
        state: { brush: update.brush },
        propagate: false,
      });
    }
    if (update.highlight) {
      this.emit({
        type: 'highlight-change',
        chartId,
        state: { highlight: update.highlight },
        propagate: false,
      });
    }
    if (update.colorScales) {
      this.emit({
        type: 'color-scale-change',
        chartId,
        state: { colorScales: update.colorScales },
        propagate: false,
      });
    }
  }
}

