import { Position } from '../../types/core/Position';

/**
 * Particle animation easing functions
 */
export enum EasingFunction {
  LINEAR = 'linear',
  EASE_IN = 'easeIn',
  EASE_OUT = 'easeOut',
  EASE_IN_OUT = 'easeInOut',
  BOUNCE = 'bounce',
  ELASTIC = 'elastic',
  BACK = 'back',
}

/**
 * Path type for particle movement
 */
export enum ParticlePath {
  LINEAR = 'linear',
  CURVED = 'curved',
  SPIRAL = 'spiral',
  BEZIER = 'bezier',
  WAVE = 'wave',
  RANDOM = 'random',
}

/**
 * Particle blend mode
 */
export enum ParticleBlendMode {
  NORMAL = 'normal',
  ADD = 'add',
  MULTIPLY = 'multiply',
  SCREEN = 'screen',
}

/**
 * Basic particle properties
 */
export interface Particle {
  id: string;
  position: Position;
  prevPosition?: Position;
  targetPosition?: Position;
  startPosition?: Position;
  velocity: { x: number; y: number };
  acceleration: { x: number; y: number };
  size: number;
  startSize?: number;
  targetSize?: number;
  color: string;
  startColor?: string;
  targetColor?: string;
  opacity: number;
  startOpacity?: number;
  targetOpacity?: number;
  rotation: number;
  startRotation?: number;
  targetRotation?: number;
  life: number;
  maxLife: number;
  active: boolean;
  path?: ParticlePath;
  pathParams?: Record<string, number>;
  easing?: EasingFunction;
  blendMode?: ParticleBlendMode;
  group?: string;
  data?: Record<string, unknown>;
}

/**
 * Particle emitter configuration
 */
export interface ParticleEmitterConfig {
  position: Position;
  rate: number;
  burstCount?: number;
  emitRadius?: number;
  direction?: number; // Angle in radians
  spread?: number; // Angle in radians
  minLife?: number;
  maxLife?: number;
  minSize?: number;
  maxSize?: number;
  minVelocity?: number;
  maxVelocity?: number;
  colors?: string[];
  minOpacity?: number;
  maxOpacity?: number;
  gravity?: { x: number; y: number };
  path?: ParticlePath;
  pathParams?: Record<string, number>;
  easing?: EasingFunction;
  blendMode?: ParticleBlendMode;
  group?: string;
}

/**
 * Transition configuration for moving particles between data states
 */
export interface ParticleTransitionConfig {
  /**
   * Source data points with positions
   */
  sourceData?: DataPoint[];

  /**
   * Target data points with positions
   */
  targetData?: DataPoint[];

  /**
   * Transition duration in milliseconds
   */
  duration: number;

  /**
   * Easing function for the transition
   */
  easing?: EasingFunction;

  /**
   * Path type for particle movement
   */
  path?: ParticlePath;

  /**
   * Additional path parameters
   */
  pathParams?: Record<string, number>;

  /**
   * Delay between individual particle transitions in milliseconds
   */
  staggerDelay?: number;

  /**
   * Whether to transition colors
   */
  transitionColors?: boolean;

  /**
   * Whether to transition sizes
   */
  transitionSizes?: boolean;

  /**
   * Whether to transition opacity
   */
  transitionOpacity?: boolean;

  /**
   * Whether to reverse the transition
   */
  reverse?: boolean;

  /**
   * Callback when transition is complete
   */
  onComplete?: () => void;

  /**
   * Callback when transition is updated
   */
  onUpdate?: (progress: number) => void;
}

/**
 * Data point for visualization
 */
export interface DataPoint {
  id?: string;
  x: number;
  y: number;
  value: number;
  size?: number;
  color?: string;
  opacity?: number;
  group?: string;
  active?: boolean;
  [key: string]: unknown;
}

/**
 * Manages a particle system for animated data transitions
 */
export class ParticleSystem {
  private particles: Map<string, Particle> = new Map();
  private emitters: Map<string, ParticleEmitterConfig> = new Map();
  private lastFrameTime: number = 0;
  private animationFrame: number | null = null;
  private transitionConfigs: Map<string, ParticleTransitionConfig> = new Map();
  private transitionTimers: Map<string, number> = new Map();
  private transitionProgress: Map<string, number> = new Map();

  /**
   * Create a new particle system
   */
  constructor() {
    this.lastFrameTime = performance.now();
  }

  /**
   * Add a particle to the system
   */
  public addParticle(particle: Omit<Particle, 'id'>): string {
    const id = `particle-${Math.random().toString(36).substring(2, 9)}`;
    this.particles.set(id, {
      ...particle,
      id,
    });
    return id;
  }

  /**
   * Remove a particle from the system
   */
  public removeParticle(id: string): boolean {
    return this.particles.delete(id);
  }

  /**
   * Add a particle emitter
   */
  public addEmitter(config: ParticleEmitterConfig): string {
    const id = `emitter-${Math.random().toString(36).substring(2, 9)}`;
    this.emitters.set(id, config);
    return id;
  }

  /**
   * Remove a particle emitter
   */
  public removeEmitter(id: string): boolean {
    return this.emitters.delete(id);
  }

  /**
   * Start the animation loop
   */
  public start(): void {
    if (this.animationFrame !== null) {
      return;
    }

    this.lastFrameTime = performance.now();
    this.animationLoop();
  }

  /**
   * Stop the animation loop
   */
  public stop(): void {
    if (this.animationFrame !== null) {
      cancelAnimationFrame(this.animationFrame);
      this.animationFrame = null;
    }
  }

  /**
   * Clear all particles
   */
  public clear(): void {
    this.particles.clear();
  }

  /**
   * Set up a transition between data states
   */
  public setupTransition(id: string, config: ParticleTransitionConfig): void {
    this.transitionConfigs.set(id, config);
    this.transitionProgress.set(id, 0);

    // Create particles for transition if needed
    if (config.sourceData && config.targetData) {
      this.createParticlesForTransition(id, config);
    }
  }

  /**
   * Start a transition between data states
   */
  public startTransition(id: string): void {
    const config = this.transitionConfigs.get(id);
    if (!config) {
      console.warn(`No transition config found with id: ${id}`);
      return;
    }

    // Start the transition timer
    const startTime = performance.now();
    this.transitionTimers.set(id, startTime);

    // Ensure animation is running
    this.start();
  }

  /**
   * Stop a transition
   */
  public stopTransition(id: string): void {
    this.transitionTimers.delete(id);
  }

  /**
   * Create particles for a data transition
   */
  private createParticlesForTransition(
    transitionId: string,
    config: ParticleTransitionConfig
  ): void {
    if (!config.sourceData || !config.targetData) {
      return;
    }

    // Clear any existing particles for this transition
    this.particles.forEach((particle, id) => {
      if (particle.group === transitionId) {
        this.particles.delete(id);
      }
    });

    // Determine how to map source to target
    const isEqualSize = config.sourceData.length === config.targetData.length;

    if (isEqualSize) {
      // Direct mapping when source and target have same number of points
      config.sourceData.forEach((sourcePoint, index) => {
        const targetPoint = config.targetData![index];
        this.createTransitionParticle(sourcePoint, targetPoint, transitionId, config);
      });
    } else if (config.sourceData.length < config.targetData.length) {
      // Source has fewer points, need to generate additional particles
      config.sourceData.forEach((sourcePoint, index) => {
        const targetPoint = config.targetData![index % config.targetData!.length];
        this.createTransitionParticle(sourcePoint, targetPoint, transitionId, config);
      });

      // Create additional particles starting from appropriate source points
      for (let i = config.sourceData.length; i < config.targetData.length; i++) {
        const sourceIndex = i % config.sourceData.length;
        const sourcePoint = config.sourceData[sourceIndex];
        const targetPoint = config.targetData[i];
        this.createTransitionParticle(sourcePoint, targetPoint, transitionId, config);
      }
    } else {
      // Target has fewer points, some source particles will converge
      config.sourceData.forEach((sourcePoint, index) => {
        const targetPoint = config.targetData![index % config.targetData!.length];
        this.createTransitionParticle(sourcePoint, targetPoint, transitionId, config);
      });
    }
  }

  /**
   * Create a single transition particle
   */
  private createTransitionParticle(
    sourcePoint: DataPoint,
    targetPoint: DataPoint,
    transitionId: string,
    config: ParticleTransitionConfig
  ): string {
    const sourceColor = sourcePoint.color || '#ffffff';
    const targetColor = targetPoint.color || '#ffffff';
    const sourceSize = sourcePoint.size || 10;
    const targetSize = targetPoint.size || 10;
    const sourceOpacity = sourcePoint.opacity !== undefined ? sourcePoint.opacity : 1;
    const targetOpacity = targetPoint.opacity !== undefined ? targetPoint.opacity : 1;

    return this.addParticle({
      position: { x: sourcePoint.x, y: sourcePoint.y },
      startPosition: { x: sourcePoint.x, y: sourcePoint.y },
      targetPosition: { x: targetPoint.x, y: targetPoint.y },
      velocity: { x: 0, y: 0 },
      acceleration: { x: 0, y: 0 },
      size: sourceSize,
      startSize: sourceSize,
      targetSize: targetSize,
      color: sourceColor,
      startColor: sourceColor,
      targetColor: targetColor,
      opacity: sourceOpacity,
      startOpacity: sourceOpacity,
      targetOpacity: targetOpacity,
      rotation: 0,
      life: 1,
      maxLife: 1,
      active: true,
      path: config.path || ParticlePath.LINEAR,
      easing: config.easing || EasingFunction.EASE_IN_OUT,
      pathParams: config.pathParams,
      group: transitionId,
      data: {
        sourcePoint,
        targetPoint,
        startTime: performance.now(),
        staggerDelay: config.staggerDelay || 0,
        transitionStarted: false,
      },
    });
  }

  /**
   * Main animation loop
   */
  private animationLoop(): void {
    const currentTime = performance.now();
    const deltaTime = (currentTime - this.lastFrameTime) / 1000; // in seconds
    this.lastFrameTime = currentTime;

    // Update transitions
    this.updateTransitions(currentTime);

    // Update emitters
    this.updateEmitters(deltaTime);

    // Update particles
    this.updateParticles(deltaTime);

    // Schedule next frame
    this.animationFrame = requestAnimationFrame(() => this.animationLoop());
  }

  /**
   * Update particle transitions
   */
  private updateTransitions(currentTime: number): void {
    this.transitionTimers.forEach((startTime, id) => {
      const config = this.transitionConfigs.get(id);
      if (!config) return;

      // Calculate progress
      const elapsedTime = currentTime - startTime;
      const progress = Math.min(1, elapsedTime / config.duration);

      // Store progress
      this.transitionProgress.set(id, progress);

      // Call update callback
      config.onUpdate?.(progress);

      // Check if transition is complete
      if (progress >= 1) {
        this.transitionTimers.delete(id);
        config.onComplete?.();
      }
    });
  }

  /**
   * Update particle emitters
   */
  private updateEmitters(deltaTime: number): void {
    this.emitters.forEach((config, id) => {
      // Calculate number of particles to emit
      const emitCount = config.burstCount || Math.floor(config.rate * deltaTime);

      // Emit particles
      for (let i = 0; i < emitCount; i++) {
        this.emitParticle(config);
      }
    });
  }

  /**
   * Emit a single particle from an emitter
   */
  private emitParticle(config: ParticleEmitterConfig): string {
    // Randomize position within emit radius
    const angle = Math.random() * Math.PI * 2;
    const radius = Math.random() * (config.emitRadius || 0);
    const position = {
      x: config.position.x + Math.cos(angle) * radius,
      y: config.position.y + Math.sin(angle) * radius,
    };

    // Randomize velocity
    const direction = (config.direction || 0) + (Math.random() - 0.5) * (config.spread || 0);
    const speed =
      config.minVelocity !== undefined && config.maxVelocity !== undefined
        ? config.minVelocity + Math.random() * (config.maxVelocity - config.minVelocity)
        : 50;

    const velocity = {
      x: Math.cos(direction) * speed,
      y: Math.sin(direction) * speed,
    };

    // Randomize life
    const life =
      config.minLife !== undefined && config.maxLife !== undefined
        ? config.minLife + Math.random() * (config.maxLife - config.minLife)
        : 1;

    // Randomize size
    const size =
      config.minSize !== undefined && config.maxSize !== undefined
        ? config.minSize + Math.random() * (config.maxSize - config.minSize)
        : 10;

    // Randomize color
    const color =
      config.colors && config.colors.length > 0
        ? config.colors[Math.floor(Math.random() * config.colors.length)]
        : '#ffffff';

    // Randomize opacity
    const opacity =
      config.minOpacity !== undefined && config.maxOpacity !== undefined
        ? config.minOpacity + Math.random() * (config.maxOpacity - config.minOpacity)
        : 1;

    // Create particle
    return this.addParticle({
      position,
      velocity,
      acceleration: config.gravity || { x: 0, y: 0 },
      size,
      color,
      opacity,
      rotation: Math.random() * Math.PI * 2,
      life,
      maxLife: life,
      active: true,
      path: config.path,
      pathParams: config.pathParams,
      easing: config.easing,
      blendMode: config.blendMode,
      group: config.group,
    });
  }

  /**
   * Update all particles
   */
  private updateParticles(deltaTime: number): void {
    this.particles.forEach((particle, id) => {
      if (!particle.active) return;

      // Handle transition particles
      if (particle.group && this.transitionTimers.has(particle.group)) {
        this.updateTransitionParticle(particle, this.transitionProgress.get(particle.group) || 0);
      } else {
        // Handle regular particles
        this.updateRegularParticle(particle, deltaTime);
      }

      // Remove dead particles
      if (particle.life <= 0) {
        this.particles.delete(id);
      }
    });
  }

  /**
   * Update a transition particle
   */
  private updateTransitionParticle(particle: Particle, transitionProgress: number): void {
    if (!particle.startPosition || !particle.targetPosition) return;

    const data = particle.data as Record<string, unknown>;
    const staggerDelay = (data.staggerDelay as number) || 0;

    // Handle staggered start
    if (!data.transitionStarted) {
      const elapsedSinceStart = performance.now() - (data.startTime as number);
      if (elapsedSinceStart < staggerDelay) {
        return;
      }
      data.transitionStarted = true;
    }

    // Apply easing
    const easedProgress = this.applyEasing(
      transitionProgress,
      particle.easing || EasingFunction.LINEAR
    );

    // Update position based on path type
    this.updateParticlePosition(particle, easedProgress);

    // Update size
    if (particle.startSize !== undefined && particle.targetSize !== undefined) {
      particle.size =
        particle.startSize + (particle.targetSize - particle.startSize) * easedProgress;
    }

    // Update opacity
    if (particle.startOpacity !== undefined && particle.targetOpacity !== undefined) {
      particle.opacity =
        particle.startOpacity + (particle.targetOpacity - particle.startOpacity) * easedProgress;
    }

    // Update color
    if (particle.startColor && particle.targetColor) {
      particle.color = this.interpolateColor(
        particle.startColor,
        particle.targetColor,
        easedProgress
      );
    }

    // Store previous position for trail effects
    particle.prevPosition = { ...particle.position };
  }

  /**
   * Update a regular particle
   */
  private updateRegularParticle(particle: Particle, deltaTime: number): void {
    // Update velocity
    particle.velocity.x += particle.acceleration.x * deltaTime;
    particle.velocity.y += particle.acceleration.y * deltaTime;

    // Store previous position for trail effects
    particle.prevPosition = { ...particle.position };

    // Update position
    particle.position.x += particle.velocity.x * deltaTime;
    particle.position.y += particle.velocity.y * deltaTime;

    // Update life
    particle.life -= deltaTime;

    // Update opacity based on life
    const lifeRatio = Math.max(0, particle.life / particle.maxLife);
    particle.opacity = lifeRatio;
  }

  /**
   * Update particle position based on path type
   */
  private updateParticlePosition(particle: Particle, progress: number): void {
    if (!particle.startPosition || !particle.targetPosition) return;

    const start = particle.startPosition;
    const end = particle.targetPosition;

    switch (particle.path) {
      case ParticlePath.LINEAR:
        // Simple linear interpolation
        particle.position.x = start.x + (end.x - start.x) * progress;
        particle.position.y = start.y + (end.y - start.y) * progress;
        break;

      case ParticlePath.CURVED:
        // Curved path with a quadratic bezier
        const controlX = (start.x + end.x) / 2;
        const controlY = Math.min(start.y, end.y) - Math.abs(end.x - start.x) * 0.2;

        const t = progress;
        const invT = 1 - t;

        particle.position.x = invT * invT * start.x + 2 * invT * t * controlX + t * t * end.x;
        particle.position.y = invT * invT * start.y + 2 * invT * t * controlY + t * t * end.y;
        break;

      case ParticlePath.SPIRAL:
        // Spiral path
        const turns = (particle.pathParams?.turns || 2) * Math.PI * 2;
        const angle = progress * turns;
        const radius =
          (1 - progress) * Math.min(Math.abs(end.x - start.x), Math.abs(end.y - start.y)) * 0.2;

        particle.position.x = start.x + (end.x - start.x) * progress + Math.cos(angle) * radius;
        particle.position.y = start.y + (end.y - start.y) * progress + Math.sin(angle) * radius;
        break;

      case ParticlePath.BEZIER:
        // Cubic bezier curve
        const cp1x = start.x + (end.x - start.x) * 0.3;
        const cp1y = start.y - Math.abs(end.y - start.y) * 0.3;
        const cp2x = start.x + (end.x - start.x) * 0.7;
        const cp2y = end.y + Math.abs(end.y - start.y) * 0.3;

        const t1 = progress;
        const t2 = t1 * t1;
        const t3 = t2 * t1;
        const invT1 = 1 - t1;
        const invT2 = invT1 * invT1;
        const invT3 = invT2 * invT1;

        particle.position.x =
          invT3 * start.x + 3 * invT2 * t1 * cp1x + 3 * invT1 * t2 * cp2x + t3 * end.x;
        particle.position.y =
          invT3 * start.y + 3 * invT2 * t1 * cp1y + 3 * invT1 * t2 * cp2y + t3 * end.y;
        break;

      case ParticlePath.WAVE:
        // Wavy path
        const amplitude =
          particle.pathParams?.amplitude ||
          Math.min(Math.abs(end.x - start.x), Math.abs(end.y - start.y)) * 0.1;
        const frequency = particle.pathParams?.frequency || 3;
        const waviness = Math.sin(progress * Math.PI * frequency) * amplitude;

        // Calculate the normal vector to the path
        const dx = end.x - start.x;
        const dy = end.y - start.y;
        const length = Math.sqrt(dx * dx + dy * dy);
        const normalX = -dy / length;
        const normalY = dx / length;

        particle.position.x = start.x + (end.x - start.x) * progress + normalX * waviness;
        particle.position.y = start.y + (end.y - start.y) * progress + normalY * waviness;
        break;

      case ParticlePath.RANDOM:
        // Random path with controlled randomness
        const seeds = (particle.data?.randomSeeds as number[]) || [];
        if (!seeds.length) {
          // Create random seeds for consistent randomness
          for (let i = 0; i < 10; i++) {
            seeds.push(Math.random());
          }
          (particle.data as Record<string, unknown>).randomSeeds = seeds;
        }

        const jitter = particle.pathParams?.jitter || 0.1;
        const jitterSize = Math.min(Math.abs(end.x - start.x), Math.abs(end.y - start.y)) * jitter;

        // Use seeds and progress to generate controlled randomness
        const index = Math.floor(progress * 10);
        const subProgress = (progress * 10) % 1;
        const seed1 = seeds[index % seeds.length];
        const seed2 = seeds[(index + 1) % seeds.length];

        const randomX =
          (seed1 * 2 - 1) * jitterSize * (1 - subProgress) +
          (seed2 * 2 - 1) * jitterSize * subProgress;
        const randomY =
          (seeds[(index + 2) % seeds.length] * 2 - 1) * jitterSize * (1 - subProgress) +
          (seeds[(index + 3) % seeds.length] * 2 - 1) * jitterSize * subProgress;

        particle.position.x = start.x + (end.x - start.x) * progress + randomX;
        particle.position.y = start.y + (end.y - start.y) * progress + randomY;
        break;

      default:
        // Default to linear
        particle.position.x = start.x + (end.x - start.x) * progress;
        particle.position.y = start.y + (end.y - start.y) * progress;
    }
  }

  /**
   * Apply easing function to progress
   */
  private applyEasing(progress: number, easing: EasingFunction): number {
    switch (easing) {
      case EasingFunction.LINEAR:
        return progress;

      case EasingFunction.EASE_IN:
        return progress * progress;

      case EasingFunction.EASE_OUT:
        return progress * (2 - progress);

      case EasingFunction.EASE_IN_OUT:
        return progress < 0.5 ? 2 * progress * progress : -1 + (4 - 2 * progress) * progress;

      case EasingFunction.BOUNCE:
        // Bounce effect
        const bounce = (t: number): number => {
          const a = 7.5625;
          const b = 2.75;

          if (t < 1 / b) {
            return a * t * t;
          } else if (t < 2 / b) {
            return a * (t -= 1.5 / b) * t + 0.75;
          } else if (t < 2.5 / b) {
            return a * (t -= 2.25 / b) * t + 0.9375;
          } else {
            return a * (t -= 2.625 / b) * t + 0.984375;
          }
        };

        return bounce(progress);

      case EasingFunction.ELASTIC:
        // Elastic effect
        const elastic = (t: number): number => {
          return t === 0
            ? 0
            : t === 1
              ? 1
              : Math.pow(2, -10 * t) * Math.sin(((t * 10 - 0.75) * Math.PI) / 1.5) + 1;
        };

        return elastic(progress);

      case EasingFunction.BACK:
        // Back effect (overshooting)
        const overshoot = 1.70158;
        const back = (t: number): number => {
          return t * t * ((overshoot + 1) * t - overshoot);
        };

        return back(progress);

      default:
        return progress;
    }
  }

  /**
   * Interpolate between two colors
   */
  private interpolateColor(color1: string, color2: string, progress: number): string {
    // Parse colors
    const parseColor = (color: string): [number, number, number] => {
      // Handle hex colors
      if (color.startsWith('#')) {
        const hex = color.substring(1);
        if (hex.length === 3) {
          return [
            parseInt(hex[0] + hex[0], 16),
            parseInt(hex[1] + hex[1], 16),
            parseInt(hex[2] + hex[2], 16),
          ];
        } else {
          return [
            parseInt(hex.substring(0, 2), 16),
            parseInt(hex.substring(2, 4), 16),
            parseInt(hex.substring(4, 6), 16),
          ];
        }
      }

      // Handle rgb colors
      if (color.startsWith('rgb')) {
        const match = color.match(/rgb\((\d+),\s*(\d+),\s*(\d+)\)/);
        if (match) {
          return [parseInt(match[1]), parseInt(match[2]), parseInt(match[3])];
        }
      }

      // Default
      return [255, 255, 255];
    };

    const [r1, g1, b1] = parseColor(color1);
    const [r2, g2, b2] = parseColor(color2);

    // Interpolate
    const r = Math.round(r1 + (r2 - r1) * progress);
    const g = Math.round(g1 + (g2 - g1) * progress);
    const b = Math.round(b1 + (b2 - b1) * progress);

    // Convert back to hex
    return `#${r.toString(16).padStart(2, '0')}${g.toString(16).padStart(2, '0')}${b.toString(16).padStart(2, '0')}`;
  }

  /**
   * Get all particles
   */
  public getParticles(): Particle[] {
    return Array.from(this.particles.values());
  }

  /**
   * Get particles by group
   */
  public getParticlesByGroup(group: string): Particle[] {
    return Array.from(this.particles.values()).filter(p => p.group === group);
  }

  /**
   * Check if a transition is running
   */
  public isTransitionRunning(id: string): boolean {
    return this.transitionTimers.has(id);
  }

  /**
   * Get transition progress
   */
  public getTransitionProgress(id: string): number {
    return this.transitionProgress.get(id) || 0;
  }

  /**
   * Clear everything
   */
  public dispose(): void {
    this.stop();
    this.particles.clear();
    this.emitters.clear();
    this.transitionConfigs.clear();
    this.transitionTimers.clear();
    this.transitionProgress.clear();
  }
}

/**
 * @file ExplorationManager.ts
 * Implementation of the ExplorationManager that conforms to the BaseManager interface.
 *
 * This class handles:
 * 1. Star system management and ship assignments
 * 2. Sector scanning and discovery tracking
 * 3. Integration with ReconShipManager for ship operations
 * 4. Event-based communication with UI components
 */

import { v4 as uuidv4 } from 'uuid';
import { EventBus } from '../../lib/events/EventBus';
import { AbstractBaseManager } from '../../lib/managers/BaseManager';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import { BaseEvent, EventType } from '../../types/events/EventTypes';
import { ReconShipManagerImpl, Ship } from './ReconShipManagerImpl';

// Define Exploration specific event types as strings
// These will be cast to EventType when publishing events
export enum ExplorationEvents {
  SECTOR_DISCOVERED = 'EXPLORATION_SECTOR_DISCOVERED',
  SECTOR_SCANNED = 'EXPLORATION_SECTOR_SCANNED',
  ANOMALY_DETECTED = 'EXPLORATION_ANOMALY_DETECTED',
  RESOURCE_DETECTED = 'EXPLORATION_RESOURCE_DETECTED',
  SCAN_STARTED = 'EXPLORATION_SCAN_STARTED',
  SCAN_COMPLETED = 'EXPLORATION_SCAN_COMPLETED',
  SCAN_FAILED = 'EXPLORATION_SCAN_FAILED',
  SHIP_ASSIGNED = 'EXPLORATION_SHIP_ASSIGNED',
  SHIP_UNASSIGNED = 'EXPLORATION_SHIP_UNASSIGNED',
}

// Define a type for our exploration event data
// This will be used in the data field of BaseEvent
export interface ExplorationEventData extends Record<string, unknown> {
  sector?: Sector;
  operation?: ScanOperation;
  resource?: {
    type: string;
    amount: number;
    quality: number;
  };
  anomaly?: Anomaly;
  ship?: Ship;
  reason?: string;
  sectorId?: string;
  shipId?: string;
  anomalyId?: string;
}

// Define interfaces for the types used
export interface StarSystem {
  id: string;
  name: string;
  type?: string;
  resources?: string[];
  status: 'unmapped' | 'mapped' | 'scanning' | 'analyzed';
  assignedShips: string[];
  position: {
    x: number;
    y: number;
  };
  lastScanned?: number;
  discoveredAt?: number;
}

export interface Sector {
  id: string;
  name: string;
  status: 'unmapped' | 'mapped' | 'scanning' | 'analyzed';
  coordinates: { x: number; y: number };
  resourcePotential: number;
  habitabilityScore: number;
  anomalies: Anomaly[];
  lastScanned?: number;
  discoveredAt?: number;
  resources?: Array<{
    type: string;
    amount: number;
    quality?: number;
  }>;
}

export interface Anomaly {
  id: string;
  type: string;
  severity: 'low' | 'medium' | 'high';
  description: string;
  position: { x: number; y: number };
  discoveredAt: number;
  investigatedAt?: number;
  sectorId: string;
  data?: Record<string, unknown>;
}

export interface SystemSearchCriteria {
  name?: string;
  type?: string;
  resources?: string[];
  status?: string;
}

export interface ScanOperation {
  id: string;
  sectorId: string;
  shipId: string;
  startTime: number;
  estimatedDuration: number;
  progress: number;
  status: 'active' | 'completed' | 'failed' | 'cancelled';
  results?: Record<string, unknown>;
}

/**
 * ExplorationManager implements the exploration system functionality,
 * managing star systems, sectors, anomalies, and coordinating with ship operations.
 */
export class ExplorationManager extends AbstractBaseManager<BaseEvent> {
  // Maps to store exploration data
  private sectors: Map<string, Sector> = new Map();
  private anomalies: Map<string, Anomaly> = new Map();
  private scanOperations: Map<string, ScanOperation> = new Map();

  // References to other managers
  private shipManager: ReconShipManagerImpl;

  // Stats tracking
  private stats = {
    sectorsDiscovered: 0,
    sectorsScanned: 0,
    anomaliesDetected: 0,
    resourcesDetected: 0,
    activeScans: 0,
    completedScans: 0,
    failedScans: 0,
  };

  // Module ID for this manager (used in events)
  private moduleId: string = uuidv4();
  private moduleType: string = 'EXPLORATION'; // Using string instead of enum to avoid 'used as a value' error

  /**
   * Creates a new ExplorationManager
   *
   * @param eventBus The event bus to use for events
   * @param shipManager The ship manager to use for ship operations
   */
  constructor(eventBus: EventBus<BaseEvent>, shipManager: ReconShipManagerImpl) {
    super('ExplorationManager', eventBus);
    this.shipManager = shipManager;
  }

  /**
   * Get the version of this manager implementation
   */
  protected getVersion(): string {
    return '1.0.0';
  }

  /**
   * Get statistics for this manager (for monitoring)
   */
  protected getStats(): Record<string, number | string> {
    return {
      ...this.stats,
      sectorCount: this.sectors.size,
      anomalyCount: this.anomalies.size,
      activeScans: this.getActiveScans().length,
    };
  }

  /**
   * Initialize the exploration manager
   */
  protected async onInitialize(dependencies?: Record<string, unknown>): Promise<void> {
    console.log('ExplorationManager initialized');

    // Subscribe to ship-related events to update exploration data
    this.subscribeToEvent(EventType.STATUS_CHANGED, this.handleShipStatusChange);
  }

  /**
   * Handle updates on each tick
   */
  protected onUpdate(deltaTime: number): void {
    // Update active scan operations
    this.updateScanOperations(deltaTime);
  }

  /**
   * Clean up resources
   */
  protected async onDispose(): Promise<void> {
    // Nothing to clean up currently
    this.sectors.clear();
    this.anomalies.clear();
    this.scanOperations.clear();
  }

  /**
   * Creates a standard BaseEvent for our exploration events
   */
  private createEvent(type: string, data: ExplorationEventData): BaseEvent {
    return {
      type: type as unknown as EventType,
      timestamp: Date.now(),
      moduleId: this.moduleId,
      moduleType: this.moduleType as unknown as ModuleType,
      data: data as Record<string, unknown>,
    };
  }

  /**
   * Update active scan operations
   */
  private updateScanOperations(deltaTime: number): void {
    // Calculate progress for active scan operations
    for (const [id, operation] of this.scanOperations.entries()) {
      if (operation.status === 'active') {
        const elapsedTime = Date.now() - operation.startTime;
        const progress = Math.min(1, elapsedTime / operation.estimatedDuration);

        // Update the operation with new progress
        operation.progress = progress;

        // Check if the operation is complete
        if (progress >= 1) {
          this.completeScanOperation(id);
        }
      }
    }
  }

  /**
   * Complete a scan operation
   */
  private completeScanOperation(operationId: string): void {
    const operation = this.scanOperations.get(operationId);
    if (!operation) return;

    // Update the operation status
    operation.status = 'completed';
    operation.progress = 1;
    this.stats.completedScans++;
    this.stats.activeScans--;

    // Update the sector
    const sector = this.sectors.get(operation.sectorId);
    if (sector) {
      sector.status = 'analyzed';
      sector.lastScanned = Date.now();

      // Generate discoveries based on the scan
      this.generateDiscoveries(sector);

      // Emit a scan completed event
      this.publishEvent(
        this.createEvent(ExplorationEvents.SCAN_COMPLETED, {
          sector,
          operation,
        })
      );
    }
  }

  /**
   * Generate discoveries (resources and anomalies) for a sector
   */
  private generateDiscoveries(sector: Sector): void {
    // Logic to generate discoveries based on sector properties
    // This is simplified for now

    // Generate resources
    const resourceCount = Math.floor(sector.resourcePotential * 5);
    if (resourceCount > 0) {
      const resources = [];
      for (let i = 0; i < resourceCount; i++) {
        const resource = {
          type: this.getRandomResourceType(),
          amount: Math.floor(Math.random() * 100) + 10,
          quality: Math.random(),
        };
        resources.push(resource);
        this.stats.resourcesDetected++;

        // Emit a resource detected event
        this.publishEvent(
          this.createEvent(ExplorationEvents.RESOURCE_DETECTED, {
            resource,
            sector,
          })
        );
      }
      sector.resources = resources;
    }

    // Generate anomalies
    const anomalyChance = 0.3 + sector.habitabilityScore * 0.2;
    if (Math.random() < anomalyChance) {
      const anomaly: Anomaly = {
        id: uuidv4(),
        type: this.getRandomAnomalyType(),
        severity: this.getRandomSeverity(),
        description: 'Anomalous readings detected in this sector',
        position: {
          x: sector.coordinates.x + (Math.random() * 0.4 - 0.2),
          y: sector.coordinates.y + (Math.random() * 0.4 - 0.2),
        },
        discoveredAt: Date.now(),
        sectorId: sector.id,
      };

      sector.anomalies = [...(sector.anomalies || []), anomaly];
      this.anomalies.set(anomaly.id, anomaly);
      this.stats.anomaliesDetected++;

      // Emit an anomaly detected event
      this.publishEvent(
        this.createEvent(ExplorationEvents.ANOMALY_DETECTED, {
          anomaly,
          sector,
          anomalyId: anomaly.id,
        })
      );
    }
  }

  /**
   * Handle ship status changes
   */
  private handleShipStatusChange = (event: BaseEvent): void => {
    // Cast to a more specific event type if needed
    const shipEvent = event as unknown as {
      shipId: string;
      status: string;
      data?: Record<string, unknown>;
    };

    // Handle different ship status changes
    switch (shipEvent.status) {
      case 'scanning':
        // Start a new scan operation if the ship is assigned to a sector
        if (shipEvent.data?.sectorId) {
          this.startScanOperation(shipEvent.shipId, shipEvent.data.sectorId as string);
        }
        break;

      case 'idle':
        // Cancel any active scan operations for this ship
        this.cancelScanOperationsForShip(shipEvent.shipId);
        break;
    }
  };

  /**
   * Start a new scan operation
   */
  private startScanOperation(shipId: string, sectorId: string): void {
    const sector = this.sectors.get(sectorId);
    if (!sector) return;

    // Create a new scan operation
    const operation: ScanOperation = {
      id: uuidv4(),
      sectorId,
      shipId,
      startTime: Date.now(),
      estimatedDuration: this.calculateScanDuration(sector),
      progress: 0,
      status: 'active',
    };

    // Add the operation to the map
    this.scanOperations.set(operation.id, operation);
    this.stats.activeScans++;

    // Update the sector status
    sector.status = 'scanning';

    // Emit a scan started event
    this.publishEvent(
      this.createEvent(ExplorationEvents.SCAN_STARTED, {
        operation,
        sector,
        sectorId,
        shipId,
      })
    );
  }

  /**
   * Cancel scan operations for a ship
   */
  private cancelScanOperationsForShip(shipId: string): void {
    for (const [id, operation] of this.scanOperations.entries()) {
      if (operation.shipId === shipId && operation.status === 'active') {
        operation.status = 'cancelled';
        this.stats.activeScans--;

        // Emit a scan failed event
        this.publishEvent(
          this.createEvent(ExplorationEvents.SCAN_FAILED, {
            operation,
            reason: 'cancelled_by_ship',
            sectorId: operation.sectorId,
            shipId,
          })
        );
      }
    }
  }

  /**
   * Calculate the duration for a scan operation
   */
  private calculateScanDuration(sector: Sector): number {
    // Base duration is 30 seconds
    let duration = 30000;

    // Adjust for resource potential
    duration += sector.resourcePotential * 10000;

    // Adjust for habitability score
    duration += sector.habitabilityScore * 5000;

    // Add some randomness
    duration *= 0.8 + Math.random() * 0.4;

    return duration;
  }

  /**
   * Get a random resource type
   */
  private getRandomResourceType(): string {
    const types = ['minerals', 'energy', 'gas', 'exotic', 'water', 'metals'];
    return types[Math.floor(Math.random() * types.length)];
  }

  /**
   * Get a random anomaly type
   */
  private getRandomAnomalyType(): string {
    const types = ['spatial', 'temporal', 'quantum', 'biological', 'gravitational', 'unknown'];
    return types[Math.floor(Math.random() * types.length)];
  }

  /**
   * Get a random severity level
   */
  private getRandomSeverity(): 'low' | 'medium' | 'high' {
    const severities = ['low', 'medium', 'high'];
    return severities[Math.floor(Math.random() * severities.length)] as 'low' | 'medium' | 'high';
  }

  /**
   * Create or add a new sector
   */
  public addSector(sectorData: Omit<Sector, 'id' | 'anomalies' | 'discoveredAt'>): Sector {
    const now = Date.now();
    const id = uuidv4();

    const sector: Sector = {
      ...sectorData,
      id,
      anomalies: [],
      discoveredAt: now,
    };

    this.sectors.set(sector.id, sector);
    this.stats.sectorsDiscovered++;

    // Emit a sector discovered event
    this.publishEvent(
      this.createEvent(ExplorationEvents.SECTOR_DISCOVERED, {
        sector,
        sectorId: sector.id,
      })
    );

    return sector;
  }

  /**
   * Assign a ship to scan a sector
   */
  public assignShipToSector(shipId: string, sectorId: string): boolean {
    const sector = this.sectors.get(sectorId);
    if (!sector) return false;

    // Check if ship exists and update its status using the ship manager
    const ship = this.shipManager.getShipById(shipId);
    if (!ship) return false;

    // Assign the ship to the sector in the ship manager
    if (!this.shipManager.assignShipToSector(shipId, sectorId)) {
      return false;
    }

    // Start the scan operation
    this.startScanOperation(shipId, sectorId);

    // Emit a ship assigned event
    this.publishEvent(
      this.createEvent(ExplorationEvents.SHIP_ASSIGNED, {
        sectorId,
        shipId,
        sector,
        ship,
      })
    );

    return true;
  }

  /**
   * Unassign a ship from scanning a sector
   */
  public unassignShip(shipId: string): boolean {
    // Get the ship
    const ship = this.shipManager.getShipById(shipId);
    if (!ship) return false;

    // Cancel any active scan operations
    this.cancelScanOperationsForShip(shipId);

    // Unassign the ship in the ship manager
    if (!this.shipManager.unassignShip(shipId)) {
      return false;
    }

    // Emit a ship unassigned event
    this.publishEvent(
      this.createEvent(ExplorationEvents.SHIP_UNASSIGNED, {
        shipId,
        ship,
      })
    );

    return true;
  }

  /**
   * Get a sector by id
   */
  public getSector(sectorId: string): Sector | undefined {
    return this.sectors.get(sectorId);
  }

  /**
   * Get all sectors
   */
  public getAllSectors(): Sector[] {
    return Array.from(this.sectors.values());
  }

  /**
   * Get sectors by status
   */
  public getSectorsByStatus(status: Sector['status']): Sector[] {
    return Array.from(this.sectors.values()).filter(sector => sector.status === status);
  }

  /**
   * Get an anomaly by id
   */
  public getAnomaly(anomalyId: string): Anomaly | undefined {
    return this.anomalies.get(anomalyId);
  }

  /**
   * Get all anomalies
   */
  public getAllAnomalies(): Anomaly[] {
    return Array.from(this.anomalies.values());
  }

  /**
   * Get anomalies by sector
   */
  public getAnomaliesBySector(sectorId: string): Anomaly[] {
    return Array.from(this.anomalies.values()).filter(anomaly => anomaly.sectorId === sectorId);
  }

  /**
   * Get active scan operations
   */
  public getActiveScans(): ScanOperation[] {
    return Array.from(this.scanOperations.values()).filter(op => op.status === 'active');
  }

  /**
   * Get scan operations by sector
   */
  public getScanOperationsBySector(sectorId: string): ScanOperation[] {
    return Array.from(this.scanOperations.values()).filter(op => op.sectorId === sectorId);
  }

  /**
   * Get scan operations by ship
   */
  public getScanOperationsByShip(shipId: string): ScanOperation[] {
    return Array.from(this.scanOperations.values()).filter(op => op.shipId === shipId);
  }
}

