/**
 * @file ExplorationManagerImpl.ts
 * Implementation of the ExplorationManager that conforms to the BaseManager interface.
 *
 * This class handles:
 * 1. Star system management and ship assignments
 * 2. Sector scanning and discovery tracking
 * 3. Integration with ship managers for operation
 * 4. Event-based communication with UI components
 */

import { v4 as uuidv4 } from 'uuid';
import { EventBus } from '../../lib/events/EventBus';
import { AbstractBaseManager } from '../../lib/managers/BaseManager';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import { BaseEvent, EventType } from '../../types/events/EventTypes';

// Define Exploration specific event types
export enum ExplorationEvents {
  SECTOR_DISCOVERED = 'EXPLORATION_SECTOR_DISCOVERED',
  SECTOR_SCANNED = 'EXPLORATION_SECTOR_SCANNED',
  ANOMALY_DETECTED = 'EXPLORATION_ANOMALY_DETECTED',
  RESOURCE_DETECTED = 'EXPLORATION_RESOURCE_DETECTED',
  SCAN_STARTED = 'EXPLORATION_SCAN_STARTED',
  SCAN_COMPLETED = 'EXPLORATION_SCAN_COMPLETED',
  SCAN_FAILED = 'EXPLORATION_SCAN_FAILED',
  SHIP_ASSIGNED = 'EXPLORATION_SHIP_ASSIGNED',
  SHIP_UNASSIGNED = 'EXPLORATION_SHIP_UNASSIGNED',
  SYSTEM_CREATED = 'EXPLORATION_SYSTEM_CREATED',
  SYSTEM_UPDATED = 'EXPLORATION_SYSTEM_UPDATED',
}

// Define interfaces for the types used
export interface StarSystem {
  id: string;
  name: string;
  type?: string;
  resources?: string[];
  status: 'unmapped' | 'mapped' | 'scanning' | 'analyzed';
  assignedShips: string[];
  position: {
    x: number;
    y: number;
  };
  lastScanned?: number;
  discoveredAt?: number;
}

export interface SystemSearchCriteria {
  name?: string;
  type?: string;
  resources?: string[];
  status?: string;
}

// Ship interface to break circular dependency
export interface IShip {
  id: string;
  name: string;
  type: string;
  status: string;
  assignedTo?: string;
}

// Ship manager interface to break circular dependency
export interface IShipManager {
  getShipById(shipId: string): IShip | undefined;
  updateShipStatus(shipId: string, status: string): void;
  updateShipAssignment(shipId: string, systemId: string): void;
  getAllShips(): IShip[];
  getShipsByType(type: string): IShip[];
  getShipsByStatus(status: string): IShip[];
}

// Event data interface
export interface ExplorationEventData extends Record<string, unknown> {
  system?: StarSystem;
  shipId?: string;
  systemId?: string;
  reason?: string;
}

/**
 * ExplorationManagerImpl implements the exploration manager functionality,
 * managing star systems and their assignments.
 */
export class ExplorationManagerImpl extends AbstractBaseManager<BaseEvent> {
  private systems: Map<string, StarSystem> = new Map();

  // Module ID for this manager (used in events)
  private moduleId: string = uuidv4();

  // Statistics
  private stats = {
    systemsCreated: 0,
    systemsUpdated: 0,
    shipsAssigned: 0,
    shipsUnassigned: 0,
  };

  /**
   * Create a new ExplorationManagerImpl
   *
   * @param eventBus The event bus for events
   * @param shipManager The ship manager implementation
   */
  constructor(
    eventBus: EventBus<BaseEvent>,
    private shipManager: IShipManager
  ) {
    super('ExplorationManagerImpl', eventBus);
  }

  /**
   * Get the version of this manager implementation
   */
  protected getVersion(): string {
    return '1.0.0';
  }

  /**
   * Get statistics for this manager (for monitoring)
   */
  protected getStats(): Record<string, number | string> {
    return {
      ...this.stats,
      systemCount: this.systems.size,
    };
  }

  /**
   * Initialize the exploration manager
   */
  protected async onInitialize(dependencies?: Record<string, unknown>): Promise<void> {
    console.log('ExplorationManagerImpl initialized');

    // No initialization needed at this time
    return Promise.resolve();
  }

  /**
   * Handle updates on each tick
   */
  protected onUpdate(deltaTime: number): void {
    // Currently no time-based updates needed
  }

  /**
   * Clean up resources
   */
  protected async onDispose(): Promise<void> {
    this.systems.clear();
    return Promise.resolve();
  }

  /**
   * Create an event with proper structure
   */
  private createEvent(eventType: ExplorationEvents, data: ExplorationEventData): BaseEvent {
    return {
      type: eventType as unknown as EventType,
      timestamp: Date.now(),
      moduleId: this.moduleId,
      moduleType: 'EXPLORATION' as ModuleType,
      data,
    };
  }

  /**
   * Create a new star system in the exploration manager
   */
  public createStarSystem(system: {
    id: string;
    name: string;
    status: 'unmapped' | 'mapped' | 'scanning' | 'analyzed';
    position?: { x: number; y: number };
  }): StarSystem {
    const now = Date.now();

    const newSystem: StarSystem = {
      ...system,
      position: system.position || { x: 0, y: 0 },
      assignedShips: [],
      discoveredAt: now,
    };

    this.systems.set(system.id, newSystem);
    this.stats.systemsCreated++;

    // Emit event
    const event = this.createEvent(ExplorationEvents.SYSTEM_CREATED, {
      system: newSystem,
    });
    this.publishEvent(event);

    return newSystem;
  }

  /**
   * Get a star system by ID
   */
  public getSystemById(systemId: string): StarSystem | undefined {
    return this.systems.get(systemId);
  }

  /**
   * Get all star systems
   */
  public getAllSystems(): StarSystem[] {
    return Array.from(this.systems.values());
  }

  /**
   * Add an existing star system to the exploration manager
   */
  public addStarSystem(system: {
    id: string;
    name: string;
    type: string;
    resources: string[];
    status: 'unmapped' | 'mapped' | 'scanning' | 'analyzed';
    position?: { x: number; y: number };
  }): StarSystem {
    const now = Date.now();

    const newSystem: StarSystem = {
      ...system,
      position: system.position || { x: 0, y: 0 },
      assignedShips: [],
      discoveredAt: now,
    };

    this.systems.set(system.id, newSystem);
    this.stats.systemsCreated++;

    // Emit event
    const event = this.createEvent(ExplorationEvents.SYSTEM_CREATED, {
      system: newSystem,
    });
    this.publishEvent(event);

    return newSystem;
  }

  /**
   * Update a star system
   */
  public updateSystem(
    systemId: string,
    updates: Partial<Omit<StarSystem, 'id'>>
  ): StarSystem | undefined {
    const system = this.systems.get(systemId);
    if (!system) {
      return undefined;
    }

    const updatedSystem = {
      ...system,
      ...updates,
    };

    this.systems.set(systemId, updatedSystem);
    this.stats.systemsUpdated++;

    // Emit event
    const event = this.createEvent(ExplorationEvents.SYSTEM_UPDATED, {
      system: updatedSystem,
    });
    this.publishEvent(event);

    return updatedSystem;
  }

  /**
   * Assign a ship to a star system
   */
  public assignShipToSystem(shipId: string, systemId: string): boolean {
    const system = this.systems.get(systemId);
    if (!system) {
      return false;
    }

    const ship = this.shipManager.getShipById(shipId);
    if (!ship) {
      return false;
    }

    // Update the system
    system.assignedShips.push(shipId);
    this.systems.set(systemId, system);

    // Update the ship
    this.shipManager.updateShipStatus(shipId, 'assigned');
    this.shipManager.updateShipAssignment(shipId, systemId);

    this.stats.shipsAssigned++;

    // Emit event
    const event = this.createEvent(ExplorationEvents.SHIP_ASSIGNED, {
      system,
      shipId,
      systemId,
    });
    this.publishEvent(event);

    return true;
  }

  /**
   * Unassign a ship from a system
   */
  public unassignShipFromSystem(shipId: string, systemId: string): boolean {
    const system = this.systems.get(systemId);
    if (!system) {
      return false;
    }

    // Check if the ship is actually assigned to this system
    const shipIndex = system.assignedShips.indexOf(shipId);
    if (shipIndex === -1) {
      return false;
    }

    // Update the system
    system.assignedShips.splice(shipIndex, 1);
    this.systems.set(systemId, system);

    // Update the ship
    this.shipManager.updateShipStatus(shipId, 'idle');
    this.shipManager.updateShipAssignment(shipId, '');

    this.stats.shipsUnassigned++;

    // Emit event
    const event = this.createEvent(ExplorationEvents.SHIP_UNASSIGNED, {
      system,
      shipId,
      systemId,
    });
    this.publishEvent(event);

    return true;
  }

  /**
   * Search star systems based on criteria
   */
  public searchSystems(criteria: SystemSearchCriteria): StarSystem[] {
    const results: StarSystem[] = [];

    for (const system of this.systems.values()) {
      if (this.matchesCriteria(system, criteria)) {
        results.push(system);
      }
    }

    return results;
  }

  /**
   * Check if a system matches the search criteria
   */
  private matchesCriteria(system: StarSystem, criteria: SystemSearchCriteria): boolean {
    // Check name
    if (criteria.name && !system.name.toLowerCase().includes(criteria.name.toLowerCase())) {
      return false;
    }

    // Check type
    if (criteria.type && system.type !== criteria.type) {
      return false;
    }

    // Check resources
    if (criteria.resources && criteria.resources.length > 0) {
      if (!system.resources) {
        return false;
      }

      if (!criteria.resources.every(resource => system.resources?.includes(resource))) {
        return false;
      }
    }

    // Check status
    if (criteria.status && system.status !== criteria.status) {
      return false;
    }

    return true;
  }
}

/**
 * @file ReconShipManagerImpl.ts
 * Implementation of the ReconShipManager for exploration ships.
 *
 * This file provides a basic implementation of ship management
 * functionality used by the ExplorationManager.
 */

import { EventEmitter } from 'events';
import { v4 as uuidv4 } from 'uuid';
import { Position } from '../../types/core/GameTypes';

/**
 * Interface for exploration ship data
 */
export interface Ship {
  id: string;
  name: string;
  type: string;
  status: string;
  sensorRange?: number;
  speed?: number;
  efficiency?: number;
  sectorId?: string;
  position?: Position;
  capabilities?: {
    canScan: boolean;
    canSalvage: boolean;
    canMine: boolean;
    canJump: boolean;
  };
  stealth?: {
    active: boolean;
    level: number;
    cooldown: number;
  };
  sensors?: {
    range: number;
    accuracy: number;
    anomalyDetection: number;
  };
  discoveries?: {
    mappedSectors: number;
    anomaliesFound: number;
    resourcesLocated: number;
  };
  [key: string]: unknown;
}

export interface ExplorationTask {
  id: string;
  type: 'explore' | 'investigate' | 'evade';
  target: {
    id: string;
    position: Position;
  };
  priority: number;
  assignedAt: number;
  specialization: 'mapping' | 'anomaly' | 'resource';
  status: 'queued' | 'in-progress' | 'completed' | 'failed';
  progress?: number;
  threatLevel?: number;
}

export type ShipEvent = {
  shipId: string;
  task?: ExplorationTask;
  progress?: number;
};

export type EventCallback = (event: ShipEvent) => void;

/**
 * Implementation of the ship manager for exploration ships
 */
export class ReconShipManagerImpl extends EventEmitter {
  private ships: Map<string, Ship> = new Map();
  private tasks: Map<string, ExplorationTask> = new Map();
  private lastUpdate: number = Date.now();

  /**
   * Create a new ReconShipManagerImpl
   */
  constructor() {
    super();
    this.ships = new Map();
    this.tasks = new Map();

    // Add some sample ships
    this.addShip({
      id: uuidv4(),
      name: 'Explorer I',
      type: 'recon',
      status: 'idle',
      sensorRange: 5,
      speed: 3,
      efficiency: 0.8,
      position: { x: 0, y: 0 },
    });

    this.addShip({
      id: uuidv4(),
      name: 'Explorer II',
      type: 'recon',
      status: 'idle',
      sensorRange: 6,
      speed: 2.5,
      efficiency: 0.9,
      position: { x: 0, y: 0 },
    });

    this.addShip({
      id: uuidv4(),
      name: 'Scout I',
      type: 'scout',
      status: 'idle',
      sensorRange: 3,
      speed: 5,
      efficiency: 0.7,
      position: { x: 0, y: 0 },
    });
  }

  /**
   * Add a new ship to the manager
   * @param ship The ship to add
   * @returns The added ship
   */
  public addShip(ship: Ship): Ship {
    this.ships.set(ship.id, ship);
    this.emit('shipRegistered', { shipId: ship.id });
    return ship;
  }

  /**
   * Get a ship by its ID
   * @param shipId The ID of the ship to get
   * @returns The ship or undefined if not found
   */
  public getShipById(shipId: string): Ship | undefined {
    return this.ships.get(shipId);
  }

  /**
   * Get all ships
   * @returns All ships
   */
  public getAllShips(): Ship[] {
    return Array.from(this.ships.values());
  }

  /**
   * Get ships by status
   * @param status The status to filter by
   * @returns Ships with the given status
   */
  public getShipsByStatus(status: string): Ship[] {
    return Array.from(this.ships.values()).filter(ship => ship.status === status);
  }

  /**
   * Update a ship's status
   * @param shipId The ID of the ship to update
   * @param status The new status
   * @returns True if the ship was updated, false otherwise
   */
  public updateShipStatus(shipId: string, status: string): boolean {
    const ship = this.ships.get(shipId);
    if (!ship) return false;

    ship.status = status;
    return true;
  }

  /**
   * Assign a ship to a sector
   * @param shipId The ID of the ship to assign
   * @param sectorId The ID of the sector to assign to
   * @returns True if the ship was assigned, false otherwise
   */
  public assignShipToSector(shipId: string, sectorId: string): boolean {
    const ship = this.ships.get(shipId);
    if (!ship) return false;

    ship.sectorId = sectorId;
    ship.status = 'scanning';
    return true;
  }

  /**
   * Unassign a ship from its current assignment
   * @param shipId The ID of the ship to unassign
   * @returns True if the ship was unassigned, false otherwise
   */
  public unassignShip(shipId: string): boolean {
    const ship = this.ships.get(shipId);
    if (!ship) return false;

    delete ship.sectorId;
    ship.status = 'idle';
    return true;
  }

  public registerShip(ship: Ship): void {
    this.ships.set(ship.id, ship);
    this.emit('shipRegistered', { shipId: ship.id });
  }

  public unregisterShip(shipId: string): void {
    this.ships.delete(shipId);
    this.emit('shipUnregistered', { shipId });
  }

  public assignExplorationTask(
    shipId: string,
    sectorId: string,
    position: Position,
    specialization: 'mapping' | 'anomaly' | 'resource'
  ): void {
    const task: ExplorationTask = {
      id: uuidv4(),
      type: 'explore',
      target: {
        id: sectorId,
        position,
      },
      priority: 1,
      assignedAt: Date.now(),
      specialization,
      status: 'queued',
    };

    this.tasks.set(task.id, task);
    this.emit('taskAssigned', { shipId, task });
  }

  public update(deltaTime: number): void {
    const now = Date.now();
    const dt = now - this.lastUpdate;
    this.lastUpdate = now;

    // Update all active tasks
    for (const [taskId, task] of this.tasks) {
      if (task.status === 'in-progress') {
        // Simulate task progress
        const progress = (task.progress || 0) + (deltaTime / 1000) * 0.1; // 10% per second
        if (progress >= 1) {
          task.status = 'completed';
          this.emit('taskCompleted', { shipId: taskId, task });
          this.tasks.delete(taskId);
        } else {
          task.progress = progress;
          this.emit('taskProgress', { shipId: taskId, task, progress });
        }
      }
    }
  }

  public on(event: string, listener: EventCallback): this {
    return super.on(event, listener);
  }

  public off(event: string, listener: EventCallback): this {
    return super.off(event, listener);
  }
}

import { ModuleEventType, moduleEventBus } from '../../lib/modules/ModuleEvents';

/**
 * Update priority levels
 */
export enum UpdatePriority {
  CRITICAL = 0, // Run every frame, no matter what (physics, core game state)
  HIGH = 1, // Run most frames, can be skipped occasionally (AI, combat)
  NORMAL = 2, // Run regularly, can be skipped more often (resource updates)
  LOW = 3, // Run occasionally (visual effects, non-critical updates)
  BACKGROUND = 4, // Run when there's spare time (analytics, cleanup)
}

/**
 * Update callback type
 */
export type UpdateCallback = (deltaTime: number, elapsedTime: number) => void;

/**
 * Update registration interface
 */
export interface UpdateRegistration {
  id: string;
  callback: UpdateCallback;
  priority: UpdatePriority;
  interval?: number; // Optional interval in ms (for fixed timestep updates)
  lastUpdate?: number; // Last time this was updated
}

/**
 * Game loop statistics
 */
export interface GameLoopStats {
  fps: number;
  frameTime: number;
  updateTime: number;
  renderTime: number;
  idleTime: number;
  elapsedTime: number;
  frameCount: number;
  skippedFrames: number;
  priorityStats: Record<
    UpdatePriority,
    {
      count: number;
      totalTime: number;
      averageTime: number;
    }
  >;
}

/**
 * Game loop manager configuration
 */
export interface GameLoopConfig {
  targetFPS: number;
  maxDeltaTime: number;
  priorityThrottling: boolean;
  fixedTimestep: boolean;
  throttlePriorities: UpdatePriority[];
  statsInterval: number;
  enableStats: boolean;
}

/**
 * Game loop manager for centralized timing and updates
 */
export class GameLoopManager {
  private updates: Map<string, UpdateRegistration> = new Map();
  private running: boolean = false;
  private lastFrameTime: number = 0;
  private frameCount: number = 0;
  private skippedFrames: number = 0;
  private elapsedTime: number = 0;
  private animationFrameId: number | null = null;
  private statsInterval: number | null = null;
  private stats: GameLoopStats;
  private config: GameLoopConfig;

  constructor(config: Partial<GameLoopConfig> = {}) {
    // Default configuration
    this.config = {
      targetFPS: 60,
      maxDeltaTime: 1000 / 30, // Cap at 30 FPS equivalent
      priorityThrottling: true,
      fixedTimestep: false,
      throttlePriorities: [UpdatePriority.LOW, UpdatePriority.BACKGROUND],
      statsInterval: 1000, // 1 second
      enableStats: true,
      ...config,
    };

    // Initialize stats
    this.stats = {
      fps: 0,
      frameTime: 0,
      updateTime: 0,
      renderTime: 0,
      idleTime: 0,
      elapsedTime: 0,
      frameCount: 0,
      skippedFrames: 0,
      priorityStats: {
        [UpdatePriority.CRITICAL]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.HIGH]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.NORMAL]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.LOW]: { count: 0, totalTime: 0, averageTime: 0 },
        [UpdatePriority.BACKGROUND]: { count: 0, totalTime: 0, averageTime: 0 },
      },
    };
  }

  /**
   * Start the game loop
   */
  public start(): void {
    if (this.running) {
      return;
    }

    this.running = true;
    this.lastFrameTime = performance.now();
    this.frameCount = 0;
    this.skippedFrames = 0;
    this.elapsedTime = 0;

    // Start the game loop
    this.animationFrameId = requestAnimationFrame(this.gameLoop);

    // Start stats reporting if enabled
    if (this.config.enableStats) {
      this.statsInterval = window.setInterval(() => {
        this.reportStats();
      }, this.config.statsInterval);
    }

    // Emit game loop started event
    moduleEventBus.emit({
      type: 'GAME_LOOP_STARTED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { config: this.config },
    });
  }

  /**
   * Stop the game loop
   */
  public stop(): void {
    if (!this.running) {
      return;
    }

    this.running = false;

    // Stop the animation frame
    if (this.animationFrameId !== null) {
      cancelAnimationFrame(this.animationFrameId);
      this.animationFrameId = null;
    }

    // Stop stats reporting
    if (this.statsInterval !== null) {
      clearInterval(this.statsInterval);
      this.statsInterval = null;
    }

    // Emit game loop stopped event
    moduleEventBus.emit({
      type: 'GAME_LOOP_STOPPED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { stats: this.stats },
    });
  }

  /**
   * Register an update callback
   */
  public registerUpdate(
    id: string,
    callback: UpdateCallback,
    priority: UpdatePriority = UpdatePriority.NORMAL,
    interval?: number
  ): void {
    this.updates.set(id, {
      id,
      callback,
      priority,
      interval,
      lastUpdate: performance.now(),
    });

    // Emit update registered event
    moduleEventBus.emit({
      type: 'GAME_LOOP_UPDATE_REGISTERED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { id, priority, interval },
    });
  }

  /**
   * Unregister an update callback
   */
  public unregisterUpdate(id: string): void {
    if (this.updates.has(id)) {
      this.updates.delete(id);

      // Emit update unregistered event
      moduleEventBus.emit({
        type: 'GAME_LOOP_UPDATE_UNREGISTERED' as ModuleEventType,
        moduleId: 'game-loop-manager',
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        data: { id },
      });
    }
  }

  /**
   * Get the current game loop stats
   */
  public getStats(): GameLoopStats {
    return { ...this.stats };
  }

  /**
   * Update the game loop configuration
   */
  public updateConfig(config: Partial<GameLoopConfig>): void {
    this.config = {
      ...this.config,
      ...config,
    };

    // Emit config updated event
    moduleEventBus.emit({
      type: 'GAME_LOOP_CONFIG_UPDATED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { config: this.config },
    });
  }

  /**
   * Adjust update frequency based on performance requirements
   * Used by the adaptive performance system to optimize resource usage
   *
   * @param factor - Adjustment factor (0.1-1.0) where:
   *   - 1.0 = full speed (target FPS)
   *   - 0.5 = half speed
   *   - etc.
   */
  public adjustUpdateFrequency(factor: number): void {
    if (factor <= 0 || factor > 1) {
      console.warn('[GameLoopManager] Invalid adjustment factor, must be between 0.1 and 1.0');
      return;
    }

    // Adjust target FPS based on the factor
    const baseTargetFPS = 60; // Default target
    const adjustedFPS = Math.max(15, Math.round(baseTargetFPS * factor)); // Don't go below 15 FPS

    // Update the configuration
    this.updateConfig({
      targetFPS: adjustedFPS,
      priorityThrottling: factor < 0.8, // Enable priority throttling for significant adjustments
    });

    // Update which priorities get throttled based on the factor
    const throttlePriorities = [];

    // As factor decreases, add more priority levels to throttle
    if (factor < 0.9) throttlePriorities.push(UpdatePriority.BACKGROUND);
    if (factor < 0.7) throttlePriorities.push(UpdatePriority.LOW);
    if (factor < 0.5) throttlePriorities.push(UpdatePriority.NORMAL);
    if (factor < 0.3) throttlePriorities.push(UpdatePriority.HIGH);

    // Update throttle priorities if changed
    if (throttlePriorities.length > 0) {
      this.updateConfig({ throttlePriorities });
    }

    // Emit adjustment event
    moduleEventBus.emit({
      type: 'GAME_LOOP_ADJUSTED' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        factor,
        adjustedFPS: adjustedFPS,
        throttlePriorities,
      },
    });

    console.log(
      `[GameLoopManager] Adjusted to ${Math.round(factor * 100)}% speed (${adjustedFPS} FPS)`
    );
  }

  /**
   * The main game loop
   */
  private gameLoop = (timestamp: number): void => {
    if (!this.running) {
      return;
    }

    // Schedule the next frame
    this.animationFrameId = requestAnimationFrame(this.gameLoop);

    // Calculate delta time
    const now = timestamp;
    let deltaTime = now - this.lastFrameTime;
    this.lastFrameTime = now;

    // Cap delta time to prevent spiral of death
    if (deltaTime > this.config.maxDeltaTime) {
      deltaTime = this.config.maxDeltaTime;
      this.skippedFrames++;
    }

    // Update elapsed time
    this.elapsedTime += deltaTime;
    this.frameCount++;

    // Update frame time stats
    this.stats.frameTime = deltaTime;

    // Start update timing
    const updateStartTime = performance.now();

    // Process updates by priority
    this.processUpdates(deltaTime, this.elapsedTime);

    // End update timing
    const updateEndTime = performance.now();
    this.stats.updateTime = updateEndTime - updateStartTime;

    // Calculate idle time (time left in the frame)
    const targetFrameTime = 1000 / this.config.targetFPS;
    const currentFrameTime = performance.now() - now;
    this.stats.idleTime = Math.max(0, targetFrameTime - currentFrameTime);

    // Update FPS stats
    this.stats.fps = 1000 / deltaTime;
    this.stats.elapsedTime = this.elapsedTime;
    this.stats.frameCount = this.frameCount;
    this.stats.skippedFrames = this.skippedFrames;
  };

  /**
   * Process all registered updates
   */
  private processUpdates(deltaTime: number, elapsedTime: number): void {
    // Group updates by priority
    const priorityGroups: Map<UpdatePriority, UpdateRegistration[]> = new Map();

    for (const update of this.updates.values()) {
      if (!priorityGroups.has(update.priority)) {
        priorityGroups.set(update.priority, []);
      }
      priorityGroups.get(update.priority)!.push(update);
    }

    // Process each priority group
    for (
      let priority = UpdatePriority.CRITICAL;
      priority <= UpdatePriority.BACKGROUND;
      priority++
    ) {
      const updates = priorityGroups.get(priority) || [];

      // Skip throttled priorities if enabled
      if (
        this.config.priorityThrottling &&
        this.config.throttlePriorities.includes(priority) &&
        this.frameCount % (priority + 1) !== 0
      ) {
        continue;
      }

      // Process all updates in this priority group
      for (const update of updates) {
        // Skip if interval is set and not enough time has passed
        if (update.interval && update.lastUpdate) {
          const timeSinceLastUpdate = elapsedTime - update.lastUpdate;
          if (timeSinceLastUpdate < update.interval) {
            continue;
          }
        }

        // Update the last update time
        update.lastUpdate = elapsedTime;

        // Measure update time
        const startTime = performance.now();

        try {
          // Call the update callback
          update.callback(deltaTime, elapsedTime);
        } catch (error) {
          console.error(`Error in update ${update.id}:`, error);

          // Emit error event
          moduleEventBus.emit({
            type: 'ERROR_OCCURRED' as ModuleEventType,
            moduleId: 'game-loop-manager',
            moduleType: 'resource-manager',
            timestamp: Date.now(),
            data: {
              error,
              updateId: update.id,
              priority: update.priority,
            },
          });
        }

        // Update stats
        const endTime = performance.now();
        const updateTime = endTime - startTime;

        this.stats.priorityStats[priority].count++;
        this.stats.priorityStats[priority].totalTime += updateTime;
        this.stats.priorityStats[priority].averageTime =
          this.stats.priorityStats[priority].totalTime / this.stats.priorityStats[priority].count;
      }
    }
  }

  /**
   * Report game loop stats
   */
  private reportStats(): void {
    // Calculate average FPS
    const avgFps = this.frameCount / (this.config.statsInterval / 1000);

    // Reset frame count for next interval
    this.frameCount = 0;

    // Emit stats event
    moduleEventBus.emit({
      type: 'GAME_LOOP_STATS' as ModuleEventType,
      moduleId: 'game-loop-manager',
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        ...this.stats,
        avgFps,
      },
    });

    // Log stats if in development
    if (process.env.NODE_ENV === 'development') {
      console.warn('[GameLoop] Stats:', {
        fps: avgFps.toFixed(2),
        updateTime: this.stats.updateTime.toFixed(2) + 'ms',
        idleTime: this.stats.idleTime.toFixed(2) + 'ms',
        skippedFrames: this.stats.skippedFrames,
      });
    }
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    this.stop();
    this.updates.clear();
  }
}

// Export singleton instance
export const gameLoopManager = new GameLoopManager();

import {
  DEFAULT_CONSUMPTION_RATES,
  DEFAULT_PRODUCTION_RATES,
  PRODUCTION_INTERVALS,
  RESOURCE_MANAGER_CONFIG,
  RESOURCE_PRIORITIES,
  RESOURCE_THRESHOLDS,
  STORAGE_EFFICIENCY,
  TRANSFER_CONFIG,
} from '../../config/resource/ResourceConfig';
import { EventBus } from '../../lib/events/EventBus';
import { AbstractBaseManager } from '../../lib/managers/BaseManager';
import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import { BaseEvent, EventType } from '../../types/events/EventTypes';
import {
  ResourceConsumption,
  ResourceFlow,
  ResourceManagerConfig,
  ResourceProduction,
  ResourceState,
  ResourceThreshold,
  ResourceTransfer,
  ResourceType,
} from '../../types/resources/ResourceTypes';
import { resourcePerformanceMonitor } from '../resource/ResourcePerformanceMonitor';

// Update TRANSFER_CONFIG type to include MIN_INTERVAL
const TRANSFER_CONFIG_WITH_MIN = {
  ...TRANSFER_CONFIG,
  MIN_INTERVAL: 500, // Minimum 500ms between transfers
};

/**
 * Resource operation error types
 */
type ResourceError = {
  code: 'INVALID_RESOURCE' | 'INSUFFICIENT_RESOURCES' | 'INVALID_TRANSFER' | 'THRESHOLD_VIOLATION';
  message: string;
  details?: unknown;
};

/**
 * Resource optimization strategies
 */
interface OptimizationStrategy {
  id: string;
  type: 'production' | 'consumption' | 'transfer';
  priority: number;
  condition: () => boolean;
  apply: () => void;
}

/**
 * Resource manager event interface
 */
export interface ResourceManagerEvent extends BaseEvent {
  type: EventType;
  resourceType?: ResourceType;
  amount?: number;
  source?: string;
  target?: string;
  details?: Record<string, unknown>;
}

// We need a workaround for the type issue with EventBus
// Create a type that maps ResourceManagerEventType to EventType for the AbstractBaseManager
type ResourceEventMap = {
  [K in EventType]: EventType;
};

/**
 * Manages game resources
 */
export class ResourceManager extends AbstractBaseManager<BaseEvent> {
  private resources: Map<ResourceType, ResourceState>;
  private transfers: ResourceTransfer[];
  private maxTransferHistory: number;
  private productions: Map<string, ResourceProduction>;
  private consumptions: Map<string, ResourceConsumption>;
  private flows: Map<string, ResourceFlow>;
  private storageEfficiency: number;
  private config: ResourceManagerConfig;
  private productionIntervals: Map<string, NodeJS.Timeout>;
  private errors: Map<string, ResourceError>;
  private optimizationStrategies: Map<string, OptimizationStrategy>;
  private optimizationMetrics: {
    productionEfficiency: number;
    consumptionEfficiency: number;
    transferEfficiency: number;
    lastOptimizationTime: number;
  };

  constructor(
    maxTransferHistory = 1000,
    config: ResourceManagerConfig = RESOURCE_MANAGER_CONFIG,
    eventBus?: EventBus<BaseEvent>
  ) {
    super('ResourceManager', eventBus || new EventBus<BaseEvent>());

    this.resources = new Map();
    this.transfers = [];
    this.maxTransferHistory = maxTransferHistory;
    this.productions = new Map();
    this.consumptions = new Map();
    this.flows = new Map();
    this.storageEfficiency = STORAGE_EFFICIENCY.BASE;
    this.config = config;
    this.productionIntervals = new Map();
    this.errors = new Map();
    this.optimizationStrategies = new Map();
    this.optimizationMetrics = {
      productionEfficiency: 1.0,
      consumptionEfficiency: 1.0,
      transferEfficiency: 1.0,
      lastOptimizationTime: Date.now(),
    };

    console.warn('[ResourceManager] Created with config:', config);
  }

  /**
   * @inheritdoc
   */
  protected async onInitialize(dependencies?: Record<string, unknown>): Promise<void> {
    // Initialize resources with config limits
    if (this.config.defaultResourceLimits) {
      Object.entries(this.config.defaultResourceLimits).forEach(([type, limits]) => {
        this.initializeResource(type as ResourceType, limits.min, limits.max);
      });
    } else {
      console.warn(
        '[ResourceManager] Warning: defaultResourceLimits is null or undefined in config'
      );
    }

    // Initialize optimization strategies
    this.initializeOptimizationStrategies();

    // Publish initialization event
    this.publishEvent({
      type: EventType.SYSTEM_STARTUP,
      moduleId: this.id,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: { config: this.config },
    });

    console.warn('[ResourceManager] Initialized with config:', this.config);
  }

  /**
   * @inheritdoc
   */
  protected onUpdate(deltaTime: number): void {
    // Process optimizations every 5 seconds
    if (Date.now() - this.optimizationMetrics.lastOptimizationTime > 5000) {
      this.runOptimizations();
      this.optimizationMetrics.lastOptimizationTime = Date.now();
    }

    // Other time-based updates can be added here

    // Publish update event with current resource states
    this.publishEvent({
      type: EventType.RESOURCE_UPDATED,
      moduleId: this.id,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        resources: this.getAllResourceStates(),
        deltaTime,
      },
    });
  }

  /**
   * @inheritdoc
   */
  protected async onDispose(): Promise<void> {
    // Stop all production intervals
    for (const [id, interval] of this.productionIntervals.entries()) {
      clearInterval(interval);
    }
    this.productionIntervals.clear();

    // Save state before disposing
    this.saveState();

    // Clear all maps
    this.resources.clear();
    this.transfers = [];
    this.productions.clear();
    this.consumptions.clear();
    this.flows.clear();
    this.errors.clear();
    this.optimizationStrategies.clear();

    console.warn('[ResourceManager] Disposed');
  }

  /**
   * @inheritdoc
   */
  protected getVersion(): string {
    return '1.0.0';
  }

  /**
   * @inheritdoc
   */
  protected getStats(): Record<string, number | string> {
    return {
      resourceCount: this.resources.size,
      transferCount: this.transfers.length,
      productionCount: this.productions.size,
      consumptionCount: this.consumptions.size,
      flowCount: this.flows.size,
      productionEfficiency: this.optimizationMetrics.productionEfficiency,
      consumptionEfficiency: this.optimizationMetrics.consumptionEfficiency,
      transferEfficiency: this.optimizationMetrics.transferEfficiency,
    };
  }

  /**
   * Initializes a resource type with configured limits
   */
  private initializeResource(type: ResourceType, min: number, max: number): void {
    this.resources.set(type, {
      current: 0,
      max: max * this.storageEfficiency,
      min,
      production: DEFAULT_PRODUCTION_RATES[type],
      consumption: DEFAULT_CONSUMPTION_RATES[type],
    });
  }

  /**
   * Gets the current amount of a resource
   */
  getResourceAmount(type: ResourceType): number {
    return this.resources.get(type)?.current || 0;
  }

  /**
   * Gets the full state of a resource
   */
  getResourceState(type: ResourceType): ResourceState | undefined {
    return this.resources.get(type);
  }

  /**
   * Updates a resource amount
   */
  setResourceAmount(type: ResourceType, amount: number): void {
    const state = this.resources.get(type);
    if (!state) {
      return;
    }

    const oldAmount = state.current;
    state.current = Math.max(state.min, Math.min(amount, state.max));

    // Emit resource event
    const eventType =
      state.current > oldAmount ? EventType.RESOURCE_PRODUCED : EventType.RESOURCE_CONSUMED;

    this.publishEvent({
      type: eventType,
      moduleId: this.id,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      resourceType: type,
      amount: Math.abs(state.current - oldAmount),
      data: {
        resourceType: type,
        oldAmount,
        newAmount: state.current,
        delta: state.current - oldAmount,
      },
    } as ResourceManagerEvent);

    // Also publish to legacy moduleEventBus for backward compatibility
    moduleEventBus.emit({
      type: eventType === EventType.RESOURCE_PRODUCED ? 'RESOURCE_PRODUCED' : 'RESOURCE_CONSUMED',
      moduleId: this.id,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        resourceType: type,
        oldAmount,
        newAmount: state.current,
        delta: state.current - oldAmount,
      },
    });
  }

  /**
   * Adds to a resource amount
   */
  addResource(type: ResourceType, amount: number): void {
    const state = this.resources.get(type);
    if (!state) {
      return;
    }

    this.setResourceAmount(type, state.current + amount);
  }

  /**
   * Removes from a resource amount
   */
  removeResource(type: ResourceType, amount: number): boolean {
    const state = this.resources.get(type);
    if (!state) {
      return false;
    }

    // Check if we have enough
    if (state.current < amount) {
      // Emit shortage event
      this.publishEvent({
        type: EventType.RESOURCE_SHORTAGE,
        moduleId: this.id,
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        resourceType: type,
        amount: amount,
        data: {
          resourceType: type,
          requiredAmount: amount,
          availableAmount: state.current,
          deficit: amount - state.current,
        },
      } as ResourceManagerEvent);
      return false;
    }

    this.setResourceAmount(type, state.current - amount);
    return true;
  }

  /**
   * Updates resource production rate
   */
  setResourceProduction(type: ResourceType, amount: number): void {
    const state = this.resources.get(type);
    if (!state) {
      return;
    }

    state.production = amount;
  }

  /**
   * Updates resource consumption rate
   */
  setResourceConsumption(type: ResourceType, amount: number): void {
    const state = this.resources.get(type);
    if (!state) {
      return;
    }

    state.consumption = amount;
  }

  /**
   * Sets the storage efficiency level
   */
  setStorageEfficiency(level: keyof typeof STORAGE_EFFICIENCY): void {
    const oldEfficiency = this.storageEfficiency;
    this.storageEfficiency = STORAGE_EFFICIENCY[level];

    // Update max capacities with new efficiency
    for (const [type, limits] of Object.entries(this.config.defaultResourceLimits)) {
      const state = this.resources.get(type as ResourceType);
      if (state) {
        const oldMax = state.max;
        state.max = limits.max * this.storageEfficiency;
        console.warn(
          `[ResourceManager] Updated ${type} storage capacity: ${oldMax.toFixed(2)} -> ${state.max.toFixed(2)}`
        );
      }
    }

    this.publishEvent({
      type: EventType.RESOURCE_THRESHOLD_CHANGED,
      moduleId: this.id,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        type: 'storage_efficiency',
        oldValue: oldEfficiency,
        newValue: this.storageEfficiency,
      },
    } as ResourceManagerEvent);
  }

  /**
   * Handles and logs resource operation errors
   */
  private handleError(id: string, error: ResourceError): void {
    this.errors.set(id, error);
    console.error(`[ResourceManager] Error in ${id}:`, error.message);

    this.publishEvent({
      type: EventType.ERROR_OCCURRED,
      moduleId: id,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: error,
    } as ResourceManagerEvent);
  }

  /**
   * Validates resource transfer operation
   */
  private validateTransfer(
    type: ResourceType,
    amount: number,
    source: string,
    target: string
  ): ResourceError | null {
    if (!this.resources.has(type)) {
      return {
        code: 'INVALID_RESOURCE',
        message: `Invalid resource type: ${type}`,
      };
    }

    const sourceAmount = this.getResourceAmount(type);
    if (sourceAmount < amount) {
      return {
        code: 'INSUFFICIENT_RESOURCES',
        message: `Insufficient ${type}: required ${amount}, available ${sourceAmount}`,
        details: { required: amount, available: sourceAmount },
      };
    }

    if (source === target) {
      return {
        code: 'INVALID_TRANSFER',
        message: 'Source and target cannot be the same',
        details: { source, target },
      };
    }

    return null;
  }

  /**
   * Transfers resources between modules with error handling
   */
  transferResources(type: ResourceType, amount: number, source: string, target: string): boolean {
    const error = this.validateTransfer(type, amount, source, target);
    if (error) {
      this.handleError(`transfer-${source}-${target}`, error);
      return false;
    }

    try {
      // Apply transfer configuration limits
      amount = Math.max(
        TRANSFER_CONFIG_WITH_MIN.MIN_AMOUNT,
        Math.min(amount, TRANSFER_CONFIG_WITH_MIN.MAX_BATCH_SIZE)
      );

      // Apply transfer rate multiplier for efficiency
      const transferAmount = amount * TRANSFER_CONFIG_WITH_MIN.TRANSFER_RATE_MULTIPLIER;

      // Record transfer with configured history limit
      const transfer: ResourceTransfer = {
        type,
        amount: transferAmount,
        source,
        target,
        timestamp: Date.now(),
      };

      this.transfers.push(transfer);
      if (this.transfers.length > this.maxTransferHistory) {
        this.transfers.shift();
      }

      // Emit transfer event
      this.publishEvent({
        type: EventType.RESOURCE_TRANSFERRED,
        moduleId: source,
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        data: { transfer },
      } as ResourceManagerEvent);

      console.warn(
        `[ResourceManager] Transferred ${transferAmount.toFixed(2)} ${type} from ${source} to ${target}`
      );

      return true;
    } catch (err) {
      this.handleError(`transfer-${source}-${target}`, {
        code: 'INVALID_TRANSFER',
        message: 'Transfer operation failed',
        details: err,
      });
      return false;
    }
  }

  /**
   * Gets resource transfer history
   */
  getTransferHistory(): ResourceTransfer[] {
    return [...this.transfers];
  }

  /**
   * Gets transfer history for a specific module
   */
  getModuleTransferHistory(moduleId: string): ResourceTransfer[] {
    return this.transfers.filter(t => t.source === moduleId || t.target === moduleId);
  }

  /**
   * Gets transfer history for a specific resource type
   */
  getResourceTransferHistory(type: ResourceType): ResourceTransfer[] {
    return this.transfers.filter(t => t.type === type);
  }

  /**
   * Updates resource limits
   */
  setResourceLimits(type: ResourceType, min: number, max: number): void {
    const state = this.resources.get(type);
    if (!state) {
      return;
    }

    state.min = min;
    state.max = max;

    // Clamp current value to new limits
    this.setResourceAmount(type, state.current);
  }

  /**
   * Registers a new resource production
   */
  registerProduction(id: string, production: ResourceProduction): void {
    const oldProduction = this.productions.get(id);
    this.productions.set(id, production);

    // Emit production registration event
    this.publishEvent({
      type: EventType.RESOURCE_PRODUCTION_REGISTERED,
      moduleId: id,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        production,
        oldProduction,
      },
    } as ResourceManagerEvent);

    console.warn(
      `[ResourceManager] Registered production for ${production.type}: ${production.amount}/tick every ${production.interval}ms`
    );
  }

  /**
   * Registers a new resource consumption
   */
  registerConsumption(id: string, consumption: ResourceConsumption): void {
    const oldConsumption = this.consumptions.get(id);
    this.consumptions.set(id, consumption);

    // Emit consumption registration event
    this.publishEvent({
      type: EventType.RESOURCE_CONSUMPTION_REGISTERED,
      moduleId: id,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        consumption,
        oldConsumption,
      },
    } as ResourceManagerEvent);

    console.warn(
      `[ResourceManager] Registered consumption for ${consumption.type}: ${consumption.amount}/tick every ${consumption.interval}ms`
    );
  }

  /**
   * Registers a new resource flow between modules
   */
  registerFlow(id: string, flow: ResourceFlow): void {
    const oldFlow = this.flows.get(id);
    this.flows.set(id, flow);

    // Emit flow registration event
    this.publishEvent({
      type: EventType.RESOURCE_FLOW_REGISTERED,
      moduleId: id,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        flow,
        oldFlow,
      },
    } as ResourceManagerEvent);

    console.warn(
      `[ResourceManager] Registered flow from ${flow.source} to ${flow.target} for ${flow.resources.length} resource types`
    );
  }

  /**
   * Unregisters a production
   */
  unregisterProduction(id: string): void {
    const production = this.productions.get(id);
    if (production) {
      this.productions.delete(id);
      this.publishEvent({
        type: EventType.RESOURCE_PRODUCTION_UNREGISTERED,
        moduleId: id,
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        data: { production },
      } as ResourceManagerEvent);
    }
  }

  /**
   * Unregisters a consumption
   */
  unregisterConsumption(id: string): void {
    const consumption = this.consumptions.get(id);
    if (consumption) {
      this.consumptions.delete(id);
      this.publishEvent({
        type: EventType.RESOURCE_CONSUMPTION_UNREGISTERED,
        moduleId: id,
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        data: { consumption },
      } as ResourceManagerEvent);
    }
  }

  /**
   * Unregisters a flow
   */
  unregisterFlow(id: string): void {
    const flow = this.flows.get(id);
    if (flow) {
      this.flows.delete(id);
      this.publishEvent({
        type: EventType.RESOURCE_FLOW_UNREGISTERED,
        moduleId: id,
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        data: { flow },
      } as ResourceManagerEvent);
    }
  }

  /**
   * Initializes resource optimization strategies
   */
  private initializeOptimizationStrategies(): void {
    // Production optimization - balance production rates based on demand
    this.optimizationStrategies.set('balance-production', {
      id: 'balance-production',
      type: 'production',
      priority: 1,
      condition: () => {
        const now = Date.now();
        return now - this.optimizationMetrics.lastOptimizationTime > 60000; // Run every minute
      },
      apply: () => {
        // Convert Map entries to array to avoid MapIterator error
        const resourceEntries = Array.from(this.resources.entries());
        for (const [type, state] of resourceEntries) {
          const usage = this.calculateResourceUsage(type);
          const currentProduction = state.production;
          const targetProduction = usage * 1.2; // 20% buffer

          if (Math.abs(currentProduction - targetProduction) > 0.1) {
            const oldProduction = state.production;
            state.production = targetProduction;
            console.warn(
              `[ResourceManager] Optimized production for ${type}: ${oldProduction.toFixed(2)} -> ${targetProduction.toFixed(2)}`
            );
          }
        }
        this.optimizationMetrics.productionEfficiency = this.calculateProductionEfficiency();
      },
    });

    // Consumption optimization - reduce waste and optimize resource usage
    this.optimizationStrategies.set('optimize-consumption', {
      id: 'optimize-consumption',
      type: 'consumption',
      priority: 2,
      condition: () => {
        return Array.from(this.resources.values()).some(
          state => state.current / state.max > RESOURCE_THRESHOLDS.HIGH
        );
      },
      apply: () => {
        // Convert Map entries to array to avoid MapIterator error
        const resourceEntries = Array.from(this.resources.entries());
        for (const [type, state] of resourceEntries) {
          if (state.current / state.max > RESOURCE_THRESHOLDS.HIGH) {
            const consumers = Array.from(this.consumptions.values())
              .filter(c => c.type === type)
              .sort((_a, b) => (b.required ? 1 : -1));

            for (const consumer of consumers) {
              if (!consumer.required) {
                const oldRate = consumer.amount;
                consumer.amount *= 1.5;
                console.warn(
                  `[ResourceManager] Increased consumption of ${type} for ${consumer.type}: ${oldRate.toFixed(2)} -> ${consumer.amount.toFixed(2)}`
                );
                break;
              }
            }
          }
        }
        this.optimizationMetrics.consumptionEfficiency = this.calculateConsumptionEfficiency();
      },
    });

    // Transfer optimization - optimize resource distribution
    this.optimizationStrategies.set('optimize-transfers', {
      id: 'optimize-transfers',
      type: 'transfer',
      priority: 3,
      condition: () => this.flows.size > 0,
      apply: () => {
        // Convert Map values to array to avoid MapIterator error
        const flowValues = Array.from(this.flows.values());
        for (const flow of flowValues) {
          const sourceStates = flow.resources.map(r => ({
            resource: r,
            state: this.resources.get(r.type),
          }));

          // Check if any source is below threshold
          const belowThreshold = sourceStates.some(
            s => s.state && s.state.current / s.state.max < RESOURCE_THRESHOLDS.LOW
          );

          if (belowThreshold) {
            // Reduce flow rate
            flow.resources.forEach(r => {
              const oldRate = r.amount;
              r.amount *= 0.8;
              console.warn(
                `[ResourceManager] Reduced flow rate for ${r.type}: ${oldRate.toFixed(2)} -> ${r.amount.toFixed(2)}`
              );
            });
          }
        }
        this.optimizationMetrics.transferEfficiency = this.calculateTransferEfficiency();
      },
    });
  }

  /**
   * Calculates resource usage rate
   */
  private calculateResourceUsage(type: ResourceType): number {
    const consumers = Array.from(this.consumptions.values())
      .filter(c => c.type === type)
      .reduce((total, c) => total + c.amount, 0);

    const transfers = Array.from(this.flows.values())
      .flatMap(f => f.resources)
      .filter(r => r.type === type)
      .reduce((total, r) => total + r.amount, 0);

    return consumers + transfers;
  }

  /**
   * Calculates production efficiency
   */
  private calculateProductionEfficiency(): number {
    const efficiencies = Array.from(this.resources.entries()).map(([type, state]) => {
      const usage = this.calculateResourceUsage(type);
      const { production } = state;
      return usage > 0 ? Math.min(production / usage, 1.5) : 1.0;
    });

    return efficiencies.reduce((sum, e) => sum + e, 0) / efficiencies.length;
  }

  /**
   * Calculates consumption efficiency
   */
  private calculateConsumptionEfficiency(): number {
    const efficiencies = Array.from(this.resources.entries()).map(([type, state]) => {
      const usage = this.calculateResourceUsage(type);
      return usage > 0 ? Math.min(state.current / (usage * 10), 1.0) : 1.0;
    });

    return efficiencies.reduce((sum, e) => sum + e, 0) / efficiencies.length;
  }

  /**
   * Calculates transfer efficiency
   */
  private calculateTransferEfficiency(): number {
    if (this.transfers.length === 0) {
      return 1.0;
    }

    const recentTransfers = this.transfers.filter(t => Date.now() - t.timestamp < 60000).length; // Last minute

    const successRate = recentTransfers / Math.max(this.errors.size, 1);
    return Math.min(successRate, 1.0);
  }

  /**
   * Runs optimization strategies
   */
  private runOptimizations(): void {
    const strategies = Array.from(this.optimizationStrategies.values())
      .sort((a, b) => b.priority - a.priority)
      .filter(s => s.condition());

    for (const strategy of strategies) {
      try {
        strategy.apply();
        console.warn(`[ResourceManager] Applied optimization strategy: ${strategy.id}`);
      } catch (err) {
        console.error(
          `[ResourceManager] Failed to apply optimization strategy ${strategy.id}:`,
          err
        );
      }
    }

    this.optimizationMetrics.lastOptimizationTime = Date.now();

    // Emit optimization metrics
    this.publishEvent({
      type: EventType.RESOURCE_FLOW_OPTIMIZATION_COMPLETED,
      moduleId: this.id,
      moduleType: 'resource-manager',
      timestamp: Date.now(),
      data: {
        productionEfficiency: this.optimizationMetrics.productionEfficiency,
        consumptionEfficiency: this.optimizationMetrics.consumptionEfficiency,
        transferEfficiency: this.optimizationMetrics.transferEfficiency,
        metrics: this.optimizationMetrics,
      },
    } as ResourceManagerEvent);
  }

  /**
   * Updates resource production and consumption with configured intervals
   */
  update(deltaTime: number): void {
    // Run optimizations first
    this.runOptimizations();

    // Update performance metrics for each resource
    // Convert Map entries to array to avoid MapIterator error
    const resourceEntries = Array.from(this.resources.entries());
    for (const [type, state] of resourceEntries) {
      const usage = this.calculateResourceUsage(type);
      resourcePerformanceMonitor.recordMetrics(
        type,
        state.production,
        usage,
        this.calculateTransferRate(type),
        state.current / state.max
      );
    }

    // Handle production with configured rates
    // Convert Map entries to array to avoid MapIterator error
    const productionEntries = Array.from(this.productions.entries());
    for (const [id, production] of productionEntries) {
      if (!this.checkThresholds(production.conditions)) {
        continue;
      }

      // Calculate amount to produce based on rate and time
      const amount = (production.amount * deltaTime) / production.interval;
      this.addResource(production.type, amount);

      console.warn(`[ResourceManager] Produced ${amount.toFixed(2)} ${production.type} from ${id}`);
    }

    // Handle consumption with configured rates
    // Convert Map entries to array to avoid MapIterator error
    const consumptionEntries = Array.from(this.consumptions.entries());
    for (const [id, consumption] of consumptionEntries) {
      if (!this.checkThresholds(consumption.conditions)) {
        continue;
      }

      // Calculate amount to consume based on rate and time
      const amount = (consumption.amount * deltaTime) / consumption.interval;
      const success = this.removeResource(consumption.type, amount);

      if (success) {
        console.warn(
          `[ResourceManager] Consumed ${amount.toFixed(2)} ${consumption.type} by ${id}`
        );
      } else if (consumption.required) {
        // Log error for required consumption
        this.handleError(id, {
          code: 'INSUFFICIENT_RESOURCES',
          message: `Failed to consume required resource: ${consumption.type}`,
          details: {
            type: consumption.type,
            amount,
            consumer: id,
            priority: RESOURCE_PRIORITIES[consumption.type],
          },
        });
      }
    }

    // Handle flows with configured transfer settings
    // Convert Map entries to array to avoid MapIterator error
    const flowEntries = Array.from(this.flows.entries());
    for (const [id, flow] of flowEntries) {
      if (!this.checkThresholds(flow.conditions)) {
        console.warn(`[ResourceManager] Flow ${id} skipped due to threshold conditions`);
        continue;
      }

      // Process each resource in the flow
      flow.resources.forEach(resource => {
        // Calculate amount to transfer based on rate and time
        const amount = (resource.amount * deltaTime) / (resource.interval || 1000);
        const success = this.transferResources(resource.type, amount, flow.source, flow.target);

        if (success) {
          console.warn(
            `[ResourceManager] Transferred ${amount.toFixed(2)} ${resource.type} from ${
              flow.source
            } to ${flow.target}`
          );
        } else {
          console.warn(
            `[ResourceManager] Failed to transfer ${amount.toFixed(2)} ${resource.type} from ${
              flow.source
            } to ${flow.target}`
          );
        }
      });
    }
  }

  /**
   * Checks resource thresholds against configured values
   */
  private checkThresholds(thresholds?: ResourceThreshold[]): boolean {
    if (!thresholds) {
      return true;
    }

    return thresholds.every(threshold => {
      const state = this.resources.get(threshold.type);
      if (!state) {
        return false;
      }

      const currentRatio = state.current / state.max;

      // Use configured threshold values
      if (threshold.min !== undefined && currentRatio < RESOURCE_THRESHOLDS.LOW) {
        return false;
      }

      if (threshold.max !== undefined && currentRatio > RESOURCE_THRESHOLDS.HIGH) {
        return false;
      }

      if (threshold.target !== undefined) {
        const targetRatio = threshold.target / state.max;
        return Math.abs(currentRatio - targetRatio) < 0.1; // 10% tolerance
      }

      return true;
    });
  }

  /**
   * Schedules a production cycle with configured intervals
   */
  scheduleProduction(id: string, production: ResourceProduction): void {
    // Clear any existing interval
    this.clearProductionSchedule(id);

    // Register the production
    this.registerProduction(id, production);

    // Set up the interval
    const interval = setInterval(() => {
      if (this.checkThresholds(production.conditions)) {
        const baseRate = DEFAULT_PRODUCTION_RATES[production.type];
        const amount = baseRate * production.amount;
        this.addResource(production.type, amount);

        console.warn(
          `[ResourceManager] Scheduled production: ${amount.toFixed(2)} ${production.type} from ${id}`
        );
      }
    }, production.interval || PRODUCTION_INTERVALS.NORMAL);

    this.productionIntervals.set(id, interval);

    console.warn(
      `[ResourceManager] Scheduled production for ${id} every ${
        production.interval || PRODUCTION_INTERVALS.NORMAL
      }ms`
    );
  }

  /**
   * Clears a production schedule
   */
  clearProductionSchedule(id: string): void {
    const interval = this.productionIntervals.get(id);
    if (interval) {
      clearInterval(interval);
      this.productionIntervals.delete(id);
      this.unregisterProduction(id);

      console.warn(`[ResourceManager] Cleared production schedule for ${id}`);
    }
  }

  /**
   * Schedules a resource flow with configured intervals
   */
  scheduleFlow(id: string, flow: ResourceFlow): boolean {
    try {
      // Clear any existing flow
      this.clearFlowSchedule(id);

      // Validate flow configuration
      if (!flow.resources.length) {
        throw new Error('Flow must have at least one resource');
      }

      // Register the flow
      this.registerFlow(id, flow);

      // Set up intervals for each resource
      flow.resources.forEach(resource => {
        const interval = setInterval(() => {
          if (this.checkThresholds(flow.conditions)) {
            const { amount } = resource;
            this.transferResources(resource.type, amount, flow.source, flow.target);
          }
        }, resource.interval || TRANSFER_CONFIG_WITH_MIN.DEFAULT_INTERVAL);

        this.productionIntervals.set(`${id}-${resource.type}`, interval);

        console.warn(
          `[ResourceManager] Scheduled flow for ${resource.type} from ${flow.source} to ${
            flow.target
          } every ${resource.interval || TRANSFER_CONFIG_WITH_MIN.DEFAULT_INTERVAL}ms`
        );
      });

      return true;
    } catch (err) {
      console.error(`[ResourceManager] Failed to schedule flow for ${id}:`, err);
      return false;
    }
  }

  /**
   * Clears a flow schedule
   */
  private clearFlowSchedule(id: string): void {
    const intervals = Array.from(this.productionIntervals.entries())
      .filter(entry => entry[0].startsWith(`${id}-`))
      .map(entry => entry[1]);

    intervals.forEach(interval => {
      clearInterval(interval);
    });

    this.productionIntervals.delete(`${id}-`);
    this.unregisterFlow(id);

    console.warn(`[ResourceManager] Cleared flow schedule for ${id}`);
  }

  public getAllResources(): Record<ResourceType, number> {
    const resources: Record<ResourceType, number> = {} as Record<ResourceType, number>;

    // Convert Map entries to array to avoid MapIterator error
    const resourceEntries = Array.from(this.resources.entries());
    for (const [type, state] of resourceEntries) {
      resources[type] = state.current;
    }

    return resources;
  }

  public getAllResourceStates(): Record<ResourceType, ResourceState> {
    const states: Record<ResourceType, ResourceState> = {} as Record<ResourceType, ResourceState>;
    this.resources.forEach((state, type) => {
      states[type] = { ...state };
    });
    return states;
  }

  /**
   * Get production and consumption rates for all resources
   * @returns Record of resource rates by type
   */
  public getAllResourceRates(): Record<
    ResourceType,
    { production: number; consumption: number; net: number }
  > {
    const rates: Record<ResourceType, { production: number; consumption: number; net: number }> =
      {} as Record<ResourceType, { production: number; consumption: number; net: number }>;

    // Initialize with default rates for all resource types
    // Use string keys and then cast to ResourceType to avoid TS error
    const resourceTypes = [
      'minerals',
      'energy',
      'population',
      'research',
      'plasma',
      'gas',
      'exotic',
    ];

    // Set rates for each resource type
    resourceTypes.forEach(typeKey => {
      const type = typeKey as ResourceType;
      const state = this.getResourceState(type);
      rates[type] = {
        production: state?.production || 0,
        consumption: state?.consumption || 0,
        net: (state?.production || 0) - (state?.consumption || 0),
      };
    });

    return rates;
  }

  /**
   * Get all resource flows
   * @returns Array of all registered resource flows
   */
  public getAllResourceFlows(): ResourceFlow[] {
    const flows: ResourceFlow[] = [];

    // Convert Map values to array to avoid MapIterator error
    const flowValues = Array.from(this.flows.values());
    for (const flow of flowValues) {
      flows.push({ ...flow });
    }

    return flows;
  }

  /**
   * Saves the current resource state to localStorage and emits an event
   * This method is used for persistence and state recovery after game reload
   * It captures the complete resource system state including:
   * - Current resource amounts and limits
   * - Production configurations
   * - Consumption configurations
   * - Flow configurations
   */
  private __saveResourceState(): void {
    const resourceData: Record<ResourceType, ResourceState> = {} as Record<
      ResourceType,
      ResourceState
    >;

    // Convert Map entries to array to avoid MapIterator error
    const resourceEntries = Array.from(this.resources.entries());
    for (const [type, state] of resourceEntries) {
      resourceData[type] = { ...state };
    }

    const productionData: Record<string, ResourceProduction> = {};

    // Convert Map entries to array to avoid MapIterator error
    const productionEntries = Array.from(this.productions.entries());
    for (const [id, production] of productionEntries) {
      productionData[id] = { ...production };
    }

    const consumptionData: Record<string, ResourceConsumption> = {};

    // Convert Map entries to array to avoid MapIterator error
    const consumptionEntries = Array.from(this.consumptions.entries());
    for (const [id, consumption] of consumptionEntries) {
      consumptionData[id] = { ...consumption };
    }

    const flowData: Record<string, ResourceFlow> = {};

    // Convert Map entries to array to avoid MapIterator error
    const flowEntries = Array.from(this.flows.entries());
    for (const [id, flow] of flowEntries) {
      flowData[id] = { ...flow };
    }

    // Create a complete state object
    const completeState = {
      resources: resourceData,
      productions: productionData,
      consumptions: consumptionData,
      flows: flowData,
      storageEfficiency: this.storageEfficiency,
      timestamp: Date.now(),
    };

    try {
      // Save to localStorage
      localStorage.setItem('resourceManagerState', JSON.stringify(completeState));

      // Emit an event to notify that the state has been saved
      this.publishEvent({
        type: EventType.SYSTEM_STARTUP,
        moduleId: this.id,
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        data: {
          action: 'state_saved',
          resourceCount: this.resources.size,
          productionCount: Object.keys(productionData).length,
          consumptionCount: Object.keys(consumptionData).length,
          flowCount: Object.keys(flowData).length,
        },
      } as ResourceManagerEvent);

      console.warn(
        `[ResourceManager] State saved with ${Object.keys(resourceData).length} resources`
      );
    } catch (error) {
      // Handle potential localStorage errors
      this.handleError('save-state', {
        code: 'INVALID_TRANSFER',
        message: 'Failed to save resource state',
        details: error,
      });
      console.error('[ResourceManager] Failed to save state:', error);
    }
  }

  /**
   * Loads the resource state from localStorage
   * @returns True if state was successfully loaded, false otherwise
   */
  public loadResourceState(): boolean {
    try {
      const savedState = localStorage.getItem('resourceManagerState');
      if (!savedState) {
        return false;
      }

      const parsedState = JSON.parse(savedState);

      // Validate the state structure
      if (
        !parsedState.resources ||
        !parsedState.productions ||
        !parsedState.consumptions ||
        !parsedState.flows
      ) {
        console.warn('[ResourceManager] Invalid saved state structure');
        return false;
      }

      // Clear current state
      this.resources.clear();
      this.productions.clear();
      this.consumptions.clear();
      this.flows.clear();

      // Restore resources
      Object.entries(parsedState.resources).forEach(([type, state]) => {
        this.resources.set(type as ResourceType, state as ResourceState);
      });

      // Restore productions
      Object.entries(parsedState.productions).forEach(([id, production]) => {
        this.productions.set(id, production as ResourceProduction);
      });

      // Restore consumptions
      Object.entries(parsedState.consumptions).forEach(([id, consumption]) => {
        this.consumptions.set(id, consumption as ResourceConsumption);
      });

      // Restore flows
      Object.entries(parsedState.flows).forEach(([id, flow]) => {
        this.flows.set(id, flow as ResourceFlow);
      });

      // Restore storage efficiency
      if (typeof parsedState.storageEfficiency === 'number') {
        this.storageEfficiency = parsedState.storageEfficiency;
      }

      // Emit an event to notify that the state has been loaded
      this.publishEvent({
        type: EventType.SYSTEM_STARTUP,
        moduleId: this.id,
        moduleType: 'resource-manager',
        timestamp: Date.now(),
        data: {
          action: 'state_loaded',
          resourceCount: this.resources.size,
          productionCount: Object.keys(parsedState.productions).length,
          consumptionCount: Object.keys(parsedState.consumptions).length,
          flowCount: Object.keys(parsedState.flows).length,
        },
      } as ResourceManagerEvent);

      console.warn(
        `[ResourceManager] State loaded with ${Object.keys(parsedState.resources).length} resources`
      );
      return true;
    } catch (error) {
      this.handleError('load-state', {
        code: 'INVALID_TRANSFER',
        message: 'Failed to load resource state',
        details: error,
      });
      console.error('[ResourceManager] Failed to load state:', error);
      return false;
    }
  }

  /**
   * Saves the current resource state
   * This is a public method that triggers the private __saveResourceState method
   */
  public saveState(): void {
    this.__saveResourceState();
  }

  /**
   * Calculates transfer rate for a resource
   */
  private calculateTransferRate(type: ResourceType): number {
    const recentTransfers = this.transfers
      .filter(t => t.type === type && Date.now() - t.timestamp < 60000)
      .reduce((sum, t) => sum + t.amount, 0);

    return recentTransfers / 60; // Transfers per second
  }
}

import { EventBus } from '../../lib/events/EventBus';
import { AbstractBaseManager } from '../../lib/managers/BaseManager';
import { GameEvent } from '../../types/core/GameTypes';
import { BaseEvent, EventType } from '../../types/events/EventTypes';

/**
 * Game events specific to the game manager
 */
export enum GameManagerEventType {
  GAME_STARTED = 'GAME_STARTED',
  GAME_PAUSED = 'GAME_PAUSED',
  GAME_RESUMED = 'GAME_RESUMED',
  GAME_STOPPED = 'GAME_STOPPED',
  TIME_UPDATED = 'TIME_UPDATED',
}

/**
 * Game manager event
 */
export interface GameManagerEvent extends BaseEvent {
  type: GameManagerEventType | EventType;
  gameTime?: number;
}

/**
 * Manager responsible for controlling the game loop and time
 */
export class GameManager extends AbstractBaseManager<GameManagerEvent> {
  private isRunning: boolean = false;
  private isPaused: boolean = false;
  private gameTime: number = 0;
  private lastUpdate: number = 0;
  private frameId: number | null = null;
  private subscribers: Set<(gameTime: number) => void> = new Set();
  private eventListeners: Map<string, Set<(event: GameEvent) => void>> = new Map();

  constructor(eventBus: EventBus<GameManagerEvent>) {
    super('GameManager', eventBus);
  }

  /**
   * @inheritdoc
   */
  protected async onInitialize(): Promise<void> {
    // Initialize game state
    this.isRunning = false;
    this.isPaused = false;
    this.gameTime = 0;
    this.lastUpdate = 0;

    // Register event handlers if needed
    console.log('GameManager initialized');
  }

  /**
   * @inheritdoc
   */
  protected onUpdate(deltaTime: number): void {
    if (this.isRunning && !this.isPaused) {
      // Directly update game time with the provided delta
      this.gameTime += deltaTime;

      // Notify subscribers
      this.subscribers.forEach(callback => {
        callback(this.gameTime);
      });

      // Publish time updated event
      this.publishEvent({
        type: GameManagerEventType.TIME_UPDATED,
        gameTime: this.gameTime,
      });
    }
  }

  /**
   * @inheritdoc
   */
  protected async onDispose(): Promise<void> {
    this.stop();
    this.subscribers.clear();
  }

  /**
   * @inheritdoc
   */
  protected getVersion(): string {
    return '1.0.0';
  }

  /**
   * @inheritdoc
   */
  protected getStats(): Record<string, number | string> {
    return {
      gameTime: this.gameTime,
      isRunning: this.isRunning ? 1 : 0,
      isPaused: this.isPaused ? 1 : 0,
      subscriberCount: this.subscribers.size,
    };
  }

  /**
   * Start the game loop
   */
  start(): void {
    if (!this.isRunning) {
      this.isRunning = true;
      this.lastUpdate = performance.now();
      this.frameId = requestAnimationFrame(this.update.bind(this));

      this.publishEvent({
        type: GameManagerEventType.GAME_STARTED,
        gameTime: this.gameTime,
      });
    }
  }

  /**
   * Pause the game
   */
  pause(): void {
    if (!this.isPaused && this.isRunning) {
      this.isPaused = true;

      this.publishEvent({
        type: GameManagerEventType.GAME_PAUSED,
        gameTime: this.gameTime,
      });
    }
  }

  /**
   * Resume the game
   */
  resume(): void {
    if (this.isPaused && this.isRunning) {
      this.isPaused = false;
      this.lastUpdate = performance.now();

      this.publishEvent({
        type: GameManagerEventType.GAME_RESUMED,
        gameTime: this.gameTime,
      });
    }
  }

  /**
   * Stop the game loop
   */
  stop(): void {
    if (this.isRunning) {
      this.isRunning = false;
      this.isPaused = false;

      if (this.frameId !== null) {
        cancelAnimationFrame(this.frameId);
        this.frameId = null;
      }

      this.publishEvent({
        type: GameManagerEventType.GAME_STOPPED,
        gameTime: this.gameTime,
      });
    }
  }

  /**
   * Subscribe to game updates
   * @param callback Function to call on each update
   * @returns Function to unsubscribe
   */
  subscribe(callback: (gameTime: number) => void): () => void {
    this.subscribers.add(callback);
    return () => {
      this.subscribers.delete(callback);
    };
  }

  /**
   * Add event listener
   */
  addEventListener(type: string, callback: (event: GameEvent) => void) {
    if (!this.eventListeners.has(type)) {
      this.eventListeners.set(type, new Set());
    }
    this.eventListeners.get(type)?.add(callback);
    return () => {
      this.eventListeners.get(type)?.delete(callback);
    };
  }

  /**
   * Dispatch game event
   */
  dispatchEvent(event: GameEvent) {
    this.eventListeners.get(event.type)?.forEach(callback => {
      callback(event);
    });
  }

  /**
   * Main update loop
   */
  private update(timestamp: number): void {
    if (!this.isRunning) return;

    if (!this.isPaused) {
      const deltaTime = timestamp - this.lastUpdate;
      this.gameTime += deltaTime;

      // Notify subscribers
      this.subscribers.forEach(callback => {
        callback(this.gameTime);
      });

      // Publish time updated event
      this.publishEvent({
        type: GameManagerEventType.TIME_UPDATED,
        gameTime: this.gameTime,
      });
    }

    this.lastUpdate = timestamp;
    this.frameId = requestAnimationFrame(this.update.bind(this));
  }

  /**
   * Get current game time
   */
  getGameTime(): number {
    return this.gameTime;
  }

  /**
   * Check if game is running
   */
  isGameRunning(): boolean {
    return this.isRunning;
  }

  /**
   * Check if game is paused
   */
  isGamePaused(): boolean {
    return this.isPaused;
  }
}

