/**
 * @file ModuleManagerWrapper.ts
 * Wrapper for ModuleManager that implements IModuleManager interface.
 * This provides type-safe access to the ModuleManager functionality
 * while ensuring compatibility with the new type system.
 */

import { EventBus } from '../../lib/events/EventBus';
import { BaseModule, ModularBuilding, ModuleType } from '../../types/buildings/ModuleTypes';
import { BaseEvent } from '../../types/events/EventTypes';
import {
  IModuleManager,
  LegacyModuleAction,
  Module,
  ModuleStatus,
} from '../../types/modules/ModuleTypes';
import { ModuleManager, moduleManager } from './ModuleManager';

/**
 * Convert a BaseModule to the Module interface
 * This function safely maps properties from the legacy BaseModule type
 * to the new Module interface.
 *
 * @param baseModule The legacy module object
 * @returns A properly typed Module object
 */
export function convertToModule(baseModule: BaseModule | undefined): Module | undefined {
  if (!baseModule) return undefined;

  // Extract optional properties with type safety
  // Some properties exist in the runtime but not in the type definition
  const buildingId = (baseModule as any).buildingId;
  const attachmentPointId = (baseModule as any).attachmentPointId;

  // Convert status from string to ModuleStatus enum if needed
  let status: ModuleStatus | 'active' | 'constructing' | 'inactive' = baseModule.status;

  return {
    id: baseModule.id,
    name: baseModule.name,
    type: baseModule.type,
    status,
    buildingId: buildingId, // May be undefined
    attachmentPointId: attachmentPointId, // May be undefined
    position: baseModule.position,
    isActive: baseModule.isActive || false,
    level: baseModule.level || 1,
    progress: baseModule.progress,
    subModules: baseModule.subModules as Array<unknown>,
    parentModuleId: baseModule.parentModuleId,
  };
}

/**
 * Convert an array of BaseModule objects to Module interface
 *
 * @param baseModules Array of legacy module objects
 * @returns Array of properly typed Module objects
 */
export function convertToModules(baseModules: BaseModule[]): Module[] {
  return baseModules.map(baseModule => convertToModule(baseModule)!).filter(Boolean);
}

/**
 * Wrapper for the ModuleManager that implements the IModuleManager interface.
 * This provides a bridge between the existing ModuleManager implementation
 * and the new type-safe IModuleManager interface used by ModuleContext.
 */
export class ModuleManagerWrapper implements IModuleManager {
  private manager: ModuleManager;

  constructor(manager: ModuleManager = moduleManager) {
    this.manager = manager;
  }

  // Implement IModuleManager methods

  /**
   * Get all modules
   */
  getModules(): Module[] {
    // Convert internal modules to Module type
    const internalModules = this.manager.getActiveModules() || [];
    return convertToModules(internalModules);
  }

  /**
   * Get module by ID
   */
  getModule(id: string): Module | undefined {
    const module = this.manager.getModule?.(id);
    return convertToModule(module);
  }

  /**
   * Get modules by type
   */
  getModulesByType(type: ModuleType): Module[] {
    const modules = this.manager.getModulesByType(type) || [];
    return convertToModules(modules);
  }

  /**
   * Get active modules
   */
  getActiveModules(): Module[] {
    const modules = this.manager.getActiveModules() || [];
    return convertToModules(modules);
  }

  /**
   * Get all buildings
   */
  getBuildings(): ModularBuilding[] {
    // Implementation depends on ModuleManager's methods
    // This is a compatibility layer
    return this.manager.getBuildings?.() || [];
  }

  /**
   * Get module categories
   */
  getModuleCategories(): string[] {
    // Since getModuleCategories doesn't exist on ModuleManager, provide default categories
    return ['production', 'utility', 'research', 'defense'];
  }

  /**
   * Get modules by building ID
   */
  getModulesByBuildingId(buildingId: string): Module[] {
    // Implementation depends on ModuleManager's methods
    // As a fallback, filter all modules by buildingId
    const modules = this.getModules();
    return modules.filter(module => module.buildingId === buildingId);
  }

  /**
   * Activate a module
   */
  activateModule(moduleId: string): void {
    if (this.manager.setModuleActive) {
      this.manager.setModuleActive(moduleId, true);
    }
  }

  /**
   * Deactivate a module
   */
  deactivateModule(moduleId: string): void {
    if (this.manager.setModuleActive) {
      this.manager.setModuleActive(moduleId, false);
    }
  }

  /**
   * Access to the event bus
   */
  get eventBus(): EventBus<BaseEvent> {
    return this.manager.eventBus;
  }

  /**
   * Dispatch legacy actions
   * Uses type guards instead of type assertions to safely handle different manager implementations
   */
  dispatch(action: LegacyModuleAction | { type: string }): void {
    // Check if manager has the needed dispatch methods using type guard
    if (this.hasDispatchAction(this.manager)) {
      this.manager.dispatchAction(action);
    } else if (this.hasDispatch(this.manager)) {
      this.manager.dispatch(action);
    } else {
      console.warn('ModuleManager does not support dispatch method:', action);
    }
  }

  /**
   * Type guard to check if manager has dispatchAction method
   */
  private hasDispatchAction(
    manager: any
  ): manager is { dispatchAction: (action: unknown) => void } {
    return manager && typeof manager.dispatchAction === 'function';
  }

  /**
   * Type guard to check if manager has dispatch method
   */
  private hasDispatch(manager: any): manager is { dispatch: (action: unknown) => void } {
    return manager && typeof manager.dispatch === 'function';
  }
}

// Export singleton instance
export const moduleManagerWrapper = new ModuleManagerWrapper(moduleManager);

/**
 * AdaptivePerformanceManager
 *
 * Manages adaptive performance optimization across the application.
 * Integrates ML-based resource consumption prediction with dynamic throttling
 * and resource allocation to optimize performance based on device capabilities
 * and usage patterns.
 */

import {
  ConsumptionPrediction,
  ResourceConsumptionPredictor,
} from '../../lib/ai/ResourceConsumptionPredictor';
import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import { ModuleEvent, ModuleType } from '../../types/events/ModuleEventTypes';
import { ResourceType } from '../../types/resources/ResourceTypes';
import { GameLoopManager } from '../game/GameLoopManager';
import {
  PerformanceMetrics,
  ResourcePerformanceMonitor,
  ResourcePerformanceSnapshot,
} from './ResourcePerformanceMonitor';

export interface DeviceProfile {
  deviceType: 'high-end' | 'mid-range' | 'low-end';
  cpuScore: number;
  memoryScore: number;
  batteryState?: 'charging' | 'discharging';
  batteryLevel?: number;
  networkType?: 'wifi' | 'cellular' | 'offline';
  networkLatency?: number;
}

export interface OptimizationSuggestion {
  type: 'throttling' | 'resource-allocation' | 'batch-processing' | 'rendering' | 'cache-strategy';
  target: string;
  priority: 'critical' | 'high' | 'medium' | 'low';
  description: string;
  potentialSavings: number;
  implementationDifficulty: number; // 1-10 scale
}

/**
 * Manager for adaptive performance optimization
 */
export class AdaptivePerformanceManager {
  private resourcePredictor: ResourceConsumptionPredictor;
  private performanceMonitor: ResourcePerformanceMonitor;
  private gameLoopManager: GameLoopManager | null = null;

  private deviceProfile: DeviceProfile;
  private optimizationSuggestions: OptimizationSuggestion[] = [];
  private adaptiveThrottlingEnabled = true;
  private powerSavingMode = false;

  private userInteractionCount = 0;
  private sessionStartTime: number;
  private lastOptimizationTime: number;

  private optimizationInterval: number | null = null;
  private predictionSubscription: (() => void) | null = null;

  constructor(performanceMonitor: ResourcePerformanceMonitor) {
    this.resourcePredictor = new ResourceConsumptionPredictor();
    this.performanceMonitor = performanceMonitor;
    this.sessionStartTime = Date.now();
    this.lastOptimizationTime = Date.now();

    // Initialize with default device profile
    this.deviceProfile = this.detectDeviceProfile();

    // Initialize predictor with existing metrics if available
    this.initializePredictor();

    // Subscribe to performance snapshots
    this.subscribeToPerformanceEvents();

    // Start optimization cycle
    this.startOptimizationCycle();

    console.log(
      '[AdaptivePerformanceManager] Initialized with device profile:',
      this.deviceProfile.deviceType
    );
  }

  /**
   * Connect to the GameLoopManager for timing-based optimizations
   */
  public connectGameLoopManager(gameLoopManager: GameLoopManager): void {
    this.gameLoopManager = gameLoopManager;
    console.log('[AdaptivePerformanceManager] Connected to GameLoopManager');
  }

  /**
   * Initialize the predictor with historical data
   */
  private initializePredictor(): void {
    // In a real implementation, we would load historical data from storage
    // For now, just initialize the predictor
    this.resourcePredictor.initialize();
  }

  /**
   * Subscribe to performance events
   */
  private subscribeToPerformanceEvents(): void {
    this.predictionSubscription = moduleEventBus.subscribe({
      topic: 'STATUS_CHANGED',
      callback: (event: ModuleEvent) => {
        if (
          event.moduleId === 'resource-performance-monitor' &&
          event.data.type === 'performance_snapshot'
        ) {
          this.processPerformanceSnapshot(event.data.snapshot);
        }
      },
    });
  }

  /**
   * Process a performance snapshot and update the ML model
   */
  private processPerformanceSnapshot(snapshot: ResourcePerformanceSnapshot): void {
    // Update user interaction count (would come from a real event system)
    this.userInteractionCount += Math.round(Math.random() * 3); // Placeholder

    // Process each resource metric
    for (const [resourceType, metrics] of snapshot.metrics.entries()) {
      // Convert to a data point for the predictor
      this.resourcePredictor.addDataPoint({
        timestamp: Date.now(),
        resourceType,
        value: metrics.consumptionRate,
        sessionDuration: (Date.now() - this.sessionStartTime) / 1000, // in seconds
        userActions: this.userInteractionCount,
        systemLoad: snapshot.systemLoad,
        devicePerformanceScore: this.deviceProfile.cpuScore,
      });

      // Update potential savings
      this.updatePotentialSavings(resourceType, metrics);
    }

    // Generate optimization suggestions based on bottlenecks
    if (snapshot.bottlenecks.length > 0) {
      this.generateOptimizationSuggestions(snapshot);
    }

    // Apply adaptive throttling if enabled
    if (this.adaptiveThrottlingEnabled) {
      this.applyAdaptiveThrottling(snapshot);
    }
  }

  /**
   * Update potential savings for a resource
   */
  private updatePotentialSavings(resourceType: ResourceType, metrics: PerformanceMetrics): void {
    const savings = this.resourcePredictor.calculatePotentialSavings(
      resourceType,
      metrics.consumptionRate
    );

    if (savings > 0) {
      console.log(
        `[AdaptivePerformanceManager] Potential ${resourceType} savings: ${savings.toFixed(2)} units`
      );
    }
  }

  /**
   * Generate optimization suggestions based on performance data
   */
  private generateOptimizationSuggestions(snapshot: ResourcePerformanceSnapshot): void {
    const suggestions: OptimizationSuggestion[] = [];

    // Process bottlenecks
    for (const resourceType of snapshot.bottlenecks) {
      const metrics = snapshot.metrics.get(resourceType);
      if (!metrics) continue;

      // Get prediction for this resource type
      const prediction = this.resourcePredictor.predict(resourceType, {
        sessionDuration: (Date.now() - this.sessionStartTime) / 1000,
        userActions: this.userInteractionCount,
        systemLoad: snapshot.systemLoad,
        devicePerformanceScore: this.deviceProfile.cpuScore,
      });

      if (!prediction) continue;

      // Generate different types of suggestions based on resource and prediction
      if (metrics.consumptionRate > prediction.predictedValue * 1.2) {
        // Consumption is significantly higher than predicted
        suggestions.push({
          type: 'throttling',
          target: resourceType,
          priority: 'high',
          description: `Reduce ${resourceType} consumption rate by ${Math.round(((metrics.consumptionRate - prediction.predictedValue) / metrics.consumptionRate) * 100)}%`,
          potentialSavings: metrics.consumptionRate - prediction.predictedValue,
          implementationDifficulty: 4,
        });
      }

      if (metrics.utilizationRate > 0.9) {
        // Resource is heavily utilized
        suggestions.push({
          type: 'batch-processing',
          target: resourceType,
          priority: 'medium',
          description: `Implement batch processing for ${resourceType} operations to reduce overhead`,
          potentialSavings: metrics.consumptionRate * 0.15, // Estimated 15% improvement
          implementationDifficulty: 6,
        });
      }

      if (resourceType === 'energy' && this.deviceProfile.batteryState === 'discharging') {
        // Energy optimization for battery-powered devices
        suggestions.push({
          type: 'resource-allocation',
          target: 'energy',
          priority: 'critical',
          description: 'Activate power-saving mode to extend battery life',
          potentialSavings: metrics.consumptionRate * 0.3, // Estimated 30% improvement
          implementationDifficulty: 3,
        });
      }
    }

    // Add rendering suggestions for high system load
    if (snapshot.systemLoad > 0.8) {
      suggestions.push({
        type: 'rendering',
        target: 'ui',
        priority: 'high',
        description: 'Reduce rendering quality or frame rate to decrease system load',
        potentialSavings: snapshot.systemLoad * 0.2, // Estimated 20% improvement
        implementationDifficulty: 5,
      });
    }

    // Update optimization suggestions
    this.optimizationSuggestions = suggestions;

    // Log suggestions
    if (suggestions.length > 0) {
      console.log('[AdaptivePerformanceManager] Optimization suggestions:');
      suggestions.forEach(suggestion => {
        console.log(
          `- ${suggestion.priority.toUpperCase()}: ${suggestion.description} (Savings: ${suggestion.potentialSavings.toFixed(2)})`
        );
      });
    }

    // Emit optimization suggestions event
    this.emitOptimizationSuggestions(suggestions);
  }

  /**
   * Apply adaptive throttling based on device capabilities and system load
   */
  private applyAdaptiveThrottling(snapshot: ResourcePerformanceSnapshot): void {
    if (!this.gameLoopManager) return;

    // Calculate throttling factor based on system load and device profile
    let throttleFactor = 1.0;

    // Adjust based on system load
    if (snapshot.systemLoad > 0.9) {
      throttleFactor = 0.7; // Significant throttling for very high load
    } else if (snapshot.systemLoad > 0.7) {
      throttleFactor = 0.85; // Moderate throttling for high load
    }

    // Adjust based on device type
    if (this.deviceProfile.deviceType === 'low-end') {
      throttleFactor *= 0.8; // Further throttling for low-end devices
    } else if (this.deviceProfile.deviceType === 'mid-range') {
      throttleFactor *= 0.9; // Some throttling for mid-range devices
    }

    // Adjust based on battery state
    if (
      this.deviceProfile.batteryState === 'discharging' &&
      this.deviceProfile.batteryLevel &&
      this.deviceProfile.batteryLevel < 0.2
    ) {
      throttleFactor *= 0.7; // Aggressive throttling for low battery
    }

    // Apply power saving mode if enabled
    if (this.powerSavingMode) {
      throttleFactor *= 0.6; // Very aggressive throttling in power saving mode
    }

    // Apply throttling to game loop (adjust update frequency)
    if (typeof this.gameLoopManager.adjustUpdateFrequency === 'function') {
      this.gameLoopManager.adjustUpdateFrequency(throttleFactor);
    }

    console.log(
      `[AdaptivePerformanceManager] Applied throttling factor: ${throttleFactor.toFixed(2)}`
    );
  }

  /**
   * Emit optimization suggestions event
   */
  private emitOptimizationSuggestions(suggestions: OptimizationSuggestion[]): void {
    moduleEventBus.emit({
      type: 'STATUS_CHANGED',
      moduleId: 'adaptive-performance-manager',
      moduleType: 'resource-manager' as ModuleType,
      timestamp: Date.now(),
      data: {
        type: 'optimization_suggestions',
        suggestions,
      },
    });
  }

  /**
   * Start the optimization cycle
   */
  private startOptimizationCycle(): void {
    // Run optimization every 30 seconds
    this.optimizationInterval = window.setInterval(() => {
      this.runOptimizationCycle();
    }, 30000);
  }

  /**
   * Run a full optimization cycle
   */
  private runOptimizationCycle(): void {
    // Update device profile
    this.deviceProfile = this.detectDeviceProfile();

    // Update power saving mode based on device state
    this.updatePowerSavingMode();

    // Apply the most valuable optimization suggestions
    this.applyHighPriorityOptimizations();

    this.lastOptimizationTime = Date.now();
  }

  /**
   * Update power saving mode based on device state
   */
  private updatePowerSavingMode(): void {
    // Enable power saving mode for low battery or low-end devices
    const lowBattery =
      this.deviceProfile.batteryState === 'discharging' &&
      this.deviceProfile.batteryLevel !== undefined &&
      this.deviceProfile.batteryLevel < 0.15;

    const shouldEnablePowerSaving = lowBattery || this.deviceProfile.deviceType === 'low-end';

    if (shouldEnablePowerSaving !== this.powerSavingMode) {
      this.powerSavingMode = shouldEnablePowerSaving;
      console.log(
        `[AdaptivePerformanceManager] Power saving mode ${this.powerSavingMode ? 'enabled' : 'disabled'}`
      );

      // Emit power saving mode change event
      moduleEventBus.emit({
        type: 'STATUS_CHANGED',
        moduleId: 'adaptive-performance-manager',
        moduleType: 'resource-manager' as ModuleType,
        timestamp: Date.now(),
        data: {
          type: 'power_saving_mode',
          enabled: this.powerSavingMode,
        },
      });
    }
  }

  /**
   * Apply high priority optimizations
   */
  private applyHighPriorityOptimizations(): void {
    const highPrioritySuggestions = this.optimizationSuggestions.filter(
      s => s.priority === 'critical' || s.priority === 'high'
    );

    if (highPrioritySuggestions.length === 0) return;

    // Sort by potential savings divided by implementation difficulty
    const sortedSuggestions = [...highPrioritySuggestions].sort((a, b) => {
      const valueA = a.potentialSavings / a.implementationDifficulty;
      const valueB = b.potentialSavings / b.implementationDifficulty;
      return valueB - valueA; // Higher value first
    });

    // Apply top suggestions (in a real implementation, this would connect to various systems)
    for (const suggestion of sortedSuggestions.slice(0, 2)) {
      // Apply top 2 suggestions
      console.log(`[AdaptivePerformanceManager] Applying optimization: ${suggestion.description}`);

      // In a real implementation, we would apply the optimization
      // For now, just log it
    }
  }

  /**
   * Detect device capabilities and create a device profile
   */
  private detectDeviceProfile(): DeviceProfile {
    // In a production implementation, this would use actual device detection
    // For now, we'll use simple heuristics

    const userAgent = navigator.userAgent;
    const memory = (navigator as any).deviceMemory || 4; // Cast to any for browser API
    const connection = (navigator as any).connection || { type: 'unknown', rtt: 50 }; // Cast to any for browser API

    // Detect device type based on memory and user agent
    let deviceType: 'high-end' | 'mid-range' | 'low-end';
    let cpuScore = 1.0;

    if (memory >= 8 || /powerful|gaming/i.test(userAgent)) {
      deviceType = 'high-end';
      cpuScore = 1.0;
    } else if (memory >= 4) {
      deviceType = 'mid-range';
      cpuScore = 0.7;
    } else {
      deviceType = 'low-end';
      cpuScore = 0.4;
    }

    // Get battery info if available
    let batteryState: 'charging' | 'discharging' | undefined;
    let batteryLevel: number | undefined;

    // Try to get battery info (this is an async API, but we're simplifying for now)
    if (typeof (navigator as any).getBattery === 'function') {
      (navigator as any).getBattery().then((battery: { charging: boolean; level: number }) => {
        batteryState = battery.charging ? 'charging' : 'discharging';
        batteryLevel = battery.level;
      });
    }

    // Create device profile
    return {
      deviceType,
      cpuScore,
      memoryScore: memory / 8, // Normalize to 0-1 range assuming 8GB is high-end
      batteryState,
      batteryLevel,
      networkType: connection.type === 'cellular' ? 'cellular' : 'wifi',
      networkLatency: connection.rtt || 50,
    };
  }

  /**
   * Get current optimization suggestions
   */
  public getOptimizationSuggestions(): OptimizationSuggestion[] {
    return [...this.optimizationSuggestions];
  }

  /**
   * Get resource consumption predictions
   */
  public getResourcePredictions(): Map<ResourceType, ConsumptionPrediction> {
    return this.resourcePredictor.getAllPredictions();
  }

  /**
   * Enable or disable adaptive throttling
   */
  public setAdaptiveThrottling(enabled: boolean): void {
    this.adaptiveThrottlingEnabled = enabled;
    console.log(
      `[AdaptivePerformanceManager] Adaptive throttling ${enabled ? 'enabled' : 'disabled'}`
    );
  }

  /**
   * Manually enable or disable power saving mode
   */
  public setPowerSavingMode(enabled: boolean): void {
    this.powerSavingMode = enabled;
    console.log(
      `[AdaptivePerformanceManager] Power saving mode ${enabled ? 'enabled' : 'disabled'}`
    );

    // Emit power saving mode change event
    moduleEventBus.emit({
      type: 'STATUS_CHANGED',
      moduleId: 'adaptive-performance-manager',
      moduleType: 'resource-manager' as ModuleType,
      timestamp: Date.now(),
      data: {
        type: 'power_saving_mode',
        enabled,
      },
    });
  }

  /**
   * Clean up resources
   */
  public cleanup(): void {
    // Clear intervals
    if (this.optimizationInterval !== null) {
      clearInterval(this.optimizationInterval);
    }

    // Unsubscribe from events
    if (this.predictionSubscription) {
      this.predictionSubscription();
    }

    // Clean up predictor
    this.resourcePredictor.cleanup();

    console.log('[AdaptivePerformanceManager] Cleaned up resources');
  }
}

import { moduleEventBus } from '../../lib/modules/ModuleEvents';
import { ModuleType } from '../../types/buildings/ModuleTypes';
import {
  ChainExecutionStatus,
  ConversionChain,
  ConverterNodeConfig,
  ConverterProcessStatus,
  FlowConnection,
  FlowNode,
  ResourceConversionProcess,
  ResourceConversionRecipe,
  ResourceFlow,
  ResourceState,
  ResourceStateClass,
  ResourceTransfer,
  ResourceType,
} from '../../types/resources/StandardizedResourceTypes';
import { validateResourceTransfer } from '../../utils/resources/resourceValidation';
// Import new utility classes
import { SpatialIndex, SpatialObject } from '../../utils/spatial/SpatialPartitioning';
import {
  FlowOptimizationResult,
  ResourceFlowWorkerUtil,
} from '../../utils/workers/ResourceFlowWorkerUtil';
import { AbstractBaseManager } from '../../lib/managers/BaseManager';
import { BaseEvent } from '../../lib/events/UnifiedEventSystem';

// Re-export FlowNode interface for use in other components
export type { FlowNode } from '../../types/resources/StandardizedResourceTypes';

/**
 * Extended FlowNode with spatial coordinates for geographical networks
 */
interface GeoFlowNode extends FlowNode, SpatialObject {
  // Spatial coordinates
  x: number;
  y: number;
}

/**
 * Resource flow events
 */
export interface ResourceFlowEvent extends BaseEvent {
  type: 
    | 'RESOURCE_FLOW_INITIALIZED'
    | 'RESOURCE_FLOW_OPTIMIZED'
    | 'RESOURCE_NODE_REGISTERED'
    | 'RESOURCE_NODE_UPDATED'
    | 'RESOURCE_NODE_UNREGISTERED'
    | 'RESOURCE_CONNECTION_REGISTERED'
    | 'RESOURCE_CONNECTION_UPDATED'
    | 'RESOURCE_CONNECTION_UNREGISTERED'
    | 'RESOURCE_CONVERSION_STARTED'
    | 'RESOURCE_CONVERSION_COMPLETED'
    | 'RESOURCE_CONVERSION_FAILED'
    | 'RESOURCE_TRANSFER_COMPLETED';
  nodeId?: string;
  connectionId?: string;
  resourceType?: ResourceType;
  processId?: string;
  data?: any;
}

/**
 * Manager for resource flow through the game systems
 * Responsible for:
 * - Tracking resource nodes (producers, consumers, storage, converters)
 * - Managing connections between nodes
 * - Optimizing resource distribution
 * - Processing resource conversions
 */
export class ResourceFlowManager extends AbstractBaseManager<ResourceFlowEvent> {
  // Flow network data structures
  private nodes: Map<string, FlowNode> = new Map();
  private connections: Map<string, FlowConnection> = new Map();
  private sourceConnections: Map<string, string[]> = new Map();
  private targetConnections: Map<string, string[]> = new Map();

  // Type-specific node registries
  private producerNodes: Map<string, FlowNode> = new Map();
  private consumerNodes: Map<string, FlowNode> = new Map();
  private storageNodes: Map<string, FlowNode> = new Map();
  private converterNodes: Map<string, FlowNode> = new Map();

  // Resource tracking
  private resourceStates: Map<ResourceType, ResourceState> = new Map();
  private resourceProducers: Map<ResourceType, string[]> = new Map();
  private resourceConsumers: Map<ResourceType, string[]> = new Map();
  private resourceStorage: Map<ResourceType, string[]> = new Map();

  // Conversion recipes and chains
  private conversionRecipes: Map<string, ResourceConversionRecipe> = new Map();
  private conversionChains: Map<string, ConversionChain> = new Map();
  private chainExecutions: Map<string, ChainExecutionStatus> = new Map();

  // Processing state
  private processingQueue: ResourceConversionProcess[] = [];
  private completedProcesses: ResourceConversionProcess[] = [];
  private lastProcessingTime = 0;
  private processingInterval: number | null = null;
  private optimizationInterval: number | null = null;

  // Transfer history
  private transferHistory: ResourceTransfer[] = [];
  private maxHistorySize = 1000;

  // Optimization settings
  private flowOptimizationEnabled = true;
  private optimizationIntervalMs = 5000;
  private processingIntervalMs = 1000;
  private resourceCapacityBuffer = 0.1; // 10% buffer for resource capacity

  // Resource cache for performance optimization
  private resourceCache: Map<
    ResourceType,
    {
      state: ResourceState;
      lastUpdated: number;
      expiresAt: number;
    }
  > = new Map();
  private cacheTTL = 2000; // Cache time-to-live in milliseconds

  // New optimization properties
  private workerUtil: ResourceFlowWorkerUtil | null = null;
  private spatialIndex: SpatialIndex<GeoFlowNode> | null = null;
  private useWorkerOffloading = false;
  private useSpatialPartitioning = false;
  private batchSize = 50;
  private worldBounds = { minX: 0, minY: 0, maxX: 10000, maxY: 10000 };
  private isOptimizing = false;
  private lastOptimizationResult: FlowOptimizationResult | null = null;

  private static instance: ResourceFlowManager;

  /**
   * Get the singleton instance of ResourceFlowManager
   * @returns The singleton instance
   */
  public static getInstance(): ResourceFlowManager {
    if (!ResourceFlowManager.instance) {
      ResourceFlowManager.instance = new ResourceFlowManager();
    }
    return ResourceFlowManager.instance;
  }

  /**
   * Create a new ResourceFlowManager
   *
   * @param optimizationIntervalMs - Interval for flow optimization in milliseconds
   * @param cacheTTL - Time-to-live for cached resource states in milliseconds
   * @param batchSize - Batch size for processing large networks
   * @param useWorkerOffloading - Whether to use Web Worker offloading for large networks
   * @param useSpatialPartitioning - Whether to use spatial partitioning for geographical networks
   */
  protected constructor(
    optimizationIntervalMs = 5000,
    cacheTTL = 2000,
    batchSize = 50,
    useWorkerOffloading = true,
    useSpatialPartitioning = true
  ) {
    super('ResourceFlowManager');

    this.optimizationIntervalMs = optimizationIntervalMs;
    this.cacheTTL = cacheTTL;
    this.batchSize = batchSize;
    this.useWorkerOffloading = useWorkerOffloading;
    this.useSpatialPartitioning = useSpatialPartitioning;

    // Other initializations will be done in onInitialize
  }

  /**
   * Implementation of abstract method from AbstractBaseManager
   * Initialize the manager
   */
  protected async onInitialize(dependencies?: Record<string, unknown>): Promise<void> {
    // Initialize resource states
    this.initializeResourceStates();

    // Initialize Web Worker utility if enabled
    if (this.useWorkerOffloading) {
      try {
        this.workerUtil = new ResourceFlowWorkerUtil();
      } catch (error) {
        this.handleError(error instanceof Error ? error : new Error(String(error)), {
          context: 'initializeWorker',
        });
        this.useWorkerOffloading = false;
      }
    }

    // Initialize spatial index if enabled
    if (this.useSpatialPartitioning) {
      this.spatialIndex = new SpatialIndex<GeoFlowNode>(this.worldBounds);
    }

    // Subscribe to module events that might affect resource flow
    this.subscribeToModuleEvents();

    // Start optimization and processing intervals
    this.startAsyncOptimizationInterval();
    this.startProcessingInterval(this.processingIntervalMs);

    // Publish initialization event
    this.publish({
      type: 'RESOURCE_FLOW_INITIALIZED',
      timestamp: Date.now(),
      data: {
        optimizationIntervalMs: this.optimizationIntervalMs,
        cacheTTL: this.cacheTTL,
        batchSize: this.batchSize,
        useWorkerOffloading: this.useWorkerOffloading,
        useSpatialPartitioning: this.useSpatialPartitioning,
      },
    });
  }

  /**
   * Implementation of abstract method from AbstractBaseManager
   * Update the manager state
   */
  protected onUpdate(deltaTime: number): void {
    // Process any pending conversions
    this.processConversions();

    // Update any active chains
    for (const [chainId, chainStatus] of this.chainExecutions.entries()) {
      if (chainStatus.active && !chainStatus.paused) {
        this.updateChainProgress(chainId);
      }
    }

    // Update metrics
    this.updateMetric('nodesCount', this.nodes.size);
    this.updateMetric('connectionsCount', this.connections.size);
    this.updateMetric('activeProcessesCount', this.processingQueue.length);
    this.updateMetric('deltaTime', deltaTime);
  }

  /**
   * Implementation of abstract method from AbstractBaseManager
   * Dispose of the manager's resources
   */
  protected async onDispose(): Promise<void> {
    // Clean up intervals
    if (this.processingInterval !== null) {
      clearInterval(this.processingInterval);
      this.processingInterval = null;
    }

    if (this.optimizationInterval !== null) {
      clearInterval(this.optimizationInterval);
      this.optimizationInterval = null;
    }

    // Cleanup Web Worker
    if (this.workerUtil) {
      this.workerUtil.terminate();
      this.workerUtil = null;
    }

    // Clear spatial index
    if (this.spatialIndex) {
      this.spatialIndex.clear();
    }

    // Unsubscribe from module events
    this.unsubscribeFromModuleEvents();

    // Clear data structures
    this.nodes.clear();
    this.connections.clear();
    this.sourceConnections.clear();
    this.targetConnections.clear();

    this.producerNodes.clear();
    this.consumerNodes.clear();
    this.storageNodes.clear();
    this.converterNodes.clear();

    this.resourceCache.clear();
    this.transferHistory = [];
    this.processingQueue = [];
    this.completedProcesses = [];
    this.conversionRecipes.clear();
    this.conversionChains.clear();
    this.chainExecutions.clear();
  }

  /**
   * Subscribe to module events that might affect resource flow
   */
  private subscribeToModuleEvents(): void {
    // Use the subscribe method from AbstractBaseManager to keep track of subscriptions
    this.subscribe('MODULE_CREATED', this.handleModuleCreated);
    this.subscribe('MODULE_UPDATED', this.handleModuleUpdated);
    this.subscribe('MODULE_DESTROYED', this.handleModuleDestroyed);
    this.subscribe('MODULE_ENABLED', this.handleModuleStateChanged);
    this.subscribe('MODULE_DISABLED', this.handleModuleStateChanged);
  }

