import { Singleton } from '../lib/patterns/Singleton';
import { eventSystem } from '../lib/events/UnifiedEventSystem';
import { ResourceState, ResourceTransfer, ResourceType } from '../types/resources/ResourceTypes';
import { ResourceStorageSubsystem } from './subsystems/ResourceStorageSubsystem';
import { ResourceFlowSubsystem } from './subsystems/ResourceFlowSubsystem';
import { ResourceTransferSubsystem } from './subsystems/ResourceTransferSubsystem';
import { ResourceThresholdSubsystem } from './subsystems/ResourceThresholdSubsystem';

/**
 * Configuration options for the resource system
 */
export interface ResourceSystemConfig {
  /** Time to live for resource cache in milliseconds */
  cacheTTL: number;
  /** Interval for optimization in milliseconds */
  optimizationInterval: number;
  /** Batch size for processing large networks */
  batchSize: number;
  /** Whether to use Web Worker offloading for large networks */
  useWorkerOffloading: boolean;
  /** Whether to use spatial partitioning for geographical networks */
  useSpatialPartitioning: boolean;
  /** Storage allocation strategy */
  defaultAllocationStrategy: 'balanced' | 'prioritized' | 'dedicated';
  /** How to handle storage overflow */
  overflowPolicy: 'reject' | 'redistribute' | 'convert' | 'discard';
  /** Whether to automatically rebalance storage */
  autoRebalance: boolean;
  /** Maximum history size */
  maxHistorySize: number;
}

/**
 * Default configuration for the resource system
 */
const DEFAULT_CONFIG: ResourceSystemConfig = {
  cacheTTL: 2000,
  optimizationInterval: 5000,
  batchSize: 50,
  useWorkerOffloading: true,
  useSpatialPartitioning: true,
  defaultAllocationStrategy: 'balanced',
  overflowPolicy: 'redistribute',
  autoRebalance: true,
  maxHistorySize: 100
};

/**
 * Unified Resource Management System
 * 
 * This system consolidates multiple resource-related managers into a cohesive architecture
 * with specialized subsystems for different aspects of resource management:
 * 
 * - Storage: Manages resource storage containers and allocation
 * - Flow: Optimizes resource flow through the network
 * - Transfer: Handles resource transfers between entities
 * - Threshold: Monitors resource levels and triggers actions when thresholds are reached
 */
export class ResourceSystem extends Singleton<ResourceSystem> {
  // Configuration
  private config: ResourceSystemConfig;

  // Subsystems
  private storage: ResourceStorageSubsystem;
  private flow: ResourceFlowSubsystem;
  private transfer: ResourceTransferSubsystem;
  private threshold: ResourceThresholdSubsystem;

  // Resource state cache
  private resourceCache: Map<
    ResourceType,
    {
      state: ResourceState;
      lastUpdated: number;
      expiresAt: number;
    }
  > = new Map();

  // Processing state
  private optimizationInterval: NodeJS.Timeout | null = null;
  private isInitialized = false;

  protected constructor(config?: Partial<ResourceSystemConfig>) {
    super();
    // Merge provided config with defaults
    this.config = {
      ...DEFAULT_CONFIG,
      ...config
    };

    // Initialize subsystems
    this.storage = new ResourceStorageSubsystem(this, this.config);
    this.flow = new ResourceFlowSubsystem(this, this.config);
    this.transfer = new ResourceTransferSubsystem(this, this.config);
    this.threshold = new ResourceThresholdSubsystem(this, this.config);

    // Subscribe to subsystem events
    this.initializeEventSubscriptions();
  }

  /**
   * Initialize event subscriptions between subsystems
   */
  private initializeEventSubscriptions(): void {
    // Listen for threshold events
    eventSystem.subscribe('RESOURCE_THRESHOLD_REACHED', (event) => {
      console.log('Resource threshold reached:', event);
      // Additional handling as needed
    });

    // Listen for storage overflow events
    eventSystem.subscribe('RESOURCE_STORAGE_OVERFLOW', (event) => {
      console.log('Resource storage overflow:', event);
      // Handle overflow according to policy
      this.handleStorageOverflow(event.resourceType, event.amount, event.containerId);
    });

    // Listen for resource state changes
    eventSystem.subscribe('RESOURCE_STATE_CHANGED', (event) => {
      // Invalidate cache for the resource type
      this.resourceCache.delete(event.resourceType);
    });
  }

  /**
   * Initialize the resource system
   */
  public async initialize(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      // Initialize all subsystems
      await this.storage.initialize();
      await this.flow.initialize();
      await this.transfer.initialize();
      await this.threshold.initialize();

      // Start optimization interval
      this.startOptimizationInterval();

      this.isInitialized = true;
    } catch (error) {
      console.error('Failed to initialize ResourceSystem:', error);
      throw error;
    }
  }

  /**
   * Dispose of the resource system
   */
  public async dispose(): Promise<void> {
    if (!this.isInitialized) {
      return;
    }

    try {
      // Stop optimization interval
      if (this.optimizationInterval) {
        clearInterval(this.optimizationInterval);
        this.optimizationInterval = null;
      }

      // Dispose all subsystems in reverse order
      await this.threshold.dispose();
      await this.transfer.dispose();
      await this.flow.dispose();
      await this.storage.dispose();

      // Clear cache
      this.resourceCache.clear();

      this.isInitialized = false;
    } catch (error) {
      console.error('Failed to dispose ResourceSystem:', error);
      throw error;
    }
  }

  /**
   * Start the optimization interval
   */
  private startOptimizationInterval(): void {
    if (this.optimizationInterval) {
      clearInterval(this.optimizationInterval);
    }

    this.optimizationInterval = setInterval(() => {
      this.flow.optimizeFlows().catch(error => {
        console.error('Error in flow optimization:', error);
      });
    }, this.config.optimizationInterval);
  }

  /**
   * Handle storage overflow according to policy
   */
  private handleStorageOverflow(type: ResourceType, amount: number, sourceId: string): void {
    switch (this.config.overflowPolicy) {
      case 'redistribute':
        this.storage.redistributeOverflow(type, amount, sourceId);
        break;
      case 'convert':
        // Implementation will depend on conversion rules
        break;
      case 'discard':
        // Simply ignore the overflow
        break;
      default:
        // 'reject' is the default - no action needed
        break;
    }
  }

  /**
   * Get a resource state
   */
  public getResourceState(type: ResourceType): ResourceState | undefined {
    // Check cache first
    const now = Date.now();
    const cachedEntry = this.resourceCache.get(type);

    if (cachedEntry && now < cachedEntry.expiresAt) {
      return cachedEntry.state;
    }

    // Cache miss or expired, get from flow subsystem
    const state = this.flow.getResourceState(type);

    // Update cache if state exists
    if (state) {
      this.resourceCache.set(type, {
        state: { ...state }, // Clone to prevent reference issues
        lastUpdated: now,
        expiresAt: now + this.config.cacheTTL,
      });
    }

    return state;
  }

  /**
   * Update a resource state
   */
  public updateResourceState(type: ResourceType, state: ResourceState): void {
    // Update in flow subsystem
    this.flow.updateResourceState(type, state);

    // Invalidate cache
    this.invalidateCache(type);

    // Check thresholds
    this.threshold.checkThresholds(type, state);
  }

  /**
   * Invalidate cache for a resource type
   */
  private invalidateCache(type: ResourceType): void {
    this.resourceCache.delete(type);
  }

  /**
   * Get the current resource total
   */
  public getResourceTotal(type: ResourceType): number {
    const state = this.getResourceState(type);
    return state?.current || 0;
  }

  /**
   * Get the storage subsystem
   */
  public getStorageSubsystem(): ResourceStorageSubsystem {
    return this.storage;
  }

  /**
   * Get the flow subsystem
   */
  public getFlowSubsystem(): ResourceFlowSubsystem {
    return this.flow;
  }

  /**
   * Get the transfer subsystem
   */
  public getTransferSubsystem(): ResourceTransferSubsystem {
    return this.transfer;
  }

  /**
   * Get the threshold subsystem
   */
  public getThresholdSubsystem(): ResourceThresholdSubsystem {
    return this.threshold;
  }

  /**
   * Check if a resource exists
   */
  public hasResource(type: ResourceType): boolean {
    return this.getResourceTotal(type) > 0;
  }

  /**
   * Check if a resource has at least the specified amount
   */
  public hasResourceAmount(type: ResourceType, amount: number): boolean {
    return this.getResourceTotal(type) >= amount;
  }

  /**
   * Get available resource space
   */
  public getAvailableSpace(type: ResourceType): number {
    const state = this.getResourceState(type);
    if (!state) {
      return 0;
    }
    return Math.max(0, state.max - state.current);
  }

  /**
   * Store a resource
   */
  public storeResource(type: ResourceType, amount: number, targetId?: string): number {
    if (targetId) {
      return this.storage.storeResource(targetId, type, amount);
    } else {
      return this.storage.storeResourceOptimal(type, amount);
    }
  }

  /**
   * Retrieve a resource
   */
  public retrieveResource(type: ResourceType, amount: number, sourceId?: string): number {
    if (sourceId) {
      return this.storage.retrieveResource(sourceId, type, amount);
    } else {
      return this.storage.retrieveResourceOptimal(type, amount);
    }
  }

  /**
   * Transfer resources between entities
   */
  public transferResource(
    type: ResourceType,
    amount: number,
    sourceId: string,
    targetId: string
  ): number {
    return this.transfer.transferResource(type, amount, sourceId, targetId);
  }

  /**
   * Register a resource flow
   */
  public registerResourceFlow(
    sourceId: string,
    targetId: string,
    type: ResourceType,
    rate: number
  ): boolean {
    return this.flow.registerResourceFlow(sourceId, targetId, type, rate);
  }

  /**
   * Convert resources from one type to another
   */
  public convertResources(
    inputType: ResourceType,
    inputAmount: number,
    outputType: ResourceType,
    outputAmount: number,
    sourceId: string
  ): boolean {
    // Retrieve the input resources
    const retrieved = this.retrieveResource(inputType, inputAmount, sourceId);
    if (retrieved < inputAmount) {
      // Not enough resources, return what was retrieved
      if (retrieved > 0) {
        this.storeResource(inputType, retrieved, sourceId);
      }
      return false;
    }

    // Store the output resources
    this.storeResource(outputType, outputAmount, sourceId);
    return true;
  }

  /**
   * Get all recent resource transfers
   */
  public getTransferHistory(): ResourceTransfer[] {
    return this.transfer.getTransferHistory();
  }

  /**
   * Get resource transfers for a specific type
   */
  public getTransfersByType(type: ResourceType): ResourceTransfer[] {
    return this.transfer.getTransfersByType(type);
  }
}

// Export singleton instance
export const resourceSystem = ResourceSystem.getInstance();

// Export default for easier imports
export default resourceSystem;

import { eventSystem } from '../../lib/events/UnifiedEventSystem';
import { ResourceState, ResourceTransfer, ResourceType } from '../../types/resources/ResourceTypes';
import { validateResourceTransfer } from '../../utils/resources/resourceValidation';
import { ResourceFlowWorkerUtil } from '../../utils/workers/ResourceFlowWorkerUtil';
import { ResourceSystem, ResourceSystemConfig } from '../ResourceSystem';

/**
 * Flow node types
 */
export type FlowNodeType = 'producer' | 'consumer' | 'storage' | 'converter';

/**
 * Flow priority configuration
 */
export interface FlowPriority {
  type: ResourceType;
  priority: number;
  consumers: string[];
}

/**
 * Flow node
 */
export interface FlowNode {
  id: string;
  type: FlowNodeType;
  resources: ResourceType[];
  priority: FlowPriority;
  active: boolean;
  efficiency?: number;
  converterConfig?: any; // We'll define a more specific type when needed
  converterStatus?: any; // We'll define a more specific type when needed
  config?: any; // Generic config for additional properties
}

/**
 * Flow connection
 */
export interface FlowConnection {
  id: string;
  source: string;
  target: string;
  resourceType: ResourceType;
  maxRate: number;
  currentRate: number;
  priority: FlowPriority;
  active: boolean;
}

/**
 * Resource flow
 */
export interface ResourceFlow {
  source: string;
  target: string;
  resourceType: ResourceType;
  maxRate: number;
}

/**
 * Flow optimization result
 */
export interface FlowOptimizationResult {
  transfers: ResourceTransfer[];
  updatedConnections: FlowConnection[];
  bottlenecks: string[];
  underutilized: string[];
  performanceMetrics?: {
    executionTimeMs: number;
    nodesProcessed: number;
    connectionsProcessed: number;
    transfersGenerated: number;
  };
}

/**
 * ResourceFlowSubsystem
 * 
 * Manages resource flow through the game systems
 * Responsible for:
 * - Tracking resource nodes (producers, consumers, storage, converters)
 * - Managing connections between nodes
 * - Optimizing resource distribution
 * - Processing resource conversions
 */
export class ResourceFlowSubsystem {
  // Flow network data structures
  private nodes: Map<string, FlowNode> = new Map();
  private connections: Map<string, FlowConnection> = new Map();
  private sourceConnections: Map<string, string[]> = new Map();
  private targetConnections: Map<string, string[]> = new Map();

  // Type-specific node registries
  private producerNodes: Map<string, FlowNode> = new Map();
  private consumerNodes: Map<string, FlowNode> = new Map();
  private storageNodes: Map<string, FlowNode> = new Map();
  private converterNodes: Map<string, FlowNode> = new Map();

  // Resource tracking
  private resourceStates: Map<ResourceType, ResourceState> = new Map();
  private resourceProducers: Map<ResourceType, string[]> = new Map();
  private resourceConsumers: Map<ResourceType, string[]> = new Map();
  private resourceStorage: Map<ResourceType, string[]> = new Map();

  // Processing state
  private transferHistory: ResourceTransfer[] = [];
  private lastOptimizationTime = 0;
  private isOptimizing = false;
  private lastOptimizationResult: FlowOptimizationResult | null = null;

  // Worker utility
  private workerUtil: ResourceFlowWorkerUtil | null = null;

  // Parent system reference
  private parentSystem: ResourceSystem;
  private config: ResourceSystemConfig;
  private isInitialized = false;

  constructor(parentSystem: ResourceSystem, config: ResourceSystemConfig) {
    this.parentSystem = parentSystem;
    this.config = config;

    // Initialize resource states
    this.initializeResourceStates();

    // Initialize Web Worker utility if enabled
    if (this.config.useWorkerOffloading) {
      try {
        this.workerUtil = new ResourceFlowWorkerUtil();
      } catch (error) {
        console.error('Failed to initialize ResourceFlowWorkerUtil:', error);
      }
    }
  }

  /**
   * Initialize the subsystem
   */
  public async initialize(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      // Subscribe to relevant events
      this.initializeEventSubscriptions();

      this.isInitialized = true;
    } catch (error) {
      console.error('Failed to initialize ResourceFlowSubsystem:', error);
      throw error;
    }
  }

  /**
   * Dispose of the subsystem
   */
  public async dispose(): Promise<void> {
    if (!this.isInitialized) {
      return;
    }

    try {
      // Clean up Web Worker
      if (this.workerUtil) {
        this.workerUtil.terminate();
        this.workerUtil = null;
      }

      // Clear data structures
      this.nodes.clear();
      this.connections.clear();
      this.sourceConnections.clear();
      this.targetConnections.clear();

      this.producerNodes.clear();
      this.consumerNodes.clear();
      this.storageNodes.clear();
      this.converterNodes.clear();

      this.resourceStates.clear();
      this.resourceProducers.clear();
      this.resourceConsumers.clear();
      this.resourceStorage.clear();

      this.transferHistory = [];

      this.isInitialized = false;
    } catch (error) {
      console.error('Failed to dispose ResourceFlowSubsystem:', error);
      throw error;
    }
  }

  /**
   * Initialize event subscriptions
   */
  private initializeEventSubscriptions(): void {
    // Subscribe to relevant events
    eventSystem.subscribe('MODULE_CREATED', this.handleModuleCreated);
    eventSystem.subscribe('MODULE_UPDATED', this.handleModuleUpdated);
    eventSystem.subscribe('MODULE_DESTROYED', this.handleModuleDestroyed);
    eventSystem.subscribe('MODULE_ENABLED', this.handleModuleStateChanged);
    eventSystem.subscribe('MODULE_DISABLED', this.handleModuleStateChanged);
  }

  /**
   * Initialize with default states for all resource types
   */
  private initializeResourceStates(): void {
    const defaultResourceTypes: ResourceType[] = [
      'energy',
      'minerals',
      'population',
      'research',
      'plasma',
      'gas',
      'exotic'
    ];

    // Initialize resource states for all resource types
    for (const type of defaultResourceTypes) {
      const resourceState: ResourceState = {
        current: 0,
        max: 1000,
        min: 0,
        production: 0,
        consumption: 0
      };

      this.resourceStates.set(type, resourceState);
      this.resourceProducers.set(type, []);
      this.resourceConsumers.set(type, []);
      this.resourceStorage.set(type, []);
    }
  }

  /**
   * Registers a node in the resource flow network
   */
  public registerNode(node: FlowNode): boolean {
    if (!node.id || !node.resources || node.resources.length === 0) {
      console.warn('Invalid flow node:', node);
      return false;
    }

    // Add to main nodes map
    this.nodes.set(node.id, node);

    // Add to type-specific map
    switch (node.type) {
      case 'producer':
        this.producerNodes.set(node.id, node);
        break;
      case 'consumer':
        this.consumerNodes.set(node.id, node);
        break;
      case 'storage':
        this.storageNodes.set(node.id, node);
        break;
      case 'converter':
        this.converterNodes.set(node.id, node);
        break;
    }

    // Add to resource-specific tracking
    for (const resourceType of node.resources) {
      // Invalidate parent system cache
      this.invalidateCache(resourceType);

      // Add to the appropriate resource tracking maps
      switch (node.type) {
        case 'producer':
          this.addToArray(this.resourceProducers, resourceType, node.id);
          break;
        case 'consumer':
          this.addToArray(this.resourceConsumers, resourceType, node.id);
          break;
        case 'storage':
          this.addToArray(this.resourceStorage, resourceType, node.id);
          break;
      }
    }

    return true;
  }

  /**
   * Unregisters a node from the resource flow network
   */
  public unregisterNode(id: string): boolean {
    if (!this.nodes.has(id)) {
      return false;
    }

    // Get node before removing it
    const node = this.nodes.get(id);
    if (!node) return false;

    // Remove from type-specific map
    switch (node.type) {
      case 'producer':
        this.producerNodes.delete(id);
        break;
      case 'consumer':
        this.consumerNodes.delete(id);
        break;
      case 'storage':
        this.storageNodes.delete(id);
        break;
      case 'converter':
        this.converterNodes.delete(id);
        break;
    }

    // Remove from resource-specific tracking
    for (const resourceType of node.resources) {
      // Invalidate parent system cache
      this.invalidateCache(resourceType);

      // Remove from the appropriate resource tracking maps
      switch (node.type) {
        case 'producer':
          this.removeFromArray(this.resourceProducers, resourceType, id);
          break;
        case 'consumer':
          this.removeFromArray(this.resourceConsumers, resourceType, id);
          break;
        case 'storage':
          this.removeFromArray(this.resourceStorage, resourceType, id);
          break;
      }
    }

    // Remove all connections to/from this node
    const connectionEntries = Array.from(this.connections.entries());
    for (const [connectionId, connection] of connectionEntries) {
      if (connection.source === id || connection.target === id) {
        this.unregisterConnection(connectionId);
      }
    }

    // Remove from main nodes map
    this.nodes.delete(id);

    return true;
  }

  /**
   * Registers a connection between nodes in the resource flow network
   */
  public registerConnection(connection: FlowConnection): boolean {
    if (
      !connection.id ||
      !connection.source ||
      !connection.target ||
      !connection.resourceType ||
      connection.maxRate <= 0
    ) {
      console.warn('Invalid connection:', connection);
      return false;
    }

    // Ensure source and target nodes exist
    if (!this.nodes.has(connection.source)) {
      console.warn(`Source node ${connection.source} does not exist`);
      return false;
    }

    if (!this.nodes.has(connection.target)) {
      console.warn(`Target node ${connection.target} does not exist`);
      return false;
    }

    // Ensure source node has the resource type
    const sourceNode = this.nodes.get(connection.source);
    if (!sourceNode?.resources.includes(connection.resourceType)) {
      console.warn(
        `Source node ${connection.source} does not have resource type ${connection.resourceType}`
      );
      return false;
    }

    // Add to connections map
    this.connections.set(connection.id, connection);

    // Update source and target connection maps
    this.addToArray(this.sourceConnections, connection.source, connection.id);
    this.addToArray(this.targetConnections, connection.target, connection.id);

    // Invalidate cache for affected resource
    this.invalidateCache(connection.resourceType);

    return true;
  }

  /**
   * Unregisters a connection from the resource flow network
   */
  public unregisterConnection(id: string): boolean {
    const connection = this.connections.get(id);
    if (!connection) {
      return false;
    }

    // Remove from source and target connection maps
    this.removeFromArray(this.sourceConnections, connection.source, id);
    this.removeFromArray(this.targetConnections, connection.target, id);

    // Remove from connections map
    this.connections.delete(id);

    // Invalidate cache for affected resource
    this.invalidateCache(connection.resourceType);

    return true;
  }

  /**
   * Creates a resource flow between two nodes
   */
  public registerResourceFlow(
    sourceId: string,
    targetId: string,
    resourceType: ResourceType,
    rate: number
  ): boolean {
    // Create a unique connection ID
    const connectionId = `${sourceId}-to-${targetId}-${resourceType}`;

    // Create the connection
    const connection: FlowConnection = {
      id: connectionId,
      source: sourceId,
      target: targetId,
      resourceType,
      maxRate: rate,
      currentRate: 0,
      priority: { type: resourceType, priority: 1, consumers: [] },
      active: true
    };

    // Register the connection
    return this.registerConnection(connection);
  }

  /**
   * Updates the state of a resource in the network
   */
  public updateResourceState(type: ResourceType, state: ResourceState): void {
    this.resourceStates.set(type, state);

    // Invalidate cache
    this.invalidateCache(type);
  }

  /**
   * Gets the current state of a resource in the network
   */
  public getResourceState(type: ResourceType): ResourceState | undefined {
    return this.resourceStates.get(type);
  }

  /**
   * Invalidate cache for a resource type
   */
  private invalidateCache(type: ResourceType): void {
    // Just notify that cache should be invalidated
    // This avoids circular dependency with parent system
    eventSystem.publish({
      type: 'RESOURCE_STATE_CHANGED',
      resourceType: type,
      timestamp: Date.now(),
    });
  }

  /**
   * Optimize resource flows across the network
   */
  public async optimizeFlows(): Promise<FlowOptimizationResult> {
    // Prevent concurrent optimization runs
    if (this.isOptimizing) {
      return (
        this.lastOptimizationResult || {
          transfers: [],
          updatedConnections: [],
          bottlenecks: [],
          underutilized: [],
          performanceMetrics: {
            executionTimeMs: 0,
            nodesProcessed: 0,
            connectionsProcessed: 0,
            transfersGenerated: 0,
          },
        }
      );
    }

    this.isOptimizing = true;
    const startTime = Date.now();

    try {
      // Get active nodes and connections
      const activeNodes = Array.from(this.nodes.values()).filter(node => node.active);
      const activeConnections = Array.from(this.connections.values()).filter(conn => conn.active);

      // Check if we should use Web Worker offloading
      if (this.config.useWorkerOffloading && this.workerUtil && activeNodes.length > this.config.batchSize) {
        try {
          // Offload optimization to Web Worker
          const result = await this.workerUtil.optimizeFlows(
            activeNodes,
            activeConnections,
            Object.fromEntries(this.resourceStates)
          );

          // Apply the results from the worker
          this.applyOptimizationResults(result);

          // Add execution time to performance metrics
          result.performanceMetrics = result.performanceMetrics || {
            executionTimeMs: 0,
            nodesProcessed: activeNodes.length,
            connectionsProcessed: activeConnections.length,
            transfersGenerated: result.transfers.length,
          };

          result.performanceMetrics.executionTimeMs = Date.now() - startTime;

          this.lastOptimizationResult = result;
          this.lastOptimizationTime = Date.now();
          return result;
        } catch (error) {
          console.warn('Web Worker optimization failed, falling back to main thread:', error);
          // Fall back to main thread optimization
        }
      }

      // Categorize nodes by type
      const producers = activeNodes.filter(node => node.type === 'producer');
      const consumers = activeNodes.filter(node => node.type === 'consumer');
      const storages = activeNodes.filter(node => node.type === 'storage');
      const converters = activeNodes.filter(node => node.type === 'converter');

      // Process converters
      this.processConverters(converters, activeConnections);

      // Calculate resource balance
      const { availability, demand } = this.calculateResourceBalance(
        producers,
        consumers,
        storages,
        activeConnections
      );

      // Identify resource issues
      const { bottlenecks, underutilized } = this.identifyResourceIssues(availability, demand);

      // Optimize flow rates
      const { updatedConnections, transfers } = this.optimizeFlowRates(
        activeConnections,
        availability,
        demand
      );

      // Update connections with optimized rates
      for (const connection of updatedConnections) {
        this.connections.set(connection.id, connection);
      }

      // Create and store result
      const result: FlowOptimizationResult = {
        transfers,
        updatedConnections,
        bottlenecks,
        underutilized,
        performanceMetrics: {
          executionTimeMs: Date.now() - startTime,
          nodesProcessed: activeNodes.length,
          connectionsProcessed: activeConnections.length,
          transfersGenerated: transfers.length,
        },
      };

      this.lastOptimizationResult = result;
      this.lastOptimizationTime = Date.now();
      return result;
    } finally {
      this.isOptimizing = false;
    }
  }

  /**
   * Apply optimization results
   */
  private applyOptimizationResults(result: FlowOptimizationResult): void {
    // Update connections with optimized rates
    for (const connection of result.updatedConnections) {
      if (this.connections.has(connection.id)) {
        this.connections.set(connection.id, connection);
      }
    }

    // Process transfers
    for (const transfer of result.transfers) {
      if (validateResourceTransfer(transfer)) {
        this.addToTransferHistory(transfer);
      }
    }
  }

  /**
   * Process converters
   */
  private processConverters(converters: FlowNode[], activeConnections: FlowConnection[]): void {
    // Process each converter
    for (const converter of converters) {
      if (converter.config?.type === 'advanced') {
        this.processAdvancedConverter(converter, activeConnections);
      } else {
        // Basic converter processing
      }
    }
  }

  /**
   * Process an advanced converter
   */
  private processAdvancedConverter(converter: FlowNode, activeConnections: FlowConnection[]): void {
    // This would implement advanced converter logic
    // For now, we'll leave it as a placeholder
  }

  /**
   * Calculate resource balance between producers, consumers, and storage
   */
  private calculateResourceBalance(
    producers: FlowNode[],
    consumers: FlowNode[],
    storages: FlowNode[],
    activeConnections: FlowConnection[]
  ): {
    availability: Partial<Record<ResourceType, number>>;
    demand: Partial<Record<ResourceType, number>>;
  } {
    const availability: Partial<Record<ResourceType, number>> = {};
    const demand: Partial<Record<ResourceType, number>> = {};

    // Initialize with zero values for all resource types
    for (const type of this.resourceStates.keys()) {
      availability[type] = 0;
      demand[type] = 0;
    }

    // Calculate production capacity
    for (const producer of producers) {
      for (const resourceType of producer.resources) {
        // Find outgoing connections for this resource
        const outgoingConnections = activeConnections.filter(
          conn => conn.source === producer.id && conn.resourceType === resourceType
        );

        // Sum up max rates
        const totalMaxRate = outgoingConnections.reduce((sum, conn) => sum + conn.maxRate, 0);

        // Apply efficiency if available
        const effectiveRate = producer.efficiency
          ? totalMaxRate * producer.efficiency
          : totalMaxRate;

        availability[resourceType] = (availability[resourceType] || 0) + effectiveRate;
      }
    }

    // Calculate consumer demand
    for (const consumer of consumers) {
      for (const resourceType of consumer.resources) {
        // Find incoming connections for this resource
        const incomingConnections = activeConnections.filter(
          conn => conn.target === consumer.id && conn.resourceType === resourceType
        );

        // Sum up max rates
        const totalMaxRate = incomingConnections.reduce((sum, conn) => sum + conn.maxRate, 0);

        demand[resourceType] = (demand[resourceType] || 0) + totalMaxRate;
      }
    }

    // Factor in storage capacity
    for (const storage of storages) {
      for (const resourceType of storage.resources) {
        // Use resource state if available
        const resourceState = this.resourceStates.get(resourceType);
        if (!resourceState) {
          continue;
        }

        // If storage is near capacity, reduce availability
        if (resourceState.current > resourceState.max * 0.9) {
          availability[resourceType] = Math.max(
            0,
            (availability[resourceType] || 0) - (resourceState.max - resourceState.current)
          );
        }

        // If storage is near empty, increase demand
        if (resourceState.current < resourceState.max * 0.1) {
          demand[resourceType] = (demand[resourceType] || 0) + resourceState.max * 0.2;
        }
      }
    }

    return { availability, demand };
  }

  /**
   * Identify resource bottlenecks and underutilized resources
   */
  private identifyResourceIssues(
    availability: Partial<Record<ResourceType, number>>,
    demand: Partial<Record<ResourceType, number>>
  ): {
    bottlenecks: string[];
    underutilized: string[];
  } {
    const bottlenecks: string[] = [];
    const underutilized: string[] = [];

    for (const [type, availableAmount] of Object.entries(availability)) {
      const demandAmount = demand[type as ResourceType] || 0;

      if (availableAmount < demandAmount * 0.9) {
        bottlenecks.push(type);
      } else if (availableAmount > demandAmount * 1.5) {
        underutilized.push(type);
      }
    }

    return { bottlenecks, underutilized };
  }

  /**
   * Optimize flow rates based on priorities
   */
  private optimizeFlowRates(
    activeConnections: FlowConnection[],
    availability: Partial<Record<ResourceType, number>>,
    demand: Partial<Record<ResourceType, number>>
  ): {
    updatedConnections: FlowConnection[];
    transfers: ResourceTransfer[];
  } {
    const updatedConnections: FlowConnection[] = [];
    const transfers: ResourceTransfer[] = [];
    const now = Date.now();

    // Sort connections by priority (high to low)
    const prioritizedConnections = [...activeConnections].sort(
      (a, b) => b.priority.priority - a.priority.priority
    );

    // Adjust flow rates
    for (const connection of prioritizedConnections) {
      const { resourceType } = connection;
      const availableForType = availability[resourceType] || 0;
      const demandForType = demand[resourceType] || 0;

      if (availableForType <= 0 || demandForType <= 0) {
        // No flow possible
        connection.currentRate = 0;
      } else if (availableForType >= demandForType) {
        // Full flow possible
        connection.currentRate = Math.min(connection.maxRate, demandForType);
      } else {
        // Partial flow based on ratio
        const ratio = availableForType / demandForType;
        connection.currentRate = connection.maxRate * ratio;
      }

      updatedConnections.push({ ...connection });

      // Generate transfer if flow is positive
      if (connection.currentRate > 0) {
        const transfer: ResourceTransfer = {
          type: connection.resourceType,
          source: connection.source,
          target: connection.target,
          amount: connection.currentRate,
          timestamp: now,
        };

        if (validateResourceTransfer(transfer)) {
          transfers.push(transfer);
          this.addToTransferHistory(transfer);
        }
      }
    }

    return { updatedConnections, transfers };
  }

  /**
   * Add a transfer to the history
   */
  private addToTransferHistory(transfer: ResourceTransfer): void {
    this.transferHistory.push(transfer);

    // Trim history if needed
    if (this.transferHistory.length > this.config.maxHistorySize) {
      this.transferHistory = this.transferHistory.slice(-this.config.maxHistorySize);
    }
  }

  /**
   * Get the transfer history
   */
  public getTransferHistory(): ResourceTransfer[] {
    return [...this.transferHistory];
  }

  /**
   * Get a specific node
   */
  public getNode(id: string): FlowNode | undefined {
    return this.nodes.get(id);
  }

  /**
   * Get all nodes
   */
  public getNodes(): FlowNode[] {
    return Array.from(this.nodes.values());
  }

  /**
   * Get a specific connection
   */
  public getConnection(id: string): FlowConnection | undefined {
    return this.connections.get(id);
  }

  /**
   * Get all connections
   */
  public getConnections(): FlowConnection[] {
    return Array.from(this.connections.values());
  }

  /**
   * Add an item to an array in a map
   */
  private addToArray<K, V>(map: Map<K, V[]>, key: K, value: V): void {
    const array = map.get(key) || [];
    if (!array.includes(value)) {
      array.push(value);
      map.set(key, array);
    }
  }

  /**
   * Remove an item from an array in a map
   */
  private removeFromArray<K, V>(map: Map<K, V[]>, key: K, value: V): void {
    const array = map.get(key);
    if (array) {
      const index = array.indexOf(value);
      if (index >= 0) {
        array.splice(index, 1);
        map.set(key, array);
      }
    }
  }

  // Module event handlers
  private handleModuleCreated = (event: any): void => {
    const { moduleId, moduleType, resources } = event;

    // Determine node type based on module type
    let nodeType: FlowNodeType = 'consumer';
    if (moduleType === 'producer' || moduleType === 'mining') {
      nodeType = 'producer';
    } else if (moduleType === 'storage') {
      nodeType = 'storage';
    } else if (moduleType === 'converter') {
      nodeType = 'converter';
    }

    // Create and register node
    const node: FlowNode = {
      id: moduleId,
      type: nodeType,
      resources: resources || [],
      priority: { type: resources?.[0] || 'energy', priority: 1, consumers: [] },
      active: true
    };

    this.registerNode(node);
  };

  private handleModuleUpdated = (event: any): void => {
    const { moduleId, changes } = event;

    // Get existing node
    const node = this.nodes.get(moduleId);
    if (!node) return;

    // Apply changes
    if (changes.resources) {
      node.resources = changes.resources;
    }

    if (changes.active !== undefined) {
      node.active = changes.active;
    }

    if (changes.efficiency !== undefined) {
      node.efficiency = changes.efficiency;
    }

    // Update node
    this.nodes.set(moduleId, node);

    // Invalidate cache for affected resources
    for (const resource of node.resources) {
      this.invalidateCache(resource);
    }
  };

  private handleModuleDestroyed = (event: any): void => {
    const { moduleId } = event;
    this.unregisterNode(moduleId);
  };

  private handleModuleStateChanged = (event: any): void => {
    const { moduleId, active } = event;

    // Get existing node
    const node = this.nodes.get(moduleId);
    if (!node) return;

    // Update active state
    node.active = active;

    // Update node
    this.nodes.set(moduleId, node);

    // Invalidate cache for affected resources
    for (const resource of node.resources) {
      this.invalidateCache(resource);
    }
  };
}

import { eventSystem } from '../../lib/events/UnifiedEventSystem';
import { ResourceState, ResourceTransfer, ResourceType } from '../../types/resources/ResourceTypes';
import { ResourceSystem, ResourceSystemConfig } from '../ResourceSystem';

/**
 * Storage container configuration
 */
export interface StorageContainerConfig {
  id: string;
  name: string;
  type: 'container' | 'pool' | 'storage';
  capacity: number;
  resourceTypes: ResourceType[];
  priority: number;
  location?: string;
  efficiency?: number;
  accessSpeed?: number;
  upgradeLevel?: number;
  maxUpgradeLevel?: number;
}

/**
 * Storage container state
 */
export interface StorageContainerState {
  config: StorageContainerConfig;
  resources: Map<ResourceType, ResourceState>;
  totalStored: number;
  lastUpdated: number;
}

/**
 * Storage allocation
 */
export interface StorageAllocation {
  containerId: string;
  resourceType: ResourceType;
  amount: number;
  percentage: number;
}

/**
 * ResourceStorageSubsystem
 * 
 * Manages resource storage containers, pools, and allocation strategies
 */
export class ResourceStorageSubsystem {
  private containers: Map<string, StorageContainerState>;
  private resourcePriorities: Map<ResourceType, number>;
  private transferHistory: ResourceTransfer[];
  private parentSystem: ResourceSystem;
  private config: ResourceSystemConfig;
  private isInitialized = false;

  constructor(parentSystem: ResourceSystem, config: ResourceSystemConfig) {
    this.parentSystem = parentSystem;
    this.config = config;
    this.containers = new Map();
    this.resourcePriorities = new Map();
    this.transferHistory = [];

    // Set default resource priorities
    this.setDefaultResourcePriorities();
  }

  /**
   * Set default resource priorities
   */
  private setDefaultResourcePriorities(): void {
    // Higher number = higher priority
    this.resourcePriorities.set('energy', 10);
    this.resourcePriorities.set('minerals', 8);
    this.resourcePriorities.set('population', 9);
    this.resourcePriorities.set('research', 10);
    this.resourcePriorities.set('plasma', 10);
    this.resourcePriorities.set('gas', 7);
    this.resourcePriorities.set('exotic', 6);
  }

  /**
   * Initialize the subsystem
   */
  public async initialize(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      // Additional initialization logic can go here
      this.isInitialized = true;
    } catch (error) {
      console.error('Failed to initialize ResourceStorageSubsystem:', error);
      throw error;
    }
  }

  /**
   * Dispose of the subsystem
   */
  public async dispose(): Promise<void> {
    if (!this.isInitialized) {
      return;
    }

    try {
      // Cleanup resources
      this.containers.clear();
      this.resourcePriorities.clear();
      this.transferHistory = [];

      this.isInitialized = false;
    } catch (error) {
      console.error('Failed to dispose ResourceStorageSubsystem:', error);
      throw error;
    }
  }

  /**
   * Register a storage container
   */
  public registerContainer(config: StorageContainerConfig): boolean {
    if (!config.id || !config.resourceTypes || config.resourceTypes.length === 0) {
      console.error('Invalid storage container configuration:', config);
      return false;
    }

    // Initialize resource states
    const resources = new Map<ResourceType, ResourceState>();

    for (const type of config.resourceTypes) {
      resources.set(type, {
        current: 0,
        min: 0,
        max: config.capacity / config.resourceTypes.length, // Divide capacity equally by default
        production: 0,
        consumption: 0,
      });
    }

    this.containers.set(config.id, {
      config,
      resources,
      totalStored: 0,
      lastUpdated: Date.now(),
    });

    return true;
  }

  /**
   * Unregister a storage container
   */
  public unregisterContainer(id: string): boolean {
    if (!this.containers.has(id)) {
      return false;
    }

    this.containers.delete(id);
    return true;
  }

  /**
   * Get a storage container
   */
  public getContainer(id: string): StorageContainerState | undefined {
    return this.containers.get(id);
  }

  /**
   * Get all storage containers
   */
  public getAllContainers(): StorageContainerState[] {
    return Array.from(this.containers.values());
  }

  /**
   * Get containers by resource type
   */
  public getContainersByResourceType(type: ResourceType): StorageContainerState[] {
    return Array.from(this.containers.values()).filter(container => 
      container.resources.has(type)
    );
  }

  /**
   * Store resource in a specific container
   */
  public storeResource(containerId: string, type: ResourceType, amount: number): number {
    const container = this.containers.get(containerId);
    if (!container || !container.resources.has(type) || amount <= 0) {
      return 0;
    }

    const resourceState = container.resources.get(type)!;
    const availableSpace = resourceState.max - resourceState.current;

    if (availableSpace <= 0) {
      // Emit overflow event
      eventSystem.publish({
        type: 'RESOURCE_STORAGE_OVERFLOW',
        resourceType: type,
        amount,
        containerId,
        timestamp: Date.now(),
      });
      return 0;
    }

    const amountToStore = Math.min(amount, availableSpace);

    resourceState.current += amountToStore;
    container.totalStored += amountToStore;
    container.lastUpdated = Date.now();

    // Update system resource state
    const systemState = this.parentSystem.getResourceState(type);
    if (systemState) {
      systemState.current += amountToStore;
      this.parentSystem.updateResourceState(type, systemState);
    }

    // Record transfer
    this.recordTransfer({
      type,
      source: 'external',
      target: containerId,
      amount: amountToStore,
      timestamp: Date.now(),
    });

    return amountToStore;
  }

  /**
   * Retrieve resource from a specific container
   */
  public retrieveResource(containerId: string, type: ResourceType, amount: number): number {
    const container = this.containers.get(containerId);
    if (!container || !container.resources.has(type) || amount <= 0) {
      return 0;
    }

    const resourceState = container.resources.get(type)!;

    if (resourceState.current <= 0) {
      return 0;
    }

    const amountToRetrieve = Math.min(amount, resourceState.current);

    resourceState.current -= amountToRetrieve;
    container.totalStored -= amountToRetrieve;
    container.lastUpdated = Date.now();

    // Update system resource state
    const systemState = this.parentSystem.getResourceState(type);
    if (systemState) {
      systemState.current -= amountToRetrieve;
      this.parentSystem.updateResourceState(type, systemState);
    }

    // Record transfer
    this.recordTransfer({
      type,
      source: containerId,
      target: 'external',
      amount: amountToRetrieve,
      timestamp: Date.now(),
    });

    return amountToRetrieve;
  }

  /**
   * Store resource in the best available container
   */
  public storeResourceOptimal(type: ResourceType, amount: number): number {
    if (amount <= 0) {
      return 0;
    }

    // Get all containers that can store this resource type
    const availableContainers = this.getContainersByResourceType(type);

    if (availableContainers.length === 0) {
      return 0;
    }

    // Calculate scores for each container based on the allocation strategy
    const containerScores = this.calculateContainerScores(availableContainers, type);

    // Sort containers by score (highest first)
    const sortedContainers = [...containerScores].sort((a, b) => b.score - a.score);

    let remainingAmount = amount;
    let totalStored = 0;

    // Try to store in containers by score order
    for (const { containerId } of sortedContainers) {
      if (remainingAmount <= 0) {
        break;
      }

      const amountStored = this.storeResource(containerId, type, remainingAmount);
      totalStored += amountStored;
      remainingAmount -= amountStored;
    }

    // Handle overflow if needed
    if (remainingAmount > 0 && this.config.overflowPolicy !== 'reject') {
      // Emit overflow event
      eventSystem.publish({
        type: 'RESOURCE_STORAGE_OVERFLOW',
        resourceType: type,
        amount: remainingAmount,
        timestamp: Date.now(),
      });
    }

    // Check if rebalancing is needed
    if (this.config.autoRebalance) {
      this.checkAndRebalance(type);
    }

    return totalStored;
  }

  /**
   * Retrieve resource from the best available container
   */
  public retrieveResourceOptimal(type: ResourceType, amount: number): number {
    if (amount <= 0) {
      return 0;
    }

    // Get all containers that store this resource type
    const availableContainers = this.getContainersByResourceType(type);

    if (availableContainers.length === 0) {
      return 0;
    }

    // Calculate scores for each container based on the allocation strategy
    // For retrieval, we want to prioritize containers with higher fill percentage
    const containerScores = this.calculateContainerScores(availableContainers, type, true);

    // Sort containers by score (highest first)
    const sortedContainers = [...containerScores].sort((a, b) => b.score - a.score);

    let remainingAmount = amount;
    let totalRetrieved = 0;

    // Try to retrieve from containers by score order
    for (const { containerId } of sortedContainers) {
      if (remainingAmount <= 0) {
        break;
      }

      const amountRetrieved = this.retrieveResource(containerId, type, remainingAmount);
      totalRetrieved += amountRetrieved;
      remainingAmount -= amountRetrieved;
    }

    return totalRetrieved;
  }

  /**
   * Calculate container scores for allocation
   */
  private calculateContainerScores(
    containers: StorageContainerState[],
    resourceType: ResourceType,
    forRetrieval = false
  ): Array<{ containerId: string; score: number }> {
    const weights = {
      containerPriority: 0.4,
      resourcePriority: 0.4,
      fillPercentage: 0.2,
    };

    const resourcePriorityValue = this.resourcePriorities.get(resourceType) || 5;

    return containers.map(container => {
      const resourceState = container.resources.get(resourceType)!;
      const fillRatio = resourceState.current / resourceState.max;

      // For storage, we prefer containers with lower fill percentage
      // For retrieval, we prefer containers with higher fill percentage
      const fillScore = forRetrieval ? fillRatio : 1 - fillRatio;

      // Calculate weighted score
      const score =
        container.config.priority * weights.containerPriority +
        resourcePriorityValue * weights.resourcePriority +
        fillScore * weights.fillPercentage;

      return {
        containerId: container.config.id,
        score,
      };
    });
  }

  /**
   * Redistribute overflow by expanding container capacity
   */
  public redistributeOverflow(type: ResourceType, amount: number, sourceId?: string): number {
    // Find containers that can store this resource type
    const relevantContainers = this.getContainersByResourceType(type);

    if (relevantContainers.length === 0) {
      return 0;
    }

    // If sourceId is provided, filter out that container
    const eligibleContainers = sourceId 
      ? relevantContainers.filter(c => c.config.id !== sourceId)
      : relevantContainers;

    if (eligibleContainers.length === 0) {
      // Sort by upgrade potential (containers with lower upgrade level first)
      const upgradableContainers = relevantContainers
        .filter(
          container =>
            container.config.upgradeLevel !== undefined &&
            container.config.maxUpgradeLevel !== undefined &&
            container.config.upgradeLevel < container.config.maxUpgradeLevel
        )
        .sort((a, b) => (a.config.upgradeLevel || 0) - (b.config.upgradeLevel || 0));

      if (upgradableContainers.length === 0) {
        return 0;
      }

      // Upgrade the first container
      const containerToUpgrade = upgradableContainers[0];

      // For each resource type in the container
      for (const [resType, resourceState] of containerToUpgrade.resources.entries()) {
        // Increase capacity by 20%
        const capacityIncrease = resourceState.max * 0.2;
        resourceState.max += capacityIncrease;
      }

      // Increment upgrade level
      if (containerToUpgrade.config.upgradeLevel !== undefined) {
        containerToUpgrade.config.upgradeLevel += 1;
      }

      // Try to store again if it's the target resource type
      if (sourceId) {
        return this.storeResource(containerToUpgrade.config.id, type, amount);
      } else {
        return this.storeResourceOptimal(type, amount);
      }
    } else {
      // Distribute among eligible containers
      let remainingAmount = amount;
      let totalStored = 0;

      // Calculate available space in all eligible containers
      for (const container of eligibleContainers) {
        if (remainingAmount <= 0) break;

        const resourceState = container.resources.get(type)!;
        const availableSpace = resourceState.max - resourceState.current;

        if (availableSpace <= 0) continue;

        const amountToStore = Math.min(remainingAmount, availableSpace);
        const stored = this.storeResource(container.config.id, type, amountToStore);

        totalStored += stored;
        remainingAmount -= stored;
      }

      return totalStored;
    }
  }

  /**
   * Check if rebalancing is needed and perform it
   */
  private checkAndRebalance(type: ResourceType): void {
    const containers = this.getContainersByResourceType(type);

    if (containers.length <= 1) {
      return;
    }

    // Calculate fill ratios
    const fillRatios = containers.map(container => {
      const resourceState = container.resources.get(type)!;
      return {
        id: container.config.id,
        fillRatio: resourceState.current / resourceState.max,
      };
    });

    // Find min and max fill ratios
    const minFill = Math.min(...fillRatios.map(r => r.fillRatio));
    const maxFill = Math.max(...fillRatios.map(r => r.fillRatio));

    // Check if imbalance exceeds threshold (default 0.2 or 20%)
    const threshold = 0.2;
    if (maxFill - minFill > threshold) {
      this.rebalanceContainers(type, containers);
    }
  }

  /**
   * Rebalance resources between containers
   */
  private rebalanceContainers(type: ResourceType, containers: StorageContainerState[]): void {
    // Calculate target fill ratio (average)
    let totalCurrent = 0;
    let totalMax = 0;

    for (const container of containers) {
      const resourceState = container.resources.get(type)!;
      totalCurrent += resourceState.current;
      totalMax += resourceState.max;
    }

    const targetFillRatio = totalCurrent / totalMax;

    // Calculate transfers needed
    for (const container of containers) {
      const resourceState = container.resources.get(type)!;
      const currentFillRatio = resourceState.current / resourceState.max;

      if (Math.abs(currentFillRatio - targetFillRatio) < 0.05) {
        // Close enough, skip
        continue;
      }

      const targetAmount = resourceState.max * targetFillRatio;
      const difference = targetAmount - resourceState.current;

      if (difference > 0) {
        // Need to add resources
        // Find container with excess
        const sourceContainer = containers.find(c => {
          const rs = c.resources.get(type)!;
          return rs.current / rs.max > targetFillRatio + 0.05;
        });

        if (sourceContainer) {
          const sourceState = sourceContainer.resources.get(type)!;
          const availableToTransfer = sourceState.current - sourceState.max * targetFillRatio;
          const transferAmount = Math.min(difference, availableToTransfer);

          // Transfer resources
          this.transferBetweenContainers(
            sourceContainer.config.id,
            container.config.id,
            type,
            transferAmount
          );
        }
      }
    }
  }

  /**
   * Transfer resources between containers
   */
  public transferBetweenContainers(
    sourceId: string,
    targetId: string,
    type: ResourceType,
    amount: number
  ): number {
    if (amount <= 0 || sourceId === targetId) {
      return 0;
    }

    // Retrieve from source
    const retrievedAmount = this.retrieveResource(sourceId, type, amount);

    if (retrievedAmount <= 0) {
      return 0;
    }

    // Store in target
    const storedAmount = this.storeResource(targetId, type, retrievedAmount);

    // If not all was stored, return remainder to source
    if (storedAmount < retrievedAmount) {
      const remainder = retrievedAmount - storedAmount;
      this.storeResource(sourceId, type, remainder);
    }

    // Record transfer
    this.recordTransfer({
      type,
      source: sourceId,
      target: targetId,
      amount: storedAmount,
      timestamp: Date.now(),
    });

    return storedAmount;
  }

  /**
   * Record a transfer in history
   */
  private recordTransfer(transfer: ResourceTransfer): void {
    this.transferHistory.push(transfer);

    // Trim history if needed
    if (this.transferHistory.length > this.config.maxHistorySize) {
      this.transferHistory = this.transferHistory.slice(-this.config.maxHistorySize);
    }
  }

  /**
   * Get transfer history
   */
  public getTransferHistory(): ResourceTransfer[] {
    return [...this.transferHistory];
  }

  /**
   * Get total stored amount of a resource type
   */
  public getTotalStored(type: ResourceType): number {
    let total = 0;

    for (const container of this.containers.values()) {
      const resourceState = container.resources.get(type);
      if (resourceState) {
        total += resourceState.current;
      }
    }

    return total;
  }

  /**
   * Get total capacity for a resource type
   */
  public getTotalCapacity(type: ResourceType): number {
    let total = 0;

    for (const container of this.containers.values()) {
      const resourceState = container.resources.get(type);
      if (resourceState) {
        total += resourceState.max;
      }
    }

    return total;
  }

  /**
   * Set resource priority
   */
  public setResourcePriority(type: ResourceType, priority: number): void {
    this.resourcePriorities.set(type, priority);
  }

  /**
   * Get resource priority
   */
  public getResourcePriority(type: ResourceType): number {
    return this.resourcePriorities.get(type) || 5;
  }

  /**
   * Update container configuration
   */
  public updateContainerConfig(id: string, config: Partial<StorageContainerConfig>): boolean {
    const container = this.containers.get(id);
    if (!container) {
      return false;
    }

    // Update config
    container.config = {
      ...container.config,
      ...config,
    };

    return true;
  }
}

import { eventSystem } from '../../lib/events/UnifiedEventSystem';
import { ResourceState, ResourceType } from '../../types/resources/ResourceTypes';
import { ResourceSystem, ResourceSystemConfig } from '../ResourceSystem';

/**
 * Threshold type enumeration
 */
export enum ThresholdType {
  ABSOLUTE = 'absolute',
  PERCENTAGE = 'percentage',
  RATE = 'rate',
}

/**
 * Threshold comparison type
 */
export enum ThresholdComparison {
  LESS_THAN = 'less_than',
  GREATER_THAN = 'greater_than',
  EQUAL_TO = 'equal_to',
  LESS_THAN_OR_EQUAL = 'less_than_or_equal',
  GREATER_THAN_OR_EQUAL = 'greater_than_or_equal',
}

/**
 * Threshold action type
 */
export enum ThresholdAction {
  ALERT = 'alert',
  AUTOMATE = 'automate',
  TRIGGER_EVENT = 'trigger_event',
}

/**
 * Resource threshold configuration
 */
export interface ResourceThreshold {
  id: string;
  resourceType: ResourceType;
  thresholdType: ThresholdType;
  comparison: ThresholdComparison;
  value: number;
  action: ThresholdAction;
  actionData?: any;
  enabled: boolean;
  cooldownMs?: number;
  lastTriggered?: number;
  repeat?: boolean;
  entityId?: string; // Optional entity ID for entity-specific thresholds
}

/**
 * ResourceThresholdSubsystem
 * 
 * Monitors resource levels and triggers actions when thresholds are reached
 */
export class ResourceThresholdSubsystem {
  private thresholds: Map<string, ResourceThreshold> = new Map();
  private resourceTypeThresholds: Map<ResourceType, string[]> = new Map();
  private entityThresholds: Map<string, string[]> = new Map();
  private parentSystem: ResourceSystem;
  private config: ResourceSystemConfig;
  private isInitialized = false;

  constructor(parentSystem: ResourceSystem, config: ResourceSystemConfig) {
    this.parentSystem = parentSystem;
    this.config = config;
  }

  /**
   * Initialize the subsystem
   */
  public async initialize(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      // Initialize default thresholds if needed
      this.initializeDefaultThresholds();

      this.isInitialized = true;
    } catch (error) {
      console.error('Failed to initialize ResourceThresholdSubsystem:', error);
      throw error;
    }
  }

  /**
   * Dispose of the subsystem
   */
  public async dispose(): Promise<void> {
    if (!this.isInitialized) {
      return;
    }

    try {
      // Clear thresholds
      this.thresholds.clear();
      this.resourceTypeThresholds.clear();
      this.entityThresholds.clear();

      this.isInitialized = false;
    } catch (error) {
      console.error('Failed to dispose ResourceThresholdSubsystem:', error);
      throw error;
    }
  }

  /**
   * Initialize default thresholds
   */
  private initializeDefaultThresholds(): void {
    // Example: Add a low energy threshold
    this.registerThreshold({
      id: 'default-energy-low',
      resourceType: 'energy',
      thresholdType: ThresholdType.PERCENTAGE,
      comparison: ThresholdComparison.LESS_THAN,
      value: 0.1, // 10%
      action: ThresholdAction.ALERT,
      actionData: { message: 'Energy levels critically low!' },
      enabled: true,
      cooldownMs: 60000, // 1 minute cooldown
      repeat: true,
    });

    // Example: Add a high minerals threshold
    this.registerThreshold({
      id: 'default-minerals-high',
      resourceType: 'minerals',
      thresholdType: ThresholdType.PERCENTAGE,
      comparison: ThresholdComparison.GREATER_THAN,
      value: 0.9, // 90%
      action: ThresholdAction.ALERT,
      actionData: { message: 'Mineral storage nearly full!' },
      enabled: true,
      cooldownMs: 120000, // 2 minute cooldown
      repeat: true,
    });
  }

  /**
   * Register a resource threshold
   */
  public registerThreshold(threshold: ResourceThreshold): boolean {
    if (!threshold.id || !threshold.resourceType) {
      console.warn('Invalid threshold:', threshold);
      return false;
    }

    // Store the threshold
    this.thresholds.set(threshold.id, threshold);

    // Add to resource type index
    this.addToArray(this.resourceTypeThresholds, threshold.resourceType, threshold.id);

    // Add to entity index if entity-specific
    if (threshold.entityId) {
      this.addToArray(this.entityThresholds, threshold.entityId, threshold.id);
    }

    return true;
  }

  /**
   * Unregister a resource threshold
   */
  public unregisterThreshold(id: string): boolean {
    const threshold = this.thresholds.get(id);
    if (!threshold) {
      return false;
    }

    // Remove from resource type index
    this.removeFromArray(this.resourceTypeThresholds, threshold.resourceType, id);

    // Remove from entity index if entity-specific
    if (threshold.entityId) {
      this.removeFromArray(this.entityThresholds, threshold.entityId, id);
    }

    // Remove the threshold
    this.thresholds.delete(id);

    return true;
  }

  /**
   * Get a specific threshold
   */
  public getThreshold(id: string): ResourceThreshold | undefined {
    return this.thresholds.get(id);
  }

  /**
   * Get all thresholds
   */
  public getAllThresholds(): ResourceThreshold[] {
    return Array.from(this.thresholds.values());
  }

  /**
   * Get thresholds for a specific resource type
   */
  public getThresholdsByResourceType(type: ResourceType): ResourceThreshold[] {
    const thresholdIds = this.resourceTypeThresholds.get(type) || [];
    return thresholdIds
      .map(id => this.thresholds.get(id))
      .filter(Boolean) as ResourceThreshold[];
  }

  /**
   * Get thresholds for a specific entity
   */
  public getThresholdsByEntity(entityId: string): ResourceThreshold[] {
    const thresholdIds = this.entityThresholds.get(entityId) || [];
    return thresholdIds
      .map(id => this.thresholds.get(id))
      .filter(Boolean) as ResourceThreshold[];
  }

  /**
   * Enable a threshold
   */
  public enableThreshold(id: string): boolean {
    const threshold = this.thresholds.get(id);
    if (!threshold) {
      return false;
    }

    threshold.enabled = true;
    this.thresholds.set(id, threshold);
    return true;
  }

  /**
   * Disable a threshold
   */
  public disableThreshold(id: string): boolean {
    const threshold = this.thresholds.get(id);
    if (!threshold) {
      return false;
    }

    threshold.enabled = false;
    this.thresholds.set(id, threshold);
    return true;
  }

  /**
   * Check if a threshold is triggered for a given resource state
   */
  private isThresholdTriggered(threshold: ResourceThreshold, state: ResourceState): boolean {
    // Skip disabled thresholds
    if (!threshold.enabled) {
      return false;
    }

    // Check cooldown
    if (threshold.cooldownMs && threshold.lastTriggered) {
      const timeSinceLastTrigger = Date.now() - threshold.lastTriggered;
      if (timeSinceLastTrigger < threshold.cooldownMs) {
        return false;
      }
    }

    // If one-time and already triggered, skip
    if (!threshold.repeat && threshold.lastTriggered) {
      return false;
    }

    // Get the actual value to compare
    let actualValue: number;
    switch (threshold.thresholdType) {
      case ThresholdType.ABSOLUTE:
        actualValue = state.current;
        break;
      case ThresholdType.PERCENTAGE:
        actualValue = state.current / state.max;
        break;
      case ThresholdType.RATE:
        // For rate, we compare production rate or consumption rate
        if (threshold.value >= 0) {
          actualValue = state.production;
        } else {
          actualValue = state.consumption;
        }
        break;
      default:
        return false;
    }

    // Perform the comparison
    switch (threshold.comparison) {
      case ThresholdComparison.LESS_THAN:
        return actualValue < threshold.value;
      case ThresholdComparison.GREATER_THAN:
        return actualValue > threshold.value;
      case ThresholdComparison.EQUAL_TO:
        return Math.abs(actualValue - threshold.value) < 0.001; // Float comparison with small epsilon
      case ThresholdComparison.LESS_THAN_OR_EQUAL:
        return actualValue <= threshold.value;
      case ThresholdComparison.GREATER_THAN_OR_EQUAL:
        return actualValue >= threshold.value;
      default:
        return false;
    }
  }

  /**
   * Check thresholds for a specific resource type
   */
  public checkThresholds(type: ResourceType, state: ResourceState, entityId?: string): void {
    // Get thresholds for this resource type
    const thresholdIds = this.resourceTypeThresholds.get(type) || [];

    // If entity ID is provided, also include entity-specific thresholds
    if (entityId) {
      const entityThresholdIds = this.entityThresholds.get(entityId) || [];
      thresholdIds.push(...entityThresholdIds);
    }

    // Process each threshold
    for (const id of thresholdIds) {
      const threshold = this.thresholds.get(id);
      if (!threshold) continue;

      // Skip if resource type doesn't match
      if (threshold.resourceType !== type) continue;

      // Skip if entity ID doesn't match (for entity-specific thresholds)
      if (threshold.entityId && threshold.entityId !== entityId) continue;

      // Check if triggered
      if (this.isThresholdTriggered(threshold, state)) {
        // Update last triggered time
        threshold.lastTriggered = Date.now();
        this.thresholds.set(id, threshold);

        // Handle the action
        this.handleThresholdAction(threshold, state);
      }
    }
  }

  /**
   * Handle a threshold action
   */
  private handleThresholdAction(threshold: ResourceThreshold, state: ResourceState): void {
    switch (threshold.action) {
      case ThresholdAction.ALERT:
        // Emit alert event
        eventSystem.publish({
          type: 'RESOURCE_THRESHOLD_REACHED',
          threshold,
          state,
          message: threshold.actionData?.message || `Resource threshold reached for ${threshold.resourceType}`,
          timestamp: Date.now(),
        });
        break;

      case ThresholdAction.AUTOMATE:
        // Trigger automation
        eventSystem.publish({
          type: 'RESOURCE_AUTOMATION_TRIGGERED',
          threshold,
          state,
          automationData: threshold.actionData,
          timestamp: Date.now(),
        });
        break;

      case ThresholdAction.TRIGGER_EVENT:
        // Trigger custom event
        eventSystem.publish({
          type: threshold.actionData?.eventType || 'CUSTOM_RESOURCE_EVENT',
          threshold,
          state,
          data: threshold.actionData?.data || {},
          timestamp: Date.now(),
        });
        break;
    }
  }

  /**
   * Add an item to an array in a map
   */
  private addToArray<K, V>(map: Map<K, V[]>, key: K, value: V): void {
    const array = map.get(key) || [];
    if (!array.includes(value)) {
      array.push(value);
      map.set(key, array);
    }
  }

  /**
   * Remove an item from an array in a map
   */
  private removeFromArray<K, V>(map: Map<K, V[]>, key: K, value: V): void {
    const array = map.get(key);
    if (array) {
      const index = array.indexOf(value);
      if (index >= 0) {
        array.splice(index, 1);
        map.set(key, array);
      }
    }
  }
}

import { eventSystem } from '../../lib/events/UnifiedEventSystem';
import { ResourceTransfer, ResourceType } from '../../types/resources/ResourceTypes';
import { validateResourceTransfer } from '../../utils/resources/resourceValidation';
import { ResourceSystem, ResourceSystemConfig } from '../ResourceSystem';

/**
 * ResourceTransferSubsystem
 * 
 * Handles resource transfers between entities
 * - Direct transfers between entities
 * - Transfer history tracking
 * - Transfer validation and error handling
 */
export class ResourceTransferSubsystem {
  private transferHistory: ResourceTransfer[] = [];
  private parentSystem: ResourceSystem;
  private config: ResourceSystemConfig;
  private isInitialized = false;

  constructor(parentSystem: ResourceSystem, config: ResourceSystemConfig) {
    this.parentSystem = parentSystem;
    this.config = config;
  }

  /**
   * Initialize the subsystem
   */
  public async initialize(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      // Initialize event subscriptions
      this.initializeEventSubscriptions();

      this.isInitialized = true;
    } catch (error) {
      console.error('Failed to initialize ResourceTransferSubsystem:', error);
      throw error;
    }
  }

  /**
   * Dispose of the subsystem
   */
  public async dispose(): Promise<void> {
    if (!this.isInitialized) {
      return;
    }

    try {
      // Clear transfer history
      this.transferHistory = [];

      this.isInitialized = false;
    } catch (error) {
      console.error('Failed to dispose ResourceTransferSubsystem:', error);
      throw error;
    }
  }

  /**
   * Initialize event subscriptions
   */
  private initializeEventSubscriptions(): void {
    // Subscribe to relevant events
    eventSystem.subscribe('RESOURCE_TRANSFER_REQUESTED', this.handleTransferRequest);
  }

  /**
   * Handle a transfer request event
   */
  private handleTransferRequest = (event: any): void => {
    const { type, amount, sourceId, targetId } = event;

    this.transferResource(type, amount, sourceId, targetId);
  };

  /**
   * Transfer resources between entities
   */
  public transferResource(
    type: ResourceType,
    amount: number,
    sourceId: string,
    targetId: string
  ): number {
    if (amount <= 0 || sourceId === targetId) {
      return 0;
    }

    // Get storage subsystem
    const storageSubsystem = this.parentSystem.getStorageSubsystem();

    // Retrieve from source
    const retrievedAmount = storageSubsystem.retrieveResource(sourceId, type, amount);

    if (retrievedAmount <= 0) {
      return 0;
    }

    // Store in target
    const storedAmount = storageSubsystem.storeResource(targetId, type, retrievedAmount);

    // If not all was stored, return remainder to source
    if (storedAmount < retrievedAmount) {
      const remainder = retrievedAmount - storedAmount;
      storageSubsystem.storeResource(sourceId, type, remainder);
    }

    // Record transfer
    this.recordTransfer({
      type,
      source: sourceId,
      target: targetId,
      amount: storedAmount,
      timestamp: Date.now(),
    });

    return storedAmount;
  }

  /**
   * Bulk transfer resources between multiple sources and targets
   */
  public bulkTransfer(transfers: {
    type: ResourceType;
    amount: number;
    sourceId: string;
    targetId: string;
  }[]): { transferred: number; totalRequested: number } {
    let totalTransferred = 0;
    let totalRequested = 0;

    for (const transfer of transfers) {
      totalRequested += transfer.amount;
      const transferred = this.transferResource(
        transfer.type,
        transfer.amount,
        transfer.sourceId,
        transfer.targetId
      );
      totalTransferred += transferred;
    }

    return {
      transferred: totalTransferred,
      totalRequested,
    };
  }

  /**
   * Record a transfer in the history
   */
  private recordTransfer(transfer: ResourceTransfer): void {
    // Validate transfer
    if (!validateResourceTransfer(transfer)) {
      console.warn('Invalid transfer:', transfer);
      return;
    }

    // Add to history
    this.transferHistory.push(transfer);

    // Trim history if needed
    if (this.transferHistory.length > this.config.maxHistorySize) {
      this.transferHistory = this.transferHistory.slice(-this.config.maxHistorySize);
    }

    // Emit transfer event
    eventSystem.publish({
      type: 'RESOURCE_TRANSFERRED',
      transfer,
      timestamp: Date.now(),
    });
  }

  /**
   * Get all transfer history
   */
  public getTransferHistory(): ResourceTransfer[] {
    return [...this.transferHistory];
  }

  /**
   * Get transfer history for a specific resource type
   */
  public getTransfersByType(type: ResourceType): ResourceTransfer[] {
    return this.transferHistory.filter(transfer => transfer.type === type);
  }

  /**
   * Get transfers involving a specific entity (as source or target)
   */
  public getTransfersByEntity(entityId: string): ResourceTransfer[] {
    return this.transferHistory.filter(
      transfer => transfer.source === entityId || transfer.target === entityId
    );
  }

  /**
   * Get transfers between specific entities
   */
  public getTransfersBetween(sourceId: string, targetId: string): ResourceTransfer[] {
    return this.transferHistory.filter(
      transfer => transfer.source === sourceId && transfer.target === targetId
    );
  }

  /**
   * Calculate net flow between entities
   */
  public calculateNetFlow(
    entityId: string,
    resourceType?: ResourceType
  ): Record<string, number> {
    const netFlow: Record<string, number> = {};

    // Filter transfers involving this entity
    const relevantTransfers = this.transferHistory.filter(
      transfer => 
        (transfer.source === entityId || transfer.target === entityId) && 
        (!resourceType || transfer.type === resourceType)
    );

    for (const transfer of relevantTransfers) {
      // Calculate the other entity ID
      const otherEntityId = transfer.source === entityId ? transfer.target : transfer.source;

      // Calculate flow direction - positive for incoming, negative for outgoing
      const flowAmount = transfer.source === entityId ? -transfer.amount : transfer.amount;

      // Update net flow
      netFlow[otherEntityId] = (netFlow[otherEntityId] || 0) + flowAmount;
    }

    return netFlow;
  }
}

/**
 * Network Degradation Test Suite Runner
 *
 * This script runs the network degradation test suite to evaluate application
 * performance under various network conditions. It provides command-line options
 * to run specific tests or all tests together.
 *
 * Usage:
 *   npx ts-node src/scripts/runNetworkTests.ts --all
 *   npx ts-node src/scripts/runNetworkTests.ts --api
 *   npx ts-node src/scripts/runNetworkTests.ts --resources
 *   npx ts-node src/scripts/runNetworkTests.ts --interactions
 */

import {
  NetworkDegradationTestResult,
  testApiPerformance,
  testResourceLoadingPerformance,
  testUserInteractionPerformance,
} from '../tests/performance/NetworkDegradationTestSuite';

// Parse command line arguments
const args = process.argv.slice(2);
const runAll = args.includes('--all');
const runApi = args.includes('--api') || runAll;
const runResources = args.includes('--resources') || runAll;
const runInteractions = args.includes('--interactions') || runAll;
const generateReport = args.includes('--report') || runAll;

// Define a function to format duration
function formatDuration(ms: number): string {
  if (ms < 1000) return `${ms.toFixed(0)}ms`;
  const seconds = ms / 1000;
  if (seconds < 60) return `${seconds.toFixed(1)}s`;
  const minutes = Math.floor(seconds / 60);
  const remainingSeconds = seconds % 60;
  return `${minutes}m ${remainingSeconds.toFixed(0)}s`;
}

// Function to print a result table
function printResultTable(results: NetworkDegradationTestResult[], title: string): void {
  console.log(`\n${title}`);
  console.log('-'.repeat(100));
  console.log(
    '| Network Condition       | Avg Response Time | Max Response Time | Success Rate | TTI        |'
  );
  console.log(
    '|-------------------------|-------------------|-------------------|--------------|------------|'
  );

  // Group results by network condition
  const groupedResults: Record<string, NetworkDegradationTestResult[]> = {};
  results.forEach(result => {
    const conditionName = result.networkCondition.name;
    groupedResults[conditionName] = groupedResults[conditionName] || [];
    groupedResults[conditionName].push(result);
  });

  // Sort network conditions by average response time (ascending)
  const sortedConditions = Object.entries(groupedResults)
    .map(([condition, results]) => {
      const avgResponseTime =
        results.reduce((sum, r) => sum + r.averageResponseTimeMs, 0) / results.length;

      return { condition, avgResponseTime };
    })
    .sort((a, b) => a.avgResponseTime - b.avgResponseTime);

  // Print each condition's results
  for (const { condition } of sortedConditions) {
    const condResults = groupedResults[condition];
    const avgResponseTime =
      condResults.reduce((sum, r) => sum + r.averageResponseTimeMs, 0) / condResults.length;

    const maxResponseTime = Math.max(...condResults.map(r => r.maxResponseTimeMs));

    const successCount = condResults.reduce((sum, r) => sum + r.successfulOperations, 0);

    const totalOps = successCount + condResults.reduce((sum, r) => sum + r.failedOperations, 0);

    const successRate = totalOps > 0 ? (successCount / totalOps) * 100 : 0;

    const avgTTI =
      condResults.reduce((sum, r) => sum + r.timeToInteractive, 0) / condResults.length;

    // Print the row
    console.log(
      `| %-23s | %-17s | %-17s | %-12s | %-10s |`.replace(/%(-?\d+)s/g, (match, width) => {
        const paddingLength = parseInt(width.replace('-', ''));
        const text = [
          condition.padEnd(23),
          formatDuration(avgResponseTime).padEnd(17),
          formatDuration(maxResponseTime).padEnd(17),
          `${successRate.toFixed(1)}%`.padEnd(12),
          formatDuration(avgTTI).padEnd(10),
        ][0];
        return width.startsWith('-') ? text.padEnd(paddingLength) : text.padStart(paddingLength);
      })
    );
  }

  console.log('-'.repeat(100));
}

// Function to generate a full HTML report
function generateHtmlReport(results: Record<string, NetworkDegradationTestResult[]>): void {
  // Implementation for generating a detailed HTML report
  // (This would be a more extensive implementation in the real system)
  console.log('\nGenerating HTML report...');
  console.log('HTML report generation not implemented in this demo script.');
}

async function main(): Promise<void> {
  console.log('Network Degradation Test Suite Runner');
  console.log('====================================\n');

  const startTime = Date.now();
  const results: Record<string, NetworkDegradationTestResult[]> = {};

  if (runApi) {
    console.log('Running API performance tests...');
    results.api = await testApiPerformance();
    printResultTable(results.api, 'API Performance Results');
  }

  if (runResources) {
    console.log('\nRunning resource loading performance tests...');
    results.resources = await testResourceLoadingPerformance();
    printResultTable(results.resources, 'Resource Loading Performance Results');
  }

  if (runInteractions) {
    console.log('\nRunning user interaction performance tests...');
    results.interactions = await testUserInteractionPerformance();
    printResultTable(results.interactions, 'User Interaction Performance Results');
  }

  // Print overall time
  const totalTime = Date.now() - startTime;
  console.log(`\nCompleted all tests in ${formatDuration(totalTime)}`);

  // Generate report if requested
  if (generateReport) {
    generateHtmlReport(results);
  }

  // Print usage instructions if no arguments provided
  if (!runAll && !runApi && !runResources && !runInteractions) {
    console.log('\nUsage:');
    console.log('  npx ts-node src/scripts/runNetworkTests.ts --all         Run all tests');
    console.log(
      '  npx ts-node src/scripts/runNetworkTests.ts --api         Run API performance tests'
    );
    console.log(
      '  npx ts-node src/scripts/runNetworkTests.ts --resources   Run resource loading tests'
    );
    console.log(
      '  npx ts-node src/scripts/runNetworkTests.ts --interactions Run user interaction tests'
    );
    console.log('  npx ts-node src/scripts/runNetworkTests.ts --report      Generate HTML report');
  }
}

// Run the main function
main().catch(error => {
  console.error('Error running network tests:', error);
  process.exit(1);
});

