/**
 * RecoveryService - Provides mechanisms for recovering from critical application failures
 *
 * This service handles:
 * - Saving application state snapshots
 * - Restoring previous application states
 * - Implementing different recovery strategies based on error type
 * - Providing graceful degradation options
 */

import { AbstractBaseService } from '../lib/services/BaseService';
import { ErrorType } from './ErrorLoggingService';

// Types of recovery strategies that can be applied
export enum RecoveryStrategy {
  RETRY = 'retry',
  ROLLBACK = 'rollback',
  RESET = 'reset',
  IGNORE = 'ignore',
}

// Structure for application state snapshot
export interface StateSnapshot {
  id: string;
  state: Record<string, unknown>;
  timestamp: number;
  metadata?: Record<string, unknown>;
}

// Configuration for recovery actions
export interface RecoveryConfig {
  maxSnapshots: number;
  autoSaveInterval: number;
  enableAutoRecover: boolean;
  defaultStrategy: RecoveryStrategy;
  strategyByErrorType: Partial<Record<ErrorType, RecoveryStrategy>>;
}

class RecoveryServiceImpl extends AbstractBaseService {
  private static instance: RecoveryServiceImpl;
  private snapshots: StateSnapshot[] = [];
  private config: RecoveryConfig = {
    maxSnapshots: 10,
    autoSaveInterval: 60000, // 1 minute
    enableAutoRecover: true,
    defaultStrategy: RecoveryStrategy.RETRY,
    strategyByErrorType: {
      [ErrorType.NETWORK]: RecoveryStrategy.RETRY,
      [ErrorType.RESOURCE]: RecoveryStrategy.ROLLBACK,
      [ErrorType.UNKNOWN]: RecoveryStrategy.RESET,
    },
  };

  private constructor() {
    super('RecoveryService', '1.0.0');
  }

  public static getInstance(): RecoveryServiceImpl {
    if (!RecoveryServiceImpl.instance) {
      RecoveryServiceImpl.instance = new RecoveryServiceImpl();
    }
    return RecoveryServiceImpl.instance;
  }

  protected async onInitialize(): Promise<void> {
    // Load any existing snapshots from localStorage
    try {
      const savedSnapshots = localStorage.getItem('recovery_snapshots');
      if (savedSnapshots) {
        this.snapshots = JSON.parse(savedSnapshots);
      }
    } catch (error) {
      this.handleError(error as Error);
    }
  }

  protected async onDispose(): Promise<void> {
    // Save snapshots to localStorage
    try {
      localStorage.setItem('recovery_snapshots', JSON.stringify(this.snapshots));
    } catch (error) {
      this.handleError(error as Error);
    }
  }

  public createSnapshot(
    state: Record<string, unknown>,
    metadata?: Record<string, unknown>
  ): string {
    const snapshot: StateSnapshot = {
      id: crypto.randomUUID(),
      state,
      timestamp: Date.now(),
      metadata,
    };

    this.snapshots.unshift(snapshot);

    // Trim snapshots if we exceed max size
    if (this.snapshots.length > this.config.maxSnapshots) {
      this.snapshots = this.snapshots.slice(0, this.config.maxSnapshots);
    }

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.total_snapshots = this.snapshots.length;
    metrics.latest_snapshot_timestamp = snapshot.timestamp;
    this.metadata.metrics = metrics;

    return snapshot.id;
  }

  public restoreSnapshot(snapshotId: string): Record<string, unknown> | null {
    const snapshot = this.snapshots.find(s => s.id === snapshotId);
    if (!snapshot) {
      return null;
    }

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.total_restores = (metrics.total_restores || 0) + 1;
    metrics.last_restore_timestamp = Date.now();
    this.metadata.metrics = metrics;

    return snapshot.state;
  }

  public getSnapshots(): StateSnapshot[] {
    return [...this.snapshots];
  }

  public clearSnapshots(): void {
    this.snapshots = [];
    this.metadata.metrics = {};
  }

  public getRecoveryStrategy(
    errorType: ErrorType = ErrorType.UNKNOWN,
    metadata?: Record<string, unknown>
  ): RecoveryStrategy {
    return this.config.strategyByErrorType[errorType] || this.config.defaultStrategy;
  }

  public override handleError(error: Error): void {
    // Update error metrics
    const metrics = this.metadata.metrics || {};
    metrics.total_errors = (metrics.total_errors || 0) + 1;
    metrics.last_error_timestamp = Date.now();
    this.metadata.metrics = metrics;

    // Log error in development
    if (process.env.NODE_ENV === 'development') {
      console.error('[RecoveryService] Error:', error);
    }
  }
}

// Export singleton instance
export const recoveryService = RecoveryServiceImpl.getInstance();

// Export default for easier imports
export default recoveryService;

import { AbstractBaseService } from '../lib/services/BaseService';
import { ErrorType, errorLoggingService } from './ErrorLoggingService';

export interface WorkerTask<T = unknown> {
  id: string;
  type: string;
  data: unknown;
  priority: number;
  cancelToken?: AbortController;
  progress?: number;
  result?: T;
  error?: Error;
  startTime?: number;
  endTime?: number;
}

export interface WorkerConfig {
  maxWorkers: number;
  taskTimeout: number;
  retryAttempts: number;
  priorityLevels: {
    HIGH: number;
    MEDIUM: number;
    LOW: number;
  };
}

class WorkerServiceImpl extends AbstractBaseService {
  private static instance: WorkerServiceImpl;
  private workers: Worker[] = [];
  private taskQueue: WorkerTask[] = [];
  private activeTasks: Map<string, WorkerTask> = new Map();
  private workerPool: Map<Worker, WorkerTask | null> = new Map();

  private config: WorkerConfig = {
    maxWorkers: navigator.hardwareConcurrency || 4,
    taskTimeout: 30000, // 30 seconds
    retryAttempts: 3,
    priorityLevels: {
      HIGH: 3,
      MEDIUM: 2,
      LOW: 1,
    },
  };

  private constructor() {
    super('WorkerService', '1.0.0');
  }

  public static getInstance(): WorkerServiceImpl {
    if (!WorkerServiceImpl.instance) {
      WorkerServiceImpl.instance = new WorkerServiceImpl();
    }
    return WorkerServiceImpl.instance;
  }

  protected async onInitialize(): Promise<void> {
    // Initialize worker pool
    for (let i = 0; i < this.config.maxWorkers; i++) {
      const worker = new Worker(new URL('../workers/worker.ts', import.meta.url), {
        type: 'module',
      });
      this.setupWorker(worker);
      this.workers.push(worker);
      this.workerPool.set(worker, null);
    }

    // Initialize metrics
    this.metadata.metrics = {
      total_tasks: 0,
      active_tasks: 0,
      completed_tasks: 0,
      failed_tasks: 0,
      average_task_time: 0,
    };
  }

  protected async onDispose(): Promise<void> {
    // Cancel all active tasks
    for (const task of this.activeTasks.values()) {
      this.cancelTask(task.id);
    }

    // Terminate all workers
    for (const worker of this.workers) {
      worker.terminate();
    }

    this.workers = [];
    this.taskQueue = [];
    this.activeTasks.clear();
    this.workerPool.clear();
  }

  private setupWorker(worker: Worker): void {
    worker.onmessage = (event: MessageEvent) => {
      const { taskId, type, data } = event.data;
      const task = this.activeTasks.get(taskId);

      if (!task) return;

      switch (type) {
        case 'progress':
          task.progress = data;
          break;
        case 'result':
          this.completeTask(taskId, data);
          break;
        case 'error':
          this.failTask(taskId, new Error(data));
          break;
      }
    };

    worker.onerror = (error: ErrorEvent) => {
      const task = Array.from(this.activeTasks.values()).find(
        task => this.workerPool.get(worker) === task
      );
      if (task) {
        this.failTask(task.id, error.error);
      }
    };
  }

  public async submitTask<T>(
    type: string,
    data: unknown,
    priority: keyof WorkerConfig['priorityLevels'] = 'MEDIUM'
  ): Promise<T> {
    const task: WorkerTask<T> = {
      id: crypto.randomUUID(),
      type,
      data,
      priority: this.config.priorityLevels[priority],
      cancelToken: new AbortController(),
    };

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.total_tasks = (metrics.total_tasks || 0) + 1;
    this.metadata.metrics = metrics;

    return new Promise((resolve, reject) => {
      // Add task to queue
      this.taskQueue.push(task);
      this.taskQueue.sort((a, b) => b.priority - a.priority);

      // Setup task timeout
      const timeoutId = setTimeout(() => {
        this.cancelTask(task.id);
        reject(new Error(`Task ${task.id} timed out after ${this.config.taskTimeout}ms`));
      }, this.config.taskTimeout);

      // Setup completion handlers
      const cleanup = () => {
        clearTimeout(timeoutId);
        this.taskQueue = this.taskQueue.filter(t => t.id !== task.id);
        this.activeTasks.delete(task.id);
      };

      const onComplete = (result: T) => {
        cleanup();
        resolve(result);
      };

      const onError = (error: Error) => {
        cleanup();
        reject(error);
      };

      // Store handlers with task
      Object.assign(task, { onComplete, onError });

      // Try to process task immediately
      this.processNextTask();
    });
  }

  public cancelTask(taskId: string): void {
    const task = this.activeTasks.get(taskId);
    if (!task) return;

    task.cancelToken?.abort();
    this.failTask(taskId, new Error('Task cancelled'));
  }

  private async processNextTask(): Promise<void> {
    // Find available worker
    const availableWorker = Array.from(this.workerPool.entries()).find(
      ([, task]) => task === null
    )?.[0];

    if (!availableWorker || this.taskQueue.length === 0) return;

    // Get highest priority task
    const task = this.taskQueue.shift()!;
    task.startTime = Date.now();

    // Assign task to worker
    this.activeTasks.set(task.id, task);
    this.workerPool.set(availableWorker, task);

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.active_tasks = this.activeTasks.size;
    this.metadata.metrics = metrics;

    // Send task to worker
    availableWorker.postMessage({
      taskId: task.id,
      type: task.type,
      data: task.data,
    });
  }

  private completeTask(taskId: string, result: unknown): void {
    const task = this.activeTasks.get(taskId);
    if (!task) return;

    task.endTime = Date.now();
    task.result = result;

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.completed_tasks = (metrics.completed_tasks || 0) + 1;
    metrics.active_tasks = this.activeTasks.size - 1;

    const taskTime = task.endTime - (task.startTime || 0);
    metrics.average_task_time = metrics.average_task_time
      ? (metrics.average_task_time + taskTime) / 2
      : taskTime;

    this.metadata.metrics = metrics;

    // Release worker
    const worker = Array.from(this.workerPool.entries()).find(([, t]) => t === task)?.[0];
    if (worker) {
      this.workerPool.set(worker, null);
    }

    // Call completion handler
    (task as any).onComplete?.(result);

    // Process next task
    this.processNextTask();
  }

  private failTask(taskId: string, error: Error): void {
    const task = this.activeTasks.get(taskId);
    if (!task) return;

    // Update metrics
    const metrics = this.metadata.metrics || {};
    metrics.failed_tasks = (metrics.failed_tasks || 0) + 1;
    metrics.active_tasks = this.activeTasks.size - 1;
    this.metadata.metrics = metrics;

    // Release worker
    const worker = Array.from(this.workerPool.entries()).find(([, t]) => t === task)?.[0];
    if (worker) {
      this.workerPool.set(worker, null);
    }

    // Call error handler
    (task as any).onError?.(error);

    // Process next task
    this.processNextTask();
  }

  public override handleError(error: Error): void {
    errorLoggingService.logError(error, ErrorType.RUNTIME, undefined, { service: 'WorkerService' });
  }
}

