    expect(eventBus.emittedEvents[0].type).toMatch(/EXPLORATION_SHIP_UNASSIGNED/);
    expect(eventBus.emittedEvents[0].data?.shipId).toBe('ship-1');
    expect(eventBus.emittedEvents[0].data?.systemId).toBe('system-1');
