        schedule: 'onDemand' as const,
        benchmarks: [
          {
            name: 'Benchmark 1',
            run: () => ({
              name: 'Benchmark 1',
              executionTimeMs: 100,
              timestamp: new Date(),
            }),
          },
        ],
      };

      const regressionJob = {
        id: 'regression-job',
        name: 'Regression Job',
        schedule: 'onDemand' as const,
        benchmarks: [
          {
            name: 'Benchmark 1',
            run: () => ({
              name: 'Benchmark 1',
              executionTimeMs: 150, // 50% regression
              timestamp: new Date(),
            }),
          },
        ],
        notifyOnRegression: true,
        regressionThreshold: 20,
        baselineKey: 'baseline-job',
      };

      // Register jobs
      benchmarkManager.registerJob(baselineJob);
      benchmarkManager.registerJob(regressionJob);

      // Run baseline job
      await benchmarkManager.runJob('baseline-job');

      // Mock console.warn to check for regression notifications
      const consoleWarnMock = vi.spyOn(console, 'warn').mockImplementation(() => {});
      vi.spyOn(console, 'table').mockImplementation(() => {});

      // Run regression job
      await benchmarkManager.runJob('regression-job');

      // Verify console.warn was called with regression message
      expect(consoleWarnMock).toHaveBeenCalledWith(
        expect.stringContaining('Performance regression detected')
      );

      // Restore mocks
      consoleWarnMock.mockRestore();

      // Unregister jobs
      benchmarkManager.unregisterJob('baseline-job');
      benchmarkManager.unregisterJob('regression-job');
    });
  });
});

/**
 * Unified Performance Testing Utilities
 * 
 * This module provides standardized utilities for:
 * - Measuring execution time and memory usage
 * - Running benchmarks and collecting statistics
 * - Performance reporting and analysis
 * - Optimizing test performance through caching and parallel execution
 */

import { performance } from 'perf_hooks';

// Re-export specific utilities from existing files
export { 
  executeTestsInParallel,
  parallelDescribe,
  optimizeResourceIntensiveOperation,
  clearOperationCache,
  createLazyTestValue,
