/**
 * Performance Reporting System
 */

/**
 * Interface for performance metrics
 */
export interface PerformanceMetric {
  name: string;
  samples: number[];
  threshold?: number;
}

/**
 * Interface for performance metric statistics
 */
export interface PerformanceMetricStats {
  name: string;
  count: number;
  average: number;
  median: number;
  min: number;
  max: number;
  stdDev: number;
  threshold?: number;
  exceedsThreshold: boolean;
  percentiles: Record<string, number>;
}

/**
 * Creates a performance reporter for tracking and analyzing performance metrics
 * 
 * @returns A performance reporter object with methods for recording and analyzing metrics
 * 
 * @example
 * const reporter = createPerformanceReporter();
 * reporter.record('dataProcessing', 150, 200); // Record with threshold
 * reporter.measure('sorting', sortFunction, [largeArray]);
 * const stats = reporter.getStats('dataProcessing');
 * console.log(`Average: ${stats.average}ms, Exceeds threshold: ${stats.exceedsThreshold}`);
 */
export function createPerformanceReporter() {
  const metrics = new Map<string, PerformanceMetric>();

  return {
    /**
     * Records a performance metric
     * @param name Name of the metric
     * @param value Value to record (usually time in ms)
     * @param threshold Optional threshold for warnings
     */
    record(name: string, value: number, threshold?: number): void {
      let metric = metrics.get(name);

      if (!metric) {
        metric = { name, samples: [], threshold };
        metrics.set(name, metric);
      }

      metric.samples.push(value);

      if (threshold !== undefined && metric.threshold === undefined) {
        metric.threshold = threshold;
      }
    },

    /**
     * Gets statistics for a specific metric
     * @param name Name of the metric
     * @returns Statistics for the metric or undefined if not found
     */
    getStats(name: string): PerformanceMetricStats | undefined {
      const metric = metrics.get(name);

      if (!metric || metric.samples.length === 0) {
        return undefined;
      }

      const stats = calculateStatistics(metric.samples);
      const exceedsThreshold = metric.threshold !== undefined && stats.average > metric.threshold;

      return {
        name,
        count: stats.iterations,
        average: stats.average,
        median: stats.median,
        min: stats.min,
        max: stats.max,
        stdDev: stats.stdDev,
        threshold: metric.threshold,
        exceedsThreshold,
        percentiles: stats.percentiles,
      };
    },

    /**
     * Gets statistics for all metrics
     * @returns Array of statistics for all metrics
     */
    getAllStats(): PerformanceMetricStats[] {
      return Array.from(metrics.keys())
        .map(name => this.getStats(name))
        .filter((stats): stats is PerformanceMetricStats => stats !== undefined);
    },

    /**
     * Clears all recorded metrics
     */
    clear(): void {
      metrics.clear();
    },

    /**
     * Measures the execution time of a function and records it
     * @param name Name of the metric
     * @param fn Function to measure
     * @param args Arguments to pass to the function
     * @param threshold Optional threshold for warnings
     * @returns Result of the function
     */
    measure<T, Args extends unknown[]>(
      name: string,
      fn: (...args: Args) => T,
      args: Args,
      threshold?: number
    ): T {
      const { result, executionTime } = measureExecutionTime(fn, ...args);
      this.record(name, executionTime, threshold);
      return result;
    },

    /**
     * Measures the execution time of an async function and records it
     * @param name Name of the metric
     * @param fn Async function to measure
     * @param args Arguments to pass to the function
     * @param threshold Optional threshold for warnings
     * @returns Promise resolving to the result of the function
     */
    async measureAsync<T, Args extends unknown[]>(
      name: string,
      fn: (...args: Args) => Promise<T>,
      args: Args,
      threshold?: number
    ): Promise<T> {
      const { result, executionTime } = await measureAsyncExecutionTime(fn, ...args);
      this.record(name, executionTime, threshold);
      return result;
    },

    /**
     * Generates a performance report in a human-readable format
     * @returns String containing the performance report
     */
    generateReport(): string {
      const stats = this.getAllStats();
      if (stats.length === 0) {
        return 'No performance metrics recorded';
      }

      let report = 'Performance Report\n-----------------\n\n';

      stats.forEach(stat => {
        report += `Metric: ${stat.name}\n`;
        report += `Samples: ${stat.count}\n`;
        report += `Average: ${stat.average.toFixed(2)}ms\n`;
        report += `Min/Max: ${stat.min.toFixed(2)}ms / ${stat.max.toFixed(2)}ms\n`;
        report += `Median: ${stat.median.toFixed(2)}ms\n`;
        report += `StdDev: ${stat.stdDev.toFixed(2)}ms\n`;
        report += `Percentiles: p50=${stat.percentiles.p50.toFixed(2)}ms, p95=${stat.percentiles.p95.toFixed(2)}ms, p99=${stat.percentiles.p99.toFixed(2)}ms\n`;

        if (stat.threshold !== undefined) {
          report += `Threshold: ${stat.threshold.toFixed(2)}ms (${stat.exceedsThreshold ? 'EXCEEDED' : 'OK'})\n`;
        }

        report += '\n';
      });

      return report;
    }
  };
}

/**
 * Port Manager Utility for Test Environment
 *
 * This utility helps manage port allocation for services in tests to prevent port conflicts.
 * It keeps track of used ports, provides methods to get available ports, and ensures proper cleanup.
 */

interface PortAllocation {
  port: number;
  service: string;
  timestamp: number;
}

export class PortManager {
  private static usedPorts = new Map<number, PortAllocation>();
  private static portBlacklist = new Set([3000, 8080, 8000, 4173, 5173]); // Common ports to avoid
  private static MIN_PORT = 10000;
  private static MAX_PORT = 65535;
  private static PORT_EXPIRY_MS = 30 * 60 * 1000; // 30 minutes

  /**
   * Get an available port for a service
   * @param serviceName Name of the service requesting the port
   * @returns Available port number
   */
  static getAvailablePort(serviceName = 'unknown'): number {
    // Clean up expired ports first
    this.cleanupExpiredPorts();

    let port = this.generateRandomPort();
    while (this.usedPorts.has(port) || this.portBlacklist.has(port)) {
      port = this.generateRandomPort();
    }

    this.usedPorts.set(port, {
      port,
      service: serviceName,
      timestamp: Date.now(),
    });

    console.log(`[PortManager] Allocated port ${port} for service ${serviceName}`);
    return port;
  }

  /**
   * Release a port when it's no longer needed
   * @param port Port number to release
   */
  static releasePort(port: number): void {
    if (this.usedPorts.has(port)) {
      const allocation = this.usedPorts.get(port);
      console.log(`[PortManager] Released port ${port} for service ${allocation?.service}`);
      this.usedPorts.delete(port);
    }
  }

  /**
   * Generate a random port number within the allowed range
   * @returns Random port number
   */
  private static generateRandomPort(): number {
    return Math.floor(Math.random() * (this.MAX_PORT - this.MIN_PORT) + this.MIN_PORT);
  }

  /**
   * Clean up expired port allocations
   */
  private static cleanupExpiredPorts(): void {
    const now = Date.now();
    const expiredPorts: number[] = [];

    this.usedPorts.forEach((allocation, port) => {
      if (now - allocation.timestamp > this.PORT_EXPIRY_MS) {
        expiredPorts.push(port);
      }
    });

    expiredPorts.forEach(port => {
      const allocation = this.usedPorts.get(port);
      console.log(`[PortManager] Expired port ${port} for service ${allocation?.service}`);
      this.usedPorts.delete(port);
    });
  }

  /**
   * Reset the port manager (typically used in global test teardown)
   */
  static reset(): void {
    console.log(`[PortManager] Reset - cleared ${this.usedPorts.size} port allocations`);
    this.usedPorts.clear();
  }

  /**
   * Get all currently allocated ports (for debugging)
   * @returns Array of port allocations
   */
  static getAllocatedPorts(): PortAllocation[] {
    return Array.from(this.usedPorts.values());
  }

  /**
   * Check if a specific port is available
   * @param port Port number to check
   * @returns True if the port is available
   */
  static isPortAvailable(port: number): boolean {
    return !this.usedPorts.has(port) && !this.portBlacklist.has(port);
  }
}

/**
 * Test Performance Optimization Utilities
 *
 * This file contains utilities for optimizing test performance, including:
 * - Parallel test execution
 * - Test execution time reduction
 * - Resource-intensive operation optimization
 */

/**
 * Interface for a test task that can be run in parallel
 */
export interface ParallelTestTask<T> {
  name: string;
  task: () => Promise<T>;
}

/**
 * Interface for parallel test execution options
 */
export interface ParallelExecutionOptions {
  /**
   * Maximum number of concurrent tasks
   * @default 4
   */
  concurrency?: number;

  /**
   * Whether to continue execution if a task fails
   * @default false
   */
  continueOnError?: boolean;

  /**
   * Timeout for each task in milliseconds
   * @default 5000
   */
  taskTimeout?: number;

  /**
   * Whether to log progress
   * @default false
   */
  logProgress?: boolean;
}

/**
 * Interface for parallel test execution results
 */
export interface ParallelExecutionResults<T> {
  /**
   * Results of successful tasks
   */
  results: Record<string, T>;

  /**
   * Errors from failed tasks
   */
  errors: Record<string, Error>;

  /**
   * Total execution time in milliseconds
   */
  totalExecutionTimeMs: number;

  /**
   * Number of successful tasks
   */
  successCount: number;

  /**
   * Number of failed tasks
   */
  failureCount: number;
}

/**
 * Executes test tasks in parallel with controlled concurrency
 *
 * @param tasks Array of tasks to execute
 * @param options Execution options
 * @returns Results of parallel execution
 */
export async function executeTestsInParallel<T>(
  tasks: ParallelTestTask<T>[],
  options: ParallelExecutionOptions = {}
): Promise<ParallelExecutionResults<T>> {
  const {
    concurrency = 4,
    continueOnError = false,
    taskTimeout = 5000,
    logProgress = false,
  } = options;

  const results: Record<string, T> = {};
  const errors: Record<string, Error> = {};
  const startTime = performance.now();

  // Create a queue of tasks
  const queue = [...tasks];
  const inProgress = new Set<string>();
  let successCount = 0;
  let failureCount = 0;

  // Process the queue with controlled concurrency
  const processQueue = async (): Promise<void> => {
    if (queue.length === 0 || inProgress.size >= concurrency) {
      return;
    }

    const { name, task } = queue.shift()!;
    inProgress.add(name);

    if (logProgress) {
      console.log(`Starting task: ${name}`);
    }

    try {
      // Execute the task with a timeout
      const result = await Promise.race([
        task(),
        new Promise<never>((_, reject) => {
          setTimeout(
            () => reject(new Error(`Task ${name} timed out after ${taskTimeout}ms`)),
            taskTimeout
          );
        }),
      ]);

      results[name] = result;
      successCount++;

      if (logProgress) {
        console.log(`Completed task: ${name}`);
      }
    } catch (error) {
      errors[name] = error as Error;
      failureCount++;

      if (logProgress) {
        console.error(`Failed task: ${name}`, error);
      }

      if (!continueOnError) {
        // Clear the queue to stop processing
        queue.length = 0;
      }
    } finally {
      inProgress.delete(name);

      // Process the next task
      await processQueue();
    }
  };

  // Start processing tasks up to the concurrency limit
  const initialProcessors = Math.min(concurrency, tasks.length);
  await Promise.all(Array.from({ length: initialProcessors }, () => processQueue()));

  const endTime = performance.now();

  return {
    results,
    errors,
    totalExecutionTimeMs: endTime - startTime,
    successCount,
    failureCount,
  };
}

/**
 * Creates a test suite that runs tests in parallel
 *
 * @param suiteName Name of the test suite
 * @param tests Object containing test functions
 * @param options Parallel execution options
 */
export function parallelDescribe(
  suiteName: string,
  tests: Record<string, () => Promise<void>>,
  options: ParallelExecutionOptions = {}
): void {
  describe(suiteName, () => {
    it('runs tests in parallel', async () => {
      const tasks = Object.entries(tests).map(([name, task]) => ({
        name,
        task,
      }));

      const results = await executeTestsInParallel(tasks, options);

      // Report any failures
      if (results.failureCount > 0) {
        const errorMessages = Object.entries(results.errors)
          .map(([name, error]) => `${name}: ${error.message}`)
          .join('\n');

        throw new Error(`${results.failureCount} parallel tests failed:\n${errorMessages}`);
      }
    });
  });
}

/**
 * Options for optimizing resource-intensive operations
 */
export interface ResourceOptimizationOptions {
  /**
   * Whether to use a worker thread for the operation
   * @default false
   */
  useWorker?: boolean;

  /**
   * Whether to cache the result
   * @default true
   */
  cacheResult?: boolean;

  /**
   * Time-to-live for cached results in milliseconds
   * @default 60000 (1 minute)
   */
  cacheTTL?: number;
}

// Type for cached operation results
interface CachedOperation<T> {
  result: T;
  timestamp: number;
}

// Cache for optimized operations
const operationCache = new Map<string, CachedOperation<unknown>>();

/**
 * Optimizes a resource-intensive operation by using caching and/or worker threads
 *
 * @param operationId Unique identifier for the operation
 * @param operation Function that performs the operation
 * @param options Optimization options
 * @returns Result of the operation
 */
export async function optimizeResourceIntensiveOperation<T>(
  operationId: string,
  operation: () => Promise<T> | T,
  options: ResourceOptimizationOptions = {}
): Promise<T> {
  const { cacheResult = true, cacheTTL = 60000 } = options;

  // Check cache first if enabled
  if (cacheResult) {
    const cached = operationCache.get(operationId);
    if (cached && performance.now() - cached.timestamp < cacheTTL) {
      return cached.result as T;
    }
  }

  // Execute the operation
  const result = await operation();

  // Cache the result if enabled
  if (cacheResult) {
    operationCache.set(operationId, {
      result,
      timestamp: performance.now(),
    });
  }

  return result;
}

/**
 * Clears the operation cache
 *
 * @param operationId Optional operation ID to clear specific cache entry
 */
export function clearOperationCache(operationId?: string): void {
  if (operationId) {
    operationCache.delete(operationId);
  } else {
    operationCache.clear();
  }
}

/**
 * Options for lazy initialization in tests
 */
export interface LazyInitOptions {
  /**
   * Whether to initialize immediately or on first use
   * @default false (lazy initialization)
   */
  immediate?: boolean;
}

/**
 * Creates a lazily initialized value for tests
 *
 * @param factory Factory function that creates the value
 * @param options Initialization options
 * @returns Object with get method to access the value
 */
export function createLazyTestValue<T>(
  factory: () => T,
  options: LazyInitOptions = {}
): { get: () => T; reset: () => void } {
  const { immediate = false } = options;

  let instance: T | undefined;
  let initialized = false;

  if (immediate) {
    instance = factory();
    initialized = true;
  }

  return {
    get: () => {
      if (!initialized) {
        instance = factory();
        initialized = true;
      }
      return instance as T;
    },
    reset: () => {
      instance = undefined;
      initialized = false;
    },
  };
}

/**
 * Runs setup operations in parallel to reduce test initialization time
 *
 * @param setupOperations Object containing setup operations
 * @returns Object containing the results of setup operations
 */
export async function parallelSetup<T extends Record<string, unknown>>(setupOperations: {
  [K in keyof T]: () => Promise<T[K]>;
}): Promise<T> {
  const keys = Object.keys(setupOperations) as Array<keyof T>;
  const tasks = keys.map(key => ({
    name: key as string,
    task: setupOperations[key],
  }));

  const { results } = await executeTestsInParallel(tasks);

  return results as unknown as T;
}

// Type for module implementation
type ModuleImplementation = Record<string, (...args: unknown[]) => unknown>;

/**
 * Mocks expensive operations to improve test performance
 *
 * Note: This function uses dynamic imports which may not work in all environments.
 * It's primarily intended for use in Node.js test environments.
 *
 * @param mockImplementations Object containing mock implementations
 * @returns Function to restore original implementations
 */
export function mockExpensiveOperations(mockImplementations: ModuleImplementation): () => void {
  const originalImplementations: Record<string, unknown> = {};

  // Apply mock implementations
  Object.entries(mockImplementations).forEach(async ([path, implementation]) => {
    const [modulePath, exportName] = path.split('#');

    // Store original implementation
    try {
      // Use dynamic import instead of require
      const module = await import(modulePath);
      originalImplementations[path] = module[exportName];

      // Apply mock
      module[exportName] = implementation;
    } catch (error) {
      console.error(`Failed to mock ${path}:`, error);
    }
  });

  // Return function to restore original implementations
  return () => {
    Object.entries(originalImplementations).forEach(async ([path, implementation]) => {
      const [modulePath, exportName] = path.split('#');

      try {
        // Use dynamic import instead of require
        const module = await import(modulePath);
        module[exportName] = implementation;
      } catch (error) {
        console.error(`Failed to restore ${path}:`, error);
      }
    });
  };
}

/**
 * Skips expensive setup operations in tests when they're not needed
 *
 * @param setupFn Function that performs the setup
 * @param condition Function that determines if setup is needed
 * @returns Result of the setup function or undefined if skipped
 */
export function conditionalSetup<T>(setupFn: () => T, condition: () => boolean): T | undefined {
  if (condition()) {
    return setupFn();
  }
  return undefined;
}

/**
 * Measures memory usage during test execution
 *
 * @param testFn Function that performs the test
 * @returns Object containing the test result and memory usage information
 */
export async function measureMemoryUsage<T>(
  testFn: () => Promise<T> | T
): Promise<{ result: T; memoryUsageMB: number }> {
  // Capture memory usage before the test
  const memoryBefore = process.memoryUsage?.() || { heapUsed: 0 };

  // Run the test
  const result = await testFn();

  // Capture memory usage after the test
  const memoryAfter = process.memoryUsage?.() || { heapUsed: 0 };

  // Calculate memory usage difference in MB
  const memoryUsageMB = (memoryAfter.heapUsed - memoryBefore.heapUsed) / (1024 * 1024);

  return {
    result,
    memoryUsageMB,
  };
}

/**
 * @file TypeUtils.ts
 * Utility types and functions for improved type safety across the codebase
 *
 * This file provides:
 * 1. Generic type utilities to reduce the need for "as unknown as" casts
 * 2. Type guards for common operations
 * 3. Helper types for event handling and data processing
 * 4. Unified interfaces that work in both production and test code
 */

/**
 * Type-safe cast utility - use this instead of "as unknown as" when possible
 * Generic function that provides a safer way to cast from one type to another
 * when you need to bypass TypeScript's type system but maintain type safety.
 */
export function typeCast<T, U>(value: T): U {
  return value as unknown as U;
}

/**
 * Runtime type checking utilities - use these to verify types at runtime
 */

/**
 * Helper to check if a value is a non-null object
 */
export function isObject(value: unknown): value is Record<string, unknown> {
  return typeof value === 'object' && value !== null;
}

/**
 * Helper to check if a value has a specific property
 */
export function hasProperty<K extends string>(
  value: unknown,
  property: K
): value is { [P in K]: unknown } {
  return isObject(value) && property in value;
}

/**
 * Helper to check if a value has specific properties
 */
export function hasProperties<K extends string>(
  value: unknown,
  properties: K[]
): value is { [P in K]: unknown } {
  if (!isObject(value)) return false;
  return properties.every(prop => prop in value);
}

/**
 * Generic class property type - gets the type of a property on a class
 */
export type PropertyType<T, K extends keyof T> = T[K];

/**
 * Event handler types - unify event handling patterns
 */
export type EventHandler<T> = (event: T) => void;
export type AsyncEventHandler<T> = (event: T) => Promise<void>;
export type EventUnsubscribe = () => void;

/**
 * EventEmitter interface that works for both testing and production
 */
export interface EventEmitter<T> {
  emit(event: T): void;
  on(handler: EventHandler<T>): EventUnsubscribe;
  off(handler: EventHandler<T>): void;
}

/**
 * Manager interface base - for consistent manager implementations
 */
export interface Manager {
  initialize(): void | Promise<void>;
  dispose(): void;
}

/**
 * Types for dealing with nullable values
 */
export type Nullable<T> = T | null;
export type Optional<T> = T | undefined;

/**
 * Type safe extraction from data objects with unknown structure
 */
export function getPropertySafe<T>(obj: unknown, path: string, defaultValue: T): T {
  if (!isObject(obj)) return defaultValue;

  const parts = path.split('.');
  let current: unknown = obj;

  for (const part of parts) {
    if (!isObject(current) || !(part in current)) {
      return defaultValue;
    }
    current = current[part];
  }

  return current as unknown as T;
}

/**
 * Narrowing helper for discriminated unions
 */
export function isOfType<T, K extends string>(
  obj: unknown,
  discriminator: K,
  value: string
): obj is T & { [P in K]: string } {
  return (
    isObject(obj) &&
    hasProperty(obj, discriminator) &&
    typeof obj[discriminator] === 'string' &&
    obj[discriminator] === value
  );
}

/**
 * Type-safe partial - preserves required vs optional properties
 */
export type StrictPartial<T> = {
  [P in keyof T]?: T[P] extends Record<string, unknown> ? StrictPartial<T[P]> : T[P];
};

/**
 * Data transformation utilities - for safely working with any type
 */
export function safeMapRecord<T extends Record<string, unknown>, U>(
  record: T,
  mapFn: (value: unknown, key: string) => U
): Record<string, U> {
  return Object.fromEntries(Object.entries(record).map(([key, value]) => [key, mapFn(value, key)]));
}

/**
 * Safe array mapper - ensures type safety when mapping arrays
 */
export function safeMap<T, U>(
  array: unknown[],
  predicate: (item: unknown) => item is T,
  mapFn: (item: T) => U
): U[] {
  return array.filter(predicate).map(mapFn);
}

/**
 * Type guard factory - creates type guards for specific interfaces
 */
export function createTypeGuard<T>(
  properties: Array<keyof T>,
  typeChecks: Partial<Record<keyof T, (val: unknown) => boolean>> = {}
): (obj: unknown) => obj is T {
  return (obj: unknown): obj is T => {
    if (!isObject(obj)) return false;

    // Check that all required properties exist
    if (!properties.every(prop => prop in obj)) return false;

    // Apply custom type checks if provided
    return Object.entries(typeChecks).every(([prop, check]) => {
      if (!(prop in obj)) return false;
      if (check && typeof check === 'function') {
        return check(obj[prop]);
      }
      return true;
    });
  };
}

/**
 * Type-safe event mapper - converts event data safely
 */
export function mapEventData<T, U>(
  eventData: unknown,
  predicate: (data: unknown) => data is T,
  mapper: (data: T) => U
): U | undefined {
  if (predicate(eventData)) {
    return mapper(eventData);
  }
  return undefined;
}

/**
 * Type-safe object property accessor
 */
export function getProperty<T, K extends keyof T>(obj: T, property: K): T[K] {
  return obj[property];
}

/**
 * Type-safe JSON parsing with a fallback
 */
export function safeJsonParse<T>(json: string, fallback: T): T {
  try {
    return JSON.parse(json) as T;
  } catch (error) {
    return fallback;
  }
}

/**
 * Creates a type-safe function that casts to a specific enum
 */
export function createEnumParser<T extends Record<string, string | number>>(
  enumObj: T
): (value: string) => T[keyof T] | undefined {
  const validValues = Object.values(enumObj);
  return (value: string) => {
    const enumValue = enumObj[value as keyof T];
    if (enumValue !== undefined) return enumValue;

    // Check if the value itself matches any enum value
    if (validValues.includes(value as T[keyof T])) {
      return value as T[keyof T];
    }

    return undefined;
  };
}

/**
 * Deep readonly type for immutable data
 */
export type DeepReadonly<T> = T extends (infer R)[]
  ? ReadonlyArray<DeepReadonly<R>>
  : T extends (...args: unknown[]) => unknown
    ? T
    : T extends object
      ? { readonly [P in keyof T]: DeepReadonly<T[P]> }
      : T;

import { z } from 'zod';

/**
 * Base configuration value types that can be represented in JSON
 */
export type ConfigValuePrimitive = string | number | boolean | null;
export type ConfigValue =
  | ConfigValuePrimitive
  | ConfigValuePrimitive[]
  | Record<string, ConfigValuePrimitive>
  | Record<string, ConfigValuePrimitive>[];

/**
 * Feature flag status enum
 */
export enum FeatureStatus {
  ENABLED = 'enabled',
  DISABLED = 'disabled',
  PREVIEW = 'preview',
  EXPERIMENTAL = 'experimental',
  BETA = 'beta',
  DEPRECATED = 'deprecated',
}

/**
 * Feature flag targeting conditions
 */
export interface FeatureTargeting {
  userRoles?: string[];
  environments?: string[];
  percentageRollout?: number;
  dateRange?: {
    start?: string;
    end?: string;
  };
  customRules?: Record<string, any>;
}

/**
 * Runtime feature flag definition
 */
export interface FeatureFlag {
  key: string;
  name: string;
  description: string;
  status: FeatureStatus;
  defaultValue: boolean;
  targeting?: FeatureTargeting;
  metadata?: Record<string, ConfigValue>;
}

/**
 * Configuration category for organizing config values
 */
export interface ConfigCategory {
  id: string;
  name: string;
  description: string;
  items: ConfigItem[];
}

/**
 * Configuration item with schema validation
 */
export interface ConfigItem<T extends z.ZodType = z.ZodType> {
  key: string;
  name: string;
  description: string;
  schema: T;
  defaultValue: z.infer<T>;
  category?: string;
  tags?: string[];
  metadata?: Record<string, ConfigValue>;
  isSecret?: boolean;
  isRequired?: boolean;
  source?: string;
}

/**
 * Configuration validation error
 */
export interface ConfigValidationError {
  key: string;
  message: string;
  path?: string[];
  value?: any;
}

/**
 * Configuration validation result
 */
export interface ConfigValidationResult {
  valid: boolean;
  errors: ConfigValidationError[];
}

/**
 * Configuration manager options
 */
export interface ConfigManagerOptions {
  validateOnAccess?: boolean;
  strictMode?: boolean;
  logErrors?: boolean;
  onValidationError?: (errors: ConfigValidationError[]) => void;
  onConfigChange?: (key: string, newValue: any, oldValue: any) => void;
}

/**
 * Type-safe configuration manager class
 */
export class TypeSafeConfigManager {
  private configItems: Map<string, ConfigItem> = new Map();
  private featureFlags: Map<string, FeatureFlag> = new Map();
  private configValues: Map<string, any> = new Map();
  private categories: Map<string, ConfigCategory> = new Map();
  private options: ConfigManagerOptions;
  private userContext: Record<string, any> = {};

  constructor(options: ConfigManagerOptions = {}) {
    this.options = {
      validateOnAccess: true,
      strictMode: false,
      logErrors: true,
      ...options,
    };
  }

  /**
   * Register a configuration item with type-safe schema
   */
  registerConfig<T extends z.ZodType>(config: ConfigItem<T>): void {
    if (this.configItems.has(config.key)) {
      throw new Error(`Config with key "${config.key}" is already registered`);
    }

    this.configItems.set(config.key, config);
    this.configValues.set(config.key, config.defaultValue);

    // Add to category if specified
    if (config.category && this.categories.has(config.category)) {
      const category = this.categories.get(config.category);
      if (category) {
        category.items.push(config);
      }
    }
  }

  /**
   * Register multiple configuration items
   */
  registerConfigs(configs: ConfigItem[]): void {
    configs.forEach(config => this.registerConfig(config as any));
  }

  /**
   * Register a category for organizing config items
   */
  registerCategory(category: ConfigCategory): void {
    if (this.categories.has(category.id)) {
      throw new Error(`Category with id "${category.id}" is already registered`);
    }
    this.categories.set(category.id, { ...category, items: [] });
  }

  /**
   * Register a feature flag
   */
  registerFeature(feature: FeatureFlag): void {
    if (this.featureFlags.has(feature.key)) {
      throw new Error(`Feature flag with key "${feature.key}" is already registered`);
    }
    this.featureFlags.set(feature.key, feature);
  }

  /**
   * Register multiple feature flags
   */
  registerFeatures(features: FeatureFlag[]): void {
    features.forEach(feature => this.registerFeature(feature));
  }

  /**
   * Set user context for feature flag targeting
   */
  setUserContext(context: Record<string, any>): void {
    this.userContext = context;
  }

  /**
   * Get the value of a configuration item with type safety
   */
  get<T extends z.ZodType>(key: string): z.infer<T> | undefined {
    const config = this.configItems.get(key) as ConfigItem<T> | undefined;
    if (!config) {
      if (this.options.strictMode) {
        throw new Error(`Config with key "${key}" is not registered`);
      }
      return undefined;
    }

    const value = this.configValues.get(key);

    // Validate on access if enabled
    if (this.options.validateOnAccess) {
      const validation = config.schema.safeParse(value);
      if (!validation.success) {
        const errors = this.formatZodErrors(key, validation.error);

        if (this.options.logErrors) {
          console.error(`Config validation error for "${key}":`, errors);
        }

        if (this.options.onValidationError) {
          this.options.onValidationError(errors);
        }

        if (this.options.strictMode) {
          throw new Error(`Config validation failed for "${key}": ${errors[0]?.message}`);
        }

        return config.defaultValue;
      }
    }

    return value as z.infer<T>;
  }

  /**
   * Set a configuration value with validation
   */
  set<T extends z.ZodType>(key: string, value: z.infer<T>): ConfigValidationResult {
    const config = this.configItems.get(key) as ConfigItem<T> | undefined;
    if (!config) {
      if (this.options.strictMode) {
        throw new Error(`Config with key "${key}" is not registered`);
      }
      return { valid: false, errors: [{ key, message: `Config not registered` }] };
    }

    const oldValue = this.configValues.get(key);
    const validation = config.schema.safeParse(value);

    if (!validation.success) {
      const errors = this.formatZodErrors(key, validation.error);
      if (this.options.logErrors) {
        console.error(`Config validation error for "${key}":`, errors);
      }
      if (this.options.onValidationError) {
        this.options.onValidationError(errors);
      }
      return { valid: false, errors };
    }

    // Update the value
    this.configValues.set(key, validation.data);

    // Call change handler if provided
    if (this.options.onConfigChange) {
      this.options.onConfigChange(key, validation.data, oldValue);
    }

    return { valid: true, errors: [] };
  }

  /**
   * Check if a feature flag is enabled
   */
  isFeatureEnabled(key: string): boolean {
    const feature = this.featureFlags.get(key);
    if (!feature) {
      if (this.options.strictMode) {
        throw new Error(`Feature flag "${key}" is not registered`);
      }
      return false;
    }

    // If feature is disabled or deprecated, it's always disabled
    if (feature.status === FeatureStatus.DISABLED || feature.status === FeatureStatus.DEPRECATED) {
      return false;
    }

    // Check targeting rules if present
    if (feature.targeting) {
      // Check user roles
      if (
        feature.targeting.userRoles &&
        feature.targeting.userRoles.length > 0 &&
        this.userContext.role
      ) {
        if (!feature.targeting.userRoles.includes(this.userContext.role)) {
          return false;
        }
      }

      // Check environments
      if (
        feature.targeting.environments &&
        feature.targeting.environments.length > 0 &&
        this.userContext.environment
      ) {
        if (!feature.targeting.environments.includes(this.userContext.environment)) {
          return false;
        }
      }

      // Check percentage rollout
      if (feature.targeting.percentageRollout !== undefined) {
        const userId = this.userContext.id || '';
        // Simple deterministic percentage rollout based on user ID
        const hash = this.simpleHash(key + userId);
        const percentage = hash % 100;
        if (percentage >= feature.targeting.percentageRollout) {
          return false;
        }
      }

      // Check date range
      if (feature.targeting.dateRange) {
        const now = new Date();
        if (feature.targeting.dateRange.start) {
          const startDate = new Date(feature.targeting.dateRange.start);
          if (now < startDate) {
            return false;
          }
        }
        if (feature.targeting.dateRange.end) {
          const endDate = new Date(feature.targeting.dateRange.end);
          if (now > endDate) {
            return false;
          }
        }
      }
    }

    return feature.defaultValue;
  }

  /**
   * Validate all configuration values
   */
  validateAllConfigs(): ConfigValidationResult {
    const errors: ConfigValidationError[] = [];

    this.configItems.forEach((config, key) => {
      const value = this.configValues.get(key);
      const validation = config.schema.safeParse(value);

      if (!validation.success) {
        errors.push(...this.formatZodErrors(key, validation.error));
      }
    });

    const valid = errors.length === 0;
    if (!valid && this.options.logErrors) {
      console.error('Configuration validation errors:', errors);
    }

    if (!valid && this.options.onValidationError) {
      this.options.onValidationError(errors);
    }

    return { valid, errors };
  }

  /**
   * Export all configuration values
   */
  exportConfig(): Record<string, any> {
    const result: Record<string, any> = {};
    this.configValues.forEach((value, key) => {
      result[key] = value;
    });
    return result;
  }

  /**
   * Export all feature flags with their status
   */
  exportFeatures(): Record<string, boolean> {
    const result: Record<string, boolean> = {};
    this.featureFlags.forEach((feature, key) => {
      result[key] = this.isFeatureEnabled(key);
    });
    return result;
  }

  /**
   * Import configuration from an object
   */
  importConfig(config: Record<string, any>): ConfigValidationResult {
    const errors: ConfigValidationError[] = [];

    Object.entries(config).forEach(([key, value]) => {
      const configItem = this.configItems.get(key);
      if (!configItem) {
        if (this.options.strictMode) {
          errors.push({ key, message: `Config not registered` });
        }
        return;
      }

      const validation = configItem.schema.safeParse(value);
      if (!validation.success) {
        errors.push(...this.formatZodErrors(key, validation.error));
      } else {
        this.configValues.set(key, validation.data);
      }
    });

    const valid = errors.length === 0;
    if (!valid && this.options.logErrors) {
      console.error('Configuration import errors:', errors);
    }

    if (!valid && this.options.onValidationError) {
      this.options.onValidationError(errors);
    }

    return { valid, errors };
  }

  /**
   * Get a list of all config items
   */
  getConfigItems(): ConfigItem[] {
    return Array.from(this.configItems.values());
  }

  /**
   * Get a list of all categories
   */
  getCategories(): ConfigCategory[] {
    return Array.from(this.categories.values());
  }

  /**
   * Get a list of all feature flags
   */
  getFeatureFlags(): FeatureFlag[] {
    return Array.from(this.featureFlags.values());
  }

  /**
   * Helper to format Zod errors into ConfigValidationErrors
   */
  private formatZodErrors(key: string, error: z.ZodError): ConfigValidationError[] {
    return error.errors.map(err => ({
      key,
      message: err.message,
      path: err.path.map(p => p.toString()),
      value: undefined,
    }));
  }

  /**
   * Simple hash function for percentage rollout
   */
  private simpleHash(str: string): number {
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
      const char = str.charCodeAt(i);
      hash = (hash << 5) - hash + char;
      hash = hash & hash; // Convert to 32bit integer
    }
    return Math.abs(hash);
  }
}

/**
 * Create a type-safe config manager instance with default options
 */
export function createConfigManager(options?: ConfigManagerOptions): TypeSafeConfigManager {
  return new TypeSafeConfigManager(options);
}

/**
 * Helper to create a typed config item
 */
export function createConfigItem<T extends z.ZodType>(
  key: string,
  schema: T,
  defaultValue: z.infer<T>,
  options: Omit<ConfigItem<T>, 'key' | 'schema' | 'defaultValue'> = { name: '', description: '' }
): ConfigItem<T> {
  return {
    key,
    schema,
    defaultValue,
    name: options.name || key,
    description: options.description || '',
    category: options.category,
    tags: options.tags || [],
    metadata: options.metadata || {},
    isSecret: options.isSecret || false,
    isRequired: options.isRequired || false,
    source: options.source,
  };
}

/**
 * Helper to create a feature flag
 */
export function createFeatureFlag(
  key: string,
  defaultValue: boolean,
  options: Omit<FeatureFlag, 'key' | 'defaultValue'> = {} as any
): FeatureFlag {
  return {
    key,
    defaultValue,
    name: options.name || key,
    description: options.description || '',
    status: options.status || FeatureStatus.DISABLED,
    targeting: options.targeting,
    metadata: options.metadata,
  };
}

/**
 * React hook for type-safe configuration
 */
export function useTypedConfig<T extends z.ZodType>(
  configManager: TypeSafeConfigManager,
  key: string,
  defaultValue?: z.infer<T>
): z.infer<T> {
  const value = configManager.get<T>(key);
  return value !== undefined ? value : (defaultValue as z.infer<T>);
}

/**
 * React hook for feature flags
 */
export function useFeatureFlag(
  configManager: TypeSafeConfigManager,
  key: string,
  defaultValue = false
): boolean {
  return configManager.isFeatureEnabled(key) || defaultValue;
}

/**
 * @file SharedEventTypes.ts
 * Shared event types and interfaces for both production and test code
 *
 * This file provides unified event types to:
 * 1. Eliminate "as unknown as" casts in event handling
 * 2. Create consistent interfaces for mocks and real implementations
 * 3. Provide type-safe event creation and handling
 */

import { EventHandler as BaseEventHandler, EventUnsubscribe } from '../TypeUtils';

/**
 * Base event interface that all events should extend
 */
export interface BaseEvent {
  type: string;
  timestamp: number;
  data?: unknown;
}

/**
 * Generic typed event interface
 */
export interface TypedEvent<T extends string, D = unknown> extends BaseEvent {
  type: T;
  data?: D;
}

/**
 * Event Emitter interface compatible with both production and test code
 */
export interface EventEmitter<E extends BaseEvent = BaseEvent> {
  emit(event: E): void;
  on(eventType: string, handler: EventHandler<E>): EventUnsubscribe;
  off(eventType: string, handler: EventHandler<E>): void;
}

/**
 * Event Bus interface for more complex event routing
 */
export interface EventBus<E extends BaseEvent = BaseEvent> extends EventEmitter<E> {
  subscribe(eventType: string, handler: EventHandler<E>): EventUnsubscribe;
  unsubscribe(eventType: string, handler: EventHandler<E>): void;
  subscribeToMultiple(eventTypes: string[], handler: EventHandler<E>): EventUnsubscribe;
  clear(): void;
}

/**
 * Mock Event Bus for testing - implements the same interface
 */
export interface MockEventBus<E extends BaseEvent = BaseEvent> extends EventBus<E> {
  getEmittedEvents(): E[];
  getSubscriptions(): Map<string, Array<EventHandler<E>>>;
  reset(): void;
}

/**
 * Type-safe event creator - ensures proper event structure
 */
export function createEvent<T extends string, D>(type: T, data?: D): TypedEvent<T, D> {
  return {
    type,
    timestamp: Date.now(),
    data,
  };
}

/**
 * Type-safe event data extractor - safely extracts data from events
 */
export function getEventData<T>(event: BaseEvent): T | undefined {
  return event.data as T | undefined;
}

/**
 * Event type guard - checks if an event is of a specific type
 */
export function isEventOfType<T extends string>(event: BaseEvent, type: T): event is TypedEvent<T> {
  return event.type === type;
}

/**
 * Event data type guard - checks if event data conforms to a specific shape
 */
export function hasEventData<T>(
  event: BaseEvent,
  predicate: (data: unknown) => data is T
): event is BaseEvent & { data: T } {
  return event.data !== undefined && predicate(event.data);
}

/**
 * Event handler with type filtering - only calls handler for matching event types
 */
export function createTypedEventHandler<T extends string, E extends BaseEvent, D>(
  type: T,
  handler: (data: D, event: E) => void
): EventHandler<E> {
  return (event: E) => {
    if (event.type === type && event.data !== undefined) {
      handler(event.data as D, event);
    }
  };
}

/**
 * Common event object pattern shared across the application
 */
export interface EventObject<T extends string = string> {
  eventType: T;
  payload: unknown;
}

/**
 * Generic event map interface for strongly typed event data
 */
export interface EventDataMap {
  [eventType: string]: unknown;
}

/**
 * Type-safe event subscription helper
 */
export function typedSubscribe<
  E extends BaseEvent,
  T extends string,
  M extends EventDataMap,
  K extends keyof M & string,
>(
  bus: EventBus<E>,
  eventType: K,
  handler: (data: M[K], event: TypedEvent<K, M[K]>) => void
): EventUnsubscribe {
  const wrappedHandler: EventHandler<E> = (event: E) => {
    if (event.type === eventType) {
      handler(event.data as M[K], event as unknown as TypedEvent<K, M[K]>);
    }
  };

  return bus.subscribe(eventType, wrappedHandler);
}

/**
 * Type-safe event emission helper
 */
export function typedEmit<E extends BaseEvent, M extends EventDataMap, K extends keyof M & string>(
  bus: EventEmitter<E>,
  eventType: K,
  data: M[K]
): void {
  const event: TypedEvent<K, M[K]> = {
    type: eventType,
    timestamp: Date.now(),
    data,
  };

  bus.emit(event as unknown as E);
}

// Re-export these types
export type EventHandler<T> = BaseEventHandler<T>;
export type { EventUnsubscribe };

/**
 * Type-Safe State Management
 * 
 * This module provides utilities for creating strongly typed reducers, actions,
 * and state transitions to enhance the type safety of application state management.
 */

import React, { Reducer, useReducer, useMemo, useState, useCallback, useContext } from 'react';

/**
 * Base Action interface that all action types should extend
 * Enforces inclusion of a type property for action discrimination
 */
export interface Action<T extends string = string> {
  type: T;
}

/**
 * Action with payload interface that extends the base Action
 * Provides proper typing for actions that include data
 */
export interface PayloadAction<T extends string, P> extends Action<T> {
  payload: P;
}

/**
 * Helper type to define a discriminated union of action types based on a record
 * Makes it easy to define all the possible action types for a reducer
 */
export type ActionUnion<T extends Record<string, (...args: any[]) => Action<string>>> = ReturnType<T[keyof T]>;

/**
 * Type for a function that creates a PayloadAction
 */
export type ActionCreator<T extends string, P> = (payload: P) => PayloadAction<T, P>;

/**
 * Type for a function that creates an Action without payload
 */
export type SimpleActionCreator<T extends string> = () => Action<T>;

/**
 * Creates an action creator for actions with payloads
 * 
 * @param type The action type string
 * @returns An action creator function that takes a payload and returns a properly typed action
 */
export function createAction<T extends string, P>(type: T): ActionCreator<T, P> {
  return (payload: P): PayloadAction<T, P> => ({
    type,
    payload
  });
}

/**
 * Creates an action creator for actions without payloads
 * 
 * @param type The action type string
 * @returns An action creator function that returns a properly typed action
 */
export function createSimpleAction<T extends string>(type: T): SimpleActionCreator<T> {
  return (): Action<T> => ({ type });
}

/**
 * Helper to create a record of action creators from a record of action types
 * 
 * @param actionMap Record of action types mapped to their payload types
 * @returns Record of action creators
 */
export function createActionCreators<
  T extends Record<string, any>,
  K extends keyof T = keyof T
>(actionMap: { [P in K]: T[P] extends undefined ? string : [string, T[P]] }) {
  const creators: Record<string, ActionCreator<string, any> | SimpleActionCreator<string>> = {};

  for (const key in actionMap) {
    const value = actionMap[key];
    if (typeof value === 'string') {
      creators[key] = createSimpleAction(value);
    } else {
      creators[key] = createAction<string, T[K]>(value[0]);
    }
  }

  return creators as {
    [P in K]: T[P] extends undefined
      ? SimpleActionCreator<string>
      : ActionCreator<string, T[P]>
  };
}

/**
 * Type-safe reducer builder that enforces action type discrimination
 * Ensures that action types are properly typed and matched in the reducer
 */
export class ReducerBuilder<S, A extends Action = Action> {
  private handlers: Partial<Record<A['type'], (state: S, action: any) => S>> = {};

  /**
   * Add a handler for a specific action type
   * 
   * @param type The action type to handle
   * @param handler The handler function for this action type
   * @returns The builder for chaining
   */
  addCase<T extends A['type'], AC extends Extract<A, { type: T }>>(
    type: T,
    handler: (state: S, action: AC) => S
  ): ReducerBuilder<S, A> {
    this.handlers[type] = handler as any;
    return this;
  }

  /**
   * Add a default handler for unmatched action types
   * 
   * @param handler The default handler function
   * @returns The builder for chaining
   */
  addDefaultCase(handler: (state: S) => S): ReducerBuilder<S, A> {
    this.handlers['DEFAULT'] = (state) => handler(state);
    return this;
  }

  /**
   * Build the reducer function
   * 
   * @returns A properly typed reducer function
   */
  build(): Reducer<S, A> {
    return (state: S, action: A) => {
      const handler = this.handlers[action.type] || this.handlers['DEFAULT'];
      return handler ? handler(state, action) : state;
    };
  }
}

/**
 * Creates a reducer builder for a specific state and action type
 * 
 * @param initialState The initial state (used for type inference)
 * @returns A reducer builder instance
 */
export function createReducer<S, A extends Action = Action>(
  initialState: S
): ReducerBuilder<S, A> {
  return new ReducerBuilder<S, A>();
}

/**
 * Hook to create a state slice with type-safe actions and reducer
 * 
 * @param reducer The reducer function
 * @param initialState The initial state
 * @param actions Object of action creators
 * @returns Tuple of [state, actions with dispatch bound]
 */
export function useTypedReducer<
  S,
  A extends Action,
  AC extends Record<string, (...args: any[]) => A>
>(
  reducer: Reducer<S, A>,
  initialState: S,
  actions: AC
): [S, { [K in keyof AC]: (...args: Parameters<AC[K]>) => void }] {
  const [state, dispatch] = useReducer(reducer, initialState);

  const boundActions = useMemo(() => {
    const result: Record<string, any> = {};
    for (const key in actions) {
      const actionCreator = actions[key];
      result[key] = (...args: any[]) => dispatch(actionCreator(...args));
    }
    return result as { [K in keyof AC]: (...args: Parameters<AC[K]>) => void };
  }, [actions, dispatch]);

  return [state, boundActions];
}

/**
 * Creates a type-safe state slice
 * Combines reducer creation and action binding in one utility
 * 
 * @param initialState The initial state
 * @param reducerMap Map of action type to reducer handlers
 * @param actionMap Map of action creators
 * @returns A hook to use this state slice
 */
export function createTypedStateSlice<
  S,
  AM extends Record<string, (...args: any[]) => Action>,
  A extends ReturnType<AM[keyof AM]>
>(
  initialState: S,
  reducerMap: Record<A['type'], (state: S, action: A) => S>,
  actionMap: AM
) {
  // Create the reducer
  const reducer: Reducer<S, A> = (state = initialState, action) => {
    const handler = reducerMap[action.type];
    return handler ? handler(state, action) : state;
  };

  // Return a hook that provides the state and bound actions
  return () => {
    const [state, dispatch] = useReducer(reducer, initialState);

    const boundActions = useMemo(() => {
      const result: Record<string, any> = {};
      for (const key in actionMap) {
        const actionCreator = actionMap[key];
        result[key] = (...args: any[]) => dispatch(actionCreator(...args));
      }
      return result as { [K in keyof AM]: (...args: Parameters<AM[K]>) => void };
    }, [dispatch]);

    return [state, boundActions] as const;
  };
}

/**
 * Type for a complex state transition function
 * Used for multi-step state transitions that involve side effects
 */
export type StateTransitionFn<S, R = void> = (
  getState: () => S,
  setState: (update: Partial<S> | ((prevState: S) => Partial<S>)) => void
) => Promise<R> | R;

/**
 * Hook to manage complex state transitions in a type-safe way
 * 
 * @param initialState The initial state
 * @returns A tuple of [state, setState, runTransition]
 */
export function useTypedTransitions<S>(initialState: S) {
  const [state, setState] = useState<S>(initialState);

  /**
   * Set state with type checking
   */
  const setTypedState = useCallback((update: Partial<S> | ((prevState: S) => Partial<S>)) => {
    setState(prev => {
      const newParts = typeof update === 'function' ? update(prev) : update;
      return { ...prev, ...newParts };
    });
  }, []);

  /**
   * Run a complex state transition with proper typing
   */
  const runTransition = useCallback(<R = void>(
    transitionFn: StateTransitionFn<S, R>
  ): Promise<R> => {
    const getState = () => state;
    return Promise.resolve(transitionFn(getState, setTypedState));
  }, [state, setTypedState]);

  return [state, setTypedState, runTransition] as const;
}

/**
 * Type-safe selector hook
 * 
 * @param state The state object
 * @param selector Function to select a portion of state
 * @returns The selected state portion
 */
export function useTypedSelector<S, R>(state: S, selector: (state: S) => R): R {
  return useMemo(() => selector(state), [state, selector]);
}

/**
 * Creates a type-safe async reducer
 * Handles loading, error, and success states for async operations
 * 
 * @param actionType Base action type for the async action
 * @param handler Function that processes the action payload
 * @returns An object with action creators and a reducer
 */
export function createAsyncReducer<S, P, R>(
  actionType: string,
  handler: (payload: P) => Promise<R>
) {
  // Define action types
  const PENDING = `${actionType}/pending`;
  const FULFILLED = `${actionType}/fulfilled`;
  const REJECTED = `${actionType}/rejected`;

  // Create action creators
  const actionCreators = {
    pending: createSimpleAction(PENDING),
    fulfilled: createAction<typeof FULFILLED, R>(FULFILLED),
    rejected: createAction<typeof REJECTED, Error>(REJECTED),
    trigger: (payload: P) => async (dispatch: (action: Action) => void) => {
      dispatch(actionCreators.pending());
      try {
        const result = await handler(payload);
        dispatch(actionCreators.fulfilled(result));
        return result;
      } catch (error) {
        dispatch(actionCreators.rejected(error instanceof Error ? error : new Error(String(error))));
        throw error;
      }
    }
  };

  // Create reducer
  type AsyncState = {
    loading: boolean;
    error: Error | null;
    data: R | null;
  };

  type AsyncReducerState = S & AsyncState;

  const reducer = createReducer<AsyncReducerState, Action>(
    { loading: false, error: null, data: null } as AsyncReducerState
  )
    .addCase(PENDING, (state) => ({
      ...state,
      loading: true,
      error: null
    }))
    .addCase(FULFILLED, (state, action) => ({
      ...state,
      loading: false,
      data: action.payload,
      error: null
    }))
    .addCase(REJECTED, (state, action) => ({
      ...state,
      loading: false,
      error: action.payload
    }))
    .build();

  return {
    actions: actionCreators,
    reducer
  };
}

/**
 * Type-safe context state utility
 * Combines the createReducer and useReducer patterns specifically for React contexts
 * 
 * @param reducer The reducer function
 * @param initialState The initial state
 * @returns A context provider and hooks to use the state
 */
export function createTypedContext<
  S,
  A extends Action = Action
>(
  reducer: Reducer<S, A>,
  initialState: S
) {
  const StateContext = React.createContext<S | undefined>(undefined);
  const DispatchContext = React.createContext<React.Dispatch<A> | undefined>(undefined);

  const Provider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [state, dispatch] = useReducer(reducer, initialState);

    return (
      <StateContext.Provider value={state}>
        <DispatchContext.Provider value={dispatch}>
          {children}
        </DispatchContext.Provider>
      </StateContext.Provider>
    );
  };

  const useStateContext = () => {
    const context = useContext(StateContext);
    if (context === undefined) {
      throw new Error('useStateContext must be used within a Provider');
    }
    return context;
  };

  const useDispatchContext = () => {
    const context = useContext(DispatchContext);
    if (context === undefined) {
      throw new Error('useDispatchContext must be used within a Provider');
    }
    return context;
  };

  return {
    Provider,
    useStateContext,
    useDispatchContext
  };
}

/**
 * D3 Drag Types
 *
 * This module provides type-safe wrappers and utilities for D3 drag behaviors.
 * It ensures proper typing for drag events, subjects, and related operations
 * while maintaining compatibility with D3's drag behavior API.
 */

import * as d3 from 'd3';
import { SimulationNodeDatum } from './D3Types';

/**
 * Type-safe drag event structure that properly extends D3's drag event
 * with generic type parameters for subject, parent data, and container element
 */
export interface TypedDragEvent<
  Datum extends object,
  ParentDatum extends object = object,
  ContainerElement extends Element = Element,
> extends d3.D3DragEvent<ContainerElement, Datum, ParentDatum> {
  // Additional type-safe properties can be added here
}

/**
 * Type-safe drag behavior configuration
 */
export interface DragBehaviorConfig<
  Datum extends object,
  ParentDatum extends object = object,
  ContainerElement extends Element = Element,
> {
  /**
   * Function called when drag starts
   */
  onDragStart?: (event: TypedDragEvent<Datum, ParentDatum, ContainerElement>) => void;

  /**
   * Function called during dragging
   */
  onDrag?: (event: TypedDragEvent<Datum, ParentDatum, ContainerElement>) => void;

  /**
   * Function called when drag ends
   */
  onDragEnd?: (event: TypedDragEvent<Datum, ParentDatum, ContainerElement>) => void;

  /**
   * Container to which the drag behavior should listen for events
   * Defaults to the window if not specified
   */
  container?: ContainerElement | null;

  /**
   * Filter function to determine if a drag should start
   * Return true to allow the drag, false to prevent it
   */
  filter?: (event: Event, datum: Datum) => boolean;

  /**
   * Whether to enable drag events on touchscreen devices
   * Defaults to true
   */
  touchable?: boolean;
}

/**
 * Creates a type-safe D3 drag behavior
 *
 * @param config Configuration for the drag behavior
 * @returns A properly typed D3 drag behavior
 */
export function createTypedDragBehavior<
  Datum extends object,
  ParentDatum extends object = object,
  ContainerElement extends Element = Element,
>(
  config: DragBehaviorConfig<Datum, ParentDatum, ContainerElement> = {}
): d3.DragBehavior<ContainerElement, Datum, ParentDatum> {
  // Create the drag behavior
  const drag = d3.drag<ContainerElement, Datum, ParentDatum>();

  // Configure drag behavior based on provided config
  if (config.onDragStart) {
    drag.on('start', config.onDragStart);
  }

  if (config.onDrag) {
    drag.on('drag', config.onDrag);
  }

  if (config.onDragEnd) {
    drag.on('end', config.onDragEnd);
  }

  if (config.container) {
    drag.container(() => config.container as ContainerElement);
  }

  if (config.filter) {
    drag.filter((event: any, d: Datum) => config.filter!(event, d));
  }

  if (config.touchable !== undefined) {
    drag.touchable(config.touchable);
  }

  return drag;
}

/**
 * Creates a drag behavior specifically for simulation nodes
 * This is particularly useful for flow diagrams or network visualizations
 *
 * @param simulation The D3 force simulation
 * @param config Additional configuration for the drag behavior
 * @returns A properly typed D3 drag behavior for simulation nodes
 */
export function createSimulationDragBehavior<
  NodeDatum extends SimulationNodeDatum,
  ContainerElement extends Element = SVGElement,
>(
  simulation: d3.Simulation<NodeDatum, undefined>,
  config: Partial<DragBehaviorConfig<NodeDatum, object, ContainerElement>> = {}
): d3.DragBehavior<ContainerElement, NodeDatum, object> {
  // Standard drag behavior for force simulations
  const handleDragStart = (event: TypedDragEvent<NodeDatum, object, ContainerElement>) => {
    if (!event.active) simulation.alphaTarget(0.3).restart();
    // Fix the node position during drag
    event.subject.fx = event.subject.x;
    event.subject.fy = event.subject.y;

    // Call custom handler if provided
    if (config.onDragStart) {
      config.onDragStart(event);
    }
  };

  const handleDrag = (event: TypedDragEvent<NodeDatum, object, ContainerElement>) => {
    // Update the fixed position to follow the pointer
    event.subject.fx = event.x;
    event.subject.fy = event.y;

    // Call custom handler if provided
    if (config.onDrag) {
      config.onDrag(event);
    }
  };

  const handleDragEnd = (event: TypedDragEvent<NodeDatum, object, ContainerElement>) => {
    if (!event.active) simulation.alphaTarget(0);
    // Release the fixed position when drag ends (unless configured otherwise)
    event.subject.fx = null;
    event.subject.fy = null;

    // Call custom handler if provided
    if (config.onDragEnd) {
      config.onDragEnd(event);
    }
  };

  // Create the drag behavior with simulation-specific defaults
  return createTypedDragBehavior<NodeDatum, object, ContainerElement>({
    onDragStart: handleDragStart,
    onDrag: handleDrag,
    onDragEnd: handleDragEnd,
    ...config,
  });
}

/**
 * Creates a custom drag behavior for visualization elements that need special handling
 *
 * @param options Custom options for specialized drag behavior
 * @returns A properly typed D3 drag behavior
 */
export function createCustomDragBehavior<
  Datum extends object,
  ParentDatum extends object = object,
  ContainerElement extends Element = Element,
>(
  options: {
    /** Maintain position relative to container */
    constrainToContainer?: boolean;
    /** Snap to grid with specified size */
    snapToGrid?: number;
    /** Only allow horizontal movement */
    horizontalOnly?: boolean;
    /** Only allow vertical movement */
    verticalOnly?: boolean;
    /** Minimum allowed position */
    minPosition?: { x?: number; y?: number };
    /** Maximum allowed position */
    maxPosition?: { x?: number; y?: number };
  } & DragBehaviorConfig<Datum, ParentDatum, ContainerElement>
): d3.DragBehavior<ContainerElement, Datum, ParentDatum> {
  // Wrap the standard drag handlers with custom behavior
  const onDrag = (event: TypedDragEvent<Datum, ParentDatum, ContainerElement>) => {
    let x = event.x;
    let y = event.y;

    // Apply custom constraints
    if (options.snapToGrid) {
      const gridSize = options.snapToGrid;
      x = Math.round(x / gridSize) * gridSize;
      y = Math.round(y / gridSize) * gridSize;
    }

    if (options.horizontalOnly) {
      y = (event.subject as any).y;
    }

    if (options.verticalOnly) {
      x = (event.subject as any).x;
    }

    if (options.minPosition) {
      if (options.minPosition.x !== undefined) {
        x = Math.max(x, options.minPosition.x);
      }
      if (options.minPosition.y !== undefined) {
        y = Math.max(y, options.minPosition.y);
      }
    }

    if (options.maxPosition) {
      if (options.maxPosition.x !== undefined) {
        x = Math.min(x, options.maxPosition.x);
      }
      if (options.maxPosition.y !== undefined) {
        y = Math.min(y, options.maxPosition.y);
      }
    }

    // Update event coordinates with constrained values
    event.x = x;
    event.y = y;

    // Call the original drag handler if provided
    if (options.onDrag) {
      options.onDrag(event);
    }
  };

  // Create a drag behavior with the wrapped handler
  return createTypedDragBehavior<Datum, ParentDatum, ContainerElement>({
    ...options,
    onDrag: onDrag,
  });
}

/**
 * Helper for applying a typed drag behavior to a D3 selection
 *
 * @param selection The D3 selection to apply the drag behavior to
 * @param dragBehavior The typed drag behavior to apply
 * @returns The selection with drag behavior applied
 */
export function applyDragBehavior<
  GElement extends Element,
  Datum extends object,
  PElement extends Element = Element,
  PDatum extends object = object,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  dragBehavior: d3.DragBehavior<Element, Datum, PDatum>
): d3.Selection<GElement, Datum, PElement, PDatum> {
  return selection.call(dragBehavior as any);
}

/**
 * D3 Selection Types
 *
 * This module provides type-safe wrappers and utilities for D3 selections.
 * It ensures proper typing for selection operations, data binding, and DOM manipulations
 * while maintaining compatibility with D3's selection API.
 */

import * as d3 from 'd3';

/**
 * Type-safe selection creator for SVG elements
 *
 * @param selector CSS selector string
 * @returns A properly typed D3 selection
 */
export function selectSvg(
  selector: string
): d3.Selection<SVGSVGElement, unknown, HTMLElement, any> {
  return d3.select<SVGSVGElement, unknown>(selector);
}

/**
 * Type-safe selection creator for SVG group elements
 *
 * @param selector CSS selector string
 * @returns A properly typed D3 selection
 */
export function selectGroup(
  selector: string
): d3.Selection<SVGGElement, unknown, HTMLElement, any> {
  return d3.select<SVGGElement, unknown>(selector);
}

/**
 * Type-safe selection creator for any SVG element
 *
 * @param selector CSS selector string
 * @param elementType Type of SVG element to select
 * @returns A properly typed D3 selection
 */
export function selectSvgElement<E extends SVGElement>(
  selector: string,
  elementType: new () => E
): d3.Selection<E, unknown, HTMLElement, any> {
  return d3.select<E, unknown>(selector);
}

/**
 * Type-safe selection creator for HTML elements
 *
 * @param selector CSS selector string
 * @param elementType Type of HTML element to select
 * @returns A properly typed D3 selection
 */
export function selectHtmlElement<E extends HTMLElement>(
  selector: string,
  elementType: new () => E
): d3.Selection<E, unknown, HTMLElement, any> {
  return d3.select<E, unknown>(selector);
}

/**
 * Type-safe selection creator for multiple elements
 *
 * @param selector CSS selector string
 * @returns A properly typed D3 selection
 */
export function selectAllSvg(
  selector: string
): d3.Selection<SVGSVGElement, unknown, HTMLElement, any> {
  return d3.selectAll<SVGSVGElement, unknown>(selector);
}

/**
 * Type-safe selection creator for multiple SVG group elements
 *
 * @param selector CSS selector string
 * @returns A properly typed D3 selection
 */
export function selectAllGroups(
  selector: string
): d3.Selection<SVGGElement, unknown, HTMLElement, any> {
  return d3.selectAll<SVGGElement, unknown>(selector);
}

/**
 * Type-safe selection creator for any multiple SVG elements
 *
 * @param selector CSS selector string
 * @param elementType Type of SVG element to select
 * @returns A properly typed D3 selection
 */
export function selectAllSvgElements<E extends SVGElement>(
  selector: string,
  elementType: new () => E
): d3.Selection<E, unknown, HTMLElement, any> {
  return d3.selectAll<E, unknown>(selector);
}

/**
 * Type-safe data binding for selections
 *
 * @param selection The D3 selection to bind data to
 * @param data Array of data items to bind
 * @param key Data join key function or string
 * @returns A properly typed update selection
 */
export function bindData<GElement extends Element, Datum, PElement extends Element, PDatum>(
  selection: d3.Selection<GElement, PDatum, PElement, any>,
  data: Datum[],
  key?: ((datum: Datum, index: number, groups: Datum[]) => string) | string
): d3.Selection<GElement, Datum, PElement, PDatum> {
  return selection.data(data, key as any);
}

/**
 * Type-safe join operation for selections
 *
 * @param selection The D3 selection to perform join on
 * @param elementType Tag name for new elements
 * @param enter Function to handle enter selection
 * @param update Function to handle update selection
 * @param exit Function to handle exit selection
 * @returns A merged selection
 */
export function joinElements<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
  NewElement extends Element,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  elementType: string,
  enter?: (
    selection: d3.Selection<d3.EnterElement, Datum, PElement, PDatum>
  ) => d3.Selection<NewElement, Datum, PElement, PDatum>,
  update?: (
    selection: d3.Selection<GElement, Datum, PElement, PDatum>
  ) => d3.Selection<GElement, Datum, PElement, PDatum>,
  exit?: (selection: d3.Selection<GElement, Datum, PElement, PDatum>) => void
): d3.Selection<NewElement | GElement, Datum, PElement, PDatum> {
  return selection.join(elementType, enter as any, update, exit) as d3.Selection<
    NewElement | GElement,
    Datum,
    PElement,
    PDatum
  >;
}

/**
 * Type-safe attribute setter for selections
 *
 * @param selection The D3 selection to set attributes on
 * @param attributes Object containing attribute name-value pairs
 * @returns The selection with attributes applied
 */
export function setAttributes<GElement extends Element, Datum, PElement extends Element, PDatum>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  attributes: Record<
    string,
    string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
  >
): d3.Selection<GElement, Datum, PElement, PDatum> {
  Object.entries(attributes).forEach(([key, value]) => {
    if (typeof value === 'function') {
      selection.attr(key, value as (d: Datum, i: number) => string);
    } else {
      selection.attr(key, value as string);
    }
  });

  return selection;
}

/**
 * Type-safe style setter for selections
 *
 * @param selection The D3 selection to set styles on
 * @param styles Object containing style name-value pairs
 * @returns The selection with styles applied
 */
export function setStyles<GElement extends Element, Datum, PElement extends Element, PDatum>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  styles: Record<
    string,
    string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
  >
): d3.Selection<GElement, Datum, PElement, PDatum> {
  Object.entries(styles).forEach(([key, value]) => {
    if (typeof value === 'function') {
      selection.style(key, value as (d: Datum, i: number) => string);
    } else {
      selection.style(key, value as string);
    }
  });

  return selection;
}

/**
 * Type-safe append operation for selections
 *
 * @param selection The D3 selection to append to
 * @param elementType Type of element to append
 * @returns A selection of the newly appended elements
 */
export function appendElement<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
  NewElement extends Element,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  elementType: string
): d3.Selection<NewElement, Datum, PElement, PDatum> {
  return selection.append<NewElement>(elementType);
}

/**
 * Type-safe event handler attachment for selections
 *
 * @param selection The D3 selection to attach event handlers to
 * @param eventType Type of event to listen for
 * @param listener Event listener function
 * @returns The selection with event handler attached
 */
export function addEventHandler<GElement extends Element, Datum, PElement extends Element, PDatum>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  eventType: string,
  listener: (event: Event, d: Datum, i: number, g: GElement[]) => void
): d3.Selection<GElement, Datum, PElement, PDatum> {
  return selection.on(eventType, listener);
}

/**
 * Type-safe transition creator for selections
 *
 * @param selection The D3 selection to create a transition on
 * @param name Optional name for the transition
 * @returns A properly typed transition
 */
export function createTransition<GElement extends Element, Datum, PElement extends Element, PDatum>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  name?: string
): d3.Transition<GElement, Datum, PElement, PDatum> {
  return name ? selection.transition(name) : selection.transition();
}

/**
 * Creates a typed builder for D3 selections to enable fluent chaining of operations
 *
 * @param selection The D3 selection to wrap
 * @returns A builder object with fluent methods
 */
export function createSelectionBuilder<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(selection: d3.Selection<GElement, Datum, PElement, PDatum>) {
  return {
    /**
     * The underlying D3 selection
     */
    selection,

    /**
     * Sets attributes on the selection
     */
    attr(
      key: string,
      value: string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
    ) {
      selection.attr(key, value as any);
      return this;
    },

    /**
     * Sets multiple attributes from an object
     */
    attrs(
      attributes: Record<
        string,
        string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
      >
    ) {
      return setAttributes(selection, attributes), this;
    },

    /**
     * Sets a style property on the selection
     */
    style(
      key: string,
      value: string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
    ) {
      selection.style(key, value as any);
      return this;
    },

    /**
     * Sets multiple style properties from an object
     */
    styles(
      styles: Record<
        string,
        string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
      >
    ) {
      return setStyles(selection, styles), this;
    },

    /**
     * Sets text content on the selection
     */
    text(value: string | ((d: Datum, i: number) => string)) {
      selection.text(value as any);
      return this;
    },

    /**
     * Sets HTML content on the selection
     */
    html(value: string | ((d: Datum, i: number) => string)) {
      selection.html(value as any);
      return this;
    },

    /**
     * Appends a new element to each element in the selection
     */
    append<NewElement extends Element>(elementType: string) {
      const newSelection = appendElement<GElement, Datum, PElement, PDatum, NewElement>(
        selection,
        elementType
      );
      return createSelectionBuilder(newSelection);
    },

    /**
     * Adds an event listener to the selection
     */
    on(eventType: string, listener: (event: Event, d: Datum, i: number, g: GElement[]) => void) {
      selection.on(eventType, listener);
      return this;
    },

    /**
     * Creates a transition on the selection
     */
    transition(name?: string) {
      const transition = createTransition(selection, name);
      return createTransitionBuilder(transition);
    },

    /**
     * Filters the selection
     */
    filter(filterFn: (d: Datum, i: number) => boolean) {
      const filtered = selection.filter(filterFn);
      return createSelectionBuilder(filtered);
    },

    /**
     * Binds new data to the selection
     */
    data<NewDatum>(
      data: NewDatum[],
      key?: ((datum: NewDatum, index: number, groups: NewDatum[]) => string) | string
    ) {
      const newSelection = bindData<GElement, NewDatum, PElement, PDatum>(
        selection as any,
        data,
        key
      );
      return createSelectionBuilder(newSelection);
    },

    /**
     * Joins data with elements
     */
    join<NewElement extends Element>(
      elementType: string,
      enter?: (
        selection: d3.Selection<d3.EnterElement, Datum, PElement, PDatum>
      ) => d3.Selection<NewElement, Datum, PElement, PDatum>,
      update?: (
        selection: d3.Selection<GElement, Datum, PElement, PDatum>
      ) => d3.Selection<GElement, Datum, PElement, PDatum>,
      exit?: (selection: d3.Selection<GElement, Datum, PElement, PDatum>) => void
    ) {
      const joined = joinElements<GElement, Datum, PElement, PDatum, NewElement>(
        selection,
        elementType,
        enter,
        update,
        exit
      );
      return createSelectionBuilder(joined);
    },

    /**
     * Calls a function with the selection
     */
    call(fn: (selection: d3.Selection<GElement, Datum, PElement, PDatum>) => void) {
      selection.call(fn);
      return this;
    },
  };
}

/**
 * Creates a typed builder for D3 transitions to enable fluent chaining of operations
 *
 * @param transition The D3 transition to wrap
 * @returns A builder object with fluent methods
 */
export function createTransitionBuilder<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(transition: d3.Transition<GElement, Datum, PElement, PDatum>) {
  return {
    /**
     * The underlying D3 transition
     */
    transition,

    /**
     * Sets the duration of the transition
     */
    duration(milliseconds: number) {
      transition.duration(milliseconds);
      return this;
    },

    /**
     * Sets the delay of the transition
     */
    delay(milliseconds: number | ((d: Datum, i: number) => number)) {
      transition.delay(milliseconds as any);
      return this;
    },

    /**
     * Sets the easing function of the transition
     */
    ease(easingFn: d3.EasingFn) {
      transition.ease(easingFn);
      return this;
    },

    /**
     * Sets an attribute with a transition
     */
    attr(
      key: string,
      value: string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
    ) {
      transition.attr(key, value as any);
      return this;
    },

    /**
     * Sets a style property with a transition
     */
    style(
      key: string,
      value: string | number | boolean | ((d: Datum, i: number) => string | number | boolean)
    ) {
      transition.style(key, value as any);
      return this;
    },

    /**
     * Adds an event listener for transition events
     */
    on(eventType: 'start' | 'end' | 'interrupt', listener: (event: Event, d: Datum) => void) {
      transition.on(eventType, listener);
      return this;
    },

    /**
     * Calls a function with the transition
     */
    call(fn: (transition: d3.Transition<GElement, Datum, PElement, PDatum>) => void) {
      transition.call(fn);
      return this;
    },
  };
}

/**
 * Creates a type-safe selection for a new SVG element
 *
 * @param parent The parent element to append the SVG to
 * @param width Width of the SVG
 * @param height Height of the SVG
 * @returns A selection builder for the new SVG
 */
export function createSvg(
  parent: string | d3.Selection<HTMLElement, unknown, HTMLElement, any>,
  width: number,
  height: number
) {
  const parentSelection =
    typeof parent === 'string' ? d3.select<HTMLElement, unknown>(parent) : parent;

  const svg = parentSelection
    .append<SVGSVGElement>('svg')
    .attr('width', width)
    .attr('height', height)
    .attr('viewBox', `0 0 ${width} ${height}`)
    .style('overflow', 'visible');

  return createSelectionBuilder(svg);
}

/**
 * Creates a defs element for SVG definitions like markers, patterns, etc.
 *
 * @param svg The SVG element to append defs to
 * @returns A selection of the defs element
 */
export function createDefs(svg: d3.Selection<SVGSVGElement, unknown, HTMLElement, any>) {
  return svg.append<SVGDefsElement>('defs');
}

/**
 * Creates a marker definition for arrow heads etc.
 *
 * @param defs The defs element to append the marker to
 * @param id Unique ID for the marker
 * @param options Configuration options for the marker
 * @returns A selection of the marker element
 */
export function createMarker(
  defs: d3.Selection<SVGDefsElement, unknown, HTMLElement, any>,
  id: string,
  options: {
    width?: number;
    height?: number;
    refX?: number;
    refY?: number;
    viewBox?: string;
    orient?: string;
    path?: string;
    color?: string;
  } = {}
) {
  const {
    width = 10,
    height = 10,
    refX = 5,
    refY = 5,
    viewBox = '0 0 10 10',
    orient = 'auto',
    path = 'M0,0L10,5L0,10z',
    color = 'black',
  } = options;

  const marker = defs
    .append<SVGMarkerElement>('marker')
    .attr('id', id)
    .attr('markerWidth', width)
    .attr('markerHeight', height)
    .attr('refX', refX)
    .attr('refY', refY)
    .attr('viewBox', viewBox)
    .attr('orient', orient)
    .attr('markerUnits', 'userSpaceOnUse');

  marker.append<SVGPathElement>('path').attr('d', path).attr('fill', color);

  return marker;
}

/**
 * D3 Zoom Types
 *
 * This module provides type-safe wrappers and utilities for D3 zoom behaviors.
 * It ensures proper typing for zoom events, transformations, and related operations
 * while maintaining compatibility with D3's zoom behavior API.
 */

import * as d3 from 'd3';

/**
 * Type-safe zoom event structure that properly extends D3's zoom event
 * with generic type parameters for the container element
 */
export interface TypedZoomEvent<
  Element extends d3.BaseType,
  Datum,
  PElement extends d3.BaseType = d3.BaseType,
  PDatum = any,
> extends d3.D3ZoomEvent<Element, Datum> {
  // Additional type-safe properties can be added here
  sourceEvent: Event;
}

/**
 * Type-safe zoom behavior configuration
 */
export interface ZoomBehaviorConfig<Element extends d3.BaseType, Datum = any> {
  /**
   * Function called when zoom starts
   */
  onZoomStart?: (event: TypedZoomEvent<Element, Datum>) => void;

  /**
   * Function called during zooming
   */
  onZoom?: (event: TypedZoomEvent<Element, Datum>) => void;

  /**
   * Function called when zoom ends
   */
  onZoomEnd?: (event: TypedZoomEvent<Element, Datum>) => void;

  /**
   * Minimum zoom scale factor
   * Defaults to 0.1
   */
  scaleExtentMin?: number;

  /**
   * Maximum zoom scale factor
   * Defaults to 8
   */
  scaleExtentMax?: number;

  /**
   * Filter function to determine if a zoom should be allowed
   * Return true to allow the zoom, false to prevent it
   */
  filter?: (event: Event) => boolean;

  /**
   * Whether to enable wheel zooming
   * Defaults to true
   */
  wheelZoom?: boolean;

  /**
   * Whether to enable double-click to zoom
   * Defaults to true
   */
  dblClickZoom?: boolean;

  /**
   * Duration of transition for zoom events in milliseconds
   * Set to 0 to disable transitions
   * Defaults to 250ms
   */
  transitionDuration?: number;

  /**
   * Initial transform to apply
   */
  initialTransform?: d3.ZoomTransform;
}

/**
 * Creates a type-safe D3 zoom behavior
 *
 * @param config Configuration for the zoom behavior
 * @returns A properly typed D3 zoom behavior
 */
export function createTypedZoomBehavior<Element extends d3.BaseType, Datum = any>(
  config: ZoomBehaviorConfig<Element, Datum> = {}
): d3.ZoomBehavior<Element, Datum> {
  // Create the zoom behavior
  const zoom = d3.zoom<Element, Datum>();

  // Configure zoom behavior based on provided config
  if (config.onZoomStart) {
    zoom.on('start', config.onZoomStart);
  }

  if (config.onZoom) {
    zoom.on('zoom', config.onZoom);
  }

  if (config.onZoomEnd) {
    zoom.on('end', config.onZoomEnd);
  }

  // Set scale extent (min/max zoom level)
  const minScale = config.scaleExtentMin ?? 0.1;
  const maxScale = config.scaleExtentMax ?? 8;
  zoom.scaleExtent([minScale, maxScale]);

  // Apply filter if provided
  if (config.filter) {
    zoom.filter(config.filter);
  }

  // Configure wheel zoom
  if (config.wheelZoom !== undefined) {
    // If wheelZoom is false, we need to filter out wheel events
    if (!config.wheelZoom) {
      const originalFilter = zoom.filter();
      zoom.filter(function (event: any) {
        return event.type !== 'wheel' && originalFilter.call(this, event);
      });
    }
  }

  // Configure double-click zoom
  if (config.dblClickZoom !== undefined) {
    zoom.interpolate(config.dblClickZoom ? d3.interpolateZoom : null);
  }

  return zoom;
}

/**
 * Helper to apply a typed zoom behavior to a D3 selection
 *
 * @param selection The D3 selection to apply the zoom behavior to
 * @param zoomBehavior The typed zoom behavior to apply
 * @returns The selection with zoom behavior applied
 */
export function applyZoomBehavior<
  GElement extends d3.BaseType,
  Datum,
  PElement extends d3.BaseType,
  PDatum,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  zoomBehavior: d3.ZoomBehavior<GElement, Datum>
): d3.Selection<GElement, Datum, PElement, PDatum> {
  return selection.call(zoomBehavior);
}

/**
 * Creates a zoom behavior specifically for SVG visualizations
 * This is particularly useful for pan/zoom in diagrams or charts
 *
 * @param config Configuration for the SVG zoom behavior
 * @returns A properly typed D3 zoom behavior for SVG elements
 */
export function createSvgZoomBehavior<Element extends SVGElement = SVGSVGElement, Datum = any>(
  config: ZoomBehaviorConfig<Element, Datum> & {
    /**
     * Target element (usually a group) to transform during zoom
     * If not provided, zooming will apply to the element the zoom behavior is attached to
     */
    targetElement?: d3.Selection<SVGGElement, any, any, any>;

    /**
     * Whether to enable panning
     * Defaults to true
     */
    enablePan?: boolean;

    /**
     * Whether to constrain panning to prevent the content from leaving the viewport
     * Defaults to false
     */
    constrainPan?: boolean;

    /**
     * Extent of the viewable area [x, y, width, height]
     * Defaults to the element's viewport
     */
    extent?: [[number, number], [number, number]];
  } = {}
): d3.ZoomBehavior<Element, Datum> {
  // Create base zoom behavior
  const zoom = createTypedZoomBehavior<Element, Datum>(config);

  // Configure SVG-specific options
  if (config.enablePan === false) {
    // Disable panning by only allowing scaling transformations
    zoom.on('zoom', (event: any) => {
      const transform = event.transform;
      const newTransform = d3.zoomIdentity.scale(transform.k);

      if (config.targetElement) {
        config.targetElement.attr('transform', `scale(${transform.k})`);
      }

      // Call the original zoom handler if provided
      if (config.onZoom) {
        // Create a modified event with the new transform
        const modifiedEvent = Object.assign({}, event, { transform: newTransform });
        config.onZoom(modifiedEvent as TypedZoomEvent<Element, Datum>);
      }
    });
  } else if (config.targetElement) {
    // Apply normal pan/zoom transformation to target element
    zoom.on('zoom', (event: any) => {
      config.targetElement!.attr('transform', event.transform);

      // Call the original zoom handler if provided
      if (config.onZoom) {
        config.onZoom(event as TypedZoomEvent<Element, Datum>);
      }
    });
  }

  // Set extent if provided
  if (config.extent) {
    zoom.extent(config.extent);
  }

  // Add constraint handling for panning
  if (config.constrainPan) {
    const originalZoom = zoom.on('zoom');

    zoom.on('zoom', (event: any) => {
      // Constrain the transform to prevent content from leaving viewport
      const transform = event.transform;
      const constrainedTransform = constrainTransform(transform, config.extent);

      // Update the event transform with the constrained one
      event.transform = constrainedTransform;

      // Call the original zoom handler
      if (originalZoom) {
        originalZoom.call(this, event);
      }
    });
  }

  return zoom;
}

/**
 * Constrains a zoom transform to keep content within the viewport
 *
 * @param transform The transform to constrain
 * @param extent The extent of the viewable area
 * @returns A constrained transform
 */
function constrainTransform(
  transform: d3.ZoomTransform,
  extent?: [[number, number], [number, number]]
): d3.ZoomTransform {
  if (!extent) return transform;

  const [[x0, y0], [x1, y1]] = extent;
  const width = x1 - x0;
  const height = y1 - y0;

  // Calculate constraints based on scale and extent
  const maxX = width * (1 - transform.k);
  const maxY = height * (1 - transform.k);

  // Create a new transform with constrained values
  return d3.zoomIdentity
    .translate(Math.min(0, Math.max(transform.x, maxX)), Math.min(0, Math.max(transform.y, maxY)))
    .scale(transform.k);
}

/**
 * Creates a zoom behavior for panning and zooming a d3 simulation visualization
 *
 * @param config Configuration for the simulation visualization zoom behavior
 * @returns A properly typed D3 zoom behavior for simulation visualizations
 */
export function createSimulationZoomBehavior<
  Element extends SVGElement = SVGSVGElement,
  Datum = any,
>(
  config: ZoomBehaviorConfig<Element, Datum> & {
    /**
     * The container group that holds all simulation elements
     */
    container: d3.Selection<SVGGElement, any, any, any>;

    /**
     * Function to update the simulation view after zoom/pan
     */
    updateView?: (transform: d3.ZoomTransform) => void;
  }
): d3.ZoomBehavior<Element, Datum> {
  const { container, updateView, ...zoomConfig } = config;

  // Handler for zoom events
  const handleZoom = (event: TypedZoomEvent<Element, Datum>) => {
    // Apply transform to the container
    container.attr('transform', event.transform.toString());

    // Call custom update function if provided
    if (updateView) {
      updateView(event.transform);
    }

    // Call original handler if provided
    if (config.onZoom) {
      config.onZoom(event);
    }
  };

  // Create the zoom behavior
  return createTypedZoomBehavior<Element, Datum>({
    ...zoomConfig,
    onZoom: handleZoom,
  });
}

/**
 * Helper function to get the initial transform to fit content within a viewport
 *
 * @param width Width of the viewport
 * @param height Height of the viewport
 * @param contentWidth Width of the content to fit
 * @param contentHeight Height of the content to fit
 * @param padding Padding to add around the content (default: 20)
 * @returns ZoomTransform that will fit the content within the viewport
 */
export function getFitToViewportTransform(
  width: number,
  height: number,
  contentWidth: number,
  contentHeight: number,
  padding: number = 20
): d3.ZoomTransform {
  // Calculate scale to fit content
  const scaleX = (width - padding * 2) / contentWidth;
  const scaleY = (height - padding * 2) / contentHeight;
  const scale = Math.min(scaleX, scaleY, 1); // Don't scale up more than 1x

  // Calculate translation to center content
  const translateX = (width - contentWidth * scale) / 2;
  const translateY = (height - contentHeight * scale) / 2;

  return d3.zoomIdentity.translate(translateX, translateY).scale(scale);
}

import React, { forwardRef } from 'react';
import { Button, ButtonProps } from '../Button';
import { cn } from '../../../../utils/cn';

/**
 * Ability interface for game abilities
 */
export interface Ability {
  /** Unique ID of the ability */
  id: string;
  /** Display name of the ability */
  name: string;
  /** Description of what the ability does */
  description: string;
  /** URL or path to the ability's icon */
  iconUrl?: string;
  /** Whether the ability is passive (always active) */
  isPassive?: boolean;
  /** Whether the ability is unlocked */
  isUnlocked?: boolean;
  /** The ability's cooldown in seconds (0 means no cooldown) */
  cooldown: number;
  /** The ability's energy cost */
  energyCost?: number;
  /** The ability's mana cost */
  manaCost?: number;
  /** The ability's required level */
  requiredLevel?: number;
  /** The ability's damage type */
  damageType?: 'physical' | 'energy' | 'thermal' | 'explosive' | 'none';
  /** The ability's range in units */
  range?: number;
  /** The ability's duration in seconds */
  duration?: number;
  /** Tags for filtering and categorization */
  tags?: string[];
  /** Custom metadata */
  metadata?: Record<string, any>;
}

/**
 * AbilityButton props
 */
export interface AbilityButtonProps extends Omit<ButtonProps, 'leadingIcon'> {
  /** The ability to display */
  ability: Ability;
  /** Current cooldown time remaining in seconds */
  cooldownRemaining?: number;
  /** Whether the player has enough resources to use this ability */
  hasResources?: boolean;
  /** Whether to show the cooldown timer */
  showCooldown?: boolean;
  /** Whether to show the ability tooltip on hover */
  showTooltip?: boolean;
  /** Whether to show the key binding */
  showKeybinding?: boolean;
  /** Keyboard shortcut for this ability */
  keybinding?: string;
  /** Whether the ability is currently selected */
  isSelected?: boolean;
  /** Click handler for the ability */
  onUse?: (ability: Ability) => void;
}

/**
 * AbilityButton component
 * 
 * A specialized button for displaying and activating game abilities,
 * with support for cooldowns, resource costs, and tooltips.
 */
export const AbilityButton = forwardRef<HTMLButtonElement, AbilityButtonProps>(
  (
    {
      ability,
      cooldownRemaining = 0,
      hasResources = true,
      showCooldown = true,
      showTooltip = true,
      showKeybinding = true,
      keybinding,
      isSelected = false,
      className,
      onUse,
      ...props
    },
    ref
  ) => {
    // Calculate cooldown percentage for the overlay
    const cooldownPercent = ability.cooldown > 0
      ? (cooldownRemaining / ability.cooldown) * 100
      : 0;

    // Determine button state
    const isOnCooldown = cooldownRemaining > 0;
    const isDisabled = isOnCooldown || !hasResources || !ability.isUnlocked;

    // Handle ability use
    const handleClick = () => {
      if (!isDisabled && onUse) {
        onUse(ability);
      }
    };

    // Create tooltip content
    const tooltipContent = showTooltip ? (
      <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 p-2 bg-gray-800 text-white text-sm rounded shadow-lg z-10 w-48 opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none">
        <div className="font-bold">{ability.name}</div>
        <div className="text-xs mt-1">{ability.description}</div>
        <div className="grid grid-cols-2 gap-x-2 mt-2 text-xs">
          {ability.cooldown > 0 && (
            <div>Cooldown: {ability.cooldown}s</div>
          )}
          {ability.energyCost && ability.energyCost > 0 && (
            <div>Energy: {ability.energyCost}</div>
          )}
          {ability.manaCost && ability.manaCost > 0 && (
            <div>Mana: {ability.manaCost}</div>
          )}
          {ability.range && (
            <div>Range: {ability.range}m</div>
          )}
          {ability.duration && (
            <div>Duration: {ability.duration}s</div>
          )}
          {ability.damageType && ability.damageType !== 'none' && (
            <div>Type: {ability.damageType}</div>
          )}
        </div>
      </div>
    ) : null;

    // Create cooldown overlay
    const cooldownOverlay = (showCooldown && isOnCooldown) ? (
      <div className="absolute inset-0 bg-black bg-opacity-60 rounded-md flex items-center justify-center text-white font-bold">
        <div className="absolute inset-0 bg-black bg-opacity-60" style={{ 
          clipPath: `inset(0 0 ${cooldownPercent}% 0)` 
        }} />
        <span className="z-10">{Math.ceil(cooldownRemaining)}s</span>
      </div>
    ) : null;

    // Create keybinding indicator
    const keybindingIndicator = (showKeybinding && keybinding) ? (
      <div className="absolute top-0 right-0 bg-gray-800 text-white text-xs px-1 rounded-bl-md">
        {keybinding}
      </div>
    ) : null;

    // Icon for the ability
    const abilityIcon = ability.iconUrl ? (
      <img 
        src={ability.iconUrl} 
        alt={ability.name} 
        className="w-6 h-6"
      />
    ) : (
      <div className="w-6 h-6 bg-gray-300 rounded-full flex items-center justify-center">
        <span className="text-xs">{ability.name.charAt(0)}</span>
      </div>
    );

    return (
      <div className={cn(
        "relative group",
        isSelected && "ring-2 ring-blue-500 rounded-md",
        className
      )}>
        {tooltipContent}

        <Button
          ref={ref}
          variant={ability.isPassive ? 'tertiary' : 'primary'}
          disabled={isDisabled}
          onClick={handleClick}
          className={cn(
            "relative min-w-[48px] min-h-[48px] p-2",
            !hasResources && !isOnCooldown && "border-red-500 border-2",
            ability.isPassive && "bg-gray-200 text-gray-700"
          )}
          leadingIcon={abilityIcon}
          {...props}
        >
          {props.children}
        </Button>

        {cooldownOverlay}
        {keybindingIndicator}

        {!ability.isUnlocked && (
          <div className="absolute inset-0 bg-gray-900 bg-opacity-80 flex items-center justify-center rounded-md">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
            </svg>
          </div>
        )}
      </div>
    );
  }
);

AbilityButton.displayName = 'AbilityButton';

export default AbilityButton;

import React, { forwardRef } from 'react';
import { Card, CardProps } from '../Card';
import { cn } from '../../../../utils/cn';

/**
 * Module status types
 */
export type ModuleStatus = 'active' | 'inactive' | 'damaged' | 'destroyed' | 'upgrading' | 'locked';

/**
 * Module type
 */
export interface Module {
  /** Unique ID of the module */
  id: string;
  /** Display name of the module */
  name: string;
  /** Description of what the module does */
  description: string;
  /** The module's current status */
  status: ModuleStatus;
  /** The module's current level (0 = not installed) */
  level: number;
  /** The module's maximum possible level */
  maxLevel: number;
  /** The module's energy consumption */
  energyConsumption: number;
  /** The module's efficiency (0-100%) */
  efficiency: number;
  /** The module's remaining durability (0-100%) */
  durability: number;
  /** Time until the module is repaired/upgraded (in seconds) */
  timeRemaining?: number;
  /** The module's image or icon URL */
  imageUrl?: string;
  /** The module's size/footprint (in grid units) */
  size?: { width: number; height: number };
  /** The module's position in the grid */
  position?: { x: number; y: number };
  /** The module's required level to unlock */
  requiredLevel?: number;
  /** The module's tags */
  tags?: string[];
  /** Resources consumed per cycle */
  resourceConsumption?: Record<string, number>;
  /** Resources produced per cycle */
  resourceProduction?: Record<string, number>;
  /** Custom metadata */
  metadata?: Record<string, any>;
}

/**
 * ModuleCard props
 */
export interface ModuleCardProps extends Omit<CardProps, 'title' | 'subtitle'> {
  /** The module to display */
  module: Module;
  /** Whether to show the module level */
  showLevel?: boolean;
  /** Whether to show the module energy consumption */
  showEnergy?: boolean;
  /** Whether to show the module efficiency */
  showEfficiency?: boolean;
  /** Whether to show the module durability */
  showDurability?: boolean;
  /** Whether to show module resource I/O */
  showResources?: boolean;
  /** Whether the module can be selected */
  selectable?: boolean;
  /** Whether the module is selected */
  selected?: boolean;
  /** Whether to show the upgrade button */
  showUpgrade?: boolean;
  /** Click handler for the upgrade button */
  onUpgrade?: (module: Module) => void;
  /** Click handler for the module card */
  onSelect?: (module: Module) => void;
  /** Whether to show the repair button */
  showRepair?: boolean;
  /** Click handler for the repair button */
  onRepair?: (module: Module) => void;
}

/**
 * ModuleCard component
 * 
 * A specialized card for displaying ship or station modules,
 * with support for status indicators, resource I/O, and action buttons.
 */
export const ModuleCard = forwardRef<HTMLDivElement, ModuleCardProps>(
  ({
    module,
    showLevel = true,
    showEnergy = true,
    showEfficiency = true,
    showDurability = true,
    showResources = false,
    selectable = false,
    selected = false,
    showUpgrade = false,
    onUpgrade,
    onSelect,
    showRepair = false,
    onRepair,
    className,
    ...props
  }, ref) => {
    // Status colors
    const statusColors: Record<ModuleStatus, string> = {
      active: 'bg-green-500',
      inactive: 'bg-gray-500',
      damaged: 'bg-yellow-500',
      destroyed: 'bg-red-500',
      upgrading: 'bg-blue-500',
      locked: 'bg-purple-500'
    };

    // Status labels
    const statusLabels: Record<ModuleStatus, string> = {
      active: 'Active',
      inactive: 'Inactive',
      damaged: 'Damaged',
      destroyed: 'Destroyed',
      upgrading: 'Upgrading',
      locked: 'Locked'
    };

    // Format time remaining
    const formatTime = (seconds?: number): string => {
      if (!seconds) return '';
      if (seconds < 60) return `${Math.ceil(seconds)}s`;
      if (seconds < 3600) return `${Math.ceil(seconds / 60)}m`;
      return `${Math.floor(seconds / 3600)}h ${Math.ceil((seconds % 3600) / 60)}m`;
    };

    // Handle card click for selection
    const handleClick = () => {
      if (selectable && onSelect) {
        onSelect(module);
      }
    };

    // Create the header with status indicator
    const moduleHeader = (
      <div className="flex items-center justify-between">
        <div className="flex items-center">
          <div className={cn(
            "w-3 h-3 rounded-full mr-2",
            statusColors[module.status]
          )} />
          <span className="font-medium">{statusLabels[module.status]}</span>
        </div>
        {module.timeRemaining && module.timeRemaining > 0 && (
          <div className="text-sm text-gray-600">
            {formatTime(module.timeRemaining)}
          </div>
        )}
      </div>
    );

    // Create the footer with action buttons
    const moduleFooter = (
      (showUpgrade || showRepair) && (
        <div className="flex justify-end space-x-2">
          {showRepair && module.status === 'damaged' && (
            <button 
              className="px-3 py-1 bg-yellow-500 hover:bg-yellow-600 text-white text-sm rounded"
              onClick={(e) => {
                e.stopPropagation();
                if (onRepair) onRepair(module);
              }}
            >
              Repair
            </button>
          )}
          {showUpgrade && module.level < module.maxLevel && module.status !== 'destroyed' && (
            <button 
              className="px-3 py-1 bg-blue-500 hover:bg-blue-600 text-white text-sm rounded"
              onClick={(e) => {
                e.stopPropagation();
                if (onUpgrade) onUpgrade(module);
              }}
            >
              Upgrade to Lvl {module.level + 1}
            </button>
          )}
        </div>
      )
    );

    // Create progress bars for durability and efficiency
    const renderProgressBars = () => (
      <div className="mt-3 space-y-2">
        {showDurability && (
          <div>
            <div className="flex justify-between text-xs mb-1">
              <span>Durability</span>
              <span>{module.durability}%</span>
            </div>
            <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
              <div 
                className={cn(
                  "h-full rounded-full",
                  module.durability > 60 ? "bg-green-500" :
                  module.durability > 30 ? "bg-yellow-500" : "bg-red-500"
                )}
                style={{ width: `${module.durability}%` }}
              />
            </div>
          </div>
        )}

        {showEfficiency && (
          <div>
            <div className="flex justify-between text-xs mb-1">
              <span>Efficiency</span>
              <span>{module.efficiency}%</span>
            </div>
            <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
              <div 
                className="h-full bg-blue-500 rounded-full"
                style={{ width: `${module.efficiency}%` }}
              />
            </div>
          </div>
        )}
      </div>
    );

    // Create resource I/O display
    const renderResources = () => {
      if (!showResources) return null;

      const hasConsumption = module.resourceConsumption && Object.keys(module.resourceConsumption).length > 0;
      const hasProduction = module.resourceProduction && Object.keys(module.resourceProduction).length > 0;

      if (!hasConsumption && !hasProduction) return null;

      return (
        <div className="mt-3 text-sm border-t border-gray-200 pt-2">
          {hasConsumption && (
            <div>
              <span className="text-xs font-medium text-red-600">Consumes:</span>
              <div className="grid grid-cols-2 gap-x-2 ml-2">
                {Object.entries(module.resourceConsumption!).map(([resource, amount]) => (
                  <div key={resource} className="flex justify-between">
                    <span>{resource}</span>
                    <span>-{amount}/s</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {hasProduction && (
            <div className="mt-1">
              <span className="text-xs font-medium text-green-600">Produces:</span>
              <div className="grid grid-cols-2 gap-x-2 ml-2">
                {Object.entries(module.resourceProduction!).map(([resource, amount]) => (
                  <div key={resource} className="flex justify-between">
                    <span>{resource}</span>
                    <span>+{amount}/s</span>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      );
    };

    return (
      <Card
        ref={ref}
        variant="bordered"
        hoverable={selectable}
        selectable={selectable}
        selected={selected}
        disabled={module.status === 'destroyed' || module.status === 'locked'}
        title={
          <div className="flex justify-between items-center">
            <span>{module.name}</span>
            {showLevel && (
              <span className="text-sm bg-gray-100 px-2 py-0.5 rounded">
                Lvl {module.level}/{module.maxLevel}
              </span>
            )}
          </div>
        }
        subtitle={module.description}
        header={moduleHeader}
        footer={moduleFooter}
        className={cn(
          "border-l-4",
          module.status === 'active' && "border-l-green-500",
          module.status === 'inactive' && "border-l-gray-500",
          module.status === 'damaged' && "border-l-yellow-500",
          module.status === 'destroyed' && "border-l-red-500",
          module.status === 'upgrading' && "border-l-blue-500",
          module.status === 'locked' && "border-l-purple-500",
          className
        )}
        onClick={handleClick}
        {...props}
      >
        <div className="flex flex-col">
          {/* Module image if available */}
          {module.imageUrl && (
            <div className="mb-3 flex justify-center">
              <img 
                src={module.imageUrl} 
                alt={module.name}
                className="h-24 object-contain rounded"
              />
            </div>
          )}

          {/* Main module stats */}
          <div className="grid grid-cols-2 gap-2 text-sm">
            {showEnergy && (
              <div className="flex justify-between">
                <span>Energy:</span>
                <span className="text-orange-600">{module.energyConsumption} kW</span>
              </div>
            )}

            {/* You can add other specific module stats here */}
            {module.size && (
              <div className="flex justify-between">
                <span>Size:</span>
                <span>{module.size.width}x{module.size.height}</span>
              </div>
            )}
          </div>

          {/* Progress bars */}
          {renderProgressBars()}

          {/* Resource I/O */}
          {renderResources()}
        </div>
      </Card>
    );
  }
);

ModuleCard.displayName = 'ModuleCard';

export default ModuleCard;

/**
 * EventBatchingRxJS.ts
 *
 * Advanced event batching utilities using RxJS for time-based event processing.
 * Provides specialized operators and utilities for working with event streams.
 */

import { Observable, Subject, interval, of, timer } from 'rxjs';
import {
  bufferTime,
  bufferToggle,
  catchError,
  filter,
  map,
  mergeMap,
  switchMap,
  take,
  throttleTime,
} from 'rxjs/operators';
import { EventPriority } from './EventPrioritizer';

/**
 * Configuration for time-based event batching
 */
export interface TimeBatchConfig {
  /**
   * Time window in milliseconds to collect events before processing
   */
  timeWindow: number;

  /**
   * Maximum number of events to include in a batch before processing
   */
  maxBatchSize?: number;

  /**
   * Whether to emit batches on a fixed schedule regardless of event counts
   */
  emitOnSchedule?: boolean;

  /**
   * Fixed schedule interval in milliseconds (if emitOnSchedule is true)
   */
  scheduleInterval?: number;

  /**
   * Group events by a specific property
   */
  groupBy?: (event: any) => string;

  /**
   * Priority threshold - events with priority below this will be batched
   */
  priorityThreshold?: EventPriority;

  /**
   * Whether to sort events by timestamp within batches
   */
  sortByTimestamp?: boolean;
}

/**
 * Default configuration for time-based event batching
 */
const DEFAULT_TIME_BATCH_CONFIG: TimeBatchConfig = {
  timeWindow: 100,
  maxBatchSize: 100,
  emitOnSchedule: false,
  scheduleInterval: 1000,
  sortByTimestamp: true,
};

/**
 * Time-based batch result with performance metrics
 */
export interface TimeBatchResult<T> {
  /**
   * Events in the batch
   */
  events: T[];

  /**
   * Start timestamp of the batch window
   */
  startTime: number;

  /**
   * End timestamp of the batch window
   */
  endTime: number;

  /**
   * Size of the batch
   */
  batchSize: number;

  /**
   * Whether the batch was triggered by reaching the time window
   */
  timeWindowReached: boolean;

  /**
   * Whether the batch was triggered by reaching the max batch size
   */
  maxSizeReached: boolean;

  /**
   * Events per second rate during this batch window
   */
  eventsPerSecond: number;

  /**
   * Time between first and last event in the batch (ms)
   */
  eventSpread: number;
}

/**
 * Create a time-based batched event stream
 *
 * @param source - Source event stream
 * @param config - Batching configuration
 */
export function createTimeBatchedStream<T>(
  source: Observable<T>,
  config: Partial<TimeBatchConfig> = {}
): Observable<TimeBatchResult<T>> {
  // Merge with default config
  const batchConfig: TimeBatchConfig = {
    ...DEFAULT_TIME_BATCH_CONFIG,
    ...config,
  };

  // If emitting on a fixed schedule
  if (batchConfig.emitOnSchedule && batchConfig.scheduleInterval) {
    return source.pipe(
      // Buffer events based on fixed timer intervals
      bufferToggle(interval(batchConfig.scheduleInterval), () => timer(batchConfig.timeWindow)),
      // Filter out empty batches
      filter(events => events.length > 0),
      // Create batch result
      map(events => createBatchResult(events, batchConfig, false, false))
    );
  }

  // If priority threshold is set, split the stream
  if (batchConfig.priorityThreshold !== undefined) {
    // High priority stream (process immediately)
    const highPriorityStream = source.pipe(
      filter(
        (event: any) =>
          event.priority !== undefined && event.priority < batchConfig.priorityThreshold!
      ),
      map(event => [event]),
      map(events => createBatchResult(events, batchConfig, false, false))
    );

    // Low priority stream (batch)
    const lowPriorityStream = source.pipe(
      filter(
        (event: any) =>
          event.priority === undefined || event.priority >= batchConfig.priorityThreshold!
      ),
      bufferTime(batchConfig.timeWindow, null, batchConfig.maxBatchSize || Number.MAX_SAFE_INTEGER),
      filter(events => events.length > 0),
      map(events =>
        createBatchResult(
          events,
          batchConfig,
          true,
          events.length >= (batchConfig.maxBatchSize || Number.MAX_SAFE_INTEGER)
        )
      )
    );

    // Merge the streams
    return highPriorityStream.pipe(
      mergeMap(result => of(result)),
      mergeMap(result =>
        lowPriorityStream.pipe(
          take(0),
          mergeMap(() => of(result)),
          catchError(() => of(result))
        )
      )
    );
  }

  // Standard batching approach
  return source.pipe(
    // Buffer events by time window and max size
    bufferTime(batchConfig.timeWindow, null, batchConfig.maxBatchSize || Number.MAX_SAFE_INTEGER),
    // Filter out empty batches
    filter(events => events.length > 0),
    // Create batch result
    map(events =>
      createBatchResult(
        events,
        batchConfig,
        true,
        events.length >= (batchConfig.maxBatchSize || Number.MAX_SAFE_INTEGER)
      )
    )
  );
}

/**
 * Create a batch result object from a batch of events
 */
function createBatchResult<T>(
  events: T[],
  config: TimeBatchConfig,
  timeWindowReached: boolean,
  maxSizeReached: boolean
): TimeBatchResult<T> {
  // Get timestamps (if available)
  const now = Date.now();
  const timestamps = events.map(event => (event as any).timestamp || now);

  // Sort events by timestamp if configured
  if (config.sortByTimestamp && events.length > 1) {
    // Create tuples of [event, timestamp]
    const tuples = events.map((event, i) => [event, timestamps[i]]);
    // Sort by timestamp
    tuples.sort((a, b) => (a[1] as number) - (b[1] as number));
    // Extract sorted events
    events = tuples.map(tuple => tuple[0]) as T[];
  }

  // Calculate start and end times
  const startTime = Math.min(...timestamps);
  const endTime = Math.max(...timestamps);

  // Calculate events per second
  const durationSec = (endTime - startTime) / 1000 || 0.001; // Avoid division by zero
  const eventsPerSecond = events.length / durationSec;

  // Create the result
  return {
    events,
    startTime,
    endTime,
    batchSize: events.length,
    timeWindowReached,
    maxSizeReached,
    eventsPerSecond,
    eventSpread: events.length > 1 ? endTime - startTime : 0,
  };
}

/**
 * Group batched events by a specific property
 */
export function groupBatchedEvents<T>(
  batchResult: TimeBatchResult<T>,
  groupFn: (event: T) => string
): Record<string, T[]> {
  const groups: Record<string, T[]> = {};

  for (const event of batchResult.events) {
    const key = groupFn(event);
    if (!groups[key]) {
      groups[key] = [];
    }
    groups[key].push(event);
  }

  return groups;
}

/**
 * Create a time-based event batch processor
 */
export function createTimeBatchProcessor<T, R>(
  processor: (events: T[]) => R,
  config: Partial<TimeBatchConfig> = {}
): (event: T) => void {
  // Create a subject for events
  const eventSubject = new Subject<T>();

  // Create a batched stream
  const batchedStream = createTimeBatchedStream(eventSubject.asObservable(), config);

  // Subscribe to the batched stream
  batchedStream.subscribe(result => {
    processor(result.events);
  });

  // Return a function that adds events to the subject
  return (event: T) => {
    eventSubject.next(event);
  };
}

/**
 * Create a throttled event stream that limits the rate of events
 */
export function createThrottledEventStream<T>(
  source: Observable<T>,
  throttleMs: number,
  trailing = false
): Observable<T> {
  return source.pipe(throttleTime(throttleMs, undefined, { leading: true, trailing }));
}

/**
 * Process events in dynamic time windows based on system load
 */
export function createAdaptiveTimeBatchedStream<T>(
  source: Observable<T>,
  getSystemLoad: () => number, // 0-1 value representing system load
  config: {
    minTimeWindow: number;
    maxTimeWindow: number;
    minBatchSize: number;
    maxBatchSize: number;
  }
): Observable<TimeBatchResult<T>> {
  return interval(100).pipe(
    // Sample system load every 100ms
    map(() => {
      const load = getSystemLoad();
      // Calculate adaptive time window based on load
      const timeWindow = Math.floor(
        config.minTimeWindow + (config.maxTimeWindow - config.minTimeWindow) * load
      );
      // Calculate adaptive batch size based on load (inverse relationship)
      const batchSize = Math.floor(
        config.maxBatchSize - (config.maxBatchSize - config.minBatchSize) * load
      );

      return { timeWindow, batchSize };
    }),
    // Switch to a new buffer strategy based on current load
    switchMap(adaptiveConfig =>
      source.pipe(
        bufferTime(adaptiveConfig.timeWindow, null, adaptiveConfig.batchSize),
        filter(events => events.length > 0),
        map(events =>
          createBatchResult(
            events,
            { timeWindow: adaptiveConfig.timeWindow },
            true,
            events.length >= adaptiveConfig.batchSize
          )
        )
      )
    )
  );
}

/**
 * @file EventDevTools.ts
 * Developer tools for monitoring and debugging event system.
 *
 * This utility provides:
 * 1. Event monitoring and visualization
 * 2. Subscription tracking
 * 3. Performance profiling
 * 4. Memory leak detection
 * 5. Event flow visualization
 */

import { EventBus, EventPerformanceMetrics } from '../../lib/events/EventBus';
import {
  BaseEvent,
  EVENT_CATEGORY_MAP,
  EventCategory,
  EventType,
} from '../../types/events/EventTypes';

/**
 * Configuration options for EventDevTools
 */
export interface EventDevToolsConfig {
  /**
   * Whether to enable dev tools (can be toggled at runtime)
   */
  enabled: boolean;

  /**
   * Maximum number of events to keep in history
   */
  maxEventHistory: number;

  /**
   * Events to exclude from logging
   */
  excludedEvents?: EventType[];

  /**
   * Categories to exclude from logging
   */
  excludedCategories?: EventCategory[];

  /**
   * Whether to log events to console
   */
  consoleLogging: boolean;

  /**
   * Whether to track subscription activity
   */
  trackSubscriptions: boolean;

  /**
   * Whether to track performance metrics
   */
  trackPerformance: boolean;

  /**
   * Threshold in milliseconds for slow event processing warnings
   */
  slowEventThreshold: number;

  /**
   * Whether to detect potential memory leaks
   */
  detectMemoryLeaks: boolean;

  /**
   * Warning threshold for number of subscribers to a single event
   */
  subscriberWarningThreshold: number;
}

/**
 * Default configuration for EventDevTools
 */
const DEFAULT_CONFIG: EventDevToolsConfig = {
  enabled: process.env.NODE_ENV === 'development',
  maxEventHistory: 100,
  consoleLogging: true,
  trackSubscriptions: true,
  trackPerformance: true,
  slowEventThreshold: 50, // ms
  detectMemoryLeaks: true,
  subscriberWarningThreshold: 20,
};

/**
 * Event history entry with additional metadata
 */
interface EventHistoryEntry<T extends BaseEvent = BaseEvent> {
  event: T;
  timestamp: number;
  processingTime?: number;
  sequenceId: number;
}

/**
 * Subscription activity entry
 */
interface SubscriptionActivity {
  eventType: EventType | '*';
  action: 'subscribe' | 'unsubscribe';
  source?: string;
  timestamp: number;
  subscriptionId: string;
}

/**
 * Memory leak detection report entry
 */
interface MemoryLeakSuspect {
  eventType: EventType | '*';
  subscriptionCount: number;
  oldestSubscriptionAge: number; // ms
  sources: string[];
}

/**
 * EventDevTools class for monitoring and debugging events
 */
export class EventDevTools<T extends BaseEvent = BaseEvent> {
  /**
   * Configuration options
   */
  private config: EventDevToolsConfig;

  /**
   * Event history with metadata
   */
  private eventHistory: EventHistoryEntry<T>[] = [];

  /**
   * Subscription activity history
   */
  private subscriptionActivity: SubscriptionActivity[] = [];

  /**
   * Event sequence counter
   */
  private eventSequence = 0;

  /**
   * Map to track processing time by event type
   */
  private eventProcessingTimes: Map<EventType, number[]> = new Map();

  /**
   * Set of excluded event types
   */
  private excludedEventTypes: Set<EventType>;

  /**
   * Set of component sources that have subscribed but not unsubscribed
   */
  private activeSubscriptionSources: Set<string> = new Set();

  /**
   * Creates a new EventDevTools instance
   * @param targetEventBus The event bus to monitor
   * @param config Configuration options
   */
  constructor(
    private readonly targetEventBus: EventBus<T>,
    config: Partial<EventDevToolsConfig> = {}
  ) {
    // Apply default config with overrides
    this.config = { ...DEFAULT_CONFIG, ...config };

    // Initialize excluded event types
    this.excludedEventTypes = new Set(this.config.excludedEvents || []);

    // Add excluded categories
    if (this.config.excludedCategories) {
      for (const category of this.config.excludedCategories) {
        // Use imported EVENT_CATEGORY_MAP instead of trying to access it from EventBus
        const categoryEvents = Object.entries(EVENT_CATEGORY_MAP)
          .filter(([_, cat]) => cat === category)
          .map(([eventType, _]) => eventType as EventType);

        categoryEvents.forEach(eventType => this.excludedEventTypes.add(eventType));
      }
    }

    // Set up monitoring if enabled
    if (this.config.enabled) {
      this.setupMonitoring();
    }
  }

  /**
   * Set up event bus monitoring
   */
  private setupMonitoring(): void {
    // Monitor all events
    const unsubscribeEvents = this.targetEventBus.subscribe('*', this.handleEvent.bind(this), {
      source: 'EventDevTools',
    });

    // Add cleanup method
    this.dispose = () => {
      unsubscribeEvents();
      console.log('[EventDevTools] Stopped monitoring events');
    };

    console.log('[EventDevTools] Started monitoring events');
  }

  /**
   * Handle an event for monitoring
   * @param event The event to monitor
   */
  private handleEvent(event: T): void {
    // Skip if disabled or event is excluded
    if (!this.config.enabled || this.excludedEventTypes.has(event.type)) {
      return;
    }

    const startTime = performance.now();
    const sequenceId = ++this.eventSequence;

    // Add to history
    this.eventHistory.push({
      event,
      timestamp: startTime,
      sequenceId,
    });

    // Trim history if needed
    if (this.eventHistory.length > this.config.maxEventHistory) {
      this.eventHistory.shift();
    }

    // Calculate processing time after all subscribers have been notified
    setTimeout(() => {
      // Get performance metrics for this event type
      const metrics = this.targetEventBus.getPerformanceMetrics(event.type);

      // Update the event history entry with processing time
      const historyEntry = this.eventHistory.find(entry => entry.sequenceId === sequenceId);
      if (historyEntry) {
        historyEntry.processingTime = metrics.lastProcessingTime;
      }

      // Track processing time for this event type
      if (!this.eventProcessingTimes.has(event.type)) {
        this.eventProcessingTimes.set(event.type, []);
      }
      this.eventProcessingTimes.get(event.type)!.push(metrics.lastProcessingTime);

      // Check for slow event processing
      if (
        this.config.trackPerformance &&
        metrics.lastProcessingTime > this.config.slowEventThreshold
      ) {
        console.warn(
          `[EventDevTools] Slow event processing: ${event.type} took ${metrics.lastProcessingTime.toFixed(2)}ms (threshold: ${this.config.slowEventThreshold}ms)`
        );
      }

      // Log event to console if enabled
      if (this.config.consoleLogging) {
        const timeString = metrics.lastProcessingTime
          ? ` (${metrics.lastProcessingTime.toFixed(2)}ms)`
          : '';

        console.log(`[EventDevTools] Event: ${event.type}${timeString}`, event);
      }
    }, 0);
  }

  /**
   * Track subscription activity
   * @param eventType The event type being subscribed to
   * @param action The subscription action (subscribe or unsubscribe)
   * @param source The source of the subscription
   * @param subscriptionId The ID of the subscription
   */
  trackSubscription(
    eventType: EventType | '*',
    action: 'subscribe' | 'unsubscribe',
    source: string = 'unknown',
    subscriptionId: string
  ): void {
    if (!this.config.enabled || !this.config.trackSubscriptions) {
      return;
    }

    // Track subscription activity
    this.subscriptionActivity.push({
      eventType,
      action,
      source,
      timestamp: Date.now(),
      subscriptionId,
    });

    // Track active subscription sources
    if (action === 'subscribe') {
      this.activeSubscriptionSources.add(source);
    } else {
      // Check if this source has any other active subscriptions
      const hasOtherSubscriptions = this.subscriptionActivity.some(
        activity =>
          activity.source === source &&
          activity.action === 'subscribe' &&
          activity.subscriptionId !== subscriptionId &&
          !this.subscriptionActivity.some(
            unsubActivity =>
              unsubActivity.action === 'unsubscribe' &&
              unsubActivity.subscriptionId === activity.subscriptionId
          )
      );

      if (!hasOtherSubscriptions) {
        this.activeSubscriptionSources.delete(source);
      }
    }

    // Check for subscriber count warnings
    const subscriberCount = this.targetEventBus.getSubscriptionCountForType(eventType);
    if (subscriberCount > this.config.subscriberWarningThreshold && action === 'subscribe') {
      console.warn(
        `[EventDevTools] High subscriber count: ${eventType} has ${subscriberCount} subscribers (threshold: ${this.config.subscriberWarningThreshold})`
      );
    }

    // Log subscription activity
    if (this.config.consoleLogging) {
      console.log(
        `[EventDevTools] ${action === 'subscribe' ? 'Subscribed to' : 'Unsubscribed from'} ${eventType} (source: ${source})`
      );
    }
  }

  /**
   * Check for potential memory leaks (components that subscribed but didn't unsubscribe)
   * @returns List of potential memory leak suspects
   */
  detectPotentialMemoryLeaks(): MemoryLeakSuspect[] {
    if (!this.config.enabled || !this.config.detectMemoryLeaks) {
      return [];
    }

    const suspects: MemoryLeakSuspect[] = [];
    const now = Date.now();
    const eventTypes = new Set([...this.subscriptionActivity.map(activity => activity.eventType)]);

    // Check each event type
    for (const eventType of eventTypes) {
      // Get subscriptions for this event type
      const subscriptions = this.subscriptionActivity.filter(
        activity => activity.eventType === eventType
      );

      // Group by source
      const sources = new Set(subscriptions.map(activity => activity.source).filter(Boolean));
      const activeSourcesForType: string[] = [];

      // For each source, check if there are subscriptions without matching unsubscriptions
      for (const source of sources) {
        if (!source) continue;

        const sourceSubscriptions = subscriptions.filter(activity => activity.source === source);

        // Count unbalanced subscriptions
        const subscribes = sourceSubscriptions.filter(activity => activity.action === 'subscribe');
        const unsubscribes = sourceSubscriptions.filter(
          activity => activity.action === 'unsubscribe'
        );

        if (subscribes.length > unsubscribes.length) {
          activeSourcesForType.push(source);
        }
      }

      // If there are active sources, calculate the oldest subscription age
      if (activeSourcesForType.length > 0) {
        const oldestSubscription = subscriptions
          .filter(
            activity =>
              activity.action === 'subscribe' &&
              activeSourcesForType.includes(activity.source || '')
          )
          .sort((a, b) => a.timestamp - b.timestamp)[0];

        const oldestSubscriptionAge = now - oldestSubscription.timestamp;

        // Add to suspects list
        suspects.push({
          eventType,
          subscriptionCount: activeSourcesForType.length,
          oldestSubscriptionAge,
          sources: activeSourcesForType,
        });
      }
    }

    return suspects;
  }

  /**
   * Generate a performance report for event processing
   * @returns Object containing performance metrics for event processing
   */
  generatePerformanceReport(): Record<string, EventPerformanceMetrics> {
    if (!this.config.enabled || !this.config.trackPerformance) {
      return {};
    }

    // Get metrics for all event types
    const metrics: Record<string, EventPerformanceMetrics> = {};

    // Get overall metrics
    metrics.all = this.targetEventBus.getPerformanceMetrics('all');

    // Get metrics for each event type that has been processed
    for (const eventType of this.eventProcessingTimes.keys()) {
      metrics[eventType.toString()] = this.targetEventBus.getPerformanceMetrics(eventType);
    }

    return metrics;
  }

  /**
   * Enable or disable event monitoring
   * @param enabled Whether to enable event monitoring
   */
  setEnabled(enabled: boolean): void {
    if (this.config.enabled === enabled) {
      return;
    }

    this.config.enabled = enabled;

    if (enabled) {
      this.setupMonitoring();
    } else {
      this.dispose();
    }
  }

  /**
   * Get event history
   * @returns Array of event history entries
   */
  getEventHistory(): EventHistoryEntry<T>[] {
    return [...this.eventHistory];
  }

  /**
   * Get subscription activity history
   * @returns Array of subscription activity entries
   */
  getSubscriptionActivity(): SubscriptionActivity[] {
    return [...this.subscriptionActivity];
  }

  /**
   * Clean up resources
   * This will be replaced when monitoring is set up
   */
  dispose(): void {
    // Default no-op, replaced when monitoring is set up
  }
}

/**
 * Singleton instance for global access
 */
let devToolsInstance: EventDevTools<BaseEvent> | null = null;

/**
 * Initialize the event dev tools with the given event bus
 * @param eventBus The event bus to monitor
 * @param config Configuration options
 * @returns The EventDevTools instance
 */
export function initializeEventDevTools<T extends BaseEvent>(
  eventBus: EventBus<T>,
  config: Partial<EventDevToolsConfig> = {}
): EventDevTools<T> {
  if (devToolsInstance) {
    console.warn('[EventDevTools] Dev tools already initialized, disposing previous instance');
    devToolsInstance.dispose();
  }

  // Create a new instance with the given event bus
  const newInstance = new EventDevTools<T>(eventBus, config);

  // Store it as the global instance (this is fine since we're just using it for type compatibility)
  devToolsInstance = newInstance as unknown as EventDevTools<BaseEvent>;

  return newInstance;
}

/**
 * Get the current EventDevTools instance
 * @returns The current EventDevTools instance, or null if not initialized
 */
export function getEventDevTools<T extends BaseEvent>(): EventDevTools<T> | null {
  return devToolsInstance as unknown as EventDevTools<T> | null;
}

/**
 * D3 Accessor Benchmark Utility
 *
 * This utility provides benchmarking tools to compare the performance of
 * type-safe accessors versus direct property access in D3 visualizations.
 */

import { d3Accessors, SimulationNodeDatum } from '../../types/visualizations/D3Types';

/**
 * Test case configuration
 */
export interface BenchmarkTestCase {
  /** Name of the test case */
  name: string;
  /** Description of what is being tested */
  description: string;
  /** Function to execute for the test */
  testFn: () => void;
  /** Number of iterations to run */
  iterations?: number;
}

/**
 * Benchmark result
 */
export interface BenchmarkResult {
  /** Name of the test case */
  name: string;
  /** Description of what was tested */
  description: string;
  /** Average execution time in milliseconds */
  averageTimeMs: number;
  /** Median execution time in milliseconds */
  medianTimeMs: number;
  /** Total execution time in milliseconds */
  totalTimeMs: number;
  /** Number of iterations run */
  iterations: number;
  /** Individual execution times */
  executionTimes: number[];
  /** Timestamp when the benchmark was run */
  timestamp: Date;
}

/**
 * Benchmark comparison result
 */
export interface BenchmarkComparison {
  /** Baseline test case name */
  baseline: string;
  /** Comparison test case name */
  comparison: string;
  /** Performance difference as a percentage (positive means slower, negative means faster) */
  percentageDifference: number;
  /** Absolute time difference in milliseconds */
  absoluteDifferenceMs: number;
  /** Whether the comparison is significantly different (>5% difference) */
  isSignificant: boolean;
}

/**
 * Run a single benchmark test
 *
 * @param testCase The test case to run
 * @returns Benchmark result
 */
export function runBenchmark(testCase: BenchmarkTestCase): BenchmarkResult {
  const iterations = testCase.iterations || 1000;
  const executionTimes: number[] = [];

  // Run test iterations
  for (let i = 0; i < iterations; i++) {
    const startTime = performance.now();
    testCase.testFn();
    const endTime = performance.now();
    executionTimes.push(endTime - startTime);
  }

  // Calculate statistics
  const totalTimeMs = executionTimes.reduce((sum, time) => sum + time, 0);
  const averageTimeMs = totalTimeMs / iterations;

  // Calculate median (sort first)
  const sortedTimes = [...executionTimes].sort((a, b) => a - b);
  const medianTimeMs =
    iterations % 2 === 0
      ? (sortedTimes[iterations / 2 - 1] + sortedTimes[iterations / 2]) / 2
      : sortedTimes[Math.floor(iterations / 2)];

  return {
    name: testCase.name,
    description: testCase.description,
    averageTimeMs,
    medianTimeMs,
    totalTimeMs,
    iterations,
    executionTimes,
    timestamp: new Date(),
  };
}

/**
 * Compare two benchmark results
 *
 * @param baseline Baseline benchmark result
 * @param comparison Comparison benchmark result
 * @returns Benchmark comparison
 */
export function compareBenchmarks(
  baseline: BenchmarkResult,
  comparison: BenchmarkResult
): BenchmarkComparison {
  const absoluteDifferenceMs = comparison.averageTimeMs - baseline.averageTimeMs;
  const percentageDifference = (absoluteDifferenceMs / baseline.averageTimeMs) * 100;

  return {
    baseline: baseline.name,
    comparison: comparison.name,
    percentageDifference,
    absoluteDifferenceMs,
    isSignificant: Math.abs(percentageDifference) > 5,
  };
}

/**
 * Format benchmark results for display
 *
 * @param result Benchmark result
 * @returns Formatted string
 */
export function formatBenchmarkResult(result: BenchmarkResult): string {
  return `
Benchmark: ${result.name}
Description: ${result.description}
Iterations: ${result.iterations}
Average Time: ${result.averageTimeMs.toFixed(6)} ms
Median Time: ${result.medianTimeMs.toFixed(6)} ms
Total Time: ${result.totalTimeMs.toFixed(2)} ms
  `.trim();
}

/**
 * Format benchmark comparison for display
 *
 * @param comparison Benchmark comparison
 * @returns Formatted string
 */
export function formatBenchmarkComparison(comparison: BenchmarkComparison): string {
  const differenceDescription =
    comparison.percentageDifference > 0
      ? `${comparison.percentageDifference.toFixed(2)}% slower`
      : `${Math.abs(comparison.percentageDifference).toFixed(2)}% faster`;

  const significance = comparison.isSignificant
    ? 'Significant difference'
    : 'No significant difference';

  return `
Comparison: ${comparison.comparison} vs ${comparison.baseline}
Difference: ${differenceDescription} (${Math.abs(comparison.absoluteDifferenceMs).toFixed(6)} ms)
Assessment: ${significance}
  `.trim();
}

// Simulate typical D3 simulation data
function generateTestNodes(count: number): SimulationNodeDatum[] {
  const nodes: SimulationNodeDatum[] = [];

  for (let i = 0; i < count; i++) {
    nodes.push({
      id: `node-${i}`,
      x: Math.random() * 100,
      y: Math.random() * 100,
      vx: Math.random() * 2 - 1,
      vy: Math.random() * 2 - 1,
    });
  }

  return nodes;
}

/**
 * Run a benchmark comparing type-safe accessors vs direct property access
 *
 * @param nodeCount Number of nodes to use in the test
 * @param iterations Number of iterations to run
 * @returns Benchmark comparison
 */
export function benchmarkAccessors(
  nodeCount: number = 1000,
  iterations: number = 10000
): BenchmarkComparison {
  const nodes = generateTestNodes(nodeCount);

  // Test case for direct property access
  const directAccessTest: BenchmarkTestCase = {
    name: 'Direct Property Access',
    description: `Access x/y coordinates directly on ${nodeCount} nodes`,
    iterations,
    testFn: () => {
      // Simulate typical D3 simulation tick function with direct access
      for (let i = 0; i < nodes.length; i++) {
        const node = nodes[i];
        // Direct property access
        const x = node.x || 0;
        const y = node.y || 0;
        // Do something with coordinates (simulate transform application)
        const transform = `translate(${x}, ${y})`;
      }
    },
  };

  // Test case for type-safe accessor functions
  const safeAccessTest: BenchmarkTestCase = {
    name: 'Type-Safe Accessor Functions',
    description: `Access x/y coordinates via d3Accessors on ${nodeCount} nodes`,
    iterations,
    testFn: () => {
      // Simulate typical D3 simulation tick function with safe accessors
      for (let i = 0; i < nodes.length; i++) {
        const node = nodes[i];
        // Safe accessor functions
        const x = d3Accessors.getX(node);
        const y = d3Accessors.getY(node);
        // Do something with coordinates (simulate transform application)
        const transform = `translate(${x}, ${y})`;
      }
    },
  };

  // Run both benchmarks
  const directResult = runBenchmark(directAccessTest);
  const safeResult = runBenchmark(safeAccessTest);

  // Compare results
  return compareBenchmarks(directResult, safeResult);
}

/**
 * Run a comprehensive set of benchmarks for D3 simulations
 */
export function runComprehensiveBenchmarks(): {
  results: BenchmarkResult[];
  comparisons: BenchmarkComparison[];
} {
  const results: BenchmarkResult[] = [];
  const comparisons: BenchmarkComparison[] = [];

  // Test scenario 1: Basic coordinate access (small dataset)
  const nodes100 = generateTestNodes(100);

  // Direct access test (small)
  const directSmall = runBenchmark({
    name: 'Direct Access (100 nodes)',
    description: 'Direct property access on 100 nodes',
    iterations: 10000,
    testFn: () => {
      for (const node of nodes100) {
        const x = node.x || 0;
        const y = node.y || 0;
        const transform = `translate(${x}, ${y})`;
      }
    },
  });
  results.push(directSmall);

  // Safe access test (small)
  const safeSmall = runBenchmark({
    name: 'Safe Access (100 nodes)',
    description: 'Type-safe accessor functions on 100 nodes',
    iterations: 10000,
    testFn: () => {
      for (const node of nodes100) {
        const x = d3Accessors.getX(node);
        const y = d3Accessors.getY(node);
        const transform = `translate(${x}, ${y})`;
      }
    },
  });
  results.push(safeSmall);

  // Compare small dataset
  comparisons.push(compareBenchmarks(directSmall, safeSmall));

  // Test scenario 2: Basic coordinate access (large dataset)
  const nodes10000 = generateTestNodes(10000);

  // Direct access test (large)
  const directLarge = runBenchmark({
    name: 'Direct Access (10000 nodes)',
    description: 'Direct property access on 10000 nodes',
    iterations: 100,
    testFn: () => {
      for (const node of nodes10000) {
        const x = node.x || 0;
        const y = node.y || 0;
        const transform = `translate(${x}, ${y})`;
      }
    },
  });
  results.push(directLarge);

  // Safe access test (large)
  const safeLarge = runBenchmark({
    name: 'Safe Access (10000 nodes)',
    description: 'Type-safe accessor functions on 10000 nodes',
    iterations: 100,
    testFn: () => {
      for (const node of nodes10000) {
        const x = d3Accessors.getX(node);
        const y = d3Accessors.getY(node);
        const transform = `translate(${x}, ${y})`;
      }
    },
  });
  results.push(safeLarge);

  // Compare large dataset
  comparisons.push(compareBenchmarks(directLarge, safeLarge));

  // Test scenario 3: Simulation tick function (realistic scenario)
  const simulationNodes = generateTestNodes(500);

  // Direct access simulation test
  const directSim = runBenchmark({
    name: 'Direct Access Simulation',
    description: 'Simulation tick function with direct property access',
    iterations: 1000,
    testFn: () => {
      // Simulate a D3 force simulation tick
      for (let i = 0; i < simulationNodes.length; i++) {
        const d = simulationNodes[i];
        // Direct property access with manual null checking
        const x = d.x !== undefined ? d.x : 0;
        const y = d.y !== undefined ? d.y : 0;

        // Apply force simulation logic (simplified)
        const vx = d.vx || 0;
        const vy = d.vy || 0;
        d.x = x + vx * 0.1;
        d.y = y + vy * 0.1;

        // Constrain to bounds
        if (d.x! < 0) d.x = 0;
        if (d.x! > 500) d.x = 500;
        if (d.y! < 0) d.y = 0;
        if (d.y! > 500) d.y = 500;
      }
    },
  });
  results.push(directSim);

  // Safe access simulation test
  const safeSim = runBenchmark({
    name: 'Safe Access Simulation',
    description: 'Simulation tick function with type-safe accessors',
    iterations: 1000,
    testFn: () => {
      // Simulate a D3 force simulation tick
      for (let i = 0; i < simulationNodes.length; i++) {
        const d = simulationNodes[i];
        // Safe accessor functions
        const x = d3Accessors.getX(d);
        const y = d3Accessors.getY(d);

        // Apply force simulation logic (simplified)
        const vx = d.vx || 0;
        const vy = d.vy || 0;
        d.x = x + vx * 0.1;
        d.y = y + vy * 0.1;

        // Constrain to bounds (type-safe version still needs non-null assertion for assignment)
        if (d.x! < 0) d.x = 0;
        if (d.x! > 500) d.x = 500;
        if (d.y! < 0) d.y = 0;
        if (d.y! > 500) d.y = 500;
      }
    },
  });
  results.push(safeSim);

  // Compare simulation tests
  comparisons.push(compareBenchmarks(directSim, safeSim));

  return { results, comparisons };
}

/**
 * Generate a comprehensive benchmark report
 */
export function generateBenchmarkReport(): string {
  const { results, comparisons } = runComprehensiveBenchmarks();

  let report = `
# D3 Accessor Performance Benchmark Report
Generated: ${new Date().toISOString()}

## Summary
This report compares the performance of type-safe accessors versus direct property access
in D3 visualizations across different scenarios and data sizes.

## Results
`;

  // Add individual results
  results.forEach(result => {
    report += `\n### ${result.name}\n`;
    report += formatBenchmarkResult(result) + '\n';
  });

  // Add comparisons
  report += `\n## Comparisons\n`;
  comparisons.forEach(comparison => {
    report += `\n${formatBenchmarkComparison(comparison)}\n`;
  });

  // Add recommendations
  report += `
## Recommendations
`;

  // Determine if type-safe accessors have a significant performance impact
  const hasSignificantImpact = comparisons.some(
    comp => comp.isSignificant && comp.percentageDifference > 0
  );

  if (hasSignificantImpact) {
    report += `
- Type-safe accessors show a measurable performance impact in some scenarios
- Consider implementing optimizations such as memoization for performance-critical sections
- Evaluate usage patterns to determine where direct access might be necessary
`;
  } else {
    report += `
- Type-safe accessors show minimal performance impact across tested scenarios
- Continue using type-safe accessors for improved code safety and maintainability
- No need for extensive optimization at this time
`;
  }

  return report;
}

/**
 * Run all benchmarks and output results to console
 */
export function runAndLogBenchmarks(): void {
  console.log('Running D3 Accessor Benchmarks...');

  const report = generateBenchmarkReport();
  console.log(report);

  console.log('Benchmark complete.');
}

/**
 * D3 Animation Frame Manager
 *
 * This module provides a centralized system for efficiently coordinating multiple
 * animations. It optimizes performance by:
 *
 * 1. Batching animations in a single requestAnimationFrame loop
 * 2. Prioritizing animations based on visibility and importance
 * 3. Distributing animation work across frames for complex animations
 * 4. Providing synchronization mechanisms between related animations
 * 5. Managing frame budgets to ensure smooth performance
 */

import * as d3 from 'd3';
import {
  AnimationConfig,
  TypedAnimationSequence,
} from '../../types/visualizations/D3AnimationTypes';
import { AnimationPerformanceReport, createAnimationProfiler } from './D3AnimationProfiler';

/**
 * Priority level for animations
 */
export type AnimationPriority = 'critical' | 'high' | 'medium' | 'low' | 'background';

/**
 * Status of a registered animation
 */
export type AnimationStatus = 'idle' | 'running' | 'paused' | 'completed' | 'error';

/**
 * Visibility state of an animation to determine if it should be updated
 */
export type AnimationVisibility = 'visible' | 'partially-visible' | 'hidden';

/**
 * Animation type to help optimize similar animations
 */
export type AnimationType = 'transition' | 'simulation' | 'timer' | 'sequence' | 'custom';

/**
 * Configuration for registered animations
 */
export interface RegisteredAnimationConfig extends AnimationConfig {
  /** Unique ID for the animation */
  id: string;
  /** User-friendly name for the animation */
  name: string;
  /** Priority level determines scheduling order */
  priority: AnimationPriority;
  /** Animation type for optimization opportunities */
  type: AnimationType;
  /** Whether the animation should continue even when off-screen */
  runWhenHidden?: boolean;
  /** Maximum amount of time per frame for this animation (ms) */
  frameTimeBudget?: number;
  /** Whether to enable profiling for this animation */
  enableProfiling?: boolean;
  /** Callback for animation completion */
  onComplete?: () => void;
  /** Synchronization group ID to coordinate related animations */
  syncGroup?: string;
}

/**
 * The animation frame callback function signature
 */
export type AnimationFrameCallback = (
  elapsed: number,
  deltaTime: number,
  frameInfo: FrameInfo
) => boolean | void;

/**
 * Information about the current animation frame
 */
export interface FrameInfo {
  /** Frame timestamp */
  timestamp: number;
  /** Time elapsed since animation start */
  elapsed: number;
  /** Time since last frame */
  deltaTime: number;
  /** Current frame number */
  frameCount: number;
  /** Duration left in the frame budget (ms) */
  remainingFrameBudget: number;
  /** Whether the animation should try to finish critical work this frame */
  isFrameOverBudget: boolean;
  /** Current FPS based on recent frames */
  currentFps: number;
}

/**
 * Registered animation object
 */
export interface RegisteredAnimation {
  /** Animation configuration */
  config: RegisteredAnimationConfig;
  /** Frame callback function */
  callback: AnimationFrameCallback;
  /** Current animation status */
  status: AnimationStatus;
  /** Current visibility state */
  visibility: AnimationVisibility;
  /** Start time of the animation */
  startTime: number;
  /** Last frame timestamp */
  lastFrameTime: number;
  /** Elapsed time since start */
  elapsedTime: number;
  /** Elapsed time when paused (to resume from) */
  pausedElapsedTime?: number;
  /** Pause start timestamp */
  pauseStartTime?: number;
  /** Current frame number */
  frameCount: number;
  /** Animation-specific data storage */
  data?: Record<string, unknown>;
  /** Performance profiling data if enabled */
  profilingData?: {
    /** Animation profiler if enabled */
    profiler: ReturnType<typeof createAnimationProfiler>;
    /** Animation performance report if available */
    report?: AnimationPerformanceReport;
  };
}

/**
 * Configuration for the animation frame manager
 */
export interface AnimationFrameManagerConfig {
  /** Target frames per second */
  targetFps?: number;
  /** Frame budget in milliseconds (defaults to 16ms for 60fps) */
  frameBudget?: number;
  /** Whether to enable auto-pausing of hidden animations */
  autoPauseHidden?: boolean;
  /** Maximum animations to process per frame */
  maxAnimationsPerFrame?: number;
  /** Whether to enable performance profiling */
  enableProfiling?: boolean;
  /** Whether to automatically cancel long-running animations */
  autoCancelLongRunning?: boolean;
  /** Time threshold for long-running animations in ms */
  longRunningThreshold?: number;
  /** Whether to log diagnostic information to console */
  debugMode?: boolean;
}

/**
 * Creates a centralized animation frame manager for coordinating multiple animations
 *
 * @param config Configuration options for the manager
 * @returns Animation frame manager API
 */
export function createAnimationFrameManager(config: AnimationFrameManagerConfig = {}) {
  // Configuration with defaults
  const {
    targetFps = 60,
    frameBudget = (1000 / targetFps) * 0.8, // 80% of frame time
    autoPauseHidden = true,
    maxAnimationsPerFrame = 0, // 0 means no limit
    enableProfiling = false,
    autoCancelLongRunning = false,
    longRunningThreshold = 30000, // 30 seconds
    debugMode = false,
  } = config;

  // Internal state
  const animations = new Map<string, RegisteredAnimation>();
  const syncGroups = new Map<string, Set<string>>();
  let isRunning = false;
  let lastFrameTimestamp = 0;
  let rafId: number | null = null;
  let frameCount = 0;
  let frameTimes: number[] = [];
  let startTime = 0;

  // FPS tracking (last 60 frames)
  const fpsBufferSize = 60;
  const fpsBuffer: number[] = Array(fpsBufferSize).fill(0);
  let fpsBufferIndex = 0;
  let currentFps = targetFps;

  /**
   * Update FPS calculation with the latest frame time
   *
   * @param deltaTime Time since last frame in ms
   */
  function updateFps(deltaTime: number) {
    // Add latest frame time to buffer
    fpsBuffer[fpsBufferIndex] = deltaTime > 0 ? 1000 / deltaTime : 0;
    fpsBufferIndex = (fpsBufferIndex + 1) % fpsBufferSize;

    // Calculate average FPS from buffer, ignoring zeros
    const validFrames = fpsBuffer.filter(fps => fps > 0);
    currentFps =
      validFrames.length > 0
        ? validFrames.reduce((sum, fps) => sum + fps, 0) / validFrames.length
        : targetFps;
  }

  /**
   * Main animation frame loop
   *
   * @param timestamp Current frame timestamp
   */
  function animationFrame(timestamp: number) {
    // Calculate timing information
    const deltaTime = lastFrameTimestamp ? timestamp - lastFrameTimestamp : 0;
    lastFrameTimestamp = timestamp;
    const elapsedTime = timestamp - startTime;

    // Update FPS tracking
    updateFps(deltaTime);
    frameCount++;

    // Update frame timing history (keep last 10 frames)
    frameTimes.push(deltaTime);
    if (frameTimes.length > 10) {
      frameTimes.shift();
    }

    // Process animations in priority order
    const frameStartTime = performance.now();
    let frameTimeBudgetRemaining = frameBudget;
    let animationsProcessed = 0;

    // Sort animations by priority
    const sortedAnimations = Array.from(animations.values())
      .filter(anim => anim.status === 'running')
      .sort((a, b) => {
        // Order by priority first
        const priorityOrder = {
          critical: 0,
          high: 1,
          medium: 2,
          low: 3,
          background: 4,
        };

        const priorityDiff = priorityOrder[a.config.priority] - priorityOrder[b.config.priority];
        if (priorityDiff !== 0) return priorityDiff;

        // Then by visibility
        const visibilityOrder = {
          visible: 0,
          'partially-visible': 1,
          hidden: 2,
        };

        return visibilityOrder[a.visibility] - visibilityOrder[b.visibility];
      });

    // Process each animation
    for (const animation of sortedAnimations) {
      // Skip if we've reached the per-frame animation limit
      if (maxAnimationsPerFrame > 0 && animationsProcessed >= maxAnimationsPerFrame) {
        if (debugMode) {
          console.debug(
            `Animation frame manager: Reached max animations per frame (${maxAnimationsPerFrame})`
          );
        }
        break;
      }

      // Skip hidden animations if configured to do so
      if (autoPauseHidden && animation.visibility === 'hidden' && !animation.config.runWhenHidden) {
        continue;
      }

      // Check if we have time remaining in the frame budget
      const now = performance.now();
      const timeElapsed = now - frameStartTime;
      frameTimeBudgetRemaining = Math.max(0, frameBudget - timeElapsed);

      // Skip if we're out of time
      if (frameTimeBudgetRemaining <= 0) {
        if (debugMode) {
          console.debug(
            `Animation frame manager: Frame budget exhausted, skipping remaining animations`
          );
        }
        break;
      }

      // Prepare frame info
      const frameInfo: FrameInfo = {
        timestamp,
        elapsed: elapsedTime,
        deltaTime,
        frameCount,
        remainingFrameBudget: frameTimeBudgetRemaining,
        isFrameOverBudget:
          frameTimeBudgetRemaining < (animation.config.frameTimeBudget || frameBudget * 0.2),
        currentFps,
      };

      // Update animation timing info
      animation.lastFrameTime = timestamp;
      animation.elapsedTime = timestamp - animation.startTime;
      animation.frameCount++;

      // Record frame for profiling if enabled
      if (animation.config.enableProfiling && animation.profilingData?.profiler) {
        animation.profilingData.profiler.recordFrame();
      }

      try {
        // Execute animation callback
        const callbackStart = performance.now();
        const result = animation.callback(animation.elapsedTime, deltaTime, frameInfo);
        const callbackDuration = performance.now() - callbackStart;

        // Handle result (return true to stop the animation)
        if (result === true) {
          completeAnimation(animation.config.id);
        }

        // Auto-cancel long-running animations if enabled
        if (
          autoCancelLongRunning &&
          animation.elapsedTime > longRunningThreshold &&
          !animation.config.loop
        ) {
          if (debugMode) {
            console.debug(
              `Animation frame manager: Auto-cancelling long-running animation ${animation.config.id}`
            );
          }
          completeAnimation(animation.config.id);
        }
      } catch (error) {
        console.error(`Error in animation ${animation.config.id}:`, error);
        animation.status = 'error';
      }

      animationsProcessed++;
    }

    // Continue the animation loop if we have active animations
    if (animations.size > 0 && hasRunningAnimations()) {
      rafId = requestAnimationFrame(animationFrame);
    } else {
      stopAnimationLoop();
    }
  }

  /**
   * Check if there are any running animations
   */
  function hasRunningAnimations(): boolean {
    return Array.from(animations.values()).some(anim => anim.status === 'running');
  }

  /**
   * Start the animation frame loop
   */
  function startAnimationLoop() {
    if (isRunning) return;

    isRunning = true;
    startTime = performance.now();
    lastFrameTimestamp = 0;
    frameCount = 0;
    frameTimes = [];

    if (debugMode) {
      console.debug('Animation frame manager: Starting animation loop');
    }

    rafId = requestAnimationFrame(animationFrame);
  }

  /**
   * Stop the animation frame loop
   */
  function stopAnimationLoop() {
    if (!isRunning) return;

    isRunning = false;

    if (rafId !== null) {
      cancelAnimationFrame(rafId);
      rafId = null;
    }

    if (debugMode) {
      console.debug('Animation frame manager: Stopping animation loop');
    }
  }

  /**
   * Register a new animation with the frame manager
   *
   * @param config Animation configuration
   * @param callback Animation frame callback function
   * @returns Animation ID
   */
  function registerAnimation(
    config: Omit<RegisteredAnimationConfig, 'id'> & { id?: string },
    callback: AnimationFrameCallback
  ): string {
    // Generate ID if not provided
    const id = config.id || `animation-${Date.now()}-${Math.floor(Math.random() * 10000)}`;

    // Create full configuration with defaults
    const fullConfig: RegisteredAnimationConfig = {
      id,
      name: config.name || `Animation ${id}`,
      priority: config.priority || 'medium',
      type: config.type || 'custom',
      duration: config.duration || 1000,
      easing: config.easing,
      loop: config.loop || false,
      runWhenHidden: config.runWhenHidden || false,
      frameTimeBudget: config.frameTimeBudget || frameBudget * 0.5,
      enableProfiling: config.enableProfiling || enableProfiling,
      onComplete: config.onComplete,
      syncGroup: config.syncGroup,
    };

    // Create animation entry
    const animation: RegisteredAnimation = {
      config: fullConfig,
      callback,
      status: 'idle',
      visibility: 'visible',
      startTime: 0,
      lastFrameTime: 0,
      elapsedTime: 0,
      frameCount: 0,
    };

    // Set up profiling if enabled
    if (fullConfig.enableProfiling) {
      animation.profilingData = {
        profiler: createAnimationProfiler({
          targetFps,
          detailedMetrics: true,
        }),
      };
    }

    // Add to sync group if specified
    if (fullConfig.syncGroup) {
      let group = syncGroups.get(fullConfig.syncGroup);
      if (!group) {
        group = new Set<string>();
        syncGroups.set(fullConfig.syncGroup, group);
      }
      group.add(id);
    }

    // Store the animation
    animations.set(id, animation);

    if (debugMode) {
      console.debug(`Animation frame manager: Registered animation ${id} (${fullConfig.name})`);
    }

    return id;
  }

  /**
   * Start a registered animation
   *
   * @param id Animation ID
   * @param resetTime Whether to reset the animation time
   */
  function startAnimation(id: string, resetTime = true): void {
    const animation = animations.get(id);
    if (!animation) {
      if (debugMode) {
        console.warn(`Animation frame manager: Cannot start animation ${id} - not found`);
      }
      return;
    }

    // Reset or resume timing
    const now = performance.now();
    if (resetTime) {
      animation.startTime = now;
      animation.lastFrameTime = now;
      animation.elapsedTime = 0;
      animation.frameCount = 0;
    } else if (animation.status === 'paused' && animation.pausedElapsedTime !== undefined) {
      // Resume from pause - adjust start time to maintain elapsed time
      animation.startTime = now - animation.pausedElapsedTime;
      animation.lastFrameTime = now;
    }

    animation.status = 'running';

    // Reset pause tracking
    animation.pausedElapsedTime = undefined;
    animation.pauseStartTime = undefined;

    // Start profiling if enabled
    if (animation.config.enableProfiling && animation.profilingData?.profiler) {
      animation.profilingData.profiler.start(
        animation.config.id,
        animation.config.name,
        animation.config
      );
    }

    if (debugMode) {
      console.debug(`Animation frame manager: Started animation ${id} (${animation.config.name})`);
    }

    // Start the animation loop if needed
    if (!isRunning) {
      startAnimationLoop();
    }
  }

  /**
   * Pause a running animation
   *
   * @param id Animation ID
   */
  function pauseAnimation(id: string): void {
    const animation = animations.get(id);
    if (!animation || animation.status !== 'running') {
      return;
    }

    animation.status = 'paused';
    animation.pausedElapsedTime = animation.elapsedTime;
    animation.pauseStartTime = performance.now();

    if (debugMode) {
      console.debug(`Animation frame manager: Paused animation ${id} (${animation.config.name})`);
    }
  }

  /**
   * Resume a paused animation
   *
   * @param id Animation ID
   */
  function resumeAnimation(id: string): void {
    const animation = animations.get(id);
    if (!animation || animation.status !== 'paused') {
      return;
    }

    startAnimation(id, false);

    if (debugMode) {
      console.debug(`Animation frame manager: Resumed animation ${id} (${animation.config.name})`);
    }
  }

  /**
   * Stop and complete an animation
   *
   * @param id Animation ID
   */
  function completeAnimation(id: string): void {
    const animation = animations.get(id);
    if (!animation) {
      return;
    }

    animation.status = 'completed';

    // Generate final profiling report if enabled
    if (animation.config.enableProfiling && animation.profilingData?.profiler) {
      animation.profilingData.report = animation.profilingData.profiler.stop();
    }

    // Call completion handler if provided
    if (animation.config.onComplete) {
      try {
        animation.config.onComplete();
      } catch (error) {
        console.error(`Error in animation completion handler for ${id}:`, error);
      }
    }

    if (debugMode) {
      console.debug(
        `Animation frame manager: Completed animation ${id} (${animation.config.name})`
      );
    }
  }

  /**
   * Cancel an animation without completing it
   *
   * @param id Animation ID
   */
  function cancelAnimation(id: string): void {
    const animation = animations.get(id);
    if (!animation) {
      return;
    }

    // Stop profiling if active
    if (
      animation.config.enableProfiling &&
      animation.profilingData?.profiler &&
      animation.profilingData.profiler.getStatus().isRunning
    ) {
      animation.profilingData.report = animation.profilingData.profiler.stop();
    }

    animations.delete(id);

    // Remove from sync group if needed
    if (animation.config.syncGroup) {
      const group = syncGroups.get(animation.config.syncGroup);
      if (group) {
        group.delete(id);
        if (group.size === 0) {
          syncGroups.delete(animation.config.syncGroup);
        }
      }
    }

    if (debugMode) {
      console.debug(
        `Animation frame manager: Cancelled animation ${id} (${animation.config.name})`
      );
    }
  }

  /**
   * Update the visibility state of an animation
   *
   * @param id Animation ID
   * @param visibility New visibility state
   */
  function updateVisibility(id: string, visibility: AnimationVisibility): void {
    const animation = animations.get(id);
    if (!animation) {
      return;
    }

    const oldVisibility = animation.visibility;
    animation.visibility = visibility;

    // Auto-pause/resume based on visibility if enabled
    if (autoPauseHidden && !animation.config.runWhenHidden) {
      if (visibility === 'hidden' && animation.status === 'running') {
        pauseAnimation(id);
      } else if (
        visibility !== 'hidden' &&
        oldVisibility === 'hidden' &&
        animation.status === 'paused'
      ) {
        resumeAnimation(id);
      }
    }

    if (debugMode) {
      console.debug(`Animation frame manager: Updated visibility for ${id} to ${visibility}`);
    }
  }

  /**
   * Update the priority of an animation
   *
   * @param id Animation ID
   * @param priority New priority level
   */
  function updatePriority(id: string, priority: AnimationPriority): void {
    const animation = animations.get(id);
    if (!animation) {
      return;
    }

    animation.config.priority = priority;

    if (debugMode) {
      console.debug(`Animation frame manager: Updated priority for ${id} to ${priority}`);
    }
  }

  /**
   * Synchronize multiple animations in the same group
   *
   * @param groupId Synchronization group ID
   * @param action Action to perform on all animations in the group
   */
  function syncAnimations(
    groupId: string,
    action: 'start' | 'pause' | 'resume' | 'complete' | 'cancel'
  ): void {
    const group = syncGroups.get(groupId);
    if (!group || group.size === 0) {
      return;
    }

    for (const animationId of group) {
      switch (action) {
        case 'start':
          startAnimation(animationId);
          break;
        case 'pause':
          pauseAnimation(animationId);
          break;
        case 'resume':
          resumeAnimation(animationId);
          break;
        case 'complete':
          completeAnimation(animationId);
          break;
        case 'cancel':
          cancelAnimation(animationId);
          break;
      }
    }

    if (debugMode) {
      console.debug(`Animation frame manager: Synchronized group ${groupId} with action ${action}`);
    }
  }

  /**
   * Get performance metrics for an animation
   *
   * @param id Animation ID
   * @returns Performance report if available
   */
  function getPerformanceReport(id: string): AnimationPerformanceReport | undefined {
    const animation = animations.get(id);
    if (!animation || !animation.profilingData) {
      return undefined;
    }

    // If animation is still running, get current report
    if (
      animation.status === 'running' &&
      animation.profilingData.profiler &&
      animation.profilingData.profiler.getStatus().isRunning
    ) {
      return animation.profilingData.profiler.stop();
    }

    return animation.profilingData.report;
  }

  /**
   * Get the current status of the animation frame manager
   */
  function getStatus() {
    return {
      isRunning,
      animationCount: animations.size,
      runningAnimations: Array.from(animations.values()).filter(a => a.status === 'running').length,
      frameCount,
      currentFps,
      syncGroups: syncGroups.size,
      averageFrameTime:
        frameTimes.length > 0
          ? frameTimes.reduce((sum, time) => sum + time, 0) / frameTimes.length
          : 0,
    };
  }

  /**
   * Get all registered animations
   */
  function getAnimations() {
    return Array.from(animations.entries()).map(([id, anim]) => ({
      id,
      name: anim.config.name,
      status: anim.status,
      priority: anim.config.priority,
      visibility: anim.visibility,
      type: anim.config.type,
      elapsedTime: anim.elapsedTime,
      frameCount: anim.frameCount,
    }));
  }

  /**
   * Check whether an animation is visible in the viewport
   *
   * @param element Element to check visibility for
   * @returns Visibility state based on element position
   */
  function checkElementVisibility(element: Element): AnimationVisibility {
    if (!element) {
      return 'hidden';
    }

    // Use IntersectionObserver API if available
    if ('IntersectionObserver' in window) {
      // This is a simplified version - in practice, you would set up and maintain
      // observers for elements. For now, we'll use getBoundingClientRect as a fallback.
    }

    // Fallback to getBoundingClientRect
    const rect = element.getBoundingClientRect();
    const windowHeight = window.innerHeight || document.documentElement.clientHeight;
    const windowWidth = window.innerWidth || document.documentElement.clientWidth;

    // Check if completely visible
    if (
      rect.top >= 0 &&
      rect.left >= 0 &&
      rect.bottom <= windowHeight &&
      rect.right <= windowWidth
    ) {
      return 'visible';
    }

    // Check if partially visible
    if (
      rect.top <= windowHeight &&
      rect.bottom >= 0 &&
      rect.left <= windowWidth &&
      rect.right >= 0
    ) {
      return 'partially-visible';
    }

    // Not visible at all
    return 'hidden';
  }

  /**
   * Create a utility that automatically updates animation visibility based on element visibility
   *
   * @param animationId Animation ID
   * @param element Element to track
   * @param options Configuration options
   * @returns Cleanup function
   */
  function createVisibilityTracker(
    animationId: string,
    element: Element,
    options: {
      checkInterval?: number; // How often to check visibility (ms)
      autoStartWhenVisible?: boolean; // Whether to auto-start the animation when visible
      autoPauseWhenHidden?: boolean; // Whether to auto-pause when hidden
    } = {}
  ): () => void {
    const {
      checkInterval = 500,
      autoStartWhenVisible = false,
      autoPauseWhenHidden = true,
    } = options;

    // Initial visibility check
    let currentVisibility = checkElementVisibility(element);
    updateVisibility(animationId, currentVisibility);

    // Set up interval to check visibility
    const intervalId = setInterval(() => {
      const newVisibility = checkElementVisibility(element);

      // Only update if visibility changed
      if (newVisibility !== currentVisibility) {
        currentVisibility = newVisibility;
        updateVisibility(animationId, newVisibility);

        // Handle auto-start/pause if configured
        const animation = animations.get(animationId);
        if (!animation) return;

        if (
          autoStartWhenVisible &&
          (newVisibility === 'visible' || newVisibility === 'partially-visible') &&
          animation.status === 'idle'
        ) {
          startAnimation(animationId);
        } else if (
          autoPauseWhenHidden &&
          newVisibility === 'hidden' &&
          animation.status === 'running'
        ) {
          pauseAnimation(animationId);
        } else if (
          autoStartWhenVisible &&
          (newVisibility === 'visible' || newVisibility === 'partially-visible') &&
          animation.status === 'paused'
        ) {
          resumeAnimation(animationId);
        }
      }
    }, checkInterval);

    // Return cleanup function
    return () => {
      clearInterval(intervalId);
    };
  }

  /**
   * Create animation synchronization utility that keeps animations in sync
   * even when some are paused or resumed at different times
   *
   * @param animationIds Animation IDs to synchronize
   * @returns Synchronized animation control functions
   */
  function createSynchronizedAnimations(animationIds: string[]) {
    // Generate a unique sync group ID
    const syncGroupId = `sync-group-${Date.now()}-${Math.floor(Math.random() * 10000)}`;

    // Update all animations to use this sync group
    animationIds.forEach(id => {
      const animation = animations.get(id);
      if (animation) {
        animation.config.syncGroup = syncGroupId;

        // Add to sync group
        let group = syncGroups.get(syncGroupId);
        if (!group) {
          group = new Set<string>();
          syncGroups.set(syncGroupId, group);
        }
        group.add(id);
      }
    });

    // Return control functions that operate on the whole group
    return {
      startAll: () => syncAnimations(syncGroupId, 'start'),
      pauseAll: () => syncAnimations(syncGroupId, 'pause'),
      resumeAll: () => syncAnimations(syncGroupId, 'resume'),
      completeAll: () => syncAnimations(syncGroupId, 'complete'),
      cancelAll: () => syncAnimations(syncGroupId, 'cancel'),
      getSyncGroupId: () => syncGroupId,
    };
  }

  // Return the public API
  return {
    registerAnimation,
    startAnimation,
    pauseAnimation,
    resumeAnimation,
    completeAnimation,
    cancelAnimation,
    updateVisibility,
    updatePriority,
    getPerformanceReport,
    getStatus,
    getAnimations,
    checkElementVisibility,
    createVisibilityTracker,
    createSynchronizedAnimations,

    // Direct sync group management
    syncAnimations,
  };
}

/**
 * Singleton instance for simple usage
 */
export const animationFrameManager = createAnimationFrameManager();

/**
 * Helper to register a D3 transition with the animation frame manager
 *
 * @param selection D3 selection to animate
 * @param config Animation configuration
 * @returns Animation ID and the selection
 */
export function registerD3Transition<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  config: Omit<RegisteredAnimationConfig, 'id' | 'type'> & { id?: string }
): { id: string; selection: d3.Selection<GElement, Datum, PElement, PDatum> } {
  // Create complete configuration
  const fullConfig: Omit<RegisteredAnimationConfig, 'id'> & { id?: string } = {
    ...config,
    type: 'transition',
  };

  // Store original transition method
  const originalTransition = selection.transition;

  // Register animation with frame manager
  const id = animationFrameManager.registerAnimation(
    fullConfig,
    (elapsed, deltaTime, frameInfo) => {
      // Calculate progress based on elapsed time
      const duration = fullConfig.duration || 1000;
      const progress = Math.min(1, elapsed / duration);

      // Check if animation is complete
      if (progress >= 1 && !fullConfig.loop) {
        return true; // Complete the animation
      }

      return false;
    }
  );

  // Start the animation
  animationFrameManager.startAnimation(id);

  return { id, selection };
}

/**
 * Helper to register a D3 timer animation with the frame manager
 *
 * @param callback D3 timer callback
 * @param config Animation configuration
 * @returns Animation ID and stop function
 */
export function registerD3Timer(
  callback: (elapsed: number) => boolean | void,
  config: Omit<RegisteredAnimationConfig, 'id' | 'type'> & { id?: string }
): { id: string; stop: () => void } {
  // Create complete configuration
  const fullConfig: Omit<RegisteredAnimationConfig, 'id'> & { id?: string } = {
    ...config,
    type: 'timer',
  };

  // Register animation with frame manager
  const id = animationFrameManager.registerAnimation(
    fullConfig,
    (elapsed, deltaTime, frameInfo) => {
      // Call the original callback
      const result = callback(elapsed);

      // Check if animation should complete
      if (
        result === true ||
        (fullConfig.duration && elapsed >= fullConfig.duration && !fullConfig.loop)
      ) {
        return true; // Complete the animation
      }

      return false;
    }
  );

  // Start the animation
  animationFrameManager.startAnimation(id);

  // Return the ID and a stop function
  return {
    id,
    stop: () => animationFrameManager.cancelAnimation(id),
  };
}

/**
 * Register a TypedAnimationSequence with the frame manager for optimal performance
 *
 * @param sequence Animation sequence to register
 * @param config Animation configuration
 * @returns Animation ID and sequence controller
 */
export function registerAnimationSequence<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(
  sequence: TypedAnimationSequence<GElement, Datum, PElement, PDatum>,
  config: Omit<RegisteredAnimationConfig, 'id' | 'type'> & { id?: string }
): {
  id: string;
  controller: { start: () => void; stop: () => void; pause: () => void; resume: () => void };
} {
  // Store original sequence methods to intercept them
  const originalStart = sequence.start;
  const originalStop = sequence.stop;

  // Create complete configuration
  const fullConfig: Omit<RegisteredAnimationConfig, 'id'> & { id?: string } = {
    ...config,
    type: 'sequence',
  };

  // Register animation with frame manager
  const id = animationFrameManager.registerAnimation(
    fullConfig,
    (elapsed, deltaTime, frameInfo) => {
      // Check if sequence is complete based on its configuration
      // Since we don't have direct access to sequence's internal state,
      // we rely on the manager to handle timing
      const duration = fullConfig.duration || 1000;

      if (elapsed >= duration && !fullConfig.loop) {
        return true; // Complete the animation
      }

      return false;
    }
  );

  // Create controller
  const controller = {
    start: () => {
      // Start in frame manager
      animationFrameManager.startAnimation(id);
      // Start the actual sequence
      originalStart.call(sequence);
    },
    stop: () => {
      // Stop in frame manager
      animationFrameManager.cancelAnimation(id);
      // Stop the actual sequence
      originalStop.call(sequence);
    },
    pause: () => {
      animationFrameManager.pauseAnimation(id);
      // Note: TypedAnimationSequence doesn't have pause method by default
    },
    resume: () => {
      animationFrameManager.resumeAnimation(id);
      // Note: TypedAnimationSequence doesn't have resume method by default
    },
  };

  return { id, controller };
}

/**
 * D3 Animation Performance Profiler
 *
 * This module provides specialized profiling tools for measuring and improving
 * animation performance in D3 visualizations. It includes utilities for:
 *
 * 1. Tracking frame rates and frame timing during animations
 * 2. Measuring interpolation performance
 * 3. Identifying bottlenecks in animation sequences
 * 4. Generating performance reports and recommendations
 */

import * as d3 from 'd3';
import {
  AnimationConfig,
  TypedAnimationSequence,
} from '../../types/visualizations/D3AnimationTypes';

/**
 * Performance metrics for a single animation frame
 */
export interface AnimationFrameMetrics {
  /** Timestamp when the frame started processing */
  frameStartTime: number;
  /** Timestamp when the frame was completed */
  frameEndTime: number;
  /** Duration of the frame in milliseconds */
  frameDuration: number;
  /** Number of DOM updates in this frame */
  domUpdateCount: number;
  /** Number of interpolations performed in this frame */
  interpolationCount: number;
  /** Time spent on interpolation calculations */
  interpolationTime: number;
  /** Time spent on DOM updates */
  domUpdateTime: number;
}

/**
 * Performance data for a complete animation
 */
export interface AnimationPerformanceData {
  /** Unique ID for the animation being profiled */
  animationId: string;
  /** Name of the animation being profiled */
  animationName: string;
  /** When the animation started */
  startTime: number;
  /** When the animation ended */
  endTime: number;
  /** Total duration of the animation */
  totalDuration: number;
  /** Configuration of the animation */
  animationConfig: AnimationConfig;
  /** Performance metrics for each frame */
  frames: AnimationFrameMetrics[];
  /** Average frame duration */
  averageFrameDuration: number;
  /** Minimum frame duration */
  minFrameDuration: number;
  /** Maximum frame duration */
  maxFrameDuration: number;
  /** Target FPS */
  targetFps: number;
  /** Actual average FPS achieved */
  actualFps: number;
  /** Number of frames that exceeded target frame duration */
  droppedFrames: number;
  /** Percentage of frames that met the target duration */
  frameSuccessRate: number;
}

/**
 * Performance bottleneck identification
 */
export interface AnimationBottleneck {
  /** Type of bottleneck */
  type: 'interpolation' | 'dom_updates' | 'javascript' | 'rendering' | 'unknown';
  /** Severity level from 0-1 (0 = minor, 1 = severe) */
  severity: number;
  /** Description of the bottleneck */
  description: string;
  /** Suggested fix */
  suggestion: string;
  /** Frame indices where this bottleneck occurred */
  affectedFrames: number[];
}

/**
 * Animation performance report
 */
export interface AnimationPerformanceReport {
  /** Performance data for the animation */
  performanceData: AnimationPerformanceData;
  /** Identified bottlenecks */
  bottlenecks: AnimationBottleneck[];
  /** Overall performance score (0-100) */
  performanceScore: number;
  /** Specific recommendations to improve performance */
  recommendations: string[];
  /** Whether the animation meets performance targets */
  meetsTargets: boolean;
}

/**
 * Configuration for animation profiling
 */
export interface AnimationProfilerConfig {
  /** Target FPS to maintain */
  targetFps?: number;
  /** Enable detailed per-frame metrics */
  detailedMetrics?: boolean;
  /** Duration to profile (ms) - undefined for entire animation */
  profileDuration?: number;
  /** Whether to track DOM updates */
  trackDomUpdates?: boolean;
  /** Whether to track interpolation performance */
  trackInterpolation?: boolean;
  /** Callback to execute when profiling is complete */
  onComplete?: (report: AnimationPerformanceReport) => void;
}

/**
 * Creates a profiler for measuring animation performance
 *
 * @param config Profiler configuration
 * @returns Animation profiler object
 */
export function createAnimationProfiler(config: AnimationProfilerConfig = {}) {
  const {
    targetFps = 60,
    detailedMetrics = true,
    profileDuration,
    trackDomUpdates = true,
    trackInterpolation = true,
    onComplete,
  } = config;

  // Initialize profiling state
  let animationId = `animation-${Date.now()}`;
  let animationName = 'Unnamed Animation';
  let isRunning = false;
  let startTime = 0;
  let frameCount = 0;
  let frames: AnimationFrameMetrics[] = [];
  let lastFrameTime = 0;
  let animationConfig: AnimationConfig = { duration: 0 };
  let selectionUpdates = 0;
  let interpolationMeasurements: Array<{ count: number; duration: number }> = [];

  const targetFrameDuration = 1000 / targetFps;

  /**
   * Starts profiling an animation
   *
   * @param id Optional custom ID for the animation
   * @param name Optional name for the animation
   * @param config Configuration of the animation being profiled
   */
  function start(id?: string, name?: string, config?: AnimationConfig) {
    if (isRunning) {
      stop();
    }

    if (id) {
      animationId = id;
    }

    if (name) {
      animationName = name;
    }

    if (config) {
      animationConfig = config;
    }

    isRunning = true;
    startTime = performance.now();
    lastFrameTime = startTime;
    frameCount = 0;
    frames = [];
    selectionUpdates = 0;
    interpolationMeasurements = [];

    // Set up duration-limited profiling if requested
    if (profileDuration) {
      setTimeout(() => {
        if (isRunning) {
          stop();
        }
      }, profileDuration);
    }
  }

  /**
   * Records metrics for a single animation frame
   *
   * @param frameMetrics Optional metrics to include
   */
  function recordFrame(frameMetrics?: Partial<AnimationFrameMetrics>) {
    if (!isRunning) return;

    const now = performance.now();
    const frameDuration = now - lastFrameTime;

    const metrics: AnimationFrameMetrics = {
      frameStartTime: lastFrameTime,
      frameEndTime: now,
      frameDuration,
      domUpdateCount: frameMetrics?.domUpdateCount || 0,
      interpolationCount: frameMetrics?.interpolationCount || 0,
      interpolationTime: frameMetrics?.interpolationTime || 0,
      domUpdateTime: frameMetrics?.domUpdateTime || 0,
    };

    frames.push(metrics);
    frameCount++;
    lastFrameTime = now;
  }

  /**
   * Records DOM update performance
   *
   * @param updateCount Number of elements updated
   * @param duration Time taken for the updates
   */
  function recordDomUpdates(updateCount: number, duration: number) {
    if (!isRunning || !trackDomUpdates) return;

    selectionUpdates += updateCount;

    // Update the last frame with DOM update information
    if (frames.length > 0) {
      const lastFrame = frames[frames.length - 1];
      lastFrame.domUpdateCount += updateCount;
      lastFrame.domUpdateTime += duration;
    }
  }

  /**
   * Records interpolation performance
   *
   * @param count Number of interpolations performed
   * @param duration Time taken for the interpolations
   */
  function recordInterpolation(count: number, duration: number) {
    if (!isRunning || !trackInterpolation) return;

    interpolationMeasurements.push({ count, duration });

    // Update the last frame with interpolation information
    if (frames.length > 0) {
      const lastFrame = frames[frames.length - 1];
      lastFrame.interpolationCount += count;
      lastFrame.interpolationTime += duration;
    }
  }

  /**
   * Stops profiling and generates a performance report
   *
   * @returns A performance report for the animation
   */
  function stop(): AnimationPerformanceReport {
    if (!isRunning) {
      return createEmptyReport();
    }

    isRunning = false;
    const endTime = performance.now();
    const totalDuration = endTime - startTime;

    // Calculate frame statistics
    const frameDurations = frames.map(f => f.frameDuration);
    const averageFrameDuration =
      frameDurations.reduce((sum, duration) => sum + duration, 0) / frames.length || 0;
    const minFrameDuration = Math.min(...frameDurations) || 0;
    const maxFrameDuration = Math.max(...frameDurations) || 0;
    const actualFps = 1000 / averageFrameDuration || 0;
    const droppedFrames = frames.filter(f => f.frameDuration > targetFrameDuration).length;
    const frameSuccessRate = 1 - droppedFrames / frames.length || 0;

    // Create performance data object
    const performanceData: AnimationPerformanceData = {
      animationId,
      animationName,
      startTime,
      endTime,
      totalDuration,
      animationConfig,
      frames,
      averageFrameDuration,
      minFrameDuration,
      maxFrameDuration,
      targetFps,
      actualFps,
      droppedFrames,
      frameSuccessRate,
    };

    // Identify bottlenecks
    const bottlenecks = identifyBottlenecks(performanceData);

    // Calculate overall performance score (0-100)
    const performanceScore = calculatePerformanceScore(performanceData, bottlenecks);

    // Generate recommendations
    const recommendations = generateRecommendations(performanceData, bottlenecks);

    // Determine if performance targets are met
    const meetsTargets = performanceScore >= 80 && frameSuccessRate >= 0.95;

    // Create the full report
    const report: AnimationPerformanceReport = {
      performanceData,
      bottlenecks,
      performanceScore,
      recommendations,
      meetsTargets,
    };

    // Call the completion callback if provided
    if (onComplete) {
      onComplete(report);
    }

    return report;
  }

  /**
   * Creates an empty performance report
   */
  function createEmptyReport(): AnimationPerformanceReport {
    return {
      performanceData: {
        animationId,
        animationName,
        startTime: 0,
        endTime: 0,
        totalDuration: 0,
        animationConfig,
        frames: [],
        averageFrameDuration: 0,
        minFrameDuration: 0,
        maxFrameDuration: 0,
        targetFps,
        actualFps: 0,
        droppedFrames: 0,
        frameSuccessRate: 0,
      },
      bottlenecks: [],
      performanceScore: 0,
      recommendations: ['No performance data available.'],
      meetsTargets: false,
    };
  }

  /**
   * Identifies performance bottlenecks in the animation
   */
  function identifyBottlenecks(data: AnimationPerformanceData): AnimationBottleneck[] {
    const bottlenecks: AnimationBottleneck[] = [];

    // Check for frame rate issues
    if (data.actualFps < targetFps * 0.9) {
      bottlenecks.push({
        type: 'unknown',
        severity: Math.min(1, (targetFps - data.actualFps) / targetFps),
        description: `Frame rate below target (${data.actualFps.toFixed(1)} fps vs target ${targetFps} fps)`,
        suggestion: 'Review the animation for complexity and optimize rendering performance',
        affectedFrames: frames.map((_, index) => index),
      });
    }

    // Check for DOM update bottlenecks
    const domHeavyFrames = frames
      .map((frame, index) => ({
        index,
        ratio: frame.domUpdateTime / frame.frameDuration,
      }))
      .filter(frame => frame.ratio > 0.5);

    if (domHeavyFrames.length > frames.length * 0.2) {
      bottlenecks.push({
        type: 'dom_updates',
        severity: Math.min(1, domHeavyFrames.length / frames.length),
        description: `DOM updates consuming >50% of frame time in ${domHeavyFrames.length} frames`,
        suggestion: 'Reduce DOM updates by batching changes or using virtual DOM techniques',
        affectedFrames: domHeavyFrames.map(f => f.index),
      });
    }

    // Check for interpolation bottlenecks
    const interpolationHeavyFrames = frames
      .map((frame, index) => ({
        index,
        ratio: frame.interpolationTime / frame.frameDuration,
      }))
      .filter(frame => frame.ratio > 0.3);

    if (interpolationHeavyFrames.length > frames.length * 0.2) {
      bottlenecks.push({
        type: 'interpolation',
        severity: Math.min(1, interpolationHeavyFrames.length / frames.length),
        description: `Interpolation consuming >30% of frame time in ${interpolationHeavyFrames.length} frames`,
        suggestion:
          'Implement memoization for interpolation results or reduce interpolation complexity',
        affectedFrames: interpolationHeavyFrames.map(f => f.index),
      });
    }

    // Check for rendering issues (high variance in frame times)
    const frameDurationStdDev = calculateStandardDeviation(frames.map(f => f.frameDuration));

    if (frameDurationStdDev > targetFrameDuration * 0.5) {
      bottlenecks.push({
        type: 'rendering',
        severity: Math.min(1, frameDurationStdDev / targetFrameDuration),
        description: 'High variance in frame times indicates inconsistent rendering performance',
        suggestion:
          'Look for layout thrashing, implement requestAnimationFrame properly, or use CSS transitions where possible',
        affectedFrames: frames
          .map((frame, index) => ({ index, duration: frame.frameDuration }))
          .filter(
            frame => Math.abs(frame.duration - data.averageFrameDuration) > targetFrameDuration
          )
          .map(frame => frame.index),
      });
    }

    return bottlenecks;
  }

  /**
   * Calculates standard deviation for an array of numbers
   */
  function calculateStandardDeviation(values: number[]): number {
    const avg = values.reduce((sum, val) => sum + val, 0) / values.length;
    const squareDiffs = values.map(value => Math.pow(value - avg, 2));
    const avgSquareDiff = squareDiffs.reduce((sum, val) => sum + val, 0) / squareDiffs.length;
    return Math.sqrt(avgSquareDiff);
  }

  /**
   * Calculates an overall performance score based on metrics and bottlenecks
   */
  function calculatePerformanceScore(
    data: AnimationPerformanceData,
    bottlenecks: AnimationBottleneck[]
  ): number {
    // Base score from frame success rate (0-50 points)
    const frameRateScore = data.frameSuccessRate * 50;

    // Score from actual FPS vs target FPS (0-20 points)
    const fpsRatio = Math.min(1, data.actualFps / targetFps);
    const fpsScore = fpsRatio * 20;

    // Penalty from bottlenecks (0-30 points)
    const bottleneckPenalty = bottlenecks.reduce(
      (penalty, bottleneck) => penalty + bottleneck.severity * 10,
      0
    );
    const bottleneckScore = Math.max(0, 30 - bottleneckPenalty);

    // Calculate total score (0-100)
    return Math.round(frameRateScore + fpsScore + bottleneckScore);
  }

  /**
   * Generates performance improvement recommendations
   */
  function generateRecommendations(
    data: AnimationPerformanceData,
    bottlenecks: AnimationBottleneck[]
  ): string[] {
    const recommendations: string[] = [];

    // Add bottleneck-specific recommendations
    bottlenecks.forEach(bottleneck => {
      recommendations.push(bottleneck.suggestion);
    });

    // Add general recommendations based on performance data
    if (data.actualFps < targetFps * 0.8) {
      recommendations.push('Consider reducing animation complexity or extending duration');
    }

    if (data.frames.some(f => f.domUpdateCount > 50)) {
      recommendations.push('Limit DOM updates per frame to improve performance');
    }

    if (data.frames.some(f => f.interpolationCount > 100)) {
      recommendations.push(
        'Reduce the number of interpolations per frame or implement memoization'
      );
    }

    // If performance is good, acknowledge it
    if (data.frameSuccessRate > 0.95 && data.actualFps >= targetFps * 0.95) {
      recommendations.push('Animation performance is good, no critical issues detected');
    }

    return [...new Set(recommendations)]; // Remove duplicates
  }

  /**
   * Wraps a selection's transition method to enable profiling
   *
   * @param selection D3 selection to profile
   * @returns The modified selection
   */
  function wrapSelection<GElement extends Element, Datum, PElement extends Element, PDatum>(
    selection: d3.Selection<GElement, Datum, PElement, PDatum>
  ): d3.Selection<GElement, Datum, PElement, PDatum> {
    if (!isRunning || !trackDomUpdates) {
      return selection;
    }

    // Store the original methods
    const originalTransition = selection.transition;
    const originalAttr = selection.attr;
    const originalStyle = selection.style;

    // Wrap transition method
    selection.transition = function (...args: any[]): any {
      const startTime = performance.now();
      const result = originalTransition.apply(this, args);
      const duration = performance.now() - startTime;

      // Record transition creation
      recordDomUpdates(1, duration);

      return result;
    } as any;

    // Wrap attr method
    selection.attr = function (...args: any[]): any {
      const startTime = performance.now();
      const result = originalAttr.apply(this, args);
      const duration = performance.now() - startTime;

      // Record attribute updates
      recordDomUpdates(selection.size(), duration);

      return result;
    } as any;

    // Wrap style method
    selection.style = function (...args: any[]): any {
      const startTime = performance.now();
      const result = originalStyle.apply(this, args);
      const duration = performance.now() - startTime;

      // Record style updates
      recordDomUpdates(selection.size(), duration);

      return result;
    } as any;

    return selection;
  }

  /**
   * Creates a wrapped interpolator for performance measurement
   *
   * @param interpolator Original interpolator function
   * @returns Wrapped interpolator that measures performance
   */
  function wrapInterpolator<T>(interpolator: (t: number) => T): (t: number) => T {
    if (!isRunning || !trackInterpolation) {
      return interpolator;
    }

    return (t: number): T => {
      const startTime = performance.now();
      const result = interpolator(t);
      const duration = performance.now() - startTime;

      recordInterpolation(1, duration);

      return result;
    };
  }

  /**
   * Gets the current profiling status
   */
  function getStatus() {
    return {
      isRunning,
      frameCount,
      elapsedTime: isRunning ? performance.now() - startTime : 0,
      currentFps:
        isRunning && frameCount > 0 ? frameCount / ((performance.now() - startTime) / 1000) : 0,
    };
  }

  // Return the profiler API
  return {
    start,
    stop,
    recordFrame,
    recordDomUpdates,
    recordInterpolation,
    wrapSelection,
    wrapInterpolator,
    getStatus,
  };
}

/**
 * Helper to profile a D3AnimationSequence
 *
 * @param sequence Animation sequence to profile
 * @param config Profiler configuration
 * @returns Promise that resolves to a performance report
 */
export function profileAnimationSequence<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(
  sequence: TypedAnimationSequence<GElement, Datum, PElement, PDatum>,
  config: AnimationProfilerConfig = {}
): Promise<AnimationPerformanceReport> {
  return new Promise(resolve => {
    const profiler = createAnimationProfiler({
      ...config,
      onComplete: report => resolve(report),
    });

    // Start profiling
    profiler.start(
      'sequence-' + Date.now(),
      'Animation Sequence',
      // Extract duration from the first transition if available
      sequence['config'] && sequence['config'].transitions && sequence['config'].transitions[0]
        ? { duration: sequence['config'].transitions[0].duration || 0 }
        : { duration: 0 }
    );

    // Create a frame tracking wrapper
    const frameTracker = () => {
      profiler.recordFrame();
      if (profiler.getStatus().isRunning) {
        requestAnimationFrame(frameTracker);
      }
    };

    // Start frame tracking
    requestAnimationFrame(frameTracker);

    // Start the sequence
    sequence.start();

    // Stop profiling when sequence completes or after timeout
    const stopTimeout = setTimeout(() => {
      if (profiler.getStatus().isRunning) {
        profiler.stop();
      }
    }, config.profileDuration || 10000); // Default to 10 seconds max

    // Try to access private property to detect completion
    const privateConfig = (sequence as any).config;
    if (privateConfig && !privateConfig.loop && privateConfig.onComplete) {
      const originalOnComplete = privateConfig.onComplete;
      privateConfig.onComplete = () => {
        originalOnComplete();
        clearTimeout(stopTimeout);
        if (profiler.getStatus().isRunning) {
          profiler.stop();
        }
      };
    }
  });
}

/**
 * Formats a performance report as a readable string
 *
 * @param report Animation performance report
 * @returns Formatted report as string
 */
export function formatPerformanceReport(report: AnimationPerformanceReport): string {
  const { performanceData, bottlenecks, performanceScore, recommendations } = report;

  const lines = [
    `# Animation Performance Report for "${performanceData.animationName}"`,
    ``,
    `## Overview`,
    `- **Performance Score**: ${performanceScore}/100${performanceScore >= 80 ? ' ✅' : ' ⚠️'}`,
    `- **Target FPS**: ${performanceData.targetFps}`,
    `- **Actual FPS**: ${performanceData.actualFps.toFixed(1)}${performanceData.actualFps >= performanceData.targetFps * 0.95 ? ' ✅' : ' ⚠️'}`,
    `- **Duration**: ${performanceData.totalDuration.toFixed(0)}ms`,
    `- **Frame Success Rate**: ${(performanceData.frameSuccessRate * 100).toFixed(1)}%`,
    `- **Dropped Frames**: ${performanceData.droppedFrames} of ${performanceData.frames.length}`,
    ``,
    `## Performance Bottlenecks`,
  ];

  if (bottlenecks.length === 0) {
    lines.push('No significant bottlenecks detected.');
  } else {
    bottlenecks.forEach((bottleneck, i) => {
      lines.push(`### Bottleneck ${i + 1}: ${bottleneck.type.replace('_', ' ').toUpperCase()}`);
      lines.push(`- **Severity**: ${(bottleneck.severity * 100).toFixed(0)}%`);
      lines.push(`- **Description**: ${bottleneck.description}`);
      lines.push(`- **Suggestion**: ${bottleneck.suggestion}`);
      lines.push(``);
    });
  }

  lines.push(`## Recommendations`);
  if (recommendations.length === 0) {
    lines.push('No specific recommendations.');
  } else {
    recommendations.forEach((rec, i) => {
      lines.push(`${i + 1}. ${rec}`);
    });
  }

  return lines.join('\n');
}

/**
 * Default profiler instance for simple usage
 */
export const defaultAnimationProfiler = createAnimationProfiler();

/**
 * D3 Animation Quality Manager
 *
 * This module provides a system for dynamically adjusting animation quality based on
 * device performance capabilities. It detects device performance characteristics and
 * automatically adjusts visualization complexity to maintain smooth animation experiences.
 *
 * Key features:
 * 1. Performance detection and categorization
 * 2. Quality presets for different device capabilities
 * 3. Dynamic quality adjustment based on real-time FPS
 * 4. Integration with existing animation systems
 * 5. User preference overrides
 */

import * as d3 from 'd3';

/**
 * Performance tier categorization
 */
export type PerformanceTier = 'ultra' | 'high' | 'medium' | 'low' | 'minimal';

/**
 * Quality settings that can be adjusted
 */
export interface QualitySettings {
  /** Maximum number of animated elements */
  maxElementCount: number;

  /** Target frames per second */
  targetFps: number;

  /** Animation smoothness (1 = every frame, 2 = every other frame, etc.) */
  animationStepFactor: number;

  /** Visual complexity level (0-1) affecting details like shadows, gradients */
  visualComplexity: number;

  /** Whether to use WebGL acceleration when available */
  useWebGLWhenAvailable: boolean;

  /** Number of intermediate animation steps */
  interpolationSteps: number;

  /** Whether to use physics simulation */
  enablePhysics: boolean;

  /** Physics simulation detail level */
  physicsDetail: number;

  /** Whether to enable visual effects */
  enableEffects: boolean;

  /** Whether to enable batching for DOM operations */
  enableBatching: boolean;

  /** Whether to enable animation memoization */
  enableMemoization: boolean;

  /** Whether transitions should follow precise timing */
  preciseTiming: boolean;
}

/**
 * Device capability information
 */
export interface DeviceCapabilities {
  /** CPU benchmark score */
  cpuScore: number;

  /** GPU benchmark score */
  gpuScore: number;

  /** Memory available (approximation) */
  memoryScore: number;

  /** Connection speed category */
  connectionType: 'slow' | 'medium' | 'fast' | 'unknown';

  /** Whether the device is a mobile device */
  isMobile: boolean;

  /** Whether the device supports WebGL */
  hasWebGL: boolean;

  /** Whether the device is in battery saving mode */
  isBatterySaving: boolean;

  /** Screen resolution category */
  screenCategory: 'low' | 'medium' | 'high' | 'ultra';

  /** Browser performance capability */
  browserPerformance: 'low' | 'medium' | 'high';
}

/**
 * Quality management configuration
 */
export interface QualityManagerConfig {
  /** Whether to enable automatic quality adjustment */
  enableAutoAdjustment?: boolean;

  /** Minimum acceptable FPS before reducing quality */
  minAcceptableFps?: number;

  /** How often to check performance (ms) */
  performanceCheckInterval?: number;

  /** How quickly to adjust quality (0-1, higher = faster) */
  adjustmentResponsiveness?: number;

  /** Whether to respect prefers-reduced-motion */
  respectPrefersReducedMotion?: boolean;

  /** Whether to respect battery saving modes */
  respectBatterySaving?: boolean;

  /** Whether to save quality settings to local storage */
  persistSettings?: boolean;

  /** Initial quality tier override */
  initialQualityTier?: PerformanceTier;

  /** Maximum allowed elements regardless of performance */
  absoluteMaxElementCount?: number;

  /** Debug mode */
  debugMode?: boolean;
}

/**
 * Performance monitoring state
 */
interface PerformanceState {
  currentFps: number;
  fpsHistory: number[];
  lastAdjustmentTime: number;
  adjustmentCount: number;
  currentTier: PerformanceTier;
  detectedCapabilities: DeviceCapabilities;
  isInitialized: boolean;
  isAdjusting: boolean;
}

/**
 * Animation quality manager for D3 visualizations
 */
export class D3AnimationQualityManager {
  /** Default quality presets for each performance tier */
  private qualityPresets: Record<PerformanceTier, QualitySettings> = {
    ultra: {
      maxElementCount: 10000,
      targetFps: 60,
      animationStepFactor: 1,
      visualComplexity: 1.0,
      useWebGLWhenAvailable: true,
      interpolationSteps: 60,
      enablePhysics: true,
      physicsDetail: 1.0,
      enableEffects: true,
      enableBatching: true,
      enableMemoization: true,
      preciseTiming: true,
    },
    high: {
      maxElementCount: 5000,
      targetFps: 60,
      animationStepFactor: 1,
      visualComplexity: 0.8,
      useWebGLWhenAvailable: true,
      interpolationSteps: 45,
      enablePhysics: true,
      physicsDetail: 0.8,
      enableEffects: true,
      enableBatching: true,
      enableMemoization: true,
      preciseTiming: true,
    },
    medium: {
      maxElementCount: 2000,
      targetFps: 40,
      animationStepFactor: 2,
      visualComplexity: 0.6,
      useWebGLWhenAvailable: true,
      interpolationSteps: 30,
      enablePhysics: true,
      physicsDetail: 0.5,
      enableEffects: false,
      enableBatching: true,
      enableMemoization: true,
      preciseTiming: false,
    },
    low: {
      maxElementCount: 1000,
      targetFps: 30,
      animationStepFactor: 2,
      visualComplexity: 0.4,
      useWebGLWhenAvailable: true,
      interpolationSteps: 20,
      enablePhysics: false,
      physicsDetail: 0.3,
      enableEffects: false,
      enableBatching: true,
      enableMemoization: true,
      preciseTiming: false,
    },
    minimal: {
      maxElementCount: 500,
      targetFps: 20,
      animationStepFactor: 3,
      visualComplexity: 0.2,
      useWebGLWhenAvailable: false,
      interpolationSteps: 10,
      enablePhysics: false,
      physicsDetail: 0.1,
      enableEffects: false,
      enableBatching: true,
      enableMemoization: false,
      preciseTiming: false,
    },
  };

  /** Current quality settings */
  private currentSettings: QualitySettings;

  /** User preference overrides */
  private userOverrides: Partial<QualitySettings> = {};

  /** Current performance state */
  private performanceState: PerformanceState = {
    currentFps: 0,
    fpsHistory: [],
    lastAdjustmentTime: 0,
    adjustmentCount: 0,
    currentTier: 'medium',
    detectedCapabilities: this.getDefaultCapabilities(),
    isInitialized: false,
    isAdjusting: false,
  };

  /** Performance monitoring interval */
  private monitoringInterval: number | null = null;

  /** Animation callbacks by ID */
  private qualityChangeCallbacks: Map<string, (settings: QualitySettings) => void> = new Map();

  /** Animation quality overrides by ID */
  private animationQualityOverrides: Map<string, Partial<QualitySettings>> = new Map();

  /** Animation FPS history */
  private animationFpsTracking: Map<string, number[]> = new Map();

  /**
   * Create a new animation quality manager
   */
  constructor(private config: QualityManagerConfig = {}) {
    // Set up default configuration
    this.config = {
      enableAutoAdjustment: true,
      minAcceptableFps: 30,
      performanceCheckInterval: 5000,
      adjustmentResponsiveness: 0.5,
      respectPrefersReducedMotion: true,
      respectBatterySaving: true,
      persistSettings: true,
      initialQualityTier: undefined,
      absoluteMaxElementCount: 20000,
      debugMode: false,
      ...config,
    };

    // Initialize with medium settings first (will be adjusted)
    this.currentSettings = this.qualityPresets.medium;

    // Load saved settings if enabled
    if (this.config.persistSettings) {
      this.loadSavedSettings();
    }

    // Apply initial quality tier if specified
    if (this.config.initialQualityTier) {
      this.setQualityTier(this.config.initialQualityTier);
    }

    // Initialize the system
    this.initialize();
  }

  /**
   * Initialize the quality management system
   */
  private async initialize(): Promise<void> {
    // Load browser capability detection
    await this.detectDeviceCapabilities();

    // Set initial quality based on detected capabilities
    if (!this.config.initialQualityTier) {
      const detectedTier = this.detectOptimalQualityTier();
      this.setQualityTier(detectedTier);
    }

    // Start monitoring if auto-adjustment is enabled
    if (this.config.enableAutoAdjustment) {
      this.startPerformanceMonitoring();
    }

    // Monitor system events that might affect performance
    this.setupSystemEventListeners();

    // Mark as initialized
    this.performanceState.isInitialized = true;

    if (this.config.debugMode) {
      console.log('D3AnimationQualityManager initialized', {
        capabilities: this.performanceState.detectedCapabilities,
        qualityTier: this.performanceState.currentTier,
        settings: this.currentSettings,
      });
    }
  }

  /**
   * Get default capabilities when proper detection isn't available
   */
  private getDefaultCapabilities(): DeviceCapabilities {
    return {
      cpuScore: 50,
      gpuScore: 50,
      memoryScore: 50,
      connectionType: 'unknown',
      isMobile: false,
      hasWebGL: false,
      isBatterySaving: false,
      screenCategory: 'medium',
      browserPerformance: 'medium',
    };
  }

  /**
   * Detect device capabilities
   */
  private async detectDeviceCapabilities(): Promise<void> {
    const capabilities: DeviceCapabilities = this.getDefaultCapabilities();

    // Mobile detection
    capabilities.isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(
      navigator.userAgent
    );

    // WebGL support
    try {
      const canvas = document.createElement('canvas');
      capabilities.hasWebGL = !!(
        window.WebGLRenderingContext &&
        (canvas.getContext('webgl') || canvas.getContext('experimental-webgl'))
      );
    } catch (e) {
      capabilities.hasWebGL = false;
    }

    // Battery API check
    if ('getBattery' in navigator) {
      try {
        const battery = await (navigator as any).getBattery();
        capabilities.isBatterySaving = battery.charging === false && battery.level < 0.2;

        // Listen for battery changes
        battery.addEventListener('levelchange', () => {
          this.performanceState.detectedCapabilities.isBatterySaving =
            battery.charging === false && battery.level < 0.2;
          if (this.config.respectBatterySaving) {
            this.adjustQualityIfNeeded();
          }
        });
      } catch (e) {
        // Battery API not available
      }
    }

    // Screen resolution category
    const pixelRatio = window.devicePixelRatio || 1;
    const screenWidth = window.screen.width * pixelRatio;
    const screenHeight = window.screen.height * pixelRatio;
    const resolution = screenWidth * screenHeight;

    if (resolution > 4000 * 3000) {
      capabilities.screenCategory = 'ultra';
    } else if (resolution > 2000 * 1500) {
      capabilities.screenCategory = 'high';
    } else if (resolution > 1000 * 750) {
      capabilities.screenCategory = 'medium';
    } else {
      capabilities.screenCategory = 'low';
    }

    // Connection type detection
    if ('connection' in navigator) {
      const connection = (navigator as any).connection;
      if (connection) {
        const effectiveType = connection.effectiveType;
        if (effectiveType === '4g') {
          capabilities.connectionType = 'fast';
        } else if (effectiveType === '3g') {
          capabilities.connectionType = 'medium';
        } else {
          capabilities.connectionType = 'slow';
        }
      }
    }

    // Perform quick CPU benchmark
    const cpuBenchmarkStart = performance.now();
    let benchmarkResult = 0;
    for (let i = 0; i < 1000000; i++) {
      benchmarkResult += Math.sqrt(i);
    }
    const cpuBenchmarkTime = performance.now() - cpuBenchmarkStart;

    // Normalize CPU score (lower is better, so invert)
    capabilities.cpuScore = Math.min(100, Math.max(0, 100 - cpuBenchmarkTime / 50));

    // GPU benchmarking is more complex and would require WebGL,
    // for simplicity we'll estimate based on other factors
    capabilities.gpuScore = capabilities.hasWebGL
      ? capabilities.isMobile
        ? 60
        : 80
      : capabilities.isMobile
        ? 30
        : 50;

    // Attempt to estimate available memory
    if ('deviceMemory' in navigator) {
      const deviceMemory = (navigator as any).deviceMemory;
      if (typeof deviceMemory === 'number') {
        // deviceMemory is in GB, normalize to 0-100 scale
        // Assuming 8GB as high-end, 16GB+ as maximum
        capabilities.memoryScore = Math.min(100, Math.max(0, (deviceMemory / 16) * 100));
      }
    } else {
      // Default assumption based on device type
      capabilities.memoryScore = capabilities.isMobile ? 40 : 70;
    }

    // Browser performance estimation
    // This is a very rough heuristic
    const browserScore =
      (capabilities.cpuScore + capabilities.gpuScore + capabilities.memoryScore) / 3;
    if (browserScore > 70) {
      capabilities.browserPerformance = 'high';
    } else if (browserScore > 40) {
      capabilities.browserPerformance = 'medium';
    } else {
      capabilities.browserPerformance = 'low';
    }

    // Update the state
    this.performanceState.detectedCapabilities = capabilities;
  }

  /**
   * Determine the optimal quality tier based on detected capabilities
   */
  private detectOptimalQualityTier(): PerformanceTier {
    const caps = this.performanceState.detectedCapabilities;

    // Basic score combining all factors
    const overallScore =
      caps.cpuScore * 0.3 +
      caps.gpuScore * 0.3 +
      caps.memoryScore * 0.2 +
      (caps.isMobile ? 0 : 20) +
      (caps.hasWebGL ? 10 : 0) +
      (caps.isBatterySaving ? -15 : 0) +
      (caps.screenCategory === 'ultra'
        ? 10
        : caps.screenCategory === 'high'
          ? 5
          : caps.screenCategory === 'low'
            ? -5
            : 0);

    // Map overall score to quality tier
    if (overallScore >= 80) {
      return 'ultra';
    } else if (overallScore >= 60) {
      return 'high';
    } else if (overallScore >= 40) {
      return 'medium';
    } else if (overallScore >= 20) {
      return 'low';
    } else {
      return 'minimal';
    }
  }

  /**
   * Start monitoring performance to adjust quality dynamically
   */
  private startPerformanceMonitoring(): void {
    if (this.monitoringInterval !== null) {
      return; // Already monitoring
    }

    let lastFrameTime = performance.now();
    let frameCount = 0;

    // FPS monitoring function
    const measureFps = () => {
      frameCount++;
      const now = performance.now();
      const elapsed = now - lastFrameTime;

      if (elapsed >= 1000) {
        const currentFps = Math.round((frameCount * 1000) / elapsed);
        this.performanceState.currentFps = currentFps;
        this.performanceState.fpsHistory.push(currentFps);

        // Keep only the last 10 measurements
        if (this.performanceState.fpsHistory.length > 10) {
          this.performanceState.fpsHistory.shift();
        }

        frameCount = 0;
        lastFrameTime = now;
      }

      requestAnimationFrame(measureFps);
    };

    // Start measuring FPS
    requestAnimationFrame(measureFps);

    // Start the periodic quality adjustment check
    this.monitoringInterval = window.setInterval(() => {
      this.adjustQualityIfNeeded();
    }, this.config.performanceCheckInterval);
  }

  /**
   * Stop performance monitoring
   */
  private stopPerformanceMonitoring(): void {
    if (this.monitoringInterval !== null) {
      clearInterval(this.monitoringInterval);
      this.monitoringInterval = null;
    }
  }

  /**
   * Set up listeners for system events that might affect performance
   */
  private setupSystemEventListeners(): void {
    // Listen for reduced motion preference
    if (this.config.respectPrefersReducedMotion) {
      const motionQuery = window.matchMedia('(prefers-reduced-motion: reduce)');

      const handleMotionChange = (e: MediaQueryListEvent | MediaQueryList) => {
        if (e.matches) {
          // User prefers reduced motion, force lower quality
          this.setQualityTier('low');
        } else {
          // User doesn't prefer reduced motion, redetect optimal quality
          this.setQualityTier(this.detectOptimalQualityTier());
        }
      };

      // Check initial state
      handleMotionChange(motionQuery);

      // Listen for changes
      if (typeof motionQuery.addEventListener === 'function') {
        motionQuery.addEventListener('change', handleMotionChange);
      } else if (typeof motionQuery.addListener === 'function') {
        // Older browsers
        motionQuery.addListener(handleMotionChange);
      }
    }

    // Listen for visibility changes to adjust quality when tab becomes visible again
    document.addEventListener('visibilitychange', () => {
      if (document.visibilityState === 'visible') {
        // Page is now visible, check if we need to adjust quality
        this.adjustQualityIfNeeded();
      }
    });

    // Listen for window resize events, which might affect performance
    let resizeTimeout: number | null = null;
    window.addEventListener('resize', () => {
      if (resizeTimeout !== null) {
        clearTimeout(resizeTimeout);
      }

      // Debounce resize events
      resizeTimeout = window.setTimeout(() => {
        this.adjustQualityIfNeeded();
        resizeTimeout = null;
      }, 500);
    });
  }

  /**
   * Adjust quality settings if needed based on performance
   */
  private adjustQualityIfNeeded(): void {
    if (!this.config.enableAutoAdjustment || this.performanceState.isAdjusting) {
      return;
    }

    this.performanceState.isAdjusting = true;

    try {
      // Get average FPS from recent history
      const avgFps =
        this.performanceState.fpsHistory.length > 0
          ? this.performanceState.fpsHistory.reduce((sum, fps) => sum + fps, 0) /
            this.performanceState.fpsHistory.length
          : this.performanceState.currentFps;

      const currentTierIndex = this.getTierIndex(this.performanceState.currentTier);
      const minAcceptableFps = this.config.minAcceptableFps || 30;

      // Check if we need to adjust quality
      if (avgFps < minAcceptableFps && currentTierIndex > 0) {
        // Performance is poor, reduce quality
        const newTier = this.getTierByIndex(currentTierIndex - 1);
        this.setQualityTier(newTier);

        if (this.config.debugMode) {
          console.log(`Reducing quality to ${newTier} due to low FPS (${avgFps.toFixed(1)})`);
        }
      } else if (avgFps > minAcceptableFps * 1.5 && currentTierIndex < 4) {
        // Performance is good, we might be able to increase quality
        // But do this cautiously and less frequently

        const timeSinceLastAdjustment =
          performance.now() - this.performanceState.lastAdjustmentTime;
        const requiredTimeBetweenUpgrades = 30000; // 30 seconds between upgrades

        if (timeSinceLastAdjustment > requiredTimeBetweenUpgrades) {
          const newTier = this.getTierByIndex(currentTierIndex + 1);
          this.setQualityTier(newTier);

          if (this.config.debugMode) {
            console.log(`Increasing quality to ${newTier} due to good FPS (${avgFps.toFixed(1)})`);
          }
        }
      }
    } finally {
      this.performanceState.isAdjusting = false;
    }
  }

  /**
   * Get the index of a quality tier
   */
  private getTierIndex(tier: PerformanceTier): number {
    const tiers: PerformanceTier[] = ['minimal', 'low', 'medium', 'high', 'ultra'];
    return tiers.indexOf(tier);
  }

  /**
   * Get a quality tier by index
   */
  private getTierByIndex(index: number): PerformanceTier {
    const tiers: PerformanceTier[] = ['minimal', 'low', 'medium', 'high', 'ultra'];
    return tiers[Math.max(0, Math.min(tiers.length - 1, index))];
  }

  /**
   * Set quality tier
   */
  public setQualityTier(tier: PerformanceTier): void {
    // Update the current tier
    this.performanceState.currentTier = tier;

    // Get the preset for this tier
    const presetSettings = this.qualityPresets[tier];

    // Apply user overrides
    this.currentSettings = {
      ...presetSettings,
      ...this.userOverrides,
    };

    // Apply the new settings to all registered animations
    this.applyQualitySettingsToAnimations();

    // Save settings if persistence is enabled
    if (this.config.persistSettings) {
      this.saveSettings();
    }

    // Update last adjustment time
    this.performanceState.lastAdjustmentTime = performance.now();
    this.performanceState.adjustmentCount++;
  }

  /**
   * Apply current quality settings to all registered animations
   */
  private applyQualitySettingsToAnimations(): void {
    // Notify all registered animations of quality changes
    this.qualityChangeCallbacks.forEach((callback, animationId) => {
      try {
        // Apply global settings with any animation-specific overrides
        const animationSettings = {
          ...this.currentSettings,
          ...(this.animationQualityOverrides.get(animationId) || {}),
        };

        callback(animationSettings);
      } catch (err) {
        console.error(`Error applying quality settings to animation ${animationId}:`, err);
      }
    });
  }

  /**
   * Register an animation for quality management
   */
  public registerAnimation(
    animationId: string,
    onQualityChange: (settings: QualitySettings) => void,
    animationSpecificOverrides?: Partial<QualitySettings>
  ): void {
    // Store the callback
    this.qualityChangeCallbacks.set(animationId, onQualityChange);

    // Store any animation-specific overrides
    if (animationSpecificOverrides) {
      this.animationQualityOverrides.set(animationId, animationSpecificOverrides);
    }

    // Initialize FPS tracking for this animation
    this.animationFpsTracking.set(animationId, []);

    // Apply current settings immediately
    const settings = {
      ...this.currentSettings,
      ...(animationSpecificOverrides || {}),
    };

    onQualityChange(settings);
  }

  /**
   * Unregister an animation
   */
  public unregisterAnimation(animationId: string): void {
    this.qualityChangeCallbacks.delete(animationId);
    this.animationQualityOverrides.delete(animationId);
    this.animationFpsTracking.delete(animationId);
  }

  /**
   * Update FPS for a specific animation
   */
  public updateAnimationFps(animationId: string, fps: number): void {
    const fpsHistory = this.animationFpsTracking.get(animationId);
    if (fpsHistory) {
      fpsHistory.push(fps);

      // Keep only the last 10 measurements
      if (fpsHistory.length > 10) {
        fpsHistory.shift();
      }
    }
  }

  /**
   * Set user preference override for specific settings
   */
  public setUserPreference(settingKey: keyof QualitySettings, value: any): void {
    this.userOverrides[settingKey] = value;

    // Apply the change immediately
    this.currentSettings = {
      ...this.qualityPresets[this.performanceState.currentTier],
      ...this.userOverrides,
    };

    // Apply to all animations
    this.applyQualitySettingsToAnimations();

    // Save settings if persistence is enabled
    if (this.config.persistSettings) {
      this.saveSettings();
    }
  }

  /**
   * Clears all user preference overrides
   */
  public clearUserPreferences(): void {
    this.userOverrides = {};

    // Reapply preset settings
    this.currentSettings = this.qualityPresets[this.performanceState.currentTier];

    // Apply to all animations
    this.applyQualitySettingsToAnimations();

    // Save settings if persistence is enabled
    if (this.config.persistSettings) {
      this.saveSettings();
    }
  }

  /**
   * Save settings to localStorage
   */
  private saveSettings(): void {
    try {
      const settingsToSave = {
        qualityTier: this.performanceState.currentTier,
        userOverrides: this.userOverrides,
      };

      localStorage.setItem('d3-animation-quality-settings', JSON.stringify(settingsToSave));
    } catch (err) {
      console.error('Error saving animation quality settings:', err);
    }
  }

  /**
   * Load saved settings from localStorage
   */
  private loadSavedSettings(): void {
    try {
      const savedSettings = localStorage.getItem('d3-animation-quality-settings');

      if (savedSettings) {
        const parsed = JSON.parse(savedSettings);

        // Apply saved tier if it exists
        if (parsed.qualityTier && typeof parsed.qualityTier === 'string') {
          this.performanceState.currentTier = parsed.qualityTier as PerformanceTier;
        }

        // Apply saved user overrides
        if (parsed.userOverrides && typeof parsed.userOverrides === 'object') {
          this.userOverrides = parsed.userOverrides;
        }
      }
    } catch (err) {
      console.error('Error loading animation quality settings:', err);
    }
  }

  /**
   * Get current quality settings
   */
  public getCurrentSettings(): QualitySettings {
    return { ...this.currentSettings };
  }

  /**
   * Get current performance state
   */
  public getPerformanceState(): Readonly<PerformanceState> {
    return { ...this.performanceState };
  }

  /**
   * Get detected device capabilities
   */
  public getDeviceCapabilities(): Readonly<DeviceCapabilities> {
    return { ...this.performanceState.detectedCapabilities };
  }

  /**
   * Enable or disable auto-adjustment
   */
  public setAutoAdjustment(enabled: boolean): void {
    this.config.enableAutoAdjustment = enabled;

    if (enabled && this.monitoringInterval === null) {
      this.startPerformanceMonitoring();
    } else if (!enabled && this.monitoringInterval !== null) {
      this.stopPerformanceMonitoring();
    }
  }
}

// Singleton instance for easier access
export const animationQualityManager = new D3AnimationQualityManager();

/**
 * Helper to create a quality-adaptive D3 visualization
 */
export function createQualityAdaptiveVisualization<GElement extends Element = SVGSVGElement>(
  selector: string | GElement,
  animationId: string,
  setupFunction: (
    container: d3.Selection<GElement, unknown, null, undefined>,
    settings: QualitySettings
  ) => void,
  qualityOverrides?: Partial<QualitySettings>
): d3.Selection<GElement, unknown, null, undefined> {
  // Select the container element
  const selection = d3.select(selector) as d3.Selection<GElement, unknown, null, undefined>;

  // Register with the quality manager
  animationQualityManager.registerAnimation(
    animationId,
    settings => {
      // Clear previous content
      selection.selectAll('*').remove();

      // Set up visualization with current quality settings
      setupFunction(selection, settings);
    },
    qualityOverrides
  );

  return selection;
}

/**
 * Create quality-adaptive D3 transitions
 */
export function createQualityAdaptiveTransition<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  duration?: number
): d3.Transition<GElement, Datum, PElement, PDatum> {
  const settings = animationQualityManager.getCurrentSettings();

  // Adjust duration based on quality settings
  let adjustedDuration = duration;
  if (duration !== undefined) {
    // Scale duration by animation step factor
    // If animationStepFactor is 2, transitions will take twice as long but use half as many frames
    adjustedDuration = duration * settings.animationStepFactor;
  }

  // Create transition with quality-appropriate settings
  const transition = selection.transition().duration(adjustedDuration);

  // If precise timing isn't needed, use a more efficient easing function
  if (!settings.preciseTiming) {
    transition.ease(d3.easeLinear); // Linear is most efficient
  }

  return transition;
}

/**
 * Helper to create a quality-adaptive data binding
 */
export function bindDataWithQualityAdjustment<
  GElement extends Element,
  OldDatum,
  NewDatum,
  PElement extends Element,
  PDatum,
>(
  selection: d3.Selection<GElement, OldDatum, PElement, PDatum>,
  data: NewDatum[],
  keyFunction?: (d: NewDatum, i: number, data: NewDatum[]) => string
): d3.Selection<GElement, NewDatum, PElement, PDatum> {
  const settings = animationQualityManager.getCurrentSettings();

  // If we need to reduce the number of elements for performance reasons
  const maxElements = settings.maxElementCount;
  let limitedData = data;

  if (data.length > maxElements) {
    // Limit the number of elements based on quality settings
    limitedData = data.slice(0, maxElements);
  }

  // Bind the data, potentially with a key function
  return keyFunction ? selection.data(limitedData, keyFunction) : selection.data(limitedData);
}

/**
 * Helper to create a quality-adaptive simulation
 */
export function createQualityAdaptiveSimulation<NodeDatum extends d3.SimulationNodeDatum>(
  nodes: NodeDatum[]
): d3.Simulation<NodeDatum, undefined> {
  const settings = animationQualityManager.getCurrentSettings();

  // Create simulation with quality-adaptive settings
  const simulation = d3.forceSimulation<NodeDatum>().nodes(nodes);

  // Adjust simulation parameters based on quality settings
  const alphaDecay = settings.physicsDetail < 0.5 ? 0.1 : 0.02; // Faster convergence for low quality
  const velocityDecay = settings.physicsDetail < 0.5 ? 0.5 : 0.4; // More damping for low quality
  const iterations = Math.max(1, Math.round(settings.physicsDetail * 4)); // Fewer iterations for low quality

  simulation.alphaDecay(alphaDecay).velocityDecay(velocityDecay).alphaTarget(0).alphaMin(0.001);

  // Set tick iterations based on quality
  if (typeof simulation.tick === 'function') {
    const originalTick = simulation.tick;
    simulation.tick = function () {
      for (let i = 0; i < iterations; i++) {
        originalTick.call(this);
      }
      return this;
    };
  }

  return simulation;
}

/**
 * Helper to create a quality-adaptive interpolator
 */
export function createQualityAdaptiveInterpolator<T>(
  a: T,
  b: T,
  interpolatorFactory: (a: T, b: T) => (t: number) => T = d3.interpolate
): (t: number) => T {
  const settings = animationQualityManager.getCurrentSettings();
  const baseInterpolator = interpolatorFactory(a, b);

  // For high quality settings, use the full interpolator
  if (settings.interpolationSteps >= 60) {
    return baseInterpolator;
  }

  // For lower quality, create a stepped interpolator with fewer intermediate values
  return (t: number) => {
    // Quantize the t value to reduce the number of unique outputs
    const steps = settings.interpolationSteps;
    const steppedT = Math.round(t * steps) / steps;
    return baseInterpolator(steppedT);
  };
}

/**
 * React hook for using quality-adaptive D3 visualizations
 */
export function useQualityAdaptiveD3(
  animationId: string,
  qualityOverrides?: Partial<QualitySettings>
) {
  const [qualitySettings, setQualitySettings] = React.useState<QualitySettings>(
    animationQualityManager.getCurrentSettings()
  );

  React.useEffect(() => {
    // Register with quality manager
    animationQualityManager.registerAnimation(
      animationId,
      newSettings => {
        setQualitySettings(newSettings);
      },
      qualityOverrides
    );

    // Unregister on cleanup
    return () => {
      animationQualityManager.unregisterAnimation(animationId);
    };
  }, [animationId, qualityOverrides]);

  return qualitySettings;
}

/**
 * D3 Batched Updates System
 *
 * This module provides utilities for batching multiple DOM updates into a single render cycle,
 * reducing layout thrashing and improving animation performance. Key features include:
 *
 * 1. Update batching across multiple animations and selections
 * 2. Prioritized update scheduling based on visibility and importance
 * 3. DOM read/write separation to prevent layout thrashing
 * 4. Automatic microtask scheduling for optimal performance
 * 5. Integration with existing animation frame manager
 */

import * as d3 from 'd3';
import { animationFrameManager, AnimationPriority } from './D3AnimationFrameManager';

/**
 * Type for DOM operations that need to be scheduled together
 */
type BatchOperation = {
  /** Operation type - read operations execute before write operations */
  type: 'read' | 'write';
  /** The operation's unique ID */
  id: string;
  /** The callback function to execute */
  callback: () => void;
  /** Priority level for the operation */
  priority: BatchOperationPriority;
  /** When the operation was scheduled (for ordering operations with same priority) */
  timestamp: number;
  /** Element the operation is associated with (for grouping) */
  element?: Element;
  /** Animation ID this operation is associated with (for grouping) */
  animationId?: string;
};

/**
 * Priority levels for batch operations
 */
export type BatchOperationPriority = 'critical' | 'high' | 'normal' | 'low' | 'idle';

/**
 * Scheduling mode for batched updates
 */
export type BatchSchedulingMode = 'immediate' | 'animation-frame' | 'microtask' | 'idle-callback';

/**
 * Configuration options for the batch update system
 */
export interface BatchSystemConfig {
  /** Default scheduling mode */
  defaultSchedulingMode?: BatchSchedulingMode;
  /** Whether to automatically optimize the scheduling mode based on browser conditions */
  autoOptimize?: boolean;
  /** Maximum operations to process per batch before forcing a flush */
  maxOperationsPerBatch?: number;
  /** Whether to separate read and write operations to prevent layout thrashing */
  separateReadsWrites?: boolean;
  /** Whether to group operations by element */
  groupByElement?: boolean;
  /** Whether to enable debug logging */
  debugMode?: boolean;
}

/**
 * Options for batched operations
 */
export interface BatchOperationOptions {
  /** Operation priority */
  priority?: BatchOperationPriority;
  /** Element the operation is associated with */
  element?: Element;
  /** Animation ID this operation is associated with */
  animationId?: string;
  /** Scheduling mode for this operation */
  schedulingMode?: BatchSchedulingMode;
  /** Custom ID for the operation (for deduplication) */
  id?: string;
}

/**
 * Options for flushing batched operations
 */
export interface FlushOptions {
  /** Only flush operations for a specific element */
  forElement?: Element;
  /** Only flush operations for a specific animation */
  forAnimation?: string;
  /** Only flush operations with priority >= the specified level */
  minPriority?: BatchOperationPriority;
  /** Only flush read operations */
  readOnly?: boolean;
  /** Only flush write operations */
  writeOnly?: boolean;
}

/**
 * Stateful batch update manager for D3 animations
 */
export class D3BatchUpdateManager {
  /** Queue of pending read operations */
  private readQueue: BatchOperation[] = [];

  /** Queue of pending write operations */
  private writeQueue: BatchOperation[] = [];

  /** Whether a batch flush is scheduled */
  private flushScheduled = false;

  /** Current batch ID for tracking which batch operations belong to */
  private currentBatchId = 0;

  /** Whether the system is currently flushing operations */
  private isFlushing = false;

  /** Completed operation count for statistics */
  private completedOperations = 0;

  /** Map of operation IDs to avoid duplicates */
  private operationIds = new Set<string>();

  /** Priority order for sorting operations */
  private priorityOrder: Record<BatchOperationPriority, number> = {
    critical: 0,
    high: 1,
    normal: 2,
    low: 3,
    idle: 4,
  };

  /**
   * Create a new batch update manager
   */
  constructor(private config: BatchSystemConfig = {}) {
    this.config = {
      defaultSchedulingMode: 'microtask',
      autoOptimize: true,
      maxOperationsPerBatch: 100,
      separateReadsWrites: true,
      groupByElement: true,
      debugMode: false,
      ...config,
    };
  }

  /**
   * Determine the best scheduling mode based on browser conditions
   */
  private determineBestSchedulingMode(): BatchSchedulingMode {
    if (!this.config.autoOptimize) {
      return this.config.defaultSchedulingMode!;
    }

    // Use immediate mode for small batches or if we're already in a requestAnimationFrame
    if (this.getTotalPendingOperations() < 10) {
      return 'immediate';
    }

    // Use requestAnimationFrame for larger batches
    if (this.getTotalPendingOperations() >= 10 && this.getTotalPendingOperations() < 50) {
      return 'animation-frame';
    }

    // Use idle callback for background operations if available
    if (
      typeof window.requestIdleCallback === 'function' &&
      this.readQueue.every(op => op.priority === 'idle' || op.priority === 'low') &&
      this.writeQueue.every(op => op.priority === 'idle' || op.priority === 'low')
    ) {
      return 'idle-callback';
    }

    // Default to microtask for most cases
    return 'microtask';
  }

  /**
   * Schedule a batch flush with the appropriate timing
   */
  private scheduleFlush(mode?: BatchSchedulingMode): void {
    if (this.flushScheduled) return;

    this.flushScheduled = true;
    const schedulingMode = mode || this.determineBestSchedulingMode();

    switch (schedulingMode) {
      case 'immediate':
        this.flush();
        break;

      case 'animation-frame':
        requestAnimationFrame(() => this.flush());
        break;

      case 'microtask':
        Promise.resolve().then(() => this.flush());
        break;

      case 'idle-callback':
        if (typeof window.requestIdleCallback === 'function') {
          window.requestIdleCallback(
            deadline => {
              const timeRemaining = deadline.timeRemaining();
              // Only process if we have enough time
              if (timeRemaining > 10 || deadline.didTimeout) {
                this.flush();
              } else {
                // If not enough time, reschedule with animation frame
                this.flushScheduled = false;
                this.scheduleFlush('animation-frame');
              }
            },
            { timeout: 100 }
          ); // Give it a reasonable timeout
        } else {
          // Fall back to requestAnimationFrame if requestIdleCallback is not available
          requestAnimationFrame(() => this.flush());
        }
        break;
    }
  }

  /**
   * Flush all pending operations
   */
  private flush(options?: FlushOptions): void {
    this.isFlushing = true;
    this.flushScheduled = false;

    // Track the current batch
    const batchId = this.currentBatchId++;

    // Filter operations based on options
    const filterOperation = (op: BatchOperation): boolean => {
      if (options?.forElement && op.element !== options.forElement) return false;
      if (options?.forAnimation && op.animationId !== options.forAnimation) return false;
      if (
        options?.minPriority &&
        this.priorityOrder[op.priority] > this.priorityOrder[options.minPriority]
      )
        return false;
      return true;
    };

    // Get operations to flush
    const readOps = options?.writeOnly ? [] : this.readQueue.filter(filterOperation);
    const writeOps = options?.readOnly ? [] : this.writeQueue.filter(filterOperation);

    // Sort operations by priority and timestamp
    const sortOperations = (a: BatchOperation, b: BatchOperation) => {
      if (a.priority !== b.priority) {
        return this.priorityOrder[a.priority] - this.priorityOrder[b.priority];
      }
      return a.timestamp - b.timestamp;
    };

    readOps.sort(sortOperations);
    writeOps.sort(sortOperations);

    // Remember operations we're going to process
    const readOpsToProcess = readOps;
    const writeOpsToProcess = writeOps;

    // Remove operations we're about to process from the queues
    this.readQueue = this.readQueue.filter(op => !readOpsToProcess.includes(op));
    this.writeQueue = this.writeQueue.filter(op => !writeOpsToProcess.includes(op));

    try {
      // Process read operations first to prevent layout thrashing
      readOpsToProcess.forEach(op => {
        try {
          op.callback();
          this.completedOperations++;
        } catch (err) {
          console.error(`Error in read operation ${op.id}:`, err);
        }
      });

      // Then process write operations
      writeOpsToProcess.forEach(op => {
        try {
          op.callback();
          this.completedOperations++;
        } catch (err) {
          console.error(`Error in write operation ${op.id}:`, err);
        }
      });
    } finally {
      this.isFlushing = false;

      // Clear processed operation IDs
      readOpsToProcess.concat(writeOpsToProcess).forEach(op => {
        this.operationIds.delete(op.id);
      });
    }

    // If there are still operations pending, schedule another flush
    if (this.getTotalPendingOperations() > 0) {
      this.scheduleFlush();
    }

    if (this.config.debugMode) {
      console.debug(
        `Batch #${batchId} processed: ${readOpsToProcess.length} reads, ${writeOpsToProcess.length} writes`
      );
    }
  }

  /**
   * Get the total number of pending operations
   */
  private getTotalPendingOperations(): number {
    return this.readQueue.length + this.writeQueue.length;
  }

  /**
   * Add a read operation to the batch queue
   */
  public read<T>(callback: () => T, options: BatchOperationOptions = {}): T | undefined {
    return this.addOperation('read', callback, options);
  }

  /**
   * Add a write operation to the batch queue
   */
  public write<T>(callback: () => T, options: BatchOperationOptions = {}): T | undefined {
    return this.addOperation('write', callback, options);
  }

  /**
   * Add an operation to the appropriate queue
   */
  private addOperation<T>(
    type: 'read' | 'write',
    callback: () => T,
    options: BatchOperationOptions
  ): T | undefined {
    const { priority = 'normal', element, animationId, schedulingMode, id: customId } = options;

    // Generate a unique ID for the operation
    const baseId = customId || `${type}-${Date.now()}-${Math.round(Math.random() * 10000)}`;

    // For element-specific operations, make the ID element-specific to enable deduplication
    const id = element ? `${baseId}-${element.tagName}-${priority}` : baseId;

    // Skip if this exact operation was already scheduled (deduplication)
    if (this.operationIds.has(id)) {
      if (this.config.debugMode) {
        console.debug(`Skipping duplicate operation ${id}`);
      }
      return undefined;
    }

    // If we're already flushing and this is an immediate operation, execute it directly
    if (this.isFlushing && schedulingMode === 'immediate') {
      try {
        return callback();
      } catch (err) {
        console.error(`Error in immediate ${type} operation:`, err);
        return undefined;
      }
    }

    // Create the operation
    const operation: BatchOperation = {
      type,
      id,
      callback: () => callback(),
      priority,
      timestamp: Date.now(),
      element,
      animationId,
    };

    // Add to the appropriate queue
    if (type === 'read') {
      this.readQueue.push(operation);
    } else {
      this.writeQueue.push(operation);
    }

    // Record the operation ID
    this.operationIds.add(id);

    // Schedule a flush if needed
    if (!this.flushScheduled) {
      this.scheduleFlush(schedulingMode);
    } else if (priority === 'critical' && schedulingMode === 'immediate') {
      // Force an immediate flush for critical operations
      this.flush({
        minPriority: 'critical',
      });
    }

    // For immediate operations, we can return the result
    if (schedulingMode === 'immediate') {
      return callback();
    }

    return undefined;
  }

  /**
   * Manually flush all pending operations
   */
  public flushAll(): void {
    this.flush();
  }

  /**
   * Manually flush operations for a specific element
   */
  public flushForElement(element: Element): void {
    this.flush({ forElement: element });
  }

  /**
   * Manually flush operations for a specific animation
   */
  public flushForAnimation(animationId: string): void {
    this.flush({ forAnimation: animationId });
  }

  /**
   * Get statistics about the batch manager
   */
  public getStats() {
    return {
      pendingReads: this.readQueue.length,
      pendingWrites: this.writeQueue.length,
      totalPending: this.getTotalPendingOperations(),
      completedOperations: this.completedOperations,
      isFlushing: this.isFlushing,
      flushScheduled: this.flushScheduled,
      batchCount: this.currentBatchId,
    };
  }

  /**
   * Cancel all pending operations
   */
  public cancelAll(): void {
    this.readQueue = [];
    this.writeQueue = [];
    this.operationIds.clear();
    this.flushScheduled = false;
  }
}

/**
 * Singleton instance for easy access
 */
export const batchUpdateManager = new D3BatchUpdateManager();

/**
 * Helper to batch read operations related to DOM measurements
 */
export function batchRead<T>(
  callback: () => T,
  options: BatchOperationOptions = {}
): T | undefined {
  return batchUpdateManager.read(callback, options);
}

/**
 * Helper to batch write operations related to DOM modifications
 */
export function batchWrite<T>(
  callback: () => T,
  options: BatchOperationOptions = {}
): T | undefined {
  return batchUpdateManager.write(callback, options);
}

/**
 * Creates optimized D3 selection methods that use batched updates
 */
export function createBatchedSelection<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  options: BatchOperationOptions = {}
): d3.Selection<GElement, Datum, PElement, PDatum> {
  // Clone the selection to avoid modifying the original
  const batchedSelection = selection.clone() as d3.Selection<GElement, Datum, PElement, PDatum>;

  // Store original methods
  const originalAttr = batchedSelection.attr;
  const originalStyle = batchedSelection.style;
  const originalProperty = batchedSelection.property;
  const originalHtml = batchedSelection.html;
  const originalText = batchedSelection.text;

  // Override attr to use batched writes
  batchedSelection.attr = function (...args: any[]): any {
    if (args.length === 1) {
      // Read operation - needs to execute right away to return the value
      return originalAttr.apply(this, args);
    }

    // Write operation - can be batched
    batchWrite(
      () => {
        originalAttr.apply(batchedSelection, args);
      },
      {
        ...options,
        element: (batchedSelection.node() as Element) || undefined,
      }
    );

    return batchedSelection;
  } as any;

  // Override style to use batched writes
  batchedSelection.style = function (...args: any[]): any {
    if (args.length === 1) {
      // Read operation - needs to execute right away to return the value
      return originalStyle.apply(this, args);
    }

    // Write operation - can be batched
    batchWrite(
      () => {
        originalStyle.apply(batchedSelection, args);
      },
      {
        ...options,
        element: (batchedSelection.node() as Element) || undefined,
      }
    );

    return batchedSelection;
  } as any;

  // Override property to use batched writes
  batchedSelection.property = function (...args: any[]): any {
    if (args.length === 1) {
      // Read operation - needs to execute right away to return the value
      return originalProperty.apply(this, args);
    }

    // Write operation - can be batched
    batchWrite(
      () => {
        originalProperty.apply(batchedSelection, args);
      },
      {
        ...options,
        element: (batchedSelection.node() as Element) || undefined,
      }
    );

    return batchedSelection;
  } as any;

  // Override html to use batched writes
  batchedSelection.html = function (...args: any[]): any {
    if (args.length === 0) {
      // Read operation - needs to execute right away to return the value
      return originalHtml.apply(this, args);
    }

    // Write operation - can be batched
    batchWrite(
      () => {
        originalHtml.apply(batchedSelection, args);
      },
      {
        ...options,
        element: (batchedSelection.node() as Element) || undefined,
      }
    );

    return batchedSelection;
  } as any;

  // Override text to use batched writes
  batchedSelection.text = function (...args: any[]): any {
    if (args.length === 0) {
      // Read operation - needs to execute right away to return the value
      return originalText.apply(this, args);
    }

    // Write operation - can be batched
    batchWrite(
      () => {
        originalText.apply(batchedSelection, args);
      },
      {
        ...options,
        element: (batchedSelection.node() as Element) || undefined,
      }
    );

    return batchedSelection;
  } as any;

  return batchedSelection;
}

/**
 * Enhances a D3 selection factory function to use batched updates
 */
export function createBatchedSelectionFactory<GElement extends Element = HTMLElement>(
  options: BatchOperationOptions = {}
) {
  return function selectWithBatching<Datum = any>(
    selector: string | GElement
  ): d3.Selection<GElement, Datum, null, undefined> {
    const selection = d3.select(selector) as d3.Selection<GElement, Datum, null, undefined>;
    return createBatchedSelection(selection, options);
  };
}

/**
 * Enhances D3 transitions with batched updates
 */
export function createBatchedTransition<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  options: BatchOperationOptions = {}
): d3.Selection<GElement, Datum, PElement, PDatum> {
  // Store original transition method
  const originalTransition = selection.transition;

  // Override transition to batch operations
  selection.transition = function (...args: any[]): any {
    const transition = originalTransition.apply(this, args);

    // Store original methods
    const originalAttr = transition.attr;
    const originalStyle = transition.style;

    // Override attr to use batched writes
    transition.attr = function (name: string, value?: any): any {
      if (arguments.length === 1) {
        return originalAttr.call(this, name);
      }

      // Schedule the update to happen at each tick of the transition
      const originalTween = transition.attrTween;
      transition.attrTween(name, function (d, i, nodes) {
        const node = nodes[i];
        const interpolator =
          typeof value === 'function'
            ? d3.interpolate(originalAttr.call(d3.select(node), name), value(d, i, nodes))
            : d3.interpolate(originalAttr.call(d3.select(node), name), value);

        return function (t: number) {
          const interpolated = interpolator(t);
          // Batch the DOM update
          batchWrite(
            () => {
              d3.select(node).attr(name, interpolated);
            },
            {
              ...options,
              element: node,
              priority: t === 1 || t === 0 ? 'high' : 'normal', // Prioritize start and end values
            }
          );
          return interpolated;
        };
      });

      return this;
    } as any;

    // Override style to use batched writes
    transition.style = function (name: string, value?: any, priority?: string): any {
      if (arguments.length === 1) {
        return originalStyle.call(this, name);
      }

      // Schedule the update to happen at each tick of the transition
      const originalTween = transition.styleTween;
      transition.styleTween(name, function (d, i, nodes) {
        const node = nodes[i];
        const interpolator =
          typeof value === 'function'
            ? d3.interpolate(originalStyle.call(d3.select(node), name), value(d, i, nodes))
            : d3.interpolate(originalStyle.call(d3.select(node), name), value);

        return function (t: number) {
          const interpolated = interpolator(t);
          // Batch the DOM update
          batchWrite(
            () => {
              d3.select(node).style(name, interpolated, priority);
            },
            {
              ...options,
              element: node,
              priority: t === 1 || t === 0 ? 'high' : 'normal', // Prioritize start and end values
            }
          );
          return interpolated;
        };
      });

      return this;
    } as any;

    return transition;
  } as any;

  return selection;
}

/**
 * Integrates batch update manager with the animation frame manager
 */
export function registerBatchUpdateSystem(
  animationId: string,
  priority: AnimationPriority = 'high'
): void {
  animationFrameManager.registerAnimation(
    {
      id: `batch-updater-${animationId}`,
      name: 'Batch Update Processor',
      priority,
      type: 'custom',
      duration: 0, // Runs indefinitely
      loop: true,
    },
    (_elapsed, _deltaTime, frameInfo) => {
      // Flush batched operations on each animation frame
      // Use frame budget to determine how much we can process
      if (frameInfo.remainingFrameBudget > 2) {
        // Only flush if we have time left in the frame
        batchUpdateManager.flushAll();
      }

      return false; // Never complete this animation
    }
  );

  // Start the animation
  animationFrameManager.startAnimation(`batch-updater-${animationId}`);
}

/**
 * Helper to optimize a D3 visualization with batched updates
 */
export function optimizeWithBatchedUpdates<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  animationId: string,
  options: BatchOperationOptions = {}
): d3.Selection<GElement, Datum, PElement, PDatum> {
  // Set up batching integration with animation frame manager
  registerBatchUpdateSystem(animationId);

  // Set up default options
  const batchOptions: BatchOperationOptions = {
    animationId,
    priority: 'normal',
    ...options,
  };

  // Apply batched selections
  const batchedSelection = createBatchedSelection(selection, batchOptions);

  // Apply batched transitions
  const transitionSelection = createBatchedTransition(batchedSelection, batchOptions);

  return transitionSelection;
}

/**
 * D3 Interpolation Cache
 *
 * This module provides utilities for memoizing D3 interpolation calculations
 * to improve animation performance. It includes:
 *
 * 1. Caching strategies for different interpolation types
 * 2. Time and space optimized memoization utilities
 * 3. Cache monitoring and performance tracking
 * 4. Integration with animation frame manager
 */

import * as d3 from 'd3';
import { TypedInterpolator, typedInterpolators } from '../../types/visualizations/D3AnimationTypes';
import { animationFrameManager } from './D3AnimationFrameManager';

/**
 * Configuration for interpolation caching
 */
export interface InterpolationCacheConfig {
  /** Maximum number of entries to cache */
  maxCacheSize?: number;
  /** Time-to-live for cached values in milliseconds */
  cacheTTL?: number;
  /** Whether to use a least-recently-used eviction policy */
  useLRU?: boolean;
  /** Resolution for numeric input rounding (smaller numbers = more precision) */
  resolution?: number;
  /** Whether to enable cache statistics */
  trackStats?: boolean;
  /** Whether to profile performance impact */
  profilePerformance?: boolean;
}

/**
 * Cache statistics for performance monitoring
 */
export interface CacheStats {
  /** Total number of cache lookups */
  lookups: number;
  /** Number of cache hits */
  hits: number;
  /** Number of cache misses */
  misses: number;
  /** Hit rate as a percentage */
  hitRate: number;
  /** Number of cached entries */
  entryCount: number;
  /** Average time saved per lookup in milliseconds */
  avgTimeSaved: number;
  /** Total time saved in milliseconds */
  totalTimeSaved: number;
  /** Number of cache evictions */
  evictions: number;
  /** Memory usage estimation in bytes */
  estimatedMemoryUsage: number;
}

/**
 * Cache entry with metadata
 */
interface CacheEntry<T> {
  /** Cached value */
  value: T;
  /** When the entry was last accessed */
  lastAccessed: number;
  /** When the entry was created */
  created: number;
  /** Size estimation in bytes */
  size: number;
  /** Time taken to compute the value in ms */
  computeTime: number;
}

/**
 * Base memoization cache for any interpolation function
 */
export class InterpolationCache<T> {
  private cache = new Map<string, CacheEntry<T>>();
  private stats: CacheStats = {
    lookups: 0,
    hits: 0,
    misses: 0,
    hitRate: 0,
    entryCount: 0,
    avgTimeSaved: 0,
    totalTimeSaved: 0,
    evictions: 0,
    estimatedMemoryUsage: 0,
  };

  /**
   * Create a new interpolation cache
   */
  constructor(private config: InterpolationCacheConfig = {}) {
    const {
      maxCacheSize = 5000,
      cacheTTL = 30000, // 30 seconds
      useLRU = true,
      resolution = 0.001,
      trackStats = true,
      profilePerformance = true,
    } = config;

    this.config = {
      maxCacheSize,
      cacheTTL,
      useLRU,
      resolution,
      trackStats,
      profilePerformance,
    };
  }

  /**
   * Generate a cache key for the input parameters
   */
  private generateKey(t: number): string {
    // Round to the configured resolution to avoid excessive unique values
    const roundedT = Math.round(t / this.config.resolution!) * this.config.resolution!;
    return roundedT.toString();
  }

  /**
   * Estimate the size of a value in bytes
   */
  private estimateSize(value: T): number {
    if (typeof value === 'number') {
      return 8; // 8 bytes for a number
    } else if (typeof value === 'string') {
      return value.length * 2; // Approximate 2 bytes per character
    } else if (typeof value === 'boolean') {
      return 4;
    } else if (value === null || value === undefined) {
      return 0;
    } else if (Array.isArray(value)) {
      return (value as any[]).reduce(
        (size, item) => size + this.estimateSize(item as unknown as T),
        0
      );
    } else if (typeof value === 'object') {
      let size = 0;
      for (const key in value) {
        if (Object.prototype.hasOwnProperty.call(value, key)) {
          size += key.length * 2; // Key size
          size += this.estimateSize((value as any)[key]); // Value size
        }
      }
      return size;
    }
    return 16; // Default estimation for unknown types
  }

  /**
   * Memoize an interpolation function
   *
   * @param interpolator Original interpolator function
   * @returns Memoized interpolator function
   */
  memoize(interpolator: TypedInterpolator<T>): TypedInterpolator<T> {
    return (t: number): T => {
      // Skip caching logic if t is outside 0-1 range
      if (t < 0 || t > 1) {
        return interpolator(t);
      }

      const key = this.generateKey(t);
      const now = performance.now();

      if (this.config.trackStats) {
        this.stats.lookups++;
      }

      // Check cache
      if (this.cache.has(key)) {
        const entry = this.cache.get(key)!;

        // Check if entry is expired
        if (this.config.cacheTTL && now - entry.created > this.config.cacheTTL) {
          this.cache.delete(key);
          if (this.config.trackStats) {
            this.stats.evictions++;
          }
        } else {
          // Cache hit
          if (this.config.trackStats) {
            this.stats.hits++;
            this.stats.hitRate = this.stats.hits / this.stats.lookups;
            this.stats.totalTimeSaved += entry.computeTime;
            this.stats.avgTimeSaved = this.stats.totalTimeSaved / this.stats.hits;
          }

          // Update last accessed time for LRU
          if (this.config.useLRU) {
            entry.lastAccessed = now;
          }

          return entry.value;
        }
      }

      // Cache miss - compute the value
      const startTime = this.config.profilePerformance ? performance.now() : 0;
      const value = interpolator(t);
      const computeTime = this.config.profilePerformance ? performance.now() - startTime : 0;

      if (this.config.trackStats) {
        this.stats.misses++;
        this.stats.hitRate = this.stats.hits / this.stats.lookups;
      }

      // Check if we need to evict entries before adding a new one
      if (this.config.maxCacheSize && this.cache.size >= this.config.maxCacheSize) {
        this.evictEntries();
      }

      // Add to cache
      const size = this.estimateSize(value);
      this.cache.set(key, {
        value,
        lastAccessed: now,
        created: now,
        size,
        computeTime,
      });

      if (this.config.trackStats) {
        this.stats.entryCount = this.cache.size;
        this.stats.estimatedMemoryUsage += size;
      }

      return value;
    };
  }

  /**
   * Evict entries based on the configured policy
   */
  private evictEntries(): void {
    if (this.cache.size === 0) return;

    if (this.config.useLRU) {
      // Find the least recently used entry
      let oldestKey: string | null = null;
      let oldestTime = Infinity;

      for (const [key, entry] of this.cache.entries()) {
        if (entry.lastAccessed < oldestTime) {
          oldestTime = entry.lastAccessed;
          oldestKey = key;
        }
      }

      if (oldestKey) {
        const entry = this.cache.get(oldestKey);
        if (entry && this.config.trackStats) {
          this.stats.estimatedMemoryUsage -= entry.size;
        }
        this.cache.delete(oldestKey);
        if (this.config.trackStats) {
          this.stats.evictions++;
          this.stats.entryCount = this.cache.size;
        }
      }
    } else {
      // Simple random eviction (evict ~10% of entries)
      const keysToEvict = Math.max(1, Math.floor(this.cache.size * 0.1));
      const keys = Array.from(this.cache.keys());

      for (let i = 0; i < keysToEvict; i++) {
        const randomIndex = Math.floor(Math.random() * keys.length);
        const key = keys[randomIndex];

        const entry = this.cache.get(key);
        if (entry && this.config.trackStats) {
          this.stats.estimatedMemoryUsage -= entry.size;
        }

        this.cache.delete(key);
        keys.splice(randomIndex, 1);

        if (this.config.trackStats) {
          this.stats.evictions++;
        }
      }

      if (this.config.trackStats) {
        this.stats.entryCount = this.cache.size;
      }
    }
  }

  /**
   * Clear the cache
   */
  clear(): void {
    this.cache.clear();

    if (this.config.trackStats) {
      this.stats.entryCount = 0;
      this.stats.estimatedMemoryUsage = 0;
    }
  }

  /**
   * Get cache statistics
   */
  getStats(): CacheStats {
    return { ...this.stats };
  }

  /**
   * Resize the cache
   *
   * @param newSize New maximum cache size
   */
  resize(newSize: number): void {
    this.config.maxCacheSize = newSize;

    // If new size is smaller than current size, evict entries
    if (newSize < this.cache.size) {
      const entriesToEvict = this.cache.size - newSize;
      for (let i = 0; i < entriesToEvict; i++) {
        this.evictEntries();
      }
    }
  }
}

