/**
 * Cache that spans across multiple animations
 */
class GlobalInterpolationCache {
  private caches: Map<string, InterpolationCache<any>> = new Map();

  /**
   * Get or create a cache for a specific animation ID
   */
  getCache<T>(animationId: string, config?: InterpolationCacheConfig): InterpolationCache<T> {
    if (!this.caches.has(animationId)) {
      this.caches.set(animationId, new InterpolationCache<T>(config));
    }
    return this.caches.get(animationId) as InterpolationCache<T>;
  }

  /**
   * Clear the cache for a specific animation
   */
  clearCache(animationId: string): void {
    if (this.caches.has(animationId)) {
      this.caches.get(animationId)!.clear();
    }
  }

  /**
   * Clear all caches
   */
  clearAll(): void {
    for (const cache of this.caches.values()) {
      cache.clear();
    }
  }

  /**
   * Get aggregate statistics across all caches
   */
  getAggregateStats(): CacheStats {
    const stats: CacheStats = {
      lookups: 0,
      hits: 0,
      misses: 0,
      hitRate: 0,
      entryCount: 0,
      avgTimeSaved: 0,
      totalTimeSaved: 0,
      evictions: 0,
      estimatedMemoryUsage: 0,
    };

    for (const cache of this.caches.values()) {
      const cacheStats = cache.getStats();
      stats.lookups += cacheStats.lookups;
      stats.hits += cacheStats.hits;
      stats.misses += cacheStats.misses;
      stats.entryCount += cacheStats.entryCount;
      stats.totalTimeSaved += cacheStats.totalTimeSaved;
      stats.evictions += cacheStats.evictions;
      stats.estimatedMemoryUsage += cacheStats.estimatedMemoryUsage;
    }

    stats.hitRate = stats.lookups > 0 ? stats.hits / stats.lookups : 0;
    stats.avgTimeSaved = stats.hits > 0 ? stats.totalTimeSaved / stats.hits : 0;

    return stats;
  }
}

/**
 * Global cache instance for shared use
 */
export const globalInterpolationCache = new GlobalInterpolationCache();

/**
 * Integration with the animation frame manager to memoize animations automatically
 */
export function createMemoizedAnimation<T>(
  animationId: string,
  memoizationFn: (t: number) => T,
  config?: InterpolationCacheConfig
): (t: number) => T {
  // Get or create a cache for this animation
  const cache = globalInterpolationCache.getCache<T>(animationId, config);

  // Memoize the function
  return cache.memoize(memoizationFn);
}

/**
 * Utility to create a suite of memoized interpolators for a specific animation
 */
export function createMemoizedInterpolators(
  animationId: string,
  config?: InterpolationCacheConfig
) {
  return {
    number: (start: number, end: number): TypedInterpolator<number> => {
      const interpolator = typedInterpolators.number(start, end);
      return createMemoizedAnimation<number>(animationId, interpolator, config);
    },

    color: (start: string, end: string): TypedInterpolator<string> => {
      const interpolator = typedInterpolators.color(start, end);
      return createMemoizedAnimation<string>(animationId, interpolator, config);
    },

    date: (start: Date, end: Date): TypedInterpolator<Date> => {
      const interpolator = typedInterpolators.date(start, end);
      return createMemoizedAnimation<Date>(animationId, interpolator, config);
    },

    numberArray: (start: number[], end: number[]): TypedInterpolator<number[]> => {
      const interpolator = typedInterpolators.numberArray(start, end);
      return createMemoizedAnimation<number[]>(animationId, interpolator, config);
    },

    object: <T extends Record<string, number>>(start: T, end: T): TypedInterpolator<T> => {
      const interpolator = typedInterpolators.object(start, end);
      return createMemoizedAnimation<T>(animationId, interpolator, config);
    },
  };
}

/**
 * Helper to create a memoized D3 interpolator for any method
 */
export function memoizeD3Interpolator<T>(
  interpolatorFactory: (a: any, b: any) => (t: number) => T,
  a: any,
  b: any,
  config?: InterpolationCacheConfig
): TypedInterpolator<T> {
  const interpolator = interpolatorFactory(a, b);
  const cache = new InterpolationCache<T>(config);
  return cache.memoize(interpolator);
}

/**
 * Utility for dynamically adapting cache configurations based on performance
 */
export function createAdaptiveCache<T>(
  baseConfig: InterpolationCacheConfig = {}
): InterpolationCache<T> {
  // Start with default configuration
  const config: InterpolationCacheConfig = {
    maxCacheSize: 1000,
    resolution: 0.001,
    useLRU: true,
    trackStats: true,
    profilePerformance: true,
    ...baseConfig,
  };

  const cache = new InterpolationCache<T>(config);

  // Set up adaptation interval
  const adaptInterval = setInterval(() => {
    const stats = cache.getStats();

    // Adapt cache size based on hit rate
    if (stats.hitRate > 0.9 && stats.lookups > 100) {
      // High hit rate, might benefit from larger cache
      cache.resize(config.maxCacheSize! * 1.2);
    } else if (stats.hitRate < 0.5 && stats.lookups > 100) {
      // Low hit rate, reduce cache size to save memory
      cache.resize(Math.max(100, config.maxCacheSize! * 0.8));
    }

    // Adapt resolution based on performance gain
    if (stats.avgTimeSaved < 0.05 && stats.lookups > 100) {
      // Very small time savings, use coarser resolution
      config.resolution = Math.min(0.01, config.resolution! * 2);
    } else if (stats.avgTimeSaved > 0.5 && stats.lookups > 100) {
      // Significant time savings, use finer resolution
      config.resolution = Math.max(0.0001, config.resolution! * 0.5);
    }
  }, 5000); // Adapt every 5 seconds

  // Return the cache with a wrapped clear method that also clears the interval
  const originalClear = cache.clear.bind(cache);
  cache.clear = () => {
    clearInterval(adaptInterval);
    originalClear();
  };

  return cache;
}

/**
 * Integration with the animation frame manager to automatically memoize animations
 *
 * @param animationId Animation ID to enhance with memoization
 * @param config Memoization configuration
 */
export function enhanceAnimationWithMemoization(
  animationId: string,
  config?: InterpolationCacheConfig
): void {
  // Get the animation from the frame manager
  const animations = animationFrameManager.getAnimations();
  const animation = animations.find(a => a.id === animationId);

  if (!animation) {
    console.warn(`Animation ${animationId} not found in frame manager`);
    return;
  }

  // Store the animation ID in global cache for reference
  globalInterpolationCache.getCache(animationId, config);

  // Animation is now ready to use memoized interpolators via createMemoizedInterpolators
}

/**
 * Cleanup memoization resources when an animation is removed
 */
export function cleanupMemoizationForAnimation(animationId: string): void {
  globalInterpolationCache.clearCache(animationId);
}

/**
 * Get global memoization statistics
 */
export function getMemoizationStats(): CacheStats {
  return globalInterpolationCache.getAggregateStats();
}

/**
 * Utility to optimize an entire D3 selection's transitions with memoization
 */
export function optimizeD3Transitions<
  GElement extends Element,
  Datum,
  PElement extends Element,
  PDatum,
>(
  selection: d3.Selection<GElement, Datum, PElement, PDatum>,
  config?: InterpolationCacheConfig
): d3.Selection<GElement, Datum, PElement, PDatum> {
  // Store original transition method
  const originalTransition = selection.transition;

  // Replace with memoized version
  selection.transition = function (...args: any[]): any {
    const transition = originalTransition.apply(this, args);

    // Store original interpolation functions
    const originalAttrTween = transition.attrTween;
    const originalStyleTween = transition.styleTween;

    // Replace with memoized versions
    transition.attrTween = function (name: string, factory: any): any {
      // Create a memoized version of the factory function
      const memoizedFactory = function (d: Datum, i: number, a: any) {
        const interpolator = factory(d, i, a);
        if (typeof interpolator === 'function') {
          const cache = new InterpolationCache<any>(config);
          return cache.memoize(interpolator);
        }
        return interpolator;
      };

      return originalAttrTween.call(this, name, memoizedFactory);
    };

    transition.styleTween = function (name: string, factory: any): any {
      // Create a memoized version of the factory function
      const memoizedFactory = function (d: Datum, i: number, a: any) {
        const interpolator = factory(d, i, a);
        if (typeof interpolator === 'function') {
          const cache = new InterpolationCache<any>(config);
          return cache.memoize(interpolator);
        }
        return interpolator;
      };

      return originalStyleTween.call(this, name, memoizedFactory);
    };

    return transition;
  } as any;

  return selection;
}

/**
 * D3 Performance Optimizations
 *
 * This utility provides optimizations for D3 visualizations to address
 * common performance bottlenecks identified by the profiler.
 */

import * as d3 from 'd3';
import { memoizedD3Accessors } from './D3PerformanceProfiler';

/**
 * Configuration options for D3 performance optimizations
 */
export interface PerformanceOptimizationConfig {
  /** Whether to use memoized accessors */
  useMemoizedAccessors: boolean;
  /** Whether to use optimized simulation tick logic */
  useOptimizedSimulation: boolean;
  /** Whether to batch DOM updates */
  useBatchedDOMUpdates: boolean;
  /** Whether to use throttled rendering */
  useThrottledRendering: boolean;
  /** Minimum frame time in ms (for throttling) */
  minFrameTimeMs: number;
  /** Whether to use worker-based simulation */
  useWorkerSimulation: boolean;
}

/**
 * Default optimization configuration
 */
export const defaultOptimizationConfig: PerformanceOptimizationConfig = {
  useMemoizedAccessors: true,
  useOptimizedSimulation: true,
  useBatchedDOMUpdates: true,
  useThrottledRendering: false,
  minFrameTimeMs: 16, // ~60fps
  useWorkerSimulation: false,
};

/**
 * Apply optimizations to a D3 force simulation
 *
 * @param simulation The simulation to optimize
 * @param config Optimization configuration
 */
export function optimizeForceSimulation(
  simulation: d3.Simulation<d3.SimulationNodeDatum, undefined>,
  config: Partial<PerformanceOptimizationConfig> = {}
): void {
  const mergedConfig = { ...defaultOptimizationConfig, ...config };

  // Set optimal alpha decay for faster convergence
  if (mergedConfig.useOptimizedSimulation) {
    simulation.alphaDecay(0.03); // Faster convergence
    simulation.alphaMin(0.001); // Stop at lower energy
  }

  // Optimize force distances based on node count
  const nodeCount = simulation.nodes().length;
  const linkForce = simulation.force('link');
  const chargeForce = simulation.force('charge') as d3.ForceManyBody<d3.SimulationNodeDatum>;

  if (linkForce && 'distance' in linkForce) {
    // Adjust link distance based on node count
    (linkForce as any).distance(nodeCount > 100 ? 30 : nodeCount > 50 ? 40 : 50);
  }

  if (chargeForce) {
    // Adjust charge strength based on node count
    const chargeStrength = nodeCount > 200 ? -30 : nodeCount > 100 ? -50 : -70;

    chargeForce.strength(chargeStrength);

    // Use theta for approximation in large simulations
    if (nodeCount > 100) {
      chargeForce.theta(0.9); // Higher theta = more approximation = faster but less accurate
    }
  }
}

/**
 * Create a throttled tick function for improved rendering performance
 *
 * @param simulation The simulation to optimize
 * @param tickCallback The function to call on each tick
 * @param config Optimization configuration
 * @returns A function to start/stop the optimized simulation
 */
export function createOptimizedTicker(
  simulation: d3.Simulation<d3.SimulationNodeDatum, undefined>,
  tickCallback: () => void,
  config: Partial<PerformanceOptimizationConfig> = {}
): { start: () => void; stop: () => void } {
  const mergedConfig = { ...defaultOptimizationConfig, ...config };

  let rafId: number | null = null;
  let lastFrameTime = 0;

  // Optimized ticker function
  const ticker = () => {
    const currentTime = performance.now();
    const elapsed = currentTime - lastFrameTime;

    // Run multiple simulation steps for complex simulations with throttled rendering
    if (mergedConfig.useOptimizedSimulation) {
      const iterations = Math.min(
        4, // Max iterations per frame
        Math.max(
          1, // At least one iteration
          Math.floor(elapsed / 8) // ~8ms per iteration target
        )
      );

      // Run simulation steps
      for (let i = 0; i < iterations; i++) {
        simulation.tick();
      }
    } else {
      // Standard single tick
      simulation.tick();
    }

    // Throttle rendering for performance
    if (!mergedConfig.useThrottledRendering || elapsed >= mergedConfig.minFrameTimeMs) {
      tickCallback();
      lastFrameTime = currentTime;
    }

    // Continue animation if simulation is still active
    if (simulation.alpha() > simulation.alphaMin()) {
      rafId = requestAnimationFrame(ticker);
    } else {
      // Ensure one final render when simulation ends
      tickCallback();
      rafId = null;
    }
  };

  return {
    start: () => {
      if (rafId === null) {
        lastFrameTime = performance.now();
        simulation.alpha(1).restart();
        rafId = requestAnimationFrame(ticker);
      }
    },
    stop: () => {
      if (rafId !== null) {
        cancelAnimationFrame(rafId);
        rafId = null;
      }
    },
  };
}

/**
 * Optimize D3 selection updates for performance
 *
 * @param selection The D3 selection to optimize
 * @param updateFn The function that performs updates on the selection
 * @param config Optimization configuration
 */
export function optimizeSelectionUpdates<T extends d3.Selection<any, any, any, any>>(
  selection: T,
  updateFn: (selection: T) => void,
  config: Partial<PerformanceOptimizationConfig> = {}
): void {
  const mergedConfig = { ...defaultOptimizationConfig, ...config };

  if (mergedConfig.useBatchedDOMUpdates) {
    // Temporarily detach elements from DOM for batch updates
    // This only works for SVG elements that are already in the DOM
    const parent = selection.node()?.parentNode;
    const nextSibling = selection.node()?.nextSibling;

    if (parent) {
      const fragment = document.createDocumentFragment();
      selection.each(function () {
        fragment.appendChild(this);
      });

      // Apply updates to detached elements
      updateFn(selection);

      // Reattach elements
      if (nextSibling) {
        parent.insertBefore(fragment, nextSibling);
      } else {
        parent.appendChild(fragment);
      }
    } else {
      // Fall back to normal updates if parent not found
      updateFn(selection);
    }
  } else {
    // Standard updates
    updateFn(selection);
  }
}

/**
 * Create a data structure index for faster lookups
 *
 * @param data Array of data items
 * @param keyFn Function to extract a key from each item
 * @returns An object mapping keys to data items
 */
export function createDataIndex<T>(data: T[], keyFn: (item: T) => string): Record<string, T> {
  return data.reduce(
    (index, item) => {
      const key = keyFn(item);
      index[key] = item;
      return index;
    },
    {} as Record<string, T>
  );
}

/**
 * Create an optimized D3 accessor function for node properties
 *
 * @param propName The property name to access
 * @param defaultValue Default value to return if property not found
 * @param useMemoized Whether to use memoization
 * @returns An accessor function for the specified property
 */
export function createOptimizedAccessor<T>(
  propName: string,
  defaultValue: T,
  useMemoized = true
): (node: unknown) => T {
  // Use WeakMap for memoization if requested
  const cache = new WeakMap<object, T>();

  // Create the accessor function
  const accessor = (node: unknown): T => {
    if (!node || typeof node !== 'object') {
      return defaultValue;
    }

    // Type-safe property access
    return (node as any)[propName] !== undefined ? (node as any)[propName] : defaultValue;
  };

  // Return memoized version if requested
  if (useMemoized) {
    return (node: unknown): T => {
      if (!node || typeof node !== 'object') {
        return defaultValue;
      }

      // Check cache first
      if (cache.has(node as object)) {
        return cache.get(node as object) as T;
      }

      // Calculate and cache result
      const result = accessor(node);
      cache.set(node as object, result);
      return result;
    };
  }

  return accessor;
}

/**
 * Optimize node coordinates by pre-computing for the current frame
 *
 * @param nodes Array of simulation nodes
 * @returns Object with optimized coordinate getters
 */
export function createCoordinateCache(nodes: d3.SimulationNodeDatum[]): {
  getX: (node: d3.SimulationNodeDatum) => number;
  getY: (node: d3.SimulationNodeDatum) => number;
} {
  // Pre-compute and cache coordinates
  const xCache = new Map<string, number>();
  const yCache = new Map<string, number>();

  // Populate caches
  nodes.forEach(node => {
    const id = (node as any).id || Math.random().toString();
    xCache.set(id, memoizedD3Accessors.getX(node));
    yCache.set(id, memoizedD3Accessors.getY(node));
  });

  return {
    getX: (node: d3.SimulationNodeDatum): number => {
      const id = (node as any).id || '';
      return xCache.has(id) ? xCache.get(id) || 0 : memoizedD3Accessors.getX(node);
    },
    getY: (node: d3.SimulationNodeDatum): number => {
      const id = (node as any).id || '';
      return yCache.has(id) ? yCache.get(id) || 0 : memoizedD3Accessors.getY(node);
    },
  };
}

/**
 * Optimize data transformations by memoizing results
 *
 * @param transformFn The function that transforms the data
 * @returns A memoized version of the transform function
 */
export function memoizeTransform<T, R>(transformFn: (data: T) => R): (data: T) => R {
  const cache = new Map<string, R>();

  return (data: T): R => {
    // Create a cache key based on JSON stringification
    // Note: This assumes data is serializable and not too large
    const key = JSON.stringify(data);

    if (cache.has(key)) {
      return cache.get(key) as R;
    }

    const result = transformFn(data);
    cache.set(key, result);
    return result;
  };
}

/**
 * Apply all available performance optimizations to a D3 visualization
 *
 * @param simulation The force simulation to optimize
 * @param selectionUpdater Function that updates the visualization's DOM elements
 * @param config Optimization configuration
 * @returns Controls for the optimized simulation
 */
export function optimizeVisualization(
  simulation: d3.Simulation<d3.SimulationNodeDatum, undefined>,
  selectionUpdater: () => void,
  config: Partial<PerformanceOptimizationConfig> = {}
): { start: () => void; stop: () => void } {
  const mergedConfig = { ...defaultOptimizationConfig, ...config };

  // 1. Optimize the force simulation parameters
  optimizeForceSimulation(simulation, mergedConfig);

  // 2. Create optimized ticker with throttled rendering
  const optimizedTicker = createOptimizedTicker(
    simulation,
    () => {
      // Clear accessor caches on each frame
      if (mergedConfig.useMemoizedAccessors) {
        memoizedD3Accessors.clearAllCache();
      }

      // Call the selection updater function to render changes
      selectionUpdater();
    },
    mergedConfig
  );

  return optimizedTicker;
}

/**
 * D3 Performance Profiler
 *
 * This utility provides tools for profiling D3 visualizations to identify
 * performance bottlenecks in simulation ticks and rendering operations.
 */

import * as d3 from 'd3';
import { d3Accessors, SimulationNodeDatum } from '../../types/visualizations/D3Types';

/**
 * Types of performance measurements
 */
export enum ProfilerMeasurementType {
  SIMULATION_TICK = 'simulation_tick',
  FORCE_CALCULATION = 'force_calculation',
  COORDINATE_ACCESS = 'coordinate_access',
  DOM_MANIPULATION = 'dom_manipulation',
  ATTRIBUTE_UPDATE = 'attribute_update',
  TRANSITION = 'transition',
  DATA_PREPARATION = 'data_preparation',
  OTHER = 'other',
}

/**
 * Performance measurement result
 */
export interface PerformanceMeasurement {
  /** Type of operation measured */
  type: ProfilerMeasurementType;
  /** Name/description of the specific operation */
  name: string;
  /** Duration in milliseconds */
  durationMs: number;
  /** Number of operations performed */
  operationCount: number;
  /** Whether this operation is a bottleneck */
  isBottleneck: boolean;
  /** Additional metadata */
  metadata?: Record<string, unknown>;
  /** Timestamp when the measurement was taken */
  timestamp: Date;
}

/**
 * A section of code to profile
 */
export interface ProfilerSection {
  /** Name/description of the section */
  name: string;
  /** Type of operation */
  type: ProfilerMeasurementType;
  /** Function to execute */
  fn: () => void;
  /** Operation count (e.g., number of nodes processed) */
  operationCount: number;
  /** Additional metadata */
  metadata?: Record<string, unknown>;
}

/**
 * Performance profile result
 */
export interface PerformanceProfile {
  /** All measurements taken */
  measurements: PerformanceMeasurement[];
  /** Total duration of all measurements */
  totalDurationMs: number;
  /** Identified bottlenecks */
  bottlenecks: PerformanceMeasurement[];
  /** Start time of the profile */
  startTime: Date;
  /** End time of the profile */
  endTime: Date;
  /** Recommendations for performance optimization */
  recommendations: string[];
}

/**
 * Methods for profiling D3 visualization performance
 */
export class D3Profiler {
  private measurements: PerformanceMeasurement[] = [];
  private startTime: Date | null = null;
  private endTime: Date | null = null;
  private bottleneckThreshold = 5; // ms

  /**
   * Create a new profiler instance
   *
   * @param bottleneckThreshold Threshold in ms to consider an operation a bottleneck
   */
  constructor(bottleneckThreshold?: number) {
    if (bottleneckThreshold !== undefined) {
      this.bottleneckThreshold = bottleneckThreshold;
    }
  }

  /**
   * Start a profiling session
   */
  startProfiling(): void {
    this.measurements = [];
    this.startTime = new Date();
  }

  /**
   * End a profiling session and return the profile
   *
   * @returns The performance profile
   */
  endProfiling(): PerformanceProfile {
    this.endTime = new Date();

    if (!this.startTime) {
      throw new Error('Cannot end profiling session that has not been started');
    }

    // Calculate total duration
    const totalDurationMs = this.measurements.reduce(
      (total, measurement) => total + measurement.durationMs,
      0
    );

    // Identify bottlenecks
    const bottlenecks = this.measurements.filter(
      measurement =>
        measurement.durationMs > this.bottleneckThreshold ||
        measurement.durationMs / totalDurationMs > 0.1 // >10% of total time
    );

    // Generate recommendations
    const recommendations = this.generateRecommendations(bottlenecks, totalDurationMs);

    return {
      measurements: this.measurements,
      totalDurationMs,
      bottlenecks,
      startTime: this.startTime,
      endTime: this.endTime,
      recommendations,
    };
  }

  /**
   * Measure the performance of a section of code
   *
   * @param section The section to profile
   * @returns The performance measurement
   */
  measureSection(section: ProfilerSection): PerformanceMeasurement {
    const startTime = performance.now();
    section.fn();
    const endTime = performance.now();

    const durationMs = endTime - startTime;
    const isBottleneck = durationMs > this.bottleneckThreshold;

    const measurement: PerformanceMeasurement = {
      type: section.type,
      name: section.name,
      durationMs,
      operationCount: section.operationCount,
      isBottleneck,
      metadata: section.metadata,
      timestamp: new Date(),
    };

    this.measurements.push(measurement);
    return measurement;
  }

  /**
   * Generate optimization recommendations based on the profile
   *
   * @param bottlenecks The identified bottlenecks
   * @param totalDurationMs Total duration of all measurements
   * @returns Array of recommendation strings
   */
  private generateRecommendations(
    bottlenecks: PerformanceMeasurement[],
    totalDurationMs: number
  ): string[] {
    const recommendations: string[] = [];

    if (bottlenecks.length === 0) {
      recommendations.push('No significant bottlenecks detected.');
      return recommendations;
    }

    // Group bottlenecks by type
    const bottlenecksByType = bottlenecks.reduce(
      (groups, bottleneck) => {
        const group = groups[bottleneck.type] || [];
        group.push(bottleneck);
        groups[bottleneck.type] = group;
        return groups;
      },
      {} as Record<ProfilerMeasurementType, PerformanceMeasurement[]>
    );

    // Generate recommendations for each type of bottleneck
    if (bottlenecksByType[ProfilerMeasurementType.SIMULATION_TICK]) {
      recommendations.push(
        'Optimize simulation tick function by reducing complexity or frequency of updates.'
      );
      recommendations.push(
        'Consider using a lower alpha decay rate to reduce the number of simulation ticks.'
      );
    }

    if (bottlenecksByType[ProfilerMeasurementType.COORDINATE_ACCESS]) {
      recommendations.push(
        'Consider memoizing coordinate accessors for frequently accessed nodes.'
      );
      recommendations.push(
        'In performance-critical loops, use direct property access with proper type checking.'
      );
    }

    if (bottlenecksByType[ProfilerMeasurementType.DOM_MANIPULATION]) {
      recommendations.push('Reduce DOM manipulation frequency by batching updates.');
      recommendations.push(
        "Use D3's enter/update/exit pattern efficiently to minimize DOM operations."
      );
    }

    if (bottlenecksByType[ProfilerMeasurementType.ATTRIBUTE_UPDATE]) {
      recommendations.push('Reduce the number of attribute updates during animation frames.');
      recommendations.push(
        'Consider using CSS transitions for simple animations instead of JavaScript.'
      );
    }

    if (bottlenecksByType[ProfilerMeasurementType.DATA_PREPARATION]) {
      recommendations.push('Cache transformed data to avoid recalculating on each render.');
      recommendations.push('Implement lazy evaluation for data transformations when possible.');
    }

    // General recommendations
    recommendations.push(
      'Consider using React.memo or useMemo for components that render D3 visualizations.'
    );

    return recommendations;
  }
}

/**
 * Specialized profiler for D3 force simulations
 */
export class ForceSimulationProfiler {
  private profiler: D3Profiler;
  private originalTick: Function | null = null;
  private simulation: d3.Simulation<d3.SimulationNodeDatum, undefined> | null = null;
  private tickMeasurements: PerformanceMeasurement[] = [];
  private forceMeasurements: Record<string, PerformanceMeasurement[]> = {};

  constructor(bottleneckThreshold?: number) {
    this.profiler = new D3Profiler(bottleneckThreshold);
  }

  /**
   * Attach the profiler to a D3 force simulation
   *
   * @param simulation The D3 force simulation to profile
   */
  attachToSimulation(simulation: d3.Simulation<d3.SimulationNodeDatum, undefined>): void {
    this.simulation = simulation;
    this.originalTick = simulation.tick;

    // Wrap the tick function to measure performance
    const originalTick = this.originalTick;
    const profiler = this;

    // Need to use a proper type that matches D3's expected return type
    simulation.tick = function customTick() {
      const startTime = performance.now();
      // Call original tick in the context of the simulation
      const result = originalTick?.apply(simulation);
      const endTime = performance.now();

      profiler.tickMeasurements.push({
        type: ProfilerMeasurementType.SIMULATION_TICK,
        name: 'Simulation Tick',
        durationMs: endTime - startTime,
        operationCount: simulation.nodes().length,
        isBottleneck: endTime - startTime > (profiler.profiler as any).bottleneckThreshold,
        metadata: {
          nodeCount: simulation.nodes().length,
          alpha: simulation.alpha(),
        },
        timestamp: new Date(),
      });

      return result;
    };

    // Wrap each force to measure performance
    // Use proper typings for on() method (which is available in D3 force objects)
    const linkForce = simulation.force('link');
    const chargeForce = simulation.force('charge');
    const centerForce = simulation.force('center');
    const collisionForce = simulation.force('collision');

    if (linkForce && 'on' in linkForce) {
      (linkForce as any).on('tick.profile', this.measureForce('link'));
    }

    if (chargeForce && 'on' in chargeForce) {
      (chargeForce as any).on('tick.profile', this.measureForce('charge'));
    }

    if (centerForce && 'on' in centerForce) {
      (centerForce as any).on('tick.profile', this.measureForce('center'));
    }

    if (collisionForce && 'on' in collisionForce) {
      (collisionForce as any).on('tick.profile', this.measureForce('collision'));
    }
  }

  /**
   * Generate a function to measure force calculation performance
   *
   * @param forceName The name of the force to measure
   * @returns A function to measure the force
   */
  private measureForce(forceName: string): () => void {
    return () => {
      const startTime = performance.now();
      // The force calculation happens automatically
      const endTime = performance.now();

      if (!this.forceMeasurements[forceName]) {
        this.forceMeasurements[forceName] = [];
      }

      this.forceMeasurements[forceName].push({
        type: ProfilerMeasurementType.FORCE_CALCULATION,
        name: `${forceName} Force Calculation`,
        durationMs: endTime - startTime,
        operationCount: this.simulation?.nodes().length || 0,
        isBottleneck: endTime - startTime > (this.profiler as any).bottleneckThreshold,
        metadata: {
          forceName,
          nodeCount: this.simulation?.nodes().length || 0,
          alpha: this.simulation?.alpha() || 0,
        },
        timestamp: new Date(),
      });
    };
  }

  /**
   * Detach the profiler from the simulation
   */
  detachFromSimulation(): void {
    if (!this.simulation || !this.originalTick) return;

    // Restore original tick function
    this.simulation.tick = this.originalTick as any;

    // Remove event listeners from forces
    const linkForce = this.simulation.force('link');
    const chargeForce = this.simulation.force('charge');
    const centerForce = this.simulation.force('center');
    const collisionForce = this.simulation.force('collision');

    if (linkForce && 'on' in linkForce) {
      (linkForce as any).on('tick.profile', null);
    }

    if (chargeForce && 'on' in chargeForce) {
      (chargeForce as any).on('tick.profile', null);
    }

    if (centerForce && 'on' in centerForce) {
      (centerForce as any).on('tick.profile', null);
    }

    if (collisionForce && 'on' in collisionForce) {
      (collisionForce as any).on('tick.profile', null);
    }

    this.simulation = null;
    this.originalTick = null;
  }

  /**
   * Get the profile results
   *
   * @returns The performance profile
   */
  getProfile(): PerformanceProfile {
    const allMeasurements = [
      ...this.tickMeasurements,
      ...Object.values(this.forceMeasurements).flat(),
    ];

    const totalDurationMs = allMeasurements.reduce(
      (total, measurement) => total + measurement.durationMs,
      0
    );

    const bottlenecks = allMeasurements.filter(
      measurement =>
        measurement.durationMs > (this.profiler as any).bottleneckThreshold ||
        measurement.durationMs / totalDurationMs > 0.1 // >10% of total time
    );

    // Calculate average tick time
    const avgTickTime =
      this.tickMeasurements.length > 0
        ? this.tickMeasurements.reduce((sum, m) => sum + m.durationMs, 0) /
          this.tickMeasurements.length
        : 0;

    // Generate specialized recommendations
    const recommendations = this.generateForceRecommendations(bottlenecks, avgTickTime);

    // Sort measurements by time (most expensive first)
    const sortedMeasurements = allMeasurements.sort((a, b) => b.durationMs - a.durationMs);

    return {
      measurements: sortedMeasurements,
      totalDurationMs,
      bottlenecks,
      startTime: this.tickMeasurements.length > 0 ? this.tickMeasurements[0].timestamp : new Date(),
      endTime:
        this.tickMeasurements.length > 0
          ? this.tickMeasurements[this.tickMeasurements.length - 1].timestamp
          : new Date(),
      recommendations,
    };
  }

  /**
   * Generate recommendations for force simulation optimization
   *
   * @param bottlenecks The identified bottlenecks
   * @param avgTickTime Average tick time in milliseconds
   * @returns Array of recommendation strings
   */
  private generateForceRecommendations(
    bottlenecks: PerformanceMeasurement[],
    avgTickTime: number
  ): string[] {
    const recommendations: string[] = [];

    // Check for slow tick performance
    if (avgTickTime > 16) {
      // 16ms = 60fps threshold
      recommendations.push(
        `Simulation tick performance is below 60fps (${avgTickTime.toFixed(2)}ms per tick). Consider the following optimizations:`
      );

      recommendations.push('- Reduce the number of nodes in the simulation');
      recommendations.push('- Use a higher alpha decay rate to converge faster');
      recommendations.push('- Implement a step-based simulation instead of continuous');
    }

    // Identify problematic forces
    const forceBottlenecks = bottlenecks.filter(
      b => b.type === ProfilerMeasurementType.FORCE_CALCULATION
    );

    if (forceBottlenecks.length > 0) {
      // Group by force name
      const forceGroups = forceBottlenecks.reduce(
        (groups, bottleneck) => {
          const forceName = (bottleneck.metadata?.forceName as string) || 'unknown';
          const group = groups[forceName] || [];
          group.push(bottleneck);
          groups[forceName] = group;
          return groups;
        },
        {} as Record<string, PerformanceMeasurement[]>
      );

      // Generate recommendations for each slow force
      Object.entries(forceGroups).forEach(([forceName, measurements]) => {
        const avgForceTime =
          measurements.reduce((sum, m) => sum + m.durationMs, 0) / measurements.length;

        recommendations.push(
          `Force '${forceName}' is taking ${avgForceTime.toFixed(2)}ms on average. Consider:`
        );

        if (forceName === 'link') {
          recommendations.push('- Reduce the number of links or link distance');
          recommendations.push('- Use a more efficient link force implementation');
        } else if (forceName === 'charge') {
          recommendations.push('- Reduce charge strength or increase distance cutoff');
          recommendations.push('- Use a more approximate charge calculation (higher theta value)');
        } else if (forceName === 'collision') {
          recommendations.push('- Reduce collision radius or iteration count');
          recommendations.push('- Consider using a simpler collision detection strategy');
        }
      });
    }

    return recommendations;
  }

  /**
   * Reset the profiler
   */
  reset(): void {
    this.tickMeasurements = [];
    this.forceMeasurements = {};
  }
}

/**
 * Profile the performance of coordinate access methods
 *
 * @param nodes The nodes to test coordinate access on
 * @param iterations Number of iterations to run
 * @returns The performance measurement
 */
export function profileCoordinateAccess(
  nodes: SimulationNodeDatum[],
  iterations: number = 1000
): PerformanceProfile {
  const profiler = new D3Profiler();
  profiler.startProfiling();

  // Profile direct property access
  profiler.measureSection({
    name: 'Direct Property Access',
    type: ProfilerMeasurementType.COORDINATE_ACCESS,
    operationCount: nodes.length * iterations,
    fn: () => {
      for (let i = 0; i < iterations; i++) {
        for (const node of nodes) {
          const x = node.x || 0;
          const y = node.y || 0;
          const transform = `translate(${x}, ${y})`;
        }
      }
    },
  });

  // Profile type-safe accessor functions
  profiler.measureSection({
    name: 'Type-Safe Accessor Functions',
    type: ProfilerMeasurementType.COORDINATE_ACCESS,
    operationCount: nodes.length * iterations,
    fn: () => {
      for (let i = 0; i < iterations; i++) {
        for (const node of nodes) {
          const x = d3Accessors.getX(node);
          const y = d3Accessors.getY(node);
          const transform = `translate(${x}, ${y})`;
        }
      }
    },
  });

  return profiler.endProfiling();
}

/**
 * Profile DOM manipulation performance
 *
 * @param containerSelector The CSS selector for the container element
 * @param nodeCount Number of nodes to create
 * @returns The performance measurement
 */
export function profileDOMOperations(
  containerSelector: string,
  nodeCount: number = 1000
): PerformanceProfile {
  const profiler = new D3Profiler();
  profiler.startProfiling();

  const container = d3.select(containerSelector);

  // Generate test data
  const nodes = Array.from({ length: nodeCount }, (_, i) => ({
    id: `node-${i}`,
    x: Math.random() * 500,
    y: Math.random() * 500,
    value: Math.random() * 100,
  }));

  // Profile DOM creation
  profiler.measureSection({
    name: 'DOM Creation',
    type: ProfilerMeasurementType.DOM_MANIPULATION,
    operationCount: nodeCount,
    fn: () => {
      container.selectAll('*').remove();
      const svg = container.append('svg').attr('width', 500).attr('height', 500);

      svg
        .selectAll('circle')
        .data(nodes)
        .enter()
        .append('circle')
        .attr('cx', d => (d as any).x)
        .attr('cy', d => (d as any).y)
        .attr('r', d => (d as any).value / 10)
        .attr('fill', 'steelblue');
    },
  });

  // Profile attribute updates
  profiler.measureSection({
    name: 'Attribute Updates',
    type: ProfilerMeasurementType.ATTRIBUTE_UPDATE,
    operationCount: nodeCount,
    fn: () => {
      container
        .selectAll('circle')
        .attr('cx', d => (d as any).x + Math.random() * 10 - 5)
        .attr('cy', d => (d as any).y + Math.random() * 10 - 5)
        .attr('r', d => (d as any).value / 10 + Math.random() * 2);
    },
  });

  // Profile transitions
  profiler.measureSection({
    name: 'Transitions',
    type: ProfilerMeasurementType.TRANSITION,
    operationCount: nodeCount,
    fn: () => {
      container
        .selectAll('circle')
        .transition()
        .duration(500)
        .attr('cx', d => (d as any).x + Math.random() * 20 - 10)
        .attr('cy', d => (d as any).y + Math.random() * 20 - 10);
    },
  });

  // Profile DOM removal
  profiler.measureSection({
    name: 'DOM Removal',
    type: ProfilerMeasurementType.DOM_MANIPULATION,
    operationCount: nodeCount,
    fn: () => {
      container.selectAll('*').remove();
    },
  });

  return profiler.endProfiling();
}

/**
 * Create a memoized version of the D3 accessor functions
 */
export const memoizedD3Accessors = {
  // Cache for getX results
  xCache: new WeakMap<object, number>(),

  // Cache for getY results
  yCache: new WeakMap<object, number>(),

  /**
   * Get the x coordinate of a node (with memoization)
   */
  getX: (node: unknown): number => {
    if (!node || typeof node !== 'object') {
      return 0;
    }

    // Check cache first
    if (memoizedD3Accessors.xCache.has(node)) {
      return memoizedD3Accessors.xCache.get(node) || 0;
    }

    // Calculate and cache result
    const result = d3Accessors.getX(node);
    memoizedD3Accessors.xCache.set(node, result);
    return result;
  },

  /**
   * Get the y coordinate of a node (with memoization)
   */
  getY: (node: unknown): number => {
    if (!node || typeof node !== 'object') {
      return 0;
    }

    // Check cache first
    if (memoizedD3Accessors.yCache.has(node)) {
      return memoizedD3Accessors.yCache.get(node) || 0;
    }

    // Calculate and cache result
    const result = d3Accessors.getY(node);
    memoizedD3Accessors.yCache.set(node, result);
    return result;
  },

  /**
   * Clear the cache for a specific node
   */
  clearCache: (node: object): void => {
    memoizedD3Accessors.xCache.delete(node);
    memoizedD3Accessors.yCache.delete(node);
  },

  /**
   * Clear the entire cache
   */
  clearAllCache: (): void => {
    memoizedD3Accessors.xCache = new WeakMap<object, number>();
    memoizedD3Accessors.yCache = new WeakMap<object, number>();
  },
};

/**
 * @file HookPerformanceDashboard.tsx
 * A React component for visualizing hook performance data.
 *
 * This component provides:
 * 1. Real-time visualization of hook performance metrics
 * 2. Filtering and sorting of performance data
 * 3. Performance optimization recommendations
 */

import React, { ChangeEvent, useEffect, useMemo, useState } from 'react';
import {
  clearAllHooksPerformanceData,
  clearHookPerformanceData,
  getAllHooksPerformanceData,
  getHookPerformanceReport,
  HookPerformanceData,
} from './hookPerformanceMonitor';

interface HookPerformanceDashboardProps {
  /**
   * Whether to auto-refresh the dashboard data
   */
  autoRefresh?: boolean;

  /**
   * Refresh interval in milliseconds
   */
  refreshInterval?: number;

  /**
   * Whether to show the dashboard in a collapsed state initially
   */
  initiallyCollapsed?: boolean;

  /**
   * Whether to show detailed data for each hook
   */
  showDetails?: boolean;

  /**
   * Filter hooks by name
   */
  filterByHook?: string;
}

// Type for the sort options
type SortOption = 'name' | 'renders' | 'selectors' | 'computations';

/**
 * Hook Performance Dashboard component
 */
const HookPerformanceDashboard: React.FC<HookPerformanceDashboardProps> = ({
  autoRefresh = true,
  refreshInterval = 1000,
  initiallyCollapsed = true,
  showDetails = false,
  filterByHook,
}) => {
  const [collapsed, setCollapsed] = useState(initiallyCollapsed);
  const [performanceData, setPerformanceData] = useState<Record<string, HookPerformanceData>>({});
  const [selectedHook, setSelectedHook] = useState<string | null>(null);
  const [sortBy, setSortBy] = useState<SortOption>('renders');
  const [filterText, setFilterText] = useState(filterByHook || '');
  const [showOnlySlowHooks, setShowOnlySlowHooks] = useState(false);

  // Refresh performance data
  useEffect(() => {
    const refreshData = () => {
      setPerformanceData(getAllHooksPerformanceData());
    };

    // Initial refresh
    refreshData();

    // Auto-refresh if enabled
    let interval: NodeJS.Timeout | null = null;
    if (autoRefresh) {
      interval = setInterval(refreshData, refreshInterval);
    }

    return () => {
      if (interval) {
        clearInterval(interval);
      }
    };
  }, [autoRefresh, refreshInterval]);

  // Calculate derived data
  const filteredAndSortedHooks = useMemo(() => {
    // Convert to array for filtering and sorting
    let hooks = Object.entries(performanceData).map(([hookName, data]) => ({
      hookName,
      renderCount: data.renderCount,
      selectorCount: Object.keys(data.selectorTimes).length,
      computationCount: Object.keys(data.computationTimes).length,
      avgSelectorTime: calculateAverageTime(data.selectorTimes),
      avgComputationTime: calculateAverageTime(data.computationTimes),
      data,
    }));

    // Filter by name if filterText is provided
    if (filterText) {
      hooks = hooks.filter(hook => hook.hookName.toLowerCase().includes(filterText.toLowerCase()));
    }

    // Filter slow hooks if showOnlySlowHooks is true
    if (showOnlySlowHooks) {
      hooks = hooks.filter(hook => hook.avgSelectorTime > 2 || hook.avgComputationTime > 5);
    }

    // Sort hooks
    switch (sortBy) {
      case 'name':
        hooks.sort((a, b) => a.hookName.localeCompare(b.hookName));
        break;
      case 'renders':
        hooks.sort((a, b) => b.renderCount - a.renderCount);
        break;
      case 'selectors':
        hooks.sort((a, b) => b.avgSelectorTime - a.avgSelectorTime);
        break;
      case 'computations':
        hooks.sort((a, b) => b.avgComputationTime - a.avgComputationTime);
        break;
    }

    return hooks;
  }, [performanceData, sortBy, filterText, showOnlySlowHooks]);

  // Get selected hook report
  const selectedHookReport = useMemo(() => {
    if (!selectedHook) return null;
    return getHookPerformanceReport(selectedHook);
  }, [selectedHook]);

  // Calculate average time from a record of arrays
  function calculateAverageTime(timeRecord: Record<string, number[]>): number {
    const allTimes = Object.values(timeRecord).flat();
    if (allTimes.length === 0) return 0;
    return allTimes.reduce((sum, time) => sum + time, 0) / allTimes.length;
  }

  // Handle sort option change
  const handleSortChange = (e: ChangeEvent<HTMLSelectElement>) => {
    setSortBy(e.target.value as SortOption);
  };

  // Clear all performance data
  const handleClearAll = () => {
    clearAllHooksPerformanceData();
    setPerformanceData({});
    setSelectedHook(null);
  };

  // Clear selected hook performance data
  const handleClearSelected = () => {
    if (selectedHook) {
      clearHookPerformanceData(selectedHook);
      setPerformanceData(prevData => {
        const newData = { ...prevData };
        delete newData[selectedHook];
        return newData;
      });
      setSelectedHook(null);
    }
  };

  // If collapsed, only show the header
  if (collapsed) {
    return (
      <div
        style={{
          position: 'fixed',
          bottom: '10px',
          right: '10px',
          backgroundColor: 'rgba(0, 0, 0, 0.8)',
          color: 'white',
          padding: '10px',
          borderRadius: '5px',
          zIndex: 9999,
          cursor: 'pointer',
        }}
        onClick={() => setCollapsed(false)}
      >
        <h3 style={{ margin: 0 }}>
          📊 Hook Performance Monitor ({Object.keys(performanceData).length} hooks)
        </h3>
      </div>
    );
  }

  return (
    <div
      style={{
        position: 'fixed',
        bottom: '10px',
        right: '10px',
        width: '600px',
        maxHeight: '80vh',
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        color: 'white',
        padding: '15px',
        borderRadius: '5px',
        zIndex: 9999,
        overflowY: 'auto',
        fontFamily: 'monospace',
      }}
    >
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '10px',
        }}
      >
        <h3 style={{ margin: 0 }}>
          📊 Hook Performance Monitor ({filteredAndSortedHooks.length} hooks)
        </h3>
        <div>
          <button
            onClick={handleClearAll}
            style={{
              marginRight: '5px',
              padding: '5px',
              backgroundColor: '#d9534f',
              border: 'none',
              borderRadius: '3px',
              color: 'white',
            }}
          >
            Clear All
          </button>
          <button
            onClick={() => setCollapsed(true)}
            style={{
              padding: '5px',
              backgroundColor: '#5bc0de',
              border: 'none',
              borderRadius: '3px',
              color: 'white',
            }}
          >
            Minimize
          </button>
        </div>
      </div>

      <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
        <input
          type="text"
          placeholder="Filter hooks..."
          value={filterText}
          onChange={e => setFilterText(e.target.value)}
          style={{ padding: '5px', flex: 1 }}
        />

        <select value={sortBy} onChange={handleSortChange} style={{ padding: '5px' }}>
          <option value="name">Sort by Name</option>
          <option value="renders">Sort by Renders</option>
          <option value="selectors">Sort by Selector Time</option>
          <option value="computations">Sort by Computation Time</option>
        </select>

        <label style={{ display: 'flex', alignItems: 'center' }}>
          <input
            type="checkbox"
            checked={showOnlySlowHooks}
            onChange={e => setShowOnlySlowHooks(e.target.checked)}
          />
          Slow Hooks Only
        </label>
      </div>

      <div
        style={{
          marginBottom: '15px',
          maxHeight: '200px',
          overflowY: 'auto',
          border: '1px solid #444',
          borderRadius: '3px',
        }}
      >
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ backgroundColor: '#333', position: 'sticky', top: 0 }}>
              <th style={{ padding: '8px', textAlign: 'left' }}>Hook</th>
              <th style={{ padding: '8px', textAlign: 'right' }}>Renders</th>
              <th style={{ padding: '8px', textAlign: 'right' }}>Selector Avg (ms)</th>
              <th style={{ padding: '8px', textAlign: 'right' }}>Compute Avg (ms)</th>
              <th style={{ padding: '8px', textAlign: 'center' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredAndSortedHooks.map(hook => (
              <tr
                key={hook.hookName}
                style={{
                  backgroundColor: selectedHook === hook.hookName ? '#1e5f74' : 'transparent',
                  cursor: 'pointer',
                  borderBottom: '1px solid #444',
                }}
                onClick={() => setSelectedHook(hook.hookName)}
              >
                <td style={{ padding: '8px' }}>{hook.hookName}</td>
                <td style={{ padding: '8px', textAlign: 'right' }}>{hook.renderCount}</td>
                <td
                  style={{
                    padding: '8px',
                    textAlign: 'right',
                    color: hook.avgSelectorTime > 2 ? '#ff6b6b' : 'inherit',
                  }}
                >
                  {hook.avgSelectorTime.toFixed(2)}
                </td>
                <td
                  style={{
                    padding: '8px',
                    textAlign: 'right',
                    color: hook.avgComputationTime > 5 ? '#ff6b6b' : 'inherit',
                  }}
                >
                  {hook.avgComputationTime.toFixed(2)}
                </td>
                <td style={{ padding: '8px', textAlign: 'center' }}>
                  <button
                    onClick={e => {
                      e.stopPropagation();
                      clearHookPerformanceData(hook.hookName);
                      setPerformanceData(prevData => {
                        const newData = { ...prevData };
                        delete newData[hook.hookName];
                        return newData;
                      });
                      if (selectedHook === hook.hookName) {
                        setSelectedHook(null);
                      }
                    }}
                    style={{
                      padding: '2px 5px',
                      backgroundColor: 'transparent',
                      border: '1px solid #999',
                      borderRadius: '3px',
                      color: 'white',
                    }}
                  >
                    Clear
                  </button>
                </td>
              </tr>
            ))}
            {filteredAndSortedHooks.length === 0 && (
              <tr>
                <td colSpan={5} style={{ padding: '20px', textAlign: 'center' }}>
                  No performance data available
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {selectedHook && (
        <div style={{ borderTop: '1px solid #444', paddingTop: '10px' }}>
          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '10px',
            }}
          >
            <h4 style={{ margin: 0 }}>Hook Details: {selectedHook}</h4>
            <button
              onClick={handleClearSelected}
              style={{
                padding: '5px',
                backgroundColor: '#f0ad4e',
                border: 'none',
                borderRadius: '3px',
                color: 'white',
              }}
            >
              Clear Selected
            </button>
          </div>

          <pre
            style={{
              maxHeight: '200px',
              overflowY: 'auto',
              backgroundColor: '#222',
              padding: '10px',
              borderRadius: '3px',
              margin: 0,
              fontSize: '12px',
            }}
          >
            {selectedHookReport}
          </pre>
        </div>
      )}

      <div
        style={{
          marginTop: '15px',
          borderTop: '1px solid #444',
          paddingTop: '10px',
          fontSize: '12px',
        }}
      >
        <p>
          <strong>Performance Tips:</strong>
          <br />
          • Slow selectors (&gt;2ms) may indicate inefficient state access
          <br />
          • Slow computations (&gt;5ms) may be causing render delays
          <br />• High render counts may indicate missing dependency arrays in useEffect/useMemo
        </p>
      </div>
    </div>
  );
};

export default HookPerformanceDashboard;

/**
 * Dynamic Budget Adjustment
 *
 * This file provides a system for automatically refining performance budgets
 * based on real-world usage data, ensuring that they remain realistic and
 * relevant as the application evolves.
 */

import { BenchmarkResult } from './PerformanceBenchmarkTools';
import { ALL_PERFORMANCE_BUDGETS, PerformanceBudget } from './PerformanceBudgets';

/**
 * Configuration for performance telemetry
 */
export interface PerformanceTelemetryConfig {
  /**
   * Whether telemetry is enabled
   */
  enabled: boolean;

  /**
   * Sampling rate (0-1, where 1 means 100% of events are sampled)
   */
  samplingRate: number;

  /**
   * Maximum number of samples to collect per category
   */
  maxSamplesPerCategory: number;

  /**
   * Whether to record device information with samples
   */
  recordDeviceInfo: boolean;

  /**
   * Endpoint for uploading telemetry data (if applicable)
   */
  uploadEndpoint?: string;

  /**
   * Whether to automatically adjust budgets based on telemetry
   */
  autoAdjustBudgets: boolean;

  /**
   * Buffer range for budget adjustment (percentage)
   *
   * e.g., 0.2 means budgets will be set 20% higher than observed p95
   */
  budgetBuffer: number;
}

/**
 * Performance sample with metadata
 */
export interface PerformanceSample {
  /**
   * Name of the operation
   */
  name: string;

  /**
   * Timestamp when the sample was collected
   */
  timestamp: Date;

  /**
   * Execution time in milliseconds
   */
  executionTimeMs: number;

  /**
   * Memory usage in MB (if available)
   */
  memoryUsageMB?: number;

  /**
   * Operations per second (if applicable)
   */
  operationsPerSecond?: number;

  /**
   * User agent string (for client samples)
   */
  userAgent?: string;

  /**
   * Device type (desktop, mobile, tablet)
   */
  deviceType?: 'desktop' | 'mobile' | 'tablet';

  /**
   * Screen dimensions (for client samples)
   */
  screenDimensions?: { width: number; height: number };

  /**
   * Application version
   */
  appVersion?: string;

  /**
   * Environment (development, staging, production)
   */
  environment?: 'development' | 'staging' | 'production';

  /**
   * Custom metadata
   */
  metadata?: Record<string, unknown>;
}

/**
 * Statistical analysis of performance samples
 */
export interface PerformanceStatistics {
  /**
   * Name of the operation
   */
  name: string;

  /**
   * Number of samples
   */
  sampleCount: number;

  /**
   * Mean execution time
   */
  meanExecutionTimeMs: number;

  /**
   * Median execution time
   */
  medianExecutionTimeMs: number;

  /**
   * 95th percentile execution time
   */
  p95ExecutionTimeMs: number;

  /**
   * 99th percentile execution time
   */
  p99ExecutionTimeMs: number;

  /**
   * Standard deviation of execution time
   */
  stdDevExecutionTimeMs: number;

  /**
   * Minimum execution time
   */
  minExecutionTimeMs: number;

  /**
   * Maximum execution time
   */
  maxExecutionTimeMs: number;

  /**
   * Mean memory usage (if available)
   */
  meanMemoryUsageMB?: number;

  /**
   * 95th percentile memory usage (if available)
   */
  p95MemoryUsageMB?: number;

  /**
   * Mean operations per second (if applicable)
   */
  meanOperationsPerSecond?: number;

  /**
   * 5th percentile operations per second (if applicable)
   */
  p5OperationsPerSecond?: number;

  /**
   * Timestamp of the oldest sample
   */
  oldestSample: Date;

  /**
   * Timestamp of the newest sample
   */
  newestSample: Date;

  /**
   * Device type breakdown (percentage of samples from each device type)
   */
  deviceTypeBreakdown?: Record<string, number>;
}

/**
 * Budget adjustment recommendation
 */
export interface BudgetAdjustmentRecommendation {
  /**
   * Original budget
   */
  originalBudget: PerformanceBudget;

  /**
   * Recommended budget
   */
  recommendedBudget: PerformanceBudget;

  /**
   * Reason for the recommendation
   */
  reason: string;

  /**
   * Performance statistics this recommendation is based on
   */
  statistics: PerformanceStatistics;

  /**
   * Confidence level (0-1)
   */
  confidence: number;
}

/**
 * Collection of performance samples for analysis
 */
class PerformanceTelemetryCollector {
  private samples: PerformanceSample[] = [];
  private config: PerformanceTelemetryConfig;

  constructor(config: PerformanceTelemetryConfig) {
    this.config = config;
  }

  /**
   * Record a performance sample
   *
   * @param sample Performance sample to record
   * @returns Whether the sample was recorded
   */
  public recordSample(sample: PerformanceSample): boolean {
    if (!this.config.enabled) return false;

    // Apply sampling rate
    if (Math.random() > this.config.samplingRate) return false;

    // Check if we've reached maximum samples for this category
    const existingSamples = this.samples.filter(s => s.name === sample.name);
    if (existingSamples.length >= this.config.maxSamplesPerCategory) {
      // Replace oldest sample
      const oldestIndex = this.samples.indexOf(
        existingSamples.reduce((oldest, current) =>
          oldest.timestamp < current.timestamp ? oldest : current
        )
      );
      if (oldestIndex >= 0) {
        this.samples[oldestIndex] = sample;
        return true;
      }
      return false;
    }

    // Add the sample
    this.samples.push(sample);

    // Trigger upload if endpoint is configured
    if (this.config.uploadEndpoint && this.samples.length % 10 === 0) {
      this.uploadSamples().catch(err =>
        console.error('Failed to upload performance samples:', err)
      );
    }

    return true;
  }

  /**
   * Record a benchmark result as a sample
   *
   * @param result Benchmark result to record
   * @returns Whether the sample was recorded
   */
  public recordBenchmarkResult(result: BenchmarkResult): boolean {
    return this.recordSample({
      name: result.name,
      timestamp: result.timestamp,
      executionTimeMs: result.executionTimeMs,
      memoryUsageMB: result.memoryUsageMB,
      operationsPerSecond: result.operationsPerSecond,
      environment: 'development', // Benchmark results are typically from development
      metadata: { additionalMetrics: result.additionalMetrics },
    });
  }

  /**
   * Get all samples for a specific operation
   *
   * @param name Name of the operation
   * @returns Array of samples for the operation
   */
  public getSamplesForOperation(name: string): PerformanceSample[] {
    return this.samples.filter(sample => sample.name === name);
  }

  /**
   * Get all samples
   *
   * @returns All collected samples
   */
  public getAllSamples(): PerformanceSample[] {
    return [...this.samples];
  }

  /**
   * Clear all samples
   */
  public clearSamples(): void {
    this.samples = [];
  }

  /**
   * Upload samples to the configured endpoint
   */
  private async uploadSamples(): Promise<void> {
    if (!this.config.uploadEndpoint) return;

    const batchSize = 100;
    const batches = [];

    // Split samples into batches
    for (let i = 0; i < this.samples.length; i += batchSize) {
      batches.push(this.samples.slice(i, i + batchSize));
    }

    // Upload each batch
    for (const batch of batches) {
      try {
        await fetch(this.config.uploadEndpoint, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(batch),
        });
      } catch (error) {
        console.error('Failed to upload performance samples:', error);
      }
    }
  }

  /**
   * Calculate statistics for all operations
   *
   * @returns Map of operation names to statistics
   */
  public calculateStatistics(): Map<string, PerformanceStatistics> {
    const operationNames = new Set(this.samples.map(sample => sample.name));
    const result = new Map<string, PerformanceStatistics>();

    for (const name of operationNames) {
      const samples = this.getSamplesForOperation(name);
      if (samples.length === 0) continue;

      result.set(name, this.calculateStatisticsForSamples(name, samples));
    }

    return result;
  }

  /**
   * Calculate statistics for a set of samples
   *
   * @param name Operation name
   * @param samples Samples to analyze
   * @returns Statistical analysis
   */
  private calculateStatisticsForSamples(
    name: string,
    samples: PerformanceSample[]
  ): PerformanceStatistics {
    // Extract execution times and sort them
    const executionTimes = samples.map(sample => sample.executionTimeMs).sort((a, b) => a - b);

    // Calculate percentiles
    const p95Index = Math.floor(executionTimes.length * 0.95);
    const p99Index = Math.floor(executionTimes.length * 0.99);
    const medianIndex = Math.floor(executionTimes.length * 0.5);

    // Calculate mean
    const meanExecutionTimeMs =
      executionTimes.reduce((sum, time) => sum + time, 0) / executionTimes.length;

    // Calculate standard deviation
    const squaredDifferences = executionTimes.map(time => Math.pow(time - meanExecutionTimeMs, 2));
    const variance =
      squaredDifferences.reduce((sum, diff) => sum + diff, 0) / executionTimes.length;
    const stdDevExecutionTimeMs = Math.sqrt(variance);

    // Find timestamps
    const timestamps = samples.map(sample => sample.timestamp);
    const oldestSample = new Date(Math.min(...timestamps.map(t => t.getTime())));
    const newestSample = new Date(Math.max(...timestamps.map(t => t.getTime())));

    // Calculate memory usage statistics if available
    let meanMemoryUsageMB: number | undefined;
    let p95MemoryUsageMB: number | undefined;

    const memorySamples = samples
      .filter(sample => sample.memoryUsageMB !== undefined)
      .map(sample => sample.memoryUsageMB as number)
      .sort((a, b) => a - b);

    if (memorySamples.length > 0) {
      meanMemoryUsageMB = memorySamples.reduce((sum, mem) => sum + mem, 0) / memorySamples.length;
      p95MemoryUsageMB = memorySamples[Math.floor(memorySamples.length * 0.95)];
    }

    // Calculate operations per second statistics if applicable
    let meanOperationsPerSecond: number | undefined;
    let p5OperationsPerSecond: number | undefined;

    const opsSamples = samples
      .filter(sample => sample.operationsPerSecond !== undefined)
      .map(sample => sample.operationsPerSecond as number)
      .sort((a, b) => a - b);

    if (opsSamples.length > 0) {
      meanOperationsPerSecond = opsSamples.reduce((sum, ops) => sum + ops, 0) / opsSamples.length;
      p5OperationsPerSecond = opsSamples[Math.floor(opsSamples.length * 0.05)];
    }

    // Calculate device type breakdown if available
    let deviceTypeBreakdown: Record<string, number> | undefined;

    const deviceTypes = samples
      .filter(sample => sample.deviceType !== undefined)
      .map(sample => sample.deviceType as string);

    if (deviceTypes.length > 0) {
      deviceTypeBreakdown = {};
      for (const deviceType of deviceTypes) {
        deviceTypeBreakdown[deviceType] = (deviceTypeBreakdown[deviceType] || 0) + 1;
      }

      // Convert to percentages
      for (const deviceType in deviceTypeBreakdown) {
        deviceTypeBreakdown[deviceType] =
          (deviceTypeBreakdown[deviceType] / deviceTypes.length) * 100;
      }
    }

    return {
      name,
      sampleCount: samples.length,
      meanExecutionTimeMs,
      medianExecutionTimeMs: executionTimes[medianIndex],
      p95ExecutionTimeMs: executionTimes[p95Index],
      p99ExecutionTimeMs: executionTimes[p99Index],
      stdDevExecutionTimeMs,
      minExecutionTimeMs: executionTimes[0],
      maxExecutionTimeMs: executionTimes[executionTimes.length - 1],
      meanMemoryUsageMB,
      p95MemoryUsageMB,
      meanOperationsPerSecond,
      p5OperationsPerSecond,
      oldestSample,
      newestSample,
      deviceTypeBreakdown,
    };
  }
}

/**
 * Dynamic budget adjuster that analyzes performance data and recommends budget adjustments
 */
export class DynamicBudgetAdjuster {
  private telemetryCollector: PerformanceTelemetryCollector;
  private budgets: PerformanceBudget[];
  private config: PerformanceTelemetryConfig;

  constructor(
    config: PerformanceTelemetryConfig = {
      enabled: true,
      samplingRate: 0.1,
      maxSamplesPerCategory: 1000,
      recordDeviceInfo: true,
      autoAdjustBudgets: false,
      budgetBuffer: 0.2, // 20% buffer
    },
    initialBudgets: PerformanceBudget[] = ALL_PERFORMANCE_BUDGETS
  ) {
    this.config = config;
    this.telemetryCollector = new PerformanceTelemetryCollector(config);
    this.budgets = [...initialBudgets];
  }

  /**
   * Record a performance sample
   *
   * @param sample Performance sample to record
   * @returns Whether the sample was recorded
   */
  public recordSample(sample: PerformanceSample): boolean {
    const recorded = this.telemetryCollector.recordSample(sample);

    // If auto-adjustment is enabled, check if we need to adjust budgets
    if (recorded && this.config.autoAdjustBudgets) {
      this.checkForBudgetAdjustments();
    }

    return recorded;
  }

  /**
   * Record multiple performance samples
   *
   * @param samples Array of performance samples to record
   * @returns Number of samples successfully recorded
   */
  public recordSamples(samples: PerformanceSample[]): number {
    let recordedCount = 0;

    for (const sample of samples) {
      if (this.recordSample(sample)) {
        recordedCount++;
      }
    }

    return recordedCount;
  }

  /**
   * Record benchmark results
   *
   * @param results Benchmark results to record
   * @returns Number of results successfully recorded
   */
  public recordBenchmarkResults(results: BenchmarkResult[]): number {
    let recordedCount = 0;

    for (const result of results) {
      if (this.telemetryCollector.recordBenchmarkResult(result)) {
        recordedCount++;
      }
    }

    // If auto-adjustment is enabled, check if we need to adjust budgets
    if (recordedCount > 0 && this.config.autoAdjustBudgets) {
      this.checkForBudgetAdjustments();
    }

    return recordedCount;
  }

  /**
   * Check if budgets need to be adjusted based on current telemetry
   *
   * @returns Array of budget adjustment recommendations
   */
  public checkForBudgetAdjustments(): BudgetAdjustmentRecommendation[] {
    const statistics = this.telemetryCollector.calculateStatistics();
    const recommendations: BudgetAdjustmentRecommendation[] = [];

    for (const budget of this.budgets) {
      const stats = statistics.get(budget.name);
      if (!stats || stats.sampleCount < 30) continue; // Need sufficient samples

      let shouldAdjust = false;
      const newBudget = { ...budget };
      let reason = '';
      let confidence = 0;

      // Check execution time
      const buffer = 1 + this.config.budgetBuffer;
      const p95WithBuffer = stats.p95ExecutionTimeMs * buffer;

      if (p95WithBuffer > budget.maxExecutionTimeMs * 1.5) {
        // Current budget is unrealistically low (more than 50% lower than p95)
        newBudget.maxExecutionTimeMs = Math.ceil(p95WithBuffer);
        reason = `Budget was too low (${budget.maxExecutionTimeMs}ms vs ${stats.p95ExecutionTimeMs.toFixed(2)}ms p95)`;
        shouldAdjust = true;
        confidence = 0.9;
      } else if (p95WithBuffer < budget.maxExecutionTimeMs * 0.5) {
        // Current budget is unrealistically high (more than double the p95)
        newBudget.maxExecutionTimeMs = Math.ceil(p95WithBuffer);
        reason = `Budget was too high (${budget.maxExecutionTimeMs}ms vs ${stats.p95ExecutionTimeMs.toFixed(2)}ms p95)`;
        shouldAdjust = true;
        confidence = 0.8;
      }

      // Check memory usage
      if (stats.p95MemoryUsageMB !== undefined && budget.maxMemoryUsageMB !== undefined) {
        const p95MemoryWithBuffer = stats.p95MemoryUsageMB * buffer;

        if (p95MemoryWithBuffer > budget.maxMemoryUsageMB * 1.5) {
          newBudget.maxMemoryUsageMB = Math.ceil(p95MemoryWithBuffer);
          reason += reason ? ', ' : '';
          reason += `Memory budget was too low (${budget.maxMemoryUsageMB}MB vs ${stats.p95MemoryUsageMB.toFixed(2)}MB p95)`;
          shouldAdjust = true;
          confidence = Math.max(confidence, 0.85);
        } else if (p95MemoryWithBuffer < budget.maxMemoryUsageMB * 0.5) {
          newBudget.maxMemoryUsageMB = Math.ceil(p95MemoryWithBuffer);
          reason += reason ? ', ' : '';
          reason += `Memory budget was too high (${budget.maxMemoryUsageMB}MB vs ${stats.p95MemoryUsageMB.toFixed(2)}MB p95)`;
          shouldAdjust = true;
          confidence = Math.max(confidence, 0.75);
        }
      }

      // Check operations per second
      if (
        stats.p5OperationsPerSecond !== undefined &&
        budget.minOperationsPerSecond !== undefined
      ) {
        // For operations per second, use p5 (worst 5%) with a buffer to ensure realistic expectations
        const p5OpsWithBuffer = stats.p5OperationsPerSecond / buffer;

        if (p5OpsWithBuffer < budget.minOperationsPerSecond * 0.75) {
          newBudget.minOperationsPerSecond = Math.floor(p5OpsWithBuffer);
          reason += reason ? ', ' : '';
          reason += `Operations budget was too high (${budget.minOperationsPerSecond} ops/s vs ${stats.p5OperationsPerSecond.toFixed(2)} ops/s p5)`;
          shouldAdjust = true;
          confidence = Math.max(confidence, 0.8);
        } else if (p5OpsWithBuffer > budget.minOperationsPerSecond * 1.5) {
          newBudget.minOperationsPerSecond = Math.floor(p5OpsWithBuffer);
          reason += reason ? ', ' : '';
          reason += `Operations budget was too low (${budget.minOperationsPerSecond} ops/s vs ${stats.p5OperationsPerSecond.toFixed(2)} ops/s p5)`;
          shouldAdjust = true;
          confidence = Math.max(confidence, 0.7);
        }
      }

      if (shouldAdjust) {
        recommendations.push({
          originalBudget: budget,
          recommendedBudget: newBudget,
          reason,
          statistics: stats,
          confidence,
        });

        // Apply the adjustment if auto-adjust is enabled
        if (this.config.autoAdjustBudgets) {
          this.adjustBudget(budget.name, newBudget);
        }
      }
    }

    return recommendations;
  }

  /**
   * Adjust a budget based on a recommendation
   *
   * @param operationName Name of the operation to adjust budget for
   * @param newBudget New budget values
   * @returns Whether the adjustment was successful
   */
  public adjustBudget(operationName: string, newBudget: PerformanceBudget): boolean {
    const index = this.budgets.findIndex(b => b.name === operationName);
    if (index === -1) return false;

    this.budgets[index] = { ...this.budgets[index], ...newBudget };

    // Log the adjustment
    console.info(`Adjusted performance budget for "${operationName}":`, {
      before: this.budgets[index],
      after: newBudget,
    });

    return true;
  }

  /**
   * Get the current adjusted budgets
   *
   * @returns Array of current budgets
   */
  public getCurrentBudgets(): PerformanceBudget[] {
    return [...this.budgets];
  }

  /**
   * Get telemetry statistics for all operations
   *
   * @returns Map of operation names to statistics
   */
  public getTelemetryStatistics(): Map<string, PerformanceStatistics> {
    return this.telemetryCollector.calculateStatistics();
  }

  /**
   * Reset the telemetry collector
   */
  public resetTelemetry(): void {
    this.telemetryCollector.clearSamples();
  }

  /**
   * Generate a full report with budget recommendations and statistics
   *
   * @returns Object containing budgets, statistics, and recommendations
   */
  public generateReport(): {
    budgets: PerformanceBudget[];
    statistics: Map<string, PerformanceStatistics>;
    recommendations: BudgetAdjustmentRecommendation[];
  } {
    return {
      budgets: this.getCurrentBudgets(),
      statistics: this.getTelemetryStatistics(),
      recommendations: this.checkForBudgetAdjustments(),
    };
  }
}

/**
 * Performance Benchmarking Tools
 *
 * This file provides a comprehensive set of tools for benchmarking and analyzing
 * performance across different systems in the application, with a particular focus on:
 *
 * - ResourceFlowManager optimization
 * - Event System processing
 * - React component rendering
 * - Memory usage analysis
 *
 * Use these tools to establish performance baselines, detect regressions,
 * and validate performance optimizations.
 */

import * as d3 from 'd3';
import { performance } from 'perf_hooks';
import { ResourceFlowManager } from '../../../managers/resource/ResourceFlowManager';
import { FlowNodeType } from '../../../types/resources/FlowTypes';
import { ResourceType } from '../../../types/resources/ResourceTypes';

/**
 * Performance benchmark result
 */
export interface BenchmarkResult {
  name: string;
  description?: string;
  executionTimeMs: number;
  memoryUsageMB?: number;
  operationsCount?: number;
  operationsPerSecond?: number;
  additionalMetrics?: Record<string, number>;
  timestamp: Date;
}

/**
 * System-specific benchmark results
 */
export interface ResourceFlowBenchmarkResult extends BenchmarkResult {
  nodeCount: number;
  connectionCount: number;
  nodesProcessed: number;
  connectionsProcessed: number;
  transfersGenerated: number;
  optimizationCycles: number;
}

export interface EventSystemBenchmarkResult extends BenchmarkResult {
  eventCount: number;
  eventsPerSecond: number;
  listenersTriggered: number;
  averageEventProcessingTimeMs: number;
  batchCount?: number;
  batchSize?: number;
}

export interface RenderingBenchmarkResult extends BenchmarkResult {
  componentCount: number;
  renderCount: number;
  fps: number;
  frameTimeMs: number;
  maxFrameTimeMs: number;
  minFrameTimeMs: number;
  jankFrames: number; // Frames exceeding 16ms
}

/**
 * Options for benchmark execution
 */
export interface BenchmarkOptions {
  iterations?: number;
  warmupIterations?: number;
  setupFn?: () => void;
  teardownFn?: () => void;
  memoryMeasurement?: boolean;
  timeout?: number;
}

/**
 * Benchmark test case
 */
export interface BenchmarkTestCase<T extends BenchmarkResult = BenchmarkResult> {
  name: string;
  description?: string;
  run: () => Promise<T> | T;
  options?: BenchmarkOptions;
}

/**
 * Record of memory usage before and after a benchmark
 */
interface MemoryMeasurement {
  before: NodeJS.MemoryUsage;
  after: NodeJS.MemoryUsage;
  diffHeapUsed: number;
  diffHeapTotal: number;
  diffExternal: number;
  diffRss: number;
}

/**
 * Measures the memory usage of a function
 * @param fn Function to measure
 * @returns Memory measurement
 */
export function measureMemoryUsage(fn: () => void): MemoryMeasurement {
  // Force garbage collection if available
  if (global.gc) {
    global.gc();
  }

  // Measure memory before
  const before = process.memoryUsage();

  // Run the function
  fn();

  // Measure memory after
  const after = process.memoryUsage();

  // Calculate difference
  return {
    before,
    after,
    diffHeapUsed: (after.heapUsed - before.heapUsed) / (1024 * 1024), // MB
    diffHeapTotal: (after.heapTotal - before.heapTotal) / (1024 * 1024), // MB
    diffExternal: (after.external - before.external) / (1024 * 1024), // MB
    diffRss: (after.rss - before.rss) / (1024 * 1024), // MB
  };
}

/**
 * Runs a benchmark for a synchronous function
 * @param fn Function to benchmark
 * @param options Benchmark options
 * @returns Benchmark results
 */
export function runBenchmark<T>(fn: () => T, options: BenchmarkOptions = {}): BenchmarkResult {
  const {
    iterations = 100,
    warmupIterations = 10,
    setupFn,
    teardownFn,
    memoryMeasurement = false,
  } = options;

  // Run warmup iterations
  for (let i = 0; i < warmupIterations; i++) {
    if (setupFn) setupFn();
    fn();
    if (teardownFn) teardownFn();
  }

  // Prepare for actual benchmark
  const executionTimes: number[] = [];
  let memoryUsage: MemoryMeasurement | null = null;

  // Execute the benchmark
  for (let i = 0; i < iterations; i++) {
    if (setupFn) setupFn();

    if (memoryMeasurement && i === Math.floor(iterations / 2)) {
      // Measure memory usage halfway through the iterations
      memoryUsage = measureMemoryUsage(fn);
    } else {
      const startTime = performance.now();
      fn();
      const endTime = performance.now();
      executionTimes.push(endTime - startTime);
    }

    if (teardownFn) teardownFn();
  }

  // Calculate statistics
  const totalTime = executionTimes.reduce((sum, time) => sum + time, 0);
  const averageTime = totalTime / executionTimes.length;
  const sortedTimes = [...executionTimes].sort((a, b) => a - b);
  const medianTime =
    sortedTimes.length % 2 === 0
      ? (sortedTimes[sortedTimes.length / 2 - 1] + sortedTimes[sortedTimes.length / 2]) / 2
      : sortedTimes[Math.floor(sortedTimes.length / 2)];

  return {
    name: 'Benchmark',
    executionTimeMs: averageTime,
    memoryUsageMB: memoryUsage?.diffHeapUsed,
    operationsCount: iterations,
    operationsPerSecond: (1000 * iterations) / totalTime,
    additionalMetrics: {
      medianTimeMs: medianTime,
      minTimeMs: Math.min(...executionTimes),
      maxTimeMs: Math.max(...executionTimes),
      stdDevMs: calculateStandardDeviation(executionTimes),
    },
    timestamp: new Date(),
  };
}

/**
 * Runs an async benchmark
 * @param fn Async function to benchmark
 * @param options Benchmark options
 * @returns Promise resolving to benchmark results
 */
export async function runAsyncBenchmark<T>(
  fn: () => Promise<T>,
  options: BenchmarkOptions = {}
): Promise<BenchmarkResult> {
  const {
    iterations = 100,
    warmupIterations = 10,
    setupFn,
    teardownFn,
    memoryMeasurement = false,
    timeout = 30000,
  } = options;

  // Create a timeout promise
  const timeoutPromise = new Promise<never>((_, reject) => {
    setTimeout(() => {
      reject(new Error(`Benchmark timed out after ${timeout}ms`));
    }, timeout);
  });

  // Run warmup iterations
  for (let i = 0; i < warmupIterations; i++) {
    if (setupFn) setupFn();
    await fn();
    if (teardownFn) teardownFn();
  }

  // Prepare for actual benchmark
  const executionTimes: number[] = [];
  let memoryUsage: MemoryMeasurement | null = null;

  // Execute the benchmark
  for (let i = 0; i < iterations; i++) {
    if (setupFn) setupFn();

    if (memoryMeasurement && i === Math.floor(iterations / 2)) {
      // Measure memory usage halfway through the iterations
      const memoryFn = async () => {
        await Promise.race([fn(), timeoutPromise]);
      };
      memoryUsage = measureMemoryUsage(() => {
        // This is a sync function that wraps the async function
        // We can't measure memory usage of an async function directly
        // But we can trigger it to run and measure the initial setup
        memoryFn();
      });
    } else {
      const startTime = performance.now();
      await Promise.race([fn(), timeoutPromise]);
      const endTime = performance.now();
      executionTimes.push(endTime - startTime);
    }

    if (teardownFn) teardownFn();
  }

  // Calculate statistics
  const totalTime = executionTimes.reduce((sum, time) => sum + time, 0);
  const averageTime = totalTime / executionTimes.length;
  const sortedTimes = [...executionTimes].sort((a, b) => a - b);
  const medianTime =
    sortedTimes.length % 2 === 0
      ? (sortedTimes[sortedTimes.length / 2 - 1] + sortedTimes[sortedTimes.length / 2]) / 2
      : sortedTimes[Math.floor(sortedTimes.length / 2)];

  return {
    name: 'Async Benchmark',
    executionTimeMs: averageTime,
    memoryUsageMB: memoryUsage?.diffHeapUsed,
    operationsCount: iterations,
    operationsPerSecond: (1000 * iterations) / totalTime,
    additionalMetrics: {
      medianTimeMs: medianTime,
      minTimeMs: Math.min(...executionTimes),
      maxTimeMs: Math.max(...executionTimes),
      stdDevMs: calculateStandardDeviation(executionTimes),
    },
    timestamp: new Date(),
  };
}

/**
 * Calculate standard deviation of an array of numbers
 */
function calculateStandardDeviation(values: number[]): number {
  const average = values.reduce((sum, value) => sum + value, 0) / values.length;
  const squareDiffs = values.map(value => Math.pow(value - average, 2));
  const avgSquareDiff =
    squareDiffs.reduce((sum, squareDiff) => sum + squareDiff, 0) / squareDiffs.length;
  return Math.sqrt(avgSquareDiff);
}

/**
 * Creates a test ResourceFlowManager with specified number of nodes and connections
 */
export function createTestResourceNetwork(
  nodeCount: number,
  connectionCount: number,
  resourceTypes: ResourceType[] = [
    ResourceType.IRON,
    ResourceType.COPPER,
    ResourceType.OXYGEN,
    ResourceType.SILICON,
  ]
): ResourceFlowManager {
  // Create a resource flow manager
  const manager = new ResourceFlowManager(100, 1000, 50);

  // Generate node IDs
  const nodeIds = Array.from({ length: nodeCount }, (_, i) => `node-${i}`);

  // Create nodes with different types
  const producers = Math.floor(nodeCount * 0.3);
  const consumers = Math.floor(nodeCount * 0.3);
  const storage = Math.floor(nodeCount * 0.2);
  const converters = nodeCount - producers - consumers - storage;

  // Add nodes to manager
  for (let i = 0; i < nodeCount; i++) {
    const nodeId = nodeIds[i];
    let nodeType: FlowNodeType;

    if (i < producers) {
      nodeType = 'producer';
    } else if (i < producers + consumers) {
      nodeType = 'consumer';
    } else if (i < producers + consumers + storage) {
      nodeType = 'storage';
    } else {
      nodeType = 'converter';
    }

    // Determine resource type
    const resourceType = resourceTypes[i % resourceTypes.length];

    // Create and register node
    manager.registerNode({
      id: nodeId,
      type: nodeType,
      active: true,
      resources: [resourceType],
      capacity: 1000,
      rate: 10,
      priority: Math.floor(Math.random() * 10),
      efficiency: 0.8 + Math.random() * 0.2,
    });
  }

  // Create connections
  for (let i = 0; i < connectionCount; i++) {
    const sourceIndex = Math.floor(Math.random() * nodeCount);
    let targetIndex = Math.floor(Math.random() * nodeCount);

    // Avoid self-connections
    while (targetIndex === sourceIndex) {
      targetIndex = Math.floor(Math.random() * nodeCount);
    }

    const sourceId = nodeIds[sourceIndex];
    const targetId = nodeIds[targetIndex];
    const resourceType = resourceTypes[i % resourceTypes.length];

    manager.createConnection({
      id: `connection-${i}`,
      sourceId,
      targetId,
      resourceType,
      flowRate: 5 + Math.random() * 10,
      active: true,
      maxFlowRate: 20,
    });
  }

  return manager;
}

/**
 * Run ResourceFlowManager benchmark
 */
export function runResourceFlowBenchmark(
  benchmarkName: string,
  nodeCount: number,
  connectionCount: number,
  options: {
    batchSize?: number;
    cacheTTL?: number;
    iterations?: number;
    optimizationInterval?: number;
  } = {}
): Promise<ResourceFlowBenchmarkResult> {
  const { batchSize = 50, cacheTTL = 1000, iterations = 10, optimizationInterval = 100 } = options;

  return new Promise(resolve => {
    // Create benchmark function
    const benchmarkFn = () => {
      // Create manager with specified options
      const manager = new ResourceFlowManager(optimizationInterval, cacheTTL, batchSize);

      // Add test network
      createTestResourceNetwork(nodeCount, connectionCount);

      // Run optimization
      const result = manager.optimizeFlows(true);

      // Clean up
      manager.cleanup();

      return {
        nodeCount,
        connectionCount,
        nodesProcessed: result.performanceMetrics?.nodesProcessed || 0,
        connectionsProcessed: result.performanceMetrics?.connectionsProcessed || 0,
        transfersGenerated: result.performanceMetrics?.transfersGenerated || 0,
        optimizationCycles: 1,
        executionTimeMs: result.performanceMetrics?.executionTimeMs || 0,
      };
    };

    // Run the benchmark
    const basicResult = runBenchmark(benchmarkFn, {
      iterations,
      warmupIterations: 2,
      memoryMeasurement: true,
    });

    // Create the complete result
    const result: ResourceFlowBenchmarkResult = {
      ...basicResult,
      name: benchmarkName,
      nodeCount,
      connectionCount,
      nodesProcessed: 0,
      connectionsProcessed: 0,
      transfersGenerated: 0,
      optimizationCycles: iterations,
    };

    resolve(result);
  });
}

/**
 * Creates a visual performance report from benchmark results
 * @param results Benchmark results
 * @param targetElement DOM element to render the visualization
 */
export function createPerformanceVisualization(
  results: BenchmarkResult[],
  targetElement: HTMLElement
): void {
  // Set up SVG dimensions
  const margin = { top: 30, right: 30, bottom: 70, left: 60 };
  const width = 800 - margin.left - margin.right;
  const height = 400 - margin.top - margin.bottom;

  // Create SVG
  const svg = d3
    .select(targetElement)
    .append('svg')
    .attr('width', width + margin.left + margin.right)
    .attr('height', height + margin.top + margin.bottom)
    .append('g')
    .attr('transform', `translate(${margin.left},${margin.top})`);

  // Create scales
  const x = d3
    .scaleBand()
    .domain(results.map(d => d.name))
    .range([0, width])
    .padding(0.2);

  const y = d3
    .scaleLinear()
    .domain([0, d3.max(results, d => d.executionTimeMs) || 0])
    .nice()
    .range([height, 0]);

  // Add X axis
  svg
    .append('g')
    .attr('transform', `translate(0,${height})`)
    .call(d3.axisBottom(x))
    .selectAll('text')
    .attr('transform', 'translate(-10,0)rotate(-45)')
    .style('text-anchor', 'end');

  // Add Y axis
  svg.append('g').call(d3.axisLeft(y));

  // Add bars
  svg
    .selectAll('rect')
    .data(results)
    .enter()
    .append('rect')
    .attr('x', d => x(d.name) || 0)
    .attr('y', d => y(d.executionTimeMs))
    .attr('width', x.bandwidth())
    .attr('height', d => height - y(d.executionTimeMs))
    .attr('fill', '#4dabf7');

  // Add title
  svg
    .append('text')
    .attr('x', width / 2)
    .attr('y', -10)
    .attr('text-anchor', 'middle')
    .style('font-size', '16px')
    .text('Performance Benchmark Results');

  // Add labels
  svg
    .selectAll('.label')
    .data(results)
    .enter()
    .append('text')
    .attr('class', 'label')
    .attr('x', d => (x(d.name) || 0) + x.bandwidth() / 2)
    .attr('y', d => y(d.executionTimeMs) - 5)
    .attr('text-anchor', 'middle')
    .text(d => `${d.executionTimeMs.toFixed(2)}ms`);
}

