/**
 * Loads benchmark results from localStorage
 * @param key Storage key
 * @returns Benchmark results
 */
export function loadBenchmarkResults(key: string): BenchmarkResult[] {
  if (typeof localStorage !== 'undefined') {
    const stored = localStorage.getItem(key);
    if (stored) {
      try {
        return JSON.parse(stored);
      } catch (e) {
        console.error('Failed to parse stored benchmark results', e);
      }
    }
  }
  return [];
}

/**
 * Schedulable benchmark job
 */
export interface BenchmarkJob {
  id: string;
  name: string;
  description?: string;
  schedule: 'daily' | 'weekly' | 'onDemand';
  lastRun?: Date;
  benchmarks: BenchmarkTestCase[];
  notifyOnRegression?: boolean;
  regressionThreshold?: number;
  baselineKey?: string;
}

/**
 * Performance Benchmark Manager
 * Manages scheduling and execution of benchmark jobs
 */
export class PerformanceBenchmarkManager {
  private jobs: Map<string, BenchmarkJob> = new Map();
  private results: Map<string, BenchmarkResult[]> = new Map();

  /**
   * Registers a benchmark job
   * @param job Benchmark job
   */
  registerJob(job: BenchmarkJob): void {
    this.jobs.set(job.id, job);
  }

  /**
   * Unregisters a benchmark job
   * @param jobId Job ID
   */
  unregisterJob(jobId: string): void {
    this.jobs.delete(jobId);
  }

  /**
   * Gets all registered jobs
   */
  getJobs(): BenchmarkJob[] {
    return Array.from(this.jobs.values());
  }

  /**
   * Runs a benchmark job
   * @param jobId Job ID
   * @returns Promise resolving to benchmark results
   */
  async runJob(jobId: string): Promise<BenchmarkResult[]> {
    const job = this.jobs.get(jobId);
    if (!job) {
      throw new Error(`Job with ID ${jobId} not found`);
    }

    const results: BenchmarkResult[] = [];

    // Run each benchmark in the job
    for (const benchmark of job.benchmarks) {
      try {
        const result = await Promise.resolve(benchmark.run());
        results.push({
          ...result,
          name: benchmark.name,
          description: benchmark.description,
          timestamp: new Date(),
        });
      } catch (error) {
        console.error(`Error running benchmark ${benchmark.name}:`, error);
      }
    }

    // Save results
    this.results.set(jobId, results);
    job.lastRun = new Date();

    // Check for regressions if needed
    if (job.notifyOnRegression && job.baselineKey) {
      const baselineResults = this.results.get(job.baselineKey) || [];
      const regressionAnalysis = detectPerformanceRegressions(
        results,
        baselineResults,
        job.regressionThreshold
      );

      if (regressionAnalysis.regressions.length > 0) {
        this.notifyRegressions(jobId, regressionAnalysis);
      }
    }

    return results;
  }

  /**
   * Gets results for a job
   * @param jobId Job ID
   */
  getJobResults(jobId: string): BenchmarkResult[] {
    return this.results.get(jobId) || [];
  }

  /**
   * Notifies about performance regressions
   * @param jobId Job ID
   * @param analysis Regression analysis
   */
  private notifyRegressions(
    jobId: string,
    analysis: ReturnType<typeof detectPerformanceRegressions>
  ): void {
    console.warn(`Performance regression detected in job ${jobId}:`);
    console.table(analysis.regressions);
    // In a real application, this could send an email, create a Slack notification, etc.
  }
}

// Export a singleton instance of the benchmark manager
export const benchmarkManager = new PerformanceBenchmarkManager();

/**
 * @file hookPerformanceMonitor.ts
 * Provides performance monitoring utilities for hooks.
 *
 * This file implements:
 * 1. Execution time measurement for hooks
 * 2. Performance reporting for hooks
 * 3. Threshold-based warnings for slow operations
 */

/**
 * Configuration for hook performance monitoring
 */
export interface HookPerformanceConfig {
  /**
   * Whether performance monitoring is enabled
   */
  enabled: boolean;

  /**
   * Threshold in milliseconds for when to warn about slow selector operations
   */
  selectorThreshold?: number;

  /**
   * Threshold in milliseconds for when to warn about slow data computation
   */
  computationThreshold?: number;

  /**
   * Whether to log all measurements, not just those exceeding thresholds
   */
  verbose?: boolean;

  /**
   * Name of the hook (for identification in logs)
   */
  hookName: string;
}

/**
 * Default performance monitoring configuration
 */
export const defaultPerformanceConfig: Omit<HookPerformanceConfig, 'hookName'> = {
  enabled: process.env.NODE_ENV === 'development',
  selectorThreshold: 2, // 2ms
  computationThreshold: 5, // 5ms
  verbose: false,
};

/**
 * Performance data collected for a hook
 */
export interface HookPerformanceData {
  /**
   * Name of the hook
   */
  hookName: string;

  /**
   * Execution times for selectors
   */
  selectorTimes: Record<string, number[]>;

  /**
   * Execution times for computations
   */
  computationTimes: Record<string, number[]>;

  /**
   * Number of renders
   */
  renderCount: number;

  /**
   * Last render timestamp
   */
  lastRenderTime: number;
}

/**
 * Global store for all hooks performance data
 */
const hooksPerformanceData: Record<string, HookPerformanceData> = {};

/**
 * Measures the execution time of a selector function
 *
 * @param selectorName Name of the selector
 * @param fn Function to measure
 * @param config Performance monitoring configuration
 * @returns Result of the function
 */
export function measureSelectorTime<T>(
  selectorName: string,
  fn: () => T,
  config: HookPerformanceConfig
): T {
  if (!config.enabled) {
    return fn();
  }

  // Initialize performance data for this hook if it doesn't exist
  if (!hooksPerformanceData[config.hookName]) {
    hooksPerformanceData[config.hookName] = {
      hookName: config.hookName,
      selectorTimes: {},
      computationTimes: {},
      renderCount: 0,
      lastRenderTime: Date.now(),
    };
  }

  // Initialize selector times for this selector if it doesn't exist
  const hookData = hooksPerformanceData[config.hookName];
  if (!hookData.selectorTimes[selectorName]) {
    hookData.selectorTimes[selectorName] = [];
  }

  // Measure execution time
  const start = performance.now();
  const result = fn();
  const end = performance.now();
  const duration = end - start;

  // Store the execution time
  hookData.selectorTimes[selectorName].push(duration);

  // Keep only the last 100 measurements
  if (hookData.selectorTimes[selectorName].length > 100) {
    hookData.selectorTimes[selectorName].shift();
  }

  // Log warning if execution time exceeds threshold
  if (duration > (config.selectorThreshold || 2)) {
    console.warn(
      `[${config.hookName}] Slow selector '${selectorName}': ${duration.toFixed(2)}ms (threshold: ${config.selectorThreshold}ms)`
    );
  } else if (config.verbose) {
    console.log(`[${config.hookName}] Selector '${selectorName}': ${duration.toFixed(2)}ms`);
  }

  return result;
}

/**
 * Measures the execution time of a computation function
 *
 * @param computationName Name of the computation
 * @param fn Function to measure
 * @param config Performance monitoring configuration
 * @returns Result of the function
 */
export function measureComputationTime<T>(
  computationName: string,
  fn: () => T,
  config: HookPerformanceConfig
): T {
  if (!config.enabled) {
    return fn();
  }

  // Initialize performance data for this hook if it doesn't exist
  if (!hooksPerformanceData[config.hookName]) {
    hooksPerformanceData[config.hookName] = {
      hookName: config.hookName,
      selectorTimes: {},
      computationTimes: {},
      renderCount: 0,
      lastRenderTime: Date.now(),
    };
  }

  // Initialize computation times for this computation if it doesn't exist
  const hookData = hooksPerformanceData[config.hookName];
  if (!hookData.computationTimes[computationName]) {
    hookData.computationTimes[computationName] = [];
  }

  // Measure execution time
  const start = performance.now();
  const result = fn();
  const end = performance.now();
  const duration = end - start;

  // Store the execution time
  hookData.computationTimes[computationName].push(duration);

  // Keep only the last 100 measurements
  if (hookData.computationTimes[computationName].length > 100) {
    hookData.computationTimes[computationName].shift();
  }

  // Log warning if execution time exceeds threshold
  if (duration > (config.computationThreshold || 5)) {
    console.warn(
      `[${config.hookName}] Slow computation '${computationName}': ${duration.toFixed(2)}ms (threshold: ${config.computationThreshold}ms)`
    );
  } else if (config.verbose) {
    console.log(`[${config.hookName}] Computation '${computationName}': ${duration.toFixed(2)}ms`);
  }

  return result;
}

/**
 * Tracks a render of a hook
 *
 * @param config Performance monitoring configuration
 */
export function trackHookRender(config: HookPerformanceConfig): void {
  if (!config.enabled) {
    return;
  }

  // Initialize performance data for this hook if it doesn't exist
  if (!hooksPerformanceData[config.hookName]) {
    hooksPerformanceData[config.hookName] = {
      hookName: config.hookName,
      selectorTimes: {},
      computationTimes: {},
      renderCount: 0,
      lastRenderTime: Date.now(),
    };
  }

  // Increment render count
  const hookData = hooksPerformanceData[config.hookName];
  hookData.renderCount++;

  // Calculate time since last render
  const now = Date.now();
  const timeSinceLastRender = now - hookData.lastRenderTime;
  hookData.lastRenderTime = now;

  // Log if verbose
  if (config.verbose) {
    console.log(
      `[${config.hookName}] Render #${hookData.renderCount} (${timeSinceLastRender}ms since last render)`
    );
  }
}

/**
 * Gets performance data for a specific hook
 *
 * @param hookName Name of the hook
 * @returns Performance data for the hook
 */
export function getHookPerformanceData(hookName: string): HookPerformanceData | undefined {
  return hooksPerformanceData[hookName];
}

/**
 * Gets performance data for all hooks
 *
 * @returns Performance data for all hooks
 */
export function getAllHooksPerformanceData(): Record<string, HookPerformanceData> {
  return { ...hooksPerformanceData };
}

/**
 * Gets average execution time for a selector
 *
 * @param hookName Name of the hook
 * @param selectorName Name of the selector
 * @returns Average execution time in milliseconds
 */
export function getAverageSelectorTime(hookName: string, selectorName: string): number {
  const hookData = hooksPerformanceData[hookName];
  if (
    !hookData ||
    !hookData.selectorTimes[selectorName] ||
    hookData.selectorTimes[selectorName].length === 0
  ) {
    return 0;
  }

  const times = hookData.selectorTimes[selectorName];
  return times.reduce((sum, time) => sum + time, 0) / times.length;
}

/**
 * Gets average execution time for a computation
 *
 * @param hookName Name of the hook
 * @param computationName Name of the computation
 * @returns Average execution time in milliseconds
 */
export function getAverageComputationTime(hookName: string, computationName: string): number {
  const hookData = hooksPerformanceData[hookName];
  if (
    !hookData ||
    !hookData.computationTimes[computationName] ||
    hookData.computationTimes[computationName].length === 0
  ) {
    return 0;
  }

  const times = hookData.computationTimes[computationName];
  return times.reduce((sum, time) => sum + time, 0) / times.length;
}

/**
 * Gets a performance report for a hook
 *
 * @param hookName Name of the hook
 * @returns Performance report for the hook
 */
export function getHookPerformanceReport(hookName: string): string {
  const hookData = hooksPerformanceData[hookName];
  if (!hookData) {
    return `No performance data available for hook '${hookName}'`;
  }

  let report = `Performance Report for '${hookName}':\n`;
  report += `Total Renders: ${hookData.renderCount}\n\n`;

  report += `Selectors:\n`;
  Object.entries(hookData.selectorTimes).forEach(([selectorName, times]) => {
    const average = times.reduce((sum, time) => sum + time, 0) / times.length;
    const max = Math.max(...times);
    report += `  - ${selectorName}: avg ${average.toFixed(2)}ms, max ${max.toFixed(2)}ms (${times.length} samples)\n`;
  });

  report += `\nComputations:\n`;
  Object.entries(hookData.computationTimes).forEach(([computationName, times]) => {
    const average = times.reduce((sum, time) => sum + time, 0) / times.length;
    const max = Math.max(...times);
    report += `  - ${computationName}: avg ${average.toFixed(2)}ms, max ${max.toFixed(2)}ms (${times.length} samples)\n`;
  });

  return report;
}

/**
 * Clears performance data for a specific hook
 *
 * @param hookName Name of the hook
 */
export function clearHookPerformanceData(hookName: string): void {
  delete hooksPerformanceData[hookName];
}

/**
 * Clears performance data for all hooks
 */
export function clearAllHooksPerformanceData(): void {
  Object.keys(hooksPerformanceData).forEach(hookName => {
    delete hooksPerformanceData[hookName];
  });
}

/**
 * LongSessionMemoryTracker
 *
 * A utility for tracking memory usage over extended application sessions.
 * It helps identify memory leaks and gradual performance degradation that
 * only become apparent with prolonged application use.
 *
 * Features:
 * - Periodic memory snapshots
 * - Memory growth trend analysis
 * - Leak detection with statistical analysis
 * - Timeline visualization data
 * - Memory allocation and garbage collection tracking
 */

import { moduleEventBus } from '../../../lib/modules/ModuleEvents';
import { ModuleType } from '../../../types/events/ModuleEventTypes';

// Define TypeScript interfaces for browser APIs that might not have type definitions
interface PerformanceMemory {
  usedJSHeapSize: number;
  totalJSHeapSize: number;
  jsHeapSizeLimit: number;
}

// Extend the Performance interface to include the memory property
// Use a module augmentation approach to avoid conflicts with existing definitions
declare global {
  interface Performance {
    // Use a non-colliding property name
    memory?: PerformanceMemory;
  }
}

// Extend the Window interface to potentially support manual GC
declare global {
  interface Window {
    gc?: () => void;
  }
}

/** Memory snapshot data structure */
export interface MemorySnapshot {
  /** Timestamp when the snapshot was taken (ms since epoch) */
  timestamp: number;

  /** Used JavaScript heap size (MB) */
  usedHeapSizeMB: number;

  /** Total allocated JavaScript heap size (MB) */
  totalHeapSizeMB: number;

  /** Maximum JavaScript heap size limit (MB) */
  heapLimitMB: number;

  /** DOM node count (if available) */
  domNodeCount?: number;

  /** Number of detached DOM nodes (if available) */
  detachedDomNodes?: number;

  /** Event listeners count (if available) */
  eventListenerCount?: number;

  /** Active timers count (if available) */
  timerCount?: number;

  /** Active animation frames (if available) */
  animationFrameCount?: number;

  /** Number of large arrays (>10K elements) in memory (if available) */
  largeArrayCount?: number;

  /** Number of active XHR objects (if available) */
  xhrCount?: number;

  /** Number of active fetch requests (if available) */
  fetchCount?: number;

  /** Custom metadata for this snapshot */
  metadata?: Record<string, unknown>;
}

/** Memory trend analysis result */
export interface MemoryTrendAnalysis {
  /** Overall trend (positive means growth, negative means reduction) */
  overallTrend: number;

  /** Growth rate per minute (MB/min) */
  growthRatePerMinute: number;

  /** Growth rate per hour (MB/hour) */
  growthRatePerHour: number;

  /** Extrapolated time until memory limit is reached (ms) */
  estimatedTimeToLimit: number;

  /** Whether memory growth is accelerating */
  isAccelerating: boolean;

  /** Confidence in this analysis (0-1) */
  confidence: number;

  /** Whether there is a suspected memory leak */
  suspectedLeak: boolean;

  /** Potential leak cause if identified */
  leakCause?: string;

  /** Leak severity (1-5, 5 being most severe) */
  leakSeverity?: number;
}

/** Configuration options for the memory tracker */
export interface MemoryTrackerOptions {
  /** Interval between memory snapshots (ms) */
  snapshotIntervalMs?: number;

  /** Maximum number of snapshots to keep */
  maxSnapshots?: number;

  /** Whether to attempt to get detailed memory info */
  detailedMemoryInfo?: boolean;

  /** Whether to track DOM nodes */
  trackDomNodes?: boolean;

  /** Whether to track detached DOM nodes */
  trackDetachedNodes?: boolean;

  /** Whether to attempt garbage collection before snapshots */
  attemptGarbageCollection?: boolean;

  /** Whether to periodically send memory reports to the event bus */
  reportToEventBus?: boolean;

  /** Report interval in ms (how often to send reports) */
  reportIntervalMs?: number;

  /** Threshold for leak detection (growth rate in MB/min) */
  leakThresholdMBPerMinute?: number;

  /** Logging level (0=none, 1=errors, 2=warnings, 3=info) */
  loggingLevel?: number;

  /** Callback when a memory snapshot is taken */
  onSnapshot?: (snapshot: MemorySnapshot) => void;

  /** Callback when memory analysis is updated */
  onAnalysisUpdate?: (analysis: MemoryTrendAnalysis) => void;

  /** Callback when a potential memory leak is detected */
  onLeakDetected?: (analysis: MemoryTrendAnalysis) => void;
}

/**
 * Default configuration for memory tracking
 */
const DEFAULT_OPTIONS: MemoryTrackerOptions = {
  snapshotIntervalMs: 60000, // 1 minute
  maxSnapshots: 120, // 2 hours of data at 1 snapshot per minute
  detailedMemoryInfo: true,
  trackDomNodes: true,
  trackDetachedNodes: false, // Expensive operation, off by default
  attemptGarbageCollection: false,
  reportToEventBus: true,
  reportIntervalMs: 300000, // 5 minutes
  leakThresholdMBPerMinute: 0.5, // 0.5 MB per minute sustained growth could indicate a leak
  loggingLevel: 2,
  onSnapshot: undefined,
  onAnalysisUpdate: undefined,
  onLeakDetected: undefined,
};

/**
 * Core class for tracking memory usage over extended sessions
 */
export class LongSessionMemoryTracker {
  /** Memory snapshots collected over time */
  private snapshots: MemorySnapshot[] = [];

  /** Timestamp when tracking started */
  private startTime: number;

  /** Latest memory analysis */
  private latestAnalysis: MemoryTrendAnalysis | null = null;

  /** Configuration options */
  private options: MemoryTrackerOptions;

  /** Interval ID for snapshots */
  private snapshotIntervalId: number | null = null;

  /** Interval ID for reporting */
  private reportIntervalId: number | null = null;

  /** Whether tracking is currently active */
  private isTracking = false;

  /** Browser support info */
  private browserSupport = {
    memoryAPI: false,
    performanceAPI: false,
    domCountAPI: false,
    gc: false,
  };

  /** Session markers for significant events */
  private sessionMarkers: {
    timestamp: number;
    name: string;
    metadata?: Record<string, unknown>;
  }[] = [];

  /**
   * Create a new memory tracker
   */
  constructor(options: MemoryTrackerOptions = {}) {
    this.options = { ...DEFAULT_OPTIONS, ...options };
    this.startTime = Date.now();
    this.detectBrowserSupport();
  }

  /**
   * Detect browser API support
   */
  private detectBrowserSupport(): void {
    // Check Performance API support
    this.browserSupport.performanceAPI = typeof performance !== 'undefined';

    // Check Memory API support (Chrome)
    this.browserSupport.memoryAPI =
      this.browserSupport.performanceAPI && typeof (performance as any).memory !== 'undefined';

    // Check DOM Count API support
    this.browserSupport.domCountAPI =
      typeof document !== 'undefined' && typeof document.querySelectorAll === 'function';

    // Check GC support (rarely available in browsers)
    this.browserSupport.gc = typeof window !== 'undefined' && typeof window.gc === 'function';

    if (this.options.loggingLevel && this.options.loggingLevel >= 3) {
      console.log('[LongSessionMemoryTracker] Browser support detected:', this.browserSupport);
    }

    // Warn if memory API not available
    if (
      !this.browserSupport.memoryAPI &&
      this.options.loggingLevel &&
      this.options.loggingLevel >= 2
    ) {
      console.warn(
        '[LongSessionMemoryTracker] Performance.memory API not available in this browser. Memory tracking will be limited.'
      );
    }
  }

  /**
   * Start tracking memory usage
   */
  public startTracking(): void {
    if (this.isTracking) return;

    this.isTracking = true;
    this.startTime = Date.now();
    this.snapshots = [];
    this.sessionMarkers = [];

    // Take initial snapshot
    this.takeSnapshot();

    // Set up periodic snapshots
    this.snapshotIntervalId = window.setInterval(
      () => this.takeSnapshot(),
      this.options.snapshotIntervalMs!
    );

    // Set up periodic reporting if enabled
    if (this.options.reportToEventBus) {
      this.reportIntervalId = window.setInterval(
        () => this.sendMemoryReport(),
        this.options.reportIntervalMs!
      );
    }

    if (this.options.loggingLevel && this.options.loggingLevel >= 3) {
      console.log('[LongSessionMemoryTracker] Started tracking memory usage');
    }

    // Add session start marker
    this.addSessionMarker('tracking_started');
  }

  /**
   * Stop tracking memory usage
   */
  public stopTracking(): void {
    if (!this.isTracking) return;

    // Clear intervals
    if (this.snapshotIntervalId !== null) {
      clearInterval(this.snapshotIntervalId);
      this.snapshotIntervalId = null;
    }

    if (this.reportIntervalId !== null) {
      clearInterval(this.reportIntervalId);
      this.reportIntervalId = null;
    }

    this.isTracking = false;

    // Take one final snapshot
    this.takeSnapshot();

    // Add session end marker
    this.addSessionMarker('tracking_stopped');

    if (this.options.loggingLevel && this.options.loggingLevel >= 3) {
      console.log('[LongSessionMemoryTracker] Stopped tracking memory usage');
    }
  }

  /**
   * Take a memory snapshot
   */
  public takeSnapshot(): MemorySnapshot {
    // Attempt garbage collection if configured and supported
    if (this.options.attemptGarbageCollection && this.browserSupport.gc) {
      try {
        window.gc!();
      } catch (e) {
        // Ignore, GC might fail or be unavailable
      }
    }

    // Create base snapshot
    const snapshot: MemorySnapshot = {
      timestamp: Date.now(),
      usedHeapSizeMB: 0,
      totalHeapSizeMB: 0,
      heapLimitMB: 0,
    };

    // Add memory info if available
    if (this.browserSupport.memoryAPI) {
      // Use explicit casting to access the browser-specific memory properties
      // This avoids TypeScript errors while still allowing us to access these properties
      const memoryInfo = (performance as any).memory as PerformanceMemory;
      snapshot.usedHeapSizeMB = memoryInfo.usedJSHeapSize / (1024 * 1024);
      snapshot.totalHeapSizeMB = memoryInfo.totalJSHeapSize / (1024 * 1024);
      snapshot.heapLimitMB = memoryInfo.jsHeapSizeLimit / (1024 * 1024);
    }

    // Count DOM nodes if configured and supported
    if (this.options.trackDomNodes && this.browserSupport.domCountAPI) {
      snapshot.domNodeCount = document.querySelectorAll('*').length;
    }

    // Add to snapshots array, ensuring we don't exceed maximum
    this.snapshots.push(snapshot);
    if (this.snapshots.length > this.options.maxSnapshots!) {
      this.snapshots.shift();
    }

    // Run analysis after sufficient data is collected
    if (this.snapshots.length >= 3) {
      this.analyzeMemoryTrend();
    }

    // Notify via callback if configured
    if (this.options.onSnapshot) {
      this.options.onSnapshot(snapshot);
    }

    return snapshot;
  }

  /**
   * Analyze memory usage trend
   */
  private analyzeMemoryTrend(): void {
    // Need at least 3 snapshots for basic trend analysis
    if (this.snapshots.length < 3) return;

    const snapshots = this.snapshots;
    const timeSpanMs = snapshots[snapshots.length - 1].timestamp - snapshots[0].timestamp;

    // Skip analysis if time span is too short (avoid division by zero issues)
    if (timeSpanMs < 10000) return; // Need at least 10 seconds of data

    // Extract memory usage values
    const memoryValues = snapshots.map(s => s.usedHeapSizeMB);
    const timestamps = snapshots.map(s => s.timestamp);

    // Calculate overall trend (linear regression)
    const { slope, correlation } = this.calculateLinearRegression(timestamps, memoryValues);

    // Calculate rates
    const growthRatePerMinute = slope * 60000; // Convert to MB per minute
    const growthRatePerHour = growthRatePerMinute * 60; // Convert to MB per hour

    // Skip processing if we don't have memory limit data
    let estimatedTimeToLimit = Number.POSITIVE_INFINITY;
    if (snapshots[snapshots.length - 1].heapLimitMB > 0 && growthRatePerMinute > 0) {
      const latestSnapshot = snapshots[snapshots.length - 1];
      const remainingMemoryMB = latestSnapshot.heapLimitMB - latestSnapshot.usedHeapSizeMB;
      estimatedTimeToLimit = (remainingMemoryMB / growthRatePerMinute) * 60000; // in ms
    }

    // Detect if growth is accelerating
    const isAccelerating = this.isGrowthAccelerating(timestamps, memoryValues);

    // Determine confidence level (based on data points and correlation strength)
    const confidence =
      Math.min(
        snapshots.length / 10, // More data points = higher confidence, max at 10 points
        1 // Cap at 1.0
      ) * Math.abs(correlation); // Scale by correlation strength

    // Detect potential memory leak
    const suspectedLeak =
      growthRatePerMinute > this.options.leakThresholdMBPerMinute! &&
      confidence > 0.7 &&
      snapshots.length >= 5; // Need at least 5 data points

    // Create analysis result
    const analysis: MemoryTrendAnalysis = {
      overallTrend: slope,
      growthRatePerMinute,
      growthRatePerHour,
      estimatedTimeToLimit,
      isAccelerating,
      confidence,
      suspectedLeak,
      leakSeverity: suspectedLeak ? this.calculateLeakSeverity(growthRatePerMinute) : undefined,
    };

    this.latestAnalysis = analysis;

    // Trigger callbacks if configured
    if (this.options.onAnalysisUpdate) {
      this.options.onAnalysisUpdate(analysis);
    }

    // Log potential leak detection
    if (suspectedLeak) {
      if (this.options.loggingLevel && this.options.loggingLevel >= 2) {
        console.warn(
          `[LongSessionMemoryTracker] Potential memory leak detected! Memory growing at ${growthRatePerMinute.toFixed(2)} MB/minute`
        );
      }

      if (this.options.onLeakDetected) {
        this.options.onLeakDetected(analysis);
      }

      // Add marker for leak detection
      this.addSessionMarker('leak_detected', {
        growthRatePerMinute,
        severity: analysis.leakSeverity,
      });
    }
  }

  /**
   * Calculate linear regression on time series data
   */
  private calculateLinearRegression(
    xValues: number[],
    yValues: number[]
  ): { slope: number; intercept: number; correlation: number } {
    const n = xValues.length;

    if (n === 0 || xValues.length !== yValues.length) {
      return { slope: 0, intercept: 0, correlation: 0 };
    }

    // Convert timestamps to seconds from start to avoid precision issues
    const startTime = xValues[0];
    const xValuesNormalized = xValues.map(x => (x - startTime) / 1000);

    // Calculate means
    const meanX = xValuesNormalized.reduce((sum, x) => sum + x, 0) / n;
    const meanY = yValues.reduce((sum, y) => sum + y, 0) / n;

    // Calculate sums for regression formula
    let numerator = 0;
    let denominator = 0;
    let sumSquaredErrors = 0;
    let totalSumOfSquares = 0;

    for (let i = 0; i < n; i++) {
      const x = xValuesNormalized[i];
      const y = yValues[i];

      const xDiff = x - meanX;
      const yDiff = y - meanY;

      numerator += xDiff * yDiff;
      denominator += xDiff * xDiff;
      totalSumOfSquares += yDiff * yDiff;
    }

    // Avoid division by zero
    if (denominator === 0) {
      return { slope: 0, intercept: meanY, correlation: 0 };
    }

    // Calculate regression parameters
    const slope = numerator / denominator;
    const intercept = meanY - slope * meanX;

    // Calculate predicted values and sum of squared errors
    for (let i = 0; i < n; i++) {
      const predicted = slope * xValuesNormalized[i] + intercept;
      sumSquaredErrors += Math.pow(yValues[i] - predicted, 2);
    }

    // Calculate correlation coefficient
    let correlation = 0;
    if (totalSumOfSquares > 0) {
      correlation = Math.sqrt(1 - sumSquaredErrors / totalSumOfSquares);

      // Adjust sign based on slope
      if (slope < 0) correlation = -correlation;
    }

    // Convert slope back to original time units (MB per ms)
    const originalSlope = slope / 1000;

    return { slope: originalSlope, intercept, correlation };
  }

  /**
   * Determine if memory growth is accelerating
   */
  private isGrowthAccelerating(timestamps: number[], memoryValues: number[]): boolean {
    if (timestamps.length < 6) return false; // Need at least 6 points for reliable acceleration detection

    // Split data into first and second half
    const midpoint = Math.floor(timestamps.length / 2);
    const firstHalfX = timestamps.slice(0, midpoint);
    const firstHalfY = memoryValues.slice(0, midpoint);
    const secondHalfX = timestamps.slice(midpoint);
    const secondHalfY = memoryValues.slice(midpoint);

    // Calculate growth rate for each half
    const firstHalfRegression = this.calculateLinearRegression(firstHalfX, firstHalfY);
    const secondHalfRegression = this.calculateLinearRegression(secondHalfX, secondHalfY);

    // Compare slopes
    return secondHalfRegression.slope > firstHalfRegression.slope * 1.2; // 20% faster growth = acceleration
  }

  /**
   * Calculate leak severity on a scale of 1-5
   */
  private calculateLeakSeverity(growthRatePerMinute: number): number {
    // Scale from 1-5 based on growth rate
    // 1: slow leak (<1MB/min)
    // 2: moderate leak (1-2MB/min)
    // 3: significant leak (2-5MB/min)
    // 4: serious leak (5-10MB/min)
    // 5: critical leak (>10MB/min)

    if (growthRatePerMinute >= 10) return 5;
    if (growthRatePerMinute >= 5) return 4;
    if (growthRatePerMinute >= 2) return 3;
    if (growthRatePerMinute >= 1) return 2;
    return 1;
  }

  /**
   * Get all collected memory snapshots
   */
  public getSnapshots(): MemorySnapshot[] {
    return [...this.snapshots];
  }

  /**
   * Get the latest memory analysis
   */
  public getLatestAnalysis(): MemoryTrendAnalysis | null {
    return this.latestAnalysis;
  }

  /**
   * Add a session marker for significant events
   */
  public addSessionMarker(name: string, metadata?: Record<string, unknown>): void {
    this.sessionMarkers.push({
      timestamp: Date.now(),
      name,
      metadata,
    });
  }

  /**
   * Get all session markers
   */
  public getSessionMarkers(): {
    timestamp: number;
    name: string;
    metadata?: Record<string, unknown>;
  }[] {
    return [...this.sessionMarkers];
  }

  /**
   * Send memory report to event bus
   */
  private sendMemoryReport(): void {
    if (!this.options.reportToEventBus || !this.isTracking || !this.latestAnalysis) return;

    moduleEventBus.emit({
      type: 'STATUS_CHANGED',
      moduleId: 'long-session-memory-tracker',
      moduleType: 'resource-manager' as ModuleType, // Use a valid ModuleType
      timestamp: Date.now(),
      data: {
        type: 'memory_report',
        snapshots: this.snapshots.slice(-10), // Only send most recent 10 snapshots
        analysis: this.latestAnalysis,
        markers: this.sessionMarkers.slice(-5), // Only send most recent 5 markers
        sessionDurationMs: Date.now() - this.startTime,
      },
    });
  }

  /**
   * Get total session duration in milliseconds
   */
  public getSessionDurationMs(): number {
    return Date.now() - this.startTime;
  }

  /**
   * Force run garbage collection (if supported)
   */
  public attemptGarbageCollection(): boolean {
    if (this.browserSupport.gc && window.gc) {
      try {
        window.gc();
        return true;
      } catch (e) {
        // GC failed or unavailable
      }
    }
    return false;
  }

  /**
   * Clear all accumulated data (snapshots and markers)
   */
  public clearData(): void {
    this.snapshots = [];
    this.sessionMarkers = [];
    this.latestAnalysis = null;
  }
}

/**
 * MultitabCommunicationChannel
 *
 * A utility for establishing communication between multiple browser tabs
 * for coordinated performance testing. This enables testing scenarios where
 * multiple tabs are running the application simultaneously to measure performance impact.
 *
 * The communication is based on BroadcastChannel API with localStorage fallback
 * for older browsers.
 */

export interface TabMessage {
  /** Unique ID of the tab that sent the message */
  senderId: string;

  /** Type of message being sent */
  type: 'HELLO' | 'GOODBYE' | 'SYNC' | 'START_TEST' | 'END_TEST' | 'REPORT' | 'CONTROL' | 'ERROR';

  /** Timestamp when the message was sent */
  timestamp: number;

  /** Message data payload */
  payload?: Record<string, unknown>;
}

export interface TabInfo {
  /** Unique ID of the tab */
  id: string;

  /** Role of the tab in the test (coordinator or worker) */
  role: 'coordinator' | 'worker';

  /** When the tab joined the session */
  joinedAt: number;

  /** Current state of the tab */
  state: 'idle' | 'ready' | 'testing' | 'complete' | 'error';

  /** Last time we received a message from this tab */
  lastSeenAt: number;
}

export interface TestConfiguration {
  /** Type of test to run */
  testType: string;

  /** Test-specific parameters */
  parameters: Record<string, unknown>;

  /** Duration of the test in milliseconds */
  durationMs: number;

  /** Whether to synchronize the start of the test across tabs */
  synchronizeStart: boolean;
}

export type MessageHandler = (message: TabMessage) => void;

/**
 * Class for managing communication between tabs
 */
export class MultitabCommunicationChannel {
  /** Unique ID for this tab */
  private tabId: string;

  /** Role of this tab in the test */
  private role: 'coordinator' | 'worker';

  /** BroadcastChannel instance if supported */
  private broadcastChannel: BroadcastChannel | null = null;

  /** Information about all known tabs */
  private knownTabs: Map<string, TabInfo> = new Map();

  /** Whether we're using localStorage fallback */
  private usingLocalStorageFallback = false;

  /** Storage key prefix for localStorage communication */
  private readonly storageKeyPrefix = 'multitab_perf_test_';

  /** Timestamp when we last checked localStorage */
  private lastStorageCheck = 0;

  /** Registered message handlers */
  private messageHandlers: MessageHandler[] = [];

  /** Storage polling interval ID */
  private pollingIntervalId: number | null = null;

  /** Whether the channel is currently active */
  private active = false;

  /**
   * Create a new MultitabCommunicationChannel
   *
   * @param role Role of this tab (coordinator or worker)
   */
  constructor(role: 'coordinator' | 'worker' = 'worker') {
    // Generate a unique ID for this tab
    this.tabId = this.generateTabId();
    this.role = role;

    // Add this tab to the known tabs
    this.knownTabs.set(this.tabId, {
      id: this.tabId,
      role: this.role,
      joinedAt: Date.now(),
      state: 'idle',
      lastSeenAt: Date.now(),
    });

    // Set up communication
    this.setupCommunication();
  }

  /**
   * Generate a unique ID for this tab
   */
  private generateTabId(): string {
    return `tab-${Date.now()}-${Math.random().toString(36).substring(2, 9)}`;
  }

  /**
   * Set up the communication channel
   */
  private setupCommunication(): void {
    // Try to use BroadcastChannel if available
    if (typeof BroadcastChannel !== 'undefined') {
      try {
        this.broadcastChannel = new BroadcastChannel('galactic_sprawl_multitab_perf_test');
        this.broadcastChannel.onmessage = event => {
          this.handleIncomingMessage(event.data);
        };
        console.log(`[Tab ${this.tabId}] Using BroadcastChannel for communication`);
      } catch (error) {
        console.warn(
          'BroadcastChannel initialization failed, falling back to localStorage:',
          error
        );
        this.setupLocalStorageFallback();
      }
    } else {
      console.log(
        `[Tab ${this.tabId}] BroadcastChannel not supported, falling back to localStorage`
      );
      this.setupLocalStorageFallback();
    }
  }

  /**
   * Set up localStorage fallback for environments without BroadcastChannel
   */
  private setupLocalStorageFallback(): void {
    this.usingLocalStorageFallback = true;

    // Listen for storage events
    window.addEventListener('storage', event => {
      if (event.key && event.key.startsWith(this.storageKeyPrefix)) {
        try {
          const message = JSON.parse(event.newValue || '');
          // Ignore our own messages
          if (message.senderId !== this.tabId) {
            this.handleIncomingMessage(message);
          }
        } catch (error) {
          console.error('Error parsing multitab message:', error);
        }
      }
    });

    // Set up polling to periodically check localStorage in case storage events aren't reliable
    this.pollingIntervalId = window.setInterval(() => {
      this.checkLocalStorageMessages();
    }, 500) as unknown as number;
  }

  /**
   * Check localStorage for new messages
   */
  private checkLocalStorageMessages(): void {
    const now = Date.now();
    // Only check messages newer than our last check
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      if (key && key.startsWith(this.storageKeyPrefix)) {
        try {
          const messageData = localStorage.getItem(key);
          if (messageData) {
            const message = JSON.parse(messageData) as TabMessage;

            // Only process if it's not our own message and it's newer than our last check
            if (message.senderId !== this.tabId && message.timestamp > this.lastStorageCheck) {
              this.handleIncomingMessage(message);
            }
          }
        } catch (error) {
          console.error('Error checking localStorage message:', error);
        }
      }
    }
    this.lastStorageCheck = now;
  }

  /**
   * Handle an incoming message from another tab
   */
  private handleIncomingMessage(message: TabMessage): void {
    // Update last seen time for the sender tab
    if (this.knownTabs.has(message.senderId)) {
      const tabInfo = this.knownTabs.get(message.senderId)!;
      tabInfo.lastSeenAt = Date.now();
      this.knownTabs.set(message.senderId, tabInfo);
    } else if (message.type === 'HELLO') {
      // New tab joined
      this.knownTabs.set(message.senderId, {
        id: message.senderId,
        role: message.payload?.role || 'worker',
        joinedAt: message.timestamp,
        state: message.payload?.state || 'idle',
        lastSeenAt: Date.now(),
      });

      // If we're the coordinator, send a HELLO back to let them know about us
      if (this.role === 'coordinator') {
        this.sendMessage({
          type: 'HELLO',
          payload: {
            role: this.role,
            state: this.getState(),
            knownTabs: Array.from(this.knownTabs.values()),
          },
        });
      }
    } else if (message.type === 'GOODBYE') {
      // Tab left
      this.knownTabs.delete(message.senderId);
    }

    // Notify handlers
    this.notifyHandlers(message);
  }

  /**
   * Notify all registered message handlers
   */
  private notifyHandlers(message: TabMessage): void {
    this.messageHandlers.forEach(handler => {
      try {
        handler(message);
      } catch (error) {
        console.error('Error in message handler:', error);
      }
    });
  }

  /**
   * Send a message to all tabs
   */
  public sendMessage(message: Omit<TabMessage, 'senderId' | 'timestamp'>): void {
    if (!this.active) {
      console.warn('Attempted to send message on inactive channel');
      return;
    }

    const fullMessage: TabMessage = {
      ...message,
      senderId: this.tabId,
      timestamp: Date.now(),
    };

    // Send via BroadcastChannel if available
    if (this.broadcastChannel) {
      this.broadcastChannel.postMessage(fullMessage);
    }

    // Also send via localStorage if we're using fallback or want to ensure delivery
    if (this.usingLocalStorageFallback) {
      const key = `${this.storageKeyPrefix}${fullMessage.timestamp}_${this.tabId}`;
      localStorage.setItem(key, JSON.stringify(fullMessage));

      // Clean up old messages after a delay
      setTimeout(() => {
        localStorage.removeItem(key);
      }, 10000);
    }
  }

  /**
   * Register a handler for incoming messages
   */
  public addMessageHandler(handler: MessageHandler): void {
    this.messageHandlers.push(handler);
  }

  /**
   * Remove a previously registered message handler
   */
  public removeMessageHandler(handler: MessageHandler): void {
    const index = this.messageHandlers.indexOf(handler);
    if (index !== -1) {
      this.messageHandlers.splice(index, 1);
    }
  }

  /**
   * Get information about all known tabs
   */
  public getKnownTabs(): TabInfo[] {
    return Array.from(this.knownTabs.values());
  }

  /**
   * Get the current state of this tab
   */
  public getState(): 'idle' | 'ready' | 'testing' | 'complete' | 'error' {
    const tabInfo = this.knownTabs.get(this.tabId);
    return tabInfo?.state || 'idle';
  }

  /**
   * Update the state of this tab
   */
  public setState(state: 'idle' | 'ready' | 'testing' | 'complete' | 'error'): void {
    const tabInfo = this.knownTabs.get(this.tabId);
    if (tabInfo) {
      tabInfo.state = state;
      this.knownTabs.set(this.tabId, tabInfo);

      // Notify other tabs of the state change
      this.sendMessage({
        type: 'SYNC',
        payload: { state },
      });
    }
  }

  /**
   * Clean up old tabs
   * Tabs that haven't sent a message in more than 30 seconds are considered closed
   */
  public cleanupOldTabs(): void {
    const now = Date.now();
    const threshold = now - 30000; // 30 seconds

    for (const [tabId, tabInfo] of this.knownTabs.entries()) {
      if (tabId !== this.tabId && tabInfo.lastSeenAt < threshold) {
        this.knownTabs.delete(tabId);
      }
    }
  }

  /**
   * Activate the communication channel and announce presence
   */
  public activate(): void {
    if (this.active) return;

    this.active = true;

    // Announce our presence
    this.sendMessage({
      type: 'HELLO',
      payload: {
        role: this.role,
        state: this.getState(),
      },
    });

    // Set up cleanup interval
    setInterval(() => {
      this.cleanupOldTabs();
    }, 10000);
  }

  /**
   * Deactivate the communication channel and clean up
   */
  public deactivate(): void {
    if (!this.active) return;

    // Announce our departure
    this.sendMessage({
      type: 'GOODBYE',
      payload: null,
    });

    // Clean up
    if (this.broadcastChannel) {
      this.broadcastChannel.close();
      this.broadcastChannel = null;
    }

    if (this.pollingIntervalId !== null) {
      clearInterval(this.pollingIntervalId);
      this.pollingIntervalId = null;
    }

    this.active = false;
  }

  /**
   * Get this tab's ID
   */
  public getTabId(): string {
    return this.tabId;
  }

  /**
   * Get this tab's role
   */
  public getRole(): 'coordinator' | 'worker' {
    return this.role;
  }

  /**
   * Check if this tab is the coordinator
   */
  public isCoordinator(): boolean {
    return this.role === 'coordinator';
  }

  /**
   * Check if this tab is a worker
   */
  public isWorker(): boolean {
    return this.role === 'worker';
  }

  /**
   * Find the coordinator tab
   */
  public findCoordinator(): TabInfo | null {
    for (const tabInfo of this.knownTabs.values()) {
      if (tabInfo.role === 'coordinator') {
        return tabInfo;
      }
    }
    return null;
  }

  /**
   * Check if there are any active tabs (including this one)
   */
  public getActiveTabCount(): number {
    return this.knownTabs.size;
  }
}

/**
 * Network Degradation Simulator
 *
 * This utility provides tools for simulating various network conditions to test
 * application performance and resilience under different network scenarios.
 *
 * Key capabilities:
 * - Simulate latency, bandwidth limitations, and packet loss
 * - Apply network degradation to fetch, WebSocket, and XMLHttpRequest
 * - Create realistic network profiles matching common scenarios
 * - Offer programmatic and declarative APIs for testing
 */

export interface NetworkCondition {
  /** Name describing this network condition */
  name: string;

  /** Description with more details about the network condition */
  description: string;

  /** Network latency in milliseconds (one-way) */
  latencyMs: number;

  /** Throughput in kilobits per second */
  throughputKbps: number;

  /** Packet loss probability (0-1) */
  packetLoss: number;

  /** Latency jitter in milliseconds (variance in latency) */
